INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4122,200,3.jpg\" alt=\"\" width=\"176\" height=\"200\" />У  ці дні Різдвяних свят бажаємо Вам ще більше відкрити для себе глибину  величезної Божої Любові до нас, бути улюбленим дитям Божим та  спадкоємцем усіх Небесних скарбів!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Веселих свят Різдва Христового та щасливого Нового Року!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нехай благословить Вас Господь у Новому році!</span></span></p>','2012-01-08 16:18:31',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4122,200,3.jpg\" alt=\"\" />&laquo;Христос народжується - славте!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос iз небес - зустрічайте!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос на Землі - бадьортеся!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Співай Господеві, вся земле, і весело заспівайте, люди, бо Він прославився!&raquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ірмос Канону Різдва</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Христос рождається! </em></strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дорогі у Христі! </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прийміть щиросердечні вітання з нагоди Дня Великої Радості Неба і Землі &ndash;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світлого Празника Різдва Христового!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У цей ясний зимовий день ділимося з Вами радістю Христового Воплочення. Ця дивовижна подія, що сталася більш ніж дві тисячі років тому не перестає дивувати нас і сьогодні, відкриваючи нам невичерпну таємницю Божої премудрості. Бог приходить до нас дитиною... Дитя тієї далекої Вифлеємської печери не хоче щоб ми боялися Його. Воно входить у наше серце не страхом і силою, не доказами своєї влади та могутності, а тільки Любов&rsquo;ю.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У ці дні Різдвяних свят бажаємо Вам ще більше відкрити для себе глибину величезної Божої Любові до нас, бути улюбленим дитям Божим та спадкоємцем усіх Небесних скарбів!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Веселих свят Різдва Христового та щасливого Нового Року!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нехай благословить Вас Господь у Новому році!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В імені вірних Української Греко-Католицької Церкви міста Житомира </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о. Віталій Сидорук</span></span>, <span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">протопресвітер Житомирський</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Всесвітлішому мітрофорному протоієрею</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>пароху храму св. Миколая Чудотворця </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>на Аскольдовій могилі</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>о. Ігорю Онишкевичу</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>10.06.2011 р. Б.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Всесвітліший о. Ігоре, дорогі парохіяни!</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від щирого серця висловлюю вдячність від імені Благодійного Фонду &laquo;Карітас Київ&raquo;, якому допомагаєте Ви особисто та Ваша парохія. Від серпня 2010 р. до червня 2011 р. Вами було передано для потребуючих людей, якими опікується &laquo;Карітас Київ&raquo;: цукерки у коробках та розважні (16 пачок), кокосове молоко, одяг, 2 інвалідні коляски та гроші із &laquo;Благодійних скриньок&raquo; (4688 грн) та інше.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цукерки та кокосове молоко було роздано дітям вулиці, ветеранам, багатодітним сім&rsquo;ям. Кошти ж отримані із &laquo;Благодійної скриньки&raquo;, яка розміщена у Вашому храмі, використано на придбання медикаментів для хворих людей.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діла милосердя &ndash; це наша спільна справа, до якої кличе нас Христос, тому спільними зусиллями зможемо повноцінно надати потребуючим допомогу, служачи в такий спосіб Богові.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Соціальна діяльність Української Греко-Католицької Церкви&nbsp; завжди була і є одною з найважливіших форм душпастирської опіки над потребуючими та хворими, тому кожен християнин та спільнота віруючих має обов&rsquo;язок чинити діла милосердя. У Євангелії читаємо, як Христос приходив до хворих, упосліджених, перебував із ними, лікував та оздоровлював їх. Місія Христової Церкви сьогодні &ndash; це робити так само - &laquo;Іди і ти роби так само&raquo;, - каже Христос у притчі про милосердного Самарянина , а Церквою є саме ми - віруючі. Тому так важливо у нашому житті включати і соціальну харитативну діяльність. Це ознака того, що нам важливими є всі люди і, що для нас їх проблеми, потреби, хвороби не є далекими, але близькими, і ми хочемо їх розділити із ними.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Водночас хочу запросити до подальшої співпраці у допомозі потребуючим. Господь Бог є милосердним, тому запрошує нас до діл милосердя. Творімо добро, якого так потребує сьогоднішній світ. Кожна допомога для потребуючих дітей чи людей похилого віку є важливою та цінною, тому будемо дуже раді, якщо матимете можливість допомагати і надалі тим, хто потребує Вашої допомоги. Кожна вкинена лепта у скриньку милосердя буде винагороджена сторицею.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нехай Господь Бог винагородить Вас сторицею за діла милосердя, які щоденно творите для добра людей.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З вдячністю за підтримку, надією на подальшу співпрацю та побажанням всіх Божих ласк</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Директор БФ &laquo;Карітас Київ&raquo; о. Роман Сиротич</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сердечно вітаю Вас з Новим роком та РіздвомХристовим. БажаюбагатощедрихБожихблагословінь, ревності та наснаги для праці у Христовому винограднику, щастя у Бозі, святості в житті, миру у серці.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зірничка з неба світлокидає</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Христос-Дитятко нинісярождає</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світи весело Христу й Марії</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кріпіть у серцівіру й надію</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хай цязірничка в Різдвяні свята</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Несещастя до Вашоїхати</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Здоров&rsquo;я, потіху і ласку від Бога</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Багатство і долю на многіїліта.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">о.Михайло Будник, Рух Назаретських Родин, парафіяни та родина.</span></span></strong></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://cerkvaimisija.com.ua-/\">http://cerkvaimisija.com.ua-</a>мій блог.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ДРУЗІ! </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ВІТАЮ ЗІ СВЯТОМ РІЗДВА ХРИСТОВОГО!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для нашого народу - це особливе свято. Не випадково, навіть у найскрутніші часи українці гідно і велично відзначали Різдво. Адже містерія цього свята вселяє у наші серця впевненість, що з Божою допомогою можна здолати усі труднощі і негаразди.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Особливо зворушливо відзначали Різдво вояки УПА. У цей день вони згадували своїх побратимів, які зі зброєю в руках загинули за волю України. У ці святкові дні в тихій молитві згадуємо усіх, хто боровся за визволення нашої Батьківщини, а особливо тих, хто не дожив до наших днів, коли утверджується державність Української нації.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">БАЖАЮ, ЩОБ БОЖЕ НЕМОВЛЯТКО ЗІГРIЛО ТЕПЛОМ І ЛЮБОВ\'Ю КОЖНЕ СЕРЦЕ!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З повагою,</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Богдан ЧЕРВАК, перший заступник Голови ОУН</span></span></strong></p>','Різдво Христове 2012 - привітання','uk',2639,4122,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4136,200,400939_329625420403412_198406070192015_1027661_242487019_n.jpg\" alt=\"\" /><strong>Христос Народився!</strong><br /> У неділю, <strong>29 січня 2012 року</strong>, традиційно, біля  храму св. Миколи Чудотворця на Аскольдові Могилі <strong>вшановуватимуть пам&lsquo;ять  Героїв Крут</strong>. Довший час покладання квітів та молитви за загиблими  складали біля дерев&lsquo;яного хреста. Оскільки, з часом років, хрест перестав відповідав сакральному статусу, на могилі Героїв Крут встановили гранітний Козацький Хрест.</span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /> Цьогорічні святкування будуть особливими, адже в їх основі буде   освячення гранітного Хреста, як символ пам&lsquo;яті нащадків про незабутній   подвиг, який звершили Герої Крут.</span></span></p>','2012-01-28 23:49:50',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4136,200,400939_329625420403412_198406070192015_1027661_242487019_n.jpg\" alt=\"\" /><strong>Христос Народився!</strong><br /> У неділю, <strong>29 січня 2012 року</strong>, традиційно, біля  храму св. Миколи Чудотворця на Аскольдові Могилі <strong>вшановуватимуть пам&lsquo;ять  Героїв Крут</strong>. Довший час покладання квітів та молитви за загиблими  складали біля дерев&lsquo;яного хреста. Оскільки, з часом років, хрест перестав відповідав сакральному статусу, на могилі Героїв Крут встановили гранітний Козацький Хрест.<br /> Цьогорічні святкування будуть особливими, адже в їх основі буде  освячення гранітного Хреста, як символ пам&lsquo;яті нащадків про незабутній  подвиг, який звершили Герої Крут.<br /> Запрошуємо усіх і кожного зокрема  взяти участь у молебні пам&lsquo;яті Героїв Крут. <strong>Початок спільної молитви у  неділю, 29 січня 2012 року, о 10.00 біля храму св. Миколи Чудотворця на  Аскольдові Могилі.</strong><br /> <strong>Славімо Його!</strong></span></span></p>','Вшанування пам\'яті Героїв Крут','uk',2653,4136,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4170,200,70221354.jpg\" alt=\"\" />Збір паломників призначено на 8-00 годину 17 березня ц.р. (субота) біля церкви на Аскольдовій могилі у Києві. Виїзд автобусом передбачено з 8-30 годин. Панахида пам&rsquo;яті Героїв Крут та екскурсія Меморіалом Героїв Крут з 11-00 години, екскурсія містом Ніжин, відвідування Миколаївського собору та Благовіщенського собору у місті Ніжині з наступною обідньою перервою з 12-30 до 14-30 години. З 16-00 &ndash;&nbsp; ознайомлення з Трьох-Святительською церквою у с. Лемеші; Вознесенською церквою та Собором Різдва Богородиці (де знаходиться семиярусний іконостас &ndash; один із найбільших в Україні) у Козельці. Повернення до Києва о 20-й годині.</span></span></p>','2012-03-10 13:50:54',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4170,200,70221354.jpg\" alt=\"\" width=\"200\" height=\"200\" />Громада&nbsp; храму Св. Миколая Чудотворця, що на Аскольдовій могилі у м. Києві, з благословення настоятеля храму о. Ігоря Онишкевича запрошує всіх бажаючих здійснити паломництво до меморіалу у с. Крути (Ніжинський р-н).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Паломництво здійснюється з нагоди річниці перепоховання загиблих Героїв Крут, які віддали своє життя за волю та незалежність Української держави, та вшанування пам&rsquo;яті українських патріотів спільною молитвою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,4170,200,300px-Battle_of_Kruty.jpg\" alt=\"\" />Збір паломників призначено на 8-00 годину 17 березня ц.р. (субота) біля церкви на Аскольдовій могилі у Києві. Виїзд автобусом передбачено з 8-30 годин. Панахида пам&rsquo;яті Героїв Крут та екскурсія Меморіалом Героїв Крут з 11-00 години, екскурсія містом Ніжин, відвідування Миколаївського собору та Благовіщенського собору у місті Ніжині з наступною обідньою перервою з 12-30 до 14-30 години. З 16-00 &ndash;&nbsp; ознайомлення з Трьох-Святительською церквою у с. Лемеші; Вознесенською церквою та Собором Різдва Богородиці (де знаходиться семиярусний іконостас &ndash; один із найбільших в Україні) у Козельці. Повернення до Києва о 20-й годині.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість проїзду комфортабельним автобусом &laquo;Неоплан&raquo; за паломницьким маршрутом &ndash; 65 гривень; квитки для ознайомлення з музейними експозиціями &ndash; за бажанням; паломницька карта &laquo;Аскольдова могила &ndash; Крути&raquo; &ndash; у вільному продажу в книжковому ятку поруч з церквою. З собою брати бутерброди, фрукти, термоси з теплими напоями та добрий настрій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організаційний комітет з паломництва</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бажаючим здійснити паломництво просимо записуватися у послушниці книжкового ятка&nbsp; біля&nbsp; церкви св. Миколая&nbsp; або зателефонувати п. Руслані <strong><em><span style=\"text-decoration: underline;\">+38&nbsp;(097)&nbsp;931 21 24</span></em></strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Записатися можна за електронною адресою: <a href=\"http://freemail.ukr.net/q/start\"><strong>nikolia7@ukr.net</strong></a></span></span></p>','ПАЛОМНИЦТВО до МЕМОРІАЛУ ПАМ’ЯТІ ГЕРОЇВ  КРУТ 17 березня 2012 року','uk',2687,4170,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,4208,200,anons.png\" alt=\"\" />Одним з&nbsp; перших&nbsp; сподвижників християнської віри на Україні є Аскольд-Миколай, цар і мученик за віру Христову.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Презентація анімаційного фільму &laquo;Велика історія маленької церкви&raquo;, присвячена 1130 річчю церкви на Аскольдовій Могилі, відбудеться 22 квітня у&nbsp; неділю о 14 год.</span></span></p>','2012-04-22 17:18:57',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одним з&nbsp; перших&nbsp; сподвижників християнської віри на Україні є Аскольд-Миколай, цар і мученик за віру Христову.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Презентація анімаційного фільму &laquo;Велика історія маленької церкви&raquo;, присвячена 1130 річчю церкви на Аскольдовій Могилі, відбудеться 22 квітня у&nbsp; неділю о 14 год.</span></span></p>','«Велика історія маленької церкви»','uk',2725,4208,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.haruk.org.ua/_si/0/50251836.jpg\" alt=\"\" width=\"113\" height=\"147\" /><strong>5  березня</strong> у суботній вечір <strong>о 19:00</strong> у церкві св.. Миколая Чудотворця <strong>на  Аскольдовій Могилі</strong> із запаленими свічками буде відправлена <strong>панахида  пам&rsquo;яті Романа Шухевича</strong>. На панахиді пом&rsquo;януть усіх, хто віддав життя за  волю України, чиїх могил не знаємо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6  березня</strong> після дитячої Служби Божої <strong>о 13:30 </strong>парафіяльні діти  представлять <strong>концерт</strong> присвячений пам&rsquo;яті Романа Шухевича та його  побратимів.</span></span></p>','2011-03-05 09:14:05',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.haruk.org.ua/_si/0/50251836.jpg\" alt=\"\" width=\"136\" height=\"176\" />5 березня у суботній вечір о 19:00 у церкві св.. Миколая Чудотворця на Аскольдовій Могилі із запаленими свічками буде відправлена панахида пам&rsquo;яті Романа Шухевича. На панахиді пом&rsquo;януть усіх, хто віддав життя за волю України, чиїх могил не знаємо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6 березня після дитячої Служби Божої о 13:30 парафіяльні діти представлять концерт присвячений пам&rsquo;яті Романа Шухевича та його побратимів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вшанування пам\'яті у спільноті</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://uk.wikipedia.org/wiki/12_%D0%B6%D0%BE%D0%B2%D1%82%D0%BD%D1%8F\">12 жовтня</a> <a href=\"http://uk.wikipedia.org/wiki/2007\">2007</a> року указом Президента України <a title=\"Віктор Ющенко\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D1%96%D0%BA%D1%82%D0%BE%D1%80_%D0%AE%D1%89%D0%B5%D0%BD%D0%BA%D0%BE\">В.Ющенка</a> &laquo;за визначний особистий внесок у національно-визвольну боротьбу за свободу і незалежність України та з нагоди 100-річчя від дня народження та 65-ї річниці створення Української повстанської армії&raquo; посмертно нагороджений званням <a title=\"Герой України\" href=\"http://uk.wikipedia.org/wiki/%D0%93%D0%B5%D1%80%D0%BE%D0%B9_%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D0%B8\">Героя України</a><sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-42\">[43]</a></sup>. У <a href=\"http://uk.wikipedia.org/wiki/2010\">2010</a> році Донецький окружний адміністративний суд в порушення статті 19 Кодексу адміністративного судочинства України<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-43\">[44]</a></sup> і в порушення статті 5 Закону України &laquo;Про державні нагороди України&raquo;<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-44\">[45]</a></sup> визнав указ недійсним, посилаючись на те, що &laquo;звання Героя України можуть отримати тільки громадяни України, а Шухевич громадянином України не був&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У відповідь на позбавлення звання &laquo;Герой України&raquo; низка міст західної України присвоїла Романові Шухевичу звання почесного громадянина. Так, <a href=\"http://uk.wikipedia.org/wiki/16_%D0%B1%D0%B5%D1%80%D0%B5%D0%B7%D0%BD%D1%8F\">16 березня</a><a href=\"http://uk.wikipedia.org/wiki/2010\">2010</a> року він отримав звання &laquo;<a title=\"Почесний громадянин міста Хуст (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BE%D1%87%D0%B5%D1%81%D0%BD%D0%B8%D0%B9_%D0%B3%D1%80%D0%BE%D0%BC%D0%B0%D0%B4%D1%8F%D0%BD%D0%B8%D0%BD_%D0%BC%D1%96%D1%81%D1%82%D0%B0_%D0%A5%D1%83%D1%81%D1%82&amp;action=edit&amp;redlink=1\">Почесний громадянин міста Хуст&raquo;</a>, <a href=\"http://uk.wikipedia.org/wiki/30_%D0%BA%D0%B2%D1%96%D1%82%D0%BD%D1%8F\">30 квітня</a>&nbsp;&mdash; &laquo;<a title=\"Почесний громадянин міста Тернопіль (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BE%D1%87%D0%B5%D1%81%D0%BD%D0%B8%D0%B9_%D0%B3%D1%80%D0%BE%D0%BC%D0%B0%D0%B4%D1%8F%D0%BD%D0%B8%D0%BD_%D0%BC%D1%96%D1%81%D1%82%D0%B0_%D0%A2%D0%B5%D1%80%D0%BD%D0%BE%D0%BF%D1%96%D0%BB%D1%8C&amp;action=edit&amp;redlink=1\">Почесний громадянин міста Тернопіль&raquo;</a>, <a href=\"http://uk.wikipedia.org/wiki/6_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\">6 травня</a>&nbsp;&mdash; &laquo;<a title=\"Почесний громадянин міста Івано-Франківськ (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BE%D1%87%D0%B5%D1%81%D0%BD%D0%B8%D0%B9_%D0%B3%D1%80%D0%BE%D0%BC%D0%B0%D0%B4%D1%8F%D0%BD%D0%B8%D0%BD_%D0%BC%D1%96%D1%81%D1%82%D0%B0_%D0%86%D0%B2%D0%B0%D0%BD%D0%BE-%D0%A4%D1%80%D0%B0%D0%BD%D0%BA%D1%96%D0%B2%D1%81%D1%8C%D0%BA&amp;action=edit&amp;redlink=1\">Почесний громадянин міста Івано-Франківськ&raquo;</a>, <a href=\"http://uk.wikipedia.org/wiki/7_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\">7 травня</a>&nbsp;&mdash; &laquo;<a title=\"Почесний громадянин міста Львів (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BE%D1%87%D0%B5%D1%81%D0%BD%D0%B8%D0%B9_%D0%B3%D1%80%D0%BE%D0%BC%D0%B0%D0%B4%D1%8F%D0%BD%D0%B8%D0%BD_%D0%BC%D1%96%D1%81%D1%82%D0%B0_%D0%9B%D1%8C%D0%B2%D1%96%D0%B2&amp;action=edit&amp;redlink=1\">Почесний громадянин міста Львів&raquo;</a><sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-45\">[46]</a></sup>, <a href=\"http://uk.wikipedia.org/wiki/13_%D1%81%D1%96%D1%87%D0%BD%D1%8F\">13 січня</a> <a href=\"http://uk.wikipedia.org/wiki/2011\">2011</a> року&nbsp;&mdash; &laquo;<a title=\"Почесний громадянин міста Теребовля (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9F%D0%BE%D1%87%D0%B5%D1%81%D0%BD%D0%B8%D0%B9_%D0%B3%D1%80%D0%BE%D0%BC%D0%B0%D0%B4%D1%8F%D0%BD%D0%B8%D0%BD_%D0%BC%D1%96%D1%81%D1%82%D0%B0_%D0%A2%D0%B5%D1%80%D0%B5%D0%B1%D0%BE%D0%B2%D0%BB%D1%8F&amp;action=edit&amp;redlink=1\">Почесний громадянин міста Теребовля&raquo;</a><sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-46\">[47]</a></sup>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://upload.wikimedia.org/wikipedia/uk/f/f7/Moneta-shuchevyc.jpg\" alt=\"\" />Ювілейна 5-гривнева монета із серії &laquo;Знамениті особистості України&raquo;, присвячена Роману Шухевичу, 2008 рік</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Згідно з рішенням виконавчого комітету Львівської міської ради від <a href=\"http://uk.wikipedia.org/wiki/20_%D1%81%D0%B5%D1%80%D0%BF%D0%BD%D1%8F\">20 серпня</a><a href=\"http://uk.wikipedia.org/wiki/1991\">1991</a> року &laquo;Про створення музею командуючого УПА генерал-хорунжого Романа Шухевича&raquo;, <a href=\"http://uk.wikipedia.org/wiki/23_%D0%B6%D0%BE%D0%B2%D1%82%D0%BD%D1%8F\">23 жовтня</a> <a href=\"http://uk.wikipedia.org/wiki/2001\">2001</a> року у <a title=\"Львів\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D1%8C%D0%B2%D1%96%D0%B2\">Львові</a>, в <a title=\"Білогорща (місцевість)\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D1%96%D0%BB%D0%BE%D0%B3%D0%BE%D1%80%D1%89%D0%B0_%28%D0%BC%D1%96%D1%81%D1%86%D0%B5%D0%B2%D1%96%D1%81%D1%82%D1%8C%29\">Білогорщі</a>, в колишньому підпільному штабі ОУН, де помер Шухевич, було урочисто відкрито <a title=\"Музей Головного Командира Української Повстанської Армії генерал-хорунжого Романа Шухевича (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%9C%D1%83%D0%B7%D0%B5%D0%B9_%D0%93%D0%BE%D0%BB%D0%BE%D0%B2%D0%BD%D0%BE%D0%B3%D0%BE_%D0%9A%D0%BE%D0%BC%D0%B0%D0%BD%D0%B4%D0%B8%D1%80%D0%B0_%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D1%81%D1%8C%D0%BA%D0%BE%D1%97_%D0%9F%D0%BE%D0%B2%D1%81%D1%82%D0%B0%D0%BD%D1%81%D1%8C%D0%BA%D0%BE%D1%97_%D0%90%D1%80%D0%BC%D1%96%D1%97_%D0%B3%D0%B5%D0%BD%D0%B5%D1%80%D0%B0%D0%BB-%D1%85%D0%BE%D1%80%D1%83%D0%BD%D0%B6%D0%BE%D0%B3%D0%BE_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD%D0%B0_%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87%D0%B0&amp;action=edit&amp;redlink=1\">музей</a>. <a href=\"http://uk.wikipedia.org/wiki/5_%D0%B1%D0%B5%D1%80%D0%B5%D0%B7%D0%BD%D1%8F\">5 березня</a> <a href=\"http://uk.wikipedia.org/wiki/2009\">2009</a> року навпроти нього відкрили пам\'ятник генералу<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-47\">[48]</a></sup>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://uk.wikipedia.org/wiki/30_%D1%87%D0%B5%D1%80%D0%B2%D0%BD%D1%8F\">30 червня</a> <a href=\"http://uk.wikipedia.org/wiki/2007\">2007</a> року у селі <a href=\"http://uk.wikipedia.org/wiki/%D0%A2%D0%B8%D1%88%D0%BA%D1%96%D0%B2%D1%86%D1%96\">Тишківці</a> Івано-Франківської області урочисто відкрито <a title=\"Будинок-музей родини Шухевичів (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%91%D1%83%D0%B4%D0%B8%D0%BD%D0%BE%D0%BA-%D0%BC%D1%83%D0%B7%D0%B5%D0%B9_%D1%80%D0%BE%D0%B4%D0%B8%D0%BD%D0%B8_%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87%D1%96%D0%B2&amp;action=edit&amp;redlink=1\">Будинок-музей родини Шухевичів</a>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Іменем Романа Шухевича названі дві вулиці у Львові (<a title=\"Вулиця Чупринки (Львів)\" href=\"http://uk.wikipedia.org/wiki/%D0%92%D1%83%D0%BB%D0%B8%D1%86%D1%8F_%D0%A7%D1%83%D0%BF%D1%80%D0%B8%D0%BD%D0%BA%D0%B8_%28%D0%9B%D1%8C%D0%B2%D1%96%D0%B2%29\">Вулиця Чупринки</a> в <a title=\"Новий Світ (Львів)\" href=\"http://uk.wikipedia.org/wiki/%D0%9D%D0%BE%D0%B2%D0%B8%D0%B9_%D0%A1%D0%B2%D1%96%D1%82_%28%D0%9B%D1%8C%D0%B2%D1%96%D0%B2%29\">Новому світі</a> і <a title=\"Вулиця Романа Шухевича (Львів) (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%92%D1%83%D0%BB%D0%B8%D1%86%D1%8F_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD%D0%B0_%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87%D0%B0_%28%D0%9B%D1%8C%D0%B2%D1%96%D0%B2%29&amp;action=edit&amp;redlink=1\">Вулиця Романа Шухевича</a> на <a title=\"Білогорща (місцевість)\" href=\"http://uk.wikipedia.org/wiki/%D0%91%D1%96%D0%BB%D0%BE%D0%B3%D0%BE%D1%80%D1%89%D0%B0_%28%D0%BC%D1%96%D1%81%D1%86%D0%B5%D0%B2%D1%96%D1%81%D1%82%D1%8C%29\">Білогорщі</a>)<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-48\">[49]</a></sup>, Славському (названа 26 липня 2007 року)<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-49\">[50]</a></sup>, Чернівцях (названа 28 лютого 2008 року)<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-50\">[51]</a></sup>, Луцьку (названа 23 серпня 2009 року)<sup><a href=\"http://uk.wikipedia.org/wiki/%D0%A8%D1%83%D1%85%D0%B5%D0%B2%D0%B8%D1%87_%D0%A0%D0%BE%D0%BC%D0%B0%D0%BD_%D0%99%D0%BE%D1%81%D0%B8%D0%BF%D0%BE%D0%B2%D0%B8%D1%87#cite_note-51\">[52]</a></sup>.</span></span></p>','Панахида пам’яті борцям за волю України','uk',2327,3810,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1532,askoldova.jpg\" alt=\"\" /></span></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">приходьте до нас:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Аскольдова Могила, вул. Паркова Дорога 1, м. Київ, Україна</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><strong>Парафія Церкви Миколая Чудотворця на Аскольдовій Могилі</strong></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наш сучасний Київ не можливо уявити без храмів, що появляють історико-архітектурне обличчя міста. Серед них - церква-ротонда на Аскольдовій Могилі, де за радянської влади було знищено кладовище з похованням багатьох видатних особистостей. Саму каплицю безбожники переробили у парковий павільйон. Церкву на Аскольдовій Могилі було реставровано стараннями Української греко-католицької громади відповідно до первинного проекту Меленського А.І.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це місце - найулюбленіше не лише для киян. Омріяне серпанком легенди, дуже світле й урочисте. І людей тут зустрінеш усміхнених, щирих, гостинних...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійницьке служіння парафіяни храму Св. Миколи розпочали 1992 року, коли заснували доброчинну організацію \"Карітас-Київ\". Її покликання - надавати милосердну, безкорисливу допомогу та служити усім потребуючим людям. Пожертви парафіян - головне джерело коштів для щоденної мозолистої праці Фонду. Але лише матеріальні ресурси були б ніщо без щирих сердець його співробітників та добровільних помічників, сповнених Христовою любов\'ю до ближнього. Молитва додає їм снаги для подвигу милосердя, що освячує серця і спасає душі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>23 червня 2001 року Аскольдова Каплиця стала місцем прочанської молитви святішого отця Івана Павла ІІ перед чудотворним образом Зарваницької Богородиці, коли він усилено просив про особливе покровительство Пречистої Приснодіви Марії над долею українського народу.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2005 року вірні відзначили 15-літній ювілей заснування парафії, яка за так малий час чимало прислужилася Церкві й Україні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Настоятель церкви святого Миколая Мирлікійського Чудотворця на Аскольдовій могилі - митрофорний протопресвітер о. Ігор-Ілля Онишкевич.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p class=\"mainCxSpFirst\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першу парафію св.. Миколая Мирлікійського у Києві, у жовтні 1990 р.Б. заснував нововисвячений священик Ігор Онишкевич. Продовж 2 років новостворена парафія перебувала у пошуках храму. 26 квітня 1992 р.Б. була відправлена перша святочна літургія Воскресіння Христового у стінах зруйнованої церкви на Аскольдовій Могилі. Від тоді парафія носить народну назву &laquo;парафія св.. Миколая Чудотворця на Аскольдовій Могилі. Того ж року було поставлено перший козацький хрест на могилі героям Крут.</span></p>\r\n<p class=\"mainCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У 1995 р.Б. на почитання у храмі було встановлено копію образу Зарваницької Богородиці. Через три роки цей образ врятував церковцю від пожежі. Береже її і до нині. Перед цим образом 2001 р.Б. молився нині блаженний папа Іван Павло ІІ.</span></p>\r\n<p class=\"mainCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перед візитом папи Івана Павла ІІ у церкві було встановлено мармуровий іконостас, та розпочато реставраційні роботи.</span></p>\r\n<p class=\"mainCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2007 р.Б. стараннями парафіян та меценатів на схилах Дніпра було встановлено єдину на теренах Київщини Хресну дорогу (початок від Набережного шосе, поблизу ст.. метро Дніпро, дніпровськими схилами до церкви св.. Миколая)</span></p>\r\n<p class=\"mainCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Остаточно реставрація та розпис храму завершились 2011 р.Б. Купол храму зображає Друге пришестя Ісуса Христа, нижній ярус відображає епізоди із життя св.. Миколая Чудотворця. Частково тривають реставраційні роботи крипти (нижній храм)</span></p>\r\n<p class=\"mainCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">При парафії діють: &laquo;Карітас-Печерський&raquo;, &laquo;Порадня&raquo;, &laquo;Катехитична школа&raquo;, &laquo;Рух подружніх пар&raquo;, &laquo;Молодіжна спільнота&raquo;, &laquo;Матері в молитві&raquo;, молитовна спільнота &laquo;Оранта&raquo;, пенітенціарне&nbsp; служіння.</span></p>\r\n<p class=\"mainCxSpLast\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафія св.. Миколая Мирлікійського на Аскольдовій Могилі є яскравим прикладом &laquo;живої парафії&raquo; у лоні УГКЦ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>','2008-08-23 11:35:12',NULL,'P','','','uk',552,1532,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1839,200,askoldova_mogila_1.jpg\" alt=\"\" />Аскольдова могила - це історична місцевість, розташована на крутих схилах над Дніпром недалеко від Липок і Берестового. Тут розташований парк \"Аскольдова могила\", археологічний культурний шар села Угорського, церква св. Миколая, декілька пам\'ятників та Зелений театр.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За літописами в цій місцевості у 882 відбулося вбивство київських князів Аскольда і Діра воєводою новгородського князя Рюрика Олегом, який після того захопив владу в Києві. Невдовзі Аскольда ніби-то поховали тут же.<br /> Ця легенда має деякі розходження з науковими даними. Наприклад, відомо, що князь Дір був попередником Аскольда і помер задовго до нього.</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','2008-12-25 18:16:42',NULL,'P','<p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" bgcolor=\"#ffffff\">\r\n<tbody>\r\n<tr valign=\"top\">\r\n<td height=\"10\" valign=\"top\"><br /></td>\r\n<td colspan=\"2\" align=\"left\">Київ м., Печерський р-н, Аскольдова Могила іст. місц. , Паркова дорога</td>\r\n<td><br /></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td height=\"10\" valign=\"top\"><br /></td>\r\n<td width=\"10%\">Статус:</td>\r\n<td width=\"80%\" align=\"left\">Пам\'ятка історії національного значення, Пам\'ятка природи місцевого значення, Пам\'ятка археології національного значення</td>\r\n<td width=\"9%\"><br /></td>\r\n</tr>\r\n<tr valign=\"top\">\r\n<td width=\"1%\" height=\"10\" valign=\"top\"><br /></td>\r\n<td width=\"10%\" align=\"left\">Тип:</td>\r\n<td width=\"80%\" align=\"left\">Садово-паркові, Поховальні</td>\r\n<td width=\"9%\"><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<p align=\"justify\">Аскольдова могила - це історична місцевість, розташована на крутих схилах над Дніпром недалеко від Липок і Берестового. Тут розташований парк \"Аскольдова могила\", археологічний культурний шар села Угорського, церква св. Миколая, декілька пам\'ятників та Зелений театр.</p>\r\n<p align=\"justify\">За літописами в цій місцевості у 882 відбулося вбивство київських князів Аскольда і Діра воєводою новгородського князя Рюрика Олегом, який після того захопив владу в Києві. Невдовзі Аскольда ніби-то поховали тут же.<br /> Ця легенда має деякі розходження з науковими даними. Наприклад, відомо, що князь Дір був попередником Аскольда і помер задовго до нього.</p>\r\n<p align=\"justify\">Ще за часів Київської Русі на Аскольдовій могилі була збудована дерев\'яна церква. Вона неодноразово перебудовувалася, знищувалася і відновлювалася. Остання дерев\'яна трибанна Миколаївська церква була збудована у XVII столітті.</p>\r\n<p align=\"justify\">З 1786 цвинтар, що існував навколо церкви, стає міським.</p>\r\n<p align=\"justify\">У 1809-1810 на місці старої дерев\'яної церкви була збудована мурована церква-ротонда св. Миколи (арх. Меленський О.).</p>\r\n<p align=\"justify\">У 1845 цвинтар був закритий для загального користування. Після цього тут здійснювали поховання лише найбільш заможних мешканців Києва.<br /> Однак після 1871 кладовище знову починає активно використовуватися.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1862--timm_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1862--timm_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила. Малюнок Тімма В., 1862.</p>\r\n<p align=\"justify\">У 1866 навколо Миколаївської церкви збудували огорожу з дзвіницею при вході.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/189%21%7E%7E---001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/189%21%7E%7E---001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила: церква св. Миколи, цвинтар і огорожа з дзвіницею. Малюнок к. ХІХ ст.</p>\r\n<p align=\"justify\">У 1892-1894 церква св. Миколи була відремонтована.</p>\r\n<p align=\"justify\">В 1901 була збудована нова дзвіниця.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Аскольдова могила: Миколаївська церква, кладовище і огорожа з новою дзвіницею. Фото п. ХХ ст.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--002_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--002_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--003_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/190%21%7E%7E--003_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Усипальниця баронів Штейнгель (арх. Городецький В.) та могила-пам\'ятник Яценку О. Фото п. ХХ ст.</p>\r\n<p align=\"justify\">У 1919 кладовище було закрите.</p>\r\n<p align=\"justify\">У 1932 металеві частини надгробків і огорожі були вилучені на металобрухт.</p>\r\n<p align=\"justify\">У 1935 кладовище офіційно закрито і ліквідовано разом з переважною більшістю надгробків та склепів (ряд могил перенесено на інші кладовища).<br /> Над церквою св. Миколи у 1936 була надбудована колонада, і вона набула вигляду паркового павільйону.</p>\r\n<p align=\"justify\">Під час окупації Києва німецькими військами на Аскольдовій могилі було влаштовано кладовище німецьких солдат і офіцерів.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1943--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/1943--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Німецьке кладовище на Аскольдовій могилі. Фото 1943.</p>\r\n<p align=\"justify\">Після звільнення Києва, у 1944 це кладовище ліквідували.</p>\r\n<p align=\"justify\">Після 1945 Аскольдова могила стала одним з військових кладовищ для загиблих учасників визволення Києва від фашистських загарбників.<br /> У 1957 прах загиблих радянських солдатів та офіцерів було перенесено до парку Вічної Слави.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/198%21%7E%7E--001_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/198%21%7E%7E--001_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Церква св. Миколи, перебудована в парковий павільйон. Фото до реставрації.</p>\r\n<p align=\"justify\">У 1992 церква була передана УГКЦ.<br /> Під час реставрації у 1997-1998 церкві попернули її первісний вигляд.</p>\r\n<p align=\"justify\">Нижче - фото Аскольдової могили 2005.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--337_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--337_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--362_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--362_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Парк та Церква св. Миколи на Аскольдовій могилі.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--353_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--353_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Каплиця Воздвиження Чесного Хреста.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--359_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--359_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--355_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--355_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Безіменні могили в парку та пеньок колись врослого в огорожу дерева.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--339_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--339_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />В літописі є згадка про угрів, які ніби-то спинялися в цій місцевості табором у 898 р. Аскольдову могилу також називали Угорським урочищем, за однією версією - саме через цей угорський табір. Однак є і інша версія: від давньослов\'янського &laquo;утор&raquo; - крутий землистий берег.<br /> Як би там не було, над Парковою алеєю встановили пам\'ятний знак з угорською символікою. Фото 2005.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--343_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--343_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />Пам\'ятник студентам, які загибли під Крутами в бою з більшовиками у 1918.</p>\r\n<p>&nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--327_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--327_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--330_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--330_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> &nbsp; <a href=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--332_%7E183.jpg\"> <img src=\"http://www.oko.kiev.ua/img/Kyiv/Pecherskyy/2005.08.11--332_%7E183--th.jpg\" border=\"0\" alt=\"\" /></a> <br />На іншому боці Паркової дороги у 2000 на честь 2000-річчя Різдва Христового був споруджений храм св. Андрія Первозванного, встановлений пам\'ятний знак та пам\'ятник святому. Фото 2005.</p>\r\n<p>&nbsp;</p>','Аскольдова могила (комплекс) (IX - XX ст.)','uk',865,1839,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2026,200,s7.jpg\" alt=\"\" />Котрий рік поспіль у церкві св. Миколая на Аскольдовій Могилі ведеться розпис храму та реставрація крипти. Художники у свою роботу вкладають усю душу. Згодом наш храм засяє своєю величчю часів княгині Ольги. А поки пропоную вашій увазі короткі фото замальовки. Так виглядає творчий робочий процес.</span></span></p>\r\n<p>&nbsp;</p>','2008-09-26 18:59:32',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любов Михайлюк</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2026,200,s2.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2026,200,s1.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2026,200,s4.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2026,200,s3.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2026,200,s5.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2026,200,s6.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,2026,200,s9.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2026,200,s8.jpg\" alt=\"\" /><br /></span></span></p>','Реставрація храму','uk',1052,2026,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2170,200,121.jpg\" alt=\"\" />10 березня минає 148 років з дня смерті українського пророка, поета, літератора, мистецтвознавця, культуролога Тараса Григоровича Шевченка.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У понеділок 9 березня, о 19:00 у церкві св. Миколая Чудотворця на Аскольдовій Могилі буде відправлена поминальна панахида і пройде 45-та &laquo;Тарасова ніч&raquo;, започаткована українським художником, скульптором Володимиром Кальненком ще у Санкт-Петербурзі (тодішній Ленінград) у 1964 році. На Аскольдовій Могилі &laquo;Тарасова ніч&raquo; проходитиме сьомий рік поспіль. Запрошуємо усіх вшанувати пам\'ять великого українця, невтомного борця за українську націю.</span></span></p>','2009-03-09 12:43:43',NULL,'P','','Пам‘яті Тараса Григоровича Шевченка','uk',1196,2170,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2327,200,16.jpg\" alt=\"\" />Як годиться кожному українцю до Великодня слід готуватися заздалегідь. Так і наші дітки з Аскольдової Могили готувались до цього величного свята, вони власноруч розписували писанки. Свої писанкові рукотвори діти передадуть у дитячий будинок. Відроджуючи власні традиції ми збагачуємось духовно, а головно виховуємо здорове українське покоління.</span></span></p>','2009-04-13 02:57:17',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Любові Михайлюк</span></span></p>\r\n<p style=\"text-align: justify;\"><br /><img style=\"float: left;\" src=\"image,2327,200,13.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2327,200,14.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,2327,200,15.jpg\" alt=\"\" width=\"200\" height=\"150\" /><img style=\"float: right;\" src=\"image,2327,200,17.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,2327,200,21.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2327,200,19.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,2327,200,16.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,2327,200,20.jpg\" alt=\"\" /></p>','Писанка - український символ','uk',1353,2327,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3277,200,DSCN2079.JPG\" alt=\"\" />30 серпня&nbsp; - святковий день на Аскольдовій Могилі. Сьогодні зібралося тут багато парафіян громади святого Миколая Чудотворця, адже скоро дітям потрібно іти до школи, і вони хочуть отримати благословення від Бога, священиків на добру науку.</span></span></p>','2009-08-31 12:05:24',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото Олега Качура</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3277,200,DSCN2050.JPG\" alt=\"\" width=\"200\" height=\"150\" /><img style=\"float: right;\" src=\"image,3277,200,DSCN2059.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3277,200,DSCN2114.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3277,200,DSCN2085.JPG\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3277,200,DSCN2078.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,3277,200,DSCN2098.JPG\" alt=\"\" /><br /></span></span></p>','Наш найбільший скарб - діти','uk',1794,3277,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3384,200,123.jpg\" alt=\"\" />19 грудня 2009 р. о 17-й год в актовій залі Національного педагогічного університету ім. М.Драгоманова (вул. Пирогова, 9) відбудеться IV фестиваль-конкурс &laquo;Аскольдів глас&raquo;. Гасло фестивалю - <em>&rdquo;Співайте Господеві, благословіть Його ім&rsquo;я&rdquo;</em>.<strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&rdquo;Аскольдів глас&rdquo; </strong>&ndash; фестиваль-конкурс духовної пісні, започаткований у 2007 р. за ініціативи отця-мітрата Ігоря Онишкевича та громади УГКЦ св. Миколая Чудотворця на Аскольдовій могилі. Проводиться двічі на рік &ndash; на теплого (травень) та зимового (19 грудня) Миколая.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','2009-12-19 13:50:09',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,3384,200,123.jpg\" alt=\"\" />19 грудня 2009 р. о 17-й год в актовій залі Національного педагогічного університету ім. М.Драгоманова (вул. Пирогова, 9) відбудеться IV фестиваль-конкурс &laquo;Аскольдів глас&raquo;. Гасло фестивалю - <em>&rdquo;Співайте Господеві, благословіть Його ім&rsquo;я&rdquo;</em>.<strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&rdquo;Аскольдів глас&rdquo; </strong>&ndash; фестиваль-конкурс духовної пісні, започаткований у 2007 р. за ініціативи отця-мітрата Ігоря Онишкевича та громади УГКЦ св. Миколая Чудотворця на Аскольдовій могилі. Проводиться двічі на рік &ndash; на теплого (травень) та зимового (19 грудня) Миколая.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цей мистецький захід покликаний ознайомити широкий загал із історичними подіями минувшини, постаттю київського князя Аскольда (в хрещенні названого Миколаєм), популяризувати кращі зразки духовної та народної музики, залучити до участі аматорські та професійні навчальні і церковні хорові колективи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\" lang=\"UK\">На ІV фестивалі-конкурсі &rdquo;Аскольдів глас&rdquo; журі визначатиме трьох переможців, у виступі яких твори про київського князя Аскольда будуть визнано найкращими. Премія переможців конкурсу становить:<br /> І премія - 7 тис. грн.<br /> ІІ премія &ndash; 5 тис. грн.<br /> ІІІ премія &ndash; 3 тис. грн.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','IV фестиваль-конкурс «Аскольдів глас»','uk',1901,3384,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,3502,200,CRW_6836.jpg\" alt=\"\" />Понеділок після Вербної  неділі традиційно стає понеділком оздоровлення Духу та Тіла. У церкві св. Миколая Чудотворця на Аскольдовій Могилі відбувся Чин  Соборування. Боже благословення зійшло на кожного від маляти до людей похилого віку. Постом, молитвою, сповіддю, святим причастя та  єлеєпомазанням &nbsp;ми очищаємо свої душі перед Христовим Воскресінням. Божої ласки кожному з нас на прославу Христа Спасителя, який приніс себе у жертву на спасіння наших душ.<br /></span></span></p>','2010-03-30 12:50:42',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото Любові Михайлюк</span></span></p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3502,200,CRW_6815.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3502,200,CRW_6819.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"image,3502,200,CRW_6821.jpg\" alt=\"\" /><img style=\"float: right;\" src=\"image,3502,200,CRW_6820.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><img styl