INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, <strong>15 липня 2010 року Божого о 09:00</strong> <strong>служитиметься Архієрейська Божественна Літургія</strong> у церкві св.. Миколая Чудотворця на Аскольдовій Могилі. <strong>Після Літургії посвячуватиметься відновлена крипта Аскольда, царя і мученика за віру христову.</strong></span></span></p>','2010-07-15 16:48:38',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, <strong>15 липня 2010 року Божого о 09:00 служитиметься Архієрейська Божественна Літургія</strong> у церкві св.. Миколая Чудотворця на Аскольдовій Могилі. <strong>Після Літургії посвячуватиметься відновлена крипта Аскольда, царя і мученика за віру христову.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Достеменно відомо &ndash; 18 липня 860 року Божого флотилія русинів з князем Аскольдом потерпіла велику поразку. У Києві стояв плач за загиблими воїнами. Саме про цю подію нам нагадує свято положення Чесної Ризи Пресвятої Владичиці і нашої Богородиці Влахерні. Ця поразка для Аскольда стала поштовхом до прийняття християнства і поєднанням з вселенською Христовою церквою. Завдячуючи просвітителям Кирилу та Мефодію Аскольд хрестився іменем Миколая і був коронований папою на царство.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1150 років назад закладався камінь христової віри опертий на апостола Петра і його наступників. 860 рік одноголосно прийнятий істориками як початок творення державності Руси-України, початок дипломатичної діяльності нашої держави, адже тобі був заключний догорів миру та любові з Візантійською імперією.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Українське християнство крізь віки починається з 860 року і довершується 866 роком коли Аскольд прийняв хрещення і став християнином. Тому посвяченням оновленої крипти Аскольда, наша громада церкви св.. Миколая Чудотворця на Аскольдовій Могилі розпочинає святкування хрещення Аскольда.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Запрошую Вас на святкування освячення крипти Аскольда. </strong>Заступництвом Аскольда-Миколая, царя і мученика за віру христову хай примножиться для Вас благо, спасіння, здоров&lsquo;я у благодаті Божій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З любов\'ю у Христі, ієрей Ігор Онишкевич.</span></span></p>','Посвячення відновленої крипти Аскольда','uk',2099,3582,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуючи пожертвам парафіян, придбані медикаменти для жінок, що відбувають покарання в місцях позбавлення волі, потреба якої підтверджена офіційно рецептами медсанчастини виправної установи, а також матеріальна допомога ув&rsquo;язненим жінкам, які долучаються до благодійної діяльності своєю творчою роботою.</span></span></p>','2012-05-01 00:39:58',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуючи пожертвам парафіян, придбані медикаменти для жінок, що відбувають покарання в місцях позбавлення волі, потреба якої підтверджена офіційно рецептами медсанчастини виправної установи, а також матеріальна допомога ув&rsquo;язненим жінкам, які долучаються до благодійної діяльності своєю творчою роботою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Матеріальна допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. Крім того, кожен знайшов у пакуночку духовну літературу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів спаковано і відправлено адресатам 12 посилок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая о.Ігорем Онишкевичем.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомогу надано:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Раїсі О. (Чорноморська ВК, м.Одеса) на суму 31 грн. 14коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Термін &mdash; 11 років, в ув\'язненні від початку&nbsp; 2005 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Син Олександр (15.10.1997 р. н.), залишився під опікою батьків Раїси О. Батьки дуже хворі: у батька грижа, необхідна операція, але нема коштів; мама майже лежача. В серпні 2010 р. сина взяла під свою опіку свекруха Раїси.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;<strong>Большое Вам спасибо за все я получила 10.1.2012 г. Я очень благодарна Вам что Господь через Вас благословил меня&hellip; и за медикаменты</strong>. <strong>Я Вам выслала вышивку</strong>. <strong>Если Вы сможете мне выслать ткань и продается журнал для вышивки чтобы дальше вышивать иконы</strong>... <strong>если сможите выслать карточку чтобы звонить по таксофоне</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 07.05.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїса О. постійно присилає свої роботи: вишивані хрестиком та гладдю квіткові композиції, ікони.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Раїсі О.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">20.07.2009 р. на суму 40 грн.06 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22.09.2009 р. на суму 65 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26.10.2010 р. на суму 42 грн. 03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.02.2011 р. на суму 55 грн. 94 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.03.2011 р. на суму 75 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21.06.2011 р. на суму 172 грн.01 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26.09.2011 р. на суму 54 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">06.01.2012 р. &ndash; &laquo;Різдвяний подарунок&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру в с.Будків Вінницької обл.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">03.12.2009 р. на суму 70 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08.12.2010 р. на суму 73 грн.03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29.08.2011 р. на суму 138 грн. 57 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.12.2011р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 8</strong><strong>4</strong><strong>1</strong><strong> г</strong><strong>рн. </strong><strong>78</strong><strong> коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Марині Л. (Чернігівська ВК), в т.ч.<span style=\"text-decoration: underline;\"> ліки </span>на суму 43 грн. 51 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; <strong>мне 26 лет</strong>. Дорогой о.Игорь пожалуста если сможите то <strong>помогите мне лекарствами&hellip; я вич-инфицырованая</strong> и у меня мало клеток&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 15.03.2012 р.<strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash;43 грн.51 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Юлії Т. &nbsp;(Чернігівська ВК), в т.ч. <span style=\"text-decoration: underline;\">ліки</span> на суму 89 грн. 69 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; мне 28 лет&hellip;срок 3 года. <strong>Я заболела кожным заболеванием мокрый герпес,</strong> и все бы ничего, но я <strong>ВИЧ-инфецированная</strong>, и эта <strong>болезнь начала прогрессировать очень быстро и по всему телу, все сочится чешется и печет, бывает посещают мысли, что хочется умереть</strong>, но я верую в Господа Бога и Он помогает мне держаться.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родственников у меня нет и помочь некому, недайте умереть, я Вас очень прошу. <strong>Я влаживаю рецепт, по возможности вышлите мне пожайлуста эти лекарства</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 20.03.2012 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 89 грн.69 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Аллі А. (Чернігівська ВК), &nbsp;в т.ч. <span style=\"text-decoration: underline;\">ліки</span> на суму 34 грн. 11 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 34 грн.11 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ларисі Б. (Чернігівська ВК), в т.ч. <span style=\"text-decoration: underline;\">ліки</span> на суму 69 грн. 15 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; За свои содеяные грехи я нахожусь в МЛС. <strong>Болею, у меня астма</strong>. Слава Иисусу, о.Виталий который посещает нас, помог мне с ингалятором, но сейчас началась весна и для лечения <strong>я нуждаюсь в препаратах, которые выписаны в рецепте</strong>. Они смогут облегчить мне дыхание. <strong>Помогите мне пожалуйста</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 13.03.2012 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 34 грн.11 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Любові С. (Збаразька ВК, Тернопільська обл.) на суму 25 грн.90 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;<strong> спасибо за Библию</strong>. Я когда увидела ее при получении у меня сердце чуть-чуть не выскочило с груди, нет слов для объяснения моего состояния души на тот момент вручения этой дорогой для меня книги&hellip; Недавно приезжали к нам с монастыря Почаевского женщины (монашки) тоже привезли много книг, гуманитарную помощь с одежды. Они рассказывали о Божьей Матери Пресвятой Марии Богородице, очень красиво пели&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист - 05.08.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Любові С.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23.02.2012 р. на суму 50 грн. &ndash; Біблія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.04.2012 р. на суму 25 грн.90 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 75 грн.90 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій &nbsp;Ірині В. &nbsp;(Качанівська ВК, м.Харків) &nbsp;на суму 25 грн. 90 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; &nbsp;<strong>Моя мрія</strong> зробити одну дуже важливу справу тут в колонії&hellip; я хочу <strong>вишити гарний широкий узір хрестиком на великих рушниках до вівтаря </strong>і хочу щоб у цій церкві було 2 комплекти вишивки. Один чорними і червоними то на кожен день, а на Паску синіми і голубими і написати вишивкою &laquo;Христос воскрес&raquo;&hellip; я записую молитви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&hellip;Також проситиму у Вас взуття 39 р., чорні прості колготи і пральний порошок&hellip; футболок і шорти і бриджі для роботи літом на вулиці&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 28.09.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Ірині В.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">06.01.2012 р. &mdash; &laquo;Різдвяний подарунок&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дитині Ірини В. в Кіровоградську обл.:&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.12.2011 р. &mdash; &laquo;Подарунок від св.Миколая&raquo;<strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 25 грн.90 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Жанні М. (Чернігівська ВК) на суму 31 грн.14 коп.+ нитки для в&rsquo;язання </strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;&hellip; Я Вас очень прошу выслать мне для вязания ниток </strong>и что именно мне вязать...&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; серпень 2011р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Передала набір майстерно зв&rsquo;язаних шкарпеток.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Жанні М.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">03.12.2011 р. на суму 39 грн.92 коп. (в т.ч. спиці і нитки для в&rsquo;язання)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.02.2012 &nbsp;на суму 35 грн. &ndash; окуляри</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &ndash; 106 грн.06 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Зарині Д. (Чернігівська ВК) на суму 31 грн.14 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; мне 26 лет. Я инвалид ІІ групы. Я очень люблю рисовать&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&hellip;я Вас очень прошу, <strong>вышлите мне краски &laquo;Акрил&raquo;. Если сможете, по возможности кипятильник и мыльные принадлежности. Помогите продуктами</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прислала вірші власного авторства, малюнки на релігійну тематику.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 30.01.2012 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Зарині Д.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23.02.2012 р. на суму 16 грн.92 коп., в т.ч. фарби акварельні &ndash; 10 грн.75 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &ndash; 48 грн. 06 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Тетяні С. (Качанівська ВК, м.Харків) на суму 38 грн. 90 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; <strong>я отримала нитки і вже повязала дівчатам рукавички та теплі носки </strong>і вони дуже мені вдячні навіть ті які звільнились з собою забрали. Якщо в Вас будуть нитки то висилайте я і літом буду в\'язати і давати на церкву хто потребує. &hellip; Я дуже вдячна&nbsp; Вам &nbsp;за все, що Ви &nbsp;для мене зробили, і скільки &nbsp;Господь дав мені цього життя хочу прожити служінням Йому&hellip; <strong>Я б дуже хотіла мати</strong> <strong>молитвенник</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок листування &mdash; вересень 2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Тетяні С.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.11.2010 р. на суму 65 грн.21 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.02.2011 р. на суму 89 грн. 44 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">01.11.2011 р. на суму 70 грн.20 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">06.01.2012 р. &ndash; &laquo;Різдвяний подарунок&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 263 грн.75 коп</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>11.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій &nbsp;&nbsp;Надії А. &nbsp;&nbsp;(Уманська ВК,&nbsp; Черкаська обл.) &nbsp;&nbsp;на&nbsp; суму&nbsp; 25 грн. 90 коп.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; Вдячна Вам за адресу отця Віталія. Я звільняюся 20 вересня і обов&rsquo;язково звернуся до нього. Дуже приємно коли комусь я потрібна, а також потрібна моя допомога&hellip; <strong>Тяжко просто тут, і коли є люди які допомагають і пишуть листи, то є надія на майбутнє.</strong> Я в колонії п&rsquo;ятий рік&hellip;&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 04.05.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Надії А.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.02.2011 р. на суму 90 грн.94 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">05.04.2011 р. на суму 60 грн.21 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &ndash; 1</strong><strong>77</strong><strong> грн.</strong><strong>0</strong><strong>5 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>12.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Звільненій з ув\'язнення Наталії Х. (Донецька обл.) на суму 13 грн. (молитовник і 3 вервиці)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;пишет Вам мама Данила Х., которого Вы радовали своими подарками и не забывали, когда я была в МЛС. Я до сих пор Вам благодарна за то, что Вы дарите детям свою любовь и поддержку. Спасибо Вам и низкий поклон!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Желаю Вам здоровья, любви и просто огромного человеческого счастья, которого Вы заслуживаете. Пусть этот год принесет Вам столько добра, сколько вместит Ваш дом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У нас все хорошо. Я работаю, Данилка скоро закончит 5-й класс. Кстати, он сидит рядом и передает Вам свои поздравления. В общем все неплохо, если не считать того, что я неизлечимо больна. Если будет на то воля Божья, я проживу еще несколько лет, мне главное Данилку поднять на ноги, ведь ему только 11 лет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я хотела бы Вас попросить помолиться за меня, за Данилку и за мою маму (ее зовут Людмила) и если будет у Вас такая возможность выслать мне 3 священных крестика&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума матеріальної допомоги 10.04.2012 р. </strong>&mdash; <strong>459 грн.48 коп.,</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>в т.ч. медикаментів на суму 236 грн.46 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>26 грн.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; <strong>152 грн.12 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від&nbsp; 10.04.2012 р. &mdash; 637 грн. 60 коп. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна вага поштових відправлень від 10.04.2012 р. &ndash; 28,4  кг.</strong></span></span></p>','ЗВІТ №6 про надання допомоги (в т.ч.  медикаментозної) жінкам, які відбувають покарання в місцях позбавлення волі,  (10.04.2012 р.)','uk',2732,4215,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екскурсія королівсьим містом та відвідування католицьких костелів Кракова.   Прогулянку ми ми почнемо з Вавельского пагорбу , резиденції королів Польщі.   Дзвін Сигізмунда Кафедральний собор, в якому відбувалась коронація польських   королів і де знаходиться королівська усипальниця. Далі Костел Св. Петра и   Павла, Костел cв. Анджея, Костел Францишканів, Домініканський Собор та   українська греко-католицька церква Воздвиження Чесного Хреста, в якій брав   шлюб Степан Бандера. Закінчиться наша прогулянка на центральній площі   Кракова - площі Ринок, де ми відвідаємо Маріацький Костел.</span></span></p>','2009-06-11 16:13:47',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">*Краків - Ченстохова - Відень - Венеція - Рим (Ватикан) - Будапешт - Егер*   <br /><br />*(7-15 липня 2009 р. Б.)   <br />*   <br /><br />*1 день**, вівторок   <br />*   <br /><br />8:00 Спільна молитва в соборі св. Юра за щасливу поїздку. Виїзд зі Львова.   <br />*Прибуття в Краків*. Поселення в готель.   <br />Вільний час. Для бажаючих, відвідування Аквапарку - одного з найбільших   <br />критих водяних парків Європи. 8 водних гірок-американок (найдовша гірка   <br />довжиною 201 м.), фонтани, гідромасажі, джакузі, водні атракції. Загальна   <br />площа басейнів 1586 кв. м. Вся вода озонована з температурою 28-32 С. Чудове   <br />місце для відпочинку всією родиною.   <br /><br />*2 день, **середа***   <br /><br />Екскурсія королівсьим містом та відвідування католицьких костелів Кракова.   <br />Прогулянку ми ми почнемо з Вавельского пагорбу , резиденції королів Польщі.   <br />Дзвін Сигізмунда Кафедральний собор, в якому відбувалась коронація польських   <br />королів і де знаходиться королівська усипальниця. Далі Костел Св. Петра и   <br />Павла, Костел cв. Анджея, Костел Францишканів, Домініканський Собор та   <br />українська греко-католицька церква Воздвиження Чесного Хреста, в якій брав   <br />шлюб Степан Бандера. Закінчиться наша прогулянка на центральній площі   <br />Кракова - площі Ринок, де ми відвідаємо Маріацький Костел.   <br />Виїзд з Кракова. *Прибуття в Ченстохова* - місто, в якому знаходиться   <br />чудотворна Ікона Матері Божої Ченстоховської . Ще її називають \"Чорна   <br />Мадонна\", або \"Цариця Польщі\". Це місце займає третє місце по популярності в   <br />європейських паломників після Люрду та Фатіми. Тут ми зможемо помолитися,   <br />вклонити голову до нашої заступниці...   <br />Вільний час. Поселення в готель.   <br /><br />*3 день, **четвер***   <br /><br />Сніданок. 9:00 Виїзд з готелю. Переїзд до Австрії. *Прибуття в Відень* -   <br />місто австрійських імператорів та музикантів, місто музики, вальсів,   <br />неповторних парків, чудового вина.... Вас запрошує Відень. Пішохідна оглядова   <br />екскурсія з можливістю побачити історичні пам\'ятки. Парламент, Ратушу,   <br />символ міста - Собор св. Стефана, в якому знаходиться знаменита Ікона Марії   <br />Повчанської, перенесена сюди австро-угорським імператором з закарпатського   <br />містечка Повч. Ікона прославилась сотнями зцілень невиліковно хворих людей,   <br />які провідували церкву, де вона знаходилась і ревно молились. Також ми   <br />відвідаєм Віденський Дім, знамениту Віденську Оперу, імператорську   <br />резиденцію Хофбург, прогулятись по &lt;&lt;обручальному кільцю Відня&gt;&gt; під   <br />столітніми деревами Рінга...   <br />Після екскурсії, радимо відпочити в одному з багатьох затишних кафе та   <br />ресторанчиків Відня, відчути неповторний смак віденської кави і повітряних   <br />тістечок, насолодитись атмосферою Відня в мерехтінні вечірніх вогнів. По   <br />можливості, відвідування української церкви св. Варвари. Розміщення в   <br />готелі.   <br /><br />*4 день, **п\'ятниця   <br /><br />Сніданок. 9:00 Виїзд з готелю. *Прибуття в Венецію. *Самостійна прогулянка   <br />містом. Рекомендуємо оглянути центральну частину міста: палац Дожей, Собор   <br />св. Марка, який ще називають &lt;&lt;золотим&gt;&gt;, Міст ювелірів... Хто має романтичну   <br />натуру, може здійснити мандрівку на гондолі старовинними водними вуличками   <br />Венеції, чи відправитись на швидкісному катері по каналу Джудека до   <br />острівної частини міста. А сувенірні крамниці &lt;&lt;міста на воді&gt;&gt; нікого не   <br />залишать байдужим.   <br /><br />Виїзд з Венеції. Нічний переїзд до Риму.   <br /><br />*5 день, **субота***   <br /><br />*Прибуття в Рим*. Відвідування церкви св. Альфонса, в якій зберігається   <br />чудотворна Ікона Матері Божої Неустанної Помочі.   <br /><br />Вільний час для молитви.   <br /><br />Розміщення в готелі.   <br /><br />Автобусно-пішохідна екскурсія Римом. Знайомство з &lt;&lt;Вічним містом&gt;&gt; ми   <br />розпочнемо з його античної частини, грандіозних пам\'яток розквіту Римської   <br />імперії: амфітеатру Колізею, Римського Форуму, Імператорських форумів,   <br />Капітолію... Відвідаємо символ Риму - фонтан Треві, прогуляємось площами   <br />Ротонда та Навона. Завершуємо екскурсію ми біля собору св. Петра в Ватикані.   <br />Вільний час.   <br /><br />*6 день, **неділя**   <br />*   <br /><br />Сніданок.   <br /><br />*Відвідування Базиліки св. Петра - головного католицького храму світу.   <br />Там, у підземеллях Ватикану, усі бажаючі можуть відвідати поховання Апостола   <br />Петра, піднятися на купол Базиліки чи бути присутнім на недільній літургії.   <br />Окрім того, всі паломники можуть отримати благословіння від Папи Римського   <br />Бенедикта XVI о 12:00 на площі св. Петра.*   <br /><br />Вільний час.   <br /><br />Виїзд з Риму. Нічний переїзд до Будапешту.   <br /><br />*7 день, **понеділок**   <br />*   <br /><br />*Прибуття в Будапешт*. Розміщення в готелі.   <br /><br />Пропонуємо відвідати термальні купальні &lt;&lt;Сечені&gt;&gt; (15 ароматизованих саун,   <br />відкритих та закритих басейнів (12 Євро) та вечірню прогулянку на кораблі по   <br />Дунаю з келихом шампанського (15 Євро). Вечірній Будапешт, підкорить вас   <br />своєю красою. Нічліг.   <br /><br />*8 день, **вівторок***   <br /><br />9:00.* *Виїзд з готелю. Сніданок. Оглядова екскурсія містом. Ми побачимо   <br />легендарні пам\'ятники історії Буди і Пешту - Площа Героїв і славетний   <br />пам\'ятник Тисячоліттю, архітектурний ансамбль Хуняді, Парламент, набережна   <br />Дунаю, церква Матяша (місце вінчання і усипальниця багатьох угорських   <br />королів), Рибальський бастіон, Королівський палац.   <br /><br />*Переїзд в м. Егер* - місто угорського бароко, термальних джерел і, звичайно   <br />ж, вина! Запрошуємо вас до винного погребу на дегустацію вин і знаменитого   <br />угорського гуляшу (10 Євро).   <br /><br />Відвідування гіпермаркету &lt;&lt;TESKO&gt;&gt;. Виїзд на кордон.   <br /><br />*9 день, **середа**   <br />*   <br /><br />Прибуття у Львів.   <br /><br />*Поїздка відбувається у супроводі священника УГКЦ*   <br /><br /><br /><br />*Вартість **345** Євро + 100 грн.*   <br /><br />*Для священнослужителів, які формують групи прочан *   <br /><br />*кількістю від 15 осіб, поїздка безкоштовна.*   <br /><br />*У вартість входить:*   <br /><br />Проїзд автобусом єврокласу;   <br /><br />Проживання в готелях (5 ночей) зі сніданками;   <br /><br />Екскурсійне обслуговування   <br /><br />(Краків, Відень, Рим, Будапешт, Егер)   <br /><br />*У вартість не входить:*   <br /><br />рекомендовані програми; вхідні квитки;   <br /><br />проїзд в громадському транспорті;   <br /><br />особисті витрати (обіди, вечері, сувеніри);   <br /><br />*Консульский **збір** - 35 EURO* (крім громадян, що звільнені від оплати -   <br />пенсіонери, студенти до 21 року (стаціонар), діти до 18 років)   <br /><br />*Необхідні документи:*   <br /><br />Закордонний паспорт;   <br /><br />Дві фотографії 3,5 х 4,5;   <br /><br />Копія внутрішнього паспорта;   <br /><br />Копія ідентифікаційного коду;   <br /><br />Довідка з місця роботи про розмір зарплати за останні 6 міс.   <br /><br />Для пенсіонерів, довідка з пенсійного фонду про розмір пенсії за 6 міс.   <br /><br />Для студентів, довідка з деканату.   <br /><br />*Детальніша інформація за тел.: *   <br /><br />*(032) 224-81-12, (032) 275-06-31,*   <br /><br />*(063) 783-20-96*   <br />*Андрій.*</span></span></p>','Програма прощі до Головного католицького храму світу - Собору св. Петра в Ватикані','uk',1561,3044,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 10 по 17 травня у храмі св. Миколая Чудотворця на Аскольдовій Могилі перебуватиме <strong>Ікона Вишгородської Богородиці</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На цей тиждень &nbsp;заплановано такий порядок богослужінь і молитов біля Ікони.</span></span></p>','2009-05-17 06:01:36',NULL,'N','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 10 по 17 травня у храмі св. Миколая Чудотворця на Аскольдовій Могилі перебуватиме <strong>Ікона Вишгородської Богородиці</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На цей тиждень &nbsp;заплановано такий порядок богослужінь і молитов біля Ікони.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Неділя 10 травня </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10:00 Зустріч Ікони Вишгородської Богородиці. Молебень до Пресвятої Богородиці на всяке прощення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Понеділок 11 травня </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:00 Вервиця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:30 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:30 Молебень до Пресвятої Богородиці за покликання</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Молебень до Пресвятої Богородиці за тих, хто перебуває на чужині</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вівторок 12 травня</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ікона перебуватиме у лікарні, у каплиці Покрови, якою опікується отець Олексій.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:30 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:30 Акафіст до Всецариці за тяжкохворих</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Молебень до Пресвятої Богородиці за тяжкохворих</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Середа 13 травня</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:00 Вервиця. Повернення Ікони Вишгородської Богородиці у храм св. Миколая Чудотворця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:30 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:30 Молебень до Пресвятої Богородиці за дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Акафіст до Зарваницької Богородиці, матері в молитві за дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Четвер 14 травня</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:00 Вервиця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:30 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:30 Молебень до Пресвятої Богородиці за узалежнених. Освячення води. Молитва за оздоровлення</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Молебень до Пресвятої Богородиці за успіхи у навчанні та поступлення дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>П\'ятниця 15 травня</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:00 Вервиця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:30 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:30 Акафіст до Самбірської Богородиці за хворих</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Молебень до Пресвятої Богородиці за жертводавців парафії</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Субота 16 травня</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8:00 Утреня</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9:00 Божественна літургія за померлих. Панахида. Вервиця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17:30 Вечірня</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19:00 Молебень до Пресвятої Богородиці за оздоровлення недужих. Прощання з Іконою Вишгородської Богородиці</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Телефон для довідок:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">+38 (050) 4473157 о. Ігор</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">+38 (050) 4473155 о. Олексій</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">+38 (050) 2847731 о. Анатолій</span></span></p>','Розклад Богослужі нь з 10 по 17 травня','uk',1453,2509,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З благословення отця Ігоря Онишкевича,&nbsp; вивчаємо думку та&nbsp;побажання&nbsp;парафіян УГКЦ святого Миколая Чудотворця щодо проведення християнського просвітницько-оздоровчого табору.&nbsp;Попередньо можемо&nbsp;лише повідомити, що наразі&nbsp;обговорюються&nbsp;варіанти десятиденної поїздки в Карпати&nbsp;(кінець&nbsp;червня - початок липня).</span></span></p>','2012-05-15 00:21:03',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З благословення отця Ігоря Онишкевича,&nbsp; вивчаємо думку та&nbsp;побажання&nbsp;парафіян УГКЦ святого Миколая Чудотворця щодо проведення християнського просвітницько-оздоровчого табору.&nbsp;Попередньо можемо&nbsp;лише повідомити, що наразі&nbsp;обговорюються&nbsp;варіанти десятиденної поїздки в Карпати&nbsp;(кінець&nbsp;червня - початок липня).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">УВАГА! Батьки теж можуть поїхати відпочивати, дихати свіжим повітрям, збирати ягоди та гриби, відвідувати катехитичні заняття та богослужіння, тощо. Заповнені анкети просимо надсилати на e-mail: <a href=\"mailto:mirpress@ukr.net\">mirpress@ukr.net</a></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Опитування парафіяльних дітей, які бажають провести літній відпочинок у християнському просвітницько-оздоровчому таборі </span></strong></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">УГКЦ св. Миколая Чудотворця на Аскольдовій могилі</span></strong></span></span></p>\r\n<table style=\"width: 462px; height: 208px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"103\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Прізвище</strong></span></span></p>\r\n</td>\r\n<td width=\"372\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"103\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;Ім</strong><strong>&rsquo;</strong><strong>я</strong></span></span></p>\r\n</td>\r\n<td width=\"372\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"103\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дата нар.</strong></span></span></p>\r\n</td>\r\n<td width=\"372\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"103\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Контакти батьків </strong></span></span></p>\r\n</td>\r\n<td width=\"372\" valign=\"top\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ПІБ__________________________________________________</strong></span></span></p>\r\n<p><span style=