INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Католицький Університет запрошує Вас на відкриту   лекцію пана *Мирослава Мариновича* під   назвою <strong>*\"Публічна сфера та релігійні цінності\"*</strong>, що відбудеться <strong>21 травня о   19.30</strong> у приміщенні церкви Св. Василія Великого (<strong>Вознесенський узвіз, 7</strong>).</span></span></p>','2009-05-21 23:16:20',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Католицький Університет запрошує Вас на відкриту   лекцію пана *Мирослава Мариновича* (засновника та президента Інституту   релігії та суспільства, відомого громадського діяча, колишнього дисидента,   правозахисника, публіциста, автора численних статей та монографій) під   назвою <strong>*\"Публічна сфера та релігійні цінності\"*</strong>, що відбудеться <strong>21 травня о   19.30</strong> у приміщенні церкви Св. Василія Великого (<strong>Вознесенський узвіз, 7</strong>).</span></span></p>','Відкрита лекція Мирослава Мариновича у Києві','uk',1491,2547,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кожної останньої суботи засідання Церковної ради</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щонеділі&nbsp;&ndash; збір молоді о 14.00 - Наука за кавою в Церковній хатині</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">П\'ятниця о 18:00, Субота о 12:00, Неділя о 12.00</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Недільна школа для потребуючих дітей з гарячими обідами, в цей час мають можливість пообідати потребуючі парафіяни в благодійній парафіяльній їдальні Карітас-Печерськ.</span></span></p>','2012-05-19 13:20:30',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Маївки в місяці травні. Після кожної Служби Божої відправляється Молебень до Богородиці з проповіддю і молитвою&nbsp;Під Твою милість прибігаємо</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Понеділок</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.00&nbsp; Божественна літургія. Молитва заступництва св. Шарбеля і Благословення мощами, оливопомазання</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19.00 Акафіст Теодору Ромжі з благословенням мощами</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Вівторок</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.00&nbsp; Божественна літургія. Акафіст Богородиці Всецариці за онкохворих, молитви оздоровлення, оливо помазання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія, Молобен до Блаженного Омеляна Ковча. Катехизація для парафіян</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Середа</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.00&nbsp;Божественна літургія поминальна. Молебень до Богородиці</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія поминальна, Панахида</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19.00 Акафіст до Богородиці Зарваницької, молитва матерів за дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Четвер</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.00 Божественна літургія. &nbsp;Молитви за оздоровлення недужих, подорожуючих, поміч Святого Духа, оливопомазання</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9.15 Акафіст до святого Миколая Чудотворця з чином &nbsp;великого освячення води</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія. Молебень до Блаженного Миколая Чернецького перед його іконою</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">П\'ятниця</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.00&nbsp;Божественна літургія поминальна. Молебень до Христа Чоловіколюбця</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія поминальна.&nbsp;Біблійні читання</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Субота</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.15&nbsp;Утреня</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9.00&nbsp;Божественна літургія за спочилих. Парастас</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.00&nbsp;Божественна літургія. Вечірня. Збір молоді</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Неділя</span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.15&nbsp;Божественна літургія зі співом вірних</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.00&nbsp;Божественна літургія з хором</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.30&nbsp;Божественна літургія для дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кожної першої п\'ятниці місяця - молитовна зустріч Апостольства молитви</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8.30; 18.00 Божественна літургія, Сповідь</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Напередодні в четвер</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19.00 Свята година. Сповідь, відвідування і причастя хворих&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кожної останньої суботи засідання Церковної ради</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щонеділі&nbsp;&ndash; збір молоді о 14.00 - Наука за кавою в Церковній хатині</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">П\'ятниця о 18:00, Субота о 12:00, Неділя о 12.00</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Недільна школа для потребуючих дітей з гарячими обідами, в цей час мають можливість пообідати потребуючі парафіяни в благодійній парафіяльній їдальні Карітас-Печерськ.</span></span></p>','Розклад богослужінь на місяць травень 2012-го р. Божого','uk',2743,4226,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛЬВІВ &mdash; Офіційна Інтернет-сторінка Центру військового капеланства Львівської архиєпархії УГКЦ розпочала роботу 16 листопада - RISU. Одна з найперших розвідок військових капеланів  про князя Аскольда.</span></span></p>','2007-11-20 08:58:17',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ЛЬВІВ &mdash; Офіційна Інтернет-сторінка Центру військового капеланства Львівської архиєпархії УГКЦ розпочала роботу 16 листопада - RISU. Одна з найперших розвідок військових капеланів  про князя Аскольда.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подаємо уривок \"Історичні джерела свідчать, що першим із князів хрестився у 867 році Аскольд, який після кількох успішних походів на Візантію робить спробу запровадити у своїй державі християнство. Деякі церковні історики вважають цю подію &laquo;першим хрещенням Київської Русі&raquo;, в результаті чого з\'явилася 61-а єпархія &mdash; Руська - у списку Константинопольського патріархату. У 955 році в Царгороді була охрещена княгиня Ольга. Слід зауважити, що у Києві за часів Ігоря та княгині Ольги вже діяли церкви. І серед воїнів того часу були чимало християн. Підтвердження цього являється факт присутності на присяганні виконувати умови русько-візантійського договору воїнів дружини князя Ігоря, що присягали в церкві святого Іллі на Подолі . Однак на Русі ще панували родоплемінні релігії. Офіційне хрещення Русі відбулось у 988 році в Києві, коли за наказом князя Володимира в річці Почайні, що впадала в Дніпро під сучасною Володимирською гіркою, було здійснено хрещення киян. З Києва християнство поширилось у Чернігів, Володимир - Волинський, Полоцьк, Туров, Тмутаракань.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фактично з цього моменту розпочалась співпраця держави та Церкви, на теренах України. Новий, релігійний вплив поширився на усі сфери державного життя, і таким чином просто не міг оминути один із суб\'єктів держави, її військо. Так, під час походу на половців у 1111 році духовенство тісно співпрацювало із князівським військом. Підтвердженням цього є слова із літопису: &laquo;Князь Володимир приставив попів своїх, щоб їхали наперед війська й співали тропарі та кондаки Чесного Христа та канон Святої Богородиці&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отже, союз служителів віри та воїнів розпочав своє формування з перших віків християнства на Русі. Руське воїнство сприймалось тоді як святе і доблесне, називали його &laquo;христолюбивим&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На церковній мові слово &laquo;воїн&raquo; має особливе забарвлення. Серед святих, вшанованих Церквою, велика кількість воїнів, руських князів. Серед них небесний покровитель руського воїнства св. Юрій Побідоносець, сам хреститель Русі, святий рівноапостольний князь Володимир, святі князі Борис та Гліб, що першими зараховані до лику святих, князі Михайло та Гліб Чернігівські, архангел Михаїл - покровитель міста Києва і багато інших.\"</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Повний текст http://www.kapelanstvo.org.ua</span></span></p>','Військові про Аскольда','uk',235,1215,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Людина є Дух, у Духа є душа, яка знаходиться у фізичному тілі. Григорій Планчак. Відеозапис у \"Бібліотеці \"Аскольдова криниця\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Браття і сестри, на наших сторінках ви  можете подивитися і прослухати духовні науки ієроманаха Григорія  Планчака. Відеозапис зроблено у Києві 8-10 березня 2007 року.</span></span></p>','2007-10-15 16:26:26',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"image,1186,planchak.jpg\"><img style=\"float: right;\" src=\"image,1186,250,planchak.jpg\" alt=\"\" width=\"144\" height=\"108\" /></a>Людина є Дух, у Духа є душа, яка знаходиться у фізичному тілі. Григорій Планчак. Відеозапис у \"Бібліотеці \"Аскольдова криниця\"</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Браття і сестри, на наших сторінках ви можете подивитися і прослухати духовні науки ієроманаха Григорія Планчака. Відеозапис зроблено у Києві 8-10 березня 2007 року.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організували ці реколекції у найбльшій залі столичного педагогічного університету священнослужителі та парафіяни Української греко-католицької церкви, що розташована на священному древньому місці -- Аскольдова могила.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ці триденні духовні науки з величезною увагою і пошаною прослухали сотні, як не тисячі, мешканців столиці і гостей з інших місць. Неоціненна заслуга Церкви полягає в тому, що її духовні науки відкривають кожній людині очі на себе і на ближнього. Ці знання дають можливість нам жити в мирі між собою і з собою, відкривають шлях до Благодаті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Часом люди змушені через лихо, нещастя, біду чи й навіть задля задоволення цікавості шукають іменитих психоаналітиків, порпаються у грубезних томах наукових чи науково-популярних видань з психології. Проте практично ніхто не бачить людину такою, якою вона є &ndash; триєдиною (Дух-душа-тіло). Григорій Планчак &ndash; справжній цілитель. Він дарує свою рідкісну науку спраглим без всяких умов і обмежень. Аби лишень людина могла справді чути і справді бачити.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина перша<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/69pFRgfxWX8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/69pFRgfxWX8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина друга<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/QkJKA8P6POE&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/QkJKA8P6POE&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина третя<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/xPR5bmpmEoI&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/xPR5bmpmEoI&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина четверта<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/_sDTYJk7dHk&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/_sDTYJk7dHk&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина п\'ята<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/5dRictZ1lO8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/5dRictZ1lO8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина шоста<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/jo5AcvbqW2g&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/jo5AcvbqW2g&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Частина сьома<br />\r\n<object width=\"425\" height=\"344\" data=\"http://www.youtube.com/v/V7E8-s28ihc&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" type=\"application/x-shockwave-flash\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/V7E8-s28ihc&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" />\r\n<param name=\"allowfullscreen\" value=\"true\" />\r\n</object>\r\n</span></span></p>','Дух, душа і тіло. Правдиво і без прикрас ','uk',206,1186,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Людина і місто. Чи замислювались ви коли-небудь - як багато важить для історії окрема постать? І не обов\'язково політика чи громадського діяча.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Меленський, про якого піде мова, був київським архітектором, представником класицизму, що визначав європейські архітектурні смаки першої половини ХІХ століття.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Народжений 1766 р. в Москві, майбутній архітектор Києва отримав прекрасну освіту, навчаючись спочатку в Експедиції Кремлівського будівництва, а в 1787-1792 р.р. в Петербурзі у самого Джакомо Кваренгі.</span></span></p>','2008-09-25 23:52:00',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Людина і місто. Чи замислювались ви коли-небудь - як багато важить для історії окрема постать? І не обов\'язково політика чи громадського діяча.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Меленський, про якого піде мова, був київським архітектором, представником класицизму, що визначав європейські архітектурні смаки першої половини ХІХ століття.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Народжений 1766 р. в Москві, майбутній архітектор Києва отримав прекрасну освіту, навчаючись спочатку в Експедиції Кремлівського будівництва, а в 1787-1792 р.р. в Петербурзі у самого Джакомо Кваренгі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">По закінченні навчання Андрій Меленський працював і в Москві, і в Петербурзі, але визнання здобув у Києві, отримавши у 33 роки посаду головного архітектора міста.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкви і дзвіниці, монастирські келії і приватні будинки, присутствені місця і міський театр - багатогранність вподобань майстра втілилась в його творіннях. Та першою справою Андрія Меленського в Києві став пам\'ятник Магдебурзькому праву - здобутої, втраченої і знову здобутої вільності.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Право на самоврядування або магдебурзьке право було скасовано за часів правління Катерини ІІ, відновлено по її смерті Павлом І і підтверджено наприкінці 1802 р. Олександром І. Остання подія&nbsp; і спонукала владу міста Києва оголосити збір коштів на пам\'ятник. Було розглянуто чимало проектів, запропоновано декілька місць для розміщення монумента. Врешті - решт зупинилися на невеличкому урочищі, що плавно спадало до Дніпра, і, де за давніми переказами князь Володимир охрестив своїх синів. Споконвіку тут било джерело з цілющою водою. Саме тут Андрій Меленський вирішив зробити восьмигранний басейн під арочним постаментом, а над ним 18-ти метрову колону тосканського ордеру рустовану знизу і увінчану кулею з хрестом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Монумент називали по - різному: \"Пам\'ятником&nbsp; Хрещення Русі\", після спорудження в 1853р. пам\'ятника св. Володимиру - \"Нижнім пам\'ятником св. Володимиру\", \"Пам\'ятником Магдебурзькому праву\". Остання назва є історично правильною, про що свідчить напис на нижньому русті колони: \"Усердием киевского гражданства за утверждение прав древней сей столицы Всероссийским Императором Александром І - 1802 года, сентября 15 дня\". У Великодню неділю 1808 року пам\'ятник було освячено.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перебуваючи 30 років на посаді головного архітектора Києва, Андрій Меленський спроектував і збудував багато архітектурних шедеврів. Не всі вони збереглися до нашого часу: щось було поруйновано ще в ХІХ ст., щось у ХХ ст.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В 1808-1809 роках Андрій Меленський напружено працював над новим планом забудови міста. Впроваджуючи принципи європейського містобудування, головний архітектор Києва дбав насамперед про те, щоб місто не втратило свого історичного обличчя і залишалось уособленням високої духовності.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Скільки складнощів здолав Андрій Меленський перш ніж розроблений під його керівництвом Генеральний план Києва затвердили в Петербурзі. На цей час місто вже мало, збудований архітектором перший Міський театр ( на цьому місці зараз - Український дім ). Площа, яка тільки почала формуватися отримала ім\'я - Театральної. Одночасно на пагорбі у Липках були споруджені Присутственні місця ( 1806-1810 ) - центр адміністративних установ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Згідно із Генеральним планом Андрія Меленського три розрізнені райони Києва: Старе місто, Поділ і Печерськ, повинні були об\'єднатися в єдиний міський організм. Планово почали з\'являтися нові вулиці, Козине болото стало перехрестям основних доріг, опановуючи нову для себе назву - Хрещатик.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закоханий в класичну архітектуру Давньої Греції та Риму, будівничий не забуває про здобутки митців епохи барокко.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На долю кожного митця випадають і радість, і випробування. Для головного архітектора Києва таким випробуванням стала страшна пожежа Подолу 1811 року. 9 липня о 9 годині ранку набат із дзвіниці Воскресенської церкви сповістив про лихо. Люди рятувалися як могли, будівлі врятувати було неможливо. Три дні горів Поділ. На четвертий залишилося саме згарище.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливо, хтось інший впав би у розпач. Треба було відбудовувати найбільший на той час район Києва. Андрій Меленський, не гаючись, взявся за відродження Подолу. Звичайно, якби не надмірна опіка з Петербурга, ми мали б інше Нижнє місто, можливо, менш прямолінійне. Було прокладено нові вулиці, розплановано нові площі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Те, що зумів зробити Андрій Меленський - вражає. Житлові будинки по вулицях Набережно-Хрещатицькій, 4, Крутому узвозі, 4, Московській, 40 - це шедеври цивільного містобудування першої половини ХІХ ст. Класичність форм і барочна легкість втілення - стиль Андрія Меленського. Окремою сторінкою його творчої біографії стала Покровська вулиця. Будинок майстра-ювеліра С. Стрельбицького (вул. Покровська,5), торгові крамниці (вул.Покровська, 11), маєток (вул.Покровська,8), монастир Миколи Доброго з притулком для дітей-сиріт (збереглася тільки дзвіниця монастиря).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Меленський був учнем В. Баженова і М. Казакова. Може тому в його творчості домінувало церковне будівництво.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відбудова Іллінської церкви після пожежі Подолу 1811 р., ремонтно-реставраційні роботи в Андріївській церкві 1816-1828рр., спорудження на місці дерев\'яної кам\'яної церкви Миколи Притиска, Хрестоздвиженська церква на Кожум\'яках, дзвіниця у Видубецькому монастирі, Трапезна (Тепла) церква на території Софії Київської, дзвіниця церкви Спаса на Берестові, комплекс споруд, що включає - дзвіницю, трапезну з церквою св. Духа, старий та новий Академічні корпуси, систему келійних споруд - це частковий перелік участі видатного архітектора у створенні обличчя міста.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Київ Андрія Меленського - це і Бурса по вул. Набережно-Хрещатицькій, 27, де поєдналися таланти двох майстрів: І. Григоровича-Барського з ХVІІІст. та А. Меленського з ХІХст., старий і новий Контрактові будинки, Гостиний двір, збудований за проектом архітектора А. Руска під керівництвом А. Меленського, Будинок намісника Дальніх печер Києво-Печерської лаври, низка келійних споруд на території Ближніх і Дальніх печер, келійний комплекс Кирилівського монастиря, будинки для прочан у вищезгаданих монастирях, відбудова всього комплексу Флорівського монастиря.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Меленський першим привніс до Києва церкви-ротонди - незнані до того в місті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В 1810 р. в Угорському урочищі на Аскольдовій могилі серед хрестів і надгробків давнього кладовища на місці дерев\'яної з\'являється кам\'яна, прикрашена класичною колонадою церква-ротонда на честь св. Миколая Мірлікійського.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кругла церква - символ безкінечності світу Божого. \"Я є альфа і омега. Я є початок і кінець\", - сказано в Святому Письмі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкви-ротонди - архітектор створив їх декілька. Крім згаданої св. Миколая на Аскольдовій могилі, Різдва Христового на Володимирському узвозі (нещодавно відновлена), храм Введенської общини та Воскресенська церква Флорівського монастиря.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Меленський був головним архітектором Києва майже 30 років. Можна впевнено сказати: Києву поталанило, що сюди приїхав такий майстер, але не менш поталанило і майстру - творити в такому чарівному місті, надихаючись його старовиною і духовною благодаттю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Колись на горі Щекавиці було кладовище, де видатні діячі нашої культури знаходили свій останній притулок. Там у 1833р. було поховано Андрія Меленського. Кладовище майже зникло - лишилась пам\'ять про архітектора - його творіння... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://otherside.com.ua/news/detail.php?id=9608\">Сергій ГРАБАР, культуролог, спеціально для \"Іншого Боку\"&nbsp;</a><a href=\"http://otherside.com.ua/news/detail.php?id=9608\">&nbsp;</a></span></span></p>','Архітектор Андрій Меленський','uk',39,1019,1,'Andriy Gushuley');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мне 29 лет, зовут Анна сама родом из Запорожья. Болею неизлечимой болезнью ВИЧ... &nbsp;Когда-то очень давно, у меня была прекрасная семья, которая состояла из четырех человек &ndash; Мама, бабашка, сестра и - я, как на тот момент мне казалось &ndash; я была самым непослушным ребенком в семье. Мама работала, бабушка ей помогала, ну, а мы с сестрой были всегда вместе с первого класса. До 14-ти лет в семье был покой и семейный уют, а вот после 14-ти&hellip; все и началось&hellip;</span></span></p>','2010-03-15 17:10:03',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мир и покой дому вашему! Пишет Вам человек, который очень сильно нуждается в Вашей помощи. Внесу немного ясности в суть своего письма, и расскажу немного о себе и своей судьбе&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мне 29 лет, зовут Анна сама родом из Запорожья. Болею неизлечимой болезнью ВИЧ... &nbsp;Когда-то очень давно, у меня была прекрасная семья, которая состояла из четырех человек &ndash; Мама, бабашка, сестра и - я, как на тот момент мне казалось &ndash; я была самым непослушным ребенком в семье. Мама работала, бабушка ей помогала, ну, а мы с сестрой были всегда вместе с первого класса. До 14-ти лет в семье был покой и семейный уют, а вот после 14-ти&hellip; все и началось&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мы с сестрой познакомились с парнями, но тогда мы еще даже не догадывались, что это знакомство станет для нашей семьи &ndash; роковым. Они употребляли наркотики. Уговорили попробовать и нас. Если бы мы знали, тогда какие будут последствия после этих наркотиков, то ни за что бы в жизни не прикоснулись даже к шприцу. Мой первый укол и перечеркнул всю мою жизнь&hellip; Это продолжалось очень долгое время. Мать начала пить, и ее запои были ужасными&hellip; Она очень болела, у нее был цирроз печени. В 1997 году она умерла на моих глазах, прямо в квартире. Ее печень не выдержала такой нагрузки. Это было ужасно! Потеря матери для нас была необратима&hellip; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У меня был нервный срыв, по скорой помощи забрали в больницу. Тогда я впервые узнала, что такое &ndash;&nbsp; сердце&hellip; Выйдя из больницы, я с сестрой еще больше начала употреблять наркотики. Жизнь потеряла весь смысл&hellip; &nbsp;А ведь мне тогда было 17 лет. Я искала выход, но не могла найти&hellip; Я искала поддержку, а ее не оказалось рядом&hellip; Я была разбита&hellip; Только в наркотиках я находила утешение. Доза увеличивалась все больше&hellip; Нужны были деньги. Чтоб их добыть, пришлось идти на преступление. Мы с сестрой начали воровать, обманывать людей&hellip; Господи, как я об этом сожалею. Пусть меня простят те люди, которые были нами, обмануты&hellip; Мы были уже в полной зависимости. Наркотик нас понемногу убивал&hellip; Начало гнить тело, появились язвы, отказывали ноги. Резко болезнь дала о себе знать. Мы с сестрой слегли практически в один момент. У меня было двухстороннее воспаление легких, абсцесс правого легкого, парализовало полностью правую сторону. Я не вставала. Рядом со мной стояла баночка, в которую я сплевывала кровь. У сестры сильно гнило в области паха (эти последствия были от инъекций наркотика). Я умирала медленно и болезненно. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бабушка, не выдержала этого, вызвала скорую помощь. Меня забрали в больницу. Там я пробыла месяц, не долечившись я сорвалась домой, чтобы знать в каком состоянии мои бабушка и сестра, и какая обстановка в доме. Приехав к себе домой я долго звонила в дверь&hellip; но никто не открывал&hellip; Несколько минутами позже, я услышала шуршание за дверью&hellip; Я ждала&hellip; И вот&hellip; Мне открыла дверь сестра&hellip; То, что я увидела на тот момент, это было ужасно&hellip; Передо мной стояла сестра, другими словами &ndash; живой труп. Она очень похудела, глаза впали, тело было земляного цвета. Она держалась двумя руками за стену, ее ноги были что колодки, налитые жидкостью. Из ее глаз текли слезы&hellip; Бедная моя сестричка&hellip; Не могу, вспоминаю, и сердце от боли разрывается&hellip; Что ей пришлось пережить&hellip; В больницу я так и не вернулась&hellip; Я вызвала скорую помощь, врачи, осмотрев сестру, поставили диагноз &ndash; сепсис, общее загрязнение крови&hellip; Как сейчас помню, завел меня на кухню врач и сказал &ndash; готовьтесь, вам ее не спасти&hellip; Ей осталось жить не больше месяца&hellip; Поздно&hellip; Я всеми усилиями старалась хоть как-то вернуть ее к жизни, подбодрить, развеселить, сделать для нее что-нибудь приятное.&nbsp; Все лучшее ей. Но ее здоровье ухудшалось с каждым днем все хуже и хуже. Появились пролежни на теле, образовался трупный запах в квартире&hellip; Она умирала в муках, прямо в моих руках, издав последний вздох, отдав Богу душу&hellip; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи! Боже мой! Где Ты? Почему забираешь у меня последнее, самое дорогое мне? За что? Почему Ты так со мной? Чем я тебя прогневила? Мать, сестра&hellip; кто дальше, а??? Кто? Забери меня! Не оставляй здесь, среди живых мучиться! Для чего? Зачем?.. Я кричала, плакала, звала Его, молила о прошении&hellip; Но, Он меня не слышал&hellip; Я была разбита. Жизнь потеряла весь смысл&hellip; Стоя на коленях в комнате, перед ее телом, я вообще себя не контролировала. У меня был шок, после которого моя голова покрылась в некоторых местах сединой&hellip; Сестру забрали в морг. Бабушка потеряла рассудок, одним словом &ndash; сошла сума. Начала очень сильно пить, не узнавала даже меня... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Похоронив сестру, сама, без какой-либо помощи, я вернулась снова к наркотикам&hellip; Доза была очень большая. На тот момент в них только я видела утешение. Моя жизнь была бессмысленной и обреченной на вечные мучения. Я на себе поставила крест&hellip; Опустила руки&hellip; Время шло&hellip; Бабушка пила, я кололась&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В 2000 году я впервые была задержана сотрудниками милиции на воровстве, за что и понесла наказание &ndash; 2 года лишения свободы. Пока отбывала свой срок, бабушку постигла ужасная участь. У нее обманным путем отобрали квартиру, нашу двухкомнатную где я была прописана, пообещав домик с доплатой, которого естественно, как потом оказалось &ndash; не было. Она бомжевала полгода, после чего ее труп нашли в своем же подъезде, под своей же дверью. Я освободилась в никуда. В нашей же квартире жили чужие люди&hellip; Соседи мне все рассказали, что происходило в мое отсутствие... Я была шокирована. Куда идти? Как быть? Что делать дальше? Как жить? За что? В голове создавалось куча вопросов, ни на один из них, я не находила подходящего ответа&hellip; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разбитая, заплаканная, обессиленная, я вернулась к своим старым друзьям &ndash; наркоманам&hellip; Притоны, подвалы, улица, были моим &laquo;домом&raquo;. Так еще прошли 4 года с тем же образом жизни&hellip; Это были страшные дни&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В 2006 году меня снова задержала милиция, на этот раз меня осудили на три с половиной года лишения свободы&hellip; Тяжело дался мне этот срок&hellip; Долгие годы, проведенные с наркотиком, очень сказались на моем здоровье. Больная печень, посыпались зубы, сердечко пошаливает, до сих пор гниют ноги, уже почти семь лет. Требуется лечение. Диагноз &ndash; трофические&nbsp; язвы обеих ног, видна даже кость на левой ноге. Пошло ухудшение. Тяжело ходить. Перенесла тяжелую операцию &ndash; хронический перитонит, удалили оде трубы (по-женски). Теперь я никогда не смогу иметь ребенка и продолжить свой род&hellip; Это ужасно&hellip; Ни родных, ни близких, никого у меня нет на всем белом свете. Я &ndash; сирота, никому не нужная&hellip; Господи! И зачем Господь оставил меня здесь, среди живых? Почему не забрал &laquo;туда&raquo; - к маме, бабушке, сестре?.. До сих пор не понимаю&hellip; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В 2009 году в феврале я освободилась с колонии, отсидев три с половиной года до конца. Зима, холодно, идти было некуда&hellip; Я ночевала на притонах, спала в подъездах, грелась у таксистов в машине, каталась в трамваях от конечной до конечной остановки, чтоб хоть как-то согреться&hellip; Находясь еще в лагере, я все рвалась на &laquo;свободу&raquo;, считала дни&hellip; А теперь, я понимаю, что не вся та &laquo;свобода&raquo;, что по ту сторону забора&hellip; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На тот момент эти дни &laquo;свободы&raquo; для меня показались адом. Никому не нужная, в своем же городе, я искала приюта, где придется. Я не хотела возвращаться к прошлому&hellip; Я устала&hellip; Сил нет больше&hellip; Это &ndash; смерть&hellip; Чтоб хоть как-то выжить, нужны были деньги. Я засыпала в холодных подъездах, боялась просыпаться, зная о том, что меня ждут тяжелые дни. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я очень заболела. Ходила по холоду с воспалением легких. Температура не спадала. Не соображая уже, что делаю, я пошла на воровство, чтобы хоть чем-то прокормить себя. Нужны были деньги на лекарство, бинты антибиотики. Ноги гнили, процесс прогрессировал. Силы исчерпывались. Буквально через два с половиной месяца после моего освобождения, меня снова задержала милиция&hellip; Дали три года лишения свободы, которые я отбываю сейчас в той же колонии, откуда освобождалась&hellip;&nbsp; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я до сих пор задаю себе один и тот же вопрос &ndash; зачем Господь меня оставил мучиться дальше? Для чего? Я проклята&hellip; Мне кажется, у меня будущего не будет никогда&hellip; Я никогда не буду счастлива&hellip; Я еще молодая &ndash; 29 лет. Я хочу жить! Действительно хочу жить, а не существовать. И будет ли когда-нибудь такое в моей жизни?... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я обратилась к Богу, начала читать Библию, молиться, ходить в церковь&hellip;&nbsp; Мне легко на душе! Господь мне помогает, меняет меня, мое отношение к людям&hellip; Вот вкратце описанная моя жизнь, если ее можно назвать&hellip;&nbsp; Еще два с половиной года строгого режима, лагерь, зона&hellip; Тяжело мне очень, как морально, так и материально&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу обратиться к вам за помощью. Вы уже знаете, я - сирота, какой-либо помощи и поддержки со свободы у меня нет. А я так в ней нуждаюсь. Нужны срочно деньги, на лечение моих ног, антибиотики, медицинские сеточки на ноги. Если сможете, умоляю, помогите пожалуйста, я от всей души буду благодарна вам. Также очень нуждаюсь в мыльных и бытовых принадлежностях, прокладках, продуктах питания, конвертах. Прошу Вас, чем сможете, помогите! На Вас и на Господа Бога одна надежда. Я верю, что жизнь, она есть, вот только я немного запуталась в ней. Верю, в то, что Господь не отвернется от меня и укажет мне правильный путь, по которому я и пойду по жизни. Дорогие братья и сестры, прошу, не откажите мне в моей просьбе. Я очень нуждаюсь в Вашей помощи. Я буду молиться, и ждать вашего ответа. Заранее благодарю вас за все. И пусть Господь хранит Вас всех. Со всей душой и Христом в сердце. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Анна&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Дорогі парафіяни час Великого посту, до нас звернулась Анна, яка відбуває покарання. Ви бачте з її листа про непросту долю і бажання навернутись. Бог любить всіх нас&nbsp;і коли хтось звертається з покаянням, отримає спасіння. Прошу Вас моліться за Анну, також хто бажає допомогти свою посильну допомогу принесіть в церкву на Аскольдову могилу. Також можна написати листа , підтримати духовно. Ваш лист буде відправлено через нашу тюремну службу милосердя. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Боже дай мені бачити свої гріхи і не осуджувати брата мого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ваш священник Ігор.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','Бог любить нас!','uk',2000,3483,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва - це духовна пожива кожної людини, християнина чи нехристиянина, бо молитвою підносимося до божих висот і лучимося з Богом, стаємо перед ним і кажемо йому все те, що в нашій душі радісне, чи що її болить, чи чогось вона потребує, чи за щось вона вдячна, чи прагне на мить бути понад землею.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Без молитви наша душа німа, холодна, а може бути й мертвою перед Богом, коли з ним, джерелом нашого життя-буття, не має вона зв\'язку. Молитва - наша дорога до Бога. У молитві Бога шукаємо, і знаходимо, за словами нашого Господа: &laquo;Кожний, хто просить, одержує; хто шукає, находить; хто стукає, тому відчинять&raquo; (Мт. 7,8). Молитва - це наша поміч, наше світло, наше добро.</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','2007-12-25 18:19:18',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\">За благословенням блаженнішого патріярха</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Мирослава Івана</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">СЛОВО ПАТРІЯРХА</span></span></strong></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">&laquo;Моліться безперестанку, за все дякуйте. </span></span></em></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Така бо воля Божа щодо вас у Христі Ісусі&raquo;. </span></span></em></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">(1 Сл. 5,17)</span></span></em></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Молитва - це духовна пожива кожної людини, християнина чи нехристиянина, бо молитвою підносимося до божих висот і лучимося з Богом, стаємо перед ним і кажемо йому все те, що в нашій душі радісне, чи що її болить, чи чогось вона потребує, чи за щось вона вдячна, чи прагне на мить бути понад землею.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Без молитви наша душа німа, холодна, а може бути й мертвою перед Богом, коли з ним, джерелом нашого життя-буття, не має вона зв\'язку. Молитва - наша дорога до Бога. У молитві Бога шукаємо, і знаходимо, за словами нашого Господа: &laquo;Кожний, хто просить, одержує; хто шукає, находить; хто стукає, тому відчинять&raquo; (Мт. 7,8). Молитва - це наша поміч, наше світло, наше добро.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Є різні засоби для молитви, як і різні її способи. Одним із найпростіших засобів - це молитовник, в якому списані різні молитви пера великих мужів молитви, їх ми читаємо не тільки устами, а і думкою, і серцем, доповнюючи їх нашими переживаннями і почуваннями. Між ними є теж молитви, які співаємо чи говоримо спільно в християнській громаді - це молитви літургічні, молитви нашого освячення. Це зміст кожного молитовника і його користь є незмірна.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Дорогі Браття і Сестри! Видаємо ось такий молитовник для наших вірних з усіма потрібними для них молитвами, особливо ж з літургійними. Нехай стане він першою книжкою Вашої родини, Вашої громади і кожного з Вас. Не розставайтеся з ним ніколи і нехай не буде дня, зокрема святочного, щоб він не був у Ваших руках. Це принесе Вам, нашому Народові і нашій Церкві великі благодаті.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Пам\'ятаймо великі слова святого отця нашого Івана Золотоустого, який знав ціну молитви: &laquo;Молитва є могутня зброя, невичерпний скарб, невичерпне багатство, джерело, мати безчисленних дібр&raquo;.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Цими словами й думками благословлю на духовну путь цей молитовник і благаю нашого Господа, щоб цим молитовником він учив нас молитися, щоб він став помічником для українських душ в їхнім житті й мандрівці з ним, нашим Спасом, до його вічности.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Дано в Римі</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">у Сиропусну неділю,</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">дня 17 лютого 1991 року Божого.</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Мирослав Іван Кардинал Любачівський</span></span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span>\r\n<p><a href=\"http://www.bazylianie.pl/mlolytownyk/slowo%20patriarxa.htm\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /></span></span></a></p>\r\n<span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span>\r\n<p><span style=\"color: #888888;\"><a href=\"http://www.bazylianie.pl/mlolytownyk/Molytwy.htm\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">\"Найголовніша справа в житті &ndash; молитва, а всі інші &ndash; справи другорядні\"</span></span><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /></span></span><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /></span></span><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Св. Григорій Богослов</span></span></a></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">http://www.truechristianity.info/ua/molytovnyk.php</span></span></p>\r\n<span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">http://www.truechristianity.info/ua/prayers_ua.php</span></span></p>\r\n<span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">http://www.truechristianity.info/ua/books/molytovnyk_dytyny.php</span></span></p>\r\n</span></p>','МОЛИТОВНИК','uk',887,1861,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна організація &laquo;Пласт - Національна скаутська організація України&raquo; впродовж багатьох років сприяє всебічному вихованню української молоді та дітей. Однією з традиційних акцій цієї організації є &laquo;Вифлеємський вогонь миру&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сценарій передачі \"Вифлеємського вогню миру\" у Києві. Місце - Софійська площа. Дата - неділя, 21 грудня 2008 року.</span></span></p>','2008-12-21 12:25:52',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна організація &laquo;Пласт - Національна скаутська організація України&raquo; впродовж багатьох років сприяє всебічному вихованню української молоді та дітей. Однією з традиційних акцій цієї організації є &laquo;Вифлеємський вогонь миру&raquo; <br /><br />Ця акція, котра набула значного поширення серед молоді європейських країн. Вогонь, котрий завжди горить у печері, де народився Спаситель, поширюється молодими людьми, минаючи кордони, долаючи тисячі кілометрів, символізуючи єдність світу навколо ідеї Добра, Братерства, Справедливості та Єднання. <br /><br />В Україні акція відбувається під патронатом Голови наглядової ради Міжнародного благодійного фонду &laquo;Україна 3000&raquo; п. Катерини Ющенко. її проведення в цьому році вже благословили Глави та Предтстоятелі більшості традиційних церков (УАПЦ, УПЦ КП, УГКЦ, РКЦ). <br /><br />Цього року акція &laquo;Вифлеємський вогонь миру&raquo; в Україні буде проведена уже вдесяте. Протягом її проведення планується взяти участь української делегації у старті акції у Відні, провести прес-конференцію, поширити Вогонь у 300 міст і сіл України у всіх областях, піднятися з ним на Говерлу, видати спеціальний випуск журналу &laquo;Пластовий шлях&raquo;, книжку з підсумками 10-ліття акції в Україні та ще багато іншого. До Києва Вогонь потрапить завдяки велопробігу від україно-польського кордону, що завершиться 21 грудня великим урочистим святом на Софійській площі. У ньому візьмуть представники всіх областей України, які поширюватимуть Вогонь далі. Планується також участь молоді з Росії, Молдови, Грузії та Вірменії. <br /><br /> Запрошуємо Вас долучитися до проведення акції &laquo;Вифлеємський вогонь миру&raquo;, відвідати акцію передачі Вифлиємського Вогню у Києві у неділю, 21 грудня 2008 р. на Софійському майдані у Києві. Офіційний початок о 14.00. <br /><br />Сценарій передачі \"Вифлеємського вогню миру\" у Києві. Місце - Софійська площа. Дата - неділя, 21 грудня 2008 року. Ведуча - Соломія Кейван, координатор міжнародної акції &laquo;Віфлеємський вогонь миру&raquo; в Україні <br /><br />14.00 - 14.10 - офіційний початок, вступне слово ведучої.   <br /><br />14.10 - 14.15 - поява велосипедистів, які під спів хору завозять Вифлеємський вогонь до сцени.   <br /><br />14.15 - 14.25 - передача вогню від велосипедистів до Соломії Кейван   <br /><br />14.25 - 14.40 - вогонь передається священику, молебень, відспів &laquo;Отче Наш&raquo;   <br /><br />14.40 - 15.00 - привітання представників усіх церков   <br /><br />15.00 - 15.10 - привітання Катерини Ющенко   <br /><br />15.10 - 15.15 - привітання Вячеслава Стебницького   <br /><br />15.15 - 15.30 - оголошення імен представників регіонів, що повезуть по Україні Вогонь.   <br /><br />15.30 - 16.00 - запалення всіх лампадок і свічок, передача Вогню усім присутнім.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: <a href=\"http://chs.org.ua/\" target=\"_blank\">http://chs.org.ua</a>/</span></span></p>','«Вифлеємський вогонь миру» - ОГОЛОШЕННЯ','uk',828,1808,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжне святкування Стрітення стало вже доброю традицією серед молоді Київської архиєпархії УГКЦ. Цьогорічні \"Стрітенські вечорниці\" відбудуться 14 лютого в музеї Івана Гончара за адресою вул. Івана Мазепи, 29. Свято розпочнеться о 17:30. Організаторами свята є Молодіжна комісія Київської архиєпархії УГКЦ та греко-католицька молодь Київщини.</span></span></p>','2009-02-12 18:21:26',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжне святкування Стрітення стало вже доброю традицією серед молоді Київської архиєпархії УГКЦ. Цьогорічні \"Стрітенські вечорниці\" відбудуться 14 лютого в музеї Івана Гончара за адресою вул. Івана Мазепи, 29. Свято розпочнеться о 17:30. Організаторами свята є Молодіжна комісія Київської архиєпархії УГКЦ та греко-католицька молодь Київщини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святкування стрітенських вечорниць є в Україні давньою традицією. Наші предки вірили, що в цей день зима зустрічається з весною, а для християн - це насамперед зустріч Симеона з Ісусом, що свідчить про чудесну силу віри. Сьогодні \"Стрітенські вечорниці\"- не лише данина традиції, але й сучасне переосмислення цього християнського свята.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\"В нелегкий час, цей вечір приносив молоді багато радості. Ми щасливі, що сьогодні можемо відновлювати давні традиції і створювати нові\" - кажуть організатори свята. Свято розпочнеться театралізованим дійством, із відображенням давніх і сучасних звичаїв, згодом всі бажаючі зможуть взяти участь у народних танцях та бансах, а на завершення відбудеться вечір української музики.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Додаткову інформацію можна отримати за тел.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">80977626210 (Тетяна),</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">80632644960 (Григорій)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Повідомила Тетяна Шпайхер</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>','Вдруге на Київщині християнська молодь святкуватиме \"Стрітенські вечорниці\"  ','uk',1123,2097,0,'Любов Михайлюк'),
(0,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Місія Благодійний фонд &bdquo;Карітас-Київ\" безкорисно надає матеріальну допомогу та  соціальні послуги найбільш потребуючим особам Дніпровського району міста Києва та Київської області на засадах християнської любові і милосердя з метою поліпшення їх соціально-економічного становища та сприяння соціальній реабілітації.</span></span></p>\r\n<h2 style=\"text-align: justify;\">\r\n<p>&nbsp;</p>\r\n</h2>','2007-07-27 23:51:14',NULL,'P','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Код</span></span>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21477781   КФ ВАТ &rdquo;Кредобанк&rdquo; МФО 321897<br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">у</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">грн. р/р 26000012593; в USD в/р</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26009002593; в EUR в/р 26009002593</span></span></p>\r\n</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span s