INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">24 квітня Пасха Воскресіння Христове</span></span></strong></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">00:00 Пасхальна Утреня зі Службою Божою</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07:30 Божественна Літургія, освячення пасок</span></span></p>','2011-04-24 11:06:57',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Великий понеділок 8:30 Літургія Передосвячених Дарів, Парастас</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Великий вівторок 18:00 Літургія Передосвячених Дарів, Парастас</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Велика середа запрошуємо до сповіді</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18 квітня. <strong>Великий понеділок</strong> о 17:00 розпочнеться <strong>Чин Соборування</strong>,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В церкві буде постійна нагода до сповіді.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21 квітня. <strong>Великий четвер</strong>. Страсті, спомин тайної вечері</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08:30 Вечірня з Літургією Василія Великого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Страсна утреня з читанням дванадцять Євангелій</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22 квітня.<strong> Велика пятниця</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08:15 Великі царські часи</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17:00 <strong>Вечірня з виставленням Плащаниці</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Єрусалимська утреня</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква буде відкрита цілу ніч, запрошуємо на молитву біля гробу Господнього</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23 квітня. <strong>Велика субота</strong>,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08:15 Вечірня з Літургією Василя Великого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Освячена пасок  18:00, 19:00, 20:00, 21:00, 22:00</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23:30 <strong>Надгробне</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">24 квітня <strong>Пасха Воскресіння Христове</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">00:00 Пасхальна Утреня зі Службою Божою</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07:30 Божественна Літургія, освячення пасок</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світлий тиждень. На кожний день</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08:15 Воскресла Утреня,&nbsp; 09:00 Служба Божа</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18:00 Вечірня, Служба Божа</span></span></p>','Страсний тиждень 2011 рік','uk',2409,3892,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>A column of fire in the village Velyki Birky 63 years ago.</p>\r\n<p>&nbsp;</p>\r\n<p>У неділю після Богослужіння парафіянка церкви св. Миколая на Аскольдовій могилі розповіла проникливого вірша про Ісуса, про святу Касіану, від якої вона того вірша почула, та про диво, яке сталося 63 року тому на Тернопільщині. \"Оранта\" подає розповідь парафіянки:</p>\r\n<p>&nbsp;</p>','2008-08-25 12:43:22',NULL,'P','<p>A column of fire in the village Velyki Birky 63 years ago.</p>\r\n<p>&nbsp;</p>\r\n<p>У неділю після Богослужіння парафіянка церкви св. Миколая на Аскольдовій могилі розповіла проникливого вірша про Ісуса, про святу Касіану, від якої вона того вірша почула, та про диво, яке сталося 63 року тому на Тернопільщині. \"Оранта\" подає розповідь парафіянки:</p>\r\n<p>&nbsp;</p>\r\n<p>\"Вірш \"Іду до Ісуса\" навчила мене моя рідна тітка Катерина Шумейко - монахиня Чину найсвятішого Ізбавителя, редемптаристка Касіанна. Було це в 1942 році, коли мені сповнилось 4 роки, а згадала я його і в перше записала на папері в 1995 р. Не знаю, хто точно є автором цього вірша, бо мені про те ніхто не говорив, але я думаю, що монахиня Касіана. Всі інші вірші, яких вона мене навчила, я забула і згадати не можу. Пам\'ятаю тільки, що я була дуже розчулена ними і часто повторювала їх в дні різних свят.</p>\r\n<p>&nbsp;</p>\r\n<p>Народилась монахиня Касіана в 1921 році в багатодітній сім\'ї була останньою дитиною. Перед нею ще були Марія, Петро, Антоніна, Ксенія ( це ті, яких я знала і бачила) та моя мама Володимира. Батько їхній - Микола Шумейко і мати - Ганна з родини Гуйдів-Гебровських були добропорядними і побожними людьми.</p>\r\n<p>&nbsp;</p>\r\n<p>Жили вони у Великих Бірках, що на Тернопільщині, там відійшли в інший світ, там і поховані їхні тіла. Наймолодша їхня донька Катерина вирізналась з-посеред інших дітей своєю серйозністю, строгістю, працелюбністю і великою любов\'ю до Бога. З 1936 року по 1942 р. вона працювала в церкві св. Парасковеї, спочатку молодшою, а потім старшою сестрицею. В монастир редемптористів, що на Львівщині, вона пішла в 1942 році і була там півроку послушницею, а тоді стала монахинею.</p>\r\n<p>&nbsp;</p>\r\n<p>Через те, що радянянська влада зщакривала і знищувала церкви і монастирі та монахів терпіли від вигнання, наприкінці червня 1945 року монахиння Касіана повернулась додому, у Великі Бірки, дуже виснаженою і тяжко хворою на туберкульоз. Не довго ходила біля неї її мати. Через два місяці в день Успіння Божої Матері, 28 серпня 1945 р. в післяобідню пору вона померла.</p>\r\n<p>&nbsp;</p>\r\n<p>В той час, коли ми були в хаті біля неї і молилися, на подвір\'я зайшло кілька місцевих людей, здивованих незрозумілим їм явищем. Привело їх сюди те, що кожний з них бачив зі своїх дворів високий вогняний стовп, який звисав над нашою хатою, і подумали що в нас пожежа.</p>\r\n<p>&nbsp;</p>\r\n<p>Цій події дав пояснення один з священників в день поховання монахині. Під-час проповіді він сказав, що вогняний стовп був для померлої прямою дорогою до Бога, і що таке буває тільки тоді, коли помирає свята людина.</p>\r\n<p>&nbsp;</p>\r\n<p>День поховання монахині Касіанни був дуже урочистим і особливим. Крім родини і місцевих людей, були монахині і священники з Тернополя, Львова та інших міст. Були відправлені служби Божі і вдома, і в церкві св. Парасковеї, і на кладовищі. Поховали її у Великих Бірках в центрі кладовища. Біля неї поруч похована її мама Ганна Шумейко, яка померла через рік, в 1946 році, на католицьке Різдво Христове.\"</p>\r\n<p>&nbsp;</p>\r\n<p>Племінниця монахині Касіани - Ольга Горбатюк, спеціально для &laquo;Оранти&raquo;</p>\r\n<p>&nbsp;</p>\r\n<p>Колаж з фото, наданих парафіянкою Ольгою Горбатюк. \"Оранта\"</p>\r\n<p>&nbsp;</p>','Стовп вогню у с. Великі Бірки 63 роки тому ','uk',430,1410,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>\"Людина є Дух, він від Бога, у Духа є душа і вона знаходиться у фізичному тілі\" Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила.<p/><p>1 день, 3 частина<p/>','2007-09-21 16:08:50',NULL,'P','<p>\"Людина є Дух, він від Бога, у Духа є душа і вона знаходиться у фізичному тілі\" Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила.<p/><p>1 день, 3 частина<p/><p>Ключові поняття: Молитва починається на тілесному рівні, духовна спрага, Господь добавляє благодаті<p/>','Духовні науки - 1 день, 3 частина','uk',182,1162,0,NULL),
(0,NULL,'<p>\"Оранта\" отримала звіт київських паломників про відвідання парафій української церкви у Чехії і про Злату Прагу<p/><p>Pilgrims from Kiev have narrated about their visit to the parishes of the Ukrainian Greek Catholic Church in the Czech Republic and about Zlata Prague. <p/>','2008-08-15 22:11:30',NULL,'P','<p>\"Оранта\" отримала звіт київських паломників про відвідання парафій української церкви у Чехії і про Злату Прагу<p/><p>Pilgrims from Kiev have narrated about their visit to the parishes of the Ukrainian Greek Catholic Church in the Czech Republic and about Zlata Prague. <p/><p>12.08.20008 ми відвідали Прагу. Приїхали ми швидше, ніж очікували. Пан Дмитро, наш гід і провідник, одразу ж повів нас до найстарішої частини Праги (Пражський Град), де знаходиться резиденція президента Чеської республіки (гарний великий палац у стилі барокко), різні музеї, храм св. Георгія Переможця та найвища сакральна будова в середній Європі – Катедральний собор св. Віта, головного патрона Землі Чеської. <p/><p>На вході до Граду стояли два солдати з рушницями. Гарні причесані, блискучі та налаковані вони стояли і не рухались, навіть коли їх смішили люди, а стояти їм треба аж 2 години!<p/><p> Найперше ми відвідали Катедральний собор.  Цей храм (з 925 року заложений перший фундамент, в 1060 р. – прибудовано дві вежі, в 1344 р. – початок готичного будівництва) був побудований у готичному стилі і виглядав велично, як ззовні, так і всередині. Собор був не просто великим, а ВЕЛИЧЕЗНИМ! Усередині панувала тиша і напівтемрява, яку порушували тільки клацання й спалахи фотоапаратів та захоплені вигуки японських туристів. У соборі прекрасні високі вітражні вікна з біблійною тематикою – витвір відомих митців художників початку минулого століття (напр. Альфонс Муха), біля кожного вікна були ікони та поховання видатних діячів церкви, кардиналів та єпископів. Також у соборі був діючий орган на 2 поверхи (!) і усипальниця святого Яна Непомуцького з XIV століття зроблена з чистого срібла. Де ми всі і помолилися вервицю за мир у Грузії. Шкода, що ми незмогли подивитись на поховання королів та правителів стародавньої Чехії, які знаходяться під храмом  – було закрито на ремонт.<p/><p>  Потім ми відвідали музей іграшок у якому ми побачили не тільки виставку Барбі від її початку (1952 р.) до сьогодення, а також і фігури із «Зоряних війн» та ще багато багато інших цікавих старовинних іграшок, і механічних, які добре збереглися в робочому стані (їм вже понад 100 років!). Старанні руки майстрів робили їх якісно. Вони і сьогодні могли б задовільнити кожну примху сучасної дитини.<p/><p>Коли ми вийшли із Пражського Граду, то побачили ще одну варту, але на цей раз вони змінювали один одного. Їх рухи були настільки сплановані й чіткі, що здавалося що то не люди, а роботи. Після того ми вийшли на Карлів міст, який проходить через річку Влтаву. Його побудував мудрий правитель Карл IV по спеціальному місячному календарю (міст з XV століття!) і присвятив його всім Cвятим.<p/><p> Після того, як ми перейшли міст, ми пішли до єдиного у світі годиника Орлой. О рівній годині з віконечок показуються 12 апостолів, а смерть-скелет дзвонить у дзвіночок. Там зібралось дуже багато туристів зі всього світу, щоб подивитись на годинник життя та смерті...<p/><p>На обід ми пішли у національний чешський ресторанчик, де смачно і весело попоїли. На жаль, наше перебування в Празі закінчилось, тому ми не встигли побачити усю красу цього  екстравагантного старовинного міста. <p/><p>Прага – унікальне красиве місто де містично переплітається минулість та сучастність і я б хотіла відвідати його ще неодин раз.<p/><p>Ольга Загоруйко.<p/><p>P.S. Дати, імена та назви підказав наш провідник пан Дмитро Серба.<p/><p>Довідка \"Оранти\"<p/><p>10-ро дітей і три виховника на парафіяльому мікроавтобусі подорожують Чехією і проводять катехитичне навчання у Карлових Варах. 06.09. 2008 вони прибули до Карлсбада. У неділю вони cфотографувалися біля пам`ятника першому президентові Чехії Т. Масарику Сьогодні надійшло фото із київськими паломниками на вулиці Андрійській <p/><p>Керівник групи Ігор Онишкевич (парох УГКЦ св. Миколая на Аскольдовій могилі), опікується паломниками о. Андрій Пенюк (декан парох ГКЦ св. Андрія, Карлсбад), представником от. Андрія Пенюка у справах координації діяльності з київською місією от. Ігоря онишкевича Дмитро Серба (пасторальний асистент, катихит, Карлсбад). <p/><p>On August 6, the van of the St. Nicholas Church (located at the Askoldova Mohyla historic site in Kiev) carrying ten children and three mentors crossed the Polish border in Krakowetz on its way to Karlovy Vary in the Czech<p/><p>Republic. They are traveling there on the invitation of Father Andriy, administrator of the charitable foundation “Caritas” of the Carlsbad parish of the Ukrainian Greek Catholic Church. <p/><p>Подорож здійснюється на запрошення отця Андрія, адміністратора «Карітасу» Карлсбадської парафії УГКЦ. Мета подорожі – відпочинок та катехитичні навчання, вивчення Святого письма. Керівник групи настоятель церкви св. Миколая, декан м. Києва Київської єпархії от. Ігор Онишкевич. Відпочинок і навчання триватиме 10 днів. Божествені літургії для дітей правитиме от. Андрій.<p/><p>Можливість подорожувати за кордон отримали діти, які відвідували недільну катехитичну школу при церкві. Поїздка для дітей цілком безкоштовна: витрати покривають українці з Карлсбада, парафіяни місцевої церкви, та громада церкви св. Миколая на Аскольдовій могилі.<p/><p>«Плануємо регулярно подавати інформацію про подорож, відпочинок і навчання через інтернет», повідомив парох церкви св. Миколая з на кордоні Краковецької митниці. <p/><p><p/><p><p/><p><p/><p><p/>','Озброєна варта пропустила паломників до старої Праги','uk',419,1399,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Історичні документи та розвідки. Завжди актуальні статті, або книжки релігійної тематики.<p/>','2006-04-14 17:35:03',NULL,'P','<p>Історичні документи та розвідки. Завжди актуальні статті, або книжки релігійної тематики. Для життєдайної снаги віри і духовності кожен спраглий зможе зачерпнути для себе тут із молитовної спадщиши Церкви, творів Святих отців, документів Церкви, роздумів богословів.<p/><p>Від початку дві тисячі восьмого року Божого вміщуємо у \"Аскольдовій криниці\" біографії з фото ієрархів Української греко-католицької церкви. <p/><p>Готуємо до публікації на Інтернет-сторінці «\"Аскольдова криниця\" роман львівського письменника Різника «Мойсей» про Шептицького і Франка.<p/><p>На цій сторінці також будемо розміщувати різноманітні відео, зокрема записи ре колекцій от. Григорія (Планчака).<p/><p>Ми також отримали для розміщення на Інтернет-сторінці «\"Аскольдова криниця\" відеофільм про прощу парафіян церкви на Аскольдовій могилі від села Заздрості, де народився патріарх Йосиф Сліпий, до села Зарваниця, де розташований Духовний центр Матері-Божої зарваницької і б’є свячене джерело життєдайної води. Саме цими дорогами патріарх Йосиф, ходив у дитинстві до школи, розташованої поблизу с.Зарваниця.<p/>','Мета \"Аскольдової криниці\"','uk',89,1069,0,NULL),
(0,NULL,'<p>Ісусе, Ти прийшов на цей світ, щоб відкрити нам Отця,<p/><p>Твого Отця і нашого Отця.<p/><p>Ти прийшов навчити нас любити одне одного.<p/><p>Так, як Ти нам обіцяв, зішли нам Святого Духа,<p/><p>щоб Він зробив нас знаряддям миру і єднання<p/><p>у цьому світі війн і поділу.<p/>','2006-10-14 20:11:36',NULL,'P','<p>Ісусе, Ти прийшов на цей світ, щоб відкрити нам Отця,<p/><p>Твого Отця і нашого Отця.<p/><p>Ти прийшов навчити нас любити одне одного.<p/><p>Так, як Ти нам обіцяв, зішли нам Святого Духа,<p/><p>щоб Він зробив нас знаряддям миру і єднання<p/><p>у цьому світі війн і поділу.<p/><p>Ісусе, Ти покликав нас іти за Тобою<p/><p>у спільноті “Віра і Світло”.<p/><p>Ми хочемо сказати Тобі “так”.<p/><p>Ми хочемо жити, поєднані любов’ю<p/><p>у цій великій родині, яку Ти дав нам,<p/><p>де можемо ділити терпіння і труднощі,<p/><p>радість і надію одні одних.<p/><p>Навчи нас приймати наші рани, наші слабості,<p/><p>щоб у них проявилася Твоя сила.<p/><p>Навчи нас віднаходити Твоє лице, Твій образ <p/><p>в усіх наших братах та сестрах, а особливо в найслабших.<p/><p>Навчи нас іти за Тобою шляхом Євангелія.<p/><p>Ісусе, ми віддаємо себе Тобі, як Твоя Мати Марія.<p/><p>Вона перша Тебе прийняла.<p/><p>Допоможи кожному з нас подібно прийняти Тебе <p/><p>до наших сердець і до наших спільнот.<p/><p>Допоможи нам бути вірно присутніми разом із Нею<p/><p>у підніжжі хреста, біля всіх розп’ятих цього світу.<p/><p>Допоможи нам жити Твоїм Воскресінням.<p/><p>Амінь.<p/>','Молитва “Віри і Світла”','uk',118,1098,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Архітектор, провідний київський зодчий першої третини&nbsp;XIX&nbsp;ст., талановитий представник стилю ампір.</p>','2008-09-25 23:45:15',NULL,'N','<p style=\"text-align: justify;\"><strong>МЕЛЕНСЬКИЙ Андрій Іванович (1766-1833).</strong></p>\r\n<p style=\"text-align: justify;\">Архітектор, провідний київський зодчий першої третини XIX ст., талановитий представник стилю ампір.</p>\r\n<p style=\"text-align: justify;\">Народився у Москві. Вчився мистецтву архітектури у Джакомо Кваренгі. Працював у московській та петербурзькій будівних комісіях. Наприкінці 1790-х років призначений губернським архітектором Волині, а у 1799 р. - міським архітектором Києва.</p>\r\n<p style=\"text-align: justify;\">Провадив значну будівну діяльність. Звів, зокрема, перший міський театр (1803-05, не зберігся) і пам\'ятник Хрещення Русі (1802). Спорудив нову церкву Миколи Доброго та дзвіницю Воскресенської церкви на Подолі (не збереглися), нову Микільську церкву на Аскольдовій могилі, перебудував подільську бурсу та трапезну Софійського собору. Звів синагогу на Печерську. План Києва, складений під керівництвом Меленського у 1803 р., є унікальним джерелом для вивчення історії забудови міста.</p>\r\n<p style=\"text-align: justify;\">Після нищівної пожежі Подолу (1811 р.) Меленський доклав багатьох зусиль до відбудови району за новим регулярним планом Василя Гесте. Він виконав відбудову й реконструкцію головних святинь Подолу, пошкоджених вогнем (зокрема, споруд Братського монастиря, Успенської церкви тощо). Завершив розпочату ще до пожежі церкву Різдва Христова. Здійснив широке оновлення Флорівського монастиря, де спорудив Воскресенську церкву, будинок ігумені, готель та чотири корпуси келій, відновив дзвіницю. Одночасно склав проект нової Хрестоздвиженської церкви на Кожум\'яках, яку було добудовано вже після його смерті. Керував будівництвом нового корпусу Духовної академії за проектом Людвика Шарлеманя (1822-25). За проектом Меленського зведено дзвіницю церкви Спаса на Берестові, дзвіницю Видубицького монастиря. Цікавий проект нової Десятинної церкви, запропонований Меленським, не було здійснено.</p>\r\n<p style=\"text-align: justify;\">Мав садибу на розі сучасних вулиць Хорива та Констянтинівської (№ 11), де у 1820-і роки збудував собі будинок. Тут Меленський мешкав і після того, як у березні 1829 р. за станом здоров\'я залишив посаду міського архітектора. Помер 1 січня 1833 р., могила на Щекавицькому кладовищі не збереглася.</p>\r\n<p style=\"text-align: justify;\">Михайло Кальницький&nbsp;</p>','МЕЛЕНСЬКИЙ Андрій Іванович, біографія','uk',38,1018,0,'Andriy Gushuley'),
(0,NULL,'<p>Багато нового та корисного дізналися представники районної виконавчої влади, які опікуються даними проблемами і освітяни під час цього семінару.<p/>','2007-11-16 19:25:52',NULL,'P','<p>Багато нового та корисного дізналися представники районної виконавчої влади, які опікуються даними проблемами і освітяни під час цього семінару. Зокрема про підготовку закону прямої дії щодо попередження насильства в сім’ї, “батьківські університети”, “педагогічний десант”, методику “Я рівний з тобою”, інші форми профілактичної роботи.<p/><p>Організували та провели семінар-практикум на тему: “Ні насильству в сім’ї та суспільстві Управління у справах сім’ї та молоді спільно з центром соціальних служб для сім’ї, дітей та молоді 12 листопада 2007 року об 11.00 год. в приміщенні сесійної зали Святошинської районної у місті Києві державної адміністрації (пр-т. Перемоги, 97). Об’єднання зусиль фахівців, які працюють над розв’язанням проблеми домашнього насильства з метою пошуку ефективних шляхів подолання цього ганебного явища” для соціальних педагогів загальноосвітніх навчальних закладів Святошинського району та педагогів-організаторів централізованої системи підліткових клубів за місцем проживання “Щасливе дитинство”.<p/><p>Про це повідомив \"Хрещатик\" http://www.kreschatic.kiev.ua/ua/3186/news/1195213301.html <p/><p>У семінарі взяли участь представники усіх шкіл району (сесійна зала зібрала близько 90 освітян), бо жорстоке поводження з дітьми є актуальною проблемою усього українського суспільства, глобальними наслідками якої є: бездоглядність та безпритульність дітей, торгівля людьми та дітьми, комерційна сексуальна експлуатація дітей, формування насильницького менталітету нації, зростання кількості розлучень і, нажаль, цей перелік можна продовжувати.<p/>','У Святошині відбувся семінар «Ні насиллю в сім’ї та суспільстві»','uk',230,1210,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Благодійний фонд &laquo;Рідний дім&raquo; привітав близько 23 тисяч школярів у Києві і Черкасах</p>\r\n<p>&nbsp;</p>\r\n<p>The charitable foundation &ldquo;Family Home&rdquo; sent gifts to some 23 thousand schoolchildren in Kiev and Cherkasy.</p>\r\n<p>&nbsp;</p>','2008-09-04 18:34:03',NULL,'P','<p>Благодійний фонд &laquo;Рідний дім&raquo; привітав близько 23 тисяч школярів у Києві і Черкасах</p>\r\n<p>The charitable foundation &ldquo;Family Home&rdquo; sent gifts to some 23 thousand schoolchildren in Kiev and Cherkasy.</p>\r\n<p>1 вересня з нагоди Дня знань 137 дітей спеціалізованої загальноосвітньої школи-інтернату для розумововідсталих дітей та 329 дітей зі спеціалізованої загальноосвітньої школи-інтернату для сиріт з наслідками поліомієліту та церебральним паралічем. отримали подарункові набори від БФ &laquo;Рідний дім&raquo;. Ще для вихованців 28-ми шкіл м.Києва партнери БФ &laquo;Рідний дім&raquo; розвезли до свята близько 20 000 порцій морозива.</p>\r\n<p>Напередодні Дня знань подарунки від БФ &laquo;Рідний дім&raquo; отримали понад 700 школярів м.Чернігів та 1,5 тисяч дітей м. Черкаси та області, а також 70 постраждалих від повені дітей Рожнятівського району Івано-Франківської області. Для дітей була організована святкова програма за участю дитячих творчих колективів та української зірки естради Аліни Гросу.</p>\r\n<p>Святкові заходи приурочені Дню знань БФ &bdquo;Рідний дім&rdquo; проводить вп&rsquo;яте.  Сама назва фонду декларує його мету: зробити Україну рідним Домом для усіх її дітей. Фундамент такого Дому фонд закладає сьогодні, надаючи підтримку талановитим дітям з малозабезпечених родин, дітям-сиротам, дітям з функціональними обмеженнями. Головним завданням фонду є забезпечення гармонійного розвитку маленької людини шляхом організації позашкільної дитячої зайнятості. У створених Фондом центрах по роботі з дітьми в Черкасах і Чернігові більше 300 дітей з соціально-незахищених сімей, а також діти з функціональними обмеженнями, вивчають комп\'ютер, англійську мову, історію рідного краю, розвивають свої таланти в різних творчих секціях. Фонд відкрив додатково 10 спортивних секцій для дитячих команд з воллейболу, футболу, баскетболу, легкої і важкої атлетики, боксу, в яких займається понад 250 дітей. Побудував  сучасний зал хореографії для більш ніж 200 дітей. В цілому, в гуртках художньої самодіяльності за підтримки фонду займається близько 1700 талановитих дітей.</p>\r\n<p>Анастасія Горєлова, віце-президент з питань інформаційної політики БФ &laquo;РД&raquo;, спеціально для &laquo;Оранти&raquo;</p>\r\n<p>Довідка &laquo;Оранти&raquo;</p>\r\n<p>Благодійний фонд &laquo;Рідний дім&raquo; заснований 2004 року.  Основними напрямками його діяльності є здійснення благодійної діяльності та надання адресної допомоги дітям з особливими потребами, дітям, які позбавлені батьківської уваги. Фонд проводить свою діяльність в Чернігівській, Черкаській та Київській областях. Фонд постійно опікується понад 25 000 дітей. Ініціатором та Почесним Президентом фонду є Софієнко Леся Степанівна.</p>\r\n<p>Щороку проводиться низка благодійних акцій, конкурсів, освітніх занять в Чернігові, Черкасах, Харкові, спрямовані на підтримку та розвиток дітей соціально-незахищених верств населення:</p>\r\n<p>&bull;	7 березня 2007р. під час благодійної акції &laquo;Малюю мамі rазку навесні&raquo;, що проводилась в Черкасах, зібрано 1 904 грн. 18 коп. Усі кошти передано до Шевченківської спеціалізованої школи-інтернату Черкаської облради за узгодженням обласної адміністрації. Ці кошти підуть на організацію ігрової кімнати для 60 дітей.</p>\r\n<p>&bull;	Під час цьогорічних Пасхальних свят представники Фонду роздали дітям 5500 подарунків в храмах Черкас та Чернігова.</p>\r\n<p>&bull;	14 липня 2007 року в Храмі-комплексі &laquo;Всіх святих чернігівських&raquo; був встановлений унікальний вітраж-ікону з зображенням Христа, що воскрес.</p>\r\n<p>&nbsp;</p>','Благодійний фонд «Рідний дім» привітав близько 23 тисяч школярів у Києві і Черкасах','uk',439,1419,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Божественне полум\'я єдиний раз на рік займається тут напередодні православного Великодня. Його диво полягає в тому, що в храмі знаходиться один тільки православний священик, і лампада запалюється без людського втручання, під час його молитви.</p>\r\n<p>&nbsp;</p>','2008-04-29 14:38:15',NULL,'P','<p>Божественне полум\'я єдиний раз на рік займається тут напередодні православного Великодня. Його диво полягає в тому, що в храмі знаходиться один тільки православний священик, і лампада запалюється без людського втручання, під час його молитви.</p>\r\n<p>&nbsp;</p>\r\n<p>Фото Богдана Тернопільского, зроблене переl Кувуклією у суботу перед Паскою у 2007-му році Божому</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Щоб виключити людське втручання, в храмі в п\'ятницю ввечері гасяться всі свічки, лампади і паникадила. Частина паломників закривається в будівлі разом із мусульманським сторожем.</p>\r\n<p>&nbsp;</p>\r\n<p>Зазвичай Благодатний вогонь сходить приблизно о 14:00.</p>\r\n<p>&nbsp;</p>\r\n<p>Літанія (церковна церемонія) Святого Вогню починається приблизно за добу до початку православного Великодня. У Храмі Гроба Господнього починають збиратися паломники, які бажають на власні очі побачити це диво.</p>\r\n<p>&nbsp;</p>\r\n<p>Сам Храм вміщає до 10 тисяч людей, також опиняються заповненими народом вся площа перед ним і анфілади навколишніх споруд.</p>\r\n<p>&nbsp;</p>\r\n<p>В суботу вранці в Кувуклії (каплиці), побудованій над мармуровим ложем, на якому, за переказами, лежало тіло Христа після зняття з хреста, ставиться незасвічена лампада з олією та 33 незасвічених свічки (за числом років Спасителя).</p>\r\n<p>&nbsp;</p>\r\n<p>Приміщення опечатується у присутності ізраїльських поліцейських. За часів османського правління за відсутністю фальсифікації стежили яничари.</p>\r\n<p>&nbsp;</p>\r\n<p>Потім ворота храму відкриваються для всіх охочих, але, за традицією, першими сюди заходять галасливі арабські підлітки-християни, які влаштовують жартівливі колотнечі, сидячи на плечах один в одного, і б\'ють у барабани, закликаючи Небесний вогонь або, арабською, Суботнє світло (Нур ас-Сабт), божественне походження якого визнають і мусульмани.</p>\r\n<p>&nbsp;</p>\r\n<p>За арабською молоддю закріплено право галасувати, оскільки один раз, коли підлітків вивели геть за порушення порядку, Благодатний вогонь не зійшов. Патріарх безуспішно молився протягом трьох годин, поки не наказав привести хлопців назад. Тоді диво нарешті відбулося, і з тих пір молодим арабам ніхто не заважає веселитися.</p>\r\n<p>&nbsp;</p>\r\n<p>Також за традицією, відбуваються сутички між греками і вірменами, які сперечаються за право першими увійти до храму.</p>\r\n<p>&nbsp;</p>\r\n<p>Опівдні почалася хода від єрусалимської патріархії, очолюваної грецькими ієрархами церкви.</p>\r\n<p>&nbsp;</p>\r\n<p>В самому Храмі до єрусалимського патріарха приєднався вірменський предстоятель. Після молитви їх роздягли до подрясников, перевірили, чи немає при них джерел вогню, і замкнули в Кувуклії.</p>\r\n<p>&nbsp;</p>\r\n<p>В різні роки від замкнення патріархів у каплиці до зішестя вогню проходило від кількох хвилин до кількох годин.</p>\r\n<p>&nbsp;</p>\r\n<p>За свідченнями очевидців, перед сходженням Благодатного вогню храм починають осявати яскраві спалахи, тут і там проскакують маленькі блискавки. При сповільненій зйомці добре видно, що вони виходитимуть із різних місць храму - від ікони, що висить над Кувуклією, від бані Храму, від вікон і з інших місць, і заливають все навколо яскравим світлом.</p>\r\n<p>&nbsp;</p>\r\n<p>За мить весь Храм опиняється опоясаним блискавками і відблисками, які зміяться його стінами і колонами вниз, ніби стікають до підніжжя храму і розтікаються за площею серед паломників.</p>\r\n<p>&nbsp;</p>\r\n<p>Одночасно з цим у тих, хто стоїть у Храмі і на площі спалахують свічки, самі запалюються лампади, які знаходяться з боків Кувуклії, потім починає сяяти і сама Кувуклія, а з отвору в бані Храму на Гроб із неба опускається вертикальний широкий стовп світла.</p>\r\n<p>&nbsp;</p>\r\n<p>В цей же час двері печери відкриваються і виходить православний патріарх, який благословляє присутніх і роздає Благодатний вогонь.</p>\r\n<p>&nbsp;</p>\r\n<p>Перші хвилини вогонь не палить і віруючі омивають ним свої обличчя та руки.</p>\r\n<p>&nbsp;</p>\r\n<p>Пізніше від Благодатного Вогню будуть засвічені лампади по всьому Єрусалиму, спеціальними авіарейсами Вогонь буде доправлено на Кіпр і до Греції, звідки буде розвезений по всьому світу. Частинку благодаті отримає й українська православна делегація.</p>\r\n<p>&nbsp;</p>\r\n<p>З погляду православ\'я, Благодатний вогонь є заставою між Богом і людьми, у виконання обітниці, даної воскреслим Христом його послідовникам: \"Я є з вами у всі дні до кінця віку\".</p>\r\n<p>&nbsp;</p>\r\n<p>Вважається, що рік, коли Небесний вогонь не зійде на Гроб Господній, означатиме кінець світу і владу антихриста.</p>\r\n<p>&nbsp;</p>\r\n<p>Одне з пророцтв, які зберігаються в Єрусалимській православній церкві, свідчить: \"Якщо пролилася кров християн біля Гробу Господнього, значить, скоро закриється вхід до цієї найбільшої святині і настануть для Церкви Христової особливо тяжкі часи\".</p>\r\n<p>&nbsp;</p>\r\n<p>Диво православної церкви</p>\r\n<p>&nbsp;</p>\r\n<p>Найраніші згадки про сходження Благодатного вогню на Гроб Господній в Єрусалимі напередодні Воскресіння Христова зустрічаються у Григорія Нісського, Евсевія і Сильвії Аквітанської і датуються IV століттям.</p>\r\n<p>&nbsp;</p>\r\n<p>В них є опис і раніших сходжень Благодатного вогню. За свідченням святих отців, нетварне Світло освітило Гроб Господній незабаром після Воскресіння Христа, що побачив апостол Петро.</p>\r\n<p>&nbsp;</p>\r\n<p>Диво сходження Благодатного вогню відбувається тільки при молитві православного патріарха.</p>\r\n<p>&nbsp;</p>\r\n<p>Так, в 1099 році Єрусалим був завойований хрестоносцями і римська церква, вважаючи православних віровідступниками, вигнала їх із Гроба Господня. Вже в 1101 році у Велику Суботу не здійснилося диво зішестя Святого вогню в Кувуклії, доки не були запрошені для участі в цьому обряді східні християни. Тоді король Балдуїн I поклопотався про повернення місцевим християнам їхніх прав.</p>\r\n<p>&nbsp;</p>\r\n<p>Власниками Храму Господня є одночасно представники кількох Християнських Церков. У 1579 році священикам вірменської церкви (яка не є православною через невизнання боголюдської природи Христа), всупереч традиції, вдалося підкупити султана Мурата Правдивого і місцеве градоначальство, щоб ті дозволили їм єдиноосібно святкувати Великдень і приймати Благодатний Вогонь.</p>\r\n<p>&nbsp;</p>\r\n<p>Православних же не пустили не лише в Кувуклію, але і в Храм, вони залишилися стояти біля входу. Вірменський патріарх молився близько доби, проте, ніякого дива не послідувало. Воднораз із неба вдарив промінь, як це зазвичай буває при зішесті Вогню, і потрапив саме в колону біля входу, поряд з якою був православний патріарх. Тріщина в колоні збереглася досі.</p>\r\n<p>&nbsp;</p>','Єрусалим-2007. Субота. У Храмі Гроба Господнього багато людей з усього світу','uk',372,1352,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Браття і сестри, на наших сторінках ви можете подивитися і прослухати духовні науки ієроманаха Григорія Планчака. Відеозапис зроблено у Києві 8-10 березня 2007 року. <p/>','2007-10-13 13:37:42',NULL,'P','<p>Браття і сестри, на наших сторінках ви можете подивитися і прослухати духовні науки ієроманаха Григорія Планчака. Відеозапис зроблено у Києві 8-10 березня 2007 року. <p/><p>Григорій Планчак: \"людина це є Дух, у духа є душа, яка знаходиться у фізичному тілі\" <p/><p>Організували ці реколекції у найбльшій залі столичного педагогічного університету священнослужителі та парафіяни Української греко-католицької церкви, що розташована на священному древньому місці -- Аскольдова могила. <p/><p>Ці триденні духовні науки з величезною увагою і пошаною прослухали сотні, як не тисячі, мешканців столиці і гостей з інших місць. Неоціненна заслуга Церкви полягає в тому, що її духовні науки відкривають кожній людині очі на себе і на ближнього. Ці знання дають можливість нам жити в мирі між собою і з собою, відкривають шлях до Благодаті.<p/><p>Часом люди змушені через лихо, нещастя, біду чи й навіть задля задоволення цікавості шукають іменитих психоаналітиків, порпаються у грубезних томах наукових чи науково-популярних видань з психології. Проте практично ніхто не бачить людину такою, якою вона є – триєдиною (Дух-душа-тіло). Григорій Планчак – справжній цілитель. Він дарує свою рідкісну науку спраглим без всяких умов і обмежень. Аби лишень людина могла справді чути і справді бачити.<p/><p>Щоб подивитися відео, зверність до Бібліотеки \"Аскольдова криниця\" просто на цій сторінці!<p/>','Григорій Планчак: \"... у Духа є душа, яка знаходиться у фізичному тілі\" ','uk',192,1172,0,NULL),
(0,NULL,'<p>В день свята Святого Священномученика Йосафата, 25 листопада, відчинилися двері храму УГКЦ у м.Заставна, Чернівецької області (Буковинський вікаріат).<p/>','2007-11-28 19:34:51',NULL,'P','<p>В день свята Святого Священномученика Йосафата, 25 листопада, відчинилися двері храму УГКЦ у м.Заставна, Чернівецької області (Буковинський вікаріат). Із хлібом-сіллю та словами привіту зустрічали Архієрея Миколая жителі міста на чолі з о. Ігорем Базюком, який запросив Владику освятити Церкву Святого Священномученика Йосафата і відслужити Архієрейську Літургію у наміренні Божого благословення для усієї Буковини. <p/><p>Розділити радість освячення нової церкви прибуло вісім священиків Буковинського вікаріату, серед яких – о. мітрат Валерій Сиротюк, вікарій Буковинський, та о. мітрат Василь Гасинець, декан Чернівецький. На Літургії був присутній також міський голова п. Ярослав Цуркан, представництво районної державної адміністрації та інші поважні гості, які засвідчили свою відкритість до співпраці на благо суспільного добра. <p/><p>Крім того, слід відзначити приїзд Чернівецької громади, яка тим самим показує свою братерську любов і старається допомагати там, де це є конечно потрібним. <p/><p>По завершенні освячення храму Владика Миколай привітав присутніх із цією урочистою подією і наголосив, що кожна охоча людина тепер зможе приклонити коліна та схилити свою голову перед Богом, присутнім Євхаристійно у кивоті, і у живій присутності просити про потрібні ласки. <p/><p>У часі Літургії молилися за жертв голодомору 1932-33 рр., а також шахтарів, які трагічно загинули. Владика Миколай у проповіді відзначив необхідність вшанування пам’яті усіх жертв страшного терору, спрямованого на знищення української нації. \"Ця страшна ідеологія має бути засуджена всіма і назавжди\", – сказав єпарх. <p/><p>На основі Євангельського читання Владика засудив також не властивий людській природі стиль життя, який відображає теза: Спочивай, їж, пий та веселися! Живучи подібно, людина йде проти власної природи і нищить в собі подобу Божу. <p/><p>Після Літургії Архієрей нагородив отця Ігоря Базюка високою церковною нагородою – нагрудним жовтим хрестом. Подячними іменними грамотами були нагороджені благодійники, які найбільше виявили свою жертовність у будівництві храму. <p/><p><p/><p><p/><p><p/><p>Д-н В.Перцович, прес-секретар <p/><p>Коломийсько-Чернівецької єпархії УГКЦ<p/>','Буковина збагатилася ще одним греко-католицьким храмом','uk',242,1222,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Ватиканське радіо. Тарас Шагал, уродженець Трускавця на Львівщині. Здобув вищу богословську освіту в Хорватії, Римі та у Відні.<p/>','2007-11-18 11:13:19',NULL,'P','<p>Ватиканське радіо. Тарас Шагал, уродженець Трускавця на Львівщині. Здобув вищу богословську освіту в Хорватії, Римі та у Відні.  Декрет про призначення прочитав генеральний вікарій католиків  візантійського обряду в Австрії, о. прелат д-р Олександр  Остгайм-Дзерович<p/><p><p/><p>11 листопада 2007 року у Відні, в українській греко-католицькій  церкві святої Варвари, владика Софрон Мудрий, ЧСВВ, єпископ-емерит  Івано-Франківський, очолив Святу Літургію з нагоди введення нового  пароха, о. Тараса Шагали, уродженця Трускавця на Львівщині. Отеець  Тарас здобув вищу богословську освіту в Хорватії, Римі та у Відні.  Декрет про призначення прочитав генеральний вікарій католиків  візантійського обряду в Австрії, о. прелат д-р Олександр  Остгайм-Дзерович<p/><p> Українська греко-католицька церква святої Варвари існує у Відні від  1775 року, а від 1784 вона є парафіяльною. Отже, ця парафія є  найстрашою в українській діаспорі в світі. Варто пригадати, що саме  в каплиці цієї церкви до кінця Другої світової війни зберігались  мощі святого Йосафата, які тепер почитаються в базиліці святого  Петра у Ватикані. <p/><p> У святковій події брали участь численні представники української  громади та студенти, що навчаються у Відні, як також постійний  представник України при міжнародних організаціях у Відні, Володимир  Єльченко.<p/>','Новий парох у Відні ','uk',233,1213,0,NULL),
(0,NULL,'<p>Владика Тарас (Сеньків) вручив подарунки сиротам першокласникам<p/><p>15.08.2008 [14:23] // Новини // o.Oleg ugcc.org<p/><p><p/>','2008-08-16 21:16:13',NULL,'P','<p>Владика Тарас (Сеньків) вручив подарунки сиротам першокласникам<p/><p>15.08.2008 [14:23] // Новини // o.Oleg ugcc.org<p/><p><p/><p>Семеро дітей сиріт і напівсиріт зі Стрия та Стрийського району, які цього року вперше підуть до школи, 13 серпня 2008 року отримали портфелик із необхідним шкільним приладдям, м\'які іграшки та солодощі. У каплиці Канцелярії Стрийської єпархії Владика помолився разом з дітьми за Боже благословення для них та їхніх опікунів. Благодійну акцію організував Карітас Стрийської єпархії.<p/><p><p/><p>Троє дітей є цілковитими сиротами. Четверо дітей є сиротами при живих батьках. В  однієї дитини нещодавно померла мама, а перед тим – батько. Діти живуть і виховуються біля дідуся і бабусі, або в сиротинцях. В чотирьох дітей батьки позбавлені батьківських прав. В декого батьки розлучені і живуть з іншими сім\'ями. Одна дитина живе в сиротинці м. Стрия, двоє в сиротинці у с. Йосиповичі, а ще троє – в сиротинці с. Жулин на Стрийщині.<p/><p><p/><p>Благодійний проект Карітасу Стрийської єпархії під назвою „Шкільний портфелик” проводиться третій рік поспіль. За словами його директора, Лідії Ковальської, проект здійснено при підтримці стрийських підприємців Ігоря Маланчука та Максима Похило. <p/><p><p/><p>Вони спорядили сім ранців зошитами, щоденниками, ручками, пеналами, папками для зошитів, зошитами для малювання, нот та кольоровим папером. Подарунки до ранців придбані за рахунок благодійної допомоги Оксани Зозулі із США.<p/><p><p/><p>о. Павло Худ <p/><p>керівник Прес-служби Стрийської єпархії<p/>','Владика Тарас (Сеньків) вручив подарунки сиротам першокласникам','uk',420,1400,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Волонтери \"Карітас-Печерськ\" відвідали інтернат для спаралізованих дітей на Житомирщині 17 May, 2008.  Volunteers of &ldquo;Caritas&ndash;Pechersk&rdquo; visited a hospital for the paralyzed children in Zhytomyr Oblast.</p>\r\n<p>&nbsp;</p>\r\n<p>У школі-інтернаті для дітей вражених церебральним паралічем у с. Потієвка</p>\r\n<p>&nbsp;</p>','2008-05-18 18:11:22',NULL,'P','<p>Волонтери \"Карітас-Печерськ\" відвідали інтернат для спаралізованих дітей на Житомирщині 17 May, 2008.  Volunteers of &ldquo;Caritas&ndash;Pechersk&rdquo; visited a hospital for the paralyzed children in Zhytomyr Oblast.</p>\r\n<p>&nbsp;</p>\r\n<p>У школі-інтернаті для дітей вражених церебральним паралічем у с. Потієвка</p>\r\n<p>&nbsp;</p>\r\n<p>Радомишльського р-ну на Житомирщині проживають і навчаються біля 90 дітей, є багато дітей сиріт. Більшість дітей з неповних родин. Інтернат розміщенний за 130 км. м. Києва.</p>\r\n<p>&nbsp;</p>\r\n<p>Ініціатива добродія Сергія Харчука і його родини, більше 10 років Церковна служба Карітас помогає дітям. Ця поїздка біла посвячена служінню хворму згідно Євангелії неділі послабленного.</p>\r\n<p>&nbsp;</p>\r\n<p>У поїздці брали участь волонтери \"Карітас-Печерськ\", вони роздали допомогу дитячим одягом від органзації &laquo;ЗУАДК&raquo;, яку очолює п. Віра Принько, дарунки біли від фірми &laquo;Оболонь&raquo; діти отримали солодку воду. Сергій Харчук пригостив дітей цукорками і кукурудзяними паличками.</p>\r\n<p>&nbsp;</p>\r\n<p>Співали і грали на абандурі Катерина Ганочка, переможиця конкурсу &laquo; Червона Рута&raquo; і Наталка З ущенко.</p>\r\n<p>&nbsp;</p>\r\n<p>Бісіста Віталіна, \"Оранта\", фото автора</p>\r\n<p>&nbsp;</p>','Подарунки паралізованим дітям від громади з ініціативи родини одного з парафіян','uk',382,1362,0,NULL),
(0,NULL,'<p>Глава УГКЦ Блаженніший Любомир зустрівся з викладачами та студентами Києво-Могилянської академії 15 листопада об 11.40 у Конґреґаційній залі Національного університету «Києво-Могилянська академія» (НаУКМА)<p/>','2007-11-16 19:52:38',NULL,'P','<p>Глава УГКЦ Блаженніший Любомир зустрівся з викладачами та студентами Києво-Могилянської академії 15 листопада об 11.40 у Конґреґаційній залі Національного університету «Києво-Могилянська академія» (НаУКМА)<p/><p>У заході взяв участь Президент НаУКМА п. Сергій Квіт. <p/><p>У рамках зустрічі Предстоятель УГКЦ радо відповів на запитання студентів, викладачів та науковців про історію і сучасне життя Церкви, поділився своїми баченнями щодо питань релігійного плюралізму в Україні, перспектив екуменічного діалогу, патріотизму, Патріархату УГКЦ, християнської етики у школах тощо. Модератором зустрічі була професор НаУКМА Людмила Филипович. <p/><p>Так зокрема, говорячи про найбільшу проблему, яку сьогодні переживає УГКЦ, Блаженніший Любомир відповів, що завданням кожної Церкви є старатися бути Церквою, тобто залишатися собою. «Маємо не дозволити, щоб людські немочі забруднювали життя церковної спільноти», – пояснив Глава УГКЦ. Це, за словами Предстоятеля УГКЦ, є важливим сталим завданням. А сучасним викликом є старатися про «загоєння зранених комунізмом та іншими «ізмами» душ громадян нашої держави». При цій нагоді Блаженніший Любомир повідомив, що для досягнення цього під час Патріаршого Синоду Єпископів Української Греко-Католицької Церкви у 2006 році було визначено як стратегію нашої Церкви на наступні десятиліття гасло: «Святість об\'єднаного Божого люду», головною ідеєю якого є бажання повчити членів Церкви змістові нашої віри та практики віри, тобто допомогти людям пізнати Закон Божий та згідно із ним жити. <p/><p>Не менш цікавою була відповідь Глави УГКЦ на запитання щодо перспектив досягнення єдності Церков. За словами Блаженнішого Любомира, єдність є можлива, але для цього потрібні дві передумови: правдиве бажання всіх сторін досягти єдності та респект релігійної свободи ближнього (визнавати право кожного шукати свій шлях до Бога). <p/><p>Відповідаючи на запитання про те, чи релігійний плюралізм не загрожує національній ідентичності, Блаженніший Любомир сказав: «Загроза є тоді, коли хтось представляє себе єдино правильним. Релігійна свобода – не загроза, а нагода вчитися шанувати права людини. Це будує суспільство». <p/><p>Департамент інформації УГКЦ <p/>','Любомир Гузар: Релігійна свобода –  нагода шанувати права людини','uk',231,1211,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Даніела Деребера, керівник дискусійного проекта \"Формула життя\"</p>\r\n<p>&nbsp;</p>','2008-02-02 15:53:55',NULL,'P','<p>Даніела Деребера, керівник дискусійного проекта \"Формула життя\"</p>\r\n<p>&nbsp;</p>\r\n<p>У попередній розмові ми з&rsquo;ясували, що кожна людина в першу чергу є духовним творінням. Врожай, що виріс, означає факт того, що було посіяне насіння. Будь-яка плоть, будь-яка річь, яка існує в світі, є фактом посіяного раніше насіння, тобто слова. Фізичний світ &ndash; це світ фактів, або світ врожаю. Людина як духовне творіння має владу над усіма силами природи &ndash; як тієї природи, що бачимо, так і тіеї, що не бачимо. Сфера благословенного, або успішного життя людини &ndash; це сфера духа. Будь-яка спроба реалізувати себе по-за сферою духа приречена на провал. Інше питання полягає в тому, що дух від Духа відрізняється. Але, знову ж таки, це є питанням віри та особистого вибору. Хто сподівається на себе, той не розумний; а хто ходить в мудрості, той буде цілий (Прит.28:26). Людина завжди знаходиться під чиєюсь владою &ndash; або Духа Святого, або духа нечистого. В цьому полягає наш вибір. Через людську плоть бачимо його незримого хазяїна &ndash; в її думках, словах, вчинках. Тож за їхніми плодами пізнаєте їх (Матф.7:20).</p>\r\n<p>&nbsp;</p>\r\n<p>Людська душа, або наш внутрішній розум, сприймають всі значення слів чи висловів прямолінійно, не залежно від того, подвійний вони мають сенс чи потрійний. Це лише одна із причин, згідно з якою ми повинні дуже уважно ставитися до всього, що чуємо або промовляємо. І сказав їм: дотримуйтесь того, що чуєте (Мк.4:24).</p>\r\n<p>&nbsp;</p>\r\n<p>Давайте розглянемо де-які вислови українською та російською мовами, які викликають хвороби в нашому тілі, руйнують наше життя, наводять &laquo;порчу&raquo; чи стають &laquo;зглазом&raquo; для наших дітей:</p>\r\n<p>&nbsp;</p>\r\n<p>-очі б мої не дивилися</p>\r\n<p>&nbsp;</p>\r\n<p>- здохнути на цьому місці</p>\r\n<p>&nbsp;</p>\r\n<p>- мене нудить від того</p>\r\n<p>&nbsp;</p>\r\n<p>- паршива дитина (терміном &laquo;паршивий&raquo; в давні часи називали тих, хто був хворим на лишай чи проказу)</p>\r\n<p>&nbsp;</p>\r\n<p>- ці діти &ndash; самий головний біль</p>\r\n<p>&nbsp;</p>\r\n<p>- душить образа</p>\r\n<p>&nbsp;</p>\r\n<p>- з глузду з&rsquo;їхати</p>\r\n<p>&nbsp;</p>\r\n<p>-немає клепки в голові</p>\r\n<p>&nbsp;</p>\r\n<p>- я у шоці</p>\r\n<p>&nbsp;</p>\r\n<p>- скрутні часи</p>\r\n<p>&nbsp;</p>\r\n<p>- дістало до печінки</p>\r\n<p>&nbsp;</p>\r\n<p>- нічого собі (взагалі нічого собі?)</p>\r\n<p>&nbsp;</p>\r\n<p>Додавайте, додавайте свій список. Вам, напевно, є, чим його доповнити.</p>\r\n<p>&nbsp;</p>\r\n<p>Вислови дружини на адресу коханого чоловіка:</p>\r\n<p>&nbsp;</p>\r\n<p>-	нажерся, як скотина (не забули, що ви із ним одна плоть?)</p>\r\n<p>&nbsp;</p>\r\n<p>-	чого розвалився (це запитання чи констатація факту?)</p>\r\n<p>&nbsp;</p>\r\n<p>-	мені твої друзі поперек горла стоять</p>\r\n<p>&nbsp;</p>\r\n<p>Для чого Ви зупинили свій вибір саме на цьому чоловікові, не замислювались? Багато хто відповідають: так іншого ж не було&hellip; !!! Не замислювались, чому не було? І чи повинна бути для дівчини відсутність в її оточенні нормальних, тверезих чоловіків підставою для того, щоб обирати шлюб з п&rsquo;яницею? Але цьому питанню ми приділимо увагу пізніше.</p>\r\n<p>&nbsp;</p>\r\n<p>Пропоную подивитися новими очами на те, що відкрилося внаслідок прочитаного, на проблему алкоголізму.</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;Ні злодії, ні лихварі, ні п&rsquo;яниці ні лихословці, ні хижаки &ndash; Царства Божого не успадкують&raquo; (1Кор.6:10).</p>\r\n<p>&nbsp;</p>\r\n<p>Зверніть увагу &ndash; будь-де в Священому Письмі не має слів про те, що алкоголь як такий є чимось поганим. Навпаки, в багатьох місцях можемо побачити, якими добрими є алкогольні напої і скільки користі буває від них. Алкоголь використовується:</p>\r\n<p>&nbsp;</p>\r\n<p>- на сімейних трапезах (Бут.27:25; Мк.2:22; Мф.9:17)</p>\r\n<p>&nbsp;</p>\r\n<p>- на святкуваннях (Есф 1:8-10 ; Дан 5:1-4)</p>\r\n<p>&nbsp;</p>\r\n<p>- на весіллях (Ін.2:3)</p>\r\n<p>&nbsp;</p>\r\n<p>- як приношення Богу (Вих 29:38,40; Лев 23:12,13)</p>\r\n<p>&nbsp;</p>\r\n<p>- як десятина (Втор 12:17 ; Неєм 13:5)</p>\r\n<p>&nbsp;</p>\r\n<p>- як плата (2Пар 2:10)</p>\r\n<p>&nbsp;</p>\r\n<p>- торгівля (Єзек 27:18)</p>\r\n<p>&nbsp;</p>\r\n<p>- ліки (Лк 10:34 ; 1Тим 5:23)</p>\r\n<p>&nbsp;</p>\r\n<p>і т.д. і т.п. Тобто в самому напої немає будь-якого зла, але зло знаходиться в перевернені його призначення, яке призводить до залежності від нього.</p>\r\n<p>&nbsp;</p>\r\n<p>В психіатрії алкоголізм розглядається як психічне захворювання, і це підтверджується тим, що під час очищення організму від алколоїдів тяжіння пацієнта до спиртного не перестає. Перенасичення алкоголем призводить до знебещеного стану духу (зверніть увагу, саме духу), тому саму проблему алкоголізму треба розглядати як духовну (втім, як і будь-які проблеми взагалі, чи вони пов&rsquo;язані з родиною, грошима, робатою, ремонтом, чи пересмаженими млинцями на сковородці).</p>\r\n<p>&nbsp;</p>\r\n<p>Алкоголізм лікується. Це є факт.</p>\r\n<p>&nbsp;</p>\r\n<p>Для людей це неможливо, та не для Бога, бо все можливо Богові (Мар.10:27).</p>\r\n<p>&nbsp;</p>\r\n<p>Для досягнення бажаного результату працювати потрібно не з фактом, як ми вже з&rsquo;ясували, а з духом, тобто з тією сферою, в якій знаходиться причина всього, що існує.</p>\r\n<p>&nbsp;</p>\r\n<p>Зверніть увагу, що майже всі залежні від алкоголю люди чимось невловимо схожі між собою. Вони нібито відмічені печаткою одного й того ж духу. Їм притаманні схожа поведінка, схожий спосіб міркувати, схожі звички. Дуже часто трапляється так, що батьки, дідусі, прадіди передають свій вибір бути рабом духа алкоголізму своїм нащадкам, виховують їх в цьому дусі, закладаючи в них способи поведінки та мислення, що є характерними для цього духу. Тому що, хто не має в собі Духа істини, про тих промовляли: &laquo;батьки їли кислий виноград, а у дітей на зубах оскома&raquo; (Єз.18:2). Перечитайте цей абзац стільки разів, скільки Вам знадобиться для того, щоб зрозуміти, про що йдеться. Це не так легко. Хто ким буває переможений, той тому і раб (2Пет.2:19).</p>\r\n<p>&nbsp;</p>\r\n<p>Сучасні засоби &laquo;лікування&raquo; алкоголізму (підшивання ампул, ін&rsquo;єкції, хороші, не заперечую, методики щодо очищення крові і т.д.) можуть викликати лише тимчасовий ефект. Найбільш складними є справи, що стосуються кодування, тому що внаслідок такого насильства над психікою можуть виникнути ще більш глибокі проблеми (з&rsquo;являються агресивність, злоба, ненависть, смуток, депресія і далі за списком). Господь випробовує праведного і нечестивого, той же, хто любить неправду, ненавидить душу свою (Пс.10:5). Будь-яка дія здійснюється в дусі і приносить свої плоди. Тому дуже важливо не лише не дозволяти собі здійснювати насильство над іншими людьми або природою, але й не дозволяти чинити насильство над своєю власною душею. Частіш за все алкоголіками стають саме ті люди, які все своє дитинство знаходилися під неусипним контролем батьків, бабусь, дідусів, воля яких постійно сковувалася під гнітом їх уваги. Такі люди взагалі не мають досвіду &ndash; як це, бути вільними духовно. Вони просто не змогли знайти іншого способу захистити себе від такого насильства, яке багато хто чомусь вважає за любов. Їх цьому не навчили. Але і батьків звинувачувати в поведінці такого роду неможна, тому що в дефіциті любові вихивувались і вони. Їх також не було кому вчити.</p>\r\n<p>&nbsp;</p>\r\n<p>Душа алкоголіка важко хвора. Вона хворіє на страхи, образи дитинства, злобу, ненависть, відчай, гординю. Людини, яка зростала під гнітом, бути підсвідомо намагатися вчиняти насильство до всього оточуючого її світу. Подивіться на родини алкоголиків &ndash; скільки страждань і болю дружини (чоловіки), діти, батьки людини, що пиячить. Пастка? Ні.</p>\r\n<p>&nbsp;</p>\r\n<p>Але перш ніж починати допомогати Вашій близькій людині зцілюватися від страшної залежності, припиніть бути рабом самі. Адже подібне притягується подібним. Не лякайтесь і не засмучуйтесь від не дуже приємних слів. Я поясню, що маю на увазі. Якщо Ви маєте в собі дух гордині, яка проявляється в Вас у формі жертовності &ndash; наприклад: це мій хрест; яка я свята на його фоні; я така працевита &ndash; тягну на собі всю сім&rsquo;ю та чоловіка-алкоголика; я готова приносити себе в жертву, лише б йому було добре, - нехай не думає така людина одержати що-небудь від Господа (Як.1:7). Тому що це є лицемірством в першу чергу перед самим собою. Двоєдушна людина нестійка на всіх шляхах своїх (Як.1:8). Навчитеся виявляти істиність своїх бажань, і, якщо побачите, що Вами володіє гординя, це означатиме, що Ваше бажання або намір не є благими. Просить у Бога вказати Вам шлях і дотримуйтесь того, що яуєте (Мар.4:24). З&rsquo;ясуйте, в чому полягає Ваше бажання &ndash; бути жертвою чи стати господарем свого життя. Лише після цього, коли Ви почнете змінюватись, Ваша допомога коханій людині набере сенсу.</p>\r\n<p>&nbsp;</p>\r\n<p>Якщо ж у кого з вас не вистачає мудроти, нехай просить у Бога, який дає всім просто і без докорів, - і дасться йому. Але нехай просить з вірою, нітрохи не сумніваючись, бо той, хто сумнівається, подібний до морської хвилі, яку вітер здіймає і розвіває. Нехай не думає така людина одержати що-небудь від Господа. Двоєдушна людина нестійка на всіх шляхах своїх (Як.1:5-8).</p>\r\n<p>&nbsp;</p>','Формула життя: хто ким буває переможений, той тому і раб','uk',311,1291,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Донецькі бізнесмени допомагають будувати греко-католицький храм у м. Димитрові<p/><p>18 вересня, напередодні   різдва пресвятої  Богородиці завершено  фундаментні  роботи будівництва храму свв. верховних апп. Петра і Павла в м. Димитрово Донецької  області Донецько-Харківського екзархату УГКЦ. <p/>','2008-09-18 16:24:32',NULL,'P','<p>Донецькі бізнесмени допомагають будувати греко-католицький храм у м. Димитрові<p/><p>18 вересня, напередодні   різдва пресвятої  Богородиці завершено  фундаментні  роботи будівництва храму свв. верховних апп. Петра і Павла в м. Димитрово Донецької  області Донецько-Харківського екзархату УГКЦ. <p/><p>\"Хтось  із священиків тут, на сході,  говорив - \"хочете побачити диво - почніть будувати храм\"... Парафія в Димитрові  - невелика, але храм росте догори, окрім того завезено 56 тонн щебеню   піску для буд вництва стін. Тому ще потребуємо людей доброї  волі, які  б змогли вкласти свою лепту в будівництво нашого храму. Бо не тільки в храмах наших сердець живе Бог, але також потребуємо   святинь для нашого народу, де би він міг прославляти нашого Бога!\", сказав \"Оранті\" настоятель парафії у місті Димитрово от. Іван Василенко<p/><p>4 фірми міста допомогли парафії грошима.<p/><p>The number of parishioners of the young Greek Catholic vicar Ivan Vasylenko has grown almost ten-fold over the past five years.<p/><p>Donetsk businessmen are providing assistance for construction of the new Ukrainian Greek Catholic church in the village Dymytrove, Donetsk Oblast.<p/><p>\"Оранта\"<p/><p>На фото: священник Іван Василенко з м. Димитрвоа на будівництві храму своєї парафії, фото Івана Димитрівського, \"Оранта\"<p/><p>Довідка \"Оранти\"<p/><p>Реквізити громади Храму св. апп Петра і Павла: Храм Петра   Павла, місто Димитрово, Красноармійський район Донецької  області. Проспект Миру, 32 АКБ \"Укрсоцбанк\".  І. код. 26154580, МФО 334011 р/р 2600725063581<p/>','Встановлено наріжника нового храму на Донбасі','uk',451,1431,0,NULL),
(0,NULL,'<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила. \" Людина є Дух, він від Бога, у Духа є душа і вона знаходиться у фізичному тілі\"<p/><p>1 день, 6 частина<p/>','2007-09-21 16:26:15',NULL,'P','<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила. \" Людина є Дух, він від Бога, у Духа є душа і вона знаходиться у фізичному тілі\"<p/><p>1 день, 6 частина<p/><p>Ключові слова: Григорій Планчак,оздоровлення, зцілення, направляти до Бога, духовні записи, блокнот, Господь тебе випробовує і кріпить; витримати, зберегти вірність, духовна наука, душа, тіло.<p/>','Григорій Планчак. Духовні науки - 1 день, 6 частин','uk',185,1165,0,NULL),
(0,NULL,'<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила</p>\r\n<p>&nbsp;</p>\r\n<p>1 день, 1 частина</p>\r\n<p>&nbsp;</p>','2007-09-17 22:18:41',NULL,'P','<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила</p>\r\n<p>&nbsp;</p>\r\n<p>1 день, 1 частина</p>\r\n<p>\r\n<object classid=\"clsid:d27cdb6e-ae6d-11cf-96b8-444553540000\" width=\"425\" height=\"344\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,40,0\">\r\n<param name=\"allowFullScreen\" value=\"true\" />\r\n<param name=\"src\" value=\"http://www.youtube.com/v/69pFRgfxWX8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" /><embed type=\"application/x-shockwave-flash\" width=\"425\" height=\"344\" src=\"http://www.youtube.com/v/69pFRgfxWX8&amp;hl=en&amp;fs=1&amp;rel=0&amp;color1=0xe1600f&amp;color2=0xfebd01\" allowfullscreen=\"true\"></embed>\r\n</object>\r\n</p>\r\n<p>Ключові слова: складові елементи душі: розум, воля, серце (почуття); дух живиться молитвою; молитвою спілкуємося з Богом</p>\r\n<p>&nbsp;</p>','Духовні науки','uk',180,1160,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 2 частина<p/>','2007-09-21 16:04:54',NULL,'P','<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 2 частина<p/><p>Ключові поняття: Бог є дух, Людина - духовна істота, дитина може побачити невидимий світ, до Господа повертається дух людини.<p/>','Духовні науки - 1 день, 2 частина','uk',181,1161,0,NULL),
(0,NULL,'<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 4 частина<p/>','2007-09-21 16:17:54',NULL,'P','<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 4 частина<p/><p>Ключові слова: складові елементи душі: розум, воля, серце (почуття); дух живиться молитвою; молитвою спілкуємося з Богом<p/>','Духовні науки - 1 день, 4 частина','uk',183,1163,0,NULL),
(0,NULL,'<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 5 частина<p/><p>Ключові слова: домінантою має бути віра і довіра до Бога, подумай що Бог тобі говорить.<p/>','2007-09-21 16:21:30',NULL,'P','<p>Духовні науки ієроманаха Григорія Планчака, 8-10 березня 2007 року, м.Київ, Аскольдова могила<p/><p>1 день, 5 частина<p/><p>Ключові слова: домінантою має бути віра і довіра до Бога, подумай що Бог тобі говорить.<p/>','Духовні науки - 1 день, 5 частина','uk',184,1164,0,NULL),
(0,NULL,'<p>ЕНЦИКЛІКА DEUS CARITAS EST СВЯТІШОГО ОТЦЯ ВЕНЕДИКТА XVI ЄПИСКОПАМ, СВЯЩЕНИКАМ, ДИЯКОНАМ, ПОСВЯЧЕНИМ ОСОБАМ І ВІРНИМ МИРЯНАМ ПРО ХРИСТИЯНСЬКУ ЛЮБОВ</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2008-03-10 17:25:07',NULL,'P','<p>ЕНЦИКЛІКА DEUS CARITAS EST СВЯТІШОГО ОТЦЯ ВЕНЕДИКТА XVI ЄПИСКОПАМ, СВЯЩЕНИКАМ, ДИЯКОНАМ, ПОСВЯЧЕНИМ ОСОБАМ І ВІРНИМ МИРЯНАМ ПРО ХРИСТИЯНСЬКУ ЛЮБОВ</p>\r\n<p>&nbsp;</p>\r\n<p>ВСТУП</p>\r\n<p>&nbsp;</p>\r\n<p>1. &laquo;Бог є любов, і хто перебуває в любові, той перебуває в Бозі, і Бог перебуває в ньому&raquo; (1 Ів. 4, 16). Ці слова з Першого послання Івана особливо чітко відтворюють суть християнської віри, християнський образ Бога і образ людини та її життя, який з нього [образу Бога] випливає. Крім цього, у тому самому стиху св. Іван подає, можна сказати, формулу християнського життя: &laquo;Ми пізнали й увірували в ту любов, яку Бог до нас має&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>Увірувати в Божу любов: такими словами християнин може окреслити основний вибір свого життя. Започатковує християнське існування не етичне рішення чи висока ідея, а подія, зустріч з Особою, яка надає нашому життю нову перспективу і визначає його напрямок. Святий Іван представив цю подію у своєму Євангелії так: &laquo;Бог бо так полюбив світ, що Сина свого Єдинородного дав, щоб кожен, хто вірує в нього &hellip; жив життям вічним&raquo; (3, 16). Християнська віра, визначаючи любов своєю основною засадою, прийняла те, що становило суть віри Ізраїлю, надавши їй водночас нових глибини і об&rsquo;єму. Віруючий ізраїльтянин насправді молиться щодня словами з Книги Второзаконня, знаючи, що вони становлять осердя його існування: &laquo;Слухай, Ізраїлю! Господь Бог наш, Господь єдиний. Любитимеш Господа, Бога твого, всім серцем твоїм і всією душею твоєю, і всією силою твоєю&raquo; (6, 4&ndash;5). Ісус об&rsquo;єднав цю заповідь любові до Бога із заповіддю любові до ближнього з Книги Левіт: &laquo;Любитимеш ближнього твого, як самого себе&raquo; (Лев. 19, 18; пор. Мр. 12, 29&ndash;31). Оскільки Бог першим полюбив нас (пор. 1 Ів. 4, 10), любов вже не просто &bdquo;заповідь&rdquo;, а відповідь на дар любові, з яким Бог приходить до нас.</p>\r\n<p>&nbsp;</p>\r\n<p>У світі, в якому з іменем Бога іноді пов&rsquo;язують помсту чи навіть обов&rsquo;язок ненависті й насильства, ця ідея є дуже актуальною і важливою. Саме тому свою першу енцикліку я хочу присвятити любові, яку нам подарував Господь і яку ми повинні передавати іншим. Про це, по суті, йдеться у двох частинах цього листа, які тісно між собою пов&rsquo;язані. Перша має більш теоретичний характер, оскільки я хотів би в ній уточнити &ndash; на початку свого понтифікату &ndash; декілька істотних фактів про любов, яку Бог таїнственно та цілком добровільно дарує людині, і водночас вказати на внутрішній зв&rsquo;язок між цією Господньою любов&rsquo;ю і любов&rsquo;ю людською. Друга частина має більш конкретний характер, оскільки в ній ідеться про виконання Церквою заповіді любові до ближнього. Вказана тема має багато аспектів, однак довгі міркування не є метою цієї енцикліки. Хочу лише виділити декілька основних елементів, щоб таким чином надати нового імпульсу відповіді людей на божественну любов.</p>\r\n<p>&nbsp;</p>\r\n<p>ПЕРША ЧАСТИНА</p>\r\n<p>&nbsp;</p>\r\n<p>ЄДНІСТЬ ЛЮБОВІ У ТВОРІННІ ТА ІСТОРІЇ СПАСІННЯ</p>\r\n<p>&nbsp;</p>\r\n<p>Мовна проблема</p>\r\n<p>&nbsp;</p>\r\n<p>2. Любов Бога до нас є основою життя і ставить два важливі запитання про те, ким є Бог і ким є ми. Розмірковуючи на цю тему, ми зіштовхуємося передусім із мовною проблемою. Термін &laquo;любов&raquo; став сьогодні одним із найбільш вживаних і найбільш знецінених; це слово, яке сприймають в найрізноманітніший спосіб. Навіть якщо тема цієї енцикліки зосереджена здебільшого на проблемі розуміння і вияву любові у Святому Письмі й у Традиції Церкви, ми не можемо так просто абстрагуватися від сенсу, якого цьому слову надають у різних культурах і в сучасному мовленні. Насамперед згадаймо про семантичний спектр слова &laquo;любов&raquo;: можна говорити про любов до батьківщини, любов до своєї справи, любов між друзями, любов до праці, любов між батьками й дітьми, братню любов і любов у родині, любов до ближнього і любов до Бога. Однак, у всьому цьому розмаїтті значень любов між чоловіком і жінкою, у якій нерозривно пов&rsquo;язані тіло й душа і в якій людині відкривається нездоланна обітниця щастя, є немовби прототипом досконалої любові: перед нею тьмяніють, на перший погляд, всі інші форми любові. Отож, постає запитання: чи, врешті-решт, всі ці форми якось поєднані між собою, і &ndash; попри все розмаїття своїх проявів &ndash; любов є одна і єдина, чи, навпаки, ми лише вживаємо одне й те саме слово для позначення цілком різних реалій?</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;Ерос&raquo; і &laquo;агапе&raquo; &ndash; відмінність і єдність</p>\r\n<p>&nbsp;</p>\r\n<p>3. Любов між чоловіком і жінкою, яка не є осмисленою та добровільною, а, так би мовити, накидається людині, стародавні греки називали &laquo;ерос&raquo;. Скажемо вже наперед, що грецький Старий Завіт вживає слово &laquo;ерос&raquo; лише двічі, а Новий Завіт &ndash; жодного разу: з трьох грецьких слів на позначення любові &ndash; ерос, філіо (любов-дружба) і агапе &ndash; новозавітні тексти надають перевагу останньому, яке в грецькій мові було радше другорядним. Щодо поняття любові-дружби (філіо), то воно вживається в Євангелії від Івана у глибшому значенні, щоб передати стосунки між Ісусом та Його учнями. Уникання слова &laquo;ерос&raquo;, як і нове бачення любові, відтворене у слові &laquo;агапе&raquo;, вносить, поза всяким сумнівом, суттєву новизну у християнське розуміння любові. У критиці християнства, яка дедалі радикальніше розвивалася з часів Просвітництва, ця новизна була сприйнята вкрай негативно. На думку Фрідріха Ніцше, християнство отруїло ерос, який, хоч і не загинув через це, та деградував до пороку [1]. Цим твердженням німецький філософ виражав дуже поширене уявлення: чи ж Церква своїми вимогами і заборонами не отруює нам все найкраще, що є в житті? Чи не ставить заборон саме там, де радість, передбачена для нас Творцем, дає нам щастя, яке вже наперед дозволяє нам скуштувати щось божественне?</p>\r\n<p>&nbsp;</p>\r\n<p>4. Чи й справді так? Чи дійсно християнство руйнує ерос? Погляньмо на дохристиянський світ. Аналогічно до інших культур стародавні греки вбачали в еросі передусім сп&rsquo;яніння, щось на кшталт &laquo;священного божевілля&raquo;, яке опановує розум, висмикує людину зі скінченності її існування і дозволяє їй, охопленій священною владою, пізнати найвище блаженство. Будь-яка інша влада між небом і землею, як випливало з цього, має другорядне значення: &laquo;Omnia vincit amor&raquo;, &ndash; пише Вергілій у своїх &laquo;Буколіках&raquo; &ndash; любов перемагає все &ndash; і додає: &laquo;Et nos cedamus amori&raquo; &ndash; і ми теж підкоряємося любові [2]. У релігіях такий підхід набуває форми культів плодючості, до якої належала й &laquo;священна&raquo; проституція, що процвітала у багатьох античних храмах. Еросу, отже, поклонялися як священній владі, як єднанню з божественним. Такій формі релігії, що, як величезна спокуса, суперечила вірі в одного Бога, Старий Завіт різко протистояв і боровся з нею як з перекрученням релігійності. При цьому, однак, він не заперечував еросу як такого, а оголосив війну його викривленим та шкідливим формам, оскільки фальшиве обожнення еросу применшувало його гідність і знелюднювало його. Справді, гетер, які повинні були дарувати це божественне сп&rsquo;яніння, не сприймали як людей чи осіб, а лише як засіб для досягнення &laquo;священного божевілля&raquo;: вони були не богинями, а людьми, яких просто використовували. Ось чому ерос &ndash; сп&rsquo;янілий і невпорядкований &ndash; є не піднесенням, &laquo;екстазом&raquo; до Вищого, а падінням, деградацією людини. Вочевидь, щоб дарувати людині не миттєве задоволення, а справжнє передчуття вершини існування, блаженства, якого прагне все наше єство, ерос потребує виховання та очищення.</p>\r\n<p>&nbsp;</p>\r\n<p>5. У цьому короткому огляді концепту еросу в історії і в сучасності увиразнюються дві речі. Передусім те, що існує певний зв&rsquo;язок між любов&rsquo;ю і божественним: любов обіцяє безконечність, вічність &ndash; реальність вищу і цілком відмінну від нашого буденного існування. Виявилося, однак, що шлях до цієї мети полягає не в слідуванні за власним інстинктом. Потрібні очищення і зрілість, яких можна досягти шляхом самозречення. Це не відмова від еросу, не &laquo;отруєння&raquo;, а зцілення та відновлення його справжньої величі. Це зумовлено насамперед людською природою &ndash; тілесною і духовною водночас. Людина стає справді собою, коли тіло і душа перебувають у внутрішній єдності, а виклики еросу можна подолати тоді, коли вдасться досягти цієї єдності. Якщо людина прагне бути лише духом і відмовляється від плоті як від чогось тваринного, тоді і дух, і тіло втрачають свою гідність. З іншого боку, якщо людина заперечує дух і вважає матерію, тіло єдиною реальністю, &ndash; вона втрачає свою велич. Епікуреєць Гассенді жартівливо звертався до Декарта вітанням: &laquo;О, Душе!&raquo;. А Декарт відповідав йому: &laquo;О, Плоте!&raquo; [3]. Однак любить не лише душа чи лише тіло: любить людина, особа як цілісне творіння, яке складається з тіла і душі. Тільки тоді, коли обидва виміри справді творять єдність, людина стає сама собою. Лише в такий спосіб любов &ndash; ерос &ndash; може дозрівати, досягаючи справжньої величі. Сьогодні християнству нерідко докоряють, що в минулому воно було ворогом всього тілесного; справді, такі тенденції в ньому завжди були. Проте спосіб вивищення тіла, до якого вдаються сьогодні, є хибним. Ерос, зведений лише до &laquo;сексу&raquo;, стає товаром, простою &laquo;річчю&raquo;, яку можна купити й продати; точніше, сама людина стає товаром. Це далеко не найкращий спосіб утвердження людиною свого тіла. Радше навпаки, людина сприймає тепер своє тіло і свою сексуальність як суто матеріальну частину самої себе, яку можна використовувати й експлуатувати з розрахунку, &ndash; частину, яку людина сприймає не як сферу реалізації власної свободи, а як щось таке, що вона намагається зробити водночас приємним і нешкідливим. Насправді ж стикаємося з деградацією людського тіла, яке вже не є інтегрованим у повноту свободи нашого існування і живим виявом повноти нашого буття, а просто виштовхнуте у суто біологічну сферу. Такий вияв захоплення тілом може дуже швидко трансформуватися в ненависть до тілесності. Християнська віра, натомість, завжди вважала людину єднісно-двоїстим буттям, в якому дух і матерія переплітаються, набуваючи притому ще більшої шляхетності. Так, ерос бажає нас піднести &laquo;в екстазі&raquo; до Божественного, вивести нас за наші власні межі, і саме для цього потрібен шлях піднесення, зречення, очищення і зцілення.</p>\r\n<p>&nbsp;</p>\r\n<p>6. Яким чином ми повинні прокладати цей шлях піднесення і очищення? Як слід переживати любов, щоб реалізувати уповні її обіцянки &ndash; і людські, і божественні? Першу важливу вказівку знаходимо в одній з книг Старого Завіту, яку добре знали містики &ndash; Пісні пісень. Згідно з поширеним сьогодні тлумаченням, вірші, вміщені у цій книзі, є піснями кохання, можливо, приуроченими до юдейського весілля, де вони покликані були оспівувати подружню любов. Повчальним у цьому контексті є те, що в цій книзі знаходимо два різні слова для окреслення любові. Передусім маємо слово &laquo;додім&raquo;, вжите у множині, яке вказує на ще непевну, невизначену любов, &ndash; таку, що шукає. Згодом це слово замінено на &laquo;агаба&raquo;, яке у грецькому перекладі Старого Завіту передано словом, співзвучним із &laquo;агапе&raquo; &ndash; воно, як ми побачили, стало характерним вираженням біблійного поняття любові. На відміну від любові невизначеної і такої, що шукає, цей термін виражає досвід любові, яка передбачає справжнє відкриття іншого, підіймаючись над егоїзмом (досі його панування було абсолютним). Любов стає тепер піклуванням про іншого. Вона не шукає більше саму себе, занурення у щастя, що п&rsquo;янить; навпаки, прагне добра для улюбленої істоти: вона стає зреченням, готова до самопожертви і навіть її шукає. Елементом розвитку любові до вищого рівня, її глибинного очищення є пошук визначеності, причому у подвійному сенсі: в сенсі винятковості &ndash; &laquo;лише ця особа&raquo; &ndash; і в сенсі &laquo;назавжди&raquo;. Любов охоплює повноту існування у всіх його вимірах, в тому числі у вимірі часу. Не могло б бути інакше, оскільки обіцянка любові скерована до її остаточної мети: любов прагне вічності. Так, любов &ndash; &laquo;екстаз&raquo;, але не в сенсі миттєвого сп&rsquo;яніння, а в сенсі шляху, постійного виходу за межі зосередженості на собі до звільнення через дарування самого себе і таким чином до справжнього відкриття самого себе, навіть більше &ndash; до відкриття Бога: &laquo;Хто буде намагатися спасти своє життя, той його погубить; а хто його погубить, той збереже його живим&raquo; (Лк. 17, 33), &ndash; каже Ісус. Це його твердження знаходимо в Євангелії у багатьох варіантах (пор. Мт. 10, 39; 16, 25; Мр. 8, 35; Лк. 9, 24; Ів. 12, 25). Ісус описує в такий спосіб свій особистий шлях, який привів Його через хрест до воскресіння; це шлях зерна, що впало в родючу землю і принесло рясні плоди. Виходячи з глибини особистої жертви і любові, яка досягла в Ньому повноти, цими словами Він описує також суть любові й людського існування як такого.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>7. Наші роздуми над суттю любові, які на початку були радше філософськими, шляхом внутрішньої динаміки привели нас до біблійної віри. На початку ми ставили собі запитання: чи за різними, часом суперечливими, значеннями слова &laquo;любов&raquo; стоїть глибинна єдність, чи, навпаки, їх належить відокремлювати? Важливішим, однак, запитанням було те, чи ідея любові, проголошена нам Біблією і Традицією Церкви, має щось спільне із загальнолюдським досвідом любові, чи, навпаки, йому суперечить. У зв&rsquo;язку з цим ми натрапили на два фундаментальні слова: &laquo;ерос&raquo;, що позначає &laquo;земну&raquo; любов, і &laquo;агапе&raquo;, що позначає любов, яка ґрунтується на вірі і моделюється нею. Часто ці два поняття протиставляють як любов &laquo;низхідну&raquo; і любов &laquo;висхідну&raquo;. Існують ще й інші схожі класифікації: наприклад, розрізняють любов власницьку і любов жертовну (amor concupiscentiае &ndash; amor benevolentiаe); часом у це розрізнення включають ще корисливу любов. У філософській і богословській дискусії ці розбіжності часто перебільшували аж до протиставлення: любов низхідну, жертовну, а саме агапе, назвали б типово християнською; і навпаки, любов висхідна, власницька і ненаситна, тобто ерос, була б характерна для нехристиянської культури, передусім грецької. Якщо б ці протиріччя довести до максимуму, то суть християнства була б відокремленою від фундаментальних стосунків, характерних для людського існування і створила б світ у собі, &ndash; можливо чудовий, проте дуже далекий від повноти людського існування. Насправді ж ерос і агапе &ndash; любов висхідна і любов низхідна &ndash; ніколи не можуть бути цілковито відокремленими одне від одного. Що більше ці дві любові, навіть у різних вимірах, знаходять свою справжню єдність в одній реальності любові, то більше реалізується справжня природа любові як такої. Навіть якщо первісно любов-ерос є передусім ненаситною, висхідною &ndash; яка приваблює обіцянкою великого щастя, &ndash; то в міру наближення до іншої людини дедалі менше зосереджуватиметься на собі, шукатиме щастя для іншого, щораз то більше турбуватиметься про того, кого любить, даруватиме себе і бажатиме &laquo;жити для іншого&raquo;. Саме в цей момент до любові-еросу додається вимір агапе; бо інакше ерос занепаде і втратить самого себе. З іншого боку, людина не може жити тільки в любові жертовній, низхідній. Людина не може завжди лише давати, а повинна також отримувати. Той, хто бажає дарувати любов, має і приймати її як дар. Справді, як говорив нам Господь, людина може стати джерелом ріки живої води (пор. Ів. 7, 37&ndash;38). Та щоб стати таким джерелом, слід щоразу знову пити з того першого і єдиного джерела, що ним є Ісус Христос, з пробитого серця якого витікає Божа любов (пор. Ів. 19, 34).</p>\r\n<p>&nbsp;</p>\r\n<p>У розповіді про драбину Якова Отці Церкви вбачали символ нерозривного зв&rsquo;язку між піднесенням і сходженням, між любов&rsquo;ю-еросом, яка шукає Бога, і любов&rsquo;ю-агапе, що віддає отриманий дар. У цьому біблійному тексті сказано, що патріарх Яків під час сну побачив над каменем, що слугував йому подушкою, драбину, яка сягала неба і по якій підіймалися й спускалися Божі ангели (пор. Бут. 28, 12; Ів. 1, 51). Папа Григорій Великий цікаво тлумачить це видіння у своєму &laquo;Пастирському правилі&raquo;. Добрий пастир, як каже він, повинен бути закоріненим у споглядання. Лише тоді він зможе відчути потреби інших так, що вони стануть його власними: &laquo;Per pietatis viscera in se infirmitatem caeterorum transferat&raquo; [4]. Святий Григорій у цьому контексті посилається на св. Павла, який був піднятий аж до найвищих таємниць Бога і з того моменту, коли спустився вниз, був готовий чинити все для всіх (пор. 2 Кор. 12, 2&ndash;4; 1 Кор. 9, 22). Крім того, св. Григорій наводить приклад Мойсея, який знову і знову заходить до священного шатра, спілкується з Богом, щоб мати змогу згодом служити своєму народові. &laquo;Всередині [шатра], піднесений на висоти спогляданням, він може поза [шатром] приймати тягар тих, хто страждає: &laquo;Іntus in contemplationem rapitur, foris infirmantium negotiis urgentur&raquo; [5].</p>\r\n<p>&nbsp;</p>\r\n<p>8. Таким чином ми знайшли першу відповідь, ще доволі загальну, на два попередні запитання: по суті, &laquo;любов&raquo; &ndash; одна, проте має різні виміри; то один, то другий може виділятися. Там, де, однак, два виміри значно віддаляються один від одного, з&rsquo;являється карикатурна або, у всякому разі, обмежена форма любові. Ми також побачили, що біблійна віра не будує паралельного світу чи світу, який суперечить первісному людському феномену любові, а сприймає цілісну людину, втручаючись в її пошук любові, щоб очистити та відкрити новий вимір. Цю новизну біблійної віри чітко видно насамперед у двох елементах, які слід підкреслити: образі Божому й образі людському.</p>\r\n<p>&nbsp;</p>\r\n<p>Новизна біблійної віри</p>\r\n<p>&nbsp;</p>\r\n<p>9. Тут передусім йдеться про новий образ Бога. В культурах, які оточують світ Біблії, образ Бога і богів залишається малозрозумілим і суперечливим. У світлі біблійної віри, навпаки, дедалі чіткішим і однозначним стає те, на що вказує Shema, основна молитва Ізраїлю, словами: &laquo;Слухай, Ізраїлю: Господь Бог наш, Господь єдиний&raquo; (Втор. 6, 4). Існує єдиний Бог, Творець неба і землі, який є також Богом усіх людей. У цьому твердженні є два особливі моменти: всі інші боги насправді не є Богом і вся дійсність, у якій ми живемо, походить від Бога, тобто Він її створив. Природно, ідея створення існує також в інших релігіях, але лише тут абсолютно зрозуміло, що не якийсь бог, а єдиний справжній Бог є творцем всієї дійсності; походить вона з могутності Його творчого Слова. Це означає, що Його творіння &ndash; дороге Йому, бо Він його хотів, Він його &laquo;робив&raquo;. Таким чином з&rsquo;являється другий важливий елемент: Бог любить людину. Божественна сила, яку прагнув осягти у своїх роздумах Аристотель на вершині грецької філософії, є для всіх істот предметом бажання і любові &ndash; і ця бажана та люблена божественність приводить в рух світ [6], &ndash; але сама вона нічого не потребує і нікого не любить; вона є лише предметом любові. І навпаки, єдиний Бог, в якого вірить Ізраїль, любить сам. Навіть більше, Його любов є любов&rsquo;ю вибору: серед всього люду він обрав Ізраїль і любить його, маючи, однак, план зцілення для всього людства. Він любить, і Його любов може бути окреслена, без жодного сумніву, як ерос, який водночас і повною мірою є також агапе [7].</p>\r\n<p>&nbsp;</p>\r\n<p>Пророки Осія і Єзекиїл описували цю пристрасть Бога до свого народу сміливими еротичними образами. Відносини Бога й Ізраїлю представляли за допомогою метафори заручин і шлюбу; і, як наслідок, ідолопоклонство є чужоложством та проституцією. Такими образами згадували &ndash; як ми вже бачили &ndash; культи плодючості з притаманними їм зловживаннями еросу, але водночас описували також стосунки вірності між Ізраїлем та його Богом. Історія любові Бога до Ізраїлю в глибшому сенсі виявляється в тому, що Він дає народові Тору, тобто відкриває Ізраїлю очі на справжню природу людини і вказує йому шлях істинного гуманізму. Ця історія базується на тому, що людина, яка живе у вірності єдиному Богові, починає пізнавати, що означає бути тим, кого любить Бог, і знаходити радість в істині, у справедливості, &ndash; радість у Бозі, який стає його найбільшим щастям: &laquo;Кого, крім тебе, мав я на небі? І коли я з тобою, нічого на землі не хочу. А мені благо &ndash; близько Бога бути&raquo; (Пс. 72(73), 25&ndash;28).</p>\r\n<p>&nbsp;</p>\r\n<p>10. Любов-ерос Бога до людини, як ми вже казали, є водночас любов&rsquo;ю-агапе. Не тільки тому, що дається вона абсолютно задарма, без жодної попередньої заслуги, але ще й тому, що є любов&rsquo;ю, яка прощає. Пророк Осія показав нам вимір агапе у любові Бога до людини, що значно перевищує аспект безкорисливості. Ізраїль вчинив &laquo;чужоложство&raquo;, він розірвав союз; Бог повинен був його засудити і відкинути. Саме тут чітко видно, що Бог є Богом, а не людиною: &laquo;Як мені тебе лишити, Ефраїме? Як мені видати тебе на поталу, Ізраїлю?.. Серце моє в мені обернулось, зворушився ввесь жаль мій. Не виконаю палаючого гніву мого, не буду нищити більш Ефраїма, бо я &ndash; Бог, не людина; Святий посеред тебе&raquo; (Ос. 11, 8-9). Палка любов Бога до свого народу &ndash; до людини &ndash; є водночас любов&rsquo;ю, яка прощає. Вона така велика, що повертає Бога проти самого себе, Його любов проти Його справедливості. Тут для християнина вже проглядається таїна хреста: Бог любить людину так, що сам, ставши людиною, приймає смерть, і примирює таким чином справедливість і любов.</p>\r\n<p>&nbsp;</p>\r\n<p>Філософський та історично-релігійний моменти, які потрібно підкреслити у цьому біблійному баченні, полягають у тому, що, з одного боку, перед нами &ndash; суто метафізичний образ Бога: Бог є абсолютним і первісним джерелом всякого буття; але цей універсальний принцип творення &ndash; Логос, первісний розум &ndash; є водночас кимсь, хто любить з усією палкістю справжньої любові. Таким чином ерос ушляхетнюється найвищою мірою і водночас очищується аж до того, що розчиняється в агапе. Стає зрозуміло, що належність Пісні пісень до книг Святого Письма тлумачиться тим, що ці пісні любові, по суті, описують стосунки Бога з людиною і людини з Богом. Отже, книга Пісні пісень стала як у християнській, так і в юдейській літературі джерелом містичного знання і досвіду, в якому виявляється суть біблійної віри. Так, існує з&rsquo;єднання людини з Богом &ndash; це первісна мрія людини, &ndash; але воно не полягає в тому, щоб розчинитися і втопитися в безіменному океані Божества. Ця єдність творить любов, у якій дві сторони, Бог і людина, залишаючись самими собою, стають одним: &laquo;Хто ж пристає до Господа, є одним духом&raquo;, &ndash; писав святий Павло (1 Кор. 6, 17).</p>\r\n<p>&nbsp;</p>\r\n<p>11. Перша новизна біблійної віри полягає, як ми побачили, в Божому образі; другу &ndash; тісно пов&rsquo;язану з нею, ми знаходимо в образі людини. Біблійна розповідь про створення світу говорить про самотність першої людини, Адама, якому Господь захотів дати допомогу. Жодне із творінь не могло бути допомогою для чоловіка, якої він потребував, хоч він і дав назви всім тваринам і птахам, включаючи їх у свій життєвий простір. Отож, з ребра чоловіка Бог створив жінку. Адам відтоді отримав допомогу, якої потребував: &laquo;Це справді кість від моїх костей і тіло від мого тіла&raquo; (Бут. 2, 23). На тлі цієї розповіді проглядаються концепції, які, наприклад, з&rsquo;являються у міфі, описаному Платоном: згідно з ним спочатку людина мала сферичну форму, що забезпечувала їй самодостатність. Але, щоб покарати її за гординю, Зевс розділив її надвоє, так що одна половина постійно шукає свою другу половину і прямує до неї, щоб повернути свою цілісність [8]. У біблійній розповіді не йдеться про покарання, однак твердження, що людина була певною мірою неповною і потребувала другої частини, якої їй бракувало до цілісності, навіює думку, що лише у єдності з іншою статтю вона може стати &laquo;цілісною&raquo;, і в цьому немає жодного сумніву. Біблійна розповідь завершується пророцтвом, яке стосується Адама: &laquo;Так то полишає чоловік свого батька й матір і пристає до своєї жінки, і стануть вони одним тілом&raquo; (Бут. 2, 24).</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Тут важливими є два аспекти: ерос немовби закорінений у самій природі людини; Адам перебуває в пошуку і він &laquo;полишає свого батька і матір&raquo;, щоб пристати до жінки; лише разом вони представляють повноту людства, ставши &laquo;одним тілом&raquo;. Другий аспект не менш важливий: ерос вже від моменту створення скеровує людину до одруження, до зв&rsquo;язку, що характеризується унікальністю і остаточністю; так і лише так реалізується глибинне призначення людини. Образові монотеїстичного Бога відповідає моногамне одруження. Одруження, засноване на унікальній і визначеній любові, стає образом ставлення Бога до свого народу, і навпаки: спосіб, у який Бог любить, стає мірилом людської любові. Цей тісний зв&rsquo;язок між еросом і одруженням у Біблії практично не має паралелі поза біблійною літературою.</p>\r\n<p>&nbsp;</p>\r\n<p>Ісус Христос &ndash; втілена любов Бога</p>\r\n<p>&nbsp;</p>\r\n<p>12. Навіть якщо ми досі говорили здебільшого про Старий Завіт, вже стало очевидним глибоке взаємопроникнення двох Завітів як єдиного Письма християнської віри. Справжня новизна Нового Завіту полягає не в нових ідеях, а власне в постаті Христа, який цим ідеям надав тіла і крові &ndash; нечуваний реалізм. Вже у Старому Завіті біблійна новизна полягала не тільки в ідеях, помислах, а в Божих діяннях &ndash; непередбачуваних і, на перший погляд, дивних. Ці Божі діяння набувають драматизму в тому, що в особі Ісуса Христа сам Бог розшукує &laquo;загублену вівцю&raquo; &ndash; стражденне і заблукане людство. Коли Ісус у своїх притчах говорить про пастиря, який іде шукати загублену вівцю, про жінку, яка шукає драхму, про батька, який вибігає назустріч блудному синові і обіймає його, то це не просто слова, а, по суті, пояснення Його справжнього буття і діяння. У смерті на хресті Бог повертається проти самого себе, Він жертвує собою, щоб підняти людину і спасти її &ndash; такою є любов у її найрадикальнішій формі. У спогляданні на пробитий бік Христа, про який говорить Іван (пор. 19, 37), стає зрозумілим вихідний пункт цієї енцикліки: &laquo;Бог &ndash; любов&raquo; (1 Ів. 4, 8). Тут можна побачити істину. Звідси починається визначення, що таке любов. У цьому спогляданні християнин знаходить шлях свого життя і своєї любові.</p>\r\n<p>&nbsp;</p>\r\n<p>13. Цьому акту жертвоприношення Ісус надав тривалу присутність через встановлення Євхаристії під час Тайної вечері. Він передбачив свою смерть і своє воскресіння, віддаючи вже в той час своїм учням себе самого у хлібі й вині, даруючи їм своє тіло і свою кров як нову манну (пор. Ів. 6, 31&ndash;33). Якщо стародавній світ тільки мріяв, що справжньою поживою людини &ndash; тим, чим вона як людина живе &ndash; є Логос, вічна мудрість, то тепер Логос справді став поживою для нас &ndash; як любов. Євхаристія включає нас у жертовний акт Ісуса: ми не лише статично приймаємо воплочене Слово, а й беремо участь у динаміці Його жертви. Образ шлюбу між Богом та Ізраїлем незбагненним для нас чином став дійсністю: те, що було перебуванням у присутності Бога, через участь у жертві Ісуса стало участю в Його тілі й крові, &ndash; стало союзом. &laquo;Містика&raquo; Таїнства, яка ґрунтується на приниженні Бога до нас, здійснюється на рівні, цілком відмінному від нашого, і підіймає нас значно вище, аніж це здатне зробити найбільше містичне піднесення людини.</p>\r\n<p>&nbsp;</p>\r\n<p>14. Тепер слід звернути увагу на ще інший аспект: &laquo;містика&raquo; Таїнства має суспільний характер, бо в причасті я єднаюся з Господом, як і всі люди, які причащаються: &laquo;Тому, що один хліб, &ndash; нас багато становить одне тіло, бо всі ми беремо участь в одному хлібі&raquo; (1 Кор. 10, 17). Єдність з Христом є водночас єдністю з усіма, кому Він себе дає. Я не можу тримати Христа лише для себе; я можу належати Йому лише в єдності з усіма тими, хто також став або буде Його. Причастя виводить мене за мої власні межі &ndash; до Нього і, водночас, до єдності з усіма християнами. Ми стаємо &laquo;одним тілом&raquo;, повністю поєднані в одному існуванні. Любов до Бога і любов до ближнього тепер стали нероздільними: воплочений Бог притягує нас до себе. Зрозуміло, отже, що агапе стає також окресленням Євхаристії: у ній агапе Бога приходить до нас фізично, щоб продовжувати свою справу в нас і через нас. Лише на цій христологічно-таїнственій основі можна правильно зрозуміти вчення Ісуса про любов. Перехід, який Він робить &ndash; від Закону і Пророків до подвійної заповіді любові до Бога і ближнього, і виведення життя віри з цього принципу як її центру, &ndash; не просто мораль, яка може існувати автономно від віри в Христа і Його постійного уреальнення у Святих Таїнствах. Віра, культ і етос, проникаючи одне в одне, стають єдиною дійсністю, що виявляється у нашій зустрічі з агапе Бога. Тут звичне протиставлення культу і етики можна просто відкинути. Сам &laquo;культ&raquo;, євхаристійне причастя, поєднує те, що мене люблять і що я люблю інших. Євхаристія, яка не перетворюється на конкретну діяльну любов, стає обмеженою. Взаємний вимір &ndash; ми невдовзі розглянемо це детальніше &ndash; &laquo;заповіді&raquo; любові стає можливим тільки тому, що вона є не лише вимогою. Любов можна &laquo;заповісти&raquo; тільки тому, що спершу вона була подарована.</p>\r\n<p>&nbsp;</p>\r\n<p>15. Цей принцип слід брати до уваги і тоді, коли хочемо зрозуміти великі Ісусові притчі. Багатий чоловік благає з місця осудження (пор. Лк. 16, 19&ndash;31), щоб його брати дізналися про те, що буде тому, хто легковажно ігнорує злидаря в його потребі. Ісус приймає це благання про допомогу і робить з нього застереження, щоб повернути нас на істинний шлях. Притча про доброго самарянина (пор. Лк. 10, 25&ndash;37) роз&rsquo;яснює ще два моменти. Якщо у той час поняття &laquo;ближній&raquo; стосувалася тих, хто належав до того самого народу та іноземців, які замешкали на землі Ізраїлю, тобто тісно згуртованої спільноти однієї країни чи одного народу, то тепер це обмеження скасоване. Той, хто потребує мене і кому я можу допомогти, є моїм ближнім. Поняття ближнього стало більш універсальним, залишаючись водночас конкретним. Попри поширення на все людство воно не обмежується виявом узагальненої та абстрактної любові, яка сама по собі ні до чого не зобов&rsquo;язує, а вимагає моєї конкретної участі тут і зараз. Це становить одне із завдань Церкви &ndash; пояснювати знову й знову відношення між тим, що далеке, і тим, що близьке, беручи до уваги практику щоденного життя своїх членів. Врешті, доречно було б нагадати тут Ісусову притчу про Страшний суд (пор. Мт. 25, 31&ndash;46), у якій любов стає критерієм для остаточного рішення стосовно цінності чи марності людського життя. Ісус ототожнює себе з тими, хто перебуває у потребі: голодними, спраглими, чужинцями, нагими, хворими та ув&rsquo;язненими. &laquo;Усе, що ви зробили одному з моїх братів найменших &ndash; ви мені зробили&raquo; (Мт. 25, 40). Любов до Бога і любов до ближнього тісно переплетені: у найменшому браті ми зустрічаємо самого Ісуса і в Ісусі ми зустрічаємо Бога.</p>\r\n<p>&nbsp;</p>\r\n<p>Любов до Бога і любов до ближнього</p>\r\n<p>&nbsp;</p>\r\n<p>16. Поміркувавши над суттю любові та її значенням у біблійній вірі, слід відповісти на два запитання стосовно нашої поведінки: чи справді можна любити Бога, не маючи змоги бачити Його? І наступне: чи можна наказати любити? Стосовно подвійної заповіді любові можна висунути подвійне заперечення, яке прозвучало у цих двох запитаннях. Ніхто ніколи не бачив Бога: як ми можемо Його любити? Крім того, до любові не можна зобов&rsquo;язати. Адже це почуття, що може бути або може не бути, але яке не виникає за бажанням. Святе Письмо підсилює перше заперечення, коли говорить: &laquo;Коли хтось каже: &bdquo;Я люблю Бога&rdquo;, а ненавидить брата свого, той не правдомовець. Бо хто не любить брата свого, якого бачить, той не може любити Бога, якого він не бачить&raquo; (1 Ів. 4, 20). Однак цей текст зовсім не виключає любов до Бога як щось неможливе; навпаки, весь контекст Першого послання апостола Івана, яке ми щойно цитували, вказує на те, що така любов є чіткою вимогою. Цим підкреслено нерозривний зв&rsquo;язок між любов&rsquo;ю до Бога і любов&rsquo;ю до ближнього. Обидві настільки переплітаються, що твердження про любов до Бога стає брехнею, якщо людина відвертається від свого ближнього, або, що більше, ненавидить його. Цей уривок із Послання апостола Івана потрібно пояснити, радше, так: любов до ближнього є шляхом до зустрічі з Богом, тоді як відсутність уваги до ближнього &ndash; сліпота щодо Бога.</p>\r\n<p>&nbsp;</p>\r\n<p>17. Справді, ніхто ніколи не бачив Бога таким, яким Він є. Однак, Бог не є цілком невидимим для нас, недоступним нам. Бог першим полюбив нас, як сказано в Посланні Івана, цитованому вище (пор. 4, 10), і ця любов Бога проявилася серед нас, вона стала видимою, бо Він &laquo;свого єдинородного Сина послав у світ, щоб ми жили через Нього&raquo; (1 Ів. 4, 9). Бог став видимим: в Ісусі ми можемо бачити Отця (пор. Ів. 14, 9). Справді, Бога можна бачити різним чином. В історії любові, що її нам розповідає Біблія, Він виходить нам назустріч, Він шукає спосіб &laquo;завоювати&raquo; нас &ndash; аж до Тайної вечері, до пробитого на хресті серця, до об&rsquo;явлень після Воскресіння і до великих діянь, якими Він через апостолів скеровував шлях новонародженої Церкви. Так само в дальшій історії Церкви Господь ніколи не був відсутнім: Він завжди знову й знову виходить нам назустріч &ndash; через людей, в яких об&rsquo;являється, через своє Слово, Святі Таїнства, особливо Євхаристію. У Літургії Церкви, у її молитві, у живій спільноті вірних ми пізнаємо любов Бога, відчуваємо Його присутність і вчимося впізнавати її в нашому щоденному житті. Він бо першим полюбив нас і продовжує першим нас любити; тому ми також можемо відповісти Йому любов&rsquo;ю. Бог не вимагає від нас такого почуття, якого ми не здатні викликати в собі. Він нас любить, Він дозволяє нам побачити і відчути Його любов, в нас також може народитися любов у відповідь на &laquo;першість&raquo; Божої любові.</p>\r\n<p>&nbsp;</p>\r\n<p>Під час цієї зустрічі стає зрозуміло, що любов є не лише почуттям. Почуття приходить і відходить. Почуття може бути чудесною іскрою, але воно не є повнотою любові. На початку ми говорили про процес очищення і дозрівання, через який ерос стає цілковито самим собою, стає любов&rsquo;ю у повному значенні слова. Любов є зрілою, коли залучає весь потенціал людини, враховує її цілісність. Зустріч з видимими проявами Божої любові може викликати в нас почуття радості, яке народжується з усвідомлення того, що нас люблять. Така зустріч також включає нашу волю і наш розум. Пізнання живого Бога є шляхом до любові, і &laquo;так&raquo;, сказане нашою волею Його волі, поєднує волю, розум і почуття в акт всеохоплювальної любові. Однак, це постійний процес: любов ніколи не закінчується, ані не стає абсолютно досконалою; вона змінюється упродовж життя, вона дозріває і саме тому залишається вірною самій собі. Idem velle atque idem nolle [9] &ndash; бажати того самого і відкидати те саме; ось те, що в давнину визнавали істинним змістом любові: коли хтось стає схожим на іншого, це зумовлює спільність бажання і думки. Історія любові між Богом і людиною полягає саме в тому, що така спільність бажання зростає у спільності думки і почуття, і таким чином наше бажання і Божа воля збігатимуться дедалі більше: воля Божа для мене вже не є чужою, не є чимось, що мені нав&rsquo;язують заповіді ззовні, а вона є моєю власною волею, яка базується на досвіді, що справді Бог є ближчим мені, аніж я сам собі [10]. Ось так зростає послух Богові і Бог стає нашою радістю (пор. Пс. 72 [73], 23&ndash;28).</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>18. Таким чином любов до ближнього можлива у сенсі, вказаному в Біблії та об&rsquo;явленому Ісусом. Вона полягає у тому, що в Бозі і з Богом я люблю також того, хто не дуже мені подобається або кого я навіть не знаю. Це можливо лише за умови близької зустрічі із Богом, &ndash; зустрічі, яка сталася завдяки подібності бажань і навіть почуттів. Тоді я вчуся дивитися на іншу особу не тільки своїми очима і сприймати її власними почуттями, а також з перспективи Ісуса Христа: Його друзі є моїми друзями. Оминаючи увагою все зовнішнє в іншій людині, я помічаю її внутрішнє очікування на знаки любові й уваги, чого я не можу дати їй лише за посередництвом організацій, створених для цього, сприймаючи це, можливо, як політичну необхідність. Я дивлюся очима Христа і можу дати іншій людині значно більше від того, що, як видається зовнішньо, їй необхідне: я можу дати їй погляд любові, якого вона потребує. Тут відбувається належна взаємодія між любов&rsquo;ю до Бога і любов&rsquo;ю до ближнього, про яку наполегливо говорить Перше послання Івана. Якщо у моєму житті відсутній зв&rsquo;язок з Богом, я завжди бачитиму в іншій людині лише іншу людину і мені ніколи не вдасться розпізнати в ній Божого образу. Однак, якщо ж я нехтую ближнім, бажаючи бути лише &laquo;побожним&raquo; і виконати свої &laquo;релігійні обов&rsquo;язки&raquo;, тоді мої відносини з Богом охолоджуються: тоді ці відносини будуть &laquo;правильними&raquo;, але позбавленими любові. Лише моя готовність виходити назустріч ближньому і виявляти йому свою любов роблять мене чутливим також і супроти Бога. Тільки служіння ближньому відкриває мені очі на те, що Бог робить для мене і як Він мене любить. Святі &ndash; візьмемо за приклад блаженну Терезу з Калькутти &ndash; знову і знову черпали здатність любити ближнього у зустрічі з Господом в Євхаристії, а ця зустріч набувала свого реалізму і своєї глибини власне завдяки їхньому служінню іншим. Любов до Бога і любов до ближнього нероздільні: це одна заповідь. Обидві живляться любов&rsquo;ю, що походить від Бога, який перший полюбив нас. Отже, вже не йдеться про &laquo;заповіді&raquo;, які приходять ззовні й закликають нас до неможливого, а навпаки, про пізнання любові, подарованої нам зсередини, &ndash; любові, яку, згідно з її природою, слід розділити з іншими. Любов зростає з любові. Любов є &laquo;божественною&raquo;, тому що походить від Бога і єднає нас із Богом, і через цей процес поєднання перетворює нас усіх в &laquo;ми&raquo;, яке підноситься над нашими поділами і змушує нас стати одним, аж до того, щоб, врешті-решт, Бог був &laquo;усім в усьому&raquo; (1 Кор. 15, 28).</p>\r\n<p>&nbsp;</p>\r\n<p>ДРУГА ЧАСТИНА</p>\r\n<p>&nbsp;</p>\r\n<p>CARITAS - МИЛОСЕРДЯ ЦЕРКВИ ЯК &laquo;СПІЛЬНОТИ ЛЮБОВІ&raquo;</p>\r\n<p>&nbsp;</p>\r\n<p>Милосердя Церкви як вияв тринітарної любові</p>\r\n<p>&nbsp;</p>\r\n<p>19. &laquo;Якщо бачиш любов, бачиш Пресвяту Трійцю&raquo;, &ndash; писав св. Августин [11]. У вищеподаних роздумах ми могли споглядати відкритий бік Ісуса, Того, кого &laquo;прокололи&raquo; (пор. Ів. 19, 37; Зах. 12, 10), усвідомлюючи задум Отця, який з любові (пор. Ів. 3, 6) послав свого Єдинородного Сина у світ для спасіння людства. Вмираючи на хресті &ndash; як описує євангелист &ndash; Ісус &laquo;віддав духа&raquo; (Ів. 19, 30) &ndash; завдаток того дару Святого Духа, який Він мав дати після свого воскресіння (пор. Ів. 20, 22). Таким чином повинна була сповнитися обітниця про ріки живої води, які завдяки виливанню Святого Духа мали потекти з нутра вірних (пор. Ів. 7, 38&ndash;39). Адже Дух виявляється через внутрішню силу, що приводить їхні серця у гармонію з серцем Христа і спонукає їх любити ближніх так, як Він їх любив, коли схилявся, щоб вмити ноги апостолам (пор. Ів. 13, 1&ndash;13), а особливо, коли віддав за всіх своє життя (пор. Ів. 13, 1; 15, 13). Дух є також силою, що змінює серце церковної спільноти, щоб вона була свідком любові Отця, який хоче перетворити людство на єдину сім&rsquo;ю у своєму Сині. Вся діяльність Церкви &ndash; це вияв любові, що прагне повного добра для людини: євангелізація Словом і Таїнствами, що часто в історії ставало героїзмом, та сприяння розвитку людини у різних ділянках її життя і діяльності. Отже, любов &ndash; це служіння, яке виконує Церква, щоб безнастанно допомагати людям у їхніх стражданнях і потребах, зокрема матеріальних. На цей аспект, на це служіння, я хотів би звернути увагу у другій частині енцикліки.</p>\r\n<p>&nbsp;</p>\r\n<p>Милосердя як завдання Церкви</p>\r\n<p>&nbsp;</p>\r\n<p>20. Любов до ближнього, закорінена в Божій любові, є передусім обов&rsquo;язком кожного вірного, однак вона також є завданням всієї церковної спільноти, притому на всіх її рівнях: від місцевої спільноти до помісної Церкви і Вселенської Церкви в загальному. Церква як спільнота також повинна плекати любов. У зв&rsquo;язку з цим любові потрібна організація для упорядкованого служіння спільноті. Усвідомлення цього завдання виконувало у Церкві від самого її початку конститутивну роль: &laquo;Всі віруючі були вкупі й усе мали спільним. Вони продавали свої маєтки та достатки й роздавали їх усім, як кому чого треба було&raquo; (Ді. 2, 44&ndash;45). Лука розповідає нам це, подаючи своєрідну дефініцію Церкви, суттєвими елементами якої він вважав вірність &laquo;апостольській науці&raquo; і перебування у &laquo;спільноті&raquo; (koinonia), &laquo;на ламанні хліба й молитвах&raquo; (Ді. 2, 42). Елемент &laquo;спільноти&raquo; (koinonia), спершу не визначений чітко, був конкретизований у вищенаведених стихах: спільнота полягає саме в тому, що вірні мають все спільне і що між ними вже немає поділу на багатих і бідних (пор. також 4, 32&ndash;37). Цю радикальну форму матеріальної спільності, звичайно, годі було втримати, коли Церква почала поширюватися. Однак, її суть залишилася незмінною: у спільноті вірних не може бути такого виду бідності, щоб для когось були недоступними блага, необхідні для гідного життя людини.</p>\r\n<p>&nbsp;</p>\r\n<p>21. Вирішальним вчинком у процесі втілення в життя цього еклезіального основоположного принципу був вибір семи чоловіків, який започаткував дияконське служіння (пор. Ді. 6, 5&ndash;6). У первісній Церкві між групами, які говорили єврейською і грецькою мовами виникла нерівність у наданні щоденної опіки для вдів. Апостоли, яким насамперед була доручена &laquo;молитва&raquo; (Євхаристія і Літургія) та &laquo;служіння Слова&raquo;, почувалися надмірно обтяженими &laquo;служінням при столах&raquo;; тому вони вирішили скерувати зусилля на своє основне служіння, а для нового завдання, також потрібного Церкві, створити колегію з семи осіб. Ця група повинна була виконувати не лише суто технічну функцію роздавання дібр: це мали бути мужі &laquo;повні Духа та мудрості&raquo; (пор. Ді. 6, 1&ndash;6). Це означає, що вони мали виконувати конкретне соціальне служіння, яке водночас було вповні духовним служінням. Їхня роль була справді духовною: в ній реалізувалося одне із основоположних завдань Церкви &ndash; впорядкована любов до ближнього. З утворенням колегії сімох &laquo;дияконія&raquo; &ndash; спільне організоване служіння з любові до ближнього &ndash; стала складовою фундаментальної структури Церкви.</p>\r\n<p>&nbsp;</p>\r\n<p>22. З часом, коли Церква почала поступово розширюватися, сaritas поряд зі звершенням Святих Таїнств і проголошенням Слова став одним з істотних секторів її діяльності: діла милосердя супроти вдів і сиріт, в&rsquo;язнів, хворих і нужденних становлять її суть такою самою мірою, як звершення Святих Таїнств і проголошення Слова. Церква не може знехтувати ділами милосердя, як не може не звершувати Таїнств і не проголошувати Слова. Щоб проілюструвати це, достатньо навести декілька прикладів. Мученик Юстин (&dagger;155), описуючи християнське святкування неділі, згадує про благодійну діяльність, пов&rsquo;язану з Євхаристією: багатші складали пожертви мірою своїх можливостей, хто скільки хотів; з тих пожертв єпископ надавав підтримку сиротам, вдовам і тим, хто потребував допомоги через хворобу чи з інших причин, а також в&rsquo;язням та іноземцям [12]. Великий християнський письменних Тертуліан (&dagger; після 220) розповідав, як захоплювалися погани турботою християн про тих, хто потребував допомоги [13]. Якщо Ігнатій Антіохійський (&dagger; близько 117) називає Римську Церкву &laquo;першою в любові&raquo; (аgape) [14], то можна впевнено стверджувати, що у цьому визначені він хотів певним чином вказати на її конкретну благодійну діяльність.</p>\r\n<p>&nbsp;</p>\r\n<p>23. У цьому контексті буде доцільним згадати ранні легальні структури благодійності Церкви. Приблизно в середині IV століття в Єгипті сформувалася так звана &laquo;дияконія&raquo; &ndash; інституція, відповідальна в поодиноких чоловічих монастирях за всю сферу допомоги, тобто милосердного служіння &ndash; caritas. Такі інститути розвивалися аж до VI століття, формуючи об&rsquo;єднання з правовим статусом, яким держава довіряла навіть частину збіжжя для роздавання людям. В Єгипті не тільки кожен монастир, але й кожна єпархія мала свою дияконію, яка пізніше поширилася як на Сході, так і на Заході. Папа Григорій Великий (&dagger; 604) згадував зокрема про дияконію в Неаполі. У Римі дияконію зафіксовано щойно з VII&ndash;VIII століть; але, звісно, вже раніше, від самого початку до діяльності Римської Церкви належала опіка над нужденними і хворими відповідно до принципів християнського життя, вміщених в Діяннях апостолів. Живим прикладом цього є постать диякона Лаврентія (&dagger; 258). Драматичний опис його мучеництва був відомий вже св. Амброзію (&dagger; 397) і представляє нам істинну суть святого. Після арешту його співбратів і Папи йому, як відповідальному за забезпечення бідних, дали ще певний час, щоб він зібрав церковні скарби і передав їх світським інстанціям. Лаврентій поділив гроші, які мав, між бідними, а потім разом з ними &ndash; як зі справжнім скарбом Церкви &ndash; став перед можновладцями [15]. Не залежно від оцінки історичної достовірності тих обставин, Лаврентій залишився у пам&rsquo;яті як видатний представник церковної благодійності.</p>\r\n<p>&nbsp;</p>\r\n<p>24. Згадка про постать цезаря Юліана Відступника (&dagger; 363) ще раз може вказувати, наскільки важливою була для ранньої Церкви впорядкована благодійна діяльність. Коли Юліанові було шість років, гвардія цезаря вбила його батька, брата та інших рідних. Відповідальність за цю жорстокість &ndash; справедливо чи безпідставно &ndash; він приписав імператору Костянтину, якого вважали великим християнином. Через це християнська віра була для нього раз і назавжди дискредитована. Ставши імператором, він вирішив відродити поганство, стару римську релігію, реформувавши її так, щоб вона справді могла стати справжньою потугою держави. Притому він дуже багато запозичував у християнства. Встановив ієрархію з митрополитів і священиків. Священики мали плекати любов до Бога і ближнього. В одному із своїх листів [16] він написав, що єдиним елементом у християнстві, який припав йому до душі, є благодійна діяльність Церкви. Отже, для нового поганства вирішальним пунктом було на противагу милосердю Церкви запровадити аналогічну активність у рамках його релігії. &laquo;Галілейці, &ndash; так він говорив, &ndash; таким чином здобули світ&raquo;. До них треба дорівнятися чи навіть перевершити їх. Таким чином імператор стверджував, що милосердна любов &ndash; сaritas &ndash; основна ознака християнської спільноти, Церкви.</p>\r\n<p>&nbsp;</p>\r\n<p>25. У цьому пункті належить звернути увагу на два важливі моменти наших роздумів: a) суть Церкви виявляється у потрійному завданні: проповідуванні Божого Слова (kerygma-martyria), здійсненні Таїнств (leiturgia) і вчинках любові (diakonia). Ці завдання тісно між собою пов&rsquo;язані і їх не можна розділяти. Вияв милосердя є для Церкви не одним із видів благодійності, яку можна доручити комусь іншому, а належить до її суті, є проявом її справжнього буття [17]; b) Церква &ndash; це Божа сім&rsquo;я у світі. У цій сім&rsquo;ї не може бути нужденних. Водночас, однак, сaritas-аgape виходить за межі Церкви: притча про милосердного самарянина залишається мірилом, передбачає універсальність любові, скерованої до того, хто потребує допомоги, до &laquo;випадкового зустрічного&raquo; (пор. Лк. 10, 31), хоч би ким він був. Поряд з такою заповіддю церковним завданням також залишається, щоб у Церкві як родині не страждав жоден з її членів. Підкреслюють це слова з Послання до галатів: &laquo;Отож, поки маємо час, робімо добро всім, а зокрема рідним у вірі&raquo; (6, 10).</p>\r\n<p>&nbsp;</p>\r\n<p>Справедливість і любов</p>\r\n<p>&nbsp;</p>\r\n<p>26. З XIX століття почали з&rsquo;являтися закиди стосовно благодійності Церкви, які потім настійливо поглиблював марксизм. Убогі, мовляв, потребують не діл милосердя, а справедливості. Діла милосердя &ndash; милостиня &ndash; насправді є для заможних людей засобом і способом, за допомогою якого вони можуть уникнути утвердження справедливості і заспокоїти сумління, щоб зберегти своє становище, позбавляючи бідних їхніх прав. Замість того, щоб підтримувати таку ситуацію поодинокими ділами милосердя, належить встановити справедливий лад, за якого всі отримували б свою частку дібр світу і більше не потребували б милостині. Слід визнати, що у цій аргументації дещо правильне, але є багато хибного. Справді, основоположним принципом держави має бути прагнення встановити справедливість і метою справедливого суспільного ладу, згідно з принципом субсидіарності, є забезпечення кожній людині її частки добра спільноти. На цьому завжди наголошувалося у християнському вченні про державу і в соціальній науці Церкви. Питання про справедливий суспільний лад &ndash; з історичної точки зору &ndash; увійшло в нову фазу в ХІХ столітті, коли було сформовано індустріальне суспільство. Поява сучасної індустрії захитала старі суспільні структури, а зростання числа залежних від зарплатні робітників зумовило радикальні зміни в укладанні суспільних відносин, в яких основним питанням стало відношення капіталу і роботи &ndash; у такій формі це питання до того часу не ставили. Структури виробництва і капітал стали новою владою, яка, потрапивши в руки незначної кількості людей, вела до того, що робочі маси втрачали свої права; і цьому треба було протистояти.</p>\r\n<p>&nbsp;</p>\r\n<p>27. Слід визнати, що пройшло багато часу, поки представники Церкви усвідомили, що питання справедливого суспільного ладу постало в новий спосіб. Однак у цій ділянці були піонери: один із них - Кеттер з Майнца (+1877). У відповідь на конкретні потреби з&rsquo;являлися г</p>\r\n<p>&nbsp;</p>\r\n<p>ЗАКІНЧЕННЯ</p>\r\n<p>&nbsp;</p>\r\n<p>40. Поміркуймо наприкінці над життям святих, які проявляли милосердя у винятковий спосіб. Передусім спадає на думку св. Мартин Турський (+397), спершу солдат, згодом &ndash; монах і єпископ: майже як ікона він показував неповторну вартість індивідуального свідчення милосердя. Поблизу міської брами Ам&rsquo;єна св. Мартин розірвав надвоє свій плащ, щоб поділитися ним з бідняком: наступної ночі сам Ісус явився йому уві сні, одягнутий у цей плащ, щоби підтвердити вічне значення євангельських слів: &laquo;Бо я нагий був, і ви мене одягли&hellip; Усе, що ви зробили одному з моїх братів найменших &ndash; ви мені зробили&raquo; (Мт. 25, 36. 40). В історії Церкви можна назвати безліч інших свідчень милосердя! Зокрема, увесь рух монашества, починаючи від св. абата Антонія (+356), представляв величезне служіння милосердю щодо ближнього. Зустрівшись &laquo;віч-на-віч з Богом&raquo;, який є любов, монах відчував чітку вимогу присвятити все своє життя служінню, &ndash; служінню Богові і ближньому. Саме цим можна пояснити заснування поблизу монастирів будинків, де приймали, лікували й доглядали убогих. Цим також можна пояснити ініціативи щодо людського розвитку і християнського виховання, призначенні передусім для служіння найубогішим: їх протягом довгої історії Церкви брали на себе спершу монаші й жебрацькі ордени, а згодом &ndash; чоловічі й жіночі монаші конгрегації. Постаті святих, зокрема Франциска Ассізького, Ігнатія Лойоли, Івана Божого, Камілла Лелліса, Вінкентія де Пауло, Луїзи де Маріллак, Дона Боско, Йосифа Б. Коттоленго, Луїджі Оріоне, матері Терези з Калькутти &ndash; і це лише кілька імен з безлічі &ndash; є яскравим взірцем чинного милосердя для всіх людей доброї волі. В історії ці святі є справжніми носіями світла, бо вони &ndash; люди віри, надії і любові.</p>\r\n<p>&nbsp;</p>\r\n<p>41. Серед усіх святих найбільшу перевагу має Марія, Матір Господа, яка є дзеркалом усякої святості. З Євангелія від Луки знаємо, що Вона доглядала за своєю родичкою Єлизаветою, в якої перебула &laquo;місяців зо три&raquo; (1, 56), під час останнього терміну Єлизаветиної вагітності. &laquo;Magnificat anima mea Dominum&raquo;, &ndash; сказала Вона під час цих відвідин &ndash; &laquo;Величає душа моя Господа&raquo; (Лк. 1, 46). Так Вона висловила всю свою життєву програму &ndash; ставити в центрі не себе, а Бога, з яким Вона зустрічалася як у молитві, так і в служінні ближньому, &ndash; все заради того, щоб світ став кращим. Велич Марії полягала саме в тому, що Вона прагнула цієї величі не для себе, а для Бога. Вона смиренна: не бажала бути ніким іншим, а тільки слугинею Господньою (пор. Лк. 1, 38.48). Вона знала, що бере участь в спасінні світу не шляхом реалізації власних задумів, а лише цілковито віддаючись в розпорядження Божої волі. Вона є жінкою надії: лише тому, що Вона вірить в Божу обітницю і чекає на спасіння Ізраїлю, ангел може прийти до Неї і покликати Її до служіння цій обітниці. Це жінка віри: &laquo;Щаслива та, що повірила&raquo;, &ndash; сказала до Неї Єлизавета (Лк. 1, 45). Magnificat &ndash; цей, так би мовити, портрет Її душі повністю вигаптовано нитками Святого Письма, &ndash; нитками Божого Слова. Отже, зрозуміло, що в Божому Слові Марія &ndash; як у себе дома, звідси Вона цілком природно виходить і сюди ж повертається. Вона говорить і думає згідно з Божим Словом; Слово Боже стає Її словом, і Її слово народжується з Божого Слова. Навіть більше, саме так виявляється, що Її думки співзвучні з Божими думками і що Її воля полягає у тому, щоб бажати того, чого бажає Бог. Будучи цілковито просякнутою Божим Словом, Вона змогла стати Матір&rsquo;ю воплоченого Слова. Нарешті, Марія &ndash; це жінка, яка любить. Та й чи могло бути інакше? Як віруюча людина, яка у вірі думає Божими думками і бажає сповнення Божої волі, Вона могла бути лише люблячою жінкою. Це виявляється в її тихих вчинках, про які йдеться в євангельських розповідях про дитинство. Ми бачимо це в тій делікатності, з якою Вона помічає незручну ситуацію, що в ній опинилися наречені у Кані Галілейській, і звертає на це увагу Ісуса. Ми бачимо це в покорі, з якою Вона перебуває в тіні під час публічного життя Ісуса, знаючи, що Її Син повинен заснувати нову спільноту і що година Його Матері настане лише під хрестом, який і буде справжньою годиною Ісуса (пор. Ів. 2, 4; 13, 1). Отоді, коли учні втечуть, Вона стоятиме при хресті (пор. Ів. 19, 25&ndash;27); згодом, під час П&rsquo;ятдесятниці, учні зберуться навколо Неї в очікуванні Зіслання Святого Духа (пор. Ді. 1, 14).</p>\r\n<p>&nbsp;</p>\r\n<p>42. До життя святих належить не тільки їхня земна біографія, а і їхнє життя та їхні діяння в Бозі після смерті. У святих є очевидним, що той, хто йде до Бога, не віддаляється від людей, а навпаки, наближається до них. Краще, ніж в будь-якій іншій людині, це видно на прикладі Марії. Слова Розіп&rsquo;ятого, сказані до учня Івана, а через нього до всіх учнів Ісуса: &laquo;Ось Матір твоя&raquo; (Ів. 19, 27) &ndash; залишаються назавжди, незважаючи на зміну поколінь, правильними. Справді, Марія стала Матір&rsquo;ю всіх віруючих. До Її материнської доброти, як і до Її чистоти та непорочної краси, звертаються люди всіх часів у всіх куточках світу зі своїми потребами і сподіваннями, радощами і стражданнями, на самоті і в спільноті. Вона безупинно дарує Їм свою доброту й безмежну любов, що витікає з глибини Її серця. Свідченням вдячності, яке виходить від людей різних культур на різних континентах, є визнання цієї чистої любові, яка шукає не себе, а лише того, що є добре. Водночас почитання Її з боку вірних є безпомилковим способом уможливлення цієї любові: вона народжується із тісного зв&rsquo;язку з Богом, через якого Він наповнює нас до краю &ndash; ось умова, що дає змогу тому, хто напився з джерела Божої любові, самому стати джерелом &laquo;води живої&raquo; (Ів. 7, 38). Діва Марія, Мати Божа, показує нам, чим є любов, звідки вона походить і звідки постійно бере нові сили. Їй ми довіряємо Церкву, її місію служіння Любові:</p>\r\n<p>&nbsp;</p>\r\n<p>Свята Маріє, Мати Божа, Ти дала світові справжнє світло, Ісуса, свого сина &ndash; Сина Божого. Ти цілковито зреклася себе на заклик Бога і стала сама джерелом доброти, що витікає з Нього. Покажи нам Ісуса. Приведи нас до Нього. Навчи нас визнавати й любити Його, щоб і ми стали здатними на справжню любов і були джерелом води живої у спраглому світі.</p>\r\n<p>&nbsp;</p>\r\n<p>Дано в Римі, у соборі Святого Петра, 25 грудня 2005 року, на свято Різдва Христового, у перший рік мого понтифікату.</p>\r\n<p>&nbsp;</p>\r\n<p>Венедикт XVI</p>\r\n<p>&nbsp;</p>\r\n<p>http://www.caritas-ua.org/?pwt=136</p>\r\n<p>&nbsp;</p>','DEUS CARITAS EST ','uk',326,1306,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Звернення Блаженнішого ЛЮБОМИРА,</p>\r\n<p>&nbsp;</p>\r\n<p>Верховного Архиєпископа Києво-Галицького Української Греко-Католицької Церкви, з нагоди тисяча двадцятої річниці Хрещення Київської Русі</p>\r\n<p>&nbsp;</p>\r\n<p>http://www.ugcc.org.ua</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Дорогі в Христі!</p>\r\n<p>&nbsp;</p>','2008-05-15 08:48:25',NULL,'P','<p>Звернення Блаженнішого ЛЮБОМИРА,</p>\r\n<p>&nbsp;</p>\r\n<p>Верховного Архиєпископа Києво-Галицького Української Греко-Католицької Церкви, з нагоди тисяча двадцятої річниці Хрещення Київської Русі</p>\r\n<p>&nbsp;</p>\r\n<p>http://www.ugcc.org.ua</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Дорогі в Христі!</p>\r\n<p>&nbsp;</p>\r\n<p>Цього року офіційно відзначаємо 1020-ту річницю Хрещення Київської Русі. І хоч це не вповні кругла дата, варто звернути на неї увагу. Урочисті святкування, присвячені тисячоліттю хрещення, у 1988 році відбувалися в Москві, бо так було вигідно окупантам України. Сьогодні, коли ми є господарями власної землі, можемо розпоряджатися нашим часом. Таку знаменну подію, як Хрещення Київської Русі, треба було відзначати вже десять років тому, але, можливо, ще тоді ми були не вповні до того готові. Тепер, думаємо, настав слушний час.</p>\r\n<p>&nbsp;</p>\r\n<p>Хрещення, яке було обов\'язковим для тодішньої держави, хоч, цілком природно, не було всюди здійснене одночасно, спонукає нас призадуматися. Передусім ми повинні у цей ювілейний рік усвідомити, що офіційне державне хрещення &ndash; це не початок християнства на території України. Християнство було знаним в Україні майже від його початків і поширювалося через своїх і чужоземних торговців та подорожніх. Цікавим для нас є розповідь &ndash; загальновідома як легенда, але, можливо, не цілком позбавлена історичних підстав &ndash; про перебування святого апостола Андрія на київських горах. Сьогодні важко науково довести достовірність тих відвідин, однак факт, що щось таке закарбувалося у свідомості наших предків правдоподібно ще далеко перед офіційним прийняттям християнства, не може бути випадковістю.</p>\r\n<p>&nbsp;</p>\r\n<p>Часто, представляючи історичний розвиток нашого народу, твердять, немовби християнство стало причиною утворення Київської держави. Радше навпаки, вже існуюча Київська держава стала великою і сильною, бо прийняла християнство. Християнство, з одного боку, стало чинником, який надав цій державі особливого характеру, свідченням чого, для прикладу, була відсутність смертної кари у законодавстві та властивої для поганських часів геополітичної агресії. А з другого боку &ndash; завдяки прийняттю християнської віри Київська Русь могла нав\'язати широкі безпосередні стосунки із усіма державами Європи, свідченням чого є подружні зв\'язки дітей князя Ярослава в різних кутках тодішньої Європи. Сьогодні можемо сміливо стверджувати: Київська Русь, як і її прямий спадкоємець &ndash; Українська держава &ndash; є європейською державою не тільки тому, що знаходиться на цьому європейському географічному континенті, а головно тому, що дуже високою мірою, незважаючи на татарські та комуністичні лихоліття, зберегла християнські вартості, які характеризують насамперед духовну, а вже потім політичну культуру Європи.</p>\r\n<p>&nbsp;</p>\r\n<p>Офіційне християнство, яке свідомо плекали найвизначніші державні провідники упродовж століть, ґрунтовно просякло нашу культуру. Виключити християнство - означає зруйнувати нашу культуру дощенту. Таку мету ставив перед собою комуністичний режим, намагаючись досягти її через переписування на свій лад наших літературних творів, руйнування надбань архітектури та іконописного мистецтва тощо. Він завдав глибоких ран, але не вирвав Бога із людських сердець. Сьогодні нашим великим завданням є відбудувати наші храми, належно їх розписати, очистити художні твори всіх родів від безбожницької зматеріалізованої халтури.</p>\r\n<p>&nbsp;</p>\r\n<p>Немає найменшого сумніву, що християнство було домінантною релігією у формуванні культури нашого народу, однак упродовж першого тисячоліття християнської ери вже також існували відомі на території сучасної України інші монотеїстичні релігії. І сьогодні наша держава не є суто християнською, але це не означає, що святкування 1020-ї річниці прийняття християнства як офіційної державної релігії є святом лише для нас, християн. Прибічники інших світових релігій повинні радіти разом з нами, бо для кожної щиро віруючої людини сам факт, що народ вірує, тішиться своєю вірою та сумлінно і послідовно старається бути релігійним, є достатньою підставою поділяти загальну радість.</p>\r\n<p>&nbsp;</p>\r\n<p>Україна сьогодні є багатоконфесійною. Однак, це не є свідченням її безбожництва. Різні щодо часу та наміру соціологічні опитування, проведені професійними організаціями, подають відрадну інформацію, що незважаючи на жахливі роки комуністичного режиму, в народі збереглася пам\'ять про Бога і про Христа. Так, для прикладу, цього року понад чотири п\'ятих населення України заявили, що для них Великдень &ndash; це свято, а не просто вихідні.</p>\r\n<p>&nbsp;</p>\r\n<p>Знаючи, що далеко не всі громадяни України належать до християнських Церков, ми повинні звернути увагу на таку заяву і дуже серйозно застановитися, як тим людям, які не належать до організованих релігійних кіл, повніше представити Божу науку. Саме ювілейний рік, щоб він не обмежився тільки святкуванням, має спонукати християнські Церкви до цієї важливої справи. Бо останніми десятиліттями в Україні з\'явилося багато релігійних новоутворень, які, відповідаючи на ту чи іншу релігійну потребу людини, притягують її до себе, однак не здатні людину вповні задовольнити. Натомість християнство є не відповіддю на поодинокі потреби людського життя, а цілісною релігією, яка охоплює всі види людського життя і не повинна бути представлена фрагментарно. Тому християнські Церкви повинні звернути на це особливу увагу і усім тим, хто зберіг лише поверхово, може, навіть тільки через якісь зовнішні звичаї пам\'ять про Христа, представити Його та Його науку в повноті, щоб допомогти їм знову відкрити для себе релігійну сторінку людського існування. Ювілейний 1020-й рік є для цього чудовою нагодою.</p>\r\n<p>&nbsp;</p>\r\n<p>Такі надзвичайно важливі аспекти життя нашого народу, як державна політика, наука, мистецтво &ndash; все те, що творить культуру, не може бути єдиною темою роздумів упродовж цього ювілейного року. Підставою святкувань є наші зрілі, свідомі, задіяні у практичному житті релігійні переконання. Якщо ми про це не пам\'ятатимемо, то всі важливі аспекти особистого, родинного, суспільного, а особливо державного життя можуть втратити свою життєдайну цінність або стати іграшками геополітики.</p>\r\n<p>&nbsp;</p>\r\n<p>Відзначення Хрещення Київської Русі повинно нагадати нам про ще один дуже важливий аспект, який здійснюється у нашому житті, коли ми щиро і глибоко приймаємо правди християнської релігії, а саме &ndash; про свободу духу. Всякі тоталітарні режими упродовж віків поборювали християнство, бо вони боялися свободи. Щось подібне властиве й усім псевдорелігіям, які сковують дух людини, позбавляючи її можливості свобідно зріло перевірити свій зміст.</p>\r\n<p>&nbsp;</p>\r\n<p>Типовою ознакою спотвореної релігійності є почуття страху. Багато з нас ще дуже чітко пам\'ятає імперію, яка володіла мільйонами людей, занурюючи їх у почуття безнадійного страху. Упродовж століть також різного роду псевдорелігійні рухи або навіть неправильно зрозуміла правдива релігія старалися контролювати своїх прибічників через це негативне почуття і в такий спосіб нібито вести їх до Небесного Царства. Таке часто трапляється ще й сьогодні у спільнотах, які видають себе за релігійні, але полонять своїх членів страхом. Тому, якщо хочемо цього року бачити християнську релігію у всій її красі, треба через її правильне розуміння подолати всякі форми страху, якими різні сили хотіли б ув\'язнити дух нашого народу.</p>\r\n<p>&nbsp;</p>\r\n<p>Жодний свідомий громадянин України не сміє оспорювати важливості Хрещення, здійсненого за дорученням святого рівноапостольного князя Володимира для Київської Русі та сьогоднішньої України. Ми знаємо, що наші предки цю знаменну подію відзначали дуже урочисто в міру своїх можливостей і обставин щоп\'ятдесят років, бо добре усвідомлювали всебічне значення цього акту. Однак ми, розуміючи зміст і властиве значення акту більше ніж тисячолітньої давнини, повинні відзначати його щороку, і то не тільки зовнішньою помпезністю, а й нашим щоденним життям. Не можна допустити, щоб воно було забруднене політичними міркуваннями чи розрахунками. Ми мусимо втримати його принаймні на тому духовному та релігійному рівні, з яким Хрещення відбувалося 1020 років тому і намагатися з року в рік на підставі того, що сталося з нашим народом за часів святого князя Володимира, робити щорічне святкування дедалі святішим, як належить справжнім його спадкоємцям не тільки за тілом, а й передусім за духом.</p>\r\n<p>&nbsp;</p>\r\n<p>Дорогі в Христі, вітаймо ініціативу урочистого святкування Хрещення Київської Русі. Це саме Боже провидіння нагадує нам про те, що в житті кожного із нас, наших родин, наших громад, наших сіл і міст, нашого народу та всієї нашої держави найважливіше бути з Богом. Не проґавмо цієї важливої нагоди. Нехай ніщо минуще чи другорядне не відверне нашої уваги від того, що в людському житті є найбільш суттєвим. Гляньмо на всіх людей очима віри. Пам\'ятаймо, що це наші брати і сестри у Господі, хоч би ким вони були, і треба врахувати їхні права та шанувати їхню гідність як Божих дітей. Виявляймо до них свою зичливість. Святий рівноапостольний князь Володимир за якимсь особливим натхненням із неба поставив нас на шлях, що веде до Бога. Нехай ніщо із цього шляху нас не зіб\'є. Відречімося від сатани та всіх його діл. З\'єднаймося з Христом та поклонімося Йому.</p>\r\n<p>&nbsp;</p>\r\n<p>Христос воскрес!</p>\r\n<p>&nbsp;</p>\r\n<p>Від імені Синоду Єпископів</p>\r\n<p>&nbsp;</p>\r\n<p>Української Греко-Католицької Церкви</p>\r\n<p>&nbsp;</p>\r\n<p>+ ЛЮБОМИР</p>\r\n<p>&nbsp;</p>\r\n<p>Дано у місті Києві, при соборі Воскресіння Христового,</p>\r\n<p>&nbsp;</p>\r\n<p>7 травня 2008 року Божого, 1020-го від Хрещення Русі-України</p>\r\n<p>&nbsp;</p>','Звернення Блаженнішого ЛЮБОМИРА з нагоди тисяча двадцятої річниці Хрещення Київської Русі','uk',381,1361,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Звіт учениці недільної школи церкви св. Миколая на Аскольдовій могилі про подорож до місць, де лікувався генерал Манергейм<p/><p>Here is a report of a student of the Sunday school of the St. Nicholas Church (located at the Askoldova Mohyla historic site in Kiev) about her trip to the Czech Republic.  <p/><p>Божий Дар<p/>','2008-08-28 15:19:46',NULL,'P','<p>Звіт учениці недільної школи церкви св. Миколая на Аскольдовій могилі про подорож до місць, де лікувався генерал Манергейм<p/><p>Here is a report of a student of the Sunday school of the St. Nicholas Church (located at the Askoldova Mohyla historic site in Kiev) about her trip to the Czech Republic.  <p/><p>Божий Дар<p/><p>Ми поселилися в мальовничому містечку Божий Дар, що знаходиться на кордоні з Німеччиною, де взимку займаються горнолижним спортом, неподалік від Карлових Вар у 3-поверховому парафіяльному будиночку.<p/><p>Кожного дня ми мали можливість бути на службі Божій та на духовних навчаннях в костелі св. Анни, гарному величному храмі XVIII століття з красивими скульптурами всередині, де панував незвичайний спокій.<p/><p>Місцевість мальовнича, з гарною чистою гірською природою. В кількох хвилинах пішки від нашого місця проживання – плантації чорниць. Чудесна нагода поласувати ягодами та милуватися недоторканим краєвидом. <p/><p>Тепла річка<p/><p>В частих екскурсіях до Карлових Вар (також і до Праги) нас допроводжував пан Дмитро Серба, пасторальний асистент греко-католицької церкви від храму св. Андрія у Карлових Варах. Ми змогли відвідати місцеві старовинні храми, цвинтар, де був похований син Моцарта, відкритий басейн з гарячою мінеральною водою „Термал“, ну і звичайно – гарячі лікувальні джерела. Вода насичена різними мінералами з температурою від 78°C до 30°C допомагає при різних внутрішніх хворобах. Джерела в лікувальних цілях використовуються вже більше 400 років. Їх відкрив Карел IV, король Чехії з XV століття! Тому місто називається – Карлові Вари.<p/><p>Через місто тече річка Тепла. Але це тільки назва – річка насправді холодна. Кожного року тут проходять змагання на байдарках. В центрі, біля головного джерела  та старої деревяної Колонади з XVIII століття, знаходиться памятник, збудований на честь святої Трійці. Він був встановлений після епідемії чуми на знак подяки тих людей, які вижили. <p/><p>Також відвідали та молилися в римо-католицькому храмі св. Марії Магдалини і звичайно в нашому греко-католицькому храмі св. Андрія, який зараз цілком ремонтується – найстаріший храм XV століття в Карлових Варах .<p/><p>Пражський Град<p/><p>12.08.2008 у вівторок була велика цікава екскурсія до Праги – столиці Чеської республіки. Прага – старовинне чудове величезне місто з красивою архітектурою, надзвичайними храмами та особливою духовною атмосферою. Першим ми відвідали „Пражський Град“, де є резиденція-палац президента республіки з катедральним собором св. Віта X століття, потім перебудованим у готичному стилі. Велич храму вражає: надзвичайної краси ікони, вітражі, скульптури святих, розпяття, усипальниці королів, святих та єпископів – все було як не з цього світу, все вказувало на святість цього особливого місця та людей, які це все з Божою поміччю зтворили...<p/><p>Запам`яталися відвідини музею іграшок, де ми залюбки переглянули велику колекцію ляльок, машинок, меблів, посуду, літаків та всякої дитячої всячини з минулого та позаминулого століття. Особливо вразила експозиція ляльок Барбі. Вони вбрані в найрізноманітніший одяг – від простеньких футболок та спідничок аж до екстравагантних плать та маскарадних костюмів. Лялька Барбі нещодавно відсвяткувала 56 років!<p/><p>Гордість короля<p/><p>Пішохідний Карлів міст через Влтаву потребує особливої уваги. І не тільки тому, що він гарний та величний а тому, що він старий – вже майже 600 років – один з найстаріших європейських готичних мостів! Вздовж нього красуються скульптури всіх святих патронів (покровителів) Чехії. Карел IV особисто слідкував за його будівництвом – по зірках визначив точний час та розташування мосту. Цим пояснюють надзвичайну міцність споруди і скульптур, які дуже добре збереглися до наших днів. <p/><p>Клементінум<p/><p>На другому боці Влтави, за Карловим мостом та костелом св. Франтішка з Ассізі, розташований величний комплекс в стилі Барокко – Клементінум. В 1232 році споруди належали отцям Домініканам, в році 1558 - отцям Єзуітам. З року 1773 це університетська бібліотека, а зараз національна бібліотека, де зберігаються книги, декотрим з яких біля 2000 років! Також в його ареалі розташовуються: римо-католицький храм св. Сальватора та головна греко-католицька катедра святого Клемента. При вході до нашої катедри отці священники роздавали запрошення на концерт органної музики. Шкода, що у нас було обмаль часу і ми не змогли потрапити ні на коцерт, ні до храму – був перед службою зачинений. <p/><p>Старе намєсті<p/><p>На площі „Старе намєсті“ біля широковідомого годинника споруджений памятник Янові Гусу (XV століття), котрий поборював тогочасні порядки римської церкви – вважав, що богослужба повинна звершуватися мовою народу (чеською), підтримував бідних та заперечував накопичування матеріального багатства.<p/><p>12 апостолів і смерть з Орлоя<p/><p>На черзі, наприкінці нашої чудової подорожі, відкриттям для нас став знаменитий годинник Орлой. Над неповторним середньовічним циферблатом розташувались два віконечка, в яких кожної години зявляються 12 апостолів, а смерть у вигляді людського скелета дзвонить у дзвіночок, начебто в останній раз – попереджає, що вона може прийти коли завгодно і без попередження... Це триває лише кілька секунд, проте запамяталося на все життя.<p/><p>На цій містичній ноті закінчилась наша мандрівка старовинною Прагою. Бажаємо ще хоч раз відвідати це чудове місто, де віє сивою давниною і водночас ультрасучастністю.<p/><p>Велич Дмитра і Вероніки<p/><p>Особлива подяка пану Дмитру Сербі та його доньці Вероніці, які нас всюди проводили, розповідали багато цікавого та зворушливого. Також дякуємо нашому доброму Богу, що нам дав можливість бачити чужу цікаву країну з дивовижною стародавньою історією. А також велика подяка всім: отцям, їхнім дружинам та спонсорам, всім, хто нам допоміг побачити цей дивовижний куточок світу»<p/><p> 06.08 -13.08 2008 р.Б. <p/><p>Марта Остапів, спеціально для «Оранти»<p/><p>Київ – Божий Дар, Карлові Вари, Прага – Київ <p/><p>На фото: паломники з Аскольдової могили під дзигарем Орлой<p/><p>Довідка «Оранти»<p/><p>«У серпні 10-ро дітей і три виховника на парафіяльому мікроавтобусі церкви св. Миколая на Аскольдовій могилі подорожували Чехією і проводили заняття з катехитичного навчання у Карлових Варах. 06.09. 2008 вони прибули до Карлсбада. <p/><p>Опікувалися паломниками керівник групи о. Ігор Онишкевич (декан, парох церкви св. Миколая, Київ), о. Андрій Пенюк (декан, парох церкви св. Андрія, Карлсбад) та  катехит Дмитро Серба (пасторальний асистент, Карлсбад). <p/>',' Теплa річка з холодною водою','uk',433,1413,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Кажу ж вам, що за кожне слово пусте, яке скажуть люди, дадуть вони відповідь судного  дня! Бо зо слів своїх будеш виправданий, і зо слів своїх будеш  засуджений. Мф,12:36,37</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2008-02-11 08:25:30',NULL,'P','<p>Кажу ж вам, що за кожне слово пусте, яке скажуть люди, дадуть вони відповідь судного  дня! Бо зо слів своїх будеш виправданий, і зо слів своїх будеш  засуджений. Мф,12:36,37</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Все частіше та частіше промовляємо один одному слова: &laquo;Не кажи такі слова!&raquo; І все частіше та частіше переконуємося у наслідках безвідповідального, неусвідомленого вживання слів в своєму житті, не пам&rsquo;ятаючи, що за кожне вимовлене слово ми дамо відповідь перед Богом (Мф.12:36,37). Коли дамо відповідь? Зараз, в ту саму мить, коли вимовили, а, точніше, посіяли, і маємо зняти стократний врожай. Який? Судіть самі &ndash; з того, що відбувається в вашому житті, в роботі, у стосунках у родині, в країні, зарештою, - на скільки ви задоволені результатом?</p>\r\n<p>&nbsp;</p>\r\n<p>Що б людина не робила в своєму житті, чим би не займалася, вона неминуче користується мовою, словами. Навіть коли мовчить &ndash; її думки течуть в образах, яким відповідають певні слова. Слова відображають наші емоції, почуття, преживання, і не тільки.</p>\r\n<p>&nbsp;</p>\r\n<p>Словами пронизані всі аспекти людського буття.</p>\r\n<p>&nbsp;</p>\r\n<p>В слові закладені смерть та життя, ганьба та слава, добробут родини, любов, надія. ВСІ дива, ВЕСЬ план Всесвіту, ВСЯ творчисть закладені в словах.</p>\r\n<p>&nbsp;</p>\r\n<p>Наше здоров&rsquo;я, багатство, рівень життя, душевна рівновага залежать від того, що ми промовляємо і що думаємо, а головне &ndash; як ми це робимо.</p>\r\n<p>&nbsp;</p>\r\n<p>Я неодноразово задавала собі питання: Для чого чоловік чи  жінка, а іноді навіть дитина, - створюють собі проблеми, із яких потім не в змозі вибратися самостійно, чи хвороби, від яких іноді навіть гинуть?.</p>\r\n<p>&nbsp;</p>\r\n<p>Чи можемо ми змінити себе настільки, щоб змінився наш світ, наше буття, наше оточення? І головне &ndash; як зрозуміти, що потрібно змінювати? Ось головне питання!</p>\r\n<p>&nbsp;</p>\r\n<p>Насправді, цей світ існує та розвивається за суворими законами, і фундаментом цієї законодавчої бази є Бог, чи Слово, а також Закон сіяння та жнив, що покладений Ним в основі світобудови. Велика кількість людей навіть не здогадується, що ВСЕ, буквально ВСЕ, що трапляється з ними в їх житті, долі, роботі, сім&rsquo;ї, країні є пов&rsquo;язаним ( прошу звернути увагу на слово ПОВ&rsquo;ЯЗАНИМ &ndash; &laquo;Що тільки зв\'яжете на землі, зв\'язане буде на небі&raquo; (Мф.18:18) з їх особистим язиком, а, точніше, тими словами, що язиком промовляються. І витрачають більшу частину своєї Богом даної духовної сили на створення в своєму житті болю та страждання, замість того, щоб використовувати свій неймовірний божественний Дар для створення свого особистого простору радості та любові.</p>\r\n<p>&nbsp;</p>\r\n<p>Одже, почнемо.</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;На початку було Слово, а Слово в Бога було, і Бог було Слово. Воно в Бога було споконвіку. Усе через Нього постало, і ніщо, що постало, не постало без Нього. І життя було в Нім, а життя було Світлом людей&raquo; (Ін.1:1-4).</p>\r\n<p>&nbsp;</p>\r\n<p>Спочатку значення еврейського слова &laquo;давар&raquo; (&laquo;річ&raquo; та &laquo;слово&raquo;) &ndash; означає &laquo;зворотний бік&raquo;, &laquo;підгрунтя&raquo;, тобто сенс будь-якої речі, її сутність, те, що визначає її внутрішній характер. Інакше кажучи, слово є духом матерії, чи видимого світу. От чому Бог все неіснуюче називає як існуюче. От чому &laquo;і сказав Господь&raquo; є синонімом &laquo;і створив Господь&raquo;. Сробуйте навести лише один приклад із Священого письма, в якому Слово, що сказане Богом, не набуло б форми, чи, просто кажучи, не здійснилося б. Те саме відбувається в світі людей. Все, що здійснюється таємно в духовному світі, в світі слів та думок, ми бачимо явно в світі матеріальному, фізичному.</p>\r\n<p>&nbsp;</p>\r\n<p>Господь наділив людину словом, тобто створив її подібною до себе &ndash; такою, що творить Словом. Думки вимірюються і проявляються в слові, вони є пов&rsquo;язаними з мовою. &laquo;Чим серце наповнене, те говорять уста&raquo; (Мф.12:34, Лк.6:45). Мову, яку ми сприймаємо за допомогою слуху, набагато легше контролювати, ніж думки, які ми не чуємо. Але якщо навчитися виконанню заповітів Господа и Бога нашого Іісуса Христа та жити в пильнуванні духу, тобто в постійному контролі над собою, своєю свідомістю, можна навчитися контролювати свій язик, потім &ndash; думки, а потім сам процес творіння свого життя. &laquo;Коли хто не помиляється в слові, то це муж досконалий, спроможний приборкувати й усе тіло&raquo; (Як.3:2).</p>\r\n<p>&nbsp;</p>\r\n<p>Даніела (Таїсія) Деребера, керівник проекта &laquo;Формула життя&raquo;</p>\r\n<p>&nbsp;</p>','Формула життя: людина труїться і зцілюється словом своїм','uk',315,1295,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Католик-Медіа з посиланням на Кореспондент.net повідомив, що Нацбанк випустив золоту пам\'ятну монету «Острозька Біблія».<p/>','2007-11-18 10:32:03',NULL,'P','<p>Католик-Медіа з посиланням на Кореспондент.net повідомив, що Нацбанк випустив золоту пам\'ятну монету «Острозька Біблія».<p/><p>Національний банк України продовжує серію «Духовні скарби України» і вводить в обіг з 15 листопада 2007 року пам\'ятну монету «Острозька Біблія» номіналом 100 гривень. Монета присвячена першому повному друкованому виданню Біблії, від 1581 року церковнослов\'янською мовою. <p/><p>«Ця Біблія, що включає 1256 сторінок, вийшла великим на той час накладом – 1500 екземплярів», – зазначається в телеграмі. <p/><p>За даними НБУ, монета виготовлена із золота 900 проби: маса 31,1 г., діаметр –32,0 мм., наклад – 4 тис. штук. <p/><p>Нацбанк запланував на 2007 рік випуск пам\'ятних монет 23 найменувань і 28 видів (за рядом найменувань випускаються монети в різних номіналах і з різних металів). До цього часу НБУ випустив близько 300 видів пам\'ятних і ювілейних монет, з яких біля третини – золоті й срібні.<p/>','«Острозька Біблія» в золоті - Католик-Медіа','uk',232,1212,0,NULL),
(0,NULL,'<p>Київ після Володимирового хрещення (кінець Х – початок ХІІ століття). Найдавніші згадки про Микільський монастир. Повідомлення Житія Феодосія про жіночий Микільський монастир. Легенда про заснування чоловічого Микільського монастиря. Печера пустельників поблизу Микільського монастиря.<p/>','2006-04-18 17:38:16',NULL,'P','<p>Київ після Володимирового хрещення (кінець Х – початок ХІІ століття). Найдавніші згадки про Микільський монастир. Повідомлення Житія Феодосія про жіночий Микільський монастир. Легенда про заснування чоловічого Микільського монастиря. Печера пустельників поблизу Микільського монастиря.<!--break--><p/><p><h2>Київ після Володимирового хрещення (кінець Х – початок ХІІ століття).</h2><p/><p><p>Після Володимирового хрещення Київ вступає в блискучий період своєї історії. За княжіння св. Володимира (980-1015) центральна частина міста значно розширюється і укріплюється могутніми земляними валами. В центрі дітинця будується славетна Десятинна церква, оточена князівськими палацами. Засновуються перші школи.</p><p/><p><p>По смерті св. Володимира після деякого періоду міжусобних війн на київському столі утверджується його син Ярослав Мудрий (княжив у 1015-1054 роках) – гідний продовжувач справ батька. За його правління будуються нові міські укріплення із Золотими воротами, зводиться митрополичий собор св. Софії, при ньому виникає скрипторій та бібліотека, розвивається літописання. В роки правління Ярослава в місті виникає ряд монастирів, у тому числі славетний Печерський монастир.</p><p/><p><p>Поступальний розвиток Києва триває в роки правління Ярославових синів – Ізяслава, Святослава та Всеволода. На початку ХІІ століття на київський стіл сідає онук Ярослава Мудрого – уславлений переможець половців, видатний державний діяч і талановитий письменник Володимир Мономах (княжив у 1113-1125 роках). Політику Мономаха продовжують його сини – князі Мстислав Великий (1125-1132) та Ярополк (1132-1139).</p><p/><p><p>Протягом усього цього часу Київ залишається тим центром, звідки християнство поширюється у все більш віддалені куточки Руської держави та навколишні землі.</p><p/><p><p>Саме в цей період на Аскольдовій могилі виникає монастир св. Миколая – спочатку жіночий, а потім і чоловічий.</p><p/><p><h2>Найдавніші згадки про Микільський монастир.</h2><p/><p><p>У літописах згадки про Микільський монастир в урочищі Угорському відсутні. Однак відомості про заснування цього монастиря містяться в житії преподобного Феодосія Печерського, складеному Нестором-літописцем наприкінці ХІ – на початку ХІІ століття, та в легенді, що дійшла до нас у тексті Київського Синопсису , створеного в третій чверті XVII століття. Факт існування монастиря в урочищі Угорському ще в давньоруську добу опосерековано підтверджується також знахідкою в цій місцевості печери, в якій мешкали ченці.</p><p/><p><h2>Повідомлення Житія Феодосія про жіночий Микільський монастир.</h2><p/><p><p>Преподобний Феодосій Печерський – видатний церковний діяч Київської Русі, один з найпопулярніших українських святих, що разом із Антонієм Печерським вважається засновником найвідомішого монастиря України-Русі – Києво-Печерської лаври.</p><p/><p><p>Будучи зовсім молодою людиною, Феодосій утік від своєї матері, що мешкала тоді в Курську, разом із групою купців прийшов до Києва і став одним з перших ченців Печерського монастиря, заснованого незадовго перед тим преподобним Антонієм. Але мати Феодосія – жінка із владним характером, яка перед тим не раз карала сина за непокору – знайшла його навіть у Києві і хотіла повернути додому. Та, наштовхнувшись на рішучий опір Феодосія та його опікуна, преподобного Антонія, врешті поступилася їхній волі й сама стала черницею.</p><p/><p><p>«<i>В єдин же убо день, пришедши к нему (преподобному Феодосію – І.А.) мати його, глаголя йому: “Се, чадо, все велімеє ми тобою створю і к тому не возвращаюся в град свій, но, яко Богу волящу, йду в манастирь женский і ту остригшися, і пребуду посліднії дні свої. Се бо от твого ученія разумех, яко нічто же єсть світ сей маловременний”. І сіє слишав, блаженний Феодосій возрадовася духом і вошед, повіда великому Антонію. Сіа же слишав преподобний і прослави Бога, обратившего серце єя на таковоє покаяніє. І шед к ній великий Антоній і много поучив ю, єже о пользі душі, і возвісти о ній княгині. Впусти ю в манастирь женский, іменуєм святого Миколи, і ту пострижені їй бивши, в мнішескую одежду оболчені…</i>» ([19], с.31).</p><p/><p><p>Сучасні дослідники вважають, що Феодосій був пострижений у ченці 11 січня 1058 року, у віці 22 років [15]. Очевидно, невдовзі по тому стала черницею і його мати. Тоді княгиня, згадана в уривку – це, мабуть, Гертруда-Олісава (Єлизавета), донька польського короля Мешка ІІ Ламберта, дружина великого князя київського Ізяслава Ярославича (сина Ярослава Мудрого, онука св. Володимира). Ця княгиня була покровителькою преподобного Антонія і пізніше врятувала його від гніву князя Ізяслава (коли без дозволу князя Антоній прийняв до чернечої громади кількох придворних Ізяслава). Цікаво відзначити, що автограф княгині Олісави зберігся до нашого часу на хорах Софійського собору. Цитований уривок Житія Феодосія змушує думати, що княгиня опікувалася жіночим Микільським монастирем, а, можливо, навіть була його засновницею .</p><p/><p><p>Можна думати, що розміщувався цей монастир десь у районі теперішнього палацу піонерів. Храм монастиря був мурованим. Сильвестра Косов у своїй книзі “Paterikon” (польськомовний переказ Києво-Печерського Патерика , виданий у Печерській типографії 1635 року) свідчить, що в його часи від згаданого храму залишалися лише кам’яні фундаменти серед винограднику, який належав у цей час Пустинно-Микільському монастирю (на місці пізнішого Велико-Микільського собору) [11].</p><p/><p><h2>Легенда про заснування чоловічого Микільського монастиря.</h2><p/><p><p>Судячи з повідомлень джерел, десь поблизу жіночого Микільського монастиря пізніше виник чоловічий монастир тієї ж назви.</p><p/><p><p>Збереглася легенда, що в 1113 році князь Мстислав, син Володимира Мономаха, бувши на полюванні в пізню годину на території, що її пізніше обіймав Микільський монастир, заблукав у глибині густого лісу. Розшукуючи шлях, він раптово побачив яскраве світло. Коли князь наблизився до місця, звідки йшло це світло, то побачив образ Святителя Миколая, що стояв на високому пні дерева. Поруч ішла та сама дорога, яку шукав князь. У пам’ять про диво Мстислав побудував біля того пня церкву, заснувавши при ній чоловічий Микільський монастир. Оскільки ця обитель були звідусіль оточена пустинним лісом, то її скоро стали називати Пустинною [9].</p><p/><p><p>Як уже згадувалося, після смерті батька Мстислав Володимирович у 1125-1132 роках був великим князем Київським і увійшов в історію як Мстислав Великий. Був засновником Федорівського монастиря у найдавнішій частині Києва, так званому городі Володимира, та церкви Богородиці Пирогощі на Подолі. Після смерті канонізований [8].</p><p/><p><p>Деякі дослідники висловлюють думку, що князь Мстислав перетворив на чоловічий згаданий у Печерському патерику жіночий монастир св. Миколая. Цій думці суперечить різне розташування монастирів: жіночого – на верхньому плато наддніпрянської кручі, чоловічого – нижче по схилу, в районі теперішньої Аскольдової могили. Якщо згадане вище повідомлення С.Косова вірне, то скоріше можна припустити, що чоловічий та жіночий монастирі існували одночасно.</p><p/><p><p>Ченці Печерського монастиря, обґрунтовуючи (очевидно, десь у XVII столітті) свої претензії на зверхність над Микільським монастирем, стверджували, що він був заснований саме вихідцями з Печерського монастиря і нібито був з цієї причини приписаний до Печерського монастиря ще Андрієм Боголюбським у 1174 році. Втім, більшість сучасних дослідників визнає так звані грамоти Андрія Боголюбського Печерському монастирю пізнішими фальсифікатами.</p><p/><p><h2>Печера пустельників поблизу Микільського монастиря.</h2><p/><p><p>У 1853 році під час робіт на Панкратіївському узвозі (попередник теперішнього Дніпровського узвозу, що вів з Печерська до Ланцюгового мосту) будівельники наштовхнулися на печеру. Вона розташовувалася приблизно на 160 метрів на південь від Ланцюгового мосту, на висоті близько 50 метрів над рівнем ріки. Довжина печерного коридору складала 16 метрів, висота – 2 метри, ширина – 1 метр. У п’яти кроках від входу з лівого боку було виявлено крипту з лежанкою, де знайшли видряпане на стіні зображення святого та давньоруські написи. В 11 метрах від входу до печери, з правого боку печерного коридору, йшов хід угору. Вісім сходинок вели до іншого коридору завдовжки 25 метрів, розташованого перпендикулярно до нижньої печери. Південний край коридору закінчувався завалом, тут також відкрито нішу з написами. З північного боку коридор закінчувався криптою з лежанкою, на підлозі лежала напівзотлілі дошка з вирізаним написом. Серед написів, знайдених у печері, були, зокрема, прочитані імена “Іван грішний” та “Феофіл”. Відомий палеограф І.І.Срезневський датував знайдені написи ХІ-ХІІ століттями [11,25].</p><p/><p><p>Здогадно печеру пов’язують із чоловічим Микільським монастирем. Вона, очевидно, була притулком ченців-затворників, як і відомі печерні лабіринти в Печерському і Видубицькому монастирях.</p><p/>','Заснування Пустинно-Микільського монастиря','uk',100,1080,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Народився 5 лютого 1962 року у м. Прудентополісі (штат Парана, Бразилія). Закінчив Малу семінарію Отців Василіян у Прудентополісі, після чого 7 лютого 1979 року вступив до Василіянського чину.</p>\r\n<p>&nbsp;</p>','2008-01-03 17:07:46',NULL,'P','<p>Народився 5 лютого 1962 року у м. Прудентополісі (штат Парана, Бразилія). Закінчив Малу семінарію Отців Василіян у Прудентополісі, після чого 7 лютого 1979 року вступив до Василіянського чину. 10 лютого 1981 року, після закінчення новіціяту, склав свої перші обіти в Чині. Шкільне навчання та університетські філософські студії закінчив у Семінарії Святого Василія у Бастелі (Куритиба). У 1986 році його скерували на навчання до Риму, до Папської колегії Святого Ансельма, де у 1989 році він одержав ступінь бакалавра богослов\'я, а в 1992 році в Салезіянському Інституті (Салезіянумі) &ndash; ступінь лінцеціята педагогіки.</p>\r\n<p>&nbsp;</p>\r\n<p>1 січня 1988 року у Римі склав урочисті вічні обіти і відтак у Прудентополісі отримав священичі свячення (святитель Владики Єфрем Кривий, ЧСВВ). Отець Мирон виконував різні душпастирські завдання, а також займав посади професора, настоятеля та ректора у Василіянських інститутах Прудентополіса та Куритиби. Він також був настоятелем Головної семінарії Святого Василія. У липні 2004 року о. Мирона призначили протоігуменом Отців Василіян у Бразилії. 21 грудня 2005 року Папа Венедикт XVI іменував отця Мирона Мазура єпископом-помічником Української Католицької Єпархії св. Івана Хрестителя в Куритибі (Бразилія), титулярним єпископом Сімітту. 26 лютого 2006 року в Прудентополісі у храмі Святого свщмч. Йосафата відбулася єпископська хіротонія. Головний святитель Блаженніший Любомир, а співсвятителі &ndash; Високопреосвященний Владика Стефан (Сорока), Митрополит Філадельфійський, та Преосвященний Владика Єфрем (Кривий), Єпарх Куритибський.</p>\r\n<p>&nbsp;</p>\r\n<p>\"Оранта\", Юлія Боєчко, ДІ УГКЦ</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Народився 5 лютого 1962 року у м. Прудентополісі (штат Парана, Бразилія). Закінчив Малу семінарію Отців Василіян у Прудентополісі, після чого 7 лютого 1979 року вступив до Василіянського чину. 10 лютого 1981 року, після закінчення новіціяту, склав свої перші обіти в Чині. Шкільне навчання та університетські філософські студії закінчив у Семінарії Святого Василія у Бастелі (Куритиба). У 1986 році його скерували на навчання до Риму, до Папської колегії Святого Ансельма, де у 1989 році він одержав ступінь бакалавра богослов\'я, а в 1992 році в Салезіянському Інституті (Салезіянумі) &ndash; ступінь лінцеціята педагогіки.</p>\r\n<p>&nbsp;</p>\r\n<p>1 січня 1988 року у Римі склав урочисті вічні обіти і відтак у Прудентополісі отримав священичі свячення (святитель Владики Єфрем Кривий, ЧСВВ). Отець Мирон виконував різні душпастирські завдання, а також займав посади професора, настоятеля та ректора у Василіянських інститутах Прудентополіса та Куритиби. Він також був настоятелем Головної семінарії Святого Василія. У липні 2004 року о. Мирона призначили протоігуменом Отців Василіян у Бразилії. 21 грудня 2005 року Папа Венедикт XVI іменував отця Мирона Мазура єпископом-помічником Української Католицької Єпархії св. Івана Хрестителя в Куритибі (Бразилія), титулярним єпископом Сімітту. 26 лютого 2006 року в Прудентополісі у храмі Святого свщмч. Йосафата відбулася єпископська хіротонія. Головний святитель Блаженніший Любомир, а співсвятителі &ndash; Високопреосвященний Владика Стефан (Сорока), Митрополит Філадельфійський, та Преосвященний Владика Єфрем (Кривий), Єпарх Куритибський.</p>\r\n<p>&nbsp;</p>\r\n<p>\"Оранта\", Юлія Боєчко, ДІ УГКЦ</p>\r\n<p>&nbsp;</p>','Владика Мирон Мазур ЧСВВ, Єпископ-Помічник  Куритибський','uk',259,1239,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Наші парафіяни за прикладом Євангельських товаришів, що через стелю опустили розслабленого до ніг Ісуса, який оздоровив його поспішили сьогодні в перший день весни&nbsp;зробити добре діло. Ірина і Володимир допомогли коштами придбати ліки для тяжкохворої&nbsp; Ольги з Чернігівської установи. Вона пише нам, що має дуже низький&nbsp; гемоглобін, лікарі говорять, що без ліків не зможе вижити. Найперши просить пігулки тітанол. Вона дуже хоче повернутись до своїх дітей, яких залишилось троє дітей під опікою рідних. Також просить вислати вервицю для молитви.</p>','2010-03-01 21:04:31',NULL,'N','<p>Наші парафіяни за прикладом Євангельських товаришів, що через стелю опустили розслабленого до ніг Ісуса, який оздоровив його поспішили сьогодні в перший день весни&nbsp;зробити добре діло. Ірина і Володимир допомогли коштами придбати ліки для тяжкохворої&nbsp; Ольги з Чернігівської установи. Вона пише нам, що має дуже низький&nbsp; гемоглобін, лікарі говорять, що без ліків не зможе вижити. Найперши просить пігулки тітанол. Вона дуже хоче повернутись до своїх дітей, яких залишилось троє дітей під опікою рідних. Також просить вислати вервицю для молитви.</p>\r\n<p>Волонтери Карітасу за кошти придбали ліки і вислали посилку загальною вартістю 170 грн.. Також сьогодні для лікарні №11, терапевтичне віділення було передано гумунітарну допомогу памперси для дорослих і рідке мило 24 кг..</p>\r\n<p>Все це стало можливим завдяки старанням волонтерів Карітас - Печерськ п. Антоніни, п. Зоряни, п. Михайла.</p>\r\n<p>Ділами милосрдя оздоровляється наша душа, радіє весні, очікує Паску.</p>\r\n<p>Матеріал Ільї</p>','Піст - весна для душі!','uk',1979,3462,0,'Віталіна Онишкевич'),
(0,NULL,'<p>Ніконівський літопис подає ім\'я князя у формі <em>Осколд</em> (&laquo;О князи Рустем Осколде&raquo;). За версією Бориса Рибакова це ім\'я може походити від старовинної племенної самоназви подніпровських мешканців: сколоти.</p>','2008-09-25 22:10:11',NULL,'P','<p><strong>Ім\'я</strong></p>\r\n<p style=\"line-height: normal;\">Ніконівський літопис подає ім\'я князя у формі <em>Осколд</em> (&laquo;О князи Рустем Осколде&raquo;). За версією Бориса Рибакова це ім\'я може походити від старовинної племенної самоназви подніпровських мешканців: сколоти.</p>\r\n<p style=\"line-height: normal;\"><strong>Походження</strong></p>\r\n<p style=\"line-height: normal;\">У Повісті временних літ розповідається про те, що ніби Аскольд та Дір були боярами Рюрика і відпросились у князя йти на службу в Константинополь. Однак по дорозі, побачили чудовий град (градок) Київ, у якому ніхто не княжив, де і залишились князями. Сучасні історики відкидають цю легенду як пізнішу вставку, щоб виправдати загарбання Києва Олегом. Існує теорія, що Аскольд правив перед Діром і вони були останніми нащадками князівської династії Кия.</p>\r\n<p style=\"line-height: normal;\"><strong>Походи до Константинополя</strong></p>\r\n<p style=\"line-height: normal;\">З Никонівського літопису відомо про три походи Аскольда на Візантію.</p>\r\n<p style=\"line-height: normal;\"><strong>Перший похід</strong></p>\r\n<p style=\"line-height: normal;\">У 860 році Аскольд очолив перший документований похід Русі на Константинополь. З цьго приводу Нестор записав у своєму творі: &laquo;Коли почав царювати (імператор Візантії) Михаїл, стала так прозиватися Руська земля. Дізнались ми про це тому, що за цього царя приходила Русь (війною) на Царгород, як пишеться про це в літописанні грецькому (візантійському). Ось чому з цієї пори почнемо (розповідь) і число (лік рокам) покладемо&raquo;.</p>\r\n<p style=\"line-height: normal;\">З візантійських джерел відома точна дата нападу Аскольда: 18 червня 860 року. На світанку того дня руські лодії стрімко увійшли до Золотого Рогу, і розпочали облогу мурів Царґорода. Незабаром потому наспіла й піша рать. Патріарх Фотій в емоційно передав почуття й страх обложених при появі небаченого раніше грізного ворога: &laquo;Народ (руси) від країни північної..., і племена піднялись від країв землі, тримаючи лук і спис; вони жорстокі й немилосердні, голос їх шумить, мов море&raquo;. У своїй другій проповіді Фотій пригадував, що &laquo;місто ледве не було підняте на спис&raquo;, як у давнину називали здобуття фортець приступом. Венеціанський хроніст Іоанн Диякон стверджував, що ті, хто облягав візантійську столицю, повернулися додому &laquo;з тріумфом&raquo;.</p>\r\n<p style=\"line-height: normal;\"><strong>Другий похід</strong></p>\r\n<p style=\"line-height: normal;\">У 874&nbsp;році Аскольд зробив другий похід до Константинополя. В &laquo;лѣто ҂s҃.т҃.о҃д (6374-5500 за болг. л.с., а не 6374-5508=866 за візан. л.с.). Иде Асколдъ и Дирд̑ъ на Грѣкы. и приде въ . д҃ı . (14) лѣто . Михаила цс̑рѧ . црс̑ю же ѿшедъшю . на Агарѧны&raquo;. Який закінчився поразкою русів, у &laquo;Повісті временних літ&raquo; знаходимо:\"... море було спокійне, та тут раптом здійнялася буря з вітром, і великі хвилі розкидали кораблі язичників-русів, і прибило їх до берега і переламало так, що небагатьом із них пощастило уникнути цієї біди й повернутись додому...\" та далі &laquo;...Повернулися Аскольд і Дір від Царгорода з малою дружиною, і був плач великий&raquo;.</p>\r\n<p style=\"line-height: normal;\"><strong>***</strong></p>\r\n<ul type=\"disc\">\r\n<li style=\"line-height: normal;\">Воював з уличами та печенігами.</li>\r\n<li style=\"line-height: normal;\">Є відомості про перше хрещення Русі за Аскольда.</li>\r\n</ul>\r\n<p style=\"line-height: normal;\"><strong>Смерть</strong></p>\r\n<p style=\"line-height: normal;\">За літописним переказом Аскольд і Дір були вбиті Олегом, зробив він це не у відкритому двобої, а підступним способом. Разом із своєю дружиною переодягнувся купцем і вбив їх, коли вони прийшли до нього торгувати.</p>\r\n<p style=\"line-height: normal;\">За легендою, Аскольда поховали на місці загибелі, на правому березі Дніпра в урочищі, що в давнину називалося Угорським. Зараз це частина мальовничого парку, що має назву Аскольдова могила.</p>\r\n<p>За матеріалами <a href=\"http://uk.wikipedia.org/\">Вікіпедії</a></p>','Історична довідка','uk',36,1016,0,'Andriy Gushuley');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>ПРОСИМО ДОБРОЧИНЦІВ ДОПОМОГТИ МИКОЛАЮ ПРИНЕСТИ ДАРУНКИ ДІТЯМ. Розклад ЦСМ: 19-22.12 8.00 Утреня 9.00 Літургія 17.00 Вечірня 18.00  Духовні науки о.Леоніда; 23.12 - 12.30 Літургія для дітей 14.00 Дарунки дітя. Дитячий  концерт.<p/>','2007-11-16 12:10:40',NULL,'P','<p>ПРОСИМО ДОБРОЧИНЦІВ ДОПОМОГТИ МИКОЛАЮ ПРИНЕСТИ ДАРУНКИ ДІТЯМ. Розклад ЦСМ: 19-22.12 8.00 Утреня 9.00 Літургія 17.00 Вечірня 18.00  Духовні науки о.Леоніда; 23.12 - 12.30 Літургія для дітей 14.00 Дарунки дітя. Дитячий  концерт. Концерт духовної пісні «Аскольда Глас». 15.00 Конкурс Духовної пісні «Аскольда Глас». Нагородження переможців. Гостина. Спілкування.<p/><p><p/><p>19-22 грудня <p/><p>8.00 Утреня <p/><p>9.00 Божественна літургія <p/><p>17.00 Вечірня <p/><p>18.00  Духовні науки о.Леоніда<p/><p>23 грудня           <p/><p>12.30   Божественна Літургія для дітей(Неділя)	 <p/><p>14.00 Актовий зал транспортного університету на Печерську - З дарунками для дітей прийшов Святий Миколай. Дитячий  концерт. Концерт духовної пісні «Аскольда Глас», керівник Мар’ян Гаденко. <p/><p>15.00 Конкурс Духовної пісні «Аскольда Глас». Нагородження переможців. Гостина. Спілкування.<p/><p>Дарунки чекаємо в церкві на Аскольдовій могилі<p/><p>Тел. 570-32-90, 8-050-447-31-57<p/><p>Відданий в Христі                                                    <p/><p>Адміністратор<p/><p>Митрофорний протопресвітер 			 Ігор Онишкевич                        <p/><p><p/><p>Просимо доброчинців допомогти святому Миколаю принести дарунки дітям парафії, дітям-сиротам, що будуть запрошені, а також дітям матерів, що засуджені до тюремного ув’язнення, неповносправним з дітям інтернату в Потіївці Житомирської обл.<p/>','зимове Миколая','uk',228,1208,0,NULL),
(0,NULL,'<p>Предстоятель УГКЦ Патріарх Любомир (Гузар) 15 листопада у Києві провів зустріч з Надзвичайним і повноважним послом Австрійської Республіки в Україні Йозефом-Маркусом Вукетічем.<p/>','2007-11-16 13:35:06',NULL,'P','<p>Предстоятель УГКЦ Патріарх Любомир (Гузар) 15 листопада у Києві провів зустріч з Надзвичайним і повноважним послом Австрійської Республіки в Україні Йозефом-Маркусом Вукетічем. У зустрічі взяв участь також єпископ-помічник Київської архиєпархії владика Богдан (Дзюрах). Як повідомляє Департамент інформації УГКЦ, сторони обговорили сучасну суспільно-релігійну ситуацію в Україні, а також міжконфесійні відносини в Україні та розвиток УГКЦ у пост-радянському періоді. Інформує \"Хрещатик\"<p/><p>Під час розмови сторони порушили питання шкільництва та перспектив заснування католицьких шкіл в Україні. Посол також цікавився сучасним станом будівництва Патріаршого центру УГКЦ із собором у Києві, перспективами його завершення та духовним значенням реалізації цього проекту. <p/><p>У рамках зустрічі Глава УГКЦ розповів про часи свого вимушеного проживання в Австрії, коли йому 1944 року разом із сім\'єю довелось залишити батьківщину. Розповідаючи про це, Патріарх Любомир дякував австрійському народові за захист і підтримку, яку вони тоді надали йому та тисячам інших сімей з України. <p/><p>Надзвичайний і повноважний посол Австрійської Республіки в Україні також зустрівся з Предстоятелем УПЦ КП Патріархом Філаретом. Під час зустрічі, яка відбулася 12 листопада, обговорили питання загальнолюдських цінностей європейської цивілізації та релігійної ситуації у світі та Україні.<p/>','Посол Австрії зустрівся з єрархами українських Церков','uk',229,1209,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Президент України Віктор Ющенко ініціює проведення акції &laquo;Чудо творить Миколай&raquo;, щоб привернути увагу до потреб дітей з багатодітних родин, сиріт та дітей, позбавлених батьківського піклування.</p>\r\n<p>&nbsp;</p>','2007-11-22 07:47:18',NULL,'P','<p>Президент України Віктор Ющенко ініціює проведення акції &laquo;Чудо творить Миколай&raquo;, щоб привернути увагу до потреб дітей з багатодітних родин, сиріт та дітей, позбавлених батьківського піклування.</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;Хрещатик&raquo; повідомив, що Глава держави наголосив про це учора у Київській області під час відвідання Центру соціальної опіки дитини &laquo;Отчий дім&raquo; однойменної Міжнародної благодійної фундації.</p>\r\n<p>&nbsp;</p>\r\n<p>У рамках акції &laquo;Чудо творить Миколай&raquo;, яка стартувала 21 листопада і триватиме до 19 грудня, такі діти по всій Україні отримають президентські подарунки, в комплект яких входитимуть: теплий одяг, курточка, шкіряні чобітки тощо. Сьогодні Президент вручив подарунки вихованцям Центру &laquo;Отчий дім&raquo;.</p>\r\n<p>&nbsp;</p>\r\n<p>- Моя мета через це благочинство привернути увагу українського бізнесу, влади, людей доброї волі &ndash; давайте звернемо увагу на цих дітей, &ndash; сказав Віктор Ющенко. Глава держави також висловив переконання, що в нашій країні Акція без сумніву має знайти підтримку серед широких кіл громадськості.</p>\r\n<p>&nbsp;</p>\r\n<p>Віктор Ющенко також оглянув навчальні та житлові приміщення Центру, поспілкувався з його вихователями та дітьми. Президент Міжнародної благодійної фундації Роман Корнійко розповів Вікторові Ющенку про діяльність Центру &laquo;Отчий дім&raquo; протягом останніх трьох років. За його словами, сьогодні робота з дітьми здійснюється у кілька етапів, серед яких &ndash; спілкування з так званими &laquo;вуличними дітьми&raquo; безпосередньо на вулицях, первинна реабілітація дітей у навчальному таборі, духовна та соціальна реабілітація у Центрі опіки та перебування дітей у Центрі сімейного виховання. На сьогодні в закладі перебуває 63 дитини.</p>\r\n<p>&nbsp;</p>','Чудо творить Миколай','uk',236,1216,0,NULL),
(0,NULL,'<p>Проща до Зарваниці 7 вересня 2008<p/><p>В світлу пам’ять Патріарха Йосипа Сліпого († 7 . 09. 1984) та народного єпископа Івана Маргітича († 7 . 09. 2003)<p/><p>Запрошуємо до участі в хресній дорозі за маршрутом: село Заздрість – село Зарваниця<p/><p>Вартість доїзду, харчування та нічлігу становить 180 грн. з особи. Для студентів та молоді – знижка 50%<p/>','2008-07-18 12:06:59',NULL,'P','<p>Проща до Зарваниці 7 вересня 2008<p/><p>В світлу пам’ять Патріарха Йосипа Сліпого († 7 . 09. 1984) та народного єпископа Івана Маргітича († 7 . 09. 2003)<p/><p>Запрошуємо до участі в хресній дорозі за маршрутом: село Заздрість – село Зарваниця<p/><p>Вартість доїзду, харчування та нічлігу становить 180 грн. з особи. Для студентів та молоді – знижка 50%<p/><p>При собі обов’язково мати: vолитовник, вервицю, зручне взуття, дощовик, пісну трапезу.<p/><p>Виїзд поїздом Київ-Тернопіль, в п’ятницю 5 вересня. Повернення до Києва – 8 вересня о 6 ранку.<p/><p>Запис на Аскольдовій могилі Повідомте та запросіть своїх друзів та рідних з усіх кутків України!<p/><p>Громада церкви св. Миколая на Аскольдовій могилі 80504473157<p/>','Громада запрошує на прощу Заздрість-Зарваниця','uk',407,1387,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Розклад богослужінь на щодень протягом 2008-го р.Б.<p/><p>Понеділок<p/><p>8.30 Часи. Божественна літургія <p/><p>18.00 Божественна літургія<p/><p>19.00 Акафіст Теодору Ромжі з благословенням мощами<p/><p>Вівторок<p/><p>8.30 Часи. Божественна літургія Акафіст Богородиці Всецариці за онкохворих.<p/><p>18.00 Божественна літургія. Катехизація для парафіян<p/><p>Середа<p/>','2008-03-10 17:47:05',NULL,'P','<p>Розклад богослужінь на щодень протягом 2008-го р.Б.<p/><p>Понеділок<p/><p>8.30 Часи. Божественна літургія <p/><p>18.00 Божественна літургія<p/><p>19.00 Акафіст Теодору Ромжі з благословенням мощами<p/><p>Вівторок<p/><p>8.30 Часи. Божественна літургія Акафіст Богородиці Всецариці за онкохворих.<p/><p>18.00 Божественна літургія. Катехизація для парафіян<p/><p>Середа<p/><p>8.30 Божественна літургія. Молебень до Богородиці<p/><p>18.00 Божественна літургія.<p/><p>19.00 Акафіст до Богородиці Зарваницької, молитва матерів за дітей<p/><p>Четвер<p/><p>8.00 Акафіст до святого Миколая Чудотворця<p/><p>8.30 Божественна літургія. Чин великого освячення води.<p/><p>Молитви за оздоровлення недужих, подорожуючих, поміч Святого Духа, оливопомазання.<p/><p>18.00 Божественна літургія<p/><p>П\'ятниця<p/><p>8.30 Божественна літургія. Молебень до Христа Чоловіколюбця.<p/><p>18.00 Божественна літургія. Акафіст до Ісуса Христа<p/><p>Субота<p/><p>8.15 Утреня<p/><p>9.00 Божественна літургія за спочилих. Парастас<p/><p>18.00 Божественна літургія. Вечірня. Збір молоді<p/><p>Неділя<p/><p>8.15 Божественна літургія зі співом вірних<p/><p>10.00 Божественна літургія з хором<p/><p>12.30 Божественна літургія для дітей<p/><p>Кожної першої п\'ятниці місяця - збір Апостольства молитви<p/><p>8.30; 18.00 Божественна літургія, Сповідь. Напередодні в четвер, 19.00 Свята година, Сповідь, відвідування і причастя хворих<p/><p>Кожної останньої суботи збір Церковної ради<p/><p>Щосуботи – збір молоді на Вечірню о 18.00 - Наука за кавою в Церковній хатині<p/><p><p/><p> Церковний уряд<p/><p>адміністратор, митрофорний протопресвітер<p/><p>Ігор Онишкевич<p/><p>8-050-447-31-57 8 (044) 570-32-90 e-mail: oranta-gazeta@ukr.net<p/><p><p/><p>сотрудник, ієрей Олексій Геник<p/><p>8-050-447-31-55<p/><p>сотрудник, ієрей Анатолій Тесля<p/><p>8-050-284-77-31<p/><p>диякон Олександр Соколовський<p/><p>424-42-71<p/><p>Довідково про Храм і парафію. \"Оранта\"<p/><p>...Після побудування храмів Великого Миколи і Малого Миколи дерев`яна трибанна Миколаївська церква на Аскольдовій могилі, що існувала тут із XVII століття, залишалася у складі Микільського Пустинно-Слупського монастиря, як приписана до нього, і виконувала роль цвинтарного храму. З часом вона стала ветхою і не прикрашала собою кладовище, яке 1786 року набуло статусу міського. Отож 1 вересня 1809 року відбулося закладення, а 1810 року (називаються 1 січня, 20 серпня, 1 вересня) й освячення спорудженого в класичному стилі за проектом київського архітектора А.І. Меленського і на кошти у сумі 8 тисяч рублів воронізького міського голови купця С.М. Мещерякова мурованого, круглого в плані двоповерхового храму-ротонди в ім`я все того ж св. Миколая.<p/><p>Нижній поверх (ярус) церкви призначався для здійснення у ньому поховань тих її благодійників, які того бажали. Під час відзначення 1866 року 1000-ліття хрещення князя Аскольда країною блукала легенда про те, що тут знаходиться також і його поховання. На запитання про це богомольців військові писарі показували на місце за вівтарем храму, де існувало поховання невідомої особи, а пастирські служки уточнювали: \"Не тут, а під церквою у склепі\". Тоді ж на його фасаді було встановлено пам`ятну дошку (не збереглася).<p/><p>10 вересня 1847 року церкву на Аскольдовій могилі відвідав імператор Микола І. На донесення інженерів про її начебто аварійний стан через тріщини, отримані нею від зсуву сусіднього Кургана, він відповів: \"Падіння церкві не загрожує, її потрібно дещо поправити, і вона має існувати\".<p/><p>На закритому 1845 року для загального користування цвинтарі, починаючи з другої половини XIX століття, почали здійснювати поховання найбільш заможних жителів Києва. Зрозумівши, що від цього можна мати неабиякий зиск, монастирське начальство стало практикувати широкий розпродаж місць на ньому, а на початку 1880-х років запровадило навіть посаду наглядача, першим із яких виявився ієромонах о. Рафаїл. Він багато зробив для того, аби зберегти й утвердити звичай прибирати могили й утримувати їх у належному стані, перетворюючи це похмуре місце туги й смутку на привабливий куточок міста.<p/><p>Після своєї смерті, яка сталася у поважному віці, о. Рафаїл теж знайшов тут вічний для себе спокій. Його місце заступив у 1892 році ієромонах того самого монастиря о. Веніамін. Найперше він звернув увагу на церкву, яка своїм зовнішнім виглядом і внутрішнім оздобленням не відповідала станові кладовища з його чистотою, прекрасними надгробними пам`ятниками і склепами. Не маючи в кишені жодної зайвої копійки, наглядач звернувся за допомогою до приватних осіб, які мали тут могили своїх близьких, і несподівано для себе отримав широку підтримку.<p/><p>Усередині церкви з`явилися привезений із Італії біло-мармуровий іконостас, новий настінний розпис, виконаний за ескізами живописця В.М. Васнецова, і дубовий паркет. Освячення оновленого храму здійснив 7 липня того самого 1894 року митрополит Київський Іоанікій.<p/><p>Миколаївську церкву із частиною надмогильних пам`ятників навколо неї зобразив 1846 року Т.Г. Шевченко на своєму малюнку \"Аскольдова могила\". За літописними мотивами й народними переказами про Аскольда і Діра письменник М.М. Загоскін написав 1833 року роман \"Аскольдова могила\", а композитор О.М. Верстовський за цим твором створив 1835 року однойменну оперу. Декабрист М.П. Бестужев-Рюмін, перебуваючи в ув`язненні у Петро-Павлівській фортеці, заповідав поховати його на Аскольдовій могилі, яку свого часу часто відвідував. Кладовище навколо Миколаївської церкви вважалося аристократичним. Надгробні склепи і скульптурні зображення належали до творів високої художньої вартості. На межі XIX і XX століть тут з`явилася ошатна дзвіниця, споруджена за проектом єпархіального архітектора Є.Ф. Єрмакова (розібрана, як і споруджені 1861 року будинок настоятеля та мурований паркан, 1935 року).<p/><p>За радянської влади Аскольдову могилу було перетворено на парк, а церкву архітектор П.Г.Юрченко перебудував 1935 року у паркову ротонду із колонадою, у якій розмістився ресторан. Вона існувала до 1992 року, коли греко-католицька громада, отримавши її 26 квітня у своє розпорядження, шляхом здійснення ремонту повернула їй первісний вигляд культової споруди. Це сталося у 1997-1998 роках із використанням коштів, що їх надала міська влада, і за сприяння Управління охорони пам`яток історії, культури та історичного середовища Києва.<p/><p>Освячення Миколаївської церкви здійснив 22 травня 1998 року Преосвященний Екзарх Києво-Вишгородський УГКЦ Любомир Гузар. Посаду її адміністратора обіймає сьогодні митрофорний протопресвітер о. Ігор Ілля Онишкевич.<p/><p>Уривки з твору Василя Галайби \"Храми св. Миколая в Києві\"<p/><p>Уклав В`ячеслав Михайлишин<p/>','Розклад Богослужень, церковний уряд і довідка','uk',327,1307,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Слава Ісусу Христу!<p/><p>Сьогодні хочу пригадати події творення нашої парафії, але заглянувши в сиву давнину, 882 р., коли підступно було вбито Київського Царя Аскольда в Хрещенні Миколая. З того часу поставлена Церква на його могилі. Не раз палена і нищена, але в пам’яті народній збережена. <p/>','2007-06-14 20:01:21',NULL,'P','<p>Слава Ісусу Христу!<p/><p>Сьогодні хочу пригадати події творення нашої парафії, але заглянувши в сиву давнину, 882 р., коли підступно було вбито Київського Царя Аскольда в Хрещенні Миколая. З того часу поставлена Церква на його могилі. Не раз палена і нищена, але в пам’яті народній збережена. <p/><p>Божою Волею, як нагорода за вірність Христу нашої підпільної Церкви ця святиня джерело віри нашої, повертається в лоно Вселенської Церкви. <p/><p>Наша парафія, яка започаткувалася, починаючи з часу моїх свячень (це є жовтень, 1990 рік), і з того часу тут в Києві почала молитися, спочатку – по будинках, по майстернях художників, потім під церквами молилися. І ось в 1992 році на самий день пам\'яті Чорнобиля – це було 26 квітня – нам надали можливість молитися в церкві, бо тоді, в 1992 році, крім пам\'яті цієї трагічної події, це ще була велика радість християнства – Воскресіння Христове, Пасха. І ось з того дня ми почали молитися на Аскольдовій Могилі – колисці християнської Руси-України.<p/><p>Боже Провидіння дало нам можливість, щоб ми в ту церкву зайшли – в маленьку церковцю, на яку ніхто не звертав уваги, і всі її обходили. Була вона закинута, філіал Музею Історії Києва, протікала... Нижня крипта, яка називається криптою Св. Сильвестра (зараз відновлена), то був там страшний смітник і, прикро мовити про це, відхоже місце. То було таке... Коли ми прийшли, застали розбите все там, без дверей, хто хтів, той заходив. Нижня крипта Св. Сильвестра була варварськи перекопана вже сучасними вандалами. Це вже робилося в наш час – копали там, золото шукали, там все було перекопане.<p/><p>І ось нам ту церковцю дає мер міста пан Салій на Пасху, бо до цього ми молилися в церкві Свято-Троїцькій (це Іонівський скит в Ботанічному саду). а потім – під церквою. І коли наші брати-православні дізнались, що ось греко-католики там моляться (а ми вже молились всередині церкви за домовленістю з дирекцією Ботанічного саду), то тоді був великий скандал, була велика біда і вони почали нас усувати звідти, почались погрози.<p/><p>Ми вирішили шукати свого щастя, молитися біля стін Собору Св. Софії, пам’ятника Св. Володимиру, і в такий спосіб вимолили таку ласку у Бога – я думаю, що то є ласка не лише для нашої парафії, але й для цілої нашої страждальної Церкви. Бо знаємо, що Свята Софія зараз не належить нікому. Сподіваємось, що прийде той день і час, коли наш Патріарх і об\'єднані ми – всі християни тут на Україні – будемо спільно молитися, і буде приналежна нам Свята Софія так, як колись була приналежна нам. Того часу ми дочекаємося. <p/><p>І тут почались дивні події. Салій видав нам дозвіл на два тижні молитися у Церкві Св. Михаїла. Два тижні ми помолилися,– почались суперечки з Музеєм Історії Києва. І ми вирішили внутрішньо, кожен з нас, парафіян, вирішив – ми вже в цю церкву зайшли, то ми звідти вже не маєм права вийти. Ніяка сила не може нас примусити. Ми законослухняно віддавали ключі, а потім знов ставали біля церкви молитися.<p/><p>Ще отакий пам\'ятаю випадок: роковини пам\'яті спочинку Івана Франка. Йдемо, пишемо в то управління культури, там нам кажуть: «Йдіть до Івана» (такий керівник там був), «Нам треба за Івана Франка помолитися, ви – культура, дайте дозвіл, відкрийте церкву!». Відкривають, знову закривають. Ми так бавилися з ними, бавилися ще кілька місяців, доки, нарешті, не поставили свій замок, закрили церкву і сказали: «Вибачте, церкву ми освятили молитвою, часи, в які руйнується церква і осквернюються святині, минулися, ми тут будемо молитися». По тому був великий скандал, газети, преса почали писати великі статті. «Тяжба за Аскольдову Могилу» – ось стаття з газети «Киевские ведомости». То вже йшло: 92-й, 93-й, 94-й, потім був 95-й рік (коли була надрукована ця стаття), 96-й! Музей Історії Києва постійно ставив нам перешкоди. Ну, очевидно, не Музей Історії Києва, а якісь вищі чинники в нашому місті Києві не бажали, щоб ми були в тій церкві. Але немає вищої сили, як сила Божа! Божа воля була така, щоб ми там були.<p/><p>І нарешті, це постійне листування з Верховною Радою, з Президентом. А тут ще був такий мер міста Косаківський. Скільки ми до нього написали! Скільки находили! І депутати народні ходили. На жаль, цей чоловік все зробив, щоб церкву нам не передали. І ось сталося чудо! <p/><p>Коли п. Косаківський пішов у відпустку, то його заступник Іван Данькевич відгукнувся на наші листі і видав розпорядження 1057 від 8 жовтня 1993 р.: <p/><p> «Дирекції Музею Історії м. Києва передати релігійній громаді Української греко католицької церкви у безоплатне користування культову будівлю ротонду Церкви святого Миколая на Аскольдовій Могилі на умовах охоронного договору згідно чинного законодавства».<p/><p>ніколи не бачився з п. Іваном, але завжди його поминаю у молитвах, прошу Божого Благословення для нього і родини.<p/><p>Це рішення було можливе тільки тому, що особисто перший посол Америки Роман Порадюк з дружиною п. Джудіт і 4 діток відвідували нашу Церкву, записалися до парафіяльної книги.<p/><p>Змінюється влада в Києві, міська влада. Замість Косаківського стає мером міста Олександр Олександрович Омельченко. І коли ми принесли йому ту папку з листуванням нашим з Косаківським, всі листи (а їх вже назбиралось більше 20, дуже багато!), він приймає рішення, щоб ми мали нормальну можливість молитися в церкві.<p/><p>І тим більше – виникла велика загроза, що церква буде зруйнована. Ми не могли своїми силами щось зробити. Почалися тріщини, великі. А, вірніше, ті ж тріщинки ще були старі. Але найстрашніше – це замокання: навколо церкви і згори церква промокала. Те, що ми латали, не давало ефекту. Тоді Олександр Омельченко дає дозвіл нам на реставрацію. Бо до цього часу ми добилися були дозволу Міської Ради на те, що ми маємо право на умовах охоронного договору в цій церкві молитися. І цей охоронний договір з Музеєм оформили.<p/><p>Але, починаючи десь вже з 1993 року, ми просили про ремонт. Ми навіть зібрали великі кошти були на ремонт: на тодішній час по тих цінах ми могли своїми силами зробити peмонт – такі в нас були великі кошти! Але Музей Історії Києва і мер міста Косаківський не давав такого дозволу. Коли приходить Олександр Омельченко, перше, що для нас він робить – він дає дозвіл почати реставраційні і будівельні .роботи. Він був на Аскольдовій Могилі, все це бачив.<p/><p>Одного разу (а точніше – 15 листопада 1996 р.) він відвідав церкву Св. Миколая разом з Президентом Угорщини Арпадом Ґьонцем. І коли Президент Угорщини прийшов зі своїм почтом – бо це угорське місце (угорські племена, які йшли з Уралу, тут зупинилися і наш князь Аскольд їм дозволив тут в мирі перебувати без жодної ворожнечі). Президент прийшов, взяв свічечку, поставив за свій народ і за наш народ. А церковця убога, а церковця зруйнована... І тоді • я побачив по обличчю Олександра Омельченка, як йому соромно було, що місце, про яке знає угорський народ, яке відвідує Президент Угорщини, є в такому страшному стані. Це був 96-й рік.<p/><p>І тоді є то рішення в 97-му році про підготовку реставраційних робіт. Для цього потрібно було: підготувати документацію, проект, кошторис. Все це робилось коштом громади – всі ці проектні документи. І коли ми майже всe підготували, тоді Олександр Омельченко в серпні місяці видає постанову розпочати реставраційні роботи і дає кошти для першочергових реставраційних робіт – це 200 тис. гривень, про які добрі люди знають, про це говорилось. Але це були не реставраційні роботи, а протиаварійні роботи. Щоб не там щось собі робити, якісь прикраси, а тільки врятувати цю пам\'ятку національної і християнської історії України. <p/><p>Роботи розпочалися. До цього часу багато можна говорити про труднощі - - скільки нам не вдавалось, скільки нас відсилали з нічим, як нам не дозволяли давати дозвіл на реставрацію. Але це сталося з Божим Промислом і Господь навіть так закерував, що коштами держави. Я повторюю: громада докладала свої кошти на всі проектно-пошукові роботи. Бо це так: археологічні дослідження, геологічні, технологічні дослідження, дослідження стану вологи в церкві, залягання води, протизсувні дослідження (супру), проект вкінці, які виконали Інститут «Укрпроектреставрація» (Центральний Інститут, де директор Антонюк). Це зробили і ось тоді почалися роботи.<p/><p>1 вересня 1997 року до робіт приступає за наказом Олександра Омельченка Київська міська реставраційна майстерня, директор – Петро Миколайович Семьошкін. Надійшли кошти від міста. Кошти міста контролював і розпоряджувався ними голова (або керівник) Управління охорони пам\'яток історії, культури та історичного середовища м. Києва п. Руслан Іванович Кухаренко. Він був офіційним представником від міністра, який відав цими коштами, які виділило місто, і контролював їх, направляв і був головною дійовою особою в реставрації церкви на Аскольдовій Могилі. Щотижня щовівторка він проводив наради, де ми збиралися, бо Генеральним підрядником була Київська реставраційна майстерня, а Головним відповідальним був начальник Управління охорони пам\'яток м.Києва. Почали всі – і Музей включився в цю роботу. Як Генеральний підрядник Петро Миколайович збирав найкращих фахівців. Найкращі люди виконували оці протиаварійні роботи, те, щоб укріпити фундамент (це дуже складна робота – укріплення фундаменту!) і всівсі інші роботи. Звичайно, майстерня ця, люди, які там працюють – надзвичайно добрі фахівці своєї справи. Але також мені сподобалось, що це – люди віруючі, які розуміли, що роблять. Ми багато говорили з ними про віру, про Бога і про церкву. Всі – київські майстри, звідси, з цієї землі. Щоправда, тинькували церкву – це вінницькі майстри.<p/><p>Робота йшла. Кошти були видані, але, звичайно, їх не вистачало. Тому було прийняте ще одне рішення: для закінчення першочергових робіт додати ще 150 тис. гривнів. Але реставраційна майстерня вклала коштів набагато більше, бо це важка, тяжка праця і треба було рятувати пам\'ятку. Таким чином, решта коштів докладалась громадою, бо великі кошти затрачені, дорога вартість реставрації. І ось, праця йшла і наближався той день закінчення реставрації.<p/><p>Нарешті, все було зроблено. Найбільше – це бетонне склепіння, баня церкви, потім – покриття мідною бляхою і встановлення позолоченого хреста та установлення дзвону. В нижній частині було зроблено: добудова абсиди (тобто святилища, яке було зруйноване більшовиками) і цю абсиду відновлено, встановлена як опалення церкви тепла підлога і покрита підлога гранітними плитами. І, звичайно, тинькування – внутрішнє тинькування, зовнішнє тинькування, фарбування. І в нижній церкві – каплиці Св. Сильвестра – теж був проведений повний комплекс реставраційних робіт: установлення підлоги, тинькування внутрішнє, вся столярка і вироби металеві, – все це було зроблено. Навколо церкви було зроблено водовідвід і навколо церкви була встановлена бруківка із водонепроникаючою ізоляцією (бо то дуже велика біда – вода, яка проникає всередину – це дуже велика небезпека для церкви, бо ґрунти, де вона стоїть – дуже небезпечні).<p/><p>І коли роботи були закінчені, то, як ви знаєте, 22 травня 1998 року Єпископ-Помічник Патріарха нашого Кардинала Мирослава Івана Любачівського Єпископ Любомир Ґузар, владика Екзарх наш Василій Медвіт, і також Папський Нунцій Архієпископ Антоніо Франко у співслужінні численного духівництва і мирян освятили церкву, освятили престіл. Здійснив освячення владика Любомир Ґузар. Сталася ця урочистість 22 травня о 10-й годині. Чому 22 травня? Тому що це – наше храмове свято, свято «Теплого Миколи», свято, яке був установив Папа для пам\'яті перенесення мощей св.Миколая Чудотворця з Мір Лікійських до міста Бари на півдні Італії. Ще тоді, за тих літописних часів, тут стояла церква СвМиколая, побудована після смерті Аскольда (у хрещенні Миколая) – мученика за християнську віру. Тому в той день і була освячена відновлена церква.<p/><p>Це посвячення вже третє з черги є. Тому що церква мала їх багато, доля складна була в церкви. Були випадки, що вже ось мала бути зруйнована, знесена -- ще за царської Росії. Начальники, які будували дорогу цю Паркову, чи Петрову Алею, як ми її називаємо, панську ту дорогу, сказали, що в цій церкві почались дуже великі тріщини. А врятував церкву цар Микола І. Він особисто прийшов, подивився церковцю і сказав: «Ні, церква буде стояти, контрофорси поставте і вона буде стояти» (ми ті контрофорси зняли, бо фактично вони не відіграли жодної ролі: не було після цього випадку – це була десь середина XIX ст. – жодних подвижок землі, дуже маленькі були). Але він поставив контрофорси, церкву примусив відремонтувати і вона збереглась.<p/><p>Не збереглась церква, коли прийшов більшовицький режим, ця безбожна влада, і Затонський своїм підписом дає наказ зруйнувати церкву. Церква була зруйнована, а поряд з церквою – і кладовище. Коли ми прокладали інженерні споруди, траси, то постійно бачили склепіння склепів, дороги йдуть по склепах, постійно траплялись могили. Археологи сказали, що тут, в землі, де ми прокладали кабель, були поховання і XI ст., і там інші століття – сліди життя людини тут, на Аскольдовій Могилі. Ось така історія...<p/><p>22 травня 1998 року освятили і з цього часу ми молимося у цій церкві. <p/><p>Був поставлений мармуровий іконостас, розписується нижня крипта св. Сильвестра, де за переказами знаходиться поховання Аскольда. Готуємося до розпису верхньої Церкви, що буде реставраційним відновленням попереднього вигляду. <p/><p>Парафія зростала і було прийнято рішення шукати ділянку на спорудження дочірньої Церкви. Допомогою п. Лариси Скорик, було отримано ділянку по вул.. Вознесенській (Смирнова-Ласточкіна). Проект Церкви (Каплиці) Вознесіння Господнього виконала архітектор Л. Скорик за кошти парафії і свої пожертви. Нами була підготовлена ділянка під будову, але не вдалося її розпочати, оскільки всі зусилля були направлені на реставрацію Аскольдової Могили. Тоді Церковна рада прийняла рішення, передати цю ділянку ОО.Василіянам, що благословив Владика Висилій Медвіт. Так постав Висиліянський монастир на Львівській площі. <p/><p>Аскольдова могила стала осередком духовного життя греко-католиків у м. Києві. <p/><p>Пставлення Екзарха Києво-Вишгородського Владику Любомир Гузара звершилося на Аскольдовій Могилі. Тоді привітати владику Любомира прибув патріарх Філарет, що викликало велике піднесення серед вірних і надію на відновлення єдності Церкві часів Аскольда. <p/><p>Служили на Аскольдовій Могилі Владика Михаїл Колтун, Владика Василь Мед віт.<p/><p>1996 р. святкування 400-ліття Берестейської Унії відбулося на Аскольдовій Могилі.<p/><p>Особливою подією стала молитва Святішого Отця папи Івана-Павла ІІ 23 червня 2001 р. в часі апостольської подорожі в Україну. Це була перша Церква, куди прийшов молитись Святіший Отець Іван-Павло ІІ перед образом перевезеної Зарваницької Богородиці. Він просив Заступництва Богородиці для своєї подорожі і України.<p/><p>Осінь 2004 р. Сприянням парафії поставлена Хресна дорога на славу Господа і Спасіння України з Благословення єпископа Михаїла Сабриги с. Задрість-Зарваниця, від Патріарха Йосипа Сліпого до Матері Божої Зарваницької. Донеї йдуть численні паломники з Києва і цілої України, несучи свої благання, свої надії.<p/><p>Чудом Божим постала Вільна незалежна Україна, вимолена і відмолена в Господа свідомою боротьбою і молитвою найдостойніших синів і дочок України, кров’ю замучених по тюрмах і Сибірах, заморених голодом, а разом із нею і наша парафія. Так і зростаємо ми разом.<p/><p>Щиро вітаю парафіян, киян, численних гостей і паломників з ювілеєм 15-ліття парафії і 1125-літтям пам’яті Київського царя і мученика за віру Христову.<p/><p>Божого Благословення, здоров’я і радості вам у Господі.<p/><p>З любов’ю, Ігор Онишкевич, <p/><p>греко-католицький священик і зачинатель молитви на Аскольдовій Могилі.<p/>','О. Ігор Онишкевич: Нове життя відновленої святині','uk',155,1135,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>У неділю 11 листопада 2007 року Божественну літургію в Церкві святого Миколая Чудотворця, що на Аскольдовій Могилі у стольному граді Києві, відправив владика Богдан Дзюрах. <p/>','2007-11-13 07:15:35',NULL,'P','<p>У неділю 11 листопада 2007 року Божественну літургію в Церкві святого Миколая Чудотворця, що на Аскольдовій Могилі у стольному граді Києві, відправив владика Богдан Дзюрах. <p/><p>Чисельна громада радо вітала владику, просила Божого благословення для всієї України. У проповіді владика наголосив про те, що віра кожного з нас повинна бути діяльною, що вірою ми повинні повсякчас свідчити проте, що ми є християни – любити ближнього свого. Наприкінці Божественної літургії владики привітав співом Многая і благая літа усіх парафіян а також усіх іменинниць, наречених іменем Анастасія, і окремо парафіяльне подружжя Оксани і Юрія, які повінчалися напередодні і є учасниками хору Церкви святого Миколая Чудотворця на Аскольдовій Могилі. <p/><p>Парафіяни щиро дякували владиці Богдану Дзюраху, адміністратор парафії вітав владику співаючи Многая і балаган літа, просив Божого благословення на його труди, щоб Дух святий сприяв, переказав владиці, що завжди парафіяни просять його на Святу літургію, щоб у цьому святому місці з тисячолітньою історією нашої церкви, нашої духовності була і його молитва. <p/>','Богдан Дзюрах: \"Віра повинна бути діяльною\"','uk',227,1207,0,NULL),
(0,NULL,'<p>У неділю 18 листопада газеті \"Хрещатик\" стало відомо, що керівництво ДАІ надіслало лист предстоятелям церков із проханням у проповідях нагадувати парафіянам про дотримання правил дорожнього руху.<p/>','2007-11-19 08:20:41',NULL,'P','<p>У неділю 18 листопада газеті \"Хрещатик\" стало відомо, що керівництво ДАІ надіслало лист предстоятелям церков із проханням у проповідях нагадувати парафіянам про дотримання правил дорожнього руху. За інформацію газети “Хрещатик”, такі звернення адресовано предстоятелям усіх церков України. <p/><p>До такого кроку спонукала надзвичайно критична ситуація із ДТП. Тотальний радянський контроль і суворі покарання зникли, а моральний стрижень людей спрацьовує далеко не завжди. За даними ДАІ, лише за 9 місяців цього року на автошляхах сталося понад 51 тис. ДТП із потерпілими, в яких 7,5 тис. осіб загинули й більш як 63,5 тис. травмувалися. Щоб відчути, наскільки величезною є ця цифра, можна порівняти — за всі дев’ять років перебування радянських військ в Афганістані загинуло в кілька разів менше вихідців з України, ніж за рік у ДТП. Нагадування парафіянам у проповідях про дотримання правил безпечного поводження на вулицях й автошляхах стало б важливим елементом у профілактиці аварійності, переконані в МВС. Бо церква є однією із суспільних інституцій, що впливає на думки людей ще на стадії їхнього зародження.<p/><p><p/><p>Адміністратор церкви на Аскольдовій могилі отець Ігор заявив “Хрещатику”, що церква не проти долучитися до ініціативи МВС. “Я сам водій. Жертв стільки, як на війні. Я особисто звертатимуся у проповідях, і ми разом з парафіянами молитимемося, щоб на шляхах стало спокійніше”,—сказав священик.<p/><p>http://www.kreschatic.kiev.ua/ua/3187/art/1195417128.html<p/>','О. Ігор: на дорогах жертв, як на війні','uk',234,1214,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>У чому полягає сила слова? Чому у Бога не залишиться безсилим ніяке слово (Лук.1:37)? Даніела Деребера, для \"Оранти\"</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2008-02-01 11:39:58',NULL,'P','<p>У чому полягає сила слова? Чому у Бога не залишиться безсилим ніяке слово (Лук.1:37)? Даніела Деребера, для \"Оранти\"</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>З Священного писання ми знаємо, що Слово боже є проявом Його сили, а одним з вищих проявів Його сили є створення світу. По силі дії Слово Боже подібне до молота, що розбиває скелю (Ієр 23:29). Людина живе словом, що виходить з уст Господа (Втор.8:3). Але чому все ж слова одних з нас мають силу, вагу, значення для інших людей, ведуть за собою народи, а слова інших ми не хочемо чути, відчуваємо негативні емоції, гнів, роздратування?</p>\r\n<p>&nbsp;</p>\r\n<p>Чи стане мудрий відповідати знанням порожнім і наповнювати живіт свій вітром пекучим, виправдовуватися словами даремними і мовою, що не має ніякої сили? (Іов.15:2-4).</p>\r\n<p>&nbsp;</p>\r\n<p>Ймовірно, велика таємниця слова прихована в його дусі, а точніше, в образі, який стоїть за КОЖНИМ словом. &laquo;Життя і смерть у владі мови&raquo; (Прит.18:22). За кожним словом є певний образ, і, вимовляючи слова, ми тим самим задаємо певну програму нашій підсвідомості. Підсвідомість (або совість, тобто наш безпосередній зв\'язок з Богом), що не розуміє жартів, сарказму, несерйозності, сприймає буквально кожен образ (слово) обов\'язково утілить його з духовної сфери у сфері матеріальній (причому негайно!), а ми зберемо стократний урожай з того, що воно втілило.</p>\r\n<p>&nbsp;</p>\r\n<p>Зайве дошкуляти собі роздумами і пошуками прикладів. Скільки разів ми жартома говорили один одному: &laquo;Поїдемо на метрі&raquo;? Правописна комісія при уряді ухвалила, що слово &laquo;метро&raquo; в українській мові повинне відмінюватися, і тепер ми дійсно їздимо на метрі. Принаймні, поки. Закон сіяння і жнив спрацює завжди бездоганно, женці завжди наздоженуть сіяча, тобто людини, що вимовила слово, хочемо ми цього, чи ні, готові ми до таких жнив або не готові, подобаються нам плоди або не подобаються.</p>\r\n<p>&nbsp;</p>\r\n<p>Давайте дуже уважно розглянемо слова пророка Ісаїі, які мають безпосереднє відношення до нашого часу: Той, хто ходить у правді і говорить істину; хто зневажає користь від утиску, утримує руки свої від хабарів, затуляє вуха свої, щоб не чути про кровопролиття, і закриває очі свої, щоб не бачити зла; той буде на висотах; притулок його - неприступні скелі; хліб буде йому; вода у нього не вичерпається (Іс.33:15-16). Зауважте на словах хто. затуляє вуха свої, щоб не чути про кровопролиття. Скільки разів на день ви дозволяєте собі обговорити з друзями жахливу політичну ситуацію в країні? Ви вже назвали її жахливою, Ви вже посіяли сім\'я, Ви вже споживаєте плоди Ваших слів (або насіння). Ви спустошили і пограбували себе, свій народ і свою країну, позбавили себе доброго і мирного життя своєю власною мовою. Горе тобі, спустошувач, який не був спустошуваний, і грабіжник, якого не грабували! Коли кінчиш спустошення, будеш спустошений і ти; коли припиниш грабіжництво, розграбують і тебе (Іс.33:1). Але ми пам\'ятаємо, що вірний Бог, і що те, що ви зв\'яжете на землі, то буде зв\'язано на небі; і що дозволите на землі, то буде дозволено на небі (Матф.18:18).</p>\r\n<p>&nbsp;</p>\r\n<p>Ось лише деякі слова, якими ми найчастіше чинимо, м\'яко кажучи, неприємності і собі, і людям:</p>\r\n<p>&nbsp;</p>\r\n<p>- усюди грабують</p>\r\n<p>&nbsp;</p>\r\n<p>- країна банкірів і злодіїв</p>\r\n<p>&nbsp;</p>\r\n<p>- постійно когось убивають</p>\r\n<p>&nbsp;</p>\r\n<p>- вулицями страшно ходити</p>\r\n<p>&nbsp;</p>\r\n<p>- у уряді одні бандити</p>\r\n<p>&nbsp;</p>\r\n<p>- грошей немає і не буде</p>\r\n<p>&nbsp;</p>\r\n<p>- нехай вона буде проклята, ця зарплата</p>\r\n<p>&nbsp;</p>\r\n<p>- клята, ця робота</p>\r\n<p>&nbsp;</p>\r\n<p>- клята, ця Влада</p>\r\n<p>&nbsp;</p>\r\n<p>- збираємо гроші про чорний день (проголосили його в своєму житті, отже обов\'язково настане, чекайте)</p>\r\n<p>&nbsp;</p>\r\n<p>Ви вже прокляли і уряд, і країну, і свою роботу, і багато що інше. Ви називаєте себе християнами, так чому ж забуваєте про головне &ndash; Отже перш за все прошу чинити молитви, прохання, моления, подяки за всіх людей, за царів і за всіх керманичів, щоб нам життя було тихе і безтурботне у всякому благочесті і чистоті, бо це добре і угодно спасителю нашому &ndash; Богові (1Тим.2:1-4). Зауважте &ndash; за царів і за всіх керманичів потрібно молитися перш за все, щоб нам життя було тихе і безтурботне. Це угодно спасителю нашому &ndash; Богові.</p>\r\n<p>&nbsp;</p>\r\n<p>Давайте просто зараз зв\'яжемо на землі кожне гниле слово, кожне гниле сповідання, сказане нами в своє життя, в життя наших дітей, в життя нашої країни і попросимо прощення у Господа і спасителя нашого Ісуса Христа за все зло, яке створили перед Ним своїми язиками, і розв\'яжемо &ndash; кожен в своїй простій, щирій молитві &ndash; благословенну, повне віри і любові життя. І Господь, який хоче, щоб всі люди врятувалися і досягли пізнання істини (1Тим.2:4), почує нас і дасть нам те, про що просимо.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Далі буде</p>\r\n<p>&nbsp;</p>\r\n<p>Даніела Деребера</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','Життя і смерть у царстві мови','uk',306,1286,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>УКРАЇНСЬКА ГРЕКО-КАТОЛИЦЬКА ЦЕРКВА (УГКЦ) є найбільшою Східною Католицькою Церквою свого права (Ecclesia sui juris). <p/><p><strong>URL: <a href=\"http://www.ugcc.org.ua\">http://www.ugcc.org.ua</a></strong><p/>','2006-12-25 15:43:27',NULL,'P','<p>УКРАЇНСЬКА ГРЕКО-КАТОЛИЦЬКА ЦЕРКВА (УГКЦ) є найбільшою Східною Католицькою Церквою свого права (Ecclesia sui juris). <p/><p><strong>URL: <a href=\"http://www.ugcc.org.ua\">http://www.ugcc.org.ua</a></strong><p/><p>УГКЦ належить до групи Церков візантійського обряду, які перебувають у повному сопричасті з Римcьким Архиєреєм і визнають його духовну та юрисдикційну владу. У цьому контексті \"обряд\" означає літургічне, богословське, духовне та правове насліддя. <p/><p><dl><strong>На сайті представлено</strong> поточні події, прес релізи, матеріали про Церкву в Україні, Історію УГКЦ. Також, можна переглянути офіційні документи  та дізнатися про Церкву - її діяльність, організації, вірних. В розділі \"Бібліотека\" публікуються лекції, інтерв\'ю, статті, а також твори Отців Церкви, богословські книжки, фотографії, аудіо- та відеофайли.</dl><p/><p><dt>Діє рубрика \"Запитуємо священика\". Всі охочі можуть поставити свої запитання про віру, Церкву, мораль, духовність, і протягом короткого часу отримати на них відповіді від провідних священнослужителів нашої Церкви.</dt><p/>','Українська Греко-Католицька Церква','uk',125,1105,0,NULL),
(0,NULL,'<p>ХРЕСТ на місці початку будівництва Храму святого Йосипа Обручника Пречистої діви Марії у рідному селі патріарха Йосифа Сліпого, що споруджється стараннями парафіян Богдана і Світлани Тернопільських Цервкви св.<p/>','2007-10-20 11:04:22',NULL,'P','<p>ХРЕСТ на місці початку будівництва Храму святого Йосипа Обручника Пречистої діви Марії у рідному селі патріарха Йосифа Сліпого, що споруджється стараннями парафіян Богдана і Світлани Тернопільських Цервкви св. Миколая на Аскольдовій Могилі у Києві, освятили Нунцій Ітерович Владика Іван а також покійний нині світлої пам’яті владика Михаїл Сабрига. <p/><p><img <p/><p>src=\"http://www.askoldova-mohyla.org/files/images/hrest1.thumbnail.jpg\" align=\"left\"alt=\"hrest\"<p/><p>Ця проща триває  від села Задрість Тернопільської області до села Зарваниця що також на Тернопільщині. У селі Заздрість Патріарх Йосип Сліпий народився, а поблизу святого місця – села Зарваниці, де нині є комплекс храмів Зарваницької Мати Божої – він навчався і тому чимало виходив стежками, якими ішли прочани.<p/><p>У селі, де народився український святий, Любомир Гузар сказав:<p/><p>«Важливо не те, щоби якомога більше людей відвідали це святе місце, де народився великий Патріарх Йосип. Значно більш важливо, щоб ми навчилися жити і вірити так, як жив і вірив Патріарх Йосип»<p/><p>Блаженніший благословив учасників прощі від Заздрості до Зарваниці.<p/><p>Традиційно хід хода віруючих святими місцями відбувається протягом перших вихідних після чергової річниці з дня відходу у вічність Святого Йосипа Сліпого.<p/><p>У селі Заздрість Тернопільської області народився Патріарх Йосип Сліпий, заступництва якого щороку просять прочани з Києва та інших міст.  <p/><p>Цього разу прощу було відкладено на тиждень через освячення Собору святого Йосипа сліпого Обручника Пречистої Діви Марії у селі Заздрість, де народився Патріарх Йосип Сліпий. <p/><p>Збудований Храм є головною будівлею комплексу споруд Духовного центру Патріарха Йосипа Сліпого Обручника Пречистої Діви Марії у селі Заздрість Тернопільської області.<p/><p>Започатковано з ініціативи Всеукраїнського об’єднання   «Порозуміння», яке є родинним фондом Тернопільських – парафіян Церкви святого Миколая на Аскольдовій Могилі.<p/><p>Храм споруджено на пожертви Київської християнської громадськості, зокрема <p/><p>об’єднання  «Порозуміння», Петро Жука зі Львова, котрий надав понад 10 тис. доларів,<p/><p>громади села Заздрість і Єпархіального управління м. Тернопіль.  <p/><p>Проща отримала привітання від родини Ющенків, які подарували панікадило (центральну люстру) свяченому храму.<p/><p>Збудований Храм є головною будівлею комплексу споруд Духовного центру Патріарха Йосипа Сліпого Обручника Пречистої Діви Марії у селі Заздрість Тернопільської області<p/><p>14-16 вересня цього року відбулася чергова проща християнської громадськості міста Патріаршого осідку Української греко-католицької церкви – Києва та інших міст за заступництвом Патріарха Йосипа Сліпого Обручника Пресвятої Діви Марії. Насамперед на такі прощі виручають парафіяни Греко-католицької церкви, що розташована на святій землі Аскольдової могили на Дніпровських кручах, <p/>','Хрест у Заздрості','uk',209,1189,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Чи довіку бути нам під сузір\'ям Совка, або як збагнути гідність і покликання жінки.</p>\r\n<p>&nbsp;</p>\r\n<p>Професор Московської духовної академії диякон Андрій Кураєв запропонував вибрати інший день у році для вшановування жінок. Він відзначив, що за своїм первинним змістом 8 Березня - не день жінок, а \"день революціонерок, терористів жіночої статі, екстремістів XIX століття\". Якщо ми сьогодні по-справжньому стурбовані \"політичним і релігійним екстремізмом як серйозною глобальною загрозою\", то добре, сказав А. Кураєв, відмовитися від \"оспівування терористів минулих епох\". <a href=\"http://pravoslavye.org.ua/\">pravoslavye.org.ua</a></p>\r\n<p>&nbsp;</p>','2006-03-09 11:05:12',NULL,'P','<p>Чи довіку бути нам під сузір\'ям Совка, або як збагнути гідність і покликання жінки.</p>\r\n<p>&nbsp;</p>\r\n<p>Професор Московської духовної академії диякон Андрій Кураєв запропонував вибрати інший день у році для вшановування жінок. Він відзначив, що за своїм первинним змістом 8 Березня - не день жінок, а \"день революціонерок, терористів жіночої статі, екстремістів XIX століття\". Якщо ми сьогодні по-справжньому стурбовані \"політичним і релігійним екстремізмом як серйозною глобальною загрозою\", то добре, сказав А. Кураєв, відмовитися від \"оспівування терористів минулих епох\". <a href=\"http://pravoslavye.org.ua/\">pravoslavye.org.ua</a></p>\r\n<p>&nbsp;</p>\r\n<p>День 8 березня цього року припадає на початок Великого Посту. Отже нісенітним є для віруючої людини влаштовувати вітання, забави в память про щось так далеке від праведності й святкування того, що не є святе.</p>\r\n<p>&nbsp;</p>\r\n<p>Вже 2000 року Божого вшановувати дівчат і жінок, що якось за за думом Творця (див. Бут. 3.15) таємничо несуть у собі гідність і достоєнство Божої Матері Пресвятої Богородиці, о. Ігор (Онишкевич) започаткував у День Благовіщення. Цей день для хлопчиків і мужчин є шансом краще збагнути привілей кожної дівчини і жінки.</p>\r\n<p>&nbsp;</p>\r\n<p>К. Пантелей.</p>\r\n<p>&nbsp;</p>','Думки про вшанування дівчат і жінок','uk',52,1032,0,NULL),
(0,NULL,'<p>Чи може людина, що є сином всемогутнього Отця, що знаходиться в Його обіцяннях, зазнавати якісь нестаки? Якщо вам скажуть &laquo;Так!&raquo; - раджу змінити консультанта. Даніела Деребера</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Пам\'ятаєте старий &laquo;добрий&raquo; анекдот:</p>\r\n<p>&nbsp;</p>\r\n<p>Отаман зібрав перед штабом своїх людей і запитує:</p>\r\n<p>&nbsp;</p>\r\n<p>- Хлопці, чи потрібні птахам гроші?</p>\r\n<p>&nbsp;</p>','2008-02-21 12:18:42',NULL,'P','<p>Чи може людина, що є сином всемогутнього Отця, що знаходиться в Його обіцяннях, зазнавати якісь нестаки? Якщо вам скажуть &laquo;Так!&raquo; - раджу змінити консультанта. Даніела Деребера</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Пам\'ятаєте старий &laquo;добрий&raquo; анекдот:</p>\r\n<p>&nbsp;</p>\r\n<p>Отаман зібрав перед штабом своїх людей і запитує:</p>\r\n<p>&nbsp;</p>\r\n<p>- Хлопці, чи потрібні птахам гроші?</p>\r\n<p>&nbsp;</p>\r\n<p>- Ні, батько, - відповідали ті.</p>\r\n<p>&nbsp;</p>\r\n<p>- Орли, я пропив вашу зарплату!</p>\r\n<p>&nbsp;</p>\r\n<p>Деякі християни (щоб не сказати більшість) інколи нагадують мені вищезазначених &laquo;орлів&raquo;. З невідомих їм причина вони вважають, що гроші &ndash; це зло. Примудряються посилатися при цьому на Священне писання.</p>\r\n<p>&nbsp;</p>\r\n<p>Починаєш питати: а як же Давид, Соломон, інші пророки, сам Ісус? І чуєш парадоксальну відповідь: &laquo;так тож Ісус!&raquo; Або: &laquo;так тож пророки! Чого ви рівняєте?!&raquo;. Хоча, хіба є кращі приклади для наслідування?!</p>\r\n<p>&nbsp;</p>\r\n<p>І ще один парадокс. Чомусь у багатьох християн склалася думка, що Ісус терпів нестатки матеріальних благ під час свого земного служіння. Так, дійсно, Він казав, що &laquo;лисиці мають нори і птахи небесні - гнізда, а Син людський не має, де прихилити голову&raquo; (Матф.8:20). Але Він мав на увазі людське серце, в якому немає місця Богові, а не житло або будинок.</p>\r\n<p>&nbsp;</p>\r\n<p>Він мав від Отця все необхідне для свого служіння &ndash; і свій будинок, і свого скарбника, і багаті побожні люди приносили Йому від свого майна. Якщо вам ваш Отець не дав всього з надлишком для вашого служіння, це означає, що ви так Йому служите.</p>\r\n<p>&nbsp;</p>\r\n<p>Почитайте послання Апостолів і переконайтеся в тому, що ви правильно молитеся, правильно мислите, правильно розумієте учення Господнє. Якщо виявиться, що ви щось робите не так, прочитайте молитву покаяння, і почніть поступати по-іншому. Все у ваших руках.</p>\r\n<p>&nbsp;</p>\r\n<p>Для чого Бог пообіцяв кожному, хто вірить Йому, зробити так, щоб &laquo;ви всім багаті були на всяку щедрість, яка через нас проводить дяку Богові&raquo; (2Кор.9:11)?</p>\r\n<p>&nbsp;</p>\r\n<p>&laquo;Всім багаті були&raquo; &ndash; це означає взагалі ВСІМ. Я можу прославляти Його ім\'я лише тими засобами, які Він мені дає. А якщо Він сам говорить, що Його ім\'я ті, що люблять Його (Бога), повинні прославляти милостинею, подаянням, десятиною, то як же Він не забезпечить нас засобами, що дають нам можливості здійснювати Його ж заповіді? Треба лише правильно просити Його забезпечити нас всім необхідним.</p>\r\n<p>&nbsp;</p>\r\n<p>Правильно &ndash; це означає шанувати Його закон, це означає доводити свою віру справами, не прагнути виконувати Його заповіді, а жити в них. Давид сказав: &laquo;Пастир мій; я ні в чому не матиму потреби&raquo; (Пс.22:1). Зауважте, &ndash; &laquo;Пастир мій&raquo;, тобто Той, на Якого я покладаюся в усьому, покладаю все своє життя, всі свої турботи і справи, потреби, радощі, проблеми, &ndash; ВСЕ.</p>\r\n<p>&nbsp;</p>\r\n<p>Хто такий пастир? &ndash; це той, хто забезпечує мою безпеку, мій прожиток, той, хто піклується про мене в кожній сфері мого життя. Але якщо людина вважає, що в неї є причини не робити так, як пропонує їй Бог &ndash; в такому разі &laquo;не думає така людина отримати що-небудь від Бога&raquo; (Іак.1:7). Навчитеся правильно відноситися до Бога &ndash; лише в цьому випадку ви навчитеся правильно просити Його. Отже, &laquo;якщо ви, будучи злі, умієте дари благі давати дітям вашим, тим більше Отець ваш Небесний дасть блага тим, хто просить у Нього&raquo; (Матф.7:11).</p>\r\n<p>&nbsp;</p>\r\n<p>Я наведу у приклад лише кілька принципів із Священого письма, яких необхідно дотримувати, щоб мати:</p>\r\n<p>&nbsp;</p>\r\n<p>- Не відмовляй в благодіянні тому, хто має потребу, коли рука твоя в силі зробити його (благодіяння) (Прит.3:27).</p>\r\n<p>&nbsp;</p>\r\n<p>- Шануй Господа від майна твого і від початків усіх прібутків твоїх, і наповняться скарбниці твої до надлишку, і точила твої будуть переливатися новим вином (Прит.3:9-10).</p>\r\n<p>&nbsp;</p>\r\n<p>- Коли багатство примножується, не прикладайте [до нього] серця (Пс.61:11).</p>\r\n<p>&nbsp;</p>\r\n<p>- Принесіть усі десятини у дім сховища, щоб у домі Моєму була їжа, і хоча б у цьому випробуйте Мене, - говорить Господь Саваоф: чи не відкрию Я для вас отворів небесних і чи не виллю на вас благословення до надлишку? (Мал.3:10).</p>\r\n<p>&nbsp;</p>\r\n<p>- Не піклуйся про те, щоб нажити багатство; залиш такі думки твої (Прит.23:4).</p>\r\n<p>&nbsp;</p>\r\n<p>Поки що достатньо. Але для того, щоб мати досконале знання, необхідно мати досконале учення. Вивчайте Священне писання, і ви знайдете відповіді на всі питання, що цікавлять вас.</p>\r\n<p>&nbsp;</p>\r\n<p>Даніела (Таїсія) Деребера, керівник проекта \"Формула життя\".</p>\r\n<p>&nbsp;</p>\r\n<p>Спеціально для &ldquo;Оранти&raquo;</p>\r\n<p>&nbsp;</p>','Формула життя: мати серце багатія','uk',319,1299,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<p>Щиро запрошуємо вас, ваших рідних, друзів та близьких 31 серпня 2008 о 12:30<p/><p>на велике свято благословення Божого на початок навчального року,<p/><p>котре розпочнеться Божественною літургією, молебнем посвяти дітей, концертом “Аскольдів глас” та обов’язковою гостиною для усіх дітей!<p/><p>Чекаємо на вашу підтримку та пропозиції. <p/>','2008-07-18 12:14:54',NULL,'P','<p>Щиро запрошуємо вас, ваших рідних, друзів та близьких 31 серпня 2008 о 12:30<p/><p>на велике свято благословення Божого на початок навчального року,<p/><p>котре розпочнеться Божественною літургією, молебнем посвяти дітей, концертом “Аскольдів глас” та обов’язковою гостиною для усіх дітей!<p/><p>Чекаємо на вашу підтримку та пропозиції. <p/><p>Громада церкви св. Миколая на Аскольдовій могилі<p/>','Свято благословення Божого школярикам','uk',408,1388,0,NULL),
(0,NULL,'<p>Щиросердно дякуємо парафіянам церкви св. Дженаро з м. Беневенто (Італія) та особисто парохові цієї церкви дону Паскуале за допомогу, яку вони надають Благодійному фондові \"Карітас-Київ\". У березні 2005 р. ми отримали гуманітарну допомогу (одяг) та розподіляємо її серед дітей із малозабезпечених, багатодітних та неповних родин, а також серед дітей-інвалідів та дітей-сиріт.</p>\r\n<p>&nbsp;</p>','2006-05-16 15:57:11',NULL,'P','<p>Щиросердно дякуємо парафіянам церкви св. Дженаро з м. Беневенто (Італія) та особисто парохові цієї церкви дону Паскуале за допомогу, яку вони надають Благодійному фондові \"Карітас-Київ\". У березні 2005 р. ми отримали гуманітарну допомогу (одяг) та розподіляємо її серед дітей із малозабезпечених, багатодітних та неповних родин, а також серед дітей-інвалідів та дітей-сиріт.</p>\r\n<p>&nbsp;</p>\r\n<p>Людям доброї волі, великого милосердя з церкви св. Дженаро завдячуємо працею Благодійної їдальні, яка харчує дітей.</p>\r\n<p>&nbsp;</p>\r\n<p>Від імені цих дітей вітаємо парафіян церкви св. Дженаро зі світлим святом Воскресіння Христового. Миру радості й Божого благословення Вам і Вашим родинам!.</p>\r\n<p>&nbsp;</p>\r\n<p>о. Ігор Онишкевич</p>\r\n<p>&nbsp;</p>\r\n<p>Sinceramente diciamo grazia ai benefattori dal parocchia S.Gennaro, Benevento, Italia e in persona Don Pascuale per aiuto particolar eggiato a fondo beneficio \"Karitas Kyiv\". In marzo 2005 abbiamo ricevuto aiuto umanitario (vestiti), quali distribuiamo fra i bambini dal familie con prole numerose e indigente, bambini mutilati e orfani.</p>\r\n<p>&nbsp;</p>\r\n<p>Grazia ai genti di buona volonta, grande missericordia dai parocchia S.Gennaro, abbiamo possibilita mantenere \"La mensa beneficenza\" per bambini che sofrano. In nome di questi bambini tanti auguri e buona Pascua ai tutti dal parocchia S.Gennaro.</p>\r\n<p>&nbsp;</p>\r\n<p>Pace, gioia e benedizione di Dio peri vostri familie!</p>\r\n<p>&nbsp;</p>\r\n<p>Padre Igor Onyschkevych</p>\r\n<p>&nbsp;</p>','ПОДЯКА ВІД \"КАРІТАС-КИЇВ\"','uk',113,1093,0,NULL),
(0,NULL,'<pre style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про дозвіл на використаннякоштів відновлюючої вартості<br />для започаткування парку \"Калиновий гай\" на схилах Дніпра На<br />виконання окремого доручення Прем\'єр-міністра України Ю. Єханурова від<br />06.11.2005 \"Про закладання на схилах Дніпра парку \"Калиновий гай\"<br />як невід\'ємної складової частини меморіального комплексу жертв голодоморів і<br />політичних репресій в Україні\":</span></span></pre>\r\n<p>&nbsp;</p>','2005-12-06 19:55:12',NULL,'P','<pre style=\"text-align: justify;\"><p style=\"text-align: center;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">КИЇВСЬКА МІСЬКА ДЕРЖАВНА АДМІНІСТРАЦІЯ </span></span></p><p style=\"text-align: center;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Р О З П О Р Я Д Ж Е Н Н Я </span></span></p><p style=\"text-align: justify;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">N 2242 від<br />06.12.2005</span></span></p><p style=\"text-align: justify;\"><span style=\"font-size: x-small;\"></span></p><p><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про дозвіл на використаннякоштів відновлюючої вартості<br />для започаткування парку \"Калиновий гай\" на схилах Дніпра На<br />виконання окремого доручення Прем\'єр-міністра України Ю. Єханурова від<br />06.11.2005 \"Про закладання на схилах Дніпра парку \"Калиновий гай\"<br />як невід\'ємної складової частини меморіального комплексу жертв голодоморів і<br />політичних репресій в Україні\":</span></span></p><p><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Дозволити Київському комунальному<br />об\'єднанню зеленого будівництва та експлуатації зелених насаджень міста<br />\"Київзеленбуд\" використати 230 тис. грн. Коштів відновлюючої<br />вартості&nbsp; для закупівлі саджанців калини, підготовки посадочних місць, завезення<br />рослинного ґрунту і створення вхідної групи до парку \"Калиновий гай\" з<br />боку площі Андрія Первозванного та виконання інших робіт, пов\'язаних з висаджуванням<br />калини 26.11.2005 та інших дерев і кущів до згаданої дати.</span></span></p><p><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Контроль за виконанням цього<br />розпорядження покласти на заступника голови Київської міської державної адміністрації<br />Данькевича І.П.</span></span></p><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br />&nbsp;Голова   О.Омельченко</span></span><p style=\"text-align: justify;\"><span style=\"font-size: x-small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://lawua.info/</span></span></p>\r\n\r\n\r\n\r\n</pre>','Про дозвіл на використання коштів відновлюючої вартості для започаткування парку \"Калиновий гай\" на схилах Дніпра','uk',1025,1999,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(0,NULL,'<ul>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 14 коробів+3 мішки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри дитячі &ndash; 2 коробки</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">книжечки дитячі</span></li>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">прокладки антисептичні для хворих &ndash; 1 упаковка</span></li>\r\n</ul>','2013-06-21 09:56:26',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Тріумф серця&raquo;, розподілила їх таким чином:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; уділила потребуючим мирянам;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&mdash; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">одяг чоловічий, жіночий, дитячий &ndash; 14 коробів+3 мішки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">взуття &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">іграшки дитячі &ndash; 1 мішок</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ігри дитячі &ndash; 2 коробки</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">книжечки дитячі</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">прокладки антисептичні для хворих &ndash; 1 упаковка</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Робота виконана &nbsp;силами волонтерів: п.Мирослави, п.Віри, п.Світлани, п.Анни.</span></p>','ЗВІТ №13 про розподілення гуманітарної допомоги від БФ «Тріумф серця», а також благодійних пожертвувань парафіян церкви св. Миколая на Аскольдовій могилі (17.05.2013 р.)','uk',3365,4848,0,'Любов Михайлюк'),
(1,1,'<!--TYPO3SEARCH_begin-->\n<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3495,200,ad2a24ce7b.jpg\" width=\"86\" height=\"120\" />Коротка історія</p>\n<p align=\"justify\">Празник торжественного в\'їзду  Ісуса Христа до Єрусалима належить до найдавніших празників у Східній  Церкві. Свідчення про його святкування сягають III століття. Святкування  цього празника почалося в Єрусалимі і невдовзі стало празником усієї  Східної Церкви.\n','2010-03-25 11:58:21','http://oranta.org/index.php?option=com_content&view=article&id=511:nedilja-kvitna-vhid-gospodni-u-jerusalym&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Неділя Квітна. Вхід Господній у Єрусалим','uk',2034,3517,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--TYPO3SEARCH_begin-->\n<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3495,200,ad2a24ce7b.jpg\" width=\"86\" height=\"120\" />Коротка історія</p>\n<p align=\"justify\">Празник торжественного в\'їзду  Ісуса Христа до Єрусалима належить до найдавніших празників у Східній  Церкві. Свідчення про його святкування сягають III століття. Святкування  цього празника почалося в Єрусалимі і невдовзі стало празником усієї  Східної Церкви.\n','2010-03-25 11:58:21','http://www.oranta.org/index.php?option=com_content&view=article&id=511:nedilja-kvitna-vhid-gospodni-u-jerusalym&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Неділя Квітна. Вхід Господній у Єрусалим','uk',2014,3497,0,NULL),
(1,1,'<!--[if !mso]> v\\:* {behavior:url(#default#VML);} o\\:* {behavior:url(#default#VML);} w\\:* {behavior:url(#default#VML);} .shape {behavior:url(#default#VML);}  <![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5131,200,P1010089.JPG\" />Минула субота, 28 вересня, для парафіян Церкви Миколи Мерликійського на Аскольдовій Могилі минула у Чернігові. До міста з 1300-літньою історією великим автобусом вирушили понад 50 людей, половина з них – діти, вихованці катехитичної школи, а також діти парафіяльної служби Божого милосердя Карітас-Печерськ. Мандрівка, як годиться, почалась з Молебня до Богородиці в храмі Петра і Павла, яку відправив о. Анатолій Тесля, &nbsp;духівник прощі. Далі гості поринули у сиві часи давнини, відвідуючи храми, в яких молились ще чернігівські князі.</p>\n','2013-10-04 22:57:19','http://oranta.org/index.php?option=com_content&view=article&id=1526:duhovna-proshcha-do-chernigova&catid=25:parafija-cat&Itemid=27','P',NULL,'Духовна проща до Чернігова','uk',3649,5132,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5096,200,agenda-de-francisco-.jpg\" />Примітка редактора: Це інтерв’ю з Папою Франциском відбулося під час трьох зустрічей у серпні 2013&nbsp;р. у Римі. Інтерв’ю взяв Антоніо Спадаро&nbsp;SJ, головний редактор La</em> <em>Civiltà Cattolica&nbsp;— італійського єзуїтського видання. Інтерв’ю проводилось від імені La</em> <em>Civiltà Cattoliа, America, та кількох інших єзуїтських ЗМІ. Редакційні колегії кожного видання готували питання і передавали їх о.&nbsp;Спадаро, а той їх систематизував. Розмова провадилася італійською мовою.</em>\n','2013-09-22 02:21:38','http://oranta.org/index.php?option=com_content&view=article&id=1491:velyke-serce-vidkryte-bogovi-intervju-z-papoju-francyskom-ch-1&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Велике серце, відкрите Богові: інтерв’ю з Папою Франциском (ч. 1)','uk',3614,5097,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img src=\"http://askoldova-mohyla.org/uk/image,5485,200,5w1Mmd.jpg\" style=\"margin: 10px; float: right;\" />Греко-католицький єпископат оприлюднив офіційне звернення з приводу ситуації, що склалась у державі. Під заявою поставили свої підписи владики та єпископи Української Греко-Католицької Церкви, серед яких також і сам Патріарх Святослав (Шевчук) разом з Блаженнішим Любомиром (Гузаром), повідомляє&nbsp;<a target=\"_blank\" href=\"http://news.ugcc.org.ua/articles/zvernennya_grekokatolitskogo_iepiskopatu_ukrainiz_privodu_susp%D1%96lnopol%D1%96tichnoi_situats%D1%96i_v_derzhav%D1%96_69031.html\">офіційний сайт</a> УГКЦ. У підсумку документа духовенство закликає безперервну цілодобову молитву за Україну, як і приватні молитви; оголосити строгий піст у Єпархіях та Екзархатах за розкладом; читати молитви Андрія Шептицького за кращу долю українського народу.</p>\n','2014-02-01 12:39:20','http://oranta.org/index.php?option=com_content&view=article&id=1823:zvernennja-ugkc-z-pryvodu-sytuaciji-v-krajini&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'ЗВЕРНЕННЯ УГКЦ З ПРИВОДУ СИТУАЦІЇ В КРАЇНІ','uk',4003,5486,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4965,200,66752_412593348849524_602197989_n.jpg\" />Слава Ісусу Христу!</p>\n<p>Несподіване запрошення і спонтанна відповідь – можливо змінять ваше життя і долю, або, чи стане дане запрошення, як своєрідний тест ефективності віртуального спілкування задля реального єднання…</p>\n<p>*** Ким і якими є сьогодні слуги Божі?! – Складне і дратівливе це питання Є, надто ж, для сучасного суспільства…</p>\n','2013-08-03 13:43:38','http://oranta.org/index.php?option=com_content&view=article&id=1388:veloproshcha-zarvanycja-kyjiv-2013&catid=27:gk-v-ukrajini-cat&Itemid=57','P','','Велопроща Зарваниця-Київ 2013','uk',3483,4966,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,200905080849270.jpg\" />Прагнення греко-католиків України віднайти та <a href=\"http://catholicnews.org.ua/ugkc-hoche-perepohovati-mitropolita-isidora-v-kiievi\">перенести в Патріярший Собор Воскресіння Христового мощі першого київського кардинала – митрополита Київського Ізидора</a>, одного з найактивніших прихильників поєднання Церков, <a href=\"https://www.facebook.com/yevstr/posts/543244802390475\" target=\"_blank\">викликало в соціальних мережах правдиву бурю обурення київських і московських православних України</a>.</p>\n<p>Здавалося б, ну, яке кому діло кого греко-католики вважають героями своєї віри. А тим більше Флорентійська Унія навіть при баламандській плутанині ну аж ніяк не підходить під прозелітизм – Константинопільська церква воз’єдналася з Римською. Ну, який же ж тут прозелітизм?</p>\n','2013-08-15 21:35:32','http://oranta.org/index.php?option=com_content&view=article&id=1413:ukrajinske-pravoslavja-zdajetsja-staje-doroslishym&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Українське православ’я, здається, стає дорослішим','uk',3512,4995,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,510737_1_300_0.jpg\" />Предстоятель УГКЦ Блаженніший Святослав висловив намір перенести до столиці України мощі Київського митрополита Ісидора, який від імені Руси у 1439 році підписав Флорентійську унію. Такий крок УГКЦ пов’язує із освяченням патріаршого храму в Києві та вважає, що він символізуватиме повернення УГКЦ «до дому».</p>\n<p>Така, на перший погляд, невинна заява уже викликала збурення у православному середовищі. Найтолерантніше висловився єпископ УПЦ-КП Євстратій Зоря, на думку якого, перенесення мощів Ісидора однозначно ускладнить діалог між УГКЦ і православними церквами в Україні. Натомість апологет УПЦ-МП Юрій Чорноморець у соціальних мережах назвав намір УГКЦ «плювком» у православних і… «розчарувався».</p>\n','2013-08-18 21:32:55','http://oranta.org/index.php?option=com_content&view=article&id=1412:kamin-spotykannja--mytropolyt-isydor&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Камінь спотикання – митрополит Ісидор','uk',3519,5002,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,510737_1_300_0.jpg\" />Предстоятель УГКЦ Блаженніший Святослав висловив намір перенести до столиці України мощі Київського митрополита Ісидора, який від імені Руси у 1439 році підписав Флорентійську унію. Такий крок УГКЦ пов’язує із освяченням патріаршого храму в Києві та вважає, що він символізуватиме повернення УГКЦ «до дому».</p>\n<p>Така, на перший погляд, невинна заява уже викликала збурення у православному середовищі. Найтолерантніше висловився єпископ УПЦ-КП Євстратій Зоря, на думку якого, перенесення мощів Ісидора однозначно ускладнить діалог між УГКЦ і православними церквами в Україні. Натомість апологет УПЦ-МП Юрій Чорноморець у соціальних мережах назвав намір УГКЦ «плювком» у православних і… «розчарувався».</p>\n','2013-08-19 21:32:55','http://oranta.org/index.php?option=com_content&view=article&id=1412:kamin-spotykannja-mytropolyt-isydor&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Камінь спотикання – митрополит Ісидор','uk',3521,5004,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,dzjurax_monashestvo.jpg\" />У третій день роботи Синоду Єпископів УГКЦ владика Богдан (Дзюрах), керівник Адміністрації Патріаршої курії, представив синодальним отцям звіт про діяльність Патріаршої курії за минулий рік.</p>\n<p>На початку доповіді владика Богдан відзначив, що «діяльність Патріаршої курії була передусім скерована на матірну територію, але ми щоразу більше намагаємося виходити на глобальний, міжнародний рівень».</p>\n','2013-08-14 21:28:33','http://oranta.org/index.php?option=com_content&view=article&id=1411:vladyka-bogdan-dzjurah-predstavyv-synodalnym-otcjam-zvit-pro-dijalnist-patriarshoji-kuriji-za-mynuly-rik&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Владика Богдан (Дзюрах) представив синодальним отцям звіт про діяльність Патріаршої курії за минулий рік','uk',3509,4992,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,kardynal-lehat.jpg\" />Напередодні свого візиту до Києва cпеціальний посланець Папи Франциска на святкуваннях 1025-річчя Хрещення Русі-України, кардинал Аюдріс Юзас Бачкіс, вислужений Архиєпископ Вільнюський, відповів на запитання Департаменту інформації УГКЦ.</p>\n<p><strong><em>Еміненціє, Папа Франциск призначив Вас своїм делегатом&nbsp; на святкування 1025-ліття Хрещення Київської Русі. Які найважливіші думки висловив Святіший Отець у своєму листі, адресованому Вашій Еміненції з цієї нагоди?</em></strong></p>\n','2013-08-16 21:38:49','http://oranta.org/index.php?option=com_content&view=article&id=1414:listorija-vashoji-cerkvy-naochno-pokazuje-svitovi-mozhlyvist-jednosti-hrystovyh-uchnivr-ajudris-juzas-bachkis&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Історія вашої Церкви наочно показує світові можливість єдності Христових учнів», - Аюдріс Юзас Бачкіс','uk',3514,4997,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5080,200,shevchuk_kolehiya2.jpg\" />Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) звернувся до Голови Верховної Ради України Володимира Рибака та голів депутатських фракцій Партії регіонів, ВО «Батьківщина», партії «УДАР Віталія Кличка» та ВО «Свобода» не підтримувати законопроекти №№ 2993, 3194 та 3007, метою яких є передача знакових християнських святинь одній конфесії.</p>\n','2013-09-16 22:51:21','http://oranta.org/index.php?option=com_content&view=article&id=1474:glava-ugkc-zaklykav-golovu-verhovnoji-rady-ukrajiny-ta-narodnyh-deputativ-ne-peredavaty-znakovyh-hrystyjanskyh-svjatyn-odni-konfesiji&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ закликав Голову Верховної Ради України та народних депутатів не передавати знакових християнських святинь одній конфесії','uk',3598,5081,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5117,200,65765.jpg\" />Канонізація, тобто, проголошення святими Католицької Церкви блаженних Пап Івана ХХІІІ та Івана Павла ІІ відбудеться 27 квітня 2014 р., в Неділю Божого Милосердя. Цю дату, разом з канонізаційними декретами, затверджено під час консисторії кардиналів, яка відбулася під проводом Римського Єпископа Франциска у понеділок, 30 вересня 2013 р.</p>\n<p>Йоан ХХІІІ стане святим за 50 років по смерті й 13&nbsp;років по беатифікації.</p>\n<p>Шлях померлого 2&nbsp;квітня 2005 року Йоана Павла&nbsp;ІІ до проголошення святим визнано за найшвидший у сучасній історії.</p>\n','2013-09-30 16:47:22','http://oranta.org/index.php?option=com_content&view=article&id=1511:-shljah-oana-pavla-ii-do-svjatosti&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Шлях Йоана Павла ІІ до святості','uk',3638,5121,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5189,200,946032_651225124920502_721062215_n.jpg\" />Глави традиційних українських Церков звернулися до вірних та всіх людей доброї волі із закликом долучитися до всеукраїнської громадської ініціативи – Всенародного дня пам’яті з нагоди 80-х роковин Голодомору.</p>\n<p>Цей заклик – солідарність з ініціативою Громадського комітету із вшанування пам’яті жертв Голодомору-геноциду 1932-1933 років в Україні, до якого увійшли відомі громадські, мистецькі діячі, науковці та представники українських Церков.</p>\n','2013-10-24 05:28:35','http://oranta.org/index.php?option=com_content&view=article&id=1573:glavy-ukrajinskyh-cerkov-zvernulysja-do-ukrajinskogo-narodu-z-nagody-80-h-rokovyn-golodomoru-genocydu-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Глави українських Церков звернулися до українського народу з нагоди 80-х роковин Голодомору-геноциду ','uk',3708,5191,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5199,200,dsc_0013_bc1be.jpg\" />Одна з дуже притаманних рис української культури, нашого національного спадку й гордості, яким український народ прославився на цілий світ, була, є і має бути особлива культура праці.</p>\n<p>Про це сьогодні під час проповіді до вірних у Патріаршому соборі Воскресіння Христового, що в Києві, говорив Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук).</p>\n','2013-10-27 23:05:02','http://oranta.org/index.php?option=com_content&view=article&id=1577:lmy-chujemo-pro-lljudski-resursyr-a-ne-pro-gidnist-pracivnykar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Ми чуємо про «людські ресурси», а не про гідність працівника»','uk',3717,5200,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5236,200,587398b-1.jpg\" />Співвідношення між Церквою Європи і позаєвропейськими Церквами найкраще розглядати з перспективи пособорової науки про communio ecclesiarum, спільності, яка формується обміном дібр і служб, її результатом є взаємозбагачення. Католицька Церква у Європі, особливо в Західній Європі, вже століттями живе поряд з протестантськими християнами, а на її Сході поряд з православними. Найкатолицькішим континентом є Латинська Америка. У Північній Америці католики складають відносну більшість.</p>\n','2013-11-07 20:08:55','http://oranta.org/index.php?option=com_content&view=article&id=1604:ivan-pavlo-ii-jevropa-v-konteksti-inshyh-kontynentiv&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Іван Павло ІІ: Європа в контексті інших континентів','uk',3754,5237,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5258,200,800px-Kuntsevich_by_Repin.jpg\" />Православні його затаврували як зрадника, а католики східного обряду вбачають у ньому символ єднання Східної Церкви з Престолом Святого Петра. Коли ж завершаться політично-релігійні поділи, залишиться тільки питання про те, чи любила ця людина Христа і чи жила цією любов’ю.</p>\n<p>Йосафат Кунцевич (1580-1623), хресне ім’я – Іван, народився в родині міщан у Володимирі-Волинському.</p>\n','2013-11-13 00:07:41','http://oranta.org/index.php?option=com_content&view=article&id=1632:-svjaty-osafat-jepyskop-i-muchenyk&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Святий Йосафат, єпископ і мученик','uk',3779,5262,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5268,200,RTEmagicC_svyatoslav_n_02.jpg.jpg\" />Ексклюзивне інтерв’ю Глави УГКЦ, Верховного Архиєпископа Києво-Галицького Блаженнішого Святослава (Шевчука) газеті «Слово Просвіти».</p>\n<p><strong>— Ваше Блаженство, хоч від освячення Патріаршого собору УГКЦ минуло понад два місяці, проте хотів би розпочати розмову від тієї події. Як свідчать давні літописи і хроніки XIX сторіччя, побудова великих храмів — знакова подія у тогочасному житті. </strong></p>\n','2013-11-16 02:39:53','http://oranta.org/index.php?option=com_content&view=article&id=1640:lpotreba-jednosti-je-duhovnym-zapytom-virujuchyh-hrystyjan-ukrajincivr&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Потреба єдності є духовним запитом віруючих християн-українців»','uk',3788,5271,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5451,200,408911.jpg\" />Преподобний Амвросій Оптинський полюбляв розповідати таку притчу:</p>\n<p>Їхав один чоловік ранком. Довкола юрма люду, гамір, галас, тлум. Та він на все це зовсім не зважав, а тільки помалу підганяв конячку: «Но-но! Но-но!» Так потрохи й переїхав через цілісінький ранок.</p>\n','2014-01-22 22:03:25','http://oranta.org/index.php?option=com_content&view=article&id=1791:molysja-i-dy-vpered-23-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молися і йди вперед (23 січня)','uk',3976,5459,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5117,200,ua.Just-a-little-self-denial....jpg\" />Про красу виховання у вірі Папа говорив до 1600 катехитів з усього світу. Він закликав їх мати відвагу торувати нові шляхи для звіщення Євангелія. Святіший Отець виголосив спеціальну катехезу про віру для учасників Міжнародного Катехитичного Конгресу, що зараз триває у Ватикані. Конгрес передує Дню Катехита, що пройде завтра в рамках святкування Року Віри.</p>\n','2013-09-30 17:02:13','http://oranta.org/index.php?option=com_content&view=article&id=1512:katehyt-ne-mozhe-buty-bojaguzom-chy-muzenym-eksponatom&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Катехит не може бути боягузом чи музейним експонатом','uk',3639,5122,0,NULL);
