INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5436,200,rus.jpg\" />На особливий плід Хрищення, яким є те, що воно вчиняє нас членами Христового Тіла та Божого люду, звернув увагу Папа Франциск. Розпочавши попередньої середи цикл повчань про Святі Таїнства, цього разу він знову говорив про Хрищення, зазначивши, що подібно як «з покоління у покоління передається життя, так само і з покоління в покоління, через відродження у хрищальній купелі, також передається благодать, і завдяки цій благодаті Божий люд прямує крізь час, немов ріка, яка напоює землю та поширює у світі Боже благословення».</p>\n','2014-01-16 05:13:04','http://oranta.org/index.php?option=com_content&view=article&id=1774:syloju-hryshchennja-my-je-uchnjamy-ta-misioneramy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Силою Хрищення ми є учнями та місіонерами!','uk',3955,5438,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5038,200,img_4478.jpg\" />Отець Василь Поточняк:</strong> <em>«Якщо ви відкриєте своє серце через покаяння, наблизитеся до Господа – не майте жодного сумніву в тому, що ви ще сьогодні будете зцілені у вірі в Бога!»</em></p>\n<p>У Таїнстві Єлеопомазання, у Сповіді, в Євхаристії – у &nbsp;близькому перебуванні нас у Бозі – &nbsp;відбувається зцілення любов’ю і вірою. Бог таїнственно здатний зцілювати те, що сатана викрадає, а тому через Таїнство єлеопомозання Святий Дух зцілює людину.</p>\n','2013-09-01 00:00:59','http://oranta.org/index.php?option=com_content&view=article&id=1438:kalictvo-fizychne--to-ne-problema-velyka-tragedija--kalictvo-duhovne-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Каліцтво фізичне – то не проблема. Велика трагедія – каліцтво духовне. ','uk',3557,5040,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5063,200,dtoPvMciCFY.jpg\" />Чесноту надії не слід плутати з людським оптимізмом. На це звернув увагу Папа Франциск: «Христос у вас – надія слави», він зазначив, що для християнина надією є особа Христа, Його сила визволити та вчинити новим кожне життя.</strong></p>\n<p>За словами Святішого Отця, надія є Ісусовим даром. Вона відрізняється від оптимізму, який є «лише людським наставленням, яке залежить від багатьох факторів».</p>\n','2013-09-09 16:58:35','http://oranta.org/index.php?option=com_content&view=article&id=1464:ne-plutate-nadiju-z-optymizmom-chy-horoshym-nastrojem&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Не плутайте надію з оптимізмом чи хорошим настроєм','uk',3582,5065,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5096,200,pope1_0.jpg\" />Досвід керівництва Церквою</strong></p>\n<p>Який досвід керівництва Церквою допоміг сформувати отця Берґольйо, що був провінціалом Товариства Ісуса? Стиль управління Товариства передбачає, що рішення ухвалює керівник, хоча й не без інтенсивних консультацій з радниками. Тож я питаю: «Чи вважаєте Ви, що Ваш досвід керівництва у минулому згодиться при керуванні Вселенською Церквою? Після короткого роздуму лунає відповідь:</p>\n','2013-09-22 02:28:02','http://oranta.org/index.php?option=com_content&view=article&id=1492:velyke-serce-vidkryte-bogovi-intervju-z-papoju-francyskom-ch-2&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Велике серце, відкрите Богові: інтерв’ю з Папою Франциском (ч. 2)','uk',3615,5098,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5102,200,000_Mvd6505043.jpg\" />Папа з чернечого ордену</strong></p>\n<p>Папа Франциск — перший Понтифік із чернечого ордену з часів камальдула Григорія&nbsp;XVI, обраного&nbsp;1831 року. Я&nbsp;запитую: «Яке особливе місце монахів та монашок в сьогоднішній Церкві?»\n','2013-09-23 19:07:47','http://oranta.org/index.php?option=com_content&view=article&id=1502:velyke-serce-vidkryte-bogovi-intervju-z-papoju-francyskom-ch-3&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Велике серце, відкрите Богові: інтерв’ю з Папою Франциском (ч. 3)','uk',3620,5103,0,NULL),
(1,1,'<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5127,200,996.jpg\" />По-перше, ангели могутні. Нехай вас не вводять в оману всі ці голенькі херувимчики, якими епоха бароко заселила нашу уяву. У Писанні перше, що завжди говорить людині ангел, коли з\'являється їй, – «Не бійся!» А що сказали б херувимчики з голими сідничками? Напевно, щось на кшталт: «підвяжи мені підгузник». Але згідно з давньою традицією, ангели вселяють благоговіння. Рільке каже, що ангел лякає. У чому ж їх могутність?</strong></p>\n','2013-10-03 11:44:29','http://oranta.org/index.php?option=com_content&view=article&id=1516:-hto-taki-angely-i-chym-vony-zamajutsja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Хто такі ангели і чим Вони займаються?','uk',3647,5130,0,NULL),
(1,1,'<a href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B0%D0%B9%D0%BB:Andrew_of_Crete.jpg\" title=\"Святий Андрій Критський та Свята Марія Єгипетська\"><img style=\"float: left;\" src=\"http://upload.wikimedia.org/wikipedia/commons/thumb/b/bb/Andrew_of_Crete.jpg/250px-Andrew_of_Crete.jpg\" height=\"327\" width=\"250\" /></a> <a href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B0%D0%B9%D0%BB:Andrew_of_Crete.jpg\" title=\"Збільшити\"><img src=\"http://uk.wikipedia.org/skins-1.5/common/images/magnify-clip.png\" height=\"11\" width=\"15\" /></a> Святий Андрей Критський та Свята Марія Єгипетська\n<p><strong>Святий Андрей Критський</strong> (<a href=\"http://uk.wikipedia.org/wiki/7_%D1%81%D1%82%D0%BE%D0%BB%D1%96%D1%82%D1%82%D1%8F\" title=\"7 століття\">7 століття</a>) - християнський <a href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B2%D1%8F%D1%82%D0%B8%D0%B9\" title=\"Святий\">святий</a>, богосолов та гимнотворець часів Візантії, родом зі Сирії. Був архієпископом міста Гортіна на грецькому острові <a href=\"http://uk.wikipedia.org/wiki/%D0%9A%D1%80%D1%96%D1%82\" title=\"Кріт\">Кріт</a>.</p>\n<p>Святитель Андрей, архиєпископ Критський, народився в місті Дамаску в сім\'ї благочестивих християн. До семирічного віку хлопчик був німий. Потім одного разу після <a href=\"http://uk.wikipedia.org/wiki/%D0%9F%D1%80%D0%B8%D1%87%D0%B0%D1%81%D1%82%D1%8F\" title=\"Причастя\">причастя</a> Святих Христових Таїн він почав говорити. З того часу юнак почав посилено вивчати Святе Писання і Богословські науки.</p>\n<p>Чотирнадцятилітнім він віддалився до <a href=\"http://uk.wikipedia.org/wiki/%D0%84%D1%80%D1%83%D1%81%D0%B0%D0%BB%D0%B8%D0%BC\" title=\"Єрусалим\">Єрусалиму</a> і там прийняв постриження в обителі преподобного Савви Освяченого. Святий Андрій проводив строге, цнотливе життя, був покірливий, стриманий, так що всі дивувалися з його чесноти і розуму. У 680 році містоблюститель Єрусалимської патріаршої катедри Теодор включив архідиякона Андрія в число представників Святого Міста на IV Вселенському Соборі. За правління імператора Юстиніана II (<a href=\"http://uk.wikipedia.org/wiki/685\" title=\"685\">685</a>-<a href=\"http://uk.wikipedia.org/wiki/695\" title=\"695\">695</a>) святий Андрій був рукопокладений на архієпископа міста Гортіни на острові Кріт. На новому місці він проявив себе як істинний світильник Церкви, великий ієрарх - Богослов, вчитель і гимнотворець.</p>\n<ul>\n<li> </li>\n</ul>\n<p> </p>\n<p>Пам\'ять - <a href=\"http://uk.wikipedia.org/wiki/17_%D0%BB%D0%B8%D0%BF%D0%BD%D1%8F\" title=\"17 липня\">17 липня</a> (Св. Андрея, архиєп. Критського. Преп. Марти, матері св. Симеона Дивногорця. Бл. свящмуч. Павла (Гойдича)) \n','2009-02-25 12:46:15','http://www.oranta.org/index.php?option=com_content&view=article&id=220:svjaty-andre-krytsky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Святий Андрей Критський','uk',1178,2152,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<a href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B0%D0%B9%D0%BB:Martyrdom_of_St_Ignatius_of_Antioch.jpg\" title=\"Мучеництво святого Игнатія\"><img style=\"float: right;\" src=\"http://upload.wikimedia.org/wikipedia/commons/thumb/c/c7/Martyrdom_of_St_Ignatius_of_Antioch.jpg/180px-Martyrdom_of_St_Ignatius_of_Antioch.jpg\" height=\"218\" width=\"180\" /></a> <a href=\"http://uk.wikipedia.org/wiki/%D0%A4%D0%B0%D0%B9%D0%BB:Martyrdom_of_St_Ignatius_of_Antioch.jpg\" title=\"Збільшити\"><img src=\"http://uk.wikipedia.org/skins-1.5/common/images/magnify-clip.png\" height=\"11\" width=\"15\" /></a>\n<p><strong>Ігна́тій Богоно́сець</strong> (<a href=\"http://uk.wikipedia.org/wiki/%D0%93%D1%80%D0%B5%D1%86%D1%8C%D0%BA%D0%B0_%D0%BC%D0%BE%D0%B2%D0%B0\" title=\"Грецька мова\">грец.</a> <em>Ιγνάτιος ο Θεοφόρος</em> ), († <a href=\"http://uk.wikipedia.org/wiki/107\" title=\"107\">107</a>, <a href=\"http://uk.wikipedia.org/wiki/%D0%A0%D0%B8%D0%BC\" title=\"Рим\">Рим</a>) - один з перших <a href=\"http://uk.wikipedia.org/wiki/%D0%9E%D1%82%D1%86%D1%96_%D0%A6%D0%B5%D1%80%D0%BA%D0%B2%D0%B8\" title=\"Отці Церкви\">отців Церкви</a>, <a href=\"http://uk.wikipedia.org/wiki/%D0%84%D0%BF%D0%B8%D1%81%D0%BA%D0%BE%D0%BF\" title=\"Єпископ\">єпископ</a> Антіохії, учень апостола <a href=\"http://uk.wikipedia.org/wiki/%D0%86%D0%B2%D0%B0%D0%BD_%D0%91%D0%BE%D0%B3%D0%BE%D1%81%D0%BB%D0%BE%D0%B2\" title=\"Іван Богослов\">Івана Богослова</a>, <a href=\"http://uk.wikipedia.org/wiki/%D0%A1%D0%B2%D1%8F%D1%82%D0%B8%D0%B9\" title=\"Святий\">святий</a>, мученик.</p>\n<p>Святий Ігнатій Богоносець був учнем св. Івана Богослова. Згодом він став єпископом Антіохії і, як добрий пастир, впродовж 40 літ підтримував дух свого стада в час переслідування християн. За правління імператора <a href=\"http://uk.wikipedia.org/wiki/%D0%A2%D1%80%D0%B0%D1%8F%D0%BD\" title=\"Траян\">Траяна</a> його ув\'язнили і повезли до Рима на <a href=\"http://uk.wikipedia.org/wiki/%D0%A1%D1%83%D0%B4\" title=\"Суд\">суд</a>. Під час довгої подорожі Ігнатій укріпляв місцевих християн у правдах святої віри. Тоді ж він написав сім листів до різних церковних громад. Саме Ігнатій Богоносець у листі до вірних міста Смирни в Малій Азії вперше використав вислів «Вселенська Церква».</p>\n<p>\n','2009-02-08 22:44:33','http://www.oranta.org/index.php?option=com_content&view=article&id=208:11-ljutogo-107-roku-perenesennja-moshche-svjashchennomuchenyka-ignatija-bogonoscja-i-stolittja&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'11 лютого 107 року Перенесення мощей священномученика Ігнатія Богоносця (І століття)','uk',1119,2093,0,NULL),
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fcatholics%2Fugcc%2F53318%2F\"> </a>\n<p>Про  це Патріарх Святослав (Шевчук) сказав під час спілкування з Данилом  Яневським – ведучим нового проекту УГКЦ – соціально-релігійного  інтернет-телебачення «Живе. ТБ», яке стартувало сьогодні.</p>\n<p>«Я хочу під час освячення собору Воскресіння Христового у Києві  побачити радість вірних від зустрічі з живим Христом. Центральним  обрядом освячення храму є чин освячення престолу у ньому, на якому  містяться святі дари – це символ тіла Хрестового».</p>\n','2013-08-12 03:48:36','http://oranta.org/index.php?option=com_content&view=article&id=1405:glava-ugkc-hoche-na-osvjachenni-patriarshogo-soboru-ugkc-v-kyjevi-pobachyty-na-lyci-virnyh-radist&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ хоче на освяченні Патріаршого собору УГКЦ в Києві побачити на лиці вірних радість','uk',3505,4988,0,NULL),
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fcommunity%2Fchurch_and_school%2F53003%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53003_Husar_konf.jpg\" alt=\"Husar_konf.jpg\" title=\"Husar_konf.jpg\" height=\"192\" width=\"134\" />У  виставковому центрі біля Патріаршого собору Воскресіння Христвого УГКЦ,  що в&nbsp; Києві, 17 серпня 2013 року відбудеться Освітній форум на тему:  «Християнські цінності в освіті. Виклики і перспективи». Захід  розпочнеться о 10.30 в рамках Всецерковної прощі до Патріаршого собору  Воскресіння Христового, що проходитиме 16-19 серпня.</p>\n<p>Почесним гостем дискусії під час форуму буде Блаженніший Любомир  (Гузар), Архиєпископ-емерит УГКЦ. Ініціатор проведення - Комісія УГКЦ у  справах освіти та виховання у співпраці із Комісією УГКЦ у справах  мирян, Громадською радою з питань співпраці з Церквами та релігійними  організаціями при Міністерстві освіти і науки, молоді та спорту України,  Катехитично-педагогічним інститутом УКУ.</p>\n<p> </p>\n','2013-07-18 19:47:53','http://oranta.org/index.php?option=com_content&view=article&id=1339:blazhennishy-ljubomyr-guzar-vizme-uchast-v-osvitnomu-forumi-pid-chas-vsecerkovnoji-proshchi-v-kyjevi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Любомир (Гузар) візьме участь в Освітньому форумі під час Всецерковної прощі в Києві','uk',3445,4928,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fconfessional%2Finterchurch_relations%2F52589%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52589_Svyatoslav.jpg\" alt=\"Svyatoslav.jpg\" title=\"Svyatoslav.jpg\" height=\"153\" width=\"168\" />Питання  про необхідність безпосередньої участі представників УГКЦ у  католицько-православному діалозі було поставлене сьогодні кардиналу  Курту Коху під час його лекції у Львові про «Перспективи екуменічного  діалогу між Католицькою і Православними Церквами».</p>\n<p>Як наголосив кардинал, хоча УГКЦ сьогодні не є членом  католицько-православного діалогу, Глава цієї Церкви Верховний  Архиєпископ Святослав (Шевчук) є членом Папської Ради сприяння  християнської єдності. Тому голос УГКЦ є почутий.</p>\n','2013-06-10 20:48:59','http://oranta.org/index.php?option=com_content&view=article&id=1244:my-chitko-usvidomljujemo-svoju-rol-u-pravoslavno-katolyckomu-dialozi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Ми чітко усвідомлюємо свою роль у православно-католицькому діалозі','uk',3322,4805,0,NULL),
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fculture%2Finheritance%2F52685%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52685_%DE%CD%C5%D1%CA%CE.jpg\" alt=\"ЮНЕСКО.jpg\" title=\"ЮНЕСКО.jpg\" height=\"126\" width=\"189\" />Спостережна  місія ЮНЕСКО заявила, що Україна систематично не виконує приписи з  охорони буферних зон заповідників \"Софія Київська\" і \"Києво-Печерська  лавра\". Про це йдеться у звіті місії ЮНЕСКО, що відвідала Київ у квітні.</p>\n<p>До кінця року Україні рекомендували прийняти новий закон про  культурну спадщину, в якому описувалися б поняття буферних зон, а також  об\'єднати буферні зони заповідників і ввести в них мораторій на нові  забудови. У Київраді сумніваються, що рекомендації будуть виконані.</p>\n','2013-06-19 11:33:00','http://oranta.org/index.php?option=com_content&view=article&id=1265:2013-06-19-08-34-38&catid=37:spilnota-cat&Itemid=49','P',NULL,'Місія ЮНЕСКО розкритикувала Україну за ігнорування рекомендації щодо буферних зон «Софії Київської» та «Києво-Печерської лаври»','uk',3354,4837,0,NULL),
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fworld_news%2F52836%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52836_Ioan23.jpg\" alt=\"Іван ХХІІІ\" title=\"Іван ХХІІІ\" height=\"118\" width=\"158\" />Комісія  кардиналів та єпископів з Конгрегації у справах канонізації святих 2  липня розглянула справу ймовірного чудесного зцілення за заступництвом <a href=\"http://risu.org.ua/ua/index/all_news/catholics/vatikan/42124/\" target=\"_blank\">блаженного</a> Папи Івана Павла ІІ. Як інформує ватиканіст Андреа Торніеллі, ця ж  комісія має висловити своє рішення і у справі чуда за заступництвом  блаженного Папи Івана ХХІІІ. Також цілком можливо, що цих двох пап  римських канонізують під час однієї урочистості на початку грудня.</p>\n','2013-07-04 14:30:17','http://oranta.org/index.php?option=com_content&view=article&id=1316:blazhennyh-ivana-pavla-ii-ta-ivana-hhiii-kanonizujut-na-pochatku-grudnja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженних Івана Павла ІІ та Івана ХХІІІ канонізують на початку грудня','uk',3411,4894,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<a href=\"http://vk.com/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fworld_news%2F53065%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53065_%B2%E2%E0%ED_%CF%E0%E2%EB%EE_II.jpg\" alt=\"Іван_Павло_II.jpg\" title=\"Іван_Павло_II.jpg\" loop=\"1\" height=\"142\" width=\"176\" />Богословська  комісія Ватикану визнала Папу Римського Іван Павла II святим.  Дослідники знайшли докази чотирьох чудес одного з найпопулярніших  предстоятелів за всю історію Католицької Церкви. Процедура беатифікації  зайняла кілька років. Офіційна церемонія, найімовірніше, відбудеться в  грудні.</p>\n<p>Папа Іван Павло II стане святим, заявила минулої п\'ятниці прес-служба  Ватикану. Спеціальна богословська комісія підтвердила факт чотирьох  офіційно задокументованих чудес, і в п\'ятницю вранці діючий Папа  Римський Франциск підписав указ.</p>\n','2013-07-24 00:47:53','http://oranta.org/index.php?option=com_content&view=article&id=1354:bogoslovska-komisija-vatykanu-vyznala-ivana-pavla-ii-svjatym-gotujetsja-kanonizacija&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Богословська комісія Ватикану визнала Івана Павла II святим: готується канонізація','uk',3463,4946,0,NULL),
(1,1,'<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fcommunity%2Fconferences%2F51979%2F\"> </a>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_51979_%D3%CA%D3_2.jpg\" alt=\"УКУ_2.jpg\" title=\"УКУ_2.jpg\" height=\"121\" width=\"181\" />Науковці  з різних куточків України під час ХVI Всеукраїнської конференції  «Болісні травми минулого: історична правда та імперативи сумління»  шукали шляхи розв’язання історичних конфліктів.</p>\n<p>Конференція відбулась в Українському католицькому університеті 12-13 квітня.</p>\n','2013-04-15 13:28:32','http://oranta.org/index.php?option=com_content&view=article&id=1115:naukovci-z-usijeji-ukrajiny-u-stinah-uku-obgovoryly-shljahy-podolannja-istorychnyh-konfliktiv&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Науковці з усієї України у стінах УКУ обговорили шляхи подолання історичних конфліктів','uk',3165,4648,0,NULL),
(1,1,'<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fall_news%2Fstate%2Fnational_religious_question%2F43658%2F\" onmouseup=\"this._btn=event.button;this.blur();\" onclick=\"return  VK.Share.click(0, this);\"> </a>\n<p><img style=\"margin: 10px; float: right;\" ilo-full-src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_43658_huzar_portret02_w2.jpg\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_43658_huzar_portret02_w2.jpg\" alt=\"huzar_portret02_w2.jpg\" title=\"huzar_portret02_w2.jpg\" loop=\"1\" height=\"154\" width=\"115\" />Українці не цінять достатньо і не вміють користуватися даром  незалежності своєї держави, а свободи бояться, оскільки не хочуть  приймати відповідальності, яка тісно пов’язана з нею. Таку думку в  спеціальному інтерв’ю для <a href=\"http://risu.org.ua/ua/index\">РІСУ </a>висловив  колишній Глава УГКЦ Патріарх Любомир (Гузар).</p>\n<p>«Мені здається, що ми не користаємо достатньо із того дару  незалежності. Бо незалежність народу, держави - то приблизно так само,  як незалежність особи, - сказав, зокрема, владика Любомир. - Людина  повинна бути свобідною, щоби бути собою. Господь Бог дає нам це і каже:  \"Якщо будете сповняти мій закон, що Бог дав ще на горі Синай (а пізніше  Ісус Христос поширив це в Нагірній проповіді), якщо будете це робити, то  будете свобідними, будете вільними\". Не знаю, чи ми достатньо вміємо  цінувати свободу. Бо свобода – це не робити, що тобі хочеться, не  сваволя. Свобода – це могти робити добро, щоби бути свобідним. Хто  сповняє волю і закон Божий - той свобідний, не є зв’язаний, не є  поневолений».</p>\n','2011-08-06 11:37:25','http://oranta.org/index.php?option=com_content&view=article&id=778:ukrajinci-ne-cinjat-svoju-nezalezhnist-i-bojatsja-svobody-patriarh-ljubomyr-guzar&catid=37:spilnota-cat&Itemid=49','P',NULL,'Українці не цінять свою незалежність і бояться свободи, - Патріарх Любомир (Гузар)','uk',2545,4028,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fexclusive%2Freportage%2F52017%2F\"> </a>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52017_hhh.jpg\" height=\"142\" width=\"212\" />В  Українському Католицькому Університеті миряни та священнослужителі  УГКЦ, які під час правління комуністичного режиму перебували у підпіллі,  зустрілися із молоддю, аби поділилися своїми спогадами про минуле.</p>\n<p>Зустріч під назвою «Мужність свідчити віру в сучасному світі» 17  квітня провели Фонд Святого Володимира, Інститут Історії Церкви УКУ та  Інститут лідерства та управління УКУ у межах проекту «ДІАЛОГ ПОКОЛІНЬ».</p>\n','2013-04-17 23:59:53','http://oranta.org/index.php?option=com_content&view=article&id=1123:pokolinnja-pidpillja-ugkc-peredalo-molodi-dosvid-zberezhennja-viry&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Покоління підпілля УГКЦ передало молоді досвід збереження віри','uk',3171,4654,0,NULL),
(1,1,'<a href=\"http://vkontakte.ru/share.php?url=http%3A%2F%2Frisu.org.ua%2Fua%2Findex%2Fexpert_thought%2Finterview%2F43849%2F\" onmouseup=\"this._btn=event.button;this.blur();\" onclick=\"return  VK.Share.click(0, this);\"> </a>\n<p><img style=\"margin: 10px; float: left;\" ilo-full-src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_43849_04.jpg\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_43849_04.jpg\" alt=\"Любомир (Гузар)\" title=\"Любомир (Гузар)\" loop=\"1\" height=\"202\" width=\"134\" />Цими днями українці відзначають 20-ту річницю  незалежності своєї держави. За два десятки років виросло нове покоління,  яке не знає впливу комуністичного режиму, відбулося чимало подій, що  змінили, іноді навіть докорінно, життя в Україні. Чи зуміли українці  оцінити здобуту на початку 90-х і омріяну, часто вистраждану  попередниками незалежність; яким є релігійно-духовне, церковне,  суспільне обличчя сучасної, молодої України; чим живе в ній і як  розвивається, зокрема, Українська Греко-Католицька Церква? Про це —  сьогоднішня розмова РІСУ з людиною, яку, згідно з соціологічними  опитуваннями, визнають для себе беззаперечним моральним авторитетом  українці різних соціальних прошарків і культурних традицій, різних  конфесій і віросповідань, — Предстоятелем УГКЦ в 2001-2010 роках  Патріархом ЛЮБОМИРОМ (Гузаром).</p>\n','2011-08-15 10:18:11','http://oranta.org/index.php?option=com_content&view=article&id=780:patriarh-ljubomyr-guzar-my-je-ljudmy-jaki-shchono-probudzhujutsja-do-svobody&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Патріарх ЛЮБОМИР (Гузар): “Ми є людьми, які щойно пробуджуються до свободи...”','uk',2552,4035,0,NULL),
(1,1,'<a id=\"c1344\"></a>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\"><img src=\"http://www.oranta.org/images/stories/256b6e094d.jpg\" style=\"margin: 10px; float: right; border: #000000 1px solid;\" />Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n<p align=\"justify\">Первісно празник Преображення святковано в місяці лютому. Одначе тому, що цей радісний празник звичайно випадав у часі Великого Посту, а це було незгідне з духом посту й покути, то його перенесено на день 6 серпня. Чому якраз на цей день? Історик Євсевій і св. Іван Дамаскин є тієї думки, що Господнє Преображення відбулося 40 днів перед Христовою смертю. Тож св. Церква йдучи за цією думкою, перенесла цей празник з місяця лютого на 6 серпня тому, бо 40 днів пізніше, це є 14 вересня, випадає празник Воздвиження Чесного Хреста - пам\'ять Христових мук і смерті.</p>\n','2009-08-17 21:22:34','http://www.oranta.org/index.php?option=com_content&view=article&id=407:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','uk',1738,3221,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<a id=\"c1808\"></a>  Коротка історія\n<p align=\"justify\">Наш церковний рік це наче прегарна мозаїка, яку творять празники Господні, Богородичні та непроглядне число Мучеників і Святих. Осередок цієї величної мозаїки це Господь Наш Ісус Христос, як Бог і як Чоловік. На землі день і ніч славить Його Церква-Паломниця, а в небі вічну славу співає Йому Церква прославлена: превеликий хор Ангелів і Святих. Св. Ангели це відвічний Божий хор слави, це Божа сторож і Божі слуги. Ангели сповняють також велику ролю у відкупленні людського роду.\n','2009-11-18 17:50:06','http://www.oranta.org/index.php?option=com_content&view=article&id=454:sobor-sv-arhystratyga-myhajila&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Собор св. Архистратига Михаїла','uk',1881,3364,0,NULL),
(1,1,'<article>\n<p><img style=\"margin: 10px; float: left;\" alt=\"сповідь\" src=\"http://www.credo-ua.org/wp-content/uploads/2012/04/confession.jpg\" height=\"139\" width=\"209\" /></p>\n<p>Папа Франциск переконаний, Таїнство  Покаяння, як необхідність приймати «благодать сорому», допомагає нам відчути Боже прощення. Проповідь Святіший Отець побудував на <a href=\"http://www.credo-ua.org/2011/10/52666\">Першому читанні</a> з Послання Святого Апостола Павла.</p>\n<p>Для багатьох дорослих віруючих сповідь перед священиком стає важким  зусиллям, яке призводить до уникнення цього Таїнства, або ж з «хвилини  істини» воно перетворюється у фікцію.</p>\n','2013-10-26 22:54:01','http://oranta.org/index.php?option=com_content&view=article&id=1576:chomu-vazhlyvo-spovidatysja-pered-svjashchenykom&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чому важливо сповідатися перед священиком?','uk',3715,5198,0,NULL),
(1,1,'<blockquote>\n<blockquote>\n<blockquote><strong>КРУТИ</strong></blockquote>\n<blockquote><br /></blockquote>\n<blockquote>За набої в стінах Софії,<br />За криваву скруту Крут, -<br />Хай московське серце Росії<br />Половецькі пси роздеруть.<br />Євген Маланюк<br /></blockquote>\n</blockquote>\n</blockquote>\n<p><br />Боже, скільки святого гніву,<br />Самозреченого, як аскет,<br />У карбованім ритмі співу<br />Тим прокляттям прорік поет</p>\n<p>\n','2009-01-28 22:06:52','http://www.oranta.org/index.php?option=com_content&view=article&id=198:-poezija-ivan-gnatjuka&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Поезія Іван Гнатюка','uk',1080,2054,0,NULL),
(1,1,'<blockquote>\n<div style=\"text-align: center;\"></div>\n<p style=\"text-align: center;\"><strong>Звернення<br /> Блаженнішого Любомира,<br /> Верховного Архиєпископа Києво-Галицького,<br /> до вірних Української Греко-Католицької Церкви <br /> та людей доброї волі з нагоди дня Бога-Творця</strong></p>\n<p style=\"text-align: right;\"><em>Узяв Господь Бог чоловіка й осадив його в Едемському саді<br /> порати його й доглядати його.</em><br />Бут. 2, 15</p>\n','2009-09-10 23:30:22','http://www.oranta.org/index.php?option=com_content&view=article&id=432:zvernennja-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Любомира','uk',1791,3274,0,NULL),
(1,1,'<blockquote>\n<p style=\"text-align: right;\"><em>Київ, 17 серпня 2009 року Божого</em><strong></strong></p>\n<p style=\"text-align: center;\"><strong>Звернення Блаженнішого Любомира, Верховного Архиєпископа Києво-Галицького, <br />у день празника Успіння Пресвятої Богородиці <br />до духовенства та мирян Української Греко-Католицької Церкви, <br />які з нагоди цього празника здійснюють прощі до великих відпустових місць, <br />Богородичних святинь і чудотворних ікон</strong></p>\n<p style=\"text-align: center;\"> Дорогі у Христі!</p>\n<p>Звертаюся з проханням до Преосвященних Владик і достойних проповідників, щоб вони, перш ніж почнуть свою празничну проповідь, прочитали це моє невелике слово до вас.</p>\n','2009-08-26 19:37:24','http://www.oranta.org/index.php?option=com_content&view=article&id=419:zvernennja-blazhennishogo-ljubomyra-u-den-praznyka-uspinnja-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Любомира у день празника Успіння Пресвятої Богородиці','uk',1753,3236,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<br />\n<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_055.jpg\" style=\"float: right;\" /> 25 грудня більшість християн світу урочисто святкують празник Різдва Христового. Також і ті, які відзначатимуть його за старим стилем, тобто 7 січня, єднаються з братами і сестрами у радісній прославі Господа нашого Ісуса Христа, що прийнявши людське тіло, народився у Вифлиємі.<br /> <br /> Вчора опівночі ватиканська базиліка святого апостола Павла була по вінця заповнена вірними, які з\'їхались з усього світу, щоб разом із Папою Венедиктом XVI відсвяткувати містерію Христового Різдва - таїнства Божого Світла, що просвічує темряву, Слова, що стало Тілом, Хліба спа сіння, що зійшов з небес.<br /> <br /> Сьогодні опівдні за римським часом з центральної лоджії ватиканської базиліки святого апостола Петра Вселенський Архиєрей Венедикт XVI виголосив різдвяне звернення до вірних та всіх людей доброї волі й привітав народи світу 64-ма мовами, серед яких також і українською.</p>\n<p>\n','2008-12-25 17:22:14','http://www.oranta.org/index.php?option=com_content&view=article&id=161:rizdvo-u-vatykani-z-papoju-venedyktom-xvi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Різдво у Ватикані з Папою Венедиктом XVI','uk',864,1838,0,NULL),
(1,1,'<br />\n<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_holod__02.jpg.jpg\" />У суботу, 22 листопада, Українська Греко-Католицька Церква, разом із іншими Церквами та релігійними організаціями України, буде торжественно поминати невинні жертви трагічного Голодомору 1932-33 років. Зібрані на своєму Митрополичому Синоді 29-30 жовтня цього року, єпископи УГКЦ постановили \"підтримати та всебічно сприяти належному проведенню відзначення 75-ти річчя Голодомору в Україні, широко залучивши до цього можливості наших парафій\".</p>\n<p>У свою чергу, листом до всіх отців-душпастирів Києво-Галицького Верховного Архиєпископства УГКЦ Блаженніший Любомир, Глава УГКЦ, доручив священнослужителям відзначення 75 річниці Голодомору \"належно провести на парафіяльному рівні та взяти участь у офіційних місцевих відзначеннях\". У рамках поминальних днів у всіх парафіях УГКЦ буде відслужено заупокійні Святі Літургії та панахиди з відповідними проповідями. Також запропоновано організувати походи на могили покійних, провести траурні академії, запалення свічок у вікнах тощо. У ці дні серед вірних Церкви буде поширено Пастирський лист Митрополита Андрея (Шептицького) та єпископату УГКЦ з проханням про поміч голодуючим на Великій Україні.</p>\n<p>\n','2008-11-21 09:02:52','http://www.oranta.org/index.php?option=com_content&view=article&id=110:za-qvid-golodu-konajuchyh-brativq-ugkc-molylasja-shche-u-1933-roci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'За \"від голоду конаючих братів\" УГКЦ молилася ще у 1933 році','uk',691,1671,0,NULL),
(1,1,'<br />\n<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_kupoly_2008.jpg.jpg\" />1 - 7 грудня 2008 року Преосвященний Владика Богдан (Дзюрах), голова Наглядової ради будівництва Патріаршого центру УГКЦ у Києві, перебуває у США. Мета його візиту - зустріч із членами Закордонного відділу інформаційно-фінансового забезпечення будівництва Патріаршого центру для поінформування їх про стан будівництва Патріаршого собору та обговорення подальших кроків проведення збірки на території США.</p>\n<p>Під час свого перебування у США Владика Богдан також зустрінеться із Преосвященним Владикою Павлом (Хомницьким), головою Всеамериканського комітету розвитку Патріаршого центру, Єпархом Стемфордським УГКЦ, та Преосвященним Владикою Василем (Лостеном), Єпископом-емеритом Стемфордської єпархії, який бере активну участь у проведенні збірки для будівництва собору. Крім того заплановано низку зустрічей із дійсними та потенційними жертводавцями, зокрема із єпископом <strong>римо-католицької єпархії з Рокфелер Центру, який уже двічі пожертвував по 100 тисяч доларів.</strong></p>\n<p>\n','2008-12-02 11:00:29','http://www.oranta.org/index.php?option=com_content&view=article&id=124:budivnyctvo-patriarshogo-soboru-planujetsja-zavershyty-voseny-2010-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Будівництво Патріаршого собору планується завершити восени 2010 року','uk',741,1721,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<br />\n<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_16-11-08.jpg.jpg\" />Щорічно у листопаді у Ватикані відбувається Інтернаціональна конференція, що організовується Папською комісією Душпастирства охорони здоров\'я, робота якої спрямована на розгляд актуальних питань пасторальної та соціальної опіки над хворими. Участь у цих конференціях беруть єпископи, священики, монахи і монахині, лікарі, студенти пасторальної та соціальної медицини з усіх куточків світу. Почесним президентом та головою конференції є Кардинал Хав\'єр Лодзано Барраґан, що відповідає за душпастирство охорони здоров\'я в усій Католицькій Церкві.</p>\n<p>\n','2008-11-17 21:23:03','http://www.oranta.org/index.php?option=com_content&view=article&id=103:lpastoralna-opika-hvoryh-diter&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Пасторальна опіка хворих дітей»','uk',663,1643,0,NULL),
(1,1,'<br />\n<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_konf_migratsiya.jpg.jpg\" height=\"112\" width=\"150\" />У Дзеркальному залі Львівського Національного університету ім. Івана Франка 24 вересня 2010 року відбулась дискусія на тему «Перспективи євродіалогу у міграційній сфері: участь Церкви». Нею завершилася 10-денна німецько-українська програма вивчення досвіду міграції, яку спільно з німецькими колегами фонду Реновабіс, ініціювала і провела Комісія у справах мігрантів УГКЦ.</p>\n','2010-09-27 08:15:28','http://oranta.org/index.php?option=com_content&view=article&id=590:-synod-ne-lyshe-analizuvav-stan-migraciji-ale-pracjuvav-nad-programoju-vidpovidi-cerkvy-vladyka-osyf-miljan-&catid=37:spilnota-cat&Itemid=49','P',NULL,' Синод не лише аналізував стан міграції, але й працював над програмою відповіді Церкви, – Владика Йосиф (Мілян) ','uk',2159,3642,0,NULL),
(1,1,'<br />\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3806,200,voznyak_press.jpg\" />Високопреосвященним і Преосвященним Владикам,<br />всесвітлим, всечесним і преподобним отцям,<br />преподобним ченцям і черницям,<br />любим у Христі мирянам<br />Української Греко-Католицької Церкви</p>\n<p style=\"text-align: center;\">Дорогі у Христі!</p>\n<p>Як було повідомлено усім вірним нашої Церкви, Святіший Отець Венедикт  XVI поблагословив прохання Блаженнішого Любомира про звільнення його з  уряду Верховного Архиєпископа УГКЦ. Приймаємо цю подію з великою довірою  до Господа Бога та до Його опіки над своєю Церквою.</p>\n','2011-02-22 11:04:04','http://oranta.org/index.php?option=com_content&view=article&id=678:zaklyk-do-molytvy-za-uspishne-provedennja-vyborchogo-synodu-jepyskopiv-ukrajinskoji-greko-katolyckoji-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Заклик до молитви за успішне проведення Виборчого Синоду Єпископів Української Греко-Католицької Церкви','uk',2325,3808,0,NULL),
(1,1,'<br />\n<p><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_milyan1.jpg.jpg\" />Такими словами можна окреслити подію, яка відбулася у четвер, 18 червня 2009 року, у Патріаршому соборі Воскресіння Христового у Києві. У цей день було здійснено єпископську хіротонію Владики Йосифа (Міляна), Єпископа-помічника Київської архиєпархії. Ці єпископські свячення, які вперше у новітній історії нашої Церкви відбулися у Києві, стали своєрідним містком із подіями, які відбувалися у столиці нашої держави чотириста років тому. Адже саме Київський Митрополит Михайло (Рогоза) ініціював та спільно із Синодом Єпископів Київської Митрополії прийняв рішення про відновлення єдності Української Церкви з Римським Апостольським Престолом, забезпечивши при цьому збереження східної християнської традиції і власної церковної та етно-культурної самобутності.</p>\n','2009-06-20 11:00:31','http://www.oranta.org/index.php?option=com_content&view=article&id=350:ne-pryshly-a-povernulysja-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Не прийшли, а повернулися!  ','uk',1579,3062,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div align=\"JUSTIFY\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,76543656.jpg\" />Дідусь нахилився над п’ятилітнім онуком і поцілував його надобраніч. Хлопчик одразу витер щоку.</p>\n</div>\n<div align=\"JUSTIFY\">\n<p>«Чому ти так робиш, синку? — спитала мама. — Коли хтось тебе цілує, не треба витирати поцілунок».</p>\n</div>\n<div align=\"JUSTIFY\">\n<p>«Мамусю, — пояснив маленький, — я не витираю, щоб витерти. Я втираю його в себе».</p>\n','2013-01-26 03:20:36','http://oranta.org/index.php?option=com_content&view=article&id=958:nazovni-i-vseredyni&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Назовні і всередині (26 січня)','uk',2924,4407,0,NULL),
(1,1,'<div align=\"center\">\n<p><img style=\"margin: 10px; float: left;\" src=\"http://www.religion.in.ua/uploads/posts/2013-07/thumbs/1373914591_0.jpg\" alt=\"Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина II\" title=\"Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина II\" height=\"147\" width=\"147\" /></p>\n</div>\n<p> </p>\n<p>&nbsp;<strong>3. Образ “другого Єрусалима” в релігійній уяві пращурів</strong></p>\n<p> </p>\n<p>&nbsp;Чимало іноземців, мандрівників, філософів, дипломатів назавжди  заворожив і заворожує донині своїми величними святинями Золотоверхий  Київ. Іще Адам Бременський називав Київ «соревнователем  констянтипольського скипетра і славнійшою прикрасою Греції». Як  справедливо зазначає православний богослов і історик архімандрит  Констянтин (проф. К.І. Зайцев): «Привабливою й славною була Київська  Русь в очах зовнішнього світу.</p>\n','2013-08-25 13:57:30','http://oranta.org/index.php?option=com_content&view=article&id=1421:ujavlennja-pro-kyjiv-jak-ldrugy-jerusalymr-v-cerkovno-suspilni-dumci-rusi-ukrajiny-chastyna-ii&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина II','uk',3535,5018,0,NULL),
(1,1,'<div align=\"right\">\n<p><em>Ви – світло світу. Не можна сховати місто, що лежить на верху гори.</em></p>\n<p><em>Так нехай світить перед людьми ваше світло, щоб вони бачивши ваші добрі вчинки, прославляли вашого Отця, що на небі.</em></p>\n</div>\n<div align=\"right\">\n<p><em>Мт</em><em>. 5, 14, 16</em></p>\n</div>\n<div>\n<p>З Божим благословенням отець Ігор Онишкевич, настоятель храму святого Миколая на Аскольдовій могилі та отець Олег Лучка з села Вербівка, Ружинського району Житомирської області церкви Різдва Пресвятої Богородиці, 30&nbsp;червня&nbsp;2012&nbsp;року Божого відправили дітей у християнський табір до села Заріччя…</p>\n','2012-07-06 13:11:09','http://oranta.org/index.php?option=com_content&view=article&id=871:litni-vidpochynok-u-karpatah&catid=25:parafija-cat&Itemid=27','P',NULL,'Літній відпочинок у Карпатах','uk',2767,4250,0,NULL),
(1,1,'<div class=\"aphoto\">\n<div class=\"rpad\">\n<div class=\"mpic\">\n<div class=\"mp1\"><img src=\"http://img.istpravda.com.ua/images/doc/d/6/d6e6dd5-300-k.jpg\" style=\"float: right; margin: 10px;\" height=\"155\" width=\"239\" /></div>\n</div>\n<div class=\"pt\">Предстоятель Української греко-католицької церкви Святослав (Шевчук)</div>\n<div class=\"h5\"></div>\n</div>\n</div>\n<div class=\"text _ga1_on_\">\n<p class=\"subtitle2\">\"Для того, щоб чітко відповісти на запитання, проти кого був направлений Голодомор - класу чи нації - потрібні дослідження. Але на основі нинішніх даних, це справді була найдешевша зброя масового знищення. Скерована насамперед проти українців\".</p>\n<p>Кажуть, що час лікує. Але є трагедії, здатні вплинути на долю цілої нації, назавжди змінити спосіб мислення та життєвий уклад людей. З запитаннями, які стосуються переосмислення Голодомору теперішнім поколінням, про те, як запобігти повтореню подібної трагедії у майбутньому, ми звернулись до Глави УГКЦ Блаженійшого Святослава ШЕВЧУКА.</p>\n</div>\n','2012-11-25 02:45:32','http://oranta.org/index.php?option=com_content&view=article&id=908:patriarh-ugkc-qpamjat-pro-golodomor-ne-maje-na-meti-z-kymos-pokvytatysjaq&catid=37:spilnota-cat&Itemid=49','P',NULL,'Патріарх УГКЦ: \"Пам\'ять про Голодомор не має на меті з кимось поквитатися\"','uk',2865,4348,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"block65\">\n<div class=\"rpad\">\n<div class=\"mpic\">\n<div class=\"mp1\"><img style=\"margin: 10px; float: left;\" ilo-full-src=\"http://img.pravda.com.ua/images/doc/6/e/6e71254-cvet.jpg\" src=\"http://img.pravda.com.ua/images/doc/6/e/6e71254-cvet.jpg\" height=\"151\" width=\"230\" /></div>\n</div>\n</div>\n</div>\n<p><em>Під Київ, у Княжичі їдемо ми з отцем прес-секретарем УГКЦ Ігорем  Яцівим – до резиденції глави Української греко-католицької церкви.  Раніше доводилось бувати тут під час інтерв’ю з Блаженнійшим Любомиром  Гузаром. Гузар і по цей час живе у Княжичах, але все таки хазяїн  \"господи\" змінився. Втім, цей двоповерховий дім – не маєток окремої  особи, а, радше, штаб-квартира, де на нараду до очільника церкви  приїздять святі отці, де приймають рішення та спілкуються з пресою. </em></p>\n','2011-07-09 14:35:10','http://oranta.org/index.php?option=com_content&view=article&id=772:svjatoslav-shevchuk-ja--proty-revoljuci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святослав Шевчук: Я – проти революцій','uk',2528,4011,0,NULL),
(1,1,'<div class=\"block65\">\n<div class=\"rpad\">\n<div class=\"mpic\">\n<div class=\"mp1\"><img style=\"margin: 10px; float: left;\" src=\"http://img.pravda.com.ua/images/doc/8/d/8db3941-cvet.jpg\" height=\"133\" width=\"204\" /></div>\n</div>\n</div>\n</div>\n<p align=\"center\">Більше як десять років тому  мені трапилося таке. Я працював у своїй кімнаті, коли до мене зайшов  один знайомий. З першої миті я спостеріг його велику стурбованість. І  справді, коли ми привіталися, він почав розповідати про свої проблеми,  яких було чимало: особисті, родинні, господарські, громадські...  Здавалося, усе, з чого складається нормальне людське життя, в якийсь  спосіб у нього було захитане.</p>\n','2012-11-15 11:31:45','http://oranta.org/index.php?option=com_content&view=article&id=903:jakymy-ja-b-hotiv-bachyty-deputativ&catid=37:spilnota-cat&Itemid=49','P',NULL,'Якими я б хотів бачити депутатів','uk',2855,4338,0,NULL),
(1,1,'<div class=\"field field-type-filefield field-field-img\">\n<div class=\"field-items\">\n<div class=\"field-item odd\"><img style=\"margin: 10px; float: left;\" src=\"http://expres.ua/gfx/imagecache/th220/events/stepan_bandera.jpg\" alt=\"Фото з сайту marichka2.multiply\" height=\"205\" width=\"220\" /></div>\n</div>\n</div>\n<div class=\"field field-type-text field-field-summary\">\n<div class=\"field-items\">\n<div class=\"field-item odd\">Брати Бандери помирали важко, нiмцi хотiли, щоб вони мучились не одну годину</div>\n</div>\n</div>\n<p><strong>Уже традицiйно для України Януковича 1 сiчня у нашiй державi  -- не бiльше анiж свято похмiлля i вчорашнього олiв\'є. Бо для Януковича i  його донецької команди день народження людини, у якої вони нахабно  вiдiбрали звання Героя України, -- не свято.</strong></p>\n<p>Втiм, поза усiляким сумнiвом, Бандера залишається найсуперечливiшою фiгурою української iсторiї. Його вороги досi не можуть прийняти того, що вiн не зламався. Його друзi вдячнi йому за те, що вiн вистояв.</p>\n','2013-01-15 01:09:26','http://oranta.org/index.php?option=com_content&view=article&id=920:tajemnyci-rodyny-stepana-bandery&catid=37:spilnota-cat&Itemid=49','P',NULL,'Таємницi родини Степана Бандери','uk',2889,4372,0,NULL),
(1,1,'<div class=\"field field-type-filefield field-field-img\">\n<div class=\"field-items\">\n<div class=\"field-item odd\">\n<p><a target=\"_blank\" href=\"http://www.expres.ua/gfx/events/svyatoslav_shevchuk_patriarh7.jpg\" rel=\"lightbox[field_img]\"><img style=\"margin: 10px; float: right;\" src=\"http://www.expres.ua/gfx/imagecache/thnode/events/svyatoslav_shevchuk_patriarh7.jpg\" alt=\"У дитинстві нового Предстоятеля УГКЦ усі називали... Сонечком\" width=\"128\" height=\"171\" /></a></p>\n</div>\n</div>\n</div>\n<div class=\"field field-type-text field-field-summary\">\n<div class=\"field-items\">\n<div class=\"field-item odd\">\n<p>...Реанiмацiя районної лiкарнi. \"Швидка\" щойно доправила сюди хлопчину. Безвусого, ще зовсiм дитину.  -- Пульс! Пульс пропадає!..  -- Серце зупиняється! Вiн йде вiд нас!  Навколо пацiєнта метушаться стурбованi лiкарi. Пiт рясно скроплює їхнi скронi.</p>\n</div>\n</div>\n</div>\n<p>Утiм, зусилля марнi. Смерть сильнiша за людей. Люди в бiлих халатах поволi <br /> виходять з реанiмацiйної. А один з них залишається. Стає навколiшки,<br /> починає щось шепотiти.</p>\n','2011-04-07 15:49:41','http://oranta.org/index.php?option=com_content&view=article&id=716:u-dytynstvi-novogo-predstojatelja-ugkc-usi-nazyvaly-sonechkom&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'У дитинстві нового Предстоятеля УГКЦ усі називали... Сонечком','uk',2394,3877,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"g-container story-body\">\r\n<div class=\"bodytext\">\r\n<div class=\"module align-center \">\r\n<div class=\"image img-w386\"><img style=\"margin: 10px; float: left;\" src=\"http://wscdn.bbc.co.uk/worldservice/assets/images/2011/03/29/110329131416_shevchuk_unian_ukraine_catholics_386x217_unian.jpg\" alt=\"Глава УГКЦ\" width=\"386\" height=\"217\" /></div>\r\n</div>\r\n<p class=\"ingress\"><strong> Новообраний провідник</strong><strong> Української Греко-Католицької Церкви Святослав Шевчук вперше на посаді  зустрівся у Ватикані з Папою Римським. Під час зустрічі у четвер ішлося  передусім про діалог між католицькою та православною церквами в Україні. </strong></p>\r\n<p>Як розповів в інтерв\'ю Бі-Бі-Сі Верховний  Архієпископ УГКЦ Святослав Шевчук, під час зустрічі з Папою Римським не  йшлося про створення патріархату УГКЦ. За його словами, метою візиту  було засвідчити \"повну єдність\" церкви з Ватиканом. Під час розмови з  Бенедиктом XVI йшлося про виклики та завдання, які стоять перед УГКЦ,  яка нараховує близько 5 млн вірних.</p>\r\n</div>\r\n</div>','2011-04-01 12:01:13','http://oranta.org/index.php?option=com_content&view=article&id=707:glava-ugkc-pro-povnu-jednist-z-vatykanom&catid=32:gk-svit-cat&Itemid=58','N','','Глава УГКЦ про \'\'повну єдність\'\' з Ватиканом','uk',2380,3863,0,NULL),
(1,1,'<div class=\"img\"><img style=\"margin: 10px; float: left;\" src=\"http://i.obozrevatel.com/8/1043496/104939.jpg\" alt=\"Гузар: гумору не втрачаю\" width=\"258\" height=\"172\" /></div>\n<p>З Блаженнійшим Любомиром Гузаром я маю честь бути знайомою вже не один  рік. Наше перше інтерв’ю було записано у розпал помаранчевої революції, а  далі зустрічі стали регулярними. Але заочне знайомство відбулося ще  раніше: у 2001-му глава греко-католиків був єдиним з предстоятелів  церков, хто відмовився засудити учасників акції «Україна без Кучми». Так  само він, мабуть, був єдиним, хто жив і живе за принципом «Богу –  Богове, а кесарю – кесареве», не змішуючи грішне з праведним і не  поклоняючись тимчасовому правителю (хто б ним не був) як Отцю  Небесному.</p>\n','2011-04-11 12:23:59','http://oranta.org/index.php?option=com_content&view=article&id=718:ja-duzhe-rady-shcho-vybraly-blazhennishogo-svjatoslava-ljubomyr-guzar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Я дуже радий, що вибрали Блаженнішого Святослава, - Любомир Гузар','uk',2395,3878,0,NULL),
(1,1,'<div class=\"img\"><img style=\"margin: 10px; float: right;\" src=\"http://i.obozrevatel.ua/8/1223978/433214.jpg\" alt=\"Глава УКГЦ Святослав Шевчук: Нас вважають опозицією. Ч.1\" height=\"163\" width=\"245\" /></div>\n<p>Ввечері 1 грудня ми разом із керівником прес-департаменту  Української Греко-католицької церкви отцем Ігорем Яцівим їдемо у Княжичі  – місцину під Києвом, де розташована резиденція глави УКГЦ. Дорога  знайома – раніше нею прямували на зустріч до Любомира Гузара, котрий  цього року з власної волі склав обов’язки глави церкви (випадок  безпрецедентний!), щоб поступитися місцем молодшому і міцнішому  здоров’ям – Святославу Шевчуку.</p>\n','2011-12-06 10:00:50','http://oranta.org/index.php?option=com_content&view=article&id=812:glava-ukgc-svjatoslav-shevchuk-nas-vvazhajut-opozycijeju-ch1&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УКГЦ Святослав Шевчук: Нас вважають опозицією. Ч.1','uk',2619,4102,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"itemIntroText\">\n<p class=\"itemIntroText\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4935,200,0DnAEcSib9g.jpg\" />Один міністр сидів собі якось на бордюрі центрального міського фонтану, задумавшись про щось, та й упав у воду.</p>\n<p class=\"itemIntroText\">Одразу ж збіглися люди, вони простягали йому руки і говорили:</p>\n</div>\n','2013-07-28 23:46:06','http://oranta.org/index.php?option=com_content&view=article&id=1349:slovo-29-lypnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Слово (29 липня)','uk',3470,4953,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,942042_10151640264912241_866843490_n.jpg\" />Ріка, біжучи до моря, по дорозі наткнулася на пустелю і -  зупинилася. Перед нею-бо були лише скелі, всіяні гострими шпичаками і  глибокими печерами, та піщані дюни, що зливалися з видноколом.</p>\n</div>\n<p>«Така моя доля. Я не зможу здолати пустелю. Пісок поглине мої води і я  загину. Не допливу до моря. Усе даремно», - бідкалася ріка.</p>\n','2013-06-06 22:43:23','http://oranta.org/index.php?option=com_content&view=article&id=1234:rika-i-pustelja-7-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Ріка і пустеля (7 червня)','uk',3300,4783,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4490,200,846a06cd15b7.jpg\" />Юнак заявив із погордою вчителеві:</p>\n</div>\n<p>- Врешті я досягнув досконалості. Тепер  абсолютно впевнений у власних силах. Сам собі господар. Легко втримую в  шорах нерви, запанував над почуттям помсти, усунув із серця свого  всіляку хтивість, слава більше не турбує мене, прагнення влади й грошей,  навіть найменші сексуальні бажання ніколи не навідують мене, отож…  Учиталь обірвав юнака на пів слові:</p>\n','2013-03-11 07:08:27','http://oranta.org/index.php?option=com_content&view=article&id=1029:u-teoriji-11-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'У теорії (11 березня)','uk',3008,4491,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,1_1220.JPG\" />Десятеро селян ішли дорогою на свої  поля. Несподівано налетів ураган. Він несамовито шарпав дерева, земля  здригалася і глухо стугоніла.</p>\n</div>\n<p>Утікаючи від грому і граду, люди заховалися в руїнах старого замку.</p>\n<p>Світло блискавиць, спалахуючи то тут, то там, творило страхітливий  танець. Наростав шум вітру, наближався гуркіт грому. Селяни добряче  налякалися, і поміж них прошелестів шумок, що, мовляв, серед них мусить  бути грішник, який стягнув на всіх це шаленство природи.</p>\n','2013-03-19 01:36:16','http://oranta.org/index.php?option=com_content&view=article&id=1049:cap-vidbuvalo-19-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Цап-відбувайло (19 березня)','uk',3055,4538,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4844,200,4891899730_d451ae4491_z.jpg\" />Троє людей опинилися якось на березі річки, яка несла свої  спінені бурхливі води. Ці люди мусили перебратися на інший берег. Для  них це було дуже важливо.</p>\n</div>\n<p>Перший чоловік, хитрий купець і великий комбінатор, який умів  керувати справами і людьми, став навколішки і звернувся до Бога:  «Господи, зроби так, щоб я відважився кинутися у ці небезпечні води і  переправився через ріку. На тому боці мене чекають важливі справи. Я  подвою свої прибутки, проте мушу поспішити...» Підвівся і, по хвилі  вагання, кинувся до води. Але ріка забрала його зі собою.</p>\n','2013-06-20 23:32:44','http://oranta.org/index.php?option=com_content&view=article&id=1270:rika-21-chnrvnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Ріка (21 червня)','uk',3366,4849,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,14799.jpg\" />На мальовничому екзотичному гамірному  базарі в одному місті на Сході заможна американська туристка побачила  дивовижне дзеркало у срібній оправі.</p>\n<p>Однак його ціна була високою, надто високою. Коли вони торгувалися,  продавець сказав, що дзеркало має чарівну силу - у цілому світі такого  немає.</p>\n</div>\n','2013-07-11 22:14:16','http://oranta.org/index.php?option=com_content&view=article&id=1306:dzerkalo-12-lypnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дзеркало (12 липня)','uk',3425,4908,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5161,200,timthumb.php.jpg\" />Чотири князенки хотіли навчитися чогось такого, чого ніхто не вмів.</p>\n<p>Вони сказали собі:</p>\n<p>- Обійдемо всю землю, щоб навчитись.</p>\n</div>\n','2013-10-16 00:35:09','http://oranta.org/index.php?option=com_content&view=article&id=1543:chotyry-knjazenky-16-zhovtnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Чотири князенки (16 жовтня)','uk',3688,5171,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5271,200,993005.jpg\" />У саду, край палацу великого  правителя Вірменії, ріс трояндовий кущ. То була найбільш доглянута  рослина у світі. За легендою, коли кущ розквітне, її пан буде  обдарований вічною молодістю.</p>\n<p>Щоранку пан нахилявся над кущем і з хвилюванням у серці  пересвідчувався, чи не з\'явилися на гілці довгоочікувані пуп\'янки.  Марно!</p>\n</div>\n','2013-11-16 23:46:31','http://oranta.org/index.php?option=com_content&view=article&id=1618:pan-trojandy-17-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пан троянди (17 листопада)','uk',3789,5272,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5273,200,25610484.jpg\" />Один вельможа влаштував у своєму замку велику учту і запросив на неї  усіх мешканців ближнього села. Але запасів вина у пивницях щедрого  шляхтича було замало, щоб заспокоїти спрагу такого великого числа  запрошених.</p>\n<p>Тож він звернувся до мешканців села з таким проханням: «Кожен з вас  нехай принесе свого вина і влиє до бочки, яка буде стояти посеред  подвір\'я. Під час бенкету всі зможуть зачерпнути з неї і пити досхочу».</p>\n</div>\n','2013-11-18 23:58:25','http://oranta.org/index.php?option=com_content&view=article&id=1621:zaproshennja-19-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Запрошення (19 листопада)','uk',3794,5277,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5273,200,270641272219808544.jpg\" /></p>\n</div>\n<p>Один старий мудрий індіянський вождь дав таку пораду надто гарячим молодим воїнам свого племені.</p>\n<p>- Коли ти розгнівався на когось, хто, зрештою, завдав тобі смертельної образи, і присягаєшся його вбити, аби&nbsp;кров\'ю змити ганьбу, то перш ніж це зробити, набий тютюном люльку і викури її.</p>\n','2013-11-22 23:15:42','http://oranta.org/index.php?option=com_content&view=article&id=1625:try-ljulky-23-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Три люльки (23 листопада)','uk',3810,5293,0,NULL),
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5273,200,825523_a7cb8fce.jpg\" />Один король запросив до свого замку  торгівця кіньми. Той прийшов, привівши двох чудових огирів на продаж. На  вигляд майже однакові - обоє молоді, міцні й добре збудовані, однак  торгівець зажадав за одного з них удвічі більшу ціну, аніж за іншого.</p>\n</div>\n<p>Заінтригований, король скликав своїх придворних і сказав:</p>\n','2013-11-19 23:01:50','http://oranta.org/index.php?option=com_content&view=article&id=1622:vidminnist-20-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Відмінність (20 листопада)','uk',3798,5281,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"itemIntroText\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5273,200,rJiWBsz81aI.jpg\" />Один монах сидів на березі річечки,  заглибившись у роздуми. Коли відкрив очі, то помітив скорпіона, який  упав у воду і відчайдушно борсався, намагаючись зі всіх сил втриматися  на поверхні і вирятуватися.</p>\n</div>\n<p>Сповнений співчуття, монах занурив руку у воду, зловив скорпіона і  випустив його на берег. Скорпіон у відповідь несподівано обернувся і  боляче вжалив свого добродія.</p>\n','2013-11-21 23:10:15','http://oranta.org/index.php?option=com_content&view=article&id=1624:skorpion-22-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Скорпіон (22 листопада)','uk',3803,5286,0,NULL),
(1,1,'<div class=\"itemIntroText\">\r\n<p><img style=\"margin: 10px; float: right;\" src=\"image,4844,200,4891899730_d451ae4491_z.jpg\" alt=\"\" />Троє людей опинилися якось на березі річки, яка несла свої спінені бурхливі води. Ці люди мусили перебратися на інший берег. Для них це було дуже важливо.</p>\r\n</div>\r\n<p>Перший чоловік, хитрий купець і великий комбінатор, який умів керувати справами і людьми, став навколішки і звернувся до Бога: &laquo;Господи, зроби так, щоб я відважився кинутися у ці небезпечні води і переправився через ріку. На тому боці мене чекають важливі справи. Я подвою свої прибутки, проте мушу поспішити...&raquo; Підвівся і, по хвилі вагання, кинувся до води. Але ріка забрала його зі собою.</p>','2013-06-20 23:32:44','http://oranta.org/index.php?option=com_content&view=article&id=1270:rika-21-chnrvnja&catid=25:parafija-cat&Itemid=27','I','','Ріка (21 червня)','uk',3363,4846,0,NULL),
(1,1,'<div class=\"left_chr\">\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4281,200,nativity_of_virgin_03.jpg\" />Різдво Пресвятої Богородиці, християнське свято, в Православній  Церкві входить в число Богородичних двонадесятих свят, з нього  починається річне коло церковних свят.</p>\n<p>Пресвята Діва Марія народилася в той час, коли люди дійшли до таких меж  морального занепаду, при яких їхнє повстання здавалося вже неможливим.  Кращі уми тієї епохи усвідомлювали і часто відкрито говорили, що Бог  повинен зійти у світ, щоб виправити віру і не допустити погибелі роду  людського. Син Божий схотів для порятунку людей прийняти людське єство, і  Пречисту Діву Марію, єдину гідну вмістити в Себе і втілити Джерело  чистоти і святості, Він обирає Собі Матір\'ю.</p>\n','2012-09-21 13:57:26','http://oranta.org/index.php?option=com_content&view=article&id=889:rizdvo-presvjatoji-bogorodyci&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Різдво Пресвятої Богородиці','uk',2809,4292,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" ilo-full-src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/st_peter_basilica_vatican_01.jpg\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/st_peter_basilica_vatican_01.jpg\" height=\"137\" width=\"188\" /></div>\n<p class=\"rtejustify\">Минулого тижня в Бразилії відбулася V сесія Патріяршого собору УГКЦ,  яка направду підняла одне з нагальних питань нашої Церкви – питання  монашества. Адже з часу великих христологічних контроверсій монашество  завжди було і головним оборонцем Церкви, і Її постійним болем. Роль  монашества у Церкві важко переоцінити. Тому питання монашества для  будь-якої Помісної Церкви є надзвичайно важливим – адже це люди, які у  повноті посвятили своє життя Богові, вони покликані бути євангельським  знаком цьому світові в особливий спосіб. І тому питання ефективності  монашества є надзвичайно важливим у загальній стратегії євангелізації  світу.</p>\n','2011-09-07 10:55:09','http://oranta.org/index.php?option=com_content&view=article&id=785:dlja-rezhymu-janukovycha-cerkva--ce-biznes&catid=37:spilnota-cat&Itemid=49','P',NULL,'Для режиму Януковича Церква – це бізнес','uk',2563,4046,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/110329131416_shevchuk_unian_ukraine_catholics_386x217_unian_0.jpg\" width=\"249\" height=\"182\" /></div>\n<p>Після Інтронізації нового Глави УГКЦ помаленьку починає вщухати медійна  буря довкола зміни на Патріаршому Престолі УГКЦ. Але не перестає  вщухати інтерес до майбутньої політики Блаженнішого Святослава Шевчука.  Для того, щоб привідкрити завісу таємниці у цьому питанні не потрібно  шукати прихованих циркулярів, паперів і збирати плітки. Варто звернутися  до перших прес-конференцій та інтерв’ю нового Глави УКГЦ. Адже кожен  громадський діяч, коли виходить на новий щабель своєї діяльності, прагне  дати громаді розуміння власного бачення речей, власної програми. Тому  варто спробувати зробити аналіз висловлювань Блаженнішого Святослава.</p>\n','2011-04-06 16:23:20','http://oranta.org/index.php?option=com_content&view=article&id=711:shcho-ochikuvaty-vid-novogo-glavy-ugkc&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Що очікувати від нового Глави УГКЦ?','uk',2389,3872,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/154.jpg\" height=\"149\" width=\"202\" /></div>\n<p>Упродовж тижня, від 6-го до 10-го березня, тривав «Львівський  псевдособор», покликаний ліквідувати Українську Греко-Католицьку Церкву.  На згадку про ці дні львівський Музей історії релігії організовує в  суботу, 9 березня, о 15-ій годині, вечір пам’яті.</p>\n<p>Виконавчий директор Інституту історії церкви кандидат історичних наук  Світлана Гуркіна розповіла&nbsp; кореспондентові Львівського радіо, як  готувався Львівський псевдособор.</p>\n','2013-03-09 00:00:25','http://oranta.org/index.php?option=com_content&view=article&id=1024:lvivsky-psevdosobor--drama-ugkc-u-berezni-1946-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Львівський псевдособор – драма УГКЦ у березні 1946 року','uk',3002,4485,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_647950.jpg\" height=\"182\" width=\"249\" /></div>\n<p>Споконвіків представники УГКЦ дбали про пізнання дітьми та  дорослими основних правд християнської віри. Так, ще Митрополит Андрей  Шептицький наголошував на важливості катехизації дітей: «Одне слово з  Божої науки, висказане ревним катехитом, може на ум і на серце дитини  зробити таке враження, що не дасться затерти до старости». Тепер же  Блаженніший Святослав Шевчук, Глава УГКЦ, зазначає, що&nbsp; важливими  складовими «живої парафії» є&nbsp; добрий душпастир, літургійне життя –  звершення таїнств Христової Церкви, катехизація – навчання віри,  койнонія – об’єднання вірян у єдину спільноту, дияконія – служіння  спільноти вірян іншим людям, місійність – коли віряни діляться своєю  вірою з близькими.</p>\n','2013-01-21 23:55:11','http://oranta.org/index.php?option=com_content&view=article&id=929:2013-01-21-21-57-07&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' «Закордоном катехизація конкурує із спортом і танцями», − Владика Петро (Стасюк)','uk',2906,4389,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/2576.jpg\" height=\"182\" width=\"249\" /></div>\n<p>Імена двох поважних релігійних особистостей звучали під час розмови із Верховним Архиєпископом <strong>Святославом Шевчуком</strong>, новим лідером <strong>Української Греко-Католицької Церкви</strong>. Один помер ще до того, як Архиєпископ народився. Інший – тоді, коли він закоренився у релігійній кар’єрі.</p>\n<p class=\"rtejustify\">Дух Митрополита Андрея Шептицького - благочестивого, кмітливого і  сміливого лідера Української Греко-Католицької Церкви під час ІІ  світової війни, і папи Івана Павла ІІ обіцяють бути провідними силами на  початку подорожі Блаженнішого Святослава, котрий очолює глобальну  церкву, що налічує понад п’ять мільйонів вірних.</p>\n','2011-06-15 23:52:17','http://oranta.org/index.php?option=com_content&view=article&id=760:blazhennishy-svjatoslav-ldlja-vas-ja-jepyskop-ale-razom-z-vamy-ja-hrystyjanynr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Святослав: «Для вас я єпископ, але разом з вами я - християнин»','uk',2493,3976,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/800-pharisees.jpg\" height=\"182\" width=\"249\" /></div>\n<p>З малечку наша первородним гріхом зранена природа нас привчає уникати  болю, страждання, терпіння, терпцю, одним словом, неприємних ситуацій.  Людина створена Богом з любові та для любові, для щастя і насолоди, але  гріх вніс в історію людського року викривлення усього цього. Усе сталося  затьмарене похіттю та егоїзмом. Людина перетворилася на єство, здатне  своїми діями зіпсувати практично усе. Поняття любові часто зведено до  поняття сексуальної хтивості, поняття щастя до посідання багатства,  поняття насолоди ототожнене з пізньоримськими оргіями.</p>\n','2012-03-19 02:34:23','http://oranta.org/index.php?option=com_content&view=article&id=840:grih-vidsutnosti-ljubovi-do-istyny-abo-lipshe-solodka-brehnja-anizh-girka-pravda&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Гріх відсутності любові до істини, або Ліпше солодка брехня, аніж гірка правда','uk',2700,4183,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/golovna.jpg\" height=\"182\" width=\"249\" /></div>\n<br />\n<p class=\"rtejustify\">Сучасний стан Церкви в Україні, ЇЇ взаємини з владою, а також  непоодинокі випадки утисків греко-католицьких громад, зокрема нещодавній  інцидент в Донецьку, коли вірним УГКЦ відмовилися продовжити оренду,  чим залишили їх без культової споруди, для<a href=\"http://catholicnews.org.ua/\"> «Католицького Оглядача»</a> прокоментувала п.<strong> Людмила Филипович</strong>, релігієзнавець, д-р філос. наук, проф., зав. відділенням релігійних процесів Інституту філософії НАНУ.</p>\n','2011-10-16 22:29:10','http://oranta.org/index.php?option=com_content&view=article&id=797:negatyvnu-gromadsku-dumku-pro-katolykiv-postino-pidzhyvljujut-rizni-politychni-syly-biljacerkovni-struktury-zmi-ljudmyla-fylypovych&catid=37:spilnota-cat&Itemid=49','P',NULL,'Негативну громадську думку про католиків постійно підживлюють різні політичні сили, біляцерковні структури, ЗМІ, – Людмила Филипович','uk',2586,4069,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/snimok_88.png\" height=\"182\" width=\"249\" /></div>\n<p>Московська церква в Україні дуже часто наголошує, що вона є найбільшою  конфесією у нашій державі. Але реальних підтверджень таким заявам нема.  Дані держслужб повністю спростовують цей міф.&nbsp; Що більше, Україна є  багатоконфесійною державою, в якій жодну з конфесій не можливо окреслити  як переважаючу. Звісно, тут необхідно враховувати факт, що властиво  Московський патріархат стрімко втрачає свої позиції на Сході країни на  користь протестантських деномінацій. Можливо, усвідомлення цього і  спонукає Московську церкву до супертісної співпраці з теперішньою  державною владою, яка виявляє величезну прихильність до цієї  деномінації.</p>\n','2011-07-16 23:27:28','http://oranta.org/index.php?option=com_content&view=article&id=774:chynna-ukrajinska-vlada-ta-upc-mp-rozhytujut-mizhreliginu-sytuaciju-v-ukrajini&catid=37:spilnota-cat&Itemid=49','P',NULL,'Чинна українська влада та УПЦ МП розхитують міжрелігійну ситуацію в Україні','uk',2534,4017,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/img_6120.jpg\" width=\"182\" height=\"249\" /></div>\n<p>У четвер, 31 березня 2011 р. у Ватиканському прес-центрі відбулася  зустріч Блаженнішого Святослава Шевчука з журналістами, присвячена  початку його служіння на чолі УГКЦ та зустрічі з Римським Архиєреєм  Бенедиктом XVI. Його супроводжували Преосвященні владики Богдан Дзюрах,  ЧНІ, Секретар Синоду Єпископів УГКЦ, та владика Кен Новаківський, Єпарх  Нью-Вестмінстерський (Канада).</p>\n<p class=\"rtejustify\">Вітаючи гостя, о. Федеріко Ломбарді, Директор Ватиканського  прес-центру, зазначив, що «новий Верховний Архиєпископ знає майже всі  мови, які ми можемо знати, отож, нема проблем у спілкуванні». Отож,  прес-конференція була проведена італійською, іспанською та англійською  мовами.</p>\n<p class=\"rtejustify\">Під час вступного слова Блаженніший Святослав сказав, що цей день для  Української Греко-Католицької Церкви є особливим, адже після Синоду  єпископи прибули до Апостольської Столиці, щоб, насамперед, виявити  природу цієї Церкви, яка є «східною», що живе своєю традицією,  «синодальною», якою керує Синод на чолі з Верховним Архиєпископом, та  «католицькою», яка переживає свою самобутність у повній, видимій та  дійсній єдності зі Святішим Отцем. І сьогоднішній візит – це нагода  виявати повне сопричастя зі Святішим Отцем.\n','2011-03-31 18:55:36','http://oranta.org/index.php?option=com_content&view=article&id=706:priorytetom-cerkovnogo-sluzhinnja-je-jevangelizacija&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Пріоритетом церковного служіння є євангелізація','uk',2378,3861,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/17_06_11.jpg\" height=\"182\" width=\"249\" /></div>\n<p><strong>Всесвітліші та всечесніші отці!</strong></p>\n<p class=\"rtejustify\"><strong>Преподобні ченці та черниці!</strong></p>\n<p class=\"rtejustify\"><strong>Дорогі у Христі брати і сестри!</strong></p>\n<p class=\"rtejustify\">&nbsp;29 червня 2011 року Божого Святіший Отець  Венедикт XVI святкуватиме 60-річчя своїх священичих свячень. За Божим  Провидінням цей Вселенський Архиєрей став Христовим священиком саме в  той день, коли Церква святкує празник Святих верховних апостолів Петра і  Павла (за григоріанським календарем). Цей ювілей спонукає нас до  вдячної молитви за дар Христового священства, що його прийняв 60 років  тому Глава Католицької Церкви, а водночас дає нам гарну нагоду  застановитися над важливістю його служіння у&nbsp; Вселенській Церкві.</p>\n','2011-06-19 23:08:29','http://oranta.org/index.php?option=com_content&view=article&id=763:zvernennja-synodu-ugkc-z-nagody-shistdesjatyrichchja-svjashchenychyh-svjachen-svjatishogo-otcja-venedykta-xvi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Звернення Синоду УГКЦ з нагоди шістдесятиріччя священичих свячень Святішого Отця Венедикта XVI','uk',2500,3983,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/230-9-1.jpg\" height=\"182\" width=\"249\" /></div>\n<p><strong>Особистий секретар Глави УГКЦ о. Іван Дацько — про Патріарха і про сучасну релігійну ситуацію</strong></p>\n<p>Про кардинала Йосифа Сліпого, главу Української греко-католицької  церкви (1944 — 1984), говорив увесь світ ще до того, як у Радянському  Союзі почули термін «горбачовська перебудова». Про його звільнення  клопотав американський президент Джон Кеннеді, а стараннями Папи Івана  XXIII йому було дозволено виїхати у вільний світ — до Риму. В жовтні  1963-го він виступив із промовою на ІІ Ватиканському Соборі, де,  всупереч традиції, його вітали оплесками. Після 18 років ГУЛАГу він ще  чверть століття керував Церквою. Попрощатися з ним прийшов Папа Іван  Павло ІІ, і знову, всупереч традиції, поцілував його руку. Згідно із  заповітом, 7 вересня 1992-го тіло Патріарха Йосифа перепоховане у крипті  Львівського кафедрального собору св. Юра.</p>\n','2011-12-17 02:05:37','http://oranta.org/index.php?option=com_content&view=article&id=813:lu-osyfa-slipogo-bula-dalekogljadnist-andreja-sheptyckogor&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'«У Йосифа Сліпого була далекоглядність Андрея Шептицького»','uk',2621,4104,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/article_0.jpg\" width=\"249\" height=\"182\" /></div>\n<p><strong>Доброго дня Вам всім. Слава Ісусу Христу!</strong></p>\n<p class=\"rtejustify\">Ці питання, які озвучив о. Ігор, постійно мені задавали Ви або Ваші  колеги протягом тих останніх трьох днів. Що нас очікує, куди ми йдемо,  куди ми рухаємось? Найперше, я хочу Вам сказати, що вважаю себе,  можливо, таким духовним сином і послідовником Блаженнішого Любомира. В  неділю в своєму слові на закінчення Літургії я сказав, що він, як той  добрий батько, вміло мене виховував, тобто, з одного боку, пригортав  мене до свого серця, втаємничував у всі свої плани, мрії, бо я свого  часу був його секретарем, а відтак будував патріаршу курію ще тоді у  Львові, а потім мене відіслав на другий кінець світу, аж до Аргентини  для того, щоб я, як він тоді казав, навчився давати собі раду. І тому, з  одного боку, я був залучений до будівництва тих всіх структур,  процесів, які ми сьогодні маємо в нашій Церкві, а, з іншого боку, мав  нагоду, знаєте, як той художник, який для того, щоб побачити цілісність  того, що він намалював, відходить трошки назад.</p>\n','2011-03-29 15:15:35','http://oranta.org/index.php?option=com_content&view=article&id=704:persha-pres-konferencija-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Перша прес-конференція Блаженнішого Святослава','uk',2370,3853,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/foto_01_2.jpg\" width=\"249\" height=\"182\" /></div>\n<p><span style=\"text-decoration: underline;\"><a href=\"http://212.77.9.15/audio/ra/00254661.RM\"><img src=\"http://www.radiovaticana.org/ucr/img/listen.gif\" border=\"0\" /></a></span>«<em>З  великою радістю вітаю сьогодні Блаженнішого Святослава Шевчука, нового  Верховного Архиєпископа Києво-Галицького, разом з єпископами та вірними,  які його супроводжують. Запевняю мою постійну молитву, щоб Пресвята  Тройця уділяла повноту дарів, зберігаючи в мирі та злагоді любий  український народ</em>», – цими словами Святіший Отець Венедикт&nbsp;XVI  розпочав своє привітання для вірних УГКЦ, які прийшли на загальну  аудієнцію 30&nbsp;березня 2011&nbsp;р., очолювані новим Главою і Отцем Блаженнішим  Святославом, разом з іншими єпископами та духовенством.</p>\n<p> </p>\n','2011-03-30 15:13:35','http://oranta.org/index.php?option=com_content&view=article&id=705:lneha-presvjata-tricja-zmicnjuje-v-myri-ta-zlagodi-uljubleny-ukrajinsky-narodr-papa-venedykt-xvi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Нехай Пресвята Трійця зміцнює в мирі та злагоді улюблений український народ», - Папа Венедикт XVI','uk',2375,3858,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/shevchuk_ryzy_usmishka.jpg\" width=\"249\" height=\"182\" /></div>\n<p>«Сьогодні Церква потребує вашої спонтанності та інтуїтивного відчуття  реальності Бога, що є присутнім між нами. Саме ви є устами, якими  Церква вітає цього дня свого Царя, співаючи: «Благословен той, хто йде в  ім’я Господнє!».</p>\n<p>Такими словами <a href=\"http://www.ugcc.org.ua/1753.0.html\">Блаженніший Святослав звернувся до молоді</a> у своєму першому після сходження на престол Глави і Отця Української  Греко-Католицької Церкви пастирському посланні з нагоди Квітної неділі –  святкового дня тріумфального входу Господнього до стольного града  Єрусалима.</p>\n','2011-04-15 17:59:05','http://oranta.org/index.php?option=com_content&view=article&id=724:lne-bitesja-zustritysja-z-hrystom-i-ne-soromtesja-vyznaty-sebe-hrystyjanamyr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Не бійтеся зустрітися з Христом і не соромтеся визнати себе християнами»','uk',2403,3886,0,NULL),
(1,1,'<div class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/rtemagicc_slipyj_b.jpg.jpg\" height=\"182\" width=\"133\" /></div>\n<p><strong>ПАСТИРСЬКЕ ПОСЛАННЯ</strong></p>\n<p class=\"rtecenter\">Синоду Єпископів Української Греко-Католицької Церкви до вірних<br /> з нагоди 120-літнього ювілею Патріарха Йосифа (Сліпого)</p>\n<p>Дорогі у Христі!</p>\n<p>Кожен ювілей наших слуг Божих та ісповідників віри – це не лише добра  нагода соборно згадати їхні імена і всім Божим народом нашої Церкви  помолитися за упокій їхніх душ та просити Бога про їхню прославу на  землі: ми робимо це постійно. Відзначення ювілеїв – це також і духовний  привілей нового спілкування зі світочами нашої церковної історії, що  здійснюється через читання їхнього слова та осмислення їхнього чину. Це  робить нас причасниками їхньої мудрості та вдячними спадкоємцями їхнього  досвіду.</p>\n','2012-02-16 18:35:52','http://oranta.org/index.php?option=com_content&view=article&id=833:pastyrske-poslannja-synodu-jepyskopiv-ukrajinskoji-greko-katolyckoji-cerkvy-do-virnyh-z-nagody-120-litnogo-juvileju-patriarha-osyfa-slipogo&catid=27:gk-v-ukrajini-cat&Itemid=57','P','','ПАСТИРСЬКЕ ПОСЛАННЯ Синоду Єпископів Української Греко-Католицької Церкви до вірних з нагоди 120-літнього ювілею Патріарха Йосифа (Сліпого)','uk',2670,4153,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/033264ea68.jpg\" height=\"104\" width=\"150\" /></div>\n<p>«Ми хочемо посвідчити всім українцям саме те, що випливає зі  святкування Пасхи − Христос є Той, що смертю смерть подолав. І той, хто  справді святкує Пасху, звільняється від розчарування, отримує надію, а  отже, отримує силу любові, яка здатна запропонувати щось конструктивне».</p>\n<p>Це сказав Предстоятель Української Греко-Католицької Церкви  Блаженніший Святослав (Шевчук) у пасхальному інтерв\'ю «5 каналу»,  відповідаючи на запитання, як бути справжнім християнином в Україні,  коли зростає розчарування, нетерпимість і ненависть, особливо до  політичних речей?</p>\n','2012-04-17 22:05:36','http://oranta.org/index.php?option=com_content&view=article&id=853:lsvjatkuvannja-pashy-zvilnjaje-vid-rozcharuvannjar--glava-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Святкування Пасхи звільняє від розчарування», − Глава УГКЦ ','uk',2723,4206,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/09c2fa9cce.jpg\" height=\"96\" width=\"150\" /></div>\n<p>17 квітня відбулася зустріч Всеукраїнської ради Церков і  релігійних організацій (ВРЦіРО) з Головою Верховної Ради Володимиром  Рибаком та депутатами ВР із різних депутатських фракцій.</p>\n<p>Ця зустріч відбулася з ініціативи міжфракційного депутатського  об’єднання «За сприяння свободі сумління». На цій зустрічі від імені  ВРЦіРО головував Блаженніший Святослав (Шевчук).</p>\n','2013-04-17 20:52:21','http://oranta.org/index.php?option=com_content&view=article&id=1120:2013-04-17-20-53-22&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Блаженніший Святослав: «Зневага парламентаризму в Україні викликає велике занепокоєння в суспільстві» ','uk',3167,4650,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/1a90187012.jpg\" height=\"100\" width=\"150\" /></div>\n<p>«У сьогоднішньому світі важко бути молодим, а ще важче бути  молодим християнином. Переслідування завжди були притаманні життю  Церкви, але в сучасному західному світі вони стали іншими, більш  витонченими, прихованими, а тому – не менше загрозливими та руйнівними.  Часто молода людина соромиться сказати про своє християнство, боячись  бути осміяною».</p>\n','2013-04-24 00:52:20','http://oranta.org/index.php?option=com_content&view=article&id=1140:2013-04-24-05-54-14&catid=37:spilnota-cat&Itemid=49','P',NULL,' «У сьогоднішньому світі важко бути молодим, а ще важче бути молодим християнином», - Блаженніший Святослав ','uk',3191,4674,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/2fb205e110.jpg\" height=\"217\" width=\"150\" /></div>\n<p>Десять років тому,&nbsp;26 січня 2001 року,&nbsp;було офіційно  повідомлено, що новим Главою УГКЦ обрано Блаженнішого Любомира. У  неділю, 28 січня 2001 року, у соборі Святого Юра у Львові було  відслужено урочисту Архиєрейську Літургію, під час якої відбулася  інтронізація (введення на престол) новообраного Предстоятеля УГКЦ. У  своїй інтронізаційній промові (<a href=\"http://www.youtube.com/user/pressugcc\" target=\"_blank\">дивитися</a>) Блаженніший Любомир вказав на своїх два основних завдання: життя у вірі і єдність у Христі.</p>\n<p>«Жити у вірі, допомагати віруючим і невіруючим, щоб ми усі з\'єдналися  у тій вірі, щоб наш народ, наша Церква була тим великим одним в Ісусі  Христі. І це не є моя особиста програма. Це те, що ми одержали від наших  предків. І ми хочемо це здійснювати. Сьогодні в наших обставинах і тим  способом ми прославимо Бога, який дав нам цей дар», – сказав 28 січня  2001 року Блаженніший Любомир.</p>\n','2011-01-29 10:07:37','http://oranta.org/index.php?option=com_content&view=article&id=670:zhyttja-u-viri-i-jednist-u-hrysti-progolosyv-programnymy-zasadamy-cerkvy-10-rokiv-tomu-blazhennishy-ljubomyr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Життя у вірі і єдність у Христі проголосив програмними засадами Церкви 10 років тому Блаженніший Любомир ','uk',2305,3788,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/45f6d00026.jpg\" height=\"104\" width=\"150\" /></div>\n<p><strong>Наша Церква опрацьовує стратегію до 2020 року, -  наголосив на зустрічі голів молодіжних комісій Блаженніший Святослав,  яка відбулася 10 червня 2011 р. у залі засідань Львівської архиєпархії.</strong></p>\n<p><strong> </strong></p>\n<p><strong>Завдання Синоду</strong></p>\n<p>На початку зустрічі Глава Церкви повідомив, що попередній Синод  визначив групу єпископів, які створили групу №1. Ця група думала над  тим, якою ми хочемо бачити нашу Церкву. У цю групу, серед інших, також  був включений і Блаженніший Святослав і вона мала випрацювати стратегію  розвитку до 2020 р.</p>\n','2011-06-13 12:50:08','http://oranta.org/index.php?option=com_content&view=article&id=758:lv-ukrajini-vidbuvajetsja-ideologichna-zachystka-molodizhnyh-organizaci-i-ruhivr-blazhennishy-svjatoslav-na-zustrichi-iz-golovamy-molodizhnyh-komisi-&catid=37:spilnota-cat&Itemid=49','P',NULL,'«В Україні відбувається ідеологічна зачистка молодіжних організацій і рухів», - Блаженніший Святослав на зустрічі із головами молодіжних комісій ','uk',2489,3972,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/50d55595ad.jpg\" width=\"150\" height=\"100\" /></div>\n<p>«Я відважився на цей крок, бо переконаний, що ми вже дійшли до  того ступеня синодального розвитку, осягнули певну організаційну форму,  яка дає нам можливість зробити такий перехід спокійно». Про це  Блаженніший Любомир, Глава УГКЦ у 2001-2011 роках, сказав в<strong> </strong>інтерв’ю виданню «Обозреватель», яке зараз готується до опублікування.</p>\n<p>На думку Блаженнішого Любомира, синодальний устрій є питомою для  східних християн давньою традицією, і синодальне ведення Церкви є  великим досяганням УГКЦ за останніх двадцять років, відколи Україна  стала вільною.</p>\n','2011-04-07 15:24:59','http://oranta.org/index.php?option=com_content&view=article&id=715:blazhennishy-ljubomyr-pojasnyv-chomu-vidvazhyvsja-peredaty-urjad-cerkvy-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Любомир пояснив, чому відважився передати уряд Церкви ','uk',2392,3875,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/5a8c42debf.jpg\" width=\"150\" height=\"225\" /></div>\n<p>Як формувалося священиче покликання новобраного Глави УГКЦ, хто  був його небесним заступником, про несподівану єпископську хіротонію -  зі слів Верховного Архиєпископа Києво-Галицького&nbsp;<a href=\"http://dyvensvit.org/cerkva/175/2898\" target=\"_blank\">Святослава Шевчука</a>. А також - його бачення єпископського служіння, привітання Блаженнішого Любомира (Гузара) та Папи Венедикта XVI у матеріалі.</p>\n<p><strong>Священство.</strong> Оскільки священики в часи підпілля  підтримували не лише Церковні традиції, а й громади в цілому, молодь  часто ставила перед собою питання про священство. Також їх надихав  приклад монашества – в їхніх очах можна було побачити «живого  страждаючого Христа». Таким прикладом був і покійний отець Михайло  Косила, який виконував уряд підпільного ректора підпільної духовної  семінарії Івано-Франківської єпархії, що знаходилася в селищі Дора біля  Яремча.</p>\n','2011-03-25 20:29:40','http://oranta.org/index.php?option=com_content&view=article&id=697:lnapershe-svojim-poklykannjam-ja-zavdjachuju-svoji-peresliduvani-znevazheni-a-vidtak-voskresli-cerkvir-blazhennishy-svjatoslav-shevchuk-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Найперше своїм покликанням я завдячую своїй переслідуваній, зневаженій, а відтак воскреслій Церкві», - Блаженніший Святослав (Шевчук) ','uk',2361,3844,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/5dcc384c8e.jpg\" height=\"102\" width=\"150\" /></div>\n<p>«Сьогодні відчуваємо себе людьми Божими, Божим народом. Бачимо,  що християнство – це не якась \"одна з релігій світу\", де людина шукає  Бога. Християнство – життя у Святому Дусі. Євангеліє Христове не є ще  однією теорією чи моральною доктриною, яку нас запрошують вивчити й  пізнати. Євангеліє – проголошення істини, що я і ви можемо одержати нове  серце. Дух Святий може зійти на кожного, хто Його прикликає і входить у  членство Церкви Христової».</p>\n','2013-06-24 18:26:34','http://oranta.org/index.php?option=com_content&view=article&id=1285:2013-06-24-15-27-49&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Блаженніший Святослав: «Просимо сьогодні Духа Святого, щоб він усунув з-посеред нас кам’яні серця і дав здатність розуміти ближнього» ','uk',3377,4860,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/670daff0c3.jpg\" height=\"100\" width=\"150\" /></div>\n<p>13 червня 2013 року Святіший Отець Франциск&nbsp;вперше за час свого  понтифікату служив Святу Месу іспанською мовою, оскільки в  Євхаристійному Богослужінні у ватиканській резиденції Святої Марти взяли  участь представники посольств та консульств Аргентини в Італії та при  міжнародній організації ФАО. Того дня із Вселенським Архиєреєм  співслужив також Блаженніший Святослав (Шевчук), Глава УГКЦ, який цими  днями брав участь у засіданнях ХІІІ Звичайної Загальної Ради при  Генеральному Секретаріаті Синоду Єпископів</p>\n','2013-06-13 20:50:51','http://oranta.org/index.php?option=com_content&view=article&id=1253:-blazhennishy-svjatoslav-molyvsja-zi-svjatishym-otcem-&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Блаженніший Святослав молився зі Святішим Отцем ','uk',3334,4817,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/73eb41ab41.jpg\" height=\"181\" width=\"125\" /></div>\n<p>«Зі смиренною вдячністю схиляю голову перед матір\'ю кожного  священика нашої Церкви. Висловлюю вдячність матері священика передусім  за її жертви, за живу віру, – за віру, пронизану любов’ю, яку вона з  любов\'ю прищепила своєму синові», - пише у <a href=\"http://ugcc.org.ua/2732.0.html\">Листі до дорогих матерів священиків, які служать в УГКЦ</a>, Блаженніший Святослав (Шевчук), Глава УГКЦ.</p>\n<p>У цьому листі автор зазначає, що найурочистіший і найбільш натхненний  момент, що його може пережити вірний нашої Церкви, – це мати привілей  бути на свяченнях молодого юнака та отримати з його рук перше священиче  благословення на завершення чину рукоположення.&nbsp;</p>\n','2013-05-10 21:16:55','http://oranta.org/index.php?option=com_content&view=article&id=1174:2013-05-10-18-19-46&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' «Бути матір\'ю священика - це привілей і благородне покликання…» - Блаженніший Святослав ','uk',3229,4712,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/74a302dc06.jpg\" height=\"104\" width=\"150\" /></div>\n<p>«Викликає нашу стурбованість те, що на порозі майбутніх  парламентських виборів в Україні деякими політиками та політичними  угрупованнями знову ставиться розмінною монетою питання української  мови. Нас хвилюють рівно ж повідомлення про закриття україномовних шкіл в  деяких регіонах нашої Батьківщини. Ми переконані, що ця вкрай  недалекоглядна і безвідповідальна політика не служить утвердженню  української державності», − з такими словами звернулися єпископи  Української Греко-Католицької Церкви у листі до Президента України  Віктора Януковича. Цей лист вони написали у Бразилії, під час проведення  цьогорічного засідання Синоду Єпископів УГКЦ. Про це 5 жовтня, під час  прес-конференції в УНІАНі, повідомив Блаженніший Святослав (Шевчук),  Глава УГКЦ.</p>\n','2011-10-05 11:44:26','http://oranta.org/index.php?option=com_content&view=article&id=792:jepyskopy-ugkc-napysaly-prezydentu-ukrajiny-pro-ukrajinsku-movu-emigraciju-pravosuddja-ta-vidnosyny-mizh-cerkvamy-i-derzhavoju-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Єпископи УГКЦ написали Президенту України: про українську мову, еміграцію, правосуддя та відносини між Церквами і державою ','uk',2579,4062,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/89c9c041a6.jpg\" height=\"104\" width=\"150\" /></div>\n<p>«Священик повинен бути праведником, що, за словами ап. Павла,  живе з віри... &nbsp;Він повинен навчати віри, проголошуючи Слово Боже...  Душпастир повинен навчати віри насамперед свідченням власного життя», -  підкреслює Блаженніший Святослав (Шевчук), Глава УГКЦ, у <a href=\"http://ugcc.org.ua/2705.0.html\">Посланні до священнослужителів УГКЦ на Страсний четвер.</a></p>\n','2013-04-26 03:51:14','http://oranta.org/index.php?option=com_content&view=article&id=1148:2013-04-27-05-53-08&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Молюся, щоб Святий Дух пробуджував у серцях священиків справжній місійний дух», - Блаженніший Святослав (Шевчук) ','uk',3199,4682,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/904a65d8e7.jpg\" height=\"104\" width=\"150\" /></div>\n<p><strong> </strong></p>\n<p>Пізнати Господа серед щоденного життя, коли інші будуть Його  зневажати, заміняти світськими ідолами і можновладцями, закликав Глава  Української Греко-Католицької Церкви Блаженніший Святослав у Вербну  неділю.</p>\n<p>Під час Архиєрейської Божественної Літургії в один з дванадцяти  найбільших празників у літургійному році у Патріаршому соборі  Воскресіння Христового у Києві за участі багатолюдної спільноти вірних  Церкви з Главою УГКЦ співслужили Владика Богдан (Дзюрах), Секретар  Синоду Єпископів, Владика Йосиф (Мілян), Єпископ-помічник Київської  Архиєпархії, та духовенство Київської архиєпархії УГКЦ.</p>\n','2011-04-18 09:47:11','http://oranta.org/index.php?option=com_content&view=article&id=725:blazhennishy-svjatoslav-u-verbnu-nedilju-lspytamo-sebe-hto-je-nashym-caremr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Святослав у Вербну неділю: «Спитаймо себе, хто є нашим царем?» ','uk',2408,3891,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/bf37548e2b.jpg\" height=\"111\" width=\"150\" /></div>\n<p>Глава і Отець Української Греко-Католицької Церкви закликає  українських християн наслідувати порозуміння в «глобальному  православно-католицькому діалозі». «Ми мусимо перестати тішитися тільки  собою, власними інтелектуальними подіями, акціями, ми повинні навчитися  тішитися присутністю ближнього», - сказав Предстоятель УГКЦ Блаженніший  Святослав 7 червня на круглому столі «Україна в православно-католицькому  діалогові» за участю кардинала Курта Коха, президента Папської ради  сприяння єдності християн та співголови змішаної Міжнародної комісії з  богословського діалогу між Католицькою і Православною Церквами, який  зараз перебуває з візитом в Україні.</p>\n','2013-06-09 09:27:18','http://oranta.org/index.php?option=com_content&view=article&id=1241:2013-06-09-06-29-30&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Люди втомилися від розколів і поділів. Подекуди вони є більше сміливими і відкритими, ніж ієрархи», - Блаженніший Святослав ','uk',3316,4799,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/ca5ccd2377.jpg\" height=\"153\" width=\"150\" /></div>\n<p>«Блаженний Папа Іван Павло II відчував потребу відвідувати  світ. Це не було його особистим способом діяння, а своєрідним релігійним  завданням. Такі мандрівки світом були його способом проповідування  Ісуса Христа. Підкреслюю, Папа був мужем молитви і людиною віри. Мені  здається, коли згадую його людські характеристики, його особисту  історію, що цей елемент глибокої та щирої молитовності пояснює його  великий вплив на світ», – про це сказав Блаженніший Любомир (Гузар),  Архиєпископ-емерит УГКЦ, в інтерв’ю для «Історичної правди з Вахтангом  Кіпіані» телеканалу ZIK.</p>\n','2013-04-03 09:25:01','http://oranta.org/index.php?option=com_content&view=article&id=1085:2013-04-03-06-26-40&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Блаженніший Любомир (Гузар): «Глибока і щира молитва блаженного Папи Івана Павла II пояснює його великий вплив на світ» ','uk',3112,4595,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/d4f55e5857.jpg\" height=\"112\" width=\"150\" /></div>\n<p class=\"xfmc0\">«З нагоди Року дияконії (соціального служіння) в УГКЦ у час Великого посту Благодійний фонд <a href=\"http://www.caritas-kiev.org.ua/\" target=\"_blank\">«Карітас-Київ»</a> завдяки жертводавцям провів масштабну акцію допомоги незрячим і літнім  людям в Україні - \"Годинники для незрячих\". Закуплено та роздано майже  1500 годинників і будильників. У планах ще така ж кількість, а, можливо,  і більша», - повідомляє о. Роман Сиротич, директор Благодійного фонду  «Карітас-Київ».</p>\n','2013-04-22 23:31:16','http://oranta.org/index.php?option=com_content&view=article&id=1135:2013-04-22-21-02-16&catid=29:propovidi-cat&Itemid=34','P',NULL,' «Півтори тисячі годинників для незрячих – це прояв любові до ближніх», - о. Роман Сиротич, директор БФ «Карітас-Київ» ','uk',3186,4669,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/e5e78203d6.jpg\" height=\"112\" width=\"150\" /></div>\n<p>Під час освячення Патріаршого собору буде освячено центральний  престол у храмі, повідомив владика Йосиф (Мілян),&nbsp;голова наглядової ради  будівництва собору Воскресіння Христового. Як зазначив єпископ, зараз,  зокрема, кладуть підлогу у святилищі, де й стоятиме центральний престол.  Залишається відкритим питання, чи викладатимуть підлогу мармуром у  всьому Соборі. Нині вже завершили підлогове опалення, потім заливатимуть  стяжку під мармур. «Ми будуємо Собор на століття, тому нашвидкуруч  робити не хочеться. Гадаю, після освячення, до зими, ми це реалізуємо»</p>\n','2013-06-14 20:48:57','http://oranta.org/index.php?option=com_content&view=article&id=1252:2013-06-15-17-50-19&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Владика Йосиф (Мілян): «Маємо амбітний план, тому надіємося на Божу допомогу і підтримку добрих людей» ','uk',3333,4816,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2b62079f52.jpg\" height=\"101\" width=\"150\" /></div>\n<p>Близько 20 тисяч вірних взяло участь у першій всеукраїнській  прощі до Страдчу, що на Львівщині, присвяченій пам’яті страдецьких  блаженних мучеників – о. Миколая Кондрата і дяка Володимира Прийми, Дню  мирянина та Року віри.</p>\n<p>На початку паломництва студенти Львівської духовної семінарії Святого  Духа та Чортківської дяківсько-катехитичної академії імені  священномученика Григорія Хомишина співали молебень та співом  супроводжували пішу ходу до Страдецької гори.</p>\n','2013-06-26 21:52:12','http://oranta.org/index.php?option=com_content&view=article&id=1299:2013-06-26-19-12-48&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Страдч - гора блаженств нашого українського народу…»','uk',3389,4872,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2c62b5c356.gif\" height=\"201\" width=\"150\" /></div>\n<p>«Закон проти корупції може стати ефективним тільки тоді, коли  члени нашого суспільства змалечку будуть виховані в дусі чесної  поведінки». Про це Глава УГКЦ в 2001-2011 роках Блаженніший Любомир  написав в опублікованій виданням «Українська правда» статті <a href=\"http://www.pravda.com.ua/columns/2011/06/2/6264424/\" target=\"_blank\">«Як побороти корупцію»</a>.</p>\n<p>Аналізуючи значення Закону № 7487 від 7 квітня 2011 року «Про засади  запобігання і протидії корупції в Україні», Блаженніший Любомир  зазначає, що це дуже важлива подія в житті нашого народу, щонайменше під  двома оглядами: «<em>По-перше</em>, устами своїх представників у  Верховній Раді наш народ заявив, що вважає корупцію, чи хабарництво,  великим лихом, якого треба позбутися. <em>По-друге</em>, на підставі  закону від 17 квітня 2010 року творяться похідні закони, згідно з якими  можна карати порушників у цій ділянці, бо корупція − це злочин,  недотримання урядових повноважень задля власної користі», − зазначається  у статті.</p>\n','2011-06-04 16:09:13','http://oranta.org/index.php?option=com_content&view=article&id=752:blazhennishy-ljubomyr-zaklykav-ukrajinciv-podbaty-pro-mabutnje-bez-korupciji-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Любомир закликав українців подбати про майбутнє без корупції ','uk',2474,3957,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/4d8ec5f941.jpg\" height=\"84\" width=\"150\" /></div>\n<p>28 липня в Українській Греко-Католицькій Церкві соборно  відзначили день пам’яті рівноапостольного князя Володимира. Божественну  Архиєрейську Літургію в Патріаршому соборі Воскресіння в Києві очолив  Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав  (Шевчук). З Главою УГКЦ на Літургії співслужили: владика Ігор  (Возьняк), Архиєпископ і Митрополит Львівський, владика Володимир  (Війтишин), Архиєпископ і Митрополит Івано-Франківський, владика Василь  (Семенюк), Архиєпископ і Митрополит Тернопільсько-Зборівський, а також  владика Михайло (Колтун), Єпарх Сокальсько-Жовківський, владика Дмитро  (Григорак), Єпарх Бучацький, владика Степан (Меньок), Екзарх  Донецько-Харківський, владика Йосафат (Говера), Екзарх Луцький, владика  Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, та владика Йосиф  (Мілян), Єпископ-помічник Київської архиєпархії.</p>\n','2013-07-28 23:41:42','http://oranta.org/index.php?option=com_content&view=article&id=1381:-v-ugkc-soborno-vidznachyly-den-pamjati-rivnoapostolnogo-knjazja-volodymyra-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' В УГКЦ соборно відзначили день пам’яті рівноапостольного князя Володимира ','uk',3468,4951,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/56c6269126.jpg\" height=\"154\" width=\"106\" /></div>\n<p>Історія Русі-України вчить, що саме український народ є новим  Христовим людом, який зродився у хрещальних водах Дніпра. Як Богоявленню  на Йордані передувало Різдво Божого Сина і Його приховане життя в  Назареті, так і Богоявленню на Дніпрі передувала кількасотлітня історія  прищеплення християнства та його непомітного, але впевненого зростання  на українській землі. Таку думку висловив Блаженніший Святослав  (Шевчук), Глава УГКЦ, у <a href=\"http://www.ugcc.org.ua/2777.0.html\">Посланні до вірних УГКЦ та всіх людей доброї волі з нагоди відзначення 1025-річчя Хрещення Русі-України</a>.</p>\n','2013-07-22 20:37:21','http://oranta.org/index.php?option=com_content&view=article&id=1353:-qhreshchennja-stalo-lvesnoju-nashogo-narodur-spovnjujuchy-nacionalny-organizm-duhom-voskreslogo-tila-hrystovogoq-glava-ugkc-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' \"Хрещення стало «весною нашого народу», сповнюючи національний організм духом воскреслого Тіла Христового...\" - Глава УГКЦ ','uk',3462,4945,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/6034aff6e7.jpg\" height=\"106\" width=\"150\" /></div>\n<p>«Сьогодні існує багато сучасних засобів комунікації, які значно  полегшують спілкування. Гадаю, потрібно їх використовувати. Адже, коли  ми думаємо разом і обмінюємося власними думками та досвідами, ми стаємо  сильнішими і, відповідно, можемо більше зробити для Церкви і свого  народу. Заохочую вас через ці сучасні засоби комунікації бути в контакті  один з одним: в контакті думок та діл», - закликав молодіжних лідерів  Блаженніший Любомир (Гузар), Архиєпископ-емерит УГКЦ, під час Форуму  молодіжних лідерів УГКЦ, що відбувся 28-30 червня у Львівській духовній  семінарії Святого Духа УГКЦ.</p>\n','2013-07-01 20:23:49','http://oranta.org/index.php?option=com_content&view=article&id=1314:lzaohochuju-vas-buty-v-kontakti-odyn-z-odnymr-blazhennishy-ljubomyr&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Заохочую вас бути в контакті один з одним…», - Блаженніший Любомир','uk',3404,4887,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/63f70967fe.jpg\" height=\"96\" width=\"150\" /></div>\n<p>«Дорогі брати й сестри! Дякую Господу Богу за те, що можу  відслужити цю Святу Месу з нагоди початку Петрового служіння в день  пам’яті святого Йосифа, Обручника Пречистої Діви Марії та Небесного  покровителя Вселенської Церкви: це дуже багатий за змістом збіг. Крім  того, сьогодні іменини мого шанованого попередника: ми єднаємося з ним  молитвою, сповненою любові та вдячності», – цими словами Папа Франциск  розпочав свою проповідь під час інавгураційної Служби Божої, яку він  очолив на площі Святого Петра у вівторок, 19 березня 2013 року.</p>\n','2013-03-19 23:42:10','http://oranta.org/index.php?option=com_content&view=article&id=1061:oberigaty-bozhi-dary-shchob-zapalyty-svitlo-nadiji&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Оберігати Божі дари, щоб запалити світло надії!','uk',3062,4545,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/86c7edfa70.jpg\" height=\"175\" width=\"150\" /></div>\n<p>З нагоди двохсотріччя з дня народження о. Маркіяна Шашкевича <em>(6 листопада 1811 року)</em> та з приводу його річниці смерті <em>(7 червня 1843 року)</em> Блаженніший Святослав звернувся з <a href=\"http://www.ugcc.org.ua/1826.0.html\">Посланням</a> до українського духовенства та мирян. Глава УГКЦ закликав бачити у  постаті цього священика приклад любові до свого народу, який захищав  його права і свободи та підносив культуру&nbsp;й духовність.</p>\n<p>Незважаючи на коротке життя о. Маркіяна (32 роки), його внесок у  формування українського національного духу та національне пробудження  нашого народу є неоціненним. «Великий поет, визначний лінгвіст, а  передусім невтомний, ревний і святий священик, котрого Господь послав  нашому народові в часи великого духовно-культурного занепаду, щоб  пробудити його, показати йому його гідність і велич, скерувати його на  шлях національного та духовного відродження», – таким бачить його особу  Глава УГКЦ.</p>\n','2011-06-08 19:03:15','http://oranta.org/index.php?option=com_content&view=article&id=756:ugkc-vshanovuje-pamjat-o-markijana-shashkevycha-jaky-nacionalno-i-duhovno-vidrodzhuvav-ukrajinu-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'УГКЦ вшановує пам’ять о. Маркіяна Шашкевича, який національно і духовно відроджував Україну ','uk',2480,3963,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/9d84f8fccc.jpg\" height=\"217\" width=\"150\" /></div>\n<p>У Страсний (Чистий) четвер, 21 квітня, Глава Української  Греко-Католицької Церкви Блаженніший Святослав (Шевчук), очолить Вечірню  з Літургією Василія Великого та вперше здійснить Чин омивання ніг  дванадцятьом священикам. Урочистості проходитимуть у Патріаршому соборі  Воскресіння Христового у Києві (<em>вул.</em><em> Микільсько-Слобідська, 5</em>). <strong>(Літургія розпочнеться о 7-00, Чин омивання ніг – 9-00).</strong></p>\n<p>«Першим жестом – який ми звершуємо у катедральних храмах під час чину  вмивання ніг саме у Великий четвер – Христос у видимий і зворушливий  спосіб упокорює себе перед власними учнями і вказує їм на те, що Він є  серед них як слуга. … Таким чином Спаситель навчає нас, що Його  священство не є запорукою людського владарювання чи привілеєм. <em>Бути священиком – означає бути для всіх слугою</em>,  бути тим, хто щодня обмиває людську нечистоту, тобто служить ближньому  за прикладом свого Господа», - наголошує Блаженніший Святослав у <a href=\"http://www.ugcc.org.ua/1765.0.html\">Посланні</a> до священнослужителів УГКЦ .</p>\n','2011-04-20 14:28:14','http://oranta.org/index.php?option=com_content&view=article&id=729:u-strasny-chetver-blazhennishy-svjatoslav-upershe-zdisnyt-chyn-omyvannja-nig-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'У Страсний четвер Блаженніший Святослав уперше здійснить Чин омивання ніг ','uk',2415,3898,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/aea724377c.jpg\" height=\"92\" width=\"150\" /></div>\n<p><em>Lumen Fidei</em> («<em>Світло віри</em>») – так називається  перша енцикліка Папи Франциска, підписана 29 червня 2013 року, яка  вийшла в п’ятницю, 5 липня. Звернена до єпископів, пресвітерів,  дияконів, богопосвячених осіб та мирян і поділена на чотири розділи,  вона, як зазначає сам Папа Франциск у 7 пункті, була майже закінчена  його попередником, і додає: «За це я йому дуже вдячний й у Христовому  братерстві приймаю цю цінну працю, роблячи свій вклад<em> </em>».</p>\n','2013-07-06 10:14:14','http://oranta.org/index.php?option=com_content&view=article&id=1318:-lumen-fidei--persha-encyklika-papy-francyska-&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Lumen Fidei – перша енцикліка Папи Франциска ','uk',3415,4898,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/d418ae54a5.jpg\" height=\"100\" width=\"150\" /></div>\n<p>Надзвичайні досягнення блаженного Папи Івана Павла ІІ  показують, що такого поняття, як «історична неминучість» не існує.  Ситуація не мусить бути такою, якою вона є, - криву історії можна  зігнути в більш людському напрямку. Таку думку висловив автор  бестселерів про Папу, всесвітньовідомий філософ Джордж Вайґель на  міжнародному симпозіумі «Церква у ХХІ столітті: виклики служіння у  глобалізованому світі», що відбувся 5 липня в Українському католицькому  університеті. Про це повідомляє прес-служба УКУ.</p>\n','2013-07-08 13:59:54','http://oranta.org/index.php?option=com_content&view=article&id=1320:lpryklad-ivana-pavla-ii-vchyt-shcho-kryvu-istoriji-mozhna-zignutyr&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Приклад Івана Павла ІІ вчить, що криву історії можна зігнути»','uk',3419,4902,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/d5e2b131d3.jpg\" height=\"93\" width=\"150\" /></div>\n<p>Прибути на освячення Патріаршого собору Воскресіння Христового,  щоб оновити свій світильник віри, закликав Глава Української  Греко-Католицької Церкви Блаженніший Святослав (Шевчук) українців у <a href=\"http://ugcc.tv/ua/media/66829.html\" target=\"_blank\">відеозверненні</a> з Лівого берега Дніпра.</p>\n<p>«Тут, на святих київських горах, на берегах древнього  Дніпра-Славутича, по-особливому відчутна глибина української душі і  ширина українського світу.</p>\n','2013-07-26 23:40:27','http://oranta.org/index.php?option=com_content&view=article&id=1380:-lz-nagody-hreshchennja-rusi-ukrajiny-zasjaje-shche-odyn-svitylnyk-viry-knjazja-volodymyrar-blazhennishy-svjatoslav-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «З нагоди Хрещення Русі-України засяє ще один світильник віри князя Володимира», - Блаженніший Святослав ','uk',3469,4952,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/typo3temp/pics/2aeaf73e40.jpg\" height=\"104\" width=\"150\" /></div>\n<p class=\"Default\">«Проголошення Доброї Новини всім людям, тобто  євангелізація, − це місія, яку доручив Церкві Господь Бог. Українська  Греко-Католицька Церква, як невід’ємна частина Вселенської Церкви, також  бере участь у цій місії», − сказав у своєму вступному слові під час  прес-конференції в УНІАН 5 жовтня 2011 року Блаженніший Святослав  (Шевчук), Глава УГКЦ, розповідаючи про <em>Стратегію розвитку УГКЦ на період до 2020 року</em>.</p>\n<p class=\"Default\">За словами Предстоятеля<strong> </strong>УГКЦ, для  виконання вищевказаної місії Церкви, єпископи вирішили якнайчіткіше  визначити стратегію діяльності Церкви на наступні роки. Щоб полегшити  втілення цього проекту та кращого відстеження його перебігу, було  визначено період десять років: до 2020 року (включно).</p>\n','2011-10-05 11:46:21','http://oranta.org/index.php?option=com_content&view=article&id=793:pro-strategiju-rozvytku-ugkc-na-period-do-2020-roku-lzhyva-parafija--misce-zustrichi-z-zhyvym-hrystomr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Про Стратегію розвитку УГКЦ на період до 2020 року: «Жива парафія – місце зустрічі з живим Христом» ','uk',2578,4061,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/typo3temp/pics/4143aee650.jpg\" width=\"150\" height=\"217\" /></div>\n<p>Виборчий Синод Єпископів Української Греко-Католицької Церкви,  зібраний у Львові-Брюховичах 21-24 березня, відповідно до приписів  Кодексу канонів Східних Церков та Партикулярного права Української  Греко-Католицької Церкви, обрав Верховним Архиєпископом УГКЦ  Преосвященного Владику Святослава (Шевчука).</p>\n<p>Святійший Отець Венедикт XVI потвердив і поблагословив вибір  Преосвященного Владики Святослава згідно з рішенням Виборчого Синоду  Єпископів УГКЦ від 23 березня 2011 року.</p>\n<p>Обраний Глава УГКЦ склав перед Синодом Єпископів визнання віри та обітницю вірно виконувати свій уряд.</p>\n','2011-03-25 20:25:09','http://oranta.org/index.php?option=com_content&view=article&id=696:oficiny-komunikat-vyborchogo-synodu-jepyskopiv-ukrajinskoji-greko-katolyckoji-cerkvy-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Офіційний комунікат Виборчого Синоду Єпископів Української Греко-Католицької Церкви ','uk',2362,3845,0,NULL),
(1,1,'<div class=\"news-single-img\"><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/4600ab1bf3.jpg\" height=\"165\" width=\"115\" /></div>\n<p>Глава Української Греко-Католицької Церкви Блаженніший  Святослав (Шевчук) в інтерв\'ю журналу «Кореспондент» заявив, що вірить у  можливість створення єдиної помісної Української Церкви.</p>\n<p>Видання пише, що Блаженніший Любомир говорив про можливість  сопричастя УГКЦ з усіма українськими православними. У зв\'язку з цим  журналісти Анна Мороз та Ірина Соломко, які проводили інтерв’ю,  поцікавилися, чи є Блаженніший Святослав прихильником об\'єднання.</p>\n<p>«Він (Блаженніший Любомир, − <em>ред.</em>) не раз висловлювався за  те, що єдина помісна Церква − реальність. Передусім тому, що вона вже  існувала в такому вигляді, але в силу певних причин потім була розділена  на кілька конфесій. Оскільки є спільний фундамент, є і шанси, що ця  єдність відновиться. Але до цього нам потрібно йти», − заявив  Предстоятель УГКЦ.</p>\n','2011-06-04 16:10:37','http://oranta.org/index.php?option=com_content&view=article&id=753:glava-ugkc-zajavyv-lkorrespondentur-shcho-viryt-u-stvorennja-jedynoji-ukrajinskoji-cerkvy-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ заявив «Корреспонденту», що вірить у створення єдиної української Церкви ','uk',2473,3956,0,NULL),
(1,1,'<div class=\"nimages\"><img style=\"margin: 10px; float: left;\" src=\"http://galinfo.com.ua/gallery/intxt/c/d/cd.jpg\" /></div>\n<p>Блаженніший Патріарх Святослав (Шевчук) – один з наймолодших  очільників Української греко-католицької церкви. Із його обранням Главою  УГКЦ, повязують нове дихання Церкви та посилення її впливу на суспільні  процеси. Молодість та енергія Блаженнішого дозволяє активніше  впроваджувати ідеї й принципи християнського буття не лише в Україні,  але й за її межами.</p>\n<p>Обрання Святослава (Шевчука) Патріархом було неочікуваним, однак  прогнозованим. Вірні УГКЦ позитивно та з надією сприйняли молодого Главу  Церкви.</p>\n','2011-10-18 20:44:24','http://oranta.org/index.php?option=com_content&view=article&id=799:ljedyna-pomisna-cerkva-v-ukrajini-mozhe-vidbutysja-skorishe-anizh-my-dumajemor-blazhennishy-svjatoslav&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Єдина Помісна Церква в Україні може відбутися скоріше, аніж ми думаємо», - Блаженніший Святослав','uk',2588,4071,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div class=\"nimages\"><img style=\"margin: 10px; float: right;\" src=\"http://zik.ua/gallery/intxt/n/y/nynciy.png\" /></div>\n<strong>Архієпископ Томас Ґалліксон – новий нунцій Папи Римського, шеф  Ватиканської дипломатичної місії в Україні розповів, чому затягується  надання УГКЦ статусу патріархату і беатифікація Шептицького, чи може  католик слухати Боба Марлі та чи приїде до нас Папа</strong>\n<p><strong>Як сталося, що Ви, мешканець протестантських США, стали католиком?</strong></p>\n<p>–  Заждіть – але ж майже 25% мешканців США є католиками! Католицька Церква  – найбільша конфесія цієї держави, тож називати Сполучені Штати  протестантською країною неправильно. Інколи США справді асоціюють із  протестантизмом, але католицизм там є і завжди був на дуже міцних  позиціях. Прізвище Ґалліксон – норвезького походження. Коли мій батько  зустрів мою матір, яка походить з ірландсько-німецької родини, вона була  католичкою, а він – ні. І прийняти католицизм він вирішив ще до  одруження.</p>\n','2012-03-28 23:50:19','http://oranta.org/index.php?option=com_content&view=article&id=845:nunci--pro-rastamaniv-dyjavola-ta-beatyfikaciju-sheptyckogo&catid=27:gk-v-ukrajini-cat&Itemid=57','P','','Нунцій – про растаманів, диявола та беатифікацію Шептицького','uk',2708,4191,0,NULL),
(1,1,'<div class=\"storycontent\">\n<p>«Відмінне трактування деяких історичних періодів в різних частинах України не є чимось новим. Подібний досвід зокрема переживає Німеччина, Іспанія, Бельгія. Завдання істориків в тому, щоб використовувати різноманітні історичні джерела для кращого розуміння процесів, які відбувалися в різних частинах країни протягом тривалого часу. Про це сказав Марк фон Хаґен, з університету штату Арізона на міжнародній конференції в УКУ науковців з України, Росії, Білорусі, США та Західної Європи.</p>\n','2010-10-10 20:35:14','http://oranta.org/index.php?option=com_content&view=article&id=596:mark-fon-hagen-istorychna-nauka-ne-povynna-zalezhaty-vid-togo-hto-pryshov-do-vlady-v-krajini-&catid=37:spilnota-cat&Itemid=49','P',NULL,'Марк фон Хаґен: Історична наука не повинна залежати від того, хто прийшов до влади в країні ','uk',2169,3652,0,NULL),
(1,1,'<div style=\"text-align: center;\">Христос посеред нас!<br /></div>\n<p><br /> <strong>Вступ</strong><br /> Дорогі у Христі,<br /> слова християнського привіту «Христос посеред нас» дають усім нам зрозуміти, що усі ми є дітьми Того, хто з превеликої своєї любові задля нашого спасення зійшов із небес, жив поміж нас, страждав, помер та воскрес із мертвих. Ці основні істини віри ми сповідуємо щодня у Святій Літургії. Вони також дають нам зрозуміти, що ми є єдині у Христі, що ми є Його Церквою, якою Він набув чесною своєю кров\'ю. У ній усі ми, за словами апостола Павла, є немовби членами одного тіла, у якому кожний має свою єдину і неповторну місію.</p>\n','2009-10-13 23:39:31','http://www.oranta.org/index.php?option=com_content&view=article&id=442:pastyrske-poslannja-do-svjashchenykiv-monahiv-monahyn-ta-usih-virnyh-kyjivskoji-arhyjeparhiji&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Пастирське послання до священиків, монахів, монахинь та усіх вірних Київської архиєпархії','uk',1830,3313,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">«Дорогі діти! У цей особливий спосіб я закликаю Вас усіх молитися за мої  наміри щоби через ваші молитви ви могли зупинити план нечистого для цього світу, котрий із кожною дниною віддаляється від Господа і котрий заміщає собою Господа та руйнує все прекрасне і добре у душах кожного з вас. Для  цього, малі діточки, озброюйте себе молитвою і постом, щоби ви змогли усвідомити як багато вас любить Господь і змогли виконувати Його волю. Дякую вам за те, що відповіли на моє запрошення.»<br /><br />український переклад</span></div>','2008-10-29 15:48:32','http://www.oranta.org/index.php?option=com_content&view=article&id=82:poslannja-presvjatoji-divy-mariji-dane-u-medzhugorije-5-zhovtnja-2008-roku&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Послання Діви Марії, дане у Меджугоріє 5 жовтня 2008 року','uk',579,1559,0,NULL),
(1,1,'<div style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">У неділю, 26 жовтня 2008 р., вранці, в базиліці Святого Петра у Ватикані, Папа Венедикт XVI очолив урочисту Службу Божу з нагоди закриття Синоду Єпископів, що від 5 жовтня тривав у Ватикані. Разом з Вселенським Архиєреєм співслужили понад 300 Синодальних Отців та усі ті, що були задіяні у підготовці та проведенні Синоду.</span></div>\n','2008-10-27 09:53:08','http://www.oranta.org/index.php?option=com_content&view=article&id=81:zavershyvsja-synod-jepyskopiv-katolyckoji-cerkvy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Завершився Синод Єпископів Католицької Церкви','uk',567,1547,0,NULL),
(1,1,'<div style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">„... Тільки для Вас маю жити, - для </span></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Вас цілим серцем і душею працювати,</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">для Вас усе посвятити, - ба, навіть за</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Вас, як цього треба буде, й життя своє віддати\".</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Андрей Шептицький</span></p>\n<p>\n','2008-10-30 21:59:33','http://www.oranta.org/index.php?option=com_content&view=article&id=85:mytropolyt-ukrajinskoji-greko-katolyckoji-cerkvy-andre-sheptycky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Митрополит УГКЦ Андрей Шептицький','uk',588,1568,0,NULL),
(1,1,'<div style=\"text-align: right;\">\n<pre><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3535,200,10.JPG\" /><span style=\"font-size: 12pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">«Витай між нами, Христе, витай!</span></span></pre>\n<pre><span style=\"font-size: 12pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щастя єдине в цей час гостини</span></span></pre>\n<pre><span style=\"font-size: 12pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В невиннім серці, серці дитини:<br />Ти наше сонце, життя і рай,<br />Витай між нами, Христе, витай!»</span></span></pre>\n</div>\n<pre><br /></pre>\n','2010-05-11 20:58:26','http://oranta.org/index.php?option=com_content&view=article&id=526:lnakrashcha-nauka-ce-dobry-prykladr-ruslana-dmytrovska&catid=25:parafija-cat&Itemid=27','P','','«Найкраща наука – це добрий приклад» - Руслана Дмитровська','uk',2062,3545,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div><img style=\"margin: 10px; float: right;\" src=\"http://3.bp.blogspot.com/-Ug9mMRjzkHc/UcCJ6cebMWI/AAAAAAAAENg/DT2L1YlgJzQ/s400/3039.jpg\" height=\"127\" width=\"191\" /></div>\n<p><strong>18 червня 2013 року </strong>в  актовій залі Центрального будинку художника відбулася презентація  виставки-конкурсу образотворчого мистецтва в\'язнів «Переображення  Господньою Любов\'ю». В рамках заходу було представлено <a href=\"http://prison-pastoral.blogspot.com/2013/06/blog-post_18.html\" target=\"_blank\">етапи підготовки конкурсу</a> та соціальні аспекти цієї мистецької події.&nbsp;Захід відбувся за сприяння  Національної спілки художників України для широкого інформування про  майбутню виставку-конкурс та про важливість процесу реінтеграції наших  співгромадян, що опинилися у місцях позбавлення волі. Виставка-конкурс  відкриється у Києві у Музеї Івана Гончара у серпні 2013 року. \n','2013-06-19 11:45:53','http://oranta.org/index.php?option=com_content&view=article&id=1266:2013-06-19-08-55-58&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'У Києві пройшла презентація Всеукраїнської виставки-конкурсу образотворчого мистецтва в\'язнів «Переображення Господньою Любов’ю».','uk',3355,4838,0,NULL),
(1,1,'<div>\n<div>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5499,200,index.jpg\" />Учасник Ініціативної групи “Першого грудня” Мирослав Попович — про владу і революцію, пошуки істини, правди і свободи.</p>\n</div>\n</div>\n<p>Ми зустрілися з Мирославом Володимировичем у його кабінеті в  Інституті філософії, який розташований між Михайлівською площею та  захопленим маніфестантами Українським домом. Безперечно, місце вплинуло  на нашу розмову.</p>\n','2014-02-07 21:11:17','http://oranta.org/index.php?option=com_content&view=article&id=1832:2014-02-07-19-18-16&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Не можна допустити, щоб наші міста й села стали полем бою»','uk',4017,5500,0,NULL),
(1,1,'<div>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ukrainabezsyrit.org/_service/14859/display/img_version/505075/img_name/2551_14859_4ede6daeec.jpg\" height=\"138\" width=\"207\" /></p>\n</div>\n<p>Цього сесійного тижня депутати будуть увосьме вирішувати,  чи потрібно приєднуватись до&nbsp;Конвенції про захист дітей та  співробітництво з питань міждержавного усиновлення, яка спрямована на  впорядкування механізму цього процесу. Саме міждержавне усиновлення, на  жаль, досить часто залишається останньою надією отримати сім’ю для старших  та хворих дітей. Представникам прес-служби Альянсу «Україна без сиріт»  вдалося поспілкуватись із Блаженнішим Любомиром Гузаром та почути його  думку щодо сирітства, інтернатів, національного та міждержавного  усиновлення.</p>\n','2013-09-18 01:14:10','http://oranta.org/index.php?option=com_content&view=article&id=1476:usi-dity-nashi-dity&catid=37:spilnota-cat&Itemid=49','P',NULL,'Усі діти - наші діти','uk',3602,5085,0,NULL),
(1,1,'<div>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,65323693_1287131830_41.jpg\" />Один учений мав у своїй робітні великий стінний дзиґар. Дзиґар вибивав години врочисто, повільно і дуже голосно.</p>\n</div>\n<p>- Це вам не заважає? – спитав студент.</p>\n<p>- Ні, бо я весь час ставлю собі питання, що зробив за годину, яка оце минула, - відповів учений.</p>\n','2013-01-22 02:36:42','http://oranta.org/index.php?option=com_content&view=article&id=954:dzygar-z-majatnykom-22-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дзигар з маятником (22 січня)','uk',2918,4401,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<div>\n<p><strong>Сандармох – 75 років розстрілу</strong></p>\n</div>\n<p>В рамках спеціального проекту, приуроченого 75-м роковинам розстрілу  «соловецького етапу» в урочищі Сандармох у Карелії, Радіо Свобода  публікує <a href=\"http://www.radiosvoboda.org/content/article/24477308.html\" target=\"_blank\">списки українських жертв «Сандармоху»</a> і просить всіх небайдужих долучитися до&nbsp;<a href=\"http://www.radiosvoboda.org/sandarmoh.html\" target=\"_blank\">цього проекту</a>.&nbsp;Ідея позначити 2012 рік як рік пам’яті жертв «Сандармоху» належить Оксані Забужко. Вона висловила цю думку&nbsp;<a href=\"http://www.radiosvoboda.org/content/article/24441132.html\" target=\"_blank\">в ефірі «Вашої свободи»</a></p>\n','2012-05-10 15:23:56','http://oranta.org/index.php?option=com_content&view=article&id=863:spysok-rozstriljanyh-u-sandarmosi-ukrajinciv-i-vyhidciv-z-ukrajiny&catid=37:spilnota-cat&Itemid=49','P',NULL,'Список розстріляних у Сандармосі українців і вихідців з України','uk',2739,4222,0,NULL),
(1,1,'<em>\n<p><em><em>Можу уявити навіть комуніста помаранчевого, тобто людину, яка служить своєму народу... Мені дуже не подобається Секретаріат Президента... Криза мине, а ось мистецтво дзвенить віками... </em></em></p>\n<p><em><em>Голова Української греко-католицької церкви Блаженніший Владика Любомир Гузар відповів на запитання УНІАН.</em></em></p>\n<p><em><strong>(Наше інтерв\'ю з Блаженнішим відбувалося в ті дні, коли Партія регіонів вивела людей на Майдан. Владика, як завжди, у доброму гуморі, сам почав мову про мітинги, каже що мітинги - це завжди позитивно.)</strong></em></p>\n<p><em><strong>То Ви не маєте нічого проти мітингів? Навіть якщо мітингує Партія регіонів?</strong></em></p>\n<p><em><img style=\"margin: 10px; float: right;\" src=\"http://images.unian.net/photos/2008_06/1214295731.jpg\" />Мітинги - це нормальна річ у демократичній державі.</em></p>\n<p><em>Одначе виглядає неприродно й смішно, коли, як свідчать мас-медії, на мітинги люди йдуть за гроші. Знаєте, воно якось несмачно звучить. Бо ті люди не йдуть з переконання, це не є справжній політичний мітинг. Це все одно, коли артисти платять комусь, аби вони дуже голосно їх оплескували. Це - то саме. То нещире й неправдиве. Як на мене, воно радше компрометує, ніж переконує.</em></p>\n<p><em>Якби справді вийшло п\'ятнадцять тисяч з переконання - це щось би значило. А як там вийшла групка людей, і то оплачені, це не робить жодного вражіння, треба бути хіба сліпим і немудрим, аби то серйозно трактувати.</em></p>\n<p><em><em></em></em></p>\n','2009-04-12 09:18:36','http://www.oranta.org/index.php?option=com_content&view=article&id=278:lguzar-nazyvaty-polityka-pomaranchevym--ce-niby-prypysuvaty-omu-velyki-chesnoty&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Л.Гузар: Називати політика помаранчевим – це ніби приписувати йому великі чесноти','uk',1347,2321,0,NULL),
(1,1,'<h2 id=\"newsAnnotation\"><img style=\"margin: 10px; float: left;\" alt=\"383898085\" src=\"http://www.rekoldim.com/wp-content/uploads/2013/11/383898085-285x185.jpg\" height=\"172\" width=\"266\" /></h2>\n<!--[if gte mso 9]><xml> <o:OfficeDocumentSettings> <o:AllowPNG /> </o:OfficeDocumentSettings> </xml><![endif]-->\n<p>Сталося… Київський Євромайдан упав. Україну знову відкинуто з її природнього еволюційного шляху. Вона знову входить у період політичної турбулентності, під час якого ціна мудрого рішення – як і ціна помилки – різко зростає.</p>\n<p>Воістину, пророчі слова сказав Тарас Возняк: «Розвиднілося: попереду морок».</p>\n','2013-11-30 22:51:54','http://oranta.org/index.php?option=com_content&view=article&id=1661:potribno-vidkynuty-dribnychkovi-konflikty-ambiciji-myroslav-marynovych&catid=37:spilnota-cat&Itemid=49','P',NULL,'Потрібно відкинути дрібничкові конфлікти й амбіції - Мирослав Маринович','uk',3824,5307,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<h3>14 січня 2009 року <br /><br /><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_vasylij_velykyj.jpg.jpg\" width=\"88\" height=\"181\" />Обрізання Господа Бога і Спаса нашого Ісуса Христа.<br /> Св. Василія Великого, архієп. Кесарії Кападокійської.</h3>\n<p><strong>Коротка історія празнику Обрізання ГНІХ</strong></p>\n<p align=\"justify\">Найближчою важливою подією із життя Ісуса Христа, що наступає після празника Христового Різдва, є Господнє Обрізання і надання імени. Святе Євангеліє про це так говорить: \"Як сповнились вісім днів, коли мали обрізати хлоп\'ятко, назвали Його Ісус - ім\'я, що надав був ангел, перше, ніж Він почався у лоні\" (Лк. 2, 21). Ісус Христос, як Бог і Законодавець, не був зобов\'язаний виконувати релігійні приписи ізраїльського народу, та все таки Він їм добровільно підкорився і їх зберігав. З тієї причини Він восьмого дня після народження піддався обряду обрізання. Цю подію святкуємо першого січня.</p>\n','2010-01-13 09:21:51','http://www.oranta.org/index.php?option=com_content&view=article&id=179:-sv-vasylija-velykogo-arhijep-kesariji-kapadokiskoji&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Св. Василія Великого, архієп. Кесарії Кападокійської','uk',950,1924,0,NULL),
(1,1,'<h3></h3>\n<h3><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_the_crucifiction.jpg.jpg\" height=\"137\" width=\"99\" /></h3>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Використання святої плащаниці в богослужбах Великої п\'ят­ниці й суботи так як ми її тепер почитаємо, відносно недавнє, бо має за собою заледве кількасот літ, але своїм походженням вона сягає самої смерті Ісуса Христа. Плащаниця це ніщо інше, як те простирало чи саван, у якому було завинуте мертве Тіло Христа Господа, коли Його покладено до гробу. Східна Церква у відправах Великої п\'ятниці й суботи не знала плащаниці майже півтори тисячі літ!</p>\n','2011-04-22 09:50:29','http://oranta.org/index.php?option=com_content&view=article&id=279:velyka-pjatnycja-svjata-plashchanycja&catid=28:tema-nomera-cat&Itemid=2','N','','Велика П\'ятниця (Свята Плащаниця)','uk',2027,3510,0,NULL),
(1,1,'<h3></h3>\n<h3><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_the_crucifiction.jpg.jpg\" width=\"99\" height=\"137\" /></h3>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Використання святої плащаниці в богослужбах Великої п\'ят­ниці й суботи так як ми її тепер почитаємо, відносно недавнє, бо має за собою заледве кількасот літ, але своїм походженням вона сягає самої смерті Ісуса Христа. Плащаниця це ніщо інше, як те простирало чи саван, у якому було завинуте мертве Тіло Христа Господа, коли Його покладено до гробу. Східна Церква у відправах Великої п\'ятниці й суботи не знала плащаниці майже півтори тисячі літ!</p>\n','2010-04-01 09:50:29','http://www.oranta.org/index.php?option=com_content&view=article&id=279:velyka-pjatnycja-svjata-plashchanycja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Велика П\'ятниця (Свята Плащаниця)','uk',1362,2336,0,NULL),
(1,1,'<h3></h3>\n<p align=\"right\"><em>Хто переможний – він одягнеться в одежі білі, </em><em><br /><em>і вже не викреслю його з книги життя, </em><br /><em>і визнаю ім’я його перед Отцем моїм.</em><br /></em>Одкр. 3, 5</p>\n<p align=\"center\"><em>Улюблена в Христі українська молоде! </em></p>\n<p>Переживаючи Квітну неділю, яка безпосередньо підводить нас до  таїнства Пасхи Господньої, знову звертаюся до вас зі своїм словом.  Найперше хочу подякувати вам за всі можливості зустрічей та спілкування з  вами впродовж останнього року. Здійснюючи візити до наших громад у  різних країнах світу, я шукав цих зустрічей. Свою радість з приводу  пережитого хочу висловити словами блаженного Папи Івана Павла ІІ: «Я  чекав на вас і ви прийшли, і я вдячний вам за це!»</p>\n','2012-04-03 16:14:26','http://oranta.org/index.php?option=com_content&view=article&id=848:poslannja-blazhennishogo-svjatoslava-verhovnogo-arhyjepyskopa-kyjevo-galyckogo-do-molodi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Послання Блаженнішого Святослава, Верховного Архиєпископа Києво-Галицького, до молоді','uk',2713,4196,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<h3><br /></h3>\n<h3><br /></h3>\n<h3><img src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_blahovischennya.jpg.jpg\" style=\"float: left;\" /></h3>\nКоротка історія \n<ul>\n</ul>\n<p>Святкування Благовіщення почалося у Східній Церкві в кінці IV або на початку V ст. Цісар Маврикій (582-602) робить цей празник обов\'язковим у цілій державі. У перші століття цей празник уважався Господським, як на Сході, так і на Заході. На це вказують його первісні назви: Христове Зачаття, Благовіщення про Христа, Початок відкуплення, Благовіщення, Благовіщення ангела Марії, День Поздоровлення, День або празник Благовіщення. Щойно в VII віці усталюється сьогоднішня назва для цілої Східної Церкви: Благовіщення Пресвятої Богородиці, а сам празник належить до Богородичних.</p>\n<p>На святкування празника 25 березня вплинуло Христове Різдво, що настає через дев\'ять місяців після Благовіщення. При тому існувало старовинне побожне передання, що 25 березня відбулося створення світу, воплочення Божого Сина і Його смерть на хресті. Олександрійська Пасхальна хроніка з 624 року і Царгородська Пасхальна хроніка з початку VII ст. визначають дату празника Благовіщення - 25 березня.</p>\n<p>Службу на цей празник уклали святий Йоан Дамаскин, святий Косма Маюмський, Теофан, єпископ Нікейський і монах Йоан. Наступного дня після празника Східна Церква святкує Собор святого архангела Гавриїла. Це давній звичай Східної Церкви, щоб наступного дня після великого празника віддавати шану тим особам, які брали визначну участь в події празника.</p>\n<p> </p>\n','2009-04-06 21:06:30','http://www.oranta.org/index.php?option=com_content&view=article&id=233:7-kvitnja-2009-roku-bozhogo-blagovishchennja-presvjatoji-vladychyci-nashoji-bogorodyci-i-prysnodivy-mariji&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'7 квітня 2009 року Божого Благовіщення Пресвятої Владичиці нашої Богородиці і Приснодіви Марії','uk',1203,2177,0,NULL),
(1,1,'<h3><br /></h3>\n<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_Pratulynski_muchenyky.jpg.jpg\" /> Синод єпископів УГКЦ постановив доповнити церковний календар нашої Церкви \"святом в честь пам\'яти християнського подвигу Пратулинських мучеників\". Поминати Пратулинських мучеників вирішено 10 січня (за григоріянським стилем - 23 січня).</p>\n<p align=\"justify\">Нашу Українську Греко-Католицьку Церкву часто називають \"Церквою мучеників\". Разом з тим, якщо поглянути на наш літургійний календар, то серед канонізованих Церквою синів і дочок побачимо в ньому лише кілька постатей - насамперед святого священномученика Йосафата, мученика за справу святої Єдности, потім цілу когорту блаженних мучеників і преподобних, які останнім часом випромінюють світло своєї святости на наші землі. Але навіть коли б для кожного з них призначити окреме свято в літургійному році, то кількість їх буде все ж таки порівняно незначною. Очевидно, справа лише в часі, в якому ми, як люди, живемо і рухаємося, і залізні лещата якого постійно відчуваємо як одне з найбільших обмежень, але якого потребуємо і на те, щоби осмислити і дослідити нашу минувшину. Там віднайдемо ще десятки і сотні нових світлих особистостей, які посвідчили Христа аж до мученицької смерти. Ми вражені тим, що більшість прославлених на престолах Вселенської Церкви українців греко-католиків підтвердила свою вірність Христові саме мученицькою смертю, найвищим свідченням любови до Бога, найвозвишенішим з Ним єднанням через Його хресне страждання в надії славного Воскресення. Без сумніву, не бракує в нас преподобних та ісповідників (бо і їх свідчення є безпосередньо пов\'язане з переслідуванням віри), і праведників, але поки що в цій царині нашу \"молоду\" Церкву, яка тільки постала після гонінь останніх часів, перемагає все той же невблаганний час (адже проведення беатифікаційного чи канонізаційного процесу є справою нелегкою і часто тривалою). Іншою \"особливістю\" святих чи блаженних нашої Церкви є те, що більшістю її свідків-мучеників є єпископи, священики або монахи і монахині.</p>\n<p align=\"justify\">\n','2009-01-20 11:23:41','http://www.oranta.org/index.php?option=com_content&view=article&id=190:23-sichnja-2009-roku-pratulynskyh-muchenykiv&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'23 січня 2009 року Пратулинських мучеників','uk',988,1962,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<h3><img src=\"http://www.oranta.org/images/stories/2008/11/_picture_file_path_31634.jpg\" style=\"float: left;\" /> Коротка історія</h3>\n<p align=\"justify\">Празник Стрітення засновується на події з життя нашого Спасителя, яку записав святий євангелист Лука <em>(2, 22-40). </em> Закон Мойсея приписував, щоб кожна жінка після народження дитини 40 днів не сміла входити до храму, бо в той час вона вважалася нечистою. Ці 40 днів називалися днями очищення. Після закін чення цих днів мати новонародженої дитини приходила до храму і приносила жертву очищення. Багатша жінка жертвувала однолітнє ягня на всепалення та молодого голуба або горлицю, а вбога повинна була принести пару голубів або пару горлиць. Пречиста Діва Марія, як Божа Мати, не була зобов\'язана до обряду очищення, бо освятилася Христовим Різдвом, як каже кондак празника: &quot;Утробу Дівичу освятив Ти різдвом Твоїм&quot;. Та все-таки вона в покорі виконує припис закону і приносить у жертву дві горлиці.</p>\n<p align=\"justify\">\n','2009-02-13 10:42:37','http://www.oranta.org/index.php?option=com_content&view=article&id=211:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Стрітення Господа Бога і Спаса нашого Ісуса Христа','uk',1145,2119,0,NULL),
(1,1,'<h3><img src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_blahovischennya.jpg.jpg\" style=\"margin: 10px; float: left;\" height=\"157\" width=\"113\" /></h3>\n<p>Коротка історія</p>\n<ul>\n</ul>\n<p>Святкування Благовіщення почалося у Східній Церкві в кінці IV або на початку V ст. Цісар Маврикій (582-602) робить цей празник обов\'язковим у цілій державі. У перші століття цей празник уважався Господським, як на Сході, так і на Заході. На це вказують його первісні назви: Христове Зачаття, Благовіщення про Христа, Початок відкуплення, Благовіщення, Благовіщення ангела Марії, День Поздоровлення, День або празник Благовіщення. Щойно в VII віці усталюється сьогоднішня назва для цілої Східної Церкви: Благовіщення Пресвятої Богородиці, а сам празник належить до Богородичних.</p>\n','2012-03-29 21:06:30','http://oranta.org/index.php?option=com_content&view=article&id=233:7-kvitnja-2009-roku-bozhogo-blagovishchennja-presvjatoji-vladychyci-nashoji-bogorodyci-i-prysnodivy-mariji&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Благовіщення Пресвятої Владичиці нашої Богородиці і Приснодіви Марії','uk',2036,3519,0,NULL),
(1,1,'<h3><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_molinnya_pro_chashu.jpg.jpg\" /></h3>\nКоротка історія\n<p>Уже апостоли й перша християнська громада почали заміняти день жидівської Пасхи днем поминання Христових мук і смерті. Роковини Христової смерті були для них дуже сумним днем. Тож, щоб його належно відсвяткувати, вони зберігали піст у цей день. І так первісна християнська Пасха почала існувати у вигляді посту. Це була хресна Пасха. За свідченням святого Іринея (к. 125 - к. 203), про яке говорить історик Церкви Євсевій (к. 260 - к. 340), перший зародок нинішнього 40-денного посту в той час обмежу­вався одним-двома днями посту, який не вважався передпасхальним, а таки самою Пасхою.</p>\n<p>У ІІІ столітті передпасхальний піст у деяких Церквах триває цілий тиждень. Це той час, який сьогодні звемо Страсним тижнем. При кінці ІІІ століття Великий піст уже сягає 40 днів. Від IV віку маємо перші свідчення про 40-денний передпасхальний піст. Перше свідчення дає нам Нікейський Собор, котрий каже, щоб помісні собори відбувалися \"один перед Чотиридесятницею , щоб після усунення всякого неладу чистий дар приносився Богові, а другий під осінній час\" (Правило 5).</p>\n<p>\n','2009-02-17 18:33:34','http://www.oranta.org/index.php?option=com_content&view=article&id=216:2-bereznja-2009-roku-pochatok-velykogo-postu&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'2 березня 2009 року  - початок Великого посту','uk',1155,2129,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img src=\"http://charnetskyj.org.ua/img/katriy/051.jpg\" align=\"left\" border=\"0\" />\n<p>Наступна неділя після Христового Воскресення має різні назви.</p>\n<p>Неділя Томина - від події у святому Євангелії, де говориться про зустріч апостола Томи з воскреслим Христом.</p>\n<p>Неділя Антипасхи, що з грецької означає \"замість Пасхи\", бо для перших християн вона була наче повторенням празника Пасхи. У Щоденнику паломництва Сильвії Аквітанської (IV ст. ) читаємо, що восьмого дня після Христового Воскресення в Єрусалимі були окремі торжества та, що того дня читали святе Євангеліє про навернення апостола Томи.</p>\n','2009-04-26 19:06:53','http://www.oranta.org/index.php?option=com_content&view=article&id=300:tomyna-nedilja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Томина неділя','uk',1409,2465,0,NULL),
(1,1,'<img style=\"float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/935f82b186.jpg\" height=\"408\" width=\"300\" />\n<h3>4 грудня 2008 року <br /><br />Введення в храм Пресвятої Богородиці</h3>\n<p> </p>\n<ul>\n<li>Коротка історія свята</li>\n<li>Про ікону празника</li>\n<li>Тропар та кондак</li>\n<li>Празничний Апостол</li>\n<li>Євангеліє</li>\n<li>Проповідь</li>\n</ul>\n<h3>Коротка історія</h3>\n<p align=\"justify\">Святе Євангеліє нічого не говорить нам про подію уведення в храм. Основою цього празника, як і празника Різдва й Успення Божої Матері, є традиція Церкви й апокрифічні книги, передусім Протоєвангеліє Якова і Псевдоєвангеліє Матея \"Про Різдво Пречистої Діви Марії\". Звідси довідуємося, що батьки Пречистої Діви Марії святі Йоаким і Анна, будучи бездітними, дали обіцянку, що, як у них з\'явиться дитина, то віддадуть її на службу Богові у храмі Єрусалима. Господь Бог вислухав їхні молитви і дав їм донечку. І коли їй було три роки, то батьки привели її до храму і віддали в руки первосвященика Захарії, батька святого Йоана Предтечі. Тут Пресвята Богородиця перебувала багато років доти, доки, як доросла дівиця, не була заручена зі святим Йосифом.</p>\n<p align=\"justify\">\n','2008-12-04 19:20:36','http://www.oranta.org/index.php?option=com_content&view=article&id=125:vvedennja-v-hram-presvjatoji-bogorodyci&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Введення в храм Пресвятої Богородиці','uk',750,1730,0,NULL),
(1,1,'<img style=\"float: left;\" src=\"http://www.umoloda.kiev.ua/img/content/i15/15901.gif\" />\n<p><strong> Якось Ромек, як інколи кликали його друзі, грав на фортепіано на вечірці в карпатському містечку Дора. Інструмент привезли на возі від однієї свідомої родини до будинку «Просвіти». Коли ж фортепіано треба було повертати господарям, «Турам» стало шкода перевозити його на возі, тому хлопці підняли інструмент разом з кріслом, у якому сидів Шухевич, та й понесли їх через усе містечко. Роман увесь час грав, і цю історію довго переповідали місцеві жителі.</strong></p>\n<h4 align=\"center\">«Тишковецькі Шухевичі»</h4>\n<p>Великий рід Шухевичів упродовж кількох поколінь збагачував Україну священиками, науковцями, митцями, юристами та військовими. Найдавніші згадки про рід сягають XVIII-XIX століть. Зокрема, розповідається про отця Миколу Шухевича, який був священиком у селі Раківці над Дністром. Миколин син Йосип, прадід Романа Шухевича, був парохом у селі Тишківці Городенківського повіту. Від нього починається «тишковецька лінія» родини Шухевичів, бо є ще й «олієвська лінія» (від села Олієв, де священиком був брат Йосипа Шухевича).</p>\n<p>\n','2009-02-17 18:27:29','http://www.oranta.org/index.php?option=com_content&view=article&id=215:pro-rid-i-dytynstvo-golovnokomanduvacha-upa-romana-shuhevycha&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Про рід і дитинство головнокомандувача УПА Романа Шухевича','uk',1154,2128,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"float: left;\" src=\"http://www.wz.lviv.ua/image/3911/articles/71333-178x122.jpeg\" />\n<p><strong>У Біблії йдеться про те, що Бог всюдисущий, тому спілкуватися з ним, тобто молитися, можна будь-де.</strong> <strong></strong></p>\n<p>Але недаремно люди створили храми - душа потребує окремого місця, де все нагадує про присутність Всевишнього та спонукає до благочестивих роздумів. Особливо це потрібно тим, кого спіткала важка хвороба, - у ліжку доводиться проводити місяці, а то й роки. Саме тому у Львівській обласній дитячій спеціалізованій клінічній лікарні діє капличка, де проводять літургії, молебні та духовні бесіди.<br /> <br /> У лікарняній каплиці - затишно. Око милують дивовижні образи, які власноруч написав меценат лікарні - Піт Спайкер. Хоч по понеділках цей міні-заклад не працює, отець Михайло Шкрібинець - капелан Львівської архієпархії УГКЦ у каплиці Львівської обласної дитячої спеціалізованої клінічної лікарні, прийшов спеціально на зустріч із кореспондентом «ВЗ». Побачивши, що капличка відчинена, сюди відразу потягнулися люди - хтось замовити молебень за здоров\'я, інші - преклонити коліна перед іконами або попрохати отця благословення.</p>\n<p>\n','2009-02-08 21:54:11','http://www.oranta.org/index.php?option=com_content&view=article&id=205:tut-slovo-lkajusr-vymovljajut-vid-dushi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Тут слово «каюсь» вимовляють від душі…','uk',1115,2089,0,NULL),
(1,1,'<img style=\"float: right;\" src=\"http://www.saintjosaphat.org.ua/typo3temp/pics/b6a2040873.jpg\" height=\"180\" width=\"90\" />\n<p><strong>У третій заповіді Церква вчить нас, як ми повинні дотримуватися постів.</strong></p>\n<p>Встановлюючи цю заповідь, Церква прагнула навчити вірних володіти собою, спонукати їх до виховання в собі сильних рис характеру (сильної волі), а також таким чином вона дає вірним нагоду виявити послух щодо неї. Піст існує стільки, скільки існує людство. Ще в раю Господь Бог заборонив людям споживати плоди дерева знання. Євреям Бог заборонив споживати свинину (Левіт 11, 2). Постив Мойсей, постив Ілля, постив св. Йоан Хрести­тель, протягом 40 днів і ночей постив сам Ісус Христос.</p>\n<p>Піст у давніх християн був дуже строгий - вони їли лише раз на день (увечері), стримувалися від вживання вина і страв, споживання яких прино­сило насолоду. Під час такого посту вони усамітнювались і постійно молились. Не дозволялось навіть напитися води.</p>\n<p>\n','2009-02-27 14:16:11','http://www.oranta.org/index.php?option=com_content&view=article&id=225:jak-povynen-postyty-hrystyjanyn-katolyk&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як повинен постити християнин-католик?','uk',1189,2163,0,NULL),
(1,1,'<img style=\"float: right;\" src=\"http://zik.com.ua/gallery/intxt/cerkava.jpg\" /> Про проблеми збереження пам\'яток народного зодчества говорили нині банкіри, депутати, меценати, мистецтвознавці та представники церкви на круглому столі «Дерев\'яні церкви України під загрозою зникнення. Як врятувати унікальну спадщину», який відбувся у Львівському інституті банківської справи. - про це сьогодні, 5 грудня, повідомив кореспондент ЗІКу.<strong> </strong>\n<p>Круглий стіл відбувся за участі ректора Університету банківської справи НБУ Тамари Смовженко, а проректора з наукової роботи цього навчального закладу Анжели Кузнєцової, директора Львівської галереї мистецтв Героя України Бориса Возницького, народного депутата Петра Писарчука, керуючого Львівською філією «Укрсоцбанку» Олега Сергеєва, начальника управління НБУ у Львівській області Марії Могильницької та інших. Оголошений у програмі виступ на тему «Проблематика збереження дерев\'яних церков в Україні» начальниці управління культури і туризму ЛОДА Галини Дорощук не прозвучав.</p>\n<p>\n','2008-12-06 15:54:53','http://www.oranta.org/index.php?option=com_content&view=article&id=128:borys-voznycky-peredast-ukrajinski-derevjani-cerkvy-do-prezydentskogo-lmysteckogo-arsenalur&catid=37:spilnota-cat&Itemid=49','P',NULL,'Борис Возницький передасть українські дерев’яні церкви до президентського «Мистецького арсеналу»','uk',763,1743,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 10px; float: left;\" alt=\"Томина неділя\" src=\"http://ecumenicalcalendar.org.ua/system/avatars/72/thumb/_.jpg?1267985590\" height=\"134\" width=\"220\" />\n<p>Наступна неділя після Христового Воскресення має різні назви.  Неділя  Томина — від події у святому Євангелії, де говориться про зустріч  апостола Томи з воскреслим Христом.</p>\n<p>Неділя Антипасхи, що з грецької означає \"замість Пасхи\", бо для  перших християн вона була наче повторенням празника Пасхи. У Щоденнику  паломництва Сильвії Аквітанської (IV ст. ) читаємо, що восьмого дня  після Христового Воскресення в Єрусалимі були окремі торжества та, що  того дня читали святе Євангеліє про навернення апостола Томи.</p>\n','2013-05-11 13:34:02','http://oranta.org/index.php?option=com_content&view=article&id=1199:nedilja-bila&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Неділя Біла','uk',3232,4715,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://blyzhchedoboga.com.ua/wp-content/uploads/2011/09/Ioan-Predtecha-205x291.jpg\" alt=\"Іван Хреститель\" title=\"Усікновення глави Іоана Хрестителя\" height=\"198\" width=\"139\" />\n<p>У плині повсякденного життя радість і скорбота йдуть поруч.  Бачимо початок нового життя і його згасання, надбання і втрати, зустрічі  й прощання, моменти відступництва й світлі миті навернення до Бога. І  серед всього цього розмаїття доль, вчинків, помислів й думок до глибини  душі проймають приклади самовідданого служіння Істині, непохитність  переконань, коли мова йде про вірність Богу, вірність Божественному  покликанню.</p>\n<p>Прикладом саме такого відданого і незрадливого служіння є життя пророка і Предтечі Господнього <a href=\"http://blyzhchedoboga.com.ua/ioan-predtecha/\">Івана Хрестителя</a>.  Сам Господь сказав про нього у Євангелії, що він — друг Жениха. Друг,  котрий настільки любить жениха й наречену, що здатен, забувши себе,  служити їх любові, ніколи не стане на їм заваді. За словами митрополита  Антонія Сурозького: «Він — друг, котрий здатен захистити любов жениха й  нареченої, залишившись зовні охоронцем таїни цієї любові.»</p>\n','2013-09-08 23:25:12','http://oranta.org/index.php?option=com_content&view=article&id=1463:usiknovennja-glavy-chesnogo-proroka-predtechi--hrestytelja-gospodnogo-ioana&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Усікновення глави чесного пророка, Предтечі й Хрестителя Господнього Іоана','uk',3577,5060,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://charnetskyj.org.ua/_si/0/s53033.jpg\" /> <img style=\"margin: 10px; float: right;\" src=\"http://charnetskyj.org.ua/_si/0/42941.jpg\" /> <strong>1884-1959</strong>\n<p align=\"justify\">ХХ століття - час народження Східної вітки Чину Найсвятішого Ізбавителя. Прийшовши на українські землі, бельгійські редемптористи спромоглися на найбільше - вони змогли стати частинкою народу, змогли влитися в його культуру, ментальність, та, що найважливіше, у його східну духовність. Палаючи духом за Господа сил, вони вели за собою тих, котрі чули у своєму серці Христовий голос: \"Ідіть і зробіть учнями всі народи.\" (Мт.28,19). Серед цих перших синів української землі, що вирішили йти і виконувати волю свого Спасителя, був великий муж святого життя - блаженний єпископ Миколай Чарнецький.</p>\n','2009-04-16 14:56:00','http://www.oranta.org/index.php?option=com_content&view=article&id=283:blazhenny-svjashchennomuchenyk-mykola-charnecky-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Блаженний Священномученик Миколай Чарнецький ','uk',1361,2335,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://expres.ua/gfx/imagecache/th220/digest/9171c59e8ef474503c74ea9cd42e74bf.0.jpg\" alt=\"Папа Римський визнав український Голодомор одним з найстрашніших геноцидів  \" height=\"166\" width=\"220\" />\n<div class=\"field-items\">\n<div class=\"field-item odd\"></div>\n<p>\"Люди, які зазнали різанини і гоніння – як під час  трьох найбільших геноцидів минулого століття вірмени, євреї та українці  –боролися за свою свободу\".</p>\n</div>\n<p>Великі держави «вмили руки» під час Геноциду вірмен, бо знали  набагато  більше, ніж говорили: адже вони не говорили про те, що під час  геноциду  спокійно вмили руки.</p>\n','2013-08-10 22:27:11','http://oranta.org/index.php?option=com_content&view=article&id=1402:papa-rymsky-vyznav-ukrajinsky-golodomor-odnym-z-nastrashnishyh-genocydiv-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа Римський визнав український Голодомор одним з найстрашніших геноцидів ','uk',3499,4982,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://olha-church.org.ua/web/uploads/kalendar_novyy-razmer.jpg\" height=\"146\" width=\"146\" />\n<p>Місяць вересень - не тільки календарний початок осені, нової пори   року. Він має важливе значення для християн східного обряду. Саме 1   вересня (14 за новим стилем, світським календарем, тобто в ці дні)   православна і греко-католицька церква святкує початок, «новоліття», або   «найменування», Нового церковного року, який розпочинає своєрідне коло   літургійних богослужінь.</p>\n<p>Вересневий початок Нового церковного року характерний лише для церков  східної традиції. Церкви західного обряду розпочинають його першою  неділею різдвяного посту (адвенту).</p>\n','2013-09-14 07:56:47','http://oranta.org/index.php?option=com_content&view=article&id=1470:pochatok-novogo-cerkovnogo-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Початок нового церковного року','uk',3592,5075,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53516_%D1%E2%FF%F2%EE%F1%EB%E0%E2_W.jpg\" alt=\"Святослав_W.jpg\" title=\"Святослав_W.jpg\" height=\"116\" width=\"176\" />\n<p>Наступного року в Українській Греко-Католицькій Церкві відзначатимуть  25-річчя виходу Церкви з підпілля. Цю дату в Церкві готуються  відзначити на гідному рівні. Про це в інтерв’ю газеті «Галичина»  повідомив Глава УГКЦ Блаженніший Святослав (Шевчук).</p>\n<p>«Хочемо, аби пам\'ять підпільної Церкви в Україні, як особлива духовна  спадщина, була донесена не лише до широкого загалу нашого суспільства, а  й за кордоном», – сказав Предстоятель.</p>\n','2013-08-30 15:05:23','http://oranta.org/index.php?option=com_content&view=article&id=1431:2014-roku-ugkc-vidznachyt-25-richchja-vyhodu-z-pidpillja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'2014 року УГКЦ відзначить 25-річчя виходу з підпілля','uk',3553,5036,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/256b6e094d.jpg\" width=\"300\" height=\"393\" /><img src=\"http://ugcc.org.ua/clear.gif\" width=\"1\" border=\"0\" height=\"5\" /><br /><strong>Коротка історія</strong>\n<p align=\"justify\">Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n<p align=\"justify\">Первісно празник Преображення святковано в місяці лютому. Одначе тому, що цей радісний празник звичайно випадав у часі Великого Посту, а це було незгідне з духом посту й покути, то його перенесено на день 6 серпня. Чому якраз на цей день? Історик Євсевій і св. Іван Дамаскин є тієї думки, що Господнє Преображення відбулося 40 днів перед Христовою смертю. Тож св. Церква йдучи за цією думкою, перенесла цей празник з місяця лютого на 6 серпня тому, бо 40 днів пізніше, це є 14 вересня, випадає празник Воздвиження Чесного Хреста - пам\'ять Христових мук і смерті.\n','2009-07-16 20:39:30','http://www.oranta.org/index.php?option=com_content&view=article&id=381:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','uk',1684,3167,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/47122728a0.jpg\" height=\"255\" width=\"182\" />&nbsp;\n<p>Коротка історія</p>\n<p>Світлий празник Пасхи завершується не менше світлим і  великим   празником Святої П’ятдесятниці. По Христовому Різдві і  Воскресінні цей   празник належить до найбільших празників нашого  церковного року.   Зіслання Святого Духа це - наче вінець і печать на ділі  спасіння   людського роду, що його доконав Божий Син. В дні св.  П’ятдесятниці сам   Святий Дух помазує апостолів на проповідників  Христового Благовістя. В   дні Зіслання Святого Духа родиться і починає  діяти Христова Церква.   Святий Дух її провадить, просвічує, освячує і  зберігає на дорозі   правди.</p>\n','2012-06-02 22:31:49','http://oranta.org/index.php?option=com_content&view=article&id=866:zislannja-svjatogo-duha-pjatydesjatnycja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Зіслання Святого Духа. П\'ятидесятниця','uk',2752,4235,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://www.ji-magazine.lviv.ua/ilustr/Brodskyj_Namagajtesya_povazhaty_zhyttya/Brodskyj.jpg\" height=\"157\" width=\"166\" />\n<p><em>«Моя таємна доповідь», - іронізував Йосип Бродський щодо своєї  промови, з якою він виступив перед випускниками університету Мічигану  (Анн-Арбор, травень 1988 року). Сім подальших років «Промову на  стадіоні» не оприлюднювали англійською мовою - її визнали «недоречною» і  «політично некоректною», розгледівши в ній «реакційність» і навіть  «расизм».</em></p>\n<p><em>У перекладах спіч публікували не раз, але більшість  публікацій мали блідий вигляд «журнального варіанту», відредагованого та  скороченого.</em><em></em>\n','2013-11-10 00:20:20','http://oranta.org/index.php?option=com_content&view=article&id=1615:namagates-povazhaty-zhyttja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Намагайтесь поважати життя','uk',3765,5248,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://www.religion.in.ua/uploads/posts/2013-07/1374867913_1.jpg\" height=\"153\" width=\"192\" />\n<div>\n<p>Загальноприйнято  вважати, що християнство на Русі веде свій початок від  988&nbsp;року. Проте  тоді християнство було остаточно утверджено на  офіційному рівні як  державну релігію. Саме ж поширення християнства на  Русі, як про це  свідчать вітчизняні та іноземні літописні джерела,  почалося значно  раніше, ще в 860-ті&nbsp;роки і тривало ціле століття.</p>\n</div>\n<p><strong>І. Початок християнізації Русі в ІХ ст. за часів князя Оскольда</strong></p>\n','2013-08-05 20:16:33','http://oranta.org/index.php?option=com_content&view=article&id=1390:protystojannja-jazychnyctva-i-hrystyjanstva-na-rusi-vid-oskolda-do-volodymyra&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Протистояння язичництва і християнства на Русі: від Оскольда до Володимира','uk',3488,4971,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/716d86efb4.jpg\" width=\"116\" height=\"145\" />\n<p>15 лютого 2010 року</p>\n<p>Коротка історія</p>\n<p align=\"justify\">Празник Стрітення засновується на події з життя нашого Спасителя, яку записав святий євангелист Лука (2, 22-40). Закон Мойсея приписував, щоб кожна жінка після народження дитини 40 днів не сміла входити до храму, бо в той час вона вважалася нечистою. Ці 40 днів називалися днями очищення.</p>\n','2010-02-14 01:35:42','http://www.oranta.org/index.php?option=com_content&view=article&id=490:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Стрітення Господа Бога і Спаса нашого Ісуса Христа','uk',1965,3448,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/pics/sunday_of_the_resurrection.jpg\" width=\"203\" height=\"132\" /><img src=\"http://www.ugcc.org.ua/clear.gif\" width=\"1\" border=\"0\" height=\"6\" /><br /> \n<ul>\n</ul>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Слово \"пасха\" походить з єврейського \"песах\", що значить \"перехід\". Тут мова про перехід Господнього ангела, який одної ночі вбив усіх єгипетських первенців, коли фараон не хотів відпус­тити ізраїльський народ, а перейшов повз доми ізраїльтян, одвірки домів яких були помазані кров\'ю одно­річного ягняти. Слово \"пасха\" у жидів означало також ягня, якого заколювали на Пасху, а потім і сам день свята на згадку про визволення з Єгипту дістав назву Пасхи.</p>\n','2010-04-03 00:09:09','http://oranta.org/index.php?option=com_content&view=article&id=287:voskresinnja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2','N','','Воскресіння Господа Бога і Спаса нашого Ісуса Христа','uk',2026,3509,0,NULL),
(1,1,'<img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/pics/sunday_of_the_resurrection.jpg\" width=\"203\" height=\"132\" /><img src=\"http://www.ugcc.org.ua/clear.gif\" width=\"1\" border=\"0\" height=\"6\" /><br /> \n<ul>\n</ul>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Слово \"пасха\" походить з єврейського \"песах\", що значить \"перехід\". Тут мова про перехід Господнього ангела, який одної ночі вбив усіх єгипетських первенців, коли фараон не хотів відпус­тити ізраїльський народ, а перейшов повз доми ізраїльтян, одвірки домів яких були помазані кров\'ю одно­річного ягняти. Слово \"пасха\" у жидів означало також ягня, якого заколювали на Пасху, а потім і сам день свята на згадку про визволення з Єгипту дістав назву Пасхи.</p>\n','2010-04-03 00:09:09','http://www.oranta.org/index.php?option=com_content&view=article&id=287:voskresinnja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Воскресіння Господа Бога і Спаса нашого Ісуса Христа','uk',1370,2344,0,NULL),
(1,1,'<img style=\"margin: 10px; float: right;\" alt=\"Богоматір сухого дерева\" src=\"http://www.credo-ua.org/wp-content/uploads/2014/02/%D0%91%D0%BE%D0%B3%D0%BE%D0%BC%D0%B0%D1%82%D0%B5%D1%80%D1%8C-%D1%81%D1%83%D1%85%D0%BE%D0%B3%D0%BE-%D0%B4%D1%80%D0%B5%D0%B2%D0%B0.jpg\" height=\"125\" width=\"125\" />\n<p><strong>Цей образ був написаний 1642 року. Він представляє дуже  рідкісний сюжет: зображення Діви Марії з Немовлям, оточених гілками  сухого дерева.</strong></p>\n<p>Цей сюжет натхнений словами пророка Єзекиїля: «Всі польові дерева  взнають, що Я&nbsp;— Господь, що Я принижую дерево&nbsp; високе, що Я підвищую  дерево низеньке, що Я висушую дерево зелене, а дерево сухе сподіюю  квітучим» (Єз 17,&nbsp;24).</p>\n','2014-02-10 00:53:58','http://oranta.org/index.php?option=com_content&view=article&id=1838:-bogomatir-suhogo-dereva&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Богоматір Сухого Дерева','uk',4023,5506,0,NULL),
(1,1,'<img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,f_1500496221350408675.jpg\" />\n<p>Якось одна знатна римлянка запитала у равина Йоссі Бен халафта: «А, що Бог робить цілими днями?»</p>\n<p>Добрий равин відповів: «Він парує людей, вирішує, хто з ким має  побратися: цей чоловік з цією жінкою, та жінка з отим чоловіком, і так  далі…»</p>\n<p>«Нічого особливого» - зауважила патриціанка. – «Те саме і я могла б робити. За один день я можу поєднати тисячу пар.»</p>\n','2013-02-15 00:33:48','http://oranta.org/index.php?option=com_content&view=article&id=984:dozvillja-gospoda-boga-15-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дозвілля Господа Бога (15 лютого)','uk',2969,4452,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 10px; float: right;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53748_%D0%F3%F1%F1%EA%E8%E9_%EC%E8%F0.jpg\" alt=\"Русский_мир.jpg\" title=\"Русский_мир.jpg\" height=\"127\" width=\"177\" />\n<p>Сьогодні християни втомилися від церковних поділів. Це відчувається і   серед православних, які не можуть зрозуміти, чому вони поділені на три   Церкви, і серед вірних УГКЦ, котрі дедалі частіше запитують, коли ж  буде  єдина помісна Церква.</p>\n<p>За словами Глави Церкви, екуменізм буде можливий тоді, коли ми не «підлаштовуватимемо Церкву під якісь геополітичні проекти».</p>\n','2013-09-21 14:53:19','http://oranta.org/index.php?option=com_content&view=article&id=1488:lrussky-myrr-ne-maje-nichogo-spilnogo-z-cerkvoju-glava-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Русский мир» не має нічого спільного з Церквою, – Глава УГКЦ','uk',3610,5093,0,NULL),
(1,1,'<img style=\"margin: 10px; float: right;\" src=\"http://texty.org.ua/mod/file/thumbnail.php?file_guid=41230&amp;size=large\" height=\"146\" width=\"239\" />\n<p>Сьогодні виповнилося б 85 років Василеві Макуху – дисидентові, який у   1968 році у центрі Києва вчинив акт самоспалення на знак протесту  проти  колоніального становища України в Радянському Союзі. Він так само   виступив проти агресивного введення військ Варшавського договору у   Чехословаччину. У Радянському Союзі про Макуха багато не говорили: його   вчинок став несподіванкою для КДБ, й у відомстві надалі робили усе   можливе, щоб такі публічні і шокуючі акти висловлювання протесту у   країні не повторилися. Через майже півстоліття після самоспалення у   Василя Макуха немає навіть меморіальної дошки, на якій вшановувався б   його вчинок.</p>\n','2013-11-05 13:42:31','http://oranta.org/index.php?option=com_content&view=article&id=1601:ljudyna-jaka-zgorila-za-ukrajinu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Людина, яка згоріла за Україну','uk',3749,5232,0,NULL),
(1,1,'<img style=\"margin: 10px; float: right;\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/04/54.jpg\" height=\"151\" width=\"298\" />\n<p>&nbsp;<strong>Великий Четвер розпочинає Пасхальне Тридення</strong></p>\n<p>Увечері Великого Четверга уся спільнота Церкви збирається на урочистій  Євхаристії Господньої Вечері. Ця Служба є особливим спомином Тайної  Вечері Ісуса з апостолами, де Господь встановив таїнство Священства та  Євхаристії.</p>\n','2011-04-21 09:47:43','http://oranta.org/index.php?option=com_content&view=article&id=731:velyky-chetver&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Великий Четвер','uk',2419,3902,0,NULL),
(1,1,'<img style=\"margin: 10px; float: right;\" src=\"http://www.golos.com.ua/img/11606.jpg\" height=\"150\" width=\"150\" />\n<p><strong>Микола Гоголь: «Я українець. І мій рід по матері боронив шаблями вітчизну»</strong></p>\n<p><em></em>Людмила КОХАНЕЦЬ</p>\n<p>В університетському видавництві «Пульсари» вийшла книжка Миколи Босака «Остання загадка Гоголя», присвячена 200-річчю від дня народження славетного письменника. Як пояснює автор, «ця книга є до певної міри літературною містифікацією. Бо містичним, загадковим досі залишається й сам Микола Гоголь. Таїною оповиті його з\'ява на світ, причина самотності, дар провидця, непоборне відчуття месіанства, нібито спалення другого тому «Мертвих душ», якщо він був, смерть і зникнення голови з труни...».</p>\n','2009-03-30 20:50:38','http://www.oranta.org/index.php?option=com_content&view=article&id=265:mykola-gogol-v-ukrajinski-literaturi&catid=37:spilnota-cat&Itemid=49','P',NULL,'Микола Гоголь в українській літературі','uk',1306,2280,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<img style=\"margin: 1px 8px; float: left;\" src=\"http://www.kyiv.ugcc.org.ua/img/upload/1%285%29.jpg\" height=\"172\" width=\"200\" /><strong></strong><strong></strong>\n<p><strong> </strong><strong>Благословення і мир у Господі!</strong></p>\n<p><strong> </strong>Дорогі у Христі,</p>\n<p>Собор Київської архиєпархії на тему євангелізації, який відбувся в днях 13-14 лютого 2009 року Божого у Києві, при монастирі св. Василія Великого, пропонує нам заходи, які повинні б допомогти усім нам краще пізнати Христа з священних книг та з Літургії, збудити в нас палке бажання жити згідно з Його божественною наукою та спонукати нас до свідчення Йому, одним словом - наповнити наш ум та серце духовним багатством, яким би ми могли ділитися з нашими ближніми. Ваш отець-душпастир вичислить і пояснить Вам ці заходи, уточнить способи здійснення їх у житті Вашої громади.</p>\n<p>\n','2009-03-02 12:16:23','http://www.oranta.org/index.php?option=com_content&view=article&id=232:virnym-kyjivskoji-arhyjeparhiji-ukrajinskoji-greko-katolyckoji-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Вірним Київської архиєпархії Української Греко-Католицької Церкви','uk',1200,2174,0,NULL),
(1,1,'<img width=\"227\" src=\"http://www.oranta.org/images/stories/rtemagicc_22.10.2008_.jpg.jpg\" height=\"170\" style=\"float: left; border: 1px solid;\" />Незважаючи на осінню прохолоду на вулиці, у залі Будинку художника, що на Львівській площі м. Києва, панувала атмосфера любові і тепла. Саме тут 18 жовтня 2008 року дарували свою любов і пісню переможці IV Всеукраїнського фестивалю-конкурсу сучасної духовної пісні «Пісня серця» та літературного конкурсу «Християнство очима і серцем дітей», що проходив в рамках святкування 1020-ліття Хрещення Русі-України. \n','2008-10-22 22:07:48','http://www.oranta.org/index.php?option=com_content&view=article&id=75:do-boga-lyne-qpisnja-sercjaq-&catid=37:spilnota-cat&Itemid=49','P',NULL,'До Бога лине \"Пісня серця\"  ','uk',548,1528,0,NULL),
(1,1,'<img width=\"98\" src=\"http://www.oranta.org/images/stories/i.jpg\" height=\"97\" style=\"float: left; margin: 5px; border: 1px solid;\" />Українська Американська Координаційна Рада, Міжнародний благодійний фонд «Україна 3000», Меценат Олександр ПРОГНІМАК представляють перший українсько-американський проект 20 жовтня, 19.00 (Український дім) вечір пам\'яті КВІТКИ ЦІСИК (1953-1998) <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> Цей вечір - унікальний прецедент пишання видатними вихідцями з України, що поширюють та відстоюють позитивний імідж України прикладом усього свого героїчного життя і творчості. Водночас це перший україно-американський проект такого високого рівня, присвячений Квітці Цісик в Україні - країні її походження, де вона була лише раз - у 1983 р. і мріяла проїхатися з концертним туром Незалежною Україною.\n','2008-10-17 18:27:01','http://www.oranta.org/index.php?option=com_content&view=article&id=69:vechir-pamjati-kvitky-cisyk&catid=37:spilnota-cat&Itemid=49','P',NULL,'Вечір пам\'яті Квітки Цісик','uk',556,1536,0,NULL),
(1,1,'<p align=\"JUSTIFY\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5158,200,383499854.jpg\" />Побожні християни з  сердечною любов’ю величають Пресвяту Діву  й Богородицю Марію, Небесну  Царицю, Володарку Божої ласки й заступницю всіх християн.</p>\n<p align=\"JUSTIFY\">Вшановує  Пресвяту Діву Марію й український народ. З того часу, як наш люд став  навколішки перед Ісусом Христом, по всій нашій землі гомонить пісня  слави й любови також і на честь Христової Матері Марії. Величаючи її,  наш народ благає в неї опіки й заступництва в різних потребах цього  земного життя.</p>\n','2013-10-13 00:56:07','http://oranta.org/index.php?option=com_content&view=article&id=1537:pohodzhennja-svjata-pokrova-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Походження свята Покрова Пресвятої Богородиці','uk',3678,5161,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6412,200,RV2947_Articolo.jpg\" />Високопреосвященні та Преосвященні владики!&nbsp;Всесвітліші, високопреподобні та всечесні отці!&nbsp;Дорогі в Христі брати і сестри!</strong></em></p>\n<p>Великий піст – це особливий період літургійного року, час духовного пробудження і очищення. Сам Господь спонукає нас до особливої уваги та роздумів над особистим життям, нашим ставленням до Бога і ближнього; запрошує нас перевірити, наскільки правильно ми переживаємо ті історичні обставини, в яких нам доводиться проходити цьогорічний шлях Великого посту.</p>\n','2015-02-23 01:08:35','http://oranta.org/index.php?option=com_content&view=article&id=2639:pastyrske-poslannja-blazhennishogo-svjatoslava-na-velyky-pist-2015-roku&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Пастирське послання Блаженнішого Святослава на Великий піст 2015 року','uk',4931,6414,0,NULL),
(1,1,'<p align=\"center\"><em>Дорогі браття і сестри, шановні співвітчизники!</em></p>\n<p>Як і щороку, в останні тижні листопада ми з великою скорботою згадуємо жертви найбільшої трагедії в історії українського народу - Голодомору 1932-33 рр.  Близько десяти мільйонів заморених голодом, знищених злою волею держави за пів року, - таким є наслідок Голодомору. 55,5 тисяч смертей на день, 2300 - на годину, майже 40 - щохвилини. Свідомість відмовляється сприймати такі цифри, але вони - реальність. Так діяла влада для того, щоби знищити тих, хто не поділяв її ідеологію, а тих, кого не знищено - залякати. Ще й досі багато свідків Голодомору побоюються давати свідчення або роблять це анонімно. Вони знають правду, але жах перед нелюдським Голодомором примушує їх боятися - навіть через три чверті століття.</p>\n<p>\n','2008-11-22 00:21:53','http://www.oranta.org/index.php?option=com_content&view=article&id=112:zvernennja-kerivnykiv-hrystyjanskyh-cerkov-ukrajiny-z-pryvodu-vshanuvannja-zhertv-golodomoru-1932-33-rr&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Звернення керівників християнських Церков України з приводу вшанування жертв Голодомору 1932-33 рр.','uk',697,1677,0,NULL),
(1,1,'<p align=\"center\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3440,200,DSCN2959.JPG\" />Інтерв’ю з парохом греко – католицької церкви святого Миколая, що на Аскольдовій Могилі, отцем Ігорем Онишкевичем щодо зимового відпочинку дітей Карітас - Печерськ у Карпатах.</p>\n<p>- Отче Ігорю, скажіть, будь ласка, яка головна мета зимової поїздки в Карпати? З якою думкою Ви відправлялися в подорож?</p>\n<p>- Наша основна мета – закріплення катехитичного навчання дітей групи Карітас у недільній школі.\n','2010-02-10 11:26:54','http://www.oranta.org/index.php?option=com_content&view=article&id=486:zymovy-vidpochynok-dite-karitas-pechersk-u-karpatah&catid=25:parafija-cat&Itemid=27','P','','Зимовий відпочинок дітей Карітас-Печерськ у Карпатах','uk',1958,3441,0,NULL),
(1,1,'<p align=\"center\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3460,200,DSCN1110.JPG\" />Інтерв’ю з пані Русланою Дмитровською - головним організатором Шевченківського свята, що планується провести 14 березня у церкві святого Миколая, що на Аскольдовій Могилі.</p>\n<p align=\"right\">Незабаром визначна дата - 9 березня – день народження Тараса Григоровича Шевченка. Вже багато поколінь українці згадують про цей день і пошановують великого поета – творця української національної ідеї. Він боровся за волю українського народу, пишучи справедливі, і водночас чутливі вірші, які закарбувалися у пам’яті нашого народу.</p>\n','2010-02-28 23:41:13','http://www.oranta.org/index.php?option=com_content&view=article&id=498:luse-znesla--peremogla-ogo-ljubovi-syla-togo-velykogo-vognju-i-smert-ne-pogasylar&catid=25:parafija-cat&Itemid=27','P','','«Усе знесла й перемогла його любові сила. Того великого вогню і смерть не погасила.»','uk',1978,3461,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3407,200,111.jpg\" width=\"79\" height=\"110\" />«Бог ся рождає», «У Вифлеємі тайна явилась велика», «Во Вифлеємі нині новина»! &nbsp;Усі знають і співають ці колядки, але хто є автором цих творів? Хто подарував нам радість слухати їх?<strong>&nbsp;</strong></p>\n<p>Остап Йосипович Нижанківський - композитор, диригент, громадський діяч. Народився 24 січня 1862&nbsp; року, а відійшов у вічність &nbsp;<a href=\"http://uk.wikipedia.org/wiki/22_%D1%82%D1%80%D0%B0%D0%B2%D0%BD%D1%8F\" title=\"22 травня\">22 травня</a> <a href=\"http://uk.wikipedia.org/wiki/1919\" title=\"1919\">1919</a> року.</p>\n','2010-01-18 09:50:13','http://www.oranta.org/index.php?option=com_content&view=article&id=474:lu-vyflejemi-tana-javylas-velykar&catid=37:spilnota-cat&Itemid=49','P',NULL,'«У Вифлеємі тайна явилась велика»','uk',1933,3416,0,NULL),
(1,1,'<p align=\"center\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4645,200,1357223320_nunciy.jpg\" />Що таке Ватикан (Святий Престол), Римська Курія, Колегія Кардиналів, хто такий Апостольський Нунцій</p>\n<p align=\"center\">Я отримав лист від брата Войцеха від 15 лютого, із запрошенням представити вам виступ під назвою: «Ватикан: погляд зблизька»; передбачається, що я маю обговорити терміни «Ватикан» та «Святий Престол», описати Римську Курію та Колегію Кардиналів для вас, а також розповісти про себе, чи радше, про мою особливу посаду, посаду Апостольського Нунція; дати вам основну інформацію, якщо хочете.</p>\n','2013-04-18 00:03:24','http://oranta.org/index.php?option=com_content&view=article&id=1122:2013-04-17-21-08-19&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'«Ватикан: погляд зблизька»','uk',3168,4651,0,NULL),
(1,1,'<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3685,200,DSCN0681.JPG\" />Фотовиставка «Реалії тюремного життя»</p>\n<p>28 жовтня в центральному краєзнавчому музеї Сімферополя, суспільна організація «Айболить», при підтримці Державного Департаменту України з питань виконання покарань в Автономній Республіці Крим, відкрила виставку відомих кримських фотографів Олександра Каднікова та Авідаса Шемітаса.&nbsp; На п’ятдесяти фото показано реалії тюремного життя у виправних колоніях Сімферополя та Харкова.</p>\n','2010-11-03 22:51:37','http://oranta.org/index.php?option=com_content&view=article&id=612:ljak-vygljadaje-zhyttja-za-koljuchym-drotomr&catid=29:propovidi-cat&Itemid=34','P',NULL,'«Як виглядає життя за колючим дротом?»','uk',2203,3686,0,NULL),
(1,1,'<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5258,200,DSC_03891.jpg\" />Дорогі у Христі брати і сестри!<strong> </strong></p>\n<p>Створені на образ і подобу Божу, ми всі народилися для того, щоб виявляти Божу красу, яка всередині нас. І цей дар має кожен із нас. Особи з розумовою неповносправністю також є посудинами цієї краси.</p>\n<p>Центр духовної підтримки осіб з особливими потребами «Емаус», мирянський рух спільнот «Віра й Світло», з яким центр «Емаус» має спільне коріння, та інші &nbsp;подібні об’єднання творилися для того, щоб допомогти людям із розумовою неповносправністю віднайти в собі неповторну цінність, красу та дари, дані Богом, і допомогти відкрити це суспільству.</p>\n','2013-11-14 14:44:37','http://oranta.org/index.php?option=com_content&view=article&id=1636:pro-dar-osib-z-rozumovoju-nepovnospravnistju&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Про дар осіб з розумовою неповносправністю','uk',3783,5266,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5771,200,111.jpg\" />Двадцять першого квітня, у другий день Великодніх свят, в Національному музеї під відкритим небом у Пирогово, пройшов всіма відмий поливаний понеділок. Можна з певністю сказати, що це вже традиція для греко-католиків Києва проводити тут святкування.</p>\n<p>З церкви святого Миколая, що на Аскольдовій могилі, багато парафіян відвідали сьогодні Пирогово. Хтось добирався сюди на власному авто, але наша парафія організувала автобус, на якому бажаючі могли доїхати до Пирогово. Автобус був переповнений. Наша молода спільнота «Відродження» в кількості десяти людей також долучилася до цього святкування.</p>\n','2014-04-22 11:38:08','http://oranta.org/index.php?option=com_content&view=article&id=2060:polyvany-ponedilok-v-pyrogovo&catid=25:parafija-cat&Itemid=27','P','','Поливаний понеділок в Пирогово','uk',4289,5772,0,NULL),
(1,1,'<p align=\"center\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5998,200,2014.jpg\" />Звернення&nbsp;<span style=\"font-size: 0.875em;\">Всеукраїнської Ради Церков і релігійних організацій&nbsp;</span><span style=\"font-size: 0.875em;\">щодо ситуації на Сході України</span></p>\n<p>Всеукраїнська Рада Церков і релігійних організацій протягом останніх тижнів зверталась зі словами підтримки позиції Президента України П. О. Порошенка щодо мирного врегулювання збройного конфлікту на Сході України та висловлювала готовність посильно сприяти в реалізації його мирного плану.</p>\n','2014-07-09 22:45:56','http://oranta.org/index.php?option=com_content&view=article&id=2271:-vseukrajinska-rada-cerkov-zaklykaje-separatystiv-sklasty-zbroju-ta-zvilnyty-vsih-zaruchnykiv&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Всеукраїнська Рада Церков закликає сепаратистів скласти зброю та звільнити всіх заручників','uk',4516,5999,0,NULL),
(1,1,'<p align=\"center\"><strong> </strong></p>\n<p>20 грудня, після Богослужіння і вистави, присвяченої празнику святого Миколая, &nbsp;розпочався шаховий турнір. На турнір прибуло лише восьмеро учасників, хоча зареєструвалось шістнадцять. &nbsp;Очевидно, більшість захворіла.</p>\n','2009-12-21 23:32:35','http://www.oranta.org/index.php?option=com_content&view=article&id=459:shahovy-turnir-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Шаховий турнір на Аскольдовій Могилі','uk',1906,3389,0,NULL),
(1,1,'<p align=\"center\"><strong><em><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5274,200,Pope-John-Paul-II-looks-a-010.jpg\" />Шановні Браття в Єпископстві, </em>вітання і апостольське благословення!</em><em> </em></strong></p>\n<p>Сяйво Істини сяє в усіх ділах Творця, а особливо в людині, створеній на образ і подобу Божу (пор. Бут. 1, 26): правда просвітлює людський розум і формує свободу людини, провадячи її у пізнанні й любові до Господа. Тому Псалмопівець молиться: „Яви нам, Господи, світло лиця Твого!” (Пс. 4, 7).\n','2013-11-18 00:01:18','http://oranta.org/index.php?option=com_content&view=article&id=1642:encyklika-veritatis-splendor-sjavo-istyny-papy-ivana-pavla-ii&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'ЕНЦИКЛІКА VERITATIS SPLENDOR – СЯЙВО ІСТИНИ ПАПИ ІВАНА ПАВЛА ІІ','uk',3793,5276,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><strong><em><em><img style=\"margin: 10px; float: left;\" src=\"image,5274,200,Pope-John-Paul-II-looks-a-010.jpg\" alt=\"\" />Шановні Браття в Єпископстві, </em>вітання і апостольське благословення!</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Сяйво Істини сяє в усіх ділах Творця, а особливо в людині, створеній на образ і подобу Божу (пор. Бут. 1, 26): правда просвітлює людський розум і формує свободу людини, провадячи її у пізнанні й любові до Господа. Тому Псалмопівець молиться: &bdquo;Яви нам, Господи, світло лиця Твого!&rdquo; (Пс. 4, 7).</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','2013-11-18 00:01:18','http://oranta.org/index.php?option=com_content&view=article&id=1642:encyklika-veritatis-splendor--sjavo-istyny-papy-ivana-pavla-ii&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','ЕНЦИКЛІКА VERITATIS SPLENDOR – СЯЙВО ІСТИНИ ПАПИ ІВАНА ПАВЛА ІІ','uk',3792,5275,0,NULL),
(1,1,'<p align=\"center\"><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5362,200,blagenn.jpg\" />Розмова з Любомиром Гузаром, главою УГКЦ (2001-2011)</em></strong></p>\n<p><strong> </strong></p>\n<p><strong>— Слава Ісусу Христу! Мене звати Мар’яна, я хотіла б поговорити з Вами про Майдан.</strong></p>\n<p><strong>— Слава навіки! Я — Любомир, теж хотів би поговорити про Майдан.</strong></p>\n','2013-12-20 15:38:25','http://oranta.org/index.php?option=com_content&view=article&id=1723:madan--ce-duzhe-gidna-vidpovid-narodu-na-diji-vlady&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Майдан — це дуже гідна відповідь народу на дії влади','uk',3880,5363,0,NULL),
(1,1,'<p align=\"center\"><strong><img src=\"http://askoldova-mohyla.org/uk/image,6264,200,023Tykh.gif\" style=\"margin: 10px; float: right;\" />25 років тому, 19 листопада 1989 року, українська земля прийняла Олексу Тихого, Юрія Литвина і Василя Стуса.</strong></p>\n<p><em>А проте: ми ще повернемось бодай – ногами вперед, але: не мертві, але: не переможені, але: безсмертні. В. Стус</em></p>\n','2014-11-20 23:21:01','http://oranta.org/index.php?option=com_content&view=article&id=2526:povernennja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Повернення','uk',4782,6265,0,NULL),
(1,1,'<p align=\"center\"><strong><img src=\"http://askoldova-mohyla.org/uk/image,6580,200,46456.jpg\" style=\"margin: 10px; float: right;\" />Вельмишановний Пане Президенте України! Вельмишановний Петре&nbsp;Олексійовичу!</strong></p>\n<p>Вислухавши уважно Ваш річний звіт, відчуваю себе зобов’язаним звернутись до Вас цим відкритим листом. Впевнений, що маю на це право не тільки як духовна особа, але і як активний учасник Євромайдану, а тепер ще й як свідок подій на Сході нашої Батьківщини.</p>\n','2015-06-08 16:18:35','http://oranta.org/index.php?option=com_content&view=article&id=2773:lyst-jepyskopa-stanislava-shyrokoradjuka-do-petra-poroshenk&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Лист єпископа Станіслава Широкорадюка до Петра Порошенк','uk',5098,6581,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3579,200,V.JPG\" />Інтерв’ю з отцем Олексієм Геником щодо літнього відпочинку дітей з парафії церкви святого Миколая</strong></p>\n<p><strong> </strong></p>\n<p><strong>- Отче Олексію, скажіть, будь ласка, з яким настроєм, думками Ви виїжджали у подорож? Яке ставили перед Собою завдання? Що Ви очікували від цієї поїздки?</strong><strong>&nbsp;</strong></p>\n<p>- Найперше варто сказати, що я їхав на море з відчуттям певного духовного обов’язку. Відпочинок – це звичайно добре, але він повинен супроводжуватися молитвою, а якщо є священик, то також і Службою Божою, духовними розмовами, сповіддю, тобто ставити акцент на духовному розвитку дитини, а не тільки на відпочинку тілесному.</p>\n','2010-07-09 15:20:40','http://oranta.org/index.php?option=com_content&view=article&id=551:vidchuttja-duhovnogo-obovjazku&catid=25:parafija-cat&Itemid=27','P','','Відчуття духовного обов’язку','uk',2098,3581,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6386,200,Chaplain_Paul_Manley.jpg\" />11 лютого 2015 року Верховна Рада України прийняла за основу проект закону </strong><a href=\"http://w1.c1.rada.gov.ua/pls/zweb2/webproc4_1?pf3511=52591\">№1154 \"Про внесення змін до деяких законодавчих актів України (щодо регулювання діяльності капеланів в органах та установах, що належать до сфери управління Державної пенітенціарної служби України)</a></p>\n<p>Законопроектом запроваджується інститут <em>\"священнослужителів (капеланів)\"</em> в органах та установах, що належать до сфери управління Державної пенітенціарної служби України з метою здійснення \"душпастирської опіки\" в даних органах та установах.</p>\n','2015-02-11 21:26:11','http://oranta.org/index.php?option=com_content&view=article&id=2615:verhovna-rada-ukrajiny-prynjala-za-osnovu-proekt-zakonu-pro-kapelanstvo-v-tjurmah&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Верховна Рада України прийняла за основу проект закону про капеланство в тюрмах','uk',4904,6387,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6451,200,56465465.jpg\" /></strong></p>\n<p align=\"center\"><strong>Слава Ісусу Христу! Дорогі доброчинці!</strong></p>\n<p>Парафія Української греко-католицької церкви на Аскольдовій могилі св. Миколая Чудотворця в місті Києві щиро вдячна Всеукраїнському &nbsp;благодійному фонду «Тріумф серця» &nbsp;за надані нам дитячі подаруночки.&nbsp; Доводимо до Вашого відома, що 15-тьма коробками ми поділилися з отцем Михайлом Будником. І вони одразу ж поїхали в село Голданівка, що на Вінниччині, &nbsp;радувати тамтешню дітвору.&nbsp; Решту – роздали&nbsp; нашим потребуючим дітям.</p>\n','2015-03-13 22:03:10','http://oranta.org/index.php?option=com_content&view=article&id=2667:2015-03-13-20-12-09&catid=25:parafija-cat&Itemid=27','P','','Служіння парафіяльної служби милосердя «Карітас – Печерськ» ','uk',4972,6455,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6563,200,93332.jpeg\" />Бесіда перша Йоана Золотоустого на Святу П’ятдесятницю і про те, чому нині не бувають знамення, і що діла і слова наші записуються.</strong></p>\n<p>1. Знову свято, знову торжество і знову многодітна і дітолюбна Церква прикрашається безліччю дітей. Але яка їй користь від дітолюбства, якщо вона тільки в свята, а не постійно бачить жадані обличчя своїх дітей, подібно до того, якщо би хтось, маючи прекрасну одежу, не мав дозволу користуватися нею постійно? Багаточисельність тих, які приходять, є одежею Церкви, як і пророк в давнину сказав, говорячи про Церкву:&nbsp;<em>Усіма ними, немов окрасою, ти вберешся, як молода, ними ти оздобишся</em> (Іс 49,18). Як цнотлива і благородна жінка, одягнувшись в одяг, що простирається до п’ят, здається благообразною і гарнішою, так і церква, покриваючись безліччю ваших тіл, немов би довгою одежею, є сьогодні більш блискучою.</p>\n','2015-05-31 21:07:57','http://oranta.org/index.php?option=com_content&view=article&id=2765:besida-oana-zolotoustogo-na-svjatu-pjatdesjatnycju&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Бесіда Йоана Золотоустого на Святу П’ятдесятницю','uk',5082,6565,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: left;\" src=\"image,5158,200,99757645.jpg\" alt=\"\" />Молитовна спільнота &laquo;Матері в молитві&raquo;</strong></p>\r\n<p>Молитовна група &laquo;Матері в молитві&raquo; при церкві св. Миколая на Аскольдовій могилі існує від травня 2006 р. Кожної першої середи місяця о 18:00 у церкві збирається група для молитви. Все починалося з трьох матерів, сьогодні у групу входять і чоловіки, що не порушує умов самої групи. Адже щирість наміру виправдовує усі засоби.</p>','2013-10-12 22:37:36','http://oranta.org/index.php?option=com_content&view=article&id=1536:molytovni-spilnoty-parafiji-sv-mykola-myrlikiskogo&catid=25:parafija-cat&Itemid=27','P','','Молитовні спільноти парафії св. Микола Мирлікійського','uk',3676,5159,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5672,200,safe_image.php.jpg\" />«Уніатизм» і православно-католицький діалог</strong></p>\n<p>Розбіжності з питання «уніатизму» віддавна є дражливим моментом у православно-католицькому діалозі. Доки Східні Католицькі Церкви у країнах колишнього соціалістичного табору не вийшли з підпілля (а ста­лося це 1988 р. і найближчого по ньому часу), видавалося, що ці розбіж­ності можна делікатно оминати: існувало мовчазне припущення, що Східні Католицькі Церкви в діаспорі поступово зникнуть, а на Близькому Сході пропадуть під мусульманським та сіоністським тиском.</p>\n','2014-03-28 02:45:21','http://oranta.org/index.php?option=com_content&view=article&id=1975:2014-03-27-21-04-19&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Проблема «уніатизму»','uk',4192,5675,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5955,200,17-03-14-6.jpg\" />Звіт &nbsp;про реалізовані проекти за період серпень 2013 – травень 2014 рр.</strong></p>\n<p>Лицарі Колумба розпочали свою діяльність в Україні <strong>21 травня 2013 р. </strong>створенням першої в Україні братерської організації – Ради Лицарів Колумба імені Святого рівноапостольного князя &nbsp;Володимира.</p>\n<p>Одним із перших кроків організації було обрання Проводу (<strong>20 вересня 2013 р.</strong>). Великий Лицар – Богдан Ковалів.\n','2014-06-22 18:46:33','http://oranta.org/index.php?option=com_content&view=article&id=2232:osnovni-naprjamky-dijalnosti-lycariv-kolumba-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Основні напрямки діяльності Лицарів Колумба в Україні','uk',4474,5957,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6394,200,maxresdefault.jpg\" />Про централізацію міста </strong></p>\n<p>Як тільки почався Майдан, почуття обов’язку громадянина Поповича зразу ж мене туди привело. Я бував там досить часто упродовж усього періоду Майдану. Ходив майже щоденно: дорога на мою роботу проходить через Майдан.</p>\n','2015-02-16 15:08:38','http://oranta.org/index.php?option=com_content&view=article&id=2622:2015-02-16-13-09-46&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Майдан: усна історія» - розмова з Мирославом Поповичем','uk',4912,6395,0,NULL),
(1,1,'<p align=\"center\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6587,200,reuters846.jpg\" />Розшифрування символів у подарунках і словах</strong></p>\n<p>Владімір Путін&nbsp;– у Ватикані, на аудієнції у Папи Римського. «Як так можна?! Зрада, сором для Святого Престолу!» – Приблизно таким «холіваром» зустріла більшість українських завсідників соціальних мереж повідомлення з Риму.</p>\n<p>А й справді, як це Папа Франциск, котрого ми так шануємо, так вболіваємо за нього, взяв і зустрівся з тим «кривавим тираном». І не лише зустрівся.&nbsp;<em>«Путін подарував Папі вишите зображення храму Христа Спасителя, а Святіший Отець вручив російському президенту медальйон роботи художника Ґвідо Верої, на якому зображений янгол миру, що закликає до творення солідарного та справедливого світу, а також примірник апостольського послання Evangelii Gaudium»,</em> – повідомляє прес-служба Ватикану.</p>\n','2015-06-12 12:37:09','http://oranta.org/index.php?option=com_content&view=article&id=2779:tajemnycja-audijenciji-putina-u-papy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Таємниця аудієнції Путіна у Папи','uk',5105,6588,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\"><strong><span style=\"text-decoration: underline;\">Сторінки життя молодіжної спільноти</span></strong><strong> </strong></p>\n<p align=\"center\">Церкви св. Миколая Чудотворця з Аскольдової Могили, м. Київ</p>\n<p align=\"center\">(вересень 2009 р. Б. – червень 2010 р. Б.)</p>\n<p align=\"center\"> </p>\n<p>Духовний провідник:<strong> Отець Олексій Геник, </strong></p>\n<p>Катехит організації:<strong> Ірина Василик, сестра Анастасія</strong></p>\n<p>Лідер організації:<strong> Ярослав Павлюк</strong></p>\n<p><span style=\"text-decoration: underline;\"> </span></p>\n<p>Наше гасло: <strong>СЛАВА БОГУ, ЧЕСТЬ МАРІЇ, НАМ МИР!</strong></p>\n<p><strong> </strong></p>\n<p><strong><span style=\"text-decoration: underline;\"></span></strong></p>\n','2010-05-11 21:59:34','http://oranta.org/index.php?option=com_content&view=article&id=529:molod-parafiji&catid=25:parafija-cat&Itemid=27','P','','Молодь парафії','uk',2057,3540,0,NULL),
(1,1,'<p align=\"center\"><strong>Звернення</strong><br /> Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ<br /> до вірних Української Греко-Католицької Церкви та людей доброї волі<br /> з нагоди другого туру виборів Президента України<o:p></o:p></p>\n<p align=\"right\">«Щасливий народ, що йому Господь – Бог його!»<br /> (Псалом 143, 156)</p>\n','2010-01-31 22:39:30','http://www.oranta.org/index.php?option=com_content&view=article&id=477:jepyskopy-ugkc-pro-drugy-tur-vyboriv-prezydenta-ukrajiny&catid=37:spilnota-cat&Itemid=49','P',NULL,'Єпископи УГКЦ про другий тур виборів Президента України','uk',1946,3429,0,NULL),
(1,1,'<p align=\"center\"><strong>КНЯЗЬ АСКОЛЬД РОЗКАЯВСЬ</strong></p>\n<p>Фотій Патріархом</p>\n<p>В Візантії правив.</p>\n<p>Під Господнім царством</p>\n<p>Матір Божу славив.<span style=\"font-family: verdana, arial, helvetica, sans-serif; font-size: 16px;\"> </span></p>\n','2009-12-29 00:33:26','http://www.oranta.org/index.php?option=com_content&view=article&id=462:tvory-pro-askolda&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Твори про Аскольда','uk',1914,3397,0,NULL),
(1,1,'<p align=\"center\"><strong>Отче Ігорю, відомо, що парафіяльна рада, будуючи плани подорожі, старається щороку змінювати місце відпочинку. Чому Ви вирішили поїхати саме у село Заріччя Івано-Франківської області, що розташувалось неподалік славного Яремче?</strong><strong>&nbsp;</strong></p>\r\n<p>Ми щороку маємо такі подорожі та, дійсно, &nbsp;змінювати місце відпочинку. Це дуже корисно для дітей, адже вони, зростаючи з парафією, мають можливість бачити різні місця, монастирі, церкви. Ми довго шукали місце відпочинку: мали зв\'язки з приватними садибами, але Бог&nbsp; благословив нас на відпочинок у Заріччі.</p>','2009-08-07 00:32:34','http://www.oranta.org/index.php?option=com_content&view=article&id=400:intervju-z-parohom-greko--katolyckoji-cerkvy-svjatogo-mykolaja-shcho-na-askoldovi-mogyli-otcem-igorem-onyshkevychem-shchodo-litnogo-vidpochynku-dite-parohiji-u-karpatah&catid=25:parafija-cat&Itemid=27','N','','Інтерв’ю з парохом греко – католицької церкви святого Миколая, що на Аскольдовій Могилі, отцем Ігорем Онишкевичем щодо літнього відпочинку дітей парохії у Карпатах','uk',1713,3196,0,NULL),
(1,1,'<p align=\"center\">Напередодні християнства </p>\n<p>Збігає друге тисячоліття від народження Ісуса - засновника світової релігії - християнства, яка стала основою західної цивілізації.  Християнство стало завершенням духовного повороту, здійсненого в першому тисячолітті до нашої ери - в період, який вчені називають  <em>осьовим часом</em>   Сватової історії людства. До цього часу панували магічні релігії,  Бога шукали поза людиною, а світогляд формувався міфами.  Світ  був  простий  i зрозумілий, всім заправляли численні боги, які вимагали послуху, проте,  умилостививши їх,  можна було скористатися  їхньою силою. Проблеми ж людини - як особистості,  як духовної істоти - не існувало.</p>\n<p>В осьовий  час  майже  одночасно  у всіх високорозвинених цивілізаціях з\'явилися вчення, які прагнули збагнути суть людини i суть Бога.  Авторів нових вчень - Лао-Цзи, Конфуція, Будду, Зороастра, Платона, іудейських пророків i, нарешті, Ісуса з  Назарету  нині називають Учителями людства.  Завдяки їм з\'явився новий тип людини  i  сформувався  той  світогляд,  який домінує й досі. Людина усвідомила сама себе, свої можливості i свої межі.  Перед нею відкрилась безодня безконечного світу  i безодня власної душі.</p>\n<p>\n','2009-01-05 12:13:58','http://www.oranta.org/index.php?option=com_content&view=article&id=169:povorot-vid-magnetyzmu-do-duhovnyh-religi&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Поворот від магнетизму до духовних релігій','uk',931,1905,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"center\">Нова радість стала, яка не бувала,</p>\n<p align=\"center\">Над вертепом звізда ясна cвітло засіяла.</p>\n<p align=\"center\">Де Христос родився, з Діви воплотився,</p>\n<p align=\"center\">Як чоловік пеленами Убого повився…\n','2010-01-12 12:24:27','http://www.oranta.org/index.php?option=com_content&view=article&id=469:hrystos-sja-rozhdaje-slavimo-ogo&catid=25:parafija-cat&Itemid=27','P','','Христос ся рождає! Славімо Його!','uk',1921,3404,0,NULL),
(1,1,'<p align=\"center\">Петриківський розпис -&nbsp; очима Андрія Масягіна – учня творчої майстерні Ганни Мастак</p>\n<p align=\"right\">Петриківський розпис не є достатньо відомий у світі. Але він кожного дня набуває популярності в Україні. Дізнаймося більше про цей чудовий стиль малювання. Відродимо петриківський розпис.</p>\n','2010-02-06 13:15:21','http://www.oranta.org/index.php?option=com_content&view=article&id=481:vchymosja-petrykivskomu-rozpysu-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Вчимося петриківському розпису на Аскольдовій Могилі','uk',1953,3436,0,NULL),
(1,1,'<p align=\"center\">Різдвяна відозва ОУН на Землях</p>\n<p>Вечірня зоря, що зійшла над малим незнаним містом, провіщала колись прихід нової віри і нової доби в історії людства.</p>\n<p>Віра простих хліборобів і вівчарів, зрісши з їх чистих і прямих сердець, мала таку міць і таку силу, що не встояли перед нею навіть фортеці і палаци чужинців, і поклонитися їм прийшли царі трьох суходолів.\n','2010-01-03 18:15:45','http://www.oranta.org/index.php?option=com_content&view=article&id=466:u-svjat-vechir&catid=37:spilnota-cat&Itemid=49','P',NULL,'У СВЯТ-ВЕЧІР','uk',1918,3401,0,NULL),
(1,1,'<p align=\"center\">ТАРАС ШЕВЧЕНКО В ОЦІНЦІ УКРАЇНСЬКОЇ ДІАСПОРИ</p>\n<p><img src=\"http://www.oranta.org/images/stories/2008/11/viewimg.php.jpg\" style=\"float: left;\" /> Після утворення незалежної України у нас значно посилилась зацікавленість до праць про Шевченка вчених української діаспори. Адже вони створили багато важливих наукових досліджень на цю тему, що витримали іспит часом. Маємо на увазі насамперед студії про Кобзаря Василя Барки, Леоніда Білецького, Миколи Глобенка, Павла Зайцева, Дмитра Чижевського, Дмитра Чуба, Юрія Шевельова, Миколи Шлемкевича тощо. Останнім часом окремі їхні праці побачили світ і в нас. На жаль, ще не здійснена спроба узагальнити досвід вчених української діаспори з проблем шевченкознавства. У межах можливого привернути увагу на їхню актуальність - основна мета нашого дослідження.<br /> Звичайно, годі гадати, що обрана нами тема буде всебічно висвітлена. Власне, вона тільки ставиться. Але передбачається бодай штрихами окреслити її грані. Для цього вводиться до наукового обігу низка важливих положень з праць представників української діаспори. Навіть при першому знайомстві з ними складається враження, що ці вчені вельми одностайні в оцінці Кобзаря. Типовою такого змісту може бути стаття проф. Миколи Глобенка &quot;Живий Шевченко&quot;. Виражаючи почуття своїх однодумців, він пафосно стверджує: &quot;Так, Шевченко - той, коли зайнялося й запалало, Пророк, Геній, Поет Нації, тобто народу, що саме в його творчості усвідомив себе, своє місце і свою мету. І до творчості його ми підходимо як до священної для кожного українця книги. І дні його народження і смерті - це великі дні для нас&quot; [4, С.270].</p>\n<p>\n','2009-03-02 12:12:38','http://www.oranta.org/index.php?option=com_content&view=article&id=229:pamjati-tarasa&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Пам\'яті Тараса','uk',1195,2169,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"justify\" lang=\"uk-UA\"><img style=\"margin: 10px; float: right;\" src=\"http://volyniany.org.ua/images/stories/reportazh/shevchenko/kanev1.jpg\" height=\"162\" width=\"254\" /><strong>22 травня </strong>–  важлива дата для кожного українця. У цей день прах Тараса Григоровича  Шевченка, згідно з його заповітом, було перепоховано в Україні.</p>\n<p align=\"justify\" lang=\"uk-UA\">Тяжкою була для Тараса Григоровича зима 1861 року. Перебуваючи далеко вiд батькiвщини, в Санк<strong>-</strong>Петербурзі,  він все частіше хворів, усе бiльше вiдчував свою самотнiсть. 9 березня,  в день його народження, до нього завiтало кiлька знайомих.</p>\n','2013-05-22 14:45:00','http://oranta.org/index.php?option=com_content&view=article&id=1219:perepohovannja-ukrajinskogo-proroka&catid=37:spilnota-cat&Itemid=49','P',NULL,'Перепоховання українського Пророка','uk',3266,4749,0,NULL),
(1,1,'<p align=\"justify\"><img src=\"http://askoldova-mohyla.org/uk/image,4225,200,400.jpg\" style=\"margin: 10px; float: right;\" />Покладення  чесної ризи Пресвятої Богородицi у Влахернi (V). У роки правління  візантійського імператора Льва Великого, Македонянина (457-474), брати  Гальбій і Кандид, наближені царя, вирушили з Константинополя до  Палестини на поклоніння святим місцям. У невеликому селищі поблизу  Назарету вони зупинилися на нічліг в однієї старої єврейки. У її будинку  паломники звернули увагу на запалені свічки і дим фіміаму. На питання,  що за святиня знаходиться в будинку, благочестива жінка довго не хотіла  відповідати, але після невідступних прохань повідала, що зберігає дорогу  святиню - Ризу Богородиці, від якої відбуваються багато чудес та  зцілень. Пресвята Діва перед Успінням подарувала одну зі Своїх риз  благочестивій дівиці-єврейці з цього роду, заповівши їй передати її  перед смертю також дівчині. Так, від покоління до покоління, Риза  Богоматері зберігалася в цій родині.</p>\n','2012-07-17 02:15:10','http://oranta.org/index.php?option=com_content&view=article&id=873:pokladennja-chesnoji-ryzy-presvjatoji-bogorodyci-u-vlaherni&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Покладення чесної ризи Пресвятої Богородицi у Влахернi','uk',2769,4252,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: left;\" src=\"http://www.catholic-media.org/ua/images/stories/swiati/honorat.jpg\" alt=\"honorat.jpg\" title=\"honorat.jpg\" width=\"92\" height=\"141\" />13  жовтня Церква вшановує блаженного отця Гонората Козьмінського,  капуцина, який є Засновником Згромадження Малих Сестер Непорочного Серця  Марії та&nbsp;багатьох інших Згромаджень, 17 з яких існує до сьогодні.  Вітаємо Сестер зі Святом та дякуємо їм за відданість&nbsp;у різноманітному  служінні Церкві в Україні, в тому числі за жертовне служіння у  Католицькому Медіа Центрі.&nbsp;Запрошуємо до перегляду відеоролика про бл.  Гонората&nbsp;та ознайомлення з біографією його життя.</p>\n','2010-10-16 00:28:23','http://oranta.org/index.php?option=com_content&view=article&id=605:13-zhovtnja-spomyn-bl-gonorata-kozminskogo&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'13 жовтня - спомин бл. Гонората Козьмінського','uk',2180,3663,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4482,200,nasl.jpg\" />Дівчинка подала своїй учительці листочок зі своїм  особистим \"рецептом для життя\". Напи­сала: \"Потрібно чотири обійми на  день, щоб прожити. Потрібно вісім обіймів на день, щоб якось іти вперед.  Потрібно дванадцять обіймів на день, щоб рости\".</p>\n<p align=\"justify\"><strong>У сім\'ї був старий чорний кіт, який у повечірню  пору поводився досить дивно. Не хотів ані їсти, ані гуляти, не хотів  узагалі нічого робити.</strong></p>\n','2013-03-08 23:38:47','http://oranta.org/index.php?option=com_content&view=article&id=1025:recept-9-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Рецепт (9 березня)','uk',3003,4486,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4807,200,molitva.jpg\" />На краю світа, у густому лісі була драбинка — звичайна драбинка, зроблена зі сухого дерева.</p>\n<p align=\"justify\">Зусібіч її оточували ялинки, модрини й берези — на диво гарні ставні дерева. Драбинка серед них виглядала геть злиденно</p>\n<p align=\"justify\">Якось лісоруби, що працювали у лісі, добралися і до того закутка. Вони зневажливо глянули на драбинку</p>\n','2013-06-13 23:58:40','http://oranta.org/index.php?option=com_content&view=article&id=1250:nepotribne-derevo-14-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Непотрібне дерево (14 червня)','uk',3331,4814,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5036,200,img2.jpg\" />Проповідник запізнювався. Сестри, які очі­кували його  в монастирській каплиці, дійшли вже до п\'ятнадцятої таємниці вервиці,  коли по­дзвонили у двері.</p>\n<p align=\"justify\">Втомлений, заклопотаний проповідник  виба­чався перед настоятелькою: \"Прикро мені, ма­тінко, але я не встиг  приготувати проповідь...\"</p>\n<p align=\"justify\">\"Це  неважливо, - відповіла сердечно насто­ятелька, - прошу, отче, говоріть усе, що  спаде на думку\".\n','2013-08-31 22:21:22','http://oranta.org/index.php?option=com_content&view=article&id=1432:govorymo-1-veresnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Говоримо (1 вересня)','uk',3555,5038,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5194,200,002r30fw.jpg\" />Один молодий хлопчина, який брав участь у змаганнях з  бігу, що проводилися у пустелі, загу­бився поміж незліченними дюнами і  скелями. Він ніяк не міг відшукати потрібного керунку. Проте не впав у  відчай, а відважно рушив далі. Після кількох годин блукання сонце і  спека геть змо­рили його. Бігун втрачав сили.</p>\n<p align=\"justify\">Ослаблений, хлопець обіперся об скелю, яка виросла на  перехресті трьох шляхів, що губилися в далечіні. „Води”, — прошепотів  він хрипко.</p>\n','2013-10-26 00:45:37','http://oranta.org/index.php?option=com_content&view=article&id=1567:molod-26-zhovtnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Молодь (26 жовтня)','uk',3714,5197,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5199,200,1378180.jpg\" />Ніхто не пам\'ятав, коли той чоловік з\'явився у місті.  Здавалося, що був там завжди – на хіднику вельми залюдненої вулиці, де  розмістилося багацько крамниць, ресторанів, кінотеатрів, де увечері  любили прогулюватися міщани, де призначали побачення закохані.</p>\n<p align=\"justify\">Він сидів на колінах на хіднику і кольоровими  крейдочками вимальовував ангелів, дивовижні крайобрази, по вінця залиті  сонцем, усміхнених дітей, розквітлі квіти і – мрії про свободу.</p>\n','2013-10-29 23:37:25','http://oranta.org/index.php?option=com_content&view=article&id=1571:kolorovi-kredochky-30-zhovtnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Кольорові крейдочки (30 жовтня)','uk',3723,5206,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5199,200,241495.jpg\" />Одної морозяної зимової ночі буддійський лама знайшов  на своєму порозі замерзлу мишку, що від холоду майже не подавала ознак  життя. Лама підняв її, зігрів, нагодував, а тоді попросив, щоб  залишилась із ним і склала йому товариство. З того часу мишка жила у  теплі. Проте, звірятко чомусь не виглядало щасливим. Лама зажурився.</p>\n','2013-10-30 23:43:13','http://oranta.org/index.php?option=com_content&view=article&id=1572:myshka-31-zhovtnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Мишка (31 жовтня)','uk',3729,5212,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5345,200,blue.jpg\" />Китайський імператор, передчуваючи набли­ження  смерти, вирішив видати заміж свою єдину дочку. Вона була не лише  найелегантнішою, але й перевершила вродою і розумом усіх дівчат імперії.  Та був у неї один ґандж: ні за які гроші не хотіла виходити заміж.</p>\n<p align=\"justify\">Проте, розуміючи, що це так важливо для бать­ка, вона  пообіцяла, що вийде заміж, однак лишень за того юнака, який подарує їй  небесну троянду.</p>\n','2013-12-17 23:18:22','http://oranta.org/index.php?option=com_content&view=article&id=1686:nebesna-trojanda-18-grudnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Небесна троянда (18 грудня)','uk',3869,5352,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5367,200,af0.jpg\" />Один молодий лицар був відомий своїм розпу­сним  способом життя Монах намагався напоумити лицаря, що він ризикує постати  перед судом Божим з душею, обтяженою гріхами.</p>\n<p align=\"justify\">„Нема чого боятися, — легковажно відповів лицар. —  Тобі ж відомо, що Господь Бог добрий і милосердний.</p>\n','2013-12-22 23:57:17','http://oranta.org/index.php?option=com_content&view=article&id=1692:try-slova-23-grudnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Три слова (23 грудня)','uk',3884,5367,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5544,200,676.jpg\" />Всеукраїнська Рада Церков і релігійних організацій рішуче засуджує  насильство, кровопролиття та вбивства, що мали місце у Києві 18 лютого  2014 року та тривають досі.</p>\n<p align=\"justify\">Ми знову і знову закликаємо, просимо й благаємо сторони протистояння  припинити силові дії, а представників влади й опозиції продовжити  переговори. До завершення переговорів ми закликаємо владу і опозицію  негайно зупинити силові дії з обох боків.</p>\n','2014-02-19 19:31:14','http://oranta.org/index.php?option=com_content&view=article&id=1869:-zajava-vrciro-z-pryvodu-eskalaciji-nasylstva-u-kyjevi-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Заява ВРЦіРО з приводу ескалації насильства у Києві ','uk',4062,5545,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://oranta.org/image,4488,200,1310482327_tn.jpg\" /><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4488,200,1310482327_tn.jpg\" />Новобранці  мусили бігати щоденно. Ще до світанку,  тільки вистрибнувши з ліжок, розігрі­вали  м\'язи. Розпочинали службу в  спеціальному військовому антитерористичному  корпусі, тому звикли до  навантажень, що постійно збіль­шувались, аж до повної  фізичної знемоги.  Те­пер усе було інакше. Ці вправи були не схожі на їхній  щоденний біг з  ритмічним співом.</p>\n','2013-03-09 22:19:31','http://oranta.org/index.php?option=com_content&view=article&id=1028:razom-10-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Разом (10 березня)','uk',3007,4490,0,NULL),
(1,1,'<p align=\"justify\"><img style=\"margin: 10px; float: right;\" src=\"http://www.prytyska.kiev.ua/images/stories//prev/Vyshgorod.jpg\" height=\"150\" width=\"133\" />Вишгородська ікона Божої Матері написана Євангелистом Лукою на дошці від столу, за яким був за трапезою Спаситель з Пречистою Матір\'ю і праведним Йосипом. Божа Мати, побачивши цей образ, промовила: \"Віднині ублажатимуть Мене всі роди. Благодать Народженого від Мене й Моя нехай буде з цією іконою\". В 1131 році ікону була надіслано до Руси з Константинополя святому князеві Мстиславові († 1132, пам\'ять 15 квітня), і вона була поставлена в Дівочому монастирі Вишгорода - давнього помісного уділу святої рівноапостольної великої княгині Ольги.</p>\n','2009-05-14 14:51:25','http://www.oranta.org/index.php?option=com_content&view=article&id=324:istorija-vyshgorodskoji-ikony-bozhoji-materi&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Історія Вишгородської ікони Божої Матері','uk',1486,2542,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"justify\"><strong> І на другий рік прийди, дорогенький Миколаю</strong></p>\n<p align=\"justify\">Пізно у вечірній час <br /> Миколай Святий до нас <br /> Так тихенько приходив, <br /> Так легенько залишив<br /> Подарунки і гостинці, <br /> Наче загадковий птах <br /> У солодких ніжних снах, <br /> Приходив, не розбудив <br /> Нікогісенько із нас.</p>\n<p align=\"justify\">\n','2008-12-16 10:05:32','http://www.oranta.org/index.php?option=com_content&view=article&id=142:poezija-zenovija-fylypchuka&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Поезія Зеновія Филипчука','uk',808,1788,0,NULL),
(1,1,'<p align=\"justify\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5420,200,na_majdani1.jpg\" />Один iз найповажніших  моральних авторитетів України, колишній предстоятель УГКЦ розповів про  революційний Майдан, враження від Януковича і перспективи України на  майбутнє.</strong><br /><br />Якщо припустимо так висловитись, то  Блаженнійший кардинал Любомир Гузар (колишній глава Української  Греко–Католицької Церкви, котрий у 2011–му добровільно подав у  відставку) — солдат усіх українських революцій. Чи то пак їхній капелан.</p>\n','2014-01-11 20:43:49','http://oranta.org/index.php?option=com_content&view=article&id=1759:pravo-na-povstannja--to-je-zakon-pryrody&catid=37:spilnota-cat&Itemid=49','P',NULL,'Право на повстання — то є закон природи','uk',3938,5421,0,NULL),
(1,1,'<p align=\"justify\"><strong>Коротка історія</strong></p>\n<p align=\"justify\">Наш церковний рік не тільки багатий на різні Богородичні празники, але він також ними починається і завершується. Його відкриває празник Різдва Пресвятої Богородиці, а завершує її славне Успіння, що в наших літургійних книгах має таку величну назву: „Успіння Пресвятої славної Владичиці нашої Богородиці і Приснодіви Марії\".</p>\n','2009-08-26 22:47:15','http://www.oranta.org/index.php?option=com_content&view=article&id=412:uspinnja-presvjatoji-bogorodyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P','','Успіння Пресвятої Богородиці','uk',1760,3243,0,NULL),
(1,1,'<p align=\"justify\"><strong>Коротка історія</strong></p>\n<p align=\"justify\">Наш церковний рік не тільки багатий на різні Богородичні празники, але він також ними починається і завершується. Його відкриває празник Різдва Пресвятої Богородиці, а завершує її славне Успіння, що в наших літургійних книгах має таку величну назву: „Успіння Пресвятої славної Владичиці нашої Богородиці і Приснодіви Марії\".</p>\n','2011-08-26 22:47:15','http://oranta.org/index.php?option=com_content&view=article&id=412:uspinnja-presvjatoji-bogorodyci&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Успіння Пресвятої Богородиці','uk',2557,4040,0,NULL),
(1,1,'<p align=\"justify\"><strong>Чистий четвер</strong> - це день весняного очищення. Ще вдосвіта, до східсонця селяни чистили в стайнях, коморах, на подвір\'ї, в хатах - все повинно бути чистим і виглядати по-святковому. В саду і на городі господар згрібав на купу торішнє листя, бадилля бур\'янів та сухе ріщя і підпалював - «щоб очистити землю від морозу, зими, смерти і всякої нечисти». Робилося це з спеціяльним закликанням:</p>\n','2009-04-16 08:15:44','http://www.oranta.org/index.php?option=com_content&view=article&id=284:chysty-chetver&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Чистий четвер','uk',1363,2337,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"justify\"><strong>Чому так важко перфекціоністам</strong></p>\n<p align=\"justify\"><em>Оправдання для нас заслужив Христос Своїми страстями. Воно дається  нам через Хрищення. Воно уподібнює нас до справедливості Бога, Який  робить нас праведними. Воно має на меті Славу Бога і Христа і дар  вічного життя. Воно є найкращим виявом Божого милосердя (ККЦ, 2020).</em></p>\n','2011-07-08 14:11:18','http://oranta.org/index.php?option=com_content&view=article&id=771:hvoroba-na-imja-lperfekcionizmr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Хвороба на ім’я «перфекціонізм»','uk',2526,4009,0,NULL),
(1,1,'<p align=\"justify\">В книзі Дж. Мак-Дауелла і Б.Хостетлера «Як допомогти молоді» описано, як  матір випадково прочитала щоденник своєї дочки-підлітка, намагаючись  зрозуміти дивну поведінку своєї дитини останнім часом. В щоденнику було  описано сексуальний досвід. Коли матір спробувала поговорити з донькою,  та спочатку неймовірно обурилась, потім «почала плакати, але між  риданнями вона розповіла історію відкинення і жорстокості, зневажливі  погляди однокласників і безсердечні жарти, коли її впихали  в шкафчик і  копали в дверку, як учні обливали її їжею і напоями, коли вона сиділа  одиноко в шкільній їдальні...</p>\n','2011-10-28 09:17:37','http://oranta.org/index.php?option=com_content&view=article&id=801:problemy-pidlitkiv-tysk-i-vidkynennja-odnolitkiv&catid=37:spilnota-cat&Itemid=49','P',NULL,'Проблеми підлітків. Тиск і відкинення однолітків','uk',2593,4076,0,NULL),
(1,1,'<p align=\"justify\">У наш вік, коли на всі боки рекламується блуд, жага до грошей, жорстокість і матеріалізм, жінки і дівчата майже зовсім втратили свою жіночість, скромність і, найважливіше, духовну красу і доброчесність - це стало вже \"не модно\".</p>\n','2009-05-03 20:39:59','http://www.oranta.org/index.php?option=com_content&view=article&id=308:svjato-zhon-myronosyc-vsehrystyjansky-zhinochy-den&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Свято жон-мироносиць - всехристиянський жіночий день','uk',1448,2504,0,NULL),
(1,1,'<p align=\"left\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4514,200,mol.jpg\" />Цими днями         Свята Церква вводить нас у благословенний час Великого посту. Християнська         традиція використовує різні назви для визначення цього періоду: Великий         піст, Чотиридесятниця, Великодній піст, Строгий піст та інші. Це є особливий         час для покаяння, молитви, боротьби з гріховними звичками. Хто як проведе         цей піст, так і зможе пережити і відчути торжество і велич Святого Христового         Воскресіння.</p>\n','2013-03-16 00:43:56','http://oranta.org/index.php?option=com_content&view=article&id=1043:shcho-take-pist-i-jak-pravylno-postyty&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Що таке піст і як правильно постити?','uk',3034,4517,0,NULL),
(1,1,'<p align=\"left\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,images.jpg\" />Гуси на подвір\'ї чітко дотримувалися усталеного  тижневого розпорядку. Того дня вони купалися у ставку, ретельно вичищали  своє пір\'я, обливалися, полоскали собі горлянки, чистили дзьоби, а  потім, крокуючи перевальцем, збиралися скраю подвір\'я, у затінку старої  плакучої верби.</p>\n<p align=\"left\">Тоді-то мудрий, всіма шанований гусак, спадкоємець блискучої традиції,  що йде від Звитяжної Гусячої Спільноти,</p>\n','2013-04-05 23:28:09','http://oranta.org/index.php?option=com_content&view=article&id=1091:gusy-6-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Гуси (6 квітня)','uk',3122,4605,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"left\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5273,200,problema_sleep.jpg\" />Послухай,  сину... Кажу тобі це, коли ти спиш, поклавши ручку під щоку, а русяве  волоссячко розсипалось по твоєму чолі. Ось я увійшов до твоєї кімнати.  Кілька хвилин тому, коли я сидів у бібліотеці і читав, мене наскрізь  пройняли докори сумління. Сповнений почуття провини, підійшов я до твого  ліжка.</p>\n','2013-11-20 23:05:04','http://oranta.org/index.php?option=com_content&view=article&id=1623:batko-zabudko21-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Батько-забудько (21 листопада)','uk',3799,5282,0,NULL),
(1,1,'<p align=\"left\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5194,200,67e9276-3b.jpg\" />Вступне слово</strong></p>\n<p align=\"left\">Як Богонатхнений пастир Митрополит Андрей Шептицький уложив</p>\n<p align=\"left\">молитву за український народ, у якій звертається до Христа-Спасителя</p>\n<p align=\"left\">з різними потребами своєї пастви.</p>\n','2013-10-25 23:50:01','http://oranta.org/index.php?option=com_content&view=article&id=1574:andre-sheptycky-ljak-buduvaty-ridnu-hatur-1941-rik-uryvok-aktualny-i-povchalny-mozhe-prysluhamosja&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Андрей Шептицький - «Як будувати рідну хату»','uk',3712,5195,0,NULL),
(1,1,'<p align=\"right\"><em><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,verbno.jpg\" style=\"margin: 10px; float: right;\" />«Величаємо Тебе,&nbsp; життєдавче&nbsp; Христе, осанна</em><em> в вишніх ми співаємо Тобі. Благословен</em><em> хто йде, у ім\'я Господнє.»</em><o:p></o:p></p>\n<p>Сьогодні мільйони православних християн з вербою в руках, виходять на зустріч Божому Синові і співаючи пісню похвали: «Осанна на висотах Сину Давидів, благословен грядий, у ім\'я &nbsp;Господнє», прославляють Його тріумфальний в\'їзд до м. Єрусалиму, древньої столиці Ізраїлю.<o:p></o:p></p>\n','2014-04-12 22:01:01','http://oranta.org/index.php?option=com_content&view=article&id=2025:vhid-gospodni-v-jerusalym&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Вхід Господній в Єрусалим','uk',4249,5732,0,NULL),
(1,1,'<p align=\"right\"><em><img src=\"http://askoldova-mohyla.org/uk/image,5758,200,know.jpg\" style=\"margin: 10px; float: left;\" /><strong>Це день, що його створив Господь,</strong></em><strong><em> </em><em>возрадуємося і возвеселімося в нім\" (Прокімен Пасхи).</em></strong></p>\n<p>Празник Христового Воскресення багатий не тільки на величні богослуження, маєстатичні співи і глибокосимволічні обряди, але він багатий також на прегарні церковно-обрядові й народні звичаї. Де­які з тих звичаїв — це вплив християнського культу, а инші похо­дять ще з передхристиянських часів, коли наш народ у той самий час відзначав свято привітання весни й весняного сонця. Християн­ська релігія багато з тих прадавніх народних звичаїв освятила, надала їм християнського значення і символіки та визнала своїми.</p>\n','2014-04-19 18:04:43','http://oranta.org/index.php?option=com_content&view=article&id=2052:cerkovno-obrjadovi-zvychaji-velykodnja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Церковно-обрядові звичаї Великодня','uk',4276,5759,0,NULL),
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,Pentecost.png\" />\"Ми виділи світло істинне, ми прийняли Духа небесного, ми знайшли віру істинну, нероздільній Тройці поклоняємося, Вона бо спасла нас\" (Стиховна вечірні П\'ятдесятниці).</em></p>\n<p>Світлий празник Пасхи завершується не менш світлим і вели­ким празником Святої П\'ятдесятниці. Після Христового Різдва і Воскресення цей празник належить до найбільших празників на­шого церковного року. Зіслання Святого Духа — це наче вінець і печать на справі спасення людського роду, що здійснив Божий Син.</p>\n','2013-06-22 23:06:54','http://oranta.org/index.php?option=com_content&view=article&id=1284:praznyk-svjatoji-pjatdesjatnyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Празник Святої П’ятдесятниці','uk',3373,4856,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5365,200,573260_3.jpg\" />«Багато людей святкують празники&nbsp;і знають їх назви,але незнають &nbsp;причин,чому вони встановленні».&nbsp;Св. Іоан Золотоуст</em></p>\n<p>Сьогодні Православна Церква святкує незвичайну подію, а саме - зачаття праведною Анною Пресвятої Богородиці.</p>\n<p>Господь, який Втілився для спасіння роду людського, поступово підготовлював для Себе на землі Пречисту Матір свою - у вибраному потомстві Авраама, в царстві іудейському, в царському племені Давида, в покірливому і праведному сімействі, у високопокірливій і цілком відданій Богу Діві. Праведні її батьки Іоаким і Анна своїм благочестям перевершували своїх сучасників.</p>\n','2013-12-21 23:06:11','http://oranta.org/index.php?option=com_content&view=article&id=1724:v-sviti-nemaje-nijakyh-vypadkovoste-i-vse-zvershujetsja-po-nezminnomu-promyslu-bozhomu-dlja-vsogo-je-vysoka-meta&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'В світі немає ніяких випадковостей і все звершується по незмінному Промислу Божому, для всього є висока мета','uk',3883,5366,0,NULL),
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6477,200,48-2fbe1.jpg\" />\"Архангельським голосом кличемо до Тебе, Пречиста: \"Радуйся благодатна, Господь з Тобою\" (Величання празника)</em>.</p>\n<p>\"Днесь спасення нашого начало і від віку таїнства явління: Син Божий сином Діви стається\"&nbsp;<em>(Тропар празника)</em>.&nbsp;Ось таким похвальним гімном наша Церква вітає празник Благовіщення Пресвятої Богородиці, називаючи його початком нашого спасення. І слушно, бо цей празник належить до найважливіших свят нашого церковного року. Про його важливість говорить уже сама назва в наших богослужбових книгах: \"Благовіщення Пресвятої Владичиці нашої Богородиці і Приснодіви Марії\".</p>\n','2015-04-06 21:04:23','http://oranta.org/index.php?option=com_content&view=article&id=2702:praznyk-blagovishchennja-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Благовіщення Пресвятої Богородиці','uk',5017,6500,0,NULL),
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5406,200,know_your_devotion_085.jpg\" />\"Величаємо Тебе, життєдавче Христе,&nbsp;що задля нас сьогодні народився тілом&nbsp;із Непорочної і Пречистої Діви Марії\"(Величання празника).</em></p>\n<p>20 грудня 386 або 388 року святий Йоан Золотоустий, виголошуючи проповідь у річницю смерти мученика Філогонія, єпископа антіохійського, заповідав своїм вірним, що цього року Антіохія перший раз святкуватиме празник Христового Різдва 25 грудня окремо від празника Богоявлення.</p>\n','2014-01-07 05:45:23','http://oranta.org/index.php?option=com_content&view=article&id=1753:praznyk-hrystovogo-rizdva&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Христового Різдва','uk',3929,5412,0,NULL),
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5406,200,know_your_devotion_091.jpg\" />\"В небі Він один від Одного Єдинородний,&nbsp;а на землі Він один від однієї Діви Єдинородний\"&nbsp;(Святий Йоан Золотоустий).</em></p>\n<p>Празник Христового Різдва показує величний Божий план відкуплення людського роду. Божий Син з безконечної любови до нас стає малою Дитиною. На свою Матір Він вибирає Пречисту Діву Марію. Завдяки Пресвятій Богородиці Божі плани могли стати дійсністю. Без неї не було б Христового Різдва, не було б відкуп­лення.</p>\n','2014-01-08 05:27:13','http://oranta.org/index.php?option=com_content&view=article&id=1751:sobor-presvjatoji-bogorodyci&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Собор Пресвятої Богородиці','uk',3931,5414,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5643,200,6045.jpg\" />…у тюрмі був, і ви прийшли до мене. (Мт.25.36)</em></p>\n<p>Наша держава переживає непростий період на дорозі до будування вільного, демократичного, і, найважливіше, справедливого суспільного життя. У вирі емоційних переживань внаслідок подій, які відбулися і відбуваються дотепер, можливо, ми не мали часу обняти увагою людей, які знаходяться в місцях позбавлення волі.</p>\n','2014-03-19 02:37:56','http://oranta.org/index.php?option=com_content&view=article&id=1948:zvernennja-komisiji-penitenciarnogo-dushpastyrstva-kyjivskoji-arhyjeparhiji-do-svjashchenykiv-ka&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Комісії Пенітенціарного Душпастирства Київської Архиєпархії до священиків КА','uk',4162,5645,0,NULL),
(1,1,'<p align=\"right\"><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6044,200,know_your_devotion_122.jpg\" /><strong>«Прийдіть вірні, заспіваймо духовну пісню і прославмо Христа,</strong></em><em><strong> </strong></em><em><strong>який світло прославив чесного Володимира Великого князя»</strong> (Канон утрені празника).</em></p>\n<p>Поміж великими і світлими мужами нашої княжої держави святий Володимир Великий, наче сонце, сяє своєю невмирущою славою, величчю і заслугами. В історії Руси-України його записано золотими літерами не тільки як могутнього і розумного володаря, але передусім як хрестителя, просвітителя і ревного апостола християнської віри. Свята віра, яку він прийняв у візантійському обряді, стає з часом серцем і душею нашого народу й держави. Вона стає сильною моральною основою нашого народу, основою нашої культури, звичаїв і обрядів.</p>\n','2014-07-28 03:33:30','http://oranta.org/index.php?option=com_content&view=article&id=2315:cvjaty-volodymyr-velyky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Cвятий Володимир Великий','uk',4570,6053,0,NULL),
(1,1,'<p align=\"right\"><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5758,200,know_.jpg\" />\"Христос воскрес із мертвих, смертю смерть подолав, і тим, що в гробах, життя дарував\" (Тропар Пасхи).</strong></em></p>\n<p>З усіх великих празників нашого церковного року найбільш давній, урочистий і радісний — це світлий празник Христового Воскресення. Він, як сказано в ірмосі 8 пісні пасхального канону утрені: \"Цар і Господь, празників празник і торжество торжеств\".</p>\n','2014-04-19 18:10:39','http://oranta.org/index.php?option=com_content&view=article&id=2053:svitly-praznyk-pashy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Світлий Празник Пасхи','uk',4277,5760,0,NULL),
(1,1,'<p align=\"right\"><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6101,200,555.jpg\" />«Величаємо Тебе, Пренепорочна Мати Христа Бога нашого,&nbsp; і славимо Твоє всеславне Успення» (Величання празника).</strong></em></p>\n<p>Предавній, загальний і глибокий культ Пресвятої Богородиці у Східній Церкві залишив особливий слід передусім на нашому літур­гійному році. Церковний рік не тільки багатий на різні Богородичні празники, але він також ними починається і завершується, його починає празник Різдва Пресвятої Богородиці, а завершує її славне Успення, що в наших літургійних книгах має таку величну назву — Успення Пресвятої славної Владичиці нашої Богородиці і Присно­діви Марії.</p>\n','2014-08-27 09:03:38','http://oranta.org/index.php?option=com_content&view=article&id=2378:praznyk-uspinnja-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Успіння Пресвятої Богородиці','uk',4620,6103,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><em>\"Тоді прибув Ісус із Галилеї на          Йордан до Йоана, <br /> щоб хреститися від нього\"<br /> (Мт. З, 13).</em></p>\n<p>Свята Церква перед празником Господнього Богоявлення у богослуженнях звертається до своїх вірних: \"Вифлеєм залишивши, преславне чудо, спішімо до Йордану душею гарячою і там побачимо страшне таїнство\" <em>(Світилен утрені з 3 січня). </em>Це таїнство Господ­нього Богоявлення, це Хрещення Господа нашого Ісуса Христа в ріці Йордані. У цьому таїнстві Ісус Христос об\'являє себе як Месію і Спасителя. Про Його Боже післанництво свідчить при Хрещенні сам Отець Небесний голосом із неба: \"Ти єси Син мій любий, у тобі — моє уподобання\" <em>(Мр. 1, 11); </em>свідчить Святий Дух, що у вигляді голуба сходить на Нього; свідчить і святий Йоан Хрести­тель, вказуючи на Нього: \"Ось Агнець Божий, який гріхи світу забирає\" <em>(Йо. 1, 29).</em></p>\n','2013-01-19 00:45:05','http://oranta.org/index.php?option=com_content&view=article&id=925:praznyk-gospodnogo-bogojavlennja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Господнього Богоявлення','uk',2904,4387,0,NULL),
(1,1,'<p align=\"right\"><img src=\"http://askoldova-mohyla.org/uk/image,6292,200,Andrew-450x345.jpg\" style=\"margin: 10px; float: right;\" />«Величаємо Тебе, святий апостоле Андрію Первозванний, і шануємо святу пам\'ять Твою…» (величання свята)</p>\n<p align=\"right\">Благоволінням Божим кожна країна і кожен народ мають у своїй історії просвітителя. Великою честю для багатьох народів стало перебування на їхній землі учнів Христа Спасителя — святих апостолів. Славить похвалами Рим апостолів Петра і Павла, Індія — Фому, Єгипет — Марка, через яких народи, що перебували у темряві невідання, просвітилися світлом істинного богопізнання.</p>\n','2014-12-12 23:02:23','http://oranta.org/index.php?option=com_content&view=article&id=2549:apostol-andri-pervozvanny-fenomen-v-zhyttja&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Апостол Андрій Первозванний - Феномен в життя','uk',4810,6293,0,NULL),
(1,1,'<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3453,200,22.JPG\" />«Діти – як квіти: поливай, то ростимуть».</p>\n<p>Петро Галуга виховує разом з дружиною Оксаною двох діточок Богданка і Олексійка. У Києві родина живе з 2001 року. А на Аскольдовій Могилі ми маємо змогу бачити їх завжди у гарному настрої та у чудових вишитих українських сорочках.</p>\n','2010-02-23 13:05:26','http://www.oranta.org/index.php?option=com_content&view=article&id=496:dilytys-svitlom-nashoji-viry-rodyna-galuga&catid=25:parafija-cat&Itemid=27','P','','Ділитись світлом нашої віри… Родина Галуга','uk',1972,3455,0,NULL),
(1,1,'<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3479,200,CRW_6644.jpg\" />«Думи мої, думи мої, Лихо мені з вами!</p>\n<p align=\"right\">Нащо стали на папері сумними рядами?..</p>\n<p align=\"right\">Чом вас вітер не розвіяв в степу, як пилину?</p>\n<p align=\"right\">Чом вас лихо не приспало, як свою дитину?..»</p>\n','2010-03-14 20:44:25','http://www.oranta.org/index.php?option=com_content&view=article&id=504:lobnimitsja-zh-braty-moji-molju-vas-blagajur-taras-shevchenko&catid=25:parafija-cat&Itemid=27','P','','«Обніміться ж, брати мої, молю вас, благаю!» - Тарас Шевченко','uk',1997,3480,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3500,200,5.JPG\" />«І ми, як діти, несучи знамена перемоги, Тобі</p>\n<p align=\"right\">Переможцеві смерти кличемо: Осанна на висотах,</p>\n<p align=\"right\">Благословен грядущий в ім’я Господнє»</p>\n<p align=\"right\">( Тропар Квітневої Неділі)</p>\n','2010-03-28 22:05:16','http://oranta.org/index.php?option=com_content&view=article&id=514:verbna-nedilja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','N','','Вербна Неділя на Аскольдовій Могилі','uk',2031,3514,0,NULL),
(1,1,'<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3500,200,5.JPG\" />«І ми, як діти, несучи знамена перемоги, Тобі</p>\n<p align=\"right\">Переможцеві смерти кличемо: Осанна на висотах,</p>\n<p align=\"right\">Благословен грядущий в ім’я Господнє»</p>\n<p align=\"right\">( Тропар Квітневої Неділі)</p>\n','2010-03-28 22:05:16','http://www.oranta.org/index.php?option=com_content&view=article&id=514:verbna-nedilja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Вербна Неділя на Аскольдовій Могилі','uk',2018,3501,0,NULL),
(1,1,'<p align=\"right\"><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3428,200,4.JPG\" />«Як добре у родині життя своє прожити,</p>\n<p align=\"right\">Як добре у родині добро й біду ділити.</p>\n<p align=\"right\">І як радіє Мама, і як радіє Тато,</p>\n<p align=\"right\">Коли в своїй родині так діточок багато.</p>\n<p align=\"right\">Ми є родина, ми є родина,</p>\n<p align=\"right\">Ми є родина у Христі.»</p>\n','2010-02-06 13:26:55','http://www.oranta.org/index.php?option=com_content&view=article&id=482:lbog-zavzhdy-z-namyr-oksana-jasnicka&catid=25:parafija-cat&Itemid=27','P','','«Бог завжди з нами» - Оксана Ясніцька','uk',1954,3437,0,NULL),
(1,1,'<p align=\"right\"><img style=\"float: left;\" src=\"http://www.kruty.org.ua/images/stories/Figol_Atanas_foto_iz_zbirny.jpg\" />(15-ті роковини бою)</p>\n<p align=\"right\">Горе народові, що забуває своїх героїв.</p>\n<p align=\"center\"><strong>ДО ІСТОРІЇ КУЛЬТУ «УКРАЇНСЬКИХ ТЕРМОШЛІВ»</strong></p>\n<p align=\"justify\">У хвилі, коли під Крутами розігралась трагічна дія епопеї відродженої України, мало хто здавав собі справу, який величний безсмертний акт історії звершився в тому дні (29. І. 1918) на полях Чернігівщини. В ім\'я найсвятішої любови Батьківщини, коли не було кому станути на захист кордонів молодої держави, пішла у бій горстка гордих юнаків, щоби своїм чином струснути совістю нації, совістю всіх майбутніх поколінь, доки не звершиться ідеал, якому вони віддали своє молоде життя.</p>\n<p align=\"justify\">\n','2009-01-28 22:03:16','http://www.oranta.org/index.php?option=com_content&view=article&id=197:-atanas-figol-qkrutyq&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Атанас Фіголь - \"Крути\"','uk',1079,2053,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3892,200,IMG_3655.JPG\" />«Вкажи мені , Боже, той шлях, ту Пречисту дорогу до Тебе, аби стати ближче до Тебе. Бо ніде так не є нам безпечно, як з Тобою, Господи!»<em> (із вступу до Хресної Дороги)</em></p>\n<p>Вже традицією стало, в часі Великого посту,&nbsp; молодіжною спільнотою церкви Миколая Чудотворця&nbsp; проводити Хресну Дорогу. Цього року окрім Хресної Дороги в храмі її провели і на схилах Дніпра.</p>\n','2011-04-20 15:26:27','http://oranta.org/index.php?option=com_content&view=article&id=730:molodizhna-hresna-doroga-na-shylah-dnipra&catid=25:parafija-cat&Itemid=27','P',NULL,'Молодіжна Хресна Дорога на схилах Дніпра','uk',2418,3901,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6508,200,10488204.jpg\" />\"Благообразний Йосиф, з древа знявши пречистеє тіло Твоє, плащаницею чистою обвив і ароматами, в гробі новім, покривши, положив\"<em>Тропар Великої п\'ятниці</em></p>\n<p>Богослуження Великої п\'ятниці характеризуються гарними обрядами, зворушливими напівами і глибокими змістом гимнами та стихирами. Головна їхня тема — це муки і смерть Господа нашого Ісуса Христа. \"Днесь висить на дереві, — співаємо на 15 антифоні утрені Великої п\'ятниці, — Той, що на водах землю повісив. Вінцем з терня вінчається — Цар ангелів. У ложну багряницю одягається Той, що небо одягає хмарами. Дістає поличник Той, що в Йордані висвободив Адама. Цвяхами пригвозджений — Жених церкви. Копієм проколений — Син Діви. Поклоняємось страстям Твоїм, Христе, поклоняємося страстям Твоїм, Христе, поклоняємося стра­стям Твоїм, Христе, покажи нам і славне Твоє воскресення\".</p>\n','2015-04-10 09:26:27','http://oranta.org/index.php?option=com_content&view=article&id=2712:velyka-pjatnycja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Велика п\'ятниця','uk',5026,6509,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3566,200,222.jpg\" />За благословенням отця Ігоря Онишкевича</p>\n<p>Все почалося в третьому класі школи, мені тоді було вісім років. Одна дівчинка спитала чи я хрещена. Діти як відомо не брешуть, я сказала “Ні”. Виявилося, що я єдина нехрещена дитина в класі.</p>\n<p>Мама тоді була войовничим атеїстом: “Релігія – це опіум для народу”, бабці діла не було до того, а тітка мене просто любила і не розуміла яка різниця чи є на шиї хрестик, чи ні – головне щоб людина була людиною.</p>\n','2010-06-10 22:52:12','http://oranta.org/index.php?option=com_content&view=article&id=545:jak-ja-ohrestylasja-v-ordani-na-dnipri&catid=25:parafija-cat&Itemid=27','P','','Як я охрестилася в Йордані на Дніпрі','uk',2084,3567,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3922,200,654.JPG\" />\"Христос воскрес із мертвих, смертю смерть подолав,і тим, що в гробах, життя дарував\" (Тропар Пасхи).</p>\n<p><em> </em></p>\n<p>30&nbsp;&nbsp;&nbsp; квітня 2011 року Божого, в Світлу суботу, після Божественної літургії з</p>\n<p>настоятелем церкви Святого Миколая Чудотворця на Аскольдовій могилі ієреєм Ігорем та ієромонахом Олександром, братами Студитського Уставу та вірними парафіянами, однокласниками, рідними сестрами Любов’ю, Вірою, Надією, поїхали на могилу де похований брат Ігор Богуш. Відправивши Панахиду провели чин освячення хреста, який встановили на могилі брата.</p>\n','2011-05-02 19:58:04','http://oranta.org/index.php?option=com_content&view=article&id=744:ldobre-maty-druziv-i-na-nebi--na-zemli-varto-shchob-pershym-sered-nyh-zavzhdy-buv-hrystosr&catid=25:parafija-cat&Itemid=27','P','','«Добре мати друзів і на небі, й на землі. Варто, щоб першим серед них завжди був Христос»','uk',2440,3923,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4205,200,pirogovo.jpg\" />У день, коли Христос воскрес,</p>\n<p align=\"right\">Давайте будемо любить</p>\n<p align=\"right\">Усіх, кого Господь створив,</p>\n<p align=\"right\">Щоби у цьому світі жить.</p>\n<p align=\"right\">У день, коли Христос воскрес,</p>\n<p align=\"right\">Весни квітучая пора,</p>\n<p align=\"right\">Здіймаєм руки до небес</p>\n<p align=\"right\">І просимо добра.</p>\n','2012-04-17 22:10:30','http://oranta.org/index.php?option=com_content&view=article&id=854:polyvany-ponedilok-abo-chudovy-vidpochynok-u-pyrogovo&catid=25:parafija-cat&Itemid=27','P','','Поливаний понеділок або чудовий відпочинок у Пирогово','uk',2724,4207,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4210,200,DSC_1076.JPG\" />Без неї? Ні! Уже ніколи!</p>\n<p align=\"right\">Вона – надія і кермо.</p>\n<p align=\"right\">Хто питомець святої школи –</p>\n<p align=\"right\">Той не піде в гріха ярмо...</p>\n<p align=\"right\">В покорі я складаю руки:</p>\n<p align=\"right\">Ми поруч у житті йдемо.</p>\n<p align=\"right\">Мої тривоги серця звуки,</p>\n<p align=\"right\">Мої степи, мої Карпати</p>\n<p align=\"right\">І втіху стріч і щем розлуки</p>\n<p align=\"right\">Покрий любов’ю Божа мати!</p>\n','2012-04-30 17:42:04','http://oranta.org/index.php?option=com_content&view=article&id=860:den-zhinok-myronosyc-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','День Жінок-Мироносиць на Аскольдовій Могилі','uk',2731,4214,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5658,200,images.jpg\" />\"Хресту Твоєму поклоняємось, Владико, і святеє Воскресення Твоє славимо\" <em>(Тропар утрені)</em>.</p>\n<p>Святий Великий піст — це час особливого внутрішнього й зовнішнього вмертвіння і покути.</p>\n','2014-03-22 23:33:27','http://oranta.org/index.php?option=com_content&view=article&id=1963:hrestopoklonna-nedilja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Хрестопоклонна неділя','uk',4177,5660,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,575302.jpg\" />\"Благообразний Йосиф, з древа знявши пречистеє тіло Твоє, плащаницею чистою обвив і ароматами, в гробі новім, покривши, положив\"</p>\n<p align=\"right\"><em><strong>Тропар Великої п\'ятниці</strong></em></p>\n<p>Богослуження Великої п\'ятниці характеризуються гарними обрядами, зворушливими напівами і глибокими змістом гимнами та стихирами. Головна їхня тема — це муки і смерть Господа нашого Ісуса Христа.</p>\n','2014-04-18 09:40:10','http://oranta.org/index.php?option=com_content&view=article&id=2048:velyka-pjatnycja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Велика п\'ятниця','uk',4272,5755,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,2-1.jpg\" />…<em>Не лякайся й не падай духом, бо Господь, Бог твій, з тобою, куди б ти не подався&nbsp;</em><span style=\"font-size: 0.875em;\">(Книга Ісуса Навина 1, 9).</span></p>\n<p align=\"center\"><em><strong>Дорогі в Христі!</strong></em></p>\n<p>Уже близько року весь наш народ перебуває в паломництві до справжньої свободи. Пройшовши визвольний досвід Революції гідності, ми спільно переносимо трагічний військовий конфлікт, анексію Криму й агресію з боку сусідньої держави на Cході України, переживаємо складну економічну кризу, постійно стоїмо перед викликами в політичній сфері. Як ми можемо мати певність, що Бог є з нами?</p>\n','2014-09-17 13:41:31','http://oranta.org/index.php?option=com_content&view=article&id=2427:synodalne-poslannja-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-vsih-ljude-dobroji-voli-pro-suspilno-politychnu-sytuaciju-v-ukrajini-naperedodni-vyboriv-do-verhovnoji-rady&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Синодальне послання до вірних Української Греко-Католицької Церкви та всіх людей доброї волі про суспільно-політичну ситуацію в Україні  напередодні виборів до Верховної Ради','uk',4675,6158,0,NULL),
(1,1,'<p align=\"right\"><img style=\"margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3254,200,09427.JPG\" alt=\"\" />&laquo;Даваймо разом руки, складімо до Христа,</p>\r\n<p align=\"right\">Нехай радіє небо і вся наша земля.</p>\r\n<p align=\"right\">Даваймо разом нині, скажімо до Христа,</p>\r\n<p align=\"right\">Що ми усі родина Небесного Отця.</p>\r\n<p align=\"right\">Ми є родина, ми є родина,</p>\r\n<p align=\"right\">Ми є родина у Христі!&raquo;</p>','2009-12-08 18:05:11','http://www.oranta.org/index.php?option=com_content&view=article&id=457:rodyna-korotushak-korabel-hrystyjanskoji-ljubovi&catid=25:parafija-cat&Itemid=27','P','','Родина Коротушак.','uk',1899,3382,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,05.jpg\" />\"Величаємо Тебе, життєдавче Христе,          і почитаємо пречистого Твого Тіла преславне Преображення\" (Величання на утрені празника).</em></strong></p>\n<p>Дев\'ятнадцятого серпня наша Церква святкує празник світлого Господ­нього Преображення. Завдання цього празника — звеличувати славну подію Преображення з життя Ісуса Христа, яке деякі святі Отці називають другим Богоявленням. Важливість події видно з того, що її записали аж три євангелисти: Матей, Марко й Лука. Тож погляньмо на подію Преображення, на установлення празника та на дух його богослужби.\n','2013-08-18 20:19:18','http://oranta.org/index.php?option=com_content&view=article&id=1409:praznyk-gospodnogo-preobrazhennja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Господнього Преображення','uk',3518,5001,0,NULL),
(1,1,'<p align=\"right\"><strong><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6247,200,066.jpg\" />\"Прийдіть усі вірні, зійшовшися, похвалімо Христового борця і мужнього світоча Димитрія\" (Стихира на малій вечірні).</em></strong></p>\n<p>Святий Климент Оле­ксан­дрій­ський велич і значення мученика описує такими словами: \"Мученик дає потрійне свідчення: про себе, що він зі справжньою вірністю вірі стоїть при Богові; проти свого противника, що він надаремно наступає на того, що непохитний у любові; для Господа, що в його науці зна­хо­ди­ться Божа пе­ре­кон­ли­ва сила, яку навіть боязнь смерти не може побідити\".</p>\n','2014-11-07 22:02:46','http://oranta.org/index.php?option=com_content&view=article&id=2511:praznyk-svjatogo-velykomuchenyka-dymytrija&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Празник Святого Великомученика Димитрія','uk',4766,6249,0,NULL),
(1,1,'<p align=\"right\"><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3825,200,3.jpg\" />Мені однаково, чи буду </em></strong></p>\n<p align=\"right\"><strong><em>Я жить в Україні, чи ні. </em></strong></p>\n<p align=\"right\"><strong><em>Чи хто згадає, чи забуде </em></strong></p>\n<p align=\"right\"><strong><em>Мене в снігу на чужині – </em></strong></p>\n<p align=\"right\"><strong><em>Однаковісінько мені.</em></strong></p>\n<p align=\"right\"><strong><em>Та не однаково мені, </em></strong></p>\n<p align=\"right\"><strong><em>Як Україну злії люде</em></strong></p>\n<p align=\"right\"><strong><em>Присплять, лукаві, і в огні </em></strong></p>\n<p align=\"right\"><strong><em>Її, окраденую, збудять… </em></strong></p>\n<p><strong><em>Ох, не однаково мені.</em></strong></p>\n','2011-03-13 19:38:37','http://oranta.org/index.php?option=com_content&view=article&id=688:shevchenkove-slovo&catid=25:parafija-cat&Itemid=27','P','','Шевченкове слово…','uk',2350,3833,0,NULL),
(1,1,'<p align=\"right\"><strong><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6177,200,cardinal_dolan_0.jpg\" />Архиєпископ Тімоті Долан</em></strong></p>\n<p>Минулого тижня я телефонував до мого товариша Святослава Шевчука, Верховного Архиєпископа Української Греко-Католицької Церкви.</p>\n<p>Впродовж останніх років я почав все більше поважати цього молодого, відважного брата-єпископа, після того, як ми з ним часто зустрічались в Римі, а особливо коли минулого року я відвідував його з нагоди посвячення Патріаршого Собору Воскресіння Христового в Києві.</p>\n','2014-09-25 16:41:35','http://oranta.org/index.php?option=com_content&view=article&id=2447:2014-09-25-13-42-57&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Продовжуйте виявляти істину! Будь ласка, не підведіть нас!»','uk',4695,6178,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><strong><em>\"Величаємо Тебе,          Пренепорочна Мати Христа Бога нашого, і славимо Твоє всеславне Успення\" (Величання празника).</em></strong></p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://www.truechristianity.info/img/know_your_devotion/know_your_devotion_130.jpg\" alt=\"Успення Пресвятої славної Владичиці нашої Богородиці\" height=\"194\" width=\"139\" />Предавній,          загальний і глибокий культ Пресвятої Богородиці у Східній Церкві залишив          особливий слід передусім на нашому літур­гійному році. Церковний рік не          тільки багатий на різні Богородичні празники, але він також ними починається          і завершується, його починає празник Різдва Пресвятої Богородиці, а завершує          її славне Успення, що в наших літургійних книгах має таку величну назву          — Успення Пресвятої славної Владичиці нашої Богородиці і Присно­діви Марії.</p>\n<p>Хоча празник Успення нагадує нам про сумну подію смерти,          все-таки він належить до радісних празників. Богослуження праз­ника Успення          повне радісних і веселих гимнів. Цього дня свята Церква радіє, бо Пресвята          Богомати з тілом і душею перейшла з туземного життя до вічної слави Свого          Сина, та що з її Успенням ми одержали в небі могутню Заступницю і Покровительку.          Тож погляньмо на її чудесне Успення та на установлення празника Успення.</p>\n','2013-08-27 16:28:26','http://oranta.org/index.php?option=com_content&view=article&id=1425:praznyk-uspinnja-presvjatoji-bogorodyci-pizna-svi-obrjad-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник Успіння Пресвятої Богородиці - Пізнай свій обряд ','uk',3541,5024,0,NULL),
(1,1,'<p align=\"right\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3561,200,2.jpg\" />«Хто їсть моє Тіло і п’є мою Кров, той має життя вічне, і я воскрешу його останнього дня».</strong></p>\n<p>Святе причастя в Божественній Літургій відіграє найважливішу роль, адже під час Богослужіння хліб і вино перетворюється у тіло і кров нашого спасителя Ісуса Христа…</p>\n<p>3 червня у цей величний день вечірню Божественну Літургію служив священик Олексій Геник.</p>\n<p>Своєю проповіддю отець Олексій зрозуміло пояснив значення і культ цього празника:</p>\n','2010-06-07 12:49:28','http://oranta.org/index.php?option=com_content&view=article&id=542:praznyk-presvjatoji-jevharystiji&catid=25:parafija-cat&Itemid=27','P','','Празник Пресвятої Євхаристії','uk',2080,3563,0,NULL),
(1,1,'<p align=\"right\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5608,200,6Zg0AMMu-ao.jpg\" />«Борітеся — поборете!</strong><strong> Вам Бог помагає!</strong><strong> За вас правда, за вас слава</strong><strong> і воля святая!»</strong><strong></strong></p>\n<p>Дев’ятого березня цього року вся Україна та українство світу святкує 200 – ліття з дня народження Тараса Шевченка. В ці трагічні та водночас багатообіцяючі дні, дні боротьби, і дні суспільних змін в нашій країні Тараса Шевченко постає перед нами, як геній – пророк.</p>\n','2014-03-10 15:16:58','http://oranta.org/index.php?option=com_content&view=article&id=1921:uvikovichnennja-kobzarja-na-askoldovi&catid=25:parafija-cat&Itemid=27','P','','Увіковічнення Кобзаря на Аскольдовій','uk',4126,5609,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6055,200,481-9-4.jpg\" />Особлива позиція А. Шептицького та його оточення, т. зв. «кола Св. Юра», в роки німецької окупації стала підставою для сприйняття керівництва Греко-католицької церкви на тогочасній політичній арені як окремої політичної сили, на рівні з УЦК та ОУН. </strong></p>\n<p>Не дивлячись на щорічні міжнародні конференції, розмаїття сучасної літератури різних жанрів, радіо- і теле- передач, оцінка постаті глави Греко-католицької церкви (ГКЦ) Андрея Шептицького, а особливо його релігійної та суспільно-політичної діяльності в роки Другої світової війни, залишаються серед найбільш дискусійних тем не лише в середовищі українських та польських істориків, а й громадськості обох країн.</p>\n','2014-07-29 12:15:44','http://oranta.org/index.php?option=com_content&view=article&id=2348:mytropolyt-andre-sheptycky-ta-ukrajinsky-nacionalny-ruh&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Митрополит Андрей Шептицький та український національний рух','uk',4573,6056,0,NULL),
(1,1,'<p align=\"right\"><strong>«Ні про що не журіться, але в усьому появляйте Богові ваші прохання молитвою і благанням з подякою» (Фил.4 ,6)</strong></p>\n<p>Завдяки пожертві від організації&nbsp; “Ліс” пані Ніни Харчук було надано допомогу дітям Божого Милосердя для придбання шкільного приладдя. Кожна дитина, згідно своїх потреб, отримала необхідне для навчання. А в неділю 4 вересня 2011 р. о 12:30 відправлена Божественна Літургія і уділено Благословення для успішного навчання дітей, студентам, вчителям.</p>\n','2011-10-05 11:56:01','http://oranta.org/index.php?option=com_content&view=article&id=794:lshkilny-portfelykr-ditjam-bozhogo-myloserdja-veresen-2011-rb&catid=25:parafija-cat&Itemid=27','P','','«ШКІЛЬНИЙ ПОРТФЕЛИК» ДІТЯМ БОЖОГО МИЛОСЕРДЯ вересень 2011 р.Б.','uk',2580,4063,0,NULL),
(1,1,'<p align=\"right\"><strong>Першому українськму мученику за віру Христову, Аскольду - князю київському і всієї Руси України присвячується</strong></p>\n<p>Достеменно відомо, що після Зіслання Святого Духа учні Ісуса Христа, святі апостоли пішли світом ширити правдиве слово Божої науки. Волею Всевишнього на українську землю прибув старший брат апостола Петра - Андрій Первозванний. Саме від нього давні українці отримали Боже благословення бути вибраними в числі перших народів землі, осяяних ласкою Творця пізнати Світло Істини. Щоб правдиво вірити і правильно славити життя і діяння Сина Божого.</p>\n','2009-11-23 09:27:09','http://www.oranta.org/index.php?option=com_content&view=article&id=456:obrani-viryty&catid=35:istorychni-postati-cat&Itemid=48','P','','Обрані вірити','uk',1886,3369,0,NULL),
(1,1,'<p align=\"right\">Є і між дарами різниця, але Дух той самий; &nbsp;різниця теж і між служінням, але Господь той самий. Різниця є і між ділами, але Бог той самий, який усе в усіх робить. &nbsp;(I Кор. 12, 4-6)</p>\n<p><strong>Дорогі в Христі, </strong>«Крила Надії» продовжує своє служіння, душпастирська праця та домашня опіка з 1998 року разом з о. Анатолієм та волонтерами, кожну першу п’ятницю місяця відвідують людей похилого віку, які терплять фізично і духовно, просять&nbsp; про духовну науку та підтримку, вони потребують сторонньої допомоги.</p>\n','2012-05-09 13:30:24','http://oranta.org/index.php?option=com_content&view=article&id=862:lkryla-nadijir&catid=25:parafija-cat&Itemid=27','P','','«Крила Надії»','uk',2738,4221,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p align=\"right\">Тож покайтеся і наверніться, щоб ваші гріхи були стерті&nbsp; (Ді 3,19)</p>\n<m:defjc m:val=\"centerGroup\"> <m:smallfrac m:val=\"off\"> <m:dispdef> <m:lmargin m:val=\"0\"> <m:rmargin m:val=\"0\"> <m:defjc m:val=\"centerGroup\"> <m:wrapindent m:val=\"1440\"> <m:intlim m:val=\"subSup\"> <m:narylim m:val=\"undOvr\"> </m:narylim></m:intlim> </m:wrapindent>\n<p>Апостол Петро звинувачує євреїв у боговбивстві й невдячності стосовно до Ісуса Христа. Ті, що були присутні при цьому, починають плакати й кажуть: «Що маємо робити, щоб ми могли отримати прощення?» Святий Петро втішає їх і вказує, що єдина їхня надія й спасіння є в Ісусі Христі, Який помер за гріхи всіх людей і воскрес. Тож покайтеся і наверніться, щоб ваші гріхи буди стерті» (Дії 3,19).</p>\n','2010-02-17 19:49:55','http://www.oranta.org/index.php?option=com_content&view=article&id=494:propovid-svjatogo-oana-vianneja-pro-pokajannja-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Проповідь Святого Йоана Віаннея про покаяння ','uk',1968,3451,0,NULL),
(1,1,'<p class=\"MsoNormal\"><span lang=\"UK\">Щонеділі о 14:00 у крипті церкви, що на Аскольдовій могилі відбуваються зустрічі молоді. <o:p></o:p></span></p>\n<p class=\"MsoNormal\"><span lang=\"UK\">Декілька слів про теми зустрічей:<o:p></o:p></span></p>\n<p class=\"MsoNormal\"><span lang=\"UK\">14.12<o:p></o:p></span></p>\n<p class=\"MsoNormal\"><span lang=\"UK\">Відбулася зустріч на тему: ,, Чи повинні ми вшановувати святих?”<o:p></o:p></span></p>\n<p class=\"MsoNormal\"><span lang=\"UK\">Метою зустрічі було обговорення чи потрібно нам вшановувати святих, яке вони мають значення у житті кожного християнина.</span></p>\n<p class=\"MsoNormal\">\n','2008-12-18 22:20:01','http://www.oranta.org/index.php?option=com_content&view=article&id=149:zibrannja-molodi&catid=25:parafija-cat&Itemid=27','P',NULL,'Зібрання молоді','uk',827,1807,0,NULL),
(1,1,'<p class=\"NoSpacing\"><em><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_o_nagirnyak.jpg.jpg\" height=\"117\" width=\"164\" />Отець Андрій Нагірняк, керівник Відділу соціальних питань Патріаршої курії, відповів на запитання Департаменту інформації УГКЦ.</em></p>\n<p class=\"NoSpacing\"><strong>– Отче Андрію, якось ви сказали, що соціальне служіння – це те, що може об’єднати різні конфесії України. Чому?</strong></p>\n','2013-07-05 11:46:05','http://oranta.org/index.php?option=com_content&view=article&id=1317:lcherez-dobri-spravy-lezhyt-doroga-do-poglyblennja-viryr--o-andri-nagirnjak&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Через добрі справи лежить дорога до поглиблення віри», – о. Андрій Нагірняк','uk',3413,4896,0,NULL),
(1,1,'<p class=\"Style9\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,1340296119_37476324322.jpg\" />Серед багатьох обраних, що перебували у великому Божому домі, проживала Солодка Свята. Молода Свята була цікавою особою, вона раз у раз поглядала вниз, на пекельну твердь, де в жахливій тісняві знемагали засуджені на вічні муки. Дуже неспокійно було у неї на серці.</p>\n<p class=\"Style9\">Серед тих, що терпіли, зауважила юнака з блакитними очима.</p>\n','2013-03-05 12:35:18','http://oranta.org/index.php?option=com_content&view=article&id=1014:solodka-svjata-5-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Солодка Свята (5 березня)','uk',2991,4474,0,NULL),
(1,1,'<p class=\"bodytext\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5034,200,images.jpg\" />Про Божу мудрість наших предків,   світоглядний вимір київського християнства та різні погляди на нього,   потребу духовної переміни українського суспільства, роль освячення   Патріаршого собору в Києві та шлях майбутнього розвитку УГКЦ, напрямні   єднання українських Церков київської традиції, глобалізаційні виклики,   пошук нових підходів в особистому житті людини та власний триб життя   сьогодні розповідає глава УГКЦ блаженніший Святослав Шевчук.</strong></p>\n','2013-08-30 13:54:18','http://oranta.org/index.php?option=com_content&view=article&id=1430:jak-vidnaty-sebe&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Як віднайти себе','uk',3552,5035,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"introduction\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3836,200,1293802404_img_1652_2_1.jpg\" />Поданий нижче текст – це роздуми мирянина про обрання нового  Глави Української Греко-Католицької Церкви, яке розпочнеться 21 березня  цього року. Церква, як відомо, належить вічності та, згідно з обітницею  Божою, є неподоланною. Але свій план Господнє Провидіння реалізовує  через людей – земних, а тому часом недосконалих і суб’єктивних. Таїнство  поєднання одного і другого якраз і відбуватиметься в УГКЦ під час  виборчого Синоду.</p>\n','2011-03-16 08:28:08','http://oranta.org/index.php?option=com_content&view=article&id=691:ukrajina-poviryt-u-sebe-lyshe-todi-koly-poviryt-u-sebe-jiji-cerkva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Україна повірить у себе лише тоді, коли повірить у себе її Церква','uk',2354,3837,0,NULL),
(1,1,'<p class=\"introduction\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3847,200,321.jpg\" />Новообраний глава  Української греко-католицької церкви, Блаженніший Святослав Шевчук, який  народився у Стрию на Львівщині, є одним із наймолодших єпископів у  світі. Єпископський синод обрав його главою, як він каже сам,  «глобальної церкви». Українська греко-католицька церква налічує понад 4  мільйони віруючих по всьому світі, вона є найбільшою католицькою церквою  візантійського обряду. У суботу, за день до інтронізації, в дорозі зі  Львова до Києва Блаженніший Святослав дав ексклюзивне інтерв’ю Радіо  Свобода.</p>\n<p><strong>– На сьогоднішній день  закінчився певний етап відродження Української греко-католицької церкви.  Ця церква сьогодні є повноправною і в Україні вона знову є справжньою  церквою. В чому Ви бачите її основну мету, в чому полягає її зміст?</strong>\n','2011-03-28 08:18:43','http://oranta.org/index.php?option=com_content&view=article&id=701:my-je-globalnoju-cerkvoju-novoobrany-glava-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Ми є глобальною церквою – новообраний глава УГКЦ ','uk',2365,3848,0,NULL),
(1,1,'<p class=\"itemIntroText\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4921,200,12066.jpg\" />Один китайський імператор урочисто присягнув: «Я позбудуся усіх  своїх ворогів – жодного не буде у моїй державі!».&nbsp;Через якийсь час  піддані побачили, як він прогулюється в імператорському парку зі своїми  ворогами, беручи їх за руки, сміючись та жартуючи.</p>\n<p class=\"itemIntroText\">- Хіба ти не обіцяв, що позбудешся ворогів? – запитав один із придворних.</p>\n<p class=\"itemIntroText\">- А чи я їх не позбувся? Я зробив з них друзів! – відповів імператор.</p>\n','2013-07-23 21:31:11','http://oranta.org/index.php?option=com_content&view=article&id=1337:prysjaga-24-lypnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Присяга (24 липня)','uk',3460,4943,0,NULL),
(1,1,'<p class=\"jcepopup\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5238,200,svyatoslav_lutsk.jpg\" />Сьогодні питання християнської моралі стоїть у центрі суспільної дискусії. Таку думку висловив Глава УГКЦ Блаженніший Святослав на презентації українського перекладу книги Серве Теодора Пінкерса «Джерела християнської моралі, її метод, зміст, історія» у Національному університеті «Києво-Могилянська академія»</p>\n','2013-11-07 23:13:30','http://oranta.org/index.php?option=com_content&view=article&id=1605:lsogodni-pytannja-hrystyjanskoji-morali-stojit-u-centri-suspilnoji-dyskusijir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Сьогодні питання християнської моралі стоїть у центрі суспільної дискусії»','uk',3756,5239,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,000bw3x7.jpg\" /><strong>Перемогти страх, відмовитися від власних схем і переконань, щоб  відкритися до горизонтів Бога. І сказати «ні» відособленості,  винятковості, паралельним шляхам, які несуть поділ, – сказав Папа  Франциск у проповіді під час Літургії на Урочистість П’ятидесятниці. Після читання Євангелія Папа сказав:&nbsp;</strong></p>\n<p>Дорогі брати і сестри, в цей день ми споглядаємо і знову переживаємо у літургії вилив Святого  Духа, посланого Воскреслим Христом на Свою Церкву; подія благодаті, що  наповнила єрусалимську світлицю, щоб поширитися по всьому світу.</p>\n','2013-06-22 15:37:52','http://oranta.org/index.php?option=com_content&view=article&id=1213:den-svjatoji-trici-pjatydesjatnycja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'День Святої Трійці. П’ятидесятниця','uk',3369,4852,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_691571.jpg\" height=\"131\" width=\"180\" />Перше, про що хотілося б сказати, коротко  розмірковуючи на цю багатогранну тему: безсумнівно, святий апостол Петро  відігравав верховну роль серед апостолів у стародавній Церкві. При  перераховуванні апостолів він часто називається першим і завжди бере  ініціативу, як, наприклад, проповідуючи в день П\'ятидесятниці або під  час риболовлі після Воскресіння нашого Господа.</p>\n','2013-05-23 00:12:07','http://oranta.org/index.php?option=com_content&view=article&id=1221:zvidky-pohodyt-verhovenstvo-papy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Звідки походить верховенство Папи?','uk',3271,4754,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_700760.jpg\" height=\"109\" width=\"149\" />Загальну аудієнцію 12 червня 2013 р., яка відбулась на площі Святого  Петра у Ватикані, і в якій взяло участь понад сімдесят тисяч паломників з  усього світу, Папа Франциск присвятив визначенню Церкви як Божого Люду,  як це окреслив ІІ Ватиканський Собор, і як говорить Катехизм  Католицької Церкви. Святіший Отець поставив ряд запитань, над якими, за  його словами, кожен зможе призадуматись.</p>\n','2013-06-13 01:26:37','http://oranta.org/index.php?option=com_content&view=article&id=1248:cerkva--misce-bozhogo-myloserdja--nadiji&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Церква – місце Божого милосердя й надії','uk',3329,4812,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/6a8d71abab9f3810320f6a7067002b1e.jpg\" height=\"121\" width=\"167\" />Минулий тиждень на європейському континенті ознаменував собою новий  етап у проведенні нової ліберальної культурної революції – гендерної.  Найстаріша демократія континенту – Франція, практично перетворилася на  ліберальну диктатуру: її <a href=\"http://catholicnews.org.ua/prezident-frantsiyi-pidpisav-zakon-pro-odnostatevi-shlyubi#point\">президент  Франсуа Олланд таки підписав закон, який зрівнює в правах  гомосексуальні пари з нормальними сім’ями, і це попри мільйонні протести  французів</a>.</p>\n','2013-05-20 15:31:24','http://oranta.org/index.php?option=com_content&view=article&id=1212:-abo-my-jevangelizujemo-suspilstvo-abo-zavtra-nas-peresliduvatymut&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Або ми євангелізуємо суспільство, або завтра нас переслідуватимуть','uk',3257,4740,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/timthumb.php__0.jpg\" height=\"128\" width=\"176\" /><em><strong>Пане Анатолію, минулої неділі в центрі Львова відбулася  хода та імпровізовані мітинги священиків та монахинь, яких більшість  львів’ян асоціюють з греко-католицькою церквою. Чи відомо вам про цю  подію і, якщо так, то що саме?</strong></em></p>\n<p class=\"news-picture\">Такі заходи відбуваються не вперше, вони є доволі регулярними, оскільки  група Антоніна Догнала ними намагається привернути увагу до себе.</p>\n','2013-06-15 21:08:11','http://oranta.org/index.php?option=com_content&view=article&id=1254:jak-zvychanym-ljudjam-vidriznyty-istynu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Як звичайним людям відрізнити істину?','uk',3335,4818,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/rtemagicc_shevchuk_den2.jpg.jpg\" height=\"176\" width=\"128\" /><em>7 червня у Києві відбувся круглий стіл «Україна в  православно-католицькому діалогові». У його роботі брав участь кардинал  Курт Кох, президент Папської ради сприяння єдності християн та  співголова змішаної Міжнародної комісії з богословського діалогу між  Католицькою і Православною Церквами. Вашій увазі пропонуємо слово, яке  на цьому заході виголосив Блаженніший Святослав, Глава УГКЦ.</em></p>\n<p class=\"news-picture\">Торік, у жовтні, ми, члени Синоду Єпископів Католицької Церкви, разом  зі Святішим Отцем Венедиктом XVI згадували 50-річчя початку діяльності  ІІ Ватиканського Собору.</p>\n','2013-06-17 10:33:31','http://oranta.org/index.php?option=com_content&view=article&id=1259:2013-06-17-07-36-49&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Той, хто скаже, що вірує в Бога, але не шукає єдності між християнами, той неправдомовець»','uk',3342,4825,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_700436.jpg\" height=\"164\" width=\"226\" />Зображення на Туринській Плащаниці було утворено, ймовірно, завдяки  електричному розряду, і її вік, за даними останніх наукових досліджень,  співвідноситься з епохою Христа. Ці два висновки знайшли відображення в  доповіді професора механіко-термічних досліджень університету Падуї  Джуліо Фанті. Відомий дослідник Туринської Плащаниці був запрошений в  Кельн з нагоди Німецького Національного Євхаристійного конгресу. В  інтерв\'ю Ватиканському радіо професор розповів про результати нових  досліджень:</p>\n','2013-06-12 12:03:35','http://oranta.org/index.php?option=com_content&view=article&id=1247:turynska-plashchanycja-novi-doslidzhennja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Туринська Плащаниця: нові дослідження','uk',3328,4811,0,NULL),
(1,1,'<p class=\"news-picture\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/huzar.jpg\" height=\"187\" width=\"136\" />Мусимо вирішити, чи хочемо ми почати з прощення або ж з  визначення того, хто більше згрішив. В такому випадку знайдемо багато  злих речей з обох сторін. Єдиним виходом однак є воля простити не лише з  однієї сторони, – вважає кардинал Любомир Гузар. На думку Верховного  Архиєпископа-емерита Києво-Галицького УГКЦ, це добре, що дійшло до  суперечок навколо відзначення 70-ої річниці Волинської Трагедії. «Для  мене це – Боже благословення, що дійшло до розпалення емоцій. Теперішня  ситуація спонукає нас до того, що ми мусимо щось зробити. Українці і  поляки повинні задуматись: Що далі? Чи маємо надалі воювати? В історії  ми вже наробили достатньо дурниць. Чи маємо так жити і надалі? Я вважаю,  що в цьому немає сенсу», – говорив кардинал Гузар в інтерв’ю <a target=\"_blank\" href=\"http://ekai.pl\">польській агенції КАІ</a>.\n','2013-05-22 13:49:22','http://oranta.org/index.php?option=com_content&view=article&id=1217:ljubomyr-guzar-musymo-vyrishyty-chy-hochemo-pochaty-z-proshchennja-chy-zh-z-vyznachennja-togo-hto-bilshe-zgrishyv&catid=37:spilnota-cat&Itemid=49','P',NULL,'Любомир Гузар: Мусимо вирішити чи хочемо почати з прощення чи ж з визначення того, хто більше згрішив','uk',3264,4747,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/1896e77d7a.jpg\" height=\"142\" width=\"98\" />«Примирення між нашими Церквами допоможе порозумітися  російському і українському народам. Як це часто буває між сусідами, у  нас є багато взаємних історичних проблем, але ми не можемо будувати  майбутнього без християнського спілкування. Процес примирення також  допоможе подолати українофобію в Росії та зупинить процес русифікації  України», - <a href=\"http://ekai.pl/wydarzenia/temat_dnia/x66767/kierujemy-do-braci-z-polski-prosbe-o-wybaczenie/?page=1\" target=\"_blank\">про це сказав Блаженніший Святослав (Шевчук), Глава УГКЦ, в інтерв’ю для КАІ (Католицької інформаційної агенції)</a>.</p>\n','2013-05-14 09:14:44','http://oranta.org/index.php?option=com_content&view=article&id=1200:2013-05-15-06-21-55&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Наша Церква є більмом в оці для всіх тих, хто не прагне справжньої єдності…» - Глава УГКЦ для КАІ ','uk',3240,4723,0,NULL),
(1,1,'<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/typo3temp/pics/73e33c9db3.jpg\" height=\"161\" width=\"111\" /></p>\n<p>З 26 по 30 червня Глава Української Греко-Католицької Церкви  Блаженніший Святослав (Шевчук) перебуватиме з офіційним візитом у  Республіці Польща (РП).</p>\n<p>Зокрема, 27 червня у Варшаві Предстоятель УГКЦ візьме участь у  Панахиді з нагоди 70-х роковин Волинської трагедії, на якій також будуть  архиєпископ Юзеф Ковальчик, Примас Польщі, кардинал Казімєж Нич,  Митрополит Варшавський, і Президент Польщі Броніслав Коморовський. Після  молитви Глава УГКЦ матиме особисту зустріч із Президентом Польщі.</p>\n','2013-06-21 14:52:59','http://oranta.org/index.php?option=com_content&view=article&id=1280:2013-06-21-11-54-35&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Головне моє послання можна було б висловити словами воскреслого Ісуса Христа: “Мир вам!”»','uk',3367,4850,0,NULL),
(1,1,'<p class=\"news-single-img\"><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/ec60b3c84a.jpg\" height=\"118\" width=\"80\" />«Наш народ і суспільство сьогодні можна порівняти з учнями  Ісуса Христа, що знову переживають втрату надії, розчарування та  безсилля, споглядаючи на свого зневаженого та розп’ятого Вчителя. Ми  сподівалися на відродження вільної держави, а натомість так часто  зазнаємо гіркоти розчарувань та досвідчуємо крах власних ілюзій». Про це  пише Глава Української Греко-Католицької Церкви Блаженніший Святослав  (Шевчук) у <a href=\"http://www.ugcc.org.ua/2722.0.html\">Великодньому посланні</a> до духовенства та всіх вірних УГКЦ.</p>\n','2013-04-30 00:09:18','http://oranta.org/index.php?option=com_content&view=article&id=1172:2013-04-29-21-14-25&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Сьогодні український народ можна порівняти з учнями Ісуса Христа»','uk',3205,4688,0,NULL),
(1,1,'<p class=\"news-submitted\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/945222_543535225710499_1928997517_n.jpg\" height=\"146\" width=\"106\" />Для багатьох обивателів західного цивілізаційного простору події, які  увійшли в історію як «Арабська весна», виглядали тріумфом демократії на  пригнічених диктаторами просторах. Падіння режимів у Тунісі, Лівії,  Єгипті ЗМІ розмальовували не інакше, як прихід демократичної  справедливості в останні оплоти диктаторського мракобісся. Але завжди  замовчували той факт, що сили, які очолювали ці революційні  перетворення, насправді є радикальними ісламістськими організаціями.</p>\n','2013-06-03 12:36:39','http://oranta.org/index.php?option=com_content&view=article&id=1229:petre-chy-ljubysh-ty-mene&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Петре, чи любиш ти мене?','uk',3291,4774,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"news-submitted\"><img style=\"margin: 10px; float: right;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/film_01.staticheskie001.jpg\" height=\"136\" width=\"187\" /><strong><em>Ваше Блаженство, наше суспільство перебуває в глибокій  кризі, багато хто говорить, що це криза духовна, і вбачає в ній  відображення кризи української родини, сім’ї як родинного осередку, і,  перш за все, як домашньої церкви, яка, з поміж іншого, перестала бути  осередком спільної молитви, перестала бути місцем, де підростаюче  покоління перебирає естафету віри від своїх батьків. Чи, на Вашу думку,  це відповідає дійсності?</em></strong></p>\n','2013-05-20 15:26:04','http://oranta.org/index.php?option=com_content&view=article&id=1211:-duhovna-kryza-pochynajetsja-z-braku-ljubovi-u-simji--blazhennishy-ljubomyr-guzar&catid=37:spilnota-cat&Itemid=49','P',NULL,' Духовна криза починається з браку любові у сім’ї, – Блаженніший Любомир Гузар','uk',3256,4739,0,NULL),
(1,1,'<p class=\"news-title\"><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_693557.jpg\" height=\"127\" width=\"174\" />Новизна, гармонія та місія – три слова, пов’язані з дією Святого Духа,  стали темою проповіді, яку Папа Франциск виголосив під час урочистого  Євхаристійного богослуження з нагоди Урочистості Зіслання Святого Духа. У  неділю, 19 травня 2013 р., що за новим стилем була неділею  П’ятидесятниці, понад 250 тисяч членів різних мирянських рухів, спільнот  та організацій, як також звичайних паломників, знову заповнили площу  Святого Петра у Ватикані та вулицю Кончіліаціоне.</p>\n','2013-05-19 22:01:12','http://oranta.org/index.php?option=com_content&view=article&id=1208:-papa-francysk-cerkovnist-je-fundamentalnoju-harakterystykoju-kozhnogo-hrystyjanyna&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Папа Франциск: церковність є фундаментальною характеристикою кожного християнина','uk',3252,4735,0,NULL),
(1,1,'<p class=\"news_anons\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4444,200,20130212163751.jpg\" />Про те, хто може стати новим Папою Римським та чи отримає УГКЦ  патріархальний устрій журналістам розповів Архієпископ-емерит  Української греко-католицької церкви, Кардинал Любомир (Гузар).</p>\n<p class=\"NoSpacing\"><strong>Блаженніший, Ви оголосили про відхід з глави церкви 10 лютого 2011 року. Звернення Папи Бенедикта </strong><strong>XVI</strong><strong> про зречення папського престолу підписане 10-тим лютим 2013 року. Може, в цьому є якийсь символізм?</strong></p>\n','2013-02-13 02:12:21','http://oranta.org/index.php?option=com_content&view=article&id=997:papoju-mozhe-staty-i-ukrajinec&catid=32:gk-svit-cat&Itemid=58','P','','Папою може стати і українець','uk',2965,4448,0,NULL),
(1,1,'<p class=\"nimages\"><img style=\"margin: 10px; float: left;\" src=\"http://dyvensvit.org/media/gallery/intxt/j/a/jakby8hrystos8ne8voskres.jpg\" /> <strong class=\"main\">Християни вірять, що Христос, прийшовши в наш уражений  гріхом світ, не міг не померти, а померши, не міг не воскреснути. Для  того Він - Бог - і став людиною. Уявити, що Він не воскресав - це все  одно, що уявити, ніби Він і не народжувався.</strong><br />Уявити таке можливо лише в рамках фантастики. І, до речі, деякі  фантасти справді зверталися до цієї теми. Для більшості з них як людей  нецерковних відсутність факту Воскресіння Христового означає, передусім,  відсутність Церкви.</p>\n','2013-05-12 13:19:24','http://oranta.org/index.php?option=com_content&view=article&id=1197:shcho-bulo-b-iz-nashoju-cyvilizacijeju-jakby-hrystos-ne-voskres&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Що було б із нашою цивілізацією, якби Христос не воскрес?','uk',3234,4717,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"p1\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5022,200,IMG_0842.jpg\" />Головною подією у святкуванні 1025-річчя хрещення  України-Русі для Української греко-католицької церкви стало освячення  патріаршого собору Воскресіння Христового у Києві. Понад двадцять тисяч  вірних із різних країн світу прибули, аби побачити омріяний символічний  храм єдності УГКЦ, робота над яким розпочалася ще 2002 р. Про важливість  новозведеного собору, ймовірність визнання патріархату, прагнення  об\'єднати Київську церкву, намір перепоховати кардинала Йосипа Сліпого —  у розмові з \"великим будівничим\" Української греко-католицької церкви  кардиналом <a href=\"http://gazeta.dt.ua/internal/pro-svobodu.html\">Любомиром Гузаром</a>.</strong></p>\n','2013-08-27 15:49:07','http://oranta.org/index.php?option=com_content&view=article&id=1424:blazhennishy-ljubomyr-guzar-pro-onovlennja-hrystyjanstva&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Блаженніший Любомир Гузар: про оновлення християнства','uk',3540,5023,0,NULL),
(1,1,'<p class=\"rtejustify\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3879,200,mh600_s.jpg\" />Такого вибору нового Глави Української Греко-Католицької Церкви не  прогнозував жоден експерт. Ніхто з них не думав, що Синод зупиниться на  наймолодшому за віком владиці — Святославу (Шевчуку). Але про такий  вибір говорили миряни. І напевно очікували, якщо дивитися по численній  позитивній реакцій.</p>\n<p class=\"rtejustify\">Чому той, про якого фактично не згадували експерти, став таким бажаним  предстоятелем УГКЦ? Спробуємо вияснити чому він і спрогнозувати що далі,  застосувавши традиційний для нововибраного поділ на три пункти, щоби  бути чітким.</p>\n','2011-04-11 13:30:57','http://oranta.org/index.php?option=com_content&view=article&id=720:novy-glava-ugkc-ce-vseroz-i-nadovgo&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Новий Глава УГКЦ: це всерйоз і надовго','uk',2397,3880,0,NULL),
(1,1,'<p class=\"rtejustify\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6257,200,guard.jpg\" />Ідеалом християнського життя у спільноті та світі є мир – особлива душевна настанова, яка полягає в гармонійному й належному стосунку особи до Бога, до самої себе, до інших людей та до всього творіння. Мир є одним з ключових понять Христової Благовісті: «Мир вам!» (Лк 24,36); «Мир залишаю вам, мій мир даю вам» (Ів 14,27, див. також: Мт 10,13; Мр 5,34; 9,50; Лк 1,79; 2,14). Мир між людьми невіддільно пов’язаний з любов’ю до ближніх: «Бо заповіді: “Не чини перелюбу”, “Не вбивай”, “Не кради”, “Не пожадай”, і всякі інші заповіді містяться у цьому слові: “Люби твого ближнього як себе самого”» (Рм 13,9) (Див.: 1 Ів 2,9-11; 3,10-24; 4,7-21).<o:p></o:p></p>\n','2014-11-17 19:40:17','http://oranta.org/index.php?option=com_content&view=article&id=2518:hrystyjansky-pogljad-na-vinu-myr-ta-samozahyst&catid=37:spilnota-cat&Itemid=49','P',NULL,'Християнський погляд на війну, мир та самозахист','uk',4775,6258,0,NULL),
(1,1,'<p class=\"text\"><img src=\"http://askoldova-mohyla.org/uk/image,4345,200,vvedennia.jpg\" style=\"float: left; margin: 10px;\" />Празники в честь Пресвятої. Богородиці в нашому церковному календарі займають перше місце після празників Господських. Головне завдання Богородичних празників – це показати нам велич, гідність і святість Пречистої Діви Марії, Її роль у відкупленні людського роду та заохотити нас до її вшанування і наслідування. Найбільші Богородичні празники, такі як: Різдво й Успіння, говорять нам про перші та останні хвилини Її життя на землі. Одинокий празник, котрий нам висвітлює дитинство Пресвятої Богоматері - це празник Введення в храм, який святкуємо 4 грудня.</p>\n','2012-12-04 02:26:34','http://oranta.org/index.php?option=com_content&view=article&id=907:vvedennja-v-hram-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Введення в храм Пресвятої Богородиці','uk',2863,4346,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p class=\"text\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4902,200,johnthebaptist.jpg\" />«Істинно кажу вам, що між народженими від жінок не було більшого від Івана Хрестителя» (Мат. 11, 11)</p>\n<p class=\"text\">Між  різними Святими, що їх почитає Східна Церква особливим культом  славиться св. Іван Хреститель, Господній Предтеча. Він єдиний з усіх  Святих після Божої Матері, що має свій празник Зачаття і празник Різдва.  Про його великий культ у Східній Церкві найкраще свідчать аж шість  празників у його честь на протязі церковного року: Празник Зачаття,  Різдва, Усікновення, Перше і Друге Знайдення Голови, Третє Знайдення  Голови і Собор після празника Богоявлення. — Тож погляньмо на його особу  й на його культ у Східній Церкві.</p>\n','2013-07-07 14:06:39','http://oranta.org/index.php?option=com_content&view=article&id=1321:praznyky-sv-ivana-hrestytelja&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Празники св. Івана Хрестителя','uk',3420,4903,0,NULL),
(1,1,'<p class=\"text\"><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2012/06/29.06_%D0%BF%D0%B5%D1%82%D1%80%D0%B0%D0%BF%D0%B0%D0%B2%D0%BB%D0%B01.jpg\" title=\"Святі Апостоли Петро і Павло\" height=\"116\" width=\"194\" />Мабуть, неможливо говорити про день свята Петра й Павла без екуменічних алюзій.</p>\n<p class=\"text\">Чим є більшість проблем, розколів, труднощів у християнстві? Сварками,  обумовленими історико-культурними та особистісними моментами. До цього  додаються доктринальні проблеми, відмова у покорі й «застигання» таких  становищ, подібне до контрактур на переламах. Хірурги знають: коли  зламані кості неправильно зростаються, їх потрібно знову ламати, щоби  скласти правильно… Отож і процес розламів та примирень у Церкві зовсім  не є солодким.</p>\n','2013-07-11 13:36:14','http://oranta.org/index.php?option=com_content&view=article&id=1323:-praznyk-sv-apostoliv-petra-i-pavla-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'  Празник св. Апостолів Петра і Павла ','uk',3426,4909,0,NULL),
(1,1,'<p class=\"uiStreamMessage userContentWrapper\" data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4873,200,DSCN9421.JPG\" />Коли  помер цар, молодий принц, дещо непокоячись, готувався зайняти його  місце. Мудрий старий вихователь сказав юнакові: «Тобі потрібна допомога.  Перш ніж вступити на престол, обери собі майбутню дружину, але вважай:  це має бути дівчина, котрій можеш повністю довіряти. Запроси всіх  дівчат, які прагнуть стати царицею. Я поясню тобі, як знайти гідну».</p>\n<p class=\"uiStreamMessage userContentWrapper\" data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\">Наймолодша з посудомийок на царській кухні була таємно закохана у принца. Вирішила теж зголоситися.</p>\n','2013-07-09 23:43:49','http://oranta.org/index.php?option=com_content&view=article&id=1304:carycja-10-lypnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Цариця (10 липня)','uk',3423,4906,0,NULL),
(1,1,'<p class=\"стиль36\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,3491452781_9f266f228b.jpg\" />Розмаїття  великодніх яєць</strong></p>\n<p class=\"стиль36\"><strong>Крашанка - </strong>яйце  пофарбоване в один колір (червоний, жовтий синій, зелений, золотавий...)</p>\n<p class=\"стиль36\"><strong>Крапанка </strong>- на поверхню яйця  нанести крапочки воску (покапати), а  після занурити його у фарбу. Коли  поверхня яйця висохне, знову покапати воском  і занурити яйце у темнішу  фарбу.</p>\n','2013-05-03 23:10:29','http://oranta.org/index.php?option=com_content&view=article&id=1165:velykden-nabilshe-svjato-na-zemli&catid=37:spilnota-cat&Itemid=49','P',NULL,'Великодня субота','uk',3214,4697,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,mowa-serce-nacji.jpg\" /><strong>\"Нації вмирають не від інфаркту. Спочатку їм відбирає мову.\"</strong><br /> <em>Ліна Костенко</em></p>\n<p>Було це давно, ще за старої Австрії, далекого 1916 року. В купе першого класу швидкого потяга \"Львів-Відень\" їхали англієць, німець, італієць. Четвертим був відомий львівський юрист Богдан Костів. Балачки точилися довкола різних тем. Нарешті заговорили про мови: чия краща, багатша і якій належить світове майбутнє. Звісно, кожен заходився вихваляти рідну.</p>\n','2013-02-06 10:36:51','http://oranta.org/index.php?option=com_content&view=article&id=986:chyja-mova-nabagatsha&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Чия мова найбагатша','uk',2952,4435,0,NULL),
(1,1,'<p data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,doubts_about_bible.jpg\" />Звернімо  увагу на нас, а краще на те, як Бог давиться на мене і на тебе. «Ви є  лист Христовий, якого пізнають і читають усі люди» (II Кор. З, 2).</p>\n<p data-ft=\"{&quot;type&quot;:1,&quot;tn&quot;:&quot;K&quot;}\">Ці прості і глибокі слова звернено до нас з Вами. По нас можна  прочитати, в кого віримо, як живемо і чи вартує іти тими дорогами, якими  йдемо. Через кожного з нас Ісус Христос посилає свій лист світові -  там, де живемо: на своїй вулиці, у будинку, під\'їзді, на своїй парохії,  на роботі, в школі, в хорі, під час відпочинку чи святкування Дня  народження і навіть як ми десь вирушаємо в дорогу, - ми є «листом»  Христовим, якого відчитують люди.</p>\n','2013-03-20 22:56:26','http://oranta.org/index.php?option=com_content&view=article&id=1066:2013-03-20-21-19-35&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'«Ви є лист Христовий,  якого пізнають і читають усі люди»','uk',3065,4548,0,NULL),
(1,1,'<p id=\"point\" class=\"column column-last\">Добігають останні хвилі понтифікату Святішого Отця Папи Бенедикта ХVІ.  Понтифікату, який випав на час генерального наступу диктатури  релятивізму на самі основи західної цивілізації. Понтифікату, який став  часом внутрішньої консолідації Вселенської Спільноти Католицької Церкви.  Понтифікату, який ще раз потвердив, що саме Католицька Церква є  основною перешкодою для сил, які змагають до дегуманізації суспільства і  деконструкції самої людини. \n','2013-02-28 18:36:01','http://oranta.org/index.php?option=com_content&view=article&id=1009:-pontyfikat-benedykta-hvi-i-ugkc&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Понтифікат Бенедикта ХVІ і УГКЦ','uk',2983,4466,0,NULL),
(1,1,'<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.oranta.org/images/stories/p101005511.jpg\" style=\"float: left; margin: 5px;\" />В один з прекрасних ранків осені підійшов я до церковної брами і радість зникла бо побачив...</span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Під‘їхавши ранком до храму на ранішню Службу разом з архітектором Олександром Степановичем Трубіним з гіркотою побачили використані шприци біля храму. Якісь негідники, \"хворі люди \"на наркоманію, по духовному одержимі духами, уколовшись смертним зіллям кинули три шприци біля входу. Вони не задумувались що поруч бігають діти і теж можуть уколотися. Це загальна біда України а зокрема Донбасу, прикро та наше місто Димитрів на окремому місці.</span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">За статистикою у кожному під‘їзді живе ВІЛ інфікований наркоман. Шприци та порожні пачки від «трамадолу» можна знайти всюди. Дійшло навіть до церкви. Цієї Служби ми молилися за всіх одержимих а зокрема за тих трьох хворих наркоманією. Помоліться і ви! </span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">о. Іван Дмитрівський </span></p>\n<p style=\"TEXT-ALIGN: justify\"><a target=\"_blank\" href=\"http://www.oranta.org/mailto:jovahni-77@ukr.net\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">jovahni-77@ukr.net</span></a></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">обл. Дніпропетровська</span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">м. Дмитрів </span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"></span></p>\n<p style=\"TEXT-ALIGN: justify\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">\n','2008-10-21 13:06:14','http://www.oranta.org/index.php?option=com_content&view=article&id=72:problemy-narkomaniji&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Проблеми наркоманії','uk',535,1515,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: center;\" class=\"rteright\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4106,200,CRW_3549.JPG\" />Високопреосвященним і Преосвященним Архиєпископам та Митрополитам,<br /> боголюбивим єпископам, всечесному духовенству, преподобному монашеству,<br /> возлюбленим братам і сестрам, в Україні та на поселеннях у світі сущим</p>\n<p style=\"text-align: right;\"><em><strong>Христос народжується – славте!<br /> Христос із небес – зустрічайте!<br /> Христос на землі – бадьортеся!<br /> </strong>Канон Утрені Різдва Христового</em></p>\n<p style=\"text-align: center;\"><strong>Дорогі у Христі! </strong></p>\n<p style=\"text-align: center;\"><strong>&nbsp;</strong></p>\n','2011-12-24 18:19:15','http://oranta.org/index.php?option=com_content&view=article&id=815:rizdvjane-poslannja-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'РІЗДВЯНЕ ПОСЛАННЯ БЛАЖЕННІШОГО СВЯТОСЛАВА','uk',2627,4110,0,NULL),
(1,1,'<p style=\"text-align: center;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3555,200,IMG_3018.jpg\" />Слава  Ісусу Христу!</p>\n<p>Дорогі брати і  сестри!Читачі нашої газети ОРАНТА!</p>\n<p>Щиросердечно вітаю  Вас,всіх хто організував святкування нашого празнику Теплого Миколая і  вшанування памяті тих, хто 200 років збудував цей камяний Храм.</p>\n<p>Але&nbsp; закликаю Вас  роздумувати про подію 1150 ліття Хрещення царя і мученика за віру  Христову Аскольда-Миколая.Сьогодні подаються різні роки цієї  події.Історично офіційні святкування 1000 Хрещення Аскольда в Царській  Росії відбувалось за Благословенням Святішого Синоду в 1866році Божому.</p>\n','2010-05-31 20:28:53','http://oranta.org/index.php?option=com_content&view=article&id=539:ukrajinske-hrystyjanstvo-kriz-viky&catid=31:kolonka-redaktora-cat&Itemid=1','P','','УКРАЇНСЬКЕ ХРИСТИЯНСТВО КРІЗЬ ВІКИ','uk',2077,3560,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong> ВЕРХОВНОГО АРХИЄПИСКОПА КИЄВО-ГАЛИЦЬКОГО<br /> УКРАЇНСЬКОЇ ГРЕКО-КАТОЛИЦЬКОЇ ЦЕРКВИ</strong><br /> <strong>Високопреосвященним та Преосвященним Владикам,<br /> всесвітлішим та високопреподобним священнослужителям,<br /> преподобним ченцям та черницям,<br /> дорогим у Христі сестрам і братам -<br /> мирянам Української Греко-Католицької Церкви<br /> та всім людям доброї волі</strong>\n','2009-04-18 12:07:11','http://www.oranta.org/index.php?option=com_content&view=article&id=286:velykodni-pryvit-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'ВЕЛИКОДНІЙ ПРИВІТ БЛАЖЕННІШОГО ЛЮБОМИРА','uk',1369,2343,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong><img src=\"http://www.oranta.org/images/stories/2008/11/christmasmanger_top1.jpg\" style=\"float: right;\" /> Христос рождається!</strong></p>\n<p style=\"text-align: center;\"><strong>Славімо його!</strong></p>\n<p>Дорогі парафіяни, читачі наших інтернет сторінок «Оранта» та «Аскольдова Могила» прийміть найщиріші вітання з Новим 2009 роком Божим, Різдвом Христовим і святим Богоявленням.</p>\n<p>\n','2008-12-30 21:43:12','http://www.oranta.org/index.php?option=com_content&view=article&id=167:-slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,' Слово від Редакції','uk',910,1884,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4360,200,1244199478_rizdvo.jpg\" />Христос Рождається!</strong></p>\n<p style=\"text-align: center;\"><strong>Славімо його!</strong></p>\n<p style=\"text-align: center;\"><strong>Дорогі у Христі!</strong></p>\n<p>З серця України, з Дніпрових круч, де у тисячолітній пам’яті&nbsp; Божого&nbsp; люду збереглась могила Аскольда-Миколая Царя і мученика за віру Христову благаю і молюсь за Боже Благословення для усіх нас.</p>\n<p>З Дитятком Ісусом з Пресвятою Родиною зустрічаємо Новий 2013 рік Божий. У цій великій сім‘ї наповняймося вірою, надією, любов‘ю!</p>\n','2012-12-31 18:24:37','http://oranta.org/index.php?option=com_content&view=article&id=919:hrystos-rozhdajetsja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Христос Рождається!','uk',2887,4370,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: center;\"><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,den_nezav_2.jpg\" />Дорогі в Христі!<o:p></o:p></strong></p>\n<p>Цього року Всевишній знову сподобив нас радості святкувати Свято Незалежності нашої Батьківщини України. У нових драматичних, але водночас величних історичних обставинах, які нам доводиться сьогодні переживати, ми по-новому відкриваємо для себе неоціненність скарбу жити у вільній, суверенній і незалежній державі.\n','2014-08-25 11:39:06','http://oranta.org/index.php?option=com_content&view=article&id=2354:zvernennja-blazhennishogo-svjatoslava-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-vsih-ukrajinciv-z-nagody-dnja-nezalezhnosti-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Святослава до вірних Української Греко-Католицької Церкви та всіх українців з нагоди Дня Незалежності України','uk',4607,6090,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong>ДОРОГІ У ХРИСТІ!</strong></p>\n<p style=\"text-align: center;\">Із цілого серця, сповненого радості й вдячності нашому Спасителеві, &nbsp;звіщаю Вам:</p>\n<p style=\"text-align: center;\"><strong>ХРИСТОС ВОСКРЕС!</strong></p>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4194,200,tytulna.jpg\" />Цей відомий усім і кожному виголос, який ми чуємо кожного року навесні, стверджує, що знову вже вкотре повторилася подія двотисячолітньої давності: Христос воскрес із мертвих, подолавши смерть! Що це означає для нас? Адже саме для того Христос прийшов у світ, був розп’ятий і помер на хресті, щоб потім, воскреснувши, відкрити і нам шлях до Неба. В усьому подібні до Христа, ми подібні до Нього і у воскресінні. І не лише в майбутньому вічному житті, але й у житті теперішньому. Як же пережити це <strong>особисте воскресіння</strong>?</p>\n','2012-04-06 12:55:54','http://oranta.org/index.php?option=com_content&view=article&id=849:hrystove-voskresinnja&catid=28:tema-nomera-cat&Itemid=2','P','','Христове воскресіння','uk',2718,4201,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong>Збірка «Неопалима купина»</strong></p>\n<p style=\"text-align: center;\"><strong>Вірш «На Аскольдовій могилі літо барви роздає...»</strong></p>\n<p> На Аскольдовій могилі літо барви роздає...<br />І чомусь удвічі радо б\'ється серденько моє.<br />Задивляюсь поміж зорі у небесную криницю...<br />Такі зорі неозорі сяють в нашій Зарваниці.<br /><br />На Аскольдовій могилі літо росами мандрує...<br />Матір Божа Зарваницька аж до Києва прошкує...<br />...З Києва колись монахи від розправи утікали,<br />І про Господові дива не відали, не гадали.  <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" />  </p>\n<p>\n','2008-10-23 14:14:21','http://www.oranta.org/index.php?option=com_content&view=article&id=77:poezija-mariji-balickoji&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Поезія Марії Баліцької','uk',558,1538,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong>Звернення Комісії УГКЦ</strong></p>\n<p style=\"text-align: center;\"><strong>у справах душ пастирства охорони здоров\'я </strong></p>\n<p style=\"text-align: center;\"><strong>з приводу поширення епідемії в Україні</strong></p>\n<p style=\"text-align: center;\"><strong> </strong><strong>Слава Ісусу Христу!</strong></p>\n<p style=\"text-align: center;\"><strong>Дорогі брати і сестри у Христі!</strong></p>\n<p>Сьогодні в Україні склалась тривожна ситуація через поширення недуги, яка уражає без огляду на вік та стать український народ. Інформація, яка розповсюджується не має певності і одностайності ані щодо справжньої причини недуги, ні щодо її визначення, ні щодо походження, як і щодо можливих прогнозів. Це лише у зайвий раз підсилює страх та тривогу людей.</p>\n','2009-11-03 19:40:43','http://www.oranta.org/index.php?option=com_content&view=article&id=451:zvernennja-komisiji-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Комісії УГКЦ','uk',1862,3345,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: center;\"><strong>Послання <br /> Блаженнішого Любомира <br /> до священиків, дияконів та семінаристів <br /> з нагоди проголошення 2009 року <br /> Роком християнського покликання з особливим наголосом на священичому покликанні</strong></p>\n<div style=\"text-align: center;\"></div>\n<p style=\"text-align: center;\">Всесвітлішим і всечеснішим ієреям та дияконам<br />та дорогим семінаристам<br />мир і Боже благословення!</p>\n<div style=\"text-align: center;\"></div>\n<p style=\"text-align: center;\"><em>Дорогі у Христі брати!</em></p>\n<p style=\"text-align: right;\"><em>«Ти ж, чоловіче Божий, шукай праведности,<br /> побожности,  віри, любові, терпеливости, лагідности. <br /> Змагайся добрим змагом віри,<br /> старайся осягнути життя вічне, до якого ти покликаний<br /> і за яке був склав гарне визання віри перед багатьма свідками».</em></p>\n<p style=\"text-align: right;\">(І Тим. 6, 11-12)</p>\n<p style=\"text-align: right;\">\n','2009-01-27 13:51:06','http://www.oranta.org/index.php?option=com_content&view=article&id=196:poslannja-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Послання  Блаженнішого Любомира','uk',1070,2044,0,NULL),
(1,1,'<p style=\"text-align: center;\"><strong>Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви до вірних та всіх людей доброї волі з приводу 70-х роковин Волинської трагедії</strong></p>\n<p style=\"text-align: center;\"><em> </em></p>\n<p style=\"text-align: center;\"><em>Всечесному духовенству, преподобному монашеству, мирянам Української Греко-Католицької Церкви та всім людям доброї волі</em></p>\n<p style=\"text-align: center;\"><strong> </strong></p>\n','2013-03-11 22:25:12','http://oranta.org/index.php?option=com_content&view=article&id=1030:zvernennja-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'ЗВЕРНЕННЯ Блаженнішого Святослава','uk',3011,4494,0,NULL),
(1,1,'<p style=\"text-align: center;\">Високодостойний Пане Президенте!<br /> Високодостойний Пане Голово Верховної Ради!<br /> Високодостойна Пані Прем\'єр-Міністр!<br /> Високоповажні керівники парламентських фракцій!</p>\n<p>Ми, єпископи Української Греко-Католицької Церкви, звертаємося до Вас у справі, яка турбує не тільки нас і мільйони наших вірних, а й ціле українське суспільство. Йдеться про новітню трудову еміграцію. Це явище саме в собі могло би бути нейтральним, але, на превеликий жаль, стало трагічним для багатьох з тих, хто через невідрадні економічні обставини в рідній країні почувався змушеним шукати розв\'язок у чужих країнах, виставляючи себе на великі небезпеки, щоб рятувати своїх найближчих і найдорожчих людей в Україні. Це факт, який ми повинні брати до уваги.</p>\n','2009-07-06 23:26:59','http://www.oranta.org/index.php?option=com_content&view=article&id=372:zvernennja-synodu-jepyskopiv-kyjevo-galyckogo-verhovnogo-arhyjepyskopstva-ugkc-shchodo-sytuaciji-ukrajinskyh-trudovyh-emigrantiv&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення  Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ щодо ситуації українських трудових емігрантів','uk',1641,3124,0,NULL),
(1,1,'<p style=\"text-align: center;\">Високопреосвященним і Преосвященним Владикам,<br /> Всесвітлішим і Високопреподобним отцям,<br /> Преподобним ченцям і черницям,<br /> Дорогим у Христі сестрам і братам мирянам<br /> Мир і Боже благословення!</p>\n<p><img src=\"http://www.oranta.org/images/stories/2008/10/blazhennishyj_lubomyr_opt.jpg\" style=\"float: left;\" /> Дорогі у Христі, через кілька днів ми будемо святкувати Різдво Господа нашого Ісуса Христа, надзвичайно важливу подію для кожного і кожної з нас, для наших родин, для громад, для народу, для Церкви і для всього людства. Ця подія настільки важлива, що ми навіть ділимо історію людства на роки до народження Ісуса Христа та після Його народження. І робимо це не принагідно чи випадково, а на підставі глибокого усвідомлення, що світ до Його приходу на землю сильно відрізнявся від світу, який настав після Його приходу. Так само і з життям кожного і кожної з нас. Коли ми втілюємося в Ісуса Христа через святе таїнство Хрещення, пізнаємо Його і зустрінемося з Ним, наше життя набуває іншого, нового, кращого вигляду. Пізнати, зрозуміти, відчути ту близькість Бога до нас та можливість нашої близькості до Господа - це річ, яка цілковито змінює наше уявлення про людське життя, наше відчуття самих себе, власного життя і життя людства загалом.</p>\n<p>Вітаючи вас із святим празником, який от-от настане, сердечно бажаю кожному із вас, як і собі самому, щоб у ці святі дні ми могли наблизитися до Господа Бога, відкрити своє серце до Його любові, прийняти Його, зрозуміти, що Він перший полюбив нас і що зміст нашого життя - відповісти на Його любов своєю любов‘ю, любов\'ю до Нього та до всіх своїх ближніх.</p>\n<p>\n','2009-01-05 12:29:37','http://www.oranta.org/index.php?option=com_content&view=article&id=171:pryvitannja-blazhennishogo-ljubomyra-na-rizdvo-gnih-2009-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Привітання Блаженнішого Любомира на Різдво ГНІХ 2009 року','uk',933,1907,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: center;\">Високопреосвященним і Преосвященним Владикам,<br /> всесвітлим, всечесним і преподобним отцям,<br /> преподобним ченцям і черницям,<br /> любим у Христі мирянам<br /> Української Греко-Католицької Церкви</p>\n<div style=\"text-align: center;\"></div>\n<p style=\"text-align: center;\"><em>Дорогі у Христі!</em></p>\n<p>З волі Божественного Провидіння  цьогорічний Великий піст стає часом підготовки не лише до світлого  празника Христового Воскресіння, а й до ще одної радісної події –  беатифікації слуги Божого Папи Івана Павла II, яка має відбутися у  Томину неділю, 1 травня цього року.</p>\n','2011-03-15 07:35:41','http://oranta.org/index.php?option=com_content&view=article&id=689:zvernennja-vladyky-igorja-voznjaka-pro-beatyfikaciju-slugy-bozhogo-papy-ivana-pavla-ii&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Владики Ігоря (Возьняка) про беатифікацію слуги Божого Папи Івана Павла II','uk',2352,3835,0,NULL),
(1,1,'<p style=\"text-align: center;\">Всесвітлішим, високопреподобним,<br /> преподобним та всечеснішим <br /> священнослужителям <br /> Української Греко-Католицької Церкви</p>\n<p style=\"text-align: center;\"><em><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_shevchuk_sviashenyky.jpg.jpg\" height=\"207\" width=\"300\" /></em>Дорогі співбрати у Христовому священстві!</p>\n<p>У Cтрасний четвер, того дня, коли наш Божественний Спаситель перед  своїми страстями зібрав своїх учнів на Тайну вечерю, хочу і я зібрати  всіх вас довкола себе за посередництвом цього послання. Під час  таїнственної трапези з апостолами Ісус Христос установив святе таїнство  Священства. До участі у цьому великому таїнстві Він вибрав і покликав  також і нас, зробив це служіння сенсом нашого життя та земної місії. В  цей святковий день хочу щиро привітати вас, що стали учасниками вічного  священства самого Спасителя, та разом з вами поміркувати над його  змістом і тими завданнями, які ми повинні виконати в нашій Церкві і в  нашому народі.</p>\n','2011-04-19 10:01:42','http://oranta.org/index.php?option=com_content&view=article&id=727:poslannja-blazhennishogo-svjatoslava-do-svjashchennosluzhyteliv-ukrajinskoji-greko-katolyckoji-cerkvy-na-strasny-chetver&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Послання Блаженнішого Святослава до священнослужителів Української Греко-Католицької Церкви на Страсний четвер','uk',2411,3894,0,NULL),
(1,1,'<p style=\"text-align: center;\">ДОРОГІ ДРУЗІ!</p>\n<p>Ось і закінчився VI фестиваль-конкурс духовної пісні \"Аскольдів глас\". Ми були свідками величного пісенного свята, незабутню атмосферу якого, сльози радості й очищення на очах викликали талановиті співаки-солісти. Своїм мистецтвом &nbsp;вони прославляли Бога і його святих Миколая та князя Аскольда. Не тільки переможці, але загалом&nbsp; всі учасники продемонстрували високий професійний рівень та ретельну підготовку до конкурсу.</p>\n','2010-12-22 23:10:48','http://oranta.org/index.php?option=com_content&view=article&id=649:podjaka&catid=25:parafija-cat&Itemid=27','P',NULL,'ПОДЯКА','uk',2262,3745,0,NULL),
(1,1,'<p style=\"text-align: center;\">Дорогі в Христі співвітчизники!</p>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_d_n2.gif.gif\" height=\"203\" width=\"300\" />З  нагоди 20-ї річниці державної Незалежності України щиросердечно вітаю  всіх вас, в Україні та на поселеннях сущих, і єднаюся з вами у подячній  молитві до Всемогутнього Господа за неоціненний дар свободи для рідного  народу і нашої держави. Цими днями ми також згадуємо із вдячністю наших  славних попередників, які століттями підносили свої молитви до Неба:  «Боже, великий, єдиний, нам Україну храни!». Вшановуємо рівно ж і тих,  хто боровся за здобуття цієї незалежності, складаючи до вівтаря Вітчизни  плоди своєї жертовної праці, а часто і цвіт свого молодечого життя. Хай  воздасть Господь своїми щедротами тим, хто передав нам цю почесну  естафету державотворення.</p>\n','2011-08-24 10:41:14','http://oranta.org/index.php?option=com_content&view=article&id=781:zvernennja-blazhennishogo-svjatoslava-verhovnogo-arhyjepyskopa-kyjevo-galyckogo-ugkc-z-nagody-20-litnogo-juvileju-nezalezhnosti-ukrajiny&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'ЗВЕРНЕННЯ Блаженнішого СВЯТОСЛАВА, Верховного Архиєпископа Києво-Галицького УГКЦ, з нагоди 20-літнього ювілею Незалежності України','uk',2556,4039,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: center;\">Дорогі в Христі!</p>\n<p style=\"text-align: right;\"><em>«Я вважав, отже, потрібним просити братів, <br /> щоб вони вийшли до вас перед нами <br /> і приготували заздалегідь той заповіджений щедрий ваш дар, <br /> щоб він був готовий як правдивий дар, а не як вимушений даток»</em><br />II Кор. 9, 5</p>\n<p>Бажаю звернутися до вас у справі фінансування церковних потреб. Під час проповідей та у різних зверненнях ми часто чуємо про брак грошей, інколи, здавалося б, аж занадто часто. При тому ми рідко отримуємо пояснення, що, де, чому і як. Отож, хочу докладніше поговорити з вами про фінансування нашої Церкви.</p>\n<p>\n','2008-12-09 08:02:06','http://www.oranta.org/index.php?option=com_content&view=article&id=130:zvernennja-blazhennishogo-ljubomyra-do-virnyh&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Любомира до вірних','uk',772,1752,0,NULL),
(1,1,'<p style=\"text-align: center;\">Дорогі у Христі!</p>\n<p><em><br /></em><strong>Вступ</strong><br /> <br /> Господь наш Ісус Христос перед вознесінням на небо звернувся до своїх  учнів із закликом: «Ідіть, отже, і зробіть учнями всі народи, христячи  їх в ім’я Отця і Сина і Святого Духа; навчаючи їх берегти все, що я вам  заповідав. Отож  я з вами по всі дні аж до кінця віку» (Мт. 28, 18–20). Цей заклик  Христа звіщати Добру Новину стосується не лише перших Христових учнів –  апостолів, він зобов’язує Церкву в усі часи, аж до кінця віків.  Українська Греко-Католицька Церква, здійснюючи це доручення  Божественного Спасителя, п’ять років тому вустами єпископів Священного  Синоду окреслила мету свого служіння як «Святість об’єднаного Божого  люду». Святість – це Божий дар, покликання кожного християнина. «Це ж  саме воля Божа: освячення ваше», – нагадує св. Павло в Посланні до  солунян (1 Сол. 4, 2–3).</p>\n','2011-12-20 22:45:04','http://oranta.org/index.php?option=com_content&view=article&id=817:pastyrsky-lyst-blazhennishogo-svjatoslava-do-duhovenstva-monashestva-i-usih-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-lzhyva-parafija--misce-zustrichi-z-zhyvym-hrystomr&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Пастирський лист Блаженнішого Святослава до духовенства, монашества і усіх вірних Української Греко-Католицької Церкви «Жива парафія – місце зустрічі з живим Христом»','uk',2630,4113,0,NULL),
(1,1,'<p style=\"text-align: center;\">Слава  Ісусу Христу!</p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3065,200,5.jpg\" />Парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі щиро вітають рукоположеного єпископа Йосифа Міляна.</p>\n<p>Хай Бог веде Вас своєю дорогою правди. Осяює шлях Великого служіння. Дарує лаку і скріплює Віру, Надію та Любов.</p>\n<p>P.S. 18 червня 2009 р., у Києві, в Патріаршому соборі Воскресіння Христового УГКЦ, відбулася єпископська хіротонія владики Йосифа Міляна, СУ, Єпископа-помічника Київської архиєпархії УГКЦ. Це було особливе свято.</p>\n','2009-06-21 23:34:31','http://www.oranta.org/index.php?option=com_content&view=article&id=353:jepyskopska-hirotonija-vladyky-osyfa-miljana&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Єпископська хіротонія владики Йосифа Міляна','uk',1583,3066,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><em><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_29-03-13-6.jpg.jpg\" height=\"149\" width=\"129\" />Священик УГКЦ о. Орест Вільчинський ще з 2004 року уважно слідкує за діяльністю</em><em> </em><em><a href=\"http://risu.org.ua/ua/index/resourses/directory/%7E%D0%94/48515/\" target=\"_blank\">релігійного угрупування Антоніна Догнала</a>,  відомим ще як «підгорецькі» отці. Багатьох людей, які потрапили до  цього угрупування, йому вдалося врятувати особисто. За це догналіти його  піддали анафемі.</em><em> </em></p>\n<p style=\"text-align: justify;\"><em>Щоправда, священика це не зупинило. Навпаки. Він з подвійною  енергією почав викривати «підгорецьких» отців та допомагати людям, які  мають з ними клопоти.</em></p>\n','2013-03-31 19:52:32','http://oranta.org/index.php?option=com_content&view=article&id=1082:rejestracija-grupy-dognala-bumerangom-vdaryt-po-derzhavi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Реєстрація групи Догнала бумерангом вдарить по державі','uk',3108,4591,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img src=\"http://askoldova-mohyla.org/uk/image,4349,200,ArticleImages_50484_Monika-visn.jpg\" style=\"float: right; margin: 10px;\" />Моніці Полянській було лише 23 роки, коли перед нею постав неймовірно складний вибір: зостатись із трилітньою донечкою, яку палко любила усім серцем, чи – відгукнутись на поклик Ісуса, присвятитись Йому всеціло і стати черницею. Загадала собі: якщо вдосвіта, коли від’їжджатиме до монастиря і цілуватиме, прощаючись, свою Янусю і та прокинеться — залишиться з нею. Дівчинка спала міцно. Відпускала матусю, дозволяючи їй здійснити своє покликання.</p>\n<h3 style=\"text-align: justify;\">Вперше переступивши поріг келії, вона обцілувала її стіни</h3>\n<p style=\"text-align: justify;\">«Коли я, приступивши до праці у постуляційному центрі, отримала завдання опрацювати справу Моніки Полянської і прочитала, що вона, залишивши маля своїй мамі, пішла в монастир, у мене з’явився спротив – чи ж можна таку людину беатифікувати? – розповідає сестра-василіянка Романа Гутник. — І я сказала настоятельці, що не зможу цим займатись. А вона відповіла: це твої перші враження, придивись, як Бог діє в її житті. У мене не залишилось вибору. І лише глибше ознайомившись із життям монахині, я збагнула, якою жертвою стала для неї відсутність спілкування із донькою, якої внутрішньої боротьби це їй вартувало».</p>\n','2012-12-08 16:38:13','http://oranta.org/index.php?option=com_content&view=article&id=909:poklykannja-materi-moniky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Покликання матері Моніки','uk',2867,4350,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img style=\"float: right;\" src=\"http://charnetskyj.org.ua/_pu/0/73900.jpg\" /><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\"> \"Світильником світлим ти засіяв, священномучениче Йосафате, бо як пастир добрий віддав життя своє за вівці\"<em>(Тропар празника).</em></span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Празник святого священномученика Йосафата для вірних Української Католицької Церкви особливо дорогий, світлий і радісний, бо це празник нашого українського святого, празник нашого брата по крові й кості, празник великого апостола й мученика за святу віру й єдність Церков.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Та не тільки наша Церква, але й ціла Католицька Церква його почитає, його славить, до нього молиться, на його честь будує святині, а його мощі спочивають у серці християнсько-католицького світу, у базиліці святого апостола Петра в Римі. Святий Йосафат - це перший український святий, якого канонізувала Католицька Церква, йому і його мученицькій крові ми завдячуємо, що сьогодні визнаємо правдиву Христову віру та що ми члени правдивої Христової Церкви.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Не думаємо пригадувати тут історію його життя, канонізації чи культу, але радше хочемо звернути увагу на знаменні риси його особи й характеру, за які свята Церква його канонізувала та вписала до переліку святих мучеників. Особу святого Йосафата можна окреслити трьома словами: Він - святий, він - апостол, він - мученик.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\"> \n','2008-11-26 16:16:05','http://www.oranta.org/index.php?option=com_content&view=article&id=115:svjaty-svjashchennomuchenyk-osafat&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Святий священномученик Йосафат','uk',705,1685,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4225,200,DSCN6149.jpg\" />Літня пора приносить нам завжди щасливі емоції, адже чи не кожна дитина відпочиває в різних куточках нашої країни: чи то в Карпатах, чи то на морі. Дітям з церкви святого Миколая, що на Аскольдовій могилі, також пощастило. Разом зі своїми друзями 30 червня 2012 року вони вирушили на відпочинок до церковного табору, що розташувався&nbsp; у Карпатах, в селі Заріччя Надвірнянського району Івано-Франківської області. Варто згадати, що тут свій відпочинок два роки тому проводила також група дітей з Аскольдової могили. Як і минулий раз, так і цьогоріч діти абсолютно задоволені відпочинком. Цього року з нами поїхали також і декілька сімей нашої парафії. А виховним процесом та катехизацією опікувались: пані Ореста Цибрівська, Світлана Заєць, Мирослава Мазур та пан Дмитро Засульський.</p>\n','2012-07-17 01:24:59','http://oranta.org/index.php?option=com_content&view=article&id=872:karpatskymy-shylamy-abo-jak-my-vidpochyvaly-u-gorah&catid=25:parafija-cat&Itemid=27','P','','Карпатськими схилами або як ми відпочивали у горах','uk',2768,4251,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,6731094_w640_h640_1.jpg\" />Мама, як звичайно, покликала з кухні:</p>\n<p style=\"text-align: justify;\">– Прошу до столу. Все готове!</p>\n<p style=\"text-align: justify;\">Її  чоловік, який читав, і два сини, що дивилися телевізор, швидко  відгукнулися і з шумом сіли до столу. Прийшла господиня, але замість  пахучих страв, кинула на стіл жменьку сіна.</p>\n','2013-04-03 09:07:27','http://oranta.org/index.php?option=com_content&view=article&id=1084:nedilny-obid-3-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Недільний обід (3 квітня)','uk',3111,4594,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5556,200,1009025.jpg\" />Не питай чому я мусив вмерти</p>\n<p style=\"text-align: justify;\">Не питай взагалі \"чому?\"</p>\n<p style=\"text-align: justify;\">Моя кров зупинила смерті</p>\n<p style=\"text-align: justify;\">Кров моя зупинила війну.</p>\n','2014-02-24 11:46:45','http://oranta.org/index.php?option=com_content&view=article&id=1881:ne-pyta--zhyvy-&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Не питай ... живи ','uk',4076,5559,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img width=\"150\" src=\"http://www.oranta.org/images/stories/2008/10/image001.jpg\" alt=\"Любомир Гузар і Галина Терещук\" height=\"127\" style=\"float: left; margin: 5px; border: #000000 1px solid;\" title=\"Любомир Гузар і Галина Терещук\" /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Як тільки українці 4 роки тому відстояли свободу і право вибору, Ви, Ваше Блаженство, одразу ж звернулись із посланням до нової української влади, щоб вона шанувала довіру людей, яку врешті вдалося повернути після радянської системи. Чому до Ваших слів не дослухались ті, хто здобув владу і як можна повернути сьогодні довіру, яку втрачено? <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> </span>\n','2008-10-11 04:12:18','http://www.oranta.org/index.php?option=com_content&view=article&id=30:lmabutnje-ukrajiny-za-novym-pokolinnjamrintervju-blazhennishogo-ljubomyra-dlja-radio-lsvobodar&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Майбутнє України – за новим поколінням','uk',506,1486,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><img width=\"150\" src=\"http://www.oranta.org/images/stories/2008/10/img_5852.jpg\" alt=\"Ілля Онишкевич\" height=\"225\" style=\"float: left; margin: 5px; border: 1px solid;\" title=\"Ілля Онишкевич\" /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Сьогодні, дорогі в Христі брати і сестри, представляємо Вам нашу працю по оновленню церковного сайту </span><a href=\"http://www.askoldova-mohyla.org/\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">www.askoldova-mohyla.org</span></a><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"> і нову версію газети «Оранта» </span><a href=\"http://www.oranta.org/\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">www.oranta.org</span></a><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"> в електронному вигляді. </span>\n','2008-10-13 02:15:36','http://www.oranta.org/index.php?option=com_content&view=article&id=35:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','H','','Слово від Редакції','uk',504,1484,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><img width=\"150\" src=\"http://www.oranta.org/images/stories/2008/10/u10.jpg\" alt=\"Ікона УНСО\" height=\"180\" style=\"float: left; margin: 5px;\" title=\"Ікона УНСО\" /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Ікона (гр. <em>εικόν</em> - <em>зображення, образ</em>) - малярське, мозаїчне або рельєфне зображення святих, якому надають священного характеру. Ікона сама в собі однак є чимось більше, як лише святим зображенням, вона є місцем присутності благодаті, місцем об\'явлень Ісуса Христа (Матері Божої і усіх постатей, зображуваних на іконі) для посилання до них молитов.</span><a name=\"_ftnref1\"></a><a href=\"http://www.lemkounion.org../#_ftn1\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">[1]</span></a><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"> Культ ікон офіційно був прийнятий на сьомому вселенському соборі 787 р. у м. Нікеї. Проте ікони не завжди в історії християнства були глибоко шановані. У VIII-IX ст. у Візантії виникло іконоборство - релігійно-політичний рух спрямований проти шанування ікон, мощей та інших предметів культу. <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> </span>\n','2008-10-13 14:31:37','http://www.oranta.org/index.php?option=com_content&view=article&id=54:ikona-v-ukrajinski-kulturi&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Ікона в українській культурі','uk',503,1483,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\">Українською катастрофою ХХ століття називає сучасна громадська думка голодомор 1932 - 1933 років в Україні. Терор голодом, запроваджений сталінським тоталітарним режимом в Україні, заподіяв смерть мільйонам хліборобів. Адже від голоду, масових репресій і депортацій Україна втратила більше ніж за роки Першої світової та громадянської воєн.</span></p>\n<div style=\"text-align: justify;\"></div>\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: arial,helvetica,sans-serif;\"> \n','2008-11-17 11:47:04','http://www.oranta.org/index.php?option=com_content&view=article&id=101:-golodomor-1932-1933-rokiv-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2','N','',' Голодомор 1932 – 1933 років в Україні','uk',660,1640,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">23 жовтня в центральному будинку художника відбудеться концерт-презентація диску з піснями чудового українського поета Костя Москальця. Поет-фантом, поет-містерія - його пісні «Вона», «Срібне поле» виконує не тільки гурт «Плач Єремії», їх співають студенти в гуртожитках, українські вояки в казармах, їх співають навіть гламурні дівчата, які в цілому не дуже обізнані з українською сучасною музикою. \n','2008-10-17 18:11:06','http://www.oranta.org/index.php?option=com_content&view=article&id=68:koncert-prezentacija-dysku-poeta-kostja-moskalcja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Концерт-презентація диску поета Костя Москальця','uk',522,1502,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.oranta.org/images/stories/21618-1.jpg\" style=\"float: left; margin: 5px;\" />Наприкінці вересня в містах Івано-Франківську та Коломиї розпочалися заходи по відзначенню 120-ліття від дня народження польського письменника Станіслава Вінценза, який у своїй творчості оспівав Українські Карпати та їхніх мешканців, зокрема, побут і духовний світ гуцулів. Станіслав Вінценз народився 30 листопада 1888 року в гірському селі Слобода Рунґорська на Гуцульщині, а помер 29 січня 1971 року в Швейцарії. \n','2008-10-23 15:59:49','http://www.oranta.org/index.php?option=com_content&view=article&id=78:120-littja-vid-dnja-narodzhennja-stanislava-vincenza&catid=32:gk-svit-cat&Itemid=58','P',NULL,'120-ліття від дня народження Станіслава Вінценза','uk',560,1540,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.oranta.org/images/stories/i.jpg\" style=\"float: left; margin: 5px;\" />Українська Американська Координаційна Рада, Міжнародний благодійний фонд «Україна 3000», Меценат Олександр ПРОГНІМАК представляють перший українсько-американський проект 20 жовтня, 19.00 (Український дім) вечір пам\'яті КВІТКИ ЦІСИК (1953-1998) \n','2008-10-17 18:27:01','http://www.oranta.org/index.php?option=com_content&view=article&id=69:vechir-pamjati-kvitky-cisyk&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вечір пам\'яті Квітки Цісик','uk',523,1503,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.oranta.org/images/stories/picture.php.jpg\" style=\"float: left; margin: 5px;\" />Постать святого Климента Римського, священномученика епохи християнської єдності, є однією з найяскравіших в історії Церкви. Ім\'я його тисячу років тому було відоме всьому християнському світові, а для середньовічної Русі стало символом її хрещення. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Згідно за гіографічними джерелами, святий Климент (бл. 30-97) був знатним римлянином, який прийняв хрещення від апостола Петра і саме ним був висвячений на єпископську катедру Рима (88р.). Климент став четвертим римським єпископом, згодом включеним до офіційно визнаного католицькою церквою списку пап. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Церковна традиція вважає його автором низки творів, серед яких Перше Послання до Коринфян, - найдавніша за часом, після творів апостольських, пам\'ятка раннього християнського вчення [3, с.65-73]. Послання особливо шанувалося християнами і читалося у церквах поряд з апостольськими посланнями. Написане з приводу ворожнечі в Коринфській церкві, воно мало примирити ворогуючі партії коринфян. З твору постає образ Климента Римського як ревносного захисника церковної єдності (в Посланні, зокрема, віддається однакова шана як апостолу Петру, так і апостолу Павлу), талановитого промовця, наполегливого проповідника ранньохристиянських ідеалів. \n','2008-10-15 20:39:41','http://www.oranta.org/index.php?option=com_content&view=article&id=65:klyment-rymsky-symvol-jednannja-hrystyjanstva-detalna-informacija&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Климент Римський - символ єднання християнства','uk',499,1479,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><img style=\"margin: 5px; float: left;\" src=\"http://www.oranta.org/images/stories/2008/11/rtemagicc_konf_sobor1.jpg.jpg\" />\"Ідея провести таку конференцію - рік після Собору - і спільно зробити аналіз того, що змінилося, думаю, була благословенна і подібне потрібно буде робити після кожної наступної сесії Патріаршого Собору, щоб їхні напрацювання не проходили без сліду\", - сказав Блаженніший Любомир, Верховний Архиєпископ Києво-Галицький, звертаючись до учасників всеукраїнської конференції \"Молодь у Церкві ІІІ тисячоліття: Рік після Собору\". Конференція, яка 26 жовтня завершилася Архиєрейською Божественною Літургією у храмі Свв. Верховних апостолів Петра і Павла у м. Новояворівську (Львівська обл.), розпочалася 23 жовтня. Вона проходила у Молодіжному реколекційному духовному центрі ім. митр. Андрея та архим. Климентія, братів Шептицьких, у рамках програми Року християнського покликання.   \n</p></span>\n','2008-10-27 08:27:06','http://www.oranta.org/index.php?option=com_content&view=article&id=80:iv-sesija-patriarshogo-soboru-stala-dzherelom-pozytyvnyh-tendenci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'IV сесія Патріаршого Собору стала джерелом позитивних тенденцій','uk',566,1546,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">«Інший сипле щедро, і йому ще додається; а другий надміру бережливий, а все одно бідніє...»</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">«Якщо у тебе буде десять шуб і ти в мороз віддаєш замерзаючому одну, чи буде це жертвою? Ні.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Якщо віддаєш дев\'ять? Теж - ні. Тільки десять! Ти можеш крикнути: але ж я замерзну! <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">В цьому і суть жертви. Ти ризикуєш відчути справжнє страждання. </span>\n','2008-10-12 15:06:01','http://www.oranta.org/index.php?option=com_content&view=article&id=59:zhertva-vidaje-shljahamy-v-carstvo-bozhe&catid=29:propovidi-cat&Itemid=34','P',NULL,'…жертва відає шляхами в Царство Боже.','uk',505,1485,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Карітас-Печерськ завдяки підтримки доброчинців та благодійників активно допомагає дітям які залишилися без материнської опіки. Їхні матері на превеликий жаль вчинили злочин і зараз відбувають покарання у виправних закладах.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Звичайно, ми не забуваємо тих, хто постраждав від злочину.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Але дитина, яка залишилась без матері ні в чому не винна. <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> </span></p>\n<span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">\n','2008-10-14 23:09:27','http://www.oranta.org/index.php?option=com_content&view=article&id=60:lsv-mykola-dopomagaje-zabuti-dytynir&catid=25:parafija-cat&Itemid=27','P',NULL,'«Св. Миколай допомагає забутій дитині»','uk',502,1482,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">Сьогодні у день великого свята Покрови Пресвятої Богородиці владика Богдан Дзюрах освятив молільну кімнату в лікарні на окраїні Києва. </span></p>\n<span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">\n','2008-10-15 16:09:47','http://www.oranta.org/index.php?option=com_content&view=article&id=64:vidkryto-novu-greko-katolycku-kaplycjusogodni-u-den-velykogo-svjata-pokrovy-presvjatoji-bogorodyci-vladyka-bogdan-dzjurah-osvjatyv-molilnu-kimnatu-v-likarni-na-okrajini-kyjeva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Відкрито нову Греко-Католицьку Каплицю','uk',500,1480,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">У неділю 12 жовтня цього року у церкві св. Миколая Чудотворця на Аскольдовій Могилі, після закінчення Святої Літургії для дітей о. Олексій Геник вручив бібліотечку катехита для священників і катехитів парафії. \n','2008-10-15 16:00:04','http://www.oranta.org/index.php?option=com_content&view=article&id=62:katehytychna-shkola-zaproshuje-do-nauky&catid=25:parafija-cat&Itemid=27','P',NULL,'Катехитична школа запрошує до науки','uk',501,1481,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right; margin-right: 8px; border: #000000 1px solid;\" src=\"http://www.oranta.org/images/stories/2009/03/ajnj 031.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий тиждень Великого посту нагадує нам про великі Господні діла, про ті чуда, що творив Ісус Христос. У Святому Євангелії від Марка, глава 2, говориться:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><sup>1</sup> Коли ж Він по кількох днях прийшов знов до Капернауму, то чутка пішла, що Він удома.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><sup>2</sup> І зібралось багато, аж вони не вміщалися навіть при дверях. А Він їм виголошував слово.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><sup>3</sup> І прийшли ось до Нього, несучи розслабленого, якого несли четверо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><sup>4</sup> А що через народ до Нього наблизитися не могли, то стелю розкрили, де Він був, і пробравши, звісили ложе, що на ньому лежав розслаблений.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><sup>5</sup> А Ісус, віру їхню побачивши, каже розслабленому: Відпускаються, сину, гріхи тобі!</span></span></p>','2009-03-15 00:32:27','http://www.oranta.org/index.php?option=com_content&view=article&id=244:lgovoryt-do-nogo-isus-ustava-vizmy-lozhe-svoje-ta--hodyr-jevangelije-vid-oana-glava-5&catid=25:parafija-cat&Itemid=27','N','','«Ісус, віру їхню побачивши, каже розслабленому: Відпускаються, сину, гріхи тобі!» - Євангеліє від Марка, глава 2','uk',1238,2212,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://www.oranta.org/images/stories/2008/10/pr1.jpg\" alt=\"\" width=\"197\" height=\"131\" /> Сьогодні, 29 січня, біля нашої святині, пам\'ятного Хреста, схилили свої голови українці: від президента держави до пересічного громадянина. Усі вклонились відважним українцям, героям, вірним синам, за їхню любов до своєї Батьківщини. Вони знали, що ідуть на смерть і йшли заради свободи своїх рідних, своєї Вітчизни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10:00 президент України Віктор Ющенко поклав вінок до пам\'ятного Хреста Героям Крут. Поминальну молитву відслужив Блаженніший Любомир Гузар разом з владикою Богданом УГКЦ та митрополитом Дмитрієм УПЦКП у супроводі хору Київської духовної семінарії. Панахида закінчилась відпустом. Блаженніший Любомир: - &laquo;Зі святими упокой Господи душі рабів Божих, загиблих студентів під Крутами, що боролись за віру Христову і за волю Батьківщини України. До лику святих і праведних їх причисли і спаси. І помилуй їхні душі.&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','2009-01-29 23:39:41','http://www.oranta.org/index.php?option=com_content&view=article&id=200:svitla-i-vichna-pamjat-studentam-gerojam&catid=25:parafija-cat&Itemid=27','P','','Світла і вічна пам‘ять студентам-героям!','uk',1087,2061,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква святого Миколая започатковує &nbsp;рубрику &laquo;Аскольд єднає нас в родину&raquo;. Серія матеріалів представляє інтерв\'ю, нариси про парафіян та їхні сім\'ї.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хто бажає висловити свої побажання, розповіді чи власну історію знайомства з Аскольдовою могилою та перебування у цій спільноті просимо Вас звертатися до ведучого рубрики &nbsp;Олега Качура: +380937891313 &nbsp;<a href=\"mailto:Oleh456@ukr.net\">Oleh456@ukr.net</a></span></span></p>','2009-10-03 15:45:47','http://www.oranta.org/index.php?option=com_content&view=article&id=438:laskoldova-mogyla--velyka-rodynar&catid=25:parafija-cat&Itemid=27','P','','«Аскольд єднає нас в родину»','uk',1822,3305,0,NULL),
(1,1,'<p style=\"text-align: justify;\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5156,200,Pawel-VI.jpg\" />Високо</strong><strong>шанованим</strong><strong> братам</strong><strong> </strong><strong>Патріархам</strong><strong>, архиєпископам, єпископам</strong><strong> та іншим </strong><strong>правлячим архиєреям</strong><strong>, котрі живуть у мирі і спільноті з Апостольським </strong><strong>Престолом</strong><strong>,</strong></p>\n<p style=\"text-align: justify;\"><strong>духовенству та вірним цілого католицького світу а також усім людям доброї волі</strong></p>\n<p style=\"text-align: center;\"><strong>ПАПА ПАВЛО </strong><strong>VI</strong></p>\n','2013-10-12 10:23:55','http://oranta.org/index.php?option=com_content&view=article&id=1534:encyklika-papy-pavla-vi-pro-nalezhny-porjadok-peredavannja-ljudskogo-zhyttja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'ЕНЦИКЛІКА ПАПИ ПАВЛА VI ПРО НАЛЕЖНИЙ ПОРЯДОК ПЕРЕДАВАННЯ ЛЮДСЬКОГО ЖИТТЯ','uk',3674,5157,0,NULL),
(1,1,'<p style=\"text-align: justify;\">Благодійний проект \"З радістю у доросле життя\" уже втретє функціонує у семи областях України (Київська, Дніпропетровська, Закарпатська, Волинська, Тернопільська, Чернівецька, Львівська). Цьогоріч стартував передусім у Рівненській та Львівській областях. Уся техніка, підготовка програми профінансована товариствами волонтерів та парафіяльними центрами. Просимо Вас взяти участь у цій діяльності, і для подальшої роботи й розвитку ваших дітей частково долучитись до фінансування. На кінець місяця ми провели семінари в шести наступних інтернатах згідно договорів про співпрацю Львівського та Рівненського управління освіти та науки.</p>\n','2012-12-26 02:12:53','http://oranta.org/index.php?option=com_content&view=article&id=917:z-radistju-u-dorosle-zhyttja&catid=29:propovidi-cat&Itemid=34','P',NULL,'З радістю у доросле життя','uk',2881,4364,0,NULL),
(1,1,'<p style=\"text-align: justify;\">Від 11 до 18 cерпня 2013 року Божого в м. Києві проходив Синод Єпископів Української Греко-Католицької Церкви, у якому взяли участь 35 єпископів з України, країн Центральної та Західної Європи, Пiвнiчної і Пiвденної Америки та Австралiї. Публікуємо Постанови цього Синоду.</p>\n','2013-08-29 12:47:27','http://oranta.org/index.php?option=com_content&view=article&id=1428:postanovy-synodu-jepyskopiv-ukrajinskoji-greko-katolyckoji-cerkvy-2013-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Постанови Синоду Єпископів Української Греко-Католицької Церкви 2013 року','uk',3549,5032,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: left;\"><img style=\"margin: 10px; float: right;\" src=\"image,3535,200,10.JPG\" alt=\"\" /><strong>&laquo;Витай між нами, Христе, витай!<br />Щастя єдине в цей час гостини<br />В невиннім серці, серці дитини:<br />Ти наше сонце, життя і рай,<br />Витай між нами, Христе, витай!&raquo;</strong></p>','2010-05-11 20:58:26','http://oranta.org/index.php?option=com_content&view=article&id=526:lnakrashcha-nauka--ce-dobry-prykladr-ruslana-dmytrovska&catid=25:parafija-cat&Itemid=27','N','','«Найкраща наука – це добрий приклад» - Руслана Дмитровська','uk',2053,3536,0,NULL),
(1,1,'<p style=\"text-align: left;\">Ваші Блаженства, еміненції, ексцеленції,<br /> Високопреосвященні та преосвященні владики, члени Синоду Єпископів УГКЦ,<br /> Улюблені браття у священстві, преподобні монахи та монахині,<br /> Високодостойні представники української влади та дипломатичного корпусу,<br /> Дорогі у Христі брати і сестри!</p>\n<div style=\"text-align: left;\"></div>\n','2011-03-27 14:14:45','http://oranta.org/index.php?option=com_content&view=article&id=698:propovid-blazhennishogo-svjatoslava-shevchuka-glavy-ugkc-z-nagody-vvedennja-na-prestol&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Проповідь Блаженнішого Святослава (Шевчука), Глави УГКЦ, з нагоди введення на престол','uk',2364,3847,0,NULL),
(1,1,'<p style=\"text-align: right;\" class=\"rteright\">«Блаженні миротворці,<br /> бо вони синами Божими назвуться» (Мт. 5, 9).</p>\n<p style=\"text-align: center;\" class=\"rtejustify\">Дорогі у Христі!</p>\n<p class=\"rtejustify\">У цей пасхальний час, коли воскреслий Христос приходить до своїх учнів  перед своїм вознесінням, ми особливо переживаємо ту істину нашої віри,  що день святої Пасхи є для нас найбільшим і найважливішим Днем перемоги.  Саме цього дня Ісус Христос власною смертю переміг нашу смерть, а своїм  славним воскресінням дарував нам вічне життя. У день Пасхи зазнав  поразки неприятель людського роду – диявол, а у Христі перемогли ми всі.  Ісус Христос приходить до апостолів крізь замкнені двері, щоб передати  їм радість перемоги. Перші слова воскреслого Спасителя до апостолів «Мир  вам!» найповніше передають цю всепереможну радість, засвідчену  апостолом Іваном: «І зраділи учні, побачивши Господа» (Ів. 20, 20).</p>\n','2011-05-31 21:59:09','http://oranta.org/index.php?option=com_content&view=article&id=750:zvernennja-synodu-jepyskopiv-kyjevo-galyckogo-verhovnogo-arhyjepyskopstva-ugkc-do-duhovenstva-i-virnyh-ugkc-ta-vsih-ljude-dobroji-voli-pro-zberezhennja-suspilnogo-myru&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ до духовенства і вірних УГКЦ та всіх людей доброї волі про збереження суспільного миру','uk',2469,3952,0,NULL),
(1,1,'<p style=\"text-align: right;\" class=\"uiStreamMessage\" data-ft=\"{&quot;type&quot;:1}\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,305153_197723566975775_100002143980527_445030_557574148_n.jpg\" /><span style=\"font-size: 12pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Минає час, пливе рікою,<br /> Кудись звертає за бугор.<br /> Та пам`ять ця завжди зі мною<br /> Про той страшний голодомор.<br /> Забути все дитячі очі<br /> На фотокартках не дають,<br /> Серденько крають проти ночі<br /> І тінями в думках снують.<br /> Ніяк не можу зрозуміти,<br /> Збагнути те не можу сам:<br /> Що завинили бідні діти<br /> «Червоним» нелюдам – катам?!</span></span></p>\n','2011-11-26 02:13:56','http://oranta.org/index.php?option=com_content&view=article&id=808:pamjati-zhurtv-golodomoru&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пам\'яті жертв голодомору','uk',2614,4097,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: right;\"><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4680,200,79767102.jpg\" />\"... й ми, як отроки, знамена перемоги          носячи, Тобі, <br /> переможцеві смерти, воскликуємо: <br /> Осанна в вишніх, благословен, хто йде в ім\'я Господнє\"<br /> (Тропар Квітної неділі).</em></p>\n','2013-04-27 08:41:14','http://oranta.org/index.php?option=com_content&view=article&id=1147:kvitna-nedilja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Квітна неділя','uk',3198,4681,0,NULL),
(1,1,'<p style=\"text-align: right;\"><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6279,200,Presentation_titian.JPG\" /></strong>ТРОПАР СВЯТА</em></p>\n<p><em>Сьогодні благовоління Божого провіщення і спасіння людей проповідання, у храмі Божому ясно Діва являється і Христа всім провіщає; їй же і ми голосно заспіваймо: радуйся, промислу Створителя здійснення.</em></p>\n','2014-12-03 21:46:15','http://oranta.org/index.php?option=com_content&view=article&id=2538:vvedennja-v-hram-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Введення в храм Пресвятої Богородиці','uk',4797,6280,0,NULL),
(1,1,'<p style=\"text-align: right;\"><em>«Яка їх, Твоїх діл, Господи сила! У премудрості все Ти створив, -<br />повна земля Твоїх створінь!»</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>Пс. 103, 24 </em></p>\n<p style=\"text-align: center;\"><em>Дорогі у Христі!</em></p>\n<p>Починаючи новий Церковний рік, ми знову задумуємось над питанням свого християнського покликання у створеному Богом світі. Це питання стає все більш актуальним в контексті важкого захворювання нашої планети, яке науковці називають екологічною кризою.</p>\n','2010-09-14 11:46:24','http://oranta.org/index.php?option=com_content&view=article&id=582:zvernennja-blazhennishogo-ljubomyra-verhovnogo-arhyjepyskopa-kyjevo-galyckogo-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy-ta-ljude-dobroji-voli-z-nagody-dnja-boga-tvorcja-2010-r-b&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Любомира, Верховного Архиєпископа Києво-Галицького, до вірних Української Греко-Католицької Церкви та людей доброї волі з нагоди дня Бога Творця (2010 р. Б.)','uk',2144,3627,0,NULL),
(1,1,'<p style=\"text-align: right;\"><em>Всесвітлішим, всечеснішим </em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>та високопреподобним отцям-душпастирям</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>Київської архиєпархії УГКЦ</em></p>\n<p style=\"text-align: center;\"><strong>Слава Ісусу Христу!</strong></p>\n<p>Цього року, в неділю, 26 липня, наша Церква буде згадувати та урочисто святкувати 1021 річницю Хрещення Русі-України, дату, знаменну для всіх нас - спадкоємців Володимирового Хрещення.</p>\n<p>З цієї нагоди поручаю всім парохам та адміністраторам Київської архиєпархії:\n','2009-07-18 21:53:06','http://www.oranta.org/index.php?option=com_content&view=article&id=383:-hreshchennja-rusi-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' ХРЕЩЕННЯ РУСІ-УКРАЇНИ','uk',1687,3170,0,NULL),
(1,1,'<p style=\"text-align: right;\"><em>Господи, дай мені з душевним спокоєм зустріти</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><em>Все, що принесе мені цей день,</em></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong><em>Молитва Оптинських старців</em></strong></p>\n<p> </p>\n<p>Боже, дай мені сили &nbsp;день, що встиг &nbsp;наступити</p>\n<p>З упованням на тебе без гріха пережити.</p>\n<p>В &nbsp;душу не допусти люті сили ворожі,</p>\n<p>Хай спокуси спотворити серце &nbsp;не зможуть.</p>\n<p> </p>\n','2011-04-03 13:03:02','http://oranta.org/index.php?option=com_content&view=article&id=708:bytva-za-dushu-abo-molytva-rankova&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Битва за душу або Молитва ранкова','uk',2383,3866,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: right;\"><em>Сказав Господь:<br /> «Шукайте ж спершу  Царства Божого і правди Його, <br /> а все це додасться вам» (М</em><em>т.</em><em> 6, 33)</em></p>\n<p style=\"text-align: center;\">Дорогі у Христі брати і сестри!</p>\n<p>Двадцять років тому наш народ підтвердив свій вибір – жити у  самостійній, незалежній державі. Перед нами відкрилися великі  можливості, а разом з тим з’явилася і велика надія, що нарешті, після  століть поневолення і розділень, наша держава духовно й матеріально  розквітне.</p>\n','2011-12-02 18:14:38','http://oranta.org/index.php?option=com_content&view=article&id=810:zvernennja-tradycinyh-ukrajinskyh-cerkov-do-virnyh-ta-do-vsih-ljude-dobroji-voli-z-nagody-dvadcjatylittja-referendumu-na-pidtverdzhennja-aktu-progoloshennja-nezalezhnosti-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення традиційних Українських Церков до вірних та до всіх людей доброї волі з нагоди двадцятиліття референдуму на підтвердження Акту проголошення незалежності України','uk',2616,4099,0,NULL),
(1,1,'<p style=\"text-align: right;\"><img src=\"http://www.oranta.org/images/stories/2008/10/zus_01.jpg\" alt=\"Архиєпархіальна молодіжна проща \" style=\"float: left; margin: 5px; border: #000000 1px solid;\" title=\"Архиєпархіальна молодіжна проща \" width=\"150\" height=\"114\" /></p>\n<p>27-28 вересня відбулася молодіжна проща Київської архиєпархії до міста Вишгорода. У заході взяли участь близько 70-ти молодих осіб з парафіяльних молодіжних спільнот міст Києва, Вінниці, Фастова, Білої Церкви, Броварів, Чернігова, Вишгорода та молодіжні організації: «Обнова», «Пласт» та «СУМ». <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" />\n','2008-09-29 23:19:01','http://www.oranta.org/index.php?option=com_content&view=article&id=39:arhyjeparhialna-molodizhna-proshcha-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Архиєпархіальна молодіжна проща ','uk',507,1487,0,NULL),
(1,1,'<p style=\"text-align: right;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4052,200,DSCN9063.JPG\" />Туди, звідкіль вертаються птахи,</p>\n<p style=\"text-align: right;\">Ми дивимось задумливо і тихо,</p>\n<p style=\"text-align: right;\">На жаль, багато ще не знаєм ми</p>\n<p style=\"text-align: right;\">у круговерті днів, словесних віхол.</p>\n<p style=\"text-align: right;\">Не ті слова зроняємо із вуст,</p>\n<p style=\"text-align: right;\">І погляд наш - докірливий, холодний,</p>\n','2011-09-15 20:36:32','http://oranta.org/index.php?option=com_content&view=article&id=790:pogljad&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Погляд...','uk',2573,4056,0,NULL),
(1,1,'<p style=\"text-align: right;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4725,200,6nedila_pro_slipoho.jpg\" height=\"177\" width=\"119\" />Рим, СС – 05.04.2013 Сліпонародженого (Ів. 9: 1-38)</p>\n<p>Фрагмент Євангелії про сліпородженого добре відображає характер фарисеїв та їм подібних, і одночасно показує, що звичайна людина своєю простотою і щирістю може виявити їх лицемірство. Головною особою євангельської події є сліпонароджений – людина, яка ніколи і нічого в своєму житті не бачила. Господь зустрічає його, оздоровляє - повертає йому зір… Крім, суто фізичного і тілесного значення, ця подія має також і <span style=\"text-decoration: underline;\">символічне значення</span>. А саме, Господь Ісус Христос є тим, хто дає прозріння, хто повертає світло людським очам. Дає їм світло Боже…</p>\n','2013-06-08 10:30:48','http://oranta.org/index.php?option=com_content&view=article&id=1204:nedflja-sliporodzhenogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Неділя сліпородженого','uk',3314,4797,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: right;\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5331,200,AirPano_Maidan.jpg\" />Стоїть Аскольд, стоїть Андрій,</p>\n<p style=\"text-align: right;\">І дивляться на Київ свій,</p>\n','2013-12-10 10:42:55','http://oranta.org/index.php?option=com_content&view=article&id=1704:sogodni-znov-nadiji-chas&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Сьогодні знов надії час','uk',3849,5332,0,NULL),
(1,1,'<p style=\"text-align: right;\"><strong><em>Бо Дитя народилося нам, даний нам Син,</em></strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong><em>і влада на раменах Його, і кликнуть ім\'я Йому:</em></strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong><em>Дивний Порадник, Бог сильний, Отець вічности, Князь миру.</em></strong></p>\n<p style=\"text-align: right;\"><strong><em>(Iс.9:5)</em></strong></p>\n','2011-11-28 22:12:22','http://oranta.org/index.php?option=com_content&view=article&id=809:zvernennja&catid=31:kolonka-redaktora-cat&Itemid=1','P','','Звернення','uk',2615,4098,0,NULL),
(1,1,'<p style=\"text-align: right;\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4114,200,rizdvo.jpg\" />«У Вифлеємі новина, </strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong>Діва сина породила,</strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong>Породила в благодаті</strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong>Непорочна Діва-Мати Марія»</strong></p>\n<p> </p>\n<p><strong>Дорогі парафіяни!</strong></p>\n<p>У Святу Різдвяну ніч!</p>\n','2011-12-31 13:41:33','http://oranta.org/index.php?option=com_content&view=article&id=818:u-svjatu-rizdvjanu-nich&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'У Святу Різдвяну ніч','uk',2632,4115,0,NULL),
(1,1,'<p style=\"text-align: right;\"><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4657,200,allfons.ru-608.jpg\" />крихітка-крокус</strong></p>\n<p style=\"text-align: right;\">у тлі-імлі на фоні всього світу</p>\n<p style=\"text-align: right;\">завмерли сни, прикуті до граніту</p>\n<p style=\"text-align: right;\">зими-злоби, гріха як моноліту.</p>\n<p style=\"text-align: right;\">земля ж в чеканні тихого привіту...</p>\n<p style=\"text-align: right;\"> </p>\n','2013-04-19 00:09:01','http://oranta.org/index.php?option=com_content&view=article&id=1128:vesnjane-do-dushi&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Весняне до душі','uk',3175,4658,0,NULL),
(1,1,'<p style=\"text-align: right;\"><strong>КОЛИ БЕРЕШ - НАПОВНЮЄШ РУКУ</strong></p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\"><strong>КОЛИ ДАЄШ - НАПОВНЮЄШ СЕРЦЕ</strong></p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2540,200,pos2.jpg\" height=\"200\" width=\"150\" />Парафія церкви св. Миколая Чудотворця на Аскольдовій Могилі продовж багатьох років за навчанням Господа нашого Ісуса Христа провадить діла милосердя. Наша церква має за мету служити своєму ближньому, слідувати науці святого Євангелія. Ми допомагаємо потребуючим віднайти у собі сили, укріпитися у вірі, навернутися до Бога, повернутися до праці, а також у такий спосіб спонукаємо їх теж надалі творити діла милосердні, допомагати потребуючим.</p>\n<p>Милосердя до ближнього засвідчено 12 літньою історією нашої парафії і тисячолітньою історією цього Світлого місця. Якщо заглянути у давнину, то ми знайдемо багато свідчень, що саме тут на Аскольдові Могилі, завжди знаходили допомогу потребуючі. Завжди принесена панахида поминання спочилих роздавалась убогим.</p>\n','2009-05-15 14:32:29','http://www.oranta.org/index.php?option=com_content&view=article&id=323:-slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,' Слово від Редакції','uk',1485,2541,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p style=\"text-align: right;\"><strong>Липень 2011 Божого церква св. Миколая Чудотворця на Аскольдовій Могилі</strong></p>\n<p style=\"text-align: center;\"><strong>Слава Ісусу Христу</strong>!</p>\n<p style=\"text-align: center;\"><strong>Дорогі парафіяни!Дорогі сестри!</strong></p>\n<p>Церковна громада покликана до молитви і служіння любові і милосердя. Бо так кличе всемилостивий Ісус: - прийдіть до мене всі втомлені й обтяжені, і я облегшу вас. І ми стараємось за Євангелієм служити ближньому. Бо що зробимо одному з найменших братів моїх, Господу зробимо і собі допоможемо царство Боже здобути. Багатолітнє служіння ув’язненій жінці, матері, дівчині і допомога покинутій дитині продовжується спільними зусиллями. Особливою подією був дарунок 50 КОБЗАРІВ у слідчий жіночий ізолятор Лук’янівської в’язниці,також було подаровано 50 БІБЛІЙ від «Карітас-Київ» стараннями директора о. Романа Сиротича.</p>\n','2011-07-04 15:24:17','http://oranta.org/index.php?option=com_content&view=article&id=768:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Слово від Редакції','uk',2517,4000,0,NULL),
(1,1,'<p style=\"text-align: right;\">КОЛИ БЕРЕШ - НАПОВНЮЄШ РУКУ</p>\n<p style=\"text-align: right;\">КОЛИ ДАЄШ - НАПОВНЮЄШ СЕРЦЕ</p>\n<p style=\"text-align: center;\"> <strong>Великодній кошик</strong></p>\n<p><img src=\"http://www.oranta.org/images/stories/2009/03/6.jpg\" style=\"float: right; margin: 10px; border: #000000 1px solid;\" />Піст, поклінні молитви, 40-ка денна дорога християнина в часі великого посту - це заслуга воскресіння паски Христової. У цій дорозі з нами перебуває, нас наставляє Сам Господь, наш Ісус Христос. У цей час кожен з нас прагне наповнити великодній кошик радістю Христового Воскресіння, благодаттю Божою для себе, для своєї родини, для України. Особливою допомогою у цій дорозі для кожного із нас є діла милосердя.</p>\n<p>Великодній піст у сьогоднішній день, в час економічної нестабільності в світі і Україні, ніби підказує, як знайти дорогу рятунку кожній особистості і загалу. Ця дорога - через діла милосердя до ближнього. У своєму першому посланні святий Йоан вчить: «коли хтось має достатки цього світу і бачить брата свого в нестачі й замикає перед ним своє серце, то як любов Божа може перебувати в ньому» 3, 17. Добровільний піст допомагає нам стати милостивим самарянином. Перші громади християн збирали пожертви (римляни 15, 25-27), віддавали бідним те, що було відкладене завдяки посту.</p>\n','2009-03-27 17:25:28','http://www.oranta.org/index.php?option=com_content&view=article&id=262:-slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,' Слово від Редакції','uk',1294,2268,0,NULL),
(1,1,'<p style=\"text-align: right;\">Київ, 27 січня &nbsp;2011 р.Б</p>\n<div style=\"text-align: right;\"></div>\n<p style=\"text-align: right;\">Вих.: КА-11/016</p>\n<p><strong> </strong></p>\n<p><strong> </strong></p>\n<p style=\"text-align: center;\"><strong>Всесвітлішим, всечеснішим ієреям преподобним ієромонахам - парохам, адміністраторам і сотрудникам Київської архиєпархії УГКЦ </strong></p>\n<p style=\"text-align: center;\"><strong>Христос рождається!</strong></p>\n<p style=\"text-align: center;\"><strong> </strong></p>\n<p><strong> </strong></p>\n<p><strong>Всесвітліші й всечесніші ієреї, преподобні ієромонахи!</strong></p>\n<p style=\"text-align: center;\"><strong> </strong></p>\n<p>За дорученням Преосвященного Владики Йосифа, повідомляю Вам, що, згідно розпорядження Блаженнішого Любомира (<em>Вих.: Р-11/005 від 06 січня 2011 р.Б.</em>) в неділю Блудного Сина потрібно оприлюднити Звернення про душпастирство позбавлених волі (Звернення у додатку), як рівно ж розмістити дане Звернення на дошці оголошень та парохіяльних ЗМІ (газета, веб-сайт)</p>\n<p>Щиро у Христі,</p>\n<p><strong>о. Василь Чудійович</strong>, нотар курії Київської архиєпархії</p>\n','2011-01-27 21:23:15','http://oranta.org/index.php?option=com_content&view=article&id=668:zvernennja-blazhennishogo-ljubomyra&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Любомира','uk',2303,3786,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>&laquo;Пасха наша, Христос, принесений у жертву&raquo;, - словами з першого листа Святого Апостола Павла до Коринтян Святіший Отець розпочав свою проповідь під час великодньої Служби Божої, яку він, о 10:15 місцевого часу, очолив на площі Святого Петра у Ватикані і яку разом з Папою молилися приблизно 200 тисяч вірних.</p>','2009-04-15 09:41:51','http://www.oranta.org/index.php?option=com_content&view=article&id=280:hrystos--nasha-pasha-velykodnja-propovid-papy-venedykta-xvi&catid=32:gk-svit-cat&Itemid=58','N','','Христос – наша Пасха. Великодня проповідь Папи Венедикта XVI','uk',1349,2323,0,NULL),
(1,1,'<p>&nbsp;</p>\r\n<p><img style=\"margin: 10px; float: left;\" title=\"Ірина Кондратюк\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53731_kondr.jpg\" alt=\"Ірина Кондратюк\" width=\"173\" height=\"173\" /></p>\r\n<p>У третю неділю вересня в Україні відзначають День батька. З цього приводу в рамках Форуму видавців у Львові видавництво &laquo;Свічадо&raquo; презентувало нову книгу &laquo;Люблю тебе, Тату&raquo;. &laquo;Насправді, причина зустрічі є значно глибша, &mdash; розпочав директор видавництва &laquo;Свічадо&raquo; Богдан Трояновський. &ndash; Коли я був студентом, професор психології сказала нам таку цікаву проблему, про яку ми навіть не задумувалися. ХІХ-ХХ століття &ndash; це були століття жінки, матері, століття, в яких жінка отримала певні права. Вона стала незалежною, самостійною. Натомість, появилася серйозна криза чоловіка, батька. Дуже часто в цій новій ролі чоловіки не могли себе віднайти. Ця проблема залишилась актуальною. Бо, дійсно, питання Що значить бути батьком, чоловіком, є важливим і сьогодні&raquo;.</p>\r\n<p>&nbsp;</p>','2013-09-23 09:47:25','http://oranta.org/index.php?option=com_content&view=article&id=1493:lmi-tato-zavzhdy-poruch-u-mene--ogo-ochir&catid=37:spilnota-cat&Itemid=49','N','','«Мій тато завжди поруч… У мене – його очі»','uk',3617,5100,0,NULL),
(1,1,'<p>&nbsp;<img style=\"margin: 10px; float: left;\" src=\"http://www.religion.in.ua/uploads/posts/2011-07/thumbs/1310923068_.0.jpg\" alt=\"Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі\" title=\"Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі\" height=\"131\" width=\"198\" />15 липня – свято Ризопокладання у Влахерні та пам\'ять про перше Оскольдове Хрещення Русі</p>\n<p>Ведучи мову про початок християнізації Русі, неможливо не зупинитися   більш детально на Чуді з Чесною Ризою пресвятої Богородиці у Влахерні   під час облоги Константинополя у 860 році русичами на чолі з князем   Оскольдом.</p>\n','2013-07-15 10:19:07','http://oranta.org/index.php?option=com_content&view=article&id=1327:svjato-ryzopokladannja-u-vlaherni-ta-pamjat-pro-pershe-oskoldove-hreshchennja-rusi&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Свято Ризопокладання у Влахерні та пам’ять про перше Оскольдове Хрещення Русі','uk',3434,4917,0,NULL),
(1,1,'<p>&nbsp;<img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,klasyfikacija-koshtovnyh-kameniv-03.jpg\" />Жив колись алхімік, який усе своє життя присвятив пошукам філософського каменя, що має силу перетворювати залізні предмети у золото. «Я досліджу усі каміння на землі, один за другим. І так напевно знайду філософський камінь», - думав він.</p>\n<p>Спершу це видавалося простим. Алхімік обв’язався залізним ланцюгом і торкався до нього кожним знайденим каменем. Він усе мандрував, а коли помітив якийсь камінь – брав його і торкався ланцюга. Той жест став усім його життям.</p>\n','2013-06-28 04:21:55','http://oranta.org/index.php?option=com_content&view=article&id=1277:alhimik-rozzjava-28-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Алхімік-роззява (28 червня)','uk',3393,4876,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>&nbsp;<img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5046,200,christ.jpg\" />Жив колись на світі славний канатоходець. Усі люди захоплювалися його  надзвичайною спритністю. Ніхто ніколи не бачив, щоб він упав чи бодай  втратив рівновагу.</p>\n<p>&nbsp;Настав час, коли в цирку, де він працював, почалися серйозні фінансові проблеми.</p>\n<p>Щоб привабити публіку, директор запропонував артистові підняти канат вище і додати небезпечних трюків.</p>\n','2013-09-06 00:15:11','http://oranta.org/index.php?option=com_content&view=article&id=1441:-kanatohodec-6-veresnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Канатоходець (6 вересня)','uk',3564,5047,0,NULL),
(1,1,'<p>&nbsp;Вироком Спеціальної колегії Київського обласного суду від 14 жовтня 1935 р. була засуджена група кобзарів, які в 1920-х та на початку 1930-х років подорожували по теренам Київської та Дніпропетровської області, виконуючи народні пісні. Мали вони бібліотеку, де було понад сімсот книжок. Тут пропонується коротка інформація про цих кобзарів.</p>\n','2011-10-07 13:49:33','http://oranta.org/index.php?option=com_content&view=article&id=796:tragichna-istorija-ukrajinskogo-kobzarstva&catid=37:spilnota-cat&Itemid=49','P',NULL,'Трагічна історія українського кобзарства (хроніка)','uk',2583,4066,0,NULL),
(1,1,'<p>&nbsp;З милості Божої, вже вкотре відбулась поїздка хору «Аскольд» до м. Коломиї. На празник Преображення Господнього, ми мали чудову нагоду разом зі священиками відслужити дві Божественні Літургії.</p>\n','2011-09-05 19:27:53','http://oranta.org/index.php?option=com_content&view=article&id=784:parafijalny-hor-laskoldr-u-kolomyji&catid=25:parafija-cat&Itemid=27','P',NULL,'Парафіяльний хор «Аскольд» у Коломиї','uk',2562,4045,0,NULL),
(1,1,'<p>* Спіраль убиває дітей! При її вживанні зачаття відбувається, але дитина найчастіше вмирає в перші дні, тому що спіраль не дозволяє їй імплантуватися (закріпитися) в матці. Якщо відбувається затримка менструації, то це значить, що дитина все таки вижила. Треба зберегти їй життя. Спіраль перетворює здорову жінку в хвору (запалення і нагноєння в матці, яйцеводах, черевній порожнині, кровотечі, болі, недокрів\'я...).</p>\n','2010-02-23 21:44:17','http://www.oranta.org/index.php?option=com_content&view=article&id=497:dytyna-darunok-bozhy&catid=37:spilnota-cat&Itemid=49','P','','Дитина дарунок Божий!','uk',1974,3457,0,NULL),
(1,1,'<p>-  Я сидів сам-один в купе потягу. Згодом за­йшла якась дівчина, -  розповідав незрячий юнак-індус. - Чоловік та жінка, що її проводжали,  напевно, були їй батьками. Вони давали їй силу-силенну порад і напучень.  Не знаю, як виглядала та дівчина, але барва її голосу мені сподобалася.</p>\n<p>\"Чи  їде вона до Дехра Дун?\" - подумав я, коли потяг рушив. І став міркувати  над тим, як не зра­дити їй, що я невидющий. Поклав собі так: якщо не  підводитися з місця, то сліпоти своєї можна й не виказати.</p>\n','2013-02-26 22:58:58','http://oranta.org/index.php?option=com_content&view=article&id=1006:zustrich-27-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Зустріч (27 лютого)','uk',2981,4464,0,NULL),
(1,1,'<p>02 серпня 2009 року діти парафіян церкви св. Миколая, що на Аскольдовій могилі, відпочиваючи в Карпатах побували між двома висотними вершинами зліва Маковицею та справа - Чорногорицею, де поблизу Яремче  розташувалося Дора - дуже давнє маленьке село на Гуцульщині.</p>\n','2009-08-12 22:00:44','http://www.oranta.org/index.php?option=com_content&view=article&id=406:-duzhe-davnje-malenke-selo-na-guculshchyni&catid=37:spilnota-cat&Itemid=49','P',NULL,'Дора – дуже давнє маленьке село на Гуцульщині','uk',1732,3215,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>1-ше вересня 1939 року ввійшло в історію як початок Другої світової війни. Для жителів Західної України з цього дня починається нова історична доба, яка й по сьогодні не закінчилась. Для корінних жителів встановлення нової влади супроводжувалось репресіями, терором, втратою приватних земельних наділів, виселенням з рідних домівок та багато іншого. З 1944 року почалось повторне встановлення радянської влади, що супроводжувалось масовим збройним спротивом при активній підтримці всього місцевого населення.</p>\n','2009-08-31 19:54:24','http://www.oranta.org/index.php?option=com_content&view=article&id=421:prymyrennja-jak-test-na-shchyrist-kandydativ-v-prezydenty&catid=37:spilnota-cat&Itemid=49','P',NULL,'Примирення, як тест на щирість кандидатів в президенти','uk',1767,3250,0,NULL),
(1,1,'<p>10 травня 2009 року у Неділю Розслабленого наша Українська Греко-Католицька Церква буде відзначати День хворого<strong> </strong>у рамках<strong> </strong>XVII Всесвітнього Дня хворого. Відзначення Дня хворого має за мету привернути увагу християнської спільноти та усього суспільства на хворих людей, їх родини та тих, хто облегшує їхні страждання, зокрема медичний персонал та волонтерів.</p>\n<p>Цього року у своєму Посланні з нагоди Всесвітнього Дня хворого Святіший Отець Венедикт XVI звертає свою увагу на дітей, які страждають від різних недуг. Зважаючи на це, Комісія УГКЦ у справах душпастирства охорони здоров\'я просить усіх віруючих та усіх людей доброї волі долучитись у цей день до спільної молитви та заходів, які мають на меті звернути особливу увагу на хворих дітей в Україні.</p>\n','2009-05-05 19:00:37','http://www.oranta.org/index.php?option=com_content&view=article&id=312:10-travnja-den-hvorogo-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'10 травня - День хворого  ','uk',1454,2510,0,NULL),
(1,1,'<p>10 травня 2009 року, у Неділю розслабленого, наша Українська Греко-Католицька Церква буде відзначати День хворого у рамках XVII Всесвітнього Дня хворого. Відзначення Дня хворого має за мету привернути увагу християнської спільноти та усього суспільства до хворих людей, їх родини та тих, хто облегшує їхні страждання, зокрема медичний персонал та волонтерів.</p>\r\n<p>Цього року у своєму Посланні з нагоди Всесвітнього Дня хворого Святіший Отець Венедикт XVI звертає свою увагу на дітей, які страждають від різних недуг. Зважаючи на це, Комісія УГКЦ у справах душпастирства охорони здоров\'я просить усіх віруючих та усіх людей доброї волі долучитись у цей день до спільної молитви та заходів, які мають на меті звернути особливу увагу на хворих дітей в Україні.</p>','2009-05-10 01:20:16','http://www.oranta.org/index.php?option=com_content&view=article&id=318:10-travnja-den-hvorogo-&catid=27:gk-v-ukrajini-cat&Itemid=57','N','','10 травня - День хворого  ','uk',1468,2524,0,NULL),
(1,1,'<p>11 - 14 грудня у місті Тренто (Італія) проходив Перший європейський православно-католицький форум на тему: \"Сім\'я - добро для людства\". У форумі взяли участь представники Римо-Католицької та Православних Церков Європи. Всього - близько 30 учасників.</p>\n<p>Від РКЦ у форумі взяли участь: Петер Кардинал Ердьо, президент Ради Єпископських Конференцій Європи (Consilium Conferentiarum Episcoporum Europae (CCEE)), Владика Тадеуш Кондрусевич, Архиєпископ Мінський та Могильовський, Владика Паоло Пецці, Архиєпископ архиєпархії Мати Божої в Москві, Монсіньйор Гжегош Кащак, секретар Папської Ради у справах родини, та інші. Православні Церкви були представлені такими учасниками: Владика Іларіон (Алфеєв) від Російської Православної Церкви, архимандрит Арсеніос (Кардамакіс) від Вселенської Патріархії, Владика Андон (Мердані) від Автокефальної Православної Церкви Албанії, Владика Сілуан від Румунської Православної Церкви та інші.</p>\n<p>\n','2008-12-16 12:33:35','http://www.oranta.org/index.php?option=com_content&view=article&id=144:pershy-jevropesky-pravoslavno-katolycky-forum-pro-rodynu&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Перший європейський православно-католицький форум про родину','uk',811,1791,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>11 листопада 2008 року в приміщенні центрального апарату Державного департаменту України з питань виконання покарань проходили спільні збори працівників Державної кримінально-виконавчої служби України (ДКВС) та представників Української міжконфесійної християнської місії «Духовна та благодійна опіка в місцях позбавлення волі». Місія забезпечує толерантне, гідне християн, співслужіння для відродження багатьох душ.</p>\n<p>\n','2008-11-12 23:34:53','http://www.oranta.org/index.php?option=com_content&view=article&id=95:poklykannja-cerkvy-do-penitenciarnogo-dushpastyrstva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Покликання Церкви до пенітенціарного душпастирства','uk',643,1623,0,NULL),
(1,1,'<p>12 листопада до Церковної хатини активний член громади, публіцист Іван Рибалко на своїх «Жигулях» першої моделі двома ходками доставив партію гуманітарної допомоги зі Штутгарту, що в Німеччині...</p>\n<p>Протягом літа - осені сеньйори з Української парафії Святого Василія Великого, що включає в себе майже всю територію Вюртембергу, з центром у Штутгарті збирали для київських дітей одяг та взуття, постільну білизну та серветки, іграшки та канцелярські товари. Акція проходила з благословення отця-пароха Романа Врущака. Шановна в Українській громаді людина, кавалер німецької урядової нагороди пані Надія Сайц (народжена Павлюк) надала під тимчасовий склад святкову залу у власному будинку. Найбільше речей для юних киян зібрала Анастасія Цюрак. До доброї справи долучилися і пан Дмитро Чабанюк, і пані Галина Наук. Багато дитячих речей надали і їх німецькі друзі та сусіди. У результаті зібралася партія з 10 картонних ящиків, 5 чемоданів та декількох великих сумок.</p>\n<p>\n','2008-11-17 00:58:36','http://www.oranta.org/index.php?option=com_content&view=article&id=99:shtutgart-kyjiv-mistok-druzhby&catid=29:propovidi-cat&Itemid=34','P',NULL,'Штутгарт – Київ. Місток дружби','uk',653,1633,0,NULL),
(1,1,'<p>12 листопада католики, що дотримуються нового календарного стилю, вшановують пам\'ять святого священомученика Йосафата. Сьогодні, як і щороку, українська спільнота Риму зібралася у найбільшому католицькому храмі світу - ватиканскій базиліці Святого Петра, де під престолом святого Василія Великого спочивають мощі першого українського святого, якого канонізував Вселенський Архиєрей.</p>\n<p>\n','2008-11-12 14:25:40','http://www.oranta.org/index.php?option=com_content&view=article&id=92:ukrajinska-svjata-liturgija-v-bazylici-svjatogo-petra-u-vatykani&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Українська Свята Літургія в базиліці Святого Петра у Ватикані','uk',637,1617,0,NULL),
(1,1,'<p>13 листопада стартувала підготовка до ІІІ Всеукраїнської екуменічної благочинної акції \"Святий Миколай мандрує Україною\". Зокрема, в Києві в приміщенні Міжнародного благодійного фонду \"Україна 3000\" відбулася робоча зустріч організаторів, регіональних координаторів та партнерів акції, - повідомили ZAXID.NET організатори.</p>\n<p>Уже третій рік поспіль у межах акції діти-сироти, неповносправні та діти з малозабезпечених сімей у 24 областях України та АР Крим отримають подарунки до Дня Святого Миколая, який в Україні відзначають традиційно 19 грудня.</p>\n<p>\n','2008-12-15 19:52:25','http://www.oranta.org/index.php?option=com_content&view=article&id=136:qsvjaty-mykola-mandruje-ukrajinojuq&catid=29:propovidi-cat&Itemid=34','P',NULL,'\"Святий Миколай мандрує Україною\"','uk',800,1780,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>14 вересня в Українській Греко-Католицькій Церкві святкуватимуть Всесвітній Екологічний <em>День Творця</em>.&nbsp; В цей день в греко-католицький храмах молитимуться та служитимуть екологічні служби за збереження природи.</p>\n<p>Молебень за участю Глави УГКЦ, Патріарха Любомира (Гузара) відбудеться у храмі св. Миколая на Аскольдовій Могилі (м. Київ, вул. Паркова Дорога 1) 18:00.</p>\n','2010-09-13 19:06:49','http://oranta.org/index.php?option=com_content&view=article&id=578:ugkc-vizme-uchast-u-vsesvitnomu-dnju-tvorcja-&catid=25:parafija-cat&Itemid=27','P','','УГКЦ візьме участь у Всесвітньому Дню Творця ','uk',2140,3623,0,NULL),
(1,1,'<p>14 липня 2011 року священик Ігор з волонтерами відвідав Чернігівську виправну установу для жінок. Недалеко від Києва 120 км. Церквою св. Катерини зустрічає нас княжий Чернігів, який завжди прагнув першості перед Києвом. До речі церква св. Катерини належиться Київському патріархату і є загроза її захопленню вірними українцями московського патріархату, які поставили поруч похідну церкву. Священик церкви гостинно нас прийняв і також був на зустрічі з ув’язненими жінками.</p>\n','2011-07-16 23:19:48','http://oranta.org/index.php?option=com_content&view=article&id=773:de-nadija-de-nadija-iz-golgoty-iz-hrestom&catid=28:tema-nomera-cat&Itemid=2','P','','Йде надія, йде надія із Голготи, із хрестом…','uk',2533,4016,0,NULL),
(1,1,'<p>14 серпня 2009 року</p>\n<p>Коротка історія</p>\n<p>Християни перших віків до великих празників завжди приготовлялися постом і молитвою. З цієї священної практики з часом розвинулися коротші чи довші пости. На першому місці стоїть тут Великий Піст перед світлим празником Господньої Пасхи. Перед празником Христового Різдва ввійшов у звичай піст Пилипівки. З особливого культу до святих верховних апостолів Петра й Павла зродився піст Петрівки. А вкінці, прийшов наймолодший з чотирьох річних постів, піст Успенський. Ним ми приготовляємо себе до найбільшого празника Пресвятої Богородиці, її святого Успення.\n','2009-08-05 00:04:03','http://www.oranta.org/index.php?option=com_content&view=article&id=397:pochatok-uspenskogo-postu&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Початок Успенського посту','uk',1709,3192,0,NULL),
(1,1,'<p>1622 — наказ царя Михайла з подання московського патріарха Філарета спалити в державі всі примірники надрукованого в Україні “Учительного Євангелія”К. Ставровецького.</p>\n<p>1696 — ухвала польського сейму про запровадження польської ови в судах і установах Правобережної України.</p>\n','2010-11-10 07:53:57','http://oranta.org/index.php?option=com_content&view=article&id=615:korotky-vyklad-istorychnyh-dat-na-jaki-prypadajut-nazhorstokishi-zaborony-ridnoji-dlja-ukrajinciv-movy&catid=37:spilnota-cat&Itemid=49','P',NULL,'Короткий виклад історичних дат, на які припадають найжорстокіші заборони рідної для українців мови','uk',2209,3692,0,NULL),
(1,1,'<p>17 грудня Святої Великомучениці Варвари</p>\n<p><img style=\"margin: 5px; float: right;\" src=\"http://diaspora.ukrinform.ua/img/trad/Varvara.jpg\" height=\"190\" width=\"140\" /></p>\n<p>Свята Варвара жила в ІІІ ст. у фінікійському місті Іліополі. Після смерті матері її виховував батько Діоскор, який був багатим і шляхетним міщанином, переконаним язичником. Познайомившись з християнами, Варвара згодом прийняла святе хрещення від олександрійського священика. Не зламали Св. Варвару ні погрози батька, ні увіщування та тортури управителя Мартіана. Зважаючи на непохитність та те, що й інші почали наслідувати її християнську дорогу Варвару вирішили стратити. У 306 році Св. Варварі та її послідовниці - дівчині Юліанії було відсічено голови, а Діоскор та Мартіан, як оповідали, загинули того ж дня від блискавки й громовиць.</p>\n<p>\n','2008-12-16 10:16:52','http://www.oranta.org/index.php?option=com_content&view=article&id=143:svjata-velykomuchenycja-varvara&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Свята Великомучениця Варвара','uk',809,1789,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>17-18 грудня 2008 року у Львові-Брюховичах відбулася Сорок друга сесія Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви. На сесійних засіданнях були присутні 17 членів Митрополичого Синоду. Сьогодні ми публікуємо постанови цієї сесії Синоду.</p>\n<p>\n','2008-12-23 09:50:50','http://www.oranta.org/index.php?option=com_content&view=article&id=157:publikujemo-postanovy-sorok-drugoji-sesiji-mytropolychogo-synodu-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Публікуємо постанови Сорок другої сесії Митрополичого Синоду ','uk',846,1826,0,NULL),
(1,1,'<p>19 вересня у м. Донецьку виходить у світ духовна книга священика о. Василя Пантелюка «Проповідуй і вчи», присвячена 60 річчю владики Донецько-харківського екзархату Степана Менька, а також 25-річчю його священства.</p>\n','2009-08-21 23:53:53','http://www.oranta.org/index.php?option=com_content&view=article&id=416:zbirnyk-istori-katolyckyh-parafi-shodu-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Збірник історій католицьких парафій сходу України','uk',1749,3232,0,NULL),
(1,1,'<p>19 листопада 2008 відбулася подія якої ще за 14 років відновленої діяльності Українського Католицького Університету не було. Перший раз в цих стінах відбулася конференція від початку і до кінця організована і проведена студентами УКУ. Тема конференції - «Стан релігійних ЗМІ в УГКЦ».</p>\n<p>Ініціаторами заходу були члени Студентської Ради (орган самоуправління студентів УКУ). Робота конференції була побудована таким чином, що спочатку учасники конференції мали змогу ознайомитися зі станом християнських ЗМІ греко-католицької Церкви, а потім поділитися своїми враженнями, пропозиціями і побажаннями під час роботи в  групах.</p>\n<p>\n','2008-11-20 20:55:37','http://www.oranta.org/index.php?option=com_content&view=article&id=108:studenty-uku-i-zhurnalistyka--shcho-spilnogo-&catid=37:spilnota-cat&Itemid=49','P',NULL,'Студенти УКУ і журналістика – що спільного?  ','uk',687,1667,0,NULL),
(1,1,'<p>19 серпня 2009 року о 10.00 у виставковому залі храму Св. Василія Великого Української Греко-Католицької Церкви <em>(Київ, Вознесенський узвіз, 7)</em>, відбудеться відкриття експозиції образотворчого та декоративно-прикладного мистецтва засуджених, які відбувають покарання в установах позбавлення волі.</p>\n','2009-08-18 22:12:15','http://www.oranta.org/index.php?option=com_content&view=article&id=408:glava-ugkc-vizme-uchast-u-vidkrytti-vystavky-obrazotvorchogo-ta-dekoratyvno-prykladnogo-mystectva-vjazniv&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ візьме участь у відкритті виставки образотворчого та декоративно-прикладного мистецтва в’язнів','uk',1739,3222,0,NULL),
(1,1,'<p>19 січня 2009 року <br /><br /><img height=\"162\" width=\"134\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_bogojavlennya.jpg.jpg\" style=\"margin: 10px; float: right;\" />Святе Богоявлення Господа Бога і Спаса нашого Ісуса Христа</p>\n<p>Коротка історія</p>\n<p align=\"justify\">Свята Церква перед празником Господнього Богоявлення у богослуженнях звертається до своїх вірних: \"Вифлеєм залишивши, преславне чудо, спішімо до Йордану душею гарячою і там побачимо страшне таїнство\" (Світилен утрені з 3 січня). Це таїнство Господнього Богоявлення, це Хрещення Господа нашого Ісуса Христа в ріці Йордані. У цьому таїнстві Ісус Христос об\'являє себе як Месію і Спасителя. Про Його Боже післанництво свідчить при Хрещенні сам Отець Небесний голосом із неба: \"Ти єси Син мій любий, у тобі - моє уподобання\" (Мр. 1, 11); свідчить Святий Дух, що у вигляді голуба сходить на Нього; свідчить і святий Йоан Хреститель, вказуючи на Нього: \"Ось Агнець Божий, який гріхи світу забирає\" (Йо. 1, 29).</p>\n','2011-01-17 18:23:46','http://oranta.org/index.php?option=com_content&view=article&id=187:svjate-bogojavlennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святе Богоявлення Господа Бога і Спаса нашого Ісуса Христа','uk',2290,3773,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>19 січня 2009 року <br /><br /><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_bogojavlennya.jpg.jpg\" width=\"134\" height=\"162\" />Святе Богоявлення Господа Бога і Спаса нашого Ісуса Христа</p>\n<p>Коротка історія</p>\n<p align=\"justify\">Свята Церква перед празником Господнього Богоявлення у богослуженнях звертається до своїх вірних: \"Вифлеєм залишивши, преславне чудо, спішімо до Йордану душею гарячою і там побачимо страшне таїнство\" (Світилен утрені з 3 січня). Це таїнство Господнього Богоявлення, це Хрещення Господа нашого Ісуса Христа в ріці Йордані. У цьому таїнстві Ісус Христос об\'являє себе як Месію і Спасителя. Про Його Боже післанництво свідчить при Хрещенні сам Отець Небесний голосом із неба: \"Ти єси Син мій любий, у тобі - моє уподобання\" (Мр. 1, 11); свідчить Святий Дух, що у вигляді голуба сходить на Нього; свідчить і святий Йоан Хреститель, вказуючи на Нього: \"Ось Агнець Божий, який гріхи світу забирає\" (Йо. 1, 29).</p>\n','2010-01-17 18:23:46','http://www.oranta.org/index.php?option=com_content&view=article&id=187:svjate-bogojavlennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святе Богоявлення Господа Бога і Спаса нашого Ісуса Христа','uk',975,1949,0,NULL),
(1,1,'<p>19 червня цього року Божого 39 дітей парафії св. Миколая Чудотворця на Аскольдовій Могилі та 7-ро вихователів виїхали на літній відпочинок у Приморське, Одеської обл. Повідомляємо батькам та нашим парафіянам, що наші діти з вихователями щасливо прибули на Чорне море. Дорогою з Одеси до турбази діти споглядали дозрілі пшеничні поля і початок жнив. У повітрі стоїть спека.</p>\n','2009-06-20 22:09:29','http://www.oranta.org/index.php?option=com_content&view=article&id=351:lito-z-bogom&catid=28:tema-nomera-cat&Itemid=2','P','','Літо з Богом','uk',1580,3063,0,NULL),
(1,1,'<p>22 жовтня, в суботу, група парафіян церкви Святого Миколая на Аскольдовій Могилі, у Києві, здійснила поїздку до трьох історичних місць на Черкащині, де творилися одні з найвизначніших сторінок української історії. Ми відвідали Чигирин – гетьманську столицю часів гетьманування Богдана Хмельницького. Оглянули музей та відновлену резиденцію Богдана Хмельницького. Побачене приємно вражає своєю красою. За допомогою екскурсовода дізналися про умови життя і звичаї того часу.</p>\n','2011-10-30 21:14:17','http://oranta.org/index.php?option=com_content&view=article&id=803:shljahamy-istoriji&catid=25:parafija-cat&Itemid=27','P','','Шляхами історії','uk',2596,4079,0,NULL),
(1,1,'<p>23 жовтня в центральному будинку художника відбудеться концерт-презентація диску з піснями чудового українського поета Костя Москальця. Поет-фантом, поет-містерія - його пісні «Вона», «Срібне поле» виконує не тільки гурт «Плач Єремії», їх співають студенти в гуртожитках, українські вояки в казармах, їх співають навіть гламурні дівчата, які в цілому не дуже обізнані з українською сучасною музикою. <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> <img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> Але при цьому майже неможливо побачити Костя Москальця на TV, чи шпальтах газет, журналів. Його не кличуть на свої ювілеї Л. Кучма та Л.Кравчук, в нього немає орденів та урядових нагород. Та і мешкає він не в багатомільйонній столиці а в селі на Чернігівщині. \n','2008-10-17 18:11:06','http://www.oranta.org/index.php?option=com_content&view=article&id=68:koncert-prezentacija-dysku-poeta-kostja-moskalcja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Концерт-презентація диску поета Костя Москальця','uk',555,1535,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>24 травня у греко-католицькій церкві святого Миколая, що на Аскольдовій могилі, відбувся фестиваль «Аскольдів Глас».Саме на цей день припало ще свято літнього святого Миколая.</p>\n','2009-05-26 18:37:14','http://www.oranta.org/index.php?option=com_content&view=article&id=335:laskoldiv-glasr-2009&catid=25:parafija-cat&Itemid=27','P','','«Аскольдів Глас» - 2009','uk',1517,2573,0,NULL),
(1,1,'<p>26-28 вересня 2008 року, у Львівській духовній семінарії Святого Духа відбувалися реколекції, які проводив протоігумен Львівської провінції ЧНІ ієрм. Василь Іванів. Протягом цих днів духовної віднови семінаристи прослухали 11 конференцій, на котрих порушувалися теми духовності, покликання, свободи духа, сміливості перед Богом, любові до ближнього, святості, наслідування Христа. \n','2008-09-29 15:12:55','http://www.oranta.org/index.php?option=com_content&view=article&id=31:rekolekciji-u-lvivski-duhovni-seminariji-svjatogo-duha-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Реколекції у Львівській духовній семінарії Святого Духа ','uk',508,1488,0,NULL),
(1,1,'<p>27 вересня церква святкує празник Воздвиження Чесного Хреста. У цей день ми притримуються строгого посту. Хрест, на якому розіп\'яли Ісуса Христа, знайшли 326 - го року, то ж празник Воздвиження Чесного Хреста є давнім святом.</p>\n','2009-09-27 23:27:19','http://www.oranta.org/index.php?option=com_content&view=article&id=436:praznyk-vozdvyzhennja-chesnogo-hresta&catid=25:parafija-cat&Itemid=27','P','<p>У церкві святого Миколая, що на Аскольдовій Могилі, також святкували цей празник. Божественну Літургію для дітей провели парох греко - католицької церкви святого Миколая отець Ігор Онишкевич та священик Роман Небожук. Атмосфера в церкві була надзвичайно піднесена, адже проповідь надихала парафіян на сприйняття цієї події з глибоким християнським змістом.</p>\r\n<p>Отець Ігор Онишкевич наголосив:</p>\r\n<p><img style=\"float: right;\" src=\"image,3298,200,111.jpg\" alt=\"\" />&laquo;Кожна наука має свій знак. Ось, наприклад, &nbsp;медицина символізує палиця на якій є змій. Чому саме так? Якось одному мудрецю розказали про тяжко хворого хлопчика. Його просили, щоб він навідався до дому того хлопця і допоміг йому. Мудрець вирішив допомогти юнаку. Він взяв свою палицю і пішов стежиною до дому хлопця. Але по дорозі на його палицю залізла змія. Він хотів вбити її, але побачив, що в неї в роті є якесь зілля. Він вийняв його і це зілля врятувало хворого юнака. Тому символом медицини є палиця зі змією.</p>\r\n<p>Символом нашого князя Володимира Великого був тризуб. На ньому він хотів знищити всіх своїх ворогів. Але після того, як Володимир повірив у Бога, він змінив свій знак, поставивши в центрі тризуба &nbsp;Хрест! Цим він хотів захистити свій народ від ворогів.</p>\r\n<p>Ми знаємо, що символом віри в Бога Ісуса Христа є Хрест. Ми повинні пам\'ятати про це та усім серцем сприймати цей символічний християнський знак.&raquo;</p>\r\n<p>Після Богослужіння відбулося миропомазування. Також отець Ігор зробив декілька оголошень про поїздку в Батурин та пригадав вірним &nbsp;розклад Богослужінь.</p>\r\n<p>Батьківський комітет того дня провів чергові збори, на яких вирішувались питання, пов\'язані з катехизацією дітей та відпочинком на природі.</p>\r\n<p>На малозабезпечених дітей чекав гарячий обід, про який подбали пані Ореста Цибрівська та Марія Бортнічук. До речі, ці обіди проводяться не тільки щонеділі, але й у інші дні тижня. Ця милосердна акція відбувається у рамках проекту &laquo;Діти Божого Милосердя&raquo;, завдання якого є огорнути малозабезпечених дітей увагою і добротою; вкласти в серце дитини віру, любов до матері, Батьківщини, рідної мови; сприяти в навчанні і здобутті фаху.</p>\r\n<p>Пообідавши, діти відпочивали біля церковної хатини. Відбулися ігри з настільного тенісу, бадмінтону.</p>\r\n<p>Матеріал підготував Олег Качур, &laquo;Оранта&raquo;</p>','Празник Воздвиження Чесного Хреста','uk',1815,3298,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>27 листопада 2008 року у Києві з ініціативи Міністерства України у справах сім`ї, молоді та спорту за підтримки Президента України Віктора Ющенка вперше в Україні відбувався Всеукраїнський форум усиновлювачів, на який було запрошено близько 470 дітей та батьків-усиновлювачів з усіх регіонів України. У форумі бере участь о.Михайло Чабан, Директор Родинного дому «Покрова» (м.Львів), яким опікується монаше Салезіянське Згромадження, місією якого є духовне та всебічне виховання дітей і молоді, особливо найбільш потребуючої та занедбаної.</p>\n<p>\n','2008-11-27 22:56:34','http://www.oranta.org/index.php?option=com_content&view=article&id=116:prezydentska-vidznaka-dlja-salezijan-&catid=29:propovidi-cat&Itemid=34','P',NULL,'Президентська відзнака для салезіян  ','uk',707,1687,0,NULL),
(1,1,'<p>28 листопада 2010 року - Початок Різдвяного посту</p>\n<p>Хто ближче приглянеться до нашого церковного року, той чітко завважує, що наша Східня Церква до великих і світлих празників завжди приготовляється коротшим чи довшим постом. Маємо великий і святий піст перед празником Пасхи. Маємо одноденні пости перед деякими празниками. Маємо багатоденні пости перед празником св. Апостолів Петра й Павла та празником Успіння Преч. Діви Марії. Маємо вкінці довгий піст перед празником Христового Різдва. Цей празник, як і празник Пасхи, належить до найбільших празників у році, тому і його піст творився на взір пасхального посту, і навіть носить назву Чотиридесятниці.</p>\n','2010-11-24 20:22:48','http://oranta.org/index.php?option=com_content&view=article&id=627:pochatok-rizdvjanogo-postu&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Початок Різдвяного посту','uk',2230,3713,0,NULL),
(1,1,'<p>29 червня - 1 липня 2009 року у місті Новояворівську Львівської області, у приміщенні Молодіжного реколекційного духовного центру ім. митр. Андрея та архим. Климентія братів Шептицьких, відбулася Сорок п\'ята сесія Синоду Єпископів Києво-Галицького Верховного Архиєпископства Української Греко-Католицької Церкви. На сесійних засіданнях було 18 членів Митрополичого Синоду. Уперше в сані єпископа взяв участь Преосвященний Владика Йосиф (Мілян), Єпископ-помічник Київської архиєпархії.</p>\n','2009-07-06 23:24:26','http://www.oranta.org/index.php?option=com_content&view=article&id=371:-pro-sorok-pjatu-sesiju-synodu-jepyskopiv-kyjevo-galyckogo-verhovnogo-arhyjepyskopstva-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Про Сорок п’яту сесію Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ ','uk',1640,3123,0,NULL),
(1,1,'<p>29-30 жовтня 2008 року у Львові-Брюховичах відбулася Сорок перша сесія Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ. На сесійних засіданнях були присутні 17 членів Митрополичого Синоду та запрошений гість Преосвященний Владика Славомир (Мікловш), Єпарх Криживецький, що у Словенії, Хорватії та Боснії і Герцеговині.</p>\n<p>\n','2008-11-05 17:59:49','http://www.oranta.org/index.php?option=com_content&view=article&id=90:pro-sorok-pershu-sesiju-synodu-jepyskopiv-kyjevo-galyckogoverhovnogo-arhyjepyskopstva-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Про Сорок першу сесію Синоду Єпископів Києво-ГалицькогоВерховного Архиєпископства УГКЦ  ','uk',621,1601,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p>3 травня 2009 р., у першу неділю місяця, Святіший Отець, після Божественної Літургії, яку він очолив в базиліці Святого Петра у Ватикані з нагоди священичих свячень, ще раз звернувся до вірних із недільним повчанням, проказав молитву «Царице Неба» та уділив усім присутнім своє Апостольське благословення. Помолитись із Вселенським Архиєреєм прибуло багато вірних із Риму, інших міст Італії та навіть з найвіддаленіших куточків світу, - центральна площа Ватикану була заповнена паломниками.</p>\n','2009-05-03 19:37:42','http://www.oranta.org/index.php?option=com_content&view=article&id=306:mariska-molytva-z-papoju-venedyktom-xvi-u-pershu-nedilju-travnja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Марійська молитва з Папою Венедиктом XVI у першу неділю травня','uk',1444,2500,0,NULL),
(1,1,'<p>5 березня минає 49 років з дня смерті генерала-хорунжого Української Повстанської Армії Романа Шухевича.</p>\n<p>У церкві святого Миколая Чудотворця на Аскольдовій Могилі цього дня о 18:00 буде відправлена поминальна панахида.<img src=\"http://www.oranta.org/plugins/editors/jce/tiny_mce/plugins/readmore/img/trans.gif\" /> \n','2009-02-10 19:15:14','http://www.oranta.org/index.php?option=com_content&view=article&id=209:pamjati-romana-shuhevycha&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Пам‘яті Романа Шухевича','uk',1124,2098,0,NULL),
(1,1,'<p>5 вихователів та 25 дітей сьогодні відпочивають та набираються сил в оздоровчому центрі «Заріччя» монастиря Андрія Первозваного. Сюди парафіяльну громаду запросив отець Йосафат, настоятель релігійної громади святого Йосафата в Івано-Франківську, із яким попередньо церква уклала угоду.</p>\n','2009-07-26 00:15:55','http://www.oranta.org/index.php?option=com_content&view=article&id=393:dity-parafijan-cerkvy-sv-mykolaja-na-askoldovi-mogyli-vyrushyly-na-karpatsky-kurort&catid=25:parafija-cat&Itemid=27','P','','Діти парафіян церкви св. Миколая на Аскольдовій Могилі вирушили на карпатський курорт','uk',1704,3187,0,NULL),
(1,1,'<p>5 січня 2010 року була надана допомога родині Елли Кібзи, села Іванків Бориспільського району. Це довгостроковий проект ще започаткований \"Карітас-Київ\" і який тепер продовжує наш парафіяльний \"Карітас\".</p>\n','2010-01-12 12:48:54','http://www.oranta.org/index.php?option=com_content&view=article&id=471:dopomoga-pryomni-rodyni&catid=25:parafija-cat&Itemid=27','P','','Допомога прийомній родині!','uk',1925,3408,0,NULL),
(1,1,'<p>7 млн українців є трудовими мігрантами. Про це повідомив сьогодні, 24 вересня, під час міжнародної дискусії «Перспективи євродіалогу у міграційній сфері: участь Церкви» Владика Йосиф (Мілян), Керівник Пасторально-місійного відділу Української Греко-Католицької Церкви.</p>\n','2010-09-25 21:55:57','http://oranta.org/index.php?option=com_content&view=article&id=589:7-mln-ukrajinciv-je-trudovymy-emigrantamy--vladyka-osyf-miljan-&catid=37:spilnota-cat&Itemid=49','P',NULL,'7 млн українців є трудовими емігрантами, – Владика Йосиф (Мілян)  ','uk',2157,3640,0,NULL),
(1,1,'<p>7-8 травня 2009 року в актовому залі Львівського національного університету ім. Івана Франка відбудеться Міжнародна науково-аналітична конференція „Гідність і покликання жінки в сучасному світі». Конференція відбудеться з нагоди 20-річчя Апостольського послання Папи Івана Павла ІІ «Mulieris dignitatem»</p>\n','2009-04-23 20:46:34','http://www.oranta.org/index.php?option=com_content&view=article&id=298:mizhnarodna-naukovo-analitychna-konferencija-gidnist-i-poklykannja-zhinky-v-suchasnomu-svitir-&catid=37:spilnota-cat&Itemid=49','P',NULL,'Міжнародна науково-аналітична конференція „Гідність і покликання жінки в сучасному світі»  ','uk',1399,2399,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><a class=\"jcepopup\" title=\"Координатор руху &laquo;За життя&raquo;: &laquo;Законодавча заборона абортів разюче зменшує кількість убитих дітей&raquo;\" href=\"http://4vlada.com/uploads/1366787719-random1557-24_04_13.jpg\" target=\"_blank\"> <img style=\"margin: 10px; float: left;\" src=\"http://4vlada.com/uploads/1366787719-random1557-24_04_13.jpg\" alt=\"\" width=\"187\" height=\"82\" /><em>Координатор руху \"За життя!\" на Рівненщині&nbsp;Антоніна Торбіч переконана, що&nbsp;материнство &ndash; це неймовірна можливість реалізації для жінки. Фото з сімейного архіву</em></a></p>','2013-05-11 13:07:48','http://oranta.org/index.php?option=com_content&view=article&id=1196:2013-05-11-10-10-56&catid=37:spilnota-cat&Itemid=49','P','','Координатор руху «За життя»: «Законодавча заборона абортів разюче зменшує кількість убитих дітей»','uk',3231,4714,0,NULL),
(1,1,'<p><a href=\"http://3.bp.blogspot.com/_WPjZST8SC40/SD3LDY9qqDI/AAAAAAAASME/xkzl2zvqZ74/s400/destauranimams121007.jpg\" target=\"_blank\">http://3.bp.blogspot.com/_WPjZST8SC40/SD3LDY9qqDI/AAAAAAAASME/xkzl2zvqZ74/s400/destauranimams121007.jpg</a><img style=\"margin: 10px; float: right;\" complete=\"true\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_caritas-in-veritate1.jpg.jpg\" />«Caritas in Veritate» (з лат. «Любов у істині») є третьою за чергою енциклікою Папи Венедикта XVI. Цього разу Святіший Отець присвячує свої роздуми соціальним проблемам та темі цілісного розвитку людини. Як відомо, нова енцикліка була підписана до друку Папою Венедиктом XVI 29 червня 2009 року, а 7 липня, напередодні самміту «Великої вісімки», що проходив у Аквілі (Італія), «Caritas in Veritate» була представлена та опублікована французькою, англійською, італійською, польською, португальською, іспанською та німецькою мовами.</p>\n<p>У п\'ятому розділі під заголовком «Співробітництво людської родини»<strong> </strong>енцикліка «Caritas in Veritate» звертає увагу світової громадськості зокрема на проблеми міграції.</p>\n<p>Як відомо, феномен міграції як з огляду української численної еміграції в країни Західної Європи та Америки, так і з огляду іноземної імміграції в Україну є неабияк актуальний і українському суспільству, який нажаль поки що не знаходить широкого відголосу ні серед політиків, ні в українських ЗМІ.\n','2009-07-20 22:29:27','http://www.oranta.org/index.php?option=com_content&view=article&id=388:tema-migraciji-v-novi-encyklici-papy-venedykta-xvi-lcaritas-in-veritater&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Тема міграції в новій енцикліці Папи Венедикта XVI «Caritas in Veritate»','uk',1694,3177,0,NULL),
(1,1,'<p><a href=\"http://catholicnews.org.ua/papa-benedikt-xvi-poproshchavsya-z-kardinalami\">Після подячного слова</a>, яке на початку останньої зустрічі Папи Бенедикта XVI з Колегією Кардиналів, що відбулася 28 лютого 2013 р., виголосив її Декан кардинал Анджело Содано, вислужений Державний Секретар, з прощальним словом до присутніх звернувся Святіший Отець:&nbsp;</p>\n<p>Шановні і дорогі браття!</p>\n<p>з великою радістю приймаю вам та складаю кожному з вас свої найщиріші побажання. Дякую кардиналові Анджело Содано, який, як завжди, зумів стати виразником почуттів усієї колегії. Дякую, еміненціє, від щирого серця. Хочу сказати вам, посилаючись на подію, що трапилася з учнями в Емаусі, що також і для мене було великою радістю крокувати разом з вами протягом цих восьми років у світлі присутності Воскреслого Господа.</p>\n','2013-02-28 22:06:03','http://oranta.org/index.php?option=com_content&view=article&id=1010:qvzhe-vid-sogodni-obicjaju-novomu-papi-svoju-bezumovnu-poshanu-ta-posluhq-benedykt-xvi-do-kardynaliv&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'\"Вже від сьогодні обіцяю новому Папі свою безумовну пошану та послух\", - Бенедикт XVI до кардиналів','uk',2984,4467,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><a href=\"http://catholicnews.org.ua/svyatishiy-otec-zaklikav-ne-buti-hristiyanami-na-paperi\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5268,200,56519.jpg\" />Папа закликав християн не бути християнами на папері</a>, не задовольнятися перебуванням у списку запрошених, а прийняти реальну участь у житті Тіла Христового. І насправді ця тема є надзвичайно актуальною, як для Церкви у світі, так і для Церкви в Україні. Адже, за оцінками, в середньому всього 10% католиків регулярно беруть участь в недільному Богослужінні.</p>\n','2013-11-15 22:32:22','http://oranta.org/index.php?option=com_content&view=article&id=1639:mozhe-vzhe-dosyt-paperovogo-hrystyjanstva-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Може, вже досить паперового християнства? ','uk',3786,5269,0,NULL),
(1,1,'<p><a href=\"http://dyvensvit.org/images/stories/_191211_1.JP\"></a><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4357,200,CRW_4839.JPG\" />Передріздвяний піст готує нас до прийняття Новонародженого Ісуса, Котрий народився, жив і помер як людина. А також ми ВІРОЮ осягаємо і те божественне, що було у Його народженні, житті і смерті. Про нашу віру у повсякденному житті, про силу прощення і молитви – у духовній науці о. Василя Поточняка.</strong></p>\n','2012-12-21 01:47:29','http://oranta.org/index.php?option=com_content&view=article&id=914:2012-12-21-00-02-55&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Віруйте!»','uk',2875,4358,0,NULL),
(1,1,'<p><a href=\"http://referaty.com.ua/ukr/dll/12978/\" target=\"_blank\"> </a></p>\n<p><strong><a href=\"http://referaty.com.ua/ukr/dll/12978/\" target=\"_blank\">Західно Українська Народна Республіка</a></strong></p>\n<p>Перша світова війна призвела до різкого загострення обстановки на західноукраїнських землях, поглиблення соціально-економічної й політичної кризи в Австро-Угорщині. Після закінчення першої світової війни в імперії Габсбургів розгорнувся рух за утворення самостійних національних держав.</p>\n<p>І на західноукраїнських землях настав якісно новий етап народних змагань. На західноукраїнських землях активізувалися виступи за створення незалежної Української держави. Здійнявшись у Галичині, хвиля загальнонаціонального повстання прокотилася Буковиною, Волинню, Закарпаттям. Українці, як і інші народи Австро-Угорщини, виступали за ліквідацію ненависної монархії на своїх землях. Революційні виступи у Східній Галичині, Буковині, Закарпатті та на Волині швидко переросли в оборонну війну проти військ новостворених держав (у першу чергу, Польщі) і тих, котрі існували й раніше (передусім Румунії).</p>\n<p>\n','2008-11-01 19:38:30','http://www.oranta.org/index.php?option=com_content&view=article&id=86:zahidno-ukrajinska-narodna-respublika&catid=28:tema-nomera-cat&Itemid=2','P','','Західно Українська Народна Республіка','uk',597,1577,0,NULL),
(1,1,'<p><a href=\"http://ugcc.org.ua/news.0.html\"></a><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_ihor_bojko_02.jpg.jpg\" /></p>\n<p>Не  боятися народити ще одну дитину, повірити, що Бог простив, та  примиритися з ненародженою дитиною. На таких моментах духовного зцілення  від гріха аборту &nbsp;наголосив член-кореспондент Папської академії \"Pro  Vita\", декан філософсько-богословського факультету Українського  католицького університету, керівник сертифікованої програми з біоетики  «На службі охорони життя» о.д-р Ігор Бойко під час третього вебінару  УГКЦ з біоетики на тему «Аборт, його історичні, юридичні та моральні  аспекти. Духовне зцілення від гріха аборту».</p>\n','2010-12-01 22:48:50','http://oranta.org/index.php?option=com_content&view=article&id=628:lne-vtrachate-nadiji-na-tajinstvo-prymyrennjar-o-d-r-igor-boko-pro-abort-ta-duhovne-zcilennja-pislja-vchynennja-cogo-griha-&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Не втрачайте надії на таїнство Примирення»: о. д-р Ігор Бойко про аборт та духовне зцілення після вчинення цього гріха ','uk',2236,3719,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><a href=\"https://www.facebook.com/events/638324226218122/?source=1\"><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5252,200,20131110_134952.jpg\" />У неділю, 10 листопада, у день молитви за сиріт у церкві св. Миколая Мир Лікійського на Аскольдовій Могилі відбувся молебень за участю зустріч Блаженнішого Любомира Гузара. Як зазвичай, на службу завітали також і діти-сироти, якими опікується громада церкви св. Миколая на Аскольдовій Могилі. </a></p>\n','2013-11-12 01:16:01','http://oranta.org/index.php?option=com_content&view=article&id=1628:spilna-molytva-za-syrit&catid=25:parafija-cat&Itemid=27','P','','Спільна молитва за сиріт','uk',3771,5254,0,NULL),
(1,1,'<p><a rel=\"2009030101\" target=\"_blank\" href=\"http://www.oranta.org/images/stories/2008/11/1.jpg\" title=\"Тарасова Ніч\" class=\"jcepopup\"><img width=\"150\" src=\"http://www.oranta.org/images/stories/2008/11/thumbnails/thumb_1.jpg\" alt=\"Тарасова Ніч\" height=\"150\" style=\"float: left; margin-right: 8px; border: #000 1px solid;\" /></a></p>\n<p>У ніч з 9 на 10 березня 2009 року Божого у церкві святого Миколая Чудотворця на Аскольдовій Могилі пройшла 45-та «Тарасова Ніч». О 19 годині парафіяни разом з отцем Ігорем Онишкевичем відправили поминальну панахиду в пам‘ять українського пророка Тараса Шевченка. У зверненні до присутніх отець наголосив, що Тарас Шевченко хоч і не зачислений до лику святих, але Він є святий для кожного українця. Його національні ідеї настільки були пророчими, що знаходять своє відображення у сьогоденні. Ми маємо гордитися тим, що ми українці, бути гідними дітьми своєї держави і відстоювати свою незалежність розумом, працею, вірою, надією й любов‘ю до Бога.</p>\n<p>\n','2009-03-10 20:50:13','http://www.oranta.org/index.php?option=com_content&view=article&id=238:ltarasova-nichr-&catid=25:parafija-cat&Itemid=27','P','','«Тарасова Ніч»','uk',1224,2198,0,NULL),
(1,1,'<p><a target=\"_blank\" href=\"http://62.77.60.84/audio/ra/00176851.RM\">http://62.77.60.84/audio/ra/00176851.RM</a></p>\n<p>(звукозапис: з проповіді Блаженнішого Йосифа Сліпого під часпосвячення собору Святої Софії у Римі, 27 вересня 1969 року)</p>\n<p><img src=\"http://ugcc.org.ua/uploads/RTEmagicC_Patr_Josyf.jpg.jpg\" complete=\"true\" style=\"float: left; margin: 10px;\" />7 вересня 2009 року виповнюється 25 років від переходу у вічність цього геройського ісповідника віри, одного з найвідважніших борців за права Христової Церкви та українського народу, світлого символу незламного борця за Божу правду й людську гідність.</p>\n<p>Протягом останніх десятиріч в історії нашої батьківщини та Церкви відбулись визначні події - незалежність України та легалізація УГКЦ дозволили перевезення тлінних останків Блаженнішого Йосифа Сліпого з собору Святої Софії у Римі до Львова, що відбулось 1992 року, і тепер вони спочивають у крипті собору святого Юра.</p>\n','2009-09-07 22:27:35','http://www.oranta.org/index.php?option=com_content&view=article&id=427:25-ta-richnycja-smerti-blazhennishogo-patriarha-osyfa-slipogo-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'25-та річниця смерті Блаженнішого Патріарха Йосифа Сліпого  ','uk',1774,3257,0,NULL),
(1,1,'<p><a target=\"_blank\" href=\"http://www.irs.in.ua/images/stories/Photo_news/2009.03.26_press_conf_b.jpg\"><img border=\"0\" vspace=\"4\" align=\"left\" width=\"200\" src=\"http://www.irs.in.ua/images/stories/Photo_news/2009.03.26_press_conf.jpg\" hspace=\"6\" alt=\"збільшити зображення\" height=\"143\" title=\"збільшити зображення\" /></a>26 березня 2009 року в інформаційному агентстві УНІАН презентували результати Міжнародного проекту соціального дослідження (ISSP), до виконання якого приєдналася Україна. Першим досвідом міжнародної співпраці українських соціологів в 2008 році став модуль релігії, до опитувальника якого вміщено 60 запитань відносно самооцінки громадянами власної релігійності, їх ставлення до релігії, залучення до релігійних практик, оцінка ролі релігії та релігійних організацій в суспільному й власному житті та інше.</p>\n','2009-03-27 17:06:08','http://www.oranta.org/index.php?option=com_content&view=article&id=259:-religinist-ukrajinciv-je-odnijeju-z-navyshchyh-u-jevropi--doslidzhennja-sociologiv&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Релігійність українців є однією з найвищих у Європі – дослідження соціологів','uk',1290,2264,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em> </em></p>\n<p><img style=\"margin: 10px; float: right;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_resurrection-Christ_sm.jpg.jpg\" height=\"204\" width=\"291\" /></p>\n<p>Високопреосвященним і Преосвященним Владикам,<br /> всесвітлішим, всечеснішим та преподобним отцям,<br /> преподобним ченцям і черницям,&nbsp; дорогим у Христі мирянам<br /> Української Греко-Католицької Церкви</p>\n<p><strong>Христос воскрес!</strong></p>\n<p>«Воскресіння день − просвітімося люди! Пасха Господня, Пасха, від смерті бо до життя і від землі до небес Христос Бог нас перевів, − перемоги пісню співаємо!»</p>\n','2011-04-20 09:29:22','http://oranta.org/index.php?option=com_content&view=article&id=728:velykodnje-poslannja-blazhennishogo-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Великоднє послання Блаженнішого Святослава','uk',2413,3896,0,NULL),
(1,1,'<p><em> </em><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4336,200,2060776_n.jpg\" />«Світ не вірить у Бога, можливо, тому, що наше свідчення недостатньо сильне, не таке, яким має бути, і ми не можемо опускати рук, а треба працювати і відновлюватися. Постійна праця над собою.</em><em></em></p>\n<p><em>Нам&nbsp; необхідна нова євангелізація для відновлення особистої віри та переконання інших людей у тому, що жити без Бога – це трагедія.»</em></p>\n','2012-11-15 11:15:39','http://oranta.org/index.php?option=com_content&view=article&id=902:posmishka-dytyny-spogady-pro-sluzhinnja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Посмішка дитини - спогади про служіння на Аскольдовій Могилі','uk',2854,4337,0,NULL),
(1,1,'<p><em>&nbsp;</em></p>\n<p align=\"center\">Дорогі у Христі!</p>\n<p>Синод Єпископів Української Греко-Католицької Церкви на своєму останньому засіданні, яке відбулося у грудні 2009 року, постановив присвятити 2010 рік темі християнського покликання з особливим наголосом на покликанні до богопосвяченого життя. Це стало логічним продовженням тем попередніх років. Зокрема, 2008 рік був присвячений розглядові християнського покликання взагалі. Ми пояснювали вірним нашої Церкви, що кожна людина одержує від Господа покликання, певне завдання, яке має здійснити у своєму житті. Особливим чином це стосується людей, які втілені в Ісуса Христа, у святу Церкву через святе таїнство Хрещення. Відтак, у 2009 році, який щойно закінчився, ми звернули особливу увагу на одну частину вірних нашої Церкви – священиків. Їхнє покликання полягає у тому, щоб служити Божому людові, святій Церкві. Священнослужителі – це група осіб, яку можна легко описати за допомогою статистичних даних і докладно вивчити різні аспекти її життя.</p>\n','2010-01-03 18:22:17','http://www.oranta.org/index.php?option=com_content&view=article&id=467:progoloshennja-virnym-ukrajinskoji-greko-katolyckoji-cerkvy-2010-roku-bozhogo-rokom-hrystyjanskogo-poklykannja-z-osoblyvym-nagolosom-na-poklykanni-do-bogoposvjachenogo-zhyttja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Проголошення вірним Української Греко-Католицької Церкви 2010 року Божого Роком християнського покликання з особливим наголосом на покликанні до богопосвяченого життя','uk',1917,3400,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6024,200,1052775.jpg\" />Доктор </em>філологічних наук</em><em> </em><em><a href=\"http://www.bbc.co.uk/blogs/legacy/ukrainian/ponomariv/\" target=\"_blank\">Олександр Пономарів відповідає на запитання читачів ВВС Україна</a>.</em></p>\n<p><strong>Яка клична форма ім\'я Вахтанг? – запитує Дмитро Понамарчук.</strong><strong></strong></p>\n<p>Потрібно казати не клична форма, а кличний відмінок – один із сімох відмінків сучасної української мови.</p>\n<p>Від Вахтанг кличний відмінок Вахтангу!</p>\n<p><strong>Данило з Канади цікавиться, як називається мова людей, що живуть в Ізраїлі. Яка відмінність між прикметниками єврейський і гебрайський?</strong></p>\n','2014-07-21 13:51:32','http://oranta.org/index.php?option=com_content&view=article&id=2292:2014-07-21-10-59-53&catid=37:spilnota-cat&Itemid=49','P',NULL,'Професор Пономарів: коли доречне «на Україні»','uk',4542,6025,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,5358,200,a8a3f50dc37c0f13fd6dc5f2d625a0b0.jpg\" style=\"margin: 10px; float: left;\" /></em></p>\n<p><em>В ефірі радіостанції \"Ехо Москви\" Верховний Архієпископ Галицький, Глава Української Греко-католицької Церкви Блаженніший Святослав.</em></p>\n<p><strong style=\"font-size: 0.875em;\">Добрий день!</strong></p>\n<p><strong style=\"font-size: 0.875em;\">Блаженніший Святослав</strong></p>\n<p><o:p></o:p></p>\n<p>Здравствуйте!</p>\n','2013-12-20 13:13:09','http://oranta.org/index.php?option=com_content&view=article&id=1722:pro-kurs-ukrajiny-cerkvu-na-madani-zustrich-z-patriarhom-kyrylom-intervju-patriarha-svjatoslava&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Про курс України, Церкву на майдані, зустріч з Патріархом Кирилом, - інтерв’ю Патріарха Святослава','uk',3879,5362,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,5764,200,1.jpg\" style=\"margin: 10px; float: left;\" />«Де твоє, смерте, жало? Де твоя, аде, перемога? Воскрес Христос і ти провалився. Воскрес Христос і впали демони. Воскрес Христос і радіють ангели. Воскрес Христос і життя панує. Воскрес Христос і нема ні одного мерця в гробі, бо Христос воскрес із мертвих і став первістком померлих» - <strong>Йоан Золотоустий</strong></em></p>\n','2014-04-20 20:46:57','http://oranta.org/index.php?option=com_content&view=article&id=2056:svitly-praznyk-hrystovogo-voskresinnja-na-askoldovi&catid=25:parafija-cat&Itemid=27','P','','Світлий празник Христового Воскресіння на Аскольдовій','uk',4282,5765,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,5834,200,01125.jpg\" style=\"margin: 10px; float: right;\" />«Був там чоловік розслаблений 38 років»</em></p>\n<p>Нинішнє Євангеліє розповідає нам про чудесне оздоровлення Спасителем чоловіка, хворого, розслабленого - спаралізованого 38 років, який майже половину свого життя був в неволі недуги. Євангелист Іван, описуючи становище хворого, не подає ніяких скарг того чоловіка на свою недугу. Розслаблений терпеливо зносить все, живе надією на своє оздоровлення бодай при кінці життя.</p>\n','2014-05-10 22:47:18','http://oranta.org/index.php?option=com_content&view=article&id=2121:nedilja-pro-rozslablenogo&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Неділя про розслабленого','uk',4353,5836,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6003,200,103768.jpg\" style=\"margin: 10px; float: right;\" />Вбивство - чи знешкодження несправедливого нападника? Католицька Церква чітко розмежо­вує безкомпромісне ставлення до заповіді \"Не вбивай\" та справедливий опір і право на захист від аг­ресора, коли громадяни держави-жертви агресії навіть зобов\'язані стати до національної оборони. А правителя-агресора, який самовільно розпоряджається життями та добром своїх підвладних за допомогою війни (отже, і несе за це повну відповідальність), ще у XVI ст. було названо не володарем, а тираном.</em></p>\n','2014-07-11 21:17:53','http://oranta.org/index.php?option=com_content&view=article&id=2276:zahyst-protystojannja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Захист. Протистояння','uk',4521,6004,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6058,200,IM.jpg\" style=\"margin: 10px; float: right;\" />І один у полі воїн. Ці слова про дуже незвичайного і прогресивного львівського священика Степана Суса. Душпастиря, який уже з десяток років ламає, трощить, кришить усі «пострадянські» стереотипи про отців (хоча сам воліє називати себе не отцем, а братом). Навіть о третій ночі до нього за порадою звертаються прихожани, але він не відмовляє, більше того —&nbsp;вважає такі речі цілком нормальними.</em> <em>До нього телефонують навіть з Європи —&nbsp;колишні прихожани, а тепер – друзі.</em></p>\n','2014-07-31 02:39:31','http://oranta.org/index.php?option=com_content&view=article&id=2350:i-odyn-u-poli-vojin&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'І один у полі воїн','uk',4580,6063,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,flat550x550075f.jpg\" style=\"margin: 10px; float: right;\" />І, взявши хліб, віддав хвалу, поламав, дав їм і мовив: \"Це – Моє тіло, що за вас віддається. Чиніть це на Мій спомин.\" Так само чашу по вечері, кажучи: \"Ця чаша ~ це Новий Завіт у Моїй крові, що за вас проливається.\" (Лк. 22, 19-20)</em></p>\n<p>Євхаристія – таїнство, яке становить центр життя християнської спільноти. Де є Євхаристія, там є Церква. Таким було розуміння цього таїнства у часи перших християн, і до сьогодні воно не змінилося.</p>\n','2014-08-21 10:57:10','http://oranta.org/index.php?option=com_content&view=article&id=2366:jevharystija&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Євхаристія','uk',4612,6095,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6280,200,TheUK_04.jpg.pagespeed.ce.zuIoRWRJo5.jpg\" style=\"margin: 10px; float: right;\" />Його ім’я у своїх «перших десятках» мріють бачити багато лідерів демократичних партій України. І не дивно. Мирослав Маринович — постать, до думки якої однаково прислухаються і юні максималісти, і літні мудреці. Він вміє говорити і з академіками, і з робітниками. А ще він знає, що їх потрібно чути.</em></p>\n<p><em>…Є спікери й інтелектуали, з яким журналістам спілкуватися важко. Вони відповідають штампами, всоте переказують одні й ті самі історії, вкотре озвучують давно озвучені меседжі. Записувати інтерв’ю з ними складно, бо вивести співрозмовника на «ексклюзив» вартує надзусиль. Вони повторюються.</em></p>\n','2014-12-04 15:36:16','http://oranta.org/index.php?option=com_content&view=article&id=2539:bazhaty-ce-braty-vidpovidalnist-na-sebe&catid=37:spilnota-cat&Itemid=49','P',NULL,'Бажати - це брати відповідальність на себе','uk',4798,6281,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6287,200,guzar_3.jpg\" style=\"margin: 10px; float: left;\" />Кардинал Любомир Гузар у проекті Українського інституту національної пам\'яті та Фонду збереження історії Майдану \"<a href=\"http://www.kmu.gov.ua/control/uk/publish/article?art_id=247773307&amp;cat_id=244277212\">Майдан: усна історія</a>\" розповів, що дня нього означають події зими 2013-2014.</em></p>\n<p><strong><em>– Чи можна сказати, що члени Ініціативної групи \"Першого грудня\", до якої Ви входите, якимось чином готували Майдан?</em></strong></p>\n','2014-12-09 14:13:55','http://oranta.org/index.php?option=com_content&view=article&id=2544:madan-usna-istorija&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Майдан: усна історія','uk',4805,6288,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6340,200,1397139718_26_4.jpg\" style=\"margin: 10px; float: left;\" />Революція гідності, втеча президента Януковича, анексія Криму, війна на Донбасі – далеко не вичерпний перелік головних подій 2014 року, які змінили історію сучасної України. Нині про наслідки того, що сталося, говорять здебільшого політики. Але мине час, і свою зважену оцінку цим подіям дадуть історики і філософи.</em></p>\n','2015-01-15 21:09:32','http://oranta.org/index.php?option=com_content&view=article&id=2580:myroslav-popovych-strategichnym-zavdannjam-dlja-jevropy-je-usunennja-putina-vid-vlady&catid=37:spilnota-cat&Itemid=49','P',NULL,'Мирослав Попович: Стратегічним завданням для Європи є усунення Путіна від влади','uk',4858,6341,0,NULL),
(1,1,'<p><em><img src=\"http://askoldova-mohyla.org/uk/image,6446,200,0000006558.jpg\" style=\"margin: 10px; float: left;\" /></em></p>\n<div>\n<p style=\"text-align: right;\"><em>Ми віруєм Твоїй силі<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>І Духу живому.<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>Встане правда! Встане воля!<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>І Тобі одному<o:p></o:p></em></p>\n<p style=\"text-align: right;\"><em>Помоляться всі язики.</em><o:p></o:p></p>\n<p>(Т. Шевченко «Кавказ»)</p>\n</div>\n','2015-03-09 21:02:54','http://oranta.org/index.php?option=com_content&view=article&id=2664:kym-buv-taras-shevchenko&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Ким був Тарас Шевченко?','uk',4965,6448,0,NULL),
(1,1,'<p><em><img src=\"http://www.oranta.org/images/stories/1048607636.jpg\" style=\"float: left; margin: 10px;\" />«Якби мене запитали, чи жалкую я про те, як склалося моє життя, про відсиджені 15 років, я б відповів: анітрохи... І якби довелося починати все спочатку та вибирати, я б обрав життя, яке прожив.</em></p>\n<p><em>Часи змінюються, інколи залишаєшся майже один. У мене таке було. Але все одно вірю, що чиню правильно. Не будемо говорити високі слова про ідею, відповідальність тощо. Ти вирішив, що так, а не інакше потрібно діяти. Віриш у свою дорогу? Іди й не звертай!...</em></p>\n<p>\n','2009-03-25 19:45:04','http://www.oranta.org/index.php?option=com_content&view=article&id=256:vichna-pamjat-i-slava-ukrajinskomu-patriotu-vjacheslavu-chornovolu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Вічна пам‘ять і слава українському патріоту В‘ячеславу Чорноволу','uk',1282,2256,0,NULL),
(1,1,'<p><em><img style=\"float: right;\" src=\"http://vrciro.org.ua/images/stories/photo_news/2008.12.16_zasidanniavrciro_1.jpg\" alt=\"засідання Всеукраїнської Ради Церков і релігійних організацій\" title=\"засідання Всеукраїнської Ради Церков і релігійних організацій\" height=\"156\" width=\"266\" /></em>16 грудня 2008 року у Прес-центрі національного інформаційного агентства «Укрінформ» під головуванням Голови Всеукраїнського Союзу об\'єднань євангельських християн - баптистів В\'ячеслава Нестерука відбулось чергове засідання Всеукраїнської Ради Церков і релігійних організацій (ВРЦіРО), на якому було ухвалено стратегічні рішення про формалізацію співробітництва Церков і релігійних організацій з профільними органами державної влади у вирішенні актуальних проблем суспільства.</p>\n<p>\n','2008-12-17 07:59:13','http://www.oranta.org/index.php?option=com_content&view=article&id=146:chleny-vrciro-na-chergovomu-zasidanni-uhvalyly-strategichni-rishennja-u-sferi-spivpraci-z-derzhavoju&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Члени ВРЦіРО на черговому засіданні ухвалили стратегічні рішення у сфері співпраці з державою','uk',814,1794,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3764,200,noname.jpeg\" />Христос народжується – славте!</em></p>\n<p><em>Христос із небес – зустрічайте!</em></p>\n<p><em>Христос на землі – бадьоріться!</em></p>\n<p><em>Співай Господеві, вся земле і</em></p>\n<p><em>весело заспівайте люди,</em></p>\n<p><em>Бо він славно прославився!</em></p>\n<p style=\"text-align: left;\">(Ірмос І пісні канону Різдва Христового)</p>\n','2011-01-06 13:51:58','http://oranta.org/index.php?option=com_content&view=article&id=655:rizdvjane-slovo&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Різдвяне слово','uk',2282,3765,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4518,200,372.jpg\" />«Неділя прощення»</em><em> – </em><em>таку назву має</em><em> </em>Неділя  перед початком Великого Посту. В цей день Церква запрошує вірних  &nbsp;простити один одному кривди та образи. Заохочує перепросити&nbsp; своїх  ближніх, щоб з чистим сумлінням стати на шлях Великого Посту, який  провадить до Воскресіння.<em>&nbsp;</em></p>\n<p><em>«Коли ви прощатимете людям їхні провини, то й Отець ваш небесний  простить вам. А коли ви не будете прощати людям, то й Отець ваш небесний  не простить вам провин ваших» (Мт. 6, 14-15)</em><em> </em></p>\n','2013-03-17 00:00:54','http://oranta.org/index.php?option=com_content&view=article&id=1044:nedilja-proshchennja&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Неділя прощення','uk',3036,4519,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4534,200,ceneri2010e.jpg\" />Віра в любов пробуджує любов: «Ми пізнали й увірували в ту любов, яку Бог до нас має» (1 Ів. 4,16)</em></p>\n<p>Дорогі брати і сестри,</p>\n<p>Відзначення Великого Посту в контексті Року Віри надає нам цінну нагоду  роздумати над відношенням між вірою і любов’ю: між вірою в Бога – Бога  Ісуса Христа – і любов’ю, яка є плодом Святого Духа і яка веде нас  шляхом посвячення себе Богові та іншим людям.</p>\n','2013-03-18 22:18:49','http://oranta.org/index.php?option=com_content&view=article&id=1053:poslannja-papy-emeryta-benedykta-xvi-na-velyky-pist&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Послання Папи-емерита Бенедикта XVI на Великий Піст','uk',3052,4535,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5076,200,dsc02922.jpg\" />Розмова з о. Хосе М. Вегас СМF, екзорцистом Архиєпархії Божої Матері в Москві, кандидатом філософських наук і викладачем Вищої Католицької Семінарії «Марія - Цариця Апостолів» в Санкт-Петербурзі.</em></p>\n<p><strong>- Розкажіть, будь ласка, про себе.</strong></p>\n<p>- Я став кларетином, тому що, коли відчув покликання, у нас школі працював священик з чину кларетинів, і він «піймав» мене. Насправді це рука Божа. Коли я був у новіціяті, то відчув, що це мій дім.</p>\n','2013-09-15 11:09:45','http://oranta.org/index.php?option=com_content&view=article&id=1473:dumate-pro-hrysta-i-vse-bude-dobre&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Думайте про Христа, і все буде добре!','uk',3596,5079,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5167,200,999679_592280984137853_732353860_n.jpg\" /><strong>«Пустіть дітей! Не бороніть їм приходити до мене, бо таких є Царство Небесне» (Мт. 19, 14).</strong></em></p>\n<p>Катехизація для кожної церкви – це досить вагома складова існування церковної спільноти. Саме розуміння своїх життєвих цінностей, формування себе як християнина, спрямованість на постійне духовне зростання закладається та корегується у катехитичній роботі.</p>\n','2013-10-15 00:52:25','http://oranta.org/index.php?option=com_content&view=article&id=1551:katehyzacija-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Катехизація на Аскольдовій могилі','uk',3685,5168,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5176,200,940713544.jpg\" />Та ось надійшов Амалек і задер бій з Ізраїлем у Рефідимі. Сказав  Мойсей до Ісуса: «Вибери собі людей та вийди вранці на бій з Амалеком; я  ж стану на вершині горбка з жезлом Божим у руці». Зробив Ісус так, як  повелів йому Мойсей, і почав битися з Амалеком. А&nbsp;Мойсей, Арон та Хур  вийшли на вершину горба. І сталось: як піднімав руки Мойсей, то брав  гору Ізраїль, а як опускав, брав гору Амалек. Коли ж руки Мойсея  обважніли, взяли камінь та й підклали йому, і він сів на ньому; Арон же і  Хур підтримували йому руки, один з одного боку, другий з другого. Так  руки його витривали твердо до заходу сонця. І розгромив Ісус Амалека і  його людей вістрям меча (Вих 17, 8‑13).</em></p>\n','2013-10-18 13:46:21','http://oranta.org/index.php?option=com_content&view=article&id=1556:-my-raby-chy-dity-ogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Ми раби чи діти Його?','uk',3695,5178,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5283,200,arhistratig.jpg\" />Небесних Сил Архистратиже, &nbsp;повсякчас молимо тебе ми, недостойнi,щоб твоїми молитвами охороняв i покровою духовної твоєї слави захищав нас,що усердно припадаємо до тебе i взиваємо:вiд напастей спасай нас як старшина Небесних Сил. </em>(Тропар, глас 4)</p>\n<p><strong>21 листопада </strong>(за старим стилем – 8 листопада) Церква відзначає свято Собору Архистратига Михаїла і всіх Небесних Сил безплотних. Це свято було встановлене на початку IV століття на помісному Лаодикійському соборі</p>\n','2013-11-21 00:10:54','http://oranta.org/index.php?option=com_content&view=article&id=1646:arhangel-myhajil-polkovodec-virnyh-bogovi-angeliv&catid=35:istorychni-postati-cat&Itemid=48','N','','Архангел Михаїл – полководець вірних Богові ангелів','uk',3802,5285,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5283,200,arhistratig.jpg\" />Небесних Сил Архистратиже, &nbsp;повсякчас молимо тебе ми, недостойнi,щоб твоїми молитвами охороняв i покровою духовної твоєї слави захищав нас,що усердно припадаємо до тебе i взиваємо:вiд напастей спасай нас як старшина Небесних Сил. </em>(Тропар, глас 4)</p>\n<p> </p>\n<p><strong>21 листопада </strong>(за старим стилем – 8 листопада) Церква відзначає свято Собору Архистратига Михаїла і всіх Небесних Сил безплотних. Це свято було встановлене на початку IV століття на помісному Лаодикійському соборі.\n','2013-11-21 00:10:54','http://oranta.org/index.php?option=com_content&view=article&id=1646:arhangel-myhajil--polkovodec-virnyh-bogovi-angeliv&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Архангел Михаїл – полководець вірних Богові ангелів','uk',3801,5284,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5944,200,Gudzak-5.jpg\" />Нам хочеться почати цю історію з прохання. Будь ласка, закрийте очі на кілька секунд. Тепер уявіть пустирище на околиці міста. А на ньому — закинутий, давно нікому не потрібний дитячий садочок «на чотири групи». Уявили? Приблизно те саме побачив молодий науковець і випускник семінарії Борис Ґудзяк, коли приїхав до Львова на початку 90-их. І саме із закинутого приміщення, давно нікому не потрібного дитячого садочка почалася історія відновлення одного з найкращих в нашій державі гуманітарних вузів — Українського Католицького Університету.</em></p>\n','2014-06-18 13:44:15','http://oranta.org/index.php?option=com_content&view=article&id=2221:-borys-gudzjak-zdobuty-vlasnu-gidnist&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Борис Ґудзяк: здобути власну гідність','uk',4462,5945,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6002,200,10527.jpg\" />Отець Андрій Зелінський ТІ провів декілька тижнів разом з нашим військом на сході України де дбав про духовний стан наших військових. Він поділився пережитим у своєму<a href=\"http://www.facebook.com/andriy.zelinskyy/posts/10202577584861114\" target=\"_blank\">профілі на фейсбуці</a>: </em></p>\n<p>У складній структурі соціальної дійсності можна натрапити на такі виміри людського буття, яких не приховати за бутафорними декораціями повсякденної боротьби за особисту значимість: там усе надзвичайно справжнє, неприкрашено прекрасне, автентичне – просте й глибоко змістовне, як саме таїнство людського життя. Я мав за честь побувати у зоні автентичності, у неприкрашено прекрасному трагічно реальному вимірі людського існування…</p>\n','2014-07-11 08:55:58','http://oranta.org/index.php?option=com_content&view=article&id=2275:reportazh-iz-zony-avtentychnosti&catid=37:spilnota-cat&Itemid=49','P',NULL,'Репортаж із зони автентичності','uk',4520,6003,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6320,200,huza.jpg\" />\"Я не хочу фільму про себе і не хочу фільму заради себе. Я просто хочу розповісти, як ми жили, щоб молодь це побачила і, можливо, по-іншому подивилась на той історичний пласт і порівняла його з теперішнім\",</em><em> </em>– відповів&nbsp;<strong>Любомир Гузар</strong> на пропозицію зняти про нього фільм.</p>\n<p>Блаженніший владика Любомир Гузар – єдиний українець, якого за ясність розуму і високу духовність могли обрати Папою Римським. Він протягом 10 років очолював Українську греко-католицьку церкву і традиційно входить до сотні найвпливовіших людей України.</p>\n','2015-01-03 00:12:58','http://oranta.org/index.php?option=com_content&view=article&id=2567:ogo-molytva-buty-dobroju-normalnoju-ljudynoju&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Його молитва – бути доброю, нормальною людиною!..','uk',4838,6321,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6375,200,02_gallery.jpg\" />\n<div style=\"text-align: center;\"><em style=\"font-size: 0.875em;\"><strong>Дорогі співбрати у Христовому священстві!</strong></em></div>\n</em></p>\n<p>Протягом останнього року в душпастирській практиці перед нами часто постають виклики і запитання, з якими ми досі не мали справи: масові смерті активістів на Майдані, війна на Сході України, атмосфера тривоги, непевності та страху, а відтак – симптоми депресії і зневіри в суспільстві. Проте водночас простежуємо в українському народі потужне автентичне пробудження людського духу і солідарності, бажання працювати заради загального добра, вияви гідної подиву самоорганізації та жертовності.</p>\n','2015-02-07 20:52:14','http://oranta.org/index.php?option=com_content&view=article&id=2611:dushpastyrstvo-v-umovah-viny&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Душпастирство в умовах війни','uk',4893,6376,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6399,200,46465.jpg\" />Скріпіть серця ваші (Як 5,8)</em></p>\n<p><strong>Дорогі брати і сестри!</strong></p>\n<p>Великий Піст – це час оновлення для Церкви, спільнот і окремих вірян. Однак насамперед це «час благодаті» (пор. 2Кор 6, 2). Господь ніколи не просить нас про щось, чого раніше Сам не дав би нам: «Ми любимо Його, бо Він перший полюбив нас» (1 Йн 4, 19).</p>\n','2015-02-19 19:23:45','http://oranta.org/index.php?option=com_content&view=article&id=2628:poslannja-papy-francyska-na-velyky-pist&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Послання Папи Франциска на Великий Піст','uk',4917,6400,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6436,200,ivasyuk6.jpg\" />Пісні Володимира Івасюка, які вже стали класикою української естради, абсолютно не видаються архаїкою — ні за мелодіями, ні за текстами, ні за звучанням, ні за духом. Вони пройшли перевірку часом, а це свідчить, що будуть вічними, як і фольклор загалом.</em></p>\n<p>Розвиток подій навколо його генія був дуже хвилеподібним — від замовчування до загального визнання. Він був первістком у родині&nbsp;<a href=\"http://www.ivasyuk.org.ua/names.php?lang=uk&amp;id=myhajlo_ivasyuk\">Михайла</a> та&nbsp;<a href=\"http://www.ivasyuk.org.ua/names.php?lang=uk&amp;id=sofiya_ivasyuk\">Софії Івасюків</a>, дитям великого кохання. Народився 4 березня 1949-го у містечку Кіцмані на Буковині — у краю, де століттями перехрещувалися кілька культур і звучали українські, єврейські, румунські пісні.</p>\n','2015-03-04 22:37:14','http://oranta.org/index.php?option=com_content&view=article&id=2657:2015-03-04-20-53-11&catid=37:spilnota-cat&Itemid=49','P',NULL,'Володимир Івасюк: «Кожен твір породжує у мені болісно-солодкий дрож, і той дрож робить серце здатним чинити добро»','uk',4954,6437,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6457,200,xrest.jpg\" />Хресна смерть – це вінець Воплочення Бога Слова</em></p>\n<p><em>(Преподобний Іоан Дамаскін)</em></p>\n<p><strong>У третю неділю посту для зміцнення людського духу Церква по-особливому наголошує вшановувати Господній Хрест. Звідси й походить назва неділі – Хрестопоклінна, яка припадає на середину Великого посту. </strong><strong></strong></p>\n','2015-03-15 11:50:18','http://oranta.org/index.php?option=com_content&view=article&id=2669:svjaty-hrest-bezkonechna-bozha-ljubov-do-nas-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Святий хрест - безконечна Божа любов до нас ','uk',4975,6458,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53959_batruch_st.jpg\" alt=\"о. Стефан БАТРУХ \" title=\"о. Стефан БАТРУХ \" height=\"157\" width=\"157\" /></em>З черговим витком переговорів із Європейським союзом в  українському  суспільстві розпочалась дискусія щодо євроінтеграції. Чи не   найсильніший негативний посил у цій полеміці у сторону Європи йде з   боку фундаменталістських представників певних конфесій. Європейський   союз звинувачують у викривлених цінностях, які несуть із собою занепад   моралі в суспільстві, пропаганду одностатевих стосунків тощо. На фоні   тотальної корумпованості нашої держави, лідерства за абортами,   алкоголізмом, наркоманією, безправності «маленької людини», соціальної   незахищеності різних верств суспільства, тотальної неповаги у стосунках   між людьми виникає справедливе запитання: «А чи може взагалі мораль в   нашому соціумі погіршитись ще більше?».</p>\n','2013-10-13 11:15:12','http://oranta.org/index.php?option=com_content&view=article&id=1539:jevrointegracija-stavyt-pered-cerkvoju-novi-zavdannja-o-stepan-batruh&catid=32:gk-svit-cat&Itemid=58','P',NULL,'“Євроінтеграція ставить перед Церквою нові завдання”, – о. Степан Батрух','uk',3680,5163,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_den1.jpg.jpg\" /></em>Нещодавно у «Дні» був надрукований матеріал політолога Павла Ковальова «Вибори одягають рясу» (Див. № 187 за 15 жовтня). У ньому йшлося про те, що місцеві вибори 2010 року запам’ятаються, зокрема, напливом священнослужителів як претендентів на депутатські місця. Далі − конкретні приклади. А поміж ними важлива деталь, мовляв, натомість греко-католицькі священики вже давно не мають права брати участь у виборах. Ще у 2006 році Синод Української Греко-Католицької Церкви ухвалив рішення заборонити священикам балотуватися в органи влади всіх рівнів. Напередодні виборів це та багато іншого «День» обговорює з Главою УГКЦ Блаженнішим Любомиром (ГУЗАРОМ).</p>\n','2010-10-29 20:56:12','http://oranta.org/index.php?option=com_content&view=article&id=609:ljubomyr-guzar-nam-treba-duzhe-duzhe-tjazhko-pracjuvaty-aby-zminjuvaty-vsenarodny-svitogljad&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Любомир ГУЗАР: Нам треба дуже-дуже тяжко працювати, аби змінювати всенародний світогляд','uk',2197,3680,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_svyatoslav_lutsk.jpg.jpg\" height=\"126\" width=\"199\" />Блаженніший Святослав (Шевчук) розповів <a href=\"http://religions.unian.net/ukr/detail/16234\" target=\"_blank\">УНІАН</a>,  чому через 1025 років після Хрещення Русі в людей формується образ  священика як функціонера-бюрократа, про небезпеки клерикалізації  суспільства та про плани запустити греко-католицьке  інтернет-телебачення.</em></p>\n<p><strong>Митрополит Андрей Шептицький у своїй праці «Як будувати Рідну  Хату», написаній наприкінці 1941-го, висловлював думки щодо об`єднання  всіх українських Церков у єдину помісну Церкву.</strong></p>\n','2013-07-31 11:38:01','http://oranta.org/index.php?option=com_content&view=article&id=1385:lmozhna-pokazuvaty-sebe-jak-velykogo-cerkovnogo-mecenata-a-nastupnogo-dnja--krasty-derzhavni-groshir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Можна показувати себе як великого церковного мецената, а наступного дня – красти державні гроші»','uk',3479,4962,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: left;\" src=\"http://www.catholic-media.org/ua/images/foto_benedyctusXVI-02/foto_402-pasha09.jpg\" height=\"159\" width=\"242\" />\"Дорогі брати і сестри з Рима та всього світу!</em></p>\n<p>Від усього серця висловлюю Вам пасхальні вітання словами Святого Августина: \"Воскресіння Господнє - це наша надія\" (Августин, Sermo 261, 1). Цими словами, великий Єпископ пояснював своїм вірним, що Ісус воскрес, щоб ми, приречені на смерть, не впадали у відчай, вважаючи, що життя повністю завершується у момент смерті; Христос воскрес, щоб дарувати нам надію.  Дійсно, ось одне з питань, яке найбільше непокоїть людство: «Що буде після смерті?» Сьогоднішня урочистість дозволяє дати відповідь на так важливе запитання, кажучи, що останнє слово не за смертю, адже вже тріумфує Життя. І це наше переконання ґрунтується не на звичайній людській логіці, а на історичному факті віри: Ісус Христос, розп\'ятий і похований, воскрес у своєму прославленому тілі. Ісус воскрес, щоб також і ми, віруючи в Нього, мали життя вічне. Ця звістка міститься у серці Євангельського послання. Святий Павло гаряче наголошує, кажучи: \"Якщо Христос не воскрес, то марна проповідь наша, марна віра ваша\". І додає: \"Якщо ми надіємося на Христа тільки в цьому житті, то ми - найнещаcніші з усіх людей\" (1 Кор. 15,14.19). Від світанку Пасхи світ проймає нова весна надії; з цього дня розпочалося наше воскресіння, оскільки Пасха не лише позначає, якийсь момент історії, а дає початок нового стану: Ісус воскрес не для того, щоб пам\'ять про Нього залишалася живою у серцях його учнів, а щоб Йому Самому жити в нас, і в Ньому ми можемо вже відчувати радість вічного життя.</p>\n','2009-04-21 08:38:44','http://www.oranta.org/index.php?option=com_content&view=article&id=289:poslannja-urbi-et-orbi-papy-benedykta-xvi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Послання “Urbi et Orbi” Папи Бенедикта XVI','uk',1376,2350,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3191,200,image003.jpg\" width=\"200\" height=\"150\" /></em></p>\n<p>Монастир, що у Гошеві на Ясній горі приймав паломників з усієї України. 28 серпня 2009 р. у свято Успення Божої Матері тут відбулася знаменна подія - коронування чудотворної ікони Божої Матері Гошівської, біля якої упродовж століть відбувалися сотні чудес зцілення, навернення, оздоровлення. Дванадцять паломників з числа парафіян церкви Св. Миколая на Аскольдовій могилі, що у м. Києві, також побували на цьому величному святі духовності та віри. Дорога прочан до Святині та була оплачена добродіями Сергієм та  Русланою. Дана пожертва стала офірою на молитву за здоров\'я та спокій родини, за  мир та процвітання України.</p>\n','2009-09-01 21:17:24','http://www.oranta.org/index.php?option=com_content&view=article&id=424:jasna-gora-prymaje-kozhnogo-palomnyka&catid=25:parafija-cat&Itemid=27','P','','Ясна гора приймає кожного паломника','uk',1770,3253,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5239,200,00015217.jpg\" />Глава Української греко-католицької церкви Блаженніший Святослав (Шевчук) нині мешкає на околиці селища Княжичі, за кілька кілометрів від столиці. Вузькі вулиці, приватні будинки (розкішні і не дуже) і повна тиша. Мешканці кількаповерхової резиденції почувають себе тут комфортніше, ніж в попередніх апартаментах. Ще кілька років тому глава церкви Блаженніший Кардинал Гузар, який відійшов на покій, та його свита мешкали в багаторівневій квартирі в новій столичній висотці. Єдина вигода того будинку була в тім, що розташувався він прямо навпроти Патріаршого Собору, який будує церква на лівому березі Києва. </em></p>\n','2013-11-08 23:51:36','http://oranta.org/index.php?option=com_content&view=article&id=1606:lmojim-osobystym-uchytelem-u-stavlenni-do-materialnyh-blag-je-teperishni-papa-francyskr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Моїм особистим учителем у ставленні до матеріальних благ є теперішній Папа Франциск»','uk',3762,5245,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5429,200,ib3920.jpg\" />Апостол: Кл 2, 8-12 Євангеліє: Лк 2, 20-21. 40-52</em></p>\n<p>На восьмий день після Різдва Христового святкуємо Його найменування, про це нам говорить Євангеліє: \"Як сповнилося вісім день, коли мали обрізати хлоп\'ятко, назвали його Ісус - ім\'я, що надав був ангел, перше, ніж він зачався у лоні\" (Лк 2, 21). У Старому Завіті знаходимо слова, котрі Господь сказав до Авраама: \"Ти берегтимеш союз мій, ти й твої потомки... Ви одріжете тіло на передній вашій шкірці, й це буде знаком союзу між мною й вами.</p>\n','2014-01-13 23:18:47','http://oranta.org/index.php?option=com_content&view=article&id=1768:obrizannja-gospodnje&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Обрізання Господнє','uk',3949,5432,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5442,200,111212_2.jpg\" />Свято Богоявлення, яке в народі називають ще Водохрещенням, за Юліанським календарем відзначають 19 січня (6 січня — за Григоріанським), воно завершує цикл Різдвяних свят. Йдеться про хрещення Ісуса Христа в ріці Йордан, під час якого Христос об\'являє себе як Месію і Спасителя. Про це свідчить при Хрещенні, як каже Євангеліє, сам Отець Небесний голосом із неба: «Ти єси Син мій любий, у тобі — моє уподобання» (Мр. 1, 11); свідчить Святий Дух, що у вигляді голуба сходить на Нього; свідчить також Іван Хреститель, вказуючи на Нього: «Ось Агнець Божий, який гріхи світу забирає» (Йо. 1, 29).</em><em></em></p>\n','2014-01-18 05:09:47','http://oranta.org/index.php?option=com_content&view=article&id=1780:navechirja-bogojavlennja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Навечір\'я Богоявлення','uk',3961,5444,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5490,200,9dcce57de00e6396838045bba563.jpeg\" />Про людину, яка покинула придворне життя і, ставши монахом, присвятила себе боротьбі з єрессю, розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.</em></p>\n<p><strong>− Як сталося так, що перший секретар імператора став монахом, а по своїй смерті - святим?</strong></p>\n','2014-02-05 12:35:41','http://oranta.org/index.php?option=com_content&view=article&id=1828:hrystos-stav-ljudynoju-shchob-ljudyna-v-hrysti-mogla-braty-uchast-v-ogo-bozhestvenni-pryrodi&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Христос став людиною, щоб людина в Христі могла брати участь в Його божественній природі','uk',4012,5495,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5553,200,oz09.jpg\" />\"Господь гряде, і хто стерпить Його страх, хто явиться перед лицем Його, але, душе моя, будь готова на стрічу\" </em></p>\n<p>Третя пісня канону утрені М\'ясопусної неділі</p>\n<p>Притча про блудного сина минулої неділі символічно показала нам  незглибиме милосердя Бога, який приймає навіть найбільшого грішника,  коли той кається і шкодує, що здійснив гріхи.</p>\n','2014-02-22 23:57:38','http://oranta.org/index.php?option=com_content&view=article&id=1877:nedilja-pro-strashny-sud&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Неділя про Страшний суд','uk',4072,5555,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5572,200,s1.reutersmedia.net_.jpg\" />Зубожів, щоб збагатити нас Своїм убозтвом </em>(пор. 2 Кор. 8,9)</p>\n<p>Дорогі брати і сестри,</p>\n<p>З нагоди Великого Посту я пропоную деякі роздуми, щоб вони могли послужити особистому та спільнотному шляху навернення. У цих роздумах я виходжу зі слів св. Павла: «Бо ви знаєте благодать Господа нашого Ісуса Христа, що Він, будучи багатим, збіднів заради вас, щоб ви збагатились Його убозтвом» (2 Кор. 8,9 ).</p>\n','2014-02-27 09:47:16','http://oranta.org/index.php?option=com_content&view=article&id=1894:poslannja-svjatishogo-otcja-francyska-na-velyky-pist-2014-roku&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Послання Святішого Отця Франциска на Великий піст 2014 року','uk',4090,5573,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,240220141764.jpg\" />Ці слова відіграли одну з ключових ролей у появі Подячного хреста, який подорожує столицями світу у подяку Господу за наше спасіння. Хрест відвідав 34 держави, біля нього молився король Бельгії, Йоан Павло ІІ і Бенедикт XVI. Тепер Подячний хрест у Києві на головному Майдані країни.</em></p>\n<p><em>Хрест уже відвідав 34 держави і залишилось ще 170 столиць світу, інші міста Хрест відвідує за запрошенням. У столиці він обов’язково має бути на Святій Месі, а після свого візиту там встановлюють інший – менший хрест.</em></p>\n','2014-03-09 19:24:33','http://oranta.org/index.php?option=com_content&view=article&id=1917:2014-03-09-19-33-11&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Це не ти несеш хрест, це хрест несе тебе»','uk',4122,5605,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5581,200,cheesefare-p.jpg\" />Мт. 6, 14-21 Ти ж, коли постиш, намасти свою голову і вмий своє лице.</em></p>\n<p>Сьогоднішня неділя, яку називають сиропусною чи також неділею прощення, розпочинає особливий час, який триває десяту частину від цілого року - час покаяння, покути та особливої молитви, час, котрий християни всіх віків, неначе ту євангельську десятину, віддають Богові.</p>\n','2014-03-01 22:32:18','http://oranta.org/index.php?option=com_content&view=article&id=1899:nedilja-syropusna&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Неділя сиропусна','uk',4100,5583,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,91273.jpeg\" />«Вийшов сіяч сіяти зерно своє…» (Лук.8:5). </em>Є чотири місця, куди воно впало.&nbsp; Проросло лише там, де була добра земля.&nbsp; Умовне співвідношення: з п»яти посіяних зерен проросло лише одне. Така ось статистика.&nbsp; На хорошій землі воно може дати врожай стократний, але це процес. Проростання, поява вруна, дозрівання колосу… Скільки ще доведеться здолати труднощів до появи спілого жнива!</p>\n','2014-03-14 23:10:19','http://oranta.org/index.php?option=com_content&view=article&id=1935:chas-rozpochatysja-sudu&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Час розпочатися суду','uk',4146,5629,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5636,200,01.komentjepyskop.jpg\" />Військовий єпископ Михаїл Колтун відповів на запитання газети «Львівська пошта». </em></p>\n<p><strong><em>Якою має бути сьогодні адекватна відповідь на ситуацію в Криму?</em></strong><em> </em></p>\n<p>Роздумую про життя і його вартість я можу з впевненням сказати: святого втрачати не можна. Як для християнина є обов’язок зберігати всі заповіді і якщо однієї не збережеш то чи ти спасешся?</p>\n','2014-03-18 00:03:59','http://oranta.org/index.php?option=com_content&view=article&id=1945:cilisnist-batkivshchyny-ce-svjashchenny-obovjazok&catid=37:spilnota-cat&Itemid=49','P',NULL,'Цілісність Батьківщини – це священний обов’язок','uk',4158,5641,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5730,200,ArticleImages_56062_03.jpg\" />Був славетний наш князь Ізяслав, який на схилі своїх літ закликав до себе синів. Дав він їм по тонкому прутику в руку та попросив показати, чи зможуть вони його зламати. Здивувались сини легкості завдання, спокійно зламали тоненькі прутики. Тоді князь узяв кілька прутиків, міцно скрутив їх та передав синам – але не змогли вони нічого з ними зробити. Отож якщо ми будемо кожен сам за себе та десь поодинці – нас здолає будь-який ворог. А якщо ми переплетемось в одне-єдине – ніякий ворог нам не страшний…</em></p>\n<p>Саме з такою промовою починались спільні солдатські молитви під час недільної поїздки з військовими капеланами та музикантами по блокпостам у Херсонській області на «кордоні» з Кримом. І саме тоді ми всі стали свідками, як плачуть польові командири.</p>\n','2014-04-12 03:50:06','http://oranta.org/index.php?option=com_content&view=article&id=2024:koly-plachut-polovi-komandyry&catid=37:spilnota-cat&Itemid=49','P',NULL,'Коли плачуть польові командири','uk',4248,5731,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,_w8e1792_01.jpg\" />Доктор философии, президент украинского ПЕН-клуба, диссидент Евгений Сверстюк – один из самых авторитетных, хоть и не самых ярких, возможно, представителей своего поколения. Что такое тоталитаризм, он знает очень хорошо, поскольку много раз сталкивался с советской карательной системой. Именно о наследии советской эпохи в современной России мы и решили поговорить с Евгением Александровичем.</em></p>\n<p><strong>&nbsp;</strong></p>\n','2014-05-05 10:02:35','http://oranta.org/index.php?option=com_content&view=article&id=2107:prshch-kotor-vskakyvaet-v-neprystonom-meste&catid=37:spilnota-cat&Itemid=49','P',NULL,'Прыщ, который выскакивает в непристойном месте','uk',4339,5822,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5851,200,czerkvy.jpg\" />Виступ на</em><em> </em><em><a href=\"http://risu.org.ua/ua/index/exclusive/reportage/56442/\">круглому столі Центру Разумкова</a></em><em> </em><em>15 травня 2014 року «Що може зробити Церква в нинішній суспільно-політичній ситуації в Україні?»</em></p>\n<p>Найбільш видатний соціальний філософ сучасності Юрген Габермас говорить про повернення релігії в публічний дискурс як доконаний факт, характерний для доби постмодерну або гіпермодерну – як би ми не називали сучасний момент.</p>\n','2014-05-17 22:42:02','http://oranta.org/index.php?option=com_content&view=article&id=2140:vidpovidalnist-cerkov-za-mabutnje-ukrajiny&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Відповідальність церков за майбутнє України','uk',4369,5852,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,shevchuk_vladyka.jpg\" />Католицьким єпископським конференціям,&nbsp;</em><em style=\"font-size: 0.875em;\">Світовим релігійним та політичним лідерам,&nbsp;</em><em style=\"font-size: 0.875em;\">Усім людям доброї волі</em></p>\n<p><o:p></o:p></p>\n<p>Впродовж девʼяти місяців українці перебувають на важкому шляху від постсовєтського страху до свободи і Богом даної гідності. Травмовані Світовими війнами двадцятого століття, коричневим та червоним тоталітаризмами і геноцидом, вони прагнуть справедливого суспільства та демократичного, європейського майбутнього. З терпеливістю, витривалістю та великими людськими жертвами вони подолали в лютому брутальний режим Віктора Януковича. У відповідь на цей моральний тріумф у березні відбулась російська територіальна анексія Криму.<o:p></o:p></p>\n','2014-08-24 15:19:55','http://oranta.org/index.php?option=com_content&view=article&id=2368:zvernennja-glavy-ugkc-blazhennishogo-svjatoslava-do-svitovoji-gromadskosti-z-pryvodu-sytuaciji-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Глави УГКЦ Блаженнішого Святослава до світової громадськості з приводу ситуації в Україні','uk',4614,6097,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,NPOTuN4F-Kk.jpg\" />(З Требника митрополита Київського Петра Могили)</em></p>\n<p>Господи Боже наш, єдиний кріпкий і сильний у війні, Ти єдиний маєш владу оживляти і умертвляти. Ти тримаєш серця всіх у руці Своїй. Ти сам даєш тому кому бажаєш кріпость і мужність. Ти в безнадійній ситуації даєш перемогу над ворогами, немічні руки кріпиш, а сильних серцем боязливими і немічними робиш.</p>\n','2014-09-01 16:56:40','http://oranta.org/index.php?option=com_content&view=article&id=2384:molytva-pro-daruvannja-peremogy-nad-vorogom&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Молитва про дарування перемоги над ворогом','uk',4630,6113,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6116,200,12_tuzhniv_zhuttya.jpg\" />Проблема абортів щороку стає гострішою. Чи то глобалізація впливає на зміну пріоритетів, коли жінка радше готова присвятити все своє життя кар’єрі, аніж вихованню дитини, чи то падає моральний рівень молоді, яка розпочинає статеве життя мало не з 13 років, часто забуваючи про засоби захисту і не думаючи про можливі наслідки, піддаються згубному впливу суспільства. Причини і наслідки абортів ми спробували виокремити у матеріалі нижче.</em></p>\n','2014-09-04 12:58:31','http://oranta.org/index.php?option=com_content&view=article&id=2392:12-tyzhniv-zhyttja&catid=37:spilnota-cat&Itemid=49','P',NULL,'12 тижнів життя','uk',4637,6120,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,456.jpg\" />Лк. 10, 32-48 </em></p>\n<p>Кожне Богордичне свято відкриває нам одну з чеснот, привілей Богородиці: Богоматеринство, Дівство, Чистоту. Свято Покрову урочисто вшановує її Заступництво. Це свято найкраще виявляє духовне материнство Марії, зокрема її молитовне заступництво, любов і опіку над нами. Добра мати завжди переживає за своїх дітей - подібно Марія дбає про наше тілесне і духовне добро, вічне щастя. У малому повечір’ї вірні кожного разу величають Марію словами: „Заступнице християн бездоганна, молитвенице до Творця незамінна”. А в Молебні звертаються до неї такими словами: „Радуйся, заступнице всіх християн і прибіжище, Владичице”.</p>\n','2014-10-14 06:20:36','http://oranta.org/index.php?option=com_content&view=article&id=2486:pokrov-presvjatoji-bogorodyci&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Покров Пресвятої Богородиці','uk',4738,6221,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6227,200,199428.jpg\" />Знайти резиденцію архієпископа-емерита Любомира Гузара виявилося нелегко. Блаженніший живе на околиці села під Києвом. Живе настільки тихо, що небагато хто з місцевих жителів знає, де розташований його будинок – там, де закінчується асфальтована дорога.</em></p>\n<p><em>Колишній голова Української Греко-католицької церкви прийняв знімальну групу \"Обозревателя\" як дорогих гостей. Ми відчували себе в його резиденції, як вдома. І як найстаріший член родини, Блаженніший Любомир, відповідаючи на наші запитання, казав про речі, що видні лише здалека – з відстані фізичної і історичної.</em>\n','2014-10-25 22:26:05','http://oranta.org/index.php?option=com_content&view=article&id=2497:ljubomyr-guzar-v-ukrajini-ne-gromadjanska-vina-my-vyhodymo-z-nepravdyvogo-myru&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Любомир Гузар: в Україні не громадянська війна, ми виходимо з неправдивого миру','uk',4749,6232,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6240,200,marynovych_2f5a0.jpg\" />Влада це - екстракт отрути, яка булькає в середині нашого суспільства. Тому просто замінити владу не змінюючи систему нічого не дасть</em></p>\n<p><strong>Від того якими є духовні та моральні цінності у суспільстві, залежить доля бізнесу як системи. Про це на лекції-зустрічі для львівських підприємців розповів, дисидент, правозахисник, віце-ректор Українського католицького університету у Львові Мирослав Маринович.</strong>\n','2014-11-03 23:57:22','http://oranta.org/index.php?option=com_content&view=article&id=2506:myroslav-marynovych-pryklad-isusa-ce-nabezumnishy-biznes-proekt&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Мирослав Маринович: приклад Ісуса - це найбезумніший бізнес-проект','uk',4758,6241,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6271,200,1417100438_14.jpg\" />«Бережись вимірювати піст простим утриманням від їжі. Ті, які утримуються від їжі, а ведуть себе погано, уподібнюються дияволові, який хоча нічого не їсть, проте&nbsp;ж не перестає&nbsp;грішити»</em></p>\n<p><em>Святий Василій&nbsp;Великий</em></p>\n<p><strong>28 листопада</strong> починається Різдвяний піст, який ще називають Пилипівським, бо він починається на наступний день після святкування пам’яті святого апостола&nbsp;Пилипа.</p>\n','2014-11-27 23:18:30','http://oranta.org/index.php?option=com_content&view=article&id=2531:rizdvjany-pist&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Різдвяний піст','uk',4789,6272,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6275,200,gudziak.jpg\" />«Там» – він єпископ для українських греко-католиків Франції, Швейцарії і країн Бенелюксу; у Парижі – в Соборі Паризької Богоматері – очолює поминальну Службу за жертв Голодомору і за Небесну Сотню, згадуючи її поіменно.</em></p>\n<p><em>«Тут» – у Львові – він залишається Президентом Українського Католицького Університету, а разом зі своїми студентами брав участь у протестах Євромайдану, будучи у Києві в ключові моменти революції гідності.</em>\n','2014-12-01 13:58:58','http://oranta.org/index.php?option=com_content&view=article&id=2535:2014-12-01-12-04-50&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Рух Богом даної гідності має поширюватися»','uk',4794,6277,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6426,200,marynovych_4.jpg\" />Розмова з віце-ректором Українського католицького університету і членом ініціативної групи «Першого грудня» Мирославом Мариновичем про Революцію гідності: як Майдан змінив українців&nbsp;і чи є шанси на те, що він стане великою історичною подією для всієї України?</em></p>\n<p><strong>«І в останню мить Господь кидає сірничку…»</strong>\n','2015-02-27 14:26:42','http://oranta.org/index.php?option=com_content&view=article&id=2650:bunt-ukrajinskogo-vola&catid=37:spilnota-cat&Itemid=49','P',NULL,'Бунт українського вола','uk',4944,6427,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6545,200,bari-04.jpg\" />«Біси тремтять не тільки від самого Розп’ятого, а й від мощів тих, що за Нього віддали життя» (св. Йоан Золотоустий).</em></p>\n<p>22 травня 1089 р. Папа Урбан ІІ встановив свято перенесення мощів св. Миколая Чудотворця. Проте цю подію не святкують ні в Греції, ні в інших православних церквах, окрім Білорусі, України та Росії.</p>\n<p>З початку VII ст. на візантійську імперію чинили набіги мусульмани, підкорюючи дедалі нові території. У ХІ ст. ці напади продовжили турки-сельджуки. Ці завойовники були дуже жорстокі. Часто вони спалювали і повністю знищували цілі міста, вирізували всіх жителів, не жаліючи нікого. Люто ненавиділи сельджуки і християнські святині: храми, монастирі, вони їх грабували, руйнували, палили. Навіть могили, де спочивали останки святих, сельджуки розкопували, а мощі спалювали. Потім попіл розвіювали по вітру, щоби ніхто й ніколи не вшановував пам’яті цих великих праведників і чудотворців на місцях їхніх поховань.</p>\n','2015-05-21 23:23:55','http://oranta.org/index.php?option=com_content&view=article&id=2745:pro-sylu-moshchiv-svjatyh&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Про силу мощів святих…','uk',5064,6547,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://oranta.org/image,4360,200,tumblr_lxf7ulVsFF1r7nhd7o1_500.jpg\" /><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4360,200,tumblr_lxf7ulVsFF1r7nhd7o1_500.jpg\" />Слава Богу на висоті, і на землі мир, у людях добра воля!(Лук.2:14)</em></p>\n<p>Це перша колядка, що пролунала над землею. Пізніше люди складуть тисячі  інших, міліони християн різних націй і народів у пісні і молитві  прославлять Різдво Спасителя. Але сьогодні звернімося до цієї, першої  хвали, яку співали ангели.&nbsp; Залишивши небесні простори, слідом за своїм  Господом, що народився в убогій печері, вони явилися убогим і простим  пастушкам з радісною вісткою. &nbsp;Багато таємниць всесвіту знали ці  безтілесні духи, перші Божі створіння. Вони славили всемогутнього Бога в  акті створення світу, співали Йому хвалу, коли народжувалися зірки і  планети, виникало життя. Бога славить кожен промінь світла,&nbsp; крапля  роси, світло блискавки. А найбільше прославився Бог ставши людиною.  Ангели раділи побачивши Бога в тілі і славили Його задум спасіння.</p>\n','2013-01-05 14:07:44','http://oranta.org/index.php?option=com_content&view=article&id=918:persha-koljadka&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Перша колядка','uk',2888,4371,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_shevchuk_mixalik.jpg.jpg\" height=\"130\" width=\"192\" /></em>Як ми вже повідомляли, днями у Варшаві між Українською Греко-Католицькою Церквою та Римо-Католицькою Церквою Польщі<strong> </strong><a href=\"http://www.ugcc.org.ua/news_single.0.html?&amp;tx_ttnews%5btt_news%5d=11590&amp;cHash=65e537bfd4ebe0fa05949fd0ccce99b6\">було підписано спільну декларацію</a> примирення і прощення, яка стала знаковою подією в житті українського й польського народів. Про візит до Польщі, <a href=\"http://www.ugcc.org.ua/2768.0.html\">Декларацію примирення і прощення</a>,  про святкування 1025-річчя Хрещення України-Русі й значення віри в  житті сучасної людини розповість читачам «Галичини» в ексклюзивному  інтерв’ю Глава УГКЦ Блаженніший Святослав.</p>\n','2013-07-06 10:22:19','http://oranta.org/index.php?option=com_content&view=article&id=1319:blazhennishy-svjatoslav-lmy-zasudyly-chotyry-ideologiji-jaki-vchat-nenavydity-ljude-inshoji-nacionalnostir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Святослав: «Ми засудили чотири ідеології, які вчать ненавидіти людей іншої національності»','uk',3416,4899,0,NULL),
(1,1,'<p><em><img style=\"margin: 10px; float: right;\" src=\"image,5636,200,01.komentjepyskop.jpg\" alt=\"\" />Військовий єпископ Михаїл Колтун відповів на запитання газети &laquo;Львівська пошта&raquo;. </em></p>\r\n<p><strong><em>Якою має бути сьогодні адекватна відповідь на ситуацію в Криму?</em></strong></p>\r\n<p>Роздумую про життя і його вартість я можу з впевненням сказати: святого втрачати не можна. Як для християнина є обов&rsquo;язок зберігати всі заповіді і якщо однієї не збережеш то чи ти спасешся? Ні. Тож коли ми говоримо про нашу Батьківщину, її цілісність, то якщо ми знехтуємо чимось найменшим то спасення нам не буде.</p>','2014-03-18 00:03:59','http://oranta.org/index.php?option=com_content&view=article&id=1945:cilisnist-batkivshchyny--ce-svjashchenny-obovjazok&catid=37:spilnota-cat&Itemid=49','N','','Цілісність Батьківщини – це священний обов’язок','uk',4157,5640,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4567,200,16FEDE43-BC3B-45FC-98FD-6ABCCA478AEA_w640_r1_s.jpg\" /></strong>Нещодавно  Архиєпископ Любомир (Гузар) відзначив 80-літній ювілей. З цієї нагоди  (неважливо — напередодні дати чи після) хотілося з ним говорити про живу  віру і любов — те, на що він найбільше звертав нашу увагу впродовж  десяти років свого патріаршого служіння. Звертає і тепер.</em></p>\n<p><em>Зрештою, владика володіє харизмою любові, у чому багато хто сам переконався. А, як відомо, любов і жива віра притягують. </em></p>\n','2013-03-26 20:45:42','http://oranta.org/index.php?option=com_content&view=article&id=1077:ljubomyr-guzar-qje-problema-vid-ponedilka-do-suboty-zhyty-tym-chym-ljudy-napovnjujutsja-v-cerkvi-v-nediljuq&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Є проблема: від понеділка до суботи жити тим, чим люди наповнюються в церкві в неділю','uk',3086,4569,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6490,200,lazare.jpg\" />\"Голос Твій, Господи, знищив царство аду, і слово Твоєї власти піднесло з гробу чотириденного. І став Лазар прообразом спасенним воскресення... \"</strong></em><strong> </strong><em><strong>(Стихира вечірні Лазаревої суботи).</strong></em></p>\n<p>Майже кожний тиждень Великого посту має свої богослужбові особливості.&nbsp; Треба передовсім сказати про суботи й неділі, кожна з яких присвячена&nbsp; якійсь події з життя Церкви, Ісуса Христа, його Пресвятої Матері або&nbsp; святих.</p>\n','2015-04-03 21:26:04','http://oranta.org/index.php?option=com_content&view=article&id=2695:svjatkuvannja-lazarevoji-suboty&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Святкування лазаревої суботи','uk',5008,6491,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><strong><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6555,200,image034.jpg\" />«Ми виділи світло істинне, ми прийняли Духа небесного, ми знайшли віру істинну, нероздільній Тройці поклоняємося, Вона бо спасла нас» (Стиховна вечірні П\'ятдесятниці).</strong></em></p>\n<p>Світлий празник Пасхи завершується не менш світлим і великим празником&nbsp; Святої П\'ятдесятниці. Після Христового Різдва і Воскресення цей празник&nbsp; належить до найбільших празників нашого церковного року. Зіслання&nbsp; Святого Духа — це наче вінець і печать на справі спасення людського&nbsp; роду, що здійснив Божий Син. У дні святої П\'ятдесятниці сам Святий Дух&nbsp; помазує апостолів на проповідників Христового Благовістя. У дні Зіслання&nbsp; Святого Духа зароджується і починає діяти Христова Церква. Святий Дух&nbsp; її провадить, просвічує, освячує і зберігає на дорозі правди.</p>\n','2015-05-30 20:01:21','http://oranta.org/index.php?option=com_content&view=article&id=2757:zislannja-svjatogo-duha&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Зіслання Святого Духа','uk',5076,6559,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4534,200,svyatoslav_u_klobuku.jpg\" />«Чи був для Вас несподіваним вибір Конклаву?»</strong></em> – з цього запитання розпочався брифінг Блаженнішого Святослава Шевчука,  Глави УГКЦ, приурочений обранню Папи Франциска. Зустріч з журналістами  відбулася ввечері, 14 березня 2013 р. Блаженніший Святослав розповів:</p>\n<p>– <em>Коли я почув проголошення імені нового Папи, був дуже зворушений.  Справді, Господь Бог дав мені можливість знати його особисто.</em></p>\n','2013-03-18 22:37:35','http://oranta.org/index.php?option=com_content&view=article&id=1054:2013-03-18-20-44-19&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Чи був для Вас несподіваним вибір Конклаву?»','uk',3053,4536,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,preobrazeniye_ivanova.jpg\" />\"Величаємо Тебе, життєдавче Христе, і почитаємо пречистого Твого Тіла преславне Преображення\" (Величання на утрені празника).</strong></em><o:p></o:p></p>\n<p>У цей день наша Церква святкує празник світлого Господнього&nbsp; Преображення. Завдання цього празника — звеличувати славну подію&nbsp; Преображення з життя Ісуса Христа, яке деякі святі Отці називають другим&nbsp; Богоявленням. Важливість події видно з того, що її записали аж три&nbsp; євангелисти: Матей, Марко й Лука. Тож погляньмо на подію Преображення,&nbsp; на установлення празника та на дух його богослужби.\n','2014-08-19 09:43:10','http://oranta.org/index.php?option=com_content&view=article&id=2364:preobrazhennja-gospodnje&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Преображення Господнє','uk',4610,6093,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6132,200,pershe.jpg\" />Ініціативна група «Першого грудня» звертається до міжнародних лідерів та світової громадськості переглянути свою дотеперішню політику щодо російсько-українського конфлікту й усвідомити, що головним джерелом війни та агресії є політичний режим Президента Путіна.</strong></em></p>\n<p>Про це йдеться у зверненні, яке оприлюднили учасники групи Євген Сверстюк, Вадим Скуратівський, Мирослав Попович та В’ячеслав Брюховецький на прес-конференції в Українському кризовому медіа-центрі 9 вересня.</p>\n','2014-09-09 17:27:08','http://oranta.org/index.php?option=com_content&view=article&id=2408:2014-09-09-12-32-29&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Всеосяжна і жорстка ізоляція режиму Путіна — найнагальніше завдання для світової громадськості — група «Першого грудня»','uk',4651,6134,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,6521,200,464654.jpg\" />Франциск</strong></em><em><strong> </strong></em><em><strong>Єпископ Рима</strong></em><em><strong> </strong></em><em><strong>Слуга слуг Божих</strong></em></p>\n<p><em><strong>До всіх, хто читатиме цей лист,&nbsp;—</strong></em><em><strong> </strong></em><em><strong>Благодать вам, милосердя і мир!</strong></em></p>\n<p>Ісус Христос є обличчям милосердя Отця. На моє переконання, таємниця християнської віри знаходить у цьому слові свій синтез. Воно стало живим, видимим і осягнуло свою повноту в Ісусі з Назарета. Отець, «багатий милосердям» (пор. Еф 2,&nbsp;4), коли об’явив Мойсеєві своє ім’я: «Бог милосердний і ласкавий, нескорий на гнів, многомилостивий, вірний» (Вих 34,&nbsp;6),&nbsp;— вчинив так, що людина могла безперервно пізнавати Його божественну природу в різні способи та в численних моментах історії. У&nbsp;«повноту часів» (пор. Гал 4,&nbsp;4), коли все було готове згідно з Його планом спасіння, Він зіслав свого Сина, народженого з Діви Марії, аби остаточним чином об’явити нам свою любов. Хто бачить Сина, той бачить і Отця (пор. Йн 14,&nbsp;9). Ісус із Назарета своїми словами, жестами та цілою своєю особою об’явив нам милосердя Бога.</p>\n','2015-04-16 15:43:37','http://oranta.org/index.php?option=com_content&view=article&id=2723:2015-04-16-14-22-39&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Булла «Misericordiae vultus» («Обличчя милосердя»)','uk',5039,6522,0,NULL),
(1,1,'<p><em><strong><img style=\"margin: 10px; float: right;\" src=\"http://kyrios.org.ua/images/stories/svyati_i_svyata/chesniy-khrest.jpg\" alt=\"Воздвиження Чесного і Животворящого Хреста Господнього\" title=\"Воздвиження Чесного і Животворящого Хреста Господнього\" height=\"183\" width=\"145\" /></strong></em></p>\n<p><em><strong>Свято <a href=\"http://kyrios.org.ua/spirituality/svyato/2568-27-veresnja-vozdvizhennja-chesnogo-hresta.html\" target=\"_blank\"><span style=\"text-decoration: underline;\">Воздвиження Животворящого Хреста Господнього</span></a> встановлено на згадку про віднайдення та воздвиження Хреста Христового. </strong></em><em><strong>Ця знаменна подія відбулася за імператора Костянтина Великого, який першим з римських імператорів припинив гоніння на християн.</strong></em></p>\n<p><em><strong> </strong></em>Рівноапостольний  імператор Костянтин, що &nbsp;за допомогою &nbsp;Божою отримав у трьох війнах  перемогу над ворогами, побачив на небі Боже знамення - Хрест із написом:  «Сім переможеш». Гаряче бажаючи відшукати Хрест, на якому був  розіп\'ятий Ісус Христос, Костянтин направив у Єрусалим свою матір,  благочестиву царицю Олену.</p>\n','2013-09-27 07:23:25','http://oranta.org/index.php?option=com_content&view=article&id=1507:vozdvyzhennja-chesnogo-hresta-gospodnogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Воздвиження Чесного Хреста Господнього','uk',3631,5114,0,NULL),
(1,1,'<p><em><sup><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6524,200,D.jpg\" />19</sup></em><em>А як звечоріло, того самого дня, першого в тижні, – а две­рі ж були замкнені там, де перебували учні: страхались бо юдеїв, – увіходить Ісус, став посередині та й каже їм: «Мир вам!»&nbsp;<sup>20</sup>Це промовивши, показав їм руки й бік. І врадувались учні, по­бачивши Господа.&nbsp;<sup>21</sup>І ще раз Ісус їм каже: «Мир вам!» Як ме­не послав Отець, так я посилаю вас».&nbsp;<sup>22</sup>Це промовивши, дихнув на них і каже їм: «Прийміть Духа Святого! 23Кому відпустите гріхи – відпустяться їм, кому ж затримаєте – затримаються».</em></p>\n<p><em><sup>24</sup></em><em>Тома ж, один з дванадцятьох, на прізвисько Близнюк, не був з ними, коли то прийшов був Ісус.&nbsp;<sup>25</sup>Тож повідали йому інші учні: «Ми Господа бачили». Та він відрік:&nbsp;«Якщо не побачу на його руках знаків від цвяхів і не вкладу свого пальця у місце, де були цвяхи, а й руки моєї не вкладу в бік його, – не повірю!».</em>\n','2015-04-18 22:25:47','http://oranta.org/index.php?option=com_content&view=article&id=2726:komentar-jevangelija-na-tomynu-nedilju-o-2019-31&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Коментар Євангелія на Томину Неділю (Йо 20,19-31)','uk',5043,6526,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em><sup><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,5786,200,21700.jpg\" />19</sup></em><em>А як звечоріло, того самого дня, першого в тижні, – а две­рі ж були замкнені там, де перебували учні: страхались бо юдеїв, – увіходить Ісус, став посередині та й каже їм: «Мир вам!»&nbsp;<sup>20</sup>Це промовивши, показав їм руки й бік. І врадувались учні, по­бачивши Господа.&nbsp;<sup>21</sup>І ще раз Ісус їм каже: «Мир вам!» Як ме­не послав Отець, так я посилаю вас».&nbsp;<sup>22</sup>Це промовивши, дихнув на них і каже їм: «Прийміть Духа Святого! 23Кому відпустите гріхи – відпустяться їм, кому ж затримаєте – затримаються».</em>\n','2014-04-26 22:08:08','http://oranta.org/index.php?option=com_content&view=article&id=2076:tomyna-nedilja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Томина неділя','uk',4308,5791,0,NULL),
(1,1,'<p><em>«Божа благодать з\'явилася спасенна всім людям» (Тит. 2,11).</em><br /><br /><em> </em><br />Дорогі брати й сестри, цими словами апостола Павла відновляю радісну вістку Христового Різдва: так, сьогодні «з\'явилася всім людям благодать Бога, нашого Спасителя!»<br /><br />З\'явилася. Саме це святкує сьогодні Церква. Божа благодать, багата добротою та лагідністю, вже більше не схована, а «з\'явилася», виявилася у тілі, показала своє обличчя. Де? У Вифлиємі. Коли? За правління Кесаря Августа, під час першого перепису населення, про який згадує також євангелист Лука. І хто ж є її об\'явителем? Новонароджений, Син Діви Марії. У Ньому об\'явилася благодать Бога, Спасителя нашого. Саме тому це Дитя зветься Jehoshua - Ісус, що означає «Бог спасає».</p>\n<p>\n','2009-01-07 12:19:48','http://www.oranta.org/index.php?option=com_content&view=article&id=177:rizdvjane-poslannja-vselenskogo-arhyjereja-venedykta-xvi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Різдвяне послання Вселенського Архиєрея Венедикта XVI','uk',941,1915,0,NULL),
(1,1,'<p><em>Не маємо іншої допомоги, не маємо іншої надії, крім тебе, Пречиста Діво! Ти нам допоможи, на тебе надіємось і тобою хвалимось: бо ми твої слуги, щоб ми не посоромилися (Молебний канон Пресвятій Богородиці).</em></p>\n<p style=\"text-align: center;\"><strong>Дорогі в Христі!</strong></p>\n<p>У дні, коли наш народ і наша Батьківщина знову переживають великі випробування, пов’язані з окупацією частини нашої землі, наші серця наповнені тривогою за майбутнє, непевністю і страхом.</p>\n','2014-03-20 22:20:38','http://oranta.org/index.php?option=com_content&view=article&id=1958:pastyrske-poslannja-blazhennishogo-svjatoslava-z-nagody-vidnovlennja-aktu-posvjaty-ukrajiny-pid-pokrov-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Пастирське послання Блаженнішого Святослава з нагоди відновлення Акту посвяти України під Покров Пресвятої Богородиці','uk',4168,5651,0,NULL),
(1,1,'<p><em>Ще студентом Папського&nbsp; университету святого Томи Аквінського у  Римі Архиєпископ Мечислав Мокшицький кілька разів підходив до кардинала  Йосифа Рацінгера на пл. св. Петра, щоб привітатися і попросити  благословення. І аж ніяк не сподівався, що через декілька років  привітний і скромний професор стане Наступником Петра, а він – його  другим секретарем </em></p>\n','2013-03-08 22:51:58','http://oranta.org/index.php?option=com_content&view=article&id=1022:papa-zriksja-prestolu-a-ne-sluzhinnja-arhyjepyskop-mechyslav-mokshycky&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа зрікся престолу, а не служіння! – Архиєпископ Мечислав Мокшицький','uk',3000,4483,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><em>о.ліц Миколай Фредина</em></p>\n<p><span style=\"text-decoration: underline;\">УЧАСТЬ МИРЯН У ПАРОХІЯЛЬНІЙ ДУШПАСТИРСЬКІЙ РАДІ</span></p>\n<p align=\"center\"><strong>ВСТУП</strong></p>\n<p>Після довгих років переслідування, вийшовши з підпілля наша Церква успадкувала надзвичайно складну релігійну ситуацію, яка сьогодні є на наших парохіях. Більшість парохій, які перейшли у лоно УГКЦ були розбиті та зруйновані практичною душпастирською бездіяльністю, яка процвітала за роки Радянської влади. Священика вважали лише культовим працівником, якого на роботу приймала громада.</p>\n','2009-10-22 23:37:30','http://www.oranta.org/index.php?option=com_content&view=article&id=446:model-suchasnoji-parohiji-abo-jaka-parohija-taka-i-cerkva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Модель сучасної парохії, або яка парохія - така і Церква','uk',1848,3331,0,NULL),
(1,1,'<p><img height=\"157\" width=\"159\" src=\"http://askoldova-mohyla.org/uk/image,3773,200,165c3b3c22.jpg\" style=\"margin: 10px; float: left;\" />У часі перебування в період Різдвяних свят в Івано-Франківську Апостольський нунцій в Україні Його Преосвященство архієпископ Іван Юркович радо погодився дати ексклюзивне інтерв\'ю для газети «Галичина».</p>\n<p>– Ваша Екселенціє, сьогодні держава-місто Ватикан підтримує дипломатичні відносини зі 178 країнами світу. Чи виступають Католицька Церква і Святий Престол також суб\'єктами міжнародного права?</p>\n<p>– Саме так. Для Церкви існування Ватикану – це не лише історичний залишок Папської держави, а насамперед юридична інституція міжнародного права з усіма прерогативами суверенної держави, існування якої повністю присвячене забезпеченню функціонування Католицької Церкви і Святого Престолу.\n','2011-01-19 08:56:12','http://oranta.org/index.php?option=com_content&view=article&id=661:apostolsky-nunci-ivan-jurkovych-v-ukrajini-zavzhdy-pochuvajusja-jak-udoma&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Апостольський нунцій Іван Юркович: В Україні завжди почуваюся як удома','uk',2291,3774,0,NULL),
(1,1,'<p><img ilo-full-src=\"http://askoldova-mohyla.org/uk/image,3990,200,CRW_1088.JPG\" style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3990,200,CRW_1088.JPG\" />Ця земля благословенна, де</p>\n<p>Ваше Слово – світле і пророче,</p>\n<p>Мудрістю і правдою цвіте</p>\n<p>Для усіх, хто йде до храму, Отче!</p>\n<p> </p>\n','2011-07-25 06:32:04','http://oranta.org/index.php?option=com_content&view=article&id=775:vitannja-vsechesnomu-otcju&catid=25:parafija-cat&Itemid=27','P',NULL,'Вітання всечесному отцю','uk',2537,4020,0,NULL),
(1,1,'<p><img ilo-full-src=\"http://askoldova-mohyla.org/uk/image,4046,200,CRW_0919.JPG\" style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4046,200,CRW_0919.JPG\" />12 серпня 2011 року на сайті  УНІАНу з’явилося повідомлення, у якому була розглянута позиція пана  Олега Хрипуна, судді Вищого господарського суду, щодо ставлення церков  до судових процесів.</p>\n<p>Суддя Хрипун твердить, що церковні провідники своїми недоречними  заявами розбурхують емоції населення на шкоду судової гілки влади. Він  також наполягає, що відповідно до 35-ї статті Конституції України Церкви  та релігійні організації жодним чином не повинні втручатися в судові  процедури.</p>\n','2011-09-07 12:02:22','http://oranta.org/index.php?option=com_content&view=article&id=786:chy-pidryvaje-cerkva-dovirja-naselennja-do-sudovoji-gilky-vlady&catid=37:spilnota-cat&Itemid=49','P',NULL,'Чи підриває Церква довір’я населення до судової гілки влади?','uk',2564,4047,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img ilo-full-src=\"http://askoldova-mohyla.org/uk/image,4049,200,IMG_2136.JPG\" style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4049,200,IMG_2136.JPG\" />Київ – Заздрість (<st1:metricconverter productid=\"28 км\">28 км</st1:metricconverter> Хресної дороги ) – Зарваниця</p>\n<p>10 - 11 вересня 2011 року Божого в сьомий раз вирушили прочани з Києва, Камянець-Подільського, Львова Хресною дорогою пам’яті Патріярха Йосифа, яка споруджена громадою церкви св.. Миколая Чудотворця на Аскольдовій Могилі та інших парафій Києва за благословенням владики Василя Медвідя і світлої пам’яті владики Михаїла Сабриги і освячена молитвою паломників 27 березня 2004 р. на славу Божу і спасіння України.</p>\n','2011-09-12 20:45:18','http://oranta.org/index.php?option=com_content&view=article&id=787:hresna-doroga--zapovit-patrijarha-osyfa&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'ХРЕСНА ДОРОГА – ЗАПОВІТ ПАТРІЯРХА ЙОСИФА','uk',2567,4050,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3225,200,3.jpg\" style=\"float: right; margin: 10px;\" />Свято Преображення Господа нашого Ісуса Христа, зібрало багато парафіян і гостей міста Києва на Утреню і Божественну Літургію. Парафіяни принесли в кошиках яблука і багато дарів саду і землі нашої, як подяку Господу за гарний урожай.</p>\n','2009-08-20 14:43:31','http://www.oranta.org/index.php?option=com_content&view=article&id=409:jabluchny-spas&catid=25:parafija-cat&Itemid=27','P',NULL,'Яблучний Спас','uk',1741,3224,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3319,200,P7050019.JPG\" style=\"margin: 10px; float: right;\" />Аскольдова Могила - це справді велика родина! У цій родині є співаки, журналісти, спортсмени, актори, інженери, вчителі, медики... Сьогодні ми б хотіли познайомити Вас з чудовою людиною і цікавою особистістю з паном Володимиром Гриньківим, регентом церковного хору!</p>\n<p><strong>- Пане Володимире, скажіть, будь ласка, коли саме ви почали відвідувати церкву святого Миколая, що на Аскольдовій Могилі?</strong></p>\n<p>- Церкву святого Миколая, що на Аскольдовій Могилі я почав відвідувати восени 1993 року.</p>\n','2009-10-27 22:32:22','http://www.oranta.org/index.php?option=com_content&view=article&id=449:moja-askoldova-rodyna&catid=25:parafija-cat&Itemid=27','P','','Моя Аскольдова родина','uk',1852,3335,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3615,200,2.jpg\" style=\"float: left; margin: 10px;\" />День знань – це трепетний день для кожного школяра. Адже 1 вересня – це початок навчального року, це своєрідний старт до активної та самовідданої праці. Кожен школяр повинен зосереджено та цілеспрямовано приступити до навчання, адже знання – це запорука успіху людини.</p>\n<p>Протягом цього літа багато дітей від церкви святого Миколая відпочивали на морі чи то в Карпатах, де провели активний та водночас пізнавальний відпочинок. За цей подарунок усі діти дякують отцю Олексію, а особливо пароху громаду отцю Ігорю Онишкевичу, жертводавцям та вихователям.</p>\n','2010-09-08 12:57:02','http://oranta.org/index.php?option=com_content&view=article&id=574:na-starti-intelektualnyh-ta-duhovnyh-znan&catid=25:parafija-cat&Itemid=27','P','','На старті інтелектуальних та духовних знань','uk',2134,3617,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3643,200,Univ_1.jpg\" style=\"margin: 10px; float: left;\" />26 - 27 вересня 2010 року, на Свято Воздвиження Чесного і Животворящого Хреста, &nbsp;під гаслом «Справедливість, віра, милосердя!» відбулося щорічне паломництво працівників пенітенціарної системи України до Свято-Успенської Унівської Лаври, що на Львівщині.</p>\n<p>Представники управлінь Київської, Чернігівської, Одеської, Донецької, Тернопільської, Львівської, Івано-Франківської областей – всього близько 200 осіб – принесли свої молитви і благання до чудотворного місця, де зберігається одна з найбільших святинь України – Унівська ікона Богородиці. Традиційно проща розпочалася у місті Перемишлянах у храмі,&nbsp; де душпастирював Блаженний Священомученик Омелян Ковч, знаний як «парох Майданека».</p>\n','2010-09-29 18:12:40','http://oranta.org/index.php?option=com_content&view=article&id=592:derzhavna-kryminalno-vykonavcha-sluzhba-ukrajiny-pid-chas-palomnyctva-otrymala-jak-dar-osvjachenu-horugvu&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Державна кримінально-виконавча служба України під час паломництва отримала як дар освячену хоругву','uk',2161,3644,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3737,200,IMG_0566.JPG\" style=\"margin: 10px; float: right;\" />Коли ми були дітьми то завжди чекали Миколая, бо він нам приносив солодкі подарунки, коли виросли то вже знаємо, насправді для кожного з нас були наші батьки Миколаями. Але тепер кожен з нас має можливість створювати свято для інших. У цій коротенькій статті хотілось подякувати тим, хто залишився за лаштунками свята, але займались його підготовкою адже це велика&nbsp; праця була підготувати фестиваль ”Аскольдів глас” і свято Миколая.</p>\n<p>Адже часто ми думаємо, що допомогти ми можемо комусь тільки матеріально, але якщо задуматись, то найдорожчим у нашому житті є час. Як часто, щодня ми повторюємо: не маю часу, &nbsp;не встигаю, і коли ми все відкладаємо і використовуємо свій час, щоб послужити ближньому своєму&nbsp; ось, що є свідченням того, те, слово Боже в нас проростає і дає плоди.</p>\n<p>Дякую всім за Вашу працю хто будь-який&nbsp; спосіб прилучився до створення свята.</p>\n<p>Ярослав Павлюк, «Оранта»</p>\n','2010-12-22 08:39:32','http://oranta.org/index.php?option=com_content&view=article&id=642:malenka-podjaka-za-velyku-pracju&catid=25:parafija-cat&Itemid=27','P',NULL,'Маленька подяка за велику працю','uk',2255,3738,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3740,200,sten.JPG\" style=\"margin: 10px; float: left;\" />Ось і завершився VI фестиваль-конкурс духовної пісні «Аскольдів глас» - величне пісенне свято. Цього року в конкурсі взяло участь 14 найобдарованіших співаків-солістів Київщини, Львівщини, Івано-Франківщини – Анастасія Попеско, Любов Кокоєва, Оксана Сливка, Руслана Лоцман, Наталя Сиротич, Мар’яна Головко, Ольга Засадна, Марина Коробко, Руслана Дмитровська, Юрій Вольбин, Андрій Маслаков, Зорина Валіхновська, Неля Франчук та Ірина Голоско. Вік учасників коливався від 14 до 42 років. Виступи співаків створили незабутню атмосферу свята, викликаючи сльози радості й очищення на очах слухачів. Усі конкурсанти продемонстрували високий професійний рівень та ретельну підготовку до конкурсу.</p>\n','2010-12-21 21:57:25','http://oranta.org/index.php?option=com_content&view=article&id=645:rezultaty-vi-festyvalju-konkursu-duhovnoji-pisni-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27','P',NULL,'Результати VI фестивалю-конкурсу духовної пісні «Аскольдів глас»','uk',2258,3741,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3773,200,1214bc128d.jpg\" style=\"margin: 10px; float: left;\" />Коротка історія празнику Обрізання ГНІХ</p>\n<p>Найближчою важливою подією із життя Ісуса Христа, що наступає після празника Христового Різдва, є Господнє Обрізання і надання імени. Святе Євангеліє про це так говорить: \"Як сповнились вісім днів, коли мали обрізати хлоп\'ятко, назвали Його Ісус — ім\'я, що надав був ангел, перше, ніж Він почався у лоні\" (Лк. 2, 21). Ісус Христос, як Бог і Законодавець, не був зобов\'язаний виконувати релігійні приписи ізраїльського народу, та все таки Він їм добровільно підкорився і їх зберігав. З тієї причини Він восьмого дня після народження піддався обряду обрізання. Цю подію святкуємо першого січня.\n','2011-01-13 08:49:12','http://oranta.org/index.php?option=com_content&view=article&id=660:sv-vasylija-velykogo&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Св. Василія Великого','uk',2292,3775,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3774,200,ac550b751c05d513d859ea611f80b816_S.jpg\" style=\"margin: 10px; float: right;\" />Українські церкви за нової влади, якій незабаром буде вже рік. Чи всі почуваються рівними? Що буде з Собором Святої Софії? Якими є стосунки греко-католиків з православними - зокрема на заході України?</p>\n<p>Це - теми інтерв\'ю, яке дав у Лондоні Українській Службі Бі-Бі-Сі глава Української Греко-Католицької церкви кардинал Любомир Гузар.</p>\n<p>Бі-Бі-Сі: Греко-католицька церква недавно скаржилась на недостатню увагу до неї - та й інших конфесій - з боку влади. Чим були викликані таки скарги?\n','2011-01-19 18:06:46','http://oranta.org/index.php?option=com_content&view=article&id=662:kardynal-guzar-ljubov-vlady-poshkodyt-upc-mp&catid=37:spilnota-cat&Itemid=49','P',NULL,'Кардинал Гузар: любов влади пошкодить УПЦ (МП)','uk',2293,3776,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3780,200,john-paul-ii-genogram.jpg\" style=\"margin: 10px; float: right;\" />У п\'ятницю, 14 січня 2011 р., вранці, Папа Бенедикт XVI прийняв на спеціальній аудієнції кардинала Анджело Амато, Префекта Конгрегації в справах святих. Під час зустрічі Святіший Отець підписав декрети, що стосуються деяких беатифікаційних та канонізаційних процесів. Серед них – визнання чуда за заступництвом Слуги Божого Івана Павла ІІ. Йдеться про зцілення від хвороби Паркінсона французької черниці Марі Сімон П\'єрр. Як зазначив в інтерв\'ю для Радіо Ватикану кардинал Анджело Амато, цей декрет відкриває дорогу до беатифікації Папи Івана Павла ІІ, яка відбудеться в Римі 1 травня 2011 р., в неділю після Пасхи, тобто неділю Божого Милосердя</p>\n<p>\n','2011-01-20 10:28:15','http://oranta.org/index.php?option=com_content&view=article&id=664:1-travnja-2011-beatyfikacija-slugy-bozhogo-ivana-pavla-ii-papa-benedykt-xvi-pidpysav-dekret-pro-vyznannja-chuda&catid=32:gk-svit-cat&Itemid=58','P',NULL,'1 травня 2011 – беатифікація Слуги Божого Івана Павла ІІ. Папа Бенедикт XVI підписав декрет про визнання чуда','uk',2299,3782,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3789,200,2.JPG\" style=\"float: right; margin: 10px;\" />У новому 2011 році діти з церкви святого Миколая, що на Аскольдовій Могилі, поїхали відпочивати у зимові засніжені Карпати.</p>\n<p>27 січня дев\'ятеро дітей та вихователі поїздом Київ – Чернівці вирушили у дорогу. У вагоні панувала радісна атмосфера, адже скоро на дітей чекатимуть три дні справжнього зимового відпочинку. У поїзді діти співали колядки, молилися і спілкувалися  один з одним. У вагоні відчувалося душевне тепло, адже менші діти бігали один за одним сміялися, гралися, старші ж хлопці стримували їх і наказували, щоб було тихо, хоча самі колись в дитинстві так веселилися...</p>\n','2011-02-03 09:58:51','http://oranta.org/index.php?option=com_content&view=article&id=671:zymovy-vidpochynok-dite-u-karpatah-2011-roku-bozhogo&catid=25:parafija-cat&Itemid=27','P','','Зимовий відпочинок дітей у Карпатах 2011 року Божого','uk',2308,3791,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3799,200,32ee2551bd.jpg\" style=\"float: left; margin: 10px;\" />Той, хто хоча б один раз зіштовхувався з главою УГКЦ патріархом Любомиром Гузаром, уже ніколи не стане іронічно заламувати брову на слово «авторитет». Це людина, до якої прислухаються всі — і „свої“, і „чужі“. Просто тому, що її слова та оцінки не спираються на хитке розуміння політичної доцільності чи модної ідеології. Може, тому ніхто в присутності глави УГКЦ не почувається „чужим“. Патріарха Любомира слухають з особливою увагою: його слова багато що ставлять на свої місця, повертають суть речам, — у світі медійних напівправд це здається справжнім дивом. Він постійно дивує своїх співрозмовників умінням бути справжнім у світі, яким правлять фальшивки. Умінням показати нам щось інше — інший погляд, інші перспективи, інші бажання — і посіяти в душах прагнення до цього іншого.</p>\n','2011-02-14 10:09:16','http://oranta.org/index.php?option=com_content&view=article&id=674:blazhennishy-ljubomyr-guzar-lmy-musymo-sebe-znovu-vidkryty-jak-ljudy-jaki-majut-bagato-spilnogor&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Любомир Гузар: «Ми мусимо себе знову відкрити — як люди, які мають багато спільного»','uk',2318,3801,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3800,200,d578897ffa.jpg\" style=\"float: right; margin: 10px;\" />Коротка історія<br /><br />Празник Стрітення засновується на події з життя нашого Спасителя, яку записав святий євангелист Лука (2, 22-40). Закон Мойсея приписував, щоб кожна жінка після народження дитини 40 днів не сміла входити до храму, бо в той час вона вважалася нечистою. Ці 40 днів називалися днями очищення. Після закін чення цих днів мати новонародженої дитини приходила до храму і приносила жертву очищення. Багатша жінка жертвувала однолітнє ягня на всепалення та молодого голуба або горлицю, а вбога повинна була принести пару голубів або пару горлиць. Пречиста Діва Марія, як Божа Мати, не була зобов\'язана до обряду очищення, бо освятилася Христовим Різдвом, як каже кондак празника: \"Утробу Дівичу освятив Ти різдвом Твоїм\". Та все-таки вона в покорі виконує припис закону і приносить у жертву дві горлиці.</p>\n<p> </p>\n','2011-02-14 19:26:13','http://oranta.org/index.php?option=com_content&view=article&id=675:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Стрітення Господа Бога і Спаса нашого Ісуса Христа','uk',2317,3800,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3804,200,IMG_6516.JPG\" style=\"float: left; margin: 10px;\" />17 лютого 1892 року в селі Заздрість Теребовлянського повіту на Тернопільщині в хліборобській сім’ї Івана Коберницького-Сліпого та Анастасії Дичковської народився син, що згодом став Архиєпископом Києво-Галицьким і всієї Руси, Патріархом Української Греко-Католицької Церкви.</p>\n<p>Київська громада стараннями подружжя Богдана і Світлани Тернопільських, які створили Всеукраїнський фонд Порозуміння, ,Патріяршого Товариства, Громад київського деканату вшанували пам’ять великого сина українського народу в день його уродин. Зібрання відбулося в кінотеатрі «КИЇВ», де громада переглянула фільм «Патріарх».</p>\n','2011-02-18 10:32:23','http://oranta.org/index.php?option=com_content&view=article&id=676:urodyny-patriarha-osyfa-slipogo&catid=25:parafija-cat&Itemid=27','P',NULL,'УРОДИНИ ПАТРІАРХА ЙОСИФА СЛІПОГО','uk',2322,3805,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,3907,200,g55_voskres.jpg\" style=\"margin: 10px; float: left;\" />Хай лине увись передзвін великодній,<br />І кличе молитва душу в політ!<br />Хай зійдуть на землю щедроти Господні<br />І крила розгорне оновлений світ!</p>\n<p>Христос Воскрес!</p>\n<p>Редакційна колегія вітає зі Світлим празником Воскресіння Христового! Божої ласки та благодаті, Христової любові у ваші серця, материнської опіки Пресвятої Богородиці.</p>\n','2013-05-04 23:46:34','http://oranta.org/index.php?option=com_content&view=article&id=735:z-voznesinnjam-hrestovym&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'З Воскресінням Христовим!','uk',2425,3908,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,4393,200,028_4001.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p align=\"center\"><strong>ПОУЧЕННЯ ТИМ, ХТО ПРИСТУПАЄ ДО СВЯТИХ ТАЇНСТВ ПОКАЯННЯ І ЄВХАРИСТІ</strong><strong>Ї</strong></p>\n<p>Святе таїнство Сповіді (Покаяння) є особливою зустріччю заблуканої  людини, якою є кожен із нас, з милосердним Богом, нашим люблячим  Батьком. Для того, щоб глибоко пережити цю зустріч, потрібно належним  чином приготуватися до неї, виконуючи наступні умови:</p>\n<p>1)&nbsp; зробити іспит совісті;</p>\n<p>2)&nbsp; збудити жаль за гріхи;</p>\n','2014-04-12 09:39:04','http://oranta.org/index.php?option=com_content&view=article&id=1039:pouchennja-tym-hto-prystupaje-do-tajinstva-pokajannja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Поучення тим, хто приступає до таїнства Покаяння','uk',3019,4502,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,03_02_16_20_web.jpg\" style=\"margin: 10px; float: left;\" />Пригадую, в середині 1990-х років не один мій співбесідник переконано повторював, як мантру: «Ось Ви так захищаєте кримських татар. А вони ж знову нас зрадять, як у часи Хмельницького зрадили під Берестечком». Йшлося про те, що кримські татари відвели тоді свої війська з поля бою, внаслідок чого козацька армія зазнала нищівної поразки.</p>\n<p>У деяких головах ця схема закріпилася намертво – дарма що впродовж більш як двадцяти років нашого відродження не було в Криму вірнішої Україні етнічної групи, ніж кримські татари.</p>\n','2014-04-13 06:57:43','http://oranta.org/index.php?option=com_content&view=article&id=2028:krymska-kryza-i-cyvilizacini-shansy-ukrajiny&catid=37:spilnota-cat&Itemid=49','P',NULL,'Кримська криза і цивілізаційні шанси України','uk',4252,5735,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,4502,200,ArticleImages_44708_vjizd.jpg\" style=\"margin: 10px; float: right;\" />Виходячи з суто людських міркувань, Пальмова неділя була найбільшим тріумфом Ісуса. Йому випадало й раніше збирати великі натовпи людей, коли помножував хліб чи проголошував Нагірну проповідь. Та ці події відбувалися в глибокій провінції, якою була Галілея, серед простих невчених людей. Тепер Його вітають у столиці, святому місті, де в Храмі мешкає Божа Слава. Вітають натовпи євреїв з усього світу, паломників, що почали з’їжджатися з нагоди Пасхи. Та й вітають Ісуса не як учителя (раббі), але як Месію, Божого Помазаника і Спасителя: титул «син Давида», яким Його величають, прямо вказує на те, за кого Ісуса приймає сповнений радості натовп.</p>\n','2014-04-12 23:59:41','http://oranta.org/index.php?option=com_content&view=article&id=2027:hto-na-jakogo-mesiju-chekaje&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Хто на якого Месію чекає?','uk',4251,5734,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,4609,200,01.jpg\" style=\"margin: 10px; float: left;\" />Благовіщення, Благовіщення Пресвятої Богородиці – велике двонадесяте свято, третє за значенням народно-релігійне свято після <a href=\"http://www.sviato.in.ua/velykden%27_rel.php\">Великодня</a> і <a href=\"http://www.sviato.in.ua/rizdvo_rel.php\">Різдва Христового</a>. Православні святкують Благовіщення 7&nbsp;квітня, католики – 25&nbsp;березня.</p>\n<p><strong>Походження свята</strong></p>\n','2013-04-07 10:58:30','http://oranta.org/index.php?option=com_content&view=article&id=1093:blagovishchennja-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Благовіщення Пресвятої Богородиці','uk',3127,4610,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,4667,200,banquet.jpg\" style=\"margin: 10px; float: left;\" />У Великі Понеділок, Вівторок і&nbsp;Середу відбуваються богослужіння, включаючи Літургію Передосвячених Дарів, що майже не відрізняються від буденних служб Чотиридесятниці.&nbsp;<o:p></o:p></p>\n','2014-04-14 10:52:47','http://oranta.org/index.php?option=com_content&view=article&id=2031:velyki-ponedilok-vivtorok-i-sereda&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Великі понеділок, вівторок і середа','uk',4256,5739,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5355,200,photo.jpg\" style=\"margin: 10px; float: right;\" />19 грудня православні люди всього світу святкують&nbsp;<strong>день святого Миколая Чудотворця</strong>. За народними повір’ями саме в цей день Миколай Чудотворець спускався на землю з небес і обходив весь світ, допомагаючи знедоленим і відновлюючи справедливість.</p>\n<p>З поміж великих святих угодників Божих святитель Миколай Чудотворець користується особливою любов’ю нашого народу.\n','2013-12-19 12:16:15','http://oranta.org/index.php?option=com_content&view=article&id=1718:svjaty-mykola&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Святий Миколай','uk',3873,5356,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5364,200,delayut-brillianty.jpg\" style=\"margin: 10px; float: right;\" />Голодний півень шукав поживу. Попорпав під дровами, у соломі, в листі, навколо цегли… Раптом застиг: перед ним лежав камінчик, що якось дивно виблискував.<o:p></o:p></p>\n<p>Зацікавлений, півень став уважно до нього приглядатися. Потім зрозумів: це був незвичайний камінчик. Його форма, його поблискування говорили про це.<o:p></o:p></p>\n','2013-12-20 23:43:55','http://oranta.org/index.php?option=com_content&view=article&id=1689:piven-i-diamant-21-grudnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Півень і діамант (21 грудня)','uk',3881,5364,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5481,200,22.jpg\" style=\"margin: 10px; float: left;\" />Жив на світі один чоловік, який вірив у Бога всім серцем. Він мав зошит, куди записував свої потреби: коли Бог виконував якусь з них, чоловік ставив навпроти неї позначку. Увесь зошит був списаний потребами і проханнями. У ого чоловіка був знайомий-безвірник. І чоловік молився за нього без упину, аби той прийняв Ісуса Христа.</p>\n','2014-01-31 21:24:11','http://oranta.org/index.php?option=com_content&view=article&id=1817:nashi-partnery-1-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Наші партнери (1 лютого)','uk',4000,5483,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5481,200,53_1.jpg\" style=\"margin: 10px; float: left;\" />На палубі великого пароплава стояв чоловік і бавився, підкидаючи і ловлячи блискучу річ. Пасажири, дивлячись на нього, запитали:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що це у вас?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це - коштовний алмаз, - відповів чоловік. – Я продав усе своє майно, яке заробляв протягом життя, аби придбати його.</p>\n','2014-02-02 22:11:19','http://oranta.org/index.php?option=com_content&view=article&id=1821:koshtovny-almaz-3-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Коштовний алмаз (3 лютого)','uk',4006,5489,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5515,200,pidgotovka-front.jpg\" style=\"margin: 10px; float: left;\" />Надумав молодий хлібороб женитися і вирушив з приятелями на свято щоб вибрати собі наречену. Такі красуні пішли в танок, що у парубків очі забігали: одна краща за іншу. Ступають наче пави, плечима поводжують, красуються. І тільки одна сидить скромно збоку, схиливши голову і опустивши очі.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он моя суджена, - показав на неї хлібороб.</p>\n','2014-02-15 00:07:26','http://oranta.org/index.php?option=com_content&view=article&id=1850:rozsudlyvy-hliborob-15-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Розсудливий хлібороб (15 лютого)','uk',4040,5523,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5525,200,bludnoho_syna.jpg\" style=\"margin: 10px; float: right;\" />16 лютого, в Неділю про блудного сина, Отець і Глава УГКЦ Блаженніший Святослав (Шевчук) під час проповіді на Божественній Літургії в Патріаршому соборі Воскресіння Христового УГКЦ, що у Києві, наголосив, що сьогодні ми переживаємо особливий момент віднайдення гідності українця.</p>\n<p>«Цей час багато мислителів окреслило як «революція гідності». Та свободу та гідність не надає нам певний президент, чи якийсь закон. Ці цінності нам дані Богом.</p>\n','2014-02-16 19:43:21','http://oranta.org/index.php?option=com_content&view=article&id=1856:2014-02-16-21-53-27&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Свободу та гідність нам не надає президент чи закон. Ці цінності нам дані Богом…»','uk',4043,5526,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5685,200,molitva4.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>Шукач скарбів прочитав у стародавній книзі, що в одному місці закопані в землі незліченні скарби і вирішив відшукати їх. Він довго розшукував цей скарб.</p>\n<p>Одного разу копаючи землю він помітив, що з неба на нього опускається золота лопата. Чоловік радісно схопив її і не тямлячись від щастя поніс додому.</p>\n<p>Про закопані скарби, він вже не думав. вважаючи, що золотої лопати йому вже вистачить. І почув з неба голос:</p>\n','2014-04-01 23:33:54','http://oranta.org/index.php?option=com_content&view=article&id=1987:ljudyna-i-skarb-2-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Людина і скарб (2 квітня)','uk',4204,5687,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5687,200,canonizzazione_mini_mini.jpg\" style=\"margin: 10px; float: left;\" />Через кілька тижнів, 27 квітня 2014 р., блаженні Папи Іван ХХІІІ та Іван Павло ІІ будуть вписані до «величезного збору святих і мучеників», як на початку третього тисячоліття назвав цей сонм Божих угодників Папа Войтила. Протягом свого понтифікату вони наголошували на тому, що святі, які показують нам «живе Христове обличчя», є для Церкви засобом євангелізації та освячення Церкви. Загалом, усім відомо про рекорди беатифікацій та канонізацій, проголошених Іваном Павлом ІІ, але й не меншим є наголошення на святості Іваном ХХІІІ, зокрема під час приготування до Другого Ватиканського Собору.<o:p></o:p></p>\n','2014-04-03 00:39:59','http://oranta.org/index.php?option=com_content&view=article&id=1991:svjati-pro-svjatist&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'СВЯТІ ПРО СВЯТІСТЬ','uk',4207,5690,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5699,200,b235a3.jpg\" style=\"margin: 10px; float: left;\" />Вулицею йшла дівчинка і вела за руку дуже п’яну жінку, яка горбилась і лаяла когось. Її волосся було розкуйовджене, а одежа дуже брудна. Люди навколо не приховували своєї огиди і відхилялися вбік, а хтось обурено сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жахіття. Яка п’яниця.</p>\n','2014-04-05 22:55:46','http://oranta.org/index.php?option=com_content&view=article&id=1999:dytjacha-ljubov-6-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дитяча любов (6 квітня)','uk',4219,5702,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5709,200,buket.jpg\" style=\"margin: 10px; float: left;\" />Надумав якось хлопець одружитися і пішов до дружини нареченої. Приніс він подарунки і солодощі. Брати нареченої питаються жениха:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Чому ти береш за себе нашу сестру?<o:p></o:p></p>\n<p>І відповів їм жених казкою притчею:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Дано, давно, велетенський дракон захопив єдине джерело у краї, люди залишилися без води, жінки плакали, діти стогнали від спраги.</p>\n','2014-04-07 10:57:31','http://oranta.org/index.php?option=com_content&view=article&id=2003:chetverty-mudrec-7-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найгарніша (7 квітня)','uk',4223,5706,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5709,200,kreshenie.jpg\" style=\"margin: 10px; float: left;\" />Якось ішов до себе старець в пустеню з молодим учнем. Дорога вела до гори. Юнак випередив свого учителя і неждано і негадано зустрів на завороті язичницького жреця.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Відступи слуго диявола, - гукнув до нього надміру запальний юнак.<o:p></o:p></p>\n<p>Жрець розсердився і щосили вдарив кривдник а палицею, юнак упав без пам’яті.</p>\n','2014-04-07 22:27:22','http://oranta.org/index.php?option=com_content&view=article&id=2008:syla-ljubovy-8-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Сила любови (8 квітня)','uk',4227,5710,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5711,200,1_1_788515.jpg\" style=\"margin: 10px; float: left;\" />Лише Ісус може допомогти нам вийти з гробниць гріха, з відмерлих частин нашого серця. Такими думками Папа Франциск поділився під час Святої Меси. Коментуючи євангельське читання про воскресіння Лазаря, він закликав виходити з гробів, які кожен з нас має у своєму нутрі.<o:p></o:p></p>\n<p>Але перед Євхаристійним богослуженням Єпископ Риму зустрівся представниками різних парафіяльних груп.</p>\n','2014-04-08 11:38:53','http://oranta.org/index.php?option=com_content&view=article&id=2009:lyshe-isus-mozhe-voskresyty-umertvleni-grihom-chastyny-nashogo-sercja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Лише Ісус може воскресити умертвлені гріхом частини нашого серця','uk',4229,5712,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5724,200,stamatis.jpg\" style=\"margin: 10px; float: right;\" /></p>\n<p>Всесвітліші, всечесні отці, преподобні сестри, дорогі брати. Сьогодні ми переживаємо з вами Лазареву суботу. Це є такий особливий день в літургійному році. Ви, мабуть, вчора, як співали стихир на Літургії Наперед Освячених Дарів, то чули такі слова, що ось закінчилася Чотиридесятниця, фактично закінчився Великий піст і сьогодні ми починаємо Квітну тріоть: новий період, новий цикл в літургійному році, який безпосередньо прив’язаний до Пасхального Таїнства. Всі ті дні, які будемо переживати сьогодні, завтра, наступного тижня, безпосередньо вже впроваджують нас до цього Таїнства страждання, смерті і воскресіння Ісуса Христа.</p>\n','2014-04-10 23:20:53','http://oranta.org/index.php?option=com_content&view=article&id=2021:lazareva-subota&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Лазарева субота','uk',4244,5727,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5752,200,4645.jpg\" style=\"margin: 10px; float: left;\" />Мудрець запитав учня у чому полягає найсумніша трагедія життя.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Напевно у тому, що людина не знаходить відповіді на свої питання, - відповів учень.</p>\n','2014-04-20 23:08:01','http://oranta.org/index.php?option=com_content&view=article&id=2051:tragedija-zhyttja-21-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Трагедія життя (21 квітня)','uk',4284,5767,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5762,200,1797501.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>«Cristus surrexit, venite et videte!» - «Христос Воскрес, прийдіть і подивіться!»<o:p></o:p></p>\n<p>Дорогі брати й сестри, благодатного і святого вам Великодня!<o:p></o:p></p>\n<p>У Церкві, розсіяній по всьому світі відлунює звіщення ангела, звернене до жінок: «Не бійтеся! Я знаю, що ви шукаєте Ісуса розп’ятого. Його нема тут. Він воскрес... Прийдіть і подивіться на місце, де Його було покладено» (Мт 28,5-6). Не бійтеся! Господь Воскрес!<o:p></o:p></p>\n','2014-04-20 20:07:14','http://oranta.org/index.php?option=com_content&view=article&id=2054:zustrich-z-voskreslym-hrystom&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Зустріч з воскреслим Христом','uk',4280,5763,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5771,200,DSC06759.JPG\" style=\"margin: 10px; float: left;\" />«Радість Христового Воскресіння цьогоріч змогли відчути і підопічні Київського Карітасу, - безхатченки й діти вулиці, самотні літні люди, діти із кризових сімей та бідні сім’ї, яким допомагає БФ «Карітас Київ». Ми розділили із ними їхні святкові Великодні столи, які накрили завдяки доброчинцям та милосердним людям», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n','2014-04-22 11:45:50','http://oranta.org/index.php?option=com_content&view=article&id=2061:2014-04-22-09-00-51&catid=29:propovidi-cat&Itemid=34','P',NULL,'БФ «КАРІТАС КИЇВ» ВЕЛИКДЕНЬ СВЯТКУВАВ ІЗ ПОТРЕБУЮЧИМИ','uk',4290,5773,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5779,200,1_0_793507.jpg\" style=\"margin: 10px; float: right;\" />Провідною думкою Святішого Отця сьогодні було існування християни, які бояться радості Воскресіння, життя яких подібне до похоронної відправи.<strong></strong></p>\n<p>На ці думки Папу спонукав євангельський уривок, прочитаний під час Меси, у якому розповідалося про появу Ісуса Христа апостолам після Воскресіння. Як підкреслив Папа, на вітання: «Мир вам!», – що прозвучало з уст Спасителя, апостоли, замість того, щоб сповнитись радістю, стривожилися і налякалися, думаючи, що бачать привида.</p>\n','2014-04-24 22:03:35','http://oranta.org/index.php?option=com_content&view=article&id=2068:strah-pered-radistju-hvoroba-hrystyjan-sogodennja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Страх перед радістю — хвороба християн сьогодення','uk',4297,5780,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5784,200,1264345930dobro.jpg\" style=\"margin: 10px; float: left;\" />До віконечка придорожньої кав’ярні для автомобілістів під’їжджає на старенькій машині жінка, яка тут вже багато років поспіль постійно замовляє каву. Перекидаючись люб’язністю з продавцем вона помічає машину, що стає за нею у чергу. У доброму настрої жінка хоче зробити якесь добро і платить продавцеві за каву для наступної машини. Продавець здвигає плечима і думає, що ці люди знайомі. Жінка від’їжджає. Наступного дня вона як завжди під’їжджає до віконечка кав’ярні.\n','2014-04-26 00:03:20','http://oranta.org/index.php?option=com_content&view=article&id=2073:dobro-zarazlyve-26-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Добро заразливе (26 квітня)','uk',4302,5785,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5786,200,sodo.jpg\" style=\"margin: 10px; float: right;\" />Коли закінчилася активна фаза Майдану, багато хто в ейфорії переконував, що українське суспільство сутнісно змінилося. Проте події останніх місцеві показують, що це не зовсім так. Українське суспільство не змінилося: воно дуже кволо і дуже повільно розпочало зміни. Але до реальних змін так&nbsp; і не прийшло – держава і надалі залишається нездатною захистити своїх громадян, олігархія продовжує верховодити в українській політиці, довкола розкрадання, корупція й хабарництво.<o:p></o:p></p>\n','2014-04-26 22:41:56','http://oranta.org/index.php?option=com_content&view=article&id=2078:ukrajinsky-sodom&catid=37:spilnota-cat&Itemid=49','P',NULL,'Український содом','uk',4310,5793,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5825,200,article171.jpg\" style=\"margin: 10px; float: left;\" />Один альпініст зважився підкорити вершину, яка вважалася однією із найважчих. Бажаючи присвоїти всю славу собі, він вирішив зробити це сам.<o:p></o:p></p>\n<p>Але вершина не піддавалася просто так. Стемніло. Зорі і місяць у ту ніч сховалися за хмари. Було темно, хоч око стрель. Але альпініст не спинився.</p>\n<p>Й от на одному небезпечному виступі він посковзнувся і зірвався вниз. І напевне би загинув, якби не застрахувався, як досвідчений верхолаз.</p>\n','2014-05-06 22:10:26','http://oranta.org/index.php?option=com_content&view=article&id=2112:dovira-7-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Довіра (7 травня)','uk',4344,5827,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5838,200,80-1.jpg\" style=\"margin: 10px; float: left;\" />Одного разу четверо ченців зачинилися в келії гірського храму склавши обітницю мовчати сім днів. До них мав доступ тільки хлопець служка, що приносив усе потрібне. Коли настала ніч у келії почав згасати світильник. Здавалося, що він от-от згасне. Один із ченців, той що сидів до вогню найближче, не на жарт стривожився і вигукнув:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Хлопчику, поправ швидше гніт.<o:p></o:p></p>\n','2014-05-12 00:16:16','http://oranta.org/index.php?option=com_content&view=article&id=2124:obitnycja-movchannja-12-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Обітниця мовчання (12 травня)','uk',4356,5839,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5848,200,9AC3D2.jpg\" style=\"margin: 10px; float: right;\" />Голосує той, хто переконаний, що його голос є важливим і це його справжній вибір. Стосовно тих, які не йдуть на голосування, то тут потрібно розуміти причину такої позиції. Якщо йдеться про позицію на кшталт: \"Мені не хочеться\" або ж \"Мені все одно\", – тоді можна говорити навіть про гріховність такого вчинку.</p>\n','2014-05-14 23:33:08','http://oranta.org/index.php?option=com_content&view=article&id=2138:2014-05-14-20-35-52&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Євроінтеграція і Церква «Якщо хтось не йде на вибори, бо йому не хочеться, то це вже гріховність…»','uk',4366,5849,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5867,200,4.JPG\" style=\"margin: 10px; float: right;\" />Цілий рік наші діти готувалися до першого причастя. Кожної неділі відвідували катехизації, кожної неділі стверджувалися у вірі на Богослужіннях. І ось – їхнє перше причастя. Наша церква дозволяє приймати святу тайну Євхаристія ще з малку, але це перше Причастя є цілком усвідомленим і цілком утвердженим, тому вона відіграє велику роль в духовному розвитку дитини.</p>\n','2014-05-22 22:06:21','http://oranta.org/index.php?option=com_content&view=article&id=2153:pershe-prychastja-jak-symvol-stverdzhennja-u-bozi&catid=25:parafija-cat&Itemid=27','P','','Перше причастя, як символ ствердження у Бозі','uk',4386,5869,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5877,200,screen-4.jpg\" style=\"margin: 10px; float: left;\" />Якось два вершники вирішили виїхати на високу гору. Перший хотів довести, що Бог тільки й уміє вимагати від людей молитов і ніяк не бажає полегшити їхній тягар. А другий бажав випробувати свою віру. І коли надвечір добралися вони до вершини, з темряви пролунав голос:</p>\n','2014-05-25 00:17:25','http://oranta.org/index.php?option=com_content&view=article&id=2160:dijamanty-pokory-25-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Діяманти покори (25 травня)','uk',4395,5878,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5879,200,dreams.jpg\" style=\"margin: 10px; float: left;\" />Одного вечора маленький Джимі, лежачи на ліжечку, пильно дивився на місяць, що був уповні.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ти робиш? – запитала мама, заходячи до кімнати.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дивлюся на місяць, - відповів хлопчик.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Уже пізно. Пора спати, - ніжно мовила мама.</p>\n','2014-05-26 08:26:47','http://oranta.org/index.php?option=com_content&view=article&id=2162:kudy-vedut-mriji-26-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Куди ведуть мрії (26 травня)','uk',4397,5880,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5881,200,001014.jpg\" style=\"margin: 10px; float: left;\" />Геолог вирушив в експедицію високо в гори. Спершу йому було важко дихати і працювати і до того ж його сильно втомлювало безсоння. Колеги заспокоювали його:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Звикнеш до висоти – згодом буде легше.</p>\n','2014-05-28 23:50:37','http://oranta.org/index.php?option=com_content&view=article&id=2166:znaty-terpinnja-29-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','Знайти терпіння (29 травня)','uk',4399,5882,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5881,200,thumb.jpg\" style=\"margin: 10px; float: left;\" />Весела молода компанія вирішила покататися на човні. Молодь попростувала до рибалки , який за плату запропонував їм свого човна. Човен був великий, просторий і подобався молоді. Коли всі зі сміхом і жартами сіли в нього й почали відчалювати, на берег вийшов дідок. Помітивши їх, старий зупинився і закричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вилазьте хутчіш! Човен ваш несправний! Він протікає: біля керма на дні невелика дірка, через яку сочиться вода. Хоча дірка й замазана, але вода скоро вимиє її, і ви будете у великій небезпеці.</p>\n','2014-05-29 23:08:41','http://oranta.org/index.php?option=com_content&view=article&id=2167:nebezpeka-30-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Небезпека (30 травня)','uk',4410,5893,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5894,200,majdan_rozaniec.jpg\" style=\"margin: 10px; float: right;\" />В УГКЦ налагоджені братерські відносини з Українською Православною Церквою у єдності з Московським Патріархатом. Уся спільна діяльність обох Церков та заяви, які вони видали в останні кілька місяців, у період Майдану, зроблені разом. Навіть більше, сьогодні черговим головуючим у Всеукраїнській раді Церков і релігійних організацій є Предстоятель Української Православної Церкви Митрополит Володимир (Сабодан).<strong><o:p></o:p></strong></p>\n','2014-05-30 21:23:11','http://oranta.org/index.php?option=com_content&view=article&id=2178:madan-ne-buv-antyrosiskym&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Майдан не був антиросійським','uk',4412,5895,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5895,200,maxresdefault.jpg\" style=\"margin: 10px; float: right;\" />Російська Федерація зараз переживає не найкращі свої часи і вже у недалекому майбутньому може перестати існувати як єдина держави, розпавшись на окремі республіки.</p>\n<p>Таку думку висловив член Римського клубу, один із засновників Всесвітнього економічного форуму в Давосі Богдан Гаврилишин у коментарі кореспондентові Укрінформу.</p>\n','2014-05-30 23:40:55','http://oranta.org/index.php?option=com_content&view=article&id=2179:moskovija-rozpadetsja-cherez-10-rokiv&catid=37:spilnota-cat&Itemid=49','P',NULL,'Московія розпадеться через 10 років','uk',4414,5897,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5898,200,2.jpg\" style=\"margin: 10px; float: right;\" /></p>\n<p>Протягом трьох днів з 29 по 31 травня 2014 року в Брюховичах, що неподалік Львова, проходив науково-практичний семінар «Царський бенкет». Мета заходу: надати учасникам якомога ґрунтовніші знання та практичні навички&nbsp; в організації і проведенні літніх християнських таборів для дітей. Організатором семінару були: Катехитично – педагогічний інститут Українського Католицького Університету, директором якого є сестра Луїза Цюпа, та Патріарша Катехитична комісія УГКЦ.</p>\n<p>29 травня о четвертій годині дня відбулася реєстрація учасників семінару. Опісля усі розмістилися у відпочинковому комплексі. Варто зауважити, що семінар охопив багато регіонів України. У цьому заході взяла участь молодь з Києва, Львова, Тернополя, Івано Франківська, Луцька, Червонограда. То ж чи не кожна єпархія УГКЦ представила своїх представників на семінарі. Загалом у науково - практичному семінарі «Царський бенкет» взяло участь більше сорока людей.<strong></strong></p>\n','2014-06-01 23:15:45','http://oranta.org/index.php?option=com_content&view=article&id=2182:2014-06-01-20-20-01&catid=25:parafija-cat&Itemid=27','P',NULL,'«Царський бенкет» - науково-практичний семінар з організації та проведення літнього християнського табору','uk',4417,5900,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5912,200,Pope_Francis_rides_through_St_Pe.jpg\" style=\"margin: 10px; float: right;\" />Церква не є заскорузлою, а – вільною. У проповіді Святіший Отець вказав на три групи осіб, які претендують називатись християнами: борці за однорідність, ті, що захищають власну думку та борці за власну користь. Для тих осіб Церква не є їхнім домом, вони лише її немов «винаймають». Багато кажуть, що вони є в Церкві, але вони у ній лише однією ногою, а другою ще не увійшли. Таким чином, вони забезпечують собі можливість бути одночасно у двох місцях.<o:p></o:p></p>\n','2014-06-05 15:40:13','http://oranta.org/index.php?option=com_content&view=article&id=2197:cerkva-ne-je-zaskoruzloju-a-vilnoju&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Церква не є заскорузлою, а – вільною','uk',4432,5915,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5912,200,im578x383-!.jpg\" style=\"margin: 10px; float: right;\" />Єпископ Української Греко-Католицької Церкви, президент Українського Греко-Католицького університету Борис Гудзяк у програмі «Вечір з Миколою Княжицьким» розповів про значення України для Європи, покликання нового президента та про Україну майбутнього</p>\n<p><strong>Розгублена Європа</strong></p>\n<p>«Ясно повинно бути для нас всіх, що, коли останнє півріччя Україна не сходила зі шпальт міжнародних медіа, це, в першій мірі, змусило європейців усвідомлювати що Україна - є. От голландці звернені були до моря завжди, вони більше знали про Південну Африку чи Бразилію.</p>\n','2014-06-06 10:35:47','http://oranta.org/index.php?option=com_content&view=article&id=2196:chorno-bila-propovid-vladyky-gudzjaka&catid=37:spilnota-cat&Itemid=49','P',NULL,'Чорно-біла проповідь владики Гудзяка','uk',4431,5914,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5927,200,rty.jpg\" style=\"margin: 10px; float: left;\" />Будь-яка етнічна культура має ключове поняття. У давніх українців воно асоціювалося з порядком, ладом (семантика «ладу», до речі, широка: це і порядок, і злагода, і влада, і устрій), яким, за народними віруваннями, керують божества: Ладо із Ладою та Доля. Саме вони приводять у порядок строкате й багатошарове суспільство, спонукуючи всіх підкорятися главі,- молодших — старшим, дітей — батькам і т. д. Причому в основі ієрархічного підкорення була не стільки сила, скільки шана: старшого, а тим більше главу, поважали, і передусім тому, що ототожнювали його з божеством або такою особою, що має якості божества.</p>\n','2014-06-12 00:39:47','http://oranta.org/index.php?option=com_content&view=article&id=2207:starosvitski-etyketni-normy-povedinky&catid=37:spilnota-cat&Itemid=49','P',NULL,'Старосвітські етикетні норми поведінки','uk',4445,5928,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5934,200,petra-i-pavla.jpg\" style=\"margin: 10px; float: left;\" />Два ченці, живучи разом в одній келії, ніколи ні про що не сперечалися. Й от якось один каже до іншого:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Посварімося хоча б один раз, як усі інші.</p>\n<p>Співрозмовець відповів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не знаю, як почати.</p>\n','2014-06-14 20:15:32','http://oranta.org/index.php?option=com_content&view=article&id=2214:chernechy-spir-15-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чернечий спір (15 червня)','uk',4452,5935,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5939,200,prayer01.jpg\" style=\"margin: 10px; float: left;\" />Є давня історія про чоловіка, якого якось уночі відвідав ангел і розповів йому про великі діла, що чекають на нього: у нього буде нагода здобути величезне багатство, заслужити гідне становище в суспільстві й одружитися з прекрасною жінкою.</p>\n<p>Усе життя чоловік чекав обіцяних чудес, але нічого не сталося, і врешті-решт він помер у самотності й злиднях. Дійшовши до воріт раю, він побачив ангела, який відвідав його багато років тому, і промовив до нього:</p>\n','2014-06-17 02:20:20','http://oranta.org/index.php?option=com_content&view=article&id=2217:jak-strah-shkodyt-mozhlyvostjam-17-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як страх шкодить можливостям (17 червня)','uk',4457,5940,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5945,200,4664.jpg\" style=\"margin: 10px; float: left;\" />Одного разу в монастирі один чернець скривдив іншого. Настоятель попросив ображеного ченця пробачити кривднику.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Цього я не можу зробити, - відповів чернець. – Це він скривдив мене і мусить бути покараний.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У цей дуже важливий момент&nbsp; брат-настоятель підніс руку до неба і почав молитися.</p>\n','2014-06-18 21:16:06','http://oranta.org/index.php?option=com_content&view=article&id=2223:isus-ne-potriben-19-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ісус не потрібен (19 червня)','uk',4464,5947,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5953,200,Vsix_sv_Zem_Ukr-620x250.jpg\" style=\"margin: 10px; float: right;\" />Почитання святих почалося ще з часів перших християн. Приклад мучеників у перших віках дав поштовх до їх почитання. Смерть за Христову віру свідчила про віддану любов до Ісуса Христа та його церкви. Сам Ісус казав: «Це моя заповідь, щоб ви любили один одного, як я вас полюбив! Ніхто неспроможний любити більше, ніж тоді, коли він за своїх друзів своє життя віддає» (Йо 15;12,13).</p>\n','2014-06-22 07:25:04','http://oranta.org/index.php?option=com_content&view=article&id=2230:vsih-svjatyh-zemli-ukrajinskoji&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Всіх святих землі Української','uk',4472,5955,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5957,200,11.jpg\" style=\"margin: 10px; float: left;\" />22 червня 2014 р. в церкві св. Миколая Чудотворця на Аскольдовій могилі відбулася традиційна виставка-конкурс квіткових композицій «Квіти для папи». Така ініціатива настоятеля храму о.Ігоря (Онишкевича) була щиро підтримана вірними на спомин про відвідини святішого отця Івана Павла ІІ України та нашого храму у 2001 р.</p>\n<p>У конкурсі взяло участь 9 квіткових композицій. Богословський, символічний та мистецький&nbsp; рівень представлених композицій оцінювало журі, до складу якого увійшли о.Михайло (Мельник), знана сучасна українська художниця п.&nbsp;Марія Томен та мистецтвознавець Катерина Чаплик.</p>\n','2014-06-23 14:38:41','http://oranta.org/index.php?option=com_content&view=article&id=2233:2014-06-23-11-41-35&catid=25:parafija-cat&Itemid=27','P','','«Квіти для Папи»','uk',4475,5958,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5960,200,woprosy1.jpg\" style=\"margin: 10px; float: left;\" />Юнака запросили у гості. Всі сіли за стіл. Хлопець полюбляв підливку, яка стояла далеко від нього. Він часто поглядав на неї, не зважуючись, однак, поставити її ближче. Навпроти юнака сиділа донька господині, і поряд з нею, власне, стояла підливка. Гадаючи, що молодик поглядає на її доньку, господиня тихенько запитала його:</p>\n','2014-06-24 22:27:15','http://oranta.org/index.php?option=com_content&view=article&id=2237:nepryjemna-istorija-25-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Неприємна історія (25 червня)','uk',4479,5962,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5963,200,7187.jpg\" style=\"margin: 10px; float: left;\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І навіть якби ти дізналася, що я маю іншу, то не стала би ревнувати?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ревнувати? З якого побиту?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А якби я думав про якусь жінку з мого минулого, будучи поряд з тобою? Ти не стала би хвилюватися про те, що я можу піти до неї?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні.</p>\n','2014-06-25 21:11:48','http://oranta.org/index.php?option=com_content&view=article&id=2239:revnoshchi-i-porivnjannja-26-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ревнощі і порівняння (26 червня)','uk',4481,5964,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5971,200,mark9.jpg\" style=\"margin: 10px; float: right;\" />Про смерть Івана Плюща Олександр Зінченко, історик та журналіст, написав у своєму Фейсбуці так: «Пішла епоха. З ВАЮ (Віктором Ющенком. — Авт.) з минулого року намовляли Івана Степановича записати довге інтерв’ю. Не схотів, узяв усе з собою».</p>\n<p>Можливо, Плющ тому відмовлявся від інтерв’ю, що вже тяжко хворів. Він пішов iз життя після тривалого онкологічного захворювання. А можливо, у Івана Степановича були й інші резони. Він залишив політику в 2012-му — відмовившись від балотування до парламенту. А до того був майже незмінним власником депутатського мандата: Плюща обирали до Ради I, II, III, IV та VI скликань. Утомився Іван Степанович чи розчарувався? Чи, може, вже не мав достатньо фізичних та емоційних резервів, аби вкотре братися до виборчої кампанії? Цього ми вже ніколи не дізнаємось. Це, як правильно зазначено, Плющ «узяв із собою».</p>\n','2014-06-28 11:04:42','http://oranta.org/index.php?option=com_content&view=article&id=2247:vichna-pamjat-ivanu-pljushchu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Вічна пам\'ять Івану Плющу','uk',4490,5973,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5977,200,strah.jpg\" style=\"margin: 10px; float: left;\" />Один провідник зустрівся з чоловіком, який стверджував, що грішна людина у ньому померла. Заінтригований проповідник запросив цього чоловіка до себе на обід. Вислухавши роздуми гостя від початку до кінця, він підніс склянку і виплеснув воду просто в лице своєму співрозмовникові. Природно, той жахливо обурився і досить гостро висловив своє невдоволення. Провідник, же відповів:</p>\n','2014-07-01 23:41:05','http://oranta.org/index.php?option=com_content&view=article&id=2255:grishna-ljudyna-2-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Грішна людина (2 липня)','uk',4497,5980,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5987,200,998777.jpg\" style=\"margin: 10px; float: left;\" />Рибалка перевозив на човні одну людину. Той підганяв рибалку.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мерщій, спізнююся на роботу.</p>\n<p>Аж ось він побачив, що на одному веслі написано: «Молися», а на іншому «Трудися».</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А це навіщо, - спитав він?</p>\n','2014-07-04 23:07:20','http://oranta.org/index.php?option=com_content&view=article&id=2262:molysja-i-trudysja-5-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молися і трудися (5 липня)','uk',4505,5988,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5995,200,huzar_velykden14.jpg\" style=\"margin: 10px; float: right;\" />Упродовж останніх майже 20 років до мене зверталися різні люди: матері, жінки чи інші родичі підсудних, утримуваних в ізоляторах чи в’язнів у тюрмах із проханням заступитися перед судейською владою за позбавлених волі. Зверталися до мене з проханням заступитися за їхніх рідних.</p>\n<p>У цих просьбах є логіка, бо кожна людина неповторна. Але, з іншого боку, вони беруть до уваги тільки когось одного, не дивлячись, що всі люди мають однакові права на справедливість.</p>\n','2014-07-08 23:29:02','http://oranta.org/index.php?option=com_content&view=article&id=2270:a-suddi-hto&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'А судді хто?','uk',4513,5996,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,5999,200,37836235180artfilerujpg.jpg\" style=\"margin: 10px; float: left;\" />У прохолодному веселому лісі, серед кушів і трав, тік струмок. Чарівний, сміхотливий, жвавий. Він усіх любив і все подобалося йому. Цілими днями бавився він сонячними промінчиками, бабками і жучками. На одному із чарівніших вигинів струмка лежав камінь, дуже поважний і статечний камінь. Вельми досвідчений..</p>\n','2014-07-09 22:21:56','http://oranta.org/index.php?option=com_content&view=article&id=2272:strumok-10-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Струмок (10 липня)','uk',4517,6000,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6011,200,10487.jpg\" style=\"margin: 10px; float: right;\" />«Агресивна війна за своєю природою аморальна. У той трагічний момент, коли вибухає війна, керівники держави, яка зазнала нападу, мають право і зобов’язані захищати країну, застосовуючи навіть силу зброї» (КСДЦ 500) – однозначна норма соціальної доктрини Католицької Церкви. І якщо до тепер могли бути певні моральні сумніви щодо реальності московистької агресії на Україну, то в ніч на празник свв. Первоверховних Апостолів Петра і Павла обстріли українських військових з території РФ і подальше входження військ РФ на територію України, відхилили усякий сумнів у реальності агресії московитської терористичної держави проти України.</p>\n','2014-07-16 23:19:30','http://oranta.org/index.php?option=com_content&view=article&id=2285:nash-obovjazok-boronyty&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Наш обов\'язок - боронити','uk',4531,6014,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6014,200,1337375744_trovant-3.jpg\" style=\"margin: 10px; float: left;\" />Одного разу учень пішов до вчителя. Вони взялися бесідувати про образу. Учитель вийшов і вказавши на камінь сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Образ цей камінь і і вдар його.</p>\n<p>Учень послухався. Тоді учитель запитав його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи цей камінь відповів тобі, або чинив якийсь опір.</p>\n','2014-07-18 23:47:00','http://oranta.org/index.php?option=com_content&view=article&id=2288:jak-obrazyty-kamin-19-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як образити камінь (19 липня)','uk',4538,6021,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6031,200,1251046073__AZZ2396-nester-olga-r4.jpg\" style=\"margin: 10px; float: left;\" />Коли дивишся на образ святої рівноапостольної княгині Ольги, чия пам\'ять вшановується24 липня, то бачиш перед собою не тільки мудрого державного правителя, а бачиш бабусю, яка виховала свого видатного онука — майбутнього Хрестителя Русі святого рівноапостольного князя Володимира.</p>\n<p>Можливо, саме з такої точки зору і треба дивитися на святу. Адже як християнка вона продемонструвала себе не в тому, як керувала державою, позаяк вона не вводила християнство як державну релігію, а в тому, як вона виховала свого онука. Те виховання принесло плоди — онук княгині Ольга урешті докорінно змінив історію Київської Русі, започаткувавши християнську державність, і від чийого Хрещення веде історію наша Церква.</p>\n','2014-07-24 05:16:53','http://oranta.org/index.php?option=com_content&view=article&id=2303:try-vihy-ta-try-mriji-z-zhyttja-svjatoji-rivnoapostolnoji-knjagyni-olgy-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Три віхи та три мрії з життя святої рівноапостольної княгині Ольги ','uk',4552,6035,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6036,200,b9f00ca80de9f8c25592e11f1e8b8584.jpg\" style=\"margin: 10px; float: left;\" />Спитав якось наставник трьох учнів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому б’ється віконне скло, коли в нього кидають камінь?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що камінь важкий, - відповів один.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що скло крихке, - відповів інший.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що рука, яка кидає камінь, була сильна, а кидок точним, - відповів третій.</p>\n','2014-07-25 12:26:32','http://oranta.org/index.php?option=com_content&view=article&id=2305:rozbyte-sklo-25-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Розбите скло (25 липня)','uk',4554,6037,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6040,200,12071.jpg\" style=\"margin: 10px; float: right;\" />В історії кожної європейської держави є свої основоположники, об’єднувачі і просвітителі. Для найближчих наших сусідів поляків — це князь Мєчислав (Мєшко). Для французів і німців — імператор Карл Великий. Для українців — князь Володимир Великий…</p>\n<p><strong>Через підступ — до влади</strong></p>\n<p>Він був позашлюбним сином князя Святослава і слов’янки Малуші, ключниці. Доти в руській княжій династії не траплялося слов’янських полукровок. Але у світі вікінгів це не вважалося чимось незвичним. Тесть Ярослава Мудрого, Олаф Скетконунг, мав за дружину доньку вождя поморян-ободритів, та й сам по матері був слов’янином — сином сестри польського князя Болеслава Хороброго Святослави (Сігріт Гордої).</p>\n','2014-07-25 17:23:26','http://oranta.org/index.php?option=com_content&view=article&id=2310:volodymyr-velyky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Володимир Великий','uk',4558,6041,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6045,200,images.jpg\" style=\"margin: 10px; float: left;\" />Дуже давно один король збудував великий палац. То був палац з мільйонами дзеркал. Усі, усі стіни, підлоги і стелі були покриті дзеркалами.</p>\n<p>Одного разу у палац забіг собака. Озирнувшись він побачив безліч собак навколо себе. Собаки ули всюди. Бувши вельми розумним собакою він вискалив зуби, щоб про всяк випадок захиститися від тієї юрми собак, що його оточила. Усі пси вискалили зуби й собі. Він загарчав і ці загрозливо відповіли.</p>\n','2014-07-26 23:01:21','http://oranta.org/index.php?option=com_content&view=article&id=2316:dzerkalo-27-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дзеркало (27 липня)','uk',4566,6049,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6051,200,537581.jpg\" style=\"margin: 10px; float: right;\" />Дипломна робота нашої парафіянки, учасниці та переможця фестивалю-конкурсу «Аскольдів глас» Христини Шонь<strong> «Феномен гумору та сміху в культурно-релігійному контексті: теорія та практика» </strong>була написана ще кілька років тому і<strong> </strong>головним чином ставить питання про сміх як феномен. Тут мова йде, не про сміх як усмішку чи психологічний стан-реакцію, але радше сміх як предмет аналізу та роздумів – сміх у дещо іншому контексті. Ця тема є пізнавальною і глибокою за своїм змістом та значенням. Гадаємо, нашій читацькій аудиторії буде цікаво відкрити для себе новий світ «сміху», його сили, ваги. Робота Христини Шонь змістовна і ґрунтовна, тому подаватимемо її частинами.</p>\n','2014-08-01 10:23:53','http://oranta.org/index.php?option=com_content&view=article&id=2325:2014-07-28-07-48-21&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'«Феномен гумору та сміху в культурно-релігійному контексті» - вступ','uk',4582,6065,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6052,200,luxfon.jpg\" style=\"margin: 10px; float: left;\" />На березі моря бавився хлопчик він підіймав і кидав камінці в море. Час від часу перш ніж кинути камінці у пінисті хвилі, він пильно їх розглядав, деякі відкладав у торбинку. Як згодом з’ясувалося він робив це щоб показати їх свої матусі. Удома він витягував камінчик за камінчиком і захоплено показував свої знахідки. Наприкінці він витягнув останній і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це мій улюблений камінь, він мені сподобався найбільше. Такий зелений і гладенький.</p>\n','2014-08-02 23:20:18','http://oranta.org/index.php?option=com_content&view=article&id=2343:nakrashchy-kaminchyk-3-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найкращий камінчик (3 серпня)','uk',4585,6068,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6052,200,shanty.jpg\" style=\"margin: 10px; float: left;\" />Один християнин потрапив до Раю. Ангел веде його до його нової оселі. Християнин побачив чудовий замок і каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мабуть – це мій дім.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, - &nbsp;відповідає Ангел, - твій дім далі.</p>\n','2014-08-05 23:29:56','http://oranta.org/index.php?option=com_content&view=article&id=2346:dim-u-raju-6-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дім у раю (6 серпня)','uk',4591,6074,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6053,200,Greg.jpg\" style=\"margin: 10px; float: right;\" />На черзі, мабуть, найцікавіший розділ, адже мова далі – про християнський вимір сміху: як і чому сміються, а може, якраз і не сміються християни. Розглядати будемо роль «сміху» та «гумору» у християнській культурі у період її «перед-історії» (час Старого Завіту) та періоді становлення і формування (від народження Христа і аж до часів Середньовіччя). Цікаво, що смисл та функціональне призначення визначених понять змінювались та трансформувались. Отож, вашій увазі – прослідкувати зміни у канонах сміху , котрі далі буде окреслено. Приємного вам прочитання та мудрих висновків!</p>\n','2014-08-09 11:15:54','http://oranta.org/index.php?option=com_content&view=article&id=2333:2014-07-28-08-24-12&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'«Феномен гумору та сміху в культурно-релігійному контексті» - частина VІІІ','uk',4597,6080,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,2801880763_bd18d75cf4.jpg\" style=\"margin: 10px; float: left;\" />Одного разу, верблюд пішов до намету. і засунув туди свого носа. Але оскільки ніхто його не гнав, він пропхав туди пів свого тулуба. Трохи згодом, верблюд зайшов у намет весь. Повернувшись, господар промовив до верблюда:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для двох мешканців цей намет замалий.</p>\n<p>Верблюд відповів:</p>\n','2014-08-31 03:02:52','http://oranta.org/index.php?option=com_content&view=article&id=2363:odyn-iz-dvoh-31-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','Один із двох (31 серпня)','uk',4609,6092,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,301892.jpg\" style=\"margin: 10px; float: left;\" />Одного разу, влітку, троє селян вийшли на високе урвище. Внизу зеленіли кущі терену і вздовж піщаного берегу текла ріка. Один із селян гукнув:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дивіться, у кущах вовк сидить.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для вовка надто жовтий, заперечив інший. Здається це лисиця.</p>\n<p>Третій селянин придивився і сказав:</p>\n','2014-08-30 02:58:01','http://oranta.org/index.php?option=com_content&view=article&id=2362:vovk-lysycja-i-zajec-30-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Вовк, лисиця і заєць (30 серпня)','uk',4625,6108,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6090,200,d8996d5-01.jpg\" style=\"margin: 10px; float: left;\" />Маленьке жабеня народилося на дні колодязя. То був маленький круглий колодязь, який стоїть у кожному селі. І це жабеня було таке раде, воно гралося на дні і думало, що має всю воду світу.</p>\n<p>Але одного разу, трішки вирісши, воно вирішило піти в мандри. Побачивши над головою світло, жабеня зважилося вилізти з колодязя. І озирнувшись довкола побачило ставок. Воно не йняло віри своїм очам. Ставок був у тисячу разів більшим від того місця де воно жило. Жабеня набралося відваги ще раз і прийшло до велетенського озера. Воно завмерло перед ним не знаючи що й думати.</p>\n','2014-08-27 07:34:48','http://oranta.org/index.php?option=com_content&view=article&id=2356:zhabenja-v-kolodjazi-27-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Жабеня в колодязі (27 серпня)','uk',4619,6102,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6110,200,1644.jpg\" style=\"margin: 10px; float: left;\" />Одного разу чоловік посадив троянду, яку дбайливо доглядав і поливав. Оглядаючи її він побачив пуп’янок, який мав скоро розпуститися. Також помітив на стеблі шпичаки і подумав: «як з рослини з безліччю колючих шпичаків може вирости гарна квітка». Засмучений цією думкою він перестав поливати троянду і квітки загинула не встигнувши розквітнути.</p>\n','2014-09-04 07:08:41','http://oranta.org/index.php?option=com_content&view=article&id=2387:prytcha-pro-trojandu-4-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про троянду (4 вересня)','uk',4636,6119,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6110,200,21720.jpeg\" style=\"margin: 10px; float: left;\" />Одного разу два пастухи сперечалися про державні справи. Один з них безтямно осуджував свого володаря а інший не до ладу захищав його правління. І захрипнувши від крику та побившись не змогли вони довести один одному нічого.</p>\n<p>Спересердя вирішили пастухи піти дорогою поки не знайдуть людину яка могла б вирішити їхній спір.</p>\n','2014-09-02 07:01:19','http://oranta.org/index.php?option=com_content&view=article&id=2385:superechka-dvoh-pastuhiv-2-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Суперечка двох пастухів (2 вересня)','uk',4629,6112,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6114,200,ArticleImages_57478_ato.jpg\" style=\"margin: 10px; float: left;\" />На початку минулого тижня Україна святкувала День Незалежності, але одночасно розпочалась уже повністю відверта агресія регулярних військ Московії в Україну. Одночасно з цим того самого 24 серпня 2014 р.Б. московський патріарх Кіріл з правдиво сатанинським цинізмом&nbsp;<a href=\"http://catholicnews.org.ua/patriarh-kiril-zvinuvativ-usih-hto-ne-nalezhit-do-yogo-patriarhatu-v-zlobi\">звинуватив</a> усіх в Україні, хто не належить до московського патріархату, в злобі й ненависті до цієї конфесії. Що більше, цей діяч «русскаґо міра»&nbsp;<a href=\"http://catholicnews.org.ua/patriarh-kiril-zaklikav-prezidenta-ukrayini-nadati-pravo-teroristam-viznachati-dolyu-ukrayini\">закликав</a> Президента України надати можливість російським терористам визначати подальшу долю України.</p>\n','2014-09-02 18:58:22','http://oranta.org/index.php?option=com_content&view=article&id=2389:ukrajina-vojuje-ljubovju&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Україна воює любов\'ю','uk',4632,6115,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6122,200,images.jpg\" style=\"margin: 10px; float: left;\" />Одного чоловіка звинуватили в тому, що він вкрав вівцю. Його привели до судді разом з обвинувачем. Суддя повелів привести вівцю, а потім наказав кожному – обвинувачу і обвинуваченому покликати вівцю.</p>\n<p>Чоловік, що звинувачував довго кликав вівцю, але та навіть не повернула голови на його поклик.</p>\n<p>Потім покликав вівцю обвинувачений, він покликав її тим самим голосом як він кликав завжди. І вівця відразу піднесла голову, замекала і побігла до господаря.</p>\n','2014-09-05 22:25:15','http://oranta.org/index.php?option=com_content&view=article&id=2395:vkradena-vivcja-6-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Вкрадена вівця (6 вересня)','uk',4640,6123,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,1410024099_1.jpg\" style=\"margin: 10px; float: right;\" />Словосполучення “політична теологія” звучить як оксюморон. Чи можна ставити поруч теологію та політику? Чи має метафізичне дотик до політичного? Чи не є це два полюси, які нескінченно віддалені один від одного? Теологія - це дисципліна&nbsp; парадоксальна (додам принагідно - одна з найдавніших та засадничих дисциплін класичної європейської освіти). Один з її парадоксів полягає в тому, що, незважаючи на свою метафізичність, вона раціоналізується та навіть демонструє схильність до політичного. В історії християнської теології було чимало доктрин, які зазнавали впливу різноманітних політичних доцільностей.</p>\n','2014-09-09 13:56:31','http://oranta.org/index.php?option=com_content&view=article&id=2404:zasady-politychnoji-teologiji-v-ukrajinskomu-konteksti&catid=37:spilnota-cat&Itemid=49','P',NULL,'Засади політичної теології в українському контексті','uk',4649,6132,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,E62A32.jpg\" style=\"margin: 10px; float: left;\" />4 вересня представники Ініціативної групи «Першого грудня» Євген Сверстюк, В’ячеслав Брюховецький, Мирослав Маринович та Мирослав Попович, на запрошення моніторингової місії ОБСЄ зустрілися з представниками місії на чолі з її керівником, Послом Ертугрулом Апаканом.</p>\n<p>Зустріч була присвячена обговоренню суспільно-політичної ситуації в Україні, насамперед — способам подолання негативного впливу на суспільство воєнних дій на Сході України та російської воєнної агресії проти України загалом.</p>\n','2014-09-09 15:36:40','http://oranta.org/index.php?option=com_content&view=article&id=2405:2014-09-09-09-25-33&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Допомагаючи Україні зупинити російську агресію, світ допомагає насамперед собі — група «Першого грудня»','uk',4650,6133,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6126,200,molitva-bogu.jpg\" style=\"margin: 10px; float: left;\" />Один чоловік мав дві вишні перед вікном. Одна була зла, інша – добра. Хай коли він виходив з дому, вони кликали його і про щось просили. Зла вишня щоразу просила різне: то обкопай мене; то напій мене; то заступи мене від сонця. А добра вишня повсякчас повторювала одне прохання: - «мій господарю, допоможи мені вродити щедрий врожай».</p>\n','2014-09-08 01:37:12','http://oranta.org/index.php?option=com_content&view=article&id=2401:dvi-vyshni-8-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дві вишні (8 вересня)','uk',4646,6129,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6140,200,1_0_824627.jpg\" style=\"margin: 10px; float: left;\" />Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа. На основі цієї думки Папа Франциск побудував свою ранкову проповідь. Проповідник також наголосив на тому, що життя християнина не є життям егоїстичним, зацикленим виключно на своїй власній особі та особистих проблемах, але є даром для інших. Лише живучи своє життя як дар, зможемо любити наших ворогів, до чого закликає нас Господь Ісус.</p>\n','2014-09-12 04:57:54','http://oranta.org/index.php?option=com_content&view=article&id=2411:lyshe-majuchy-myloserdne-serce-ljudyna-mozhe-naspravdi-nasliduvaty-isusa-hrysta&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Лише маючи милосердне серце, людина може насправді наслідувати Ісуса Христа','uk',4658,6141,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6143,200,68277_900.jpg\" style=\"margin: 10px; float: right;\" />У неділю, 14 вересня, Українська Греко-Католицька Церква, долучаючись до міжнародної екуменічно-екологічної ініціативи Константинопольського Патріархату, відзначає День Бога Творця як особливе свято, яке покликане, через прославу нашого Творця і подяку за отримані від нього дари та доброчинства, спричинитися до утвердження нашої віри та життя згідно з його заповідями (за Божою подобою) у різних вимірах: особистому, сімейному, суспільному, а зокрема, у ставленні до Його створіння – природи.</p>\n','2014-09-13 07:11:30','http://oranta.org/index.php?option=com_content&view=article&id=2416:zvernennja-bjuro-ugkc-z-pytan-ekologiji-do-dnja-boga-tvorcja-2014-r-b&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'День Бога Творця','uk',4663,6146,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6153,200,origina.JPG\" style=\"margin: 10px; float: left;\" />Отаман вів своє військо у вилазку довгою кам’янистою дорогою. Воїни, що втрачали сили на важкому шляху і боялися ворогів почали досадувати і нарікати, але отаман суворо змушував іти вперед без відпочинку і затримок.<o:p></o:p></p>\n<p>Уранці вони зненацька напали на ворога і перемогли. І після битви запитав отаман у воїнів:<o:p></o:p></p>\n<p><!--[if !supportLists]--></p>\n','2014-09-15 23:34:22','http://oranta.org/index.php?option=com_content&view=article&id=2425:zabuta-peremoga-16-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Забута перемога (16 вересня)','uk',4673,6156,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6170,200,x_c7b88854.jpg\" style=\"margin: 10px; float: left;\" />Жив на світі чоловік, він прославився своїм спокоєм і любов’ю до всього на світі. Будь-яка справа, до якої він брався виходила у нього добре. Всі того чоловіка поважали, приходили до нього за порадою. Одного разу до нього прийшов сусід, який йому у всьому заздрив. Він сказав погордливо:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я в житті усе маю! Живу в достатку. Але тебе в місті поважають більше ніж мене. До кого я подібний , як гадаєш?<o:p></o:p></p>\n','2014-09-22 12:16:47','http://oranta.org/index.php?option=com_content&view=article&id=2440:mudrec-i-zazdrisnyk-22-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мудрець і заздрісник (22 вересня)','uk',4688,6171,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6174,200,6546.jpg\" style=\"margin: 10px; float: left;\" />Одного разу прийшов юнак до старця і попросив дозволу вчитися в нього.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо це тобі? – спитав той.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу стати сильним і непереможним.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тоді стань ним! Будь добрий зі всіма, ввічливий і поштивий. Доброта і ввічливість викличе в тебе повагу до інших. Твій дух стане чистим і добрим, а значить сильним. Якщо ти навчишся запобігати конфліктам, то станеш непереможним.</p>\n','2014-09-24 06:45:28','http://oranta.org/index.php?option=com_content&view=article&id=2444:syla-i-neperemozhnist-24-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Сила і непереможність (24 вересня)','uk',4692,6175,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6180,200,Vozdvy.jpg\" style=\"margin: 10px; float: right;\" />Римські імператори-язичники намагалися повністю знищити в людстві спогади про священні місця, де постраждав за людей і воскрес Господь наш Ісус Христос. Імператор Адріан наказав засипати землею Голгофу і Гроб Господній і на штучному пагорбі поставити капище язичницької богині Венери і статую Юпітера. На це місце збиралися язичники й робили ідольські жертвоприносини.</p>\n','2014-09-26 20:40:02','http://oranta.org/index.php?option=com_content&view=article&id=2451:2014-09-26-17-46-52&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Величаємо Тебе Життєдавче Христе і шануємо Хрест Твій Святий, ним же нас спас від неволі ворожої»','uk',4699,6182,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6182,200,safe_image.jpg\" style=\"margin: 10px; float: left;\" />Ми славимо Хрест, і вся Церква наповнюється блиском слави. Ми славимо Хрест, і обличчя всієї вселенної освітлюється сяйвом радості. Ми славимо Хрест, і розсівається морок, і розливається світло. Ми славимо Хрест, і з Розп’ятим підносимося, щоб, залишивши внизу землю разом з гріхом, здобувати небо й небесне. Хрест воздвигається, і гамірні клики демонів замовкають.</p>\n','2014-09-27 11:15:40','http://oranta.org/index.php?option=com_content&view=article&id=2452:slovo-na-vseslavnne-vozdvyzhennja-hresta-svjatytelja-andreja-arhyjepyskopa-krytskogo&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Слово на Всеславнне Воздвиження Хреста святителя Андрея, архиєпископа Критського','uk',4700,6183,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,2.png\" style=\"margin: 10px; float: left;\" />Придбавши коробку печива жінка вирішила почитати журнал і дістала його з торбинки. Вона взяла одне печиво і побачила, що якийсь чоловік також пригостився з її коробки.</p>\n<p>Жінка палахнула гнівом, знову взяла печиво, зиркнула на чоловіка і побачила, що він усміхається. За хвилину він взяв ще одне печиво, вона знову спалахнула гнівом.</p>\n','2014-09-27 22:03:39','http://oranta.org/index.php?option=com_content&view=article&id=2454:korobka-pechyva-28-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Коробка печива (28 вересня)','uk',4702,6185,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6183,200,x_8fc6aa76.jpg\" style=\"margin: 10px; float: right;\" />Ситуація, яка склалася і продовжує розвиватися в Україні, набирає щораз загрозливіших обрисів. Уже зараз можемо спостерігати перші ознаки фундаментального розколу поміж більшістю представників влади і більшістю загалу суспільства. Усе частіше люди беруть у свої руки встановлення справедливості.</p>\n<p>На жаль, оговтавшись після майданного шоку, політикум і чиновництво вкотре взялися за своє. Корупція, договорняки, хабарництво, здирництво, – знову процвітають. І знову «небожителі» українського владно-політичного Олімпу не звертають уваги на суспільство, над яким вони возвишуються.</p>\n','2014-09-27 21:53:13','http://oranta.org/index.php?option=com_content&view=article&id=2453:2014-09-27-18-56-26&catid=37:spilnota-cat&Itemid=49','N','','«SOS!» українському християнству','uk',4703,6186,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,020_siyyach.jpg\" style=\"margin: 10px; float: left;\" /></p>\n<p>Селянин ревно засівав своє поле. До нього підійшов хлопчик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, коли виросту, ніколи не буду сіяти зерна.</p>\n<p>Чоловік поглянув на нього і нічого не відповів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це ж невдячна справа, - говорив далі хлопчик.</p>\n','2014-09-29 04:53:06','http://oranta.org/index.php?option=com_content&view=article&id=2456:shcho-posijesh-29-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Що посієш (29 вересня)','uk',4706,6189,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6188,200,articleimages_57555__.jpg\" style=\"margin: 10px; float: right;\" />Архієпископ Томас Едвард Ґалліксон, апостольський нунцій в Україні надав неупереджену оцінку труднощів, перед якими стоїть Церква в Україні.</p>\n<p>Архієпископ Ґалліксон зазначив, що сьогодні Україна потерпає від «дестабілізації, спровокованої переважно хижацтвами української кримінальної олігархії (які і надалі тривають),&nbsp; посиленої російською агресією супроти її територіальної цілісності та суверенітету».</p>\n','2014-09-29 14:59:50','http://oranta.org/index.php?option=com_content&view=article&id=2457:145937-srjoanna-chornii-pered-ukrajinoju-stojatyme-velychezny-vyklyk-predstavnyk-vatykanu-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Перед Україною стоятиме величезний виклик, – представник Ватикану в Україні','uk',4707,6190,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6195,200,prirod.jpg\" style=\"margin: 10px; float: left;\" />Якось посперечалися два товариші. Один вірив в Бога, а інший ні. Весь вечір безвірник намагався намагався довести, що Бога немає і що все на землі виникло з вибуху.</p>\n<p>Вранці наступного дня вірний приніс безвірнику картину. І безвірник, побачивши її, вигукнув від захвату:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Який чудовий пейзаж! Хто автор?! Я зачарований його роботою.</p>\n','2014-10-01 00:42:04','http://oranta.org/index.php?option=com_content&view=article&id=2462:prytcha-pro-velyky-vybuh-i-prekrasnu-kartynu-1-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','Притча про великий вибух і прекрасну картину (1 жовтня)','uk',4714,6197,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6200,200,suha.jpg\" style=\"margin: 10px; float: left;\" />Учень спитав учителя:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти такий мудрий. Ти завжди в доброму настрої, ніколи не сердишся, поможи і мені стати таким.</p>\n<p>Учитель згодився і попросив учня принести картоплю і прозорий пакет.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти на когось розсердишся і задаєш кривду, то візьми цю картоплину: з одного боку напиши своє ім’я, а з іншого ім’я людини з якою в тебе конфлікт і поклади цю картоплину в пакет.</p>\n','2014-10-04 01:27:17','http://oranta.org/index.php?option=com_content&view=article&id=2468:kryvda-i-mishok-kartopli-4-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кривда і мішок картоплі (4 жовтня)','uk',4719,6202,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6204,200,praying_man_at_altar.jpg\" style=\"margin: 10px; float: left;\" />Одного разу до священика прийшов молодик і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, ви маєте рацію. Мені б також треба шукати того страждальця Голгофи і стати дитиною Божою. Коли небудь я покаюся.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коли небудь? – запитав священик</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну, так, згодом.</p>\n','2014-10-06 01:55:26','http://oranta.org/index.php?option=com_content&view=article&id=2475:istynne-zhyttja-6-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Істинне життя (6 жовтня)','uk',4725,6208,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6245,200,ArticleIma.jpg\" style=\"margin: 10px; float: right;\" />Католицька Церква за Папи Франциска відчула не лише перспективи змін, а й повернення до старих істин та навіть вимірів раннього християнства. Про це йшлося під час конференції «Україна-Ватикан», яку вже сьомий раз проводить Українська асоціація релігієзнавців спільно з Інститутом філософії ім. Г. Сковороди.</p>\n<p>Цьогорічна наукова подія відбулася під знаком драматичних процесів у сучасній Україні: пошуку відповідей, які мала б та готова дати на них Церква, а також впливу самих подій на життя земного Тіла Христового.</p>\n','2014-11-06 21:03:20','http://oranta.org/index.php?option=com_content&view=article&id=2510:cerkva-i-suspilstvo-v-novyh-umovah&catid=27:gk-v-ukrajini-cat&Itemid=57','N','','Церква і суспільство в нових умовах','uk',4765,6248,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6245,200,faceboo.jpg\" style=\"margin: 10px; float: left;\" />Сучасну людину можна з усією впевненістю назвати представником «генерації ґаджетів». Маючи у сумці смартфон, планшет, ноутбук чи інший цифровий носій, ми водночас є володарями інформаційної матриці, варто лише зробити кілька дотиків на дисплеї чи перебрати пальцями на клавіатурі, і ти вже в іншому, віртуальному світі Фейсбука, Твіттера, Інстаграму, Ютюба чи Вікі-навігаторів. Зважаючи на запити суспільства,</p>\n','2014-11-06 20:50:58','http://oranta.org/index.php?option=com_content&view=article&id=2509:bog-zhyve-u-fesbuci&catid=37:spilnota-cat&Itemid=49','P',NULL,'Бог живе у Фейсбуці?','uk',4763,6246,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6260,200,250px-Josafata.jpg\" style=\"margin: 10px; float: left;\" />Життя сестри Йосафати Гордашевської не було сповнене особливими подіями, але вона вирізнялася надзвичайним ставленням до Бога, завжди залишаючи Йому широке місце у своєму серці й конкретному ревному служінні потребам свого народу.</p>\n<p>А святість сестри Йосафати полягала в тому, що вона виконувала свою щоденну працю з любов\'ю і посвятою та перебувала в постійній гармонії з Духом Ісуса, який пробуджував у ній жертовну любов. Її життя і служіння пов’язані з нашим краєм і з нашим Кристинополем – Червоноградом. Однак її діла належать усьому українському народові.</p>\n','2014-11-19 21:15:36','http://oranta.org/index.php?option=com_content&view=article&id=2521:blazhenna-sestra-sluzhebnycja-osafata-gordashevska-obruchnycja-hrystova&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Блаженна сестра – служебниця Йосафата Гордашевська – обручниця Христова','uk',4778,6261,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,1244099821_9610-31.jpg\" style=\"margin: 10px; float: left;\" />Наприкінці 20-х років (минулого століття)польська, німецька, французька, англійська преса чимало уваги приділяють подіям в Україні, подаючи часом достовірну, а часом і фантастичну інформацію насамперед про спротив селянства пограбуванню, розкуркуленню й колективізації, а також про політичні настрої та внутрішньопартійну боротьбу. Цікаво, що більшовицька публіцистика того часу, на відміну від пізніших часів, досить широко, хоч і вибірково, звертається до цих ворожих публікацій, дозволяючи собі багато що цитувати.</p>\n','2014-11-21 21:51:32','http://oranta.org/index.php?option=com_content&view=article&id=2523:geopolitychny-aspekt-golodomoru-chastyna-ii&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Геополітичний аспект Голодомору (частина ІІ)','uk',4783,6266,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,383524387.jpg\" style=\"margin: 10px; float: right;\" />Є такий трюїзм: зовнішня політика держави – це продовження її внутрішньої політики. Але з не меншою підставою можна сказати: внутрішня політика – це продовження зовнішньої. Інакше кажучи: зовнішні і внутрішні обставини еволюції суспільства взаємопов’язані і взаємозумовлені.</p>\n<p>Творці більшовицької революції в Росії мислили її як початок світової пролетарської революції. Такою вона уявлялася і частині європейського робітничого руху та ліворадикальної інтелігенції. Це були діяльні і досить потужні союзники більшовизму, без яких він не зміг би утвердити себе в революційній Росії.</p>\n','2014-11-20 21:09:16','http://oranta.org/index.php?option=com_content&view=article&id=2522:geopolitychny-aspekt-golodomoru-chastyna-i&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Геополітичний аспект Голодомору (частина І)','uk',4779,6262,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6261,200,den.jpg\" style=\"margin: 10px; float: right;\" />Не випадково, що головною небезпекою для соціалістичного будівництва оголошується, поряд із куркульством, націонал-ухильництво в самій партії. Це було важливою складовою боротьби з багатобарвною внутрішньопартійною опозицією.</p>\n<p>У згаданій книжці Ф. Тарана є окремий розділ «Опозиція в КП(б)У й підготовка до інтервенції». Починаючи з кінця 20-х років, «націонал-ухильників» дедалі брутальніше шельмують як ідеологів куркульства, пособників імперіалізму, резерв інтервентів, українських фашистів і т. д., – весь час розширюючи коло публічно ошельмованих і поступово доходячи до головних – О. Шумського й М. Скрипника.</p>\n','2014-11-22 21:58:42','http://oranta.org/index.php?option=com_content&view=article&id=2524:geopolitychny-aspekt-golodomoru-chastyna-iii&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Геополітичний аспект Голодомору (частина ІІІ)','uk',4785,6268,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6267,200,1039626.jpg\" style=\"margin: 10px; float: left;\" />Протягом 20-ти років функціонує Катехитично-недільна школа при храмі св. Миколая Чудотвоця на Аскольдовій Могилі. Вже з самого початку, незважаючи на труднощі, пов’язані з приміщенням, діти різної вікової категорії приходили для того, (як вони це пояснюють) щоб почути про Бога. Праця, яка ведеться для дітей та молоді в школі, є дуже важливою у вихованні не тільки духовно-моральному, патріотичному аспектах, але й цілісно-особистісному.</p>\n<p>Сьогодні катехитична праця здійснюється не тільки Сестрами Служебницями, як раніше, але й з участю 16-ти катехитів, вишколених за сприянням пароха о. Ігоря Онишкевича: Світлана Заяць, Ореста Цебрівська, Аня Онишкевич, Оксана Ясніцька, Оксана Сенчук, Ірина Макар, Руслана Дмитровська, Катерина Чаплик, Надія Кучерява, Ірина Василик, Любов Михайлюк, Марія та Василилина Томишинець, Анастасія Заверуха і Сестри Служебниці с. Філомена Чворсюк і с. Христосфора Буштин.</p>\n','2014-11-25 20:15:51','http://oranta.org/index.php?option=com_content&view=article&id=2529:katehytychno-nedilna-shkola&catid=25:parafija-cat&Itemid=27','P',NULL,'Катехитично-недільна школа','uk',4793,6276,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6267,200,im578x3.jpg\" style=\"margin: 10px; float: left;\" />Як дослідниця Голодомору, я певна, що сталінський геноцид українців 1933 року добряче підготував ґрунт для путінської гібридної війни на Донбасі. Тобто один кривавий диктатор передав у спадок іншому імперську справу нищення України.</p>\n<p>Чому голодна смерть є найстрашнішою?</p>\n<p>Хтось скаже: тому, що вона є найдовшою в часі, і буде правий лише частково. Так, якщо зовсім нема ніякої їжі, але є вода, людина буде згасати десь упродовж місяця, переживаючи при цьому в останній тиждень свого життя страшенні фізичні страждання.</p>\n','2014-11-24 11:54:10','http://oranta.org/index.php?option=com_content&view=article&id=2527:koreni-tragediji-na-donbasi-prorostajut-u-1933-rik&catid=37:spilnota-cat&Itemid=49','P',NULL,'Корені трагедії на Донбасі проростають у 1933 рік','uk',4786,6269,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6285,200,sogod.jpg\" style=\"margin: 10px; float: left;\" />Катерина не тільки добре знала твори мудреців старовини, але вона вивчала також твори знаменитих лікарів, таких, наприклад, як Аськлепій, Гіппократ і Галін; крім того вона навчилася ораторському і діалектичному мистецтву і знала багато мов і говір, так що всі дивувалися її вченості і знанням.</p>\n<p>Багато багатих і знатних людей сваталися до неї і з цією метою приходили до її&nbsp;матери, таємної християнки, що приховувала свою віру внаслідок жорстокого гоніння, спорудженого у той час на віруючих Максиміном.</p>\n','2014-12-06 21:24:23','http://oranta.org/index.php?option=com_content&view=article&id=2542:svjata-velykomuchenyce-kateryno-moly-boga-za-nas&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Свята Великомученице Катерино, моли Бога за нас!','uk',4803,6286,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6291,200,papa.jpg\" style=\"margin: 10px; float: left;\" />«Вже не раби, але – брати», – ці слова з Послання святого Павла до Филимона стали темою Послання Папи Франциска з нагоди 48-го Всесвітнього дня миру, який Католицька Церква відзначає у перший день нового року. Представлення цього послання відбулося у середу, 10 грудня 2014 р., у Ватиканському прес-центрі.</p>\n','2014-12-12 13:18:35','http://oranta.org/index.php?option=com_content&view=article&id=2548:poslannja-papy-francyska-z-nagody-48-go-vsesvitnogo-dnja-myru-pidsumok&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Послання Папи Франциска з нагоди 48-го Всесвітнього дня миру (підсумок)','uk',4809,6292,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6293,200,IMG_0928[1].JPG\" style=\"margin: 10px; float: left;\" />Вже так скоро найулюбленіше свято дітей Святого Миколая. Та не тільки діти, але і дорослі особливо його шанують, захоплюються, і передають один одному цей радісний дух торжества.</p>\n<p>Церква святого Миколая Чудотворця на Аскольдовій Могилі завжди в особливий спосіб святкує цей величавий празник. Цього року парафіяльна служба милосердя «Карітас-Печерськ» приділила особливу увагу тим дітям, чиї батьки загинули в ході антитерористичної операції на сході нашої держави.</p>\n','2014-12-13 22:18:55','http://oranta.org/index.php?option=com_content&view=article&id=2550:masternja-svjatogo-mykolaja&catid=25:parafija-cat&Itemid=27','P','','Майстерня Cвятого Миколая','uk',4811,6294,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6297,200,_DSC3653.JPG\" style=\"margin: 10px; float: left;\" />«12 грудня 2014 року відбувся візит Блаженнішого Любомира Гузара в Санаторій «Джерело», де проживає близько 260 вимушено переселених осіб зі Сходу України та Криму (з них 120 дітей). Зустріч була організована Київським Карітасом», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n','2014-12-15 21:01:51','http://oranta.org/index.php?option=com_content&view=article&id=2554:blazhennishy-ljubomyr-vidvidav-260-pereselenciv&catid=29:propovidi-cat&Itemid=34','P',NULL,'Блаженніший Любомир відвідав 260 переселенців','uk',4816,6299,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6306,200,54646.jpg\" style=\"margin: 10px; float: left;\" />Майже все, що оточує нас на Святий Вечір має своє християнське тлумачення. Навіть чисто прибрана хата – це символ готовності, очікування другого приходу Месії у славі. Але вся символіка Святого Вечора, як приготування до Різдва, залишається порожньою, якщо людина не приготована до найбільшого Таїнства – єднання з Месією у Пресвятій Євхаристії.</p>\n','2015-01-05 17:59:07','http://oranta.org/index.php?option=com_content&view=article&id=2561:v-ochikuvanni-rizdva&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'В очікуванні Різдва','uk',4841,6324,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6310,200,pic_cbd.jpg\" style=\"margin: 10px; float: right;\" />1. На початку нового року, який ми приймаємо як благодать і дар Божий людству, я хочу звернутися до кожної людини, а також до кожного народу і країни світу, до глав держав і урядів і до лідерів різних релігій з моїми гарячими побажаннями миру, супроводжуючи їх молитвами про те, щоб припинилися війни, конфлікти і страждання, причиною яких є як сама людина, так і епідемії, що вже давно відомі й знову з’являються, та руйнівний вплив стихійних лих. Я особливо молюся про те, щоб, відповідаючи на наше спільне покликання співпрацювати з Богом і з усіма людьми доброї волі заради зміцнення злагоди і миру в світі, ми вміли протистояти спокусі вести себе негідно нашої людяності.<o:p></o:p></p>\n','2015-01-02 00:02:39','http://oranta.org/index.php?option=com_content&view=article&id=2566:vzhe-ne-raby-ale-braty&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Вже не раби, але брати','uk',4837,6320,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6312,200,IMG_0885.jpg\" style=\"margin: 10px; float: right;\" />20 грудня 2014 р. Б. в приміщенні НПУ ім. М.&nbsp;Драгоманова відбувся <strong>ХІІІ&nbsp;Фестиваль-конкурс духовної пісні «Аскольдів глас»</strong>.</p>\n<p>Конкурс традиційно проходив у рамках святкування зимового празника Святого Миколая, який для парафії храму на Аскольдовій могилі – засновника цього мистецького заходу, - є храмовим. За сім років існування фестиваль став знаною мистецькою подією в житті нашої столиці; щороку розширюється географія учасників конкурсу, адже в ньому беруть участь мистецькі колективи та окремі виконавці з цілої України, для багатьох з яких це чудова можливість дебютувати на серйозній сцені та спробувати свої сили у змаганні з іншими виконавцями.</p>\n','2014-12-27 16:58:40','http://oranta.org/index.php?option=com_content&view=article&id=2564:2014-12-29-15-03-21&catid=25:parafija-cat&Itemid=27','P','','Результати ХІІІ ФЕСТИВАЛЮ-КОНКУРСУ ДУХОВНОЇ ПІСНІ «АСКОЛЬДІВ ГЛАС»','uk',4831,6314,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6330,200,AP2128861_Articolo.jpg\" style=\"margin: 10px; float: left;\" />Над «мудрістю серця» у світлі слів зі старозавітної Книги Йова: «Я був сліпому оком, кульгавому я був ногами» запропонував замислитися Папа Франциск у своєму посланні з нагоди Всесвітнього дня хворого, який відзначатиметься 11 лютого 2015 року. У вівторок, 30 грудня 2014 р., цей документ оприлюднено у Ватикані.</p>\n','2015-01-10 20:39:41','http://oranta.org/index.php?option=com_content&view=article&id=2574:papa-francysk-nepravda-shcho-dejaki-zhyttja-ne-gidni-shchob-buty-prozhytymy&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Папа Франциск: Неправда, що деякі життя не гідні, щоб бути прожитими','uk',4850,6333,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6344,200,10906254.jpg\" style=\"margin: 10px; float: left;\" />При парафії св.&nbsp;Миколая Чудотворця на Аскольдовій могилі (м.&nbsp;Київ) було організовано молодіжний вертеп, що має давню й цікаву історію: більше півсторіччя його грають у Донецькій області в селі Званівка нащадки переселених з Дрогобицької області на Донбас у 1951 році. Традиція жваво побутує серед переселенців і в наш час.</p>\n<p>У 2015-му році званівський вертеп вістив про народження Дитятка Ісуса Сина Божого у численних оселях.</p>\n','2015-01-17 21:43:34','http://oranta.org/index.php?option=com_content&view=article&id=2583:vidrodzhujuchy-rizdvjanu-tradyciju&catid=25:parafija-cat&Itemid=27','P',NULL,'Відроджуючи різдвяну традицію','uk',4862,6345,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6361,200,Pokhoron_1918.jpg\" style=\"margin: 10px; float: right;\" />“На вас, завзятці-юнаки, що возлюбили Україну, кладу я кращії гадки, мою сподіванку єдину”... І їх нема...</p>\n<p>Кого ми поховали? Хто зважить той жаль? Зберіть всі хмари з усього світу&nbsp;— і не складете тої нерозважної туги, що оповила серце... Жалю немає ваги. Найпалкіші діти України, найдорожчі жертви на залізний вівтар волі. Історія знає багато жертв, але такої сторінки вона ще не розгортала перед людськими очима. В часи хрестових походів ішли одбивати Єрусалим лави охоплених ентузіазмом дітей, вони і гинули лавами, з повною вірою в Царство небесне і в вічне життя.</p>\n','2015-01-28 23:30:26','http://oranta.org/index.php?option=com_content&view=article&id=2598:pamjati-junakiv-gerojiv-zamordovanyh-pid-krutamy&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Пам\'яті юнаків-героїв, замордованих під Крутами','uk',4879,6362,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6368,200,DSC_0090.JPG\" style=\"margin: 10px; float: left;\" />31 січня 2015 року Жіночий вокальний ансамбль «Аніма», що при церкві св.&nbsp;Миколая Чудотворця на Аскольдовій Могилі під керівництвом Катерини Чаплик, здійснив місійну подорож до виправних уставнов, розташованих на Києвщині. Колектив у складі Наталії Грищенко, Наталії Мальцевої, Роксолани Шпіци, Анастасії Друзюк, Катерини Чаплик, Мирослави Кирилюк, Олени Сакалюк відвідав Березанську (№ 95) і Бориспільську (№ 119) виправні колонії Управління ДПтС України у Києві та Київській області, де відбувають чоловіки. Групу супроводив капелан о. Костянтин Пантелей, керівник Відділу Курії УГКЦ з душпастирства у пенітенціарній системі України.</p>\n','2015-02-02 19:58:01','http://oranta.org/index.php?option=com_content&view=article&id=2605:2015-02-02-18-02-05&catid=25:parafija-cat&Itemid=27','P','','Ансамбль «Аніма» відвідав в\'язнів на Київщині','uk',4887,6370,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6432,200,1036361.jpg\" style=\"margin: 10px; float: right;\" />У листопаді минулого року в УГКЦ розпочався Рік Митрополита Андрея Шептицького. В історію Українського Народу він вписався не тільки як провідник Церкви у нелегкі часи, а також як його провідник і великий меценат. Його заслуги перед нашим Народом є такі великі, щоб їх усіх годі охопити одним поглядом. Його місію супроти Українського Народу досить часто прирівнюється до тієї, яку довелося сповнювати старозавітному патріарху Мойсею. Через це Митрололита досить часто називають Українським Мойсеєм.</p>\n<p>І саме тепер у дуже непрості часи, які переживає наш Народ, постать Митрополита Андрея є не те що важливою, але дуже потрібною, оскільки він показує нам, як належить усім нам ці часи переживати, щоб зберегти свою гідність та ідентичність.</p>\n','2015-03-04 17:25:29','http://oranta.org/index.php?option=com_content&view=article&id=2656:pamjatnyk-mytropolytu&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Пам’ятник Митрополиту','uk',4950,6433,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6450,200,894000087.jpg\" style=\"margin: 10px; float: right;\" />Сповідь може багато чого навчити священика. На це звернув Папа Франциск в програмі курсу – канонічні, моральні та літургійно-душпастирські аспекти Сповіді, права і обов’язки каяників, питання етики та генетики.</p>\n<p>Єпископ Риму зазначив, що серед Святих Тайн Таїнство Покання «з особливою дієвістю уприсутнює милосердне обличчя Бога». Воно його «конкретизує та безперервно показує». «Ніколи, як сповідники і як каяники, не забуваймо про те, що не існує такого гріха, якого Бог не зміг би простити! Жодного! Лише те, що уникає Божого милосердя, не може бути прощеним, подібно до того, як той, хто уникає сонця, не може бути ні освітлений, ні зігрітий ним».</p>\n','2015-03-12 22:40:06','http://oranta.org/index.php?option=com_content&view=article&id=2666:try-vymogy-do-dobrogo-spovidnyka&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Три вимоги до доброго сповідника','uk',4968,6451,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6464,200,soborovan03.jpg\" style=\"margin: 10px; float: right;\" />Таїнство Соборування – це священнодійство подароване &nbsp;Богом для полегшення страждань саме важкохворих людей. Однак, сучасна практика нашої Церкви допускає до участі один раз у році й&nbsp;людей без явних фізичних проблем, при цьому маючи на увазі їх потребу в уздоровленні не тільки тіла, але й душі. Зазвичай, такий чин відправляється для великої кількості людей у час Великого посту.</p>\n<p>Таїнство Соборування або Маслосвяття – це таїнство Церкви, в якому священик при помазуванні хворому певних частин тіла, закликає на нього благодать Божу, яка зцілює тілесні та душевні хвороби. Назву «Маслосвяття» чи «Єлеопомазання» таїнство отримало від того, що під час нього священнослужителі освячують особливий єлей (масло), яким намащують людину.</p>\n','2015-03-20 15:49:20','http://oranta.org/index.php?option=com_content&view=article&id=2674:pro-soboruvannja-maslosvjattja-chy-jeleopomazannja-jak-do-nogo-pidgotuvatysja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Про Соборування, Маслосвяття чи Єлеопомазання: як до нього підготуватися?','uk',4982,6465,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6471,200,marynovycz_0.jpg\" style=\"margin: 10px; float: right;\" />Хочу сказати кілька сутнісних думок на тему зв’язку між релігією та фундаментальною наукою в наш час. Згадую початок січня 2010 року. На той час міністру освіти і науки України Іванові Вакарчуку, група шанованих українських вчених передала звернення щодо, цитую, «недопустимої клерикалізації освіти в Україні».</p>\n','2015-03-24 12:11:56','http://oranta.org/index.php?option=com_content&view=article&id=2680:dva-kryla-cyvilizaciji&catid=37:spilnota-cat&Itemid=49','P',NULL,'Два крила цивілізації','uk',4989,6472,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6473,200,4.JPG\" style=\"margin: 10px; float: right;\" />Двадцять восьмого березня цього року були &nbsp;проведені реколекії&nbsp; на тему, яка є дуже актуальною: «Педагог сьогодні свідок надії і справедливості».&nbsp; Катехетична наука була зорганізована за допомогою Центру академічного душпастирства&nbsp; КА УГКЦ – голова отець Максим Рябуха, Комісії у справах освіти і виховання УГКЦ, голова якої є Христофора Бурштин &nbsp;та Катехетичною школою на Аскольдовій могилі, координатор – сестра Філомена Чворсюк.</p>\n','2015-03-30 13:25:56','http://oranta.org/index.php?option=com_content&view=article&id=2684:pedagog-svidok-nadiji-i-spravedlyvosti&catid=25:parafija-cat&Itemid=27','P','','Педагог - свідок надії і справедливості','uk',4995,6478,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6483,200,4646.jpg\" style=\"margin: 10px; float: left;\" />Володимир (37 років), м. Дніпродзержинськ. На війну потрапив під час третьої мобілізації, воював під Дебальцевим, в оточенні пробув 45 днів.</p>\n<p>Згадка про військові будні: кожні 2 години чергування, вільний час, сон, і так щодня. Їли зазвичай сухпайок, (який їм привозили волонтери), не було можливості готувати їжу.</p>\n','2015-03-31 22:15:28','http://oranta.org/index.php?option=com_content&view=article&id=2689:shchyrist-v-nash-chas&catid=29:propovidi-cat&Itemid=34','P','','Щирість в наш час','uk',5001,6484,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6508,200,13979.gif\" style=\"margin: 10px; float: right;\" />Віримо ми чи ні, що Христос справді воскрес із мертвих? Певно не дуже. Бо якби ми вірили, то чи святкували би ми Його Воскресіння так, як святкуємо? Якби ми справді вірили, що Христос встав із мертвих і перебуває з нами, чи могли б ми у день Його перемоги над світом так неправдиво і лицемірно святкувати цю радість? А чому неправдиво і лицемірно, спитає хтось. А тому, що наше&nbsp;<strong>правдиве святкування</strong>зводиться у кращому випадку до більше чи менше уважної і щирої молитви у храмі в неділю ранком, а в гіршому випадку взагалі не має місця у торжестві Пасхи. Чому?</p>\n','2015-04-11 19:35:20','http://oranta.org/index.php?option=com_content&view=article&id=2713:chy-spravdi-virymo-shcho-hrystos-voskres&catid=37:spilnota-cat&Itemid=49','P',NULL,'Чи справді віримо, що Христос воскрес?','uk',5031,6514,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6508,200,32417181.jpg\" style=\"margin: 10px; float: right;\" />Пасха – найбільше християнське свято, в якому світло торжествуємо воскресення з мертвих на третій день Господа нашого Ісуса Христа. На це свято на наших землях склалася традиція освячувати паски та іншу поживу, яку приносимо до храму (на подвір’я храму) для благословення, щоб потім з радістю споживати освячену їжу у великий і святий день Пасхи Господньої. Це гарний і добрий звичай, який віками існує в українському народі, однак ми з нього сотворили певного&nbsp;<strong>ідола</strong>. Що мається на увазі?</p>\n','2015-04-11 15:15:57','http://oranta.org/index.php?option=com_content&view=article&id=2711:svjachennja-pasok-navazhlyvishe-u-svjatkuvanni-velykodnja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Свячення пасок – найважливіше у святкуванні Великодня?','uk',5029,6512,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6520,200,464654.JPG\" style=\"margin: 10px; float: left;\" /></p>\n<p>Зі Святими упокій, Христе, душуі слуги Твого, де немає болю, ні печалі, ні зітхання, але життя безконечне. Сотвори йому вічную пам’ять, вічную пам’ять, вічную пам’ять, зі Святими упокій, Христе, вічная пам’ять.</p>\n<p>О. ВАСИЛЬ ПОТОЧНЯК ДУХОВНИЙ ТЕСТАМЕНТ (ЗАПОВІТ)</p>\n<p>Писаний в часі цілонічного чування в Єрусалимі 14-15 вересня 2012 року Божого у храмі Гробу ГНІХ</p>\n','2015-04-16 11:46:08','http://oranta.org/index.php?option=com_content&view=article&id=2722:o-vasyl-potochnjak-duhovny-testament-zapovit&catid=28:tema-nomera-cat&Itemid=2','P','','О. ВАСИЛЬ ПОТОЧНЯК ДУХОВНИЙ ТЕСТАМЕНТ (ЗАПОВІТ)','uk',5038,6521,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6526,200,huz.jpg\" style=\"margin: 10px; float: right;\" />Останніми десятиліттями Квітну неділю почали вважати святом молоді.</p>\n<p>У церковних осередках того дня збирається багато молодих людей, для яких ієрархи готують відповідні звернення, а сама молодь складає якісь свої програми святкування. Часто –&nbsp; і ті звернення, і роздуми самих учасників – мають за тему: а що далі? Який життєвий шлях вибрати?</p>\n','2015-04-21 10:09:41','http://oranta.org/index.php?option=com_content&view=article&id=2727:jaki-geroji-nam-potribni&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Які герої нам потрібні?','uk',5044,6527,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6535,200,lenyn-bil--12.jpg\" style=\"margin: 10px; float: left;\" />Кажуть, що на території України було споруджено коло п’яти тисяч пам’ятників Леніна. У самому Києві аж вісімнадцять! Певну частину повалено ще на початку 90-х років минулого століття. Ще деякі впродовж останнього року.</p>\n<p>Ці усунення пам’ятників вождя викликали протести. Протестують люди, які мають особливу прив’язаність до Леніна через політичні переконання. Протестують митці, які вважають, що будь-який по-мистецьки виконаний пам’ятник треба зберігати і шанувати. Протестують громадяни, головно старшого віку, для яких Ленін ? це своєрідна релігійна постать.</p>\n','2015-05-05 18:26:16','http://oranta.org/index.php?option=com_content&view=article&id=2736:idoly-padut-pro-pamjatnyky-lenina&catid=37:spilnota-cat&Itemid=49','P',NULL,'Ідоли падуть','uk',5055,6538,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6566,200,DSC01561.JPG\" style=\"margin: 10px; float: left;\" />З 5 квітня по 24 травня на Аскольдовій Могилі щонеділі в сім етапів проходив шаховий турнір «Ми діти Аскольда». Шахові змагання проводяться з метою популяризації шахів, налагодження дружних відносин між шахістами, вдосконалення спортивної майстерності і пізнання християнської історії Руси-України і постаті першого христитеся Аскольда-Миколая царя і мученика за віру христову та визначення переможців та призерів змагань.</p>\n','2015-05-31 22:53:17','http://oranta.org/index.php?option=com_content&view=article&id=2766:2015-05-31-19-55-40&catid=25:parafija-cat&Itemid=27','P','','«Ми діти Аскольда»','uk',5084,6567,0,NULL),
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6568,200,03tGBcGsrDY.jpg\" style=\"margin: 10px; float: left;\" />- Прийди, Духу Святий, любове Отця і Сина, запали наші серця вогнем Своєї любові.</p>\n<p>Людський розум, завдяки власному праву вибирати, яким наділив його Господь, допомагає своєму серцю і душі шукати Бога, у різний час та спосіб. Хтось осягає і пізнає Бога ще у ранньому віці, комусь до цього осягнення варто йти довгим шляхом. Та і в першому, і в другому випадку людський розум пізнає усю красу всього сотвореного Богом світу, показує, що Бог спрямовує все у світі до якоїсь найвищої мети – життя вічного.</p>\n<p>Сьогодні людина може дуже добре знати Святе Письмо, але це знання не наближає її до Бога, якщо вона не відкриває своє серце на поклик Бога.</p>\n','2015-06-01 21:08:23','http://oranta.org/index.php?option=com_content&view=article&id=2767:piznannja-boga-cherez-bozhu-ljubov-u-suchasnosti&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Пізнання Бога через Божу любов у сучасності','uk',5086,6569,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://askoldova-mohyla.org/uk/image,6577,200,huzar_press-konf.jpg\" style=\"margin: 10px; float: left;\" />Журналістика – надзвичайно важливий елемент демократичного ладу, бо допомагає людині розуміти й вирішувати важливі життєві справи правильно, ефективно й послідовно. Тому цього дня хочу сердечно привітати працівників ЗМІ і всіх, хто причетний до журналістики.</p>\n<p>Бажаю вам свідомо, відповідально виконувати свою роботу!</p>\n','2015-06-06 21:13:01','http://oranta.org/index.php?option=com_content&view=article&id=2772:zhurnalistyka-ce-sposib-zhyttja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Журналістика – це спосіб життя','uk',5097,6580,0,NULL),
(1,1,'<p><img src=\"http://media01.radiovaticana.va/imm/1_0_714681.JPG\" height=\"156\" align=\"left\" hspace=\"5\" width=\"202\" />Справедливіша  і більш братерська цивілізація починається від переміни серця, а серце  кожної людини може змінитись, коло до нього доторкнеться Христос, –  наголосив Папа Франциск у своєму слові під час вечірнього молитовного  чування, виголошеному португальською та іспанською мовами.</p>\n<p>Святіший  Отець згадав відому подію з життя святого Францішка з Асижу, коли той,  молячись перед розп’яттям, почув голос Ісуса: «Іди й відбудуй мій дім!»</p>\n','2013-07-29 00:00:58','http://oranta.org/index.php?option=com_content&view=article&id=1382:budte-budivnychymy-prekrasnishoji-cerkvy-ta-krashchogo-svitu-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Будьте будівничими прекраснішої Церкви та кращого світу! ','uk',3471,4954,0,NULL),
(1,1,'<p><img src=\"http://ugcc.org.ua/clear.gif\" border=\"0\" height=\"5\" width=\"1\" /><strong>Коротка історія</strong></p>\n<p align=\"justify\">Святкування цього празника сягає IV ст. В цьому часі св. Єлена, мати цісаря Костянтина Великого, збудувала храм на горі Тавор у честь Господнього Преображення. Від VI ст. цей празник урочисто святкується у Східній Церкві під назвою „Господнього Преображення\". В Західній Сирії у VIII столітті він називався „Празником Тавору\".</p>\n','2011-08-16 20:39:30','http://oranta.org/index.php?option=com_content&view=article&id=381:svjate-preobrazhennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святе Преображення Господа Бога і Спаса нашого Ісуса Христа ','uk',2125,3608,0,NULL),
(1,1,'<p><img src=\"http://uk.radiovaticana.va/global_images/x.gif\" border=\"0\" height=\"3\" width=\"470\" /></p>\n<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_687352.JPG\" height=\"147\" width=\"99\" />Соромитися  своїх гріхів – це чеснота смиренної людини, що приготовляє до прийняття  Божого прощення. Коментуючи  уривок з Першого послання святого Івана, в якому говориться, що «Бог є  світлом і жодної темряви немає в Ньому», Святіший Отець підкреслив, що  ми, натомість, в своєму житті маємо «сутінки», моменти, хвилини, «коли  все, також і у власному сумлінні, є темрявою», але це ще не означає  «ходити в темряві». «Ходити в темряві, – пояснив він, – означає бути  задоволеним самим собою: бути переконаним в тому, що мені не потребно  жодного спасіння». І з такої дороги не є легко звернути назад.</p>\n','2013-04-29 20:05:10','http://oranta.org/index.php?option=com_content&view=article&id=1171:papa-francysk-spovid-ce-ne-tortura-a-proslava-boga-jaky-proshchaje&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Папа Франциск: Сповідь – це не тортура, а прослава Бога, Який прощає','uk',3206,4689,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.askoldova-mohyla.org/uk/image,3319,200,041.jpg\" style=\"float: left; margin: 10px;\" />Молитва, віра, Бог, єднають усіх нас в одне ціле. Ми всі - велика родина парафії церкви святого Миколая. До цієї спільноти належить  також  дружня і доброзичлива родина Остапів.</p>\n<p>Їхній син Микола Остапів співає в церковному хорі, дарує парафіянам чудовий спів. Окрім того він добре вчиться, займається спортом, а особливо полюбляє гру в настільний теніс. Микола приємна і вихована дитина.\n','2009-11-20 09:47:11','http://www.oranta.org/index.php?option=com_content&view=article&id=455:rodyna-ostapiv&catid=25:parafija-cat&Itemid=27','P','','Родина Остапів','uk',1884,3367,0,NULL),
(1,1,'<p><img src=\"http://www.askoldova-mohyla.org/uk/image,3319,200,DSCN2315.JPG\" style=\"float: left; margin: 10px;\" />У  світі є чимало спортсменів, які вірять і знають, що їхнім спортивним досягненням  сприяв саме Бог. <strong>Серед них -  Девід Робінсон, Марк Прайс, Хуан Карлос Валером, Мері Джо Фернандес, Майкл Чанг, Кевін Джонсон та інші. </strong></p>\n<p><strong>Скажімо, Девід Робінсон зауважував</strong>: «Щоразу, виходячи на баскетбольний майданчик, я думаю, як би своїми діями прославити Бога. Я хочу, щоб люди звеличували не мене, а віддавали хвалу Богові, щоб вони зрозуміли, що я нічим не відрізняюся від інших і тільки через милість Божу в мене такий великий зріст. Та це не найголовніше в житті  і не можливість отримувати більше грошей. Коли я прокидаюся в ранці, я ледь не плачу від того, що Бог такий добрий та милосердний до мене. Він так любить мене, що дав мені троє гарних дітей та чудову дружину, Бог дає все найкраще вжитті.»\n','2009-11-04 19:26:23','http://www.oranta.org/index.php?option=com_content&view=article&id=450:sportyvni-talanty-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27','P',NULL,'Спортивні таланти Аскольдової Могили','uk',1861,3344,0,NULL),
(1,1,'<p><img src=\"http://www.irs.in.ua/images/stories/Photo_news_2010/2010.09.22_symposium1.jpg\" align=\"right\" border=\"0\" height=\"96\" hspace=\"6\" vspace=\"2\" width=\"130\" /></p>\n<p align=\"justify\">Ми, учасники Симпозіуму – науковці, медики, представники влади та державних інституцій, релігійних та громадських організацій, засвідчуємо єдність позиції про цінність людського життя з моменту зачаття.<br /><br /></p>\n','2010-09-28 20:56:19','http://oranta.org/index.php?option=com_content&view=article&id=591:rezoljucija-sympoziumu-qmoralno-etychni-aspekty-shtuchnogo-pereryvannja-vagitnostiq&catid=37:spilnota-cat&Itemid=49','P',NULL,'Резолюція Симпозіуму \"Морально-етичні аспекти штучного переривання вагітності\"','uk',2160,3643,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/001.jpg\" style=\"margin-right: 8px; float: left;\" /></p>\n<p>Сьогодні у підтримку «Руху подружніх пар» Блаженніший Любомир у церкві св. Миколая Чудотворця на Аскольдовій Могилі о 17 годині відправив Божественну літургію для подружжів. Служба пройшла у спів служінні священиків: отця Олексія Геник, керівника «Руху подружніх парк УГКЦ», декана, отця Ігоря Онишкевича, отця Богдана Чурила, і отця Олега, секретаря Блаженнішого.</p>\n<p>\n','2009-03-12 22:27:20','http://www.oranta.org/index.php?option=com_content&view=article&id=243:lruh-podruzhnih-parr-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','«Рух подружніх пар» на Аскольдовій Могилі','uk',1232,2206,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/1204476528.jpg\" style=\"float: left;\" /> У цьому році Українська Греко-Католицька Церква (УГКЦ) відзначатиме двадцятиріччя легалізації своєї діяльності на українських землях. Нагадаю, що в 1946 році за вказівкою Сталіна у Львові був проведений так званий «об\'єднавчий» собор, який ухвалив рішення про приєднання УГКЦ до Московського патріархату. Одразу ж було заарештовано й репресовано 10 єпископів, 1400 священників, 800 монахів і монахинь, що належали до цієї Церкви. Відтак одна із традиційних українських християнських церков стала «катакомбною». Свою місійну діяльність УГКЦ здійснювала нелегально, перебуваючи у підпіллі. Лише в час національного відродження, тобто в середині 80-х років минулого сторіччя УКГЦ легалізувалася й впродовж останніх двадцяти років діє в Україні відкрито й публічно.</p>\n<p>Без сумніву, що з нагоди цієї сумної ювілейної дати УГКЦ в Україні й світі проведе чимало заходів, які розкажуть про одну із страшних трагедій, яку пережила Церква і український народ, перебуваючи під владою Москви і окупаційного комуністичного режиму.</p>\n<p>Не стоятиме осторонь цієї дати влада і громадськість. Ще в минулому році Львівська, Тернопільська та Івано-Франківська обласні ради звернулися до Президента України, Верховної Ради з проханням ініціювати на загальнодержавному рівні процес встановлення історичної справедливості щодо УГКЦ та визнати за нею статус репресованої церкви.</p>\n<p>\n','2009-01-17 11:38:33','http://www.oranta.org/index.php?option=com_content&view=article&id=180:patriarhat-dlja-naciji&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Патріархат для нації','uk',965,1939,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/5205ceff73.jpg\" style=\"float: right;\" /> <strong>7 січня 2009 року</strong></p>\n<p><strong> Різдво Господа Бога і Спаса нашого Ісуса Христа</strong></p>\n<ul>\n<li>Коротка історія свята</li>\n<li>Про ікону празника</li>\n<li>Тропар та кондак</li>\n<li>Празничний Апостол</li>\n<li>Євангеліє</li>\n<li>Проповідь</li>\n</ul>\n<p>Коротка історія</p>\n<p align=\"justify\">Протягом трьох перших сторіч християни не мали окремого празника Христового Різдва. У той час був тільки празник Богоявлення, яке святкували 6 січня. Цей празник поєднував у собі і Христове Різдво і Хрещення Ісуса в ріці Йордані. Грецьке слово \"Епіфанія\" чи \"Теофанія\", що значить \"З\'явління\" чи \"Богоявлення\", у перших віках християнства означало не тільки появу Ісуса Христа при його хрещенні, але і його появу на землі, тобто його народження.</p>\n<p align=\"justify\">Про давню злуку цих двох празників і їхнє святкування в один день свідчить тотожний уклад у нашому богослуженні. Схема богослужби цих празників одна й та сама. Обидва мають навечір\'я з постом, Літургію святого Василія Великого з вечірнею у навечір\'я, царські часи, велике повечер\'я зі співом \"З нами Бог\", на Літургії \"Єлици\" і т. п. Різниця в їхньому богослуженні полягає тільки у змісті богослужби.</p>\n<p align=\"justify\">\n','2009-01-05 12:22:57','http://www.oranta.org/index.php?option=com_content&view=article&id=170:rizdvo-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Різдво Господа Бога і Спаса нашого Ісуса Христа','uk',932,1906,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/d1.jpg\" style=\"float: left;\" /> Парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі, разом з друзями з Америки - паном Еріком та його командою. відвідали сьогодні інтернат для неповносправних дітей у селі Потіївка, Житомирської області.</p>\n<p><img src=\"http://www.oranta.org/images/stories/2008/10/d2.jpg\" style=\"float: right;\" /> У часі канікул у інтернаті залишилось 13-ро дітей повних сиріт, яким немає куди і до кого поїхати у ці величні свята. Наш візит для цих дітей став великою подією і святом у цей Богом даний час. Для нас також дуже важливо було зустрітися з цими дітками і подарувати їм свою увагу, спілкування і, звісно, дарунки.</p>\n<p>\n','2009-01-17 16:56:58','http://www.oranta.org/index.php?option=com_content&view=article&id=184:lhto-prymaje-dytynu-v-moje-imja-to-mene-prymajer-jevangelija-vid-mateja&catid=29:propovidi-cat&Itemid=34','P','','«Хто приймає дитину в моє ім\'я, той мене приймає» - (Євангелія від Матея)','uk',971,1945,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/image13711dsc_0018.jpg\" style=\"float: left; margin-right: 8px; border: #000 1px solid;\" />12 березня річниця пам‘яті Слави Стецько.</p>\n<p>Спочила Слава цього ж дня 2003 року. Відспівували її на Аскольдовій Могилі, бо вона себе вважала нашою парафіянкою. Кожен раз коли була в Києві приходила на недільну дитячу службу Божу. сідала на свою лавочку і діти оточували її. Вона дуже любила спілкуватись з дітьми. Особливу увагу Слава Стецько приділяла найменшим, брала їх на руки, навчала, забавляла.</p>\n<p> Саме Слава Стецько допомагала і добивалася відновлення нашої святині - Аскольдової Могили. З благословення Блаженнішого Любомира Гузара 1998 року 22 травня вона перша відкрила двері церкви і промовила слово до громади.</p>\n<p>\n','2009-03-12 13:13:50','http://www.oranta.org/index.php?option=com_content&view=article&id=242:pamjati-slavy-stecko&catid=25:parafija-cat&Itemid=27','P','','Пам‘яті Слави Стецько','uk',1230,2204,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/10/image13711dsc_0018.jpg\" style=\"margin: 10px; float: left; border: #000000 1px solid;\" />Слава Ісусу Христу!</p>\n<p>Дорогі в Христі, парафіяни, читачі нашої газети «Оранта».</p>\n<p>Час Святого Преображення звершився.</p>\n<p>Пам\'ятаймо слова Господа «Це Син мій улюблений, що Його Я вподобав. Його слухайтеся» (Матей 17.5)</p>\n<p>Ці слова почули небагато вірних, які 19 серпня 1991 року прийшли молитися у Іонійський скит, Свято-Троїцького Храму у ботанічному саду, який ми освятили молитвою і вірою , але змушені були покинути через напад і насилля наших братів з Лаври.</p>\n','2009-08-24 23:04:43','http://www.oranta.org/index.php?option=com_content&view=article&id=413:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Слово від Редакції','uk',1746,3229,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/200px-taras_shevchenko.jpg\" style=\"float: right;\" /> Тарас Шевченко - унікальна творча постать не тільки тим, що він, поет, національний пророк, &quot;перший історик&quot;- філософ (П.Куліш), а й тим, що він став духовним батьком відроджуваного українства. Прийшовши в світ на прастарій Звенигородщині, він &quot;соборно&quot; успадкував козацько-гайдамацьку кров (по батькові) та західноукраїнську опришківську (по матері Катерині Бойко).Ще з ранньої творчої пори, побачивши у Вільно гідність польського народу, який зірвався з колін до змагання за волю, він протиставився рабському духу загалу - насамперед першими поемами про козацтво.</p>\n<p>Якщо в ранній творчості Шевченко опоетизував у творах із козацького минулого тяглість боротьби проти національного й релігійного ярма, то в періоді &quot;трьох літ&quot; він генералізував епічно-героїчні традиції свого народу. &quot;Розритою могилою&quot;, &quot;Чигирином...&quot;, першим &quot;Сном&quot; і &quot;Великим льохом&quot; розпочався період &quot;бурі й натиску&quot; Шевченкового &quot;романтичного націоналізму&quot; (І. Франко).</p>\n<p>\n','2009-03-02 11:29:09','http://www.oranta.org/index.php?option=com_content&view=article&id=228:-istorychni-filosofski-akordy-poeziji-tarasa-shevchenka&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Історичні й філософські акорди поезії Тараса Шевченка','uk',1194,2168,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/afisha1.jpg\" style=\"float: right;\" /> Запрошуємо!</p>\n<p>Щорічний проект  мистецького центру «Майстерня пісні»</p>\n<p>По Рождеству</p>\n<p>Образ Свята в голосах, звуках, кольорах</p>\n<p> </p>\n<p> «По Рождеству» збирає нас на своєрідний підсумок, післязвуччя  традиційних зимових свят</p>\n<p>Різдва - Нового року - Василя - Водохреща.</p>\n<p>Цього року дарунок для нас усіх - співпраця трьох :</p>\n<p>проект «Раса» -  давні коляди і щедрівки:    Наталя Половинка, Олена Костюк, Уляна Горбачевська</p>\n<p>зірка сучасної авангардної музики</p>\n<p>Клаус Кугель, Німеччина     - ударні, перкусії</p>\n<p>та креативна група Національного музею у Львові ім. А. Шептицького :</p>\n<p>Остап Лозинський, Олександр Бриндіков - мультимедійний проект «Споглядаючи ікону».</p>\n<p>                                       Рай упразнився - Христос народився!</p>\n<p> \n','2008-12-26 12:31:07','http://www.oranta.org/index.php?option=com_content&view=article&id=163:shchorichny-proekt-mysteckogo-centru-lmasternja-pisnir&catid=37:spilnota-cat&Itemid=49','P',NULL,'Щорічний проект  мистецького центру «Майстерня пісні»','uk',868,1842,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_01.jpg\" style=\"float: left;\" /> На бажання Слуги Божого Папи Івана Павла ІІ, вже від 1982 року площу Святого Петра у Ватикані прикрашає різдвяний вертеп, за розмірами він сягає двоповерхового будинку. Традиція ватиканських вертепів збережена і теперішнім Папою Венедиктом ХVІ. Деякі статуї цьогорічного різдвяного дійства, що прикрашає ватиканську площу, походять із вертепу Святого Вінченцо Паллотті, що зберігається у базиліці Святого Андреа делле Валле і сягає 1842 року. Інші, натомість, були додані до згаданого вертепу продовж пізніших років. Згідно традиції, Різдво Христове супроводжується присутністю води та вогню, які символізують душевну чистоту і відновлення та перемогу світла над темрявою; ці два елементи присутні також і цього року у вертепі на площі Святого Петра.</p>\n<p>\n','2008-12-25 13:44:37','http://www.oranta.org/index.php?option=com_content&view=article&id=159:vidkryttja-rizdvjanogo-vertepu-na-ploshchi-svjatogo-petra&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Відкриття різдвяного вертепу на площі Святого Петра','uk',854,1834,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/foto_07.jpg\" style=\"float: left;\" /> Сьогодні українські колядки та різдвяні літургічні співи лунають також і у різних частинах Риму. Адже за григоріянським календарем свята в Римі відзначають Сестри Служебниці Непорочної Діви Марії, Сестри Василіянки з Генерального Дому, Отці Василіяни, Українські Папські Колегії. Згадані спільноти вчора збиралися за свят-вечірніми столами, а сьогодні у каплицях відправлено різдвяні богослуження. У церкві Христа Царя, що при Генеральному Домі Отців Василіян, у різдвяній Божественній Літургії взяли участь численні миряни, яких доля закинула на чужину. Користаючи з тієї нагоди, що святкові дні в Італії є вихідними, натомість, 7 січня вже розпочинаються робочі будні, багато вірних у ці дні намагаються взяти участь у богослуженнях.</p>\n<p>\n','2008-12-25 13:52:26','http://www.oranta.org/index.php?option=com_content&view=article&id=160:ukrajinska-koljadka-u-rymi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Українська колядка у Римі','uk',855,1835,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/ill20.jpg\" style=\"float: left; margin-right: 8px; border: #000 1px solid;\" /> Як кожен свідомий українець, я тоді ще юнаком - закінчив середню художню школу ім.. Тараса Шевченка, перед поїздкою у «далекі краї» на навчання до Академії мистецтв в Ленінград (Санкт-Петербург)\\, вчинив вірно: я поїхав у Канів на Чернечу гору до Тараса Шевченка. І там, нищечком, у хустину взяв дещицю землі...</p>\n<p>Тоді, юнаком, я ще не уявляв, яку ДОЛЮ мені уготовано життям, щоб там, вже студентом Академії, увійти в Комітет проведення Вечорів Тараса Шевченка при меморіальній мансарді-майстерні Т.Г. Шевченка. І обставини мої були - виключними - це було КОЛО ленінградських Шевченкознавців: П.В. Жур, Марія Комісарова, Микола Браун та інші. І тоді мені прийшла ДУМКА - не спати уніч з 9 на 10 березня - читати «Кобзар», «Літопис життя та творчості Т.Г. Шевченка». І до тих «Тарасових Ночей» спершу одиниці студентів-українців, а потім і мешканців міста над Невою, а в подальшому (отих лихих років 60-70-тих, коли і до пам‘ятника Шевченка, саме 22 травня, не ходи, бо будеш підданий навіть арешту) приїздили і з Києва, і зі Львову, Таллінну. І навіть на одній з тих «Ночей» був присутній праправнук Шафарика (Прага).</p>\n<p>\n','2009-03-09 11:43:36','http://www.oranta.org/index.php?option=com_content&view=article&id=235:ltarasovi-nochir-u-spogadah-volodymyra-kalnenka&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Тарасові Ночі» у спогадах Володимира Кальненка','uk',1215,2189,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/pos1.jpg\" style=\"float: left;\" /> Наша парафіяльна служба милосердя церкви св. Миколая Чудотворця на Аскольдовій Могилі разом з отцем Ігорем, пані Орисею, пані Мартою, пані Катериною, панною Мар‘яною і волонтерами, на кошти доброчинців та жертводавців,  вже котрий рік поспіль адресно допомагають дітям, матері яких перебувають у місцях позбавлення волі. Поза увагою не залишаються і самі матері.</p>\n<p>Започаткована акція «Святий Миколай допомагає забутій дитині» дає свої плоди - матері навертаються до віри Господньої, діти відчувають, що їх ніхто не забув, не покинув, їх люблять.</p>\n<p>Цього року вже було відправлено понад 120 посилок і дітям і матерям. Посилочки для дітей формуються з духовної дитячої літератури у першу чергу, листа-звернення, зворотного конверта, для того щоб дитина могла відписати,олівців, ручок, зошитів, художньої літератури, предметів гігієни, солодощів, одягу. Матерям також відправляють духовну літературу, лист-звернення, зворотній конверт для підтримки зв\'язку, предмети гігієни, солодощі, одяг. Кожна мати, до якої звернулась наша парафіяльна служба, дякує за цю допомогу і просить надалі допомагати її дитині. До церкви приходять листи від кожної матері з якою контактує наша служба.</p>\n<p>\n','2009-02-12 20:32:29','http://www.oranta.org/index.php?option=com_content&view=article&id=210:nemaje-u-sviti-chuzhyh-dite-bo-vony-dity-bozhi&catid=29:propovidi-cat&Itemid=34','P','','Немає у світі чужих дітей, бо вони діти Божі.','uk',1142,2116,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2008/11/shk2.jpg\" style=\"float: right;\" /> Дитячий колектив при парафії св. Миколая на Аскольдовій могилі перейшов під керівництво Тетяни Стефанишин восени 2005 року. Першою спільною роботою була вистава до свята Миколая у 2005 році. Далі творчий репертуар колективу поступово поповнювався музично-поетичними композиціями, присвяченими різним церковним і національним святам.</p>\n<p>У 2006 році спеціально до свята Миколая на прохання керівника була написана вистава «Святий Миколай» , автором якої є О. Дмитрик. Також вперше на цій виставі звучали пісні про Миколая під фонограми, створені на синтезаторі «Yamaha», який парафія подарувала колективу.</p>\n<p>\n','2009-01-30 11:34:20','http://www.oranta.org/index.php?option=com_content&view=article&id=201:molodi-talanty-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27','P','','Молоді таланти Аскольдової Могили','uk',1088,2062,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2009/03/ajnj 031.jpg\" style=\"float: right; margin: 5px;\" />Другий тиждень Великого посту нагадує нам про великі Господні діла, про ті чуда, що творив Ісус Христос. У Святому Євангелії від Марка, глава 2, говориться:</p>\n<p><sup></sup></p>\n<p><sup>1</sup> Коли ж Він по кількох днях прийшов знов до Капернауму, то чутка пішла, що Він удома.</p>\n<p><sup>2</sup> І зібралось багато, аж вони не вміщалися навіть при дверях. А Він їм виголошував слово.</p>\n<p><sup>3</sup> І прийшли ось до Нього, несучи розслабленого, якого несли четверо.</p>\n<p><sup>4</sup> А що через народ до Нього наблизитися не могли, то стелю розкрили, де Він був, і пробравши, звісили ложе, що на ньому лежав розслаблений.</p>\n<p><sup>5</sup> А Ісус, віру їхню побачивши, каже розслабленому: Відпускаються, сину, гріхи тобі!</p>\n<p> </p>\n','2009-03-14 22:32:27','http://www.oranta.org/index.php?option=com_content&view=article&id=244:lgovoryt-do-nogo-isus-ustava-vizmy-lozhe-svoje-ta-hodyr-jevangelije-vid-oana-glava-5&catid=25:parafija-cat&Itemid=27','P','','«Ісус, віру їхню побачивши, каже розслабленому: Відпускаються, сину, гріхи тобі!» - Євангеліє від Марка, глава 2','uk',1239,2213,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2009/04/133-3-2.jpg\" style=\"float: right; margin: 10px; border: #000000 1px solid;\" />Сьогодні 4 річниця дня світлої пам\'яті Івана Павла ІІ. Парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі разом з отцями відправили поминальну панахиду. Папа Іван Павло ІІ для нашої парафії - Велична постать. Його відвідини та страсні моління у нашій церкві, під час візиту в Україну, назавше закарбуються у пам\'яті парафіян, паломників, прочан. Його візит став історією, історією окріплення нашої церкви.</p>\n<p>У останньому листі написаному самим Святішим Отцем при житті у великих терпіннях, які Він мав в одній із римських лікарень Джемеллі, 13 березня 2005 року в П\'яту Неділю Великого Посту (за григоріанським календарем) йшлося: ... «Закликаючи в останніх роках, особливо в Енцикліках millenio ineunte (23 nn) і в Rosarium Virginia Mariae (9 nn), до споглядання (контемпляції) Обличчя Христа, я вказав на Марію як на велику вчительку. В Енцикліці про Євхаристію я зобразив ЇЇ як «Наречену Євхаристії» (n 53). Хто ж більше, як не Марія, може нам допомогти з любов\'ю вшановувати велике Таїнство Євхаристії? Ніхто краще, ніж Вона, не може навчити нас полум\'яної ревності, з якою повинні служитися святі Таїнства і з якою ми повинні перебувати з її Сином, утаєним під євхаристійними образами. Молюсь до Неї, за всіх Вас, доручаю їй в особливіший спосіб найстарших, хворих, тих, які перебувають у труднощах. Прагну під час цього Великодня, в Році Євхаристії, повторити кожному з Вас близькі і заспокійливі слова Ісуса: «Ось Мати твоя» (Ів. 19, 27).</p>\n','2009-04-02 16:34:25','http://www.oranta.org/index.php?option=com_content&view=article&id=266:pamjati-papy-ivana-pavla-ii-ta-mykolaja-cherneckogo&catid=25:parafija-cat&Itemid=27','P',NULL,'Пам‘яті Папи Івана Павла ІІ та Миколая Чернецького','uk',1312,2286,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/2009/04/358 5.jpg\" style=\"float: right; margin: 10px; border: 1px solid #000000;\" />Дорогі парафіяни 27 березня 2004 року Божого, Божим провидінням  наша парафія за Благословенням  світлої пам\'яті владики Михаїла, і Екзарха владики Василя Медвідь встановили Хресну дорогу, Київ - Заздрість - Зарваниця.  За сприянням декана Зарваницького о. Дмитра, стараннями парафіян п. Богдана і Світлани Тернопільських, п. Орести Цембрівської було поставлено 14 стацій Хресної дороги між с. Заздрість - Зарваниця. І в цей день поставлена дорога була посвячена молитвою прочан з Києва, Житомира, Ірпеня, Харків, Херсон, Кам\'янець-Подільськ, Львів, Фастів.  Гаслом дороги є: \" На славу Божу і спасіння України! \".</p>\n<p>\n','2009-04-06 18:16:29','http://www.oranta.org/index.php?option=com_content&view=article&id=270:kyjiv--zazdrist--zarvanycja--kyjiv&catid=25:parafija-cat&Itemid=27','P',NULL,'КИЇВ – ЗАЗДРІСТЬ – ЗАРВАНИЦЯ – КИЇВ','uk',1326,2300,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/dsc_00232.jpg\" style=\"float: right;\" /> Дорогі в Христі брати і сестри, ми представляємо Вам нашу нову програму «Різдвяна допомога дітям».</p>\n<p>Парафіяльна служба милосердя, стараннями своїх волонтерів-доброчинців продовжує допомагати потребуючим дітям і виконувати ті проекти які раніше були засвідчені.</p>\n<p>Особливістю «Різдвяної допомоги дітям» є те, що ми продовжуємо вести проект допомога дітям, матері яких перебувають в ув\'язнені. На запит парафіяльної служби, з департаменту виконання покарань ми отримали листа, де подаються уточненні списки дітей, які залишилися без материнської опіки. З огляду на таку розширену інформацію цього разу волонтери Карітас запропонували нову програму допомоги цим дітям. У листі є вказані точні адреси установ де перебувають матері, тому пані Зіновія запропонувала написати кожній матері лист і отримати від неї підтвердження точної адреси дитини. У листі ми наголошуємо: - «наша християнська громада від вашого імені хоче поздоровити вашу дитину зі святами і передати дарунок, але для цього потрібно щоб ви нам відповіли і вказали точну адресу перебування дитини». Пані Зіновія приготувала 150 таких листів і відіслала їх адресатам. У кожному листі є конверт з вказаною зворотною адресою і вкладений листок для письма. У такий спосіб ми хочемо залучити маму до активності допомоги дитині і самій отримати надію на життя і спасіння.</p>\n<p>\n','2008-12-26 12:40:57','http://www.oranta.org/index.php?option=com_content&view=article&id=162:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Слово від Редакції','uk',867,1841,0,NULL),
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/image1348kalnenko1.jpg\" style=\"float: left;\" /> Аскольдова могила у музиці кольорів</p>\n<p>22 січня о 17 годині відбулася <a href=\"http://depomaranch.at.ua/news/2009-01-18-40\">презентація</a> виставки творів відомого художника Володимира Кальненка, <a href=\"http://askoldova-mohyla.org/uk/item,1348\">котрий торік визначив точне місце, з якого Тарас Шевченко малював Аскольдову могилу.</a></p>\n<p>Володимир Кальненко - відомий скульптор і дослідник життя і творчості Тараса Шевченка. Свого часу він пішки прийшов з Ленінграда до Києва і приніс з собою посмертну маску українського месії. Він є головою парафіяльної художньої ради церкви св. Миколая на Аскольдовій могилі.</p>\n<p>\n','2009-01-22 15:59:48','http://www.oranta.org/index.php?option=com_content&view=article&id=191:vystavka-volodymyra-kalnenka&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Виставка Володимира Кальненка','uk',1009,1983,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img src=\"http://www.oranta.org/images/stories/posylky.jpg\" style=\"margin: 10px; float: left; border: #000000 1px solid;\" />Парафіяльна служба милосердя «Карітас-Печерськ» церкви св. Миколая на Аскольдовій могилі відправила 76 посилок допомоги потребуючим. А саме: дітям, яких залишила мама, що відбуває покарання у тюрмі, потерпілим від повені родинам у с. Воскресенці Коломийського району Івано-франківської області.</p>\n','2009-08-13 21:46:22','http://www.oranta.org/index.php?option=com_content&view=article&id=405:lkaritas-pecherskr-vidpravyv-76-posylok-dopomogy-potrebujuchym&catid=25:parafija-cat&Itemid=27','P','','«Карітас-Печерськ» відправив 76 посилок допомоги потребуючим','uk',1731,3214,0,NULL),
(1,1,'<p><img src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_night_city.jpg.jpg\" style=\"float: left; margin: 10px;\" /> Бюро Києво-Галицького Верховного Архиєпископства УГКЦ із питань екології просить усіх небайдужих долучитися до всесвітньої акції \"Година Землі\". Зміст акції, ініційованої Всесвітнім фондом природи (WWF), полягає у тому, щоб у суботу, 28 березня, о 20.30 на одну годину вимкнути необов\'язкові джерела освітлення та побутові електроприлади. Про це йдеться у спеціальному зверненні д-ра Володимира Шеремети, керівника Бюро Києво-Галицького Верховного Архиєпископства УГКЦ із питань екології <em>(див. у додатку)</em>.</p>\n<p>\"Послання акції полягає в тому, що ніхто не має права бути бездіяльним і очікувати на рішення інших. Кожна людина може і має зробити свій внесок у порятунок планети, починаючи з найменшого - не витрачати марно електроенергії, води, газу, купувати енергозберігаючі прилади для дому та офісу тощо\", - читаємо у документі Екологічного бюро. Як пояснено у зверненні, суть акції полягає також в тому, щоб привернути увагу світової спільноти до проблеми змін клімату, що мають трагічні наслідки для сотень мільйонів людей.</p>\n','2009-03-27 17:12:16','http://www.oranta.org/index.php?option=com_content&view=article&id=261:vymknuvshy-neobovjazkovi-dzherela-osvitlennja-ta-pobutovi-elektroprylady-na-odnu-godynu-my-zrobymo-svi-vnesok-u-porjatunok-zemli&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Вимкнувши необов’язкові джерела освітлення та побутові електроприлади на одну годину, ми зробимо свій внесок у порятунок Землі','uk',1293,2267,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3438,200,IMG_5259.JPG\" />Швидкий ритм життя в Києві часто не дає можливості зупинитись на хвильку перепочити відновити свої сили, та крім фізичного відпочинку нам ще потрібно дбати і про духовний зріст. У сестер служебниць Непорочної Діви Марії є хороша практика, на початку кожного місяця проводити духовну віднову. Цю практику вирішила перейняти і молодь із спільноти церкви св. Миколая Чудотворця і &nbsp;6 лютого відбулась у домі Сестер-Служебниць НДМ духовна віднова для молоді.\n','2010-02-09 11:50:00','http://www.oranta.org/index.php?option=com_content&view=article&id=485:duhovna-vidnova-dlja-molodi&catid=25:parafija-cat&Itemid=27','P','','Духовна віднова для  молоді','uk',1957,3440,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3496,200,DSCF0272.JPG\" />Господь Бог, даючи нам день, дає разом з цим кожним днем і дорогу до себе, на якій ми робимо або півкроку, або крок, або ж долаємо цілу вічність на шляху до Нього. І однаково важливими для Отця нашого є малесенький крок і великий шлях, бо це порятунок душі. Таким і маленьким кроком, і великим шляхом стала Хресна дорога «На славу Господу і за спасіння України» з Києва до Заздрості й далі до Зарваниці для майже 100 паломників греко-католиків Києва та Кам’янця-Подільського з отцем Ігорем Онишкевичем, яка відбулася 19-21 березня 2010 року Божого і була 14 за рахунком.</p>\n','2010-03-27 00:06:28','http://www.oranta.org/index.php?option=com_content&view=article&id=513:proshcha-palomnyctvo-do-zazdrosti-ta-zarvanyci&catid=25:parafija-cat&Itemid=27','P','','ПРОЩА-ПАЛОМНИЦТВО ДО ЗАЗДРОСТІ ТА ЗАРВАНИЦІ','uk',2016,3499,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1287.jpg\" />З 27 по 28 березня відбулись щорічні молодіжні великопісні реколекції, організовані головою молодіжної комісії отцем Віталієм Марценюком. Ця дата реколекцій вибрана невипадково адже Вербна Неділя у церковному календарі також є і днем молоді. Реколекції почались у суботу 27 березня із літургії у храмі Василя Великого. В реколекціях прийняла участь молодь із всієї київської архієпархії, тому крім духовної віднови для кожного ці реколекції дали можливість &nbsp;познайомитись та обмінятись досвідом між спільнотами із різних парафій київської архієпархії.</p>\n','2010-04-01 09:37:41','http://oranta.org/index.php?option=com_content&view=article&id=517:molodizhni-velykopisni-rekolekciji&catid=25:parafija-cat&Itemid=27','N','','Молодіжні Великопісні реколекції.','uk',2028,3511,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1287.jpg\" />З 27 по 28 березня відбулись щорічні молодіжні великопісні реколекції, організовані головою молодіжної комісії отцем Віталієм Марценюком. Ця дата реколекцій вибрана невипадково адже Вербна Неділя у церковному календарі також є і днем молоді. Реколекції почались у суботу 27 березня із літургії у храмі Василя Великого. В реколекціях прийняла участь молодь із всієї київської архієпархії, тому крім духовної віднови для кожного ці реколекції дали можливість &nbsp;познайомитись та обмінятись досвідом між спільнотами із різних парафій київської архієпархії.</p>\n','2010-04-01 09:37:41','http://www.oranta.org/index.php?option=com_content&view=article&id=517:molodizhni-velykopisni-rekolekciji&catid=25:parafija-cat&Itemid=27','P','','Молодіжні Великопісні реколекції.','uk',2023,3506,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,535.jpg\" />В часі великого посту ми маємо можливість проходячи 14 стацій Хресної Дороги ще раз призадуматись, якою дорогу ціну&nbsp; заплатив за наше звільнення від гріха Ісус Христос.&nbsp; Кожна із спільнот, що готує Хресну дорогу намагається, як найглибше передати ті переживання які є характерними для неї. В часі цьогорічного великого посту молодь із церкви Миколая Чудотворця разом із молодіжною спільнотою Неопалима купина із церкви Благовіщеня Пресвятої Богородиці, до себе на хресну дорогу запросила спільнота ВІС.</p>\n','2010-04-01 09:24:12','http://oranta.org/index.php?option=com_content&view=article&id=515:hresna-doroga-vis&catid=25:parafija-cat&Itemid=27','N','','Хресна Дорога ВІС','uk',2030,3513,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,535.jpg\" />В часі великого посту ми маємо можливість проходячи 14 стацій Хресної Дороги ще раз призадуматись, якою дорогу ціну&nbsp; заплатив за наше звільнення від гріха Ісус Христос.&nbsp; Кожна із спільнот, що готує Хресну дорогу намагається, як найглибше передати ті переживання які є характерними для неї. В часі цьогорічного великого посту молодь із церкви Миколая Чудотворця разом із молодіжною спільнотою Неопалима купина із церкви Благовіщеня Пресвятої Богородиці, до себе на хресну дорогу запросила спільнота ВІС.</p>\n','2010-04-01 09:24:12','http://www.oranta.org/index.php?option=com_content&view=article&id=515:hresna-doroga-vis&catid=25:parafija-cat&Itemid=27','P','','Хресна Дорога ВІС','uk',2022,3505,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3508,200,5.JPG\" />П’ятого квітня, у другий день Пасхальних свят, діти з церкви святого Миколая, що на Аскольдовій Могилі, провели у музеї Народної архітектури та побуту «Пирогово». Організували цю подорож отець-парох Ігор Онишкевич та пані Ореста Цимбрівська.</p>\n<p>Приїхавши до музею об одинадцятій годині мікроавтобусом, діти разом з вихователями пішли на Богослужіння, яке у старовинній церкві служив єпископ УГКЦ Богдан Дзюрах. Греко-католицький священик Богдан Чурило співслужив владиці на цій святковій Літургії. Варто зауважити, що багато років поспіль отець Богдан організовує такі чудові, патріотичні гаївки.</p>\n','2010-04-07 22:21:32','http://oranta.org/index.php?option=com_content&view=article&id=519:polyvany-ponedilok-u-muzeji-lpyrogovor&catid=25:parafija-cat&Itemid=27','P','','Поливаний понеділок у музеї «Пирогово»','uk',2038,3521,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3530,200,IMG_9927_resize.JPG\" />Парафіяльна служба милосердя церкви св.. Миколая Чудотворця на Аскольдовій Могилі від тепер має власну кухню, де готуватимуться смачні гарячі обіди для тих, ким опікується наша церква. У неділю 25 квітня 2010 року Божого після дитячої Служби Божої отець Ігор Онишкевич разом з дітьми освятили кухню.</p>\n','2010-04-26 16:46:40','http://oranta.org/index.php?option=com_content&view=article&id=524:-gotumo-smachni-obidy-na-vlasni-kuhni&catid=25:parafija-cat&Itemid=27','P',NULL,'  Готуймо смачні обіди на власній кухні','uk',2049,3532,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,3319,200,2225.JPG\" />Святий Миколай опікун людей у різних обставинах їх життя вважається також покровителем ув\'язнених. Саме тому, мабуть, при храмі Св. Миколая на Аскольдовій Могилі під керівництвом парафіянки п-і Марти Бліхар вже 17-й рік діє служба для ув\'язнених, яка несе духовну і доброчинну підтримку жінкам, що перебувають у пенітенціарних установах. За сприянням настоятеля храму Св. Миколая мітрофорного протопресвітера отця Ігоря Онишкевича у неділю 20 грудня 2009 року<strong> </strong>відбулося привітання господарської обслуги, жінок, неповнолітніх, та хворих у Київському Слідчому Ізоляторі. Їм, разом із добрим, словом було передано подарунки від Св. Миколая. Силами громади було придбано солодощі. Окрім того жінки отримали трохи жіночого одягу, взуття та засобів індивідуальної гігієни фірми Oriflame.\n','2009-12-24 23:25:13','http://www.oranta.org/index.php?option=com_content&view=article&id=458:pomichnyky-sv-mykolaja-vidvidaly-vjazniv&catid=25:parafija-cat&Itemid=27','P','','Помічники Св. Миколая відвідали в\'язнів','uk',1905,3388,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: left;\" src=\"image,3496,200,DSCF0272.JPG\" alt=\"\" />Господь Бог, даючи нам день, дає разом з цим кожним днем і дорогу до себе, на якій ми робимо або півкроку, або крок, або ж долаємо цілу вічність на шляху до Нього. І однаково важливими для Отця нашого є малесенький крок і великий шлях, бо це порятунок душі. Таким і маленьким кроком, і великим шляхом стала Хресна дорога &laquo;На славу Господу і за спасіння України&raquo; з Києва до Заздрості й далі до Зарваниці для майже 100 паломників греко-католиків Києва та Кам&rsquo;янця-Подільського з отцем Ігорем Онишкевичем, яка відбулася 19-21 березня 2010 року Божого і була 14 за рахунком.</p>','2010-03-27 00:06:28','http://oranta.org/index.php?option=com_content&view=article&id=513:proshcha-palomnyctvo-do-zazdrosti-ta-zarvanyci&catid=25:parafija-cat&Itemid=27','N','','ПРОЩА-ПАЛОМНИЦТВО ДО ЗАЗДРОСТІ ТА ЗАРВАНИЦІ','uk',2032,3515,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3446,200,DSCN3025.JPG\" />«Радуйся благодатна, Богородице, бо з Тебе засяяло Сонце правди Христос Бог наш, який просвічує тих, що в темряві» (Тропар празника)</p>\n<p>«Різдвяний круг свят завершується празником&nbsp; Господнього Стрітення, що його святкуємо 15 лютого, сорокового дня по Христовому Різдві. Цей празник своїм змістом тісно зв’язаний з Христовим Різдвом. Бо як у Різдві, так і тут Христос при своїм&nbsp; пожертвувані у святині об’являє нам своє божество…</p>\n','2010-02-15 18:52:53','http://www.oranta.org/index.php?option=com_content&view=article&id=492:praznyk-gospodnogo-stritennja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Празник Господнього Стрітення на Аскольдовій Могилі','uk',1966,3449,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3473,200,333.jpg\" width=\"99\" height=\"151\" />Традиційно з року в рік на Аскольдовій Могилі 9 березня була відслужена панахида пам‘яті і прослави Тараса Шевченка. Тарасова ніч з 9 на 10 березня була морозна, зоряна і вкрита снігом. У нашій церковній хаті напалена піч, зібрались вірні, друзі, свідомі українці щоб молитись і читати «Кобзар».</p>\n<p>1840 році вийшла авторська збірка Тараса Шевченка «Кобзар». Він її сам вичитав, сам видав, сам розставив крапки та коми. Палітурка була синьою а сторінки жовтими. І починався «Кобзар» з вірша «Думи мої, думи мої…».</p>\n','2010-03-10 11:16:13','http://www.oranta.org/index.php?option=com_content&view=article&id=501:tarasova-nich-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Тарасова ніч на Аскольдовій Могилі','uk',1992,3475,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1047.jpg\" />У п’ятницю&nbsp; 26 березня&nbsp; відбулась молодіжна хресна дорога в церкві Миколая Чудотворця підготована молодіжною спільнотою церкви.&nbsp; Цього року молодь церкви Миколая Чудотворця вдруге готувала Хресну Дорогу. Це вже стає хорошою традицією кожного року молоддю проводити хресну дорогу у часі Великого Посту.&nbsp; Підготовка таких заходів, як хресна дорога дає можливість глибше порозважати над стаціями, а потім поділитись своїми переживаннями.</p>\n','2010-04-01 09:31:22','http://oranta.org/index.php?option=com_content&view=article&id=516:molodizhna-hresna-doroga&catid=25:parafija-cat&Itemid=27','N','','Молодіжна Хресна Дорога','uk',2029,3512,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3503,200,1047.jpg\" />У п’ятницю&nbsp; 26 березня&nbsp; відбулась молодіжна хресна дорога в церкві Миколая Чудотворця підготована молодіжною спільнотою церкви.&nbsp; Цього року молодь церкви Миколая Чудотворця вдруге готувала Хресну Дорогу. Це вже стає хорошою традицією кожного року молоддю проводити хресну дорогу у часі Великого Посту.&nbsp; Підготовка таких заходів, як хресна дорога дає можливість глибше порозважати над стаціями, а потім поділитись своїми переживаннями.</p>\n','2010-04-01 09:31:22','http://www.oranta.org/index.php?option=com_content&view=article&id=516:molodizhna-hresna-doroga&catid=25:parafija-cat&Itemid=27','P','','Молодіжна Хресна Дорога','uk',2021,3504,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3508,200,1885.jpg\" />Великодній час&nbsp; завжди багатий на події у царині душпастирства у тюрмах. В усіх пенітенціарних установах пройшла традиційна акція милосердя для в’язнів «Великодні кошик». Проте найважливішою стала радісна подія в одній з жіночих колоній.</p>\n<p>Як було повідомлено раніше, Товариство «Art and prison» назвало імена двох з України серед &nbsp;фіналістів&nbsp; Міжнародного конкурсу образотворчого і прикладного мистецтва в’язнів &nbsp;«Очима інших», що проходив у Берліні. Це Діана Камлюк, яка посіла друге призове місце за твір «Опійна Прірва» та Сергій Гура, що отримав звання фіналіста з його скульптурним твором «Козацькому роду нема переводу».</p>\n','2010-04-08 10:21:38','http://oranta.org/index.php?option=com_content&view=article&id=520:novyny-penitenciarnogo-dushpastyrstva-ugkc&catid=25:parafija-cat&Itemid=27','P','','Новини Пенітенціарного душпастирства УГКЦ.','uk',2039,3522,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3508,200,2.JPG\" />Великдень – одне з найважливіших свят цілого церковного року. Це свято Христового воскресіння. «Христос воскрес із мертвих, смертю смерть подолав, і тим, що в гробах, життя дарував» - співаємо ми у цей святковий час і прославляємо Бога.</p>\n<p>Христове воскресіння несе велике значення для нас. Адже це доказ вічного життя. Воскресіння душі. Воскресіння Христа – це фундамент нашої віри, це величний доказ всієї його науки.</p>\n','2010-04-07 22:12:57','http://oranta.org/index.php?option=com_content&view=article&id=518:virnist-voskreslomu-hrystu&catid=25:parafija-cat&Itemid=27','P','','Вірність Воскреслому Христу','uk',2037,3520,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3530,200,IMG_9447_resize.JPG\" />У суботу 24 квітня молодіжна спільнота церкви Миколая Чудотворця мала духовну віднову. Перейнявши цю практику від сестер молодь уже вдруге із допомогою сестер служебниць Непорочної Діви Марії проводили вдруге. Віднова почалась із Служби Божої яку відправив духівник молодіжної спільноти о. Олексій, а опісля молодь у сімейному колі разом із сестрами та отцем снідали. Після сніданку сестра Анастасія провела &nbsp;читання Святого Письма.</p>\n','2010-04-26 16:07:50','http://oranta.org/index.php?option=com_content&view=article&id=523:duhovna-vidnova-&catid=25:parafija-cat&Itemid=27','P','','Духовна віднова ','uk',2048,3531,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3319,200,IMG_0174.jpg\" />У спогадах минулого - в обіймах сучасного!</p>\n<p>«Як Бог є нашим Татом, Марія – наша Мати,</p>\n<p align=\"right\">Радіють нині з нами, своїми діточками,</p>\n<p align=\"right\">Багата наша хата, та ще багатша стане,</p>\n<p align=\"right\">Коли любов на світі усюди тай настане.</p>\n<p align=\"right\">Ми є родина, ми є родина,</p>\n<p align=\"right\">Ми є родина у Христі.»</p>\n','2009-12-24 23:45:17','http://www.oranta.org/index.php?option=com_content&view=article&id=461:rodyna-makar&catid=25:parafija-cat&Itemid=27','P','','Родина Макар','uk',1908,3391,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3394,200,08496569dd.jpg\" />Ходім лишень до Вифлиєму&nbsp; (Лк.&nbsp;2 , 15)</p>\n<p>Радуйтесь завжди в Господі (Філ. 4, 4)</p>\n<p>Стає світлом, що просвічує кожну людину ( Ів. 1,9)</p>\n<p> </p>\n<p><strong>Христос Рождається!</strong></p>\n<p><strong>Славімо його!</strong></p>\n<p> </p>\n<p>Дорогі парафіяни, дорогі наші читачі!</p>\n<p>З Дитятком Ісусом з Пресвятою Родиною зустрічаємо Новий 2010 р. Б.</p>\n<p>Божий Син кріпить нас у вірі, надії, любові! Дарує нам Своє Царство.</p>\n<p>Прийміть цей Божий дар і будьте щасливі.</p>\n','2009-12-30 02:55:29','http://www.oranta.org/index.php?option=com_content&view=article&id=465:slovo-vid-redakciji&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Слово від Редакції','uk',1915,3398,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3407,200,IMG_3508.JPG\" />За давньою традицією та церковним календарем Новий Рік зустрічали в ніч з 13 на 14 січня. Саме тому молодіжна спільнота церкви св.Миколая Чудотворця вирішила, що це хороша нагода, щоб всім зібратись після різдвяних канікул. На свята кожен їхав до себе додому, щоб у сімейному колі зустріти Різдво і тому не було змоги разом&nbsp; зібратись поколядувати.</p>\n','2010-01-16 12:04:39','http://www.oranta.org/index.php?option=com_content&view=article&id=473:zustrich-starogo-novogo-roku&catid=25:parafija-cat&Itemid=27','P','','Зустріч Старого Нового Року','uk',1930,3413,0,NULL),
(1,1,'<p><img style=\"border: 1px solid #000000; margin: 10px; float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,3419,200,IMG_4765_resize.JPG\" />У неділю 24 січня у церкві Василя Великого відбулась Велика Коляда, яку організував голова молодіжної комісії о.Віталій Марценюк. Ця коляда уже проводиться вдруге і має на меті зібрати всі молодіжні спільноти київської архієпархії для того, щоб разом відсвяткувати Різдво Ісуса Христа.&nbsp; Варто відмітити, що крім молодіжних спільнот Києва, у святкуванні взяла участь молодь із Фастова, Житомира. Розпочав коляду вітальним словом Владика Йосиф Мілян.</p>\n','2010-01-29 12:46:31','http://www.oranta.org/index.php?option=com_content&view=article&id=479:velyka-koljada&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Велика Коляда','uk',1951,3434,0,NULL),
(1,1,'<p><img style=\"float: left; margin: 10px;\" src=\"http://www.oranta.org/images/stories/2009/04/333.jpg\" />Принести радість Благовіщення людям, чия професія несе в собі покликання служити життю людини від перших днів, є важливим завданням євангелізації в імені Церкви.  У Свято Благовіщення о. Олександр (Приліп) і д-н Костянтин Пантелей відвідали три жіночі консультації, що розташовані у Дніпровському та Деснянському районах Києва.У цих закладах відбувається співпраця УГКЦ з медперсоналом для ведення школи відповідального батьківства. Співпраця у вперше почалася 2001 року і плідно триває і по нині. Заняття веде д-н Костянтин.</p>\n','2009-04-08 21:07:48','http://www.oranta.org/index.php?option=com_content&view=article&id=271:profesija-akushera-v-konteksti-svjata-blagovishchennja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Професія акушера в контексті свята Благовіщення','uk',1330,2304,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: left;\" id=\"current_img\" src=\"http://novynar.img.com.ua/img/forall/a/600/97.jpg\" height=\"218\" width=\"290\" /><strong> Ющенко: Пора остаточно розвіяти брехливі міфи про нібито зраду Івана Мазепи</strong></p>\n<p>У рамках святкування 370-річчя з дня народження Івана Мазепи Президент України Віктор Ющенко на Київщині, у селі Мазепинці Білоцерківського району взяв участь у молебні, який провів Патріарх Української Православної церкви Філарет, і поклав квіти до пам\'ятника українському гетьману.</p>\n<p>\"Сьогодні вшановуємо будівельника держави, мудрого політика, дипломата і полководця, талановитого поета, покровителя освіти і мистецтв, захисника нашої віри\", - сказав Ющенко, додавши, що мова йде також про відновлення справедливості та правди про цього видатного діяча.</p>\n','2009-03-20 22:38:42','http://www.oranta.org/index.php?option=com_content&view=article&id=250:jushchenko-zaklykav-vidnovyty-spravedlyvist-i-pravdu-pro-mazepu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Ющенко закликав відновити справедливість і правду про Мазепу','uk',1264,2238,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,4645.jpg\" />Одного разу король прийшов у сад і побачив, що гинуть дерева, кущі і квіти.</p>\n<p>Дуб сказав, що вмирає, тому що не може вирости таким високим, як сосна. Сосна опадала, тому що не може родити виноград, як виноградна лоза. А лоза всихала, тому що не могла цвісти, як троянда.</p>\n<p>Невдовзі він знайшов одну рослину, що тішила його серце, була розквітла і свіжа. Розпитавши її, він отримав таку відповідь:</p>\n','2014-06-10 06:01:04','http://oranta.org/index.php?option=com_content&view=article&id=2195:zostavasja-sam-soboju-10-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Зоставайся сам собою (10 червня)','uk',4430,5913,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,1783,200,517.jpg\" />Волонтери Карітас-Печерськ при церкві св. Миколая Чудотворця на Аскольдовій Могилі продовжують вести проект «Гарячий обід».</p>\n<p>Пані Ореста Цимбрівська. разом з пані Людмилою, пані Наталею, пані Ганною та панною Мар\'яною годують дітей вулиці, дітей з малозабезпечених сімей, бездоглядних дітей гарячими обідами.</p>\n<p>\n','2008-12-15 20:58:05','http://www.oranta.org/index.php?option=com_content&view=article&id=139:lgarjachy-obidr-dlja-kozhnoji-bezdogljadnoji-dytyny&catid=25:parafija-cat&Itemid=27','P',NULL,'«Гарячий обід» для кожної бездоглядної дитини','uk',804,1784,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,1810,200,sviatiy_mikolay.gif\" />Велике вшанування, яким св. Миколай втішається довгі сторіччя між християнами обох обрядів, є засвідченням його надзвичайної святості й небесної слави. Св. Бонавентура так писав про нього: \"Миколай, вибраний вже в лоні своєї матері, святий з дитинства, був славою юнаків, честю старців, похвалою священиків, сонячним сяйвом єпископів. Слава про його чуда розійшлася по всій землі, Миколая величають усі люди. Щодня помножуються чуда за його посередництвом. Св. Миколая прославляють на морі, величають на суші, призивають у небезпеках. Коли наближається гроза і штормить море, тоді зі сльозами кличуть на допомогу св. Миколая; коли приходить нещастя, св. Миколай є нашим заступником. І не тільки до християн, але й до поган дійшла слава його імені, він у них в такій великій шані, що вони громадами сходяться величати та прославляти його ім\'я. Між багатьма великими чудами, що їх він творив, найбільшим чудом був він сам\".</p>\n','2011-12-15 22:36:49','http://oranta.org/index.php?option=com_content&view=article&id=150:praznyk-svjatogo-mykolaja-chudotvorcja-19-grudnja&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Празник Святого Миколая Чудотворця - 19 грудня','uk',2250,3733,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,1810,200,sviatiy_mikolay.gif\" />Велике вшанування, яким св. Миколай втішається довгі сторіччя між християнами обох обрядів, є засвідченням його надзвичайної святості й небесної слави. Св. Бонавентура так писав про нього: \"Миколай, вибраний вже в лоні своєї матері, святий з дитинства, був славою юнаків, честю старців, похвалою священиків, сонячним сяйвом єпископів. Слава про його чуда розійшлася по всій землі, Миколая величають усі люди. Щодня помножуються чуда за його посередництвом. Св. Миколая прославляють на морі, величають на суші, призивають у небезпеках. Коли наближається гроза і штормить море, тоді зі сльозами кличуть на допомогу св. Миколая; коли приходить нещастя, св. Миколай є нашим заступником. І не тільки до християн, але й до поган дійшла слава його імені, він у них в такій великій шані, що вони громадами сходяться величати та прославляти його ім\'я. Між багатьма великими чудами, що їх він творив, найбільшим чудом був він сам\".</p>\n<p> </p>\n','2009-12-18 22:36:49','http://www.oranta.org/index.php?option=com_content&view=article&id=150:praznyk-svjatogo-mykolaja-chudotvorcja-19-grudnja&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Празник Святого Миколая Чудотворця - 19 грудня','uk',831,1811,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.irs.in.ua/images/stories/Photo_news/2009.03.16_foto1s.jpg\" />16 березня 2009 року в  Українському домі відбувся Круглий стіл на тему \"Україна між хаосом і диктатом. Чи існує християнська відповідь?\". Організаторами виступили Комісія Української Греко-Католицької Церкви (УГКЦ) у справах мирян та Фонд Конрада Аденауера, співорганізатором - Нарада представників Християнських Церков України. В підготовці Круглого столу також взяли участь представники громадської організації Інститут релігійної свободи.\n','2009-03-17 08:06:06','http://www.oranta.org/index.php?option=com_content&view=article&id=248:-cerkvy-ta-gromadskist-daly-hrystyjansku-vidpovid-na-pytannja-nynishnogo-stanu-ukrajinskoji-derzhavy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Церкви та громадськість дали християнську відповідь на питання нинішнього стану Української Держави','uk',1258,2232,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_27-11-08.jpg.jpg\" />27 листопада в дитячому реабілітаційному центрі «Будинок з Ангелом» відбувся круглий стіл «Ліки солідарності» за участю громадських і релігійних організацій, людей, що займаються реабілітацією, з обмеженими фізичними і розумовими можливостями. Круглий стіл відбувся у рамках соціального тижня \"На шляху до солідарності українського суспільства\", що днями проходить в Одесі. У зустрічі взяли участь представники релігійних організацій м. Одеси, члени громадських організацій, а також заступник начальника Управління соціальнї праці Одеської міської ради Регіни Анатоліївни Миколаєвої. Учасники круглого столу говорили про проблеми людей з обмеженими можливостями, про питання подолання барьерності. Зокрема, пані Миколаєва проінформувала присутніх про дії міських властей, направлені на подолання барьерності в Одесі. Вона також звернулася до присутніх на зустрічі студентів одеських ВУЗів чуйніше відгукуватися на потреби людей із обмеженими можливостями.</p>\n<p>\n','2008-11-29 21:57:05','http://www.oranta.org/index.php?option=com_content&view=article&id=118:v-odesi-vidbuvsja-krugly-stil-lliky-solidarnostir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'В Одесі відбувся круглий стіл «Ліки солідарності»','uk',713,1693,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_huzar_icon.jpg.jpg\" />\"Дуже радію, що іконописання живе, що воно не є тільки старовинним мистецтвом, а, подібно як і людське передання, традиція, передається з покоління в покоління. Передавання мистецтва писати ікони, разом з тою вірою, яка дає натхнення іконописцям, є тим скарбом, який, сподіваюсь, і ми, через сучасних митців, передамо нашим наступникам\", - сказав Блаженніший Любомир, під час вручення йому ікони Благовіщеня Пресвятої Богородиці. При цьому Предстоятель УГКЦ сердечно подякував за молитовний дарунок.</p>\n<p>\n','2009-02-28 10:34:07','http://www.oranta.org/index.php?option=com_content&view=article&id=227:qradiju-shcho-ikonopysannja-zhyveq-blazhennishy-ljubomyr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'\"Радію, що іконописання живе\": Блаженніший Любомир  ','uk',1191,2165,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_huzar_kirovohrad_01.jpg.jpg\" />9 - 13 грудня Блаженніший Любомир, Верховний Архиєпископ Києво-Галицький, перебував у Римі, де брав участь у пленарному засідання Папської Ради для сприяння єдності між християнами. Такі засідання відбуваються один раз на два-три роки. Глава УГКЦ є членом трьох ватиканських установ: Папської Ради для сприяння єдності між християнами, Папської Ради у справах культури та Конгрегації для Східних Церков.</p>\n<p>Рада для сприяння єдності між християнами має адміністративний уряд: голову та секретаріат, а також членів ради та консультантів, які походять з різних частин земної кулі. У пленарному засіданні беруть участь усі перелічені групи. Цього року у роботі ради взяли участь близько сорока осіб. Від УГКЦ у цій зустрічі також брав участь Владика Василь (Лостен), Єпископ-емерит Стемфордської єпархії.</p>\n<p>\n','2008-12-16 09:44:53','http://www.oranta.org/index.php?option=com_content&view=article&id=141:poshuk-jednosti--vazhlyve-zavdannja-cerkvy-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Пошук єдності – важливе завдання Церкви ','uk',807,1787,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_npf_ks.jpg.jpg\" />\"У сьогоднішній ситуації Синодом Єпископів Києво-Галицького Верховного Архиєпископства було вирішено, що Церква мусить взяти участь у процесі підвищення економічної грамотності своїх вірних. Недержавні пенсійні фонди, окрім прямого свого завдання: забезпечувати гідну старість, мають ще одну важливу функцію: навчати основ фінансово-економічного життя\", - сказав о. Олекса Петрів, секретар Наглядової ради Відкритого недержавного пенсійного фонду \"Покрова\", під час круглого столу \"Недержавне пенсійне забезпечення в Україні. Виклики та перспективи розвитку\". Захід був організований 10 грудня 2008 року Державною комісією з регулювання ринків фінансових послуг України за підтримки Проекту USAID \"Розвиток ринків капіталу\".</p>\n<p>\n','2008-12-11 18:04:35','http://www.oranta.org/index.php?option=com_content&view=article&id=132:npf-qpokrovaq-ta-derzhavni-reguljujuchi-organy-spilno-pracjuvatymut-dlja-zahystu-interesiv-uchasnykiv-fondu-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'НПФ \"Покрова\" та державні регулюючі органи спільно працюватимуть для захисту інтересів учасників фонду ','uk',781,1761,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_try_sviatyteli.jpg.jpg\" /><strong>12 лютого 2009 року <br /><br />Празник трьох Святителів</strong></p>\n<p> </p>\n<p>Коротка історія</p>\n<p style=\"text-align: justify;\">Празник Трьох Святителів належить до найновіших празників грецької Церкви. Поштовхом до встановлення цього празника була велика суперечка у другій половині XI ст. про те, хто з них трьох є більш цінний для Церкви. Одні вище ставили святого Василія Великого, другі святого Григорія Богослова, а ще инші святого Йоана Золотоустого. Прихильники святого Василія звались василіяни, святого Григорія - григоріяни, а святого Йоана - йоаніти. Цей спір розв\'язали самі Святителі. Вони спочатку кожен зокрема, а потім усі троє з\'явилися Йоанові - єпископові міста Евхаїти, який відзначався мудрістю, знанням, чеснотами, і сказали: \"Ми, як бачиш, одне в Бога й нічого нема в нас противного чи спірного, а кожний з нас у своєму часі окремо натхнений Божим Духом навчав, що було потрібно для спасення людей. Тож нема між нами ні першого, ні другого, але як назвеш одного, то за ним ідуть два инші. Встань, отже, і накажи тим, що через нас сваряться, щоб не ділилися, бо як за життя, так і після смерти нашим намаганням є всі сторони світу зводити до миру і єдности. Установи, отже, святкування нашої пам\'яти в одному дні так, як ми одне в Бога, а ми будемо помагати до спасення тим, що святкуватимуть нашу пам\'ять\".</p>\n<p style=\"text-align: justify;\">\n','2009-02-07 14:03:23','http://www.oranta.org/index.php?option=com_content&view=article&id=203:praznyk-troh-svjatyteliv&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Празник трьох Святителів','uk',1108,2082,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_ucu_rym.jpg.jpg\" />Урочистості з нагоди празника св. Климента Папи відбулися 8 грудня 2008 року в приміщенні філії Українського католицького університету в Римі, яка тепер має назву Інституту св. Климента Папи.</p>\n<p>Український католицький університет, що його 1963 року заснував у Римі Патріарх Йосиф (Сліпий), своїм покровителем мав цього святого Папу, який загинув як мученик на українських землях ще у І столітті. За часів Патріарха Йосифа цей празник дуже урочисто святкували, запрошуючи високих церковних достойників, наукову еліту, священиків, монахів і монахинь та питомців семінарій. На цих урочистостях св. п. Патріарх Йосиф завжди підкреслював важливість УКУ для розвитку і зростання української богословської думки, для виховання свідомого кліру й духовенства, а в майбутньому - й для вишколу мирян, які б мали творити громадянське суспільство.</p>\n<p>\n','2008-12-16 09:42:55','http://www.oranta.org/index.php?option=com_content&view=article&id=140:praznyk-sv-klymenta-papy-u-filiji-uku-v-rymi-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Празник св. Климента Папи у філії УКУ в Римі ','uk',806,1786,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_uhoda_1.jpg.jpg\" />15 березня 2009 року у Києві було підписано угоду про співробітництво між Українською Греко-Католицькою Церквою та Міністерством охорони здоров\'я України. Від УГКЦ документ підписав Блаженніший Любомир, Глава УГКЦ, а від МОЗ України - Василь Князевич, Міністр охорони здоров\'я. Сторони уклали цю угоду \"прагнучи до врегулювання засад взаємодії у справі гуманізації медичної опіки на основі поваги прав та гідності людини; традиційних духовних, морально-етичних та культурних цінностей; усвідомлюючи взаємну відповідальність за оздоровлення українського суспільства\".\n','2009-03-16 17:49:15','http://www.oranta.org/index.php?option=com_content&view=article&id=247:ukrajinska-greko-katolycka-cerkva-ta-ministerstvo-ohorony-zdorovja-pidpysaly-ugodu-pro-spivpracju-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Українська Греко-Католицька Церква та Міністерство охорони здоров’я підписали угоду про співпрацю  ','uk',1248,2222,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_viru_ne_vbyly.jpg.jpg\" />\"З нами Бог\", - казали німці й розтрощували голівки неповинних дітей. \"Ми - християни\", - запевняли російські солдати і розпинали українських священиків на стінах Бригідок. Останні вмирали гідно. Лише тихо шепотіли молитву...</p>\n<p>Лютий 2009-го. У церквах Західної України дзвони б\'ють. 102 роки тому народився архиєпископ катакомбної церкви Володимир Стернюк, а ще минає 117 літ з дня народження Патріарха Йосифа Сліпого, який став Главою УГКЦ у 1944-му, по смерті Андрея Шептицького.</p>\n<p>\n','2009-02-23 13:27:58','http://www.oranta.org/index.php?option=com_content&view=article&id=219:a-viru-ne-vbyly&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'А віру не вбили...','uk',1165,2139,0,NULL),
(1,1,'<p><img style=\"float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_z_posmishkoju.jpg.jpg\" />Сьогодні, 26 лютого 2009 року, Главі Української Греко-Католицької Церкви Блаженнішому Любомирові виповнюється 76 років. З нагоди уродин з різних куточків України і світу, від українців і представників інших народів, духовенства і мирян, політиків і громадських діячів, молоді і старших линуть привітання для Глави і Отця УГКЦ з найщирішими побажаннями щедрих Божих благодатей та благословення для подальшого успішного керівництва Церквою. Сьогодні бажають Предстоятелю УГКЦ, щоб \"Господь Бог був щедрим на все, що сприяє здійсненню його пастирського служіння, зростанню і поглибленню братніх взаємин між єпископами, священиками та мирянами Церкви\".</p>\n<p>\n','2009-02-26 16:22:40','http://www.oranta.org/index.php?option=com_content&view=article&id=222:blazhennishomu-ljubomyrovi--76-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженнішому Любомирові – 76  ','uk',1182,2156,0,NULL),
(1,1,'<p><img style=\"float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,1689,200,IMG_9288_resize.JPG\" />У суботу, 22 листопада, у калиновому гаю (Паркова дорога, м. Київ) о 17:00 годині відспівали панахиду  за душі померлих від голодомору 32-33 років.</p>\n<p> </p>\n<p>Молодь церкви св. Миколая Чудотворця з Аскольдової Могили та київські Пластуни засвітили 75 лампадок, щоб увіковічити пам\'ять загиблих українців у ці жахливі роки.</p>\n<p> </p>\n<p>\n','2008-11-28 08:50:24','http://www.oranta.org/index.php?option=com_content&view=article&id=117:vechir-pamjati-zhertv-golodomoru-32-33-rr&catid=25:parafija-cat&Itemid=27','P',NULL,'Вечір памя\'ті жертв голодомору 32-33 рр.','uk',710,1690,0,NULL),
(1,1,'<p><img style=\"float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,1770,200,IMG_6369.jpg\" />Сьогодні, 13 грудня, парафія святого апостола Андрія Первозванного у Вишгороді святкувала свій празник.</p>\n<p>На Божественній Літургії, яку очолив настоятель церкви мітрат отець Сергій Прутко у спів служінні священників: декана Київського отця Ігоря Онишкевича, отця Романа Небожука, отця Олександра Приліпа, отця Петра Жука і диякона Константина Пантелея.</p>\n<p>У своїй проповіді отець Сергій згадав про початок нашого християнства за апостольських часів, як апостол Андрій благословив, цю землю, ці київські гори, і від його благословення зросла наша церква. Також отець Сергій нагадав про митрополита Андрея Шептицького, який як апостол сіяв слово Боже на наших землях, говорив про велике переслідування церкви, яке завершиться і церква знову буде проповідувати слово Боже.</p>\n<p>\n','2008-12-13 19:32:22','http://www.oranta.org/index.php?option=com_content&view=article&id=135:hramovy-praznyk-u-vyshgorodi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Храмовий празник у Вишгороді','uk',788,1768,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: right;\" src=\"http://www.askoldova-mohyla.org/uk/image,1819,200,4.JPG\" />20 грудня 2008 р. у приміщенні актової зали НПУ ім. М.Драгоманова відбувся <em>ІІ фестиваль-конкурс духовної пісні \"Аскольдів глас\"</em>. Розпочався вечір реколекційною наукою о. Олександра про роль і значення духовного співу на Божественній Літургії. Опісля гостей і учасників фестивалю привітала заслужена артистка України, парафіянка церкви св. Миколая Чудотворця на Аскольдовій могилі - Леся Горова. Після цього, власне, і стартував конкурс.</p>\n<p>Участь у заході взяли церковні хори \"Аскольд\", \"Дзвони Подолу\", \"Хор храму Благовіщення при Патріаршому соборі\", фольклорний гурт \"Дивень\", жіночий ансамбль \"Anima\", вокально-інструментальний ансамбль \"Золоті струни\", сольні виконавці - Вікторія Михайлюк, Марія Чабан, Галина Книгиницька та Даниїла Костик. Загалом, десять учасників представили свою програму на розсуд журі та публіки. На святі пісні та співочої майстерності у виконанні конкурсантів звучали твори духовної тематики та українські старовинні і сучасні різдвяні колядки.</p>\n<p>\n','2008-12-21 22:12:18','http://www.oranta.org/index.php?option=com_content&view=article&id=155:laskoldiv-glasr-2008&catid=25:parafija-cat&Itemid=27','P',NULL,'«Аскольдів глас» 2008','uk',840,1820,0,NULL),
(1,1,'<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_22-01-09_01.jpg.jpg\" />В переддень літургічної пам\'яті Пратулинських мученників, 22 січня 2009 року у місті Холмі (тепер Chełm, Республіка Польща), в колишній українській греко-католицькій катедрі Різдва Пресвятої Богородиці Холмської Єпархії відбулося урочисте богослуження.</p>\n<p>Впродовж шести років поспіль, завдяки доброзичливому ставленні<em> </em>місцевого латинського пароха о. Казимира Бовніка, апостольського протонотара, стає можливою участь представників УГКЦ у святковій відправі присвяченій українським мученикам багатостраждальної Холмської і Підляської землі - споконвічної української етнічної території, що в силу політичних обставин опинилася поза кордонами сучасної України. Цей величний храм,<em> </em>колись Української Церкви, а тепер базиліки під тією ж назвою Люблинської Архидієцезії Римо-Католицької Церкви, що своїми розмірами перевищує Архикатедральний Собор Св. Юра у Львові, є віддавна паломницькою святинею.</p>\n<p>\n','2009-01-26 14:03:51','http://www.oranta.org/index.php?option=com_content&view=article&id=194:buduvaty-viru-na-mabutnih-pokolinnjah&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Будувати віру на майбутніх поколіннях','uk',1048,2022,0,NULL),
(1,1,'<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_25-11-08_01.jpg.jpg\" />25 листопада в день памяті святого священомученика Йосафата Кунцевича, у каплиці чоловічого монастиря «Воплоченого Слова» при парафії святих Кирила і Методія у Крихівцях-Долині, відбулося загальне (публічне) вшанування мощей святого, які адміністратор парафії о. Йосафат Бойко, ВС, привіз з Риму, де мощі перебувають у базиліці св. Петра.</p>\n<p>У каплиці знаходяться мощі першого ступеня 14 святих, які виставляються для загального вшанування у дні пам\'яті святих протягом літургійного року. До першого ступеня належать частинки з тіл святих (переважно з костей - \"ex ossibus\"). До другого ступеня мощей належать речі користовані святими (наприклад, вервиця чи біблія) і до третього - тканина, яка доторкалась тіла чи костей святого і яка переважно розповсюджується з різними іконками чи образками.</p>\n<p>\n','2008-11-29 22:01:23','http://www.oranta.org/index.php?option=com_content&view=article&id=119:vshanuvannja-moshche-pokrovytelja-ukrajinskogo-narodu-sv-osafata-kuncevycha-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Вшанування мощей покровителя українського народу св. Йосафата Кунцевича ','uk',714,1694,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_myr2.jpg.jpg\" /></p>\n<p>Презентація книги під такою назвою відбулася 24 січня 2009 року у Києві, в Інституті релігійних наук св. Томи Аквінського (вул. Якіра, 13), в рамках Тижня молитов за єдність християн. Презентоване видання присвячене 40-й річниці відзначення Всесвітнього дня миру. Книгу підготувала до друку Комісія \"Справедливість і мир\" Української Греко-Католицької Церкви. Видало збірник львівське видавництво \"Друкарські куншти\". Учасником презентації був Блаженніший Любомир, Глава УГКЦ. Головну доповідь виголосив диякон Микола Сулима, віце-канцлер Курії Верховного Архиєпископа.</p>\n<p>\n','2009-01-26 14:06:56','http://www.oranta.org/index.php?option=com_content&view=article&id=195:qvid-sliv-pro-myr--do-perekonannja-u-myriq-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'\"Від слів про мир – до переконання у мирі\"  ','uk',1049,2023,0,NULL),
(1,1,'<p><img style=\"float: right;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_symposium1.jpg.jpg\" />У м. Гамінгу (Австрія) 4-6 березня відбулася конференція, на якій йшлося про місію Східних Католицьких Церков в житті Вселенської Церкви. Серед 150 учасників було 17 єпископів (15 греко-католицьких), численні священики, провідні науковці, студенти та миряни з понад 20 країн світу. Як повідомив офіційний сайт Міжнародного теологічного інституту, організаторами конференції виступили: Міжнародний теологічний інститут в Гамінгу (Австрія), Інститут історії східного християнства, Католицький теологічний факультет Віденського університету та Український католицький університет.</p>\n<p>\n','2009-03-10 09:31:20','http://www.oranta.org/index.php?option=com_content&view=article&id=237:mizhnarodna-konferencija-vyznachyla-misiju-greko-katolykiv-u-21-stolitti--buty-svidchennjam-hrystyjanstva-ta-jednosti-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Міжнародна конференція визначила місію греко-католиків у 21 столітті – бути свідченням християнства та єдності  ','uk',1222,2196,0,NULL),
(1,1,'<p><img style=\"margin-right: 8px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_z_risu.jpg.jpg\" /></p>\n<p>10 березня у Києві відбувся Міжрелігійний круглий стіл \"Відчуваючи серцебиття мігранта: Хто і що чекає на повернення українських трудових мігрантів\", організований Комісією УГКЦ у справах мігрантів спільно із Фондом OpenUkraine. \"Ми ставимо перед собою подвійну мету. По-перше, привернути увагу суспільства до проблем українців, котрі повертаються. По-друге, розширити діалог релігійних, політичних та громадських лідерів щодо розв\'язання проблем, які супроводжують феномен міграції. Поширювана думка про масове повернення українських мігрантів наразі безпідставна. Ми повинні продемонструвати, що вони тут потрібні, бо за кожним стоять долі конкретних людей, дітей та родин, підтримання та збереження яких є одним із основних пріоритетів. Важливо, щоб в умовах фінансової кризи зв\'язок з рідними та Батьківщиною не пропадав\", - для Департаменту інформації сказав Григорій Селещук, голова Комісії УГКЦ у справах мігрантів.</p>\n<p> </p>\n','2009-03-11 19:06:48','http://www.oranta.org/index.php?option=com_content&view=article&id=240:cerkva-vidigraje-vazhlyvu-rol-u-spravi-socialnoji-adaptaciji-migrantiv-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Церква відіграє важливу роль у справі соціальної адаптації мігрантів  ','uk',1227,2201,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"1_0_704903\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/06/1_0_704903.jpg\" height=\"131\" width=\"197\" />Дорогі брати і сестри,</p>\n<p>Сьогодні я хочу коротко звернутись до ще однієї картини, яка  допомагає нам проілюструвати містерію Церкви: &nbsp;храм (пор. Lumen Gentium,  6).</p>\n<p>Про що нам нагадує слово «храм»? Воно нам нагадує про будівлю,  споруду. В основному, воно багатьом нагадує історію Ізраїльського  Народу, про яку розповідається в Старому Завіті. В Єрусалимі величний  Храм Соломона був місцем зустрічі з Богом у молитві.</p>\n','2013-06-26 20:16:59','http://oranta.org/index.php?option=com_content&view=article&id=1297:2013-06-26-17-20-17&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Про що нам нагадує слово «храм»?','uk',3387,4870,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"LUMEN FIDEI\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/07/lumen2.jpg\" height=\"166\" width=\"121\" /></p>\n<p><strong>ЕНЦИКЛІКА</strong> <strong>LUMEN FIDEI – СВІТЛО ВІРИ</strong> <strong>СВЯТІШОГО ОТЦЯ</strong> <strong>ФРАНЦИСКА</strong></p>\n<p>ЄПИСКОПАМ, СВЯЩЕНИКАМ, ДИЯКОНАМ, БОГОПОСВЯЧЕНИМ ОСОБАМ І ВСІМ ВІРНИМ МИРЯНАМ ПРО ВІРУ</p>\n<p>1. Світло віри: саме так Традиція Церкви назвала великий дар,  принесений Ісусом Христом, котрий в Євангелії від Івана говорить про  себе: “Я – світло, на світ прийшов, щоб кожен, хто в мене вірує, не  перебував у темряві. ” (Ів.12,46). Також і Св.Павло висловлюється такими  словами: “Бо Бог… освітлив серця наші, щоб у них сяяло знання Божої  слави, що на обличчі Ісуса Христа.” (ІІ Кор. 4,6)</p>\n','2013-07-20 20:55:16','http://oranta.org/index.php?option=com_content&view=article&id=1342:encyklika-papy-francyska-svitlo-viry-lumen-fidei&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Енцикліка Папи Франциска СВІТЛО ВІРИ (LUMEN FIDEI)','uk',3449,4932,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"foto_37\" src=\"http://www.catholic-media.org/ua/images/stories/papa/foto_37.jpg\" height=\"136\" width=\"200\" />Ісус   не просто щось нам каже про Бога, але Сам є об’явленням Бога, Божого   обличчя. На це звернув увагу Папа Бенедикт XVI, зазначаючи, що саме у   Христовому Різдві ми споглядали здійснення Божих обітниць про повноту   Божого об’явлення.</p>\n<p>Папа пригадав, що Старий Завіт розповідає нам,  що Бог, не зважаючи  на гріх, завжди пропонує людині Свою дружбу. Це  ставалося і через союз з  Авраамом, через завіт з вибраним народом.</p>\n','2013-01-18 00:50:48','http://oranta.org/index.php?option=com_content&view=article&id=926:kozhna-ljudyna-bazhaje-bachyty-bozhe-oblychchja-navit-nevirujuchi&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кожна людина бажає бачити Боже обличчя, навіть невіруючі','uk',2899,4382,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"papiez\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/11/papiez-300x198.jpg\" height=\"133\" width=\"202\" /></p>\n<p>Дорогі брати і сестри, доброго дня!</p>\n<p><a href=\"http://popefrancis.org.ua/?p=1970\">Минулої середи я говорив про спілкування святих</a>,  що вважається співпричастям між святими особами, тобто між нами  вірними. Сьогодні я хотів би поглибити другий аспект цієї реальності,  тобто «спільність у святих речах», в духовних благах.</p>\n','2013-11-06 23:40:15','http://oranta.org/index.php?option=com_content&view=article&id=1603:spilnist-u-svjatyh-rechah&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Спільність у святих речах','uk',3752,5235,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Блаженніший Любомир (Гузар)\" src=\"http://www.credo-ua.org/wp-content/uploads/2012/10/72.jpg\" height=\"162\" width=\"227\" /></p>\n<p><strong>У мовах різних народів існують слова, які мають декілька  значень і часто вживаються під час спілкування. Одним із таких слів є  «культура».</strong></p>\n<p>В українській мові воно має два основних значення. Перше можна  окреслити словом «мистецтво». Коли говоримо про культурні надбання  якогось народу, маємо на думці образотворче мистецтво, музику,  літературу, архітектуру тощо.</p>\n','2013-10-26 00:15:29','http://oranta.org/index.php?option=com_content&view=article&id=1575:pro-kulturu-osoby-i-kulturu-narodu&catid=37:spilnota-cat&Itemid=49','P',NULL,'Про культуру особи і культуру народу','uk',3713,5196,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Кароль Войтила\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/05/Wojtyla-sciagac.jpg\" height=\"170\" width=\"144\" /></p>\n<p><strong>Як годиться найкращому учневі в класі, Кароль Войтила перший  написав іспит на атестат зрілості, але віддав його останнім, аби не  засмучувати однокласників, – каже Євгеній Мруз, останній живий співучень  папи Йоана Павла ІІ.</strong></p>\n<p>18 травня – день чергового спомину дня народження блаженного Папи  Войтили (18.05.1920), 13 травня був день спомину замаху на нього на  площі св.Петра, а 14 травня минуло 75 років відтоді, як Кароль Войтила  склав на «відмінно» іспит на атестат зрілості у державній гімназії  ім.Мартіна Вадовіти у Вадовіцах. На шкільному заключному балу Льолек  пройшов кілька кругів танцю, але потім хутко зник, навіть не ковтнувши  вина.</p>\n','2013-05-20 15:03:47','http://oranta.org/index.php?option=com_content&view=article&id=1210:a-karol-votyla-davav-spysuvaty-na-ispytah&catid=35:istorychni-postati-cat&Itemid=48','P','','А Кароль Войтила давав списувати на іспитах?','uk',3255,4738,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Мечислав Мокшицький\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/10/mokpg.jpg\" height=\"182\" width=\"132\" /></p>\n<p><strong>Вступ</strong></p>\n<p>У грудні 1943 року був убитий о. Людвік Вродарчик, облат. Незважаючи на  перестороги, він не став ховатися, не втік, не залишив парафії в  Окопах, Луцької дієцезії, де він був настоятелем. Адже допомагав він  усім – полякам, українцям, євреям. Нічого злого не вчинив і не мав чого  таїти, але не хотів залишати Пресвятого Таїнства самого у своєму храмі.  Забрали його під час молитви й жорстоко катували. У місці, де його було  вбито, на хатній стіні в Карпилівці, залишилася чітка пляма крові, яку  неможливо змити чи зафарбувати. Світлина з того місця була передана на  вступний беатифікаційний процес, що розпочався за ініціативи єпископів  Римсько-Католицької Церкви в Україні.</p>\n','2013-07-16 21:22:37','http://oranta.org/index.php?option=com_content&view=article&id=1336:propovid-arhyjepyskopa-mechyslava-mokshyckogo-na-70-rokovynah-volynskoji-tragediji-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Проповідь архиєпископа Мечислава Мокшицького на 70 роковинах Волинської трагедії ','uk',3439,4922,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа Франциск: Ісус «став гріхом»\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/Papa150613.jpg\" height=\"130\" width=\"197\" /></p>\n<p>Християнське життя не є «перебуванням у спокої аж поки потрапиш  до неба», воно є виходом до світу, аби проголошувати, що Ісус «став  гріхом» заради примирення людей з Отцем.<strong><br /></strong></p>\n<p>Життя християнина – то не перебування у якомусь закутку, щоби втоптати  собі шлях, який вигідно провадить до неба. Воно є динамізмом, який  надихає залишатися на цій дорозі та проголошувати, що Христос примирив  нас із Богом, стаючи гріхом заради нас, – сказав Святіший Отець.</p>\n','2013-06-15 22:00:18','http://oranta.org/index.php?option=com_content&view=article&id=1257:2013-06-15-19-02-19&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Ісус «став гріхом»','uk',3337,4820,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа Франциск\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/03/%D0%BF%D0%B0%D0%BF%D0%B0-%D0%A4%D1%80%D0%B0%D0%BD%D1%86%D0%B8%D1%81%D0%BA.jpg\" height=\"142\" width=\"211\" /></p>\n<p><strong>Інавгураційна Меса Папи Франциска, яка триває на площі Святого  Петра у Ватикані, зібрала десятки тисяч вірних, 130 офіційних делегацій,  серед яких 30 президентів країн. Вперше майже за тисячу років в  інавгурації бере участь Патріарх Константинополя.</strong></p>\n<p>Свою проповідь Папа Франциск виголосив для усіх людей доброї волі, а не  лише для християн. Святіший Отець говорив про покликання зберігати  красу світу, яке стосується усіх людей.</p>\n','2013-03-19 14:03:21','http://oranta.org/index.php?option=com_content&view=article&id=1057:-ne-bitesja-buty-dobrymy-i-nizhnymy-propovid-papy-pid-chas-inavguracinoji-mesy&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Не бійтеся бути добрими і ніжними, – проповідь Папи під час інавгураційної Меси','uk',3056,4539,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа Франциск\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/1_0_702680.jpg\" height=\"131\" width=\"198\" />Любити ворогів нелегко, але цього від нас вимагає Ісус. Папа Франциск&nbsp; підкреслив, що для того, аби  могти простити ворогам, фундаментальною справою є молитися за них.</p>\n<p>Святіший Отець поставив питання про те, як можливо любити ворогів, у  загальнолюдському контексті. Як можна любити тих, які приймають рішення  про бомбардування, вбиваючи багатьох людей? Як можна любити тих, «які  задля любові до грошей» перешкоджають доступові людей похилого віку до  ліків, залишаючи їх помирати? Чи тих, які шукаючи виключно особистих  інтересів, чинять багато зла? Але цього від нас вимагає Ісус.</p>\n','2013-06-19 12:03:13','http://oranta.org/index.php?option=com_content&view=article&id=1267:ljubyty-vorogiv-ce-molytysja-za-nyh&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Любити ворогів – це молитися за них','uk',3356,4839,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа написав до газети\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/09/writing.jpg\" height=\"131\" width=\"233\" /></p>\n<p><strong>В опублікованому сьогодні, в середу, листі до італійської щоденної газети «La Repubblica</strong><strong>»  Папа Франциск написав: настав час, аби почати провадити відкритий і  вільний від упереджень діалог на тему віри. Також він підкреслив, що  невіруючий грішить тоді, коли не слухає свого сумління.</strong></p>\n<p>У безпрецедентному листі Папа послався на питання, поставлені йому  раніше засновником газети «лівих», Еудженіо Скальфарі, у реакції на  проголошену в липні енцикліку «Lumen fidei» (Світло віри).</p>\n','2013-09-12 02:12:43','http://oranta.org/index.php?option=com_content&view=article&id=1467:ne-maje-inshogo-sensu-i-mety-nizh-ta-shchob-zhyty--progoloshuvaty-svidchennja-hrysta&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Не має іншого сенсу і мети, ніж та, щоб жити й проголошувати свідчення Христа','uk',3587,5070,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа і молодь\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/08/%D0%9F%D0%B0%D0%BF%D0%B0-%D1%96-%D0%BC%D0%BE%D0%BB%D0%BE%D0%B4%D1%8C.jpg\" height=\"133\" width=\"215\" /></p>\n<p><strong>Святіший Отець підкреслив, що молодь має йти за Ісусом Христом.  «А&nbsp;Папа їх провадить і супроводжує на цьому шляху віри та надії».</strong></p>\n<p>Папа Франциск попросив  про молитву: аби молодь, яка брала участь у ВДМ, зуміла перенести цей  досвід у своє щоденне життя, у щоденну поведінку та у свої життєві  вибори.</p>\n','2013-08-07 02:04:13','http://oranta.org/index.php?option=com_content&view=article&id=1392:-ity-ne-za-papoju-ty-za-hrystom&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Іти не за Папою, йти за Христом','uk',3492,4975,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Папа\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/RNS-482x322.jpg\" height=\"124\" width=\"186\" /></p>\n<p>Папа Франциск вирішив скасувати традицію надання почесного  титулу Джентельмена Його Святості за заслуги перед Ватиканом. Цей звичай  Папа вважає архаїчним, непотрібним і шкідливим, – написала в суботу  газета «Corriere della Sera».</p>\n<p>Відмова від титулів і пов\'язаного з ними церемоніалу та парадних фраків  – це наступний крок Папи Франциска, який прагне простоти, скромності та  не хоче, щоби Ватикан і його найближче оточення трактували як двір.</p>\n','2013-06-24 22:24:34','http://oranta.org/index.php?option=com_content&view=article&id=1290:-papa-skasovuje-tytuly-i-pryvileji&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Папа скасовує титули і привілеї','uk',3381,4864,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"Перенесення мощів св. Миколая Чудотворця з Мир до міста Барі\" src=\"http://ecumenicalcalendar.org.ua/system/avatars/145/thumb/54-mykola.JPG?1268258866\" height=\"146\" width=\"109\" /></p>\n<p>В XI столітті грецька імперія переживала важкий час. Турки  спустошували її володіння у Малій Азії, розоряли міста і села, вбивали  їх жителів, і супроводжували свої розбійні напади зневагою святих  храмів, мощів, ікон і книг. Мусульмани робили замах знищити мощі  святителя Миколая, глибоко шанованого всім християнським світом.</p>\n<p>Осквернення святинь обурювало не тільки східних, але і західних  християн. Особливо побоювалися за мощі святителя Миколая християни в  Італії, серед яких було багато греків.</p>\n','2013-05-22 13:35:49','http://oranta.org/index.php?option=com_content&view=article&id=1216:perenesennja-moshchiv-sv-mykolaja-chudotvorcja-z-myr-do-mista-bari&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Перенесення мощів св. Миколая Чудотворця з Мир до міста Барі','uk',3263,4746,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"відпуст\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/06/prochenie.jpg\" height=\"156\" width=\"208\" /></p>\n<p><strong>Церква вважає гріх смертельно небезпечним духовним  захворюванням, яке можна порівняти з такими фізичними хворобами, як рак,  СНІД, або з наркотичною залежністю.</strong></p>\n<p>Особливість цього духовного захворювання полягає в тому, що сама людина  свідомо і добровільно вводить його в себе - як і в разі використання  наркотиків. Спочатку людина відчуває задоволення, але потім незрима сила  послідовно руйнує людський організм і, зрештою, - знищує його...</p>\n','2013-06-20 11:14:48','http://oranta.org/index.php?option=com_content&view=article&id=1269:-shcho-take-vidpust-indulgencija&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Що таке відпуст (індульгенція)?','uk',3358,4841,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"знак миру\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/08/znak-pokoju.jpg\" height=\"127\" width=\"177\" /></p>\n<p><strong>В Євхаристії ми беремо участь як люди грішні та слабкі. Тому на  початку літургії визнаємо: «Сповідаюся Господу Богу всемогутньому і  вам, брати і сестри, що безмірно согрішив я думкою, словом, учинком і  недбальством».</strong></p>\n<p>Гріх зачіпає всі сфери нашої особистості та щоденного життя: учинків,  слів, думок, емоцій. Там, де поганий задум і нечистий намір, там є гріх.</p>\n','2013-08-04 18:58:59','http://oranta.org/index.php?option=com_content&view=article&id=1389:lperedate-odyn-odnomu-znak-myrur&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'«Передайте один одному знак миру»','uk',3485,4968,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"зцілення сліпого\" src=\"http://www.credo-ua.org/wp-content/uploads/2013/12/%D0%B7%D1%86%D1%96%D0%BB%D0%B5%D0%BD%D0%BD%D1%8F-%D1%81%D0%BB%D1%96%D0%BF%D0%BE%D0%B3%D0%BE.png\" height=\"144\" width=\"198\" /></p>\n<p><strong>І будьте певні, що Він вас вислухає,&nbsp;— заохотив Папа Франциск.</strong></p>\n<p>Заклик до посиленої, «набридливої» молитви, істинно християнської,  поєднаної з упевненістю, що Бог вислухає, був темою роздумів Папи.  Святіший Отець звернув увагу, що молитва моє немовби два виміри: вона  «потребує» і водночас «упевнена», що Бог свого часу та у свій спосіб  вислухає те, про що ми молимося.</p>\n','2013-12-07 00:39:47','http://oranta.org/index.php?option=com_content&view=article&id=1701:-papa-nabrydate-bogovi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Набридайте Богові!','uk',3842,5325,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" alt=\"о. Георгій Чистяков\" src=\"http://www.credo-ua.org/wp-content/uploads/2014/02/chistiakov3.jpg\" height=\"161\" width=\"200\" /></p>\n<p><strong>Коментар <a href=\"http://ru.wikipedia.org/wiki/%D0%A7%D0%B8%D1%81%D1%82%D1%8F%D0%BA%D0%BE%D0%B2,_%D0%93%D0%B5%D0%BE%D1%80%D0%B3%D0%B8%D0%B9_%D0%9F%D0%B5%D1%82%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">о.&nbsp;Георгія Чистякова</a> до слів Апостола Павла «немає влади, щоб не була від Бога» (Рим 13,&nbsp;1).</strong></p>\n<p>Якщо ми з вами ось зараз переходимо до роздумів про те, що християнин  має бути активний,&nbsp;— а ми знаємо з минулого, які активні були  подвижники, які активні були праведники, яка активна була у  висловлюванні своїй поглядів на життя суспілства мати Марія Скобцова та  чимало інших людей її часу й попередніх часів,</p>\n','2014-02-09 11:18:45','http://oranta.org/index.php?option=com_content&view=article&id=1837:-pro-posluh-vladi-i-chy-kozhna-vlada-vid-boga&catid=37:spilnota-cat&Itemid=49','P',NULL,' Про послух владі, і чи кожна влада від Бога','uk',4022,5505,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://4.bp.blogspot.com/-_YI6zu4bRBc/T89gNC996lI/AAAAAAAAADM/lDRW_2bS_Gg/s200/Martire.jpg\" height=\"200\" width=\"133\" /></p>\n<div>\n<p>У історії  деякі люди мають привілей пережити свою історичну епоху. Мученики перших  віків християнства завдяки свідченню віри є одним з прикладів  історичної пам’яті християн. Люди, які покликані свідчити віру в Христа перед переслідувачами стають на шлях праведного Авеля. «Вірою  Авель приніс Богу кращу, ніж Каїн, жертву; вона засвідчила про нього,  що він праведний, коли сам Бог дав свідоцтво на користь його дарів; нею  він, хоч і мертвий, далі промовляє». Євр. 11, 4. Віра в Господа Бога стала каменем спотикання для людей, які «проміняли славу нетлінного Бога на подобу, що зображує тлінну людину».  Рим. 1, 23. Від жертви Авеля до сьогодні віра в Господа Бога проходить  випробування. Церква шанує як мучеників свідків віри, які перед людьми  які відкидали Бога, через пролиття крові власним життям засвідчили  вірність Христу.</p>\n</div>\n','2013-06-27 22:38:56','http://oranta.org/index.php?option=com_content&view=article&id=1310:-blazhenni-novomuchenyky-ugkc-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Блаженні новомученики УГКЦ ','uk',3391,4874,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3048,200,imag.JPG\" />Парафіяльна служба милосердя «Карітас-Печерськ» провадить своє служіння у розмаїтих напрямках. Милосердне милосердя активно вливається у працю спільноти «Епіфанія» Кожен другий четвер місяця спільнота «Епіфанія» при храмі святого Миколая на Аскольдовій Могилі на чолі з отцем Анатолієм Теслею відвідує похилих та хворих парафіян. Священик відправляє молебні на оздоровлення, уділяє їм св. Тайну Сповіді та Євхаристії. Опісля відбувається дружнє спілкування про життя парафії, про особисті турботи стареньких. Їм цікаво згадати молодість, своїх колишніх друзів, місця чи країни, де вони бували протягом життя.</p>\n','2014-04-30 23:53:30','http://oranta.org/index.php?option=com_content&view=article&id=2095:2014-04-30-20-58-55&catid=25:parafija-cat&Itemid=27','P','','Діяльність парафіяльної спільноти «Епіфанія»','uk',4324,5807,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3085,200,711-20-2.jpg\" />8 років тому, нашу церкву відвідав Його Святість Іван Павло ІІ. У цей день парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі молились за прославу Святішого. Отець Анатолій Тесля відправив Святу Літургію пам\'яті Папи Павла ІІ.</p>\n<p>Візит Папи Івана Павла ІІ і молитву перед копією Чудотворної Ікони Зарваницької Божої Матері у церкві св. Миколая Чудотворця на Аскольдовій Могилі пам\'ятатиме кожен наш парафіянин і віруючий УГКЦ. З візитом Папи Івана Павлі ІІ наша церква утвердилась, зросла, укріпилась вірою і збагатилася милосердям та любов\'ю до Бога.</p>\n','2009-06-25 21:38:53','http://www.oranta.org/index.php?option=com_content&view=article&id=355:8-rokiv-vizytu-papy-ivana-pavla-ii&catid=25:parafija-cat&Itemid=27','P','','8 років візиту Папи Івана Павла ІІ','uk',1603,3086,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3100,200,1.jpg\" /></p>\n<p>До відома батьків і наших благодійників, які допомогли провести цей табір на березі Чорного  моря. Нарешті ми змогли розібратись де знаходимось. Між Одесою та гирлом Дунаю у Татарбунарському районі, є курортна зона «Расейка», що розмістилась на околицях села Приморське. Щоб потрапити на наш пляж треба пройти дерев\'яним містком через озеро Джантшейське. Це прісне озеро заросле очеретом, цей очерет росте на наших очах. Озеро наповнене різною живністю, співом птахів, та кумканням жаб. У небі часто можна побачити ключі гусей, качок, а особливе захоплення у дітей викликають рожеві пелікани. Повсюди можна зустріти качок з виводками.</p>\n','2009-06-25 21:00:24','http://www.oranta.org/index.php?option=com_content&view=article&id=354:hrystyjansky-tabir-lviry-nadiji-ljubovir-parafiji-sv-mykolaja-chudotvorcja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Християнський табір «Віри, Надії, Любові» парафії св. Миколая Чудотворця на Аскольдовій Могилі','uk',1601,3084,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3101,200,3.jpg\" /></p>\n<p>Сьогоднішня субота була наповнена мандрівками. Зранку діти разом з вихователями вирушили у подорож до районного містечка Татарбунари. Від нашого місця відпочинку за 40 км. Дорога пролягала вздовж лікувального озера Сасик. Колись це зеро було з\'єднане з морем і було одним з найбільших  місць нересту морської риби.</p>\n','2009-06-27 23:49:06','http://www.oranta.org/index.php?option=com_content&view=article&id=358:hrystyjansky-tabir-lviry-nadiji-ljubovir&catid=25:parafija-cat&Itemid=27','P','','Християнський табір «Віри, Надії, Любові»','uk',1607,3090,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3296,200,PA040128.JPG\" />У неділю 4 жовтня парафіяльна недільна школа церкви св. Миколая Чудотворця на Аскольдовій Могилі з групою дітей, які готуються до першої сповіді і торжественного причастя, разом з батьками вирушили на відпочинок і катехитичні заняття на природі. У мальовничому куточку Зазіммя на Десенці спільною Службою Божою, яку відслужив священик Ігор Онишкевич, розпочалися відпочинок і катехетичні заняття. Батьки теж мали свою зустріч яку проводила катехит Марія Щербатих. Навчання завершилось спільною трапезою, до приготування якої долучилась кожна родина, традиційною стравою відпочинку були шашлики.</p>\n<p>\n','2009-10-07 03:04:54','http://www.oranta.org/index.php?option=com_content&view=article&id=440:katehytychni-vchennja-na-pryrodi&catid=25:parafija-cat&Itemid=27','P','','Катехитичні вчення на природі','uk',1827,3310,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3303,200,006.jpg\" />11 жовтня в церкві Покрови св. Богородиці в музеї Пирогова, святкували 10 ювілей відновлення Богослужінь у дерев\'яній греко-католицькій церкві. Божественну Літургію відправив владика Богдан Дзюрах у спів служінні зі священиками  Київського Деканату. У своїй проповіді Владика Богдан розказав про історію празника покрови, коли Пресвята Богородиця захистила Царгород від знищення, Вона свої покровом, покрила молільників і захистила місто від нападу чужинців.</p>\n','2009-10-12 00:02:43','http://www.oranta.org/index.php?option=com_content&view=article&id=441:svjato-pokrovy&catid=25:parafija-cat&Itemid=27','P','','Свято Покрови','uk',1829,3312,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3322,200,DSCN2435.JPG\" />Шановні читачі, парафія святого Миколая, що на Аскольдовій Могилі, є надзвичайно багатою на співочі голоси. Сьогодні ми хочемо познайомити Вас з чудовою, глибоко віруючою людиною, керівником жіночого ансамблю «Аніма» пані Надією Кучерявою. Адже її щира усмішка є прикрасою церковного хору та й усієї парафії.</p>\n<p><strong>Скажіть, будь ласка, коли Ви вперше познайомилися з церквою святого Миколая, що на Аскольдовій Могилі?</strong></p>\n<strong></strong>\n<p>На запрошення пані Лесі Потіцької (керівника дитячого гурту „Коралі\" у м. Вишгород та „Гагілка\" дитячого ансамблю з м. Києві, в якому співала донька Катруся) ми навесні 1995 р. показували невеличку концертну програму перед парафіянами церкви св. Миколая Чудотворця на Аскольдовій могилі. Пам\'ятаю сестру Хризанту, що привітно нас зустріла та о. Ігоря, котрий запрошував до церкви та до наступних виступів.</p>\n','2009-10-19 22:48:18','http://www.oranta.org/index.php?option=com_content&view=article&id=444:nasha-spivocha-parafija&catid=25:parafija-cat&Itemid=27','P','','Наша співоча парафія','uk',1845,3328,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3524,200,CRW_7040.jpg\" />У Томину неділю поминання спочилих. Цей день є пам\'ятним для  греко-католиків. За благословенням владики Софрона Дмитерка (світла і  вічна йому пам\'ять) стало традицією, на місцевому цвинтарі села  Чапаївка, у цю неділю, на місці першого поховання поминати Блаженного  Йосафата Коциловського єпископа Перемиcького.</p>\n','2013-05-15 11:19:00','http://oranta.org/index.php?option=com_content&view=article&id=1203:nash-vladyka&catid=25:parafija-cat&Itemid=27','P',NULL,'Наш Владика','uk',3243,4726,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3543,200,IMG_2116.JPG\" />16 травня 2010 р.Б. відбувся ювілейний концерт церовного хору «Аскольд» з нагоди святкування десятиліття колективу. Хор почав засновуватись наприкінці 90-х років паном Володимиром Гриньківим із числа студентів культурно-мистецьких вузів міста Києва. Прагнення духовного оновлення, потреба відчуття Божої присутності, бажання прослави Всевишнього піснеспівами –все це об’єднало молодих музикантів під девізом «Співати на славу Божу!» Регент хору, п. Володимир, наголошує, що поряд із виконавською майстерністю все ж первинною у цій справі має бути любов до Бога і до церкви.</p>\n','2010-05-19 02:18:57','http://oranta.org/index.php?option=com_content&view=article&id=530:cerkovny-hor-laskoldr&catid=25:parafija-cat&Itemid=27','P','','Церковний хор «Аскольд»','uk',2061,3544,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3550,200,12.JPG\" />На 23 травня 2010 року припадає величний та прекрасний празник Святої П’ятдесятниці. Так ми його називаємо, адже саме стільки днів пройшло після світлого празника Пасхи.&nbsp; Це свято також має назву Зшестя Святого Духа, адже після 50-ти днів по Світлому Воскресінню Господа нашого Ісуса Христа на апостолів зійшов Святий Дух. Він освітив, просвітив і зберіг на правдивій дорозі апостолів. Цей день християни називають також Днем Святої Тройці. З часу сходження Святого Духа починає діяти Христова Церква.</p>\n','2010-05-26 02:28:57','http://oranta.org/index.php?option=com_content&view=article&id=534:praznyk-svjatoji-pjatdesjatnyci&catid=25:parafija-cat&Itemid=27','P','','Празник Святої П’ятдесятниці','uk',2069,3552,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3556,200,IMG_4683.JPG\" />30 травня з нагоди&nbsp; дня захисту дітей, у храмі Миколи Доброго відбулась презентація Руху за Життя. Життя – це дар нам його подаровано і за нього ми можемо тільки подякувати і прийняти, часто ми цього не розуміємо, тому це стало причиною появи абортів та евтаназії. Саме тому основним завданням руху за життя є &nbsp;право на життя, право бути народженим і вмерти природною смертю.</p>\n','2010-06-02 02:01:08','http://oranta.org/index.php?option=com_content&view=article&id=537:ruh-za-zhyttja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Рух за життя','uk',2075,3558,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3561,200,1.jpg\" />П’ятого червня, у суботу, діти з Аскольдової могили мали змогу вирушити у подорож за межі нашої столиці. Поїздку організував парох церкви святого Миколая отець Ігор Онишкевич.</p>\n<p>О дванадцятій годині з Аскольдової могили вирушив мікроавтобус, який прямував у місто Васильків. Разом з дітьми також їхав ї екскурсовод – пані Тетяна, яка докладно розповідала про кожну споруду, церкву, повз яку ми &nbsp;проїжджали. Свій відпочинок наша спільнота розпочала з молитви, що є певним обов’язком у кожних подібних поїздках.</p>\n','2010-06-07 13:01:29','http://oranta.org/index.php?option=com_content&view=article&id=544:subotnja-podorozh-dite-parohiji-svjatogo-mykolaja&catid=25:parafija-cat&Itemid=27','P','','Суботня подорож дітей парохії святого Миколая','uk',2083,3566,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3573,200,IMG_6790.JPG\" />Літо пора відпочинку коли хочеться&nbsp; вирватись за місто на природу, подальше від міського гамору. Так молодіжна спільнота Миколая Чудотворця вирішила у неділю 20 червня організувати пікнік у етнографічному музеї Пирогово.</p>\n','2010-06-28 14:21:20','http://oranta.org/index.php?option=com_content&view=article&id=547:molodizhny-piknik&catid=25:parafija-cat&Itemid=27','P','','Молодіжний пікнік','uk',2091,3574,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3590,200,IMG_1796.JPG\" />У часі всеукраїнської прощі до Зарваниці 18 липня 2010 року Божого парафіяльна родина Ігоря, Ольги і дочки Дарії церкви св.. Миколая Чудотворця на Аскольдовій Могилі, зробили родинний дарунок - точну копію Зарваницької Богородиці вилиту із бронзи, покриту сріблом із позолоченими коронами.</p>\n','2010-07-23 00:28:17','http://oranta.org/index.php?option=com_content&view=article&id=561:darunok-zarvanycki-bozhi-materi&catid=25:parafija-cat&Itemid=27','P','','Дарунок Зарваницькій Божій Матері','uk',2109,3592,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3603,200,DSC_0171.JPG\" />Поки в столиці від надмірної спеки вже листопадять каштани, у Карпатах можна радіти буйству соковитої зелені і свіжій прохолоді, особливо після грози. Другу половину нашого таборового часу не оминули традиційні серпневі дощі, але як співав колись на Сихові папа Іван Павло: «Дощик паде, діти ростуть».</p>\n','2010-08-11 17:56:28','http://oranta.org/index.php?option=com_content&view=article&id=567:doshchyk-pade-dity-rostut&catid=25:parafija-cat&Itemid=27','P','','Дощик паде, діти ростуть.','uk',2121,3604,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3609,200,1.JPG\" />28 серпня Церква святкує велике свято – Успіння Пресвятої Богородиці.</p>\n<p>Цього дня віяло прохолодою, хмари заступили усе небо, крапав невеликий дощ, пожовклі листочки поволі падали на землю, хоча ще не прийшов «листопад». &nbsp;Але не дивлячись на пахмурну погоду, багато вірних прийшло до Аскольдової могили, щоб помолитися та взяти участь у Святій Літургії. Погода навіювала нам певний смуток, адже у цей день увійшла у вічність Пресвята Богородиця. Однак Свята Церква та й усі ми християни повинні розглядати цю подію як свято радості, адже тіло Пречистої Богородиці по смерті Діва не зазнало зотління, але разом з душею було взято на небо.</p>\n','2010-08-29 20:42:31','http://oranta.org/index.php?option=com_content&view=article&id=572:uspinnja-presvjatoji-bogorodyci&catid=25:parafija-cat&Itemid=27','P','','Успіння Пресвятої Богородиці','uk',2128,3611,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3620,200,125.jpg\" />Щороку у вересні місяці відбувається молодіжна проща до Вишгорода. Цього року проща збільшила свої масштаби, адже крім пішої прощі була також і велопроща. Велопроща&nbsp; брала свій початок у місті Ірпінь і 20 велопрочан мали подолати <st1:metricconverter productid=\"40 км\">40 км</st1:metricconverter>. Піша проща брала свій початок із міста Києва і всі прочани за задумом організаторів прощі мали зустрітись у&nbsp; місті Вишгороді.</p>\n<p>Проща до Вишгорода – це &nbsp;моя перша піша проща (у Вишгород також). З перших хвилин прощі &nbsp;відчувалася тепла, дружня, радісна атмосфера. Хоч &nbsp; дорога неблизька, та молитвою і піснею на вустах подолали ми її легко.</p>\n','2010-09-13 18:46:53','http://oranta.org/index.php?option=com_content&view=article&id=577:molodizhna-proshcha-do-vyshgoroda&catid=25:parafija-cat&Itemid=27','P','','Молодіжна проща до Вишгорода','uk',2139,3622,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3682,200,5.jpg\" width=\"152\" height=\"115\" />Цієї неділі, тридцятого жовтня, на Аскольдовій могилі панувала затишна, радісна атмосфера. У цей день парафіяни завітали до церкви, щоб прославити у Святій Літургії Бога та водночас привітати нашого пароха отця Ігоря Онишкевича з двадцятиліттям ієрейських свячень.</p>\n','2010-11-01 22:39:15','http://oranta.org/index.php?option=com_content&view=article&id=611:20-littja-ijereskyh-svjachen-otcja-igorja-onyshkevycha&catid=25:parafija-cat&Itemid=27','P',NULL,'20 - ліття ієрейських свячень отця Ігоря Онишкевича','uk',2202,3685,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3696,200,DSC_1443.jpg\" />Як відомо, в Києві днями відбувся II з’їзд мирян УГКЦ під гаслом «Моя участь в житті парафії, Церкви та держави», покликаний привернути увагу мирян та парохів до розбудови парафій. Близько трьохсот учасників з усіх єпархій УГКЦ, а також представники мирянських, парафіяльних та поза парафіяльних об\'єднань, серед яких: спільнота міжнародного християнського руху \"Віра і Світло\", \"Матері в молитві\", молодіжні об\'єднання УМХ, рух \"За життя\" та інші, впродовж двох днів мали нагоду послухати доповідачів, поділитися власним досвідом, попрацювати у групах для пошуку шляхів розв’язку наболілих питань.</p>\n','2010-11-15 22:31:03','http://oranta.org/index.php?option=com_content&view=article&id=620:kolektyv-lanimar-podaruvav-dushevny-spiv-dlja-uchasnykiv-ii-zjizdu-myrjan&catid=25:parafija-cat&Itemid=27','P','','Колектив «Аніма» подарував душевний спів для учасників ІІ з’їзду мирян','uk',2214,3697,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3736,200,IMG_1012.JPG\" />21 грудня 2010 р. у першу міську дитячу клінічну лікарню завітав святий Миколай, щоб привітати маленьких пацієнтів зі наступаючими Різдвяними святами та побажати їм швидкого одужання. Маленьке театралізоване дійство розгорнулося на четвертому поверсі у дитячому кутку біля каплички св. Агапіта Печерського. Святий разом із своїми помічниками – двома ангелятками, україночкою та християнською молоддю з Аскольдової могили співали колядки, розказували вірші та роздавали подарунки всім присутнім.</p>\n','2010-12-22 21:09:23','http://oranta.org/index.php?option=com_content&view=article&id=643:svjato-mykolaja-u-dytjachi-likarni&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Свято Миколая у дитячій лікарні','uk',2256,3739,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3737,200,1.jpg\" />18 грудня відбувся фестиваль конкурс Аскольдів Глас, організований парафіянами церкви святого Миколая, що на Аскольдовій могилі. В цей день також святкувала свій ювілей парафія святого Миколая, якій виповнилось 20 років. З цієї нагоди спілкуємось з парохом церкви святого Миколая, що на Аскольдовій могилі отцем Ігорем Онишкевичем.</p>\n','2010-12-19 18:00:19','http://oranta.org/index.php?option=com_content&view=article&id=640:intervju-z-parohom-cerkvy-svjatogo-mykolaja-otcem-igorem-onyshkevychem-shchodo-svjatkuvannja-20-richchja-parafiji&catid=25:parafija-cat&Itemid=27','P',NULL,'Інтерв’ю з парохом церкви святого Миколая отцем Ігорем Онишкевичем щодо святкування 20 - річчя парафії','uk',2254,3737,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3742,200,611.jpg\" />19 грудня – великий день для християн. Цього дня ми святкуємо день святого Миколая. За церковним переданням святий Миколай ходив від хати до хати обдаровуючи бідних дітей подарунками. За це Господь полюбив Миколая і запитав про його найбільше бажання, адже за добрі вчинки хотів віддячитись Господь Миколі. Тоді Миколай висловив своє найбільше бажання: після смерті щороку сходити на землю і дарувати дітям подарунки. Господь вислухав Миколу і виконав його бажання.</p>\n','2010-12-22 22:38:49','http://oranta.org/index.php?option=com_content&view=article&id=647:svjaty-mykola-zavitav-i-na-askoldovu-mogylu&catid=25:parafija-cat&Itemid=27','P',NULL,'Святий Миколай завітав і на Аскольдову Могилу','uk',2261,3744,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3761,200,00186.jpg\" width=\"111\" height=\"153\" />Коротка історія</p>\n<p>Протягом трьох перших сторіч християни не мали окремого празника Христового Різдва. У той час був тільки празник Богоявлення, яке святкували 6 січня. Цей празник поєднував у собі і Христове Різдво і Хрещення Ісуса в ріці Йордані. Грецьке слово \"Епіфанія\" чи \"Теофанія\", що значить \"З\'явління\" чи \"Богоявлення\", у перших віках християнства означало не тільки появу Ісуса Христа при його хрещенні, але і його появу на землі, тобто його народження.</p>\n<p>Про давню злуку цих двох празників і їхнє святкування в один день свідчить тотожний уклад у нашому богослуженні. Схема богослужби цих празників одна й та сама. Обидва мають навечір\'я з постом, Літургію святого Василія Великого з вечірнею у навечір\'я, царські часи, велике повечер\'я зі співом \"З нами Бог\", на Літургії \"Єлици\" і т. п. Різниця в їхньому богослуженні полягає тільки у змісті богослужби.</p>\n','2011-01-05 09:07:03','http://oranta.org/index.php?option=com_content&view=article&id=652:rizdvo-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Різдво Господа Бога і Спаса нашого Ісуса Христа','uk',2280,3763,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3768,200,1020_huzar_01.jpg\" height=\"155\" width=\"116\" />«Нам інколи здається, дорогі у Христі, що в нашому житті все діється принагідно, без якогось плану, без якоїсь цілі. Так воно не є. Бо як Господь Бог готував світ до приходу свого Єдинородного Сина, цей Божий план, який був від самих початків, продовжується і сьогодні». Про це під час святкової Різдвяної Літургії у храмі Святого Василія Великого у Києві сказав Глава Української Греко-католицької Церкви Блаженніший Любомир.</p>\n<p>За словами Предстоятеля УГКЦ, «ми інколи дивуємося, чому Господь Бог допускає певні речі, ми вповні не розуміємо цього плану. Але нема найменшого сумніву, що все, що діється, діється на нашу користь».</p>\n','2011-01-09 15:42:50','http://oranta.org/index.php?option=com_content&view=article&id=658:lnema-namenshogo-sumnivu-shcho-vse-shcho-dijetsja-dijetsja-na-nashu-korystr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Нема найменшого сумніву, що все, що діється, діється на нашу користь» ','uk',2286,3769,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3784,200,SIZO1.jpg\" />21 січня 2011 року за ініціативи та участі отця-настоятеля храму святого Миколая Чудотворця на Аскольдовій Могилі та волонтерів з числа парафіян відбувся візит милосердя до жінок, що перебувають у слідчому ізоляторі Лук\'янівської в\'язниці у місті Києві. Активно  допомагав організації візиту та безпосередньо брав участь у заході  отець Костянтин Пантелей, що на регулярній основі провадить тюремну службу в Лук\'янівській в\'язниці</p>\n','2011-01-23 16:08:38','http://oranta.org/index.php?option=com_content&view=article&id=665:askoldova-koljada-v-lukjanivski-vjaznyci&catid=25:parafija-cat&Itemid=27','P','','Аскольдова коляда в Лук’янівській в’язниці','uk',2301,3784,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3811,200,3c88ac7f06.jpg\" />Коротка історія</p>\n<p>Уже апостоли й перша християнська громада почали заміняти день   жидівської Пасхи днем поминання Христових мук і смерті. Роковини   Христової смерті були для них дуже сумним днем. Тож, щоб його належно   відсвяткувати, вони зберігали піст у цей день. І так первісна   християнська Пасха почала існувати у вигляді посту. Це була хресна   Пасха. За свідченням святого Іринея (к. 125 — к. 203), про яке говорить   історик Церкви Євсевій (к. 260 — к. 340), перший зародок нинішнього   40-денного посту в той час обмежу­вався одним-двома днями посту, який не   вважався передпасхальним, а таки самою Пасхою.</p>\n','2011-02-28 09:56:18','http://oranta.org/index.php?option=com_content&view=article&id=679:-7-bereznja-2011-roku-pochatok-velykogo-postu&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' 7 березня 2011 року - Початок Великого посту','uk',2329,3812,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3816,200,ArticleImages_34811_shevchenko_svichka_w.jpg\" />Господь дарував українському народові пророка, який став для нього ідейним, моральним та духовним дороговказом. Парадокс полягає у тому, що впродовж багатьох десятиліть «канонізуючи» Тараса Шевченка, мало що було зроблено для пізнання причин його істинної слави та величі.</p>\n<p>До речі, викривлене сприйняття, а той відверте фальшування місця і ролі Т. Шевченка у суспільному та духовному житті нації розпочалося аж ніяк не в радянську добу, як вважає чимало нинішніх апологетів Кобзаря. Відомий поет і філософ Євген Маланюк справедливо зазначав, що біля витоків першого міфу, який спотворив образ справжнього Шевченка у XIX сторіччі стояли українські «народники».</p>\n','2011-03-05 10:36:43','http://oranta.org/index.php?option=com_content&view=article&id=681:taras-shevchenko-mizh-dvoma-mifamy&catid=37:spilnota-cat&Itemid=49','P',NULL,'Тарас Шевченко: між двома міфами','uk',2334,3817,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3842,200,50106_100001655704757_1342439_n.jpg\" />Сьогодні уся наша Церква отримала знак з неба, бо інакше годі  пояснити вибір, який є несподіваним для багатьох, мабуть, передусім для  новообраного Блаженнішого Патріарха Святослава (Шевчука). Вибір Синоду  відважний і далекоглядний. На підставі спостережень під час дня молитви і  застанови для єпископів, під час якого я мав честь проповідувати, а  також на підставі перших відгуків архиєреїв після завершення Синоду, усе  вказує на те, що вибір відбувався в любові, мирі, радості, лагідності  та вірності. Ці риси – це ознаки плодів Святого Духа (Гл. 5, 22-23), і  вони були для владик та повинні бути для нас усіх основним критерієм для  прийняття важливих рішень. Там, де панують ці чесноти у людських  стосунках, там панує і провадить Святий Дух. Виглядає, що саме так було у  цих днях сопричастя і молитви архиєреїв, під час яких відбувся цей  знаменний вибір.</p>\n','2011-03-25 20:11:15','http://oranta.org/index.php?option=com_content&view=article&id=695:uku-vitaje-novoobranogo-glavu-ugkc&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'УКУ вітає новообраного Главу УГКЦ','uk',2360,3843,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3848,200,IMG_9590.JPG\" />У Києві в соборі Воскресіння Христового відбулася інтронізація новообраного предстоятеля Української Греко-Католицької Церкви Патріарха Святослава (Шевчука). У церемонії інтронізації взяли участь представники всіх єпархій та екзархатів УГКЦ, державної та місцевої влади, громадських організацій, різних конфесій і релігійних організацій. Таким чином завершився тижневий “марафон” з обрання глави однієї з найбільших церков Київської Традиції.\n','2011-03-28 08:54:58','http://oranta.org/index.php?option=com_content&view=article&id=702:patriarh-svjatoslav-pochatok-sluzhinnja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Патріарх Святослав: початок служіння','uk',2366,3849,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3884,200,CRW_9956.JPG\" />Що кличе нас у цю «дорогу»?..</p>\n<p>Біль, страждання, розчарування,.. невже то кличуть наші гріхи?..</p>\n<p>Любов Божа кличе нас. Його Всевишня Любов прощення, терпіння, смирення, покаяння. Так, це вона кличе нас, щоб звільнити від важких хрестів…</p>\n<p>Та не завше ми чуємо той клич Божої Любові, не завше відгукуємось, не завше йдемо…</p>\n','2011-04-12 21:03:10','http://oranta.org/index.php?option=com_content&view=article&id=723:hresna-doroga-u-kozhnogo-svoja&catid=25:parafija-cat&Itemid=27','P','','Хресна дорога у кожного своя','uk',2402,3885,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3892,200,P1010013.JPG\" />О.Віктор Яценко священик української православної Церкви,&nbsp; був вибраний головою правління української Міжконфесійної Християнської Місії «Духовна та благодійна опіка в місцях позбавлення волі». Місія, утворена в 2001р. з ініціативи капеланів і керівництва кримінально-виконавчій служби,&nbsp; є представництвом основної церкви України в справах релігійного служіння у в’язницях,&nbsp; для координації діянь капеланів і співпраці з Державною пенітенціарної службою.</p>\n<p>Від 2007 року головою правління місії протягом двох каденцій був представник римо-католицької церкви з м. Рівне отець Григорій Драус. Досягненням останніх років було проведення ряду професійних курсів для в’язничних священнослужителів з різних церков. Курси були проведені&nbsp; Білоцерківським училищем підготовки персоналу кримінально-виконавчої служби.</p>\n','2011-04-22 09:48:25','http://oranta.org/index.php?option=com_content&view=article&id=733:novy-golovny-vjaznychny-kapelan-ukrajiny&catid=37:spilnota-cat&Itemid=49','P',NULL,'Новий головний в’язничний капелан України','uk',2422,3905,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3911,200,P4230252.JPG\" />23 квітня 2011 року Божого, в такий теплий та сонячний день, для Дітей Божого Милосердя, після Служби Божої, двоє вихователів Дмитро та Анна, відправились на Лісовий ринок, де зробили закупки до святкового столу, в день світлого празника Христового Воскресіння. Ангельський хліб – Пасху, яка є символом Воскресіння, привезли пан Євгеній та Оксана.</p>\n<p>Плетених кошиків, як і бажаючих їх придбати, було чимало і вибір великий. Нашу увагу привернули кошики плетені зі світлої лози, які продавали пан Володимир та його брат Іван. Потім ми направились в м’ясний павільйон, де купили домашню ковбасу, що є символом душевної радості, яку власноруч готує пані Галина зі своїми помічниками. Про неї нам розповіла парафіянка пані Людмила.</p>\n','2011-04-26 22:58:43','http://oranta.org/index.php?option=com_content&view=article&id=740:pashalny-koshyk-dlja-dite-bozhogo-myloserdja&catid=25:parafija-cat&Itemid=27','P','','Пасхальний кошик для Дітей божого Милосердя','uk',2430,3913,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3920,200,10.JPG\" />«Віруєш, Томо, бо Мене побачив; щасливі ті, які не бачивши, увірували.» (Євангеліє від Йн. 20)</p>\n<p>Ці слова з Євангелія Блаженніший Любомир вселив сьогодні у наші серця. Його велична постать духівника наставника щоразу по доброму добавляє трему в наші душі, щоразу додає нам твердості віри і чистоти сердець. Блаженніший Любомир вкотре наголосив нам, що місце нашої скромної церковці – це ґрунтовні підвалини Христової віри: твердої віри поборника князя Аскольда, безмежної любові Миколая Мирлікійського, світлого милосердя Папи Івана Павла ІІ, місце зародку українського християнства а від недавна чисте джерело Христової віри для наших спраглих душ.</p>\n','2011-05-01 22:21:18','http://oranta.org/index.php?option=com_content&view=article&id=743:z-voskresinnjam-hrysta-voskresajemo-i-my&catid=25:parafija-cat&Itemid=27','P','','З воскресінням Христа воскресаємо і ми','uk',2438,3921,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3938,200,IMG_4706.JPG\" />15 травня, &nbsp;як і кожного року, першопричасники урочисто приймали святу тайну Євхаристію. Кожна дівчинка була вдягнена в красиве платтячко, а хлопчики в костюми. &nbsp;Перед початком Богослужіння парох церкви святого Миколая отець Ігор Онишкевич разом з дітьми та всіма парафіянами привселюдно відреклися сатани і відкрили своє щире серце до Бога, щоб разом з Ним іти впевнено життєвою дорогою.</p>\n','2011-05-16 15:14:49','http://oranta.org/index.php?option=com_content&view=article&id=745:my-dity-bozhi-znovu-prystupajemo-do-svjatogo-prychastja&catid=25:parafija-cat&Itemid=27','P',NULL,'Ми, діти Божі, знову приступаємо до Святого причастя','uk',2458,3941,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3942,200,know_your_devotion_065.jpg\" />Щопонеділка о сьомій годині вечора в церкві Святого Миколая Чудотворця на Аскольдовій Могилі в Києві відбувається акафіст Теодору Ромжі з благословеннями мощами. У щирій молитві українські християни згадують про єпископа-мученика Греко-Католицької Церкви, якого Святійший Отець Іван Павло ІІ беатифікував під час свого пастирського візиту в Україну.</p>\n<p>На жаль, треба визнати, що ім\'я і духовний подвиг владики Теодора ще маловідомі широкому загалові. Тим часом його постать, з одного боку, віддзеркалює драматичну, трагічну і водночас героїчну долю греко-католиків в Україні, а з другого — є безапеляційним вироком безбожному радянському режимові, який вчинив страшний злочин супроти єпископа Теодора Ромжі, Церкви до якої він належав, українського народу та всіх християн.</p>\n','2011-05-25 20:50:23','http://oranta.org/index.php?option=com_content&view=article&id=748:teodor-romzha-jak-vyrok-bezbozhnomu-rezhymu&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Теодор Ромжа як вирок безбожному режиму','uk',2465,3948,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3958,200,4.jpg\" />Четвертого червня діти з Аскольдової могили поїхали в славне місце – козацьку фортецю Батурин. Поїздка була організована парохом церкви святого Миколая отцем Ігорем Онишкевичем. Чисельна група з тридцяти чотирьох осіб поїхала пізнавати козацькі шляхи.</p>\n<p>До Батурина потрібно було їхати близько трьох годин. Але за цей час в автобусі діти разом з батьками мали змогу молитися, декламувати Шевченка, спілкуватися.</p>\n','2011-06-05 11:20:19','http://oranta.org/index.php?option=com_content&view=article&id=754:ditvora-z-askoldovoji-mogyly-piznaje-kozacki-shljahy&catid=25:parafija-cat&Itemid=27','P','','Дітвора з Аскольдової Могили пізнає козацькі шляхи…','uk',2476,3959,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3987,200,CRW_0976.JPG\" />Цими днями українці святкують 10-ту річницю пасторального візиту Блаженного Папи Івана Павла ІІ. У Києві святкування розпочалися ще 14 липня відкриттям виставки «Папа, Україна, Я». За словами Владики Йосипа Міляна, в основному святкування проходить у двох містах, саме де перебував Папа: Київ та Львів. У кожного міста своя програма святкувань. За організацію у Києві відповідає сам Владика, тому і бажає, щоб програма була повною, насиченою, духовною.</p>\n','2011-06-24 22:26:23','http://oranta.org/index.php?option=com_content&view=article&id=765:lpapa-avtorytet-vysokoji-moralir-blazhennishy-ljubomyr-guzar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Папа – авторитет високої моралі» - Блаженнійший Любомир Гузар','uk',2506,3989,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,3996,200,media12872701.JPG\" />З Благословення Апостольського Екзарха Німеччини і Скандинавії Кир Петра Крика 2 липня українські прочани вже вшосте вклонялися мощам блаженної Едігни. Уже здалеку, прямуючи до баварського села Пух (Puch), можна побачити рожеву дзвіницю католицької церкви Святого Севастіана та Блаженної Едігни. Як завжди група вірних з Мюнхенської парафії частину шляху до місця прощі пройшла пішки через поля сільськими дорогами, звертаючись до Блаженної з проханнями та щирими молитвами.</p>\n','2011-07-04 12:46:00','http://oranta.org/index.php?option=com_content&view=article&id=767:vshoste-do-blazhennoji-edigny&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Вшосте до Блаженної Едігни','uk',2515,3998,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4053,200,golodomor.jpg\" />Пригадую в дитинстві нашу увагу, зосередженість, пам‘ять тренували грою «знайди 10 відмінностей»: у двох, наче б то схожих малюнках необхідно було знайти відмінності. Подобалась вона мені тоді, сьогодні не дуже… в подіях 5-ти денної давнини відмінностей далеко не 10 а декілька сотень. Останніми днями ЗМІ просто кишать інформацією про те, що на смітнику знайдені меморіальні «хрести-таблички» жертвам голодомору з калинового гаю на схилах Дніпра. Кишать інформацією постфактум і не більше. Жодних свідків події, жодних відповідей від влади чи інших міських служб. Ніде, нічого, ані чик-чи-рик. Запити нардепів, звернення, все наче вода у суху землю. Складається враження, що все так і було, радше нічого не було взагалі.</p>\n','2011-09-14 21:32:16','http://oranta.org/index.php?option=com_content&view=article&id=789:znady-10-vidminnoste-abo-jaka-cina-nashoji-uvazhnoji-svidomosti&catid=37:spilnota-cat&Itemid=49','P',NULL,'Знайди 10 відмінностей, або: яка ціна нашої уважної свідомості?','uk',2571,4054,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,IMG_2794.JPG\" />20 листопада на Аскольдовій Могилі вірні парафіяни просили заступництва у св. Йосафати Гордашевської.</p>\n<p>Народжена у Львові, Михайлина (сестра Йосафата) Гордашевська стала першою черницею згромадження Сестер Служебниць Непорочної Діви Марії. Під її керівництвом семеро перших послушниць розпочали свою духовну формацію. Саме сестра Йосафата започаткувала служіння в галузі освіти й охорони здоров\'я, також відповідала та курувала відкриття нових місій.</p>\n','2011-11-24 21:46:40','http://oranta.org/index.php?option=com_content&view=article&id=806:pamjati-osafaty-gordashevskoji&catid=25:parafija-cat&Itemid=27','P','','Пам’яті Йосафати Гордашевської','uk',2611,4094,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4107,200,CRW_3090.JPG\" /></p>\n<p>17 грудня 2011 р. у затишній залі НПУ ім. М.Драгоманова відбувся VIІІ фестиваль-конкурс духовної пісні «Аскольдів глас». Пісенне свято розпочалося із привітального слова ректора Київської Трьохсвятительської духовної семінарії УГКЦ – о. Петра Жука.</p>\n','2011-12-22 10:48:06','http://oranta.org/index.php?option=com_content&view=article&id=814:rezultaty-viii-festyvalju-konkursu-duhovnoji-pisni-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27','P',NULL,'Результати VIІІ фестивалю-конкурсу духовної пісні «Аскольдів глас»','uk',2625,4108,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4126,200,55.jpg\" />15 січня у столичному Будинку художника відбулася Українська Коляда.</p>\n<p>У цьому мистецькому Різдвяному форумі взяли участь Тарас Чубай, Росова та рок-гурт «Дивні».</p>\n<p>Українську Коляду відкрив львівський гурт «Дивні» у супроводі чоловічого хору та оркестру. Цей колектив з великим мистецьким хистом виконав сучасні авторські колядки.</p>\n<p>Далі на сцену запросили Росаву, яка представила глядачам широку мистецьку палітру колядок, щедрівок та ліричних пісень про зиму в оригінальній інтерпретації. До речі, Росава у свій час зайняла перше місце та приз глядацьких симпатій на фестивалі «Червона Рута». <br />Гучними оплесками зустрічали глядачі львівського композитора, співака та рок-музикакнта, заслуженого артиста України, лідера гурту «Плач Єремії» Тараса Чубая.</p>\n','2012-01-16 22:09:57','http://oranta.org/index.php?option=com_content&view=article&id=821:ukrajinska-koljada&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Українська Коляда','uk',2644,4127,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4128,200,IMG_0782_resize.JPG\" />За давньою традицією та церковним календарем Новий Рік зустрічали в ніч з 13 на 14 січня. Саме тому молодіжна спільнота церкви св. Миколая Чудотворця вирішила, що це хороша нагода, щоб всім зібратись після різдвяних канікул. На свята кожен їхав до себе додому, щоб у сімейному колі зустріти Різдво і тому не було змоги разом&nbsp; зібратись поколядувати.</p>\n','2012-01-20 11:50:31','http://oranta.org/index.php?option=com_content&view=article&id=823:zustrich-starogo-novogo-roku&catid=25:parafija-cat&Itemid=27','P',NULL,'Зустріч Старого Нового Року','uk',2647,4130,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4138,200,CRW_4474.JPG\" />Юність – роки становлення особистості, юність – роки безтурботного переходу від дитинства до зрілості. Саме так сьогодні ми сприймаємо ЮНІСТЬ.</p>\n<p>Як сприймали свою юність студенти, які дрібкою солі стали на захист своєї держави проти безжалісної навали? Тоді вони не знали і не прагнули через століття стати героями. Тоді, вони добре усвідомлювали, що йдуть на вірну смерть. Та їхня любов до брата свого, до народу свого, до держави своєї була вище над усе, вища за смерть – «НАЙБІЛЬША ЛЮБОВ – ЖИТТЯ ПОКЛАСТИ ЗА ДРУЗІВ».<br />Через століття для кожного українця 33 юнака – ГЕРОЇ.</p>\n','2012-01-29 23:23:50','http://oranta.org/index.php?option=com_content&view=article&id=826:vony-nazavshe-zalyshatsja-junymy&catid=28:tema-nomera-cat&Itemid=2','P','','Вони назавше залишаться юними','uk',2656,4139,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4140,200,CRW_4667.JPG\" />Парафіяльна служба милосердя «Карітас-Печерськ» церкви св. Миколая Чудотворця на Аскольдовій Могилі продовжує свою опіку над жінками, які перебувають у закладах позбавлення волі. Цього разу ми провідали жінок в уманській виправної колонії №129. Нас було п’ятеро: отець Ігор Онишкевич, настоятель храму, поет і співак Зеновій Филипчук, волонтери пані Ганна Мушинська, що опікується разом з пані Маркою Бліхар жінками в неволі, Любов Михайлюк, та Орина Маслова.</p>\n','2012-01-31 00:01:07','http://oranta.org/index.php?option=com_content&view=article&id=828:i-po-to-bik-parkanu-je-zhyttja&catid=25:parafija-cat&Itemid=27','P','','І по той бік паркану є життя','uk',2658,4141,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4146,200,51.jpg\" />Бог шукає кожну людину незалежно від обставин, в яких вона опинилася. Божа справедливість засуджує зло, але людину не відкидає. Милосердний Отець завжди чекає на повернення Блудного Сина. Тому в’язниця потребує вісників Його милосердя. Бог воплотився, щоб Світло сягнуло навіть найтемніших закутків людського буття.</p>\n<p>Кожне суспільство доручає законній державній владі реагувати на злочинність за допомогою правоохоронних служб та системи правосуддя. З огляду кримінальні переступи ми, ради відносного комфорту та безпеки, приймаємо необхідність існування покарання ув\'язненням.</p>\n','2012-02-12 01:09:20','http://oranta.org/index.php?option=com_content&view=article&id=830:z-nagody-dnja-osoblyvoji-pastyrskoji-uvagy-ugkc-do-vjaznychnogo-sluzhinnja-v-nedilju-pro-bludnogo-syna-cerkva-molytsja-za-vjazniv&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'З нагоди Дня особливої пастирської уваги УГКЦ до в’язничного служіння в Неділю про Блудного Сина церква молиться за в’язнів.','uk',2664,4147,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4149,200,888.JPG\" />Кожного року настоятель греко-католицької церкви святого Миколая, що на Аскольдовій Могилі, отець Ігор Онишкевич організовує літні&nbsp; відпочинки для дітей парафії у Карпатах та на морі. Також відбуваються зимові подорожі у Карпати, де діти залюбки катаються на лижах. Ці проекти здійснюються з 1995 року. Важко повірити, але вже17 років поспіль отець Ігор Онишкевич з Божою допомогою знаходить жертводавців та організовує ці незабутні поїздки. Зауважмо, для багатьох дітей саме наша парафія відкрила море.</p>\n','2012-02-15 23:31:31','http://oranta.org/index.php?option=com_content&view=article&id=832:kudy-litom-pojidut-vidpochyvaty-dity-z-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27','P',NULL,'Куди літом поїдуть відпочивати діти з Аскольдової Могили?','uk',2668,4151,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4177,200,426323_300_index.jpg\" />Вістка про смерть Голови ОУН, останнього президента Української Народної Республіки на вигнанні, довголітнього парафіянина нашої Церкви Миколи Плав’юка застала мене у Харкові, в Свято-Дмитрівській церкві. Першою реакцією на цю звістку стала спільна молитва десятків людей, які особисто знали його і випадком долі зібралися у цей день у храмі.</p>\n<p>А може не випадком долі, а з Божої ласки?</p>\n<p>Ким був для України Микола Плав’юк?</p>\n','2012-03-14 02:06:29','http://oranta.org/index.php?option=com_content&view=article&id=838:ostanni-romantyk-ukrajinskoji-revoljuciji-pomer-golova-oun-m-plavjuk&catid=37:spilnota-cat&Itemid=49','P',NULL,'ОСТАННІЙ РОМАНТИК УКРАЇНСЬКОЇ РЕВОЛЮЦІЇ. ПОМЕР ГОЛОВА ОУН М. ПЛАВ’ЮК','uk',2695,4178,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4194,200,6.JPG\" />«І ми, як діти, несучи знамена&nbsp; перемоги, Тобі</p>\n<p>Переможцеві смерти кличимо: Осанна на висотах,</p>\n<p>Благословен грядий в ім’я Господнє»</p>\n<p><strong> </strong></p>\n<p>« І багато людей стелили свій одяг по дорозі; інші зрізували гілля з дерев і клали на шляху. Люди, що йшли перед Ним і слідом за Ним, вигукували, кажучи: Осанна Синові Давидовому! Благословенний Той, Хто йде в Ім’я Господнє! Осанна на висотах!» ( Єв. від Матвія 21.8, 9)</p>\n','2012-04-09 14:38:36','http://oranta.org/index.php?option=com_content&view=article&id=850:verbna-nedilja&catid=25:parafija-cat&Itemid=27','P',NULL,'Вербна неділя','uk',2719,4202,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4208,200,P1000941.JPG\" />17 квітня ми завітали до ув’язнених жінок Лук’янівського СІЗО – в третій день після Воскресіння Христового. Спочатку вистояли службу в храмі ім. Св. Миколая на Аскольдовій Могилі, а потім відправились до жінок. В цей раз поїхали: о. Ігор, о. Костянтин, хористка Катерина, пані Ганна, яка опікується ув’язненими, та волонтери Ганна і Орина.</p>\n<p><strong>Настрій святковий, а день&nbsp; особливий тим, що на нас чекають і нам будуть раді!</strong></p>\n','2012-04-20 18:42:02','http://oranta.org/index.php?option=com_content&view=article&id=856:my-kazaly-lhrystos-voskresr-i-nam-golosno-vidpovidaly-uvjazneni-zhinky-lvoistynu-voskresr-&catid=25:parafija-cat&Itemid=27','P','','Ми казали: «Христос Воскрес!» І нам голосно відповідали ув’язнені жінки: «Воістину Воскрес!» ','uk',2726,4209,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4210,200,DSC02223.JPG\" />«Великдень  у їхньому житті має подвійне значення», - сказав о. Роман Сиротич,  директор БФ «Карітас Київ», говорячи про самотніх людей, якими  опікується Карітас.</p>\n<p>«З  одного боку вони бачать свою неміч, самотність та страждання у ці  радісні для всіх Великодні дні,  коли інші родинами ідуть до храму чи сідають за святковий стіл, а з  іншого – вони відчувають особливу дію Воскреслого Христа, який їх  відвідує в особі милосердних людей, тому дуже важливо у ці святкові дні  відвідати тих самотніх і потребуючих людей», - говорить о. Роман.</p>\n','2012-04-27 12:44:08','http://oranta.org/index.php?option=com_content&view=article&id=858:lvelykden-u-jihnomu-zhytti-maje-podvine-znachennja-jak-smutku-tak-i-radostir-dyrektor-bf-lkaritas-kyjivr-o-roman-syrotych&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Великдень у їхньому житті має подвійне значення, як смутку, так і радості», - директор БФ «Карітас Київ» о. Роман Сиротич','uk',2729,4212,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4228,200,CRW_6391.JPG\" />20 травня 2012 р.&nbsp;Б. в церкві св.&nbsp;Миколая Чудотворця на Аскольдовій могилі пройшов ІХ всеукраїнський фестиваль-конкурс духовної пісні <strong>«Аскольдів глас»</strong>. Фестиваль був одним із заходів до 20-річного ювілею від початку моління на Аскольдовій могилі, а також 1130 річниці з дня народження святого Аскольда-Миколая для неба.</p>\n<p><strong>У</strong> фестивалі-конкурсі взяло участь вісім співаків-солістів, родом із Львівщини, Івано-Франківщини, Тернопільщини, Рівненщини, Київщини, Луганщини та Криму.</p>\n','2012-05-26 01:27:23','http://oranta.org/index.php?option=com_content&view=article&id=864:ih-vseukrajinsky-festyval-konkurs-duhovnoji-pisni-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27','P','','ІХ всеукраїнський фестиваль-конкурс духовної пісні «Аскольдів глас»','uk',2746,4229,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4236,200,3.jpg\" />«Ми бачили світло істини,&nbsp; ми прийняли Духа Небесного, ми знайшли віру істинну, Нероздільній Тройці поклоняємося, Вона cпасла нас» ( Стиховня вечірні 50-ці)</p>\n<p>Світлий празник Пасхи завершується не менш світлим і великим празником Святої П’ятидесятниці. З богослужби П’ятидесятинці прийшла до наших щоденних молитов нам добре знана молитва до св. Духа:&nbsp; « Царю небесний…», яка благословляє кожне наше Богослужіння.</p>\n<p>День Св. П’ятидесятниці святкує саму подію сходження святого Духа на апостолів, а понеділок призначений на віддання особливої чести св. Духові, як третій Божі особі . Про це Синаксар понеділка так каже: «В цей&nbsp; день, в понеділок 50-ці, празнуємо того все святого&nbsp; й животворного і всесильного Духа, єдиного з Тройці Бога, єдино гідного, єдиносущного і єдино славного Отцю і Синові… Сам так розповідає про це велике свято отець Юліан Катрій у своїй книзі «Пізнай свій обряд».</p>\n','2012-06-05 12:48:56','http://oranta.org/index.php?option=com_content&view=article&id=868:chodzhennja-svjatogo-duha&catid=25:parafija-cat&Itemid=27','P','','Cходження Святого Духа','uk',2761,4244,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,285408.jpg\" />З року в рік парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі двічі на рік проводять пішу прощу від Заздості до Зарваниці. Першу у часі перед Великоднем і другу на початку вересня.</p>\n<p>Цьогорічнапроща-паломництво проходила на честь вшанування світлої пам\'яті Блаженного  Патріарха Йосифа Сліпого. Київ-Заздрість-Зарваниця.7-9 вересня 2012 р.</p>\n','2012-09-11 23:49:28','http://oranta.org/index.php?option=com_content&view=article&id=883:stezhkamy-osypa-slipogo&catid=25:parafija-cat&Itemid=27','P','','Стежками Йосипа Сліпого','uk',2801,4284,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,Afiszahor.jpg\" />Голландський хоровий колектив, який відомий на весь світ своїми душевними співами, хор, який прославляє українську культуру, зовсім скоро приїде з концертною програмою до нашої країни. Це велика честь побувати на їхніх концертах, адже в їхньому репертуарі – пронизливі літургійні та народні твори, які залишають свій слід глибоко в серці кожного слухача.</p>\n<p>Хор імені Лисенка був заснований 1996 року великим українцем, музикологом, композитором, оперним співаком, уродженцем міста Долини, що на Івано-Франківщині Мирославом Антоновичем. Хлопець ще з дитинства виражав велику любов до музики, саме тому вивчав музичні науки й спів у Львові та виступав у різних операх. Пан Антонович закінчив навчання в Утрехтському університеті та захистив дисертацію під керівництвом професора доктора А. Смейерса.</p>\n','2012-08-31 10:02:08','http://oranta.org/index.php?option=com_content&view=article&id=881:vsesvitnovidomy-hor-imeni-lysenka-v-ukrajini&catid=37:spilnota-cat&Itemid=49','P',NULL,'Всесвітньовідомий хор імені Лисенка в Україні','uk',2797,4280,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4286,200,IMG_7915.jpg\" />З нагоди екологічного Дня-Творця, 13  вересня у&nbsp;приміщенні культурно-мистецького центру Національного  Університету Києво- могилянська Академія відбувся екологічний круглий  стіл  «Любов  до&nbsp;рідної Землі», метою якого було об’єднати зусилля Церков  для&nbsp;збереження чистоти на&nbsp;нашій землі, підвищити загальну культуру людей  та&nbsp;виховати бережне ставлення до&nbsp;природи.</p>\n','2012-09-13 22:43:46','http://oranta.org/index.php?option=com_content&view=article&id=885:cerkvy-usvidomljujut-potrebu-ekologichnogo-ochyshchennja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Церкви усвідомлюють потребу екологічного очищення','uk',2804,4287,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4290,200,DSC_0377.JPG\" />15 вересня 2012 року катехитична Рада парафії організувала перший урок для дітей і батьків з виїздом в історичне місце пов’язане з унійною православною Церквою в РАДОМИШЕЛЬ. Більше 50 дітей і батьків комфортабельним автобусом вирушили суботнього сонячного дня в паломництво до місця де в&nbsp;1780 р. було перенесено митрополію &nbsp;нашої Церкви яку очолив митрополит Київський Ясон Смогоржевський.</p>\n','2012-09-17 02:23:27','http://oranta.org/index.php?option=com_content&view=article&id=888:skarby-vidnadeni-u-mandrah&catid=25:parafija-cat&Itemid=27','P','','Скарби, віднайдені у мандрах','uk',2808,4291,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4292,200,IMG_8697.JPG\" />Катехитична школа для дітей&nbsp; на Аскольдовій могилі продовжує навчання. Сьогодні, 22.09.2012, діти, батьки, катехити, вихователі у супроводі о.Ігора та сестри Філомени ( ССПДМ) вирушили на лоно природи в Кончу-Заспу. І хоч видався дещо пасмурний ранок, та в цілому день був безвітряний і спокійний.</p>\n','2012-09-24 20:07:26','http://oranta.org/index.php?option=com_content&view=article&id=891:katehytychna-shkola-na-pryrodi&catid=25:parafija-cat&Itemid=27','P','','Катехитична школа на природі','uk',2811,4294,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4313,200,n.jpg\" /><strong>Відбулися перші публічні історичні читання на Аскольдовій могилі</strong></p>\n<p><strong> </strong></p>\n<p>Перші публічні історичні читання на Аскольдовій могилі, присвячені 1130-річчю пам’яті першого князя-християнина Київської Русі Аскольда-Миколая, не тільки представили широкому загалу нові дослідження істориків і богословів, а засвідчили об’єднавчій рух у християнському середовищі сучасної України.</p>\n','2012-10-11 23:36:58','http://oranta.org/index.php?option=com_content&view=article&id=895:podvyg-askolda-majemo-zrozumity-ta-usvidomyty&catid=25:parafija-cat&Itemid=27','P','','Подвиг Аскольда маємо зрозуміти та усвідомити','uk',2832,4315,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4322,200,ciZj-5fc3RQ.jpg\" />Традиція вшанування першого київського князя-християнина Аскольда, що отримав при хрещенні ім’я Миколай, попри тяжкі часи, руїну, війни та історичні катаклізми, зберігається у серці народу.</p>\n<p>У 1130 річницю світлої пам’яті Аскольда-Миколая, вшановуємо його як первомученика Києво-Руського. Аскольд не просто перший князь-християнин, подвиг котрого став фундаментом тисячолітньої історії християнства на Русі. Аскольд вивів Київську Русь на арену всесвітньої історії. Знання перших сторінок становлення держави надають нам сили для розбудови сучасної України.</p>\n','2012-10-19 04:11:02','http://oranta.org/index.php?option=com_content&view=article&id=900:askold-i-nyni-nadyhaje&catid=25:parafija-cat&Itemid=27','P','','Аскольд і нині надихає','uk',2840,4323,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4323,200,2.jpg\" />20 жовтня у суботу на Аскольдовій Могилі відбувся запланований захід – толока біля церкви св. Миколая. Спершу, о восьмій годині розпочалася Божественна Літургія. Згодом об одинадцятій годині розпочалася і толока. Це вже четвертий рік поспіль люди доброї волі збираються тут, щоб прибрати територію, посадити дерева, згребти листя.</p>\n<p>Найперше варто наголосити, що ініціатором цієї толоки, ще чотири роки тому став Лев Голубець, який разом з отцем Ігорем Онишкевичем організували цей захід.</p>\n','2012-10-23 01:26:33','http://oranta.org/index.php?option=com_content&view=article&id=899:chyste-dovkillja-chysti-sercja&catid=25:parafija-cat&Itemid=27','P','','Чисте довкілля – чисті серця','uk',2842,4325,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4360,200,stebliturkids.jpg\" />Цьогорічний фестиваль «Авскольдів Глас» подарував нашій парафії поки що заочне знайомство з Бориславською школою-інтернатом для дітей з наслідками поліомієліту та ДЦП (КЗ ЛОР&nbsp;Бориславська спеціалізована загальноосвітня школа-інтернат І-ІІІ ст.). Сподіваємось, що на наступному фестивалі вже зможемо почути і побачити їх гурт «Ковчег» в рядах учасників. А допоки під час фестивалю було розпочато збірку на потреби школи, зокрема на спеціальні учнівські антисколіозні столи та стільці. Скарбничка ще не набрала достатньої суми, тож заохочуються пожертви. До уваги читачів сайту лист п. Наталі Тищенко, директора:</p>\n','2012-12-27 23:55:05','http://oranta.org/index.php?option=com_content&view=article&id=916:bud-myloserdnym&catid=29:propovidi-cat&Itemid=34','P',NULL,'Будь милосердним','uk',2878,4361,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4387,200,sestra-josofata-sestry-slugebnyci.jpg\" />Ісусе, Ти моя мудрість, сила і потіха, щастя і скарб тут і у вічності. — <strong>преп. Йосафата</strong></p>\n<p>Той, хто сьогодні ступає слідами Христа, є Його свідком у світі. Складаючи обіти, богопосвячена особа передає послання Христової любові братам і сестрам.</p>\n<p>Віруюча людина, котра отримує покликання як дар, може “переступити” матеріальні потреби, залишити дім, найулюбленіших, найближчих осіб, зв`язок з іншими, і є здатною вчинити з себе цілковитий дар для Бога.</p>\n','2013-11-20 00:04:04','http://oranta.org/index.php?option=com_content&view=article&id=928:duhovny-svit-blazhennoji-osafaty-gordashevskoji&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Духовний світ Блаженної Йосафати','uk',2905,4388,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4388,200,733995_563584453674173_1196504355_n.jpg\" />На заклик парафіяльної служби милосердя «Карітас-Печерський» до спільної щирої молитви, посту душі, прояву милосердного милосердя у часі великоднього посту відгукнувся колектив компанії «Еконія», єдиного в Україні інноваційного виробника питної води для діток <a href=\"http://www.econia.com.ua/uk/\">http://www.econia.com.ua/uk/</a>.</p>\n<p>Президент компанії Ірина Варагаш добре розуміє, що основна мета милосердного милосердя полягає не тільки у матеріально-фінансовій помочі, головним і дуже важливим аспектом у цьому служінні є поміч духовна, просвітницька, пізнавальна.</p>\n','2013-04-08 18:40:23','http://oranta.org/index.php?option=com_content&view=article&id=1099:2013-04-08-15-48-20&catid=25:parafija-cat&Itemid=27','P',NULL,'Просвітницьке паломництво дітей «Божого милосердя» та дітей катехитичної школи парафії','uk',3135,4618,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4403,200,IMG_4250.JPG\" />Казковий зимовий вечір в парку Аскольдова Могила. Треба пройти ще декілька десятків метрів і ти у храмі, молодь вже сходиться, не можу не замилуватися природою навколо церкви. Сніжинки падають і так виблискують на снігу, наче небесні зірки промовляють і хвалять Творця.</p>\n<p>Вечірня в Свято Миколаївському храмі на Аскольдовій Могилі. Співає молодь, виходять отці.\n','2013-01-24 21:40:33','http://oranta.org/index.php?option=com_content&view=article&id=965:svjato-melanky-na-askoldovi&catid=25:parafija-cat&Itemid=27','P','','Свято Меланки на Аскольдовій','uk',2921,4404,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4403,200,mlynky_009_display.jpg\" />Одна історія, яку Вчитель розповів  своїм учням для того, аби ті зрозуміли, яку шкоду може принести духовно  багатим людям прив’язаність до нікчемних речей.</p>\n<p> </p>\n<p>Якось селянин проїзджав верхи повз гору.  Він нагодився саме тоді, коли в горі відкривався вхід до печери, де  зберігалися незліченні скарби, і кожен, хто хотів, міг взяти собі  скільки завгодно золота і самоцвітів. Селянин увійшов досередини і почав  квапливо набивати коштовностями сумки, приторочені до сідла на його  мулі. Він добре пам’ятав легенду, в якій говорилося, що печера  відкривається на дуже короткий час.</p>\n','2013-01-27 21:30:38','http://oranta.org/index.php?option=com_content&view=article&id=964:skarb-u-zacharovani-gori&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'СКАРБ У ЗАЧАРОВАНІЙ ГОРІ','uk',2926,4409,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4421,200,DSC05642.JPG\" />Нещодавно у БФ «Карітас Київ» відбулося Різдвяне свято, в якому взяли участь підопічні діти. Як і завжди, була інтерактивна&nbsp;коротка бесіда&nbsp;про смисл свята, дівчата заспівали народних пісень зимового циклу, колядували. Для найменших дітей був постелений килим, облаштований іграшковий майданчик, батьки мали змогу поспілкуватись. Оце останнє дуже важливе, з огляду на старання Карітасу створити спільноту взаємодопомоги з числа відвідувачів Соціального центру.</p>\n','2013-02-04 12:11:47','http://oranta.org/index.php?option=com_content&view=article&id=978:2013-02-04-10-18-18&catid=29:propovidi-cat&Itemid=34','P',NULL,'«Різдво приносить в їхні душі також радість», - о. Роман Сиротич, директор БФ «Картіас Київ»','uk',2939,4422,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4430,200,babylon-ch46-01.jpg\" />На Утрені в неділю «Про блудного сина» звучить псалом 136, <em>На берегу вавилонських рік</em>, який має ностальгічний зміст і також ностальгічну мелодію і він співаєтся тільки три рази в році (в Неділю Блудного сина, М’ясопусну і Сиропусну Неділю).</p>\n<p>Цей псалом був написаний як відповідь  чи спомин на конкретний досвід Ізраїльського народу, який жив у Вавилонському полоні (587 р. перед Христом). Йдеться переважно про єврейську еліту людей, інтелектуалів та провідників народу, які після знищення Єрусалиму були вивезені до Вавилону.</p>\n','2013-03-02 21:42:51','http://oranta.org/index.php?option=com_content&view=article&id=987:na-berezi-vavylonskyh-rik&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'На березі вавилонських рік','uk',2988,4471,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,SgkxKs1aiY4.jpg\" />Уявляю себе у присутності Христа… І в тиші відкриваюся перед ним. Тим, котрий зцілює,.. творить…</p>\n<p>Зараз я прохатиму, аби Він подав мені щонайповніший перелік усього, що бачить у мені поганого, кожен вияв мого егоїзму, кожну площину в якій я ще не визрів, кожну рису, яку я хочу змінити… І тільки Він почне говорити, я карбуватиму у своїй пам’яті Його слова – навіть, можливо, запишу, якщо вважатиму, що це мені допоможе.</p>\n','2013-02-05 14:49:32','http://oranta.org/index.php?option=com_content&view=article&id=979:odkrovennja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Одкровення','uk',2950,4433,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4443,200,631658_LOURDES150308_HP512_34.jpg\" />Уявляю собі, що заходжу вночі у храм, щоб прихилити серце та коліна перед Чесними Дарами… єдиним джерелом світла тут є запаленні свічки на вівтарі… мій погляд зупиняється на гостії, білосніжні обриси якої виділяються у темноті…</p>\n<p> </p>\n<p>Гостія схожа на магніт, бо притягує мій зір і усе моє єство до себе, як до центру… У своєму житті я, зазвичай, зосереджуюсь на зовнішній поверхневій стороні, але тут пильно вдивляюсь у саме серце речей, в центр свого існування та існування світу…</p>\n<p>Я дивлюся на гостію і мене огортає мовчання… Усі думки заспокоюються і поступово відходять геть… Безмовність тієї гостії, здається, наскрізь проймає моє тіло, а вже з нього розливається по всій святині… Так що усе у мені та довкола завмирає.</p>\n','2013-02-13 23:11:28','http://oranta.org/index.php?option=com_content&view=article&id=990:serce&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Серце','uk',2966,4449,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4445,200,pr.jpg\" />Очевидно не всім відомо про щорічний конкурс на одержання<strong> </strong>грантів Президента України для обдарованої молоді. Участь в цьому конкурсі може взяти кожен громадянин України віком до 35 років, котрий має бажання втілити свій соціально значущий творчий проект у життя. Це стосується не тільки фахових наукових досліджень, але й проектів із соціальної та гуманітарної сфери (екологія довкілля, дозвілля молоді, волонтерство, спорт, література, музика тощо).</p>\n','2013-02-17 23:10:45','http://oranta.org/index.php?option=com_content&view=article&id=991:derzhavna-pidtrymka-doslidzhen-molodi&catid=37:spilnota-cat&Itemid=49','N','','Державна підтримка досліджень молоді','uk',2964,4447,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4477,200,550859_benedykt_ix_7.jpg\" />В очікуванні оголошення дати початку конклаву італійська преса звертає  увагу на математичну цікавинку в історії понтифікатів: хоча Бенедикт XVI  був 263-ім Папою в історії, його понтифікат був 265-им з черги.</p>\n<p>Така розбіжність є результатом бурхливої історії Церкви в ХІ столітті  за часів понтифікату Бенедикта ІХ, котрий три рази ставав Папою.</p>\n','2013-03-08 15:17:41','http://oranta.org/index.php?option=com_content&view=article&id=1018:263-papy-i-265-pontyfikativ&catid=32:gk-svit-cat&Itemid=58','P',NULL,'263 Папи і 265 понтифікатів','uk',2995,4478,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4497,200,Mykola_Plaviuk.jpg\" />Щиро дякую усім, хто з нагоди річниці смерті Голови ОУН Миколи Плав’юка прийшов вшанувати його пам\'ять. Передусім вдячний настоятелю Церкви Святого Миколая Чудотворця на Аскольдовій Могилі отцю Ігорю, який відправив зворушливу панахиду за покійним і завершив її виконанням державного гімну України. Як відомо, М. Плав’юк був довголітнім парафіянином цієї Церкви, допомагав її становленню. Сьогодні ж Церква гідно вшанувала пам\'ять свого вірного парафіянина.</p>\n','2013-03-11 23:42:10','http://oranta.org/index.php?option=com_content&view=article&id=1034:pamjat-pro-golovu-oun-mpavjuka&catid=37:spilnota-cat&Itemid=49','P',NULL,'ПАМ\'ЯТЬ ПРО ГОЛОВУ ОУН М.ПЛАВ’ЮКА','uk',3015,4498,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4502,200,images.jpg\" />Одна молода хмара народилася над Середземним морем. Не встигла і вирости як вітер прогнав її разом з усіма родичами геть у далеку Африку. Щойно вони прибули на континент клімат змінився. Угорі палило сонце а внизу переливалися золотом розпечені піски сахари. Вітер вирішив погнати далі у джунглі тому, що в пустелі дощі не падають. Але допитлива молода хмарка відстала від гурту щоб оглянути велику пустелю.<o:p></o:p></p>\n','2014-04-12 23:34:35','http://oranta.org/index.php?option=com_content&view=article&id=2026:hmara-i-djuna-13-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хмара і дюна (13 квітня)','uk',4250,5733,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4511,200,1798_120579191462672_669654678_n.png\" />Брати і сестри, добрий вечір!</p>\n<p>Як вам відомо, обов’язком Конклаву було надати Риму єпископа. Виглядає  на те, що мої брати-кардинали дійшли майже до краю землі, щоб знайти  його… але ось ми тут. Дякую вам за вітання, що надійшли від єпархіальної  спільноти Риму.</p>\n<p>Перш за все хочу вас просити про молитву за нашого Папу-емерита  Бенедикта ХVІ, щоб Господь його благословив, а Мати Божа оберігала.</p>\n','2013-03-13 23:32:11','http://oranta.org/index.php?option=com_content&view=article&id=1041:majemo-papu&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Маємо Папу','uk',3029,4512,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4514,200,1298722671.jpg\" />Дивна сила молитви.Він стояв під стіною, зіпершись на неї крилом, і тихо  плакав. Гарячі сльози стікали по прекрасному лику і капали на землю.  Якби він був видимим, то усі бачили б, що з тих сліз виростають тоненькі  паростки квітів, але від того, що сльози такі гарячі й пекучі, аж до  болю, квіти в’януть. А він усе стояв і ждав, ждав, коли юнак, душа  котрого була ввірена йому Богом, вийде з тієї будівлі, куди не можна  було йому ввійти.</p>\n','2013-03-14 23:34:27','http://oranta.org/index.php?option=com_content&view=article&id=1042:angel-ohoronec&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ангел охоронець','uk',3032,4515,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4552,200,Peisakh-2.jpg\" />Від раннього ранку виглядаю Тебе. Хочу запросити Тебе в подорож. Сьогодні&nbsp; хочу мандрувати з Тобою дорогою, котрої не знаєш, йти шляхом тих, хто в пошуку. Бажаю бо вивести Тебе із тісноти твоєї, що гніздиться у серці твоєму, у зачерствілих думках, в уявленнях про Мене, у вірі в Мене, у довір`ї Мені. Я з Тобою коли Ти мене виглядаєш, шукаєш, запитуєш, прагнеш, молишся, відкриваєш, промовляєш.</p>\n','2013-03-21 12:49:33','http://oranta.org/index.php?option=com_content&view=article&id=1073:zaproshennja-na-banket&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Запрошення на банкет','uk',3071,4554,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4577,200,Dopomoga.jpg\" />Про минуле, зокрема, оте жорстоке й довговічне, можна міркувати по-різному: тямкувати про власні кривди, леліяти мрії про помсту, плекати біль і культивувати ненависть до переслідувачів. Можна також – а це далебі важче – переступити крейдове коло взаємної недовіри й ворожості, щоб створити інше майбутнє в ім’я порозуміння й примирення.</p>\n','2013-03-28 13:12:04','http://oranta.org/index.php?option=com_content&view=article&id=1078:ne-sperechamosja-pro-te-hto-bilshe-zavynyv&catid=37:spilnota-cat&Itemid=49','P',NULL,'Не сперечаймося про те, хто більше завинив','uk',3096,4579,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,161.jpg\" />Дорогі брати й сестри з Риму та всього світу, благодатного вам Великодня!</p>\n<p>Яка велика радість для мене змогти донести вам цю звістку: Христос  Воскрес! Я хотів би, щоб вона досягнула кожну домівку, кожну сім’ю,  особливо там, де є найбільше страждань, лікарні, тюрми...</p>\n','2013-04-01 09:29:18','http://oranta.org/index.php?option=com_content&view=article&id=1086:-velykodnje-poslannja-urbi-et-orbi-2013-roku-papy-francyska-&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Великоднє послання Urbi et Orbi 2013 року Папи Франциска ','uk',3113,4596,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4597,200,IMG_1619.JPG\" />Вже 15 років поспіль отець Анатолій Тесля провадить духовний провід для тяжкохворих парафіян. Щомісяця відвідує хворих на дому, у будинку престарілих на масиві \"Чайка\". Серед опікуваних відома режисерка кіностудії Довженко Світлана Світлична (будинок престарілих), поетеса Світлана Космачівська (на дому).</p>\n<p>Такий вид служіння можна поділити на підкатегорії:</p>\n<p>духовна опіка над парафіянами, які не можуть ходити до храму і просять причастя на домуопіка немічних і хворих, які потребують священика.</p>\n','2013-04-03 11:13:56','http://oranta.org/index.php?option=com_content&view=article&id=1087:duhovna-domashnjai-opika&catid=25:parafija-cat&Itemid=27','P',NULL,'Духовна домашня опіка','uk',3115,4598,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4612,200,900587608979607.jpg\" />Ніна Марченко (Смужаниця) все своє життя слідувала прикладу Матінки Божої</p>\n<p>4 квітня у Спілці письменників України відбулася презентація книги про життя Ніни Марченко (Смужаниці, 1929-2011) «Матінка Ніна» – матері відомого дисидента-шістдесятника Валерія Марченка. Серед упорядників книги – прихожанка Церкви Святого Миколая на Аскольдовій Могилі Наталія Пуряєва та Андрій Горбаль, небіж Ніни Марченко.</p>\n','2013-04-09 13:02:04','http://oranta.org/index.php?option=com_content&view=article&id=1096:2013-04-08-10-12-59&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Служаниця» свого сина (оновлено)','uk',3132,4615,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4614,200,golovne.jpg\" />Міжнародний громадський рух «Живи» ініціює в Україні новий соціальний  проект поколінь «Встигни сказати головне», який стартує 20 квітня під  девізом «Щоб звернути серця батьків до дітей».</p>\n<p>Метою проекту є об\'єднати молоде покоління країни навколо уваги і  дієвого співчуття по відношенню до старшого покоління, сприяти розвитку  почуття вдячності і поваги дітей до батьків, чути покоління, щоб  встигнути сказати головне!</p>\n','2013-04-08 14:39:05','http://oranta.org/index.php?option=com_content&view=article&id=1098:2013-04-08-11-59-37&catid=37:spilnota-cat&Itemid=49','P',NULL,'В Україні стартує соціальний проект поколінь «Встигни сказати головне»','uk',3134,4617,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4623,200,221532016.jpg\" />Вітер уривчасто завивав тужливу пісню. Дерева похитували йому розпатланими кронами в унісон. У верховітті зависло те незнане: і доки? Доки ще вередуватиме холоднеча, невже не набридло наввипередки гнатися із весною, такою несміливою і мало що не одчайдушною.</p>\n<p>Але де там!.. Той волоцюга-вітер, і де тільки йому духу вистачає, набрав повні щоки повітря і як дмухне, що аж дух перехопило! .. і нема чому дивуватися. Він не може спокійно всидіти на лавочці і белемкати ногами.</p>\n','2013-04-14 00:41:46','http://oranta.org/index.php?option=com_content&view=article&id=1107:a-koly-cvily-prolisky&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'А коли цвіли  проліски...','uk',3156,4639,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4625,200,khkdskvc.jpg\" />Торкаюся гладкої поверхні зошита, і з`являється – СЛОВО.</p>\n<p>Ставлю розділові знаки, записую думки, нанизую їх слово по слову на довгі шеренги речень – коротких, простих, цікавих та одноманітних, сірих і злегка підфарбованих.</p>\n','2013-04-10 00:30:45','http://oranta.org/index.php?option=com_content&view=article&id=1106:dotyk-slova&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дотик слова','uk',3143,4626,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4627,200,phpqmOaVrp4Cs.jpg\" />Мені часто ставлять запитання під час   різного роду зустрічей про відносини церкви і держави. Думаю, що це   зацікавлення має багато підстав.</p>\n<p>Зрозуміло, що в такій державі, як наша, де є   багато церков і релігійних організацій, їх стосунок до держави, а тим   більше до чинної державної влади цікавить свідомих, передусім віруючих   громадян. Тому хочу назвати кілька причин (які мені відомі), чому люди   ставлять таке запитання.</p>\n','2013-04-11 12:31:08','http://oranta.org/index.php?option=com_content&view=article&id=1111:pro-spivzhyttja-cerkvy-i-derzhavy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Про співжиття Церкви і Держави','uk',3146,4629,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,Josafata.jpg\" />Особливі риси: <em>духовний світ, духовні якості, чесноти і особистісні риси, покликання, віра, християнська любов, мучеництво. </em></p>\n<p><em> </em></p>\n<p><em>У статті представлено особливості становлення особистості Ольги Мацьків та риси&nbsp; с.Тарсикії на початку її богопосвяченого життя. До&nbsp; агіографічного портрету належать її&nbsp; духовні пориви та натхнення. Особливим духовним даром є її&nbsp; монаше покликання&nbsp; та покликання до мучеництва. </em></p>\n<p>Поняття «духовний світ особи» дуже часто в українській наукові літературі, а зокрема філософській, хоч і належить до поняття «духовність», нерідко носить значення змісту суспільної свідомості, що показує матеріалістичну парадигму у рамках якої мислить автор.</p>\n','2013-04-18 20:54:31','http://oranta.org/index.php?option=com_content&view=article&id=1127:duhovny-svit-blazhennoji-muchenyci-s-tarsykiji-mackiv-sndm&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Духовний світ блаженної мучениці  с. Тарсикії Мацьків, СНДМ','uk',3174,4657,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4675,200,IMG_4035.JPG\" />У вівторок 23 квітня 2013 року парафіяни з різних храмів Києва взяли участь у молодіжній Хресній дорозі, яка була організована молоддю парафії Миколая Чудотворця, що на Аскольдовій могилі. Близько сорока людей, серед яких були члени товариства «Обнова» та їхній духівник отець Володимир Мальчин, отець Олексій Геник з парафії Святого Миколая Чудотворця, отець Михайло Томич, брати-семінаристи Київської Трьохсвятительської духовної семінарії, що в Княжичах та парафіяни з різних церков Києва йшли від самого Дніпра і аж до церкви Святого Миколая Чудотворця, що на Аскольдовій могилі.</p>\n','2013-04-25 09:51:29','http://oranta.org/index.php?option=com_content&view=article&id=1142:molodizhna-hresna-doroga-u-dusi-teze-na-shylah-dnipra&catid=25:parafija-cat&Itemid=27','P',NULL,'Молодіжна Хресна дорога у дусі Тезе, на схилах Дніпра','uk',3193,4676,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,456474.jpg\" />Головним християнським обрядом у страсну п\'ятницю є винесення  плащаниці. Плащаниця символізує полотно, в яке був загорнутий Ісус  Христос після зняття з Хреста. Після Воскресіння на полотні залишився  відбиток Його Образу, який утворився, як дослідили учені, шляхом  великого випромінювання.</p>\n<p><strong>Це єдиний день в році, коли не правиться Служба Божа</strong>,  є лише Вечірня з винесенням Плащаниці. Її виносять у другій половині  дня. Іде тричі обхід довкола церкви з Плащаницею, яку несуть за чотири  кінці священики або старші парафіяни.</p>\n','2013-05-03 00:11:53','http://oranta.org/index.php?option=com_content&view=article&id=1168:strasna-pjatnycja-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Страсна п’ятниця ','uk',3212,4695,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,delegatiiblagodatnijiogoni2012.jpg\" />Досить часто ми десь “губимо” Велику Суботу. Тільки закінчується  Страсна П\'ятниця, відразу починаємо думати про святкування Воскресіння  як в храмі, так і потім вдома з родиною.</p>\n<p>Я теж для себе особисто відкрила тайну Великої Суботи зовсім недавно.  Спонукали мене до цього мої життєві переживання якоїсь темряви,  страждання, самотності.</p>\n','2013-05-04 00:30:48','http://oranta.org/index.php?option=com_content&view=article&id=1170:-velyka-subota-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Велика Субота ','uk',3215,4698,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4683,200,tajna_v.jpg\" />У цей день ми пригадуємо подію, коли Ісус Христос умив ноги своїм апостолам,          Його останню вечерю з учнями та встановлення таїнства священства. Усе          це відбулося напередодні Пасхи, тому що Спаситель знав про свої останні          дні земного життя й про завершення місії спасіння людського роду через          смерть на хресті. І тут варто пригадати, що Син Божий багато разів намагався          застерегти свого учня Юду від зради, вживаючи часом дуже гострих слів,          як наприклад: \"Та один же з вас - диявол!» (Йо 6, 70).</p>\n','2013-05-02 00:24:41','http://oranta.org/index.php?option=com_content&view=article&id=1169:velyky-chetver-spomyn-tanoji-vecheri&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Великий четвер. Спомин Тайної вечері','uk',3210,4693,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,207638595.jpg\" />Дорогі брати й сестри з усього світу, благодатного вам Великодня!<br />Яка велика радість для мене змогти донести вам цю звістку: Христос  Воскрес! Я хотів би, щоб вона досягнула кожну домівку, кожну сім’ю,  особливо там, де є найбільше страждань, лікарні, тюрми...</p>\n','2013-05-04 21:09:38','http://oranta.org/index.php?option=com_content&view=article&id=1153:-velykodnje-poslannja-papy-francyska-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Великоднє послання Папи Франциска ','uk',3216,4699,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4708,200,htmlimage.jpeg\" />Ще з часів дитинства пригадується світлий Великодній час. Родина збирається у бабусі, щоб поєднатися у молитві, поділитися свяченою паскою, похристосуватися писанками та обмінятися останніми новинами. І все це – під незмінний вже двадцять років показ Великодніх богослужінь на Першому Національному телеканалі. Відбувається Великодня трансляція з кафедральних соборів України в режимі реального часу, і в цьому також – традиція.</p>\n','2013-05-09 12:58:09','http://oranta.org/index.php?option=com_content&view=article&id=1173:velykodni-tradyciji-pershogo-nacionalnogo&catid=37:spilnota-cat&Itemid=49','P',NULL,'Великодні традиції Першого Національного','uk',3226,4709,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4758,200,1.JPG\" />Дня святого Миколая кожна дитина чекає з нетерпінням, але зазвичай лише у зимовий час. Багато хто забуває про святкування літнього святого Миколая, а особливо для нашої парафії це свято є визначним і довгоочікуваним, адже саме наша церква на Аскольдовій могилі носить ім’я цього святого чудотворця і благодійника Миколая.</p>\n<p>Саме сьогодні у травневий день 26 числа на Аскольдовій могилі зібралося багато парафіян, щоб відсвяткувати разом цей празник.</p>\n','2013-05-26 16:17:14','http://oranta.org/index.php?option=com_content&view=article&id=1222:svjato-teplogo-mykolaja-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P',NULL,'Свято теплого Миколая на Аскольдовій Могилі','uk',3276,4759,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4807,200,IMG_8057.JPG\" />Сучасний темп життя виснажує фізичні та духовні сили людини, ми потребуємо постійного відновлення, отримання Дарів Святого Духа. Церква встановлює свята, щоб пригадати християнам події описані в Євангелії, для того щоб їх глибоко пережити, як це зробили апостоли у свій час, необхідне приготування. Молитовні чування, що відбулись в суботу мали мету допомогти духовно пережити радісну подію Зіслання Святого Духа, що святкувалась наступного дня.</p>\n','2013-06-24 18:49:45','http://oranta.org/index.php?option=com_content&view=article&id=1288:22-chervnja-naperedodni-svjata-zislannja-svjatogo-duha-vidbulys-molytovni-chuvannja&catid=25:parafija-cat&Itemid=27','P','','22 червня напередодні свята Зіслання Святого Духа відбулись молитовні чування','uk',3379,4862,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4807,200,ascension-620x250.jpg\" />На 40-й день після Пасхи відзначається двунадесяте свято Вознесіння Господнє.</p>\n<p>Цього дня на Єлеонській горі поблизу Єрусалима Христос на очах у своїх  учнів знісся на небо (Лука, 24, 50, 53; Діяння 1, 9—13). В усіх  християнських церквах відбувається із цього приводу урочиста служба.\n','2013-06-12 11:39:56','http://oranta.org/index.php?option=com_content&view=article&id=1246:voznesinnja-gospodnje&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Вознесіння Господнє','uk',3326,4809,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4830,200,1005.jpg\" />Українська Греко-Католицька Церква своїми вчинками не раз засвідчувала,  що вона передусім дбає про своїх вірних, тих, які в силу різних  обставин проживають в усіх регіонах нашої країни і потребують  душпастирської опіки. Наша мета - проголошувати Добру Новину людям, які  належать до нашої Церкви, а не заманювати чи перетягувати інших християн  до нашої Церкви, як про це безпідставно заявляють представники інших  Церков.</p>\n','2013-06-18 00:26:30','http://oranta.org/index.php?option=com_content&view=article&id=1261:2013-06-17-21-32-56&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Для Господа важлива кожна душа, яка спасеться», – о. д-р Ігор Шабан про «прозелітизм» греко-католиків','uk',3349,4832,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,ArticleImages_43849_01.jpg\" />Україна – це велика країна! Таке гасло в останні роки часто звучить у наших ЗМІ.</p>\n<p>Україна багата своєю природою: безкраїми полями, горами, морями й   озерами, дорогоцінним чорноземом, природними мінералами, - старовинною   самобутньою історією, прекрасними архітектурними пам’ятками, визначними   науковцями та творцями в різних ділянках культури, суспільними діячами,   державними мужами і полководцями.</p>\n<p> </p>\n','2013-06-22 21:42:09','http://oranta.org/index.php?option=com_content&view=article&id=1281:velyka-krajina-malyh-ljude&catid=37:spilnota-cat&Itemid=49','P',NULL,'Велика країна малих людей','uk',3370,4853,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4876,200,shevchuk_mixalik.jpg\" />Представники Української греко-католицької церкви  та Римо-католицької церкви Польщі підписали у Варшаві спільну декларацію  з нагоди 70-річчя Волинської трагедії.</p>\n<p>Про це повідомляє <a href=\"http://www.ugcc.org.ua/news_single.0.html?&amp;tx_ttnews[tt_news]=11590&amp;cHash=65e537bfd4ebe0fa05949fd0ccce99b6\">офіційний сайт</a> УГКЦ.</p>\n<p>У документі підкреслюється, що насильство та етнічні чистки за жодних  обставин не мають права бути методом вирішення конфліктів і не можуть  бути виправдані ні політичним, ні економічним, ні релігійним інтересом.</p>\n','2013-06-29 01:10:18','http://oranta.org/index.php?option=com_content&view=article&id=1311:greko-i-rymo-katolyky-zaklykaly-ukrajinciv-i-poljakiv-do-prymyrennja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Греко- і римо-католики закликали українців і поляків до примирення','uk',3394,4877,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4878,200,images.jpg\" />Недарма хтось сказав, що зорі квітнуть на землі. Проте так, як вони здивували мене цього вечора, навіть годі описати.</p>\n<p>Жадібно впиваюся рідним повітрям. Воно п`янке під цю пору, наче міцний трунок. До щему в душі проникає в мене, зрошує своєю прохолодою, торкається спомином.</p>\n','2013-06-29 01:38:55','http://oranta.org/index.php?option=com_content&view=article&id=1312:rozkvitli-zori-na-zemli-&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Розквітлі зорі... на землі ','uk',3397,4880,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4921,200,32.jpg\" />Парафіяни церкви св. Миколая Чудотворця на Аскольдовій Могилі мали гарну нагоду відпочити на березі Чорного моря у Чорномоському (Крим). Усім сподобався табір. Кожен день була літургія, добре годували, тепле море і добра погода – все, що потрібно для відпочинку. Ми тут побачили багато медуз, риб, виходили на вітрильнику у відкрите море. Діти Божого милосердя пірнали, засмагали, нас багато фотографували.</p>\n','2013-07-21 21:31:15','http://oranta.org/index.php?option=com_content&view=article&id=1345:katehytychny-rodynny-tabir-daje-vidchuty-radist-sluzhinnja&catid=25:parafija-cat&Itemid=27','P',NULL,'Катехитичний родинний табір дає відчути радість служіння','uk',3455,4938,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4926,200,77.JPG\" />Зарваниця – те місце де люди відчувають духовне об’єднання людини з Богом. Майже кожного місяця відбуваються величні всеукраїнські прощі до Зарваниці, які укріплюють духовний стан людини. Кожного року Зарваниця зустрічає тисячі людей з різних областей України. Традиційно люди приїжджають до Зарваниці автобусом, але не менше людей ідуть на прощу пішки: хто з Тернополя, хто з Хмельницька, хто з Рівного, хто з Франківська, хто з сіл, що розташовані рядом зі Зарваницею.</p>\n','2013-07-17 19:31:01','http://oranta.org/index.php?option=com_content&view=article&id=1338:shljah-do-spasinnja--zarvanycja-zustrichaje-molod&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Шлях до спасіння – Зарваниця зустрічає молодь','uk',3444,4927,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4929,200,IMG_0733-20.jpg\" />Я є священиком Української Греко-Католицької Церкви. Приналежність до неї сповняє мене особливою гордістю і радістю в особливі моменти мого життя.</p>\n<p>Я радий тоді, коли йду пішки до Гошева в оточенні 20 тисяч молоді, які усміхаючись, прощею долають більше тридцяти кілометрів за кілька годин.</p>\n','2013-07-21 10:39:34','http://oranta.org/index.php?option=com_content&view=article&id=1341:skazaty-chomu-i-koly-ja-radiju-z-togo-shcho-je-greko-katolykom&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Сказати, чому і коли я радію з того, що є греко-католиком?','uk',3448,4931,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4935,200,46DBDED0.jpg\" />Останніми місяцями ведеться активна підготовка до відзначення 1025-ї  річниці Хрещення Київської Русі. Святкування планують також на  державному рівні за участю достойних гостей, представників міжнародної  спільноти, з відповідними заходами наукового та культурного характеру,  одне слово, усе відбудеться урочисто і гучно.</p>\n<p>Така ситуація спонукає нас подумати, що і як ми повинні святкувати.</p>\n','2013-07-23 16:31:57','http://oranta.org/index.php?option=com_content&view=article&id=1352:jak-vidznachyty-hreshchennja-kyjivskoji-rusi&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Як відзначити Хрещення Київської Русі?','uk',3461,4944,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4960,200,IMGP1.JPG\" />Щонеділі о 14:00 Молебень з Акафістом Покрову  Пресвятої Богородиці за оздоровлення хворих і узалежнених, охорону  подорожуючих, оливо помазання вівправляється у каплиці \"Чудо покладення  чесної ризи Пресвятої Богородицi у Влахернi\", калиця розташована позаду церкви св. Миколая Чудотворця на Аскольдовій Могилі.</p>\n<p> </p>\n<p>АКАФІСТ ПОКРОВУ ПРЕСВЯТОЇ БОГОРОДИЦІ</p>\n','2013-07-30 11:18:35','http://oranta.org/index.php?option=com_content&view=article&id=1384:prosymo-zastupnyctva-presvjatoji-bogorodyci&catid=25:parafija-cat&Itemid=27','P',NULL,'Просимо заступництва Пресвятої Богородиці','uk',3478,4961,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4972,200,IMGP9786.JPG\" />4 серпня 2013 року Божого у церкві св.  Миколая Мирлікійського на Аскольдовій Могилі пройшов Вечір пам’яті (сто  років, з дня народження для неба) відомої української поетеси,  письменниці, перекладача, культурного діяча Лесі Українки (1913-2013)</p>\n<p>Вечір пам’яті відбувся за підтримки Київського музею Лесі Українки.</p>\n','2013-08-06 08:41:08','http://oranta.org/index.php?option=com_content&view=article&id=1391:pamjati-lesi-ukrajinky&catid=25:parafija-cat&Itemid=27','P',NULL,'Пам\'яті Лесі Українки','uk',3490,4973,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4983,200,preobrazhennya.jpg\" />Є такі сонячні моменти, які не можна придбати навіть за найвищу ціну, не вдасться їх виміняти на ще кращі, проте можливо жити ними у час радості, дарований Богом . Ото й пригадалась одна така днина.</p>\n<p>То був день, коли, здавалось, сонце виграє на кларнеті. І все заливає не лише світлом, а й стрімким і сліпучим потоком радості. Воно було тихим, глибоким і спокійним. Зір відпочивав, усмішка широко розливалась, а серце молилося, співало.</p>\n','2013-08-17 22:36:46','http://oranta.org/index.php?option=com_content&view=article&id=1406:tvoje-pereobrazhennja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Твоє Переображення','uk',3516,4999,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4990,200,original.jpg\" />Господь Бог любив і благословляв ізраїльський народ. Але коли євреї  забули правдивого Бога і почали покланятися ідолам, Господь тяжко карав  їх, передусім руками поганських царів, що займали Палестину, руйнували  міста, вбивали людей, забирали їх у неволю. Таке нещастя спало на  ізраїльтян приблизно 166 року перед народженням Ісуса Христа, коли  сирійським царем був Антіох Епіфан, який намагався примусити юдеїв  перейти на грецьку поганську віру.</p>\n','2013-08-13 20:13:51','http://oranta.org/index.php?option=com_content&view=article&id=1408:svjati-muchenyky-makaveski&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'СВЯТІ МУЧЕНИКИ МАКАВЕЙСЬКІ','uk',3508,4991,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,4993,200,sobor_vechir.jpg\" />Собор Воскресіння Христового як засіб комунікації нашої Церкви</p>\n<p>Наближаємося до Міжнародної всецерковної прощі до Патріаршого Собору  Воскресіння Христового в Києві з нагоди 1025-річчя Хрещення  Русі-України, під час якої Патріарх Святослав разом з Синодом Єпископів  нашої Церкви при всенародних моліннях освятять престіл храму.</p>\n','2013-08-15 15:12:27','http://oranta.org/index.php?option=com_content&view=article&id=1415:nash-sobor&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Наш Собор','uk',3511,4994,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5005,200,5.JPG\" />Усі наші катехитично-оздоровчі табори завжди є захоплюючими і незабутніми. Таким видався і &nbsp;відпочинок у катехитично- реколекційному будинку свв. Кирила і Мефодія, що розташований в селі Заріччя Наддвірнянського району Івано-Франківської області. Тут діти з нашої парафії відпочивали вже декілька разів. Цьогорічна поїздка також була особлива, адже відпочиваючими були батьки і діти,а також молодь нашої парафії.</p>\n','2013-08-20 15:47:49','http://oranta.org/index.php?option=com_content&view=article&id=1416:jak-my-vidpochyvaly-v-karpatah&catid=25:parafija-cat&Itemid=27','P',NULL,'Як ми відпочивали в Карпатах','uk',3525,5008,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5005,200,DSCN1779.JPG\" />У 2012 році на благодійність було  витрачено 580 мільйонів 758 тисяч і 13 гривень, що на майже 227 млн грн  більше, ніж у 2011 році. Про це свідчать результати національного  рейтингу благодійників-2012, повідомив кореспондент ГолосUA.<br /><br />При цьому масштаб благодійності в Україні склав 21%.<br /><br />«Наш  рейтинг – це перший рейтинг в Україні, складений на основі  документально-підтверджених фінансових показників, витрачених на  благодійність.</p>\n','2013-08-22 16:25:02','http://oranta.org/index.php?option=com_content&view=article&id=1418:za-mynuly-rik-na-blagodinist-v-ukrajini-vytratyly-ponad-pivmiljarda-gryven&catid=29:propovidi-cat&Itemid=34','P',NULL,'За минулий рік на благодійність в Україні витратили понад півмільярда гривень','uk',3527,5010,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5010,200,383659356.jpg\" />70% українців вважають День незалежності святом.</p>\n<p>Про це свідчать результати опитування проведеного фондом  \"Демократичні ініціативи\" та Українським центром економічних і  політичних досліджень імені Олександра Разумкова, представлені на  прес-конференції в Києві.</p>\n<p>Респондентам було поставлене питання про те, як вони ставляться до Дня незалежності.</p>\n','2013-08-23 07:28:56','http://oranta.org/index.php?option=com_content&view=article&id=1419:den-nezalezhnosti-je-svjatom&catid=37:spilnota-cat&Itemid=49','P',NULL,'День незалежності є святом','uk',3529,5012,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5019,200,koch6.png\" />4 вересня розпочнуться урочистості до 100-ліття, відтоді як  митрополит Андрей Шептицький передав Національний музей у Львові в дар  українському народові. Непересічній події 100-річної давнини присвячено  низку заходів, організованих Львівською митрополією УГКЦ та Національним  музеєм імені Андрея Шептицького (м. Львів, пр. Свободи, 20).</p>\n','2013-08-27 14:35:21','http://oranta.org/index.php?option=com_content&view=article&id=1423:u-lvovi-vidznachat-100-littja-peredachi-andrejem-sheptyckym-nacionalnogo-muzeju-narodovi&catid=37:spilnota-cat&Itemid=49','P',NULL,'У Львові відзначать 100-ліття передачі Андреєм (Шептицьким) Національного музею народові','uk',3537,5020,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5032,200,405572989.jpg\" />Догмат “Filioque”, тобто<strong> </strong><strong> </strong>вчення про сходження Святого Духа від Отця і Сина є найбільш оспорюваним, він&nbsp; став каменем спотикання в екуменічному діалозі між Західною та Східною Церквами. Дуже часто цей догмат відкидають, не маючи належного розуміння, про що саме у ньому йдеться. І майже у всіх богословських дискусіях на цю тему залишають поза увагою повний текст Нікейського Символа Віри. А у його другому члені читаємо:</p>\n','2013-08-30 10:49:48','http://oranta.org/index.php?option=com_content&view=article&id=1429:fenomenologichne-obgruntuvannja-dogmatu-filioque&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Феноменологічне обґрунтування догмату “Filioque”','uk',3551,5034,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5042,200,n.jpg\" />Початок навчання дитини в першому класі — складний і відповідальний  етап у її житті, адже відбувається дуже багато змін. Для неї це не  тільки нові умови життя та діяльності, а й нові контакти, нові стосунки,  нові обов’язки.<br /><br />Новий одяг, портфель, під­ручники, олівці,  фломастери. Ця втіха у розвитку особистості дуже важлива, адже коли в  дитини є зацікавленість, то буде результат.</p>\n','2013-09-02 23:53:55','http://oranta.org/index.php?option=com_content&view=article&id=1440:pershi-kroky-v-adaptaciji-dytyny-do-shkoly&catid=37:spilnota-cat&Itemid=49','P',NULL,'Перші кроки в адаптації дитини до школи','uk',3561,5044,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5046,200,399141.jpg\" />Перед  проказуванням молитви «Ангел Господній» у неділю, 1 вересня 2013 р., в  контексті трагічної ситуації в Сирії та на Близькому Сході, Папа  Франциск оголосив про те, що постановив призначити в усій Католицькій  Церкви <strong>«День молитви і посту за мир у Сирії, на Близькому Сході та в  усьому світі»</strong> на <strong>7 вересня</strong>, навечір’я Різдва Пречистої Діви Марії за  новим стилем. Святіший Отець закликав усі місцеві Церкви організувати  відповідні моління та повідомив, що очолить молитовне чування, що  відбуватиметься того дня на площі Святого Петра від <strong>19-ї до 24-ї години</strong>.</p>','2013-09-07 12:29:41','http://oranta.org/index.php?option=com_content&view=article&id=1457:papa-vstanovyv-den-molytvy-i-postu-za-myr-u-syriji-na-blyzkomu-shodi-ta-v-usomu-sviti-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа встановив день молитви і посту за мир у Сирії, на Близькому Сході та в усьому світі ','uk',3572,5055,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5046,200,PF.big_.jpg\" />Християнин  повинен бути веселим, таким, як у час весільної гостини, – на цьому  наголосив Папа Франциск сьогодні, 6 вересня 2013 року, під час Святої  Меси у каплиці папської резиденції Святої Марти, де він тепер проживає.  Він побудував свою проповідь на відповіді Ісуса фарисеям та книжникам на  їхнє зауваження про поведінку Його учнів, що не постять і не моляться:  «Чи ж можете примусити постити весільних гостей, поки молодий з ними?»  Поки молодий з гостями, не можна постити та бути сумними. Папа  підкреслив, що Спаситель часто використовував цей образ «нареченого»,  щоб вказати на стосунок між Ним та Церквою.</p>\n','2013-09-07 02:22:41','http://oranta.org/index.php?option=com_content&view=article&id=1456:papa-francysk-hrystyjanyn-povynen-vidznachatys-radistju-ta-cilkovytoju-posvjatoju-gospodu-isusovi-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Папа Франциск: Християнин повинен відзначатись радістю та цілковитою посвятою Господу Ісусові ','uk',3568,5051,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5046,200,teqo8d.jpg\" />4 вересня Блаженніший Святослав (Шевчук), Глава УГКЦ, узяв участь у заходах, присвячених 100-річчю відтоді, як митрополит Андрей (Шептицький) урочистим актом проголосив передачу Національного музею в дар українському народові. У цьому святковому заході взяли також участь: Архиєпископ і Митрополит Львівський Ігор (Возьняк), міський голова Львова, представники влади міста й області, духовенство, мистецтвознавці та інші зацікавлені особи.</p>\n','2013-09-05 15:14:03','http://oranta.org/index.php?option=com_content&view=article&id=1459:duhovny-skarb-andreja-sheptyckogo&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Духовний скарб Андрея Шептицького','uk',3570,5053,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5063,200,shevchuk.jpg\" />Патріарх УГКЦ Святослав (Шевчук) висловив здивування бездіяльністю  представників державних органів, які дозволяють діячам невизнаного  релігійного угрупування, відомого під назвою \"Українська Правовірна  Греко-Католицька Церква\" або <a href=\"http://risu.org.ua/ua/index/resourses/directory/%7E%D0%94/48515/\" target=\"_blank\">«догналіти»</a> , безперешкодно проповідувати і проводити акції.</p>\n<p>Про це Глава УГКЦ сказав під час <a href=\"http://risu.org.ua/ua/index/exclusive/photogallery/risu_video/53564\" target=\"_blank\">веб-конференції на РІСУ</a>.</p>\n','2013-09-10 07:08:45','http://oranta.org/index.php?option=com_content&view=article&id=1465:glavu-ugkc-dyvuje-bezdijalnist-derzhavy-shchodo-sekty-dognala&catid=37:spilnota-cat&Itemid=49','P',NULL,'Главу УГКЦ дивує бездіяльність держави щодо секти Догнала','uk',3583,5066,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5066,200,1319_thxl.jpg\" />29 липня 2010 р. Генеральна асамблея ООН прийняла резолюцію, яка закріплює право людини на воду і санітарію. На жаль, це право не гарантовано бюджетом утримання арештантів в українських слідчих установах. Особливу небезпеку від води зі старого київського водогону переживають хворі, вагітні жінки і кормлячі матері, а також неповнолітні. На відсутність в них можливості споживати чисту воду звернув увагу капелан під час обходу камер. У лютому – квітні 2013 року з ініціативи Пенітенціарного душпастирства УГКЦ&nbsp; 9 камер, де утримуються неповнолітні, були забезпечені глеками-фільтрами та картриджами, яких вистачило лише до липня.</p>\n','2013-09-11 02:40:18','http://oranta.org/index.php?option=com_content&view=article&id=1466:karitas-kyjiv-ta-penitenciarne-dushpastyrstvo-ugkc-zabezpechyly-vjazniv-filtramy-dlja-vody&catid=29:propovidi-cat&Itemid=34','P',NULL,'Карітас Київ та Пенітенціарне душпастирство УГКЦ забезпечили в’язнів фільтрами для води','uk',3584,5067,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5070,200,38fcc3d4fee1a0238aeb44afc2d7a3fb.jpg\" />Глава УГКЦ підкреслив, що прийшов до Львівської політехніки не як ієрарх, а як приятель і звичайний віруючий.</p>\n<p>Блаженнійшого Святослава львівські політехніки зустрічали з таким  піднесенням і пієтетом, що мимоволі пригадалася зустріч Папи Римського  Івана Павла ІІ у Львові 2001 року. Студенти вручили главі УГКЦ  хліб­сіль, а чоловічий хор “Орфей” виконав на честь поважного гостя  духовний твір “Блаженний муж”.</p>\n','2013-09-13 03:03:26','http://oranta.org/index.php?option=com_content&view=article&id=1468:-lsvjashchenyky-povynni-bilshe-chytaty-vymagate-vid-nyh-jakisnyh-propovider&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Священики повинні більше читати. Вимагайте від них якісних проповідей»','uk',3589,5072,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5082,200,Pope-Francis-010.jpg\" />Коли  судимо ближнього, ризикуємо залишитися поза домом Небесного Отця. Боже милосердя є головною думкою трьох притч, вміщених у ній. Це притчі   про загублену вівцю, про загублену драхму та про милосердного батька і   двох синів.</p>\n<p>Всі ці притчі, за словами Папи, говорять про Божу   радість. «І в чому ж полягає радість Бога? – запитав він, відповідаючи: –   Божа радість – це прощати».</p>\n','2013-09-17 06:59:19','http://oranta.org/index.php?option=com_content&view=article&id=1475:proshchennja-je-suttju-bozhoji-radosti&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Прощення є суттю Божої радості','uk',3600,5083,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5089,200,4_52058.jpg\" />У невеличкому галілейському місті Назареті жила літня подружня пара -  Іоаким і Анна. Обоє були праведні і благочестиві. Святе подружжя Іоаким  і Анна довгий час були бездітними і плакали про те, що у них немає  дітей. Одного разу Іоаким у велике свято приніс в Єрусалимський храм  дари Господу Богу. Але священик не захотів прийняти дарів Іоакима, тому  що він був бездітний, а діти вважалися благословенням Божим.</p>\n','2013-09-21 08:55:47','http://oranta.org/index.php?option=com_content&view=article&id=1487:rizdvo-presvjatoji-bogorodyci&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Різдво Пресвятої Богородиці','uk',3608,5091,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5093,200,55416800_1266607446_Lestvica1.jpg\" />Гроші  часто спричинюють захворювання нашого мислення і віри та скеровують нас  на погану дорогу. Папа Франциск&nbsp; вважає, що від  ідолопоклонства грошам родиться інше зло, таке як гордість та суєта,  сварки та заздрість, підозри та різного роду конфлікти. «Не можна  служити Богові та грошу», – на цьому твердженні святого Апостола Павла  Папа Франциск побудував свою проповідь. Він зауважив, що грошолюбство  віддаляє людину від Господа Бога.</p>\n','2013-09-21 15:03:25','http://oranta.org/index.php?option=com_content&view=article&id=1489:shukaty-lpravednosti-pobozhnosti-viry-ljubovi-terpelyvosti-lagidnostir-bo-same-takoju-je-bozha-doroga&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Шукати «праведності, побожності, віри, любові, терпеливості, лагідності», бо саме такою є Божа дорога','uk',3611,5094,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5095,200,46DBDED0-1D31-44E1-9C85-3F2491B277BB_mw1024_n_s.jpg\" />Письменник, дисидент, філософ згадує Бориса–Антоненка  Давидовича, Олеся Гончара, Івана Світличного, Миколу Лукаша, Максима  Рильського, Василя Симоненка, Василя Стуса</p>\n<p><strong>Борис Антоненко–Давидович, письменник i мовознавець</strong></p>\n','2013-09-22 01:53:15','http://oranta.org/index.php?option=com_content&view=article&id=1490:-sim-suchasnykiv-jevgena-sverstjuka-&catid=37:spilnota-cat&Itemid=49','P',NULL,' Сім сучасників Євгена Сверстюка ','uk',3613,5096,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5106,200,927343544.jpg\" />Історія стосунків «Папа Франциск–ЗМІ» й далі розгортається за звичним сценарієм: ЗМІ надалі хвалять Папу, при чому за те, чого він не сказав, або за те, чого він не робив. Так триває практично від часу обрання його єпископом Риму.</p>\n<p>Так і минулого тижня ЗМІ в чергове шокували спантеличений світ: Папа підтримує гомосексуалістів та заперечує право Церкви говорити про гріховність абортів та контрацепції. І всьому причиною стало <a href=\"http://catholicnews.org.ua/velike-serce-vidkrite-bogovi-intervyu-z-papoyu-franciskom\">інтерв’ю Папи Франциска для видання «La Civiltà Cattoliа»</a>.</p>\n','2013-09-24 22:46:50','http://oranta.org/index.php?option=com_content&view=article&id=1503:namarno-lementujut-lortodoksyr-ta-tishatsja-liberaly-vdyvljajuchys-u-kryve-dzerkalo-zmi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'НАМАРНО ЛЕМЕНТУЮТЬ «ОРТОДОКСИ» ТА ТІШАТЬСЯ ЛІБЕРАЛИ, ВДИВЛЯЮЧИСЬ У КРИВЕ ДЗЕРКАЛО ЗМІ','uk',3625,5108,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5106,200,ekologicheskie-problemy-eto-mina-zamedlennogo-deystviya.jpg\" />Особливої уваги заслуговує міжрелігійна конференція на тему «Захист довкілля в контексті міжрелігійної відповідальності», присвячена відзначенню Дня відповідальності за створіння та «Часу створіння», яка відбулася 20 вересня. Під час цього науково-практичного заходу проф. Маркус Фогт, директор Інституту соціальної етики Мюнхенського університету, висвітлив біблійно-богословські та етичні основи відповідальності за створіння, торкнувся питання церковно-екологічного вчення, наголосив на значенні християнської духовності, а також суспільно-політичної ролі християн та Церков у справі охорони створеного Богом світу – природи.</p>\n','2013-09-25 23:06:04','http://oranta.org/index.php?option=com_content&view=article&id=1504:zahyst-dovkillja-potrebuje-praktychnoji-mizhreliginoji-vidpovidalnosti&catid=37:spilnota-cat&Itemid=49','P',NULL,'Захист довкілля потребує практичної міжрелігійної відповідальності','uk',3626,5109,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5117,200,foto_patriarch2.jpg\" />30 вересня в Україні вп’яте відзначають День усиновлення. За словами Блаженнішого Святослава (Шевчука), Глави УГКЦ, віруюча людина усиновлена Богом. Адже кожен із нас через таїнство Хрещення є сином і донькою Господа. «Ідея усиновлення, з духовного, чи богословського погляду, є важливою і невід’ємною складовою частиною християнської віри», − пояснив він.</p>\n<p>За словами Предстоятеля, сироти – одні з найбільш незахищених категорій суспільства. Тому, коли хтось допомагає сироті, її захищає чи чинить милосердя, то, згідно зі Святим Письмом, сам Бог винагороджує таку особу.</p>\n','2013-09-30 17:21:44','http://oranta.org/index.php?option=com_content&view=article&id=1513:lideja-usynovlennja-je-vazhlyvoju-i-nevidjemnoju-skladovoju-chastynoju-hrystyjanskoji-viryr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Ідея усиновлення… є важливою і невід’ємною складовою частиною християнської віри»','uk',3640,5123,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5125,200,7667.jpg\" />Делегація глав і представників українських церков і   релігійних організацій 1 жовтня 2013 року зустрілась з президентом  Європейської Ради <strong> Германом ван Ромпеєм</strong>, повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.</p>\n<p>\"Україна  для Європи є дуже важливим партнером. Ми значно просунулися вперед у  відносинах, особливо в останній час. Сподіваюся, що у листопаді буде  підписано Угоду про асоціацію.</p>\n','2013-10-01 19:41:44','http://oranta.org/index.php?option=com_content&view=article&id=1514:vidnosyny-ukrajiny-ta-jes','P',NULL,'Відносини України та ЄС','uk',3643,5126,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5125,200,universum.jpg\" />Протягом багатьох років дуже міцно в  головах багатьох людей сидить впевненість у тому, що наука спростовує  віру, тому й дуже хочеться довести протилежне. Але не потрібно впадати у  крайнощі. Серед завдань апологетики є і завдання показати, що релігія і  наука не є несумісними, не заперечують один одного і цілком можуть  співіснувати, уживатися разом не тільки в суспільстві, але і всередині  окремої людини. А намагатися довести істини віри за допомогою науки - це  називається перебором.</p>\n<p><strong>Чому це так?</strong></p>\n','2013-10-02 09:55:57','http://oranta.org/index.php?option=com_content&view=article&id=1515:-bez-naukovyh-dokaziv-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Без наукових доказів ','uk',3644,5127,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5137,200,04-10-13-4.jpg\" />«Ми кажемо щодо митрополита Андрея «великий син», але, на мою думку, краще було б сказати «великий батько нашого народу». Про це сказав Архиєпископ-емерит Блаженніший Любомир (Гузар), відкриваючи 3 жовтня в Києві виставку «Пам’яті митрополита Андрея Шептицького. Пам’ятки з колекції Музею сакрального мистецтва імені о. Антона Петрушевича Львівської митрополії УГКЦ».</p>\n','2013-10-05 22:47:28','http://oranta.org/index.php?option=com_content&view=article&id=1527:lvelyky-batko-nashogo-narodur&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Великий батько нашого народу»','uk',3655,5138,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5145,200,preview.jpg\" />Причини, чому наші молитви здаються нам  непочутими, бувають різні. Молитва не потрібна для того, щоб давати  Богові вказівки. Молитва служить для нашого повчання. Коли на наші  молитви немає відповіді, ми повинні задатися питанням: що не так у моєму  житті і що в ньому повинно змінитися?</p>\n<p>Іноді нам здається, що ми просимо про  щось хороше, позитивне, але насправді те, про що просимо, не служить  нашому спасінню.</p>\n','2013-10-08 22:30:24','http://oranta.org/index.php?option=com_content&view=article&id=1530:chomu-bog-nikoly-ne-chuje-moji-molytvy-i-ne-vidpovidaje-na-nyh&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чому Бог ніколи не чує мої молитви і не відповідає на них?','uk',3663,5146,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5151,200,news3.jpg\" />Що означає бути католиком? Святіший  Отець наголосив, що Церква є католицькою, тобто вселенською, бо є місцем,  де звіщається вся віра в її інтегральності,Церква звіщає Боже милосердя,  вчить правд віри, вказує, як жити, усіх веде до спасіння. У сім’ї  отримуємо те, що дозволяє нам зростати, дозрівати, жити.  Не можемо  зростати самі по собі, крокувати на самоті, ізолюватись від інших. Для  людського зросту потрібна родина, потрібна спільнота.</p>\n','2013-10-10 23:39:00','http://oranta.org/index.php?option=com_content&view=article&id=1532:cerkva--ce-ne-vybrana-elita-a-domivka-dlja-vsih&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Церква – це не вибрана еліта, а домівка для всіх','uk',3670,5153,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5161,200,4456.d.jpg\" />Роль жінки і материнства були в центрі промови Святішого Отця до учасників семінару, організованого у Ватикані з нагоди двадцять п\'ятої річниці Апостольського листа бл. Івана Павла II \"Mulieris dignitatem\" − \"історичного документу, першого в Папському навчанні, повністю присвяченого темі жінки\". 12 жовтня 2013р., звертаючись до присутніх у Клементійському Залі Апостольського палацу, Франциск висловив свою вдячність за увагу до цієї теми. Учасники ватиканського семінару головним чином роздумували над фрагментом Апостольського листа, в якому мова йде про те, що Бог особливим чином довірив людство жінці.</p>\n','2013-10-14 10:06:28','http://oranta.org/index.php?option=com_content&view=article&id=1540:pytannja-pro-rol-zhinky-v-suspilstvi-cerkvi-blyzke-mojemu-sercju-papa-francysk&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Роль жінки в суспільстві й Церкві','uk',3683,5166,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5170,200,CRW_8619.jpg\" />Прекрасні розписи церкви св. Миколая Чудотворця милують сьогодні око парафіян та гостей нашої церкви. Ініціатором оздоблення стін і купола церкви виступив Богдан Батрух, а виконані розписи належать київському художнику Юрію Матвійчуку, який зі своїми помічниками Іриною Ганіною і&nbsp; Валерієм Васильєвим здійснили цей чудовий розпис. Сам розпис купола здійснювали за ескізами Олександра Пигарьова.</p>\n','2013-10-16 03:12:32','http://oranta.org/index.php?option=com_content&view=article&id=1552:vidkryte-nebo-nad-hramom-gospodnim&catid=25:parafija-cat&Itemid=27','P','','Відкрите небо над храмом Господнім','uk',3689,5172,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5175,200,542676_553226144710004_1408743727_n.jpg\" />Велика радість і милість Божа огортає серце кожного, хто зголошується послужити дитині. При церкві св. Миколая на Аскольдовій могилі, за благословенням отця-настоятеля Ігоря Онишкевича, створена парафіяльна служба милосердя «Карітас-Печерськ». Упродовж п’яти років наша парафія допомагає, опікуючись дітьми з потребуючих родин, напівсиротами і такими, яких батьки перебувають у місцях позбавлення волі, а разом із ними людям, котрі потребують допомоги.</p>\n','2013-10-17 20:20:45','http://oranta.org/index.php?option=com_content&view=article&id=1555:dity-bozhogo-myloserdja&catid=25:parafija-cat&Itemid=27','P','','Діти Божого Милосердя','uk',3694,5177,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5182,200,sercya.jpg\" />«Більше всього, що стережеться, серце своє стережи» Хвороби серця були і залишаються, найбільш поширеними у світі. У 2009 р. Україна втратила більш ніж 460 тисяч людських життів внаслідок смертності від серцево-судинних захворювань. Майже кожна друга людина в Україні знайома з болем у серці. &nbsp;«Хворобам немає числа» - писав Пліній молодший. Проте, багато страшнішим недугом людини є гріх.</p>\n','2013-10-20 23:36:07','http://oranta.org/index.php?option=com_content&view=article&id=1559:transplantacija-sercja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Трансплантація серця','uk',3700,5183,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5184,200,Bible_12.jpg\" />Нерідко можна почути закиди на адресу  християн, ніби у них багато чого суперечить Святому Писанню - Біблії.  Наприклад, вони моляться Богородиці і святим, а не тільки одному Богові,  а також вшановують ікони і називають священиків «отцями», хоча Біблія  забороняє це робити. Вони виконують безліч обрядів, про які в Біблії  начебто немає ні слова.</p>\n','2013-10-21 21:04:47','http://oranta.org/index.php?option=com_content&view=article&id=1560:-chomu-u-hrystyjan-lne-vse-po-biblijir&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Чому у християн «не все по Біблії»?','uk',3703,5186,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5204,200,4fc8e161c56.jpg\" />29 жовтня 2013 року відбулась офіційна зустріч Блаженнішого Святослава (Шевчука), Отця і Глави УГКЦ, з міністром культури України Леонідом Новохатьком. Під час зустрічі було обговорено виклики, які стоять перед українським суспільством, пов’язані з глобалізацією, зокрема у сфері культури і духовності.</p>\n','2013-10-29 23:03:32','http://oranta.org/index.php?option=com_content&view=article&id=1580:losoblyvist-ukrajiny-v-tomu-shcho-cerkvy-i-religini-organizaciji-je-u-ni-vazhlyvym-chynnykom-suspilstvar&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Особливість України в тому, що Церкви і релігійні організації є у ній важливим чинником суспільства...»','uk',3722,5205,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5212,200,4266_n59881076.jpg\" />Двоєслов<a href=\"http://oranta.org/#_ftn1\">[1]</a> каже: \"<em>Бог не слухає</em> <em>цеї молитви, якої не хоче і той, що молиться</em>\". Третя ціль молитви: якийсь корм душі. І до осягнення цеї ціли конечне потрібна є увага в молитві, як каже св[ятий] Ап[остол] Павло: \"<em>Коли молюся язиками (незрозумілими), мій дух молиться, але мій ум без пожитку, що ж отже? буду молитися духом – буду молитися умом – співатиму</em> <em>духом, співатиму й умом</em>\"<a href=\"http://oranta.org/#_ftn2\">[2]</a>.</p>\n','2013-10-30 19:52:07','http://oranta.org/index.php?option=com_content&view=article&id=1581:mytropolyt-andre-pro-gospodnju-molytvu&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Митрополит Андрей про Господню молитву','uk',3730,5213,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5220,200,1395830.jpg\" />Про голод в Україні я дізнався не з газет. Мої дід та бабуся пройшли  через це пекло і багато розповідали мені малому. Добре знали люди про  те, що голод організовано штучно комуністичною владою, що метою влади  було максимально обезлюднити цю територію, а тих, кому вдасться якимось  дивом вижити, страхом перетворити на покірливе, безгласне стадо, з яким  можна робити все що завгодно. Характерний приклад: найбільше постраждала  в ті роки Харківська область. І які тепер там настрої?</p>\n<p>Після того як влада наважилася відкрити цю велетенську домовину,  почалися дослідження, стали писати в газетах, публікувати спогади  свідків.</p>\n','2013-11-01 16:26:11','http://oranta.org/index.php?option=com_content&view=article&id=1594:hliba-shchodennogo-da-nam&catid=37:spilnota-cat&Itemid=49','P',NULL,'Хліба щоденного дай нам...','uk',3738,5221,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5222,200,1426709.jpg\" />«Говорячи про слугу Божого митрополита Андрея, роздумуючи над значенням цієї постаті в історії нашої Церкви й нашого народу, ми сьогодні служимо Літургію за його прославу», – зауважив Предстоятель під час своєї проповіді до членів Патріаршого товариства.</p>\n','2013-11-02 17:37:59','http://oranta.org/index.php?option=com_content&view=article&id=1595:lnemozhlyvo-govoryty-pro-ukrajinsky-patriotyzm-bez-postati-andrejar&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'«Неможливо говорити про український патріотизм без постаті Андрея»','uk',3740,5223,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5225,200,0_85bb1_4b28f18d_L.jpg\" />У своєму історичному дослідженні, на основі доступних мені матеріалів та документів, хочу розглянути та проаналізувати особу екзарха о. Леоніда Фьодорова, його діяльність та внесок у розвиток Російської Греко-Католицької Церкви (далі РГКЦ) на тлі нелегких історичних та політичних подій того часу. Його життя та діяльність тісно проходить разом з видатною постаттю Української Греко-Католицької Церкви ХХ століття – митрополитом Андреєм Шептицьким, що від початку їхнього знайомства, став для отця Леоніда Фьодорова духовним отцем.</p>\n','2013-11-03 09:32:51','http://oranta.org/index.php?option=com_content&view=article&id=1598:mytropolyt-andre-cherez-pryzmu-blazhennogo-svjashchennomuchenyka-leonida-fodorova&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Митрополит Андрей через призму  Блаженного Священномученика Леоніда Фьодорова','uk',3743,5226,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5225,200,IMG_7414.JPG\" />Архипастирське служіння Блаженнішого Любомира (Гузара) Церкві і своєму народові вирізняє духовна проникливість і зріла та виважена християнська позиція в різних аспектах духовного і суспільного життя України.<em></em></p>\n<p><em>Як десять років тому, так і сьогодні харизматична постать Блаженнішого Любомира вирізняється високим моральним авторитетом, загальновизнаним в Україні та поза нею.</em></p>\n','2013-11-04 11:29:33','http://oranta.org/index.php?option=com_content&view=article&id=1599:potreba-duhovnoji-rivnovagy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Потреба духовної рівноваги','uk',3744,5227,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5229,200,w823Sxh5k0k.jpg\" />Я ніколи не був прихильником конспірологічних теорій. Будь-які розмови про те, що всі довкола ведуть проти України свої підступні війни, аби дискредитувати, закабалити чи зруйнувати нашу державу, викликали в мене лише скептичну посмішку. Проте історія з погруддям і пам’ятною дошкою Іванові Франку у Відні спровокували в моїй душі певні сумніви щодо попередніх переконань.</p>\n','2013-11-05 07:35:33','http://oranta.org/index.php?option=com_content&view=article&id=1600:vina-proty-franka-konspirologija-neviglastva&catid=37:spilnota-cat&Itemid=49','P',NULL,'Війна проти Франка: конспірологія невігластва','uk',3748,5231,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5231,200,6_large.jpg\" />У розпалі проєвропейської пропаганди і страстей довкола цього: буде чи не буде асоціація з ЄС, як відбити інформаційні атаки Москви і таке інше, українські ЗМІ практично не помітили надзвичайно важливої новини, яка має значення і в світлі європейськості України, із погляду її духовної спадщини. Одна з найбільш радикально налаштованих у світі організацій, що борються проти антисемітизму,</p>\n','2013-11-05 13:46:32','http://oranta.org/index.php?option=com_content&view=article&id=1602:prydimo-do-sebe&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Прийдімо до себе!','uk',3750,5233,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5242,200,128319726202006846.jpg\" />Навколо нас панує замішання, ми уражені різноманітними психозами або  ризикуємо захворіти ними. Ісус же у євангельській події простий і  прямий. Сьогодні йдеться про Нього і про нас, про наше майбутнє в Ньому,  про наше воскресення, одно слово, як би вам це не було дивно, про  Церкву.</p>\n<p>Так, вже десять років поспіль у третю неділю після Воздвиження  Чесного Хреста Господнього, читаючи це Євангеліє, ми поступово  віднаходимо багато аналогій між описаною подією і тим, що переживаємо.</p>\n','2013-11-08 23:34:33','http://oranta.org/index.php?option=com_content&view=article&id=1609:cerkva-isus-i-my-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Церква, Ісус і ми! ','uk',3761,5244,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5245,200,u_chervonyx.jpg\" />Блаженніший Святослав адресував своє слово до кардинала Крістофа Шенборна, Архиєпископа Віденського, Ординарія для вірян візантійського обряду, з нагоди симпозіуму «Що означає сьогодні, бути християнином? Із досвіду мучеництва Східних Церков», який відбувається у Відні (Австрія) 9 листопада. Це вітання зачитав владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, який представляв нашу Церкву на цьому заході.</p>\n','2013-11-09 23:16:06','http://oranta.org/index.php?option=com_content&view=article&id=1614:lnepohytna-vira-muchenykiv-hh-stolittja-mozhe-nadyhnuty-jevropeciv-staty-na-shljah-hrystyjanskogo-zhyttjar&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Непохитна віра мучеників ХХ століття може надихнути європейців стати на шлях християнського життя»','uk',3763,5246,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5258,200,420133.jpg\" />Головний пенітенціарій Київщини — про виховну роль «зони» та покращення побутових умов, медичної допомоги і харчування за колючим дротом</p>\n<p><strong>Рік тому указом Президента було схвалено концепцію державної політики у сфері реформування Державної кримінально–виконавчої служби України. За цей рік служба стала пенітенціарною.</strong></p>\n','2013-11-12 23:30:38','http://oranta.org/index.php?option=com_content&view=article&id=1630:my-bilshe-ne-karajuchy-organ&catid=37:spilnota-cat&Itemid=49','P',NULL,'Ми більше не караючий орган','uk',3776,5259,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5262,200,0_556c0_1018cf54_L.jpg\" />Я люблю ранок, його свіжість і незайманість, безтурботність. Бо з настанням дня все може скластися по-іншому. А то ще був ранок, пробудження нового дня. Осіннє сонце лінувалося виповзти із-за хмар, котрі густим рядном вкривали небо. Ні, радше й не видно було неба, бо рядно було грубим і лляним, на вигляд щойно витканим і не праним, то мабуть було угодно якось виправити цю помилку.</p>\n','2013-11-14 03:25:14','http://oranta.org/index.php?option=com_content&view=article&id=1633:ja-ljublju-ranok&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Я люблю ранок...','uk',3781,5264,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5264,200,Soprychastya-2013.jpg\" />Україна вшановує 80-ту річницю Голодомору. Серед низки меморіальних заходів, які супроводжують ці трагічні роковини, важливо не загубити пам’ять про Людину, що стала жертвою цієї неоголошеної війни.</p>\n<p>«Ми згадуємо про сам факт голоду 1932-1933 років, про його призвідників, але не в стані осягнути жаху, перед яким лице-в-лице опинився пересічний українець, з хати якого вимели останню зернину, село якого оточене кільцем вартових, діти якого просять їсти...</p>\n','2013-11-13 23:00:18','http://oranta.org/index.php?option=com_content&view=article&id=1635:ukrajinciv-zaklykajut-akcijeju-soprychastja-vshanuvaty-80-tu-richnycju-golodomoru&catid=37:spilnota-cat&Itemid=49','P',NULL,'Акція “Сопричастя” - вшануй 80-ту річницю Голодомору','uk',3782,5265,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5266,200,z_zhyvoho_tv_0.jpg\" />Блаженніший Святослав адресував своє слово до кардинала Крістофа Шенборна, Архиєпископа Віденського, Ординарія для вірян візантійського обряду, з нагоди симпозіуму «Що означає сьогодні, бути християнином? Із досвіду мучеництва Східних Церков», який відбувається у Відні (Австрія) 9 листопада. Це вітання зачитав владика Богдан (Дзюрах), Секретар Синоду Єпископів УГКЦ, який представляв нашу Церкву на цьому заході.</p>\n','2013-11-14 21:52:32','http://oranta.org/index.php?option=com_content&view=article&id=1638:shcho-oznachaje-sogodni-buty-hrystyjanynom&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Що означає сьогодні бути християнином?','uk',3784,5267,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5272,200,1462176.jpg\" />Недільний чи святковий день. Велика кількість людей спішать до храму, церкви наповнені. Після спільної молитви повертаємось до щоденних занять, звичного способу життя. &nbsp;Питання в тому, чи ми справді ідемо за Христом? Християнство, яке ми собі уявляємо є нашим, власним, показним і побудоване на великих компромісах. Часто для сучасної людини бути християнином, це час від час відвідати церкву, підійти до сповіді один, або кілька раз у рік, можливо, зробити певну пожертву.</p>\n','2013-11-17 08:54:02','http://oranta.org/index.php?option=com_content&view=article&id=1641:hrystyjanstvo-radist-ljubovi&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Християнство - радість любові','uk',3790,5273,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5275,200,1424448.jpg\" />У неділю сімнадцятого листопада о 17:00 розпочався 4 молодіжний благодійний бал. Він проходив у Центральному будинку офіцерів Збройних Сил України. Організатором заходу виступила ТУСК «Обнова», за підтримки Молодіжної Комісії Київської Архієпархії УГКЦ, Центру Академічного Душпастирства УГКЦ, Департаменту Військового Капеланства УГКЦ, Благодійного Фонду Карітас Київ.</p>\n','2013-11-19 00:32:05','http://oranta.org/index.php?option=com_content&view=article&id=1643:molodizhny-blagodiny-bal&catid=25:parafija-cat&Itemid=27','P',NULL,'Молодіжний Благодійний Бал','uk',3795,5278,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5278,200,holovatskyj.jpg\" />На жаль, у нашому журналістському житті часто трапляється так, що згадуємо про людей, коли вони відзначають або ювілеї, або ж відходять на вічний спочинок. Іменини у цьому ж ряду. Про отця Головацького хотілося написати раніше, проте повсякденні клопоти чомусь завжди стояли на заваді у здійсненні цього задуму. Жалію, що не поспілкувався зі священиком, не прислухався до його мудрих слів і порад.</p>\n','2013-11-19 17:50:11','http://oranta.org/index.php?option=com_content&view=article&id=1645:pid-stjagom-svogo-patrona-vin-nis-bozhe-slovo-pidtrymuvav-iskru-viry-nadiji-ta-ljubovi&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Під стягом свого патрона він ніс Боже Слово, підтримував іскру віри, надії та любові','uk',3797,5280,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5286,200,image.jpg\" />Відзначаючи свято Собору святого архистратига Михаїла і всіх безплотних сил (21 листопада), Церква підкреслює особливе значення ангелів у житті людей. Тому цього разу у «Відкритій розмові» з о. Василем Рудейком, доктором богослов\'я, заступником голови Патріаршої літургійної комісії УГКЦ, священиком храму Священномученика Климентія Шептицького у Львові, говоритимемо про ангелів та архистратига Михаїла.</p>\n','2013-11-21 23:56:25','http://oranta.org/index.php?option=com_content&view=article&id=1647:larhangel-myhajil-vojevoda-jaky-vojuje-na-storoni-bogar&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Архангел Михаїл – воєвода, який воює на стороні Бога…»','uk',3804,5287,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5294,200,DSCN4152.JPG\" />Християнська  пам\'ять не є пам’яттю злоби чи жалю, але перемінюється в молитву. На це  звернув увагу Блаженніший Святослав Шевчук, Глава УГКЦ, проповідуючи у  переповненій базиліці Святої Софії в Римі у суботу, 23 листопада 2013  року, коли в Україні та по всьому світі поминають жертви Голодомору  українського народу 1932-1933 років. У спільній молитві, як зазначив  Глава УГКЦ, єдналася «уся наша помісна Українська Греко-Католицька  Церква, адже співслужили єпископи з Канади, США, Бразилії, з  європейських країн та різних митрополій і єпархій української землі», а  також численні священики, які служать у Римі та Італії, і ті, які  прибули на прощу з нагоди 50-ї річниці покладання мощей святого Йосафата  в базиліці святого Петра. Були присутні представники дипломатичного  корпусу, зокрема, Посол України в Італії та Посли України і Польщі при  Святому Престолі.</p>\n','2013-11-23 18:01:32','http://oranta.org/index.php?option=com_content&view=article&id=1650:neha-nikoly-ne-zagasne-svicha-pamjati-v-sercjah-nashyh-nashchadkiv&catid=37:spilnota-cat&Itemid=49','P',NULL,'Нехай ніколи не загасне свіча пам’яті в серцях наших нащадків!','uk',3812,5295,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5294,200,index.jpg\" />У  липні 2012 р., в Італії вийшла друком книжка «Матері Собору. Двадцять  три жінки на ІІ Ватиканському Соборі» – (Madri del Concilio. Ventitré  donne al Vaticano II) про черниць та мирянок, які брали участь у Другому  Ватиканському Соборі як слухачки-аудиторки, запрошені Папою Павлом VІ.  Книжку написала Адріана Валеріо, італійський теолог та історик, професор  історії християнства та історії Церкви університету «Федеріко ІІІ» в  Неаполі.</p>\n','2013-11-25 12:55:00','http://oranta.org/index.php?option=com_content&view=article&id=1652:sestra-vasylijanka-klavdija-fedash-ta-sestra-sluzhebnycja-jeronima-hymi--uchasnyci-ii-vatykanskogo-soboru&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Сестра Василіянка Клавдія Федаш та Сестра Служебниця Єроніма Химій – учасниці ІІ Ватиканського Собору','uk',3815,5298,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5294,200,topic33.jpg\" />«<em>Lumen Fidei</em> – <em>Світло віри</em>»  – так називається перша Енцикліка Папи Франциска, підписана 29 червня  2013 р., що побачила світ у п’ятницю, 5 липня. Звернена до єпископів,  пресвітерів, дияконів, богопосвячених осіб та всіх мирян і поділена на  чотири розділи, ця енцикліка, як зазначає сам Папа Франциск у 7 пункті,  була майже завершена його попередником, і додає: За це я йому дуже  вдячний, і в Христовому братерстві, приймаю цю його цінну працю, додаючи  до тексту деякий свій вклад.</p>\n','2013-11-24 12:47:53','http://oranta.org/index.php?option=com_content&view=article&id=1651:lumen-fidei--persha-encyklika-papy-francyska&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Lumen Fidei – перша Енцикліка Папи Франциска','uk',3813,5296,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5298,200,IMG_3650.jpg\" />24 листопада цього року вірні греко –католицької церкви відзначали день пам’яті Йосафати Гордашевської. Хоча офіційна дата є 20 листопада, але цієї неділі парафіяни різних церков мали змогу відвідати Реколекційно – відпочинковий центр Сестер Служебниць в Києві, де в пам\'ять про сестру Йосафату відбувся акафіст.</p>\n<p>Варто сказати, що в цей день монастир відвідало близько п’ятдесяти людей. З нашої парафії церкви святого Миколая, що на Аскольдовій Могилі також було багато людей. До цієї події залучилися також і старшокласники, які поїхали в монастир сестер одразу після закінчення Божественної Літургії.</p>\n','2013-11-26 11:25:20','http://oranta.org/index.php?option=com_content&view=article&id=1658:den-pamjati-osafaty-gordashevskoji&catid=25:parafija-cat&Itemid=27','P',NULL,'День пам’яті Йосафати Гордашевської','uk',3818,5301,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5300,200,u_polshi.jpg\" />Перебуваючи в Римі з офіційним візитом до Ватикану, Глава УГКЦ дав інтерв’ю італійському інтернет-виданню «Vatican Insider», провідному інформаційному джерелу про життя Католицької Церкви в усьому світі. У розмові з відомим ватиканістом Андреа Торніеллі Глава УГКЦ розповів про екуменічний діалог, вплив Папи Франциска на християн в Україні та вшанування 80-ї річниці Голодомору 1932−1933 рр.</p>\n<p><strong>Який вплив понтифікату Папи Франциска у вашій країні?</strong></p>\n','2013-11-27 18:53:26','http://oranta.org/index.php?option=com_content&view=article&id=1659:pro-ekumenizm-lznyzur-lefektr-papy-francyska-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Про екуменізм «знизу», «ефект» Папи Франциска в Україні','uk',3819,5302,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5306,200,dsc_0027_f009c.jpg\" />«Сьогодні, як ніколи, нашому народові потрібна мудрість і сила не насилля, але Божої істини; сила не меча чи зброї, але Божого слова; сила не зла, яким хочеться відповісти на зло, але сила Божа, що походить від Його слова. Тільки така сила є тою, яка звільняє», – сказав Блаженніший Святослав у проповіді під час Літургії у Івано-Франківську.</p>\n','2013-12-01 21:48:58','http://oranta.org/index.php?option=com_content&view=article&id=1664:lvseperemagajuchym-je-ne-mech-ne-zbroja-a-syla-bozhogo-slovar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Всеперемагаючим є не меч, не зброя, а сила Божого слова»','uk',3827,5310,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5311,200,38741952.jpg\" />Зважаючи на суспільно-політичну ситуацію в Україні, закликаю усіх вірних Церкви та людей доброї волі до безперервної молитви за кращу долю для нашого народу. Прошу всіх молитися, щоб Господь подав свою мудрість нашому суспільству загалом і кожному громадянину зокрема. Молімося, щоб Він не допустив до кайданів зла, але своїм миром і мудрістю звільнив наш народ від усього того, що його поневолює, а відтак простелив шлях у майбутнє.</p>\n','2013-12-02 15:10:47','http://oranta.org/index.php?option=com_content&view=article&id=1666:zaklyk-blazhennishogo-svjatoslava-glavy-ugkc-do-bezperervnoji-molytvy-za-myr-i-krashchu-dolju-ukrajiny&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Заклик Блаженнішого Святослава, Глави УГКЦ, до безперервної молитви за мир і кращу долю України','uk',3829,5312,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5313,200,4.jpg\" />Підготуватися  до Різдва молитвою, справами милосердя і хвалою Богу закликав Папа  Франциск під час Літуршії в каплиці Дому Cв. Марти 2 грудня, в перший  понеділок Адвенту.</p>\n<p>Коментуючи уривок з Євангелія дня, в якому римський сотник з великою  вірою просить Ісуса зцілити свого раба, Папа нагадав про те, що в ці дні  ми \"починаємо новий шлях\", - \"шлях Церкви до Різдва\". Це свято, уточнив  Він, є не тільки періодично повторюваною датою або спогадом про щось  прекрасне\":</p>\n','2013-12-03 01:03:45','http://oranta.org/index.php?option=com_content&view=article&id=1669:rizdvo-poljagaje-v-tomu-shchob-z-vidkrytym-sercem-dozvolyty-isusovi-zustrity-nas&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Різдво полягає в тому, щоб з відкритим серцем дозволити Ісусові зустріти нас','uk',3832,5315,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5316,200,1383883.jpg\" />«Коли ми розмірковуємо про приховане життя Назарету та духовний прогрес Марії в його тиші, та коли порівняємо те, що вважається у світі так званим прогресом, то доходимо до висновку, що люди ще ніколи не говорили так багато про прогрес як тоді, коли почали нехтувати його найважливішою частиною – духовним прогресом. І який результат з цього ми маємо?</p>\n','2013-12-03 23:14:53','http://oranta.org/index.php?option=com_content&view=article&id=1670:svjaty-arhangele-myhajile-zahyshcha-nas-v-borotbi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святий Архангеле Михаїле, захищай нас в боротьбі!','uk',3834,5317,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5319,200,DSC_0329_800x531.jpg\" />З 3 грудня на Майдані чергують греко-католицькі священики: учора отці Київської Архиєпархії душпастирювали у наметовому містечку, а сьогодні, після встановлення польової каплички, перебувають на площі біля стелли, де 29 листопада відбулося жорстоке побиття студентів та молоді.</p>\n<p>Протоієрей Ігор Онишкевич, декан київський, щодня координує священиків. Сьогодні на площі була встановлена польова капличка, де мають змогу помолитися мітингуючі з усіх куточків України. Біля каплички зібралися священики УПЦ КП: “Уже маємо свою капличку – а де є церква, там буде село”, – каже один зі священиків.</p>\n','2013-12-04 23:06:24','http://oranta.org/index.php?option=com_content&view=article&id=1691:svjashchenyky-ugkc-vstanovyly-polovu-kaplycju-na-madani&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Священики УГКЦ встановили польову каплицю на Майдані','uk',3838,5321,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5320,200,gudzak.jpg\" />Стихія великого розчарування, напруження і страху, яка поєднана з колосальним піднесенням і мобілізацією, пульсує в наших головах, серцях і душах.</p>\n<p>Антиномія протилежних почуттів бентежить, мабуть, усіх. Стоїть питання: властиво, як до цього всього ставитися? Який метод буде найефективнішим для акцій мирного протистояння? Що приведе до реального позитивного результату?</p>\n','2013-12-06 02:07:23','http://oranta.org/index.php?option=com_content&view=article&id=1700:tvoja-perevaga-u-moralni-vyshchosti-nad-supernykom&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Твоя перевага у моральній вищості над суперником','uk',3840,5323,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5328,200,1338526181_time-management.jpg\" />(Закінчення. Початок <a href=\"http://oranta.org/index.php?option=com_content&amp;view=article&amp;id=1702:ne-majesh-chasu-ty-pevno-ne-tak-ogo-rozplanuvav&amp;catid=30:hrystyjanske-vchennia-cat&amp;Itemid=37\">http://oranta.org/index.php?option=com_content&amp;view=article&amp;id=1702:ne-majesh-chasu-ty-pevno-ne-tak-ogo-rozplanuvav&amp;catid=30:hrystyjanske-vchennia-cat&amp;Itemid=37</a>)</p>\n<p><strong>Отець Войцех Дудзік ОР розмовляє з Люциною Слуп.</strong></p>\n<p><em><strong>— Керування базується на виборі. Я&nbsp;не можу бути повсюди, і не можу робити багатьох речей одночасно. Чим же я маю керуватися у цьому нелегкому селекціонуванні?</strong></em></p>\n','2013-12-09 01:53:36','http://oranta.org/index.php?option=com_content&view=article&id=1703:ne-majesh-chasu-ty-pevno-ne-tak-ogo-rozplanuvav-prodovzhennja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Не маєш часу? Ти, певно, не так його розпланував… (продовження)','uk',3847,5330,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5332,200,evromaydan.jpg\" />Спостерігаючи всі ці &nbsp;революційні події по телевізору, 11- річний хлопчик, що перебував вдома через хворобу, в той час коли батьки пішли на Майдан, написав ось такі рядки.</p>\n<p> </p>\n<p><strong>Все починалося з ЄВРОПИ</strong><strong> </strong><strong>(або Історія Віктора Януковича 2013 року)</strong><strong> </strong></p>\n','2013-12-11 00:07:26','http://oranta.org/index.php?option=com_content&view=article&id=1705:dity-pro-jevromadan&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Діти про Євромайдан','uk',3851,5334,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5338,200,original-670784579.jpg\" />Розмова з екс-главою Української греко-католицької церкви Любомиром Гузаром відбулася в часі тривожних подій: масових протестів, затримання активістів та відсутності конструктивного діалогу між сторонами конфлікту. Українці вийшли на Майдан, щоб висловити свої думки та відстояти права. Блаженніший Любомир спробував пояснити в чому першопричини таких настроїв та який вихід із ситуації, що склалась.</p>\n','2013-12-12 20:31:29','http://oranta.org/index.php?option=com_content&view=article&id=1709:prygadumo-sobi-hto-my-je-i-ne-damo-sebe-duryty&catid=37:spilnota-cat&Itemid=49','N','','Пригадуймо собі, хто ми є і не даймо себе дурити','uk',3857,5340,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5338,200,original-670784579.jpg\" />Розмова з екс-главою Української греко-католицької церкви Любомиром Гузаром відбулася в часі тривожних подій: масових протестів, затримання активістів та відсутності конструктивного діалогу між сторонами конфлікту. Українці вийшли на Майдан, щоб висловити свої думки та відстояти права. Блаженніший Любомир спробував пояснити в чому першопричини таких настроїв та який вихід із ситуації, що склалась.</p>\n<p> </p>\n','2013-12-12 20:31:29','http://oranta.org/index.php?option=com_content&view=article&id=1709:prygadumo-sobi-hto-my-je-i-ne-damo-sebe-duryty','P',NULL,'Пригадуймо собі, хто ми є і не даймо себе дурити','uk',3856,5339,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5342,200,pro_majdan.jpg\" />Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав (Шевчук) під час круглого столу за участю трьох Президентів України, чинного Глави Української держави, провідних релігійних, наукових діячів і трьох лідерів опозиційних сил закликав Президента України Віктора Януковича від УГКЦ та всієї релігійної спільноти не допустити силових протистоянь в Україні.</p>\n','2013-12-14 00:23:14','http://oranta.org/index.php?option=com_content&view=article&id=1710:lvizmit-pid-osobysty-kontrol-sylovi-struktury-v-derzhavir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Візьміть під особистий контроль силові структури в державі»','uk',3860,5343,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5347,200,107905.jpg\" />Нещодавно, у рамках Суспільних днів УГКЦ, з ініціативи Комісії УГКЦ «Справедливість і мир» до Львова завітав відомий італійський фахівець у галузі аналізу та практики інформування Нікола Граціані, професор, викладач Папського університету «Santa Croce» в Римі, представник відомої Інформаційної італійської агенції АНСА (ANSA), президент Асоціації журналістів, які супроводжують главу держави. Під час цього візиту професор поділився своїми думками щодо розвитку церковної журналістики і того, як повинен реагувати на події в державі церковний журналіст, із Департаментом інформації УГКЦ.</p>\n<p><strong><em>&nbsp;</em></strong></p>\n','2013-12-16 22:04:45','http://oranta.org/index.php?option=com_content&view=article&id=1715:lobjektyvnosti-v-zhurnalistyci-ne-isnujer&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Об’єктивності в журналістиці не існує»','uk',3866,5349,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5347,200,1_7.jpg\" />Минулий тиждень приніс католикам і усім громадянам України чергові випробування, але і чергові радості. Дика спроба розгону Майдану успішно провалилася. І якщо вірити ЗМІ, принаймні один атеїст знайшов Бога у чуді ненасильницького опору озвірілому «Беркуту». Це не може не тішити кожне християнське серце.</p>\n','2013-12-17 01:21:45','http://oranta.org/index.php?option=com_content&view=article&id=1713:hrystyjansky-madan&catid=37:spilnota-cat&Itemid=49','P',NULL,'Християнський майдан','uk',3868,5351,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5367,200,mokosha.JPG\" />21 грудня 2013 р. Б. в приміщенні НПУ ім. М.&nbsp;Драгоманова відбувся <strong>ХІІ&nbsp;Фестиваль-конкурс духовної пісні «Аскольдів глас»</strong>.</p>\n<p>Учасниками конкурсу 2013 року стали вокальні ансамблі у віковій категорії 12-36 років. А саме «<strong>Дитячий фольклорний ансамбль «ВІНОЧОК»</strong> (с.&nbsp;Сигнаївка, Черкаська обл. Керівник – Тамара Лоцман)</p>\n','2013-12-23 20:47:01','http://oranta.org/index.php?option=com_content&view=article&id=1726:rezultaty-hii-festyvalju-konkursu-laskoldiv-glasr&catid=25:parafija-cat&Itemid=27','P','','Результати ХІІ Фестивалю-конкурсу «Аскольдів глас»','uk',3886,5369,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5369,200,zapisati_71.jpg\" />22 грудня Українська Греко-Католицька Церква вшановує свято Непорочного зачаття Пресвятої Богородиці святою Анною. Догма про Непорочне зачаття була проголошена спеціальною буллою Папи Пія ІХ 8 грудня 1854 року.</p>\n<p>Дана подія стала знаковою не тільки для Католицької Церкви, але й, як згодом з’ясувалося, і для всієї християнської Європи. Через 101 рік і один день після проголошення догми про непорочне зачаття (9 грудня 1955 року) комітет міністрів Ради Європи затвердив офіційну символіку – прапор Європи: золоте коло з дванадцяти зірок на синьому тлі.</p>\n','2013-12-22 10:57:07','http://oranta.org/index.php?option=com_content&view=article&id=1727:marija-i-jevropa&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Марія і Європа','uk',3887,5370,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5371,200,18.jpg\" />Память святого апостола Андрея Первозванного и память святителя Николая Мирликийского – близкие «соседи» в церковном календаре (соответственно 13 и 19 «декабря нового стиля»). Соседями являются и два киевских храма в честь этих святых на знаменитой «Аскольдовой могиле». В уходящем 2013-м году мы отметили 1025-летие Крещения Руси, и, конечно же, благодарно вспоминали о том, что путь к воцерковлению наших предков лежал через эпоху князя Аскольда.</p>\n','2013-12-24 13:43:11','http://oranta.org/index.php?option=com_content&view=article&id=1728:svjatny-askoldovo-mogyl-tekst-podano-movoju-oryginalu&catid=25:parafija-cat&Itemid=27','P',NULL,'Святыни Аскольдовой могилы (текст подано мовою оригіналу)','uk',3889,5372,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5372,200,P1070057.JPG\" />З року в рік Св. Миколай з Аскольдової радо відвідує неповносправних діток потіївської школи інтернат, що на Житомирщині. Сто двадцять дітей отримали щедрі подарунки від Миколая: солодощі, фрукти, книги для шкільної бібліотеки від компанії «А-ба-ба-га-ла-ма-га», предмети гігієни, спортивний інвентар, штангу.</p>\n<p>Стараннями парафіян: Валентини та Володимира з дітками, Оксани так Євгена, Валерії та Сергія, родини Маліярчуків, Сергія Харчука та інших добродітелей вдалося зібрати ці добрі дарунки.</p>\n','2013-12-25 21:48:04','http://oranta.org/index.php?option=com_content&view=article&id=1729:svjaty-mykola-z-askoldovoji-u-potijivci&catid=25:parafija-cat&Itemid=27','P','','Святий Миколай з Аскольдової у Потіївці','uk',3891,5374,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5373,200,DSC07459.JPG\" />19 грудня 2013 року у благодійний фонд «Карітас Київ» завітав св. Миколай, де відбулось святкування свята Миколая Чудотворця для дітей, які відвідують центр. В святкуванні взяли участь вісімдеять учасників – підопічних дітей. Особливо поважними гостями свята були актори, які виступали перед дітьми із багатодітних та неблагополучних сімей із виставою «Подарунки св. Миколая».</p>\n','2013-12-27 00:02:28','http://oranta.org/index.php?option=com_content&view=article&id=1731:svjaty-mykola-zavitav-do-dite-bf-lkaritas-kyjivr&catid=29:propovidi-cat&Itemid=34','P',NULL,'Святий Миколай завітав до дітей БФ «Карітас Київ»','uk',3894,5377,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5373,200,propovid_lds.jpg\" />Споконвічно від Отця народженому нетлінно Синові, Що в останній час безсіменно від Діви тілом народився, Христу-Богові закличмо: Ти, що підняв нашу силу, Святий єси, Господи! (Канон Різдва).</p>\n<p style=\"text-align: center;\">Христос рождається!</p>\n<p>На голос ангела, який пролунав пастушкам серед темної ночі, спішимо ми сьогодні до вбогої стаєнки у Вифлеємі.</p>\n','2013-12-26 19:54:35','http://oranta.org/index.php?option=com_content&view=article&id=1730:rizdvjane-poslannja-blazhennishogo-svjatoslava-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Різдвяне послання Блаженнішого Святослава ','uk',3893,5376,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5378,200,DSCN2681-1.JPG\" />Цьогоріч, діткам, якими опікується наша парафія, Святий Миколай передав подарунки з-за океану.</p>\n<p>Стараннями добродітелей з церкви св. Івана Хрестителя в місті Нюарку (штаті Ню Джерзі в США), якою займаються отці Редемптористи, 70 дітей, якими опікується наша парафіяльна служба милосердя, отримали надзвичайні подарунки, це 40 дітей, матері котрих знаходяться у місцях позбавлення волі, 16 дітей служби «Карітас-Печерськ» та 14 дітей малозабезпечених парафіян.</p>\n','2013-12-28 23:59:17','http://oranta.org/index.php?option=com_content&view=article&id=1733:dlja-svjatogo-mykolaja-nemaje-nemozhlyvogo-chastyna-i&catid=25:parafija-cat&Itemid=27','P','','Для Святого Миколая немає неможливого (частина І)','uk',3908,5391,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5378,200,marynovych_ac802.jpg\" />Це нове покоління мусить, очевидно, навчитися того, що жертвувати собою заради здобуття високої мети – це гарно. Це є механізм, використаний навіть Богом. Адже Бог послав свого Сина на жертву. Не в царські палати, щоби Він звідти налагодив нове життя, вніс нові закони. Чому Він послав свого Сина до звичайних людей? Виявляється – треба знизу, через зміну способу думання, а інколи й через хресну жертву творити нову реальність буття, перемагаючи старе й зашкарубле, яке гальмує й не пускає.</p>\n','2013-12-28 22:23:00','http://oranta.org/index.php?option=com_content&view=article&id=1732:lbog-je-v-dushi-kozhnogor&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Бог є в душі кожного»','uk',3906,5389,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5392,200,171-308x199.jpg\" />Один чоловік вирішив іти шукати Бога. На порозі дому його сумління запитало:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хто прикував мене до порогу цієї оселі?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, - відповів Бог.</p>\n<p>Та голос розуму заглушив кволий шепіт сумління.</p>\n','2014-01-04 23:04:49','http://oranta.org/index.php?option=com_content&view=article&id=1738:shukannja-boga-5-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Шукання Бога (5 січня)','uk',3921,5404,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5392,200,IMG_7366.jpg\" />Якось учень запитав свого вчителя:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як мені знайти найкоротшу дорогу до Бога?</p>\n<p>Учитель відповів:</p>\n','2014-01-04 00:01:31','http://oranta.org/index.php?option=com_content&view=article&id=1737:nakorotsha-doroga-do-boga-4-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найкоротша дорога до Бога (4 січня)','uk',3920,5403,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5392,200,KidsHealthy01.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тату, а Ти бачив Бога?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; НІ, синку. Але я бачив Його сліди!</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сліди? Куди Він йшов?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Він торкнувся людини і вона стала доброю?</p>\n','2014-01-01 00:48:26','http://oranta.org/index.php?option=com_content&view=article&id=1734:bozhi-slidy-1-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Божі сліди (1 січня)','uk',3912,5395,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5392,200,p_29232_1_gallerybig.jpg\" />Жив в одному селі швець. Жив він праведно, мав сильну віру. І ось перед одним з великих церковних свят швець занедужав. Засмутився він, що не зможе потрапити в храм. Як раптом, напередодні самого свята, приснилося йому, начебто чийсь голос, зовсім тихий і лагідний говорить: «Раз ти не зможеш прийти до Мене, Я прийду до тебе в цей день».</p>\n<p>Прокинувся швець і зрадів: «Невже сам Господь до мене завітає?» – Думав він.</p>\n','2014-01-02 00:56:01','http://oranta.org/index.php?option=com_content&view=article&id=1735:vizyt-boga-2-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Візит Бога (2 січня)','uk',3911,5394,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5392,200,taize-30.jpg\" />Жив один багатий колекціонер стародавніх статуй, яких у нього було безліч. Вони стояли по алеях його красивого парку. Знайомі направили до багача одного молодого студента з рекомендацією від місцевого Історичного товариства, щоб той міг ознайомитися з дивовижною колекцією.</p>\n<p>Керуючий доповів господареві про прибуття гостя, зустрів його і повів по доріжках парку до будинку. Охоплений цікавістю, студент зупинявся біля кожної статуї і захоплено зітхав. Зрештою керуючий не витримав:</p>\n','2014-01-03 00:59:55','http://oranta.org/index.php?option=com_content&view=article&id=1736:vizyt-do-bagatogo-kolekcionera-3-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Візит до багатого колекціонера (3 січня)','uk',3918,5401,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5396,200,timthumb.php.jpg\" />Архиєпископ-емерит Блаженніший Любомир (Гузар) відзначає, що 2013 рік, який минає, був справді складним роком.</p>\n<p>Про це він сказав у своєму <a href=\"http://news.ugcc.org.ua/go/aHR0cDovL3poeXZlLnR2L3Byb2dyYW0vdmlkZW9ibG9nLWJsYXpoZW5uaXNob2dvLWx5dWJvbWlyYS1ndXphcmEtcGlkc3Vtb3Z1eXVjaGktcmlrLmh0bWw=\" target=\"_blank\">відеоблозі на «Живому.ТБ»</a> <a href=\"http://www.youtube.com/watch?v=U1dMdnGec4g\">http://www.youtube.com/watch?v=U1dMdnGec4g</a>. «Цей рік був справді складний, але Богу дякувати за це! Бо це є знак, що приходять зміни», - зазначив Блаженніший Любомир.</p>\n','2013-12-30 11:22:21','http://oranta.org/index.php?option=com_content&view=article&id=1739:-lmy-buly-svidkamy-krokiv-do-zminy-systemyr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Ми були свідками кроків до зміни системи»','uk',3914,5397,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5401,200,00384423_n1.jpeg\" />У четвертому столітті жив богослов - святий Августин. Одного разу вирішив він написати книгу, щоб розповісти читачам, який насправді Бог. І от зранку він сів за стіл і написав назву книги: «Про Бога». Однак, далі книга не посувалася – усе, що спадало йому на думку, видавалося недостатнім. Він розумів, що Бог набагато величніший і дуже хотів відобразити цю велич у книзі.</p>\n','2014-01-06 23:45:12','http://oranta.org/index.php?option=com_content&view=article&id=1744:velych-boga-neosjazhna-7-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Велич Бога неосяжна (7 січня)','uk',3928,5411,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5401,200,img2.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якби не я, - нахвалявся дощ, щедро зросивши сад і поле, то на землі нічого би не росло!</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якби не я, - обережно заперечила земля, - то й не було би на чому рости!</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А якби не я… - зухвало почало говорити сонце…</p>\n','2014-01-07 23:48:59','http://oranta.org/index.php?option=com_content&view=article&id=1745:jakby-ne-bog-8-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Якби не Бог (8 січня)','uk',3930,5413,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5401,200,turgenev2.jpg\" />В одного старця оженився син. Відвідавши тата, він з жалем поглянув на батьків.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми з жінкою цілий час тримаємось за руки, ніжно дивимось одне на одного! Розповідаємо, яка сильна наша любов… Як би я хотів, щоб Ви з мамою так само&nbsp; любили одного і відчували таке щастя!</p>\n','2014-01-05 23:35:01','http://oranta.org/index.php?option=com_content&view=article&id=1743:slova-ljubovi-6-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Слова любові (6 січня)','uk',3925,5408,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5403,200,370-napoleon-1.jpg\" />До імператора Наполеона прийшла мати одного солдата, засудженого на смерть і просила про помилування.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Його засуджено справедливо, - суворо відказав імператор.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я прийшла просити не справедливості. а ласки.</p>\n','2014-01-09 22:01:33','http://oranta.org/index.php?option=com_content&view=article&id=1747:laska-i-spravedlyvist-10-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ласка і справедливість (10 січня)','uk',3934,5417,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5403,200,8ed5689787.jpg\" />На фабриці з’явилася величезна проблема – крадіжки. щодня з території потайки виносили товар. Керівники доручили охоронцям обшукати кожного робітника, що виходив з фабрики наприкінці дня.</p>\n<p>Робітники, здебільшого, охоче відкривали свої торби і дозволяли оглянути їх вміст. Охоронці працювали вельми ретельно. перевіряючи всіх, аж до останнього робітника.</p>\n','2014-01-08 21:48:27','http://oranta.org/index.php?option=com_content&view=article&id=1746:ohoronec-i-zlodi-9-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Охоронець і злодій (9 січня)','uk',3932,5415,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5417,200,images.jpg\" />У лікарні в одній палаті лежали два важкохворих чоловіки. Один лежав під вікном. а ліжко іншого стояло біля дверей.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що там видно у вікні? – якось запитав той, що лежав біля дверей.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; О! – пожвавішав перший. – Я бачу небо, хмари, подібні до звірів, озеро і ліс у далині…</p>\n','2014-01-11 00:16:02','http://oranta.org/index.php?option=com_content&view=article&id=1757:prytcha-pro-zazdrist-11-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про заздрість (11 січня)','uk',3936,5419,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5420,200,11.jpg\" />В одного правителя при дворі жив мудрець. Якось правитель звернувся до нього з питанням як йому осягти мудрість Бога.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Складне питання дали Ви мені пане, відповів мудрець. дозвольте мені подумати день. два.</p>\n','2014-01-11 22:11:57','http://oranta.org/index.php?option=com_content&view=article&id=1761:mudrist-bozha-12-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мудрість Божа (12 січня)','uk',3942,5425,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5425,200,images.jpg\" />Один скульптор виготовив прекрасну статую Хреста, але хтось із його знайомих розглядаючи її зауважив:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не розумію я вашої скульптури. Знаю, що ви майстер своєї справи, але ця скульптура недоладна і непропорційна. Хіба ви цього не помічаєте?</p>\n','2014-01-13 00:12:14','http://oranta.org/index.php?option=com_content&view=article&id=1763:skulptura-hrysta-13-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P','','Скульптура Христа (13 січня)','uk',3944,5427,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5428,200,images.jpg\" />Архиепископ-эмерит Украинской грекокатолической церкви ЛЮБОМИР ГУЗАР полагает, что улучшить жизнь в стране может только полная смена системы власти. По его мнению, украинцы должны научиться жить как единый народ и не поддаваться на провокации расколоть государство. О своем отношении к происходящим в стране событиям, а также о роли церкви в государстве Любомир Гузар рассказал корреспонденту \"Ъ\" ЮЛИИ РЯБЧУН.\n','2014-01-13 12:59:58','http://oranta.org/index.php?option=com_content&view=article&id=1765:lljudy-ne-vpevnenni-u-zavtrashnomu-dni-i-ce-treba-zminytyr&catid=27:gk-v-ukrajini-cat&Itemid=57','P','','«Люди не впевненні у завтрашньому дні і це треба змінити»','uk',3946,5429,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5429,200,3_serce_na_pisku.jpg\" />Одного разу дроворуб рубав над річною дерево і зронив у неї сокиру. Заплакав він з горя, але йому об’явився Господь і спитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому ти плачеш?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як же мені н плакати, коли я зронив сокиру у річку і н можу більше заробляти на хліб для своєї сім’ї.</p>\n','2014-01-13 22:49:26','http://oranta.org/index.php?option=com_content&view=article&id=1767:boga-ne-obdurysh-14-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Бога не обдуриш (14 січня)','uk',3948,5431,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5432,200,1533666.jpg\" />У неділю дванадцятого січня у церкві святого Миколая, що на Аскольдовій могилі, парафіяльна молодь та старшокласники після Богослужінь виступали перед вірянами з вертепом. У вертепі брало участь дванадцять людей. У цій різдвяній виставі ми заколядували багато різноманітних колядок, такі як: «Бог Предвічний», «Небо і земля», «Нова радість стала», «Ой дивна, дивна наша родина», «Будьте здорові», «Бог ся рождає». У вертепі, як і годиться, були представлені усі різдвяні персонажі: ангели, пастушки, царі, ірод, жид, смерть, чорт.</p>\n','2014-01-14 15:11:38','http://oranta.org/index.php?option=com_content&view=article&id=1769:rizdvjany-vertep-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Різдвяний вертеп на Аскольдовій Могилі','uk',3950,5433,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5432,200,images.jpg\" />Якось один чоловік спитав у мудреця:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому я мушу виконувати заповіді. якщо жодної видимої користі з цього немає?</p>\n','2014-01-16 00:46:24','http://oranta.org/index.php?option=com_content&view=article&id=1772:prytcha-pro-vykonannja-zapovide-16-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про виконання заповідей (16 січня)','uk',3954,5437,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5432,200,look.com.ua-21509.jpg\" />Один парафіянин. який раніше щонеділі брав участь у літургії перестав ходити до церкви. Минав час, чоловік не з’являвся в церкві і священик вирішив навідатися до нього. Двері в його будинок були відчинені і священик зайшов до оселі. Колишній парафіянин сидів сам однісінький перед каміном, побачивши священика, він кивнув йому головою, привітавшись і жестом запросив сісти.</p>\n','2014-01-14 22:41:58','http://oranta.org/index.php?option=com_content&view=article&id=1771:propovid-bilja-kaminu-15-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Проповідь біля каміну (15 січня)','uk',3952,5435,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5438,200,130945881.jpg\" />У домі одних багачі перестали молитися перед їжею. Якось до них у гості прийшов провідник. Стіл був накрито дуже вишукано: найкращі фруктові соки і дуже смачні страви. Сім’я і гість сіли за стіл. Усі подивилися на проповідника, думаючи. що помолиться він. Але проповідник сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; За столом мусить помолитися голова родини, бо ж він перший молільник у сім’ї.</p>\n','2014-01-17 00:29:39','http://oranta.org/index.php?option=com_content&view=article&id=1775:molytva-za-zvychajem-17-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молитва за звичаєм (17 лютого)','uk',3956,5439,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5439,200,6594.jpg\" />17 січня близько 13-ї години велика група православних із хрестами та образами зі сторони Києво-Печерської лаври попрямували у напрямку Аскольдової могили. Тут вони зробили обхід, а потім розпочали свою акцію, яка супроводжувалась агресивними викриками з вимогою віддати православним храм Святого Миколая.</p>\n','2014-01-17 21:58:15','http://oranta.org/index.php?option=com_content&view=article&id=1777:pravoslavni-vymagajut-viddaty-jim-greko-katolycki-hramy-v-kyjevi&catid=25:parafija-cat&Itemid=27','P',NULL,'... під Аскольдовою','uk',3958,5441,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5441,200,images.jpg\" />Дві золоті рибки жили в скляній посудині. Ліниво плавали по колу. вони мали час для філософських роздумів. Якось одна рибка запитала іншої:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти віриш у Бога?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Звісно!</p>\n','2014-01-18 02:03:37','http://oranta.org/index.php?option=com_content&view=article&id=1778:dvi-zoloti-rybky-18-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дві золоті рибки (18 січня)','uk',3960,5443,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5444,200,1611261.jpg\" />Часто можна почути запитання, схожі за змістом на тезу, винесену в заголовок: “Якою є роль Церкви в суспільстві та державі?”, “Якою є роль Церков у побудові Спільної Європи?”, “Яка роль Церкви у консолідації суспільства?” і тому подібне. Не зважаючи на добру волю всіх тих, хто ці запитання ставить, у них криється більше чи менше усвідомлена помилка, яка походить із незнання покликання та призначення Церкви як такої. Тому що у ставленні подібних питань присутнє латентне очікування, що Церква (чи – <em>Церкви</em>) [1] зробить або повинна зробити свій внесок у побудову та функціонування того чи іншого типу суспільства, державних чи наддержавних формацій тощо.</p>\n','2014-01-18 13:07:38','http://oranta.org/index.php?option=com_content&view=article&id=1781:rol-cerkov-u-gromadjanskomu-suspilstvi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Роль Церков у громадянському суспільстві','uk',3962,5445,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5446,200,383384.jpg\" />Чоловік шепотів: «Господи, поговори зі мною!»</p>\n<p>І лугові трави співали…</p>\n<p>Але чоловік не чув.</p>\n<p>Чоловік вигукнув тоді: «Господи, поговори зі мною!»</p>\n<p>І громовиця прокотилася по небу…</p>\n','2014-01-18 23:02:17','http://oranta.org/index.php?option=com_content&view=article&id=1783:prytcha-pro-te-jak-my-ne-pomichajemo-boga-u-svojemu-zhytti-19-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про те, як ми не помічаємо Бога у своєму житті (19 січня)','uk',3964,5447,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5448,200,945937.jpg\" />Вітер зустрів прекрасну квітку і закохався в неї. Поки він ніжно пестив квітку, та відповідала йому ще більшою любов’ю, розквітаючи та пахнучи. Але вітру видалося. що цього мало, і він вирішив: «Якщо я дав квітці свою міць та силу, то вона подарує мені щось більше». І він дмухнув на квітку могутнім подихом своєї любові. Але квітка не витримала бурхливої пристрасті і зламалася. Вітер спробував підвести її, оживити її, але не зміг. Тоді він затих і почав ніжно дихати на квітку, але та в’янула на очах. І закричав тоді вітер:</p>\n','2014-01-20 11:26:53','http://oranta.org/index.php?option=com_content&view=article&id=1785:viter-i-kvitka-20-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Вітер і квітка (20 січня)','uk',3966,5449,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5450,200,know.jpg\" />На наступний день святого Богоявлення Собор чесного і славного пророка, Предтечі і Хрестителя Господнього Івана від початку Церква прийняла святкувати. Годилося-бо вшанувати святкуванням того, хто послужив божественного Хрещення таїнству, поклавши свою руку на голову Владики. Зразу-бо після хрещення Господнього Хреститель вшановується і піснями славиться соборно. Собор-бо говоримо, коли збираються люди в церкву на спів і славословлення Боже, на честь і похвалу вшанованого святом великого Івана Предтечі і Хрестителя. Такий же собор, який по всіх, що у вселенній, церквах відбувався і нині відбувається.</p>\n','2014-01-20 13:21:35','http://oranta.org/index.php?option=com_content&view=article&id=1787:sobor-svjatogo-predtechi-i-hrestytelja-gospodnogo-oana&catid=35:istorychni-postati-cat&Itemid=48','P','','Собор святого Предтечі і Хрестителя Господнього Йоана','uk',3968,5451,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5451,200,1528579.jpg\" />Священик відходив з цього життя. Відходив у радості. немовби повертаючись на бАтьківщину. Духовні брати обступили його ложе. Вони почали просити його, щоб він залишив їм, як духовну спадщину, важливе напучення, яке дозволило би їм у досягненні християнської досконалості.</p>\n<p>Він зітхнув і сказав:</p>\n','2014-01-21 22:46:15','http://oranta.org/index.php?option=com_content&view=article&id=1790:nastanova-22-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Настанова (22 січня)','uk',3972,5455,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5451,200,166__640x480_zemlya.jpg\" />Ніхто не знав, коли той чоловік з’явився в місці. Видавалося, що він завжди був там, на тротуарі найжвавішої вулиці з численними крамничками, ресторанами, модними кінотеатрами, вечірніми променадами, зустрічами закоханих.</p>\n<p>Стоячи на колінах на асфальті зі шматочками кольорової крейди у руках, він малював ангелів і дивовижні краєвиди, де було сонце, щасливі діти, розпуклі квіти і мрії про свободу.</p>\n','2014-01-20 22:45:21','http://oranta.org/index.php?option=com_content&view=article&id=1789:riznobarvna-kreda-21-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Різнобарвна крейда (21 січня)','uk',3970,5453,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5451,200,Bankoboev.Ru_vorota_v_rai.jpg\" />Одного разу йшов дорогою чоловік з конем і псом. Коли минали вони велетенське дерево, потрапила в нього блискавка й спопелила їх усіх. Однак чоловік не відразу усвідомив, що пішов зі світу ( часом небіжчикам треба трохи більше часу, щоб усвідомити переміну свого стану).</p>\n<p>Дорога була довга і вела догори, сонце пекло безжально, і всі троє потомилися від спеки та спраги. Аж ось за поворотом виросла перед ними величезна мармурова брама, а за нею площа, брукована чистим золотом. Посередині стояв водограй з холодною і чистою водою. Мандрівник попростував до вартового, що стояв на вході.</p>\n','2014-01-23 22:13:21','http://oranta.org/index.php?option=com_content&view=article&id=1792:spravzhni-ra-24-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Справжній рай (24 січня)','uk',3980,5463,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5459,200,huzar_blogy.jpg\" />Архиєпископ-емерит Блаженніший Любомир (Гузар) вважає, що єдиним виходом з критичної ситуації в Україні є діалог.</p>\n<p>Про це він сказав у ефірі «Громадського телебачення» сьогодні, 22 грудня, у День Соборності України, відповідаючи на питання, який він бачить вихід з ситуації.</p>\n','2014-01-22 23:06:05','http://oranta.org/index.php?option=com_content&view=article&id=1798:2014-01-22-21-09-08&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Якщо не буде діалогу, то це приведе до ще більшого кровопролиття»','uk',3978,5461,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5464,200,167.jpg\" />Прийшов злидар до мельника і попросив милостині, але не дав йому нічого мельник, хоч і був багатий на хліб. І був там бідний селянин, який приїхав на млин, щоб змолоти мішок жита. Змилостивився він над убогим і відсипав зі свого мішка половину. Христа ради. Благословив його злидар і пішов невідь-куди.</p>\n','2014-01-24 23:13:22','http://oranta.org/index.php?option=com_content&view=article&id=1801:vynagoroda-25-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Винагорода (25 січня)','uk',3982,5465,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5464,200,image_6457.jpg\" />Якось одного разу прийшов новачок учитися стрільби з лука. Вчитель вирішив подивитися, на що він згоден. Новачок взяв дві стріли і пішов на стрільбище до мішеней. Учитель покивав головою і сказав:</p>\n<p>Ніколи не бери двох стріл. Поклавши надію на другу стрілу, ти недбайливо поставишся до першої. Завжди вважай, що маєш тільки одну спробу, і намагайся влучити в ціль однією стрілою!.</p>\n','2014-01-26 00:24:09','http://oranta.org/index.php?option=com_content&view=article&id=1802:prytcha-pro-dvi-strily-26-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про дві стріли (26 січня)','uk',3987,5470,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5465,200,bcd796e.jpg\" />Чим нинішній режим в Україні відрізняється від більшовицького? Комуністи чинили злочини, керуючись ідеологією. Режим Януковича робить те саме, керуючись кримінальною логікою.</p>\n<p>Владу в країні узурповано в майже неприхований спосіб. Усі шляхи правового й демократичного вирішення суспільно-політичного конфлікту заблоковано. Режим поводиться як терорист, що схопив заручника і приставив йому ніж до горла.</p>\n','2014-01-25 15:27:58','http://oranta.org/index.php?option=com_content&view=article&id=1804:golos-iz-polja-bolju-urjadovyh-imitaci&catid=37:spilnota-cat&Itemid=49','P',NULL,'ГОЛОС ІЗ ПОЛЯ БОЛЮ Й УРЯДОВИХ ІМІТАЦІЙ','uk',3984,5467,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5469,200,Blessed.JPG\" />На одній із лекцій поважний професор спитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи є серед вас віруючі люди?</p>\n<p>Кілька людей піднялися.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; На занятті із хімії ми вивчаємо речовини, які можна побачити, спробувати і до яких можна доторкнутися. А оскільки з Богом такого не зробиш, то Його немає. Тепер можете сідати.</p>\n','2014-01-28 22:42:50','http://oranta.org/index.php?option=com_content&view=article&id=1810:svi-rozum-29-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Свій розум (29 січня)','uk',3993,5476,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5469,200,f_139644.jpg\" />Приходить жінка до старця і каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми з чоловіком сваримося без упину і хочемо розлучитися.</p>\n<p>Та на це відповів їй старець:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ось тобі чудодійна водиця.</p>\n','2014-01-27 22:27:03','http://oranta.org/index.php?option=com_content&view=article&id=1809:chudodina-vodycja-28-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чудодійна водиця (28 січня)','uk',3991,5474,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5470,200,pozheertva.jpg\" />У церкві збирають пожертву. І один чоловік помилково поклав замість десяти карбованців тисячу. Зрозумівши це, він наприкінці служби поспішив до священика і озповів йому про свою помилку.</p>\n<p>Священик відповів так:</p>\n','2014-01-26 23:47:30','http://oranta.org/index.php?option=com_content&view=article&id=1807:pozhertva-27-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Пожертва (27 січня)','uk',3989,5472,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5476,200,42838991.jpg\" />Колись давно один мудрий пілігрим, що мандрував світом, ішов повз чисте поле до храму. У полі він побачив трьох робітників. Пілігрим давно вже не стрічав нікого і йому заманулося погомоніти з тими людьми. Він підійшов до робітників і, бажаючи запропонувати свою допомогу, звернувся до того, який найбільше втомився й виглядав незадоволеним, ба, навіть злим.</p>\n','2014-01-30 23:56:21','http://oranta.org/index.php?option=com_content&view=article&id=1814:doroga-do-hramu-31-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дорога до храму (31 січня)','uk',3997,5480,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5476,200,85435687_large_97699358.jpg\" />Одна дівчина вийшла заміж і пішла жити за невістку. Але вона не могла зносити повсякчасних докорів свекрухи і вирішила позбутися її. Пішла вона до знахаря і сказала йому:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не маю сили більше жити із свекрухою. Вона зводить мене з розуму. Чи не могли б ви допомогти мені?</p>\n','2014-01-29 23:47:44','http://oranta.org/index.php?option=com_content&view=article&id=1813:otruta-v-golovi-30-sichnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Отрута в голові (30 січня)','uk',3995,5478,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5481,200,1255940242_btlqwpx39ki0nm4.jpeg\" />Один чоловік помер і пішов на небо. Ангел зустрів його біля перлових воріт і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Щоби перейти через ці ворота, ти мусиш зібрати 100 балів. Розповідай мені про всі свої добрі діла на землі, а я буду говорити скільки балів ти отримав.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Добре, - відповів чоловік, - я прожив з жінкою п’ятдесят літ і жодного разу не зрадив їй, навіть у своєму серці.</p>\n','2014-02-01 22:04:15','http://oranta.org/index.php?option=com_content&view=article&id=1820:jak-potrapyty-na-nebo-2-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як потрапити на небо (2 лютого)','uk',4004,5487,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5481,200,kak-pomoc.jpg\" />Молодить служитель звернувся до свого наставника з питанням:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи мушу я цілковито зректися світу?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не хвилюйся, - відповів наставник, - якщо твоє життя буде насправді насправді християнським, то світ сам відречеться від тебе.</p>\n','2014-02-03 22:20:54','http://oranta.org/index.php?option=com_content&view=article&id=1822:vidrechennja-vid-svitu-4-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Відречення від світу (4 лютого)','uk',4007,5490,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5481,200,photo.jpg\" />«Поки що революція в умах і серцях людей, проте цього разу, дасть Бог, буде революція і в політичному устрої країни.&nbsp;Тепер люди говорять не лише про зміну фігур, що при владі, а й про зміну самої системи влади, точніше – устрою країни», – таку думку висловив проректор УКУ Мирослав Маринович. В ці дні, перебуваючи в Києві, він поспілкувався з журналісткою Олесею Бесараб для словацького медіа&nbsp;<a href=\"http://www.aktuality.sk/clanok/244870/exkluzivne-na-ukrajine-je-revolucia-hovori-hlas-z-barikad-profesor-marynovyc/\" target=\"_blank\">aktuality.sk</a></p>\n','2014-02-01 01:50:00','http://oranta.org/index.php?option=com_content&view=article&id=1819:2014-01-31-19-53-29&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Повстала ціла нація»','uk',4002,5485,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5488,200,images123.jpg\" />Учені ентузіасти дуже прагнули знайти якусь нову невідому тварину. Вони виїжджали в гори, шукали там сліди і збирали різноманітні відомості у місцевого населення.</p>\n<p>І від мешканців одного невеликого селища, розташованого у далекі глушині, вони почули. що в околиці блукає чудернацька істота. І справді, на стежках почали з’являтися дивні сліди, а вночі було чути, як хтось бродить на схилах гір.</p>\n','2014-02-06 23:02:30','http://oranta.org/index.php?option=com_content&view=article&id=1831:nebacheny-zvir-7-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Небачений звір (7 лютого)','uk',4016,5499,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5490,200,artleo.com-21923.jpg\" />Билинка полюбила сонце. Звісно, на взаємність, вона не повинна була сподіватися: у Сонця ж стільки всього на землі, що де там йому до маленької непримітної Билинки! Та й що це за пара: Билинка і Сонце!</p>\n','2014-02-05 22:44:51','http://oranta.org/index.php?option=com_content&view=article&id=1829:ljubov-bylynky-do-soncja-6-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любов билинки до сонця (6 лютого)','uk',4013,5496,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5490,200,skyt.jpg\" />Брат Йоан думав: «Я хочу бути подібним до ангелів. Вони нічого не роблять, а тільки споглядають велич Бога». І тієї ж ночі покинув монастир і вирушив у пустелю.</p>\n<p>За тиждень повернувся до монастиря і постукав у монастирські ворота. Брат на фірті почув його стукіт і спитав:</p>\n','2014-02-05 02:29:35','http://oranta.org/index.php?option=com_content&view=article&id=1827:jak-staty-angelom-5-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як стати ангелом (5 лютого)','uk',4010,5493,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5499,200,index25.jpg\" />Християнин в електричці читає Біблію Поряд сидить безвірник. І раптом на обличчі вірного проступає подив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Оце так! Та не вже?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ти там вичитав? – запитує атеїст.</p>\n','2014-02-07 22:52:48','http://oranta.org/index.php?option=com_content&view=article&id=1833:chervone-more-8-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Червоне море (8 лютого)','uk',4018,5501,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5501,200,0_7c3fd_3ef98db0_L.jpg\" />Один юнак дуже гордився тим, що у нього дуже багато приятелів. Коли він вирішив одружитися, весілля взявся готувати його батько. Опріч іншого, батько також розсилав запрошення.</p>\n<p>Однак у день весілля жоден з приятелів нареченого так і не прийшов. Хлопець розсердився і кинувся до батька з претензіями.</p>\n','2014-02-08 23:50:08','http://oranta.org/index.php?option=com_content&view=article&id=1835:zaproshennja-na-vesillja-9-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Запрошення на весілля (9 лютого)','uk',4019,5502,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5506,200,10-n.jpeg\" />Одного разу професор відомого університету поставив своїм студентам запитання.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи є Бог сотворителем усього сущого.</p>\n<p>Один зі студентів хоробро відповів:</p>\n','2014-02-10 02:00:28','http://oranta.org/index.php?option=com_content&view=article&id=1839:prytcha-pro-isnuvannja-boga-10-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про існування Бога (10 лютого)','uk',4024,5507,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5507,200,DSCN8992.JPG\" />Прийшов до царя якось чоловік і запитав, чи той не знає, як спасти себе від прогрішення.</p>\n<p>Цар повелів йому наповнити посудину оливою по самі вінця і пронести через місто не розливши жодної краплини.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо вильєш хоч краплину, то будеш без голови.</p>\n','2014-02-11 00:57:39','http://oranta.org/index.php?option=com_content&view=article&id=1841:posudyna-z-olyvoju-11-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Посудина з оливою (11 лютого)','uk',4026,5509,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5510,200,5dc4aa401b.jpg\" />Ольга Басараб народилася <a href=\"http://uk.wikipedia.org/wiki/1_%D0%B2%D0%B5%D1%80%D0%B5%D1%81%D0%BD%D1%8F\" title=\"1 вересня\">1 вересня</a> <a href=\"http://uk.wikipedia.org/wiki/1889\" title=\"1889\">1889</a> року в селі <a href=\"http://uk.wikipedia.org/wiki/%D0%9F%D1%96%D0%B4%D0%B3%D0%BE%D1%80%D0%BE%D0%B4%D0%B4%D1%8F\" title=\"Підгороддя\">Підгороддя</a> на <a href=\"http://uk.wikipedia.org/wiki/%D0%A0%D0%BE%D0%B3%D0%B0%D1%82%D0%B8%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%80%D0%B0%D0%B9%D0%BE%D0%BD\" title=\"Рогатинський район\">Рогатинщині</a> в родині відомого громадського діяча о. Михайла Левицького.</p>\n<p>Навчалася Ольга у приватному пансіоні для дівчат у Вайсвассері (<a href=\"http://uk.wikipedia.org/wiki/%D0%A1%D1%96%D0%BB%D0%B5%D0%B7%D1%96%D1%8F\" title=\"Сілезія\">Сілезія</a>) та ліцеї <a href=\"http://uk.wikipedia.org/wiki/%D0%A3%D0%BA%D1%80%D0%B0%D1%97%D0%BD%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%96%D0%BD%D1%81%D1%82%D0%B8%D1%82%D1%83%D1%82_%D0%B4%D0%BB%D1%8F_%D0%B4%D1%96%D0%B2%D1%87%D0%B0%D1%82_%D1%83_%D0%9F%D0%B5%D1%80%D0%B5%D0%BC%D0%B8%D1%88%D0%BB%D1%96\" title=\"Український інститут для дівчат у Перемишлі\">Українського інституту для дівчат у Перемишлі</a> (1902–1909), пізніше на однорічних курсах у Віденській торговій академії. З 1910&nbsp;р. працювала в страховому товаристві «Дністер», Земельному іпотечному <a href=\"http://uk.wikipedia.org/wiki/%D0%91%D0%B0%D0%BD%D0%BA\" title=\"Банк\">банку</a>.</p>\n','2014-02-12 04:58:35','http://oranta.org/index.php?option=com_content&view=article&id=1845:golgofa-olgy-basarab&catid=25:parafija-cat&Itemid=27','P','','Голгофа Ольги Басараб','uk',4030,5513,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5510,200,8287.jpg\" />Кілька хлопців ображали християнина, який ішов через їхній квартал. А той відповідав підносячи за них молитви. Хтось запитав його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому ти відповідаєш на образи молитвою?</p>\n<p>Християнин відказав:</p>\n','2014-02-11 23:11:42','http://oranta.org/index.php?option=com_content&view=article&id=1843:shcho-v-gamanci-12-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Що в гаманці (12 лютого)','uk',4028,5511,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5514,200,65772-1920x1200.jpg\" />Якось учитель з учнем ішли путівцем. Бачать на зустріч їм прямує буркочучи чоловік, увесь розбурханий, мало не плаче.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що сталося, друже мій? - запитує учитель.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Та мене щойно якийсь негідник образив останніми словами і до того ж пусто-дурно! Що я зробив йому?</p>\n','2014-02-12 23:54:29','http://oranta.org/index.php?option=com_content&view=article&id=1847:prytcha-pro-obrazu-13-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про образу (13 лютого)','uk',4032,5515,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5515,200,10308083.jpg\" />Продавець в невеличкій крамниці повісив біля входу оголошення «Продаються кошенята».</p>\n<p>Цей напис, природньо, привернув увагу місцевих дітлахів і за лічені хвилини до крамниці зайшов хлопчик. Привітавшись із продавцем, він несміливо запитав про ціну кошенят.</p>\n','2014-02-14 00:01:45','http://oranta.org/index.php?option=com_content&view=article&id=1849:koshenjata-na-prodazh-14-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кошенята на продаж (14 лютого)','uk',4034,5517,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5515,200,106407_or.jpg\" />Закінчивши проповідь священик помітив хлопця, який вже не вперше затримувався в церкві довше від усіх парафіян. Було видно, що його непокоїть якась річ. Підійшовши до хлопця, він запитав, чи може чимось допомогти йому.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скажіть, а пекло справді існує, - хлопець підвів очі сповнені страху.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З тобою сталося щось? Покайся, розкажи мені, - запропонував священик.</p>\n','2014-02-16 00:13:46','http://oranta.org/index.php?option=com_content&view=article&id=1851:hlopjachy-strah-16-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хлоп’ячий страх (16 лютого)','uk',4042,5525,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5518,200,no-v-day-1.jpg\" />Більшість магазинів у кожному місті щороку перед 14 лютого наповнюються яскравими листівками, іграшками, шоколадками і безліччю інших дрібничок.</p>\n<p>Чого тільки не зробиш, коли душа бажає свята. А коли таких душ назбирується багато, то і бажання створити їм свято збільшується. А коли за таке ще й платять, то чому б і ні? За таким життєвим принципом живе, мабуть, дуже значна частина світового бізнесу. Саме такі дії дискредитують святого, який загинув мученицькою смертю заради добра.</p>\n','2014-02-14 11:50:10','http://oranta.org/index.php?option=com_content&view=article&id=1853:den-svjatogo-valentyna-ne-dorivnjuje-dnju-zakohanyh&catid=37:spilnota-cat&Itemid=49','P',NULL,'День святого Валентина не дорівнює Дню закоханих','uk',4036,5519,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,73525.gif\" />Новий учитель прийшов до класу і помітив, що одного хлопця беруть на глузи і називають дурником.</p>\n<p>У перерві, він запитав хлопців, чому вони так прозивають його?</p>\n','2014-02-17 00:47:35','http://oranta.org/index.php?option=com_content&view=article&id=1861:uchni-17-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Учні (17 лютого)','uk',4048,5531,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5525,200,81565.jpg\" />Українські Церкви ще раз підкреслюють свою відданість виключно мирному врегулюванню громадянського конфлікту в Україні та підтверджують принципи, викладені у «Зверненні керівників Церков і релігійних організацій до своїх вірних та всіх людей доброї волі у зв’язку з суспільно-політичною ситуацією в Україні» від 10 грудня 2013 р., у «Зверненні Всеукраїнської Ради Церков і релігійних організацій з нагоди Дня Соборності та Свободи України» від 22 січня 2014 р., у Комюніке екстреного засідання Всеукраїнської Ради Церков і релігійних організацій від 22 січня 2014 р. та у «Заяві Всеукраїнської Ради Церков і релігійних організацій за результатами зустрічі з Президентом і опозицією» від 25 січня 2014 р.</p>\n','2014-02-15 16:02:23','http://oranta.org/index.php?option=com_content&view=article&id=1858:zvernennja-ukrajinskyh-cerkov-shchodo-myrnogo-rozvjazannja-suspilno-politychnoji-kryzy-ta-prypynennja-sylovogo-protystojannja-v-ukrajini&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Українських Церков щодо мирного розв’язання суспільно-політичної кризи та припинення силового протистояння в Україні','uk',4045,5528,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5537,200,_IdI4cEmq8Q.jpg\" />На узбіччі дороги, що вела до міста, сиділа стара жінка.</p>\n<p>До неї підійшов мандрівник і спитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що за люди живуть у цьому місті?</p>\n','2014-02-28 23:06:49','http://oranta.org/index.php?option=com_content&view=article&id=1898:ljudy-u-vashomu-misci-1-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Люди у вашому місці (1 березня)','uk',4098,5581,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5537,200,r018.jpg\" />Високопреосвященні та Преосвященні владики! Всесвітліші, високопреподобні та всечесні отці! Дорогі в Христі брати і сестри!</p>\n<p>У цьогорічний Великий піст наша Церква, наші вірні та весь український народ входять із відчуттям болю, страху, терпіння і тремтливої надії. Закінчилася одна з найдовших і найстрашніших зим в історії нашого народу, – зима, в якій ми глибоко і особисто відчули пітьму гріха, побачили зло, що яскраво увиразнилося в нашій новітній історії й оголило своє вбивче нутро. Гріх постав перед нами у своїй огиднiй, смертоносній природі.</p>\n','2014-02-28 21:39:50','http://oranta.org/index.php?option=com_content&view=article&id=1896:poslannja-blazhennishogo-svjatoslava-i-postinogo-synodu-na-velyky-pist-2014-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Послання Блаженнішого Святослава і Постійного Синоду на Великий піст 2014 року','uk',4096,5579,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,Wl3MXEEV8Os.jpg\" />У ті часи, коли ліхтарі запалювали вогнем. вулицями щовечора ходили ліхтарники і приносили світло у кожний провулок. Жив тоді маленький ліхтарник: щуплий дідок низького зросту. Щовечора він обходив провулки, черкав сірником по підошві і запалював ліхтарі. І кожна темна вуличка ставала яснішою ніж зазвичай.</p>\n','2014-02-19 23:27:44','http://oranta.org/index.php?option=com_content&view=article&id=1865:malenky-lihtarnyk-20-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Маленький ліхтарник (20 лютого)','uk',4063,5546,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,pan2.jpg\" />Сталося це в середньовіччі. Чернець, що керував будівництвом собору, вирішив подивитися як працюють каменярі. Він підійшов до першого і попросив його розповісти про свою роботу.</p>\n','2014-02-18 23:24:19','http://oranta.org/index.php?option=com_content&view=article&id=1864:prytcha-pro-troh-budivnykiv-19-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про трьох будівників (19 лютого)','uk',4059,5542,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5539,200,thumb.jpg\" />Маленький хлопчик спитав у мами:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому Ти плачеш?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому, що я жінка.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не розумію.</p>\n','2014-02-17 23:21:54','http://oranta.org/index.php?option=com_content&view=article&id=1863:chomu-zhinka-plache-18-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чому жінка плаче (18 лютого)','uk',4057,5540,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5543,200,u_rymi.jpg\" />Дорогі брати у священичому служінні! У зв’язку із драматичною ситуацією в нашій країні хочу нагадати всім вам фундаментальні норми священичого служіння:</p>\n<p>1.&nbsp;&nbsp;&nbsp; Церква – спільнота дітей Божих, об’єднаних вірою в Триєдиного Бога, а священик є служителем цієї спільноти, образом Христа - Доброго Пастиря, діяльно присутнім серед свого народу.</p>\n','2014-02-19 16:45:04','http://oranta.org/index.php?option=com_content&view=article&id=1868:rozporjadzhennja-otcja-i-glavy-ugkc-shchodo-pozyciji-i-povedinky-duhovenstva-v-teperishni-suspilno-politychni-sytuaciji-v-ukrajini&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Розпорядження Отця і Глави УГКЦ щодо позиції і поведінки духовенства в теперішній суспільно-політичній ситуації в Україні','uk',4061,5544,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5548,200,1037.jpg\" />Якось троє друзів купалися в ріці над водоспадом. Перепливаючи через річку. вони помітили, що їх несе вода. Течія була слабка. але що ближчим був водоспад тим швидшою ставала течія. Занепокоївшись двоє приятелів повернулися на берег і почали кликати третього. а він тільки сміявся у відповідь і кричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мені пливеться так легко.</p>\n','2014-02-21 00:24:38','http://oranta.org/index.php?option=com_content&view=article&id=1872:plavci-21-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Плавці (21 лютого)','uk',4067,5550,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5551,200,images.jpg\" />Ви одруженні вже чимало років. яка таємниця успішного подружжя.</p>\n<p>- Я повсякчас захоплюються своєю жінкою. Якщо у твого сусіда трава зеленіша - це значить, що ти не поливаєш свою траву. Я кажу жінці раз у раз: ти прекрасна, ти жадання мого серця. ти пречудова. Навіщо?</p>\n<p> </p>\n','2014-02-22 09:21:38','http://oranta.org/index.php?option=com_content&view=article&id=1873:sekret-uspishnogo-podruzhzhja-22-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Секрет успішного подружжя (22 лютого)','uk',4068,5551,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5553,200,756.jpg\" />Голка і нитка лежали в одній кравецькій коробці. Якось у них виникла суперечка, почали з’ясовувати – хто більше дає користі.</p>\n<p>Голка:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я з’єдную краї тканини і прокладаю дорогу для Тебе.</p>\n<p>Нитка почала заперечувати:</p>\n','2014-02-23 02:00:37','http://oranta.org/index.php?option=com_content&view=article&id=1878:golka-i-nytka-23-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Голка і нитка (23 лютого)','uk',4073,5556,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5556,200,849405.jpg\" />Якось учень і його вчитель посперечалися, хто за годину нарубає більше дров у лісі.</p>\n<p>Кожен вибрав собі ділянку. Учень взявся рубати і його сокира не змовкала ні на хвилину. Так, він хотів перемогти. Тоді як сокира учителя. час від часу змовкала.</p>\n','2014-02-24 05:35:45','http://oranta.org/index.php?option=com_content&view=article&id=1880:korysna-pererva-24-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Корисна перерва (24 лютого)','uk',4075,5558,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5559,200,header_img.png\" />Святий Марко детально описує змагання Ісуса зі злим духом, якого  апостоли не змогли вигнати. Пояснює також причину поразки Його учнів:  щоб перемогти злого духа, бажання замало, його слід перемагати  відповідною зброєю&nbsp;— постом і молитвою.</p>\n','2014-02-24 13:24:08','http://oranta.org/index.php?option=com_content&view=article&id=1882:molytva-vyganjaje-zlogo-duha-ale-tilky-shchyra-molytva&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молитва виганяє злого духа, але тільки щира молитва','uk',4077,5560,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5560,200,dti.jpeg\" />Три приятелі мріяли про щастя, кожен про своє. Але вони уявляли його по різному. Перший думав, що щастя – це багатство, другому щастям видався талант, третій вважав. що щастя – це сім’я.</p>\n<p>Минув час, перед смертною годиною зібралися друзі, щоб підсумувати своє життя.</p>\n','2014-02-25 04:14:21','http://oranta.org/index.php?option=com_content&view=article&id=1883:try-pryjateli-i-shchastja-25-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Три приятелі і щастя (25 лютого)','uk',4079,5562,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5564,200,463.jpg\" />Одного разу юнак зустрів у місті чудову дівчину, що продавала квіти і відразу ж закохався в неї. Він усміхнувся до неї і вона також відповіла йому усмішкою. а опісля пішла подарувавши квітку. Дізнавшись її ім’я юнак послав їй записку сподіваючись на зустріч.</p>\n<p>Минали дні. а відповіді не надходило, всі його думки були звернені до коханої, щохвилини перед його очима повстав її образ.</p>\n','2014-02-25 22:36:55','http://oranta.org/index.php?option=com_content&view=article&id=1888:divchyna-shcho-podaruvala-kvitku-26-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дівчина, що подарувала квітку (26 лютого)','uk',4085,5568,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,_DSC6707.jpg\" />На нещодавно відремонтованій лавці під розпуклою тополею сховавшись у затишній тіні біля входу до під’їзду сиділа добра бабуся.</p>\n<p>Сьогодні їй цілісінький день дзвонили онуки. Вона їх дуже любила і свого часу тішила чаєм та пригощала цукерками. Учора приїжджали діти щоб подякувати їй. Сусідка померла – більше ворогів у неї не було, тільки другі. І п’ять кішок – їй особливо подобалися ці смугасті створіння.</p>\n','2014-02-26 22:59:57','http://oranta.org/index.php?option=com_content&view=article&id=1892:babusja-27-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Бабуся (27 лютого)','uk',4089,5572,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5569,200,bild_11.jpg\" />Якось батько однієї родини як і щодень прийшов з роботи. Із серйозним виглядом лиця він сів за стіл і взявся за якісь справи. Маленький синочок побачивши, що його татусь сидить сам в кімнаті каже мамі:-</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я піду до татка, йому, напевно, самому сумно.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дай татові спокій, хіба не бачиш скільки в нього роботи?</p>\n','2014-02-27 22:35:09','http://oranta.org/index.php?option=com_content&view=article&id=1893:batko-i-syn-28-ljutogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Батько і син (28 лютого)','uk',4091,5574,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,631658.jpg\" />Якось уранці учитель і його учень йшли полями. Учень запитав його, яка їжа наблизить його до Бога. Хоча учитель завжди говорив, що всяка страва, благословенна молитвою, корисна для людини, учень йому не вірив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мусить бути якась особлива їжа, що приведе до Бога, - сказав учень.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Наприклад гриби, що ростуть ось там, - промовив учитель.</p>\n','2014-03-05 23:15:05','http://oranta.org/index.php?option=com_content&view=article&id=1910:svjashchenna-strava-6-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Священна страва (6 березня)','uk',4115,5598,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,ozhidaete-dvoynyu.jpg\" />У животі вагітної жінки розмовляли двоє малюків. Один каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти віриш у життя після пологів?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Певна річ. Усім очевидно, що життя після пологів існує. Ми тут аби стати досить сильними і готовими до того, що нас чекає згодом.</p>\n','2014-03-08 21:48:33','http://oranta.org/index.php?option=com_content&view=article&id=1916:zhyttja-pislja-pologiv-9-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Життя після пологів (9 березня)','uk',4121,5604,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5575,200,u_zadumi.jpg\" />Глава Української Греко-Католицької Церкви дає таке свідчення віри, яке дав Блаженний Іван Павло II. Під час розпалу громадянської, політичної, а тепер вже й міжнародної кризи в Україні Глава УГКЦ є відважним свідком, який постійно твердить про моральний закон, права людини та соціальне вчення Католицької Церкви.</p>\n<p>Церква повинна захищати мир і справедливість, подібно як Блаженний Іван Павло II підтримував рух солідарності у своїх пастирських візитах до рідної Польщі, яка в той час була під комуністичним режимом.</p>\n','2014-03-09 20:52:11','http://oranta.org/index.php?option=com_content&view=article&id=1918:blazhennishy-svjatoslav-shevchuk-govoryt-pravdu-svitski-vladi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Блаженніший Святослав (Шевчук) говорить правду світській владі','uk',4123,5606,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5581,200,images.jpg\" />Якось в аптеці у черзі стояв молодий чоловік. Перед ним було двоє покупців: не до сезону одягнута бабуся і молода матір, що зігнулася під вагою дитини на руках. Бабуся підійшла до віконечка, простягнула рецепт і промовила:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Доню, мені тільки ті, перші ліки, бо грошей не вистачить.</p>\n','2014-03-01 22:42:59','http://oranta.org/index.php?option=com_content&view=article&id=1900:babusja-v-apteci-2-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Бабуся в аптеці (2 березня)','uk',4099,5582,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5586,200,12120_2.jpg\" />Чоловік сидів у чайній крамничці, коли до нього підійшов сусід:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, одружуюся, - сказав він другові, - і тому дуже схвильований. А ти ніколи не думав одружуватися?</p>\n<p>Чоловік відповів:</p>\n','2014-03-02 23:50:01','http://oranta.org/index.php?option=com_content&view=article&id=1902:idealna-zhinka-3-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ідеальна жінка (3 березня)','uk',4104,5587,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5588,200,0_cbc4_50ea1d75_L.jpg\" />Якось чоловік вирушив у містом до великого мудреця. Виявилося, щ живе мудрець у старезній халупі на околиці. В оселі не було нічого, крім прогнутого ліжка і заваленого книгами стола, за яким сидів старий дід, заглибившись у читання. Гість спитав його:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Де живе мудрець?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ви мене шукаєте, - пояснив йому старий. Що вас так здивувало?</p>\n','2014-03-04 01:54:41','http://oranta.org/index.php?option=com_content&view=article&id=1904:my-tut-projizdom-4-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ми тут проїздом (4 березня)','uk',4106,5589,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5591,200,4574.jpg\" />Апостольський нунцій Томас Едвард Ґалліксон у своєму <a href=\"http://deovolenteexanimo.blogspot.com/\">блозі</a> поділився думками з приводу ситуації в Україні, звертаючись до старозавітних історій.<strong> </strong></p>\n<p>«Сьогодні я прочитав статус <a href=\"https://www.facebook.com/lesyaorobets?ref=ts&amp;fref=ts\">Лесі Оробець</a> у Фейсбуці, — пише архиєпископ Ґалліксон, -&nbsp; в якому вона стисло, але красномовно закликає Україну піднятися ще раз, тепер вже проти іноземного диктатора. Вона шкодує, що Україна не має часу оплакувати мертвих або надто опікуватися пораненими, вдовами, сиротами або батьками та сім\'ями, що сумують за втратами дітей.</p>\n','2014-03-04 12:29:44','http://oranta.org/index.php?option=com_content&view=article&id=1906:2014-03-04-10-33-31&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' «Лише у Бозі спочивай, душе моя!»','uk',4109,5592,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5594,200,images.jpg\" />Жінка переходила річку по тоненькому мосту і трусилася від страху.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Боже! – думає вона. – Якщо перейду, то віддам тисячу карбованців першому жебракові.</p>\n<p>Міст перестав хитатися.</p>\n','2014-03-05 07:47:38','http://oranta.org/index.php?option=com_content&view=article&id=1908:zhinka-na-mosti-5-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Жінка на мості (5 березня)','uk',4112,5595,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,1619599.jpg\" />Четвертий мудрець також бачив зорю, що сяяла над Вифлеємом. Але він завжди спізнювався прийти туди, де мав бути Ісус, тому що на дорозі його повсякчас спиняли бідні й убогі, що просили допомоги.</p>\n<p>Тридцять років ходячи дорогами Ісуса Христа, мудрець нарешті прийшов до Єрусалиму, ал знову спізнився. Ісус став уже чоловіком, і мудрець прийшов у місто в день розп’яття.</p>\n','2014-03-06 23:19:47','http://oranta.org/index.php?option=com_content&view=article&id=1912:chetverty-mudrec-7-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Четвертий мудрець (7 березня)','uk',4117,5600,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5597,200,228227.jpg\" />Один священик прочитав монахам прекрасний текст, що зворушив усіх. Монахи відразу спитали:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хто його написав?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо я скажу, що Ісус, то ви будете умлівати над текстом, щоранку класти на нього квіти і бити поклони. Якщо я скажу, що написав його патріарх, то ви відчуєте велику шанобу але не будете поклонятися перед ним так. як перед словами Ісуса.</p>\n','2014-03-07 22:04:47','http://oranta.org/index.php?option=com_content&view=article&id=1914:hto-avtor-8-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хто автор (8 березня)','uk',4119,5602,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5607,200,Na1.JPEG\" />Один мудрий старий пан вийшов на пенсію і купив собі скромний будиночок поряд зі школою.</p>\n<p>Уперше за кілька тижнів свого відпочинку він жив у тиші і привіллі, поки не почався навчальний рік. Уже наступного дня троє хлопчаків повні сили і енергії почали ходити вулицею і калатати по всіх сміттєвих контейнерах. що там стояли.</p>\n','2014-03-09 23:57:15','http://oranta.org/index.php?option=com_content&view=article&id=1920:mudry-pensioner-10-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мудрий пенсіонер (10 березня)','uk',4125,5608,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,1319304902_300.jpg\" />Пошана, яку людина складає Господу, має походити з її серця. Ісус Христос вчив: «Бог – Дух. Ті, що Йому поклоняються, повинні у дусі й правді поклонятися» (Йо. 4:24). Часто людина виявляє назовні все те, що діється в її душі, словами чи поведінкою. І християни виявляють назовні шану Богові спільною молитвою, піснями, хресним знаменням, поклонами тощо. Спаситель, коли молився, підносив Свої очі до неба і прихиляв коліна.</p>\n<p>Піст – час для покаяння, тому під час Великого посту читаються покаянні молитви і робляться поклони. Дуже важливо вміти правильно виконувати обрядові елементи нашої віри, зокрема, правильно хреститися і кланятися.\n','2014-03-10 18:34:12','http://oranta.org/index.php?option=com_content&view=article&id=1922:jak-pravylno-hrestytysja-i-klanjatysja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як правильно хреститися і кланятися','uk',4128,5611,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,images.jpg\" />Короля Прусії Фрідріха ІІ (1712-1786) ще за життя прозивали «великим», оскільки він насправді був видатним державним діячем, крім того його любив народ. який чи не найбільше цінував справедливість короля. У пізні роки його життя, люди ласкаво називали короля «старий Фриц».</p>\n<p>Він часто ходив у народ, щоб краще пізнати його потреби і клопоти і кожен міг не церемонячись звернутися зі своїм проханням особисто до короля.</p>\n','2014-03-16 22:39:28','http://oranta.org/index.php?option=com_content&view=article&id=1942:porjadni-ljudy-17-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Порядні люди (17 березня)','uk',4153,5636,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5610,200,ryba2-700x325.jpg\" />Якось одного разу бізнесмен стояв на причалі у маленькому селі і стежив як рибалка у вутлому човнику спіймав велетенського тунця. Бізнесмен привітав рибалку з тим успіхом і запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скільки часу потрібно щоб спіймати таку рибу?</p>\n','2014-03-10 22:07:48','http://oranta.org/index.php?option=com_content&view=article&id=1924:rybalka-i-biznesmen-11-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Рибалка і бізнесмен (11 березня)','uk',4130,5613,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5613,200,1375696.jpg\" />Жив собі багатий купець і було в нього чотири дружини.</p>\n<p>Першу купець любив понад усе, одягав її у пречудові сукні і прикрашав коштовностями. Він піклувався про неї. милував і леліяв.</p>\n<p>Дугу купець також сильно любив. Він невимовно нею гордився і при кожній нагоді старався показувати її друзям. Але завжди боявся, що колись вона піде від нього до когось іншого.</p>\n','2014-03-11 23:38:17','http://oranta.org/index.php?option=com_content&view=article&id=1925:chotyry-druzhyny-12-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чотири дружини (12 березня)','uk',4131,5614,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5614,200,2ba39ae2.jpg\" />Учитель та його учні мандрували і в дорозі не&nbsp; могли як слід поїсти. Учитель попросив їх піти знайти їжу. Учні повернулися наприкінці дня. Кожен приніс те, чим погребували інші: фрукти, що от-от мали зіпсутися, черствий хліб, гірке вино. Один з учнів, проте, приніс кошик доспілих яблук.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я мусив скоїти щось, аби допомогти учителю і своїм братам, - сказав він, розділивши яблука на всіх.</p>\n','2014-03-12 22:04:04','http://oranta.org/index.php?option=com_content&view=article&id=1928:ukradena-pozhyva-13-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Украдена пожива (13 березня)','uk',4134,5617,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,hqdefault.jpg\" />Днями (8–10 березня) Українська Греко-Католицька Церква згадувала сумну подію в своїй історії – 68-ту річницю Львівського псевдособору, на якому за наказом Сталіна було ліквідовано УГКЦ. Отець Полікарп Марцелюк, ЧСВВ, очолює Місію «Постуляційний центр беатифікації УГКЦ». Його завдання – супроводжувати процеси беатифікації на парафіяльному рівні УГКЦ та поширення славної пам’яті ісповідників віри. Отець Полікарп відповів на запитання Департаменту інформації УГКЦ.</p>\n','2014-03-13 19:14:25','http://oranta.org/index.php?option=com_content&view=article&id=1930:2014-03-13-20-15-33&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Якщо українське суспільство хоче бути демократичним, то має пам’ятати сталінські репресії»','uk',4136,5619,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5617,200,image.jpg\" />Жив собі місіонер, який уславився на весь християнський світ тим, що привів у лоно Церкви чимало людей, відвідуючи найглухіші куточки світу. Одного разу його корабель пристав до маленького острівця, де жив тільки один чоловік. Місіонера вразили його ясні очі, але ще більше те, що той нічого не чув про Бога.</p>\n','2014-03-13 22:47:42','http://oranta.org/index.php?option=com_content&view=article&id=1932:to-shcho-bizhyt-po-vodi-14-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Той, що біжить по воді (14 березня)','uk',4138,5621,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,ArticleImag.jpg\" />Цього року виповнюється 25 років виходу з підпілля Української греко-католицької Церкви. З цієї нагоди пропонуємо Вашій увазі інтерв\'ю з Архиєпископом Ігорем Возьняком, Митрополитом львівським УГКЦ.</p>\n<p><strong>– Високопреосвященний владико, чи вбачаєте Ви певну невипадковість в тому, що 25-та річниця легалізації УГКЦ випала у такий важкий для українців період? Чи можемо черпати сили, споглядаючи на шлях, який пройшла наша Церква?</strong>\n','2014-03-14 22:57:14','http://oranta.org/index.php?option=com_content&view=article&id=1934:2014-03-14-21-03-06&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Дуже важлива довіра до Бога. Він і з найтяжчих ситуацій виведе на добре»','uk',4145,5628,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5625,200,Jemchujnikov_kobzar.jpg\" />Одного разу видющий і сліпий вирушили в подорож. Цілу дорогу видющий вів сліпого за собою, переводив його через струмки та балки, застерігав ро вибоїни на дорозі. Але простелився їм шлях через темні гірські печери. Тоді каже видющий до сліпого:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тепер твоя черга мене вести. Бо в печері темно так, що хоч око вибери.</p>\n','2014-03-14 23:25:58','http://oranta.org/index.php?option=com_content&view=article&id=1936:vydjushchy-i-slipy-15-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Видющий і сліпий (15 березня)','uk',4147,5630,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,15-03-14-4.jpg\" />Сьогодні вранці Блаженніший Святослав (Шевчук), Глава УГКЦ, очолив Божественну Літургію на гробі апостола Петра у Ватикані (каплиця Папи Климента VIII). Разом із Предстоятелем УГКЦ за суверенну Україну молилися посли України та Польщі при Апостольській Столиці – Тетяна Іжевська та Пйотр Новіна-Конопка.</p>\n<p>«Стоячи біля гробу святого апостола Петра, ми відчуваємо, що головним господарем історії є Господь Бог, а не президенти, політики, дипломати чи навіть&nbsp; сила зброї.</p>\n','2014-03-15 20:41:57','http://oranta.org/index.php?option=com_content&view=article&id=1938:glava-ugkc-ocholyv-molytvu-za-ukrajinu-na-grobi-svjatogo-petra&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Глава УГКЦ очолив молитву за Україну на гробі святого Петра','uk',4149,5632,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5630,200,index.jpg\" />По сусідству жили побожна і безбожна сім’ї. Безбожні сварилися без упину. А в оселі вірних, повсякчас тиша і розуміння.</p>\n<p>Жінка безбожного каже чоловікові:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Піди до сусідів і роздивися, чому у них завжди все так добре?</p>\n','2014-03-15 23:30:02','http://oranta.org/index.php?option=com_content&view=article&id=1940:zaporuka-myru-v-rodyni-16-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Запорука миру в родині (16 березня)','uk',4151,5634,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5636,200,20aa501308494ea01af30cd753d.jpg\" />Старий рибалка ледве-ледве волік в’язку дров, зібраних у лісі. на мості він стрінувся з лісничим, який давно чекав нагоди відімстити старому за те, що той своїм благочестивим життям, вводив його в сором.</p>\n<p>Розгніваний лісник забрав у старого хмиз і жбурнув у воду. Рибалка почвалав до дому. опустивши голову.</p>\n<p>Навесні наступного року ріка вийшла далеко з берегів.</p>\n','2014-03-17 22:49:53','http://oranta.org/index.php?option=com_content&view=article&id=1944:rybalka-i-lisnychy-18-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Рибалка і лісничий (18 березня)','uk',4156,5639,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5640,200,global_freedom_network-2.jpg\" />Представники світових релігій створили асоціацію і підписали унікальну угоду у справі боротьби з рабством.</p>\n<p>17 березня в Ватикані відбулася презентація безпрецедентної угоди між представниками основних релігій під назвою Global Freedom Network («Глобальна вільна мережа»). Його метою є викорінення сучасного рабства і торгівлі людьми в усьому світі до 2020 року.</p>\n','2014-03-18 13:43:02','http://oranta.org/index.php?option=com_content&view=article&id=1946:vykorinyty-rabstvo-do-2020-roku&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Викорінити рабство до 2020 року','uk',4159,5642,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5643,200,0813dad1.jpg\" />Проповідник ішов полем, де нещодавно відбувся запеклий бій. Він помітив пораненого, підійшов до нього і співчутливо спитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не хочете, щоб я прочитав вам Слово Боже?</p>\n<p>Поранений відповів:</p>\n','2014-03-18 23:31:20','http://oranta.org/index.php?option=com_content&view=article&id=1949:propovid-dlja-poranenogo-19-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Проповідь для пораненого (19 березня)','uk',4161,5644,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5645,200,20.jpg\" />Якось ішов дорогою мудрець. Милувався красою світу і тішився життям, і ось, стрівся йому чоловік. що згорбившись двигав велетенський тягар.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо прирікаєш Ти себе на такі страждання? - запитав мудрець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я страждаю заради щастя своїх дітей та онуків, - відповів чоловік, - мій прадід все життя страждав заради щастя діла, дід страждав заради щастя мого батька і я буду страждати все життя тільки щоб діти мої і онуки були щасливі.</p>\n','2014-03-19 23:12:56','http://oranta.org/index.php?option=com_content&view=article&id=1950:shchastja-dite-20-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Щастя дітей (20 березня)','uk',4163,5646,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5647,200,1743630.jpg\" />Людина, яка покладає надію на себе саму, на власні багатства чи ідеології, приречена бути нещасливою, а той, хто покладається на Господа Бога, навіть під час засухи приносить плоди. Про це, коментуючи біблійне читання з книги пророка Єремії, проповідував Папа Франциск.</p>\n<p>«Проклятий той, хто уповає на людину і покладає на тіло свою силу... Він, мов та билина у степу без листя... Щасливий чоловік, який на Господа вповає... Він, немов дерево, посаджене над водою... під час посухи йому байдуже, воно не перестає родити...», – стверджує автор біблійного тексту, коментуючи який, Святіший Отець наголосив, що «лише у Господі наша стійка надія. Інші не приносять користі, не спасають нас, не приносять життя, не приносять радості».</p>\n','2014-03-21 05:54:20','http://oranta.org/index.php?option=com_content&view=article&id=1955:hto-pokladaje-nadiju-ne-na-boga-to-vse-vtrachaje&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Хто покладає надію не на Бога, той все втрачає','uk',4171,5654,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5649,200,951.jpg\" />Маленька дівчинка поверталася з недільної школи до дому. Назустріч їй іде атеїст, який помічає радісну усмішку на обличчі дитини.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що тебе так тішить? – спитав атеїст.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Іду з недільної школи.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну і що вам сьогодні розповідали? – чоловік вирішив посміятися з дитини.</p>\n','2014-03-20 22:14:50','http://oranta.org/index.php?option=com_content&view=article&id=1957:malenka-divchynka-ta-atejist-21-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Маленька дівчинка та атеїст (21 березня)','uk',4167,5650,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5655,200,192682.jpg\" />Один фотограф, отримавши запрошення на святковий вечір, захопив кілька своїх світлин, аби при нагоді показати їх гостям.</p>\n<p>Господиня побачивши світлини, вигукнула:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Які пречудові фотографії! Ви, мабуть, маєте дуже дорогий фотоапарат!</p>\n<p>Фотограф не відповів їй на це ні слова, але на прощання зауважив:</p>\n','2014-03-22 23:43:25','http://oranta.org/index.php?option=com_content&view=article&id=1960:gospodynja-i-fotograf-23-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Господиня і фотограф (23 березня)','uk',4176,5659,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5655,200,1959251.jpg\" />Якось до срібловолосого мудреця, обливаючись сльозами, прийшла молода і дуже вродлива дівчина:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що мені робити? – крізь сльози скаржилася вона. – Я завжди прагну по-доброму поводитися з людьми, нікого не ображати, допомогти, як тільки можу. І хоча зі всіма я привітна і ласкава, але часто замість вдячності і поваги дістаю гіркі образи і глузування. Я ні в чому не винна і це так несправедливо й образливо. що аж плач бере. Порадьте. що мені робити?</p>\n','2014-03-21 23:19:34','http://oranta.org/index.php?option=com_content&view=article&id=1959:porada-mudrecja-22-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Порада мудреця (22 березня)','uk',4173,5656,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5659,200,1273763310.jpg\" />Один чоловік вирішив порадитись зі старцем Паїсієм Святогорцем, як позбутися пристасти, що йому дошкуляла. Той чоловік мав пихатих приятелів, які часто лихословили супроти інших. Отож поки він міркував. як краще запитати про це хтось приніс старцю в подарунок чудовий кавун.</p>\n<p>Паїсій взяв його і звернувся до прочан зі словами:</p>\n','2014-03-23 21:17:48','http://oranta.org/index.php?option=com_content&view=article&id=1965:jak-vybyraty-druziv-24-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як вибирати друзів (24 березня)','uk',4179,5662,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5662,200,grivna-phl.jpg\" />Одна дівчина вирішила замовити сукню у відомого кравця, тому. що шив він гарно і сумлінно. Однак, вона вважала, що його сукні надто дорогі, а тому зайшовши в майстерню перед усім вирішила домовитися про ціну.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ви дуже дорого берете за свої сукні, - оголосила вона.</p>\n','2014-03-24 22:43:17','http://oranta.org/index.php?option=com_content&view=article&id=1967:kravec-i-divchyna-25-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кравець і дівчина (25 березня)','uk',4181,5664,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5663,200,images.jpg\" />Було у матері сім дочок. Одного разу поїхала матір до сина. який далеко жив. Вернулася вона до дому тільки через тиждень. Коли зайшла до хати. дочки одна по одній почали розповідати, як вони тужили за нею.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я тужила за тобою так, як маківка за сонячним лугом, - сказала перша дочка.</p>\n','2014-03-25 19:40:13','http://oranta.org/index.php?option=com_content&view=article&id=1970:ljubljacha-dochka-26-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любляча дочка (26 березня)','uk',4183,5666,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5667,200,45388.jpg\" />Євангелізація зумовлює також діалог. Сьогодні пріоритетними для діалогу є три середовища, в яких Церква повинна бути присутньою, щоб служити всебічному розвитку людини і шукати загальне добро: діалог з державою, діалог з суспільством – який включає діалог з культурою і наукою – і діалог з вірянами, які не належать до Католицької Церкви. «Церква завжди промовляє в світлі віри»<a href=\"http://oleh-sj.com/J/EG-IV-4.html?rnd=0.9492466526685337#_ftn1\"><span style=\"text-decoration: underline;\">[1]</span> [1]</a>, вносить свій двохтисячолітній досвід і завжди пам’ятає про життя і страждання людини. Цей досвід незрозумілий для людського розуму, але він важливий і може збагатити невіруючих і спонукати їх розширювати свій кругозір.</p>\n','2014-03-26 19:26:02','http://oranta.org/index.php?option=com_content&view=article&id=1971:suspilny-dialog-jak-vklad-u-spravu-myru&catid=37:spilnota-cat&Itemid=49','P',NULL,'Суспільний діалог як вклад у справу миру','uk',4185,5668,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5667,200,vopros_1.jpg\" />Пішов Гусак у город дивитися чи усе там гаразд. Зирк, а&nbsp; на капусті хтось сидить.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти хто? - питає гусак.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Гусениця.</p>\n','2014-03-26 23:34:49','http://oranta.org/index.php?option=com_content&view=article&id=1974:shkola-27-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Школа (27 березня)','uk',4187,5670,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5673,200,01.jpg\" />Один чоловік дуже полюбляв випити і частенько заглядав до пивнички. Але згодом у нього прокидалося сумління і він каявся:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости мені, Господи.</p>\n<p>Це траплялося раз-у-раз. Сатана дивився-дивився на це все. А тоді, не витримав і питає у Бога:</p>\n','2014-03-27 23:52:57','http://oranta.org/index.php?option=com_content&view=article&id=1976:bozhe-myloserdja-28-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Боже милосердя (28 березня)','uk',4191,5674,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5675,200,886558.jpg\" />Сьогодні Церква перебуває в епіцентрі подій. Люди шукають церковної присутності. Вони її потребують. Чи відповідає Церква на цей суспільний запит? Про це Юрій Чорноморець розмовляє з Патріархом Української греко-католицької церкви Святославом і представником Української православної церкви Київського патріархату Патріархом Філаретом.</p>\n<p align=\"center\"><strong> </strong></p>\n','2014-03-28 14:39:12','http://oranta.org/index.php?option=com_content&view=article&id=1977:cerkva-jaka-ne-movchyt&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Церква, яка не мовчить','uk',4193,5676,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5677,200,383489811.jpg\" />Якось у старенького священика хтось запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому бідні люди співчутливі до чужої біди, а багаті ні?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Виглянь у вікно, - відповів той, - що ти бачиш?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачу дерева, автомобілі, гуляють діти.</p>\n','2014-03-29 02:41:00','http://oranta.org/index.php?option=com_content&view=article&id=1979:chomu-bidni-ljudy-spivchutlyvi-29-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чому бідні люди співчутливі (29 березня)','uk',4195,5678,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5677,200,796.jpg\" />Прийшов чоловік до мудреця питати пораду.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Скажи, яка таємниця твоїх знань? Ти щасливий. До тебе ідуть люди, щоб дізнатися як поліпшити своє життя. Я багато вчуся, але незгоди сипляться на мене.</p>\n<p>Мудрець лише усміхнувся і покликав свою дружину, і через кілька хвилин увійшла вродлива жінка, її очі сяяли і тоді мудрець попросив:</p>\n','2014-03-29 22:52:37','http://oranta.org/index.php?option=com_content&view=article&id=1981:shchaslyva-simja-30-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Щаслива сім’я (30 березня)','uk',4196,5679,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5679,200,Fotolia.jpg\" />Один монах посадив оливкове дерево і заповзявся молитися:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Господи пошли деревцю моєму дощу.</p>\n<p>І послав Господь на землю дощ. Деревце напилося вологи. А монах молився далі.</p>\n','2014-03-31 01:23:40','http://oranta.org/index.php?option=com_content&view=article&id=1983:dva-monahy-ta-jihni-olyvkovi-dreva-31-bereznja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Два монахи та їхні оливкові древа (31 березня)','uk',4198,5681,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5681,200,1969126.jpg\" />Купець повертався додому з далеких країв із дорогим крамом. Зненацька погода зіпсувалася: здійнявся ураган, почали ламатися дерева, курява заступила дорогу. Купець не мав де сховатися і, врешті, збився з дороги. Він відчайдушно кликав на допомогу, але ніхто не відгукувався. Зрозумівши, що почалася ніч, він почав молитися.</p>\n','2014-04-01 11:42:56','http://oranta.org/index.php?option=com_content&view=article&id=1985:daremna-nagoroda-1-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Даремна нагорода (1 квітня)','uk',4201,5684,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,20120407130246.jpg\" />\"Архангельським голосом кличемо до Тебе, Пречиста: \"Радуйся благодатна, Господь з Тобою\"<em></em></p>\n<p><em>Величання празника</em></p>\n<p>\"Днесь спасення нашого начало і від віку таїнства явління: Син Божий  сином Діви стається\" (Тропар празника). Ось таким по­хвальним гимном  наша Церква вітає празник Благовіщення Пресвя­тої Богородиці, називаючи  його початком нашого спасення. І слуш­но, бо цей празник належить до  найважливіших свят нашого цер­ковного року.</p>\n','2014-04-06 22:22:49','http://oranta.org/index.php?option=com_content&view=article&id=2001:blagovishchennja-prechystoji-divy-mariji&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Блага вість','uk',4221,5704,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5686,200,pokrov.jpg\" /></p>\n<p>У п\'яту суботу Великого посту наша Східна Церква має особ­ливу богослужбу на честь Пречистої Діви Марії. Ця відправа, характерна тільки для Східної Церкви, має назву акафисту, а звідси й субота зветься Акафистова. Акафистова субота, як і неділя Православ\'я, не має якогось відношення до 40-ці. Вона випадає в тому часі на підставі історич­ної традиції і практики Церкви. На утрені цієї суботи береться цілий акафист Благовіщення Пресвятої Богородиці. Цей акафист можна назвати символом і вершиною великого культу Богоматері у Східній Церкві. Саме тому він заслуговує на окрему увагу.</p>\n','2014-04-05 10:23:18','http://oranta.org/index.php?option=com_content&view=article&id=1997:akafistova-subota&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Акафістова субота','uk',4215,5698,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5687,200,daun.jpg\" />Жив собі один цар, який мав надзвичайну розумну дочку. Однак, хворіла вона невідому недугу. Щороку слабшали її руки і ноги, зір і слух. Чимало лікарів марно намагалися лікувати її. Якось у двір прийшов старець про якого казали, що він знає таємницю життя.</p>\n<p>Усі царедворці поспішили звернутися до нього з проханням допомогти недужій принцесі. Старець дав принцесі кошика з лози закритого покривалом і сказав:</p>\n','2014-04-03 00:16:56','http://oranta.org/index.php?option=com_content&view=article&id=1989:pryncesa-3-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Принцеса (3 квітня)','uk',4206,5689,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5692,200,img3.jpg\" />Єдиного пасажира. що вижив після корабельної аварії викинуло на безлюдний острів. З уламків корабля, що прибилися до берега, він так-сяк збудував собі схованку, заніс туди все, що викинуло море і могло стати йому в пригоді. Він підніс хвалу і подякував Богові за свій порятунок. Минуло багато днів, та поряд з островом і не пропливало жодне судно, щоранку він обходив острів шукаючи їжі. Одного разу, під час такої мандрівки, спалахнула пожежа, полум’я пожерло усі дерева навкруги. Його житло і всі речі згоріли до чита, тільки чорний дим підносився до неба.</p>\n','2014-04-04 00:45:36','http://oranta.org/index.php?option=com_content&view=article&id=1993:robinzon-4-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Робінзон (4 квітня)','uk',4210,5693,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5694,200,img_22471.jpg\" />Помер учений і його душа стала перед Богом. Каже учений до Бога:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми, люди науки, дійшли висновку, що більше Тебе не потребуємо! Ми осягли всі таємниці, знаємо те, що знаєш Ти, вміємо клонувати людей. трансплантувати людські органи, створюємо нові види тварин і рослин… Одне слово. ми можемо робити все, що раніше вважали чудесним і приписували Твоїй Мудрості і Всемогутності.</p>\n','2014-04-05 00:07:54','http://oranta.org/index.php?option=com_content&view=article&id=1995:shcho-zh-mozhut-ljudy-5-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Що ж можуть люди (5 квітня)','uk',4213,5696,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5699,200,images.jpg\" />Вистромивши голову з гнізда орленя побачило силу силенну птахів, що літали внизу серед скель.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Мамо, що це за птахи? – спитав він.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Наші друзів, – відповіла орлиця синові, – орел живе самотньо – така його доля, але й він подеколи потребує товариства. Який же він тоді цар птахів? Усі , кого ти бачиш внизу, - наші вірні друзі.<o:p></o:p></p>\n','2014-04-13 22:25:30','http://oranta.org/index.php?option=com_content&view=article&id=2030:jak-znaty-virnyh-druziv-14-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як знайти вірних друзів (14 квітня)','uk',4255,5738,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5700,200,zwiastowanie.jpg\" />Свято Благовіщення Пресвятої Богородиці святкують християни як східної, так і західної церковних традицій. Воно оповідає нам про подію, коли Архангел Гавриїл з’явився Діві Марії, сповіщаючи про те, що незабаром вона народить Месію – Спасителя світу.</p>\n<p>Це свято заставляє нас призадуматися про дар батьківства і материнства, а також і те, як родина повинна приймати Господню волю. Про це розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.</p>\n<p><strong>&nbsp;</strong></p>\n','2014-04-07 11:41:08','http://oranta.org/index.php?option=com_content&view=article&id=2005:2014-04-07-08-47-25&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Основне послання свята Благовіщення - те, що Господь нам дає, ми повинні прийняти…»','uk',4225,5708,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5713,200,getImage.jpg\" />Прийшов один чоловік у перукарню. І час стриження і гоління заговорив він з перукарем про Бога… В якийсь момент перукар твердо сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хай що би ви мені не говорили, а я не вірю, що Бог є.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому? – запитав клієнт.</p>\n','2014-04-08 23:26:27','http://oranta.org/index.php?option=com_content&view=article&id=2011:volocjuga-i-perukar-9-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Волоцюга і перукар (9 квітня)','uk',4232,5715,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5714,200,artleo.com-6681.jpg\" />Якось сказала внучка до бабусі:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Усі чоловіки однакові. Бабусю, йому досить сказати мені перший раз привіт, а я вже знаю як він себе поводитиме, які байки розказуватиме, як усміхатиметься, сваритиметься і йтиме.</p>\n','2014-04-09 23:21:51','http://oranta.org/index.php?option=com_content&view=article&id=2013:chy-vsi-choloviky-odnakovi-10-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чи всі чоловіки однакові? (10 квітня)','uk',4233,5716,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5721,200,images.jpg\" />Якось один із найвідоміших у світі професорів, номінант на Нобелівську премію, вийшов на берег озера і попросив човняра покатати його на човні. Той згодився. Коли вони відплили далеко від берега, професор почав розпитувати човняра:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти знаєш історію?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні.</p>\n','2014-04-10 21:27:08','http://oranta.org/index.php?option=com_content&view=article&id=2017:profesor-i-chovnjar-11-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Професор і човняр (11 квітня)','uk',4239,5722,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5724,200,philos_school.jpg\" />Інститут релігії і суспільства УКУ запрошує подавати заявки на участь у літні школі 2014 р. <strong>« Соціальна думка Митрополита Андрея Шептицького:</strong><strong> </strong><strong>міжнаціональні стосунки та державне будівництво»</strong></p>\n<p>Мета школи – глибше ознайомити учасників з духовним спадком та діяльністю Митрополита Андрея та розвинути їхнє вміння жити згідно з християнськими цінностями у суспільному житті. Головний акцент цьогорічної школи буде поставлено на побудові суспільства на засадах християнської моралі й цінностей у історичному і сучасному контексті.</p>\n','2014-04-10 22:32:04','http://oranta.org/index.php?option=com_content&view=article&id=2019:2014-04-10-19-40-17&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Соціальна думка Митрополита Андрея Шептицького: міжнаціональні стосунки та державне будівництво»','uk',4243,5726,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5729,200,makaku.jpg\" />Одного разу, повернувшись із прогулянки, мудрець застав своїх учнів за бурхливими дебатами. Він запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Про що ви сперечаєтеся?</p>\n<p>Усі відповідали:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Учителю, ми прагнемо визначити, які три людські вади найстрашніші.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І якого ж висновку ви дійшли?</p>\n','2014-04-11 22:55:20','http://oranta.org/index.php?option=com_content&view=article&id=2023:try-nastrashnishi-ljudski-vady-12-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Три найстрашніші людські вади (12 квітня)','uk',4247,5730,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5739,200,1291190728_2.jpg\" />Розпочався Страсний тиждень, який нагадує нам про те, що Ісус Христос терпів за наш гріхи на хресті. Цей тиждень особливо насичений різними богослужіннями і має свої важливі дні, зокрема Страсний четвер, Велику п’ятницю і суботу… Про це і про важливість Сповіді та Причастя перед Великоднем розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.\n','2014-04-15 06:09:05','http://oranta.org/index.php?option=com_content&view=article&id=2038:2014-04-14-21-12-49&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Дбати про своє духовне життя потрібно постійно… а не тільки під час Страсного тижня і Великого посту…»','uk',4260,5743,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5740,200,samaryanin.jpg\" />Жив собі цар, і все життя не давали йому спокою три питання: яка найголовніша пора? Хто найголовніша людина? І яка найголовніша справа?</p>\n<p>Думав цар: «якщо знатиму відповіді на ці питання, весь світ підкорю, здійсню все, що забажаю, і стане народ шанувати мене, як великого мудреця».</p>\n','2014-04-14 23:11:58','http://oranta.org/index.php?option=com_content&view=article&id=2033:nagolovnishe-15-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найголовніше  (15 квітня)','uk',4258,5741,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5745,200,article4113.jpg\" />Одного разу до старого вчителя бойових мистецтв прийшов учень і сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Учителю, я – чемпіон країни з боксу і французької боротьби. Чого ви ще можете навчити мене?<o:p></o:p></p>\n','2014-04-15 22:18:24','http://oranta.org/index.php?option=com_content&view=article&id=2040:dobra-vchytelska-poradi-16-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Добра вчительська пораді  (16 квітня)','uk',4263,5746,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5749,200,644366.jpg\" />До відомого жонглера прийшов чоловік:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я хочу навчитися жонглювати як ви, - сказав він.<o:p></o:p></p>\n<p>Жонглер дав йому три кульки:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Основа нашої майстерності – каскад, підкидання трьох кульок. Приходьте, коли опануєте цей трюк.<o:p></o:p></p>\n','2014-04-16 22:44:59','http://oranta.org/index.php?option=com_content&view=article&id=2045:sekret-zhongljuvannja-17-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Секрет жонглювання (17 квітня)','uk',4267,5750,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,10154296.jpg\" />Колись давно жив один дід. Жив він у старому храмі. До того храму приходили бавитися діти. Одного разу, коли най пустотливіший хлопчик бавився на приступцях храму. До нього прилетіло три горобці. Перший горобець сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Найвеличніша річ у цьому світі – сонце. Завдяки сонцю наш світ такий прекрасний. Але люди, що звикли до його світла – сприймають сонце як щось звичайне.<o:p></o:p></p>\n','2014-04-18 22:34:15','http://oranta.org/index.php?option=com_content&view=article&id=2049:navelychnisha-rich-19-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найвеличніша річ (19 квітня)','uk',4273,5756,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,4509417_740bb203.jpg\" />Один літній чоловік мав дуже слабке здоров’я. Одного разу він почув, як Бог наказує йому, штовхати величезний камінь на своєму подвір’ї. День у день, місяць у місяць, рік у рік штовхав він цей камінь. Настав день. Коли сатана прийшов до нього і спитав наскільки далеко він пересунув камінь, штовхаючи його щодня.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні на дюйм, - відповів старий, - я взагалі його не зсунув.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ну ти і невдаха, відповів йому диявол. Бог ніколи н казав штовхати цей камінь, якби він повелів би це тобі, то ти давно би зсунув його з місця.</p>\n','2014-04-19 22:55:50','http://oranta.org/index.php?option=com_content&view=article&id=2050:ruh-i-postup-20-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Рух і поступ (20 квітня)','uk',4278,5761,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5752,200,8799.jpg\" />Жив собі в селі старий чоловік, він був дуже бідний, але навіть королі заздрили йому, тому, що в нього був пречудовий білий кінь. Йому пропонували за коня нечувані гроші, але чоловік відповідав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Цей кінь для мене, не кінь, а товариш. Як можна продати товариша?<o:p></o:p></p>\n<p>Чоловік будував, але не згоджувався продавати коня. Одного ранку він не знайшов коня у стійлі. Зібралося все село і всі почали осуджувати старого:\n','2014-04-18 09:15:10','http://oranta.org/index.php?option=com_content&view=article&id=2047:prytcha-pro-sprynjattja-disnosti-18-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про сприйняття дійсності (18 квітня)','uk',4271,5754,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5770,200,5563.jpg\" />Звернулися два брати до Бога:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навчи нас великої науки – життя!</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Добре, - відповів Всевишній. – я прикличу вас, коли будете готові.</p>\n','2014-04-22 10:54:48','http://oranta.org/index.php?option=com_content&view=article&id=2059:urok-zhyttja-22-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Урок життя (22 квітня)','uk',4288,5771,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5774,200,images.jpg\" />В одному гірському селищі жив чоловік, відомий тим, що ніколи не сперечався. Приїхав до нього кореспондент, щоб написати про це статтю. І між ними відбулася бесіда:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Скажіть, це правда, що ви прожили 90 з лишком років і жодного разу ні з ким не сперечалися?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Так. Це правда.\n','2014-04-22 23:08:02','http://oranta.org/index.php?option=com_content&view=article&id=2063:jak-zhyty-ne-sperechajuchys-23-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як жити не сперечаючись (23 квітня)','uk',4292,5775,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5776,200,1_1_793150.jpg\" />«Чому шукаєте живого між померлими?». Це запитання, яке ангел поставив жінкам-мироносицям, що прийшли до Ісусового гробу, стало темою роздумів Папи Франциска, якими він поділився з десятками тисяч паломників, які у середу, 23 квітня 2014 р., не зважаючи на дощовий ранок, заповнили площу святого Петра у Ватикані, щоб взяти участь у загальній аудієнції з Наступником святого Петра.<o:p></o:p></p>\n','2014-04-23 23:06:54','http://oranta.org/index.php?option=com_content&view=article&id=2065:2014-04-23-20-08-51&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Чому шукаєте живого між померлими?»','uk',4294,5777,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5776,200,pravoslavna-i-prav.jpg\" />Мешканці одного села усіляко переслідували жінку, яка стала християнкою. Коли вона йшла у церкву на неї сипалися глузування, погрози, обмови. Всі знущання вона терпіла спокійно, покладаючись на господа.<o:p></o:p></p>\n<p>Одного разу озлоблені селяни закидали її картоплею. Жінка ретельно повизбирувала картоплю, що лежала на землі і склала її в одну купу. Мешканці злякалися, що сердита жінка тепер їх закидуватиме картоплею і розійшлися.<o:p></o:p></p>\n','2014-04-24 00:46:08','http://oranta.org/index.php?option=com_content&view=article&id=2067:torzhestvo-dobra-24-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Торжество добра (24 квітня)','uk',4296,5779,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5779,200,9979070.jpg\" />Один старий і мудрий чоловік сказав своєму учневі:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Роздивися кімнату, де ми сидимо, і спробуй запам’ятати речі коричневого кольору.<o:p></o:p></p>\n<p>У кімнаті було повно всього коричневого, і учень швидко впорався з завданням. Але мудрець залі запитував його:\n','2014-04-24 22:19:30','http://oranta.org/index.php?option=com_content&view=article&id=2069:dobre-i-zle-25-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Добре і зле (25 квітня)','uk',4298,5781,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5786,200,1-IMG_5675.jpg\" />Одного разу чернець прийшовши до церкви почув, що деякі брати сперечаються між собою.<o:p></o:p></p>\n<p>Перш ніж повернутися до своєї келії, він обійшов її тричі і тільки потому зайшов до неї.<o:p></o:p></p>\n<p>Ті хто бачили це запитали, чому він так вчинив.<o:p></o:p></p>\n<p>Чернець відповів.\n','2014-04-26 22:00:30','http://oranta.org/index.php?option=com_content&view=article&id=2075:pokynuty-nezlagodu-27-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Покинути незлагоду (27 квітня)','uk',4307,5790,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5792,200,9.jpg\" />Один цар уславився своєю мудрістю. Але попри цю мудрість у житті не мав він спокою. І одного разу звернувся цар до мудреця з проханням:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Допоможи мені, дуже багато в цьому житті доводить мене до нестями. Маю велику схильність до пристрастей і це вельми мені заважає.<o:p></o:p></p>\n<p>Мудрець дав йому перстень на якому була викарбувана фраза – «це мине»! і сказав:\n','2014-04-27 23:05:57','http://oranta.org/index.php?option=com_content&view=article&id=2079:i-ce-myne-takozh-28-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'І це мине також (28 квітня)','uk',4311,5794,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5794,200,xristianskay.jpg\" />Жив собі брат при церкві і мав він недугу на ім’я «найкращий від усіх». Хай про що тільки мова не зайде, він повсюди старався бути найкращим від усіх: прибирати в церкві – він вже мітлою мете; всеношну молитву – він серед перших запопадливо молиться, рулади словесні виписує.<o:p></o:p></p>\n<p>І от, в ніч на П’ятидесятницю приснився йому сон – потрапляє цей брат на небо, стоїть у черзі праведників чекає і думає як Ісус його серед усіх виокремить, під білі ручки візьме, похвалою засипле.<o:p></o:p></p>\n','2014-04-29 08:40:48','http://oranta.org/index.php?option=com_content&view=article&id=2081:nakrashcha-vid-usih-29-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найкраща від усіх (29 квітня)','uk',4314,5797,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5800,200,620x400.jpg\" />Без сумніву, в історію католицького світу 27 квітня 2014 року увійде як золотий день – Служба Божа чотирьох Пап, двоє з яких проголошені святими, явище унікальне. І кожен з них, в тій чи в іншій мірі, мав відношення до української історії.</p>\n<p>Святий Іван ХХІІІ спричинився до визволення Патріярха Йосифа Сліпого з московської неволі. І це уможливило перетворення УГКЦ на Заході з розрізнених єпархій в єдину структуру, з усіма здобутками для української діаспори і для українців Краю.</p>\n','2014-04-30 00:03:01','http://oranta.org/index.php?option=com_content&view=article&id=2087:shchob-ne-bulo-pizno&catid=37:spilnota-cat&Itemid=49','P',NULL,'Щоб не було пізно','uk',4320,5803,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5800,200,image078.jpg\" />Вирішив один чоловік побудувати дім. Спершу він викопав глибоку яму під фундамент. У нечасті хвилини відпочинку видно було йому тільки хмари в небі і ноги людей, які ішли зовсім поряд.</p>\n<p>Коли фундамент був готовий, чоловік почав зводити стіни першого поверху. Тепер він стояв на землі, а під час відпочинку, міг дивитися в будь-який бік. Та й видно було йому&nbsp; відти набагато більше від того, що міг він роздивитися досі.</p>\n','2014-04-29 23:50:12','http://oranta.org/index.php?option=com_content&view=article&id=2085:pogljad-na-zhyttja-30-kvitnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Погляд на життя  (30 квітня)','uk',4319,5802,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,293026.jpg\" />Жив собі селянин, який вважав, що було би набагато ліпше, якби він міг впливати на погоду.</p>\n<p>–&nbsp;Зерно буде швидше достигати,&nbsp;–&nbsp;думав він,&nbsp;–&nbsp;і в колосках буде більше зерна.</p>\n<p>Бог почув його думки і сказав:</p>\n<p>–&nbsp;Коли гадаєш, що знаєш ліпше, яка і коли потрібна погода, то керуй нею цього літа.</p>\n','2014-05-01 23:13:50','http://oranta.org/index.php?option=com_content&view=article&id=2090:ljudska-mudrist-2-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Людська мудрість (2 травня)','uk',4326,5809,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,images.jpg\" />Бог зліпив чоловіка з глини, і залишився в Нього один зайвий шматок.</p>\n<p>–&nbsp;Що тобі зліпити?&nbsp;–&nbsp;спитав Бог.</p>\n<p>–&nbsp;Зліпи мені щастя,&nbsp;–&nbsp;попросив чоловік.</p>\n<p>Нічого не відповів Бог, а тільки поклав чоловікові в долоню решту глини.</p>\n','2014-05-02 23:18:28','http://oranta.org/index.php?option=com_content&view=article&id=2092:shchastja-3-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Щастя (3 травня)','uk',4330,5813,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,mid_63154_7535.jpg\" />Якось увечері у двері до мудреця постукав запізнілий подорожанин. Мудрець запросив його у свою оселю, почастував скромною вечерею, і вони розговорилися.</p>\n<p>–&nbsp;Послухай!&nbsp;–&nbsp;сказав гість.&nbsp;–&nbsp;Слава про твою мудрість дійшла до нашого краю. Ти багато знаєш. Чи можеш ти пояснити мені, для чого людина живе на цьому світі, у чому смисл життя?</p>\n<p>–&nbsp;А ти сам як гадаєш?&nbsp;–&nbsp;спитав мудрець.</p>\n','2014-05-03 23:23:14','http://oranta.org/index.php?option=com_content&view=article&id=2093:smysl-zhyttja-4-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Смисл життя (4 травня)','uk',4332,5815,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5803,200,priroda.jpg\" />Одному чоловікові наснився сон, що він потрапив на небо. І чудувався він з небесної краси. Які пречудові дерева, квіти, трава!</p>\n<p>Ангел запитав його, чи ж не бачив він чогось подібного на землі? Чоловік впевнено відповів, що на землі взагалі нічого подібного немає.</p>\n<p>Ангел же сказав:</p>\n','2014-04-30 23:08:42','http://oranta.org/index.php?option=com_content&view=article&id=2089:zemna-i-nebesna-krasa-1-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Земна і небесна краса (1 травня)','uk',4321,5804,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5809,200,383981284.jpg\" />1. Став першим Римським Папою неіталійського походження, обраним за останні 455 років, одним з наймолодших Понтифіків в історії і першим Папою слов\'янського походження.<o:p></o:p></p>\n<p>2. В юності захоплювався театром і мріяв стати професійним актором. У 14 років спробував себе в шкільному драматичному гуртку і написав п\'єсу «Король-Дух». У Ягеллонському Університеті в Кракові став членом театрального гуртка «Студії 38». А в 1942 році, будучи семінаристом, став одним з ініціаторів «Театру Рапсодії», що діяв підпільно.<o:p></o:p></p>\n','2014-05-02 08:27:10','http://oranta.org/index.php?option=com_content&view=article&id=2098:25-faktiv-pro-papu-ivana-pavla-ii&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'25 фактів про Папу Івана Павла ІІ','uk',4328,5811,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5816,200,145322.jpg\" />Михайло&nbsp;Димид&nbsp;розповів&nbsp;\"Сегодня\",&nbsp;чому&nbsp;поїхав на&nbsp;Майдан,&nbsp;чому,&nbsp;на його думку,&nbsp;жителі сходу&nbsp;не люблять&nbsp;Україну&nbsp;і чому&nbsp;кримчани&nbsp;віддали&nbsp;Крим</p>\n<p>Ім\'я львівського священика Михайла Димида стало відомим після гучних судових розглядів з ДАІ: у січні цього року автоінспекція намагалася «сфабрикувати» проти нього «справу» за участь в Автомайдані, але отець Михайло виграв суд, а тепер шукає імена замовників.</p>\n','2014-05-03 23:50:31','http://oranta.org/index.php?option=com_content&view=article&id=2102:2014-05-03-20-56-44&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Путін дивиться на світ як імператор і бажає замінити людям Бога»','uk',4334,5817,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5818,200,1374130.jpg\" />Два ангели подорожні зупинилися у багатій оселі. Сім’я була негостинна і не захотіла постелити ангелам у вітальні. Замість того їх поклали спати в холодний підвал. Коли вони стелили постіль, старший ангел побачив дірку в стіні і заклав її. Побачивши це , молодший запитав, навіщо. Старший відповів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не все так, як видається.</p>\n','2014-05-05 09:09:39','http://oranta.org/index.php?option=com_content&view=article&id=2104:ne-vse-take-jakym-vydajetsja-6-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Не все таке, яким видається (6 травня)','uk',4337,5820,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5822,200,4.jpg\" />Один чоловік мріяв про ліпше життя, йому не подобався той дім де він жив, одежа, яку носив, одне слово – йому не подобалось все, що його оточувало. Він замислювався над питанням, чому хтось має все, про що можна тільки мріяти, а в нього немає нічого.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; От, якби в мене був гарний дім, вродлива жінка, багато грошей, то я був би щасливий, - думав чоловік цілими днями.</p>\n','2014-05-05 22:17:05','http://oranta.org/index.php?option=com_content&view=article&id=2108:jak-znaty-shchastja-6-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як знайти щастя (6 травня)','uk',4340,5823,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5823,200,7100.jpg\" />Цими днями о. Олесь Августин Чумаков&nbsp;<a href=\"https://www.facebook.com/Rev.Olko?fref=ts\" target=\"_blank\">влучно і точно охарактеризува</a>в дуже багато українських проблем цитатою раббі Елеазара Ґадола: «Кожен хто себе робить милосердним до дійсно жорстоких, в кінці себе робить жорстоким до милосердних». Тобто, зло, яке залишається не покараним, рано чи пізно обертається проти добра. Така його природа. Непокарані фальсифікації 2004 вродили щедрий урожай в часи Януковича. І це тільки один приклад, коли відсутність належного покарання зла веде до його збільшення. У цьому світлі і сьогоднішня ситуація на Донбасі – це тільки наслідок непокараного у 2004 – 2005 Сєвєродонецького збіговиська.</p>\n','2014-05-06 21:31:35','http://oranta.org/index.php?option=com_content&view=article&id=2110:zlo-musyt-buty-pokaranym&catid=37:spilnota-cat&Itemid=49','P',NULL,'Зло мусить бути покараним','uk',4343,5826,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5827,200,4654.jpg\" />Побачив Чорторий на березі чудову Лілію і вирішив, хай там що, її домогтися. Чого ж він тільки не пропонував красуні: поплавати на його швидких хвилях, ласкаву прохолоду води в нестепну спеку і цілу водоверть при всяких розваг та втіх. Завагалася красуня. Помітив це Жук, безнадійно закоханий у неї і заповзявся відмовляти:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Згубить він тебе, пропадеш.<o:p></o:p></p>\n','2014-05-07 23:26:11','http://oranta.org/index.php?option=com_content&view=article&id=2114:spravzhnja-ljubov-8-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Справжня любов (8 травня)','uk',4346,5829,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5829,200,molitva.jpg\" />Якось на одного пустельника напосіли важкі помисли. Перебуваючи у такому стані він почав виливати свою печаль перед Богом.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Господи, - говорив він, хочу спастися, але помисли мої ніяк не пускають мене це звершити. Що мені робити? Як мені спастися?<o:p></o:p></p>\n','2014-05-09 00:13:20','http://oranta.org/index.php?option=com_content&view=article&id=2116:doroga-do-spasennja-9-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дорога до спасення (9 травня)','uk',4348,5831,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5832,200,a3a34dc3.jpg\" />Жили собі три сестри; перша була ледащиця, друга злюща презлюща, а третя і розумниця, і красуня, і рукодільниця -&nbsp; сама втіха і радість. Одного ранку спинилася підвода під їхніми воротами, сестри виглянули подивитися хто приїхав. На підводі сиділа незнайома стара.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Хто ти? – спитали вони.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я, доля. Вже пора виходити вам заміж.<o:p></o:p></p>\n','2014-05-09 21:54:36','http://oranta.org/index.php?option=com_content&view=article&id=2118:try-sestry-10-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Три сестри (10 травня)','uk',4350,5833,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5834,200,images.jpg\" />Старця Паїсія часто питали, що таке справедливість, які вчинки справедливі?<o:p></o:p></p>\n<p>Старець сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Є справедливість людська, а є Божа.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->А що каже Божа справедливість?</p>\n','2014-05-10 22:37:46','http://oranta.org/index.php?option=com_content&view=article&id=2120:bozha-spravedlyvist-11-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Божа справедливість (11 травня)','uk',4352,5835,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5841,200,original-1358941540.JPG\" />Ось історія юнака, який пішов шукати щастя.<o:p></o:p></p>\n<p>Один чоловік покликав перед смертю сина і сказав йому так:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Сину мі, я знаю, що я скоро помру, я хочу, щоб твоє життя було щасливе.<o:p></o:p></p>\n<p>Син запитав:\n','2014-05-12 23:46:46','http://oranta.org/index.php?option=com_content&view=article&id=2126:u-poshukah-shchastja-13-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'У пошуках щастя (13 травня)','uk',4359,5842,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,81948093278333.gif\" />Християнин сидить на перехресті і молиться про Божий провід. Раптом бачить, що біжить чоловік і на ходу звертається до нього:</p>\n<p>–&nbsp;За мною женуться розбишаки, не виказуй мене, будь ласка.</p>\n<p>І побіг далі.</p>\n','2014-05-14 23:19:44','http://oranta.org/index.php?option=com_content&view=article&id=2131:mudre-rishennja-15-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мудре рішення (15 травня)','uk',4365,5848,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,Biblia-sajt.jpg\" />Стоять два брати. Один гладкий, а інший худий. Гладкий каже до худого:</p>\n<p>–&nbsp;Поміркував я і вирішив, що більше розмовляти з тобою не буду.</p>\n<p>Худий брат у нестямі:</p>\n','2014-05-16 23:33:37','http://oranta.org/index.php?option=com_content&view=article&id=2133:istynny-smysl-17-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Істинний смисл (17 травня)','uk',4368,5851,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,article769.jpg\" />Зустрілися два товариші.<o:p></o:p></p>\n<p>Один каже:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Моя жінка така нечепура і недбайлива! Я їй раз за разом про це кажу, еле щороку тільки гірша і гірша.<o:p></o:p></p>\n','2014-05-13 23:16:04','http://oranta.org/index.php?option=com_content&view=article&id=2130:prytcha-pro-dvoh-zhinok-14-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про двох жінок (14 травня)','uk',4363,5846,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,dsT3O1D_WAc.jpg\" />Дожив Матвій до похилого віку. Жив він праведно, нікого не ображав, однак чесною працею любив старий збирати багатство. Сини у Матвія дорослі, до роботи меткі, самі уміли добре господарювати, от і зібралося багатства чимало.</p>\n<p>Але настав час вмирати старому, і душа його полинула до брами небесної. Довго вона там стояла і чекала, але брама не відчинялася, і став міркувати Матвій, чи не згрішив чимось, став пригадувати своє земне життя, але не знайшов за собою нічого злого.</p>\n','2014-05-15 23:24:59','http://oranta.org/index.php?option=com_content&view=article&id=2132:myrske-bagatstvo-16-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мирське багатство (16 травня)','uk',4367,5850,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,hq-wall.jpg\" />Одного разу в лісі чоловік побачив лисицю, яка не мала ніг. Він захотів дізнатися, як вона змогла вижити. І раптом побачив тигра із здобиччю в руках. Тигр трохи поїв, а решту віддав лисиці.</p>\n<p>Наступного дня Бог знову послав тигра годувати лисицю. Чоловік замислився про милосердя Боже і подумав:</p>\n','2014-05-18 23:46:18','http://oranta.org/index.php?option=com_content&view=article&id=2136:z-kogo-braty-pryklad-19-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'З кого брати приклад (19 травня)','uk',4373,5856,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,smysl.jpg\" />Мудрець мовив на прощання своєму попутнику:</p>\n<p>–&nbsp;Бажаю вам мати в житті повно клопотів!</p>\n','2014-05-19 23:50:19','http://oranta.org/index.php?option=com_content&view=article&id=2137:bezlich-klopotiv-20-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Безліч клопотів (20 травня)','uk',4377,5860,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5845,200,swan.jpg\" />Багато років один підрядник працював у великій будівельній фірмі. Одного разу він отримав завдання побудувати віллу за проєктом, який припав йому до вподоби. Він міг вибрати для будівництва хоч яке завгодно місце і не хвилюватися про витрати.</p>\n<p>Роботи почалися швидко. Але, скориставшись висловленою йому довірою, підрядник вирішив використати матеріяли найнижчої якости і найняв робітників дуже низької кваліфікації, виплачуючи їм мінімальну зарплату. Заощаджені гроші він забрав собі.</p>\n','2014-05-17 23:39:26','http://oranta.org/index.php?option=com_content&view=article&id=2134:zipsutist-18-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Зіпсутість (18 травня)','uk',4371,5854,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5857,200,majdanek.jpg\" />Він був звичайним парафіяльним священиком, а тепер багато душпастирів мають його за свого покровителя. Третій рік поспіль в травні містечко Перемишляни на Львівщині приймає греко-католицьке духовенство з усієї України на прощі, присвяченій пам’яті блаженного священомученика Омеляна Ковча. 14 травня тут зустрічаються служителі УГКЦ з нагоди 5-ї річниці проголошення о. Ковча їхнім покровителем та 25-ї річниці легалізації Церкви.</p>\n','2014-05-19 11:14:31','http://oranta.org/index.php?option=com_content&view=article&id=2143:duhovny-batko-vjazniv-madaneka&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Духовний батько в\'язнів Майданека','uk',4375,5858,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,130318.jpg\" />Старий, підійшовши до яблуні, на якій висіли достиглі налиті яблука, попросив молодого чоловіка, що лежав під деревом, зірвати одне яблуко.</p>\n<p>–&nbsp;Це твої проблеми, старий!&nbsp;–&nbsp;розреготався молодик.&nbsp;–&nbsp;Чекай, поки яблуко впаде тобі до ніг!</p>\n','2014-05-23 22:10:47','http://oranta.org/index.php?option=com_content&view=article&id=2152:tvoji-problemy-24-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Твої проблеми (24 травня)','uk',4390,5873,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,blagodarnostj2.jpg\" />Один чоловік довго і важко хворів, але за деякий час йому полегшало, і він одужав. Після недуги вийшов у сад&nbsp;–&nbsp;був гарний весняний день. І він відчув такий наплив щастя, що на очі йому ринули сльози і він, піднісши погляд до неба, голосно гукнув:</p>\n','2014-05-21 22:58:37','http://oranta.org/index.php?option=com_content&view=article&id=2150:dila-myloserdja-22-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Діла милосердя (22 травня)','uk',4384,5867,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,images.jpg\" />Придбав чоловік собі новий дім&nbsp;–&nbsp;великий, гарний&nbsp;–&nbsp;і сад з фруктовими деревами біля будинку. А поряд у старенькій хатинці жив заздрісний сусід, який повсякчас намагався зіпсувати йому настрій: то сміття під ворота підкине, то поганий напис на воротах напише, то ще якусь підлість учинить.</p>\n','2014-05-22 22:07:47','http://oranta.org/index.php?option=com_content&view=article&id=2151:prytcha-pro-vnutrishnje-bagatstvo-23-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про внутрішнє багатство (23 травня)','uk',4385,5868,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5864,200,q50EyKHqz8Q.jpg\" />Ви чули історію про принца, який шукав собі візника?</p>\n<p>Три претенденти прийшли до нього, і він повів їх до прірви. Стоячи на краєчку глибокої ущелини, запитав:</p>\n<p>–&nbsp;На якій відстані від прірви могли б ви спинити мою колісницю?</p>\n','2014-05-20 22:50:26','http://oranta.org/index.php?option=com_content&view=article&id=2148:kra-prirvy-21-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Край прірви (21 травня)','uk',4382,5865,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5871,200,ukraine-e1378712033712.jpg\" />Не дивлячись на те, що з усіх боків українцям намагаються довести, що вони ні на що не здатні, що найкращий вихід для них – «валити з цієї країни якомога швидше», що дивитися в Україні немає на що і світові на нас начхати, цифри статистики та факти говорять нам речі абсолютно протилежні, повідомляє&nbsp;<a href=\"http://rama.com.ua/ukrayina-nadzvichayna-30-neymovirnih-faktiv-pro-ukrayinu-yaki-nihto-ne-znav/\" target=\"_blank\">rama.com.ua</a>.\n','2014-05-23 11:34:29','http://oranta.org/index.php?option=com_content&view=article&id=2155:ukrajina-nadzvychana&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Україна надзвичайна','uk',4389,5872,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5873,200,280308_33.jpg\" />Коли від\'їжджав до Італії, мати вишила рушника червоними та чорними нитками. На ньому слова: \"Щирою рукою рушник вишивала та щастя у Бога для тебе благала\". Того рушника зберігаю досі.</p>\n<p>Я виїхав із дому 18-річним, а повернувся в Україну, коли мені виповнилося 79. Об\'їздив півсвіту, але марив поверненням на батьківщину.</p>\n','2014-05-24 12:06:45','http://oranta.org/index.php?option=com_content&view=article&id=2158:2014-05-24-09-11-36&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Людина за життя переходить кілька етапів любові» - Андрій САПЕЛЯК, 94 роки, єпископ','uk',4393,5876,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5881,200,1365402019_ukr-jitnitsa.jpg\" />Один мудрець усе життя був щасливий. Він увесь час усміхався, сміявся, ніхто й ніколи не бачив його сумним.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми дивуємося з вас, учителю. Чому ви ніколи не сумуєте? Як вам це вдається?</p>\n<p>На що старий відповів:</p>\n','2014-05-30 23:16:21','http://oranta.org/index.php?option=com_content&view=article&id=2168:ce-mi-vybir-31-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Це мій вибір (31 травня)','uk',4413,5896,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5881,200,429151-2560x1600.jpg\" />Літній чоловік із дорослим сином зайшли у вагон поїзда і сіли на свої місця. Хлопець сів біля вікна.</p>\n<p>Тільки-но поїзд рушив, хлопець висунув руку у вікно, щоб відчути потік повітря, і раптом захоплено закричав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тату, дивись, усі дерева біжать назад!</p>\n<p>Той тільки усміхнувся на ці слова. Поряд із чоловіком сиділа подружня пара. Вони були трохи спантеличені тим, що молодий чоловік поводить себе, як мала дитина.</p>\n','2014-05-26 22:57:33','http://oranta.org/index.php?option=com_content&view=article&id=2164:vidnovleny-zir-27-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Відновлений зір (27 травня)','uk',4400,5883,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5881,200,SIM_4265.jpg\" />При дорозі стояв жебрак і просив милостиню. Вершник, що їхав повз нього, ударив жебрака батогом по обличчю. А той, дивлячись вершникові услід, сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Будь щасливий.</p>\n<p>Селянин, що бачив усе це, почувши ці слова, запитав:</p>\n','2014-05-27 23:18:41','http://oranta.org/index.php?option=com_content&view=article&id=2165:udar-batoga-28-travnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Удар батога (28 травня)','uk',4404,5887,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5884,200,image034.jpg\" />Ісус Христос на 40-й день після свого воскресіння вознісся на небо, на 50-й зіслав Святого Духа на своїх апостолів. Період від Воскресіння до Зіслання Святого Духа в Церкві називається П’ятдесятницею. Він має свою структуру і включає в себе три великих свята. Про це розповідає о. Василь Рудейко, доктор богослов\'я, заступник голови Патріаршої літургійної комісії УГКЦ, священик храму Священномученика Климентія Шептицького у Львові.\n','2014-05-27 10:41:04','http://oranta.org/index.php?option=com_content&view=article&id=2170:2014-05-27-07-45-40&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«П’ятдесятниця - період очікування дару Святого Духа»','uk',4402,5885,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5887,200,28274000.jpg\" />1. І тоді, коли ми вшановували пам\'ять Хреста, ми звершу­вали свято поза містом. І тепер, коли в цей славний і світлий день вшановуємо Вознесіння Розп\'ятого, ми знову святкуємо поза містом. Ми робимо це не для того, щоб принизити місто, а намагаємося вшанувати мучеників. Щоб ці святі не докоряли нам і не говорили: \"Ми не сподобилися побачити жодного дня Владики нашого, який би святкувався у наших обителях\", - щоб ці святі не стали докоряти нам і говорити: \"Ось, ми проли­ли за Нього свою кров і сподобилися покласти за Нього голови свої, однак не сподобилися побачити день свята Його, який би звершувався у наших оселях\", - тому ми з нагоди цього дня, залишивши місто, прийшли до ніг цих святих, виправдуючись перед ними і за минулі часи.</p>\n','2014-05-28 01:12:58','http://oranta.org/index.php?option=com_content&view=article&id=2173:besida-na-voznesinnja-gospoda-nashogo-isusa-hrysta-svjatogo-otcja-nashogo-ioana-zolotoustogo&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Бесіда на Вознесіння Господа нашого Ісуса Христа святого Отця нашого Іоана Золотоустого','uk',4406,5889,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5892,200,5464.jpg\" />У ці дні, коли Україна стоїть перед вибором свого шляху розвитку, ми пропонуємо вам розмову з&nbsp;<strong>Євгеном Сверстюком – філософом, дисидентом, літературознавцем, письменником, одним із учасників «Ініціативи Першого грудня», головним редактором газети «Наша віра»</strong>.</p>\n<p>У 70-х роках Євген Сверстюк переслідувався тодішнім політичним режимом за свої думки й ідеї. І, незважаючи на всі випробування, не зрікся своїх переконань. До сьогодні він продовжує – своїм життям, творчістю і діяльністю – проголошувати ідею «десовєтизації» України, духовного розвитку людини та суверенності держави. Ці, здавалося б, дещо різнопланові ідеї, насправді мають у собі щось спільне. Про те, як бачить Євген Сверстюк сьогоднішню ситуацію в Україні, куди нам іти далі та як розвивати себе і духовне суспільство, а також про відстоювання власного національного обличчя – в інтерв’ю.\n','2014-05-30 13:47:28','http://oranta.org/index.php?option=com_content&view=article&id=2177:2014-05-30-10-52-41&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Громадянське суспільство має доповнювати суспільство духовне»','uk',4411,5894,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5897,200,DSC06793.jpg\" />Якось імператор покликав одного зі своїх васалів. В імперії той прославився жорстокістю і жадністю, і піддані його жили у страху.</p>\n<p>Імператор сказав до васала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, щоб ти вирушив у подорож по світу і знайшов для мене насправді добру людину.</p>\n<p>Той відповів:</p>\n','2014-05-31 23:50:52','http://oranta.org/index.php?option=com_content&view=article&id=2180:jak-u-dzerkali-1-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','Як у дзеркалі (1 червня)','uk',4415,5898,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5897,200,DSC06793.jpg\" />Якось імператор покликав одного зі своїх васалів. В імперії той прославився жорстокістю і жадністю, і піддані його жили у страху.</p>\n<p>Імператор сказав до васала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, щоб ти вирушив у подорож по світу і знайшов для мене насправді добру людину.</p>\n<p>Той відповів:</p>\n','2014-05-31 23:50:52','http://oranta.org/index.php?option=com_content&view=article&id=2181:jak-u-dzerkali-1-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як у дзеркалі (1 червня)','uk',4416,5899,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5898,200,3.jpg\" />Учень одного разу запитав у старця:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як мені розрізняти людей: кому довіряти, а кого остерігатися?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Спершу скажу тобі, кого треба остерігатися, мовив старець. –Остерігайся найсмиреннішого на вигляд! Коли побачиш, що хтось перед тобою б’є поклони, обіймає тебе і виявляє неймовірну прихильність, того бійся найбільше!</p>\n','2014-06-01 23:26:51','http://oranta.org/index.php?option=com_content&view=article&id=2183:komus-dovirjaty-a-kogos-osterigatysja-2-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Комусь довіряти, а когось остерігатися (2 червня)','uk',4418,5901,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5902,200,love.jpg\" />Жінка вийшла зі своєї оселі і побачила на вуличному дворику трьох дідів з довгими білими бородами. Вона сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я не знаю вас, але ви, мабуть, голодні. Будь ласка, заходьте в дім і пообідайте.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чоловік удома? – спитали старі.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, - відповіла вона, - його немає.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тоді ми не можемо зайти, - відповіли вони.</p>\n','2014-06-03 10:06:38','http://oranta.org/index.php?option=com_content&view=article&id=2185:ljubov-bagatstvo-i-talan-3-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любов, багатство і талан (3 червня)','uk',4420,5903,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5902,200,x_2d3e28a3.jpg\" />Один юнак ніяк не міг обрати собі дружину: то личко видавалося йому не надто симпатичним, то фігура не надто привабливою. Та одного разу йому пощастило: зустрів він чарівника, який пообіцяв розв’язати його проблему.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ось тобі сірник, - сказав чарівник, простягаючи його юнакові. – Тільки-но будеш готовий, зламай його перед будь-якою дівчиною, і вона поступово ставатиме такою, якою ти&nbsp; хочеш.</p>\n','2014-06-04 00:10:46','http://oranta.org/index.php?option=com_content&view=article&id=2186:druzhyna-4-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дружина (4 червня)','uk',4424,5907,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5904,200,popovich_miroslav_guzar_lyubo_003.jpg\" />У цю&nbsp;неділю у&nbsp;Києві громадськість зібралась на&nbsp;віче, щоб продемонструвати свій намір&nbsp;— вимагати від влади звіту. Перед громадою виступили учасник “Ініціативи першого грудня” Мирослав Попович та&nbsp;активісти Громадського сектора Євромайдану й&nbsp;озвучили ключові теми, на&nbsp;які міністри повинні звернути особливу увагу під час майбутнього звітування. Громадяни очікують отримати відповіді на&nbsp;ці&nbsp;запитання від міністрів під час Всеукраїнського віча 8&nbsp;червня. Кожен громадянин мав змогу залишити своє запитання до&nbsp;уряду. Для цього було встановлено спеціальні скриньки біля сцени Майдану.</p>\n','2014-06-03 11:08:34','http://oranta.org/index.php?option=com_content&view=article&id=2188:jevromadan-vymagaje-aby-urjadovci-8-chervnja-prozvituvaly-za-100-dniv-roboty&catid=37:spilnota-cat&Itemid=49','P',NULL,'Євромайдан вимагає, аби урядовці 8 червня прозвітували за 100 днів роботи','uk',4423,5906,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,463.jpg\" />Питання: коли птах буває більшим за гору? Відповідь: коли птах ближче, ніж гора.</p>\n<p>Звісно, жоден, навіть найбільший птах не може бути більшим навіть від найменшої гори. Але така оптична омана виникає, коли пташки, наприклад, сідають на вікно і заступають гору на видноколі.</p>\n<p>Подеколи подібна спотворена перспектива виникає, коли ми дивимося на свої проблеми. Вони видаються нам великими і грізними, вкритими чорним зловісним пір’ям, палючими очима і гострими дзьобами, готовими безжалісно роздерти нещасну комашку, що вилізла зі щілини.</p>\n','2014-06-05 05:38:55','http://oranta.org/index.php?option=com_content&view=article&id=2190:perspektyva-5-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Перспектива (5 червня)','uk',4425,5908,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,491059.jpg\" />Потрапив один мудрець на небо.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як прожив ти своє життя? – спитав його ангел.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Шукав істину, - відповів мудрець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Це добре! – похвалив мудреця ангел. – Розкажи, що робив ти, шукаючи істину.</p>\n','2014-06-06 05:42:52','http://oranta.org/index.php?option=com_content&view=article&id=2191:shukannja-istyny-6-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Шукання істини (6 червня)','uk',4426,5909,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,7607.jpg\" />Жила собі звичайна селянська сімʼя. Чоловік і жінка. І жили вони з того, що вирощували яблука у своєму саду, а восени продавали їх.</p>\n<p>Та сталося одного року, що селянин, занедужавши, не зібрав урожаю. Чимало яблук зігнило. Що ж робити? Якщо не продати врожаю, то сімʼя не виживе.</p>\n<p>Християнин зібрав усі гнилі яблука і поїхав на ринок продавати те, що є. благословила його жінка і сказала, що все буде добре. Так селянин і поїхав.</p>\n','2014-06-09 05:56:24','http://oranta.org/index.php?option=com_content&view=article&id=2194:prytcha-pro-ljubov-shcho-goduje-simju-9-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про любов, що годує сім’ю (9 червня)','uk',4429,5912,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,images.jpg\" />Старий кіт побачив мале кошеня, що гналося за своїм хвостом, і спитав його:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Навіщо ти це робиш?</p>\n<p>Кошеня відповіло:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я дізнався, що найголовніше для котів – це щастя, а для мене щастя – мій хвіст. Тому я й женуся за ним. Коли я врешті-решт його спіймаю, то стану щасливим!</p>\n','2014-06-08 05:53:04','http://oranta.org/index.php?option=com_content&view=article&id=2193:prytcha-pro-shchastja-8-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про щастя (8 червня)','uk',4428,5911,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5905,200,nazyvayut-detey-zolushkami-i-angelami.jpg\" />Прийшла якось до старця сімейна пара.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, - каже жінка, - я чекаю на дитину, а в нас вже й так четверо дітей; коли п’яте народиться – не проживемо. Благословіть зробити аборт.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачу, живеться вам нелегко, - відповідає старець, - що ж, благословляю вас убити свою дитину. Тільки вбивайте найстаршу дочку, їй уже п\'ятнадцять літ: усе ж пожила вже, світу білого побачила, а те і промінчика сонячного не бачило.</p>\n','2014-06-07 05:50:13','http://oranta.org/index.php?option=com_content&view=article&id=2192:prytcha-pro-abort-7-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про аборт (7 червня)','uk',4427,5910,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,6-600x359.jpg\" />Одного разу, коли вже почало смеркатися, селянин сидів на порозі своєї бідної оселі, втішаючись прохолодою. Поряд пролягла дорога, що вела у село. Нею ішов чоловік. Подорожній, побачивши старого, подумав: «Цей чоловік лінивий, він не працює і весь день сидить на порозі свого дому». Трохи згодом з’явився інший мандрівник. Він подумав: «Цей чоловік – дон Жуан. Сидить тут, щоби підглядати за дівчатами, і може навіть допікає їм». Врешті-решт один лісник, що йшов у село, подумав: «Цей чоловік – великий трудівник. Він напружено працював весь день і заслужив на відпочинок.</p>\n','2014-06-11 03:11:01','http://oranta.org/index.php?option=com_content&view=article&id=2203:seljanyn-11-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Селянин (11 червня)','uk',4438,5921,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5919,200,vvukr1-572x462.jpg\" />Одного разу учитель запитав своїх учнів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому люди кричать, коли сваряться?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тому що втрачають спокій, - сказав один.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Але навіщо кричати, якщо друга людина поряд з тобою? – спитав учитель. – Хіба не можна з нею розмовляти тихо? Навіщо кричати, коли ти сердишся?</p>\n','2014-06-11 23:29:47','http://oranta.org/index.php?option=com_content&view=article&id=2205:prytcha-pro-svarky-12-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про сварки (12 червня)','uk',4440,5923,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5929,200,GRIHOPADINNYA-300x197.jpg\" />Учитель вів свого учня через джунглі. Хоча учитель був зовсім старий, проте йшов швидко, а його молодий учень час від часу падав. Молодик після кожного падіння піднімався і, лаючи зрадницьку землю, плював на те місце та йшов далі.</p>\n<p>Після довгої дороги вони дійшли до священного місця. Не спиняючись, учитель повернув назад.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сьогодні ви не навчили мене нічого, - оголосив учень після чергового падіння.</p>\n','2014-06-12 22:47:44','http://oranta.org/index.php?option=com_content&view=article&id=2209:prychyny-padin-13-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Причини падінь (13 червня)','uk',4447,5930,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5931,200,img10-03.jpg\" />Священик переписав на прекрасному пергаменті, який коштував вісімнадцять золотих монет, увесь Старий і Новий Заповіт. Одного разу один брат прийшов до нього і, побачивши книгу, тайкома забрав її. Того самого дня священик заходів прочитати книгу. Побачивши, що вона зникла, він зрозумів, що її взяв брат, але не послав спитати його, остерігаючись, аби брат не додав до гріха крадіжки ще й брехню.</p>\n<p>Тим часом брат вирушив у сусіднє місто, щоб продати книгу, і попросив за неї шістнадцять золотих монет. Покупець сказав йому:</p>\n','2014-06-13 22:33:01','http://oranta.org/index.php?option=com_content&view=article&id=2211:knyga-svjashchenyka-14-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Книга священика (14 червня)','uk',4449,5932,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5934,200,345.jpg\" />Одна жінка страждала від жахливого болю, і жодне лікування не приносило полегші. Чоловік посадив її на коня і повіз у монастир, що прославився цілющою водою.</p>\n<p>Того дня йшов сильний дощ і розмив дорогу, отож подорожні прибули до монастиря, коли споночіло. Заспаний і сердитий сторож не відчинив їм браму, але мандрівці вмовили його дати їм свяченої води. Роздратований такою настирливістю, сторож наповнив глечика з найближчої калюжі.</p>\n','2014-06-15 22:04:54','http://oranta.org/index.php?option=com_content&view=article&id=2215:zcilennja-16-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Зцілення (16 червня)','uk',4453,5936,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5942,200,11101602.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мамо, чому отой дядько кричить?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Він розстроєний.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому він розстроєний?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хтось образив його.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому той хтось його образив?</p>\n','2014-06-17 21:53:00','http://oranta.org/index.php?option=com_content&view=article&id=2219:chomu-krychyt-djadko-18-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Чому кричить дядько (18 червня)','uk',4460,5943,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5947,200,images.jpg\" />Прочитав чоловік одну мудру книгу й заради Істини вирішив покинути і свою похилу хатину, і бідне містечко, і мізерну платню. Хоча дуже шкода було все покидати.</p>\n<p>Й от опинився він у велетенському прекрасному домі, навколо якого, куди оком не кинь, стелилися пречудові сади. Там жили такі самі, як він, люди, що покинули усе в цьому світі. Порівняв він колишнє своє життя з новим, і вельми сподобалося йому жити добре.</p>\n','2014-06-19 21:50:00','http://oranta.org/index.php?option=com_content&view=article&id=2225:use-lyho--vid-gordosty-20-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Усе лихо – від гордости (20 червня)','uk',4466,5949,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5951,200,DSCN6894.jpg\" />Залетіло липове зернятко на самісінький край крутого провалля.</p>\n<p>Умовляла його матір. Просили брати і сестри:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не коренися тут! Дочекайся першого ж пориву вітру і лети якомога далі від цього небезпечного місця!</p>\n<p>Але де там! Зернятко навіть слухати нікого не хотіло.</p>\n','2014-06-20 23:18:45','http://oranta.org/index.php?option=com_content&view=article&id=2227:nebezpechne-misce-21-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Небезпечне місце (21 червня)','uk',4469,5952,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5953,200,13060501e-sonce.jpg\" />Хлопчик запитав у сонечка:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Сонечко, ти бачило коли-небудь темряву?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, - відповіло сонечко, - ніколи не бачило.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочеш, я тобі покажу темряву? – сказав хлопчик. – Я знаю один підвал, де завжди дуже темно.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хочу, - відповіло сонечко, - це цікаво.</p>\n','2014-06-22 07:19:23','http://oranta.org/index.php?option=com_content&view=article&id=2229:sonechko-i-temrjava-22-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Сонечко і темрява (22 червня)','uk',4471,5954,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5955,200,39675817405804.jpg\" />Багато років тому диявол вирішив продати всі знаряддя свого ремесла. Він дбайливо повиставляв їх у скляній вітрині на загальний огляд. Що ж то була за колекція! Тут був блискучий кинджал заздрости, а поряд з ним красувався молот гніву. На іншій полиці лежав лук жадібности і пожадання, а поряд з ним – стріли хтивости і ревности. Також там були знаряддя страху, гордині і ненависти. Усі вони були чудово представлені і споряджені етикетками з назвами й ціною. А на найкращій полиці, окремо від усіх інших інструментів, лежав маленький, непоказний і досить поношений маленький клин, на якому висів ярлик: «Зневіра». Як не дивно, ціна цього знаряддя була вищою від усіх решта разом. Один перехожий запитав диявола, чому він так цінує цей дивний клинець, і той відповів:</p>\n','2014-06-22 18:32:32','http://oranta.org/index.php?option=com_content&view=article&id=2231:instrumenty-dyjavola-23-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Інструменти диявола (23 червня)','uk',4473,5956,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5959,200,63_2_b.jpg\" />На зборах у пеклі диявол розпитував підлеглих демонів, як прискорити погибель людини.</p>\n<p>Один із його слуг сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я, бувши досвідченим, говорив людям, що не існує ніякого Бога, ніякої вічности, не існує жодної різниці між гріхом і справедливістю, і що вони можуть жити, як захочуть.</p>\n<p>Злі духи це підтвердили і схвалили. Але сатана мовив:</p>\n','2014-06-23 20:33:36','http://oranta.org/index.php?option=com_content&view=article&id=2235:nakrashcha-sit-dyjavola-24-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Найкраща сіть диявола (24 червня)','uk',4477,5960,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5965,200,10449906.jpg\" />Одного разу хтось запитав у побожної бабусі:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи нагадує вам диявол про гріхи?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Певна річ, - відповіла вона, - але я посилаю його на схід, а коли він повертається, - то на захід. І так він ходить зі сходу на захід і ніяк не може знайти моїх гріхів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому саме на схід і на захід посилаєте ви свого супротивники? – запитали в бабусі.</p>\n','2014-06-26 22:13:52','http://oranta.org/index.php?option=com_content&view=article&id=2241:kudy-poslaty-dyjavola-27-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Куди послати диявола (27 червня)','uk',4484,5967,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,sueta_1.jpg\" />Сатана скликав всесвітній з’їзд бісів. У своєму вступному слові він сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми не можемо заборонити християнам ходити до церкви. Ми не можемо заборонити їм читати Біблію. Ми не можемо заборонити їм єднатися в молитві з Богом. А щойно вони налагоджують зв\'язок з Христом, ми вмить позбуваємося влади над ними. Тож хай вони ходять у свої церкви, однак ми вкрадемо їхній час, щоб вони, повсякчас заклопотані, не могли молитися і каятися з гріхів і розвивати свої стосунки з Христом.</p>\n','2014-06-27 22:32:54','http://oranta.org/index.php?option=com_content&view=article&id=2245:vsesvitni-zjizd-bisiv-28-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Всесвітній з’їзд бісів (28 червня)','uk',4488,5971,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5967,200,sueta_1.jpg\" />Сатана скликав всесвітній з’їзд бісів. У своєму вступному слові він сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ми не можемо заборонити християнам ходити до церкви. Ми не можемо заборонити їм читати Біблію. Ми не можемо заборонити їм єднатися в молитві з Богом. А щойно вони налагоджують зв\'язок з Христом, ми вмить позбуваємося влади над ними. Тож хай вони ходять у свої церкви, однак ми вкрадемо їхній час, щоб вони, повсякчас заклопотані, не могли молитися і каятися з гріхів і розвивати свої стосунки з Христом.</p>\n','2014-06-27 22:32:54','http://oranta.org/index.php?option=com_content&view=article&id=2246:vsesvitni-zjizd-bisiv-28-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','Всесвітній з’їзд бісів (28 червня)','uk',4489,5972,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,92d9758f2db7.jpg\" />Один брат, що жив у монастирі і часто знемагав від гніву, мовив сам до себе: «Піду в пустелю, може там, не маючи з ким сваритися, погамую пристрасть». І вийшов він з монастиря, і став жити самітником.</p>\n<p>Одного разу наповнив брат водою глечика і поставив його на землю. Зненацька посудина перекинулася. Те саме сталося вдруге. І третього разу також. Чернець розсердився, схопив глечика і вдарив його об землю</p>\n','2014-06-30 01:20:35','http://oranta.org/index.php?option=com_content&view=article&id=2250:borotba-z-gnivom-30-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Боротьба з гнівом (30 червня)','uk',4493,5976,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5971,200,baby-angel-cute-wings.jpg\" />Старець і його учень-послушник зустріли чоловіка, який, вважаючи послушника своїм кривдником, почав лаяти його. Учень довго, мовчки і терпляче зносив усе, але потім не втримався і почав відповідати лайкою на лайку. Тоді старець пішов далі, залишивши цих двох закінчити сварку. Коли послушник знову прийшов до старця, він з образою запитав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чому ти залишив мене самого зносити лайку цього нахаби?</p>\n','2014-06-29 01:10:38','http://oranta.org/index.php?option=com_content&view=article&id=2248:vidpovid-na-laku-29-chervnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Відповідь на лайку (29 червня)','uk',4492,5975,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5977,200,1388408850_ne-boysya11.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи є страх для вас проблемою? Чи краде він у вас радість життя? Чи заважає жити повноцінно? Якщо так, не журіться. Ліки від страху є.</p>\n<p>Одна з численних переваг християнина – це свобода від страху. Якщо ви боїтеся змін і труднощів, згадайте, що пообіцяв Бог. Якщо ви будете коритися Йому то Він ітиме перед вами і допоможе вам здобути перемогу.</p>\n','2014-07-01 03:25:10','http://oranta.org/index.php?option=com_content&view=article&id=2252:liky-vid-strahu-1-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ліки від страху (1 липня)','uk',4495,5978,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5981,200,134918.jpg\" />Один чоловік обходив села і заходив у всі церкви та пастви. Та якось він зайшов в одну церкву і помітив, що проповідь читає біс. Він не заважав йому а слухав, про що той говорить. На його подив, біс не змінював слова Божого, не прибріхував, усі його проповіді були правильними і справедливими. Чоловік дочекався до кінця служби і підійшов до «проповідника».<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Я тебе впізнав, ти біс. І я слухав твою проповідь і не знайшов у ній ані брехні, ані блюзнірства. У чому твій підступ?<o:p></o:p></p>\n','2014-07-03 02:21:13','http://oranta.org/index.php?option=com_content&view=article&id=2257:propovid-bisa-3-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Проповідь біса (3 липня)','uk',4500,5983,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5981,200,blizhc.jpeg\" />Жив один пустельник-чернець і от біс, після багатолітньої боротьби з ним пообіцяв, що не буде більше його гнітити жодними спокусами, якщо він учинить один із трьох гріхів: вбивство, блуд або пияцтво.<o:p></o:p></p>\n<p>Пустельник почав міркувати: людину убити – найбільше зло і воно заслуговує смертної кари як на Божому суді, так і на цивільному. Вчинити блуд і згубити чистоту тіла, яку я беріг і досі – шкода. Напитися ж один раз, як видається, невеликий гріх, бо ж після того людина швидко витвережується знов. Піду я вип’ю вина, що біс більше не гнітив мене і буду далі жити мирно в пустелі.<o:p></o:p></p>\n','2014-07-03 22:41:30','http://oranta.org/index.php?option=com_content&view=article&id=2259:lyshe-odyn-grih-4-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Лише один гріх (4 липня)','uk',4501,5984,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5988,200,450_137977368287.jpg\" />Потрапив чоловік до Раю, дивиться, а там усі ходять радісні, щасливі, щирі доброзичливі, а довкола все як у звичайному житті. Походив він, погуляв, сподобалось йому. І каже він Архангелу:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А можна поглянути яке пекло? Хоч одним оком.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Гаразд. Ходімо.</p>\n','2014-07-05 23:40:11','http://oranta.org/index.php?option=com_content&view=article&id=2263:u-raju-lipshe-6-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'У Раю ліпше (6 липня)','uk',4507,5990,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5990,200,skripka_i_reptiliya_1920x1200.jpg\" />Багато літ жили разом смик і скрипка і всі милувалися ними.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чудова пара, - гукнув тромбон.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - а живуть же як, - додав рояль, - у згоді, приязні.</p>\n<p>Все було добре, аж враз… старий смик вирішив, що скрипка йому не пара. Надто у неї вузька талія, подумав він і пішов собі шукати подругу.</p>\n','2014-07-06 23:36:28','http://oranta.org/index.php?option=com_content&view=article&id=2265:smyk-i-skrypka-7-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Смик і скрипка (7 липня)','uk',4509,5992,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5993,200,4061913_10472147.jpg\" />Один християнин жив простим життя в хатинці під горою. Якось в його хатину заліз злодій і оторопів від того, що там немає чого красти. Незабаром християнин повернувся до дому і застав злодія.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти так довго йшов, щоб провідати мене? – промовив він до волоцюги, - а тому не маєш повернутися порожнім, будь-ласка, візьми в дарунок мою одежу.</p>\n','2014-07-07 22:41:23','http://oranta.org/index.php?option=com_content&view=article&id=2267:ljubov-do-ljude-8-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любов до людей (8 липня)','uk',4511,5994,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5995,200,MJi1w6CJdzg.jpg\" />Якось купець вирушив в іншу країну і прийшов у дивне місто, де всі були в кайданах. Купець подумав: - «А якщо і мені одягнути кайдани, то що я буду робити?» зненацька, у ту саму мить, з’явилася варта і побачивши вільного чоловіка, закувала його в кайдани.</p>\n<p>Втративши змогу повернутися до дому, купець занепав духом. Він взявся розпитувати нещасних городян, чому вони всі в кайданах. А ті відповіли, що в їхньому місті з давна так заведено. Купець запитав:</p>\n','2014-07-08 23:23:17','http://oranta.org/index.php?option=com_content&view=article&id=2269:kadany-9-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кайдани (9 липня)','uk',4514,5997,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,5999,200,54654.jpg\" />Жили собі два сусіди. Настала зима, посипав сніг. Перший сусід рано-вранці вийшов розгортати сніг на подвір’ї. поки розчищав стежку подивився як там справи в сусіда. А в другого сусіда гарно утоптана стежка.</p>\n<p>Наступного ранку знов випав сніг. Перший сусід устав на пів години раніше і взявся за роботу. Бачить а в другого сусіда вже стежка прокладена.</p>\n','2014-07-10 22:15:40','http://oranta.org/index.php?option=com_content&view=article&id=2274:utoptana-stezhka-11-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Утоптана стежка (11 липня)','uk',4518,6001,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,DETA.jpg\" />Коли одного разу місіонер говорив про вічне життя один язичник глузливо заперечив йому. Що того, так званого вічного життя ніде не видно. Позаяк, коли приходить смерть жодної різниці між людьми немає. Місіонер відповів:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Слухай, мій друже. Ти бачив дім губернатора?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачив.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І що розташоване на найвищих поверхах?</p>\n','2014-07-16 00:11:41','http://oranta.org/index.php?option=com_content&view=article&id=2281:dim-gubernatora-16-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дім губернатора (16 липня)','uk',4528,6011,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,aser.jpg\" />Двоє молодиків освідчувалися двом дівчатам у оханні. Один сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я можу запропонувати тільки своє серце, куди може прийти одна з тих, хто згодиться розділити мій важкий шлях.</p>\n<p>Інший мовив так:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я можу запропонувати величезний палац, де розділю зі своєю супутницею радість життя.</p>\n','2014-07-11 23:58:20','http://oranta.org/index.php?option=com_content&view=article&id=2277:zolota-klitka-12-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Золота клітка (12 липня)','uk',4523,6006,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,big-34.jpg\" />Одного разу чоловік прийшов до учителя і запитав як пізнати істину і досягти досконалості.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Іди в село і там на перехресті двох доріг, ти знайдеш те, що шукаєш, - сказав він.</p>\n<p>Сповнений сподівань і передсмаку, чолові побіг у село і знайшов на перехресті звичайнісіньки крамнички. Перші торгували шматочками металу, в другій якимось деревинками, а в третій тонким дротом. До істини це немало жодного стосунку. І розчарований шукач повернувся до вчителя і сказав, що не знайшов нічого.</p>\n','2014-07-14 00:04:05','http://oranta.org/index.php?option=com_content&view=article&id=2279:arfa-14-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Арфа (14 липня)','uk',4526,6009,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,dsc_2890.jpg\" />Кілька випускників престижного навчального закладу, що досягли успіху і зробили виняткову кар’єру прийшли в гості до свого старого професора. Під час візити розмова зайшла про роботу. Випускники скаржилися на численні труднощі і життєві проблеми. Запропонувавши гостям кави професор пішов на кухню і повернувся з кавником і тацею на якій стояли усілякі горняти: порцелянові, скляні, пластикові, кришталеві. Одні були прості а інші дорогі.</p>\n<p>Коли випускники порозбирали горнята, професор сказав:</p>\n','2014-07-15 00:07:45','http://oranta.org/index.php?option=com_content&view=article&id=2280:gornjatko-kavy-15-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Горнятко кави (15 липня)','uk',4527,6010,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6005,200,e03_186365451.jpg\" />Жив якось чоловік самотній і нещасний. І почав благати він:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Господи, пошли мені прекрасну жінку. Я дуже самотній і мені потрібен друг.</p>\n<p>Бог засміявся:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому не хрест?</p>\n<p>Чоловік обурився</p>\n','2014-07-13 00:01:46','http://oranta.org/index.php?option=com_content&view=article&id=2278:hrest-13-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хрест (13 липня)','uk',4525,6008,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6011,200,46646.jpg\" />Ішов сніг, погода була тиха і великі неквапом кружляли в чудернацькому танці, повільно наближаючись до землі. Дві сніжинки, щ летіли поряд вирішили побесідувати. Боячись втратити одна одну, вони взялися за руки і одна з них весело сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як добре летіти втішаючись польотом.</p>\n','2014-07-16 23:09:05','http://oranta.org/index.php?option=com_content&view=article&id=2284:dvi-snizhynky-17-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дві сніжинки (17 липня)','uk',4530,6013,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6014,200,1a11.jpg\" />Уявіть собі, що життя це гра, яка полягає в жонглюванні п’ятьма кульками. Ті кульки: Робота; Сім’я; Здоров’я, Друзі і Душа. І вам потрібно щоб усі вони повсякчас перебували в повітрі. Невдовзі ви зрозумієте, що робота зроблена з гуми – якщо ви її ненароком впустите, то вона підстрибне і повернеться до Вас.</p>\n<p>Але інші 4 кульки: Сім’я; Здоров’я, Друзі і Душа скляні, і якщо ви впустите якусь із них то вона буде надколена, подряпана, серйозно пошкоджена, чи навіть ущент розбита. Вона ніколи н буде такою як раніше. Ви мусите усвідомлювати це і старатися, щоб цього не сталося.\n','2014-07-17 23:17:38','http://oranta.org/index.php?option=com_content&view=article&id=2286:pjat-kulok-18-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'П’ять кульок (18 липня)','uk',4535,6018,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6022,200,meloch.jpg\" />Ішов дорогою парубок, а зверху копійка лежить.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що ж, - подумав він, - і копійка – гроші.</p>\n<p>Підняв її і поклав у гаманець. І став далі думати:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що би я зробив, коли знайшов би тисячу карбованців? Купив би подарунки батькові і мамі.</p>\n','2014-07-19 23:03:58','http://oranta.org/index.php?option=com_content&view=article&id=2290:odna-kopika-20-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Одна копійка (20 липня)','uk',4540,6023,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6026,200,0_f5896_d488445d_orig.jpg\" />Місто Брюссель відоме вишуканим мереживом. У знаменитих мереживних салонах є особливі кімнати, призначені для плетення найтонших і найелегантніших моделей. Світло ледь пробивається через невеличке віконце і падає тільки на мереживо. Найвишуканіші і найпрекрасніші мережки виходять тільки тоді, коли сам ткач перебуває у темряві, а ткання – на світлі.\n','2014-07-22 10:28:04','http://oranta.org/index.php?option=com_content&view=article&id=2295:brjusselske-merezhyvo-22-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Брюссельське мереживо (22 липня)','uk',4544,6027,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6029,200,ukraine_brand_03.png\" />Одного разу король викотив на дорогу величезний камінь, а сам сховався, щоб побачити, хто прибере його. Багато хто проїжджав, чи проходив стороною, голосно лаючи короля, що він не стежить за дорогами, але нікому не спало на думку прибрати з дороги камінь. Врешті-решт біля каменя зупинився бідний селянин і відкотив його у придорожний рів. А під каменем, на свій подив, селянин знайшов повний мішок золота і лист від короля, у якому той пояснив, що це нагорода тому, хто прибере з дороги великий камінь.\n','2014-07-23 14:11:20','http://oranta.org/index.php?option=com_content&view=article&id=2299:zoloto-pid-kamenem-23-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Золото під каменем (23 липня)','uk',4548,6031,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6031,200,thumb.jpg\" />Одному чоловікові видалося, що живе він дуже важко. І прийшов він якось до Бога і розповів про свої лиха й попросив у нього:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Можна, я виберу собі іншого хреста?</p>\n<p>Подивився Бог на людину з усмішкою, завів його до схову, де лежать хрести, та й каже:</p>\n','2014-07-23 22:59:01','http://oranta.org/index.php?option=com_content&view=article&id=2301:tvi-hrest-24-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Твій хрест (24 липня)','uk',4550,6033,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6039,200,huzar.jpg\" />Сьогодні найпопулярнішими в ЗМІ є три теми.</p>\n<p>Перша тема – усе, що в будь-який спосіб пов’язане з воєнними подіями у східних областях України. Це самозрозуміло, тому немає потреби цього пояснювати.</p>\n<p>Друга тема – нагальна необхідність піднести на відповідний рівень діяльність законодавчої гілки влади. За останній рік негідна поведінка представників цієї гілки влади призвела до стількох негативних наслідків у житті українського народу, що намагання змінити її склад – і якнайшвидше – стало цілком природним і необхідним.</p>\n','2014-07-25 16:26:34','http://oranta.org/index.php?option=com_content&view=article&id=2308:majemo-sumlinno-i-vidpovidalno-ne-tilky-krytykuvaty-vladu-ale--jiji-obyraty-i-kontroljuvaty&catid=37:spilnota-cat&Itemid=49','P',NULL,'Маємо сумлінно і відповідально не тільки критикувати владу, але й її обирати і контролювати','uk',4557,6040,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6040,200,Bog-ye-lyubov.jpg\" />Жили собі в одному краї чоловік і жінка, їхня любов міцнішала щоднини. Вони вельми бідували, але кожен з них знав, що інший викохує в серці нездійснене бажання: у чоловіка був золотий годинник і він мріяв купити ланцюжка з такого самого дорогоцінного металу. Жінка мала довге м’яке русяве волосся і мріяла про гребінчика з перлиною, щоб носити його у волоссі як діадему.</p>\n','2014-07-25 23:17:08','http://oranta.org/index.php?option=com_content&view=article&id=2309:lancjuzhok-i-grebinchyk-26-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ланцюжок і гребінчик (26 липня)','uk',4561,6044,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,FWEdDgJh9ks.jpg\" />Страшенна буря розгулялася на морі. Неподалік від берега, борючись із хвилями маленьке судно подавало сигнал біди. За мить відчалив рятівний човен на допомогу судну, що мало от-от загинути. Але спроби рятівників перемогти хвилі і доплисти до корабля були марні.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що хлопці, чи не повернутися нам назад? – запропонував боцман, - Який сенс нам йти на неминучу смерть? Видно, не перемогти нам нині моря. І судно, і люди вже, напевне, погинули і їх не врятуєш.</p>\n','2014-07-31 23:58:51','http://oranta.org/index.php?option=com_content&view=article&id=2323:porjatunok-vid-buri-1-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Порятунок від бурі (1 серпня)','uk',4581,6064,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,imag.jpg\" />Один мудрець шукав здібного і талановитого учня, щоб передати йому свої знання. І вирішив він зібрати всіх своїх численних учнів.</p>\n<p>Мудрець сказав їм:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У мене виникла проблема і я хочу дізнатися, хто з вам може розв’язати її. Бачите – позаду мене найбільші, найтяжчі і наймасивніші двері в усьому місті. Хто з вас зможе відчинити їх без сторонньої допомоги.</p>\n','2014-07-28 23:10:48','http://oranta.org/index.php?option=com_content&view=article&id=2318:dveri-29-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Двері (29 липня)','uk',4571,6054,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,patriarh126.jpg\" />Покупцеві упав в око дім виставлений на продаж. Він спитав про ціну.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Один карбованець, - відповів продавець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А чому так дешево? – здивувався покупець.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Бачите, річ у тім, – сказав продавець, - що покійна господиня наказала продати дім за мінімальну ціну за умови, що покупець пожертвує на дитячий будинок 10 мільйонів карбованців, а по-друге не буде розголошувати умови угоди. Такий заповіт.</p>\n','2014-07-29 23:16:15','http://oranta.org/index.php?option=com_content&view=article&id=2319:dim-za-karbovancja-30-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дім за карбованця (30 липня)','uk',4576,6059,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,psihologi.gif\" />Михайло їхав безлюдною дорогою, раптом він побачив на дорозі заглухлий мерседес. У ньому сиділа літня жінка, що виглядала цілковито розгубленою. Він зупинився перед її машиною, вийшов зі свого старого «понтіяка» і попрямував до сповненої відчаю жінки. Дама була злякана, бо за тривалий час, поки вона сиділа в машині, так ніхто біля неї і не спинився. А цей молодик, чи скривдить він її?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Мене звати Михайло, - відрекомендувався той, - я допоможу вам. Пересядьте поки що в моє авто – там вам буде трохи тепліше.</p>\n','2014-07-30 23:19:04','http://oranta.org/index.php?option=com_content&view=article&id=2320:lancjuzhok-ljubovy-31-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ланцюжок любови (31 липня)','uk',4579,6062,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6045,200,x_d1e59096.jpg\" />Кілька тижнів тому я готував собі каву. Взяв ранішню газету і сів слухати радіо. Я повертав ручку шукаючи хвилю, поки мою увагу не привернув оксамитовий голос одного дідуся. Він щось казав про «тисячі кульок». Я зацікавився, зробив гучніше і відкинувся на спинку крісла ...<o:p></o:p></p>\n<p>- Добре, - сказав старий, - можу посперечатися, що ви дуже зайняті на роботі. Вчора, сьогодні, завтра. І нехай вам платять багато.</p>\n','2014-07-27 23:05:38','http://oranta.org/index.php?option=com_content&view=article&id=2317:tysjacha-kulok-28-lypnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Тисяча кульок (28 липня)','uk',4569,6052,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,3767535_c52dd690.jpg\" />Одного разу в школі о вчительки, що заходила у клас, підбігла заплакана дівчинка і сказала:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У мене вкрали розкладний ножичок.</p>\n<p>Учителька спокійно відповіла:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Люба дівчинко, не слід спішити звинувачувати будь-кого в тому, що якусь річ украдено. На розраду я тобі розповім коротку історію.</p>\n','2014-08-01 22:15:07','http://oranta.org/index.php?option=com_content&view=article&id=2342:rozkladny-nozhychok-2-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Розкладний ножичок (2 серпня)','uk',4583,6066,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,4546.jpg\" />Жив один чоловік і жив не вельми добре, не тямуще. Вирішив взятися за розум, добрі діла чинити, душу спасати. Чинив їх чинив, але змін на краще у самому собі і не помічав. Якось ішов він по вулиці аж бачить у бабусі ґудзик від пальто відірвався і впав на землю. Побачив, та й думає: «ну то й що, ґудзиків у неї досить. Не підіймати ж, яка дрібниця!»</p>\n<p>Але все таки буркочучи підніс ґудзика і віддав його бабусі та й забув про це.</p>\n','2014-08-03 23:23:28','http://oranta.org/index.php?option=com_content&view=article&id=2344:gudzyk-4-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ґудзик (4 серпня)','uk',4587,6070,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6052,200,molit.jpg\" />Один багатий селянин, чиї комори тріщали від зерна, часто промовляв звичну оливу «про бідних та нужденних». Молячись він завжди долучав таке прохання: - «Боже, згадай про бідних та голодних і заспокой їхню потребу». Однак сам селянин не допомагав нікому і тільки й чекав, що би це все зробив Бог.</p>\n<p>Одного разу, коли селянин знову промовив свою молитву «про бідних і голодних» син сказав до нього:</p>\n','2014-08-04 23:26:54','http://oranta.org/index.php?option=com_content&view=article&id=2345:molytva-bagatogo-5-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молитва багатого (5 серпня)','uk',4589,6072,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6055,200,images.jpg\" />Митрополит Андрей граф Шептицький увійшов в історію України не лише як релігійний, але також як визначний політичний діяч. Свою суспільно-політичну діяльність він розглядав насамперед з християнської точки зору - як вияв любові до Бога та ближнього.</p>\n<p>Вплив Шептицького на політичну історію України є безперечний, однак не слід забувати, що через ряд як об\'єктивних, так і суб\'єктивних факторів ідеї цього патріота-державника так і не були зреалізованими на сто відсотків –Вдадика Андрей двічі пережив національно-визвольне піднесення українського народу, і в обох випадках українська державність була втоплена окупантами в крові.</p>\n','2014-07-29 12:09:00','http://oranta.org/index.php?option=com_content&view=article&id=2347:suspilno-politychni-pogljady-andreja-sheptyckogo&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Суспільно-політичні погляди Андрея Шептицького','uk',4574,6057,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6065,200,d0bf.jpg\" />Спостерігаючи кілька днів поспіль за летом мушви, павук помітив, що здебільшого вона хмарами висить над виноградником.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тепер мушві буде непереливки! – посміхався павук і повз у гору по лозі.</p>\n<p>Він приплів міцну сіть до одного з найбільших духмяних грон, а сам сховався в заглибині між прохолодними виноградинами. Зі свого сховку хитрий розбійник тишком нишком нападав на безтурботних мушок, і цей підступ минався йому.</p>\n','2014-08-08 01:06:02','http://oranta.org/index.php?option=com_content&view=article&id=2352:pavuchi-siti-8-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Павучі сіті (8 серпня)','uk',4595,6078,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6065,200,lyubov2.jpg\" />- Куди іде Любов? – спитало маленьке Щастя у Батька.</p>\n<p>- Умирає, відповів батько. – Люди, сину, не бережуть того, що мають. Вони просто не вміють любити!</p>\n','2014-08-07 01:59:16','http://oranta.org/index.php?option=com_content&view=article&id=2351:shchastja-i-ljubov-7-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Щастя і любов (7 серпня)','uk',4593,6076,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,2014-01-15-3.jpg\" />Молодий чернець прийшов до старця за порадою.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Отче, - сказав він, - я живу в пустелі трохи більше ніж рік і за той час вже 5 чи 6 разів з’являлася сарана. Ти знаєш, що це за напасть? Вона залазить усюди, навіть у харч. Що мені робити?</p>\n<p>Старець, який жив у пустелі вже40 років відповів:</p>\n','2014-08-25 01:33:23','http://oranta.org/index.php?option=com_content&view=article&id=2353:sarana-25-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Сарана (25 серпня)','uk',4608,6091,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6089,200,thum.jpg\" />Одному учневі весь час не давала спокою одне питання: - як у людському житті поєднуються божественне значення і свобода вибору? З цим питанням він звернувся до вчителя і той відповів.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вирушаючи в поле збирати квіти, ти не можеш бути впевненим ані того якою дорогою підеш, ані з яких квітів збереш свій букет. І ти йдеш від квітки до квітки прислухаючись до внутрішнього голосу і зриваєш їх, складаючи до купи.\n','2014-08-26 02:09:01','http://oranta.org/index.php?option=com_content&view=article&id=2355:buket-26-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Букет (26 серпня)','uk',4616,6099,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,8ye21-frison.jpg\" />Один норовистий кінь якось перекусив вуздечку, скинув вершника і умчав світ за очі, не добираючи дороги. Думав кінь, що врешті-решт вирвався на свободу. Але не помітив попереду прірви і впав із кручі вниз.\n','2014-08-29 06:50:39','http://oranta.org/index.php?option=com_content&view=article&id=2361:norovysty-kin-29-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Норовистий кінь (29 серпня)','uk',4623,6106,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6090,200,best-wallpaper_skobka_75.jpg\" />Майстра спитали чи відчуває він подеколи розчарування через те, що його зусилля не такі плідні як йому хотілося б. Відповідаючи, майстер відповів історію про равлика який поліз на вишню у холодний вітряний день запізнілої весни.</p>\n<p>Горобці на сусідньому дереві від щирого серця тішились дивлячись на нього. Потім один підлетів і каже:</p>\n','2014-08-28 05:47:32','http://oranta.org/index.php?option=com_content&view=article&id=2360:ravlyk-na-vyshni-28-serpnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Равлик на вишні (28 серпня)','uk',4621,6104,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,1379356423_sad-dog_lecates_sa.jpg\" />Чоловік і жінка купили цуценя. Вони любили його і доглядали як сина. Цуценя виросло і стало великим, гарним, розумним собакою, який нераз рятував майно господаря. Через сім років після того, коли чоловік з жінкою взяли щеня у них народилася довгождана дитина. Вони були щасливі. Малюк забирав майже увесь їхній час, а собаці не залишалося навіть дрібки уваги.</p>\n','2014-09-01 06:51:55','http://oranta.org/index.php?option=com_content&view=article&id=2383:viddany-sobaka-1-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Відданий собака (1 вересня)','uk',4628,6111,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6110,200,wolves-chernobyl1.jpg\" />В одному лісі всі звірі дуже боялися грізного вовка. І як почують, що він іде, то утікають, хто куди.</p>\n<p>Одного разу, зібрав вовк усе лісове товариство і каже:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не бажаю я більше що би в моєму лісі панував страх. Не дивіться на мене як на ненаситного і не втікайте від мене. Мені до смаку тільки недужі та немічні. Якщо такі є, приводьте їх до мене, я буду їх їсти, а вас не зачеплю.</p>\n','2014-09-03 07:04:56','http://oranta.org/index.php?option=com_content&view=article&id=2386:vovcha-natura-3-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Вовча натура (3 вересня)','uk',4633,6116,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6120,200,17156.jpg\" />Давним-давно жив один святий старець, який багато молився і часто уболівав про гріхи людські. І дивним йому здавалося, чому це так буває, що люди в церкву ходять, Богу моляться, а живуть все так само погано, гріха не убуває. «Господи, - думав він, - невже не чуєш Ти наших молитов? Ось люди постійно моляться, щоб жити їм у мирі й покаянні, і ніяк не можуть. Невже марнота їх молитва?»<o:p></o:p></p>\n','2014-09-04 23:13:57','http://oranta.org/index.php?option=com_content&view=article&id=2393:nimi-molytvy-5-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Німі молитви (5 вересня)','uk',4638,6121,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6124,200,love.jpg\" />Якось надумав один юнак оженитися, і пішов радитися до свого батька. Батько сидів замислений, щось малюючи прутиком на піску. Юнак наблизився до нього і казав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Батьку, я зустрів чудову дівчину і хочу з нею одружитися.</p>\n<p>Але батько промовчав і малював на піску нуль.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Батьку, вона прекрасна господиня.</p>\n','2014-09-06 21:41:18','http://oranta.org/index.php?option=com_content&view=article&id=2400:ljubov-i-prynady-7-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любов і принади (7 вересня)','uk',4642,6125,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,38.jpg\" />Жила собі надзвичайно побожна жінка. Щодня вона проказувала довгі молитви і ходила до храму тою самою дорогою багато років поспіль. До жінки зверталися подорожні, простягали руки жебраки, але вона просувала заглиблена у свій внутрішній світ не зауважуючи нікого і нічого довкола. Якось пройшовши за звичаєм до храму, жінка виявила, що двері замкнені.</p>\n','2014-09-10 22:31:52','http://oranta.org/index.php?option=com_content&view=article&id=2407:zapyska-vid-boga-11-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Записка від Бога (11 вересня)','uk',4654,6137,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,images.jpg\" />При дорозі стояло засохле дерево. Однієї ночі неподалік і шов злодій. Він помітив далекий силует і подумав, що це стоїть поліцай, злякався і втік.</p>\n<p>Увечері йшов дорогою закоханий, ще здалеку помітив він граціозний силует і подумав, що це кохана давно його дожидає, його серце радісно забилося, він усміхнувся і прискорив ходу.</p>\n','2014-09-09 22:27:18','http://oranta.org/index.php?option=com_content&view=article&id=2406:zasohle-derevo-10-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Засохле дерево (10 вересня)','uk',4652,6135,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6126,200,short.jpg\" />Великий король&nbsp; хотів вибрати спадкоємця з трьох синів. Всі вони були близнюки, розумні і хоробрі і вибрати було нелегко. Він порадився з мудрецем і той підказав йому ідею.</p>\n<p>Король попросив до себе усіх трьох синів, дав кожному мішечок з квітковим насінням і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що вирушає в багатолітню прощу. І хто найкраще збереже його насіння, той і найкраще стане спадкоємцем трону.</p>\n','2014-09-09 01:50:06','http://oranta.org/index.php?option=com_content&view=article&id=2402:prytcha-pro-kvity-9-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Притча про квіти (9 вересня)','uk',4648,6131,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6142,200,97888_original.jpg\" />Два горобчики тішилися прохолодою, сидячи в гіллі однієї верби. Один із них сів на верхівці, а інший – унизу, на розгалуженні.</p>\n<p>Через деякий час горобчик, що сидів нагорі, промовив:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, які чудові ці зелені листочки.</p>\n','2014-09-12 15:46:20','http://oranta.org/index.php?option=com_content&view=article&id=2413:dva-gorobchyky-12-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Два горобчики (12 вересня)','uk',4660,6143,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6143,200,4564.jpg\" />Один старенький цілий день сидів у храмі навпроти ікони. Якось церковний прислужник запитав старого:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Що каже вам Бог?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Він нічого не каже, Він лише слухає.\n','2014-09-12 23:44:26','http://oranta.org/index.php?option=com_content&view=article&id=2415:navchytysja-sluhaty-13-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Навчитися слухати (13 вересня)','uk',4662,6145,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6148,200,valentines-day.jpg\" />Якось прийшов в одне селище старий мудрий чоловік і оселився там. Він любив дітей і проводив з ними чимало часу. Але ще він любив дарувати їм подарунки, але тільки дуже ламкі речі.</p>\n<p>Хай як старалися діти, їхні нові іграшки часто ламалися. Діти засмучувалися і гірко плакали. Минав час і мудрець знову дарував їм іграшки, але ще тендітніші. Врешті батькам урвався терпець і вони прийшли до нього:</p>\n','2014-09-13 22:57:50','http://oranta.org/index.php?option=com_content&view=article&id=2419:dorogi-podarunky-14-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Дорогі подарунки (14 вересня)','uk',4666,6149,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,9132.JPG\" />Один хлопчик, який згодом став ученим, у школі мав славу телепня.<o:p></o:p></p>\n<p>Одного разу школу відвідав поважний гість. Учитель показуючи на того хлопчика сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Це найдурніший учень у школі.<o:p></o:p></p>\n<p>Перш ніж покинути школу, відвідувач підійшов до дитини і промовив дружнє благословення такого змісту:</p>\n','2014-09-14 23:24:59','http://oranta.org/index.php?option=com_content&view=article&id=2424:blagoslovennja-15-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Благословення (15 вересня)','uk',4671,6154,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,DSC05.jpg\" />Одного разу старець прийшов д господаря поля і сказав:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Дай мені пшениці.<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->А чи ти жав, старцю?<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Ні, - відповів старець.<o:p></o:p></p>\n','2014-09-17 23:47:59','http://oranta.org/index.php?option=com_content&view=article&id=2428:jak-oderzhaty-nagorodu-18-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як одержати нагороду (18 вересня)','uk',4674,6157,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6153,200,obgyn-gift.jpg\" />Старий майстер бойових мистецтв вирішив більше ніколи не битися. Але одного разу його все таки викликав на поєдинок юний воїн, який вважав, що він куди майстерніший і сильніший.<o:p></o:p></p>\n<p>Однак старий майстер і ніяк не реагував на нього. Тоді воїн взявся ображати його і його предків, щоб спровокувати майстра, але і це не допомогло.<o:p></o:p></p>\n<p>В решті решт, юний воїн опустив руки і пішов.<o:p></o:p></p>\n','2014-09-16 23:38:09','http://oranta.org/index.php?option=com_content&view=article&id=2426:neprynjaty-dar-17-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Неприйнятий дар (17 вересня)','uk',4672,6155,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,75685543892905.jpg\" />Якось запитали мудреця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Яка таємниця вашої мудрости?<em><o:p></o:p></em></p>\n<p>Відповів він:<o:p></o:p></p>\n<p><!--[if !supportLists]-->- <!--[endif]-->Я живу простим життям. Я їм, коли їм. Сплю, коли сплю. Молюся, коли молюся.<em><o:p></o:p></em></p>\n<p>Запитувач спантеличився:\n','2014-09-19 22:06:59','http://oranta.org/index.php?option=com_content&view=article&id=2435:tajemnycja-mudrecja-20-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Таємниця мудреця (20 вересня)','uk',4682,6165,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,XYviL5TRpAA.jpg\" />Один військовий отаман вирішив напасти на супротивника, хоча ворогів було в десятеро більше. Він знав, що переможе, але його солдатів брав сумнів. По дорозі він спинився біля святині сказав своїм людям:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Помолившись у святині, я підкинув монету, якщо випаде орел – ми переможемо, а якщо решка – то програємо. Такою буде наша доля.<o:p></o:p></p>\n','2014-09-20 21:57:41','http://oranta.org/index.php?option=com_content&view=article&id=2437:vira-v-peremogu-21-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Віра в перемогу (21 вересня)','uk',4684,6167,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6162,200,h0MXG1nu9Bs.jpg\" />Одного разу шкільний учитель прийшов до вельми шанованої учительки і звинуватив її у тому, що її метод навчання цілком не логічний, що це просто якась безглузда балаканина і таке інше.</p>\n<p>Учителька дістала зі своєї торбини самоцвіт. Вона запропонувала учителеві піти в крамничку де продають вироби зі срібла і батарейки для годинників. І виторгувати за нього хоча б 10 тисяч карбованців.</p>\n','2014-09-18 23:42:21','http://oranta.org/index.php?option=com_content&view=article&id=2432:istynna-cinnist-19-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Істинна цінність (19 вересня)','uk',4680,6163,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6170,200,Yak.jpg\" />Один хлопець запитав старця:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Яку би мені добру справу учинити і жити з нею.<o:p></o:p></p>\n<p>Старець відповів:<o:p></o:p></p>\n<p><!--[if !supportLists]-->-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <!--[endif]-->Бог знає, що добре, я чув як дехто запитує мудреця:</p>\n','2014-09-22 22:27:30','http://oranta.org/index.php?option=com_content&view=article&id=2441:jak-dobru-spravu-chynyty-23-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Як добру справу чинити (23 вересня)','uk',4690,6173,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6176,200,pr_sem_1.jpg\" />Одній жінці приснився сон, що за прилавком у крамниці стоїть Господь.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Боже, це Ти?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Так, я, - відповів Бог.</p>\n','2014-09-24 22:23:23','http://oranta.org/index.php?option=com_content&view=article&id=2446:nasinnja-vsih-blag-25-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Насіння всіх благ (25 вересня)','uk',4694,6177,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6178,200,thumb.jpg\" />Юна дочка тихенько зайшла в покій батька-падишаха. Вона шанобливо чекала, поки батько побачить хвилювання в неї на обличчі і почне бесіду.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Що стривожило тебе, дочко моя? Якщо ти наважилася відвідати мене у хвилину спокою і усамітнення.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, батьку, ти мудрий і доля, та люди люблять тебе. Порадь мені як досягти твого спокою і мудрості.</p>\n','2014-09-25 23:49:49','http://oranta.org/index.php?option=com_content&view=article&id=2449:vynograd-z-kistochkamy-26-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Виноград з кісточками (26 вересня)','uk',4696,6179,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6180,200,prikorm21.jpg\" />Діти покуштували варення, яке щойно приготувала мама. Воно їм дуже сподобалось. І тут до кімнати зайшов молодший брат.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Можна і мені покуштувати варення? – запитав він.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Не їж, воно дуже кисле, - пожартували старші діти.</p>\n','2014-09-26 20:22:51','http://oranta.org/index.php?option=com_content&view=article&id=2450:kysle-varennja-27-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кисле варення (27 вересня)','uk',4698,6181,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6190,200,13754457.jpg\" />Одного разу посперечалося сонце з північним вітром, хто з них сильніший. Північний вітер вказав на чоловіка, що йшов пустелею і сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Закладаюся, що я змушу цього чоловіка скинути плащ, а ти ні.</p>\n<p>Сонце відповіло:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я приймаю цей заклад.</p>\n','2014-09-29 22:19:20','http://oranta.org/index.php?option=com_content&view=article&id=2458:hto-sylnishy-30-veresnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хто сильніший (30 вересня)','uk',4708,6191,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6192,200,1.JPG\" />28 вересня 2014 року в храмі св. Миколая Чудотворця на Аскольдовій могилі відбувся літературно-мистецький вечір <strong>«Візьму молитву – мамин оберіг»</strong> із циклу «Молюся віршами сьогодні». Перший вечір ознайомив вірян і гостей храму із поетичним доробком парафіянки Церкви Миколая Чудотворця на Аскольдовій могилі, кандидатом історичних наук, доцентом, громадським діячем<strong><em> </em>Іриною Василик</strong>.</p>\n<p>Поетеса народилася на мальовничій Тернопільщині, закінчила історичний факультет Тернопільського національного педагогічного університету імені Володимира Гнатюка. Перший вірш під назвою «О, мово рідна, що без тебе я» написала у 5 класі. Свої поезії публікувала у районній та обласних газетах, альманахах, брала участь у конференції молодих літераторів Поділля. У доробку Ірини Богданівни вірші різного жанру – для дітей дошкільного і молодшого шкільного віку, верлібри і катрени, силабо-тоніка. У 2003 р. Ірина Василик видала першу збірку віршів «Не зречуся тебе, моя мріє», у 2006 р. – другу «Сяйво сльози».</p>\n','2014-09-30 16:02:11','http://oranta.org/index.php?option=com_content&view=article&id=2460:poetychni-talanty-askoldovoji-mogyly&catid=25:parafija-cat&Itemid=27','P','','Поетичні таланти Аскольдової могили','uk',4710,6193,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,4657.jpg\" />Сім’я у вихідний день відпочивала на пляжі. Діти купалися в морі і будували замки з піску. Зненацька удалині на березі з’явилася маленька бабуся. Її сиве волосся розвівалося на вітрі, одежа була брудна і обшарпана. Вона щось бубніла сама до себе, підносячи із землі якісь предмети і складаючи їх у торбу.</p>\n','2014-10-03 01:04:07','http://oranta.org/index.php?option=com_content&view=article&id=2466:movchazna-borotba-3-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Мовчазна боротьба (3 жовтня)','uk',4718,6201,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6195,200,Ternovyy_Vinok.jpg\" />Настав кінець світу. І мільярди людей, яких перенесли на велетенську рівнину постали перед троном Божим. Багато хто відступив перед сліпучим сяйвом. Але деякі в першому ряду розмовляли. Вони говорили без шаноби, задиристо:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чи може Бог судити нас, що він знає про страждання? – пробурмотіла одна жінка. Вона закотила рукав, щоб показати номер з концентраційного табору. – Ми пережили невимовний жах, катування і смерть!</p>\n','2014-10-02 00:48:20','http://oranta.org/index.php?option=com_content&view=article&id=2464:kinec-svitu-2-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Кінець світу (2 жовтня)','uk',4715,6198,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6200,200,fL1m1it1cpg.jpg\" />Одна дівчина пішла полем де побожний чоловік ревно молився. Закон їхньої релігії не дозволяв проходити повз таке місце. Коли сільська дівчина поверталася, побожний чоловік сказав:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Як же неґречно ти вчинила, дівчино. Це ж гріх іти там, де молиться чоловік.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А що ви вважаєте молитвою?</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Молитвою? – вигукнув він, - що це таке? Солитися, значить думати про Бога.</p>\n','2014-10-04 21:59:42','http://oranta.org/index.php?option=com_content&view=article&id=2470:glybyna-molytvy-5-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Глибина молитви (5 жовтня)','uk',4723,6206,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6204,200,f_2100155071362073598.jpg\" />Маленький хлопчик побився зі своїм братом. Зазнавши нищівної поразки він насупився і сів у кутку. Коли брат підійшовши до нього спробував помиритися, він відмовився слухати. Так і супився цілісінький день.</p>\n<p>Настав вечір, мама сказала йому:</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Може пробачиш брата перед сном? Пам’ятаєш, у Святому письмі сказано: «хай сонце не заходить над вашим гнівом» (Еф. 4,26)</p>\n','2014-10-06 22:06:50','http://oranta.org/index.php?option=com_content&view=article&id=2476:gniv-i-sonce-7-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Гнів і сонце (7 жовтня)','uk',4726,6209,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6210,200,shutt.jpg\" />-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ах, любове! Я так мрію бути такою як ти. – закохано повторювала закоханість. –Ти набагато сильніша від мене.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А ти знаєш у чому моя сила – спитала любов, замислено хитаючи головою.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ти важливіша для людей.</p>\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ні, любо, зовсім не тому, - зітхнула любов і погладила закоханість по голові. – Я вмію прощати.</p>\n','2014-10-08 00:12:15','http://oranta.org/index.php?option=com_content&view=article&id=2487:ljubov-i-zakohanist-8-zhovtnja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Любов і закоханість (8 жовтня)','uk',4728,6211,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,img_9575.jpg\" />2-4 жовтня 2014 року в Перемишлі проходив Собор УГКЦ у Польщі «Жива парафія – місце зустрічі з живим Христом». У ньому взяв участь, окрім Митрополита Перемишльсько-Варшавського Івана (Мартиняка), Єпарха Вроцлавсько-Гданського Володимира (Ющака) та Єпископа-помічника Перемишльсько-Варшавського Євгена (Поповича), Секретар Синоду Єпископів УГКЦ, заступник голови робочої групи з реалізації Програми розвитку Української Греко-Католицької Церкви на період до 2020 року «Жива парафія – місце зустрічі з живим Христом» владика Богдан (Дзюрах). Про нові виклики та соборові враження – у нашому інтерв’ю.</p>\n<p><strong>&nbsp;</strong></p>\n','2014-10-08 21:57:42','http://oranta.org/index.php?option=com_content&view=article&id=2489:2014-10-08-18-59-45&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Мусимо чувати, аби така шляхетна ідея, як національна, не посіла місця Бога»','uk',4731,6214,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6212,200,venedykt_rik.jpg\" />9 жовтня Греко-Католицька Церква святкує Переставлення святого апостола і євангелиста Івана Богослова. Як відомо, саме цьому апостолу належить авторство пророчої Книги Одкровення про кінець світу. А ще святий прославився своїм навчанням про любов, якої, на його переконання, достатньо, щоб звершувалося Боже Слово на землі.<o:p></o:p></p>\n<p>Про постать наймолодшого серед апостолів — Івана Богослова, а також про те, чи варто любити своїх «агресорів», розповідає владика Венедикт Алексійчук, голова Патріаршої літургійної комісії УГКЦ.\n','2014-10-09 22:18:58','http://oranta.org/index.php?option=com_content&view=article&id=2491:darumo-nashym-agresoram-ljubov-tobto-boga&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Даруймо нашим агресорам любов, тобто Бога','uk',4733,6216,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6232,200,464548.jpeg\" />Святочним днем парафії можна назвати неділю 12 жовтня 2014 р. Б ,день пам’яті прп. Киріяка самітника. То справжнє свято для парафіяльної спільноти і духовенства церкви св. Миколая Мирлікійського на Аскольдовій Могилі, адже на Архиєрейській Божественній Літургії Владика Йосиф Мілян вручив засновнику громади, адміністратору парафії о. Ігорю Онишкевичу грамоту пароха.</p>\n<p>Маємо надію в Бозі, що ця грамота пароха, яка ствердила громаду як парохію, дасть нашим душпастерям, сестрам-служебницям Непорочної Діви Марії які обслуговують цей Храм і очолюють катехитичну недільну школу, парафіянам і прихожанам, духовне піднесення закладати цеглини Аскольдової Лаври, допоможе всім нам краще працювати для Слави Божої, добра Церкви і для спасіння повірених душ.</p>\n','2014-10-28 08:43:12','http://oranta.org/index.php?option=com_content&view=article&id=2498:paroh-i-parohija&catid=25:parafija-cat&Itemid=27','P','','Парох і парохія','uk',4750,6233,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6233,200,20111031200831.jpg\" />Внаслідок різних часових і просторових обставин серед українського суспільства стають популярними різноманітні святкування, які не мають жодного коріння в нашому народі. До них можна віднести також сьогодні популярне святкування Хелловіна (31 жовтня). З мас-медіа кожного разу напередодні цього дня можна почути запрошення на вечірки та забави з нагоди святкування «Хелловіна». Що цеза свято? Яка його історія? І чи варто греко-католикам, і, взагалі, християнам, відзначати цей день у своєму календарі?</p>\n<p><strong>&nbsp;</strong></p>\n','2014-10-30 23:30:20','http://oranta.org/index.php?option=com_content&view=article&id=2500:hellovin-halloween--antyhrystyjanske-svjato&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Хелловін (Halloween) – антихристиянське свято!','uk',4753,6236,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6236,200,Swietych.png\" />Ці «великі небесні іменини» об’єднують усіх. І тих, чиї імена завжди є в літургійному календарі, й тих, хто туди потрапляє рідко. І тих, кого Бог знає, а Церква офіційно святим не назвала, бо, може, і не здогадується, що то була свята людина. Радісна урочистість Усіх Святих промовляє до сердець віруючих: на Небесах так багато наших братів і сестер, наших заступників і помічників, – радіймо їм і живімо добре, аби долучитися до них свого часу! Урочистість Усіх Святих – це зовсім не «гробки», це торжество святості!</p>\n<p>А ще ця урочистість відображає певний «вплив», або ж реакцію християнства на явище язичництва. З історичного погляду, вшанування святих у Церкві не виникло саме по собі як окреме свято чи особливо підкреслювана побожність. Віруючі вшановували Матір Спасителя, загиблих за віру зі своїх спільнот (а потім сформувався культ мучеників), з часом Церква у своїй повноті дійшла до розуміння великої цінності дівоцтва перед Богом – постало вшанування дів…</p>\n','2014-11-01 04:45:16','http://oranta.org/index.php?option=com_content&view=article&id=2503:urochystist-usih-svjatyh&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Урочистість Усіх Святих','uk',4755,6238,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6239,200,images.jpg\" />Пострадянський простір живе подвійним, а то й потрійний життям. Кілька поколінь людей народилися в часи Сталіна, Хрущова і Брежнєва, а значить, що частина їх віку була пов\'язана з тим рівнем життя, який встановлювала КПРС та інші партійно-господарські органи. Життя було розписане по пунктах для всіх. Партія знала, хто повинен і де вчитися, якої національності мають бути майбутні інженери або лікарі, де вони працюватимуть&nbsp; за&nbsp; \"напрямками\", скільки отримувати «зарплати» і коли їм дозволяти вступати в партію. Життя радянської людини регулювали парткоми, райкоми, міськкоми і обкоми, якими, в свою чергу керувала ЦК. Нагляд здійснював КДБ і численні помічники - піонери (юні друзі міліції) і комсомольці (оперативні комсомольські загони), а також дружинники і таємні інформатори. За 74 роки виросло кілька поколінь слухняних людей, для яких розпад СРСР став особистою трагедією - вони залишилися без спрямовуючої ролі ЦК КПРС.</p>\n','2014-11-03 22:30:59','http://oranta.org/index.php?option=com_content&view=article&id=2505:rosisko-radjanska-religina-mifotvorchist&catid=37:spilnota-cat&Itemid=49','P',NULL,'Російсько-радянська релігійна міфотворчість','uk',4757,6240,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6258,200,10402447_784056904988107_5514823962148157099_n.jpg\" />«Працівники БФ «Карітас Київ» взяли участь в Третій всеукраїнській школі самодопомоги. Навчання відбувалось в місті Києві в приміщенні інституту післядипломної освіти й об’єднало спеціалістів із &nbsp;Харкова та області, Івано-Франківська, Скадовська та&nbsp; Києва. Метою школи було оволодіти теоретичними навиками організації та проведення груп самодопомоги, а також сформулювати цілі та плани щодо створення нових груп самодопомоги», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n','2014-11-18 23:14:12','http://oranta.org/index.php?option=com_content&view=article&id=2519:2014-11-18-21-16-33&catid=29:propovidi-cat&Itemid=34','P',NULL,'«Коли одна радісна людина ділиться з іншою, то веселіше стає вже двом» - БФ «Карітас Київ»','uk',4776,6259,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6267,200,1039626.jpg\" />Протягом 20-ти років функціонує Катехитично-недільна школа при храмі св. Миколая Чудотвоця на Аскольдовій Могилі. Вже з самого початку, незважаючи на труднощі, пов’язані з приміщенням, діти різної вікової категорії приходили для того, (як вони це пояснюють) щоб почути про Бога. Праця, яка ведеться для дітей та молоді в школі, є дуже важливою у вихованні не тільки духовно-моральному, патріотичному аспектах, але й цілісно-особистісному.</p>\n<p>Сьогодні катехитична праця здійснюється не тільки Сестрами Служебницями, як раніше, але й з участю 16-ти катехитів, вишколених за сприянням пароха о. Ігоря Онишкевича: Світлана Заяць, Ореста Цебрівська, Аня Онишкевич, Оксана Ясніцька, Оксана Сенчук, Ірина Макар, Руслана Дмитровська, Катерина Чаплик, Надія Кучерява, Ірина Василик, Любов Михайлюк, Марія та Василилина Томишинець, Анастасія Заверуха і Сестри Служебниці с. Філомена Чворсюк і с. Христосфора Буштин.</p>\n','2014-11-25 20:15:51','http://oranta.org/index.php?option=com_content&view=article&id=2529:katehytychno-nedilna-shkola','P','','Катехитично-недільна школа','uk',4788,6271,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6272,200,Ercole_de_Roberti_Destructio.jpg\" />Навіть серед багатьох трудностей християнин не повинен піддаватись пригніченню, але жити надією. Папа Франциск наголосив на тому, що зіпсованість та байдужість віддаляють людину від зустрічі з Господом Ісусом.</p>\n<p>Літургічні читання, взяті із новозавітньої книги Одкровення та Євангелії від святого Луки, у яких розповідається про занепад міста Вавилону та пророцтво падіння Єрусалиму, стали натхненням для Вселенського Архиєрея, щоб застановитись нам темою занепаду міст та людей, які віддалились від Бога і не прийняли Його.</p>\n','2014-11-28 09:06:53','http://oranta.org/index.php?option=com_content&view=article&id=2532:ne-viddaljamosja-vid-boga&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Не віддаляймося від Бога','uk',4790,6273,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6273,200,f_klZ7aOBPc.jpg\" />Військові дії на території Донбасу мають не тільки політичне та економічне, а й релігійне підґрунтя. Більшість представників терористичних угрупувань зараховують себе до Української православної церкви (Московського патріархату) – звучали і продовжують звучати заклики зробити «московське» православ’я офіційною релігійною системою і закріпити її статус конституційно. Власне, це вже відбулося – 9-а стаття «конституції» «ДНР» переносить нас у часи Російської імперії та обер-прокурора, який фактично керував Синодом. Прикладом може бути «інавгурація» голови угруповання «ЛНР» Ігоря Плотницького, яку відвідав колишній митрополит Луганської і Алчевської єпархій Іоаннікій – він дав святительське благословення новому очільнику псевдореспубліки, компанію йому склав настоятель Ніколо-Преображенського собору протоієрей Богдан Федьків.</p>\n','2014-11-30 22:16:19','http://oranta.org/index.php?option=com_content&view=article&id=2534:goninnja-na-inakomysljachyh&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Гоніння на інакомислячих','uk',4792,6275,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6277,200,IMAG2276.jpg\" />Щорічно 1 грудня відзначається Всесвітній день боротьби зі СНІДом. Цього дня людство згадує про те, яку серйозну загрозу для життя людей несе ця глобальна проблема.</p>\n<p>Вперше цей день відзначався 1 грудня 1988 року. Таке рішення затвердили на міжнародній зустрічі міністрів охорони здоров\'я, які обговорювали необхідність обміну інформацією щодо ВІЛ-інфекції та СНІДу.</p>\n','2014-12-01 15:17:43','http://oranta.org/index.php?option=com_content&view=article&id=2536:den-borotby-zi-snidom&catid=25:parafija-cat&Itemid=27','P','','День боротьби зі СНІДом','uk',4795,6278,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6281,200,384045840.jpg\" />Країна опинилася на роздоріжжі між двома ключовими цінностями у світі: свободи вибору та безпеки</p>\n<p>Питання цінностей є найглибшим питанням, що існує в будь-якому суспільстві. Усі біди, без винятку, усі біди Україні тільки в цьому. Всі інші речі похідні.</p>\n<p>Реформи, про які ми зараз говоримо, будуть сізіфовою працею, якщо в країні не зміняться цінності. Це не означає, що не треба проводити реформи, але поки цінності не стали іншими, вони грають лише підтримуючу роль, роблять так, щоб держава не розвалилась. Тому потрібно створити певний інкубатор для зміни цих цінностей. І першочергова задача теперішньої влади – не зіпсувати момент дозрівання нового суспільства.</p>\n','2014-12-05 15:11:37','http://oranta.org/index.php?option=com_content&view=article&id=2540:fundamentalna-zmina-cinnoste&catid=37:spilnota-cat&Itemid=49','P',NULL,'Фундаментальна зміна цінностей','uk',4799,6282,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6301,200,20141218_084101.jpg\" />У ніч, з 17 на 18 грудня 2014&nbsp;року, десь приблизно о 01:30&nbsp;год., охорона церкви Аскольдової могили побачила невідомого юнака, який зупинився біля дверей храму. Проте спершу він не викликав підозри, оскільки часто люди приїжджають молитися вночі, інколи цілими групами.</p>\n<p>Цей юнак нахилився, ніби став на коліна. Потім з’ясувалося, що він запалив горючу суміш «коктейль молотова» у двох пляшках. Ці пляшки були поєднані скотчем. Він різко піднявся і кинув їх у бік дверей церкви. Сам же втік до Дніпра.</p>\n','2014-12-19 01:06:12','http://oranta.org/index.php?option=com_content&view=article&id=2556:posjagannja-na-hram-chy-na-vlasnu-dushu&catid=25:parafija-cat&Itemid=27','P','','Посягання на храм? чи на власну душу?','uk',4819,6302,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6303,200,1084976.jpg\" />Ми справді зможемо перемогти не тільки зовнішнього агресора, а й економічні негаразди, суспільні кризові ситуації, реформувати наше суспільство лише тоді, коли навчимося робити діла милосердя, як це робив святий Миколай. Про це сьогодні, у день Святого Миколая сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав під час проповіді до вірних у храмі Святого Миколая на Аскольдовій могилі.</p>\n','2014-12-19 22:58:44','http://oranta.org/index.php?option=com_content&view=article&id=2558:2014-12-19-21-02-37&catid=25:parafija-cat&Itemid=27','P',NULL,'«Лише любов до Бога і Батьківщини може зробити людину героєм»','uk',4821,6304,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6306,200,22983_original.jpg\" />Наближається Різдво. Перед господинями постають турботи по гідну підготовку святкового&nbsp; різдвяного столу, прикраси будинку. Іноді ці матеріальні занепокоєння затуляють головне - зміст свята, Різдва Спасителя. Тим часом в пам\'яті наших багатьох віруючих є особливо зворушливі моменти святкування Різдва, коли не було багато накритих столів, але було справжнє відчуття близького приходу Спасителя.</p>\n','2014-12-24 17:45:24','http://oranta.org/index.php?option=com_content&view=article&id=2560:svjatvechir-u-stalinskomu-vyslanni-spogady-svidka&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Святвечір у сталінському висланні: спогади свідка','uk',4825,6308,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6310,200,04.jpg\" />19 грудня 2014 року сестра Василія (Гальцова) і волонтер Юрій Йосифович послужили Святителю Миколаю, передавши дарунки, зібрані в результаті доброчинної акції, що триває в громадах УГКЦ у м. Києві. Їх супроводив о. Костянтин Пантелей, керівник Відділу душпастирства у пенітенціарній системі України.</p>\n<p>В Київському слідчому ізоляторі у корпусі тримання неповнолітніх ув\'язнених відбулось урочисте привітання неповнолітніх зі святом Святого Миколая.&nbsp;Програму разом з вихованцями, утримуваними в Київському слідчому ізоляторі, приготувала с. Василія (Гальцова), ЧСВВ. Свято було у формі театралізованої вистави, учасниками якої були самі неповнолітні ув\'язнені. До дітей завітав Св. Миколай (Юрій Йосифович). Він запитав дітей про Заповіді Божі, вони приготували невеличкий концерт, читали вірші та заспівали разом святкові пісні. Св. Миколай у супроводі о. Костянтина Пантелея і с. Василії цього ж дня побував в камерах, де утримуються жінки.</p>\n','2014-12-28 21:07:16','http://oranta.org/index.php?option=com_content&view=article&id=2565:sluzhinnja&catid=29:propovidi-cat&Itemid=34','P',NULL,'Служіння','uk',4834,6317,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6312,200,unnamed.jpg\" />БФ «Карітас Київ» у рамках постійної праці надає допомогу багатьом дітям та кризовим сім’ям. Окрім цього проводяться різні акції. Однією із таких є подарунки для дітей до свята Святого Миколая. Цього року Карітас Київ надав понад тисяча подарунків для різних категорій дітей», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n<p>Святий Миколай Карітасу попрямував до дітей, які його з нетерпінням чекали. Так, подарунки отримали діти з кількох будинків сімейного типу, інтернатів, лікарень, діти із кризових, малозабезпечених, неповних та багатодітних родин, а також діти з сімей вимушених переселенців та діти, батьки яких загинули в АТО та на Майдані.</p>\n','2014-12-26 16:30:01','http://oranta.org/index.php?option=com_content&view=article&id=2563:2014-12-29-14-31-20&catid=29:propovidi-cat&Itemid=34','P',NULL,'Понад тисяча дітей отримали подарунки від Святого Миколая БФ «Картіас Київ»','uk',4830,6313,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6319,200,10906324.jpg\" />Мій Вифлеєм цьогоріч увесь пронизаний дірами, цілісінький його просторочас прошитий наскрізними дірами автоматних і кулеметних черг, уламків мін і гранат, Градів. Куди не глянь - усюди діри: фасади будинків, паркани, огорожі, вікна й дахівки, дерева й автомобілі пронизані дірами. Цьогоріч мій Вифлеєм із дірявого світу, розташованого десь на межі людського глузду та людського безумства, на сотильній грані поміж дійсним і уявним, засіяній незібраними соняхами минулорічного посіву... Сьогодні у Пісках під Донецьком - жодного натяку на безпеку, жодного сховку від жорстокоі продірявленості. Куди не ступи ногою -усюди продірявлена спотвореність розстріляноі дійсності: простір сконденсованоі деструкціі, час смисловоі дисперсіі. Дірявий світ виглядає таким безглуздим!..</p>\n','2015-01-06 16:01:38','http://oranta.org/index.php?option=com_content&view=article&id=2570:dirjavy-vyflejem&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Дірявий Вифлеєм','uk',4843,6326,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6338,200,santa.jpg\" />ДЖАННА&nbsp; БЕРЕТТА&nbsp; МОЛЛА&nbsp;&nbsp;&nbsp;&nbsp; - МАТИ, ДОКТОР МЕДИЦИНИ<strong> </strong>(1922 - 1962)</p>\n<p>Амос, 1:13.Біблія.</p>\n<p>Сьогодні витає творча пропозиція: закупити&nbsp; сільські&nbsp; садиби&nbsp; в мальовничих селах Андрієвичі&nbsp; Емільчинського району Житомирської області&nbsp; на землях льону&nbsp; та&nbsp; вишивання, на батьківщині&nbsp; Тетяни Майданович – авторки&nbsp; рукопису “Насіння&nbsp; диких&nbsp; трав”,&nbsp; у селі&nbsp; Горобіївка Канівського&nbsp; району, Черкаської області –&nbsp; в екологічно чистих&nbsp;&nbsp; нішах,&nbsp; заснувати&nbsp; дві&nbsp; Жіночі&nbsp; гімназії&nbsp; імені Джанни Беретти Молли&nbsp; та Жіночий&nbsp; інститут&nbsp; християнської етики&nbsp; ім.Джанни Беретти Молли&nbsp; для поліпшення умов виховання&nbsp; дівчат&nbsp; у дусі християнського послуху.</p>\n','2015-01-15 11:14:12','http://oranta.org/index.php?option=com_content&view=article&id=2578:svjata-maty-simji&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Свята мати сім\'ї','uk',4856,6339,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6351,200,45646.jpg\" />22 січня Україна відзначає День Соборності, приурочений до Акту об\'єднання цього дня Української Народної Республіки та Західноукраїнської Народної Республіки.</p>\n<p>Соборність українських земель має глибоке історичне коріння, спирається на споконвічну мрію народу про власну незалежну, соборну державу та є інтегральним результатом складного і довготривалого процесу її формування.</p>\n','2015-01-21 21:19:40','http://oranta.org/index.php?option=com_content&view=article&id=2589:den-ukrajinskoji-sobornosti&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'День Української Соборності','uk',4870,6353,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6354,200,IMG_3809.jpg\" />Є в нашому народі звичай, що з нагоди церковних чи світських свят вітаємо одне одного і переказуємо різні побажання. Найбільш інтенсивним щодо цього є кінець грудня і початок січня. І це не безпідставно.</p>\n<p>У другій половині грудня поступово довшає день. Отже, є надія на світло і тепло. Інша причина – закінчення одного року і початок наступного. Кінець старого року – це час підбиття підсумків того, що було, а початок нового – припущення, що буде; пора сподівань на краще.</p>\n','2015-01-23 22:52:11','http://oranta.org/index.php?option=com_content&view=article&id=2591:nashi-pobazhannja-i-nashi-dijannja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Наші побажання і наші діяння','uk',4872,6355,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6356,200,AFP2429186_Articolo.jpg\" />«Lumen Fidei – Світло віри» – так називається перша Енцикліка Папи Франциска, що була підписана 29 червня та оприлюднена 5 липня 2013 року. Як зазначає сам автор, вона була майже завершена його попередником, і додає: «За це я йому дуже вдячний, і в Христовому братерстві, приймаю цю його цінну працю, додаючи до тексту деякий свій вклад».</p>\n<p>Хто вірує, той бачить. Хто вірує, той ніколи не є самотнім, тому що віра є спільним благом, допомагаючи відрізняти добро від зла та будувати наші спільноти, даруючи надію. Саме так можна окреслити головну думку цієї енцикліки, над якою ми роздумуватимемо, присвячучи її перечитуванню кілька хвилин наприкінці наших передач у п’ятницю.</p>\n','2015-01-26 20:51:04','http://oranta.org/index.php?option=com_content&view=article&id=2593:2015-01-26-18-56-29&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Хто вірує – той ніколи не самотній». Читання з енцикліки про віру (1)','uk',4874,6357,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6358,200,Babushk.jpg\" />«Пригадую собі ту щиру віру, що у тобі, яка перш була вселилась у твою бабуню Лоїду та у твою матір Евніку, а я певен, що й у тебе. З тієї причини пригадую тобі, щоб ти оживив дар Божий, який у тобі через покладання рук моїх», – писав святий Павло до свого учня апостола Тимотея.</p>\n','2015-01-27 12:13:39','http://oranta.org/index.php?option=com_content&view=article&id=2595:papa-pro-viru-jak-dar-svjatogo-duha-i-pro-rol-zhinok-v-ogo-peredavanni&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа про віру, як дар Святого Духа і про роль жінок в його передаванні','uk',4876,6359,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6362,200,1489143.jpg\" />На Авторадіо – стан руху автотранспорту на столичних дорогах, на Хрещатику – усміхнені, задумані, втомлені і просто люди, у метро – потік людських тіл з усміхненими, задуманими, втомленими і просто людськими обличчями. Життя вирує буденними ритмами, гуляє брудними вулицями, пливе болотними артеріями зимового міста. У Києві – січень, у драмтеатрі – прем’єра, у найближчому відділі ПриватБанку – черга до терміналу, а мої хлопці – у бліндажах, у темних підземеллях українського фронту, у тих самих схованках, у яких іще вчора доводилось сидіти й мені під час артобстрілів, саме тоді, коли по Хрещатику бігали усміхнені, задумані, втомлені і просто люди, пронизуючи сірі київські будні строкатістю просто людських мрій і навіть не здогадуючись, що десь у темних підземеллях українського фронту сиджу я…</p>\n','2015-01-29 10:35:41','http://oranta.org/index.php?option=com_content&view=article&id=2599:sogodni-mozhna-povernutysja-do-kyjeva-ale-ne-mozhna-povertatysja-z-frontu&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Сьогодні можна повернутися до Києва, але не можна повертатися з фронту!!!...','uk',4880,6363,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6371,200,Vira-1024x682.jpg\" />Зовсім випадково сьогодні натрапив на три свої короткі тексти, виголошені у радіоефірі штабу АТО (АТЦ), Краматорськ, у вересні 2014 р. Іще тоді, коли живим був Вовчик (на фото ліворуч), який нещодавно загинув під Донецьким аеропортом…</p>\n<p><strong>ВІРА</strong><br /> Вона дозволяє стояти впевнено обома ногами на твердому ґрунті дійсності. Вона дає можливість відважно, крок за кроком, прямувати у напрямку досягнення остаточної мети. Вона дарує людині здатність бачити сяйво перемоги навіть у густих сутінках конкретної поразки.</p>\n','2015-02-03 21:54:20','http://oranta.org/index.php?option=com_content&view=article&id=2607:vira-nadija-ljubov&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Віра, Надія, Любов…','uk',4889,6372,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6373,200,1409720881-9162-ukraina.jpg\" />Ми, сестра Василія, згромадження василіянок, Провінція Святого архистратига Михаїла, Василина та Марія Томишинець, парафіянки церкви св. Миколая Мирлікійського на Аскольдовій Могилі, відвідуємо поранених наших Герої у Київському клінічному військовому госпіталі на Печерську.</p>\n<p>Хочемо поділитись із Вами свідченнями Героїв, про Божу присутність під час перебування на війні, незламну Віру, справжній героїзм, Любов до Батьківщини та своїх побратимів. Ці свідчення скупі, без красномовства, але настільки глибокі і проникливі, що цю присутність Бога бачиш у їхніх очах, серцях, душах.</p>\n','2015-02-04 22:09:21','http://oranta.org/index.php?option=com_content&view=article&id=2609:rozpovid-spogady-pro-poranenyh-gerojiv&catid=29:propovidi-cat&Itemid=34','P',NULL,'Розповідь – спогади про поранених Героїв','uk',4896,6379,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6373,200,1409720881-9162-ukraina.jpg\" />Ми, сестра Василія, згромадження редемптористів, Василина та Марія Томишинець, парафіянки церкви св. Миколая Мирлікійського на Аскольдовій Могилі, відвідуємо поранених наших Герої у Київському клінічному військовому госпіталі на Печерську.</p>\n<p>Хочемо поділитись із Вами свідченнями Героїв, про Божу присутність під час перебування на війні, незламну Віру, справжній героїзм, Любов до Батьківщини та своїх побратимів. Ці свідчення скупі, без красномовства, але настільки глибокі і проникливі, що цю присутність Бога бачиш у їхніх очах, серцях, душах.</p>\n','2015-02-04 22:09:21','http://oranta.org/index.php?option=com_content&view=article&id=2609:rozpovid--spogady-pro-poranenyh-gerojiv&catid=29:propovidi-cat&Itemid=34','P','','Розповідь – спогади про поранених Героїв','uk',4891,6374,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6377,200,Fotoli.jpg\" />Великий піст попереджують підготовчі неділі і седмиці. Служби підготовчих неділь і самого Великого посту містяться в Посній Тріоді. Вона починається від Неділі про Митаря і Фарисея і закінчується Великою суботою, обіймаючи 70-денний період.</p>\n<p>Передують Великому постові – святій Чотиридесятниці – Неділя про Митаря і Фарисея, Неділя і седмиця про Блудного сина, Неділя і седмиця М’ясопусна (м’ясо-пуст), Неділя і седмиця Сиропусна (сиро-пуст, сирна, масляна).</p>\n','2015-02-08 21:28:47','http://oranta.org/index.php?option=com_content&view=article&id=2613:prygotuvannja-do-velykogo-postu&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Приготування до Великого Посту','uk',4895,6378,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6388,200,hram-ato.jpg\" />Покликання духовної особи полягає винятково в духовній, релігійній площині. Священик не може бути активним учасником бойових дій, не може використовувати зброї, пам’ятаючи, що священича зброя – насамперед духовна: «…правда, справедливість, ревне проповідування Євангелія миру, непохитна як щит віра і меч духовний, тобто Слово Боже» (пор. Еф. 6, 13–17).\n','2015-02-12 23:12:59','http://oranta.org/index.php?option=com_content&view=article&id=2617:2015-02-12-21-14-03&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Священик не може бути активним учасником бойових дій»','uk',4906,6389,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1345036212_bible.jpg\" />15 лютого Христова Церква святкує Стрітення ГНІХ. В основу свята лягла подія зустрічі праведника Симеона та пророчиці Анни із Немовлятком Ісусом на сороковий день по Його народженні.</p>\n<p>У нашій церковній традиції цього дня прийнято йти до храму зі свічками та їх там освячувати. Про це розповідає владика Венедикт, голова Патріаршої літургійної комісії УГКЦ.\n','2015-02-15 21:25:02','http://oranta.org/index.php?option=com_content&view=article&id=2621:2015-02-15-19-29-26&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Стрітенська свічка – знак Христового світла…» – владика Венедикт на Стрітення ГНІХ','uk',4911,6394,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1393670913.jpg\" />Ця молитва потрапила до мене кілька років тому російською мовою, натхненний її словами я тоді переклав її українською. Я свідомий, що переклад недосконалий. Сьогодні про неї згадав і вирішив опублікувати в соцмережах, але подумав, що більше людей зможуть її прочитати, якщо вона буде опублікована тут. Свого часу ця молитва мене дуже вразила. Часом я її перечитую. Читаючи її перший раз, я ледь не плакав. Не пожалійте кілька хвилинок свого часу, щоб видрукувати її і ввечері перед сном, у тиші серця, прочитати. Мені цікаво було читати її саме з листка, а не з монітору. Цим дописом я водночас хочу попросити прощення в кожного, хто його прочитає.</p>\n','2015-02-22 15:01:17','http://oranta.org/index.php?option=com_content&view=article&id=2637:molytva-proshchennja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Молитва прощення','uk',4936,6419,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,1393670913.jpg\" />Ця молитва потрапила до мене кілька років тому російською мовою, натхненний її словами я тоді переклав її українською. Я свідомий, що переклад недосконалий. Сьогодні про неї згадав і вирішив опублікувати в соцмережах, але подумав, що більше людей зможуть її прочитати, якщо вона буде опублікована тут. Свого часу ця молитва мене дуже вразила. Часом я її перечитую. Читаючи її перший раз, я ледь не плакав. Не пожалійте кілька хвилинок свого часу, щоб видрукувати її і ввечері перед сном, у тиші серця, прочитати. Мені цікаво було читати її саме з листка, а не з монітору. Цим дописом я водночас хочу попросити прощення в кожного, хто його прочитає.\n','2015-02-22 15:01:17','http://oranta.org/index.php?option=com_content&view=article&id=2637:molytva-proshchennja','P',NULL,'Молитва прощення','uk',4929,6412,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,30613_1.jpg\" />У церковно-народній пісні співаємо так: \"В єдності сила народу: Боже, нам єдність подай\". Співаємо ці слова від щирого серця. Однак в автора цього матеріалу виникають сумніви, чи ті, хто співає, мають чітке уявлення, що таке єдність народу.</p>\n<p>Не претендуючи на вичерпне пояснення цих слів, хочу все ж таки заохотити віруючих і невіруючих співвітчизників задуматися над їх змістом. Це важливо з двох причин: віруючі українці мають розуміти, про що моляться, а всі громадяни країни повинні знати, що робити, аби здійснити єдність нашого народу.</p>\n','2015-02-14 16:06:01','http://oranta.org/index.php?option=com_content&view=article&id=2619:my-peremozhemo-koly-budemo-jedyni&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Ми переможемо, коли будемо єдині','uk',4909,6392,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6391,200,maxresdefault.jpg\" />Дорогі друзі, брати і сестри християни, хочу прозвітувати Вам про волонтерську роботу &nbsp;Греко-католицької громади Церкви СВ. Миколая на Аскольдовій могилі.</p>\n<p>З жовтня місяця ми надаємо допомогу нашим воїнам на Сході України. Спочатку до нас, через Олену Нагорну звернулися бійці, що вийшли з-під Ілловайська і знаходилися у вишкільному таборі біля Броварів, готуючись знову повернутися на війну. Хлопці фактично не мали нічого, бо деякі, що виходили&nbsp; з оточення залишилися&nbsp;&nbsp; фактично навіть без одягу, не те, що без зброї, в прямому розумінні « голодні і босі».</p>\n','2015-03-08 21:23:14','http://oranta.org/index.php?option=com_content&view=article&id=2661:dopomagamo-zahysnykam-nashoji-vitchyzny&catid=25:parafija-cat&Itemid=27','P','','Допомагаймо захисникам нашої вітчизни','uk',4963,6446,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6395,200,339226.jpg\" />Ідеальною формою співжиття між людьми та державами у цьому світі є мирне співіснування. Проте жорстока реальність наполегливо переконує нас, що це практично неможливо. Вся&nbsp; історія людства позначена постійними військовими конфліктами. Блаженний Августин у свій час зазначав, що в цьому порочному світі війна – це реальність неминуча. Він навіть вбачав у ній певного роду Божественне покарання за гріх.</p>\n<p><strong><em>Справедлива війна</em></strong></p>\n<p>Багато осіб як з церковного середовища, так і непрактикуючих чи невіруючих у різні епохи завжди бачили війну як зло, проте також вважали деякі війни виправданими, бо саме завдяки таким війнам, як це не парадоксально звучить, було можливо досягнути миру, відбивши напад агресора, відновивши справедливість та направивши заподіяну шкоду. Такі війни називали «справедливими».</p>\n','2015-02-17 13:37:33','http://oranta.org/index.php?option=com_content&view=article&id=2623:2015-02-17-11-46-01&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Мир», що вбиває справедливість, та війна, яка стоїть на її захисті?!','uk',4913,6396,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,fd60f77-paralax-1.jpg\" />Platfor.ma спільно з&nbsp;<a href=\"http://kmbs.ua/uk\" target=\"_blank\">Києво-Могилянською бізнес-школою</a>започатковує&nbsp;новий спецпроект «Changers» про тих, хто змінює світ довкола себе.&nbsp;Перший наш герой – почесний президент Києво-Могилянської академії, громадський діяч, учасник групи “Першого грудня”, літературознавець та Герой України В’ячеслав Брюховецький, який став головним ініціатором відродження Могилянки як сучасного вищого навчального закладу.Platfor.ma&nbsp;поговорила з ним про те, як реформувати вищу школу, що робити з Донбасом і що Україна може запропонувати світу.</p>\n','2015-02-20 12:16:47','http://oranta.org/index.php?option=com_content&view=article&id=2629:2015-02-20-10-19-22&catid=37:spilnota-cat&Itemid=49','N','','В’ячеслав Брюховецький: «Покоління, що змінить країну, вже приходить»','uk',4921,6404,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6402,200,fd60f77-paralax-1.jpg\" />Platfor.ma спільно з&nbsp;<a href=\"http://kmbs.ua/uk\" target=\"_blank\">Києво-Могилянською бізнес-школою</a>започатковує&nbsp;новий спецпроект «Changers» про тих, хто змінює світ довкола себе.&nbsp;Перший наш герой – почесний президент Києво-Могилянської академії, громадський діяч, учасник групи “Першого грудня”, літературознавець та Герой України В’ячеслав Брюховецький, який став головним ініціатором відродження Могилянки як сучасного вищого навчального закладу.Platfor.ma&nbsp;поговорила з ним про те, як реформувати вищу школу, що робити з Донбасом і що Україна може запропонувати світу.\n','2015-02-20 12:16:47','http://oranta.org/index.php?option=com_content&view=article&id=2629:2015-02-20-10-19-22&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'В’ячеслав Брюховецький: «Покоління, що змінить країну, вже приходить»','uk',4920,6403,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6406,200,46446.jpg\" />Піст в практиці Церкви існує від самих початків. Але з бігом часу кожна із конфесій встановила свою практику посту. Слід однак пам’ятати, що основна мета посту – духовна, тобто, насамперед, потрібно постити від гріха, злих схильностей. У дні посту необхідно стримуватись від лихих діл, думок, слів; зберігати погідність духу, внутрішній мир та радість. Стримування від&nbsp;гріха набагато важливіше ніж стримування від їжі, хоча це також необхідне та допомагає поборювати гріх, вправлятись у духовному зрості, духовному житті. Нам же потрібно пам’ятати та виконувати і одне і друге.</p>\n','2015-02-21 12:19:13','http://oranta.org/index.php?option=com_content&view=article&id=2633:pravyla-postu-v-ukrajinski-greko-katolycki-cerkvi&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Правила посту в Українській Греко-Католицькій Церкві','uk',4925,6408,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6409,200,OSSROM17663_Articolo.jpg\" />Ваше Блаженство, Ваше Високопреосвященство,</p>\n<p>Дорогі Браття в єпископстві,</p>\n<p>щиро вітаю вас у цьому домі, який є також і вашим домом. Вам добре про це відомо, тому що Наступник святого Петра завжди з братньою приязню приймав братів з України, країни, яка по праву вважається пограниччям між спадкоємцями святих Володимира й Ольги та святого Адальберта й великих каролінгських місій, але також і тих, які посилаються на святих Кирила й Методія, Апостолів Слов’ян. Але ще й перед цим, існують передання, частково задокументовані, які згадують про святого Апостола Андрія і двох Папів-мучеників – святих Климента та Мартина І. Отож, щиро вас вітаю, найдорожчі!</p>\n','2015-02-21 20:29:59','http://oranta.org/index.php?option=com_content&view=article&id=2635:promova-papy-francyska-do-jepyskopiv-ukrajiny-z-vizytom-ad-limina-apostolorum-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Промова Папи Франциска до єпископів України з візитом ad limina apostolorum ','uk',4927,6410,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6414,200,unnamed.jpg\" />23 лютого розпочався Великий піст – період, коли християни особливо роздумують над земним життя Ісуса Христа та Його страстями. Цьогорічний піст для України буде особливим. Владика Венедикт, голова Патріаршої Літургійної комісії УГКЦ, розповідає про те, як правильно провести великопісний час українцям.\n','2015-02-23 23:02:47','http://oranta.org/index.php?option=com_content&view=article&id=2641:2015-02-23-21-05-22&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Владика Венедикт: «Під час посту починаймо з малих речей, щоб вони були сталі, а тоді прийдемо до великих»','uk',4933,6416,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6423,200,2EZi_AOsfmI.jpg\" />08 лютого 2015 р., о. Ігор, нам передав в’язані шкарпетки від бабці Марії із Карпат для поранених хлопців.</p>\n<p>Наша екуменічна спільнота, Олександр, Василина і Марія, вперше вирішили завітати до Психіатричного відділення в військовому шпиталі на Печерську.</p>\n<p>Подзвонили ми в дзвіночок (там всі двері закриті на ключ), до нас вийшов черговий лікар не представився, проте спитав хто ми такі, попросив показати посвідчення Волонтерів, на що ми відповіли, що у нас ніяких посвідчень не має, хіба що можемо показати паспорт, врешті нас пропустили всередину. Ми попросили запросити до нас чотирьох хлопців (були чотири пари шкарпеток). До нас вийшли три хлопці: Валерій, Павло і Юрій, від шкарпеток відмовились, попросили передати на війну бо там вони насправді дуже потрібні, а в лікарні у них все є.</p>\n','2015-02-25 22:24:26','http://oranta.org/index.php?option=com_content&view=article&id=2647:nevidomy-shljah-chy-shljah-u-nevidomosti&catid=29:propovidi-cat&Itemid=34','P','','Невідомий шлях, чи шлях у невідомості…','uk',4941,6424,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6429,200,1.jpg\" />«Інваліди та люди літнього віку, які потребують засобів реабілітації і засобів пересування отримали одно та чотириопорні палички, ходунки, підлокітні та пахові милиці, інвалідні візки. Разом – це 315&nbsp;шт. Така можливість виникла завдяки багатьом благодійникам з України та з поза її меж, які допомогли коштами, або ж і самими засобами реабілітації», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n','2015-03-02 23:00:45','http://oranta.org/index.php?option=com_content&view=article&id=2653:2015-03-02-21-02-57&catid=29:propovidi-cat&Itemid=34','P',NULL,'БФ «Карітас-Київ» інвалідам та людям літнього віку надав засоби реабілітації','uk',4947,6430,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6449,200,6546.jpg\" />Via&nbsp;crucis,&nbsp;via&nbsp;dolorosa,&nbsp;via&nbsp;cruz, Хресна дорога…&nbsp;Ці імена використовуються на означення зображень, чи серії скульптурних форм, які відображають хресний шлях Спасителя, Ісуса Христа, на Голгофу. Ці зображення, а також особливі народні богослужіння та традиції, які ми зустрічаємо в яскравому розмаї за часів Середньовіччя, говорять про особливе співпережиття Христовим мукам, в цей час. Але перш ніж говорити про доконані, випрацювані, форми, знаної всім нам, Хресної дороги, слід піти далеко в історію, щоб побачити корені, де вона народилася, та поступовий розвиток, яким вона йшла впродовж кількох століть.</p>\n<p><strong>Що таке «стація»?</strong></p>\n','2015-03-10 19:59:56','http://oranta.org/index.php?option=com_content&view=article&id=2665:via-srucis--zarodzhennja-ta-rozvytok-hresnoji-dorogy&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Via Сrucis – зародження та розвиток Хресної дороги','uk',4967,6450,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6461,200,kap_b.jpg\" />Капелан «Правого сектору» отець Петро Бурак на війні майже рік, з 24 квітня 2014 року. Вдома його чекають шість дітей і дружина, але змінювати камуфляж на цивільний одяг він поки що не збирається. Приїздить тільки в короткі відпустки і стверджує, що на фронті більш потрібний. Зустріти його там справді легше ніж у Києві. Хоча побачити панотця УГКЦ у центрі столиці таки можна – наприклад, під час зустрічі з муфтієм, з яким він домовляється про молитовні мусульманські книжки для своїх підопічних вояків, що сповідують іслам.\n','2015-03-17 13:22:00','http://oranta.org/index.php?option=com_content&view=article&id=2671:rezervy-ukrajinskoji-naciji-nevycherpni&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Резерви української нації невичерпні','uk',4979,6462,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6465,200,31006047.jpg\" />Дорогі колеги!</p>\n<p>До вас звертаються члени Ініціативної групи «Перше грудня» – громадські та релігійні діячі, науковці, правозахисники, представники освітніх та літературно-мистецьких кіл, що стоять на позиціях духовного демократичного відродження України. Ми є неурядовою організацією, не належимо до політичних партій і не представляємо органи влади України.</p>\n<p>Звернутися до вас спонукають нас трагічні події в нашій країні.</p>\n','2015-03-21 21:16:35','http://oranta.org/index.php?option=com_content&view=article&id=2675:2015-03-21-19-19-51&catid=37:spilnota-cat&Itemid=49','P',NULL,'Звернення учасників Ініціативної групи «Першого грудня» до зарубіжних колег','uk',4983,6466,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6468,200,4PigbWl5.jpg\" />Десятигодинні відвідини Неаполя Папа Франциск закінчив зустріччю із молоддю, яка відбулася на морській набережній, де зібралося щонайменше 100 тисяч неаполітанців. Серед учасників зустрічі була також українська громада південноіталійського регіону Кампанії. Згідно з бажанням Святішого Отця, зустріч відбулася у формі діалогу, під час якого свої запитання поставили представники молоді, 94-річна жінка та подружня пара.</p>\n','2015-03-22 22:08:03','http://oranta.org/index.php?option=com_content&view=article&id=2677:papa-pro-bozhe-movchannja-nakrashchi-liky-dlja-dovgolittja-i-zagrozy-dlja-simji&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Папа про Боже мовчання, найкращі ліки для довголіття і загрози для сім’ї','uk',4986,6469,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6470,200,magik.jpg\" />Слово \"культура\" останнім часом на устах багатьох людей.</p>\n<p>Різні люди по-різному підходять до цієї тематики. Більшість тих, хто зацікавлений культурою, розуміючи її важливість у житті народу, нарікають, що державна влада дуже мало уваги присвячує культурі, а головний їхній аргумент – це нікчемний відсоток державного бюджету, виділений на сферу культури.</p>\n<p>Це, напевно, правда, бо відповідальні за інститути культури особи досить голосно говорять про брак фінансової підтримки.</p>\n','2015-03-23 19:41:49','http://oranta.org/index.php?option=com_content&view=article&id=2679:jake-znachennja-maje-kultura-dlja-nashogo-zhyttja&catid=37:spilnota-cat&Itemid=49','P',NULL,'Яке значення має культура для нашого життя?','uk',4988,6471,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6479,200,20150324_084723[1].jpg\" />Цьогоріч традиційний зимовий відпочинок парафіян церкви св. Миколая Чудотворця на Аскольдовій могилі був вдало перенесений на початок весняних&nbsp; канікул у школах (20.03.-25.03.2015 р.) &nbsp;&nbsp;Ще в п»ятницю 20 березня діти сиділи за партами, а уже по обіді – в потязі прямували до Закарпаття. &nbsp;18 годин у дорозі&nbsp; і… наче чарівна паличка перенесла нас із весняного Києва на засніжену гору Драгобрат.&nbsp; Справжній сніг, легкий морозець, чудовий краєвид, задимлений туманом, яскраве сонечко…&nbsp; Здавалось, наче гори з»єднані з небом. Дивишся на цю природу і переконуєшся: «Який талановитий творець - художник створив цей світ!»</p>\n','2015-03-30 17:34:43','http://oranta.org/index.php?option=com_content&view=article&id=2687:zymovy-vidpochynok-u-vesnjani-dni&catid=25:parafija-cat&Itemid=27','P',NULL,'Зимовий відпочинок у весняні дні!','uk',4999,6482,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6492,200,erusalim.jpg\" />Події Месіанського в’їзду Христа в Єрусалим освітлюють нам усі чотири євангелисти: Матей (Мт. 21,1–11); Марко (Мк. 11,1–11); Лука (Лк. 19,28–40); Іван (12,12–19). Кожен з них подає цю подію історії Спасіння під своїм кутом бачення, вносячи певні деталі, на які інші автори не звернули такої уваги.</p>\n<p>Усі вони однозгідні, що шлях Ісуса в Єрусалим пролягав через Оливну гору, ту саму гору, на якій за кілька днів у Гетсиманському саду Ісус буде молити Отця про чашу і де Його через зраду Юди заарештують (Мт. 26,36–56// Мк. 14,32–52// Лк. 22,39–53//&nbsp; Ів.18,1–11). Ця гора наприкінці часів розколеться надвоє, бо Бог стоятиме на ній (див. Зах. 14–1–11). Розколеться, щоб пропустити живу воду з Єрусалиму до Мертвого моря і так оздоровити його (див. Ез.47,1–12). Про цю воду і потік сповіщає книга Одкровення (див. Одкр. 22,1–2).</p>\n','2015-04-04 23:10:14','http://oranta.org/index.php?option=com_content&view=article&id=2698:mesiansky-vjizd-isusa-v-jerusalym-rozdumy-na-kanvi-kvitnoji-nedili&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Месіанський В’їзд Ісуса в Єрусалим (роздуми на канві Квітної Неділі)','uk',5011,6494,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6497,200,78578757.jpg\" />Кілька днів тому були надруковані наші роздуми&nbsp;&nbsp;<a href=\"http://www.credo-ua.org/2015/03/131561\">«Яке значення має культура для нашого життя?»</a>. Мова йшла про загальну культуру, про все те, що становить надбання народу в ділянці краси.</p>\n<p>Такі надбання охоплюють широкий спектр людської творчості: літературу, музику, образотворче мистецтво та інші гарні речі, пізнаючи, оглядаючи та слухаючи які, ми ушляхетнюємося і дістаємо велику естетичну насолоду.</p>\n','2015-04-06 11:37:16','http://oranta.org/index.php?option=com_content&view=article&id=2700:rozvyvajuchy-osobystu-kulturu-my-budujemo-novu-ukrajinu&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Розвиваючи особисту культуру, ми будуємо нову Україну','uk',5015,6498,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6503,200,zaproshennya.jpg\" />Високопреосвященним і Преосвященним владикам, всесвітлішим, всечеснішим і преподобним отцям, преподобним ченцям і черницям, дорогим у Христі мирянам Української Греко-Католицької Церкви</p>\n<p align=\"center\">Христос воскрес!</p>\n<p align=\"right\"><em> На божественній сторожі</em><em> </em><em>Богонатхненний Авакум хай стане з нами</em><em> </em><em>І покаже світлоносця-ангела, що ясно звіщає:</em><em> </em><em>Нині спасіння світові, бо воскрес Христос як всемогутній.</em><em> </em>Пісня 4 Канону Пасхи</p>\n','2015-04-08 22:34:26','http://oranta.org/index.php?option=com_content&view=article&id=2707:velykodnje-poslannja-blazhennishogo-cvjatoslava&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Великоднє послання Блаженнішого Cвятослава','uk',5022,6505,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6505,200,09-04-15-1.jpg\" />Сьогодні ми покликані особливо молитися за єдність Христової Церкви в Україні, бо це складова частина архиєрейської молитви Ісуса Христа, який молиться до Отця: Боже, збережи їх, щоб вони були одно, одно як Я в Тобі, а Ти в Мені.</p>\n<p>Про це сьогодні, у Великий Четвер, сказав Глава Української Греко-Католицької Церкви до вірних в Патріаршому Соборі Воскресіння Христового.</p>\n','2015-04-10 08:42:06','http://oranta.org/index.php?option=com_content&view=article&id=2709:2015-04-10-05-45-42&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Чувайте і моліться, щоб не ввійти у спокусу!»','uk',5024,6507,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,30079.jpg\" /><strong>&nbsp;</strong></p>\n<div style=\"text-align: center;\"><strong><strong style=\"font-size: 0.875em;\">Христос Воскрес!</strong></strong></div>\n<p> </p>\n<p style=\"text-align: justify;\">Приступім зі свічками до Христа, що виходить із гробу, немов ідучи назустріч женихові,</p>\n<p>і радісно з усіма прославляймо Пасху Божу спасительну.</p>\n<p>Від раннього ранку чуваймо і, замість мира, пісню принесім Владиці, і Христа побачимо — правди Сонце, що всім життя освітлює.</p>\n','2015-04-11 20:56:59','http://oranta.org/index.php?option=com_content&view=article&id=2716:zi-svitlym-praznykom-voskresinnja-hrystovogo&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Зі світлим празником Воскресіння Христового!','uk',5032,6515,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6509,200,DSC_0993.JPG\" />Празник Воскресіння Христового ще називають Святом Пасхи чи Великдень від того що це дійсно «Великий день». Слово «пасха» означає (з єврейської мови) «перехід» і «ягня». Свято Пасхи святкували на спомин визволення вибраного народу Божого з Єгипетської неволі, а для нас християн – символізує перехід Ісуса Христа: від життя до смерті й від смерті до життя.</p>\n<p>Ці святкування Великодня не завершуються лише Неділею, а продовжуються у так звані «Світлий Понеділок» і «Світлий вівторок».</p>\n','2015-04-13 01:06:29','http://oranta.org/index.php?option=com_content&view=article&id=2718:svitly-ponedilok-tradycija-svjatkuvannja&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Світлий понеділок: традиція святкування','uk',5034,6517,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6518,200,teodor_b.jpg\" />14 квітня 1911 народився один з найвидатніших єпископів греко-католицької Церкви на Закарпатті, убитий за наказом Сталіна Георгій Теодор (Феодор) Ромжа (1911-1947), титулярний єпископ Аппійскій, адміністратор, згодом єпископ Мукачівської греко-католицької єпархії. Беатифікований Римсько-католицькою церквою в 2001 році.</p>\n<p>Народився в Надьбочко, Підкарпаття (нині Великий Бичків, Закарпатська область) в родині залізничного службовця Павла Ромжі, мати - Марія Семак. Був дев\'ятою дитиною в сім\'ї. Чотири класи народної школи Федір закінчив у Бичкові, в 1922 році батьки визначили його в державну реальну гімназію в місті Хуст, яку він з відзнакою закінчив у 1930 році.</p>\n','2015-04-15 22:55:43','http://oranta.org/index.php?option=com_content&view=article&id=2720:jepyskop-teodor-romzha&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Єпископ Теодор Ромжа','uk',5036,6519,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6527,200,34544.jpg\" />Коментуючи біблійну розповідь про каменування святого Стефана, Папа Франциск зі зворушенням згадав про всіх тих, які сьогодні зазнають переслідувань та смерті лише за те, що вони – християни. Проповідуючи під час Святої Меси він зазначив, що існують також «приховані мученики», які шукають шляхів для допомоги ближнім, зазнаючи переслідувань з боку «сучасних Синедріонів».</p>\n','2015-04-21 21:53:19','http://oranta.org/index.php?option=com_content&view=article&id=2728:-papa-nasha-cerkva--ce-cerkva-muchenykiv&catid=32:gk-svit-cat&Itemid=58','P',NULL,' Папа: Наша Церква – це Церква мучеників','uk',5046,6529,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6533,200,svichi.jpg\" />Вони грають у теніс і співають під гітару. На сніданок у них еспрессо та вівсянка. Читають Пелевина та Айн Ренд, розбираться в дискурсах та у відтінках сірого. Ходять на Книжкові Арсенали та на прем’єри у кінотеатрі «Київ». Вільно володіють англійською. Розуміються на тонкощах економічної теорії та геополітичних планах російських і світових еліт. У них майже похвилинно розписаний цілий тиждень і половина життя: вони знають ціну свого часу. Слухають оперу у Ла Скала, бігають марафони у Бостоні, смакують вишукані тістечка на Монт-Мартрі. Люблять лижі. Вільно плавають у блогосфері. Намагаються підтримувати баланс поміж працею та відпочинком.</p>\n','2015-04-29 23:26:06','http://oranta.org/index.php?option=com_content&view=article&id=2732:pro-separatyzm-duhovny&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Про сепаратизм духовний','uk',5051,6534,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6535,200,796.jpg\" />На Аскольдовій могилі серед інших духовних цінностей відтепер зберігається ікона, виготовлена в окупованій Макіївці й передана бійцям української армії. Про це Vysnovky.com повідомили у штабі Волонтерського об\'єднання \"Патріот\".</p>\n<p>Історія цієї ікони унікальна. Образ виготовив один із в’язнів Макіївської виправної колонії і передав його для бійців української армії через своїх знайомими, які виїжджали з окупованої території. Так ікона потрапила до рук колишніх бійців батальйону \"Донбас\", які зараз стали до лав 81-ї десантно-штурмової бригади.</p>\n','2015-05-05 17:17:52','http://oranta.org/index.php?option=com_content&view=article&id=2734:ikona-z-okupovanogo-donbasu&catid=25:parafija-cat&Itemid=27','P','','Ікона з окупованого Донбасу','uk',5053,6536,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6539,200,c12ghjklas.png\" />«Той, хто здійснює місію Карітасу, той не є просто виконавцем, але – свідком Христа».</p>\n<p>Коментуючи біблійну розповідь про те, як апостоли Павло і Сила були покарані бичуванням та вкинені до в’язниці у Филиппах, але вночі зчинився землетрус і відчинилися всі двері, через що наглядач хотів вчинити самогубство, але стриманий Павлом здобув ласку навернення, Святіший Отець звернув увагу на те, що цей чоловік відразу вчинив «суттєві кроки шляху віри та спасіння». Тобто, «разом із рідними вислухав Боже слово, обмив рани Павла та Сили, прийняв Хрищення з усіма домашніми і запросив Павла та Силу до свого дому, приготував їм трапезу».</p>\n','2015-05-13 22:05:01','http://oranta.org/index.php?option=com_content&view=article&id=2738:karitas--ne-prosto-gumanitarna-organizacija-ale--svidchennja-hrysta&catid=29:propovidi-cat&Itemid=34','P',NULL,'Карітас – не просто гуманітарна організація, але – свідчення Христа','uk',5057,6540,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6557,200,u_t.jpg\" />«Українці прагнуть посісти в Європі чільне місце як християнський народ», - сказав Отець і Глава Української Греко-Католицької Церкви Блаженніший Святослав у інтерв’ю польському виданню «В мережі». «Україна усвідомлює сьогодні свою європейську ідентичність, своє місце в сім\'ї європейських народів», – зазначив він.</p>\n<p>«Коли ми говоримо про європейську свідомість України, то маємо на увазі передусім певні внутрішні процеси, що відбуваються в нашій країні, – наголосив Предстоятель УГКЦ. – Адже іскрою, яка викликала Революцію гідності, було рішення президента Януковича не підписувати угоду про Асоціацію з Європейським Союзом».</p>\n','2015-05-29 22:02:51','http://oranta.org/index.php?option=com_content&view=article&id=2763:2015-05-29-19-05-11&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«ЄС може існувати без України, але Європа не може, бо буде неповна»','uk',5075,6558,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6573,200,shutterst.jpg\" />Часто чую, або читаю одне речення, яке постійно з’являється в розмовах на різні можливі теми, а це – «я католик, але..». І після цього «але», зазвичай, йде твердження повністю протилежне з вченням Католицької Церкви.</p>\n<p>Здається, що перша частина («я католик, але..») слугує приготуванням, амортизацією, чи щоб наголосити сучасну потребу виразити переконання, яке різниться від вчення Церкви, щоб показати, що ти не «баран», маєш свої переконання і не йдеш сліпо, як більшість, яка вірить в те що в неї засаджено.</p>\n','2015-06-03 18:10:49','http://oranta.org/index.php?option=com_content&view=article&id=2769:2015-06-04-19-12-05&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Я католик, але…»','uk',5092,6575,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://askoldova-mohyla.org/uk/image,6583,200,152862.jpg\" />У неділю 7 червня 2015 р., в багатьох країнах, серед яких і в Італії, святкували урочистість Найсвятішого Тіла і Крові Христових,&nbsp; відому за латинським висловом як&nbsp; урочистість «Corpus Domini» – Тіла Господнього, що була перенесена з четверга. Тому головною темою роздумувань Святішого Отця&nbsp; перед прочитання молитви «Ангел Господній» з десятками тисяч прочан, зібраними на площі святого Петра у Ватикані, була саме Пресвята Євхаристія , що є кульмінацією усього життя Ісуса Христа, а також &nbsp;єзакликом до кожного з нас, щоб принести себе в жертву заради інших.</p>\n','2015-06-09 19:02:47','http://oranta.org/index.php?option=com_content&view=article&id=2776:presvjata-jevharystija--dzherelo-ljubovi-v-zhytti-cerkvy-ta-virnyh&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Пресвята Євхаристія – джерело любові в житті Церкви та вірних','uk',5102,6585,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/12_apostles2.jpg\" height=\"182\" width=\"249\" />Під сам кінець минулого тижня, 13 квітня, світ облетіла новина – <a href=\"http://catholicnews.org.ua/papa-priznachiv-komisiyu-yaka-dopomagatime-reformuvati-kuriy#point\">Папа створив Комісію з питань реформи Римської Курії</a>.&nbsp;  Черговий інформаційний привід. Стрічки новин знову сповнилися  заголовками про Ватикан, Церкву і Папу: «Папа іменував кардиналів, які  реформуватимуть Церкву», «Папа перебудовує Церкву», «Кардинали  управлятимуть Церквою разом з Папою»…… У тому каламутному потоці зовсім  втратився правдивий зміст новини – йдеться про реформу Римської Курії і  не більше.</p>\n','2013-04-15 12:56:14','http://oranta.org/index.php?option=com_content&view=article&id=1113:pravdyvy-hrystyjanyn-zavzhdy-je-apostolom&catid=32:gk-svit-cat&Itemid=58','P',NULL,'ПРАВДИВИЙ ХРИСТИЯНИН ЗАВЖДИ Є АПОСТОЛОМ','uk',3158,4641,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/136496_massimo_introvigne_7.jpg\" height=\"120\" width=\"164\" />Про<strong> християнофобію</strong> і необхідність оборони визнавців <strong>Христа</strong> відомий соціолог і релігієзнавець Масімо Інтровіньє розмовляв з  російським агентством «Інтерфакс-Релігія». Інтровіньє був експертом ОБСЄ  у справах аналізу і протистояння расизму, антисемітизму та  дискримінації християн і визнавців інших релігій.</p>\n','2013-04-17 23:58:43','http://oranta.org/index.php?option=com_content&view=article&id=1121:hrystyjanofobija-vsesvitnja-zagroza&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Християнофобія - всесвітня загроза','uk',3169,4652,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/154.jpg\" height=\"149\" width=\"202\" /></p>\n<p>Упродовж тижня, від 6-го до 10-го березня, тривав «Львівський  псевдособор», покликаний ліквідувати Українську Греко-Католицьку Церкву.  На згадку про ці дні львівський Музей історії релігії організовує в  суботу, 9 березня, о 15-ій годині, вечір пам’яті.</p>\n<p>Виконавчий директор Інституту історії церкви кандидат історичних наук  Світлана Гуркіна розповіла&nbsp; кореспондентові Львівського радіо, як  готувався Львівський псевдособор.</p>\n','2013-03-09 00:00:25','http://oranta.org/index.php?option=com_content&view=article&id=1024:lvivsky-psevdosobor-drama-ugkc-u-berezni-1946-roku&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Львівський псевдособор – драма УГКЦ у березні 1946 року','uk',3006,4489,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/1_0_696176.jpg\" height=\"117\" width=\"160\" />Іти за Ісусом не означає мати більше влади, тому що Його дорога – це  хресна дорога. На це звернув увагу Папа Франциск. Святіший Отець  коментував Євангельський уривок про те, як святий Петро запитував Ісуса  про те, якою буде їхня винагорода за те, що учні все залишили та пішли  за Ним.</p>\n<p>Папа зауважив, що Христос відповів, що їх чекає «багато чудових речей»,  але також і переслідування, тому що Господня дорога – «це шлях  приниження, дорога, яка завершується на хресті». І якщо «християнин не  має труднощів у житті, якщо все йде добре», – додав він, – «то у цьому  щось негаразд», можна припустити, що він «заприятелював з духом світу».</p>\n','2013-05-28 17:49:04','http://oranta.org/index.php?option=com_content&view=article&id=1224:papa-francysk-zvishchennja-isusa-ce-ne-zovnishnja-prykrasa-ale-shchos-shcho-povynno-pronykaty-do-sercja-ta-zminjuvaty-nas&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа Франциск: Звіщення Ісуса – це не зовнішня прикраса, але щось, що повинно проникати до серця та змінювати нас','uk',3280,4763,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/c834502922.jpg\" height=\"152\" width=\"209\" /><strong>Він ухиляється від комп’ютера. Коли його питають про інтернет, то  він жартує, що почне ним користуватись вже на пенсії. Ще будучи  кардиналом, користувався електричною друкарською машинкою. Усі листи, що  надходили електронною поштою, йому роздруковували і передавали  секретарі.</strong></p>\n<p>Більшість давніх сусідів, якщо пам’ятають малого Хорхе, то не з  футбольного поля. Його запам’ятали з книжками, які він постійно з собою  носив.</p>\n','2013-05-22 13:58:34','http://oranta.org/index.php?option=com_content&view=article&id=1218:shcho-ljubyt-papa-pro-hobi-francyska&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Що любить Папа – про хобі Франциска','uk',3265,4748,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/derevo2.jpg\" height=\"182\" width=\"249\" />У межах проведення Великопосної ініціативи «Екологічне навернення для  порятунку створіння», Бюро УГКЦ з питань екології закликає долучитися 20  квітня до Всеукраїнської акції «Посади дерево», метою якої є вияв  конкретних діл милосердя стосовно своїх ближніх і всього створеного  Богом світу.</p>\n<p>Бог покликав нас «порати і доглядати» (Бут. 2, 15) своє створіння, бути  його добрими садівниками, примножувати отримані дари природи. Кожне  дерево Божого саду має дуже важливе значення: \n','2013-04-09 15:37:01','http://oranta.org/index.php?option=com_content&view=article&id=1103:2013-04-09-12-39-14&catid=37:spilnota-cat&Itemid=49','P',NULL,' Звернення Бюро УГКЦ з питань екології з приводу Всеукраїнської акції «Посади дерево» ','uk',3139,4622,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/dsc_8153.jpg\" height=\"126\" width=\"172\" />Минулотижнева заява Глави УГКЦ про те, що головною темою наступної  чергової сесії Синоду УГКЦ буде нова євангелізація могла б виглядати не  цікавою для ширшого загалу, виключно єрархічною темою, якби не дві речі.<br />Перше, це те, що Блаженніший Святослав чітко окреслив проблематику: «<a href=\"http://catholicnews.org.ua/blazhennishii-svyatoslav-%C2%ABtsentralnoyu-temoyu-tsogorichnogo-#point\">Ми  міркуватимемо над тим, як більш ефективно проповідувати Боже Слово в  сучасній культурі.</a></p>\n<p><a href=\"http://catholicnews.org.ua/blazhennishii-svyatoslav-%C2%ABtsentralnoyu-temoyu-tsogorichnogo-#point\"></a></p>\n','2013-06-10 23:57:13','http://oranta.org/index.php?option=com_content&view=article&id=1245:tyzhden-ukrajinskogo-hrystyjanstva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Тиждень українського християнства','uk',3321,4804,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/papa_v_laterani.jpg\" height=\"182\" width=\"249\" />У середу, минулого тижня, Святіший Отець Франциск закликав католиків  перестати боятися і вийти з затвору церковних стін, куди їх загнав  секуляризм з його псевдотолерантною політкоректністю: «<a href=\"http://catholicnews.org.ua/promova-papi-frantsiska-na-zagalnii-audiyentsiyi-3042013r-po#point\">Спробуймо  відчувати радість від того, що ми є християнами! Ми віримо у  Воскреслого, Котрий подолав зло і смерть! Маймо також сміливість «вийти  назовні», щоб нести цю радість і світло в усі куточки нашого життя!  Воскресіння Христа є нашою найбільшою впевненістю, це – наш найцінніший  скарб!</a></p>\n','2013-04-09 22:50:37','http://oranta.org/index.php?option=com_content&view=article&id=1104:papa-zaklykav-katolykiv-vyty-z-okopiv&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа закликав католиків вийти з окопів','uk',3140,4623,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size/news/pictures/uskrsnje-jaje.gif\" height=\"132\" width=\"181\" />Христове Воскресіння, як історичний факт, сталося на перший по суботі  день, тобто у неділю. Тому християни почали святкувати цей день –  неділю.</p>\n<p class=\"rtejustify\">Для євреїв часів Ісуса число 8 (вісім) символізувало настання нових  месіанських часів. Тому часто неділю у старохристиянських текстах  називали і восьмим днем тижня, тобто месіанським.</p>\n','2013-05-05 23:05:11','http://oranta.org/index.php?option=com_content&view=article&id=1164:cikavi-fakty-pro-velykden&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Цікаві факти про Великдень','uk',3221,4704,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/1_0_683329.jpg\" height=\"249\" width=\"182\" />У суботу, 13 квітня, кардинал Анджело Амато, Префект Конгрегації у  справах святих, в базиліці святого Марка у Венеції, проголосив блаженним  отця Луку Пассі, який був ревним свідком любові Христа до найубогіших  та найбідніших дітей. У 1838 році він започаткував спільноту мирянок,  яка називається «Pia Opera di Santa Dorotea» (Милосердне Діло святої  Доротеї). Метою цієї спільноти була опіка, навчання та виховання бідних,  часто залишених своїми батьками, дівчат.</p>\n<p>Отець Лука народився в шляхетній венеціанській сім’ї, яка мешкала у м.  Берґамо (північ Італії). В 1807 році розпочинає навчання у семінарії.  Протягом того часу він тісно співпрацює з Марійською спільнотою, яка  діяла при парафії містечка Кальчінате (північ Італії).</p>\n','2013-04-16 20:21:49','http://oranta.org/index.php?option=com_content&view=article&id=1118:blazhenny-otec-luka-passi-svidok-ljubovi-hrysta-do-syrit&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Блаженний отець Лука Пассі - свідок любові Христа до сиріт','uk',3163,4646,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://catholicnews.org.ua/sites/default/files/imagecache/main_size_vertical/news/pictures/da-benedetto-a-francesco.jpg\" height=\"133\" width=\"97\" />Минуло вже два місяці з часу обрання на кафедру апостола Петра  Святішого Отця Папу Франциска; і цей короткий відрізок часу можна по  праву назвати плідним та незвичайним. З цієї нагоди книжкові полиці  поповнила нова книга отця Антонія Спадаро, директора всесвітньовідомого  католицького журналу «Католицька Цивілізація» (<em>La</em> <em>Civilt</em><em>à </em><em>Cattolica</em>), під назвою «Від Венедикта до Франциска».</p>\n','2013-05-15 09:26:59','http://oranta.org/index.php?option=com_content&view=article&id=1201:2013-05-15-06-29-57&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Від Бенедикта до Франциска» – книга, яка додає оптимізму','uk',3239,4722,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://cerkva.info/images/stories/2011/03/patriarch_tarasios.jpg\" alt=\"patriarch_tarasios\" width=\"121\" height=\"200\" /></p>\n<p>Святитель  Тарасій народився в Константинополі. Батьки його були люди благородні і  належали до патриціїв. Здобувши прекрасну освіту, святитель Тарасій  швидко підносився сходинками почестей. За розсудливість і добру вдачу  майбутнього патріарха скрізь любили, де тільки він служив: і у царському  палаці, і в інших місцях. Він був вже царським радником і сенатором,  коли відбулося його призначення на патріаршу кафедру.<br /> <br /> Це сталося за таких обставин. Імператором в цей час був Константин  Порфірородний, а патріархом — Павло Кипрянин, чоловік добродійний і  благочестивий, але дуже слабовільний. На патріарший престол він був  поставлений за царювання попередника Костянтина Порфірородного – Льва  Хазара, при якому іконоборча єресь (гоніння на св. ікони), що отримала  початок ще за імператора Льва Ісаврянина, все більше посилювалася.  Будучи людиною слабохарактерною і слабовільною, патріарх Павло побоявся  відкрито визнати іконошанування, став приховувати своє благочестя і  почав навіть входити в контакт з єретиками.</p>\n','2011-03-10 15:16:42','http://oranta.org/index.php?option=com_content&view=article&id=687:svjatytel-tarasi-patriarh-konstantynopolsky&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Святитель Тарасій, патріарх Константинопольський','uk',2345,3828,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://dyvensvit.org/media/gallery/intxt/k/t/ktds10_synovic1010kyj.jpg\" /> Греко-католицька  громада завжди була і продовжує бути  активною в суспільному житті.  Брат Сергій Синовіцький навчається на  третьому курсі Київської  Трьохсвятительської Духовної семінарії,  випускники якої стають  священиками... Про греко-католицьку громаду,  важливість служіння,  доброї справи та щирого наміру ми поговорили з  братом Сергієм<strong>.</strong></p>\n<p><strong>- Сергію, що Вас покликало вчитися на священика?</strong></p>\n','2013-05-13 13:21:53','http://oranta.org/index.php?option=com_content&view=article&id=1198:greko-katolyky-vidkryti-dlja-vsih-brat-sergi-synovicky&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Греко-католики - відкриті для всіх! - брат Сергій Синовіцький','uk',3236,4719,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://glavred.info/images/b-00069543-a-00014906.jpg\" /> Ольга Годованець, фото автора, 30.04.09</p>\n<p><strong>На початку травня Українська греко-католицька церква запрошує на концерти академічної музики «З вірою і любов\'ю».</strong></p>\n<p><em>Івано-Франківськ, Тернопіль, Львів, Київ та Одеса готуються почути зірок світової та української оперної сцени Вікторію Лук\'янець, братів Петра та Павла Приймаків, Наталю та Галину Дацько, академічний камерний оркестр «Harmonia Nobile»...</em></p>\n<p>Цей проект благодійний. І кошти, що вдасться зібрати, підуть на завершення будівництва патріаршого собору Воскресіння Христового у столиці. «Але ми хочемо ці концерти подарувати нашій спільноті, тим, хто хоче, хто здібний цим скористатися, - каже Глава УГКЦ Кардинал Любомир Гузар. - Бо криза, крім фінансової, показала кризу духовну. Криз у світі, в житті людей, в історії разом з війнами було безліч. Але ця криза, яку ми переживаємо, хоча вона всесвітня, її дуже роздраматизовують. Але вона мине. І щоб не пірнути на дно, не втратити себе, треба вірити, що є щось триваліше, ніж та криза».</p>\n','2009-05-07 13:35:46','http://www.oranta.org/index.php?option=com_content&view=article&id=314:lljudy-sebe-runujut-bo-ne-majut-cili-u-zhyttir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Люди себе руйнують, бо не мають цілі у житті»','uk',1456,2512,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://i280.photobucket.com/albums/kk188/rozdum/myronosyci_2012_rozdum.jpg\" height=\"129\" width=\"164\" /><em>Вернувшись від гробу, про все те сповістили Одинадцятьох та всіх інших. Лк.24.9</em></p>\n<p>Дорогі браття і сестри у Христі, у неділю Мироносиць, читаючи  розповідь про вірних жінок-учениць Христа, згадуємо про вірних і  побожних жінок, послідовниць Ісуса Христа. Вони є прикладом живої віри в  живого воскреслого Спасителя-Месії. Повірили у Воскресіння Спасителя,  не побачивши Його тіла, перебороли страх нерозуміння і сповістили учням  цю радісну новину.</p>\n','2013-05-19 21:31:55','http://oranta.org/index.php?option=com_content&view=article&id=1206:nedilja-myronosyc&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Неділя мироносиць','uk',3250,4733,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://img.istpravda.com.ua/images/doc/4/2/42ef344-poxoron.jpg\" alt=\"\" width=\"209\" height=\"130\" /></p>\r\n<p><strong>Багато років поспіль через дивну та незрозумілу політичну кон\'юнктуру стверджується, начебто всі, хто побував під Крутами, загинули. Але це неправда! Крім тих, хто потрапив у радянський полон, на полі бою загинули ще 10-12 юнаків...</strong></p>\r\n<p>29 січня 1918 року на підступах до Києва, під станцією Крути, зустрілися українські підрозділи Центральної Ради та більшовицькі війська, які йшли з Росії. Проти 420 українських юнкерів, студентів та гімназистів виступало понад 4 тис. радянських вояків.</p>','2013-09-27 15:36:49','http://oranta.org/index.php?option=com_content&view=article&id=1508:zhyttja-pislja-krut-jak-sklalasja-dolja-uchasnykiv-sichnevogo-boju&catid=37:spilnota-cat&Itemid=49','P','','Життя після Крут. Як склалася доля учасників січневого бою','uk',3635,5118,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_663825.JPG\" /></p>\n<p><em>Під  час консисторії, тобто зібрання кардиналів, яка була скликана 11 лютого  2013 р. для затвердження трьох канонізаційних процесів, Святіший Отець  Венедикт XVI звернувся до присутніх кардиналів з наступною заявою:<br /></em><br />Найдорожчі брати,</p>\n<p>я  скликав вас на цю консисторію не лише з огляду на три канонізації, але  також, щоб повідомити вам про дуже важливе рішення в житті Церкви.</p>\n','2013-02-11 14:22:29','http://oranta.org/index.php?option=com_content&view=article&id=988:papa-venedykt-xvi-zriksja-z-urjadu-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа Венедикт XVI зрікся з уряду ','uk',2959,4442,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_684371.JPG\" />Щодня  у Службі Божій, яку Папа Франциск очолює в каплиці ватиканської  резиденції «Святої Марти», де він мешкає, беруть участь працівники  різних ватиканських інституцій. Наприклад у середу, 17 квітня, у  ранковій Євхаристійній молитві з Папою взяли участь працівники Інституту  Релігійних Справ, так званого, ватиканського банку. Звертаючись із  коротким словом до присутніх, Святіший Отець запропонував роздумати над  прочитаним того дня біблійним уривком з книги Діянь Апостолів, у якому  Богом натхнений автор описує життя першої громади християн, розповідаючи  про те, як послідовники Ісуса жили у мирі та любові та про перше  переслідування вірних, яке розпочалось після каменування диякона  Стефана. На думку Папи Франциска – це частково є стилем життя Церкви:  між миром любові і переслідуваннями.</p>\n','2013-04-19 20:01:24','http://oranta.org/index.php?option=com_content&view=article&id=1130:papa-francysk-lyshe-jevangelizujuchy-cerkva-staje-matirju&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Папа Франциск: Лише євангелізуючи, Церква стає Матір’ю','uk',3178,4661,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_684851.JPG\" height=\"147\" width=\"221\" />Літеплі  християни – це ті, які хотіли би будувати Церкву згідно зі своїми  мірками, але така Церква не є Ісусовою Церквою. На це звернув увагу Папа  Франциск, проповідуючи під час Святої Меси в каплиці резиденції «Дім  Святої Марти», яку він відслужив 20 квітня 2013 р. вранці.</p>\n<p>У  Діяннях Апостолів читаємо, що після переслідування в первісній  християнській громаді настав період миру, під час якого вона  зміцнювалася, та ходила і зростала в богобоязні та утішенні Святого  Духа.</p>\n','2013-04-20 21:05:37','http://oranta.org/index.php?option=com_content&view=article&id=1132:2013-04-20-20-10-05&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Папа Франциск: Подолати спокусу фальшивого «здорового глузду»','uk',3180,4663,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_685963.JPG\" /> «Людська історія розпочалася зі створенням  чоловіка та жінки на Божі образ і подобу, а завершиться остаточним  Христовим судом», – сказав Святіший Отець на початку катехизи,  пропонуючи цього разу замислитися над словами молитви <em>Вірую,</em> що Ісус «знову прийде в славі, щоб судити живих і мертвих».</p>\n<p>За  словами Папи, віра в другий Христовий прихід іноді не є дуже ясною та  переконливою в серці християн, однак, Ісус під час земного життя часто  говорив про дійсність цього приходу.</p>\n','2013-04-24 19:56:43','http://oranta.org/index.php?option=com_content&view=article&id=1143:dumka-pro-strashny-sud-neha-ne-bude-prychynoju-strahu-ale-sponukoju-stavaty-krashchymy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Думка про страшний суд нехай не буде причиною страху, але спонукою ставати кращими','uk',3194,4677,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_702605.JPG\" height=\"117\" width=\"177\" />«Я  не соромлюся Євангелія... Як охрищені, ви вже не під законом, а під  благодаттю». Ці слова святого Павла стали поштовхом для Папи Франциска,  щоб під час катехизи, виголошеної експромтом, поділитися з учасниками  церковного Конгресу Римської дієцезії своїми думками про життя Божою  благодаттю та про його зв’язок з ділом євангелізації. Своє слово  Святіший Отець виголосив під час відкриття триденного конгресу, що  відбулося 17 червня 2013 р. ввечері, в залі Павла VI у Ватикані.</p>\n','2013-06-18 22:41:33','http://oranta.org/index.php?option=com_content&view=article&id=1264:budte-vidvazhnymy-ta-terpelyvymy-svidkamy&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Будьте відважними та терпеливими свідками','uk',3353,4836,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_704616.JPG\" height=\"129\" width=\"194\" /></p>\n<p>Бути  християнами – це поклик любові, поклик стати Божими дітьми, наголосив Святіший Отець. «Коли я  читаю цей уривок, – сказав Єпископ Риму, – думаю про Близький Схід та  дуже прошу у Господа, щоб дав усім нам мудрість, мудрість не сваритись  за мир – я звідси, а ти – звідти. Авраам, пояснив далі Папа, далі йшов  вперед, він залишив свою землю, щоб іти, не знаючи куди, але туди, де  йому вкаже Господь. Отже, далі йшов вперед, бо вірив слову Господа Бога,  Який «запросив його вийти із своєї землі». І цей чоловік, якому було,  можливо, 90 років, дивиться на землю, яку йому вказує Господь, і вірить.</p>\n','2013-06-25 21:38:09','http://oranta.org/index.php?option=com_content&view=article&id=1296:buty-hrystyjanynom-ce-ne-vypadkovist-a-poklyk-ljubovi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Бути християнином – це не випадковість, а поклик любові','uk',3385,4868,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_704616.JPG\" height=\"129\" width=\"194\" /></p>\n<p>Бути  християнами – це поклик любові, поклик стати Божими дітьми, наголосив Святіший Отець. «Коли я  читаю цей уривок, – сказав Єпископ Риму, – думаю про Близький Схід та  дуже прошу у Господа, щоб дав усім нам мудрість, мудрість не сваритись  за мир – я звідси, а ти – звідти. Авраам, пояснив далі Папа, далі йшов  вперед, він залишив свою землю, щоб іти, не знаючи куди, але туди, де  йому вкаже Господь. Отже, далі йшов вперед, бо вірив слову Господа Бога,  Який «запросив його вийти із своєї землі». І цей чоловік, якому було,  можливо, 90 років, дивиться на землю, яку йому вказує Господь, і вірить.</p>\n','2013-06-25 23:38:09','http://oranta.org/index.php?option=com_content&view=article&id=1296:buty-hrystyjanynom--ce-ne-vypadkovist-a-poklyk-ljubovi&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Бути християнином – це не випадковість, а поклик любові','uk',3384,4867,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_710158.JPG\" />«Якщо  усунемо віру в Бога з наших міст, між нами ослабне довіра, нас  об’єднуватиме лише страх, а стабільність буде під загрозою», – читаємо в  55-му пункті першої енцикліки Папи Франциска <em>Lumen Fidei</em> (Світло  віри). За його словами, віра, прийнята та поглиблена в сім’ї, через  досвід любові Небесного Отця, здатна просвітлювати всі суспільні  стосунки, відкриваючи нам шлях до взаємного братерства.</p>\n<p>Святіший  Отець зазначає, що центром біблійної віри є Божа любов і Його дбання про  кожну людину, і коли цей критерій притемнюється, людина втрачає своє  місце у Всесвіті, губиться серед природи, відмовляючись від моральної  відповідальності або претендуючи на роль абсолютного судді, приписуючи  собі право необмежених маніпуляцій.</p>\n','2013-07-13 18:21:52','http://oranta.org/index.php?option=com_content&view=article&id=1324:suspilne-pytannja-v-encyklici-papy-francyska-lumen-fidei-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Суспільне питання в енцикліці Папи Франциска Lumen Fidei ','uk',3429,4912,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_715717.JPG\" height=\"128\" width=\"193\" />«Братерство  – це фундамент і шлях до миру», – такою є тема 47-го Всесвітнього Дня  Миру, першого під проводом Папи Франциска. Про це йдеться в  повідомленні, яке у середу, 31 липня 2013 р., оприлюднила Папська Рада  «Справедливість і мир». Пригадаємо, що Всесвітній День Миру,  започаткований Папою Павлом VI, відзначається у перший день кожного  року.</p>\n','2013-08-08 02:08:30','http://oranta.org/index.php?option=com_content&view=article&id=1393:lbraterstvo--ce-fundament-i-shljah-do-myrur-papa-francysk-obrav-temu-vsesvitnogo-dnja-myru-2014&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Братерство – це фундамент і шлях до миру». Папа Франциск обрав тему Всесвітнього Дня Миру 2014','uk',3496,4979,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_716112.JPG\" height=\"135\" width=\"203\" />«У  народу є майбутнє, якщо він прямує вперед з цими двома полюсами: з  молоддю, тобто з силою, яке веде вперед, і з похилими віком людьми, тому  що вони є тими, хто дає життєву мудрість», – ці слова Папа Франциск  сказав, вітаючи на борту літака журналістів, які разом з його супроводом  летіли до Бразилії. Вирушаючи у свою першу Апостольську подорож,  Святіший Отець зазначив:</p>\n','2013-08-01 12:29:56','http://oranta.org/index.php?option=com_content&view=article&id=1386:papa-francysk-pro-prysutnist-poperednyka-u-vatykani-ce-nemovby-maty-vdoma-mudrogo-didusja-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Папа Франциск про присутність попередника у Ватикані: Це немовби мати вдома мудрого дідуся ','uk',3480,4963,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://media01.radiovaticana.va/imm/1_0_723796.JPG\" height=\"140\" width=\"185\" />«Ти  нас сотворив для Тебе і тривожне наше серце, аж поки не спочине в  Тобі», Папа  наголосив, що особисто його дуже вражає і спонукає до роздумів слово  «тривожне» і саме навколо цього слова він побудував свою проповідь,  розпочинаючи із запитання: якою була головна тривога у житті святого  Августина? Або, як перефразував далі Святіший Отець: які тривоги  закликає нас розбудити і підтримувати у власному житті цей великий  святий? Відповідаючи на це, він запропонував роздумати над трьома  «тривогами»: тривогою духовного пошуку, тривогою зустрічі з Богом та  тривогою любові.</p>\n','2013-08-31 20:38:51','http://oranta.org/index.php?option=com_content&view=article&id=1437:lty-nas-sotvoryv-dlja-tebe-i-tryvozhne-nashe-serce-azh-poky-ne-spochyne-v-tobir&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Ти нас сотворив для Тебе і тривожне наше серце, аж поки не спочине в Тобі»','uk',3556,5039,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://naiz1.pp.net.ua/_nw/7/s12561102.jpg\" width=\"193\" height=\"125\" />19-20 листопада 2010 р. у м. Чернігові на базі Чернігівського національного педагогічного університету ім. Т.Г. Шевченка та за підтримки Чернігівської обласної державної адміністрації проведено Міжнародну наукову конференцію “Християнізаційні впливи в Київській Русі за часів князя Оскольда: 1150 років”. Конференцію організовано з нагоди 1150-річчя походу князя Оскольда Київського на Константинополь у 860 р., укладання першого мирного договору між Руссю та Візантією та початку поступової християнізації в Київській Русі.</p>\n','2010-11-24 20:13:32','http://oranta.org/index.php?option=com_content&view=article&id=624:rezoljucija-mizhnarodnoji-naukovoji-konferenciji-lhrystyjanizacini-vplyvy-v-kyjivski-rusi-za-chasiv-knjazja-oskolda-1150-rokivr&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Резолюція  Міжнародної наукової конференції «Християнізаційні впливи в Київській Русі за часів князя Оскольда: 1150 років»','uk',2229,3712,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://oranta.org/image,4139,200,DSCN4499.JPG\" /><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4139,200,DSCN4499.JPG\" />26 січня.</p>\n<p>Цей день з нетерпінням чекало 14 дітей з церкви святого Миколая, що на Аскольдовій Могилі. Адже сьогодні з Київського вокзалу вони разом з трьома вихователями та священиком Анатолієм Теслею відправляється потяг Київ-Чернівці до&nbsp; туристичного комплексу Мигово. Весела метушня в поїзді так втомила дітей, що вони поринули у сон.<br /><br /></p>\n','2012-01-30 21:50:33','http://oranta.org/index.php?option=com_content&view=article&id=827:vidpochynok-na-lyzhah-abo-podorozh-u-mygovo&catid=25:parafija-cat&Itemid=27','P','','Відпочинок на лижах або подорож у Мигово','uk',2657,4140,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_51527_shevchenko.jpg\" alt=\"Тарас Шевченко\" title=\"Тарас Шевченко\" height=\"171\" width=\"165\" /></p>\n<p>Мирослав Маринович, <em>Виступ перед українською громадою на Шевченківському вечорі, Париж, катедра св. Володимира, 8.03.2013</em></p>\n<p>Темою виступу є Тарас Шевченко, від цьогорічних уродин якого  почнеться зворотний відлік часу до великої дати його 200-літнього  ювілею. Відтепер із цими Тарасовими днями постійно буде пов’язаний і  кожен черговий ювілей вашої єпархії. Тому моїм завданням і є поміркувати  над тим, яким місточком додаткового смислу пов’язані ці дві події.  Іншими словами, якими дороговказами для нас, християн, повниться <a href=\"http://risu.org.ua/ua/library/lb-tag?tag=1743\" target=\"_blank\">поезія Тараса</a>.</p>\n','2013-03-08 23:14:11','http://oranta.org/index.php?option=com_content&view=article&id=1023:taras-shevchenko-ne-date-narodovi-zhyty-bez-viry&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Тарас ШЕВЧЕНКО: Не дайте народові жити без віри!','uk',3001,4484,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_52390_Schotkina_risu2.jpg\" alt=\"Катерина Щоткіна\" title=\"Катерина Щоткіна\" height=\"138\" width=\"184\" />Мабуть, ніде журналіст не відчуває своєї непотрібності так гостро, як у нашій релігійній темі.</p>\n<p>«А чому, власне, світська преса має  писати про зречення Папи Римського? Це стосується католиків – нехай  католики про це й пишуть». Приблизно так був сформульований пост в  обговоренні <a href=\"http://risu.org.ua/ua/index/expert_thought/analytic/51976/\" target=\"_blank\">огляду Марини Довженко</a>.  Не знаю, як там автор статті, але я спершу розгубилася, прочитавши це –  своєчасне, панове, — питання. Ну як це – чому? Як – чому? А про що ж  іще писати? Це ж подія. Не побоюся цього слова – сенсація.</p>\n','2013-05-23 13:04:01','http://oranta.org/index.php?option=com_content&view=article&id=1220:drugy-sort-abo-navishcho-ukrajincjam-papa-i-kopty&catid=37:spilnota-cat&Itemid=49','P',NULL,'Другий сорт, або навіщо українцям Папа і копти','uk',3269,4752,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_53731_kondr.jpg\" alt=\"Ірина Кондратюк\" title=\"Ірина Кондратюк\" height=\"173\" width=\"173\" /></p>\n<p>У  третю неділю вересня в Україні відзначають День батька. З цього  приводу  в рамках Форуму видавців у Львові видавництво «Свічадо»  презентувало  нову книгу «Люблю тебе, Тату». «Насправді, причина  зустрічі є значно  глибша, — розпочав директор видавництва «Свічадо»  Богдан Трояновський. –  Коли я був студентом, професор психології  сказала нам таку цікаву  проблему, про яку ми навіть не задумувалися.  ХІХ-ХХ століття – це були  століття жінки, матері, століття, в яких  жінка отримала певні права.</p>\n','2013-09-23 09:47:25','http://oranta.org/index.php?option=com_content&view=article&id=1493:lmi-tato-zavzhdy-poruch-u-mene-ogo-ochir&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Мій тато завжди поруч… У мене – його очі»','uk',3618,5101,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://risu.org.ua/php_uploads/images/articles/ArticleImages_54141_slipyj-solo.jpg\" alt=\"Йосиф Сліпий\" title=\"Йосиф Сліпий\" height=\"158\" width=\"118\" /></p>\n<p><em>Мабуть не має більш дослідженої теми  з  біографії Блаженішого  Йосифа Сліпого, ніж питання його змагань за   патріарший статус  Української Греко-Католицької Церкви. В цьому немає   нічого дивного,  оскільки на фоні передвоєнної діяльності та 18 літнього   ув’язнення,  які є висвітлені слабше, його активність після звільнення у   1963 році з  радянського концтабору мала ефект перманентного  землетрусу.  Скоріш за  все Папа Іван ХХІІІ, який приклав багато зусиль  для звільнення   Патріярха Йосифа, не міг передбачити, яким буде  результат цього   звільнення.</em></p>\n','2013-10-31 17:45:30','http://oranta.org/index.php?option=com_content&view=article&id=1593:try-tytuly-blazhenishogo-osyfa-slipogo&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Три титули Блаженішого Йосифа Сліпого','uk',3736,5219,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://tv.ugcc.org.ua/media/gallery/full/o/other/o_kazanova.jpg\" height=\"129\" width=\"191\" /></p>\n<div class=\"inlinemedia\">\n<p>Його постать - свого роду феномен у  теперішній українській дійсності. Справді-бо, розвиток міжнаціональних  взаємовідносин на зламі попереднього й теперішнього століть  характеризується унікальним випадком, коли особа з усталеним етнічним  темпераментом, з притаманними їй рисами представника своєї рідної нації,  присвятила більшу частину свого свідомого життя служінню народові з  абсолютно відмінними звичаями і традиціями. Мова про іспанця за  походженням, священика, отця-доктора Хосепа Казанову-Мартореля.</p>\n','2013-08-27 21:38:28','http://oranta.org/index.php?option=com_content&view=article&id=1426:otec-osyf-kazanova-lukrajina--moja-doljar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Отець Йосиф Казанова: «Україна – моя доля»','uk',3543,5026,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://tv.ugcc.org.ua/media/gallery/full/o/other/o_kazanova.jpg\" height=\"129\" width=\"191\" /></p>\n<p>Його постать - свого роду феномен у  теперішній українській дійсності. Справді-бо, розвиток міжнаціональних  взаємовідносин на зламі попереднього й теперішнього століть  характеризується унікальним випадком, коли особа з усталеним етнічним  темпераментом, з притаманними їй рисами представника своєї рідної нації,  присвятила більшу частину свого свідомого життя служінню народові з  абсолютно відмінними звичаями і традиціями. Мова про іспанця за  походженням, священика, отця-доктора Хосепа Казанову-Мартореля.</p>\n','2013-08-27 21:38:28','http://oranta.org/index.php?option=com_content&view=article&id=1426:otec-osyf-kazanova-lukrajina-moja-doljar&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Отець Йосиф Казанова: «Україна – моя доля»','uk',3545,5028,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_19-07-09-3.jpg.jpg\" /></p>\n<p>Десятки тисяч прочан з усієї України 18-19 липня 2009 р. зібралися у Марійському духовному центрі Зарваниці, що на Тернопільщині. Вшанувати чудотворну ікону Матері Божої Зарваницької приїхали Глава УГКЦ блаженніший Любомир Гузар, Голова Папської комісії у справах Держави-Міста Ватикану та Губернаторства Держави-Міста Ватикану кардинал Джованні Лайоло, Митрополит Львівський РКЦ Мечислав Мокшицький, Папський нунцій в Україні архиєпископ Іван Юрковіч, владики УГКЦ.</p>\n','2009-07-20 23:06:19','http://www.oranta.org/index.php?option=com_content&view=article&id=387:vidbulasja-vseukrajinska-proshcha-do-zarvanyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Відбулася Всеукраїнська проща до Зарваниці','uk',1692,3175,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_1_08.jpg.jpg\" />«Українські Церкви представляють собою велику духовну силу, тому православний світ не може не бачити Україну. Україна - мільйони православних. Поділ в Україні - виклик для патріархів (Патріарха Кирила та Патріарха Вартоломія І, - <em>авт.</em>). Ми побачимо, що вони вміли зробити для Української Православної Церкви», - сказав Блаженніший Любомир в <a href=\"http://religion.in.ua/main/806-kardinal-lyubomir-guzar-pro-zminu-statusu-ugkc.html\" target=\"_blank\">інтерв\'ю веб-порталу «Релігія в Україні»</a>, говорячи про зустріч Патріарха РПЦ Кирила та Константинопольського Патріарха Вартоломія І, яка відбулася 4 - 6 липня у Туреччині.\n','2009-07-08 19:44:14','http://www.oranta.org/index.php?option=com_content&view=article&id=376:lpatriarh--to-je-otecr-blazhennishy-ljubomyr-pro-vizyt-patriarha-kyryla-do-turechchyny-ta-ukrajiny-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Патріарх – то є отець»: Блаженніший Любомир про візит Патріарха Кирила до Туреччини та України  ','uk',1650,3133,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_chornobyl_madonna.jpg.jpg\" height=\"225\" width=\"156\" />Дорогі брати і сестри!</p>\n<p>26 квітня минає 25 років із часу сумнозвісної аварії на  Чорнобильській атомній електростанції (ЧАЕС), яка спричинила найбільшу у  світі техногенну катастрофу і вказала на неприпустимі помилки у  використанні атомної енергії.</p>\n<p>Внаслідок вибуху четвертого реактора ЧАЕС відбувся викид радіоактивних елементів, що призвiв до забруднення близько 150 тис. км<sup>2</sup> територій нашої країни і сусідніх Білорусі та Росії. Згубного впливу  радіації зазнали також широкі простори європейських країн. Особливо  постраждали мільйони жителів радіоактивно забруднених територій і сотні  тисяч учасників ліквідації наслідків атомного лиха, які розчищали руїни  та зводили саркофаг для зруйнованого реактора.</p>\n','2011-04-26 11:32:12','http://oranta.org/index.php?option=com_content&view=article&id=738:zvernennja-blazhennishogo-svjatoslava-z-pryvodu-dvadcjat-pjatoji-richnyci-chornobylskoji-katastrofy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Звернення Блаженнішого Святослава з приводу двадцять п\'ятої річниці чорнобильської катастрофи','uk',2428,3911,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_UM_08.jpg.jpg\" width=\"111\" height=\"124\" />Церква  – добра мати, яка звертається до дітей тією мовою, яка є для них  природною, щоби звернути увагу на всі аспекти життя народу. Про це Глава  УГКЦ Блаженніший Любомир сказав на круглому столі на тему: «Церква і  українська мова», організованому Всеукраїнським товариством «Просвіта» з  нагоди Дня української писемності.</p>\n','2010-11-10 21:04:18','http://oranta.org/index.php?option=com_content&view=article&id=618:glava-ugkc-na-kruglomu-stoli-z-nagody-dnja-ukrajinskoji-pysemnosti-lcerkva-jak-dobra-maty-zvertajetsja-do-kozhnogo-narodu-ogo-movojur-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ на круглому столі з нагоди Дня української писемності: «Церква, як добра мати, звертається до кожного народу його мовою» ','uk',2211,3694,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_UM_09.jpg.jpg\" height=\"148\" width=\"135\" />«П\'ять  років тому, в 2005 році, я не прийшов до Києва, я повернувся. Після  двохсот років». Про це в інтерв\'ю російському релігійному  інтернет-ресурсу «<a href=\"http://portal-credo.ru/site/?act=news&amp;id=81235\" target=\"_blank\">Портал-Credo.Ru</a>» сказав Глава Української Греко-Католицької Церкви Блаженніший Любомир.</p>\n<p>На підтвердження цих слів Глава Церкви нагадав,  що у той час як у Західній Україні, яка після поділу Польщі у XVIII  столітті підпала під Австро-Угорську імперію, Греко-Католицька Церква  мала можливість вільно розвиватися, то на тих теренах, які відійшли до  царської Росії, Церкву потроху ліквідовували: «На початку XIX століття  заборонили вибирати греко-католицького митрополита, тому його резиденцію  перенесли до Львова», – пояснив Блаженніший Любомир.</p>\n','2010-12-08 21:44:33','http://oranta.org/index.php?option=com_content&view=article&id=632:blazhennishy-ljubomyr-rosiskomu-religinomu-resursu-lportal-credorur-lja-ne-pryshov-do-kyjeva-ja-povernuvsjar-&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Блаженніший Любомир російському релігійному ресурсу «Портал-Credo.Ru»: «Я не прийшов до Києва, я повернувся» ','uk',2241,3724,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_kalendar9_jpg_02.jpg.jpg\" />«Слуга  Божий Митрополит Андрей (Шептицький) – це постать, за яку ми повинні  дякувати Богові, бо він, як колись сказав кардинал Тісеран, звертаючись  до українців, є окрасою нашої Церкви і нашого народу. Митрополит  Шептицький був вчителем нашого народу», – так охарактеризував свого  славного попередника Блаженніший Любомир у вітальному листі до учасників  святкової академії у Хмельницькому. <a href=\"http://ugcc.org.ua/news_single.0.html?&amp;L=0&amp;tx_ttnews%5bpS%5d=1286626900&amp;tx_ttnews%5btt_news%5d=4171&amp;tx_ttnews%5bbackPid%5d=1&amp;cHash=6643986847\">Як ми повідомляли раніше</a>,  урочисте вшанування світлої пам’яті слуги Божого Митрополита Андрея  (Шептицького) відбулося 5 грудня 2010 року у приміщенні Хмельницької  обласної філармонії.</p>\n','2010-12-08 21:41:47','http://oranta.org/index.php?option=com_content&view=article&id=631:mytropolyt-andre-sheptycky--ce-svjata-ljudyna--blazhennishy-ljubomyr-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Митрополит Андрей (Шептицький) – це свята людина, – Блаженніший Любомир ','uk',2240,3723,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_molytva_.jpg.jpg\" />2 липня 2009 року у Київському національному будинку органної та камерної музики України відбулася спільна молитва за благополуччя України, яку організувало Українське біблійне товариство. У вступному слові Григорій Комендант, президент Українського біблійного товариства (християнська неконфесійна спілка, що займається перекладом та розповсюдженням Біблії та її окремих книг в Україні), закликав до молитви християн різних конфесій.</p>\n','2009-07-06 23:40:01','http://www.oranta.org/index.php?option=com_content&view=article&id=375:-spilna-ekumenichna-molytva-za-blagopoluchchja-ukrajiny-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' Спільна екуменічна молитва за благополуччя України ','uk',1644,3127,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_vid_malka_09.jpg.jpg\" />«Наша надія на краще майбутнє може мати підстави лише тоді, коли ми будемо готові захищати свою свободу як Божий дар, дотримуючись святого Господнього Закону». До цього у <a href=\"http://ugcc.org.ua/1312.0.html?&amp;L=0\">Зверненні до вірних Української Греко-Католицької Церкви та до всіх людей доброї волі з нагоди 20-ї річниці Декларації про державний суверенітет України</a> закликав Глава УГКЦ Блаженніший Любомир (Гузар).</p>\n','2010-07-19 12:53:43','http://oranta.org/index.php?option=com_content&view=article&id=555:lsuverenitet-u-svoji-glybynni-sutnosti-je-stanom-dushi-jaky-projavljajetsja-v-usvidomlenni-vlasnoji-gidnostir-blazhennishy-ljubomyr-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'«Суверенітет у своїй глибинній сутності є станом душі, який проявляється в усвідомленні власної гідності»: Блаженніший Любомир ','uk',2105,3588,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_vrciro.jpg.jpg\" />14  грудня відбулося позачергове засідання Всеукраїнської Ради Церков і  релігійних організацій. Зустріч глав та повноважних представників  конфесій проходила під головуванням Василя Райчинця, Старшого пресвітера  Союзу вільних церков християн євангельської віри України, у приміщенні  столичного храму Німецької євангелічно-лютеранської церкви, повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи</a>.</p>\n<p>Учасники засідання розглянули комплекс питань  державно-церковних відносин, актуальність яких викликана  адміністративною реформою та податковими новаціями.</p>\n','2010-12-16 22:03:47','http://oranta.org/index.php?option=com_content&view=article&id=637:vseukrajinska-rada-cerkov-i-religinyh-organizaci-vkotre-zaklykaje-prezydenta-vjanukovycha-do-dialogu-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Всеукраїнська Рада Церков і релігійних організацій вкотре закликає Президента В.Януковича до діалогу ','uk',2251,3734,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/pics/birth_of_john_the_baptist.jpg\" height=\"170\" width=\"117\" /></p>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\">Це найбільший з усіх празників св. Івана Христителя. Як празник його зачаття, так і цей празник уже був відомий у 4 столітті. Поучення на цей празник знаходимо у св. Івана Золотоустого, св. Амвросія і св. Августина. Цей останній у своїй проповіді цього дня каже: „Сьогодні празнуємо день різдва св. Івана. Це честь, якої, на нашу думку, не має ні один святий. У всьому християнському світі є назначений і почитаний тільки день Різдва Господа і св. Івана\". Св. Августин не згадує тут про празник Різдва Божої Матері, бо в тому часі цього празника ще не було. На Заході празник Різдва св. Івана Предтечі знаходиться в найдавнішому календарі африканської Церкви з 5 століття.</p>\n','2011-07-05 23:34:16','http://oranta.org/index.php?option=com_content&view=article&id=374:rizdvo-svjatogo-ivana-hrystytelja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Різдво святого Івана Христителя','uk',2520,4003,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://ugcc.org.ua/uploads/pics/birth_of_john_the_baptist.jpg\" width=\"300\" height=\"434\" /></p>\n<p><strong>Коротка історія</strong></p>\n<p align=\"justify\">Це найбільший з усіх празників св. Івана Христителя. Як празник його зачаття, так і цей празник уже був відомий у 4 столітті. Поучення на цей празник знаходимо у св. Івана Золотоустого, св. Амвросія і св. Августина. Цей останній у своїй проповіді цього дня каже: „Сьогодні празнуємо день різдва св. Івана. Це честь, якої, на нашу думку, не має ні один святий. У всьому християнському світі є назначений і почитаний тільки день Різдва Господа і св. Івана\". Св. Августин не згадує тут про празник Різдва Божої Матері, бо в тому часі цього празника ще не було. На Заході празник Різдва св. Івана Предтечі знаходиться в найдавнішому календарі африканської Церкви з 5 століття.\n','2009-07-06 23:34:16','http://www.oranta.org/index.php?option=com_content&view=article&id=374:rizdvo-svjatogo-ivana-hrystytelja&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Різдво святого Івана Христителя','uk',1643,3126,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2365,200,006.jpg\" />В Обливаний Понеділок, на другий день Великодніх Свят, лемківська церква, що розташована в музеї під відкритим небом у Пирогові, в черговий раз зібрала лемків Києва ( і не тільки лемків)   помолитися.  Ця традиційна лемківська церква збудована у 1792 році, перевезена у музей у 1974 із села Канора Воловецького району Закарпатської області. Завдяки ентузіазму наукового співробітника музею Романи Кобальчинської  10 років тому  у ній почав відправляти Службу Божу о. Богдан Чурило.</p>\n','2009-04-22 22:09:57','http://www.oranta.org/index.php?option=com_content&view=article&id=294:velykden-u-pyrogovi&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Великдень у Пирогові','uk',1392,2366,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2518,lia.jpg\" />Напередодні неділі Розслабленого УГКЦ молиться за недужих. Отець Олексій Геник священик церкви св. Миколая Чудотворця на Аскольдовій Могилі вже більше пів року є капеланом каплиці Покрови Пресвятої Богородиці у Київській клінічній лікарні №11 (район ДВЗ). Сьогодні на молебень до Пресвятої Богородиці зібралося понад 20 віруючих, хто зміг прийти: недужі, медичний персонал, важкохворих та немічних отець Олексій відвідав у палатах.</p>\n','2009-05-08 23:22:29','http://www.oranta.org/index.php?option=com_content&view=article&id=316:ukrajinska-greko-katolycka-cerkva-u-molytvi-za-neduzhyh&catid=25:parafija-cat&Itemid=27','P',NULL,'Служіння хворим у Київському деканаті УГКЦ (частина 1)','uk',1463,2519,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2522,200,1.jpg\" />Сьогодні, у переддень неділі Розслабленого, спільну молитву за недужих (онко хворих) діток відслужив отець Анатолій Тесля у національній спеціалізованій дитячій лікарні «Охматдит» (відділення онкогематології). Провідати своїх недужих однолітків разом з отцем прийшли бездоглядні діти, якими опікується наша церква св. Миколая Чудотворця на Аскольдовій Могилі. Невеличкі гостинці від св. Миколая, спільна молитва, живе спілкування додали хворим діткам трішечки радості.</p>\n','2009-05-09 23:29:54','http://www.oranta.org/index.php?option=com_content&view=article&id=317:sluzhinnja-hvorym-u-kyjivskomu-dekanati-ugkc-chastyna-2&catid=25:parafija-cat&Itemid=27','P','','Служіння хворим у Київському деканаті УГКЦ (частина 2)','uk',1465,2521,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,2526,200,DSCN0571.JPG\" />10 травня у греко-католицькій церкві святого Миколая, що на Аскольдовій могилі, відбувся дитячий концерт, який було присвячено Дню Матері.</p>\n<p>Художній керівник мистецького гурту Тетяна Стефанишин щирою усмішкою благословила учасників творчого дійства і концерт розпочався. Це були не просто «сухі» вірші чи відомі пісні. Кожна пісня, що звучала зі сцени, виходила від серця, кожне слово мало глибокий зміст. Глядачі, а їх зібралося чимало, щирими аплодисментами віддячували за гарну майстерність.</p>\n','2009-05-11 09:57:29','http://www.oranta.org/index.php?option=com_content&view=article&id=320:ridna-maty-moja&catid=25:parafija-cat&Itemid=27','P','','Рідна мати моя…','uk',1472,2528,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.askoldova-mohyla.org/uk/image,3022,200,IMG_1965.JPG\" height=\"200\" width=\"133\" />30-31 травня відбулися святкування 15-ї річниці служіння сестер служебниць Непорочної Діви Марії в м. Києві. Святкування почались у суботу із Божественної Літургії. Після літургії сестер привітав Блаженнійший Любомир Гузар. Він подякував сестрам за велику працю яку вони виконують. А це і катехизація дітей, праця з молоддю, упорядкування храмів та ще багато інших проявів милосердя, які  відповідають життєвому кредо Йосафати Гордашевської: «Виховувати серце народу і служити народові там, де є найбільша потреба».</p>\n<p>Після привітання Блаженнійшого вечір продовжився Вечірньою, після якої молодь провела молитву у Дусі Тезе. Молитва складалась із розважань Йосафати. У своїх розважаннях Йосафата повністю довіряла себе Господу, покладалась на його волю, закликала частіше роздумувати над стражданнями Христа у яких потрібно черпати сили для виконування свого покликання.</p>\n','2009-06-01 23:32:46','http://www.oranta.org/index.php?option=com_content&view=article&id=337:15-richny-juvile-zgromadzhennja-cester-cluzhebnyc-neporochnoji-divy-mariji-u-kyjevi&catid=25:parafija-cat&Itemid=27','P','','15 - річний ювілей Згромадження Cестер Cлужебниць Непорочної Діви Марії у Києві','uk',1540,3023,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.catholic-media.org/ua/images/foto_b/nenarodzenyj.jpg\" height=\"159\" width=\"150\" />«Мамо, не вбивай мене» - під такою назвою у Чернігові відбулася акція проти абортів. У рамках заходу пройшов круглий стіл, на якому духовенство, медики та представники влади розглянули причини, що спонукають жінок переривати вагітність, та можливі шляхи протидії абортам...   Її організаторами виступили Чернігівська єпархія Української Православної Церкви та Благодійний фонд «Європа».</p>\n<p>Архієпископ Амвросій, керуючий Чернігівською єпархією: «Чому підняли саме це питання? Тому що Чернігівська область - одна з найпроблемніших стосовно демографічного стану в Україні. Тут найвища смертність та найнижча народжуваність. З карти Чернігівської області щороку зникає 5, а той більше сіл. І це страшна ситуація».</p>\n','2009-04-26 21:20:51','http://www.oranta.org/index.php?option=com_content&view=article&id=303:akcija-proty-abortiv-u-chernigovi-lmamo-ne-vbyva-mener&catid=37:spilnota-cat&Itemid=49','P',NULL,'Акція проти абортів у Чернігові: «Мамо, не вбивай мене»','uk',1419,2475,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/01/ArticleImages_40104_maria_shveda.jpg\" title=\"Марія Шведа\" height=\"179\" width=\"134\" /><strong>У папській каплиці у Ватикані є велика настінна мозаїка, яку з  нагоди 50-літнього священицького служіння Йоана Павла ІІ подарувала йому  Колегія кардиналів. На мозаїці зображені мученики нових часів.  «Мученики, – пише о. Марко Рупнік, автор мозаїки, – це ті, що своїм  життям вказують на Христа, живого в історії». І серед них – українська  дівчина Марія Шведа, котру по-звірячому вбили у Львові 1982 року:  рятуючи від арешту підпільного греко-католицького священика, вона  забрала його сумку з богослужбовими речами. Тоді ця історія наробила  багато шуму у світі, про це говорили «Радіо Свобода», Ватикан. Багато з  тих, що знали її, вважають її святою. Її імя – серед тих новомучеників,  яких Постуляційний центр УГКЦ готує до беатифікації. </strong></p>\n','2013-10-11 00:09:41','http://oranta.org/index.php?option=com_content&view=article&id=1533:-ukrajinska-novomuchenycja-marija-shveda&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,' Українська новомучениця Марія Шведа','uk',3672,5155,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/03/2209.jpg\" title=\"Віктор Єленський\" height=\"140\" width=\"200\" />— Очевидно, що сама філософія свята фокусується на силі і владі – про  Ісуса Христа не йдеться зовсім. При цьому державні святкування  побудовані так, щоби максимально звузити простір для вільного вияву  почуттів, настроїв і переконань власне людьми. Напевно за задумом  організаторів святкування мали обертатися навколо «православного  президента» — справедливого і побожного, доброго отця всіх в Україні  сущих і гідного продовжувача справи Володимира Великого. Але насправді у  центрі опиняться Патріарх Кирило і Путін, а інші православні єрархи і  «православні президенти» складатимуть для них тло.</p>\n','2013-07-26 20:04:55','http://oranta.org/index.php?option=com_content&view=article&id=1383:lderzhavna-programa-svjatkuvannja-1025--ce-prynyzhennja-dlja-miloniv-ukrajinskyh-hrystyjan-i-poglyblennja-rozkolu-krajinyr-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'«Державна програма святкування “1025” – це приниження для мільйонів українських християн і поглиблення розколу країни» ','uk',3472,4955,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/07/efe94602a2d9.gif\" title=\"Ісус\" height=\"153\" width=\"202\" /></p>\n<p>&nbsp;<strong>Він не буде ні змагатися, ні кричати і голосу його ніхто не буде  чути на майданах. Він очеретини надломаної не доломить і гнота тліючого  не загасить, аж поки не доведе право до перемоги. На його ім\'я будуть  надіятися погани. (Мт 12, 19-20) </strong></p>\n<p>Мені, як священику, частенько доводиться чути нарікання різних людей  такого типу: «чому Бог його не покарає», «скільки вже можна терпіти», та  прокльони типу «Бог напевно його покарає».</p>\n','2013-07-19 21:02:37','http://oranta.org/index.php?option=com_content&view=article&id=1343:-blazhenni-tyhi-bo-vony-uspadkujut-zemlju&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Блаженні тихі, бо вони успадкують землю','uk',3450,4933,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.detdom.info/sites/default/files/552895_384198648288892_1028928356_n.jpg\" height=\"137\" width=\"210\" /></p>\n<p>Однією  з найголовніших справ свого життя відомий німецький музикант  вбачає в  тому, щоб допомагати іншим. Для нього благодійність&nbsp; - не  просто красиві  вчинки та яскраві слова, а щире прагнення бути  потрібним&nbsp; в скрутну  хвилину.<br /><br />Кремезний  та сильний – таке перше  враження справляє Інуса Давуда. В знаки дається  спортивне минуле – в  90-х він ставав багаторазовим чемпіоном Північної  Німеччини з боксу в  важкій вазі. Проте це тільки на перший погляд -  насправді Інуса дуже  привітний та щирий.</p>\n','2013-09-26 22:36:38','http://oranta.org/index.php?option=com_content&view=article&id=1505:lblagodinist-ce-stan-dushi-shcho-ne-maje-geografichnyh-kordonivr&catid=29:propovidi-cat&Itemid=34','P',NULL,'«Благодійність це стан душі, що не має географічних кордонів»','uk',3628,5111,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.dyhdzvin.org/images/2013/08_2013/spisok.jpg\" height=\"147\" width=\"176\" />Я  трималася до останнього. Але настав день, коли мої сили вичерпалися, і  як тільки чоловік поїхав на роботу, я зібрала речі, взяла нашого  14-місячного сина і пішла з дому. Той рік був єдиним, коли ми жили в  одному місті з моїми батьками. Очевидно, можливість втекти до тата й  мами спростила моє рішення залишити Білла...</p>\n<p>Заплакана і розсерджена, я пішла з мамою  на кухню. Вона тримала дитину, а я, ридаючи, виправдовувала свій  вчинок. Мама налила мені кави і сказала, що вони з татом допоможуть  мені. Я полегшено зітхнула, дізнавшись, що вони на моїй стороні.</p>\n','2013-10-06 22:59:22','http://oranta.org/index.php?option=com_content&view=article&id=1528:-spysok-jaky-vrjatuvav-moju-simju&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Список, який врятував мою сім’ю','uk',3656,5139,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.dyhdzvin.org/images/2013/09_2013/grih.jpg\" /></p>\n<p><strong>Падіння</strong></p>\n<p>Щастя  в людській сім\'ї вимагає деяких очевидних умов. По-перше, люди повинні  хотіти бути щасливими. Нікого не можна зробити щасливим у відношенні до  інших без його свобідної волі. Ніхто не може дати сімейного щастя жінці,  яка просто не хоче одружуватися - яким би ідеальним не був її  наречений. По-друге, щастя передбачає особисте зростання - ми  змінюємося, робимося іншими, щоб вмістити це щастя.</p>\n','2013-10-08 00:19:07','http://oranta.org/index.php?option=com_content&view=article&id=1529:navishcho-bog-mene-stvoryv&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Навіщо Бог мене створив?','uk',3658,5141,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.dyhdzvin.org/images/2013/10_2013/isys_ta_hrichnucia.jpg\" height=\"134\" width=\"244\" /></p>\n<p>Коли поглянути в молитвослов, то нас  може вразити, як багато в  молитвах, звернених до Бога, йдеться про гріх.  Святі, що склали ці  молитви, постійно визнавали себе грішниками,  шукаючи милосердя Божого.  Щоранку нам слід згадувати перед лицем Божим:  «…Бо беззаконня мої я  знаю, і гріх мій завжди переді мною. Перед Тобою,  Господи, я згрішив,  перед Тобою зло вчинив, тому правий Ти в Своїх  словах, коли будеш  судити» (Пс. 50,4-5).</p>\n','2013-10-22 15:10:33','http://oranta.org/index.php?option=com_content&view=article&id=1561:-grih-i-blagodat&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Гріх і Благодать','uk',3704,5187,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.dyhdzvin.org/images/2013/10_2013/love.jpg\" height=\"157\" width=\"200\" />Про  що б людина не говорила і як би не «проштовхувала» свою тему, її  почують і оцінять лише тоді, коли вона живе тим, про що йдеться, коли  вона вся «в темі». Саме тому я змушений завчасно зізнатися, що ніколи не  любив і, відповідно, через те, що я «не в темі», нічого не скажу про  любов зі свого досвіду. Усе, що я тут пишу, — лише роздуми...</p>\n<p>Те, що я не любив, мені стало зрозуміло,  коли, переступивши поріг храму, я почув про Того, Хто не міг не любити  навіть тоді, коли Його розпинали на Хресті. Я не один такий. Ось уже дві  тисячі років це повторюється з кожним із нас:</p>\n','2013-10-23 12:13:48','http://oranta.org/index.php?option=com_content&view=article&id=1562:-pered-tym-jak-ziznatysja-v-kohanni-&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Перед тим як зізнатися в коханні ','uk',3706,5189,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.dyhdzvin.org/images/2013/10_2013/monk.jpg\" height=\"212\" width=\"180\" />Людина  шукає людину. Але ще більше людина шукає Бога. У всі часи й на всіх  континентах наше спільне завдання — зустріти Його й відповісти на Його  любов. Як легше це зробити — живучи серед людей чи віддалившись від  світу? Де людина, яка любить Бога, може знайти справжню свободу?</p>\n<p>Щоб менше грішити, треба менше говорити —  silendo nemo peccat. Мовчати зовсім — це те саме, що піти від людей,  оскільки перебування в світі людей передбачає неминуче спілкування,  пристосування, піддобрювання один до одного, прихований обмін гріхами й  чеснотами. З людьми, які не хочуть рятуватися, неможливо врятуватися.  Тому великий Арсеній почув: «Арсенію, бігай людей, і спасешся».</p>\n','2013-10-25 10:20:01','http://oranta.org/index.php?option=com_content&view=article&id=1563:-zavdannja-nomer-odyn&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Завдання номер один','uk',3710,5193,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.kyiv.ugcc.org.ua/img/upload/resur%281%29.jpg\" height=\"206\" width=\"200\" />У традиційній іконографії Христової Церкви іконою Воскресіння є ікона Зшестя в ад, про яке іде мова у богослужіннях Великої суботи: «сьогодні ад викликує стогнучи: я не повинен був прийняти Людину, народжену з Марії. Він прибув і знищив мою силу. Він розбив залізну браму, і як Бог воскресив душі, які я колись тримав».</p>\n<p>Ікона Зшестя в ад представляє Христа в ту мить, коли Він, Всемогутній  визволяє наших прародичів. Ісус входить у царство князя темряви - диявола, який тримає в неволі Адама і його нащадків та дарує їм вічне життя. Гріхопадіння Адама і Єви розірвало зв\'язок людства з Богом. Христос, у своїй особі з\'єднавши Бога і людину, поновив цей зв\'язок. Своїм сходженням в ад, дарував усім праведникам життя вічне, відкрив двері Царства Небесного, бо Він Господь усього: «Я - Альфа і Омега, початок і кінець - говорить Господь Бог, хто є і хто був і хто приходить, Вседержитель... Я перший і останній, і живий, і стався мертвий, і от - я живий на віки вічні; і маю ключі смерті й аду» (Од. 1, 8-18).</p>\n','2009-04-21 08:43:51','http://www.oranta.org/index.php?option=com_content&view=article&id=291:ikona-qzshestja-v-adq&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Ікона \"Зшестя в ад\"','uk',1378,2352,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.religion.in.ua/uploads/posts/2013-07/thumbs/1374260986_0.jpg\" alt=\"Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина III\" title=\"Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина III\" height=\"162\" width=\"162\" /></p>\n<p>&nbsp;<strong>5. Повернення до Єрусалиму</strong></p>\n<p>Древній Київ та його святині здавна вважалися сакральним центром як  для українського, так і для інших православних слов’янських народів. Не  випадково після розпаду Російської імперії в 1917 р. На Півночі почалося  поступове переосмислення значення Києва та його духовної спадщини. Вже у  1918 р., за часів правління в Україні гетьмана Павла Скоропадського,  Київ стає потужним культурним, політичним і релігійним центром.</p>\n','2013-08-26 14:03:56','http://oranta.org/index.php?option=com_content&view=article&id=1422:-ujavlennja-pro-kyjiv-jak-ldrugy-jerusalymr-v-cerkovno-suspilni-dumci-rusi-ukrajiny-chastyna-iii&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Уявлення про Київ як «другий Єрусалим» в церковно-суспільній думці Русі-України. Частина III','uk',3536,5019,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/2663a53f23.jpg\" height=\"100\" width=\"150\" />13 липня Блаженніший Святослав, Глава і Отець УГКЦ, на  Всеукраїнській і молодіжній прощі у Зарваниці зустрівся із молоддю.  Перед багатотисячною аудиторією Предстоятель УГКЦ презентував аудіо-диск  «100 відповідей на запитання молоді», у якому навчав про основи  духовного життя, суть подружжя, подружню любов, зміст людського тіла та  людської статевості. Першоієрарх окремо говорив про небезпеки гендерної  філософії та ідеології.</p>\n','2013-07-14 08:44:09','http://oranta.org/index.php?option=com_content&view=article&id=1325:-u-zarvanyci-blazhennishy-svjatoslav-zasterig-molod-pered-vchennjam-gendernoji-filosofiji-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,' У Зарваниці Блаженніший Святослав застеріг молодь перед вченням гендерної філософії ','uk',3430,4913,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/typo3temp/pics/38af9dd1da.jpg\" height=\"100\" width=\"150\" /></p>\n<p>29 червня за участю кількох тисяч вірян із Польщі та з-за  кордону в Гурово-Ілавецькому розпочалося святкування 1025-ліття Хрещення  України-Русі. Божественну Архиєрейську Літургію очолив Блаженніший  Святослав (Шевчук), Глава Української Греко-Католицької Церкви.  Необхідно відзначити, що крім єпископату Греко-Католицької Церкви, в ній  взяв участь архиєпископ Селестіно Мільоре, Апостольський нунцій в  Польщі.</p>\n','2013-07-01 23:17:39','http://oranta.org/index.php?option=com_content&view=article&id=1313:-glava-ugkc-ocholyv-svjatkuvannja-1025-littja-hreshchennja-ukrajiny-rusi-v-gurovo-ilaveckomu-polshcha&catid=28:tema-nomera-cat&Itemid=2','P',NULL,' Глава УГКЦ очолив святкування 1025-ліття Хрещення України-Русі в Гурово-Ілавецькому (Польща)','uk',3403,4886,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_blazhen.jpg.jpg\" />6 червня 2009 року у Києві Українська Греко-Католицька Церква у співпраці з Державним департаментом України з питань виконання покарань, Міжнародною католицькою комісією в\'язничної душпастирської опіки (ICCPPC) та Християнською міжконфесійною місією тюремного служіння провела Міжнародну конференцію \"Юридичний вимір в\'язничного служіння\". Серед учасників конференції щодо стану законодавства європейських країн для забезпечення гуманізації у пенітенціарній сфері були присутні керівництво Держдепартаменту, члени громадської ради Держдепартаменту, представники пенітенціарного душпастирства європейських країн, члени правління Християнської міжконфесійної місії, юристи.</p>\n','2009-06-09 11:53:09','http://www.oranta.org/index.php?option=com_content&view=article&id=341:glava-ugkc-vzjav-uchast-u-mizhnarodni-konferenciji-ljurydychny-vymir-vjaznychnogo-sluzhinnjar-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ взяв участь у Міжнародній конференції «Юридичний вимір в’язничного служіння»  ','uk',1554,3037,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_huzar_dt_10.jpg.jpg\" width=\"114\" height=\"98\" />Таку думку висловив Глава УГКЦ Блаженніший Любомир <a href=\"http://www.ugcc.org.ua/1422.0.html\">в інтерв’ю УНІАН</a>, коментуючи ситуацію навколо скандалу, пов’язаного з побудовою в Одесі храму УГКЦ.</p>\n<p>За словами Блаженішого Любомира, представники УГКЦ за кілька останніх років «пройшли вже різні митарства».</p>\n','2010-09-19 00:05:09','http://oranta.org/index.php?option=com_content&view=article&id=585:blazhennishy-ljubomyr-pro-sytuaciju-v-odesi-ljakshcho-vlada-pryme-dlja-nas-nepryhylne-rishennja-to-ha-by-gidno-ogo-pojasnylar-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Блаженніший Любомир про ситуацію в Одесі: «Якщо влада прийме для нас неприхильне рішення, то хай би гідно його пояснила»  ','uk',2148,3631,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_kovch.jpg.jpg\" />Патріарший Синод Єпископів УГКЦ, що відбувся 2 - 9 вересня 2008 року, постановив проголосити блаженного священномученика Омеляна Ковча Покровителем душпастирів УГКЦ. Це рішення Синоду реалізовується в три етапи:</p>\n<blockquote>\n<p>1. 25 березня 2009 року - проща духовенства УГКЦ до Музею в Майданеку (Люблін, Польща) та прийняття там праху блаженного для виставлення його у катедральних соборах кожної єпархії та екзархату.</p>\n<p>2. 24 квітня 2009 року - торжественне проголошення бл. свщмч. Омеляна Покровителем душпастирів УГКЦ. Урочистість відбудеться у Києві на території будівництва Патріаршого центру УГКЦ за участю усіх єпископів, духовенства та мирян.</p>\n<p>3. Кожна єпархія та екзархат на локальному рівні проводять відповідне торжество.</p>\n','2009-04-23 20:44:40','http://www.oranta.org/index.php?option=com_content&view=article&id=297:zavtra-blazhennogo-omeljana-kovcha-bude-progolosheno-pokrovytelem-dushpastyriv-ugkc-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Завтра блаженного Омеляна Ковча буде проголошено покровителем душпастирів УГКЦ  ','uk',1400,2400,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_marynovych.jpg.jpg\" height=\"151\" width=\"112\" />Панахида  за упокій душ польських та українських жертв Волинської трагедії  1943-го, яку 27 червня очолив Блаженніший Патріарх УГКЦ&nbsp;<strong>Святослав Шевчук</strong> у варшавському храмі отців-василіян у присутності президента Польщі&nbsp;<strong>Броніслава Коморовського</strong> та інших високопосадовців цієї держави, відродила одну історичну  особливість, пов\'язану із греко-католицькими ієрархами. У минулому і  митрополит&nbsp;<strong>Адрей Шептицький</strong>, і патріарх&nbsp;<strong>Йосип Сліпий</strong> виступали на міжнародному рівні не лише як первоієрархи, а й як етнархи  - особи, які, крім Церкви, представляють іще й увесь народ. Цього разу  роль такого етнарха відіграв&nbsp;<strong>Патріарх Святослав</strong>.</p>\n','2013-07-03 11:02:35','http://oranta.org/index.php?option=com_content&view=article&id=1315:myroslav-marynovych-qpolitychni-lidery-povynni-zahyshchaty-zhyttja-i-chynyty-myr-a-ne-rozpaljuvaty-vinuq&catid=37:spilnota-cat&Itemid=49','P',NULL,'Мирослав Маринович: \"Політичні лідери повинні захищати життя і чинити мир, а не розпалювати війну\"','uk',3409,4892,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_prymyrennya.jpg.jpg\" height=\"147\" width=\"202\" />На  цей рік припадає 70-та річниця Волинської трагедії, коли загинуло  багато тисяч невинних людей, поляків за походженням. У зв\'язку з цією  датою з боку церковних, політичних і громадських осередків лунають різні  звернення та заяви.</p>\n<p>Пригадуються різні трагічні моменти у стосунках українського та  польського народів, як також акти примирення між українцями та поляками  на різних рівнях.&nbsp;Настало певне напруження в польсько-українських  відносинах, яке породжує неспокій та тривогу.</p>\n','2013-05-16 10:41:32','http://oranta.org/index.php?option=com_content&view=article&id=1205:chy-naspravdi-my-bazhajemo-qjedynospasajemogoq-prymyrennja&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Чи насправді ми бажаємо \"єдиноспасаємого\" примирення?','uk',3245,4728,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_velykden_2009.jpg.jpg\" />З нагоди празника Воскресіння Господа нашого Ісуса Христа, Глава УГКЦ, у співслужінні з Владикою Богданом (Дзюрахом), Єпископом-помічником Київської архиєпархії, ієромонахом Йосифом (Міляном), проголошеним Єпископом-помічником Київської архиєпархії, та численним духовенством УГКЦ, відслужив урочисту Службу Божу у храмі Святого Василія Великого у м. Києві (Вознесенський узвіз, 7, біля Львівської площі). Її, як і богослужіння, які відбувалися вночі, у прямому ефірі транслював Перший національний канал (УТ-1).</p>\n','2009-04-23 09:15:12','http://www.oranta.org/index.php?option=com_content&view=article&id=296:na-velykden-glava-ugkc-govoryv-pro-vazhlyvist-voskresinnja-u-zhytti-kozhnoji-ljudyny-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'На Великдень Глава УГКЦ говорив про важливість воскресіння у житті кожної людини  ','uk',1395,2369,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_vid_malka.jpg.jpg\" width=\"94\" height=\"126\" />Днями вийшло Звернення Блаженнішого Любомира до вірних Української Греко-Католицької Церкви та всіх людей доброї волі із закликом молитися за державу упродовж цілого року. На початку звернення Предстоятель УГКЦ наголошує на тому, що «упродовж багатьох років наша Церква та інші Церкви і релігійні організації у передвиборчий час закликають своїх вірних молитися за народ, за державу, за Україну... Тому створюється враження, що треба молитися в тому намірі тільки тоді, коли виникають великі потреби».</p>\n','2010-03-09 21:57:13','http://www.oranta.org/index.php?option=com_content&view=article&id=500:glava-ugkc-zaklykav-virnyh-molytysja-za-derzhavu-ta-jiji-vladu-ne-tilky-pered-vyboramy-a-uprodovzh-cilogo-roku-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Глава УГКЦ закликав вірних молитися за державу та її владу не тільки перед виборами, а й упродовж цілого року  ','uk',1991,3474,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ugcc.org.ua/uploads/RTEmagicC_za_jyttya_04.jpg.jpg\" /></p>\n<p>27 лютого в Українській Греко-Католицькій Церкві відзначали День пам‘яті абортованих дітей. Цей захід проходив у рамках Програми протидії абортам в Україні, затвердженої на <a href=\"http://www.ugcc.org.ua/860.0.html\" class=\"internal-link\">Сорок шостій сесії Синоду Єпископів Києво-Галицького Верховного Архиєпископства УГКЦ</a>, що проходив 13-14 вересня 2009 року.&nbsp;</p>\n<p>«Наша Церква постійно плекає різні ініціативи, зокрема, у питанні захисту життя від моменту зачаття. Яскравим прикладом плоду діяльності у цьому напрямку є прийняття Синодом «Програми протидії абортам в Україні». Це подія, яка свідчить про входження у новий етап виконання місії служіння життю», ‒ розповідає п. Степан Дмитришин, голова Комісії УГКЦ у справах душпастирства охорони здоров’я.</p>\n','2010-03-02 14:13:45','http://www.oranta.org/index.php?option=com_content&view=article&id=499:ugkc-vpershe-vidznachyla-den-pamjati-abortovanyh-dite-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'УГКЦ вперше відзначила День пам‘яті абортованих дітей  ','uk',1980,3463,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"http://www.ukrinform.ua/files/news/ukr/images/829/522/middle_8deea5d1b7dc88b5ace1f57c0093b804.jpg\" title=\"199 років тому народився Тарас Шевченко\" alt=\"199 років тому народився Тарас Шевченко\" />Щороку 9 березня українці святкують День народження Тараса Григоровича  Шевченка, символа Української держави, поета і прозаїка, художника і  мислителя.</p>\n<p>Тарас Шевченко народився 9 березня 1814 року у  с. Моринці (нині Черкаської області) в закріпаченій селянській родині.  Рано втративши матір (1823 року), яка померла від тяжкої праці й  злиднів, залишивши сиротами шестеро дітей, він з дитинства зазнав багато  горя і знущань.</p>\n','2013-03-09 13:22:37','http://oranta.org/index.php?option=com_content&view=article&id=1027:ukrajinsky-kobzar&catid=28:tema-nomera-cat&Itemid=2','P','','Український Кобзар','uk',3005,4488,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcT5RM5pUvwmsxf_M6DMQcxAKjZ9oWI9kSd3-Rcq108ievCoeARS\" height=\"136\" width=\"101\" /></p>\n<p><strong>Отець і Глава Української Греко-Католицької Церкви Блаженніший  Святослав (Шевчук) звернувся до вірян та людей доброї волі з нагоди Дня  молитви за збереження природного довкілля та всього створеного Богом  світу.</strong></p>\n<p>Голова Церкви нагадав, що цього дня християни різних конфесій в  усьому світі дякують Богові за дари створіння, просять вибачення за  шкоду, яку свідомо чи несвідомо завдають природі, та молять про захист і  спасіння всього світу.</p>\n','2013-09-15 10:51:21','http://oranta.org/index.php?option=com_content&view=article&id=1471:httpcatholicnewsorguapilniy-poglyad-na-ieretichni-proroctva-mariyi-bozhogo-miloserdya&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'День Бога Творця','uk',3594,5077,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,3682,200,P5020411.JPG\" alt=\"\" /><strong>&laquo;Витай між нами, Христе, витай!<br />Нехай радіє душа Тобою<br />I наповняється чаром-красою:<br />Ти наше сонце, життя і рай,<br />Витай між нами, Христе, витай!&raquo;</strong></p>\r\n<p>Олександр Турковський, який народився у місті Самборі Львівської області, разом зі своєю дружиною Зоєю виховує чотирьох дітей: первістка Іллю, Івана, Павла та найменшу донечку Ганусю. Отже сьогодні спілкуємося з паном Олександром про його родину, церковне та суспільне життя.</p>','2010-11-10 07:45:33','http://oranta.org/index.php?option=com_content&view=article&id=614:oleksandr-turkovsky-lja-vyhovuju-dite-a-dity-vyhovujut-mener&catid=25:parafija-cat&Itemid=27','N','','Олександр Турковський: «Я виховую дітей, а діти виховують мене…»','uk',2208,3691,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,3987,200,CRW_0976.JPG\" alt=\"\" />Цими днями українці святкують 10-ту річницю пасторального візиту Блаженного Папи Івана Павла ІІ. У Києві святкування розпочалися ще 14 липня відкриттям виставки &laquo;Папа, Україна, Я&raquo;. За словами Владики Йосипа Міляна, в основному святкування проходить у двох містах, саме де перебував Папа: Київ та Львів. У кожного міста своя програма святкувань. За організацію у Києві відповідає сам Владика, тому і бажає, щоб програма була повною, насиченою, духовною.</p>','2011-06-24 22:26:23','http://oranta.org/index.php?option=com_content&view=article&id=765:lpapa--avtorytet-vysokoji-moralir-blazhennishy-ljubomyr-guzar&catid=27:gk-v-ukrajini-cat&Itemid=57','N','','«Папа – авторитет високої моралі» - Блаженнійший Любомир Гузар','uk',2505,3988,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,4323,200,2.jpg\" alt=\"\" />20 жовтня у суботу на Аскольдовій Могилі відбувся запланований захід &ndash; толока біля церкви св. Миколая. Спершу, о восьмій годині розпочалася Божественна Літургія. Згодом об одинадцятій годині розпочалася і толока. Це вже четвертий рік поспіль люди доброї волі збираються тут, щоб прибрати територію, посадити дерева, згребти листя.</p>','2012-10-23 01:26:33','http://oranta.org/index.php?option=com_content&view=article&id=899:chyste-dovkillja--chysti-sercja&catid=25:parafija-cat&Itemid=27','N','','Чисте довкілля – чисті серця','uk',2841,4324,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,4353,200,DSCN5264.JPG\" alt=\"\" /></p>\r\n<p>15 грудня 2012 р. у затишній залі НПУ ім. М.Драгоманова відбувся Десятий фестиваль-конкурс духовної пісні &laquo;Аскольдів глас&raquo;. Пісенне свято розпочалося із привітального слова реколектанта о.&nbsp;Йосифа Будая.</p>\r\n<p>У конкурсі цьогоріч брали участь співаки-солісти в народній, академічній та естрадній виконавських манерах віком від 14 до 30 років. Заявки на участь подало дев&rsquo;ять співаків, серед яких кияни та гості з м. Самбір і м.Івано-Франківськ, а саме:Вікторія Мартин, Лілія Маківка, Любов Павлішак, Юрій Мединський, Христина Шонь, Соломія Щур, Анастасія Друзюк, Оксана Сливка та Олена Захарова.</p>','2012-12-17 01:29:17','http://oranta.org/index.php?option=com_content&view=article&id=912:2012-12-16-23-42-45&catid=25:parafija-cat&Itemid=27','P','','Незабутні сторінки  Х фестивалю-конкурсу духовної пісні «Аскольдів глас»','uk',2871,4354,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,4445,200,2.jpg\" alt=\"\" />Шостого лютого о дев&rsquo;ятій годині вечора дитяча група з Аскольдової могили відправилася поїздом Київ - Івано-Франківськ на лижній відпочинок в Карпати. Група складалася з двадцять двох осіб. З вихователів були наші парафіяни Мирослава Мазур, Дмитро Засульський, Олександр Турковський, а також парох нашої церкви отець Ігор Онишкевич з дружиною Антоніною.</p>\r\n<p>Наступного дня ми прибули в Івано-Франківськ, де на нас уже чекав автобус. Ми вирушили в гатель, який знаходився не далеко від Ворохти. Дорога була довгою, тому дехто заснув, а хтось милувався краєвидами Карпат.</p>','2013-02-15 00:20:22','http://oranta.org/index.php?option=com_content&view=article&id=992:u-zasnizhenyh-karpatah&catid=25:parafija-cat&Itemid=27','P','','У засніжених Карпатах','uk',2968,4451,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,5161,200,4456.d.jpg\" alt=\"\" />Роль жінки і материнства були в центрі промови Святішого Отця до учасників семінару, організованого у Ватикані з нагоди двадцять п\'ятої річниці Апостольського листа бл. Івана Павла II \"Mulieris dignitatem\" &minus; \"історичного документу, першого в Папському навчанні, повністю присвяченого темі жінки\". 12 жовтня 2013р., звертаючись до присутніх у Клементійському Залі Апостольського палацу, Франциск висловив свою вдячність за увагу до цієї теми. Учасники ватиканського семінару головним чином роздумували над фрагментом Апостольського листа, в якому мова йде про те, що Бог особливим чином довірив людство жінці.</p>','2013-10-14 10:06:28','http://oranta.org/index.php?option=com_content&view=article&id=1540:pytannja-pro-rol-zhinky-v-suspilstvi--cerkvi-blyzke-mojemu-sercju-papa-francysk&catid=30:hrystyjanske-vchennia-cat&Itemid=37','N','','питання про роль жінки в суспільстві й Церкві близьке моєму серцю - Папа Франциск','uk',3682,5165,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,5212,200,120.JPG\" alt=\"\" />Двадцять п&rsquo;ятого жовтня цього року розпочався осінній чотириденний відпочинок дітей з церкви, що на Аскольдовій могилі, у Закарпатті. Старшокласники мали змогу відпочивати у Рахівському районі, присілок Банський. Саме в цих місцях народилася і проживала наша сестра &ndash;монахиня Філомена, яка також брала участь у цій поїздці та змогла організувати нам чотири незабутніх дні відпочинку.</p>','2013-10-31 00:03:06','http://oranta.org/index.php?option=com_content&view=article&id=1582:chotyry-nezabutnih-dni-shljahamy-zakarpattja&catid=25:parafija-cat&Itemid=27','P','','Чотири незабутніх дні – Шляхами Закарпаття','uk',3731,5214,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" src=\"image,6343,200,1060360-crop.jpg\" alt=\"\" />Молодий обдарований&nbsp; композитор кандидат&nbsp; мистецтвознавства&nbsp;&nbsp; Катерина&nbsp; Кучерява (Чаплик), як&nbsp; і&nbsp; італійський&nbsp; педагог Марія&nbsp; Монтессорі&nbsp; з Італії, вважає: &ldquo;Тепер багато хто, у тому числі&nbsp; і&nbsp; я, вважають, що найважливіший період&nbsp; життя&nbsp; припадає&nbsp;&nbsp; не на&nbsp; студентські&nbsp; роки, а скоріше на перший період, від народження до шести років, оскільки саме тоді&nbsp; формується певний комплекс&nbsp; психічних&nbsp; властивостей, формується людська індивідуальність. У цей період&nbsp; у дитини формується&nbsp; &ldquo;чуттєвий&nbsp;&nbsp; образ&nbsp; навколишнього&nbsp; світу&rdquo;. (Із&nbsp; школи Катерини Чаплик).</p>','2015-01-16 21:09:49','http://oranta.org/index.php?option=com_content&view=article&id=2582:dialog-pro-mystectvo&catid=25:parafija-cat&Itemid=27','N','','Діалог  про  мистецтво','uk',4861,6344,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: left;\" title=\"кардинал Любомир Гузар\" src=\"http://1-12.org.ua/wp-content/uploads/2012/02/huzar_7.jpg\" alt=\"кардинал Любомир Гузар\" height=\"200\" width=\"160\" />«Я  не вважаю, що наше суспiльство полiтизоване. Корумповане – так! На  жаль, корупцiя, хабарництво охопили наше суспiльство, ми це бачимо в  рiзнi способи. Корупцiя дуже поширена, вона стала способом життя в нас,  але це не є полiтика». Таке переконання висловив Архиєпископ-емерит,  член Ініціативної групи «Першого грудня» Блаженніший Любомир (Гузар)  в&nbsp;інтерв’ю газеті «Факти та коментарі».</p>\n<p>Він переконує, що українське суспільство, включно з владою, «замало сполiтизоване».</p>\n','2013-01-15 01:51:00','http://oranta.org/index.php?option=com_content&view=article&id=922:parafija-cerkvy-sv-mykolaja-myr-likiskogo-na-askoldovi-mogyli&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Українське суспільство замало політизоване»','uk',2891,4374,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" alt=\"18.07.2010\" src=\"http://www.tze.org.ua/images/stories/articles_12.02.10/18.07.2010.jpg\" height=\"171\" width=\"255\" />Президент Єпископської Конференції Італії, архиєпископ і митрополит Генуї Анжело кардинал Баньяско (Angello Bagnasco) та Апостольський нунцій в Україні Іван Юркович взяли участь у Всеукраїнській прощі до Марійського духовного центру Зарваниця, що відбулася 17-18 липня. На чудотворному місці почесних гостей вітав блаженніший Любомир кардинал Гузар, глава УГКЦ, владика Василій Семенюк, єпарх Тернопільсько-Зборівський, 14 єпископів УГКЦ, священики та вірні. У паломництві, присвяченому року монашого покликання, взяли участь понад 80 тисяч прочан з різних областей України та закордону.</p>\n','2010-07-19 12:58:59','http://oranta.org/index.php?option=com_content&view=article&id=556:vidbulasja-vseukrajinska-proshcha-do-zarvanyci-fotohronika-&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Відбулася Всеукраїнська проща до Зарваниці ','uk',2104,3587,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" alt=\"pope-852-rtx10t1p\" src=\"http://popefrancis.org.ua/wp-content/uploads/2013/06/pope-852-rtx10t1p-300x168.jpg\" height=\"133\" width=\"236\" />Зазвичай,  коли якась особа вступає на нову посаду, перші 100 днів її праці є  перехідними. В цей час визначається її особистість і стиль праці. У  випадку Папи Франциска, світові не довелось чекати довго, щоб пізнати  його стиль. Всього за кілька місяців його відкритість оживила Церкву.  Агенція <a href=\"http://www.romereports.com/\" target=\"_blank\">Rome reports</a> визначила ключові точки, які, на їх думку, є визначальними в перші дні понтифікату Святішого Отця Франциска.\n','2013-06-24 18:39:01','http://oranta.org/index.php?option=com_content&view=article&id=1287:100-dniv-pontyfikatu-znakovi-zajavy-ta-ukrajinsko-polska-katolycka-solidarnist&catid=32:gk-svit-cat&Itemid=58','P',NULL,'100 днів понтифікату, знакові заяви та українсько-польська католицька солідарність','uk',3378,4861,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" alt=\"popovych1\" src=\"http://static.maidanua.org:8081/wp-content/uploads/2013/10/popovych1.jpg\" height=\"189\" width=\"145\" />Українцям  треба навчитись одночасно виконувати три завдання: утверджувати  незалежність, розвивати демократію і зміцнювати добробут, вважає філософ  Мирослав Попович. В інтерв’ю&nbsp;<a href=\"http://gazeta.ua/\" target=\"_blank\" rel=\"nofollow\">Gazeta.ua </a>пан Мирослав розповів про вади українців і поділився своїми оптимістичними поглядами на майбутнє.</p>\n<p><em>- Українці чекають на підписання Угоди про асоціацію з ЄС. Чи є небезпека, що ми розраховуємо на манну з неба?</em></p>\n<p>Мирослав Попович:&nbsp;Небезпека є. Перший час це більше забиратиме, аніж  даватиме щось Україні. Треба буде завойовувати європейський ринок. А наш  експорт не дуже цікавий для них.</p>\n','2013-10-17 19:24:11','http://oranta.org/index.php?option=com_content&view=article&id=1554:zaraz-istorychna-myt--zakinchujutsja-300-rokiv-nashogo-pidporjadkuvannja-moskvi&catid=37:spilnota-cat&Itemid=49','P',NULL,'Зараз історична мить – закінчуються 300 років нашого підпорядкування Москві','uk',3692,5175,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" alt=\"о.Віталій Кащук\" src=\"http://www.credo-ua.org/wp-content/uploads/2012/03/Vitaliy-Kashchuk-200x175.jpg\" height=\"124\" width=\"140\" /><strong></strong></p>\n<p><strong>Багато  вірян пам’ятають піднесений дух кінця 1980-х – початку 1990-х років.  Тоді (на тлі турбот про дефіцитні товари, виснажливих черг і дихання на  ладан тодішньої форми суспільного ладу) «дозвіл» – як це називали –  ходити до храмів був сприйнятий як довго-довго очікуване полегшення.</strong></p>\n<p>Це полегшення, якого багато віруючих не дочекалися. Одні до нього  ставилися (й досі ставляться) з глибоко вкоріненою підозрою і воліють не  похвалятися, що до якогось там храму ходили, пам’ятаючи, яку ціну в  кар’єрі треба було раніше за це платити, які сімейні та внутрішні  компроміси допускати.</p>\n','2013-06-07 09:51:55','http://oranta.org/index.php?option=com_content&view=article&id=1236:cerkva-sklykaty-chy-narodyty&catid=37:spilnota-cat&Itemid=49','P',NULL,'Церква. Скликати чи народити?','uk',3310,4793,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" ilo-full-src=\"http://www.credo-ua.org/wp-content/uploads/2011/08/Tisaca-sharikov.jpg\" src=\"http://www.credo-ua.org/wp-content/uploads/2011/08/Tisaca-sharikov.jpg\" title=\"тисяча кульок\" height=\"115\" width=\"164\" />Кілька тижнів тому я приготував собі каву, взяв ранкову газету і  сів послухати радіо. Я повертав ручку приймача, поки раптом мою увагу не  привернув оксамитовий голос якогось старого чоловіка. Він щось казав  про «тисячі кульок». Я зацікавився, зробив гучніше і відкинувся на  спинку крісла ...</p>\n<p>- Добре, - сказав старий, - можу посперечатися, що ви дуже зайняті на  роботі. Вчора, сьогодні, завтра. І нехай вам платять багато. Але за ці  гроші вони купують ваше життя. Подумайте, ви не проводите цей час зі  своїми коханими і близькими. Нізащо не повірю, що вам потрібно працювати  весь цей час, щоб звести кінці з кінцями. Ви працюєте, щоб задовольнити  ваші бажання.</p>\n','2011-08-31 10:10:14','http://oranta.org/index.php?option=com_content&view=article&id=783:prytcha-pro-tysjachu-kulok&catid=37:spilnota-cat&Itemid=49','P',NULL,'Притча про тисячу кульок','uk',2560,4043,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,1458,200,1.jpg\" /></p>\n<p>Того листопадового дня, саме <strong>13 числа</strong> у п‘ятницю, моя душа була  переповнена щастям та радістю. Отець Ігор мав посвячувати моє  помешкання. З‘їхалась уся родина, мати, батько, сестри, друзі. Душа не  відчувала ані смутку, ані жалю, лише радість і любов. Вранці приїхала у  храм на молитву. Очі не хотіли вірити тому. що побачили...</p>\n<p>Розписані та розмальовані різними нехристиянськими знаками мури  церкви, розбите вікно. кіптява, їдкий дим. Горіла церква, святиня, Божий  храм. Все це просто розірвали і розпорошили мої світлі відчуття та  почуття. Такого жалю і відчаю не відчувала продовж всього життя. Невже  могла людина, чи декілька людей наважитись на Святе. Так безжалісно і  брутально вчинити.</p>\n','2013-11-13 13:45:27','http://oranta.org/index.php?option=com_content&view=article&id=1634:askoldova-zarvanycka-bogorodycja&catid=25:parafija-cat&Itemid=27','P',NULL,'Аскольдова Зарваницька Богородиця','uk',3780,5263,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,1458,200,1.jpg\" />Поглянь, мамо! – вигукнула семирічна Марта.</p>\n<p>- Зараз, зараз! – автоматично вимовила знервована жінка за кермом, маючи на голові купу справ, що іще очікували на неї вдома.</p>\n<p>Потім була вечеря, перегляд телепрограм, купання, телефонні розмови, аж прийшов час коли треба було вкладатись спати.</p>\n<p>- Марто, вже час іти в ліжечко!</p>\n<p>Дівчинка побігла східцями вгору. Мама вже падала від утоми, та цілуючи  її, уже ж знайшла в собі сили, щоб помолитися з донечкою.</p>\n','2013-03-11 23:01:55','http://oranta.org/index.php?option=com_content&view=article&id=1032:-odyn-z-bagatoh-dniv-12-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Один з багатьох днів (12 березня)','uk',3013,4496,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3121,200,HID4.jpg\" />Напередодні Дня Всіх святих українського народу у минулу суботу відбулася IV проща до мощей блаженної Едігни з благословення Апостольського Екзарха Німеччини і Скандинавії Петра Крика. Під час подорожі прочани поклонилися до мощей блаженної Едігни, зустрілися із головою громадської організації «Товариство Едігна» Едігною Келлерманн,  а  також відслужили Божественну літургію.\n','2009-07-06 22:51:22','http://www.oranta.org/index.php?option=com_content&view=article&id=370:tradycina-proshcha-do-puhu&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Традиційна проща до Пуху','uk',1639,3122,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3284,200,3.jpg\" />27 квітня 2004 року громада церкви св. Миколая Чудотворця на Аскольдовій Могилі з благословення владики Василя Медвіда, екзарха Києво-Вишгородського, і світлої пам\'яті владики Михайла Сябри, єпарха Тернопільського, з допомогою отця мітрата Дмитра, декана Зарваницького, і отця Ігоря Онишкевича встановили Хресну дорогу - «Заздрість-Зарваниця» Ця Хресна дорога, це дорога маленького Йосипа, якою у 1893 році несла його мати на руках до Матінки Божої Зарваницької, бо він був тяжко хворий. Тоді наче сама Мати Божа на руках цією дорогою принесла Йосипа до чудотворного образу Богородиці Зарваницької.</p>\n','2009-09-14 00:23:28','http://www.oranta.org/index.php?option=com_content&view=article&id=433:kyjiv-zazdrist-zarvanycja&catid=28:tema-nomera-cat&Itemid=2','P','','Київ - Заздрість - Зарваниця','uk',1800,3283,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3535,200,IMG_1143.JPG\" />Для кожного першотравневі свята асоціюються із відпочинком і можливістю кудись поїхати, адже саме свято давно &nbsp;втратило своє першопочаткове значення. Для мене випала хороша нагода взяти участь у молодіжній прощі із Ялти&nbsp; до Судака,&nbsp; що проходила із 1 по 3 травня і організована братом Августином із згромадження Васильян. У цій велопрощі взяли участь 37 учасників із різних регіонів України. За ці три дні молодь мала подолати <st1:metricconverter productid=\"120 км\">120 км</st1:metricconverter> кримських доріг, які характерні стрімкими спусками та підйомами. Згідно розкладу всі збирались у місті Ялті біля римо-католицького костелу, на всіх хто приїздили на місце збору уже чекав обід. Після обіду молодь помолилась&nbsp; молебень до Богородиці за щасливу прощу. Вирушаючи в прощу ми просимо у певному наміренні, крім того що кожен мав, особисте намірення було також із загальне намірення, за навернення грішників та за кращу долю України.</p>\n','2010-05-11 21:18:31','http://oranta.org/index.php?option=com_content&view=article&id=527:molodizhna-veloproshcha&catid=25:parafija-cat&Itemid=27','P','','Молодіжна велопроща','uk',2054,3537,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3542,200,IMG_0306.JPG\" />13 травня 2010 р. виповнилось п’ять років щомісячній прощі до св. Софії. Духовним поштовхом для спільної молитви на Софійському майдані стала могутня всенародна молитва на майдані Незалежності в 2004 році. Цей мирянський рух проходить під гаслом «Схід і Захід під покровом Пресвятої Богородиці».</p>\n<p>Кожного 13 числа (у день Фатімських об’явлень) о 13.00, у будні – о 19.00 вірні УГКЦ, УПЦ КП, УАПЦ, УПЦ (МП), РКЦ збираються біля Софії Київської, щоб молитись за єдність Церков, за мир і злагоду в Україні.</p>\n','2010-05-15 02:47:54','http://oranta.org/index.php?option=com_content&view=article&id=531:proshcha-do-sv-sofiji-lshid-i-zahid-pid-pokrovom-presvjatoji-bogorodycir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Проща до св. Софії «Схід і Захід під покровом Пресвятої Богородиці»','uk',2063,3546,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3550,200,1.jpg\" />22 травня о 10 годині у суботу, на Аскольдовій Могилі зібралося багато парафіян, щоб відсвяткувати сьогоднішні святкові події.</p>\n<p>Саме сьогодні 200 років будованому Храму святого Миколая. Це визначна дата парафії.</p>\n<p>Крім того, усі присутні в церкві мали змогу побачити освячення розпису куполу Храму. А також парафія святкувала празник Теплого Миколая. Отже цей день був насичений різноманітними подіями і з впевненістю можна сказати, що він залишив духовний слід у серцях всіх присутніх.</p>\n','2010-05-26 02:03:35','http://oranta.org/index.php?option=com_content&view=article&id=533:svjatkovy-den-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','Святковий день на Аскольдовій Могилі','uk',2068,3551,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3556,200,3.JPG\" />29 травня на Аскольдовій Могилі зібралося чимало людей, адже сьогодні було офіційно представлено розписаний купол храму святого Миколая.</p>\n<p>В цей день були присутні високі гості: Журавський Віталій Станіславович, заступник голови Київської міської державної адміністрації – начальник Головного управління освіти і науки, Руслан Кухаренко, начальник головного управління охорони культурної спадщини КМДА, та владика УГКЦ &nbsp;Йосиф Мілян.</p>\n','2010-06-02 02:11:16','http://oranta.org/index.php?option=com_content&view=article&id=538:predstavlennja-rozpysu-kupolu-hramu-svjatogo-mykolaja-gromadskosti&catid=25:parafija-cat&Itemid=27','P','','Представлення розпису куполу храму святого Миколая громадськості','uk',2076,3559,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3556,200,IMG_3468.JPG\" />Закладається гарна традиція, другий рік, на теплого Миколая, в церкві на Аскольдовій могилі, в контексті парафіяльного свята, проводяться молодіжні чування. Чування – це цікава форма молитви, яка шляхом розважань і коротких канонів й пісень спонукає до глибшого пізнання Бога і самого себе.</p>\n<p>Так гарно співпало, що в цьому році травневі чування відбуваються в переддень великого свята – зіслання Святого Духа.</p>\n','2010-06-02 01:52:00','http://oranta.org/index.php?option=com_content&view=article&id=536:nichni-chuvannja&catid=25:parafija-cat&Itemid=27','P','','Нічні чування','uk',2074,3557,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3561,200,IMG_4453.JPG\" />Вже стало хорошою традицією молодіжної спільноти церкви Миколая Чудотворця, здійснювати екскурсійні поїздки до різних куточків України. Цього разу молодь вирішила відвідати з екскурсією&nbsp; Полтаву і в суботу 29 травня разом із о.Олексієм вирушили на екскурсію.</p>\n<p>Подорож молоді почалась із спільної молитви за щасливу подорож у церкві Миколая Чудотворця та з благословення о.Ігора. Помолившись молодь вирушила у дорогу, яка була неблизькою, адже в одну сторону їхати <st1:metricconverter productid=\"350 км\">350 км</st1:metricconverter> та молодь несумувала із цього приводу, а навпаки використовувала час у дорозі із користю: молилась вервицю, співала релігійні та народні пісні, а ще крім того мала можливість поспілкуватись та пізнати краще один одного.</p>\n','2010-06-07 12:55:25','http://oranta.org/index.php?option=com_content&view=article&id=543:ekskursija-do-poltavy&catid=25:parafija-cat&Itemid=27','P','','Екскурсія до Полтави','uk',2081,3564,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3561,200,IMG_5240.JPG\" />4&nbsp; червня у Прилуцькій виховній колонії для неповнолітніх відбулася радісна подія – літнє спортивне свято «Козацький гарт». Його було організовано коштами жертводавців Київської Архиєпархії Української Греко-Католицької Церкви за сприяння начальника установи пана В’ячеслава Радченка та співробітників колонії Олександра Гусака і&nbsp; Олександра Іщенка.</p>\n','2010-06-07 12:29:59','http://oranta.org/index.php?option=com_content&view=article&id=541:kozacky-gart-2010&catid=25:parafija-cat&Itemid=27','P','','Козацький гарт - 2010','uk',2079,3562,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3579,200,9.JPG\" />24 червня 2010 року 27 дітей з Аскольдової могили, отець Олексій Геник, сестра Анастасія та кілька катехитів вирушили у чарівний край - Херсонщину, щоб провести там 10 незабутніх днів відпочинку на морі. Головним завданням цієї подорожі було духовне збагачення, а також активний відпочинок: купання в морі, гра на теплому піску&nbsp; у футбол, волейбол...</p>\n<p>Отож 25 червня діти прибули у Херсон.</p>\n','2010-07-09 12:21:50','http://oranta.org/index.php?option=com_content&view=article&id=550:na-mori-u-poshuku-duhovnyh-skarbiv&catid=25:parafija-cat&Itemid=27','P','','На морі… У пошуку духовних скарбів…','uk',2097,3580,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3581,200,2578256.JPG\" />24 червня 27дітей разом з вихователями, сестрою Анастасією і священиком УГКЦ Олексієм Геником відправилися в подорож на 11 днів. Прямував християнський табір до Херсона в місто Залізний порт, де і знаходиться табір «Примор’я». Там і розташувались діти Аскольда.</p>\n<p>Наприкінці подорожі я поспілкувався з друзями, які поділились враженнями про духовний та фізичний відпочинок на морі.</p>\n','2010-07-12 22:48:32','http://oranta.org/index.php?option=com_content&view=article&id=552:vrazhennja-dite-z-parafiji-svjatogo-mykolaja-pro-vidpochynku-na-mori&catid=25:parafija-cat&Itemid=27','P','','Враження дітей з парафії святого Миколая про відпочинку на морі','uk',2100,3583,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3584,200,1.jpg\" />15 липня на Аскольдовій могилі зібралося багато парафіян церкви святого Микола Чудотворця, щоб взяти молитовну участь у посвяченні крипти Аскольда. У цей день була відслужена Архієрейська Божественна Літургія. Богослужіння у церкві святого Миколая очолив владика Йосиф Мілян, йому спів служили парох церкви святого Миколая – Ігор Онишкевич та інші священнослужителі УГКЦ.</p>\n','2010-07-16 01:53:30','http://oranta.org/index.php?option=com_content&view=article&id=554:posvjachennja-krypty-askolda&catid=25:parafija-cat&Itemid=27','P','','Посвячення  крипти Аскольда','uk',2102,3585,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3591,200,6.JPG\" />28 липня Україна святкує велике свято Хрещення Київської Русі князем Володимиром&nbsp; Великим. З цієї нагоди у церкві святого Миколая, що на Аскольдовій могилі, зібралося багато парафіян церкви, щоб відсвяткувати цю величну для християн подію.</p>\n<p>Молебень до святого рівноапостольного князя Володимира відслужив Блаженнійший Любомир кардинал Гузар, глава УГКЦ. Йому співслужили владика Йосиф Мілян та єпископ Богдан Дзюрах, а також священики УГКЦ разом з парохом нашої церкви отцем Ігорем Онишкевичем.</p>\n','2010-07-28 23:17:18','http://oranta.org/index.php?option=com_content&view=article&id=563:moleben-do-svjatogo-rivnoapostolnogo-knjazja-volodymyra&catid=25:parafija-cat&Itemid=27','P','','Молебень до святого рівноапостольного князя Володимира','uk',2113,3596,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3592,200,12.JPG\" /></p>\n<p><strong>Дорогий татку!</strong></p>\n<p><strong> </strong></p>\n<p>Тебе з мільйонів Бог сам вибрав,</p>\n<p>Щоб ти цей шлях зумів пройти,</p>\n<p>Щоб всю дорогу йшов невпинно,</p>\n<p>Щоб істину зміг донести.</p>\n','2010-07-24 22:02:57','http://oranta.org/index.php?option=com_content&view=article&id=562:z-dnem-narodzhennja-vas-otche-igorju&catid=25:parafija-cat&Itemid=27','P','','З Днем народження Вас, отче Ігорю!!!','uk',2110,3593,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3594,200,CRW_8530.jpg\" />30 жовтня 2010 р. Б. святкує свій важливий ювілей настоятель нашого храму протопресвітер отець Ігор Онишкевич -- 20-ліття ієрейських свячень! І це не просто річниця із дня набуття професії, як то є в мирі; це свято отримання від Бога особливого дару -- бути Його священиком, це день звіту «доброго і вірного слуги» перед милосердним Господарем виноградника, день збору «врожаю плодів земних».</p>\n','2010-10-30 18:46:15','http://oranta.org/index.php?option=com_content&view=article&id=610:vrozha-plodiv-zemnyh-20-littja-ijereskyh-svjachen-otcja-igorja-onyshkevycha&catid=25:parafija-cat&Itemid=27','P',NULL,'ВРОЖАЙ ПЛОДІВ ЗЕМНИХ: 20-ЛІТТЯ ІЄРЕЙСЬКИХ СВЯЧЕНЬ ОТЦЯ ІГОРЯ ОНИШКЕВИЧА','uk',2201,3684,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3609,200,IMG_7530.JPG\" />“Спочатку Служба Божа – потім все інше”</p>\n<p align=\"right\">с. Стефанія</p>\n<p>Віднова Ізраїлю [Іс. 44: 3–4]: “Бо я виллю воду на спраглу землю, потоки вод на сушу. Я виллю мій дух на твоїх потомків, моє благословення на твоїх нащадків. Вони ростимуть, наче трава над водою, наче верби над річками.”</p>\n','2010-09-01 21:31:53','http://oranta.org/index.php?option=com_content&view=article&id=573:molodizhny-tabir-v-zalishchykah&catid=25:parafija-cat&Itemid=27','P','','Молодіжний табір в Заліщиках','uk',2129,3612,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3637,200,Gennady-Symonov-Vira-Ndiya-lyubov.jpg\" />Нашого душпастира Ігоря-Іллі Онишкевича вітаємо з великим духовним святом.</p>\n<p>Наш парох Ігор-Ілля 20 років тому під покровом святих мучениць Віри, Надії, Любові та матері їх Софії прийняв Святу Тайну священства з рук владики Павла Василика у храмі святого Воскресіння Христового у Станіславі. І в часі Служби Божої прийняв перше святе причастя Господа Нашого Ісуса Христа як священик.</p>\n<p>У той день намірення Служби Божої – Бог бо гордим противиться, а покірливим дає благодать. 20 років це намірення Ви, отче, несете у своєму серці і служінні.</p>\n<p>Многая літа, отче Ігорю, за ваше терпеливе і смиренне служіння Богу та Україні.</p>\n<p>Вірні парафіяни.</p>','2010-09-30 22:23:15','http://oranta.org/index.php?option=com_content&view=article&id=593:my-ne-zabuly&catid=25:parafija-cat&Itemid=27','P','','Ми не забули.','uk',2163,3646,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3664,200,100379.jpg\" />Коли землю покидає праведник, в небі стає більше на одного за нас заступника, небо стає ріднішим... 14 жовтня -- день Покрова Пресвятої Богородиці -- стало з Божої волі днем пам’яті одного з таких праведників -- Валерія Марченка, українського журналіста-дисидента, який у 25 років потрапив за свої писання до радянської тюрми як в’язень сумління, а в 37 років вийшов із неї у цинковій труні. Його мати, Ніна Михайлівна Марченко, яка 12 років боролася не тільки за звільнення, але й за життя свого єдиного невиліковно хворого сина, яка всі роки після його смерті живе пам’яттю про Валерія та трудиться для увічнення його праці, попросила о. Ігоря Онишкевича молитовно згадати в цей день його світлу душу. 14 жовтня Аскольдова могила молилася за Валерія Марченка.</p>\n','2010-10-16 23:00:31','http://oranta.org/index.php?option=com_content&view=article&id=608:pamjat-pro-valerija-marchenka&catid=25:parafija-cat&Itemid=27','P',NULL,'ПАМ’ЯТЬ ПРО ВАЛЕРІЯ МАРЧЕНКА','uk',2183,3666,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3682,200,s320x240.jpeg\" />Історія Бабиного Яру в минулому сторіччі, зокрема в часи Другої світової війни страшна і повчальна. Десятки тисяч жертв – це реальна ціна за людську безвідповідальність і маніакальне бажання панувати над світом. Водночас – це повчальний урок для нинішнього покоління, яке має знати усю правду про одну з найбільших трагедій людства.</p>\n<p>Не зважаючи на те, що в історичній літературі та публіцистиці здебільшого висвітлюється єврейський аспект теми, вже сьогодні можна стверджувати про український, в тому числі християнський вимір Бабиного Яру.</p>\n','2010-11-13 17:50:41','http://oranta.org/index.php?option=com_content&view=article&id=619:hrystyjansky-vymir-babynogo-jaru&catid=37:spilnota-cat&Itemid=49','P',NULL,'Християнський вимір Бабиного Яру','uk',2213,3696,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3728,200,choir.jpg\" />11 грудня 2010 року у рамках IV Суспільних днів УГКЦ з благословення преосвященнішого владики Йосифа, єпископа-помічника Київської Архиєпрахії, у Києві відбувся семінар присвячений розвитку душпастирської опіки нашими співгромадянами за ґратами. На семінарі «Покарані, але не відкинені» йшлося про роль волонтерської праці в опіці в’язнями. У другій половині цього ж дня пройшов виступ хору Патріаршого Собору перед &nbsp;засудженими Київського слідчого ізолятора.</p>\n','2010-12-14 23:23:12','http://oranta.org/index.php?option=com_content&view=article&id=635:suspilni-dni-u-vjaznyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Суспільні дні у в’язниці','uk',2247,3730,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3731,12204189.jpg\" />Давно-давно, 1600 років тому, жив у місті Мирах, у Малій Азії, хлопець Миколай. Був він сирота. Його батько й мати померли, залишивши синові чималий спадок.</p>\n<p>У серці Миколая була велика доброта, і він вирішив роздати своє майно бідним. Та тільки ж був він дуже несміливий і тому почувався нещасливим.</p>\n<p>У безсонні ночі виходив Миколай на дах свого будинку й дивився на бідні домівки. Біля них спали їх убогі мешканці. У домівках, нагрітих за дня гарячим сонцем, було душно, отож люди лягали спати перед порогом на землі. Лежали вони в лахмітті, подертому на важкій праці в копальнях мармуру. Спали тут і малі діти, які ніколи ще не зазнали в житті радості.</p>\n','2010-12-15 22:18:34','http://oranta.org/index.php?option=com_content&view=article&id=636:svjaty-mykola--opikun-dite-&catid=35:istorychni-postati-cat&Itemid=48','P',NULL,'Святий Миколай — опікун дітей ','uk',2249,3732,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3735,200,IMG_0633.JPG\" />19 грудня 2010 р. Б о 14:30 в актовому залі Національного транспортного університету. відбулося святкування парафіяльного свята св. Миколая Чудотворця.</p>\n<p>Святкування розпочалося концертом, присвяченим цьому вельмившанованому в українському народі святому. Врочисто, по святковому, прибрана зала занурювала присутніх у передріздвяну атмосферу. Прибрані іграшками колони, наче присипана снігом сцена, прикрашена квітами, ялинками та гірляндами створювали святковий настрій.</p>\n','2010-12-20 21:19:21','http://oranta.org/index.php?option=com_content&view=article&id=644:svjato-dlja-dite&catid=25:parafija-cat&Itemid=27','P',NULL,'Свято для дітей','uk',2257,3740,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3742,200,6.jpg\" />18 грудня 2010 року в актовому залі Національного педагогічного університету ім.&nbsp;М.Драгоманова відбулася велика подія. Цього дня пройшов VІ фестиваль - конкурс духовної пісні Аскольдів Глас. Крім того, у цей день парафіяни врочисто святкували знакову подію: 20 – річчя створення парафії святого Миколая на Аскольдовій могилі.</p>\n<p>Розпочався творчий форум словами пароха церкви святого Миколая отця Ігоря Онишкевича:</p>\n','2010-12-22 22:27:26','http://oranta.org/index.php?option=com_content&view=article&id=646:vi-festyval-konkurs-duhovnoji-pisni-askoldiv-glas&catid=25:parafija-cat&Itemid=27','P',NULL,'VІ фестиваль - конкурс духовної пісні Аскольдів Глас','uk',2260,3743,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3744,200,17.jpg\" />18 грудня 2010 року відбувся шостий фестиваль – конкурс духовної пісні Аскольдів Глас. Спілкуємося з Лесею Горовою щодо проведеного конкурсу Аскольдів Глас.</p>\n<p><strong>- Пані Лесю, скажіть, будь ласка, яким на Вашу думку був мистецький рівень учасників фестиваль – конкурсу духовної пісні Аскольдів Глас?</strong></p>\n<p>- На мою думку, мистецький рівень фестивалю був дуже високий. Це виявлялося і в професіоналізмі, і в музиці, в текстах, у виконанні творів. Ми оцінювали багато різних складників: артистизм, мелодію, текст, аранжування. Я б хотіла, щоб фестиваль розвивався і надалі. Адже таким чином ми залучаємо більше людей до творчої діяльності.</p>\n','2010-12-22 23:01:06','http://oranta.org/index.php?option=com_content&view=article&id=648:intervju-z-zasluzhenoju-artystkoju-ukrajiny-chlenom-zhuri-festyval--konkursu-askoldiv-glas-leseju-gorovoju&catid=25:parafija-cat&Itemid=27','P',NULL,'Інтерв’ю  з заслуженою артисткою України, членом журі фестиваль – конкурсу Аскольдів Глас Лесею Горовою','uk',2263,3746,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3747,200,!_Koliada-4.jpg\" />23 грудня, під керівництвом Валерія Гладунця, фольклориста, учасника фольклорного гурту «Божичі» в нижньому залі церкви Василія Великого пройшов перший урок \"Школи давньої української коляди\"</p>\n<p>Наступний урок проходитиме 30 грудня у неділю о 14:30 на Василіанах (Вознесенський узвіз, 7)</p>\n<p>\"Школа коляди\" запрошує долучатися до творення спільного Духу свята Різдва.</p>\n','2012-12-24 13:28:29','http://oranta.org/index.php?option=com_content&view=article&id=915:svjatkumo-rizdvo-razom&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Святкуймо Різдво Разом','uk',2876,4359,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3749,200,rizdvo.jpg\" />Христос Рождається!</p>\n<p>Славімо Його!</p>\n<p>Дорогі брати і сестри! Читачі і дописувачі газети «Оранти»!</p>\n<p>Парафіяни і прихожани Церкви св. Миколая чудотворця на Аскольдовій Могилі!</p>\n<p>Прийміть найщиріші вітання з Рождеством Христовим і Новим 2011 роком Божим.</p>\n','2010-12-27 23:35:16','http://oranta.org/index.php?option=com_content&view=article&id=650:z-rozhdestvom-hrystovym&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'З Рождеством Христовим!','uk',2267,3750,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3761,200,images.jpg\" width=\"112\" height=\"145\" />Коротка історія</p>\n<p>Празник Христового Різдва показує величний Божий план відкуплення людського роду. Божий Син з безконечної любови до нас стає малою Дитиною. На свою Матір Він вибирає Пречисту Діву Марію. Завдяки Пресвятій Богородиці Божі плани могли стати дійсністю. Без неї не було б Христового Різдва, не було б відкуплення. Відомий церковний письменник о. М. Мешлер роздумуючи про таїнство Христового воплочення і роль в ньому Пречистої Діви Марії, вигукує: \"О пречудне і взнесле створіння! В її руки і серце зложив Бог наше спасення! Без неї ми не мали б Ісуса, без неї ми не були б відкуплені. Вона дала свою згоду, і дала її свобідно з безграничної любови до нас... Безпосередній наслідок воплочення для Марії був такий, Ідо вона справді стала Богородицею. Марія є дійсно Мати Божа якраз тому, що вона є Мати Христова\" (Життя ГНІХ у розважаннях. Т. І, с. 72 і 79).</p>\n','2011-01-06 09:15:14','http://oranta.org/index.php?option=com_content&view=article&id=653:sobor-presvjatoji-bogorodyci&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Собор Пресвятої Богородиці','uk',2281,3764,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3761,200,sviata-vecheria.jpg\" />Наближається одне з найбільших християнських свят - Різдво Христове. Напередодні - 6 січня - кожна християнська родина збирається за столом до Святої вечері, а господиня дому готує 12 пісних страв, які символізують 12 апостолів Ісуса Христа.</p>\n<p>Кутя – окраса столу. На Львівщині кутю готують густою, з пшениці, маку, родзинок, грецьких горіхів та засолоджують медом. Одного разу гостювала на Харківщині, там куштувала кутю з рису, решта інгредієнтів ті ж. На Закарпатті ще також готують рідку кутю: відвар з пшениці змішується з узваром, додається вода, в якій запарювався мак, мед, мелені горіхи та мелений мак.</p>\n','2011-01-03 09:18:09','http://oranta.org/index.php?option=com_content&view=article&id=654:svjat-vechir&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'СВЯТ-ВЕЧІР','uk',2279,3762,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3768,200,19ed241a81.jpg\" />Коротка історія</p>\n<p>У Христовому Різдві ми бачили безконечну Божу любов і жертву задля нас грішних. А третього дня після празника Христового Різдва свята Церква наводить нам геройський приклад любови Бога в особі святого первомученика Степана. Святий Григорій Ниський у проповіді на честь святого Степана каже: \"Ось ми переходимо з празника на празник і приймаємо благодать за благодать. Вчора наповняв нас подивом Господь вселенної, а сьогодні наповняє нас подивом ученик Господа. Яким способом цей, а яким той? Той (Христос) задля нас прийняв людську природу, а цей (Степан) задля Господа позбувся людської природи\". Мученицька смерть святого Степана дійсно незвичайна, але незвичайний також і культ, який він має у Христовій Церкві від апостольських часів.</p>\n','2011-01-08 15:31:05','http://oranta.org/index.php?option=com_content&view=article&id=657:svjatogo-pervomuchenyka-stepana&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Святого Первомученика Степана','uk',2287,3770,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3768,200,RTEmagicC_rizdvo_2011.jpg.jpg\" />Дорогі у Христі!</p>\n<p>Сьогодні – у неділю Святих отців – Євангеліє розповіло нам про родовід Ісуса Христа від Авраама аж до народження нашого Спасителя. Слухаючи цей докладний перелік поколінь, можна збагнути, що прихід Христа до людей був заздалегідь підготовлений, тобто Його поява на землі відбувалася за точним планом Небесного Отця. Навіть більше, не тільки народження, а й усе життя Ісуса Христа вже століття наперед було передбачене пророками. Приклад цього знову ж знаходимо у святому Євангелії: коли три мудреці зі Сходу зупинилися в Ірода, щоб дізнатися, де народився новий Цар, він покликав книжників, які без тіні сумніву сказали, що Месія має прийти на світ у Вифлеємі Юдейськім, «бо так написано пророком» (Мт. 2, 5).</p>\n','2011-01-09 15:48:54','http://oranta.org/index.php?option=com_content&view=article&id=659:rizdvjany-pryvit-blazhennishogo-ljubomyra-do-virnyh-ukrajinskoji-greko-katolyckoji-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Різдвяний привіт Блаженнішого Любомира до вірних Української Греко-Католицької Церкви','uk',2288,3771,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3786,200,DSC_0024_3Otci.jpg\" />15-го січня Парафія Святого Василія Великого з центром у Штутгарті відзначила святковою літургією день свого небесного покровителя – Святого Василія Великого. Свято Парафії Святого Василія Великого проходило у великій католицькій церкві Святого Ульріха, що в передмісті Штутгарту – Фазанхофі. Напередодні Нового року центр Швабської метрополії з Фазаненхофом з’єднала нова лінія швидкісного трамваю (U-Bahn 6), станція якого Europaplatz знаходиться біля Церкви.</p>\n','2011-01-29 09:58:43','http://oranta.org/index.php?option=com_content&view=article&id=669:bez-boga-ani-do-poroga&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Без Бога, ані до порога','uk',2304,3787,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3816,200,2.jpg\" />Сьогодні, шостого березня, на Аскольдовій могилі був один з найважливіших днів не тільки для&nbsp; парафіян церкви, але також і для усіх патріотів нашої держави. Напередодні в церкві святого Миколая була проведена панахида з дня смерті Романа Шухевича, головного командира УПА, голову Проводу ОУН.</p>\n<p>В неділю, після дитячої Божественної Літургії, урочисто був проведений концерт з цієї нагоди. Крім того, у цей день випала неділя прощення і завтра вже розпочинається Великий піст.</p>\n','2011-03-07 19:39:21','http://oranta.org/index.php?option=com_content&view=article&id=682:osoblyvy-den-dlja-kozhnogo-z-nas&catid=25:parafija-cat&Itemid=27','P',NULL,'Особливий день для кожного з нас…','uk',2336,3819,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3825,200,IMG_8211.JPG\" />Історії життя великих людей уквітчуються легендами. Їм часом приписують надприродні прояви вже змалку та прикрашують сюжетами, близькими до казки.</p>\n<p>Народницьке покоління ідеалізувало батька Тараса в шапці і з вусами, не дуже вчитуючись у нього, переконуючи один одного в тому, що він абсолютно зрозумілий навіть простому селянинові. У цьому спрощенні був самообман.</p>\n','2011-03-10 15:09:24','http://oranta.org/index.php?option=com_content&view=article&id=686:9-tarasova-nich-na-askoldovi-mogyli&catid=25:parafija-cat&Itemid=27','P','','9 Тарасова Ніч на Аскольдовій Могилі','uk',2346,3829,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3841,200,resize_image.php.jpg\" />25&nbsp;березня 2011&nbsp;р., у Ватикані повідомлено про те, що Римський Архиєрей  Венедикт&nbsp;XVI затвердив прохання про підтвердження вибору, надіслане  Преосвященним владикою Святославом Шевчуком, якого 23&nbsp;березня 2011&nbsp;року  Синод Єпископів УГКЦ канонічно обрав Верховним Архиєпископом  Києво-Галицьким. Дотепер владика Святослав виконував сліжіння  Апостольського Адміністратора вакантного осідку української  греко-католицької Єпархії Покрова Пресвятої Богородиці в Буенос-Айресі  (Аргентина).</p>\n<p>Зазначимо,  що Блаженніший Святослав є наймолодшим з-поміж єпископів УГКЦ та  четвертим за віком з-поміж наймолодших католицьких архиєреїв у світі.</p>\n','2011-03-25 14:40:45','http://oranta.org/index.php?option=com_content&view=article&id=694:vladyka-svjatoslav-shevchuk-novy-verhovny-arhyjepyskop-kyjevo-galycky-papa-venedykt-xvi-zatverdyv-rishennja-synodu-ugkc-pro-obrannja-novogo-glavy-cerkvy&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Владика Святослав Шевчук – новий Верховний Архиєпископ Києво-Галицький. Папа Венедикт XVI затвердив рішення Синоду УГКЦ про обрання нового Глави Церкви','uk',2359,3842,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3850,200,4.jpg\" />Двадцять шостого березня у суботу діти з церкви святого Миколая цілий день відпочивали відвідуючи рідні для Шевченка місця: село Моринці, село Керелівка, де пройшли його дитячі роки, сад де він бігав дитинчам, рідні дуби, гаї, які збереглися і до цього часу.</p>\n<p>Багато дітей з Аскольдової Могили поїхало відпочивати, насамперед діти «Карітас», а також ті, які активно брали участь у виступах та відвідували недільну школу.</p>\n','2011-03-28 10:40:21','http://oranta.org/index.php?option=com_content&view=article&id=703:vidpochynok-shevchenkivskymy-shljahamy&catid=25:parafija-cat&Itemid=27','P',NULL,'Відпочинок Шевченківськими шляхами…','uk',2368,3851,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3866,200,1301276268.jpg\" />\"Христос кличе нас до зустрічі, покликаної перемінити нашого духа\", – на  це звернув увагу Блаженніший Святослав, Глава Української  Греко-Католицької Церкви, під час Архиєрейської Святої Літургії в  українському греко-католицькому прокатедральному соборі Святої Софії  Премудрості Божої. Пригадаємо, що величний українсько-візантійський  собор у Вічному Місті в 1967-1968 роках спорудив Блаженніший Йосиф  Сліпий.  У той час як тодішній осідок Глави Церкви – Архикатедральний  Собор святого Юрія у Львові – був у користуванні РПЦ,  тому центром УГКЦ  став собор Святої Софії у Римі. У 1985 році Слуга Божий Іван Павло&nbsp;ІІ  встановив при цьому храмі титул пресвітерського кардинальського чину, а у  1998&nbsp;році церкві надана гідність малої базиліки. На сьогодні кардиналом  з титулом Santa Sofia in via Boccea є Блаженніший Любомир Гузар,  вислужений Архиєпископ Києво-Галицький.</p>\n','2011-04-03 14:20:27','http://oranta.org/index.php?option=com_content&view=article&id=709:zustrich-z-bozhym-synom-je-pereobrazhajuchoju-i-vyzvoljajuchoju-z-propovidi-glavy-ugkc-blazhennishogo-svjatoslava-pid-chas-svjatoji-liturgiji-v-sobori-svjatoji-sofiji-u-rymsi-3-kvitnja-2011-r&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Зустріч з Божим Сином є переображаючою і визволяючою. З проповіді Глави УГКЦ Блаженнішого Святослава під час Святої Літургії в соборі Святої Софії у Римі (3 квітня 2011 р.)','uk',2384,3867,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3881,200,ivano-frankovskuyu-ulicu-nazvali.jpg\" />Літургічний  спогад Блаженного Івана Павла ІІ буде відзначатись 22 листопада. Декрет  Конгрегації Божественного Культу та дисципліни Святих Тайн</p>\n<p>У понеділок, 11 квітня 2011 року, оприлюдено Декрет Конгрегації  Божественного Культу та дисципліни Святих Тайн щодо літургічного спогаду  майбутнього Блаженного Папи Івана Павла ІІ, який буде беатифікований  його наступником, Святішим Отцем Венедиктом ХVІ, 1 травня цього року.</p>\n','2011-04-12 17:06:52','http://oranta.org/index.php?option=com_content&view=article&id=722:lty-navchyv-nas-ne-bojatysjar-spogady-molodi-pro-ivana-pavla-ii&catid=32:gk-svit-cat&Itemid=58','P',NULL,'«Ти навчив нас не боятися». Спогади молоді про Івана Павла ІІ','uk',2401,3884,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3911,200,1001986.jpg\" />У світлий вівторок 26 квітня 2011 року Божого стараннями отця Константна-Пантелея, волонтери парафіяльної служби Карітас зі своїм священиком Ігорем відвідали жінок у Лук’янівському слідчому ізоляторі.</p>\n<p>Радість воскресіння прийшла у ці похмурі стіни, &nbsp;до наших засмучених сестер які відбувають покарання.</p>\n<p>Прибули ми з пасхальними гостинцями. Найперше - паска, яку пожертвували наші парафіяни а також Оксана та Євген, крашанки-яйця, які пожертвували наші парафіяни у загальний великодній кошик для бідних та убогих, цукерки пташине молоко, які передали Олександр і Аріна.</p>\n','2011-04-26 22:39:59','http://oranta.org/index.php?option=com_content&view=article&id=739:svitle-voskresinnja-dlja-uvjaznenyh-zhinok&catid=31:kolonka-redaktora-cat&Itemid=1','P','','Світле воскресіння для ув’язнених жінок','uk',2429,3912,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3914,200,ditu2.jpg\" />Христове Воскресіння єднає нас вірою, надією та любов\'ю. Вірою в мир і власні сили. Надією, що життя буде змінюватися на краще. Пасха – це символ очищення і перемоги життя. Паска вважається найважливішим християнським святом, котре виказує радість з приводу перемоги Божого Сина над Смертю та вічним Забуттям. У Воскресінні християни бачать підтвердження життя після смерті, що і є головним змістом святкування.</p>\n','2011-05-01 13:46:39','http://oranta.org/index.php?option=com_content&view=article&id=742:symvol-ochyshchennja-i-peremogy-zhyttja&catid=32:gk-svit-cat&Itemid=58','P',NULL,'Символ очищення і перемоги життя','uk',2432,3915,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3940,200,CRW_0368.JPG\" />Одного дня святий авва Антоній розмовляв з юнаками, які вирішили провадити пустельницьке життя. Мисливець. Що полював у тих місцях, помітивши їх, шанобливо наблизився.</p>\n<p>Авва Антоній, оточений учнями, весело сміявся і жестикулював. Розчарований таким видовищем, мисливець зажадав, щоб вони перестали сміятися, бо вполохають йому звірів.</p>\n','2013-07-25 22:53:50','http://oranta.org/index.php?option=com_content&view=article&id=1346:luk-26-lypnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Лук (26 липня)','uk',3465,4948,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3942,200,IMG_0462.jpg\" />22 травня цього року у церкві святого Миколая, що на Аскольдовій могилі, відбулося велике свято – теплий святий Миколай, а також відбірковий етап YII – го фестиваль-конкурсу духовної пісні «Аскольдів глас». <strong></strong></p>\n<p>Богослужіння&nbsp; у цей святковий день провів єпископ-помічник УГКЦ Київської архиєпархії Йосиф Мілян. Йому співслужили парох нашої церкви отець Ігор Онишкевич та інші греко-католицькі священики. В церкві панував затишок, тепле сонце зігрівало людей біля церкви.</p>\n','2011-05-23 13:57:58','http://oranta.org/index.php?option=com_content&view=article&id=747:svjaty-mykola-znovu-daruje-ditjam-radist&catid=25:parafija-cat&Itemid=27','P','','Святий Миколай знову дарує дітям радість…','uk',2463,3946,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3963,200,Lviv_2011_1.jpg\" />6 - 8 червня 2011року у Реколекційному центрі Львівської Архиєпрахії відбувався триденний семінар єпархіальних капеланів Пенітенціарного душпастирства Української Греко-Католицької Церкви. Він проходив, з благословення преосвященнішого владики Михаїла (Колтуна), єпископа Сокальсько-Жовквівського, керівника Департаменту УГКЦ у справах душпастирства силових структур України.&nbsp; Крім капеланів були присутні також катехити та волонтери цього особливого і дуже цінного служіння Церкви. Семінар «Душпастирська&nbsp; практика в’язничного служіння» загострив увагу капеланів на застосуванні у пастирських ситуаціях психолого-педагогічних знань, канонічного права, морального богослов’я, міжнародного і вітчизняного права у кримінально-виконавчій сфері.</p>\n','2011-06-09 09:36:51','http://oranta.org/index.php?option=com_content&view=article&id=757:penitenciarne-dushpastyrstvo-ugkc-na-porozi-vprovadzhennja-kapelanstva-nabuvaje-profesinyh-rys&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Пенітенціарне душпастирство УГКЦ на порозі впровадження капеланства набуває професійних рис','uk',2481,3964,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3974,200,273005.jpg\" />14 червня 2011 року Божого у вівторок після Неділі Зіслання Св. Духа настоятель храму Св. Миколая Чудотворця на Аскольдовій Могилі о. Ігор Онишкевич разом з волонтерами пенітенціарного душпастирства Орестою Цибрівською та Юлею Щербій відвідали жінок Київської слідчої установи, що на Лук’янівці. Сприянням Карітас-Київ (директор о. Роман Сиротич) та доброчинців громади Св. Миколая для жінок були передані Біблії, предмети особистої гігієни та солодощі. Окрім того, на вшановування пам\'яті 150-ліття перевезення останків Тараса Шевченка до Канева, громада посприяла, щоб на кожну камеру було передано по примірнику останнього перевидання Кобзаря.</p>\n','2011-06-15 15:32:44','http://oranta.org/index.php?option=com_content&view=article&id=759:lkatarsys-v-style-pret-a-porter&catid=25:parafija-cat&Itemid=27','P',NULL,'«Катарсис в стиле prêt-a-porte»','uk',2492,3975,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,3986,200,119_0938.JPG\" />На початку своїм словом сотворив Бог з нічого сонце, місяць і зорі, небо, землю і море. Насадив всі рослини, дав життя для тварини, а любов для людини. Перший день трудився, другий день, третій день трудився, четвертий день, п’ятий день трудився, шостий день, сьомий день відпочинок</p>\n<p align=\"center\"><strong>16 червня</strong><strong></strong></p>\n<p>Літній відпочинок організовано коштом парафії церкви Миколая Чудотворця, що на Аскольдовій могилі. Повна вартість путівки разом з дорогою становила 500 грн, а для 14 чоловік від парафіяльної служби милосердя «Карітас» – 40 грн з родини, оскільки це потребуючі родини. Збиралися о 17:00 в холі вокзалу. Нас проводжали отець Ігор Онишкевич, п. Анна Онисько та п. Ореста Цибрівська.</p>\n','2011-07-03 17:22:24','http://oranta.org/index.php?option=com_content&view=article&id=766:shchodennyk-litnogo-vidpochynkovogo-taboru-dite-myloserdja&catid=25:parafija-cat&Itemid=27','P','','Щоденник літного відпочинкового табору дітей милосердя','uk',2514,3997,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4051,200,kostel.jpg\" />Трускавець був, є, і, сподіваюся, ще довго буде курортною перлиною Карпат і України. Проте для багатьох українських християн, це місто передусім пов’язане із відродженням духовного життя. При цьому слід наголосити, що свій новий образ Трускавець отримав нещодавно: не минуло якихось 20 років.</p>\n<p>Без сумніву, що справжнім історичним надбанням міста став відновлений у 1996 році Римо-католицький костел Вознесіння Пресвятої Діви Марії, історія якого сягає у 1827 рік. Більшість мешканців підкарпатського краю пам’ятають, що за радянських часів на місці нинішнього храму стояв так заний «планетарій», а перед тим, у дусі безбожної влади, - звичайнісінький склад.</p>\n','2011-09-14 11:15:16','http://oranta.org/index.php?option=com_content&view=article&id=788:hrystyjanska-perlyna-karpat&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Християнська перлина Карпат','uk',2570,4053,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4069,200,DSC_3368.JPG\" />15–16 жовтня у м. Вишгород пройшов ІІІ фестиваль-конкурс «Вишгородська Покрова». Організаторами конкурсу виступили Вишгородська міська рада, вишгородський міський хор «Коралі», громада собору Вишгородської Богородиці УГКЦ та міський центр творчості «Джерело».</p>\n<p>Церкву св. Миколая Чудотворця на Аскольдовій Могилі на конкурсі представляв жіночий ансамбль «Аніма» (керівник Надія Кучерява), який другий рік поспіль долучається до цього гарного пісенного свята.</p>\n','2011-10-18 15:15:29','http://oranta.org/index.php?option=com_content&view=article&id=798:peremoga-nashogo-kolektyvu&catid=25:parafija-cat&Itemid=27','P','','Перемога нашого колективу','uk',2587,4070,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4084,200,DSC05426.JPG\" />11 листопада 2011 р. в рамках служіння Суспільного тижня проголошеного церквою, церковна служба християнського милосердя « Карітас-Печерськ» парафії св. Миколая Чудотворця на Аскольдовій Могилі відвідали Чернігівську виправну жіночу колонію. У служінні милосердя взяли участь: о. Ігор Онишкевич з парафіянами-волонтерами Ірина Журило і артисти. Біля виправного закладу нас зустріли с.Оксана Пелех та с. Христина Пецольд, які бувають тут досить часто: приносять передачі, виконують деякі прохання жінок, що перебувають в колонії, а ще, неподалік Чернігова, в селищі Любичі разом з священником редемптористом о. Віталієм Котиком працюють над створенням реабіталіційного центру для жінок, що звільняються. Місцева влада для цих цілей віддала старе приміщення швейного училища, де зараз коштом жертводавців провадиться ремонт.</p>\n','2011-11-14 16:54:03','http://oranta.org/index.php?option=com_content&view=article&id=805:sluzhinnja-u-ramkah-suspilnogo-tyzhnja-&catid=27:gk-v-ukrajini-cat&Itemid=57','P','','Служіння у рамках Суспільного тижня ','uk',2602,4085,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4092,200,PB060803.JPG\" />За Благословенням Владики Йосифа Міляна помічника Київської Архиєпархії,у неділю 30 жовтня 2011 р Б. На Аскольдовій Могилі парафії Святого Миколая було передано копію Чудотворної Ікони Зарваницької матері Божої, до парафії Святого Духа, м. Золотоноша, щоб Зарваницька Матір Божа була покровителькою та Заступницею м. Золотоноша та всіх парафін.</p>\n','2011-11-24 22:16:35','http://oranta.org/index.php?option=com_content&view=article&id=807:zarvanycka-bogorodycja-v-zolotonoshi&catid=25:parafija-cat&Itemid=27','P','','Зарваницька Богородиця в Золотоноші','uk',2612,4095,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4127,200,976.JPG\" />14 січня 2012 року жіночий вокальний ансамбль «Аніма» вітав глядачів різдвяного фестивалю «Велика Коляда (гуцульська) ». Концерти фестивалю, що проходить у Львові тринадцятий рік поспіль, розпочалися 7 січня і триватимуть до 29 січня, коли відбудеться заключний гала-концерт.</p>\n','2012-01-17 20:49:32','http://oranta.org/index.php?option=com_content&view=article&id=822:lanimar-na-lvelyki-koljadir-u-lvovi&catid=25:parafija-cat&Itemid=27','P',NULL,'«Аніма» на «Великій Коляді» у Львові','uk',2645,4128,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4128,200,396218.jpg\" />Від Різдва, кожен хто відвідав Церкву Святого Миколая Чудотворця на Аскольдовій Могилі міг стати свідком «дива». Неподалік храму, на місці звичного для багатьох киян і гостей столиці дерев’яного Хреста на честь героїв Крут стояв справжній пам’ятник крутянцям: виготовлений з дорогого каменя «козацький хрест» в центрі якого висічений Тризуб під яким промовиста і символічна цитата із святого Євангелія – «Найбільша любов – життя покласти за друзів».</p>\n','2012-01-20 12:09:30','http://oranta.org/index.php?option=com_content&view=article&id=824:na-askoldovi-mogyli-pohovaly-jih&catid=25:parafija-cat&Itemid=27','P',NULL,'На Аскольдовій Могилі поховали їх…','uk',2648,4131,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4144,200,DSC02796.JPG\" />Волонтери та парафіяльна служба милосердя 25 січня відвідали Борзнянську загальноосвітню школу-інтернат в Чернігівській області. Хоч і свята закінчились, але св.. Миколай ще досі роздає свої подарунки. Цього разу ми зібрали трохи менше подарунків, ніж завжди. Але і на цьому велике дякую усім, хто приніс речі, іграшки, хто допоміг матеріально, закупити різдвяні сніговички з цукерками, два комп’ютери та телевізор. Діти були дуже щасливі та вдячні. Обличчя їх світилися неймовірно!</p>\n','2012-02-06 17:45:19','http://oranta.org/index.php?option=com_content&view=article&id=829:velyka-rodyna-odna-mama-i-216-dite&catid=29:propovidi-cat&Itemid=34','P','','Велика родина: одна мама і 216 дітей!','uk',2662,4145,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4148,200,stritenya.jpg\" />\"Радуйся благодатна, Богородице Діво, бо з Тебе засяяло Сонце правди Христос Бог наш, що просвічує тих, що в темряві\"</p>\n<p>Тропар празника</p>\n<p>Різдвяне коло свят завершується празником Господнього Стрі­тення,  який святкуємо 2 лютого, на сороковий день після Христо­вого Різдва. Цей  празник своїм змістом тісно пов\'язаний з Христо­вим Різдвом, бо як у  Різдві, так і тут Христос при своїм пожерт­вуванні у святині об\'являє  нам Свою божественність. Від зустрічі Божої Дитини і Його Пресвятої  Матері з праведним Симеоном і сам празник у Східній Церкві називається  Стрітення. Візьмемо до уваги три питання: основу празника, його  встановлення та дух богослуження.</p>\n','2012-02-15 10:16:21','http://oranta.org/index.php?option=com_content&view=article&id=831:stritennja-gospoda-boga-i-spasa-nashogo-isusa-hrysta&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Стрітення Господа Бога і Спаса нашого Ісуса Христа','uk',2667,4150,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4168,200,DSC05550.JPG\" />Дивовижним чином перегукуються рядки книги «Пам’яті Героїв Крут» із тими почуттями, які переживали ми, паломники церкви святого Миколая Чудотворця на Аскольдовій могилі, вирушаючи з молитвою під проводом та ініціативою настоятеля отця Ігора Онишкевича до місця трагедії і слави, скорботи і пам`яті полеглих у бою Героїв Крутянців. Паломництво приурочене пам’яті перепоховання відважних вояків біля нашого храму.</p>\n','2012-03-20 02:29:06','http://oranta.org/index.php?option=com_content&view=article&id=841:palomnyctvo-do-krut&catid=25:parafija-cat&Itemid=27','P','','Паломництво до Крут','uk',2702,4185,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4170,200,200821.jpg\" />Вже давно хотіла написати про 8 Березня. Перерила купу інформації в Інтернеті. Зазначу те, що є як прихильники так і противники свята. Я з прихильників. І свою думку хочу підтвердити аргументами. Та ось тут і перша проблема. Все немає часу, бо маленький синочок щохвилини потребує моєї уваги. Отож, сиджу я у декретній відпустці й у перервах між «гам–гуляти–спати» пишу статтю.</p>\n','2012-03-09 00:00:15','http://oranta.org/index.php?option=com_content&view=article&id=837:8-bereznja-svjatkuvaty-chy-ni&catid=37:spilnota-cat&Itemid=49','P',NULL,'8 Березня: святкувати чи ні?','uk',2690,4173,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4177,200,00.JPG\" />Дитячий концерт на Аскольдовій могилі, присвячений Тарасу Шевченку</p>\n<pre>&nbsp;</pre>\n<pre>Дивлюся, аж світає,</pre>\n<pre>Край неба палає,</pre>\n<pre>Соловейко в темнім гаї</pre>\n<pre>Сонце зустрічає.<br /><br /></pre>\n','2012-03-18 01:47:12','http://oranta.org/index.php?option=com_content&view=article&id=839:pamjat-pro-tarasa&catid=25:parafija-cat&Itemid=27','P','','Пам’ять про Тараса','uk',2698,4181,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4230,200,zapovit.jpeg\" />Заповіт. Заповітне слово… Воно має особливу енергетичну силу, силу промовляння вустами роду, до якого ти належиш. Заповітне слово неможливо прирівняти до жодного іншого, оскільки воно містить в собі не лише інформаційну, комунікативну складову, але й виступає могутнім засобом духовного єднання людини зі своїми предками&nbsp; та нащадками. У заповіті сконцентровано дороговказ&nbsp; заповідача на майбутнє, окреслено шляхи та життєві орієнтири для інших людей.</p>\n','2012-06-02 21:18:50','http://oranta.org/index.php?option=com_content&view=article&id=865:syla-zapovitnogo-slova&catid=28:tema-nomera-cat&Itemid=2','P','','Сила заповітного слова…','uk',2748,4231,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4245,200,CRW_9922.JPG\" />До 250-ї річниці введення на престол Пилипа Володкевича, Митрополита Київського, Галицького та Всієї Русі 8 червня 2012 року була проведена Всеукраїнська науково-історична конференція на тему: «Радомишль в історії Української церкви 1682-1840 роки». Науковці, професори, доктори історичних наук приїхали з різних куточків України до міста Радомишль (Житомирська обл..) У своїх дослідженнях вони представили учасникам конференції культурно-історичну спадщину цього міста: розкрили його таємниці, діткнулися болючих питань Української церкви…</p>\n<p>Захід розпочався панахидою-пам‘яті митрополита Пилипа Володкевича, та митрополитів Лева Шептицького і Ясона Смогоржевського, які були тут поховані. Владика Йосиф (Мілян) зазначив: – &nbsp;«наша молитва в Радомишлі закладе нові підвалини розвитку та розквіту Української Греко Католицької Церкви». Владика наголосив: «Автентичну історію пишуть і трактують люди. Тому ми тут, щоб поставити певні акценти, говорити про історичні факти, заслуги, невдачі, відкривати істинну історію. Радомишль свого часу став осідком митрополитів, тому це місто повинно посісти чільне місце в історії Української Церкви».</p>\n','2012-06-11 13:46:36','http://oranta.org/index.php?option=com_content&view=article&id=869:radomyshl-osidok-uniji&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'Радомишль – осідок Унії','uk',2764,4247,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,1005041_527565343976666_673119966_n.jpg\" />Того самого дня, кілька місяців після народження, малий кульгавий Леонардо (якого звали Лео) і Томазо опинилися у дитячому будинку. Працівниці ставились до них дуже добре, а діти з початкової школи, до якої ходили – не надто. Діти часто виявляли жорстокість у стосунку до несміливого Лео, але Томазо вмів заступитися, оскільки був хлопцем сильним і розумним. Він був найліпшим учнем у класі і найспритнішим хлопцем на подвір’ї. Томазо допомагав бідному Лео і завжди залишався поруч із ним. Заспокоював його, коли той боявся, чекав на нього, коли той гуляв, бавився з ним, старався, аби друг не переймався своїм каліцтвом, веселив його, розповідаючи різні потішні історії.</p>\n','2013-06-26 23:11:39','http://oranta.org/index.php?option=com_content&view=article&id=1276:vse-27-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Все (27 червня)','uk',3390,4873,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,170388_334219.jpg\" />- Чи ви бачили у церкві якась світло? Запитала у дітей катехитка.</p>\n<p>- Люстри.</p>\n<p>- Свічки.</p>\n<p>- Лампадки.</p>\n','2013-03-12 23:13:30','http://oranta.org/index.php?option=com_content&view=article&id=1033:svitlo-13-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Світло (13 березня)','uk',3018,4501,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,398733.jpg\" />«Благодійний Фонд «Карітас Київ», у рамках постійної праці, за допомогою жертводавців, надає соціально-матеріальну допомогу 550 жінкам-в’язням Чернігівської, Збаразької, Полтавської, Харківської, Дніпропетровської в’язниць у вигляді солодощів, чіпсів, одягу, медикаментів, овочів, книг та Святого Письма. Крім цього, надано допомогу 33 дітям, які перебувають у «Будинку дитини» при Чернігівській колонії», - повідомляє о. Роман Сиротич, директор БФ «Карітас Київ».</p>\n<p>БФ \"Карітас–Київ\" є благодійною організацією, головною метою діяльності якої є здійснення благодійної діяльності в інтересах суспільства і окремих категорій потребуючих осіб. Основними напрямками діяльності фонду є: моральна, психологічна та матеріальна підтримка гуманітарних, соціальних, культурних та оздоровчих ініціатив; надання допомоги малозабезпеченим верствам населення: багатодітним сім’ям, дітям вулиці, дітям-сиротам та особам похилого віку. За 20 років роботи Фонду тисячі потребуючих одержали благодійну допомогу.</p>\n','2012-08-31 09:55:54','http://oranta.org/index.php?option=com_content&view=article&id=880:bf-lkaritas-kyjivr-dopomagaje-ljudjam-jaki-perebuvajut-u-miscjah-pozbavlennja-voli&catid=29:propovidi-cat&Itemid=34','P',NULL,'БФ «Карітас Київ» допомагає людям, які перебувають у місцях позбавлення волі','uk',2796,4279,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4274,200,669x300-89a2.jpg\" />На торгах розпорядник підняв догори скрипки. Були вони подряпані,  понищені, а струни порозтягувались та обвисли. Торговець вважав, що не  варто марнувати час на старі скрипки, а проте показав їх усім,  посміхаючись.</p>\n','2013-06-13 01:29:51','http://oranta.org/index.php?option=com_content&view=article&id=1249:stari-skrypky-13-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Старі скрипки (13 червня)','uk',3330,4813,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4281,200,1.JPG\" />Початок навчального року завжди викликає у дитини, та навіть і у студента особливі емоції, які надихають і супроводжують його протягом усього навчання. Для дітей парафії&nbsp; навчальний рік у школах проходить водночас з вивченням Божої науки, яку діти здобувають на катехитичних заняттях. Тому свято Першого Вересня також є дуже важливим для релігійної громади церкви святого Миколая.</p>\n','2012-09-03 14:52:31','http://oranta.org/index.php?option=com_content&view=article&id=882:znovu-na-stezhynu-znan&catid=25:parafija-cat&Itemid=27','P','','Знову на стежину знань','uk',2799,4282,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4281,200,DSC_0237.jpg\" />Істинне покликання, яке відчуває священик, наче зерно проростає із глибини серця, мотивуючи найрізноманітнішим способом до певного роду служіння. Тому відданий священик дбає про свою парафію так, неначе це вся його родина, його діти та найдорожчі люди.</p>\n<p>Завдяки цьому, кожний із нас відчуває цю пастирську турботу у свій духовний та практичний спосіб: богослужіння, сповідь, духовні розмови, прийняття Святих Тайн, корисні поради, підтримка у важкі моменти життя, розуміння, навчання дітей, молоді, духовні програми та заходи, які зацікавлюють кожного, видання, література, церковний спів, молитовні зібрання, капеланська діяльність у лікарнях, в’язницях, сиротинцях. Все це та ще багато іншого є активнодіючим на парафії св. Миколая Чудотворця, що на Аскольдовій могилі, управління та «пульсацію» якої задає о. міт. Ігор Онишкевич.</p>\n','2012-09-12 10:21:20','http://oranta.org/index.php?option=com_content&view=article&id=884:blagodatni-zhnyva&catid=25:parafija-cat&Itemid=27','P','','БЛАГОДАТНІ ЖНИВА','uk',2802,4285,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4288,200,IMG_01.jpg\" />Що таке церква? Це просто будівля з певною виразністю? Це місце, де люди просто моляться? Це сім’я яка об’єднана молитвою? Це місце для розмови людини з Богом чи Бога з людиною? Це довіра до священика і бажання наслідувати його? Іноді навіть для себе буває важко з’ясувати та дати однозначну відповідь.</p>\n<p>Як на мене, коли поєднати всі вищезгадані компоненти, то ми побачимо правдиву Церкву, яка об’єднує нас у єдину родину, допомагає віднайти духовний мир та сприяє тихій розмові з Богом. Всі ці компоненти глибоко закорінені у церкві св. Миколая на Аскольдовій могилі. Перебуваючи у цьому храмі, відчуваєш духовну насолоду, присутність Бога, Його Мудрості. Повноту гармонії створює священик, а разом із ним спільнота, архітектура, природа, відчуття присутності Святого Духу, світлість людей у ставленні один до одного.</p>\n','2012-09-14 02:57:04','http://oranta.org/index.php?option=com_content&view=article&id=886:povnota-garmoniji&catid=25:parafija-cat&Itemid=27','P','','Повнота гармонії','uk',2806,4289,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4292,200,IMG_0665.JPG\" />Першу в новому церковному році зустріч із Богородицею ми переживаємо 21 вересня 2012р. святкуючи її празник народження. Наша громада св. Миколая з Аскольдової могили запрошена на Храмовий празник в село Вербівка, що розмістилось на границі Київської і Житоомирської областей в Ружинському районі. Нас запросив парох села, греко-католицький священик Олег Лучка.</p>\n<p>З благословенням Владики Йосипа наша громада допомагає завершити будівництво каплиці в селі. А також парафіяльний Карітас-Печерськ надає можливу допомогу потребуючим в селі Вербівка. Троє дітей села відпочивали з нашим катехитичним табором в Карпатах.</p>\n','2012-09-23 00:45:36','http://oranta.org/index.php?option=com_content&view=article&id=890:hramove-svjato-rizdva-bogorodyci-u-seli-verbivka-&catid=25:parafija-cat&Itemid=27','P','','Храмове свято Різдва Богородиці у селі ВЕРБІВКА ','uk',2810,4293,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4305,200,0446.jpg\" />1 жовтня День людей похилого віку. У цей День БФ „Карітас Київ” відвідав понад 100 осіб із добрим словом та подарунками, - повідомляє о. Роман Сиротич, директор БФ „Карітас Київ”.</p>\n<p>Потреби людей похилого віку – один з головних напрямків діяльності БФ «Карітас-Київ».&nbsp; Вже впородвж багатьох років Карітас опікується старшими людьми, надаючи соціальну, медичну та матеріальну допомогу. Медсестри регулярно відвідують та надають допомогу старшим самотнім людям у рамках проекту «Домашня опіка».</p>\n','2012-10-06 11:22:20','http://oranta.org/index.php?option=com_content&view=article&id=893:u-den-ljude-pohylogo-viku-lkaritas-kyjivr-vidvidav-ponad-100-osib&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'У День людей похилого віку «Карітас Київ» відвідав понад 100 осіб','uk',2823,4306,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4305,200,35992.gif.jpg\" />Із кожним роком їх усе менше. Тих правдешніх бандерівців, які нюхали порох і знають, як воно — жити все життя із незаслуженим клеймом «бандита» і «зрадника». 398 волинян нині мають відношення до національно–визвольних змагань, хоча ще зовсім недавно братство вояків ОУН–УПА нараховувало 500 ветеранів. Із них тих, хто безпосередньо брав участь у бойових діях, може, кілька десятків. Ще трохи — і не матимемо в кого спитати: як воно було насправді у ті далекі 40–ві роки? Хтось з українців досі проклинає Бандеру й чекає не дочекається нового Сталіна в Україні. А хтось просто вклякає на коліна й щодня молиться за свій народ, за свою державу. Як мої герої... Повстанське подружжя Мартинюків — майже шість десятиліть разом.</p>\n','2012-10-12 18:10:14','http://oranta.org/index.php?option=com_content&view=article&id=896:llishchynar-z-ldubomr-obnjalys-&catid=37:spilnota-cat&Itemid=49','P',NULL,'«Ліщина» з «Дубом» обнялись... ','uk',2834,4317,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4320,200,DSCN7507.jpg\" />14 жовтня кожного року наша церква святкує надзвичайно важливе свято - Покрови Пресвятої Богородиці. Це день шанують у кожному домі, адже під Покров Пресвятої Богородиці завжди прибігав наш народ.</p>\n<p>Наша парафія цього дня також приготувалася до великого святкування, на проповідях трьох Богослужінь основний акцент священнослужителі ставили на важливості для нашого народу і для кожного зокрема Покрови нашої матері Богородиці.</p>\n','2012-10-17 16:53:06','http://oranta.org/index.php?option=com_content&view=article&id=898:ostanni-bi-askolda&catid=25:parafija-cat&Itemid=27','P','','Останній бій Аскольда','uk',2838,4321,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4355,200,mykolay.jpg\" />Великий не за титулом, чином, посадою чи владою, а за праведним і святим життям. А це для нас, християн, згідно з наукою Святого Письма і є єдиний правильний критерій визначення правдивої та дійсної величі людини перед Богом. Бо чого варте найбільше багатство, найвища посада, титул, чин, влада і навіть світове визнання та слава смертної людини перед вічністю? Скажімо, чим сьогодні великі перед людством: Олександр Македонський, Чингісхан, Наполеон та всі інші всесвітньовідомі полководці, царі, королі, імператори, генії наук, митці і т. д., які відійшли з цього світу? За те, що \"залишили великий слід в історії людства, як часто так про них говорять.</p>\n','2012-12-19 00:12:19','http://oranta.org/index.php?option=com_content&view=article&id=913:mykola-velyky-svjatytel-i-chudotvorec&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'Миколай - Великий Святитель і Чудотворець','uk',2873,4356,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4360,200,pozdravleniya-s-kreshheniem.jpg\" />Святе Водохреще та голодний Святвечір завершують українські  різдвяно-новорічні свята. Після 11-денної загальниці (з 7 по 17 січня), коли не було постів, 18 січня вважалося строгим  пісним днем. Деякі господарі цілий день говіли (нічого не їли або вживали лише чорний хліб та   воду). На вечерю в цей день подавали пісні страви (інколи їх було 12) та вже не запрошували   святих душ, які, вважалося, знову повертаються на \"той світ\", тому це був голодний Святвечір чи   голодна кутя. Перед вечерею господар брав у ложку куті й закликав перед вікном мороз куті їсти,   але просив не зашкодити людям, посівам і худобі. Після вечері парубки бралися \"проганяти   кутю\":<br /><br /></p>\n','2013-01-18 00:24:10','http://oranta.org/index.php?option=com_content&view=article&id=924:a-to-treti-praznyk-svjate-vodohreshche&catid=28:tema-nomera-cat&Itemid=2','P',NULL,'А ТОЙ ТРЕТІЙ ПРАЗНИК СВЯТЕ ВОДОХРЕЩЕ...','uk',2898,4381,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4383,200,5.jpg\" />Якось влітку прибирали з с. Тетяною храм і зайшла розмова про те, що добре було б мати на Аскольдовій Могилі свій великий «в ріст» вертеп, де в різдвяний час можна було б з особливим трепетом співпереживати таємницю народження Дитятка Ісуса, споглядаючи відтворену сценку Різдва. Як виявилось згодом, ця ідея витала і серед інших парафіян, тож з благословення і настанов о. Ігоря розпочався шлях творення Аскольдового Вертепу. Від&nbsp; слова до діла приступили лише в листопаді, тоді і не вірилось, що в такий короткий термін вдасться реалізувати задумане.</p>\n','2013-01-18 03:04:07','http://oranta.org/index.php?option=com_content&view=article&id=927:askoldiv-vertep&catid=25:parafija-cat&Itemid=27','P','','Аскольдів вертеп','uk',2901,4384,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4389,200,DSC05960.JPG\" />«18 січня 2013 року черговий раз БФ «Карітас Київ» організував Святу Вечерю для потребуючих дітей, серед яких діти та молодь, які проживають на вулиці, діти із кризових та малозабезпечених сімей, діти інваліди, діти позбавлені батьківського піклування та діти із багатодітних родин. Цьогоріч були присутніми на вечері понад 80 осіб, а це значно більше ніж минулого», - повідомляє директор БФ «Карітас Київ» о. Роман Сиротич.</p>\n','2013-01-22 03:08:29','http://oranta.org/index.php?option=com_content&view=article&id=930:2013-01-22-01-12-10&catid=29:propovidi-cat&Itemid=34','P',NULL,'БФ «Карітас Київ» у навечір’я свята Богоявління організував Святу Вечерю для 80 потребуючих дітей','uk',2907,4390,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4391,200,PohoronKruty.JPG\" />Мармуровим хрестом обернулася пам\'ять</p>\n<p>Про загиблих бійціх - школярів і студентів.</p>\n<p>Стільки років лягло поміж ними і нами,</p>\n<p>Та в історії мало подібних моментів.</p>\n&nbsp; \n','2013-01-22 12:28:47','http://oranta.org/index.php?option=com_content&view=article&id=932:pam-jati-gerojiv-krut&catid=1:mystecka-storinka-cat&Itemid=50','P','','Пам \'яті героїв Крут','uk',2909,4392,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,1220086895_phpthumb90.jpg\" />Один хлопець, нахилившись над столом, записував свої постанови. Його мати у той час прасувала білизну.</p>\n<p>\"Коли  побачу когось, хто потопає, - писав хлопчина, - то відразу кинуся у  воду, аби допомогти йому. Якщо загориться дім, виноситиму з вогню дітей.  Коли трапиться землетрус, без жодного страху піду серед будинків, що  валяться додолу, рятувати людей. А потім усе своє життя присвячу бідним  цілого світу\".</p>\n','2013-01-20 02:16:01','http://oranta.org/index.php?option=com_content&view=article&id=952:postanovy-20-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Постанови (20 січня)','uk',2916,4399,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,1280239889.jpg\" />Троє старанних і допитливих юнаків урешті успішно завершили навчання, здобувши високі наукові ступені. Прощаючись, пообіцяли собі, що об’їдуть увесь світ, а через рік зустрінуться, і кожен привезе найціннішу річ, яку тільки зможе віднайти.</p>\n<p>Перший із них точно знав, що шукати. Він подався за найбільшим і найгарнішим коштовним каменем у світі. Зборознив моря й пустелі, підкорив найвищі гірські вершини й здобув те, про що мріяв – найпрекраснішу дорогоцінність, яка лише існувала під сонцем. Приїхав додому, очікуючи на своїх друзів.</p>\n','2013-01-18 02:02:58','http://oranta.org/index.php?option=com_content&view=article&id=950:taktyka-kachky-18-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Тактика качки  (18 січня)','uk',2914,4397,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,171537702.jpg\" />Один подорожній затримався поблизу мальовничого села, що розкинулося  між полів. Його зацікавив невеликий цвинтар, обнесений дерев\'яним  плотом. Було там багато дерев, птахів і чудових квітів. Подорожній  поволі ходив між могилами зі світлими надгробками, що були розкидані  попід деревами.</p>\n<p>Почав читати написи. На першому було: «Ян Тарег, жив 8 років 2 тижні 3 дні». Така мала ди¬тина тут похована...</p>\n','2013-01-29 04:06:22','http://oranta.org/index.php?option=com_content&view=article&id=961:zapysnychok-29-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Записничок (29 січня)','uk',2929,4412,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,23265535.jpg\" />Двоє сміливих лицарів брали участь у турнірах, ризикованих пригодах, наражаючись на небезпеку. Служили багатьом володарям. <br /><br /> Котрогось вечора один із них, споглядаючи захід сонця, мовив: «мені залишається остання пригода». <br /><br /> - Яка? <br /> - Хочу зійти на гору, на якій живе Бог!</p>\n','2013-01-31 04:27:30','http://oranta.org/index.php?option=com_content&view=article&id=963:dvoje-lycariv-31-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Двоє лицарів (31 січня)','uk',2932,4415,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,288771_original.jpg\" />Одного вченого і шанованого всіма рабина попросили  прочитати лекцію про вихід Ізраїлю з Єгипту у найбільшому культурному  осередку міста. Слухачі були освічені та підготовлені, уважно слухали  лекцію.</p>\n<p>Рабин так описав події, що стосувалися небесної манни:  «Господь послав на землю Свій хліб, що був смачний, наче медівник, і  котрого вистачало на один день. Його не можна було зберігати до  наступного дня, за винятком п’ятниці. Коли сонце починало пригрівати,  манна топилася...»</p>\n','2013-01-27 03:35:21','http://oranta.org/index.php?option=com_content&view=article&id=959:shchodnja-27-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Щодня (27 січня)','uk',2925,4408,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,337419.gif\" />Одного  разу могутній король скликав усіх чаклунів, мудреців та жерців свого  королівства, жадаючи, аби вони показали йому Бога. їм загрожувала  найстрашніша кара, якщо не потрафлять цього зробити. Бідолахи у відчаї  рвали на голові волосся, не знаючи, що діяти. Тоді з\'явився один пастух,  який пустив вістку, що може дати собі раду з цим завданням.</p>\n<p>Його відразу представили королю. Пастух вивів короля на терасу.</p>\n','2013-01-19 02:09:51','http://oranta.org/index.php?option=com_content&view=article&id=951:bachyty-boga-19-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Бачити Бога (19 січня)','uk',2915,4398,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,UNP4ArcLzxM.jpg\" />Щодня у полуденну пору один хлопчина переступав поріг церкви, а за кілька хвилин відходив.</p>\n<p>Він носив картату сорочку і подерті джинси - як і всі його ровесники. У  руках тримав паперову торбинку з булками на обід. Священик дещо  недовірливо спитав його, для чого сюди приходить. Відомо ж бо, що в  теперішніх часах не бракує людей, які не завагаються обікрасти й церкву.</p>\n<p>\"Я приходжу, щоб помолитися\", - відповів хлопчина.</p>\n','2013-01-21 02:31:28','http://oranta.org/index.php?option=com_content&view=article&id=953:vidvidyny-21-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Відвідини (21 січня)','uk',2917,4400,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,VAvt3Ljvyc4.jpg\" />Восени у родючому ґрунті одна побіч одної лежали дві  насінини. Перша насінина сказала: «Я хочу рости! Хочу сягнути глибоко в  землю корінням, хочу випустити на поверхню молоденькі пагінці. Хочу  розвивати ніжні бруньки – немов прапорці, що звіщають прихід весни …»  Хочу відчути сонячне тепло і благодать ранкової роси на своїх  пелюстках!»</p>\n','2013-01-23 02:53:50','http://oranta.org/index.php?option=com_content&view=article&id=955:dvi-nasinyny-23-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дві насінини (23 січня)','uk',2919,4402,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,c1234a24b3825382a0e1ac3e1d925b13.jpg\" />Одна молода мати очікувала народження другої дитини. Довідавшись, що то –  дівчинка, навчила свого первістка, синочка Михайлика, аби він, поклавши  голову на її живіт, разом із нею співав колискової маляті, яке мало  народитись. Пісенька, що починалася словами: «Зірочко, зіронько, вже ніч  близенько ...» дуже подобалась хлоп’яті. Співав її часто.<br /><br /></p>\n','2013-01-30 04:16:15','http://oranta.org/index.php?option=com_content&view=article&id=962:bratyk-30-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Братик (30 січня)','uk',2931,4414,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,index_clip_image006_0003.jpg\" />Молодого монаха послали до одного монастиря у Фландрії  ткати разом з іншими великий гобелен.</p>\n<p>Якось він спустився зі своєї драбини дуже обурений</p>\n<p>- Досить! Я більше не можу працювати! Вказівки, які дають мені,  беззмістовні! Я працював золотою ниткою, а тут мені кажуть зав\'язати її і  відтяти...</p>\n','2013-01-16 01:49:57','http://oranta.org/index.php?option=com_content&view=article&id=948:gobelen-16-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Гобелен (16 січня)','uk',2913,4396,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,lyk.jpg\" />Якось монах Епіфаній, що жив на Сицилії відкрив у собі Божий дар: він умів малювати чудові ікони. <br /><br /> У нього зродилося бажання створити таку, яка би стала шедевром: понад усе він прагнув зобразити лик Христа.</p>\n<p>Але де найти відповідну модель, що здатна була б виразити водночас  страждання і радість, смерть і воскресіння, божественність і людськість?</p>\n<p>Епіфанфй не міг знайти спокою. Він рушив у подорож, зміряв цілу Європу, вдивляючись у кожне обличчя.</p>\n<p>На жаль, не було серед них такого, яке могло б представляти лик Христа.<br /><br /></p>\n','2013-01-25 03:15:41','http://oranta.org/index.php?option=com_content&view=article&id=957:lyk-isusa-25-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Лик Ісуса (25 січня)','uk',2922,4405,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,troya.jpg\" />У саду поміж деревами, кущами і квітниками росли Дуб і Троянда. Дуб був  деревом величним, а його могутнє гілля здавалося короною, що владно  нависала над скромними квітами в саду.</p>\n<p>Троянда мала лише стебло з кількома зеленими листочками і гострими колючками. Виглядала кволою, наче от-от зів’яне.</p>\n<p>Міцний Дуб кепкував з неї, зневажливо кажучи: «Ти – лише нікчемна колюча галузка!»</p>\n','2013-01-28 03:44:26','http://oranta.org/index.php?option=com_content&view=article&id=960:dub-i-trojanda-28-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дуб і Троянда (28 січня)','uk',2927,4410,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4395,200,untitlegys8.jpg\" />Один учитель сказав учням намалювати те, за що вони  хотіли б подякувати Богові. Виховник гадав, що у цих дітей – вихідців із  бідних родин – насправді не так багато причин відчувати вдячність. Він  був переконаний, що всі радше намалюють купи солодощів, столи, що  вгинаються від наїдків.</p>\n<p>Малюнок, який подав йому Михайлик, вразив виховника: на ньому хлопчина зобразив руку.</p>\n<p>Але чия це рука? Клас зацікавився тим зображенням.</p>\n<p>«Для мене це рука Бога, що дає нам хліб насущний», - висловилася одна дитина.</p>\n','2013-01-24 03:00:11','http://oranta.org/index.php?option=com_content&view=article&id=956:podjaka-24-sichnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Подяка (24 січня)','uk',2920,4403,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,-hUID4xiqqg.jpg\" />Коли його прийняли на посаду редактором авторитетного журналу, вважав,  що спіймав Бога за бороду. Зателефонував до батьків і, звичайно ж, до  коханої Мирослави, якій просто сказав: «Отримав роботу! Можемо  побратися!»  <br /><br /> Стали подружжям, й упродовж кількох років нарадилося троє діток: Мар’ян, Марта й Віктор.</p>\n<p>Шість років тривало щастя,  а далі журнал змушений був припинити свою  діяльність. Молодий батько влаштувався редактором у тижневик.  Однак ця  газета теж перестала виходити.</p>\n','2013-02-08 01:43:42','http://oranta.org/index.php?option=com_content&view=article&id=975:troje-ditok-8-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Троє діток (8 лютого)','uk',2954,4437,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,04cbf9.jpg\" />Один бухгалтер повернувся додому в гарному настрою. В елегантній  паперовій торбинці, червоній з жовтим бантом, приніс свою покупку: нові  штани. <br /><br /> Побіг у кімнату, щоб одягнути їх і похвалитися перед родиною. Однак виявилося, що штани надто довгі.</p>\n<p>-	Нічого, - подумав чоловік, - удома є троє жінок, які вміють шити:  дружина, дочка й теща. Попрошу котрусь із них, щоб укоротила штани.<br /><br /></p>\n','2013-02-10 02:06:27','http://oranta.org/index.php?option=com_content&view=article&id=977:shtany-10-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Штани (10 лютого)','uk',2956,4439,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,1071.jpg\" />Одному чоловікові, якого турбував сенс його життя і те, яким буде  останній день його життя, а передусім – Страшний суд, перед яким рано чи  пізно постане, приснився сон.</p>\n<p>Снилося йому, що по смерті наблизився, сповнений страху, до величезної  брами, яка була входом у Дім Бога.</p>\n<p>Постукав, і усміхнений ангел відчинив  йому. Запросив його до приймальні Раю.</p>\n','2013-02-06 01:13:07','http://oranta.org/index.php?option=com_content&view=article&id=973:rahunok-6-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Рахунок (6 лютого)','uk',2951,4434,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,33256526__9818.jpg\" />Відомий майстер-лучник зорганізував  змагання серед своїх учнів, щоб оцінити рівень їхньої підготовки.  Якогось дня на одному з дерев, що росли в кінці галяви, повісили  дерев\'яний диск із червоним кружальцем посередині. Далеко на другому  кінці галяви провели лінію, за якою стояли змагальники.</p>\n<p>Один із хлопців  сміливо виступив перший, бажаючи показати свою відвагу. Міцно стиснув  лук і стрілу, потім став у позицію, готовий до пострілу.</p>\n','2013-02-02 23:22:44','http://oranta.org/index.php?option=com_content&view=article&id=970:uchytel-3-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Учитель (3 лютого)','uk',2937,4420,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,699433.jpg\" />Одного дня диявол постановив збільшити ефект своїх злих діянь. Він  зібрав начальників департаменту і головний штаб дивізії пекельної  пропаґанди і реклами, аби придумати нові кампанії спокус і пасток для  людей, а також нові способи, щоб знищити сенс їхнього життя.</p>\n<p>– Скажи їм, що Бога немає, – запропонував один чорт.</p>\n','2013-02-04 23:54:36','http://oranta.org/index.php?option=com_content&view=article&id=972:dyjavolsky-pidstup-5-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Диявольський пiдступ (5 лютого)','uk',2949,4432,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,MI1SwBOz7Ok.jpg\" />Жили колись старий чоловік і старий осел. Одного дня осел упав до  криниці, уже висохлої, проте глибокої. Бідна тварина кричала цілий день і  чоловік замислювався, як витягнути її з криниці. Та врешті подумав, що  осел дуже старий і слабий, а сам він давно збирався засипати висохлу  криницю.</p>\n','2013-02-03 23:37:13','http://oranta.org/index.php?option=com_content&view=article&id=971:strategija-osla-4-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Стратегія осла (4 лютого)','uk',2938,4421,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,d091d0b5d0b7d0b8d0bcd0b5d0bdd0b8-11.jpg\" />Одного разу, відчуваючи докори сумління, Вовк зайшов до церковці у горах і сказав священикові: <br /><br /> -	Хочу висповідатись!<br /> -	Ти впевнений у цьому? – спитав добрий священик.<br /> -	Так, запевняю вас, що хочу сповідатись.<br /> -	Вклякни біля сповідальниці.<br /> -	Не можете собі уявити, - почав Вовк, - скільки я овечок задушив! Бідні звірята, спали так спокійно, а я … я їх з’їв …</p>\n','2013-02-09 01:55:21','http://oranta.org/index.php?option=com_content&view=article&id=976:spovid-vovka-9-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Сповідь Вовка (9 лютого)','uk',2955,4438,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,gvozd01_b.jpg\" />Жив колись хлопчисько із доволі складним характером. Він легко вибухав гнівом, був сварливий, робив багато шкоди.</p>\n<p>Одного дня батько вручив йому мішечок із гвіздками і сказав, аби він  забивав один цвях у палісадник, що оточував подвір’я, стільки разів,  скільки з кимось сваритиметься.</p>\n<p>У перший день хлопець забив 36 гвіздків.</p>\n','2013-01-31 23:10:07','http://oranta.org/index.php?option=com_content&view=article&id=968:gvizdky-1-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Гвіздки (1 лютого)','uk',2933,4416,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4415,200,imas.jpeg\" />Одного дня, коли світ був ще цілком новим, мешканці однієї місцевості  влаштували музичний конкурс, учасниками якого стали майже всі присутні –  від Синиці до Носорога.</p>\n<p>Під орудою Сови вирішено, що голосування під час конкурсу буде таємним і  загальним. Отож голосуватимуть усі учасники, самі створять суддівську  комісію, тобто журі.</p>\n','2013-02-07 01:37:04','http://oranta.org/index.php?option=com_content&view=article&id=974:konkurs-7-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Конкурс (7 лютого)','uk',2953,4436,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,120912_151642_90817_2.jpg\" />Один чоловік щонеділі говорив своїй жінці: \"Піди, жінко, до церкви та помолися за нас обох\".</p>\n<p>Перед друзями хвалився: \"Я не мушу ходити до церкви щонеділі, бо моя жінка робить це за нас обох\".</p>\n<p>Якось йому приснився сон: стоїть він разом із жінкою перед райською брамою і чекає, щоб їм відчинили і впустили їх усередину.</p>\n','2013-02-11 23:52:25','http://oranta.org/index.php?option=com_content&view=article&id=981:pidy-zamist-mene-12-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Піди замість мене (12 лютого)','uk',2960,4443,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,1333710202_6355.jpg\" />Чи ви коли-небудь запитували себе, як Господь Бог вибирає матерів для дітей із вродженими вадами?</p>\n<p>Спробуйте  уявити собі картину: ось Бог – вершитель доль. Поряд асистують ангели,  записуючи Божі вказівки у свою гігантську книгу.</p>\n<p>- Матуськів Марія – син. Святий покровитель – Матей.</p>\n','2013-02-13 00:02:11','http://oranta.org/index.php?option=com_content&view=article&id=982:osoblyva-mama-13-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Особлива мама (13 лютого)','uk',2963,4446,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,rozmova_z_bogom.jpg\" />Рано-вранці першого дня нового навчального року маленький школярик з мамою за руку радісно чимчикував до школи.</p>\n<p>Сонце щойно зійшло і світило в спину. Хлопчик зачаровано дивився на  свою довжелезну тінь - він почував себе тридцятиметровим велетнем.</p>\n','2013-02-14 00:14:24','http://oranta.org/index.php?option=com_content&view=article&id=983:tin-14-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Тінь (14 лютого)','uk',2967,4450,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4431,200,toX5HWQDQqY.jpg\" />Одного  хлопчика мама послала до магазину дещо купити. Він дуже ретельно  виконував своє доручення. Щоб якось винагородити маленького покупця за  таку старанність, продавець зняв з по­лиці велику пачку цукерків,  відкрив її і простяг­нув хлопчикові:</p>\n<p>\"Візьми собі цукерків, хлопче!</p>\n<p>Той узяв одну.</p>\n','2013-02-10 23:40:54','http://oranta.org/index.php?option=com_content&view=article&id=980:ruka-11-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Рука (11 лютого)','uk',2957,4440,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4444,200,1309729124_lubow.jpg\" height=\"164\" width=\"179\" />Син одного короля, як це часто трапляється в казках, закохався у прекрасну, але дуже бідну дочку пекаря і оженився на ній.</p>\n<p>Кілька років молоде подружжя жило у злагоді й щасті. По смерті батька  молодий королевич мав зайняти його місце — посісти на троні.</p>\n<p>Міністри й радники всіляко давали зрозуміти молодому королю, що для  блага держави він повинен зректися своєї дружини. Королева не має бути з  простолюду, тож йому слід одружитися з дочкою короля якоїсь сусідньої  держави. Тільки такий шлюб, мовляв, забезпечить мир і добробут у  державі.</p>\n','2013-02-19 00:48:59','http://oranta.org/index.php?option=com_content&view=article&id=995:vyshchi-interesy-19-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вищі інтереси (19 лютого)','uk',2973,4456,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4444,200,svet-i-ten_04.jpg\" />Два чоловіки отримали звістку про смерть свого багатого приятеля. І один з них запитав:</p>\n<p>«Скільки, цікаво, він залишив?»</p>\n<p>«Усе, - відповів товариш».</p>\n<p><strong>В одній далекій країні правив володар, який понад усе любив насолоджуватися своїм надзвичайним багатством. Кожного дня він зодягався у вишиті самоцвітами золототкані строї. Коли раніше сонце осяювало його обличчя, а шати починали мінитися тисячею райдужних блискіток, він виходив зі свого королівського палацу, щоб прийняти знаки поклоніння від своїх підданих.</strong>\n','2013-02-17 01:10:26','http://oranta.org/index.php?option=com_content&view=article&id=996:pochynajuchy-z-kincja-17-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Починаючи з кінця (17 лютого)','uk',2971,4454,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4445,200,Bergpredigt.jpg\" />В одному маленькому містечку, яке нічим не вирізнялося серед інших, траплялися дуже дивні речі.</p>\n<p>Діти забували виконувати уроки, дорослі, йдучи спати, не пам\'ятали, що треба зняти взуття, на вулиці ніхто ні з ким не вітався.</p>\n<p>Двері церкви були наглухо забиті. Мовчали дзвони, ніхто не вмів молитися.</p>\n<p>Якось у понеділок зранку учитель запитав учнів:</p>\n','2013-02-16 00:35:49','http://oranta.org/index.php?option=com_content&view=article&id=993:bozhevilne-msto-16-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Божевільне мiсто (16 лютого)','uk',2970,4453,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4445,200,f001a.jpg\" />Один батько залишив своїм синам у спадок поле. Брати його  справедливо поділили — навпіл. Один брат був багатий і нежонатий, а  другий — бідний і мав купу дітей.</p>\n<p>Настали жнива. Не йде сон на очі багатому братові, думки в голову лізуть:</p>\n<p>«Я багатий, навіщо мені стільки збіжжя? А мій брат бідний, йому треба годувати сім’ю».</p>\n<p>Устав з ліжка старший брат, пішов на своє поле, взяв кілька снопів і переніс на братове.</p>\n','2013-02-18 00:43:19','http://oranta.org/index.php?option=com_content&view=article&id=994:pole-18-ljutogo&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Поле (18 лютого)','uk',2972,4455,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,1.jpg\" />Плачучи ридма, хлопчик прибіг додому. Дідусь обняв його ніжно, тулить і  тулить  до своїх грудей. Та хлопчик не перестає ридати і схлипувати.  Дідусь голубить його, намагаючись заспокоїти.</p>\n<p>-	Тебе хтось ударив?  - питає.</p>\n<p>Хлопчик заперечливо хитає головою.</p>\n<p>-	Забрали в тебе щось?</p>\n','2013-02-28 23:00:16','http://oranta.org/index.php?option=com_content&view=article&id=1008:-nihto-ne-pryshov-1-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Ніхто не прийшов (1 березня)','uk',2985,4468,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,1313227979_bibl.jpg\" />Різьбяр з допомогою долота й молотка обтесував величезний шмат мармуру.</p>\n<p>Хлопчик,  що проходив поряд, затримався біля відчинених дверей майстерні, ласуючи  мо­розивом. Малюк завмер, захоплений дощем бі­лого пилу й уламками  скали, що розлітались навсебіч від знарядь майстра. Не міг збагнути, що  власне там відбувається; людина, що, мов фурія, воювала з мармуровою  скалою, здава­лась йому трохи дивною.</p>\n','2013-03-05 23:18:55','http://oranta.org/index.php?option=com_content&view=article&id=1015:skulptor-6-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Скульптор (6 березня)','uk',2992,4475,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,18393-oboi-zhizn.jpg\" />Каменяр відірвав долото від надгробка й про­мовив: \"Я закінчив\".</p>\n<p>Чоловік  оглянув камінь: фотографія батька й дві дати - 1916 і 2000, а між ними  тире, що за­ймало на площині каменю лише декілька сан­тиметрів. Хитнув  невпевнено головою, що мог­ло означати і заперечення, і здивування, а  рад­ше якесь внутрішнє, усвідомлене несприйняття, невдоволення. Вимовив:</p>\n','2013-03-04 13:03:37','http://oranta.org/index.php?option=com_content&view=article&id=1013:tyre-4-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Тире (4 березня)','uk',2990,4473,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,37033.jpg\" />На нічному столику старенької жінки, що доживала віку в будинку для  перестарілих, наступного дня по її смерті знайшла листа, адресованого  молодій доглядальниці.</p>\n<p>\"Що ти бачиш, доглядаючи мене? Кого бачиш, дивлячись на мене? Що думаєш,  коли мене залишаєш? І що кажеш, як розповідаєш про мене іншим?</p>\n','2013-03-02 22:30:44','http://oranta.org/index.php?option=com_content&view=article&id=1012:-stara-nesterpna-pani-3-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Стара нестерпна пані (3 березня)','uk',2989,4472,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,bbff4a62-8d82-4dd5-9e01-4f730b20c9ac.file.jpg\" />Караван  купців, що вже не раз долав довгі шляхи Сходу, готувався перейти  небезпечну пустелю. Мандрівка вимагала ґрунтовного знан­ня місцевости й  доріг, рухомих пісків, оаз, а та­кож і звичаїв місцевого люду. Тому  скориста­лись послугами досвідченого й знаного в краю провідника.</p>\n','2013-03-06 23:23:10','http://oranta.org/index.php?option=com_content&view=article&id=1016:providnyk-7-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Провідник (7 березня)','uk',2993,4476,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,from-god.jpg\" />- Прошу, не стійте тут і не витрачайте на мене свого дорогоцінного часу.  Нічого доброго в мені немає. Я огидна для всіх і для себе самої!</p>\n<p>Зустрівши священика, який запрошував її на зустріч групи молоді, дівчина  давала волю своєму роздратуванню. Зі злістю й гіркотою вона викидала із  себе все те, що їй самій у собі не подобалось:</p>\n','2013-03-07 23:27:13','http://oranta.org/index.php?option=com_content&view=article&id=1017:vartist-8-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вартість  (8 березня)','uk',2994,4477,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4475,200,st-francis-receiving-the-stigmata.jpg\" />Одного дня святий Франциск, виходячи з обителі, зустрів брата Джінепро.  То був вельми простий, добрий чоловік, і святий Франциск дуже любив  його.</p>\n<p>Тож, зустрівши, звернувся до нього з проханням:</p>\n<p>-	Брате Джінепро, ходи-но зі мною, будемо проповідувати.</p>\n<p>-	Отче мій, - відповів брат, - ти ж бо знаєш: я не досить освічений. Чи ж можу я промовляти до людей?</p>\n','2013-03-01 22:23:34','http://oranta.org/index.php?option=com_content&view=article&id=1011:-propovid-svjatogo-francyska-2-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,' Проповідь святого Франциска (2 березня)','uk',2986,4469,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,66560.jpg\" />Один мандрівник, ідучи селом, побачив під деревом людину з мотузкою в  руці. Чоловік обв’язав себе на поясі одним кінцем, а другий – намагався  закинути на міцну грубу гіляку.</p>\n<p>- Чоловіче, ти що надумав робити? – запитав мандрівник.</p>\n<p>- Хочу повіситись, - відповів той.</p>\n<p>- Отак? – здивувався перехожий. – Та ж мотузку зав’язують на шиї.</p>\n','2013-03-15 23:44:49','http://oranta.org/index.php?option=com_content&view=article&id=1037:slushna-dumka-16-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Слушна думка (16 березня)','uk',3033,4516,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,87655.jpg\" />У травні на луці лежала загублена кимось Сопілка. Вже давно ніхто на ній  не грав. Аж одного дня поблизу проходив Осел, він узяв та й подув у  Сопілка щосили. Ніколи у своєму житті Осел не чув такого чарівного  звуку. І не лише він,  у своєму ослячому житті, але й Сопілка – у  своєму.</p>\n<p>Ні Осел, ні Сопілка не могли зрозуміти, що сталося (обоє не були  розумними, але вірили в розумність), вони відсахнулися одне від одного ,  засоромившись найпрекраснішої події в їхньому житті.</p>\n','2013-03-13 23:27:33','http://oranta.org/index.php?option=com_content&view=article&id=1035:osel-i-sopilka-14-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Осел і сопілка (14 березня)','uk',3028,4511,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,briz_talant.jpg\" />- Ти віриш у чудо?</p>\n<p>- Вірю.</p>\n<p>- Справді? Ти бачив яке-небудь чудо?</p>\n<p>- Звичайно.</p>\n','2013-03-14 23:38:10','http://oranta.org/index.php?option=com_content&view=article&id=1036:chudo-15-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Чудо (15 березня)','uk',3031,4514,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4500,200,rozpjattia.jpg\" />Жив на світі дуже бідний чоловік. Він твердо вірив у св. Миколая і палко  до нього молився. Врешті-решт святий вирішив зустрітися з тим чоловіком  і одного дня зійшов з неба.</p>\n<p>Прибувши в дім бідняка і побачивши його злидні, св.. Миколай вирішив йому допомогти.</p>\n<p>Він доторкнувся пальцем каменя біля порогу, і той одразу ж перетворився на золото.</p>\n','2013-03-16 23:50:46','http://oranta.org/index.php?option=com_content&view=article&id=1038:bazhannja-17-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Бажання (17 березня)','uk',3035,4518,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4525,200,38617.jpg\" />Святий Андрій Критський (7 ст.)  народився в Дамаску у сім’ї благочестивих християн. До семирічного віку  хлопчик був німим. Дар мови він отримав після причастя Святих Христових  Таїн. Після цього юнак допитливо вивчав Святе Письмо та богословські  науки. У віці чотирнадцяти років він прийняв постриг в обителі  преподобного Сави Освяченого у Єгипті.</p>\n<p>Святий Андрій вів суворе та цнотливе життя. Його покірність та  стриманість, чесноти та розум викликали подив.</p>\n','2013-03-17 23:00:25','http://oranta.org/index.php?option=com_content&view=article&id=1047:-11-bereznja-2012-velyky-pokajanny-kanon-andreja-krytskogo&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,' Великий покаянний канон Андрея Критського','uk',3043,4526,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,0000t8t4.jpg\" />Семирічний Максиміліян, сердитий, з руками в кишенях, вийшов з уроку релігії. Поводячи плечима, зневажливо пробурчав: «Тільки про одне говорять: Бог, Бог, Бог … Чому не розкажуть про щось цікаве? Про футбол чи рок-групи?»</p>\n<p><strong>«Почуй мене, Господи!</strong></p>\n<strong> </strong>\n<p><strong>Мені розповідали, що Тебе нема, а я, дурень повірив у це.</strong></p>\n<strong> </strong>\n<p><strong>&nbsp;</strong></p>\n','2013-03-18 01:28:33','http://oranta.org/index.php?option=com_content&view=article&id=1048:zustrich-18-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Зустріч (18 березня)','uk',3046,4529,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4528,200,IMG_1008.jpg\" />В епоху кам’яних замків і войовничих лицарів в обладунках, один відважний юнак вирішив стати ковалем. Він прийшов до кузні учнем і дуже швидко навчився таємниць ремесла. Умів користуватися різним інструментами, кував залізо на кувадлі, надимав ковальський міх. Хлопець став вправним майстром: він навчився виковувати гострі шаблі й легкі шоломи, що витримували будь-який удар, канделябри з тисячами рельєфів і міцні ґрати.</p>\n','2013-03-19 23:43:31','http://oranta.org/index.php?option=com_content&view=article&id=1050:cheljadnyk-20-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Челядник (20 березня)','uk',3064,4547,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4536,200,1034025-580x314.jpg\" />Був собі добрий і побожний брамин, який жив на пожертви вірних. Одного дня він подумав: «Переодягнутися в біснуватого і пуду просити милостиню».</p>\n<p>Опоясався ганчір’ям, як це роблять парії, найбідніші люди Індії.</p>\n<p>Того дня ніхто не вклонився йому на вулиці, ані не дав жодної милостині.</p>\n','2013-03-20 23:45:01','http://oranta.org/index.php?option=com_content&view=article&id=1056:hto-bilshe-znachyt&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Хто більше значить (21 березня)','uk',3066,4549,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4540,200,0101.jpg\" />Час Великого Посту, є можливістю наблизитись до Бога, відкрити наші серця для його світла, для цього в першу чергу необхідне взаємне порозуміння і прощення ближнього, тому напередодні церква святкує Неділю Всепрощення. За традицією, якої дотримувались чернечі згромадження у монастирях, ми просимо один у одного вибачення своїх провин у наших спільнотах.</p>\n','2013-03-19 15:39:57','http://oranta.org/index.php?option=com_content&view=article&id=1058:2013-03-19-13-52-41&catid=25:parafija-cat&Itemid=27','P',NULL,'Триденні реколекційні науки(14-16 березня) на тему: «Вірую, Господи, допоможи моєму невірству» (Мк. 9, 24)','uk',3058,4541,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4541,200,rosary2.jpg\" />Піст в практиці Церкви існує від самих початків. Але з бігом часу кожна  із конфесій встановила свою практику посту. Слід однак пам’ятати, що  основна мета посту – духовна, тобто, насамперед, потрібно постити від  гріха, злих схильностей. У дні посту необхідно стримуватись від лихих  діл, думок, слів; зберігати погідність духу, внутрішній мир та радість.  Стримування від гріха набагато важливіше ніж стримування від їжі, хоча  це також необхідне та допомагає поборювати гріх, вправлятись у духовному  зрості, духовному житті. Нам же потрібно пам’ятати та виконувати і одне  і друге.</p>\n','2013-03-19 22:54:34','http://oranta.org/index.php?option=com_content&view=article&id=1060:pravyla-postu-v-ukrajinski-greko-katolycki-cerkvi-&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'Правила посту в Українській Греко-Католицькій Церкві ','uk',3060,4543,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,0_df0_1e67d815_L.jpg\" />В одному краї сталася велика посуха. Спершу пожовкла, а згодом і висохла трава. Зів’яли й поникли кущі та дерева. Не було жодної краплі дощу з неба, ані жодної рослинки із землі.</p>\n<p>Малі й великі звірі гинули від спраги. Тільки найсильніші спромоглися на рішучий крок і залишали цю пустелю смерти.</p>\n<p>А посуха і далі лютувала. Навіть старі й могутні дерева з глибоким корінням втратили листя. Повисихали джерела, русла струмків і річок.</p>\n','2013-03-25 01:04:07','http://oranta.org/index.php?option=com_content&view=article&id=1065:dostatno-25-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Достатньо (25 березня)','uk',3079,4562,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,2.jpg\" />Один пустельник був такий святий, що одною ногою вже, як мовлять, стояв у небі.</p>\n<p>Його печера була вибита в стіні високої, покритої зеленню гори, і в ній майже нічого не було. Пустельник збирав лісові ягоди, трави, а інколи до недільного обіду, - трохи грибів.</p>\n<p>«Як же його спокусити?» - сушив собі голову Диявол.</p>\n','2013-03-24 00:36:32','http://oranta.org/index.php?option=com_content&view=article&id=1064:spokusa-24-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Спокуса (24 березня)','uk',3078,4561,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,42079775_119474588450719500.jpg\" />Пекло було вже майже заповнене, однак перед його брамою очікувало ще багато грішників. Дияволу не залишилося жодного іншого виходу, як заблокувати двері. Він вийшов до кандидатів на вхід і промовив до них:</p>\n<p>«Залишилося тільки одне місце, і, ясна річ, його можна зайняти тільки той з вас, хто був найбільшим грішником».</p>\n<p>Потім запитав:</p>\n','2013-03-23 00:31:35','http://oranta.org/index.php?option=com_content&view=article&id=1063:ostannje-misce-23-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Останнє місце (23 березня)','uk',3077,4560,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4546,200,Gendarme.jpg\" />У корпусі величезного корабля був вкручений гвинтик. Маленький і непримітний, він нічим не відрізнявся від багатьох інших, таких же пересічних гвинтиків, що з’єднували дві величезні сталеві плити.</p>\n<p>Під час довгої подорожі через Індійський океан маленький гвинтик усвідомив, що йому обридло це сіре і непомітне існування (за всі роки не знайшлося нікого, хто принаймі подякував би йому за працю). Гвинтик заявив: «Все, з мене досить! йду геть! »</p>\n','2013-03-22 00:22:10','http://oranta.org/index.php?option=com_content&view=article&id=1062:gvyntyk-22-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Гвинтик (22 березня)','uk',3072,4555,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,1VRxNWW9snQ.jpg\" />У старовинній книзі з бібліотеки монастиря два монахи прочитали, що на краю світу є таке місце, де сходяться небо з землею. І ось вони вирішили відшукати його, пообіцявши, що не повернуться доти, доки не знайдуть.</p>\n<p>Мандруючи по світу, вони наражалися на безліч небезпек, витримували спеку і холод, терпіли страшні злигодні, які часто супроводжують паломництво. Долали тисячі перешкод, які спроможні були відвернути людину від її мети. Та все-таки монахи витримали.</p>\n','2013-03-27 23:47:40','http://oranta.org/index.php?option=com_content&view=article&id=1069:de-shodjatsja-nebo-iz-zemleju-28-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Де сходяться небо із землею (28 березня)','uk',3093,4576,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,207776597.jpg\" />Трилітній Олександр:</p>\n<p>- Розкажи мені про злого вовка.</p>\n<p>Десятилітня Оксана:</p>\n','2013-03-28 23:54:06','http://oranta.org/index.php?option=com_content&view=article&id=1070:opovidannja-pro-zlogo-vovka-29-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Оповідання про злого вовка (29 березня)','uk',3105,4588,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,4159.jpg\" />- Тату, з чого починається війна? – запитує батька маленький Андрійко, дивлячись телевізійні новини.</p>\n<p>- Все починається більш-менш однаково: якщо, наприклад, Англія і Америка виявляють, що в них різні погляди на якийсь предмет …</p>\n<p>Тут втручається мама:</p>\n','2013-03-25 23:35:20','http://oranta.org/index.php?option=com_content&view=article&id=1067:pryklad-26-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Приклад (26 березня)','uk',3082,4565,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,logo.jpg\" />Одному юнакові приснилося, що він увійшов до великої крамниці. За прилавком стояв ангел.</p>\n<p>- Що ви продаєте? - спитав юнак.</p>\n<p>- Все, що бажаєте, - чемно відповів ангел.</p>\n','2013-03-29 23:58:16','http://oranta.org/index.php?option=com_content&view=article&id=1071:kramnycja-30-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','N','','Крамниця (30 березня)','uk',3067,4550,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4548,200,normal_00014_11052008.jpg\" />Німецький поет Рільке якийсь час жив у Парижі. Щодня дорогою до університету він разом зі своєю приятелькою француженкою переходив дуже людну вулицю.</p>\n<p>На розі цієї вулиці сиділа вже старша жінка і просила милостиню у перехожих - завжди на тому самому місці, нерухомо, як статуя, з простягнутою рукою й опущеними до землі очима.</p>\n','2013-03-26 23:38:31','http://oranta.org/index.php?option=com_content&view=article&id=1068:trojanda-27-bereznja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Троянда (27 березня)','uk',3087,4570,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4567,200,p_54575_1_gallerybig.jpg\" />Вислів \"панна Бідність\", за легендою, належить католицькому святому  Франциску Ассизському. Адже вийшовши із доволі небідної родини, той жив  милостинею та проповідував смиренність і каяття. Але й не гребував, до  речі, важкою селянською працею, коли допомоги в ній потребували віряни.</p>\n<p>Тому самому, що не мав ані взуття, ані змінного одягу – ходив у рубищі, підпоясаний шматком мотузки.</p>\n','2013-03-26 20:09:44','http://oranta.org/index.php?option=com_content&view=article&id=1076:qpanna-bidnistq-ta-suchasna-cerkva&catid=27:gk-v-ukrajini-cat&Itemid=57','P',NULL,'\"Панна Бідність\" та сучасна церква','uk',3085,4568,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4577,200,92700188.jpg\" />Я сьогодні ніяк не змогла настроїти інструменту. Всі мої намагання хоч якось зарадити справі, довготривалі старання підтягнути струни не увінчалися успіхом. І то вже не лише один кілочок, а й другий був таким неслухняним: щоразу, коли його підтягувала так і вислизав з-під пальців. Я була безпорадною і просто безсилою що-небудь змінити. Струни видавали такий спотворений звук, наче жалілися мені чи то на мене. Та все ще все мало бути попереду.</p>\n','2013-03-28 14:17:34','http://oranta.org/index.php?option=com_content&view=article&id=1079:struny-chelli&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Струни челлі','uk',3099,4582,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,30020.jpg\" />Якось один чоловік затримався біля гурту хлопчаків, які бавилися на  подвір’ї між будинками. Він почав підстрибувати, танцювати та виробляти  всілякі блазнівські штучки, щоб забавити їх.</p>\n<p>Мати одного з них побачила це зі свого вікна. Через якийсь час вона вийшла на подвір’я, підійшла до свого сина і сказала:</p>\n<p> </p>\n','2013-04-01 00:45:45','http://oranta.org/index.php?option=com_content&view=article&id=1080:bavytysja-z-bogom-1-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Бавитися з Богом (1 квітня)','uk',3109,4592,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4581,200,asfdasdf-640x360.jpg\" />Здоровенний та не надто чемний самурай прийшов до низенького буддійського монаха і каже:</p>\n<p>- Монаше, скажи мені, що таке пекло, а що – небо?</p>\n<p>Монах поглянув на дужого воїна і зневажливо відповів:</p>\n<p>\n','2013-04-01 23:07:38','http://oranta.org/index.php?option=com_content&view=article&id=1081:tajemnycja-raju-2-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Таємниця Раю (2 квітня)','uk',3110,4593,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,7563.jpeg\" />Один чоловік, який шукав мудрості, постановив зійти на гору, де раз  на два роки об\'являв себе Господь Бог.</p>\n<p>Першого року він споживав те, що  давала йому земля. На другий рік уже не мав чим харчуватися, тож вирішив  повернутися до міста.</p>\n<p>\"Бог несправедливий! - крикнув він у небо. - Не зважив на те, що я  жив тут цілий рік, аби почути Його голос. А тепер я зголоднів і мушу йти  звідси, хоч так і не почув Його мови.\"</p>\n','2013-04-04 23:22:49','http://oranta.org/index.php?option=com_content&view=article&id=1089:plody-5-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Плоди (5 квітня)','uk',3120,4603,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,b1qNVPx8Sv0.jpg\" />На собі вона мала гарну сукенку жовтогарячого кольору, волосся було  стягнуте червоно-золотавою стрічкою - готувалася йти до школи. Був День  батька, отже, всі діти мали прийти до школи в супроводі своїх татусів.  Вона єдина йшла з мамою.</p>\n<p>Мама переконувала її залишитися вдома, мовляв, у  школі, найімовірніше, не зрозуміють їхнього вчинку. Та дівчинка  прагнула будь-що-будь розповісти всім про свого татуся, який сильно  відрізнявся від інших.</p>\n','2013-04-06 23:36:04','http://oranta.org/index.php?option=com_content&view=article&id=1092:den-batka-7-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'День батька (7 квітня)','uk',3126,4609,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4599,200,name_777.jpg\" />Один горобчик лежав на спині, простягаючи свої ніжки просто до неба. Прилетів другий горобчик і здивовано спитав:</p>\n<p>- Що ти робиш? Чому лежиш на спині? Що з тобою?</p>\n<p>Не рухаючись, перший відповів:</p>\n','2013-04-04 09:13:26','http://oranta.org/index.php?option=com_content&view=article&id=1088:hto-pidtrymuje-nebo-4-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Хто підтримує небо (4 квітня)','uk',3118,4601,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4610,200,46531687979.jpeg\" />Він був справді блискучим менеджером. Робочий день його був  організований бездоганно – сприяли цьому комп’ютер, мобільний телефон і  годинник. Цінував чи не кожну секунду свого часу!</p>\n<p>Одного дня він їхав до великої правничої фірми, де мав зустрітися з  поважними людьми. Але розкішний хмародер, у якому розмістився офіс тієї  фірми, був стояв у самісінькому середмісті, й менеджер якоїсь миті  збагнув, що, виїжджаючи, не врахував шаленого автомобільного руху в  центрі міста, ані того, що неодмінно матиме труднощі з паркуванням своєї  машини.</p>\n','2013-04-07 22:57:45','http://oranta.org/index.php?option=com_content&view=article&id=1094:parkuvannja-8-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Паркування (8 квітня)','uk',3128,4611,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4610,200,IMG_0126.jpg\" />Один  шанований людьми священик, що служив на маленькій парафії, завжди вельми  старанно готувався до недільної проповіді. Якось у храмі — то був  Великодній ранок — він зійшов на казальницю. А в руці тримав іржаву  металеву клітку, яку помістив так, щоб усі вірні могли її бачити.</p>\n<p>Парафіяни  були здивовані. Священик тим часом говорив:</p>\n','2013-04-08 23:05:46','http://oranta.org/index.php?option=com_content&view=article&id=1095:cina-9-kvitnjaja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Ціна (9 квітня)','uk',3136,4619,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4612,200,DSCN9899.JPG\" />Ніна Марчненко любила приходити молитись на Аскольдову Могилу і часто &nbsp;недільну Божественну Літургію молилась в Патріаршому Соборі Воскресіння Христового в Києві. Тим вона свідчила, що є християнкою не розділеною конфесіями.</p>\n<p>Її розповіді про свого сина і продовження його життя у ній у мамі, робили її святою. Мужні і світлі очі світились любов’ю і прощенням.</p>\n','2013-04-08 13:15:07','http://oranta.org/index.php?option=com_content&view=article&id=1097:mama-i-syn-z-hrystom&catid=31:kolonka-redaktora-cat&Itemid=1','P',NULL,'Мама і син з Христом','uk',3131,4614,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4621,200,caloric-table-children.jpg\" />Одна родина завітала до ресторану. Офіціантка спершу вислухала замовлення дорослих, потім звернулася до семирічного хлопчика.</p>\n<p>- А ти що замовиш? - спитала.</p>\n<p>Хлопчик обвів несміливим поглядом своїх рідних і проказав:</p>\n','2013-04-11 23:29:46','http://oranta.org/index.php?option=com_content&view=article&id=1102:oficiantka-12-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Офіціантка (12 квітня)','uk',3152,4635,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4621,200,vera.jpg\" />Жив собі на світі знак запитання. Був дуже привабливим і, як усі, властиво, знаки запитання, вельми інтелігентним.</p>\n<p>Проте від певний час мандрував по країні сумний та пригнічений, сповнений гіркоти й розчарування.</p>\n<p>Скидалося  на те, що він став нікому не потрібний. Усі дедалі частіше зверталися  до його запеклого ворога, знаку оклику. Тільки й робили, що викрикували:  \"Вперед! Зупинися! Рушай! Геть з дороги!\"</p>\n','2013-04-09 22:11:44','http://oranta.org/index.php?option=com_content&view=article&id=1100:znak-zapytannja-10kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Знак запитання (10квітня)','uk',3141,4624,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4621,200,x_68fd889f.jpg\" />Декілька молодих менеджерів, що тиждень навчалися на курсах  підвищення кваліфікації, бігли коридорами аеропорту. Це було ввечері у  п’ятницю, усі хотіли летіти на вихідні додому.</p>\n<p>Чоловіки спізнювалися. Рейс літака відкладали вже кілька разів.</p>\n<p>Бігли коридорами летовища, стискаючи в долонях паспорти, квитки та ручки валіз.</p>\n','2013-04-10 23:18:01','http://oranta.org/index.php?option=com_content&view=article&id=1101:jabluka-11-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Яблука (11 квітня)','uk',3144,4627,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,1331807683_main-7625-a384cdce6e175f9787255a9770534b3c.jpg\" />Ось правдива історія однієї дівчинки, яка вірила і знала, що любов  здатна творити дива. Її братикові загрожувала смерть від пухлини в  мозку. Батьки хлопчика зробили все, щоб його врятувати, однак заможними  не були і, витративши всі свої заощадження, відвернути лиха таки не  змогли.</p>\n<p>Якось увечері батько, із важким серцем, сказав заплаканій  матері:</p>\n<p>- Знаєш, люба, - нам, певно, не вдасться його врятувати. Тут може  зарадити тільки чудо!</p>\n','2013-04-12 23:33:42','http://oranta.org/index.php?option=com_content&view=article&id=1108:chudo-13-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Чудо (13 квітня)','uk',3154,4637,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,1333681046_13.jpg\" />Один мудрий індус мав приятеля. Приятель жив у Мілані. Вони  познайомилися в Індії, коли італієць приїздив туди зі своєю сім\'єю.  Індус провадив екскурсії для італійців, відкриваючи їм найхарактерніші  закутки своєї вітчизни.</p>\n<p>Міланець заприязнився з індусом і на знак вдячності запросив того до  свого міста. Індус довго не міг відважитися на таку далеку подорож, але  врешті-решт дав себе переконати.</p>\n','2013-04-14 22:15:40','http://oranta.org/index.php?option=com_content&view=article&id=1110:polovy-cvirkun-i-moneta-15-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Польовий цвіркун і монета (15 квітня)','uk',3157,4640,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4628,200,57641.jpg\" />Увечері професор Метью Генрі вертався з університету  додому, коли біля самісінького будинку хтось зненацька приставив йому до  чола пістолет.</p>\n<p>Грабіжник у масці велів йому віддати портфель і теку. Професор  послухався, й розбійник миттєво зник у темряві.</p>\n','2013-04-13 22:09:48','http://oranta.org/index.php?option=com_content&view=article&id=1109:pislja-napadu-grabizhnyka-14-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Після нападу грабіжника (14 квітня)','uk',3155,4638,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4644,200,Odna_doroga.jpg\" />Один відомий професор університету, якого мали представити до Нобелівської премії, вибрався на озеро.</p>\n<p>Він  попросив перевізника, аби той повозив його по озеру на своєму човні.  Добродій погодився. Коли відплили вже доволі далеко від берега, професор  узявся допитуватись.</p>\n<p>- Чи ти знаєш історію?</p>\n','2013-04-15 23:13:03','http://oranta.org/index.php?option=com_content&view=article&id=1116:profesor-i-pereviznyk-16-kviten&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Професор і перевізник (16 квітень)','uk',3164,4647,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4644,200,b8bIAVQQW8Q.jpg\" />В  одній африканській церкві, під час збору пожертв, служки ходили поміж  лавами з великим кошиком з лози, схожим на ті, що служать для збору  маніоку.</p>\n<p>Хлопчина,  який сидів в останньому ряді, не зводив зосередженого погляду з кошика,  що переходив по рядах. Він тяжко зітхнув, бо не мав нічого, щоб  пожертвувати Господеві.</p>\n','2013-04-16 23:17:01','http://oranta.org/index.php?option=com_content&view=article&id=1117:zhertva-17-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Жертва (17 квітня)','uk',3162,4645,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4645,200,vodna-stuhiia.jpg\" />Село, що розташувалося біля замку, розбудив голос каштелянового посланця, який зачитував на площі оголошення свого пана.</p>\n<p>\"Наш улюблений господар запрошує усіх своїх добрих і вірних підданих  взяти участь у гостині, яку він влаштовує з нагоди своїх уродин. На  кожного чекатиме приємна несподіванка.</p>\n','2013-04-17 23:26:30','http://oranta.org/index.php?option=com_content&view=article&id=1119:gostyna-u-zamku-18-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Гостина у замку (18 квітня)','uk',3166,4649,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,verbna_nedila.jpg\" />Одного вечора два туристи, що зупинилися в кемпінґу на  березі озера, вирішили переплисти човном на другий берег, аби посидіти в  тамтешній кнайпі.</p>\n<p>Засиділися вони до пізньої ночі і встигли спорожнити цілу батарею  пляшок.</p>\n<p>Нарешті, дещо похитуючись, туристи вийшли із кнайпи, проте їм таки  вдалося зайняти місця у човні, аби рушити у зворотній бік.</p>\n','2013-04-19 23:36:17','http://oranta.org/index.php?option=com_content&view=article&id=1126:choven-20-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Човен (20 квітня)','uk',3177,4660,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4655,200,x_129c5954.jpg\" />Орел,  король над усіма птахами, віддавна чув про незвичайний талант солов\'я.  Як добрий володар, він прагнув переконатися, правду кажуть чи ні. Аби  пересвідчитись, послав на перевірку двох своїх урядників: павича і  жайворонка. Вони мали оцінити красу і спів соловейка.</p>\n<p>Посланці виконали свою місію і повернулися до орла.</p>\n','2013-04-18 23:17:26','http://oranta.org/index.php?option=com_content&view=article&id=1125:zvit-dlja-orla-19-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Звіт для орла (19 квітня)','uk',3173,4656,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4660,200,922822_10151563659147241_739970808_n.jpg\" />Якось один чоловік підійшов до Ісуса і мовив:</p>\n<p>— Учителю, усі ми знаємо, що походиш від Бога і навчаєш нас іти  шляхом істини. Але мушу Тобі сказати, що Твої послідовники, ті, яких  називаєш апостолами, або Твоєю спільнотою, мені анітрохи не подобаються. Я побачив, що вони зовсім не відрізняються від звичайних людей.</p>\n','2013-04-20 23:01:44','http://oranta.org/index.php?option=com_content&view=article&id=1131:poblyzko-vognju-21-kvitnja&catid=25:parafija-cat&Itemid=27','P',NULL,'Поблизько вогню (21 квітня)','uk',3179,4662,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4662,200,lvivskij-kloun-stvorjuje-kavo_699_s1.jpg\" />До кабінету відомого психіатра якось увійшов чоловік, на перший погляд  цілком урівноважений, поважний зі себе й елегантний. Та за кілька хвилин  розмови лікар переконався, що відвідувач перебуває у полоні глибокої  депресії і безпросвітного гнітючого смутку.</p>\n','2013-04-21 23:14:36','http://oranta.org/index.php?option=com_content&view=article&id=1133:kloun-22-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Клоун (22 квітня)','uk',3181,4664,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4665,200,578443_470694852973531_1569590393_n.jpg\" />Якось улітку сімейство їжаків поселилося у лісі. Погода стояла чудова,  було гаряче, і їжаки цілий день гралися у затінку дерев або пустували на  полях поблизу лісу, бавилися у хованки поміж квітів, ловили мушок, аби  підживитися, а коли приходила ніч, засинали на купках моху поблизько  нірок. Та ось одного дня вони побачили, що листя з дерев уже опадає.  Наближалася осінь! Листки падали і падали. Їжаки бавилися, ганяючись за  ними.</p>\n','2013-04-24 08:45:21','http://oranta.org/index.php?option=com_content&view=article&id=1139:prygoda-z-jizhakamy-24-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пригода з їжаками (24 квітня)','uk',3190,4673,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4666,200,images.jpg\" />У  давні часи, ще коли всі ходили босі, був собі великий індіянський  вождь, який мав дуже чутливі стопи і черстве серце. Він страшенно  страждав, коли йому доводилося ходити по гострих каміннях, якими була  вкрита земля, що належала його племені.</p>\n<p>І після довгих роздумів вождь  прийняв важливе рішення: він наказав своїм воїнам убити всіх бізонів і  їх м\'якою шкірою вистелити терени володінь племені.</p>\n','2013-04-22 23:14:47','http://oranta.org/index.php?option=com_content&view=article&id=1134:velyky-vozhd-23-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Великий вождь (23 квітня)','uk',3185,4668,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4666,200,thumbnail.jpg\" />Якось одна чемна і добре вихована мишка, що мала симпатичний домашній  вигляд, відчайдушно тікаючи від кота, опинилася у пивниці багатої вілли.  Там було зовсім темно і мишка впала до якоїсь дивної калюжі. Це був  добрий коньяк, що витік через попсутий отвір дубової бочки. Мишка  несміливо лизнула дивну рідину. Смак їй сподобався. Це було щось міцне  та запашне і гріло горло, мов вогонь.</p>\n','2013-04-24 23:36:41','http://oranta.org/index.php?option=com_content&view=article&id=1141:mysha-25-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Миша (25 квітня)','uk',3192,4675,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4676,200,vyrastili-detskoe-serdce.jpg\" />Молодим людям, які приходили до нього вперше, раббі  Бунам розповідав історію вчителя Езекії, сина Єкеля, що з Кракова.</p>\n<p>По багатьох роках тяжкої праці (що, проте, зовсім не порушило його віри в  Бога), йому приснився сон, ніби він мусить податися до Праги, аби там,  під мостом, що провадить до королівського палацу, шукати скарб.</p>\n','2013-04-25 23:56:08','http://oranta.org/index.php?option=com_content&view=article&id=1144:pid-pichkoju-26-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Під пічкою (26 квітня)','uk',3195,4678,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4678,200,images.jpg\" />Один чоловік прийшов до монаха пустельника. Він спитав його: - Чого вчить тебе це життя у мовчанні? Монах, що саме набирав води з криниці, відповів своєму гостеві: - Подивись на дно цієї криниці. Що ти бачиш? Чоловік зазирнув до колодязя. - Нічого не бачу, - відповів. Якийсь час монах стояв непорушно, а тоді знову звернувся до гостя: - Подивись тепер! Що бачиш у колодязі?</p>\n','2013-04-26 23:21:55','http://oranta.org/index.php?option=com_content&view=article&id=1145:movchannja-27-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Мовчання (27 квітня)','uk',3196,4679,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4678,200,wzeeMYrW7cU.jpg\" />Приїхавши в гори на відпочинок, мати чотирилітнього Марка почала вбивати павуків, що по всіх кутках розплели своє павутиння.</p>\n<p>Марко спостерігав якийсь час за нею, а потім каже:</p>\n<p>— Мамо, малих павуків не вбивай! Мати питає його:</p>\n','2013-04-27 23:38:03','http://oranta.org/index.php?option=com_content&view=article&id=1146:pavukovi-dity-28-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Павукові діти (28 квітня)','uk',3201,4684,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,10082610421222960_f0_0.jpg\" />Одного дня на пляжі зустрілися Краса і Гидота.</p>\n<p>— Скупаймося в морі, — запропонувала Гидота Красі.</p>\n<p>Роздягнулися й попливли морем.</p>\n<p>Через якийсь час Гидота вийшла з води і, не знайшовши свого вбрання, взяла одяг Краси і пішла собі своєю дорогою.</p>\n','2013-05-04 21:55:28','http://oranta.org/index.php?option=com_content&view=article&id=1158:masky-5-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Маски (5 травня)','uk',3217,4700,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,123.jpg\" />У Північній Африці якийсь місіонер побачив, що один бедуїн лягав на землю, прихиляв вухо додолу і щось слухав.</p>\n<p>Здивований місіонер запитав його:</p>\n<p>— Що ти робиш?</p>\n','2013-04-28 20:26:56','http://oranta.org/index.php?option=com_content&view=article&id=1151:pustelja-plache&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пустеля плаче (29 квітня)','uk',3203,4686,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,1808.jpg\" />Якось в одній далекій долині почалася повінь. Дощ падав так сильно і  безперервно, що невдовзі часі всі поля і ниви були залиті водою. З  кожним днем вода підносилася все вище і вище.</p>\n<p>Раптом почувся чийсь плач. Плакала найповільніша і найдурніша черепаха.</p>\n<p>— Чому ти плачеш? — заґеґала гуска, що пролітала над нею.</p>\n','2013-04-29 21:01:14','http://oranta.org/index.php?option=com_content&view=article&id=1152:durna-cherepaha-30-kvitnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дурна черепаха (30 квітня)','uk',3204,4687,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,319525-1920x1200.jpg\" />Бідна жінка знайшла одне яйце. Дуже зрадівши, вона покликала свого чоловіка і дітей та сказала:</p>\n<p>— Наші турботи і бідування скінчилися! Дивіться! Я знайшла оце яйце. Ми  його не їстимемо, а занесемо до сусіда, щоб його квочка висиділа нам  курчатко, яке стане куркою. Очевидно, і курку не будемо їсти, а  розведемо багато курей, щоб мати багато яєць.</p>\n','2013-05-09 22:32:40','http://oranta.org/index.php?option=com_content&view=article&id=1163:jace-10-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Яйце (10 травня)','uk',3227,4710,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,35300292.jpg\" />Одного дня селянин, який жив неподалік від обителі, підійшов до  монастирської брами й енергійно застукав у неї своєю мозолистою рукою.  Коли воротар відчинив, чоловік, усміхаючись, показав йому великий кетяг  соковитого винограду:</p>\n<p>— Брате-воротарю, — сказав селянин, — знаєте, кому хочу подарувати цей чудовий виноград?</p>\n','2013-05-02 21:32:03','http://oranta.org/index.php?option=com_content&view=article&id=1156:kolo-radosty-3-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Коло радости (3 травня)','uk',3211,4694,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,427625363.jpg\" />Один нічний метелик з романтичною душею закохався в зірку. Він розповів про це мамі, а вона порадила йому закохатися в лампу.</p>\n<p>— Зірки не для того, щоб за ними літати, — пояснила йому. — А лампи… Навколо них ти можеш спокійно собі пурхати.</p>\n','2013-05-01 21:21:43','http://oranta.org/index.php?option=com_content&view=article&id=1155:nichny-metelyk-i-zirka-2-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Нічний метелик і зірка (2 травня)','uk',3209,4692,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,7fa0176de928def418b2ea14d36.jpg\" />Одна княжна на свої уродини отримала від нареченого важку, округлої  форми посилку. Дуже заінтригована, що там таке, вона швидко відкрила її і  знайшла… гарматний снаряд.</p>\n<p>Розчарована князівна кинула його на землю.</p>\n<p>При падінні зовнішня оболонка снаряда розламалася, а в ній виявився  менший снаряд зі срібла. Князівна взяла його в руки і, оглядаючи,  випадково сильно натиснула пальцями. Срібна оболонка прорвалася, а  всередині виявилась золота коробка.</p>\n','2013-04-30 21:16:42','http://oranta.org/index.php?option=com_content&view=article&id=1154:ljubovny-lyst-1travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Любовний лист (1 травня)','uk',3208,4691,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,92376290.jpg\" />Устриця пожалілася сусідці:</p>\n<p>— Відчуваю великий біль у моєму єстві. Сусідка відповіла з гордим вдоволенням:</p>\n<p>— Хвала хай буде небесам і морю! Я не відчуваю жодного болю. Я — здорова всередині й зовні! Добре мені!</p>\n','2013-05-08 22:27:07','http://oranta.org/index.php?option=com_content&view=article&id=1162:perlyna-9-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Перлина (9 травня)','uk',3224,4707,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,987554.jpg\" />Стародавня перська казка розповідає про чоловіка, що жив однією думкою,  котра його ніколи не покидала, — мати золото, якнайбільше золота.</p>\n<p>Це  прагнення з’їдало йому мозок, з’їдало і серце. В нього не було жодної  іншої думки, жодного іншого бажання. Тільки золото і золото.</p>\n','2013-05-03 21:43:22','http://oranta.org/index.php?option=com_content&view=article&id=1157:pravdyvy-slipec-4-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Правдивий сліпець (4 травня)','uk',3213,4696,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,9IqTzZaBW24.jpg\" />Він — високий, плечистий, сильний. Голос у нього гучний, поводиться впевнено. Вона — жінка ніжна, тендітна. Побралися.</p>\n<p>Він постачав їй усе, вона дбала про дім, виховувала дітей. Діти виросли, одружилися і пішли від них. Так буває завжди.</p>\n','2013-05-05 22:04:53','http://oranta.org/index.php?option=com_content&view=article&id=1159:skazhit-ce-ranishe-6-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Скажіть це раніше (6 травня)','uk',3220,4703,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,images.jpg\" />Мені було десять років, коли я побачив, як дрозд жадібно дзьобав ягоди  бузини. Через якийсь час він здійнявся, полетів на мур нашого сусіда і  почав ретельно чистити собі дзьоб об камінь.</p>\n<p>Маленьке зернятко бузини, майже невидиме, впало йому з дзьоба і закотилося під мур.</p>\n','2013-05-06 22:14:01','http://oranta.org/index.php?option=com_content&view=article&id=1160:sprava-drozda-7-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Справа дрозда (7 травня)','uk',3222,4705,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4685,200,ua.The-love-of-God-must-have-the-first-place....jpg\" />У неділю, прийшовши з церкви, жінка готувала обід. її чоловік вирішив покпити з неї і спитав:</p>\n<p>— Чи ти могла б переповісти те, що сказав нині отець на проповіді?</p>\n<p>— Я вже не пам’ятаю, — зізналася жінка.</p>\n','2013-05-07 22:22:22','http://oranta.org/index.php?option=com_content&view=article&id=1161:pamjataty-propovid-8-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пам\'ятати проповідь (8 травня)','uk',3223,4706,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,123.jpg\" />Чоловік і жінка досить пізно одружились, у них народився син. Вони  виховували його з любов’ю, піклуючись, як тільки могли. Попри те, що  самі жили убого, відправили його до школи мудрого вчителя, аби син міг  зростати духовно. Коли хлопець повернувся додому, то захотів хоч якось  віддячитися батькам.</p>\n<p>\"Чи міг би я щось для вас зробити? – спитав він. – Щось, що принесло би вам радість.\"</p>\n','2013-05-30 01:54:21','http://oranta.org/index.php?option=com_content&view=article&id=1194:nakrashche-vyno-30-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Найкраще вино (30 травня)','uk',3283,4766,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,24132615.jpg\" />Батько зі сином сиділи у бічній наві храму. За якийсь час дитина  торкнула батька і сміючись сказала: \"Тату, поглянь на того чоловіка. Він  спить\".</p>\n<p>Батько повагом подивився на сина і відповів: \"Було би ліпше, якби ти теж спав, замість обмовляти інших\".<strong></strong></p>\n','2013-05-22 00:45:54','http://oranta.org/index.php?option=com_content&view=article&id=1186:inshi-22-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Інші (22 травня)','uk',3262,4745,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,514_179_new_Papa_V1.jpg\" />Був собі монах. Жив він мирно і безтурботно. Але була одна річ, яка  позбавляла його спокою: він боявся вічності. У раю вибрані співають  хвалу Богу, так, як це роблять монахи. Але одна річ чинити це упродовж  якогось часу, а інша – цілу вічність. Яка ж нудьга охопить через кілька  мільйонів років щасливців, що перебувають поруч із Богом!</p>\n','2013-05-29 01:34:58','http://oranta.org/index.php?option=com_content&view=article&id=1193:vichnist-29-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вічність (29 травня)','uk',3282,4765,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,591.jpg\" />Одного  разу добрий Бог вирішив створити... маму. Шість днів та ночей Він  роздумував і експериментував. Та ось з\'явився ангел і каже:</p>\n<p>- Ти стільки часу тратиш на неї!</p>\n<p>-  Так... Але чи ти читав вимоги замовлення? Вона повинна складатися зі  180 рухомих частин, які можна було б при потребі замінити, її поцілунок  має лікувати все - від зламаної ноги до розчарування в коханні, також  вона мусить мати шість пар рук.</p>\n<p> </p>\n','2013-05-11 23:27:59','http://oranta.org/index.php?option=com_content&view=article&id=1176:koly-bog-tvoryv-mamu-12-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Коли Бог творив Маму (12 травня)','uk',3233,4716,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,800xorum11.jpg\" />Батько сімейства натер машину мастикою і старанно пуцував, щоб надати їй бажаного полиску.</p>\n<p>Одинадцятилітній син допомагав йому – він чистив шматкою бампер.</p>\n<p>\"Бачиш, сину, - мовив з повагою батько, - ця машина - справжній сімейний  скарб. З нею треба поводитися обережно, дбайливо, їй треба приділяти  увагу і час\"</p>\n','2013-05-31 02:00:04','http://oranta.org/index.php?option=com_content&view=article&id=1195:skarb-31-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Скарб (31 травня)','uk',3284,4767,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,8178.jpg\" />Один чоловік кілька місяців поспіль безуспішно добивався побачення із  жінкою, котру кохав. Він дуже страждав, коли чув відмову. Врешті-решт  жінка поступилася і погодилася на зустріч.</p>\n<p>\"Ось тобі година й місце. Приходь!\" - сказала вона.</p>\n<p>У призначений час, у призначеному місці чоловік нарешті сів побіля своєї  коханої. Він сягнув до кишені і вийняв стос любовних листів, які  написав за ті місяці.</p>\n','2013-05-24 00:59:46','http://oranta.org/index.php?option=com_content&view=article&id=1188:ljubovni-lysty-24-lystopada&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Любовні листи (24 листопада)','uk',3273,4756,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,82a156167aa18068c4592ca79a097f7e.jpg\" />Коли моя бабуся вирушила навідати свою маму, вона відводила на це три  дні. Один день ішов, щоб добратися туди на бричці, запряженій кіньми, ще  один – аби розповісти і послухати останні новини, посидівши трохи в  кухні, а трохи у саду, і третій день – на дорогу назад.</p>\n<p>Коли моя мати їздила до своєї мами, то їй потрібно було два дні. Вона  добиралася потягом і, якщо їй щастило з пересадками, приїжджала того ж  дня від вечір.</p>\n','2013-05-23 00:53:53','http://oranta.org/index.php?option=com_content&view=article&id=1187:postup-23-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Поступ (23 травня)','uk',3267,4750,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,83c31c2bda8c8165378f09fd78cc.jpeg\" />Дуже давно один місіонер разом зі своїм провідником-індіянином пробився  через Скелясті Гори. Щовечора, коли чубок сонця ховався за видноколом, молодий індіянин  відходив від місіонера, повертався у той бік і починав ритмічно  переступати з ноги на ногу. При цьому він упівголоса наспівував якусь  тужливу пісню.</p>\n','2013-05-17 00:17:45','http://oranta.org/index.php?option=com_content&view=article&id=1181:pro-zahid-soncja-17-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Про захід сонця (17 травня)','uk',3247,4730,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,88870.jpg\" />Одна дівчинка, приходячи до дитячого садка, завжди приносила в сумочці дві хустинки. Вихователька запитала її чому.</p>\n<p>— Бо так сказала мені моя мама, — відповіла дівчинка.</p>\n<p>— А для чого вони тобі? — питає далі вихователька.</p>\n','2013-05-10 23:11:49','http://oranta.org/index.php?option=com_content&view=article&id=1175:dvi-hustynky-11-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дві хустинки (11 травня)','uk',3230,4713,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,978346586.jpg\" />Гнучке деревце у саду прив’язали до міцного ясеневого кийка. Кийок служив деревцеві за опору й охорону, допомагав йому, щоби зростало струнким.</p>\n<p>Коли вітер запрошував у танок, юне деревце похитувало своєю щораз буйнішою кроною, розгойдувалося і кричало: «Відпусти мене, прошу, чому ти так міцно мене тримаєш? Дивись-но: усі гойдаються на вітрі. Чому ж я маю стояти мов укопане?»</p>\n','2013-05-28 01:26:06','http://oranta.org/index.php?option=com_content&view=article&id=1192:opora-28-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Опора (28 травня)','uk',3278,4761,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,DJfUEFGQR10.jpg\" />Один чоловік завжди відчував себе пригніченим через різні життєві  труднощі. І якось він поділився цим із відомим духовним наставником.</p>\n<p>- Не можу більше! Життя стало просто нестерпне!</p>\n<p>- Учитель узяв у пригорщу попелу і вкинув до склянки, у якій перед тим була кришталево чиста питна вода, і промовив:</p>\n','2013-05-15 00:01:36','http://oranta.org/index.php?option=com_content&view=article&id=1179:vybir-15-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вибір  (15 травня)','uk',3238,4721,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,L2NDGFQv1Yw.jpg\" />У  скляній посудині жили дві червоні рибки. Ліниво пересуваючись попри  скляні стінки, вони мали досить часу, аби собі пофілософствувати. І  якогось одна рибка спитала іншу:</p>\n<p>- Чи ти віриш у Бога?</p>\n<p>- Звичайно!</p>\n','2013-05-13 23:58:03','http://oranta.org/index.php?option=com_content&view=article&id=1178:hto-pro-nas-pamjataje-14-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Хто про нас пам’ятає (14 травня)','uk',3237,4720,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,TSvVeoIE9I4.jpg\" />Після страшної бурі на морі вцілілий бідолаха, учепився за рештки свого  човна, добрався до маленького безлюдного острівка. Острівець той майже  суцільно вкрили непривітні гострі скелі. Бідака став гаряче благати  Бога, аби його порятував. Кожного дня він вдивлявся, чи десь-там, з-за  горизонту, не з’явиться допомога, проте ніхто з нею не поспішав.</p>\n','2013-05-19 00:29:37','http://oranta.org/index.php?option=com_content&view=article&id=1183:sygnal-19-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Сигнал (19 травня)','uk',3249,4732,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,ce53cdf389kos.jpg\" />Багатий честолюбний юнак звернувся до учителя із проханням, аби той  допоміг йому досягнути справжньої величі. Мудрець дав йому дивне  завдання: \"Рушай у мандрівку по країні і плати людям, аби тебе прозивали  і проклинали. Чини так рік\".</p>\n<p>Хлопець міряв дороги і шляхи міст і сіл, роздавав гроші і просив, щоб його якнайдошкульніше ображали. Усі мали його за шаленця.</p>\n','2013-05-27 01:17:57','http://oranta.org/index.php?option=com_content&view=article&id=1191:zvytjazhec-27-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Звитяжець (27 травня)','uk',3277,4760,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,iir_m.jpg\" />Спекотливої літньої днини робітникові, що працював на багатого  господаря, було звелено скопати сад. Наймит неохоче взявся до роботи.  При цьому він нарікав на Адама, що через нього люди отак використовують  працю інших людей.</p>\n<p>Ці ремствування і прокльони дійшли до вух господаря.</p>\n','2013-05-16 00:08:04','http://oranta.org/index.php?option=com_content&view=article&id=1180:spokusa-16-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Спокуса (16 травня)','uk',3244,4727,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,images.jpg\" />Королівський двір гучно обходив святкування з нагоди прибуття короля до  столиці. У багато прикрашеній залі король приймав дари від своїх  підданих. Усі подарунки були дуже цінні: коштовна зброя, срібляні  келихи, тканини, гаптовані золотом.</p>\n<p>Коли вервиця жертводавців уже майже підходила до кінця, з’явилася  столітня селянка. Вона йшла, важко ступаючи у своїх дерев’яних  взувачках. Жінка накульгувала і спиралася на палицю. У повній тиші вона  вийняла з кошика дарунок, що був старанно згорнутий у полотно.</p>\n','2013-05-20 00:33:19','http://oranta.org/index.php?option=com_content&view=article&id=1184:klubok-vovny-20-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Клубок вовни (20 травня)','uk',3254,4737,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,img0.jpg\" />Над берегом одного з озер на півночі Індії жив дивний птах, який мав дві  голови. Одна була з правого боту тулуба, а інша – з лівого. Один тулуб з  двома головами.</p>\n<p>Якось птах кружляв у пошуках поживи. Очі правої голови зауважили соти з диким медом. Птах одразу кинувся згори до них.</p>\n<p>Голова, котра з лівого боку, просила: \"Дай мені трохи\".</p>\n','2013-05-21 00:39:32','http://oranta.org/index.php?option=com_content&view=article&id=1185:dvi-golovy-21-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Дві голови  (21 травня)','uk',3259,4742,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,mama-tseluet-malyisha-pak4j.jpg\" />Маленька дівчинка готувала подарунок на Різдво. Вона запакувала пуделко у  гарну барвисту обгортку, і саме робила великі банти, використовуючи  багато кольорової стрічки.</p>\n<p>\"Що ти робиш? – зганив її батько. – Ти тільки даремно переводиш стрічку. Уявляєш, скільки вона коштує?\"</p>\n<p>Дівчинці на очі виступили сльози і вона сховалася у кутку, міцно притискуючи до грудей своє пуделко.</p>\n','2013-05-25 01:07:49','http://oranta.org/index.php?option=com_content&view=article&id=1189:pudelko-25-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пуделко (25 травня)','uk',3274,4757,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,molutva.jpg\" />Катехит питав дітей чи вони моляться.</p>\n<p>\"Скажи нам, чи ти відмовляєш вечірні молитви?\"</p>\n<p>\"Так, звичайно\".</p>\n<p>\"А вранці також молишся?\"</p>\n','2013-05-26 01:14:16','http://oranta.org/index.php?option=com_content&view=article&id=1190:molytva-26-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Молитва (26 травня)','uk',3275,4758,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,ua.The-love-of-God-must-have-the-first-place....jpg\" />Якось Мойсей, блукаючи по пустелі, зустрів пастуха. Мойсей провів із ним  цілий день, допоміг подоїти овець. Коли вже стемніло, пророк зауважив,  що пастух відлив трохи молока до посудини і поставив її на скелі,  недалеко від того місця, де вони ото перебували.</p>\n','2013-05-18 00:22:02','http://oranta.org/index.php?option=com_content&view=article&id=1182:moloko-dlja-gospoda-boga-18-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Молоко для Господа Бога (18 травня)','uk',3248,4731,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4712,200,xr5osRVDx_4.jpg\" />У царя був блазень, який щодня розвеселяв його жартами і сміховинками.</p>\n<p>Одного дня цар поручив блазневі свій скипетр:</p>\n<p>— Тримай його доти, доки не знайдеш дурнішого від себе. Тоді можеш подарувати йому цей скипетр.</p>\n','2013-05-12 23:51:03','http://oranta.org/index.php?option=com_content&view=article&id=1177:casky-blazen-13-travnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Цаський блазень (13 травня)','uk',3235,4718,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4734,200,182828_138275583031727_1784860436_n.jpg\" />11-12 травня&nbsp; 2013 року відбулося свято у храмі св. Миколая Доброго на Аскольдовій моглі. До Першої св. Сповіді та Урочистого св. Причастя приступило 22 осіб. Решта дітей (4 осіб) у зв’язкуз поважними причинами святкуватимуть &nbsp;влітку під час літнього табору. На цій події були присутні батьки, родини,знайомі та з’їхалось багато гостей з різних куточків світу. Святкову св. Літургію, відслужив о. Ігор Онишкевич, настоятель храму, та о. Константин Пантелей. Рядочок дітей, вдягнених в українське вбрання, немов намистинки розсипались по церковному подвір’ю, тримаючи в руках прикрашені свічки, що символізують світло Христове, яке освітлює життя.</p>\n','2013-05-19 22:10:27','http://oranta.org/index.php?option=com_content&view=article&id=1209:urochyste-prychastja-na-askoldovi&catid=25:parafija-cat&Itemid=27','P',NULL,'Урочисте причастя на Аскольдовій','uk',3253,4736,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4740,200,images.jpg\" />Вона прийшла і обійняла мене за плече. Сиділа мовчки і, здавалось, навіть не дихала або ж дихала моїм диханням, так що cпершу зовсім не помічала її. Вона чекала, щоби вкотре бути потрібною. Вміла чекати. Тихо, самотньо, довго. Найчастіше вона з`являлася пізньою вечоровою порою, разом зі мною коротала безсонну ніч.&nbsp; Не говорила нічого, слухала. Переживала мою тривогу, неспокій, сум`яття, розчарування. Виявляла потроху мій біль. О так, тоді він краявся надвоє. Він вже не був таким нестерпним і пекучим. Не дошкуляв, не тупцював на місці. Відлягав помалу.</p>\n','2013-05-20 22:35:25','http://oranta.org/index.php?option=com_content&view=article&id=1214:samotnist--samota&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Самотність – самота','uk',3258,4741,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4768,200,CRW_5170.jpg\" />Однієї холодної зимової ночі шестеро людей опинились на безлюдному  острові. Кожен тримав у руках шматок дерева. На тому клаптику землі,  загубленому у Північному морі, зовсім не було дерев. Посередині поволі  догоряло вогнище – бракувало дров.</p>\n<p>Мороз ставав щораз дошкульніший.</p>\n','2013-06-03 11:28:40','http://oranta.org/index.php?option=com_content&view=article&id=1227:vogon-3-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Вогонь (3 червня)','uk',3288,4771,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4768,200,DSCN0013.JPG\" />Один юнак спитав наймудрішого старця, у чому криється таємниця щастя.  Мудрець порадив хлопців, аби той обійшов палац і за дві години  повернувся.</p>\n<p>«Прошу тільки, - промовив мудрець, вручаючи йому ложечку, у яку налив  дві краплі оливи, - під час мандрівки тримай ложечку так, щоб олива не  вилилась».</p>\n','2013-06-01 01:06:57','http://oranta.org/index.php?option=com_content&view=article&id=1225:tajemnycja-shchastja-1-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Таємниця щастя (1 червня)','uk',3286,4769,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4768,200,vebGIDE7vZc.jpg\" />Жив собі колись чоловік. Був він бідний, однак не журився - радів навіть  із дрібниць. А ще в нього була мрія. Навколишній світ здавався йому  сірим, жорстоким, безжальним, хворим. Чоловік страждав від цього. Якось  зранку, коли він минав майдан, залитий сонячним світлом, йому спало на  думку: «А якщо я розповідатиму людям історії? Може, мені вдасться  розбудити у них прагнення добра й любови? А це допоможе їм стати  щасливими».</p>\n','2013-06-02 01:19:11','http://oranta.org/index.php?option=com_content&view=article&id=1226:opovidach-2-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Оповідач (2 червня)','uk',3287,4770,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4776,200,ca238f1d47f6.jpg\" />Бабуся за руку з онуком увійшла до церкви.  Старенька відшукала поглядом  червону лампадку перед іконою, опустилась навколішки і стала молитися.</p>\n<p>Дитина водила очима від бабусі до лампадки і навспак. За якусь хвилину,  втрачаючи терпіння, хлопчик вимовив: «Бабусю, коли з’явиться зелене, ми  підемо, добре?»</p>\n','2013-06-03 23:45:28','http://oranta.org/index.php?option=com_content&view=article&id=1230:svitlofor-4-chervnja','P',NULL,'Світлофор (4 червня)','uk',3294,4777,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4776,200,ca238f1d47f6.jpg\" />Бабуся за руку з онуком увійшла до церкви.  Старенька відшукала поглядом  червону лампадку перед іконою, опустилась навколішки і стала молитися.</p>\n<p>Дитина водила очима від бабусі до лампадки і навспак. За якусь хвилину,  втрачаючи терпіння, хлопчик вимовив: «Бабусю, коли з’явиться зелене, ми  підемо, добре?»</p>\n','2013-06-03 23:45:28','http://oranta.org/index.php?option=com_content&view=article&id=1230:svitlofor-4-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Світлофор (4 червня)','uk',3311,4794,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4776,200,look.com.ua_66580.jpg\" />Дитину, що мешкала у долині, страшенно вабила гори, що виднілися ген-ген на обрії.</p>\n<p>Вкриті синюватою паволокою, спрямовані у небо, вони здавалися хлопчині  райським куточком, напротивагу до сірої землі, на якій мешкав.</p>\n','2013-06-04 23:50:53','http://oranta.org/index.php?option=com_content&view=article&id=1231:na-dolyni-5-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'На долині (5 червня)','uk',3296,4779,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4789,200,8UeeTliCDhs.jpg\" />Одного вечора, коли мати готувала вечерю, одинадцятилітній син прийшов до кухні з карткою в руці.</p>\n<p>З офіційним виглядом дитина подала картку мамі. Мати витерла об запуску руки і прочитала:</p>\n<p>«Очищення стежки від бур’янів – 1 гривня;</p>\n','2013-06-08 00:00:06','http://oranta.org/index.php?option=com_content&view=article&id=1238:rahunok-8-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Рахунок (8 червня)','uk',3313,4796,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4797,200,394029.jpg\" />Хлопчик щось малював.Вчитель сказав: \"Цікавий малюнок. Кого ти зобразив на ньому?\"</p>\n<p>\"Це – Бог.\"</p>\n<p>\"Але ж ніхто не знає, який є Бог.\"</p>\n<p>\"Коли я закінчу малюнок, то знатимуть усі.\"\n','2013-06-10 04:18:47','http://oranta.org/index.php?option=com_content&view=article&id=1240:maljunok-10-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Малюнок (10 червня)','uk',3317,4800,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4797,200,x_a030fb8e.jpg\" />Якось пустельник спостерігав у лісі за яструбом. Птах ніс до свого  гнізда шмат м\'яса. Він розділив його на багато маленьких кусників і став  годувати ними, крім своїх пташенят, ще й поранене вороненя.</p>\n<p>Пустельник спершу вельми здивувався, що яструб виявляє турботу про мале  вороненятко, а далі подумав:</p>\n','2013-06-09 06:15:14','http://oranta.org/index.php?option=com_content&view=article&id=1239:pryklad-9-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Приклад (9 червня)','uk',3315,4798,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4801,200,fa10471edb55ba416df253b4fb4b6159.jpg\" />На  морі лютували страшна буря. Сильні пориви холодного вітру, здавалося,  прошивали хвилями, що падали на пляж, немов удари ковальського молота. І  кожна хвиля на десятки метрів викидала раків, молюсків та інших  морських мешканців. Здавалося, немовби морське дно проорювали гігантські  сталеві лемеші.</p>\n','2013-06-10 22:45:56','http://oranta.org/index.php?option=com_content&view=article&id=1242:morski-zirky-11-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Морські зірки (11 червня)','uk',3320,4803,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4801,200,golubi_heart.jpg\" />Наступного дня Господь знову споглядав на те, що створив. Треба було дещо поправити.</p>\n<p>На березі ріки лежали чудові камінці: сірі, зелені, плямисті. А під  землею каміння було стиснене і пригнічене. Бог порухав те підземне  каміняччя – й у земній товщі з’явилися діяманти і смарагди. Мільйони  дорогоцінних скарбів, схованих у глибинах.</p>\n','2013-06-11 22:20:30','http://oranta.org/index.php?option=com_content&view=article&id=1243:vosmy-den-12-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Восьмий день (12 червня)','uk',3325,4808,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4806,200,mary-queen3.jpg\" />Однієї ночі мені приснився дивовижний сон. Я бачив довгу дорогу, що піднімалася догори, до неба, і губилася десь серед хмар.</p>\n<p>Люди ішли по ній босоніж. Гвіздки впивалися в тіло — багато хто мав закривавлені стопи.</p>\n<p>Проте ніхто не сходив зі шляху, всі бажали дійти до неба. Кожен крок завдавав страждань. Шлях був довгий і важкий.</p>\n','2013-06-15 00:32:39','http://oranta.org/index.php?option=com_content&view=article&id=1251:za-prykladom-mariji-15-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'За прикладом Марії (15 червня)','uk',3332,4815,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4818,200,126046_shutterstock_83011426.jpg\" />Цього листа знайшла медсестра у лікарні. Він лежав під подушкою одного хлопчини, який недавно закінчив життя на цьому ліжку.</p>\n<p>\"Дорога матусю, кілька днів я можу сидіти на ліжку лише пів години, а  решту дня безвладний. Серце не хоче добре працювати. Сьогодні зранку  професор сказав щось, що означало: \"Треба бути готовим\". До чого?  Молодому напевно важко умирати! Мушу бути готовий, бо на початку тижня,  може, вже не житиму - а я ще не готовий.</p>\n','2013-06-16 21:40:52','http://oranta.org/index.php?option=com_content&view=article&id=1256:shchstanni-lyst-17-cherven&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Останній лист (17 червень)','uk',3340,4823,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4818,200,4720.jpg\" />Понад берегом струмочка заснула маленька  мушка. Із глибини лісу почувся сильний глухий рик. Бідна мушка дуже  перелякалася. Могутній лев рикав з цілої сили, шукаючи собі вечері.</p>\n<p>Мушка загукала, обурена:</p>\n<p>- Гей ти! Може, замовкнеш? Що означає цей галас? Чом не даєш спати порядним громадянам? Яке ти маєш право тут бути?</p>\n','2013-06-15 21:34:02','http://oranta.org/index.php?option=com_content&view=article&id=1255:lev-i-mushka-16-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Лев і мушка (16 червня)','uk',3336,4819,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4828,200,small_2_s12.jpg\" />Нижче публікуємо працю о. Омеляна Ковча \"Чому наші від нас  утікають?\". Книга написана 1932 року та вийшла друком \"з одобрення  духовної власти\". Ця невеличка за обсягом брошура є плодом свого часу.  Блаженний о. Омелян Ковч розмірковує в ній над тим, чому греко-католики  покидали свою рідну Церкву та приєднувались до Латинської Церкви чи  інших віросповідань. Уважний читач знайде багато з цих аргументів  неактуальними сьогодні, проте деякі з тих проблем, на які вказував  автор, існують по сьогодні.</p>\n','2013-06-17 23:37:51','http://oranta.org/index.php?option=com_content&view=article&id=1260:o-emilian-kovch-chomu-nashi-vid-nas-utikajut&catid=30:hrystyjanske-vchennia-cat&Itemid=37','P',NULL,'о. Еміліан Ковч. Чому наші від нас утікають?','uk',3346,4829,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4830,200,12-strav-svyatvechir.jpg\" />Один із найяскравіших спогадів мого дитинства пов\'язаний із поверненням  батька з роботи о пів на сьому вечора. Я і мій брат чули, як батько -  для жарту - багато разів дзвонить, поки хтось із нас не відчинить йому  двері.</p>\n<p>Звичайно ми були в кухні, робили уроки, і коли чули знайомий дзвінок - видавали радісні вигуки.</p>\n','2013-06-18 00:35:03','http://oranta.org/index.php?option=com_content&view=article&id=1262:kozhnogo-vechora-18-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Кожного вечора (18 червня)','uk',3350,4833,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4834,200,compassion.jpg\" />- Якщо не дозволиш мені це зробити, я не зможу піти до школи! Мені буде соромно. Це дуже важливо, матусю!</p>\n<p>Елена вибухнула плачем. Це її найефективніша зброя.</p>\n<p>- Ох! Роби що хочеш…- пробуркотіла мати, кидаючи ложечку до мийки. – Будеш виглядати, як потвора. Тим гірше для тебе!</p>\n','2013-06-18 23:20:00','http://oranta.org/index.php?option=com_content&view=article&id=1263:rishennja-klasu-19-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Рішення класу (19 червня)','uk',3352,4835,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4839,200,x_3a3ecbbb.jpg\" />Одного дня у шпиталь до відділу інтенсивної терапії прийняли пацієнта на  ім\'я Петро. Це був високий сильний чоловік який мав рак кістки.</p>\n<p>Хоча  він дуже страждав, однак рідко скаржився. Дружина оточувала його  великою любов\'ю, усіляко старалася аби він мав якнайліпший догляд.  Кілька разів його уже піддавали хіміотерапії і його енергія, його  життєва сила вичерпалася.</p>\n','2013-06-20 10:38:55','http://oranta.org/index.php?option=com_content&view=article&id=1268:vse-garazd-20-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Все гаразд (20 червня)','uk',3357,4840,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4847,200,78890.jpg\" />Вони побрались п’ятдесят років тому. Одного дня сиділи на залізничній станції в очікуванні потяга. На лавці сиділо двійко закоханих.</p>\n<p>Старенькі мовчки спостерігали за молодою парою. Хлопець ніжно обіймав дівчину і пристрасно цілував її.</p>\n','2013-06-30 04:42:59','http://oranta.org/index.php?option=com_content&view=article&id=1279:zoloti-roky-30-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Золоті роки (30 червня)','uk',3400,4883,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4847,200,zalyshys.jpg\" />Якоїсь неділі на брамі церкви вивісили таке оголошення: «Аби всі могли прийти до церкви, у найближчий тиждень ми організуємо «особливу неділю без виправдань». У захристії розмістимо ліжка для всіх тих, котрі твердять, що неділя є єдиним днем тижня, коли вони можуть виспатися.</p>\n<p>Буде приготовано спеціальний сектор м’яких фотелів для тих, котрі вважають, що церковні лави незручні. Тим, у кого очі надто стомлені після ночі, проведеної перед телевізором, запропонуємо відповідні краплі для очей.</p>\n','2013-06-29 04:33:43','http://oranta.org/index.php?option=com_content&view=article&id=1278:bez-vypravdannja-29-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Без виправдання (29 червня)','uk',3398,4881,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,18.01-500.jpg\" />Якось один чоловік довідався, що його збирається відвідати Бог.</p>\n<p>“Мене? – занепокоївся чоловік. – У моєму домі?”</p>\n<p>Він став гарячково бігати по всіх кімнатах, піднявся по сходах,  вийшов аж на дах. Знову спустився і швидко увійшов до пивниці. Тепер,  коли до нього мав прийти Бог, він подивився на свій будинок иншими  очима.</p>\n','2013-06-21 23:21:07','http://oranta.org/index.php?option=com_content&view=article&id=1271:maje-prybuty-bog-22-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Має прибути Бог! (22 червня)','uk',3368,4851,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,208475_362778407171811_2057632751_n.jpg\" />Жила колись одна дівчинка на ім’я Цецилія її батьки дуже багато  працювали. Це була гарна, щаслива сім’я. Лише одного бракувало, але це  Цецилія завважила щойно у певний момент. Одного разу, коли Цецилія мала 9  років, вона вперше ночувала в домі своєї подруги Аделі. Коли прийшла  пора на сон, мама Аделі відгорнула ковдру і кожну з них поцілувала  надобраніч.</p>\n','2013-06-23 23:37:57','http://oranta.org/index.php?option=com_content&view=article&id=1273:de-mi-pocilunok-24-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Де мій поцілунок? (24 червня)','uk',3375,4858,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,6775.jpg\" />На парохію постукав брудний і бідно вдягнений хлопець. Був це  постачальник наркотиків, якого розшукувала поліція і банда жорстоких  конкурентів. Він не знав, де сховатися.</p>\n<p>Столітній священик, який  відчинив двері, був заклопотаний цим візитом.</p>\n','2013-06-22 23:29:17','http://oranta.org/index.php?option=com_content&view=article&id=1272:zakon-23-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Закон (23 червня)','uk',3374,4857,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,7495_10151910057887355_173454080_n.jpg\" />Жив колись дуже сумний король, який мав слугу – чоловіка напрочуд щасливого. У нього зажди був щасливий усміх на устах.</p>\n<p>- Слуго, - спитав якось король, - у чому таємниця твоєї радості?</p>\n<p>- Нема в мене жодної таємниці. Просто не маю причини бути сумним. Я щасливий, тому що можу тобі служити. Мешкаю з дружиною у будинку, який мені призначили. Маю їжу і одяг, а час від часу і кілька монет на пиво.</p>\n','2013-06-24 23:51:56','http://oranta.org/index.php?option=com_content&view=article&id=1274:klub-devjanosta-devjaty-25-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Клуб дев’яноста дев’яти (25 червня)','uk',3382,4865,0,NULL),
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4848,200,NB3DzVyUdQM.jpg\" />Поблизу джерела мешкало багато пустельників. Кожен із них збудував собі свою хатину і провадив усі дні у глибокій тиші, у роздумах і молитвах. Кожен у зосереджені взивав до Божої присутності.</p>\n<p>Бог хотів їх навідати, проте Йому не вдалося знайти дороги. Посеред пустелі бачив лише окремі точки, на відстані одна від одної.</p>\n','2013-06-25 22:03:04','http://oranta.org/index.php?option=com_content&view=article&id=1275:pustelnyky-26-chervnja&catid=1:mystecka-storinka-cat&Itemid=50','P',NULL,'Пустельники (26 червня)','uk',3383,4866,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,1,'<p><img style=\"margin: 10px; float: right;\" src=\"http://askoldova-mohyla.org/uk/image,4851,200,pope1.jpg\" />У 2000 році в часі святкування Світового дня молоді у Римі мені пощастило побувати на аудієнції в Папи Римського Івана Павла ІІ у літній папській резиденції в Кастель-Гандольфо. Це була зустріч із польською мо­лоддю, із месою в латинському обряді, з привітальним словом Папи і короткою можливістю зробити фото на пам’ять. Коли черга дійшла до нашої маленької груп­ки, Папа відразу розпізнав нашу мову і у відповідь теж звернувся українською, — це було надзвичайно.</p>\n','2013-06-22 20:37:18','http://oranta.org/index.php?option=com_content&view=article&id=1282:12-rokiv-tomu-pastorsky-vizyt-papy-ivana-pavla-ii-na-askoldovu-mogylu&catid=25:parafija-cat&Itemid=27','P',NULL,'12 років тому - ПАСТОРСЬКИЙ ВІЗИТ ПАПИ ІВАНА ПАВЛА ІІ НА АСКОЛЬДОВУ МОГИЛУ','uk',3371,4854,0,NULL);
