INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<ol>\r\n<li style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передала до військового шпиталю для бійців АТО</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; светри теплі, футболки &ndash; 37 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нові теплі шкарпетки &ndash; 1 пакунок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; кросівки &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нова постільна білизна для хірургічного відділення &ndash; 3 комплекти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ліки (передані з Майдану пожертви)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, пішви для подушок</span></p>','2015-01-10 20:51:32',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами, зокрема п.Людмилою з дочкою Марією та внучкою Анною: одяг чоловічий, жіночий та дитячий, взуття, постільна та кухонна білизна, тканини, іграшки дитячі та інші речі домашнього вжитку, розподілила їх таким чином:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділила потребуючим мирянам.</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Передала до військового шпиталю для бійців АТО</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; светри теплі, футболки &ndash; 37 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нові теплі шкарпетки &ndash; 1 пакунок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; кросівки &ndash; 2 пари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; нова постільна білизна для хірургічного відділення &ndash; 3 комплекти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ліки (передані з Майдану пожертви)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, пішви для подушок</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Уділила переселенцям з Донецької та Луганської обл.</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; ковдра тепла &ndash; 1 шт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; рушники, постільна білизна</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл.:</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; одяг чоловічий, жіночий, дитячий &ndash; 24 мішки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; &nbsp;взуття &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">&mdash;&nbsp; іграшки дитячі &ndash; 1 мішок</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Робота виконана силами волонтерів: п.Світлани, п.Михайла, п.Анни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Висловлюємо щиру подяку жертводавцям і прикликаємо Боже благословення на їх родини.</span></p>','ЗВІТ №13 про розподілення благодійних пожертвувань парафіян церкви св.Миколая на Аскольдовій могилі  (24.09.2014 р.)','uk',4851,6334,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p align=\"center\"><strong>Місія владики</strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Зречення Патріарха Любомира Гузара з уряду Верховного  Архиєпископа Української Греко-Католицької Церкви стало подією, яка  вийшла далеко за рамки життя самої Церкви, а відтак стала резонансним  явищем у суспільно-політичну житті України. Про &ldquo;відставку&rdquo; Гузара мали  за обов\'язок висловитися владики найбільших християнських церков, чільні  діячі української влади і опозиції, відомі громадські діячі. Безумовно,  все це свідчить про беззаперечний авторитет як Патріарха Любомира так і  Церкви, провід якої він донедавна очолював.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Власне, добровільне зречення Блаженнішого Любомира виявило  не лише&nbsp; високий статус УГКЦ, а й зафіксувало шляхи, якими Церква  відтепер прямуватиме уже з новим очільником.</p>','2011-02-13 20:24:26',NULL,'P','<p align=\"center\"><strong>Місія владики</strong></p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Зречення Патріарха Любомира Гузара з уряду Верховного Архиєпископа Української Греко-Католицької Церкви стало подією, яка вийшла далеко за рамки життя самої Церкви, а відтак стала резонансним явищем у суспільно-політичну житті України. Про &ldquo;відставку&rdquo; Гузара мали за обов\'язок висловитися владики найбільших християнських церков, чільні діячі української влади і опозиції, відомі громадські діячі. Безумовно, все це свідчить про беззаперечний авторитет як Патріарха Любомира так і Церкви, провід якої він донедавна очолював.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Власне, добровільне зречення Блаженнішого Любомира виявило не лише&nbsp; високий статус УГКЦ, а й зафіксувало шляхи, якими Церква відтепер прямуватиме уже з новим очільником.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Парадокс полягає у тому, що усі досягнення владики неминуче стануть викликами для його наступника і Церкви загалом. Передусім заслуговує на увагу сам факт &ldquo;відставки&rdquo;. Кардиналу Гузару вдалося здійснити акт передачі владних повноважень з неабиякою гідністю та благородством, і в час, коли УГКЦ утвердилася в суспільстві як впливовий чинник духовного, культурного, церковно-релігійного та суспільно-політичного життя країни. Кожен наступний глава Церкви буде змушений пам\'ятати про це, щоб, принаймні, зберегти і примножити той авторитет, що його своєю працею заслужив Блаженніший Любомир.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; У цьому контексті не можна оминути проблем, які успадкує новий намісник церковного престолу. Очевидно, що з висоти авторитету Гузара їх розв\'язання може видатися простішим, а, можливо, навпаки &mdash; складнішим, адже допомога Блаженнішого тепер буде виключно у формі добрих порад.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Передусім ключовим залишається питання набуття Церквою патріаршого статусу. У <em>2004 році в Пастирському посланні &ldquo;</em>Про утвердження патріаршого устрою Української Греко-Католицької Церкви&rdquo; зазначалося, &ldquo;ми поділяємо переконаність Патріарха Йосифа, що завершення нашої Церкви патріаршим вінцем є &ldquo;овочем дозрілої свідомості&rdquo; у її Божому Народі. І ми й далі утверджуватимемо в житті нашої Церкви належні патріархальні структури та патріархальну свідомість&rdquo;. Водночас констатувалося, що &ldquo;без благословення Святішого Отця і без належного в нас смирення наш патріархат міг би стати раною в тілі Вселенської Церкви, і саме це змушує нас бути обачними&rdquo;. Іншими словами, остаточна розв\'язка цієї проблеми і надалі перебуває у руках Папи Римського. Проте, це аж ніяк не означає, що УГКЦ має перейти на позицію очікування. Діяльність Блаженнішого Любомира засвідчує, що на шляху до Патріархату необхідно робити дієві кроки в Україні. Так, саме з його благословення було утворено Патріаршу Курію - важливий інститут з допомогою якого Церква&nbsp; сьогодні має можливість здійснювати свою місію на новому, вищому, &ldquo;патріархальному&rdquo; рівні.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Рано чи пізно на шляху до власного Патріархату УГКЦ доведеться з\'ясувати взаємини із Мукачівською греко-католицькою єпархією, яка перебуває у прямому зв\'язку із Апостольською Столицею, а отже не визнає зверхності Верховного Архієпископа УГКЦ. Зрозуміло, що факт існування двох греко-католицьких церков &ldquo;свого права&rdquo; на території однієї держави, аж ніяк не сприяє набуттю УГКЦ патріаршої гідності.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свого часу Конгрегація східних церков підготувала проект Декрету про утворення Ужгородської митрополії у складі Верховної Архієпископської Церкви, який мав покласти край роздробленості греко-католиків України. Однак до цього часу цей документ не набув чинності. А отже розв\'язання проблеми &mdash; попереду.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо питання набуття Патріархату і взаємин із Мукачівською єпархією можна вважати &ldquo;зовнішніми&rdquo;, тобто такими, які слід ставити перед Ватиканом, то &ldquo;внутрішні&rdquo; проблеми доведеться розв\'язувати самотужки.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Найнеприємніша з них, це необхідність нейтралізації впливів &ldquo;підгорецьких монахів&rdquo;, або ж так званої &ldquo;групи Догнала&rdquo;. Події на Тернопільщині, коли авантюристи, що поставили себе поза Вселенською Церквою, спробували у рейдерський спосіб захопити храм Преображення Господнього у Чорткові показали, що УГКЦ більше не може легковажити цією проблемою, а відтак має її невідкладно вирішити. Як і коли це буде зроблено? Відповідь на це питання значною мірою залежатиме від нового предстоятеля Церкви.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Зволікати ж з цим не можна ще й тому, що із &ldquo;внутрішньої&rdquo; проблема може стати &ldquo;зовнішньою&rdquo;. Пригадаю коментар ректора Дрогобицької семінарії УГКЦ о. Мирона Бендика щодо подій у Чорткові: &ldquo;Цю технологію також використовували в Галичині в 90-і роки ХХ століття, розпаливши боротьбу різних &ldquo;конфесій&rdquo; за храми і церковне майно&rdquo;. Але &laquo;не виключено, що після завершення &ldquo;реформи&rdquo; отці дадуть команду своїм прихильникам возз&rsquo;єднатися з Російською Православною Церквою і певним чином відтворити впливи цієї Церкви в Галичині. Адже після провалу і втрати Галичини було б наївно думати, що РПЦ змириться з цією поразкою. Тому для РПЦ і певних політичних кіл у Росії вигідна &ldquo;реформа&rdquo; підгорецьких отців, наслідки якої давали б РПЦ хоча б якісь шанси відновлення її присутності в регіоні з високою релігійністю населення&rdquo;.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Майбутні стосунки УГКЦ , зокрема її нового лідера із іншими церквами Київської Традиції &mdash; окрема тема. У Посланні &laquo;Один Божий народ у краї на Київських горах&raquo;, виголошеному з приводу повернення осідку глави УГКЦ до Києва&nbsp; Балаженніший Любомир зазначив:&laquo;Кожна конфесійна гілка Київської Церкви, історично близька до одного з християнських центрів, не мусить втрачати свої освячені часом конфесійні зв\'язки. Сьогодні можна переконано стверджувати, що вимога розірвати ті чи ті зв\'язки, яку так часто озвучували в історії України, довела свою неспроможність. Заміна юрисдикційної підлеглості на сопричасне сестринство дала б можливість не лише зберегти цінні аспекти дотеперішніх зв\'язків, а й збагатити ними спільну скарбницю Київської Церкви&raquo;. Інакше кажучи, він запропонував шлях християнської єдності, який не руйнує благочестя кожної із церков, а враховує реальний історичний досвід їх співжиття.</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Наскільки аргументи та ідеї кардинала Гузара виявляться актуальними покаже час і значною мірою діяльність його наступника.</p>\r\n<p align=\"right\"><strong>Богдан Червак,</strong></p>\r\n<p align=\"right\">парафіянин Церкви Святого Миколая Чудотворця</p>\r\n<p align=\"right\">на Аскольдовій Могилі</p>\r\n<p align=\"right\">&nbsp;</p>','Місія владики','uk',2312,3795,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p align=\"center\"><strong>Страсний тиждень 2010 рік</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Великий понеділок</strong> &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;8:30 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Великий вівторок</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;18:00 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Велика середа</strong></p>','2010-03-30 15:14:16',NULL,'P','<p align=\"center\"><strong>Страсний тиждень 2010 рік</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Великий понеділок</strong> &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;8:30 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Великий вівторок</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;18:00 Літургія Передосвячених Дарів, Парастас</p>\r\n<p><strong>Велика середа</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;<strong>Великий понеділок о 17:00 розпочнеться Чин Соборування, </strong></p>\r\n<p><strong>В&nbsp; церкві буде постійна нагода до сповіді.</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>&nbsp;</strong><strong>1 квітня. Великий четвер</strong>, Страсті спомин тайної вечері</p>\r\n<p>08:30 Вечірня з Літургією Василія Великого</p>\r\n<p>18:00 Страсна утреня</p>\r\n<p>&nbsp;</p>\r\n<p><strong>2 квітня. Велика п&rsquo;ятниця</strong>,</p>\r\n<p>08:15 Великі царські часи</p>\r\n<p>17:00 Вечірня з виставленням Плащаниці</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Єрусалимська утреня</p>\r\n<p>&nbsp;<strong><span style=\"text-decoration: underline;\">Церква буде відкрита цілу ніч, запрошуємо на молитву біля гробу Господнього</span></strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>3 квітня. Велика субота</strong>,</p>\r\n<p>08:15 Вечірня з Літургією Василя Великого</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p><strong>Освячена пасок &ndash; 18:00, 19:00, 20:00, 21:00, 22:00</strong></p>\r\n<p>23:30 Надгробне</p>\r\n<p><strong>4 квітня Пасха Воскресіння Христове</strong></p>\r\n<p>00:00 Пасхальна Утрення зі Службою Божою</p>\r\n<p>07:30 Божественна Літургія, освячення пасок</p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p><strong>Світлий тиждень</strong></p>\r\n<p>&nbsp;</p>\r\n<p>На кожний день</p>\r\n<p>&nbsp;</p>\r\n<p><strong>08:15 Воскресна Утрення,&nbsp; 09:00 Служба Божа</strong></p>\r\n<p><strong>18:00 Вечірня, Служба Божа</strong></p>','РОЗКЛАД на Страсний тиждень','uk',2020,3503,0,'Віталіна Онишкевич'),
(1,NULL,'<p align=\"right\"><img style=\"float: right;\" src=\"image,3350,200,tereza_51.jpg\" alt=\"\" width=\"112\" height=\"178\" /></p>\r\n<p align=\"right\">\"В каждом человеке я вижу Христа.</p>\r\n<p align=\"right\">А поскольку Христос навсегда один,</p>\r\n<p align=\"right\">то для меня в данный конкретный момент</p>\r\n<p align=\"right\">это тот, кто стоит передо мной\"&nbsp;</p>\r\n<p align=\"right\">&nbsp;Мать Тереза (1910-1997)</p>','2009-11-02 13:08:36',NULL,'P','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img style=\"float: right;\" src=\"image,3350,200,tereza_51.jpg\" alt=\"\" width=\"100\" height=\"160\" /></p>\r\n<p align=\"right\">\"В каждом человеке я вижу Христа.</p>\r\n<p align=\"right\">А поскольку Христос навсегда один,</p>\r\n<p align=\"right\">то для меня в данный конкретный момент</p>\r\n<p align=\"right\">это тот, кто стоит передо мной\"&nbsp;</p>\r\n<p align=\"right\">&nbsp;Мать Тереза (1910-1997)</p>\r\n<p>&nbsp;</p>\r\n<p>-Її важали святою ще при житті. Їй випала нелегка і радісна доля нести людям &ndash; в який раз! &ndash; благу вістку, що Бог є любов і що сенс життя кожного смертного тільки в тому, щоб любити і бути коханим. В ХХ столітті вона стала не просто символом милосердя, але разом зі своїми послушницями являла реальну силу, з якою не можна було не рахуватися. Нею захоплювалися, перед нею схиляли свої голови, її обожнювали.</p>\r\n<p>&nbsp;</p>\r\n<p><em>ЗАПОВІТИ&nbsp; МАТЕРІ&nbsp; ТЕРЕЗИ:</em></p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Люди бувають нерозумними, нелогічними і егоїстичними &ndash; і все ж люби їх!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коли ти робиш добро, то люди звинувачуватимуть тебе у затаєній особистій користі і самолюбстві &ndash; і все ж виявляй доброту!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти досягнув успіху, то може з&rsquo;явитися багато фальшивих друзів і справжніх ворогів &ndash; і все ж досягай успіху!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти чесний і відвертий, то люди обманюватимуть тебе &ndash; і все ж будь чесним і відвертим!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Те, що ти будував роками, може бути зруйнованим в одну мить &ndash; і все ж будуй!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Люди потребують допомоги, але вони згодом дорікатимуть тобі за неї &ndash; і все ж допомагай людям!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Якщо ти досягнув безмежного щастя, тобі будуть заздрити &ndash; і все ж будь щасливим!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Добро, яке ти зробив сьогодні, люди забудуть завтра &ndash; і все ж твори добро!</p>\r\n<p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ділись із людьми кращим, що в тебе є, і цього ніколи не буде достатньо &ndash; і все ж ділись найкращим!</p>\r\n<p>Зрештою, ти сам переконаєшся, що все це між тобою і Богом, і що це ніколи не було між ними і тобою!</p>\r\n<p>&nbsp;</p>\r\n<p><em><img style=\"float: left;\" src=\"image,3350,200,431px-Mother_Teresa0.jpg\" alt=\"\" width=\"143\" height=\"200\" />Коротка біографічна довідка:</em></p>\r\n<p>Мати Тереза (1910-1997), католицька черниця, фундаторка Ордена милосердя, чернечої конгрегації, що займається служінням бідним і хворим. У 1979 матір Тереза була визнана гідною Нобелівської премії світу.</p>\r\n<p>Мати Тереза (Агнес Гонджа Бояджиу) народилася 27 серпня 1910 у Скоп&rsquo;є (сучасна Македонія, у ті роки - Османська імперія) в албанській родині.</p>\r\n<p>У віці 12 років дівчинка вирішила стати черницею, у 18 вступила в конгрегацію лоретських сестер, ірландський католицький орден, що здійснював місіонерську діяльність в Індії.</p>\r\n<p>Спочатку вона працювала в школі для дівчинок у Дарджилингі, місті до півночі від Калькутти, протягом 20 років викладала географію в школі св. Марії неподалік від Калькутти, кілька років була директором цієї школи.</p>\r\n<p>У віці 36 років відчула заклик \"залишити монастир і служити бідним, живучи серед них\".</p>\r\n<p>У 1948 заснувала Орден милосердя і присвятила себе служінню знедоленим <img style=\"float: right;\" src=\"image,3350,200,tereza_81.jpg\" alt=\"\" width=\"129\" height=\"200\" />жителям калькуттських нетр.</p>\r\n<p>У рік, коли її конгрегація була офіційно визнана церквою, прийняла індійське громадянство (1950).</p>\r\n<p>До моменту, коли мати Тереза одержала Нобелівську премію, сестри ордена працювали вже в багатьох країнах.</p>\r\n<p>Мати Тереза один раз сказала про своє служіння, що воно засновано на її вірі в Христа. \"Через те, що ми не бачимо Христа, ми не можемо виразити йому нашу любов, але ближніх завжди можемо бачити і стосовно них надходити так, як надходили б стосовно Христу, якби бачили його\".</p>\r\n<p>У 1965 році її діяльність визнав Ватикан. А в 1979 році калькуттська черниця була удостоєна Нобелівської премії миру. Мати Тереза померла в Калькутті в березні 1997 році. Через два роки після її смерті з ініціативи Папи Іоанна Павла II Ватикан почав процедуру її канонізації.</p>\r\n<p>&nbsp;<img src=\"image,3350,200,tereza-062220080325047JN1.jpg\" alt=\"\" width=\"200\" height=\"150\" />Римо-католицька церква склала найвищі почесті померлій 5 років тому матері Терезі, оголосивши її блаженною.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','Заповіти Матері Терези','uk',1867,3350,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; <span style=\"font-size: medium;\">&nbsp;&nbsp; <span style=\"font-size: large;\"><img style=\"float: right;\" src=\"image,1452,200,CRW_6474.jpg\" alt=\"\" width=\"107\" height=\"162\" />СКАЖИ СОБІ: </span></span></span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні спіралям</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні таблеткам</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні презервативам</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні стерилізації</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні штучному заплідненню</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: large;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні гріху</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-align: justify; text-indent: -31.5pt; line-height: 130%;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; <br /></span></span></span></p>','2008-10-10 14:01:13',NULL,'P','<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні спіралям</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні таблеткам</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні презервативам<br /></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні стерилізації</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: 13.5pt; text-indent: -31.5pt; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; Ні штучному заплідненню</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-left: -0.25in; line-height: 130%; text-align: justify;\"><span lang=\"UK\">&nbsp;&nbsp;&nbsp; &nbsp; Ні гріху</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\"><img style=\"float: right;\" src=\"image,1452,200,CRW_6474.jpg\" alt=\"\" />Спіраль убиває дітей! При її вживанні зачаття відбувається, але дитина найчастіше вмирає</span><span lang=\"UK\"> </span><span lang=\"UK\">в перші дні, тому що спіраль не дозволяє їй імплантуватися (закріпитися) в матці. Якщо відбувається затримка менструації, то це значить, що дитина все таки вижила. Треба зберегти їй життя. </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Спіраль перетворює здорову жінку в хвору (запалення і нагноєння в матці, яйцеводах, черевній порожнині, кровотечі, болі, недокрів\'я...).</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Під час прийняття усіх видів таблеток, так званих &ldquo;протизаплідних&rdquo;, все ж не рідко відбувається овуляція і зачаття. У такому випадку дитина найчастіше умирає від голоду, тому що таблетки сушать залози і зменшують кількість їжі (глікогену) у слизовій оболонці матки. </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Затримка менструації говорить про те, що дитина все ж вижила. Аборт не дозволено робити й у цьому випадку. </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Жінки, що приймають таблетки, частіше інших хворіють на рак грудей, шкіри і матки, схильні до інсультів та інфарктів, запалення вен з тромбами, що можуть викликати емболію, виникає ушкодження печінки і зорового нерва, слабшає імунітет і т.д. Таблетки не є вирішенням проблеми нерегулярного менструального циклу. </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Зачаття відбувається і під час прийняття більшості інших так званих \"контрацептивних\" засобів, але і ці речовини автоматично вбивають дитину в перші дні. Ісус сказав: \"Істинно кажу вам: усе, що ви зробили одному з моїх найменших братів, ви мені зробили \" (Мф. 25, 40). А наш найменший брат чи сестра - це та маленька дитинка, щойно зачата. Її жодним чином не слід убивати. Треба довіряти Господеві. Ісус сказав дослівно так: &laquo;Не піклуйтеся і не говоріть: &laquo;що нам їсти?&raquo; чи: &laquo;що пити?&raquo; чи: &laquo;у що одягтися?&raquo;, тому що всього цього шукають язичники, і тому що Батько ваш Небесний знає, що ви маєте нестаток у всьому цьому. Шукайте ж насамперед Царство Боже і правди Його, і це все прикладеться вам!&raquo; (Мф. 6, З1-ЗЗ). </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\"><img style=\"float: right;\" src=\"image,1452,200,IMG_4103.jpg\" alt=\"\" />Вселенська Церква є учителькою віри і моралі. Вона постійно вчить нерозривності зв\'язку, що існує між подружнім актом і зачаттям, під час якого Бог дає новій людині душу. Більш докладні пояснення можна знайти в документах Католицької Церкви: \"Casti Conubii\", \"Humanae Vitae\" і \"Donum Vitae\". Так, смертним гріхом є не тільки убивство ненародженої дитини, але також і контрацепція, стерилізація і штучне запліднення як, наприклад, запліднення в пробірці та інсемінація. Діти, зачаті в пробірці, дуже рідко виживають. Більшість з них убивають, а багатьох, таких маленьких і ще живих дітей, тримають замороженими в холодильниках. </span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Не дозволено проводити такі процедури як, наприклад, ранній амніоцентез, мета якого вистежування й убивство хворих! Ніхто ще не став щасливим, убивши людину, але можна стати щасливим, постійно рятуючи життя хворого. Гріх ніколи не був і не буде вирішенням проблем. Гріх ніколи не приводив до щастя.</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Святіший Отець Пій ХІ у Енцикліці &ldquo;Casti Conubii&rdquo; (Чистого подружжя) проти контрацепції і стерилізації написав: 56. &ldquo;Оскільки деякі, відкрито відступаючи від незмінного і переданого від початку християнського вчення, вважають, що у наші дні потрібно урочисто проголосити інше вчення щодо цього питання, то Католицька Церква, якій сам Бог доручив обов&rsquo;язок навчати і захищати чесність та чистоту звичаїв, міцно стоячи посеред моральної руїни, яка її оточує, з метою збереження чистоти шлюбного зв&rsquo;язку від цієї огидної плями, піднімає свій голос на знак Божого післанництва та Нашими устами знову проголошує: \"Кожне використання шлюбу, при якому за наміренням людини подружній акт позбавлений своєї природної здатності зачати життя, порушує Божий закон і ті, хто так чинять, опоганюють себе тяжким гріхом</span>.</li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\"><img style=\"float: right;\" src=\"image,1452,200,CRW_6518.jpg\" alt=\"\" />Тому на підставі Нашого найвищого авторитету та дорученого нам обов&rsquo;язку дбати про спасіння усіх душ застерігаємо усіх священиків-сповідників та усіх душ пастирів, щоби не дозволяли порученим їм вірним грішити проти такого вагомого Божого Закону; навіть більше, щоби оберігали себе самих від таких згубних вчень, в жодному разі не потураючи їм. А якщо би хтось із сповідників чи душ пастирів, нехай Бог цього не допустить, впав сам або ж увів у подібні помилки поручених йому вірних, потвердив їх відкрито чи грішною мовчанкою, нехай знає, що муситиме скласти звіт перед Богом &ndash; Найвищим Суддею про зраду Його священної довіри та застосує до себе слова Христові: &ldquo;...сліпі проводарі сліпих! Коли ж сліпий веде сліпого, обидва впадуть у яму&rdquo; (Мт 15,4).</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Ті, хто робили, рекламували і поширювали презервативи відповідальні не тільки за участь у здійсненні гріхів, серед яких найчастіші - блуд, перелюбство і контрацепція, але вони також відповідальні і за те, що давали хибну гарантію, що інфікування не відбудеться.</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Насправді доведено, що не зважаючи на використання презерватива все ж таки переносяться такі захворювання, як СНІД, герпес генітальний, гепатит, сифіліс, і відбуваються зараження хламідією, людським папілома вірусом (HPV) що спричинює рак матки, вагінальний трихомоніаз і їм подібні. Папський консиліум про родину в документі &ldquo;Істина і значення людської сексуальності&rdquo; говорить: &ldquo;Батьки теж повинні відмовитися від пропаганди так званого \"safe sex\" (надійного сексу) чи \"safer sex\" (більш надійного сексу), небезпечної й аморальної політики, основаної на помилковій теорії, що презерватив може забезпечити надійний захист проти СНІДу.&rdquo;</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">У випадку виправданих причин уникнення зачаття, дозволяється стриманість у плідні (фертильні) дні. Сьогодні плідні і неплідні періоди можна дуже просто і точно визначити, навіть при неправильних чи нерегулярних циклах, за допомогою природних методів: Біллінгса чи симптотермічного. Не дозволяється жити сексуальним життям до чи поза таким шлюбом, який благословить Бог як нерозривний до смерті одного з подружжя.</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Давайте прислухатися до голосу Господа, щоб зрозуміти, що ми повинні ще робити для захисту моральних цінностей і життя, які перебувають під загрозою. Якщо Ви зараз хочете приєднатися до молитви і посту в цьому намірі, будь ласка, зверніться за адресою:<span>&nbsp; </span>Церква Святого Миколая на Аскольдовій Могилі, а/с 107, Товариство \"Разом за життя\", КИЇВ-90, 02090<span>&nbsp; </span>Тел/факс /044/ 570-32-90 E-mail: <a href=\"mailto:oranta-gazeta@ukr.net\">oranta-gazeta@ukr.net</a></span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span lang=\"UK\">Доктор медицини Антун Лісец. Antun Lisec, Post Box 2, Vetovo 34335, Croatia - Хорватія; E-mail: antun.lisec@po.t-com.hr</span></li>\r\n</ul>\r\n<p>фото: Любов Михайлюк</p>','Дитина дарунок Божий','uk',472,1452,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p class=\"MsoNormal\" style=\"text-align: center;\"><span style=\"color: #000000;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Сім святих таїнств</strong></span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">1. Хрещення</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">2.  	Миропомазання</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">3.  	Покаяння-сповідь</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">4. Пресвята  	Євхаристія &ndash; тіло і кров Господа нашого Ісуса Христа під видами хліба й вина</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">5. Подружжя</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">6.  	Священство</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"color: black;\">7. 	<span class=\"SpellE\">Єлеопомазання</span></span></span></span></p>','2009-02-21 18:15:38',NULL,'P','','','uk',1161,2135,0,'Любов Михайлюк'),
(1,NULL,'<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"UK\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,2574,200,IMG_1387.JPG\" alt=\"\" />У парафії св. Миколая Чудотворця на Аскольдовій Могилі вже стало доброю традицією у день празника проводити фестиваль духовної пісні &laquo;Аскольдів глас&raquo;. Традиція ця хоч і молода але щоразу збирається все більше виконавців. Цього року 24 травня (святкування празника Теплого Миколая було перенесено) після Архієрейської служби, яку відправив владика Богдан Джюрах, пройшов ІІІ фестиваль &laquo;Аскольдів Глас&raquo;. Ось його хронологія у світлинах:</span></span></span></p>','2009-05-26 18:47:30',NULL,'P','<p>&nbsp;</p>\r\n<p>#topbar_informer{position:fixed;left:0px;top:10px;width:800px;border:0px solid black;padding:0px;z-index:100;color:#000000;}</p>','«Аскольдів Глас» у празник Теплого Миколая','uk',1518,2574,0,'Любов Михайлюк'),
(1,NULL,'<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд з русинів першим охрестився,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> З вірою святою звершення творив.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князеві Аскольду Дух Святий явився,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князь Аскольд із Богом в серці вік прожив.</span></p>','2013-10-08 00:00:46',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Новою умовою фестивалю-конкурсу з 2009 р. стало виконання одного твору, присвяченого постаті князя Аскольда. Так, постала низка пісенних творів на музику композиторів Сергія Інгленда, Дмитра Гречина, Ірини Батюк, Ірини Кириліної, Наталі Сиротич, Ольги Засадної, Мар&rsquo;яни Головко, Людмили Бутуханової, Катерини Кучерявої, Зорини Валіхновської, Нелі Франчук, Ірини Голоско та ін., а також поетів Нелі Шейко-Медведевої, Зої Дикої, Нестора Мартинця, Олени Кулінської, Миколи Голоска, Тетяни Озкан, Руслани Лоцман, Люби Кокоєвої Анастасії Попеско та ін., який висловлюємо глибоку подяку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не давнина, а вічність</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. О.Кулінської, муз. С.Острової (О.Сєрової)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як вабить спокоєм це місце!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи Дух Святий його обрав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прикрасив сяйвом променистим,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В кленовім золоті сховав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Забудеться моя турбота,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I радощi, спокуса й труд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I стане свiтлою скорбота...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, гарно в тебе тут!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не давнина &ndash; а вiчнiсть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не затишок &ndash; а рай.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть вiки, як буднi звичнi,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть думки &ndash; за небокрай.</span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Подяка</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл.Н.Шейко-Медведєвої, муз. С.Острової</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підкрався вечір й світлий ліг туман</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Київ і круті Дніпрові схили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Немовби душі перших християн</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зійшлися на Аскольдовій могилі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб вшанувати в церкві Миколая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Того, хто показав їм шлях до раю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прозрів ти першим й першим забажав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб люд здобув собі Христову віру.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти першим тут на схилах цих сказав:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Людино, не твори собі кумира!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Разом зі світом &ndash; думкою і словом &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вклонися духу Правди і Любови!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підкрався вечір й світлий ліг туман</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Київ і круті Дніпрові схили.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Немовби душі русів-християн</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зійшлися на Аскольдовій могилі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб вшанувати в церкві Миколая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Того, хто й нині кличе люд до раю!</span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Святиня Аскольда</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. І.Рідоша, муз. В.Гриньківа</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. На славетних на київських кручах,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Над Дніпром, у буянні гаїв,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Де пташок лине спів милозвучно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд свій спочинок зустрів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На цьому місці, всім нам дорогому,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Побожний люд церковцю нам воздвиг.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В короні хрест!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ім&rsquo;ям в честь Миколая,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Святиню Божу він тоді нарік.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Уже часу минуло чимало,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пройшли роки, століття спливли,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Але церкву й Аскольда могилу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Історичні вітри не змели.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. І до нині у храмі святому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Божа пісня, молитва луна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Буде жити повік Україна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Доки віра у Бога жива.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Княже Аскольде</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл.Т.Чаплик, муз. П.Сидька</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми знаєм на Русі жили колись князі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вони в історії свій залишили слід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серед них Аскольд, що мріяв, як і всі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вкраїну й Київ берегти від бід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княже Аскольде, тебе пам&rsquo;ятають</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Київські схили і кручі Дніпра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нас із тобою віки розділяють,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наше єднання &ndash; віра свята.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Що думав він, чим жив,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">з ким був, кого любив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І що хотів залишити нащадкам?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про це мовчать книжки і не дізнатись нам,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це вічна нерозгадана загадка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Часом буває так, що чується вночі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На берегах Дніпра зітхання й голоси.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чия ж могила тут, хто з древніх тут спочив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І мудрістю віків усіх благословив?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;<strong>Аскольдова пам\'ять</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">&nbsp;<strong>Сл. А.Корнєва, муз. В.Рибалко</strong></span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від життя до смерті недовго йти,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вмерти, як і жити, треба вміти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І смертю смерть поправ, Спаситель повстав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда-князя до себе кликав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За що ти, княже, свій живіт поклав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І звідки ти, Аскольде, хто тебе хрестив?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Дніпра водою хто тебе кропив?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І себе вітав, і віру дарував,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До свого серця щиро прикладав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Господь кликав, то Він казав, Аскольд,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То Господь кликав, Аскольд,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То він казав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За Дніпрові кручі, у слов&rsquo;янський світ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лине світле слово те, що в нас бринить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Де стояв Андрій, там живемо ми.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про це пам&rsquo;ятаймо, навіки вітаймо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Навіки віків, навіки віків, навіки віків.</span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Аскольде, Аскольде!</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. Т.Чаплик, муз. К.Кучерявої</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прекрасна наша ненька Україна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Багато мала видатних синів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що рятували й захищали від руїни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що їх вогонь через віки горів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У стольнім нашім граді князювали,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І мудрістю провадили народ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І славу й честь країні здобували,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серед них &ndash; великий князь Аскольд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, сміливий і гордий,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великий звитяжець минулих століть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, у серці народу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ять про тебе не спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Замріяний Дніпро, завмерли хвилі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи спить, а чи когось згадав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В твоїй цілющій прохолодні сині</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд-князь силу набирав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ця сила тут, вона між нами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відвічним світочем горить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб об&rsquo;єднати Україну</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І розбудити всіх, хто спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Від Аскольда</span></strong></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. М.Влад, муз. К.Кучерявої</span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;1. Понад кручею Дніпровою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В ріднім краю живемо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Охоронені Покровою,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Є і будем &ndash; не вмремо!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від Аскольда до Шептицького</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наша віра лік веде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від Аскольда в третю тисячу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Україна вільна йде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вперед! Вперед!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Помолімся за весь світ!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вперед! Вперед!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Віра й молитва врятують світ!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;2. Нам грозилися руїною,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми встояли &ndash; не впадем!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Господь Бог із Україною,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми Його шляхом ідем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Здобували волю битвами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від Аскольда много літ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Християнськими молитвами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Порятуємо весь світ!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква маленька</span></strong></p>\r\n<p><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. Н.Шейко-Медведєвої, муз. Л.Горової</span></strong></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; text-indent: 0em; line-height: 1.3em;\">1. </span>Дай мені руку, друже<span style=\"font-family: arial, helvetica, sans-serif; text-indent: 0em; line-height: 1.3em;\">!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай мені руку, милий!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Й підемо вдвох з тобою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До джерела віри.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів</em></strong><span style=\"text-decoration: underline;\">:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква маленька &ndash; пам&rsquo;ять глибока.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В серці лунають пращурів кроки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слово вкраїнське &ndash; віра вселюдська</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В піснях любові з серця поллються.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Де дух посіяв віру в Ісуса,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Храми постали, попри спокуси.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ідолів люди творять донині,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та не здолати наші святині.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Вслухайся в голос неба,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітру дзвінкі акорди</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Й підемо в нашу церкву</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До Миколая-Аскольда.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів</em></strong><span style=\"text-decoration: underline;\">:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В церкві маленькій свічі палають,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щирі молитви тихо лунають.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слово вкраїнське &ndash; віра вселюдська.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В піснях любові з серця поллються.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З нами святії, з нами пророки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква маленька &ndash; віра глибока.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будем плекати радісні зміни</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Во славу Божу і України.</span></p>\r\n<div style=\"text-align: justify;\">\r\n<p><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Вірші Зої Ружин про князя Аскольда</strong></span></p>\r\n<p class=\"Style1\" style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар Русі &ndash; Аскольд</span></strong></p>\r\n<p class=\"Style1\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Зійшла на князя благодать...<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em; text-indent: 0em;\">І князь Аскольд прийняв хрещення.<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Настав час вірі возсіять!<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Послав Господь благословення...<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em; text-indent: 0em;\">І подвиг, й мученицький шлях...<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Безсмертному Аскольду - слава!<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">З Христовим вченням у віках -<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Під Богом і Народ! Й Держава!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,<br /></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бога прославляє!<br /></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Цар Русі - Аскольд!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воскресла пам&rsquo;ять крізь віки,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Молитви слово розквітає...</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Радіють сонце і зірки!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Нам Цар - Аскольд благо віщає!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгиня Ольга возвела</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> На честь Царя - Аскольда храми.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Княгині Ользі честь, хвала!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Під України небесами!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Бога прославляє!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Цар Русі - Аскольд!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style1\" style=\"text-align: justify;\"><strong><em><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава християнства</span></em></strong></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд із перших, християнство славив.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Першим із русинів Князь наш охрестивсь!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Древньруський воїн державою правив.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> До Бога, щоденно, Князь Аскольд моливсь:</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За своє князівство, за народ, за віру!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> За любов, за рід свій, за мир на землі!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Славив Бога щиро - отримував силу!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Правив він з Ісусом - Богом на чолі!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Подвигом Христовим, Князь підносив віру</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Крізь віки бурхливі сонцем засіяв!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князь Аскольд служитель злагоді і миру!</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Християнства славу князь наш підіймав!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>З хрестом на чолі</em></span></strong></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Древніх Русів сила йшла на штурм по морю...&nbsp;<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em; text-indent: 0em;\">Весь Константинополь до небес моливсь.<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Князь Аскольд побачив Божу, вищу волю:<br /></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Образ Богородиці в хмарах появивсь.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світлим омофором місто захистила,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Потонули в морі військо й кораблі.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Божа сила князю істину відкрила,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князь вернувсь від греків з Хрестом на чолі.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд з русинів першим охрестився,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> З вірою святою звершення творив.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князеві Аскольду Дух Святий явився,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князь Аскольд із Богом в серці вік прожив.</span></p>\r\n<p class=\"Style6\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд хрещений йменням Миколая,</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Перший Християнством землю освітив.</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Князь Аскольд молитви до нас посилає...</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"> Землю Древньоруську, як Бога любив!</span></p>','Аскольдів глас - збірник 2009','uk',3659,5142,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З питань участі у фестивалі-конкурсі звертатися:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Катерина Чаплик (голова оргкомітету) tel.: 066 447 80 99</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Світлана Заяць (заступник голови оргкомітету) tel.: 067&nbsp;220 79 70</span></p>\r\n<p class=\"aCxSpLast\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">e-mail: <a href=\"mailto:Chaplyk_Kateryna@ukr.net\">Askoldiv_glas2010</a>@ukr.net</span></p>','2013-12-27 23:38:18',NULL,'P','<p class=\"aCxSpFirst\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тексти пісень Київ 21 грудня 2013</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 1</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дитячий фольклорний ансамбль &laquo;Віночок&raquo; (Черкаська обл.)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Балада про Аскольда</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. і муз. Руслани Лоцман</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Звідки починається&nbsp;&nbsp; історія моєї України?</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">може, про це знають куполи,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">як тут наші пращури жили?</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Звідки починається&nbsp;&nbsp; історія великого народу?</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що в старих літописах живе</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам\'яттю у наші дні пливе?....</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливли човни по Дніпру</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В далекий Константинополь,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та не минувши красу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дніпровських схилів високих</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спинився воїн Аскольд,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб Київ пошанувати,-</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Йому повірив народ</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І став він тут князювати.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зібрав війська у похід,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Човнами і кораблями</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар завойовував світ</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І бився з кочівниками</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І де б Аскольд не бував-</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В Русі чи у Візантії,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В боях він перемагав,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо вірив в сили святії!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пішов по Києву дзвін,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що князь у вірі хрестився,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До Богородиці він</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За землі руські молився.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та раптом сталося зло &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підступно князя убили!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Багато часу пройшло,-</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Стоять Дніпровськії схили...</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там церква чекає нас,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Несем на могилу квіти.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми чуєм Аскольдів глас &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми княжого роду діти!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Звідки починається історія святого християнства....</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 2</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гурт &laquo;ВЕСЕЛЯТА&raquo; (м.&nbsp;Івано-Франківськ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Славний Аскольд</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. і муз. Зорини Валіхновської</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Ой князю Аскольде, руської землі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми про тебе нині згадуєм в житті.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лине спів із серця аж до ясних зір,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відгомін несеться до далеких гір.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ой ти, зіронько, ясная</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про князя Аскольда пісня ця</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ой ти, місяцю, золотий</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам\'ята Вкраїна подвиг твій.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Ой земле, слов&rsquo;янська, слався навіки,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А Аскольд із Діром &ndash; славні вояки.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Народну свідомість вони розбудили</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Київ врятували, ворога побили.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Зібрав князь дружину й рушили в похід</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Проти Візантії й сколихнули світ.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І неслася вістка про нашу державу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про народ великий і про його славу.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4. Та підступна заздрість панувала скрізь,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Успіхи русинів наводили злість.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вороги хотіли українських сліз</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та Бог допоміг нам й свободу приніс.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 3</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вокальна група хору &laquo;СВІТАНОК&raquo; (м. Самбір)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде &ndash; князю, дякуєм тобі</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. та муз. Олени Сенейко</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Аскольде &ndash; князю, дякуєм тобі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що була Русь могутня на землі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прадавня наша земленько,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До неї кличу серденьком.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За все, Аскольде, дякуєм тобі.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде &ndash; князю, дякуєм тобі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що височіє Київ на землі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що дивимось із ніжністю,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">із гордістю і з гідністю,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">як височіє Київ на землі.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сивочолий Аскольде десь,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти у сяйві ясних небес,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти у сяйві небес завжди</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">з дітьми своїми.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сивочолий Аскольде десь,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ми згадаємо знов тебе,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що залишив на кручах нам на згадку Київ.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Аскольде &ndash; князю, дякуєм тобі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що ми твої нащадки на землі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не зачерствілі душами,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">милуємося кручами,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">там до небес торкнулись куполи.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 4</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гурт &laquo;Esthetic voice&raquo; (м. Київ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Не давнина, а вічність</em></strong></span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. Олени Кулінської, муз. Світлани Острової</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як вабить спокоєм це місце!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи Дух Святий його обрав?</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прикрасив сяйвом променистим,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В кленовім золоті сховав.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Забудеться моя турбота,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I радощi, спокуса й труд.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I стане свiтлою скорбота...</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, гарно в тебе тут!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не давнина &ndash; а вiчнiсть,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не затишок &ndash; а рай.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть вiки, як буднi звичнi,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть думки &ndash; за небокрай.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 5</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Фольклорний гурт &laquo;МОКОША&raquo; (м.&nbsp;Вінниця)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 6</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молодіжний вокальний ансамбль &laquo;ЗАХІД&raquo; (м.&nbsp;Київ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольдів глас</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. Олени Сакалюк, муз. Роксолани Шпіци</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Проростає через терні забуття,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ять, що посіяна аскольдом.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За свободу, віру нашу, за життя</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Честь за славу Руську непоборну.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Не згоріло Євангеліє в вогні,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти побачив диво, богом дане.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Затремтіла Христа істина в душі,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першокняже, віри наш кагане.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Під покровом Мати Божої з небес</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Охрестив ти Русь &ndash; від бруду вмилась.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І покірно ніс свій мученичий хрест &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Твоя віра у серцях лишилась.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4. Там біля могили в церкві чути спів,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти в блаженстві з Богом спочиваєш.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Святий Миколая &ndash; твій ангел прилетів,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти в серцях людей вогнем палаєш.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5. Знов лунаж передзвін &ndash; Аскольдів глас</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мовби заклик: &laquo;Жиймо всі у Бозі!&raquo;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пломеніє факел віри &ndash; ще не згас,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо ідем по праведній дорозі.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 7</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вокальна група &laquo;ЧОРНОБРИВЦІ&raquo; (м.&nbsp;Львів)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У день святого Миколая</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сл. Руслани Лоцман, муз. Ірини Кириліної</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Коли Господь зішле на землю сніг,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вся природа в казку убереться,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прийде велике свято для усіх,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любов&rsquo;ю ороситься кожне серце.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То вслід за Катериною й Андрієм</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Йде в хату Чудотворець Миколай,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він зерна ласки Божої розсіє</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На наш чудовий український край.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ми у день святого Миколая</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Згадаємо великого царя,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай з нами древній Київ заспіває,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зійде Аскольда праведна зоря.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Святковий дзвін до церкви зазива,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збирається родина в добрий час,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І Миколай дарує нам дива:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лунає в небесах Аскольдів глас.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То молиться наш древньоруський цар за нас</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Людей, і ці місця священні.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми в пісні славим Бога за той дар</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда-Миколая у хрещенні!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У день святого Миколая</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сіяє в небесах зоря,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То древній Київ величає</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда &ndash; славного царя!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 8</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Музична формація &laquo;K_CORNFLOWER&raquo; (м. Київ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Аскольдова звитяга&raquo;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">сл. Христини Шонь&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; муз. Sam Watters</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це був той час, коли в Русі</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Люди вірили в богів</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І не знали, що Один</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бог Творець є над усім.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лиш Аскольд віру пильнував</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У Єдиного Творця,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З неба поклик віри мав.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як свіча, горів і пломенів &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Свічка для Христа&hellip;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За градом стольним Києвом</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підступно зловлений в полон</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Ти &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сподвижник віри у Христа,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти є святий.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Незламно прийняв ти Закон</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І навернувся до Христа,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли довкола всі язичниками ще були&hellip;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде&hellip;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожен день в молитвах чував,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сили в Господа просив,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бути мужнім до кінця,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде ти хотів.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та Олег змову готував</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Із боярами разом,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб твою пролити кров</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кров того, хто йшов на смерть святу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Із вірою&hellip;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Навіть якби цілий всесвіт</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Проти тебе змову мав</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти би йшов до кінця</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І правда твоя</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мученицький трон посів</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У небесах</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хвала! І честь!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тобі Аскольде, на віки!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нехай буде</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шана по всій землі тобі</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хвала! І честь!...</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 9</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вокальний ансамбль &laquo;Кант&raquo; ЛДУКіМ (м.&nbsp;Львів)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Як вабить спокоєм&raquo;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">сл. Олени Кулінської, муз. Мирона Дацка</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як вабить спокоєм це місце!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи Дух Святий його обрав?</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прикрасив сяйвом променистим,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В кленовім золоті сховав.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Забудеться моя турбота,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I радощi, спокуса й труд.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I стане свiтлою скорбота...</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, гарно в тебе тут!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не давнина &ndash; а вiчнiсть,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не затишок &ndash; а рай.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть вiки, як буднi звичнi,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть думки &ndash; за небокрай.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 10</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вокальний ансамбль &laquo;ЖУРАВЛИНА&raquo; (м.&nbsp;Київ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Боже хрещення України&raquo;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">сл. Тетяни Майданович &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;муз. Людмили Левченко-Бутуханової</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Хрестив уперше Київ князь Аскольд &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">для всіх отут відкрилась Церква Божа.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На берегах біля Дніпровських вод</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">звелися храми &ndash; ваших душ сторожа.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вже не надійтесь на забрід-варяг,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">тих посланців і спадкоємців Руса,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що на обман стоптали княжий стяг</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">та по собі і вас назвали руссю.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ви українці, ви для Мене ті,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">кому Я поле наділив-украяв,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">ви тут жили в красі і простоті,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">мій дар на спогад Раю, це-Украйна!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вже йде найглибше хрещення в сльозах &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">це покаяння і трудна спокута.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прийміть його.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Розвийтесь, як лоза.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знов князь Аскольд виводить із розпуття.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.Світ очищає хрещення, що ним</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христос хрестився &ndash; кров\'ю і стражданням.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли ж не спинить зла, із вишини</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">гряде на землю вогняне, останнє.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ваш дух не підкорився чужакам &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Русь освятилась у Дніпровськім краю.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Русь-Україну відкриваю вам,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">вскликаю всіх, народом називаю.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">№ 11</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вокальний ансамбль старовинної музики &laquo;АЛІТЕЯ&raquo; (м.&nbsp;Київ)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Дума про Аскольда&raquo;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">сл. Василя Довжика, муз. Олени Лис</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дбав про стольний Київ</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дбав про свій народ</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мудрий каган скіфів &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світлий князь Аскольд.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І щоб жить по правді,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодать нести &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Був у Цареграді,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там і охрестивсь.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На шляху у греки</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тяг човни варяг.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Взяти Київ легко</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Захотів Олаф.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В очерет потайно</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Військо приховав</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І біля Почайни</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князя подолав.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Військом на поталу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Місто звоював.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Помогли бояри &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Землі їм роздав.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та народ допіру</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вижив наш і зріс &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Християнську віру</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В Русь Аскольд приніс!</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А-а, а-а</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь у Цареграді</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Був і охрестивсь,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоби жить по правді &ndash;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодать нести! (2)</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З питань участі у фестивалі-конкурсі звертатися:</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Катерина Чаплик (голова оргкомітету) tel.: 066 447 80 99</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Світлана Заяць (заступник голови оргкомітету) tel.: 067&nbsp;220 79 70</span></p>\r\n<p class=\"aCxSpLast\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">e-mail: <a href=\"mailto:Chaplyk_Kateryna@ukr.net\"><em>Askoldiv_glas2010</em></a>@ukr.net</span></p>','Сучасні твори про князя Аскольда','uk',3896,5379,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3337,200,6.jpg\" alt=\"\" width=\"200\" height=\"109\" />&nbsp;Сьогодні 31 жовтня 2009 р в читальному залі Киево-Могилянської академії розпочався суспільний тиждень.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">ПРИМИРЕННЯ ЯК ДАР БОЖИЙ і представляємо його в світлі великого митрополита Андрея Шептицького і Патріарха Йосипа Сліпого. Як один так і другий у своєму житті вірою і правдою служили людині яка потребувала допомоги, молитви. Справді, якщо говорити про образ великого Митрополита то його життя посвячене служінню своєму народу, просто людині, народові який терпів голод 30 років, народові який терпів смерть 40р.,народові якому великий митрополит вказував шлях до істини, до Бога і сам показував цей істинний шлях.</span></span></p>','2009-10-31 19:16:39',NULL,'P','<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3337,200,6.jpg\" alt=\"\" width=\"200\" height=\"109\" />&nbsp;Сьогодні 31 жовтня 2009 р в читальному залі Киево-Могилянської академії розпочався суспільний тиждень</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3337,200,2.jpg\" alt=\"\" width=\"156\" height=\"193\" />Ведучий п.Ігор Шабан надав слово Владиці Йосифу</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Вітаю всіх в імені Блаженішого Любомира, що не зміг&nbsp; бути з нами. Сьогодні ми розпочинаємо суспільний тиждень в якому будемо говорити про</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">ПРИМИРЕННЯ ЯК ДАР БОЖИЙ і представляємо його в світлі великого митрополита</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Андрея Шептицького і Патріарха Йосипа Сліпого. Як один так і другий у своєму житті вірою і правдою служили людині яка потребувала допомоги, молитви. Справді, якщо говорити про образ великого Митр<img style=\"float: left;\" src=\"image,3337,200,1.jpg\" alt=\"\" width=\"147\" height=\"200\" />ополита то його життя посвячене служінню своєму народу, просто людині, народові який терпів голод 30 років, народові який терпів смерть 40р.,народові якому великий митрополит вказував шлях до істини, до Бога і сам показував цей істинний шлях.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ми розпочинаємо цей суспільний тиждень щоб інтенсивно послужити тим що потребують не дивлячись на конфесії і політичні загне жування.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Суспільне служіння ніколи не буде правдивим і глибоким поки людина не відчує свого гріха і великого Божого милосердя на собі і тільки тоді може гідно і милосердно служити ближньому. Цей тиждень не простий бо Україна складні ситуації пандемії, грипу. Але маємо велику надію на Бога і тому хай сильна віра і допоможе бути свідками Божої присутності серед свого народу.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3337,200,3.jpg\" alt=\"\" width=\"155\" height=\"200\" /></span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Апостольський нунцій Архієпископ Іван Юркович наголосив про страшний феномен хаосу у світі. Його дуже відчувають народженні діти які багато плачуть, бо людина відвертається від Бога.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Тому темою нашою є ПРИМИРЕННЯ-ДЛЯ ПОВЕРНЕННЯ ВІД ХАОСУ ДО БОГА.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Тільки Бог змінює суспільство. Ісус з Лазарету&nbsp;&nbsp; вказує на зв\'язок з Богом через любов до ближнього. Любов &ndash;це лікарство.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Поділився своїми думками єпископ Станіслав Широкорадюк і наголосив ,що на першому місці треба ставити Бога. У нас нема цього першенства, хоч Церква<img style=\"float: right;\" src=\"image,3337,200,5.jpg\" alt=\"\" width=\"200\" height=\"144\" /> заявила про своє служіння. Держава не сприйняла цієї заяви і в школі нема християнської етики.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Цей рік є роком СВЯЩЕНСТВА &ndash;його проголосила католицька Церква.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Бог чує нас коли маємо велику віру і молимось чистим серцем. Приклад Шептицького показує нам його ВЕЛИКУ ДОВІРУ ДО БОГА.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Світ потребує очищення сердець.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3337,200,8.jpg\" alt=\"\" width=\"133\" height=\"200\" /></span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Головний равин Яків Блейх наголосив&nbsp; про шанування один одного, треба ділитись і головне можливістю допомогти.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Примирення &ndash; це піднятися вище, я буду робити, те що хоче Бог, я це значить єдність і примирення Андрей Шептицький жив не для себе, він рятує других ціною свого життя.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Сьогодні вибори &ndash; це поважати думку інших і сказати, те що я можу зробити. Догляньмо одиноких людей. Шануймо стариків, тоді і наші діти будуть нас шанувати.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ректор Дрогобицької семінарії наголосив про життя патріарха Йосипа сліпого 1976 року про поєднання в Христі. Поєднання &ndash; це дар Божий. Віра &ndash; <img style=\"float: right;\" src=\"image,3337,200,9.jpg\" alt=\"\" width=\"200\" height=\"133\" />це найсильніший мотор діяння і примирення. Нас єднає Володимирове Хрещення, Київська традиція.&nbsp; Апостольська традиція. Благословення ап. Андрія і папи, св. Климента. Спільна молитва в Храмах. Молитва в св. Софії Київській. Церква один Божий народ.</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Це підстава єдності національної .</span></span></p>\r\n<p style=\"FONT-FAMILY: ; TEXT-ALIGN: justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; Представники громадськості, журналісти, парафіяни Геро - католицьких церков м. Києва, були задоволенні цим вечором, який розпочав тиждень суспільного служіння</span></span></p>','Примирення - Дар Божий!','uk',1854,3337,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"TEXT-ALIGN: justify; FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Я испытал настоящий шок, когда мне сообщили об уничтожении храма. Не верится, что такое может происходить сегодня, на восемнадцатом году Независимости. Последний раз церкви в Украине разрушали еще при Сталине. &ndash; так прокомментировал Одесско-Крымский экзарх УГКЦ владыка Василий Ивасюк.</span></span></p>','2009-08-26 20:01:04',NULL,'P','<div><a href=\"http://kp.ua/daily/author/\"><span style=\"text-decoration: underline;\"><span style=\"color: #0000ff;\">\r\n<div style=\"TEXT-ALIGN: justify; FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Строители разрушили двухэтажное здание посреди бела дня, не пояснив ничего церковникам.</span></span></div>\r\n<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Снежана ПАВЛОВА</span></span></span></span></a></div>\r\n<p><span id=\"font_size\"></span>\r\n<p>&nbsp;</p>\r\n<p style=\"TEXT-ALIGN: justify\">&nbsp;</p>\r\n</p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"http://kp.ua/upimg/5a3e8f353c90697f8fa8aa939d18ebfb3dd46ab0/163079.jpg\" alt=\"\" />Двухэтажный храм находился в центре Херсона, был в совместном пользовании Украинской Греко-Католической Церкви и Немецкой Евангелическо-Лютеранской Церкви в Украине.</span></span></p>\r\n<p style=\"FONT-FAMILY: \"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Представители строительной компании &laquo;Парнас&raquo;, не имея никаких разрешительных документов, с усмешкой на устах варварски разрушили христианскую святыню, а на месте храма вырыли глубокий котлован. - сообщил ужасающую новость&nbsp; декан Херсонского деканата УГКЦ, отец Степан Макар.&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Имя заказчика в церкви пока не знают, но уверены, что причина такого акта вандализма в том, что земля находится в центре города&nbsp; и она очень прибыльна для бизнеса.&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">- Я испытал настоящий шок, когда мне сообщили об уничтожении храма. Не верится, что такое может происходить сегодня, на восемнадцатом году Независимости. Последний раз церкви в Украине разрушали еще при Сталине. &ndash; так прокомментировал Одесско-Крымский экзарх УГКЦ владыка Василий Ивасюк.&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"FONT-FAMILY: \" align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Церковники собираются встретиться с представителями власти, подать иск в суд о прекращении любых действий до выяснения обстоятельств и отстаивать храм в дальнейшем согласно документам. Заявление в прокуратуру уже подали.&nbsp;</span></span></p>\r\n<p align=\"justify\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">За матеріалами: </span></span><a style=\"FONT-FAMILY: \" href=\"http://kp.ua/daily/250809/191842/\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">http://kp.ua/daily/250809/191842/</span></span></a></p>','В Херсоне снесли греко-католический храм','uk',1758,3241,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 224px; height: 168px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_budova_sobor.jpg.jpg\" alt=\"\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Адже всі мистецькі композиції, пам&rsquo;ятники чи якась особлива архітектура в столиці сконцентровані саме на правому березі, &ndash; каже Владика Йосиф. &ndash; Тому наш собор серед будов лівого берегу, серед спальних кварталів є мистецькою сакральною спорудою. Звісно, з часом собор ставатиме тільки кращим. Ми невдовзі його добудуємо. Він обросте відповідною флористикою, відповідними насадженнями&raquo;.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами Владики Йосифа, незабаром біля храму постане скульптурна експозиція &laquo;Писанка України&raquo;. У центрі виставки буде писанка з орнаментом трипільської культури. А навколо неї 24 писанки з різних областей України. Це має внести відповідний шарм і дух у цей район.</span></span></p>','2010-10-28 21:45:20',NULL,'P','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"border: 1px solid black; margin: 5px; width: 224px; height: 168px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_budova_sobor.jpg.jpg\" alt=\"\" />Патріарший собор Воскресіння Христового, що на лівому березі Києва 27 жовтня святкує своє восьмиріччя. Саме вісім років тому греко-католики України та світу заклали наріжний камінь cобору. Собор уже встигли охрестити другою Софією в Києві.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Владика Йосиф (Мілян), Голова наглядової ради будівництва Патріаршого собору Воскресіння Христового у Києві, зазначає, що Патріарший собор УГКЦ на лівому березі Києва вносить певний шарм у цей куточок міста.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Адже всі мистецькі композиції, пам&rsquo;ятники чи якась особлива архітектура в столиці сконцентровані саме на правому березі, &ndash; каже Владика Йосиф. &ndash; Тому наш собор серед будов лівого берегу, серед спальних кварталів є мистецькою сакральною спорудою. Звісно, з часом собор ставатиме тільки кращим. Ми невдовзі його добудуємо. Він обросте відповідною флористикою, відповідними насадженнями&raquo;.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами Владики Йосифа, незабаром біля храму постане скульптурна експозиція &laquo;Писанка України&raquo;. У центрі виставки буде писанка з орнаментом трипільської культури. А навколо неї 24 писанки з різних областей України. Це має внести відповідний шарм і дух у цей район.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Хтось вважає собор &laquo;довгобудом&raquo;, &ndash; говорить Голова наглядової ради, &ndash; але якщо поглянути об&rsquo;єктивно, то це не зовсім так. Пам&rsquo;ятати, що ми будуємо храм, а храми, зазвичай, будуються довго. Крім того Блаженіший наш Патріарх Любомир неодноразово зауважував, наголошував, що цей собор &ndash; собор нашого народу, наших вірних. І кожна цеглинка на будову справді окуповується нашими матерями, бабусями по 5-10 гривень. Тобто, на жаль чи на щастя, ми не мали великих меценатів. Але, мабуть, усе-таки слава Богу, що ми будуємо храм саме таким способом &ndash; ні від кого не залежними. Собор будують окремі люди, окремі індивідууми&raquo;.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://ugcc.org.ua/news_single.0.html?&amp;tx_ttnews[tt_news]=3885&amp;tx_ttnews[backPid]=2&amp;cHash=001504314a</span></span></p>','Патріаршому собору виповнюється вісім років  ','uk',2196,3679,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Такі слова Папи Венедикта XVI стали основою дискусій Світового конгресу католицької преси. Разом з кількома сотнями колег з усього світу (близько 400 учасників з 85 країн) у його роботі беруть участь о. Ігор Яців, керівник Департаменту інформації УГКЦ, та Дмитро Крикун, керівник Аналітичного відділу цього департаменту. Конгрес відбувається у Римі 4 - 7 жовтня 2010 року. Ключовими питаннями до обговорення учасниками у ближчі дні будуть &ndash; &laquo;Яка доля друкованої преси у світі?&raquo;, &laquo;Як доносити слово правди сучасним аудиторіям&raquo;, &laquo;Загрози та можливості Інтернету&raquo;.&nbsp; </span></span></p>','2010-10-04 19:29:11',NULL,'P','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Такі слова Папи Венедикта XVI стали основою дискусій Світового конгресу католицької преси. Разом з кількома сотнями колег з усього світу (близько 400 учасників з 85 країн) у його роботі беруть участь о. Ігор Яців, керівник Департаменту інформації УГКЦ, та Дмитро Крикун, керівник Аналітичного відділу цього департаменту. Конгрес відбувається у Римі 4 - 7 жовтня 2010 року. Ключовими питаннями до обговорення учасниками у ближчі дні будуть &ndash; &laquo;Яка доля друкованої преси у світі?&raquo;, &laquo;Як доносити слово правди сучасним аудиторіям&raquo;, &laquo;Загрози та можливості Інтернету&raquo;.&nbsp; </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Церква повинна якнайкраще пізнати можливості сучасних засобів комунікації, щоб максимально використовувати їх у своїй щоденній місії. При цьому ми повинні розглядати їх не тільки як засоби для проповіді Доброї новини, але й як засоби комунікації зі світом&raquo;, &ndash; сказав Архиєпископ Клаудіо Марія Челі, Голова Папської ради у справах суспільних комунікацій, відкриваючи Конгрес. Для визначення подальших кроків у справі використання новітніх технічних можливостей, на думку Владики Кладіо, важливою є активна робота кожного учасника Конгресу у групах. &laquo;Розповідаючи про дійсність у ваших країнах, ви допоможете краще розуміти завдання, які стоять перед Церковю сьогодні&raquo;, &ndash; наголосив Голова Папської ради.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час Конгресу представники УГКЦ провели робочі зустрічі з представниками Литви та Білорусі. Корисність такої зустрічі була в тому, що проблематика, яку представляли учасники зі США чи Західної Європи є відмінною від ситуації у країнах Східної Європи. &laquo;У нас в силу історичних обставин минулого століття церковні ЗМІ не є так поширені і розвинені, як у інших країнах Європи. Крім того не всюди є достатнє поширення доступу до Інтернету. Тому ми маємо випрацьовувати особливі підходи у справі поширення науки Церкви у наших країнах&raquo;, &ndash; зазначали учасники цієї зустрічі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також учасники делегації УГКЦ зустрілися з представниками української громади в Римі та Італії, вірними та священиками парафії св. Сергія та Вакха у столиці Італії, відвідали Апостольського візитатора для українців греко-католиків в Італії Владику Діонісія (Ляховича). </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Більше про Конгрес на сторінці в Інтернеті <a href=\"http://www.pccs.va/\" target=\"_blank\">www.pccs.va</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Департамент інформації УГКЦ</span></span></p>','Дослідження та подання правди про людство є покликанням комунікації, – Папа Венедикт XVI  ','uk',2166,3649,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"margin: 0pt;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_dt.jpg.jpg\" alt=\"\" width=\"114\" height=\"101\" /></strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми в дорозі &ndash; тяжкій і повільній з причин як зовнішніх, так і внутрішніх. Внутрішню ситуацію ускладнює, зокрема, те, що наша церква не є виключно на території України, а розкинута по всьому світу, і це не тільки люди, а й цілі церковні структури &ndash; в обох Америках, Західній Європі, Австралії. Зараз перед нами стоїть завдання забезпечити насамперед внутрішню єдність тих різних частин в Україні і поза її межами...<br /></span></span></p>','2009-12-01 01:48:42',NULL,'P','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Історія церкви в незалежній Україні дала безліч уроків. Один із найважливіших &ndash; пряма залежність між можливістю єдності та свободою. Чим сильніший тиск на церкву з боку політичних сил, тим більша прірва між ними. І навпаки: чим менше ідеологічної мішури, тим простіше вести діалог.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Найближчим часом, швидше за все, маятник міжцерковних відносин знову хитнеться у бік нетерпимості. Вибори. Той факт, що багато кандидатів отримали благословення кого тільки могли, не заважає їм розігрувати церковну карту як інтереси велять. Невпевнений діалог, що зародився останніми місяцями, впаде в анабіоз, перш ніж новий президент укорениться у своєму кріслі. А там видно буде.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Мислення категоріями &laquo;канонічних територій&raquo; і конфесійної приналежності виявилося дуже зручним для маніпулювання. За цими поняттями легко загубити суть як християнської, так і просто людської єдності. Тому ними так захоплено граються ті, для кого розділення &ndash; запорука ідеологічного успіху.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>До предстоятеля Української греко-католицької церкви Блаженнішого Любомира мене привело якраз питання суті єдності. Адже саме він кілька років тому виступив із грандіозною ідеєю неконфесійної, або, згідно з нинішньою гуманітарною модою, &ndash; &laquo;постконфесійної&raquo; єдності.</em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><img style=\"border: 1px solid black; margin: 5px; width: 220px; height: 191px; float: left;\" src=\"http://ugcc.org.ua/uploads/RTEmagicC_huzar_dt.jpg.jpg\" alt=\"\" />Владико, питання української помісної церкви хоч і не нове, проте не втрачає привабливості. Певний час змагання за цю роль розгорталося тільки між православними церквами, але кілька років тому Українська греко-католицька церква заявила про себе як про церкву &laquo;Київську&raquo; і про намір здобути патріарший статус. Символом того став початок будівництва патріаршого собору в Києві. Яка перспектива у проекту &laquo;Патріархат&raquo; тепер, коли перша хвиля захоплення ідеєю минула?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми в дорозі &ndash; тяжкій і повільній з причин як зовнішніх, так і внутрішніх. Внутрішню ситуацію ускладнює, зокрема, те, що наша церква не є виключно на території України, а розкинута по всьому світу, і це не тільки люди, а й цілі церковні структури &ndash; в обох Америках, Західній Європі, Австралії. Зараз перед нами стоїть завдання забезпечити насамперед внутрішню єдність тих різних частин в Україні і поза її межами. Це дуже серйозна проблема, бо йдеться про внутрішню єдність церкви. Не суто адміністративно чи структурно &ndash; на рівні наставлення: ми в Україні мусимо відчувати, що ті, хто поза межами країни, &ndash; це наші брати й сестри і ми їх потребуємо, а вони також щоби зрозуміли, що для них внутрішня єдність з Україною є надзвичайно важливою. Бути патріархатом &ndash; це нести спільну відповідальність за всіх, де б вони не були. Ми призвичаюємося, вдумуємося, що це означає &ndash; бути патріархатом.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тобто ви ще не вирішили, що це означає?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ідея патріархату для УГКЦ має довгу історію, &ndash; вона насправді виникла не вчора, а сягає XVII століття. Цілком природна думка, питома для тої традиції, до якої ми належимо, тому ця ідея час від часу виринала в різні моменти нашої історії останніх триста років. Але від 60-х років минулого століття вона стала для нас першорядною, збудивши велике зацікавлення і спричинивши багато суперечностей. Річ у тому, що відтоді патріархат перестав бути суто теоретичною конструкцією. Ми мусили зрозуміти, що це не просто традиція східної церкви, якою ми є. Ми мусили зрозуміти суть &ndash; що то є бути патріархатом, думати по-патріаршому. Тобто постало багато проблем практичного порядку. Коли почалися змагання за патріарший устрій для УГКЦ у 60-х роках, церква в Україні мовчала, бо не могла говорити. Все відбувалося поза межами України &ndash; головно у Північній Америці і трохи в Європі. Ідея патріархату тільки нещодавно стала частиною життя нашої церкви і тут, в Україні. Але коли вона сюди прийшла, збудила багато розмов без розуміння того, що все це означає.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Які є зовнішні труднощі на шляху до патріархату?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Річ у тому, що, за законами католицької церкви, патріархати встановлює або Вселенський собор, або, якщо він не збирається, від імені церкви Папа Римський. На жаль, Другий Ватиканський собор, на якому заходила мова про статус Української греко-католицької церкви, не виробив відповідної процедури. Питання зависло у повітрі. Нове законодавство &ndash; приблизно 20 років, відколи з&rsquo;явився новий кодекс церковного права, &ndash; також не вносить ясності в це питання. Він підтверджує джерело рішення &ndash; Вселенський собор або Папа, &ndash; але питання про те, коли, як, чому цей статус дається, залишилися відкритими.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дуже схожа проблема у православних братів, які також не можуть отримати автокефалії і навіть автономії, тому що порядок надання цього статусу залишається спірним і не до кінця зрозумілим. Утім, гадаю, &laquo;зовнішні труднощі&raquo; УГКЦ, як і у випадку з православними церквами України, не вичерпуються канонічним правом.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Роль міжцерковних стосунків у тих рішеннях не є таємницею. Московський патріархат неодноразово і в різний спосіб висловлював свої протести щодо становлення патріархату Української греко-католицької церкви. Але треба зазначити, що поява УГКЦ з патріаршим устроєм була би неприємною не тільки для Московського патріархату. Це підкреслило би самобутність нашої церкви, в той час як чомусь є страх, що коли ми будемо такі самобутні, то зможемо чи схочемо відірватися від всіх і вся. Можливо, що такі побоювання стримують Ватикан. Латинська ментальність спрямована на централізацію. На відміну від східної традиції, для якої патріарший устрій і соборність є природнім способом існування.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Тобто сам принцип побудови католицької церкви заважає Ватикану до кінця зрозуміти внутрішню потребу церкви, котра становить досить велику її частину? Тоді чи є у вас шанси знайти там розуміння?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не тільки ми є кандидатами на патріарший устрій у структурі католицької церкви. Дуже близька до того Сиро-Малабарська церква (одна зі східнокатолицьких церков зі статусом Верховної Архиєпископії, має найбільше вірних у Індії та індійській діаспорі у світі. &ndash; <strong>К.Щ.</strong>), яка і чисельно, і структурно є дуже поважною. Вони щороку висвячують близько 250 священиків. Тобто це велика церква. Мабуть, більша, ніж наша. І вони, і ми маємо все необхідне, щоби отримати статус патріархату. Однак що робити з системою централізованої інституції, її побоюванням, що прагнення її частини бути собою обернеться від&rsquo;єднанням, унезалежненням; страхом, що якщо хтось вийде з-під жорсткої руки, то відразу втече подалі? А тут ще зовнішні впливи &ndash; Московський патріархат та інші православні церкви, які не дуже радо бачили би патріарший устрій у греко-католиків.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чому?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бо вони не знають, що з нами робити, як до нас ставитися. Бо ми є для них парадоксом. Дивовижею. Чимось неприродним. Це ставлення має історико-богословське підґрунтя. Найбільш занепокоєний, звичайно, Московський патріархат, бо все це відбувається на території, яку вони хотіли би бачити тільки своєю. Ось вони і вживають заходів, щоби знеохотити Ватикан. Це триває вже сотні років.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Що дасть статус патріархату УГКЦ? Чи не є це просто відповідь на соціально-політичний запит на &laquo;київську помісну&raquo; церкву і бажання нації, котра отримала нещодавно незалежність, унезалежнюватися в усьому і від всіх?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все навпаки. Спільнота може складатися тільки з людей, які свідомі своєї ідентичності. Ми маємо стати собою, усвідомити, хто ми, які і звідки. Тільки тоді ми можемо стати всім довкола приятелями та співрозмовниками. Якщо ми є безликою групою безликих &ndash; ми не можемо бути приятелями. Ми не можемо будувати єдність. У деяких представників Вселенської церкви, які дуже схвильовані питанням єдності, є великі труднощі з розумінням того, на чому будується ця єдність. Ми бачимо єдність як співпричастя. Усвідомлення своєї історії, своїх особливостей, пов&rsquo;язаних із тим, що ми виросли на цій землі, &ndash; для нас не виглядає як підстава для відчуження. Радше, навпаки. Будучи свідомими духовно, богословськи, церковноправно, культурно &ndash; ми стаємо сильнішими партнерами. Бо помісна церква є втіленням Христової церкви у даній місцевості чи культурній групі. Тоді вона, окрема й самобутня, у спілкуванні з іншими становить Вселенську церкву. Це дуже важко зрозуміти представникам системи, яка є централістична. Декому важко пробитися до того розуміння, що людина задоволена не тоді, коли вона далека від усіх, а тоді, коли вона спілкується з іншими &mdash; не як залежна, а як свобідна людина. Патріархат &ndash; духовна автономія, яка дає можливість спілкуватися більш глибоко, з більшою довірою.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Здається, проблема не тільки в &laquo;нерозумінні&raquo; з боку Ватикану. Всередині УГКЦ немає одностайності в питанні необхідності патріархату. І найчастіше це пов&rsquo;язано з сумнівами вірних у здатності своїх українських владик керувати церквою. Ватикану довіряють більше.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У нас в Україні справді багато людей, які думають, що наше спасіння тільки в єдності з кимсь. Люди не довіряють собі, не розуміють своєї гідності. Вони не розуміють, що бути собою &ndash; не означає стати чиїмось ворогом. Ми мусимо здійснювати свою гідність, яку дав нам Бог, у спілкуванні з іншими. Але це має бути спілкування свобідних і рівних, &ndash; тільки тоді наша гідність реалізується. І в Україні є люди, котрі не розуміють, що неможлива повноцінна єдність в обставинах неусвідомлення себе. Вони воліють бути з Польщею чи з Росією. В нашій ментальності є трошки комплексу меншовартості. У нас не розвинене відчуття не тільки політичної, а й просто людської гідності, якщо ми думаємо, що можемо існувати тільки як частина когось іншого. Ця трудність позначається й на житті церкви.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Зараз спостерігається потепління у відносинах між Московським патріархатом і Ватиканом. Як це може позначитися на Українській греко-католицькій церкві?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мабуть, ніяк. Ватикан не хотів би, щоби ми були перешкодою у відносинах із Московським патріархатом. А з боку керівництва РПЦ я не бачу ані найменшої зміни у ставленні до нас. Все так, як і було. Повторюються одні й ті самі слова. Я не впевнений навіть у тому, що відбувається якесь потепління. Якщо є щире бажання зближення, спілкування, тоді не ставлять умов &ndash; а зустрічаються і спілкуються. А якщо ви починаєте ставити умови &laquo;зроби те і те, тоді будемо тебе любити&raquo;, &ndash; то це відсутність щирого бажання спілкуватися, відсутність справжньої любові. Різниця між політикою колишнього і нинішнього патріархів Московських тільки у формі &ndash; багато галасу, але нічого нового за суттю не відбувається.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чи були якісь спроби спілкування безпосередньо з УГКЦ з боку керівництва РПЦ?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні. Для них ми залишаємося просто перешкодою, трудністю. У мене були маленькі зустрічі з росіянами, і я зробив висновок: вони впевнені, що якщо ти росіянин, мусиш бути православний. І навпаки &ndash; якщо ти православний, тоді ти справді росіянин, або, в нашому випадку, &ndash; українець. А якщо ти православний, то ти не є католик. Усе доволі просто. Для них католик &ndash; це латинник, латинської традиції. Тобто &laquo;не наше&raquo;. Це не правильно. І ми своїм існуванням ці прості постулати заперечуємо. Ми, бачите, і православні за традицією, і католики, об&rsquo;єднані з Апостольською столицею у Римі. Як казав Папа Римський під час святкування 1000-ліття Хрещення Київської Русі, &laquo;православна віра, католицька любов&raquo;. Православними ми є у вірі, католиками &ndash; у любові, в єдності.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>УГКЦ вважають &laquo;церквою Галичини&raquo;. Чи може бути статус патріархату в регіональної церкви? Адже помісна церква повинна охоплювати всю країну.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наш ідеал &ndash; одна помісна церква і один патріарх, який засяде у Святій Софії в Києві. Ми хочемо повернутися до цього. Я про це казав і тут, і в Римі: коли ми говоримо про патріарший устрій для греко-католиків, ми не хочемо, щоби він став формою відділення нас від православних братів. Для нас патріархат &ndash; не завершення. Для нас це засіб для досягнення більш серйозної мети &ndash; єдності Київської церкви, яка була на початку. Патріархат &ndash; це шлях, а не вінець мрій. Ми будуємо Патріарший собор у Києві. Нам кажуть: от, ви будуєте собор на лівому березі на противагу Софійському на правому. Ні. Ми не будуємо свій собор як якийсь альтернативний християнський центр. Тільки як тимчасовий. До того часу, доки єдиний патріарх не засяде у Святій Софії.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>На цих твердженнях ґрунтуються звинувачення на вашу адресу в тому, що ви хочете приєднати всіх українських християн до Католицької церкви.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я хочу, щоби церква була єдиною. Це не означає, що всі мають стати католиками чи пристати до нас. Я хочу, щоби всі були у Христі одне. Коли патріарх Кирил хоче спілкуватися з патріархом Заходу &ndash; Папою, &ndash; це означає, що він збирається &laquo;католичитися&raquo;? Ні, це означає, що вони хочуть бути разом у Христі. Принаймні я на те сподіваюся. В цьому суть екуменізму &ndash; бути одним. Єдність не може бути інакшою, як спілкування різних і рівних. Це є ідеал церкви. Христос говорив арамейською мовою, але він не наполягав, щоби всі говорили арамейською. Нас об&rsquo;єднує Христос, а не якісь зовнішні речі.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ходять чутки, що ви подали до Ватикану прохання про відставку, &ndash; це правда?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні, я не подавав у відставку. Чутки, мабуть, пов&rsquo;язані з тим, що, коли мені минуло 75 років, я звернувся до своїх братів &ndash; єпископів нашого Синоду &ndash; і до Папи Римського з пропозицією подбати про майбутнє. Я вже не є молодий і не стану ані молодшим, ані сильнішим, ані здоровішим. Треба шукати нові сили. Ніхто не зможе і не стане, я гадаю, примушувати мене залишити посаду Верховного архієпископа, і я можу залишатися на цій посаді пожиттєво. А можу у слушний момент відійти й дати церкві нагоду розвиватися далі під проводом когось іншого. Але я не вважаю, що треба тягти до останнього моменту. Людина старша має бути вшанована, вона має великий досвід, але не має того розмаху, тієї сили, щоби церкву провадити. Це природна річ, і я того свідомий. Я кажу відверто &ndash; подумаймо про майбутнє. Як звичайно відбувається перехід? Хтось помер, і тоді швиденько шукаємо наступника. Але краще зробити інакше &ndash; у певний час передати справи церкви з рук до рук своєму братові.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ви вже визначилися, хто це буде?</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Шукаємо. Бачте, відтоді, як я про цей намір проголосив, минуло вже два роки. Я дотримуюся тої засади, що ніхто у світі не є незамінний. І що ідеального голови церкви ми не знайдемо. Тільки Господь Бог є ідеальним.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Яких змін можна очікувати в УГКЦ у зв&rsquo;язку з близькими виборами? Хоч би хто їх виграв, ситуація в країні, швидше за все, стане трохи інакшою.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква є церквою і повинна зберігати свою гідність, незалежно від того, хто є при владі. Держава повинна, зі свого боку, поважати церкву, а церква &ndash; допомагати державі в тих питаннях, де сфери їх піклування про суспільство перетинаються. Це в ідеалі. Але ідеалу, ще раз кажу, на землі не знайти. Кожна держава хоче контролювати церкву &ndash; не тільки в нас, у будь-якій країні це так. Більш чи менш видимо, більш чи менш інтенсивно. Такою є сама природа держави: це інституція, яка не любить мати свобідних елементів, любить контролювати все. А церква, за своєю природою, якщо вона хоче залишатися церквою, мусить бути свобідною, не підкорятися, але співдіяти у добрих справах. Тому, хоч би які були політичні зміни, церква завжди намагатиметься зберегти свою свободу. Ми, різні християнські церкви і спільноти в Україні, мусимо бути супроти держави &ndash; вільні, рівні й не використовувані для цілей суто політичних.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ваша позиція в питанні &laquo;церква і вибори&raquo; відома: ви завжди були противником використання церкви задля підтримки тієї чи іншої особи. Втім, цього разу, за чутками, на підтримку з боку УГКЦ сподівається Арсеній Яценюк. Начебто він домовився про це з вами.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, я мав зустріч із паном Яценюком. Але ніякої підтримки на виборах із боку греко-католицької церкви йому не обіцяв. Так само, як будь-кому іншому, &ndash; багато хто хотів би мати нас за собою. Бачите, тепер є така мода: всі кандидати хочуть мати коло себе якусь духовну особу для прикраси. І чим більше прикрас, тим краще. Це не є пошанівок для церкви. Це використовування церкви для своїх політичних цілей. Пошанівок для церкви &ndash; у щоденному житті, коли держава поважає церкву, а церква служить народові. Ми не віддаляємося від народу і свідомі своїх завдань у суспільстві.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Втім, позиція про &laquo;непідтримку&raquo; &ndash; декларативна. Адже важко простежити, що відбувається в кожній парафії і які політінформації проводять священики під час чи після літургії.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ні, прослідкувати неможливо, &ndash; в тому наша трудність. Але тут є ще ось який момент: що ви назвете &laquo;політінформаціями&raquo;? Ми не сміємо мовчати. Ні, ми не збираємося говорити, що треба голосувати за того чи за того кандидата. В жодному разі. Але ми повинні чітко сказати &ndash; і парафіянин хоче й має право почути це в церкві, &ndash; на що потрібно звертати увагу у своєму виборі. Церква мусить давати орієнтири. Скажімо, ми хочемо бачити владу, яка виявляє щиру любов до України. Ми хочемо бачити президентом людину, котра поводилася чесно, котра здобула авторитет політика, який дотримується слова перед народом, перед своїми виборцями. Ми мусимо про це говорити. Але не на користь того чи іншого кандидата. Під цей критерій може підійти один, три чи тридцять кандидатів. Нехай серед них і вибирають &ndash; але вже самі. Використання церкви у виборчих перегонах &ndash; це двосічний меч. Завжди, коли церква піддається спокусі пристати до якогось визначеного кандидата, &ndash; вона на тому програє. Не тільки в нашій країні &ndash; повсюди. І добре, що програє, &ndash; бо це вчить. Ми не є ані ідеологами, ані суддями, проте наше завдання сказати: вважайте, люди, не продавайте своїх голосів і своїх душ.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Виборець &ndash; відповідальний за те, що обрана людина робитиме на своїй посаді. Це питання етики, і воно особливо нелегке зараз, коли попереду в нас вибори майже без вибору.</strong></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Історія повна складних моментів. І вона нас навчила розуміння, що жодна ситуація не є безвихідною й настільки трагічною, що залишається лише впасти у відчай. Так, сьогодні ми перебуваємо в такому собі глухому куті. Але ж це не є щось нечуване в історії &ndash; чи нашій, чи інших країн. В такі моменти питання вибору особливо складне. Але ми не є повністю позбавлені сили вибору. Треба шукати найкращий вибір, який є можливим. Добрий час, щоби визначити, що є для нас найважливішим. Ми свідомі того, що переживаємо не найкращі часи, і тому Всеукраїнська рада церков звертається до народу із закликом молитися. Молитва має велику силу. Молитися не за таку чи таку розв&rsquo;язку. Не мета молитви &ndash; радити Богові, як воно буде краще. Не думаю, що Бог потребує радників. У своїй молитві ми мусимо поручити Богові свою ситуацію, &ndash; він бачить вихід із нашого глухого кута краще за нас. Він уміє розв&rsquo;язувати будь-що. Це надзвичайно важливо &ndash; свідомо залишити вибір Богові. Воно може звучати не по-земному. Воно і не є цілком по-земному, але що з того, якщо ми віримо в Бога, віримо, що він є володар історії. Навіть якщо прийде до влади неприємна нам людина, за котру б ми не голосували, &ndash; мусимо подумати: якщо так дав Бог, то це є вихід. Може, не такий, який ми вважали найкращим. Утім, це не заклик згорнути руки й чекати, коли все само собою станеться як слід. Ми поручаємо Богові наше майбутнє, нашу долю, але ми люди, отже &ndash; мусимо діяти. Є така англійська приповідка: працюй так, ніби все залежить від тебе, а молися так, ніби все залежить від Бога. То є містерія &ndash; люди роблять свої кроки, а за ними стоїть Бог.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Розмовляла Катерина Щоткіна<br /><a href=\"http://www.dt.ua/3000/3690/67613/\" target=\"_blank\">№ 42 (770) 31 жовтня &ndash; 6 листопада 2009</a></em></span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">http://www.dt.ua/<br /></span></span></p>','Блаженнійший Любомир Гузар: про єдність і гідність','uk',1893,3376,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6250,200,20141102082034.jpg\" alt=\"\" width=\"200\" height=\"112\" />ЛЮБІ ДІТИ!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Запрошуємо Вас до участі у катехизації дітей та дорослих, яка відбуватиметься щонеділі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О 11.00 год. за адресою вул.Мазепи,6 (Фундація&nbsp;ім. Ольжича).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>За додатковою інформацією звертатись до Світлани Заяць: 0672207970</em></span></p>','2014-11-07 22:06:37',NULL,'P','<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ДОРОГІ БАТЬКИ! </span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">ЛЮБІ ДІТИ!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,6250,200,20141102082034.jpg\" alt=\"\" width=\"200\" height=\"112\" />Запрошуємо Вас до участі у катехизації дітей та дорослих, яка відбуватиметься щонеділі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">О 11.00 год. за адресою вул.Мазепи,6 (Фундація&nbsp;</span>ім. Ольжича).</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em><span style=\"text-decoration: underline;\">Катехитичні групи:</span></em></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діти до 8 років (катехит Світлана і Орися).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Приготування до Св. Тайн Покаяння та Урочистого Святого Причастя (катехит с.Філомена і Аня).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діти, які вже приступали до Св. Тайни Покаяння (катехит Оксана).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Зустрічі з батьками (катехити Ірина і Руслана).</span></li>\r\n<li><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Спільнота молоді (від 14 і старше) - о 11.00 год. в катехитичному центрі на Аскольдовій (катехит Ірина).</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><em>За додатковою інформацією звертатись до Світлани Заяць: 0672207970</em></span></p>','ОГОЛОШЕННЯ','uk',4767,6250,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Дорогі парафіяни, читачі нашої електронної газети \"ОРАНТА\" до вашої уваги листи жінок, які відбувають покарання.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Кожен лист окрема історія, окрема трагедія і церква вчить бути милосердними до тих хто чинив злочини, дати їм можливість покаяння і в такий спосіб відплатити тим хто постраждав. В неділю&nbsp; Блудного сина ми почуємо про безмежне батьківське милосердя. В цей день церква просить провести збірки добровільних пожертв-допомоги людям, які відбувають покарання в тюрмах.</span></span></p>','2011-02-13 20:46:23',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Слава І</span></span><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">сусу Христу!</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Дорогі парафіяни, читачі нашої електронної газети \"ОРАНТА\" до вашої уваги листи жінок, які відбувають покарання.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Кожен лист окрема історія, окрема трагедія і церква вчить бути милосердними до тих хто чинив злочини, дати їм можливість покаяння і в такий спосіб відплатити тим хто постраждав. В неділю&nbsp; Блудного сина ми почуємо про безмежне батьківське милосердя. В цей день церква просить провести збірки добровільних пожертв-допомоги людям, які відбувають покарання в тюрмах.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Наша парафія св. Миколая Чудотворця вже 20 років допомогає увязненній жінці-матері і їх покинутим дітям. Просимо вас в часі Великого посту допоможіть зібрати пасхальний кошик радості Воскресіння Христового для тих хто увязнений.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Потребують жінки: придмети гігієни, продукти харчування, халва, згущене молоко, цукор, солодощі, теплий одяг, книги, ручки, зошити, конверти,марки, кипятильники, вітаміни і ліки за рецептами санчастини.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Ми потребуємо волонтерів для написання листів, підготовки посилок, відвідання тюрем. В часі Великого Посту будьте милосердні.</span></span></p>\r\n<p><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Священник Ігор</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><strong>Лист Маргарити Є., що відбуває покарання в Чернігівській  виправній колонії</strong><br /><br />Христос посреди нас!<br />&nbsp;Господи, благослови! Здравствуйте, о.Игорь! Мир Вам и спасение.<br />&nbsp;Простите меня грешную, но я с первых строк своего письма хочу Вас от всей души и чистого сердца поблагодарить и благодарю Вас от своей доченьки и Сергея Федоровича. Они мне написали, что посылочку от Вас получили и моя доченька за Ваш подарок была очень рада. Спасибо Вам за поддержку. Сергей Федорович мне написал, чтобы я Вас поблагодарила и за его извинилась что не смог сам написать. Ваш адрес прибрал и сам не найдет. А тут я очень переживала и каждый час прошу у нашего Господа, чтобы дал силы сейчас особенно. Помню старую присказку одна беда в дом не приходит. &nbsp;<br />&nbsp;О.Игорь, сейчас хочется поделиться своим горем с Вами. Доченька сейчас уже дома её выписали с больницы 29 сентября а почти месяц лежала в реанимации. Катались с детками на велосипеде с горы, упала, ушибла сильно головку. Забрала &ldquo;скорая помощь&rdquo; без сознания, в сознание не приходила три дня. Отец Игорь но по моему Сергей Федорович и его дочка не всё мне пишут. Забирали её в больницу соседи которые живут с нами на одной улице помогли нам и одноклассники моей доченьки все собрались чтобы моей доченьке сделали операцию черепно-мозговую. Сергей Федорович всё письмо винит себя за то, что не усмотрел я ему написала что тут как бы ни старался не уследишь. Не знаю даже как-то и мне  неловко, столько им навалилось. Но Господь какой же он всё таки милостивый предусмотрел всё. Отец Федорович и его дочь пишут мне что они в больнице только и просили у Господа чтобы всё обошлось хорошо и не забирал у меня что осталось родное моё. Спасибо, как никто они всегда были и есть рядом а я как соломинка за которую держится утопающий. Я бы наверное не перенесла бы если бы ещё и малышку потеряла она для меня всё. Я всегда говорила пускай у меня болит только она пускай растет здоровой. Пока ещё дома после больницы в школу не ходит а может скорей всего уже врачи разрешили ведь письмо получила 11.10 а сегодня 17.10.&nbsp;<br />&nbsp;Отец Игорь и сейчас очень интересная и хорошая новость. Я читала сама улыбалась. Получили посылочку от Вас, сначала они не могли понять ничего а потом вспомнили что я ведь писала за Вас. Зовёт Сергей Федорович мою доченьку Кристиночку и говорит что это всё и весь подарочек ей от Вас. Спрашивает а где Вы живёте? Он ей говорит далеко почти где мама, а кто такой? Рассказывает ей всё о верующих о Боге. Вечером сидели на лавочке, на улице уже читали &ldquo;Новый завет&rdquo;. Вообще у Сергея Федоровича много Библий. Он часто посещает церковь придерживается всех постов. Знаете о.Игорь мне таких людей наверное сам Бог послал. Родители были фивые он всегда мимо не пройдет а сейчас дак совсем мне они как самые близкие.&nbsp;<br />&nbsp;Сейчас не знаю даже что ему посоветовать, пишет чем только не растирал е помогает. Ходит уже очень плохо, у него закупорка вен на обе ноги, а в больницу не любит ходить. Говорит не знает уже чем растираться, кто что подскажет. У него давно уже закупорка вен, его жена была жива говорила что нужно уходить с тяжелых работ а он так всё до самой пенсии работал трактор, комбайн.<br />&nbsp;Хвалят мою доченьку, пишут по хозяйству везде, посуду помыть кричит сама, подмести в доме и во дворе тоже я будусама. Она Сергея Федоровича зовет дедом а его дочь зовет тё-а-тё. Услыхала, что они разговаривали между собою надо бурки ехать заказать сшили у него нога больная нужно только на заказ а моя малышка услыхала и говорит: я тебе деда сама их сошью.<br />&nbsp;Ну вот наверное на этом ограничусь. А Вам о.Игорь от всего своего и ближних чистого сердца здоровья Вам, мира и Божьей помощи.<br />&nbsp;Ангела Хранителя.<br />&nbsp;				р.б. Маргарита                          &nbsp;<br />&nbsp;Буду молиться за Вас.<br />&nbsp;Бог послал мне хороших людей и очень хорошо. Здесь мы все живём ограниченное время, как трава &mdash; сегодня взошла, а завтра посохла, так и наши дни пройдут, но вечность &mdash; никогда.&nbsp;<br />&nbsp;Пусть хранит Вас Господь Бог!				17.10.10 г.<br /><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /><strong>Лист Лариси М., яка відбуває покарання в Чернігівській виправній колонії</strong><br /><br />&nbsp;Мир Вам, благодать и любовь от Господа Нашего, Иисуса Христа!<br />&nbsp;Дорогие мои, я решила написать о себе и о том, как Бог пришел в мою жизнь.&nbsp;<br />&nbsp;Мне 40 лет, сейчас я нахожусь в колонии, отбываю срок.<br />&nbsp;С 20 лет я употребляла наркотики и третий раз нахожусь в колонии за это. Потребление наркотиков влекло за собой уголовные дела, сердце ожесточалось, здоровье рушилось. Наркотики и их добыча были на первом плане. Таким образом я потеряла связь с дочкой, которая у меня единственный родной человек, так как родителей и родственников нет, а главное я потеряла смысл жизни. На меня обрушилась куча проблем &mdash; долг за квартиру, из которого не выбраться, проблемы с милицией и всякие неизлечимые заболевания. В общем, жить я не хотела и выхода не видела. С таким настроением я попала в колонию со сроком 5 лет 9 месяцев.<br />&nbsp;Если объяснить свое состояние другими словами, то я чувствовала себя в грязном болоте, омут которого затягивает и выбраться оттуда невозможно. Пришло полное уныние и отрешённость. Вдруг я начала задумываться, я начала пересматривать свой никудышний жизненный путь. В мою жизнь приходили люди, которые мне рассказывали о Боге и жизнь которых есть добрые свидетельства для меня, так как Господь их исцелил, поднял и благословил. Из больных и погибающих наркоманов получились жизнерадостные и здоровые люди.&nbsp;<br />&nbsp;Всё это начало всплывать в моей памяти, я начала задумываться о Боге, о том, что я могу рискнуть и довериться Ему. Ведь всё равно мне терять нечего! Но так сразу принять решение о покаянии я не могла. Я понимала, что это очень ответственное решение, поэтому я точно должна решить. Но наш Господь &mdash; Он  чудный и премудрый, Слава Ему! Он чудным образом посылает мне Слово Своё через мою давнюю подругу, которая была наркоманкой и в 2002 г. покаялась и служит Господу сейчас. Она чудным образом нашла меня здесь, в колонии, через неё проговорил в моё сердце Бог: &ldquo;Дочь, возвращайся в отчий дом, Я жду тебя&rdquo;. Эти слова очень тронули сердце моё и я пришла к Господу, покаялась, попросила Его простить меня и взять мою жизнь под Свой, Божий контроль.<br />&nbsp;После этого уже прошел год, летом я приняла водное крещение и дала обещание Господу доброй совести.<br />&nbsp;Оглядываясь назад я благодарю Господа за то, что Он нашел меня в этом грешном мире, увидел моё бессилие, безнадежность и отчаянье, протянул мне руку помощи и даровал спасение.<br />&nbsp;Верой в искупительную жертву Иисуса Христа я получила прощение от грехов.<br />&nbsp;Сейчас в мою жизнь пришло желание жить, я хочу принести пользу людям, я хочу своей жизнью и делами прославлять Господа Нашего, рассказывать людям о Его нескончаемой любви.<br />&nbsp;Господь даровал мне исцеление, Он освободил меня от табакокурения, сквернословия, обмана и многого нехорошего, наполняет мою жизнь. Интерес к наркотику сменился на познание Библии, я прошу Бога открывать мне разумение к Писанию, я верю Слову Бога. Иисус пообещал тем, кто идет за ним, что &ldquo;Никто не похитит их из руки Моей, не погибнут вовек&rdquo; (Иоанна 10:27-29) и жизнь вечную. Я поверила Иисусу Христу и иду за Ним, это смысл жизни. Верю, что Господь воссоединит нас с дочкой, молюсь, верю в силу молитвы.<br />&nbsp;Это я написала, чтобы засвидетельсьвовать о Слове Божьем в жизни своей! Чтобы Вы уверовали &mdash; только в Боге спасение.<br />&nbsp;Дорогие мои, Христос любит Вас, Вы очень ценны для Него, воля Его, чтобы никто не погиб, но имел жизнь вечную.<br />&nbsp;Да благословит Вас всех Господь Бог наш покаянием в жизнь вечную!<br />&nbsp;	С любовью Христа,<br />&nbsp;					Лариса.  &nbsp;<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /><strong>&nbsp;Лист до матері, яка відбуває покарання в Чернігівській виправній колонії.</strong><br /><br />Привіт, моя мамочка!<br />&nbsp;Вибач, що так довго не писала. У мене просто навчання, пари, екзамени. Бабушка Оля мене вигнала з Коростеня і я не дождалась 1 вересня і уїхала.&nbsp;<br />&nbsp;Мамочка! Повір у Господа і все буде добре. Бог любить тебе. Я не можу ніяк паспорт зробить, грошей немає.<br />&nbsp;Мама, вибач, що без откритки. Мамочка, я тебе люблю дуже сильно, скучаю і хочу бачить. Я знаю, що ти дуже ждала пісьмо. Нічого, ти вийдеш і усе буде добре.<br />&nbsp;У мене є подруга Яна Б., її мама тоже сидить у Чернігові. Мама, пришли мені свою фотку. А я буду робить паспорт, тоже тобі пришлю. Мамуся, вибач, що не кладу конверта, бо немає. Мама, я останній год уже вчуся. А потом куди? Не знаю.<br />&nbsp;В мене є фотографія, там, де ти з Ірою (хрещеною). Помниш, вона біля серванта висіла? Я її забрала і кожен вечір дивлюся на неї.<br />&nbsp;Мама, я тебе люблю.<br />&nbsp;	Пиши.<br />&nbsp;				Обнімаю.<br />&nbsp;		Жду.<br />&nbsp;			Люблю.<br />&nbsp;	Скучаю.   &nbsp;<br />&nbsp;					Цілую.				&nbsp;<br /><br /><br />&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\">Мир Божий и благодать Господа нашего Иисуса Христа&nbsp;<br />да пребудет с Вами вовек!<br />&nbsp;Здравствуйте отец Игорь! Я очень благодарна за Ваше письмо и за поддержку мне и моей маленькой доченьке Вике. Живёт моя доченька с моей бабушкой Панченко Евдокией Яковлевной которой 5 сентября, Слава Богу, исполнилось 82 года, а моей Вике 29 марта исполнится 9 лет. Она занимается танцами, бабушка писала, что её даже по херсонскому каналу по телевизору показывали. Слава Богу, что у меня еть такая бабушка и такие дети. Жвет моя Вика в Херсонской области, г.Таврийск, ул.Коцюбинского, дом 53.<br />&nbsp;Получив Ваше письмо с предложением написать статью, мне сразу легли на сердце строки и я их Вам отсылаю, это говорит моё сердце, которое исцелил наш Господь.&nbsp;<br />Тупик<br />Господь! К Тебе взываю я с мольбой &mdash; прости! Прости! За то, что без Тебя жила, прости поступки и слова, прости насмешки и угрозы злые! Не знала я тогда, что так нельзя, что все равны между собою, что сотворил Ты всех с любовью и надеждой. &nbsp;<br />А я Твою надежду растоптала, пошла не тем путем и оказалась в тупике.&nbsp;<br />И только здесь оставшись на один с собою я осознала, сколько горя принесла родным и сколько обижала я людей таких же, как и я, Тобою сотворенных.<br />Живу сейчас я в этом тупике, а рядом зависть, ненависть и злоба. Всё то, к чему стремилась я сама.<br />Живу не ведая, что ждёт меня во тьме, а сердце бьётся, бьётся, бьётся в тесноте и рвётся выскочить на волю. Ему не место в тупике, к цветущим тянется дорогам, к любви и радости блаженной, к чистоте.<br />И яркий лучик света нашел меня во тьме, коснулся сердца моего и заживил все раны, омыл его и обелил, наполнил вечною любовью.<br />И только в этом тупике я до конца узнала, что не одна я, что со мною мой любящий Отец, Создатель мой и Избавитель.<br />Теперь живу я не во тьме, живу в Святом сияньи Его Славы!<br />Склоняюсь пред Тобою, мой Отец и приношу Тебе всю честь и славу, хвалу и благодарность за любовь и искупленье всех моих грехов!&nbsp;<br />Вот так Господь выводит с тупиков, лишь повернись к нему и дай возможность взять тебя за руку, успокоить, обогреть и увести в цветущий &mdash; вечный Город.<br />&nbsp;Я очень благодарю Бога за то, что Вы приняли решение со мной переписываться и поддерживать меня и мою доченьку. Для меня это очень большая поддержка и духовная, и моральная, и материальная в отношении моего ребёнка. За медикаменты буду очень благодарна, так как я очень в них нуждаюсь. То, что бабушка Вам ещё не написала, её можно понять. Она одна дома, а выходить она никуда не выходит, у неё какая-то проблема с ногами, она делает всё сидя, а это время. Мне и то пишет раз в 2 или 3 месяца, хотя прошу её всегда ну напиши хоть пару слов о себе, о Вике и мне ведь удет спокойней, но я её стараюсь понять, да ещё и забыть может, что хотела сделать за день. Мне она написала с такой радостью за то, что Вы ей выслали и с такой благодарностью, меня попросила Вас поблагодарить и написала, что обязательно Вам напишет. Я очень Вас прошу, пожалуйста не оставляйте мою доченьку и Бабушку им очень нужна помощь, а я в данный момент ничем не могу помочь, а только сама нуждаюсь в медикаментах. Для меня самое главное это ингаляторы, сечас наш дотор назначил мне &ldquo;Биротен&rdquo; - это гормональный препарат, а взять его неоткуда. Но на всё воля Божья, если он мне нужен значит он у меня будет. Господь всё предусмотрит.<br />&nbsp;Вышивать я не умею, да если бы и умела то такие вещи у нас высылать не разрешают, а вот написать надеюсь могу, только Вы скажите примерно, что именно и напишите: стихи на духовную тему подойдут? А ещё можно написать статью небольшую но только мне нужно на это немного время.<br />&nbsp;Молитесь за меня и за мою семью, а я буду молиться за Вас, и за них, и за себя.<br />&nbsp;Будьте благословенны. Пусть все Ваши дела будут во Славу Божью!<br />&nbsp;	С любовью Христовой<br />&nbsp;					Татьяна 	 &nbsp;<br /><br /><br /><strong>Лист дитини, мати якої, Тамара Ш., відбуває покарання в Чернігівській виправній колонії. Дитина виховується в школі-інтернаті.</strong><br /><br /><br />Шановний священник Ігор.<br />&nbsp;Я отримав від Вас посилку. Дуже Вам дякую. Всі подарунки мені сподобались. Я дуже вдячний і Вам і мамі. Я дуже люблю свою маму і хочу, щоб ми були разом.&nbsp;<br />&nbsp;	До побачення. &nbsp;<br />&nbsp;	Ще раз дякую.&nbsp;<br />&nbsp;					Андрій.<br /><br /><br /><strong>Лист 1 Наталії Ф. з Чернігівської виправної колонії</strong><br /><br />Здравствуйте дорогой отец Игорь!<br />&nbsp;Пишет Вам Наталья и мой сынок Герман. Вы проигнорировали моё предыдущее письмо, но горе, что постигло моих детей и меня обязывает написать Вам. А в принципе мне то и писать некуда и некому. Я постараюсь вкратце описать свою ужасную беду, в чем сейчас я нуждаюсь. Только я прошу Вас ответьте мне сразу, пожалуйста! Так как у меня срок времени очень маленький &mdash; 3 месяца. А решать мою проблему надо начинать уже сейчас. Как Вы знаете из моих прежних писем, дома ждали меня двое старших детей: Настя (15 лет) и Влад (13 лет) и больная мама (70 лет). Так вот у мамы был инсульт. Она умерла. Я не могу описать что я чувствую. Но это очень страшное чувство. Всё перемешалось: боль, пустота, гнев на себя, страх, обида. Я не верю в это. Детей забрали на 3 месяца в приют. Потом если я не бъявлюсь &mdash; в интернат.&nbsp;<br />&nbsp;Ну а теперь я объяснюсь, почему пишу Вам. У меня через 3 месяца открывается моя первая льгота &mdash; УДО. Я знаю со слов своего отрядного что могу его пройти. Только есть одно НО &mdash; это иск. Общая сумма 3 тыс. гривен. Но мне можно выплатить хотя бы часть иска. Эта часть 1 тыс. гривен. Я знаю, Вы мне, когда потерялось наше фото у Вас, писали что деньгами не помогаете. Но я очень Вас сильно, слёзно прошу войдите в мою ситуацию, пожалуйста! Помогите мне сколько сможете. Сделайте один раз исключение. У меня есть шанс уйти раньше. Тем более сейчас когда дети нуждаются во мне как никогда. В позапрошлом году они похоронили дедушку, в том году своего папу &mdash; моего мужа, в этом году бабушка умерла. Слишком много беды для ихних маленьких сердец. Я пишу Вам с мольбой в сердце что Вы поможете всё же. Иначе мне не пройти эту комиссию по УДО. И ждать придется что нам с Германом, что Насте с Владиком конца нашего срока. Дом опечатан, но где гарантия что его не разберут по кирпичикам. Сейчас такое творится. И не боятся же. Тут оступилась, поймали и гнев на себя имеешь, и просишь у Бога прощения, и думаешь простит ли? А это как посмотришь новости в страхе замираешь от нечеловеческой жестокости и тупости.&nbsp;<br />&nbsp;Уважаемый, дорогой отец Игорь и все служители храма! Я прошу у Господа и у всех вас, прошу как женщина и как мать. От себя и от имён своих троих деток. Помогите осуществиться нашей мечте &mdash; нашей встрече! На которую у меня большие намерения. Помогите нам дорогие люди, братья и сестры встретиться нам четверым, родным не через полтора года а через три месяца. Настенька с Владиком как раз будут три месяца в приюте, а потом их в интернат переведут. Я не хочу чтобы их в интернат отдавали, я хочу их забрать домой. Мамы уже не вернуть но есть шанс вернуть то есть забрать детей.<br />&nbsp;Дорогой отец Игорь, прочитайте моё письмо всем вслух. Я буду ждать что хоть кто нибудь но отзовётся на крик моей души. Поможет надеюсь хоть кто-нибудь, хоть какой-нибудь копеечкой. И я пройду УДО и встретятся наши четыре сердца. Я долго жду этой встречи. Прошу помогите. Времени осталось уже совсем мало &mdash; 3 месяца. Я очень хочу верить в это чудо. Это действительно будет чудо.&nbsp;<br />&nbsp;Пусть Вас хранит и благословляет Всемогущий наш Господь.<br /><br />&nbsp;С искренним уважением<br />&nbsp;				              Наталья и мой малыш.&nbsp;<br />&nbsp;			30.09.2010 г.&nbsp;<br />&nbsp;С нетерпением жду ответ.   &nbsp;<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial, sans-serif;\"><span style=\"font-size: small;\"><br /><strong>Лист 2 Наталії Ф. з Чернігівської виправної колонії</strong><br /><br />Мир Вам и благословения Господа нашего Иисуса Христа!<br />Здравствуйте, дорогой наш отец Игорь&nbsp;<br />и все, пребывающие в Аскольдовой могиле!<br />&nbsp;<br />&nbsp;Не могу обойти вас своим скромным вниманием. На протяжении всего моего заключения, а это не маленький срок - два года, вы помогали мне с моим сыночком. Помогали также и остальным мамам с ихними детками. Я очень это ценю. Ведь вы нас не знаете вообще, ни разу в жизни не видели и с буквы Закона мы преступницы. Каждая из нас несёт своё заслуженное наказание. Только кому на корысть пойдёт этот исправительный срок, а кому впрок &mdash; это зависит от каждой личности индивидуально. И несмотря на все эти доводы вы проявляете своё милосердие, протягиваете нам руку помощи. Ваше внимание, доброта, забота, духовная пища, тёплые письма &mdash; пробуждают очерствелые души к светлому и хорошему, к слезам радости и к побуждениям творить добро и менять себя в лучшую сторону, быть похожими на вас. Не знаю, что испытывают другие, так как пишу сейчас исключительно о себе. Как же я могу быть вам не благодарна! Моё сердце открыто, мысли чистые и пишу я искренно. Что внутри меня, то я выношу сейчас на бумагу.&nbsp;<br />&nbsp;Вот подходит к завершению 2010 г. Для каждого человека этот год оставил свой отпечаток. Для меня он был слишком болезненным, тяжёлым и трагическим, так как я потеряла маму. Для кого-то он был хорошим, светлым с приятными событиями. Но все мы одинаково должны провести этот уходящий 2010 г. достойно! И с хорошим настроением встретить наступающий Новый 2011 год! Каким он будет и что с собой принесет, никто не знает, лишь Господь. По своей милости Господь благословит ешё один Новый год. На всё воля Бога! Дорогой наш отец Игорь, хочу пожелать Вам в Новом году: достижения планированных целей, духовного роста и мудрости, здоровья вам и вашим близким, милости и благословения любящего нас Господа! Новых, приятных перемен и изменений, если они необходимы, в лучшую сторону. Ну и нас не забывайте          Любите и будьте любимы, это ДАР и он ещё не каждому дан.<br />&nbsp;Уважаемый отец Игорь, я отправила вам наши фотографии. Я надеюсь вы их получили? И в завершение своего последнего письма в этом году хочу задать вам немаловажный вопрос. Так как наше освобождение приближается и очень быстро. Этот день не за горами. Я по поводу моего письма которое вы опубликовали в прессу. Что, всё так плохо? Никто так и не откликнулся? Это просто миф, в который я безнадежно верила? А сейчас пришло время забыть обо всём? Ответьте мне пожалуйста! Я очень волнуюсь и переживаю. Мне почему-то кажется, что все мои несчастья никогда от меня не отступят и будут преследовать всегда. Столько много всего произошло и ничего хорошего. То беда, то болезни, то неприятности, разочарования, то горе, то проблемы и т.д. А вот хорошего &mdash; ничего. И держусь я здесь благодаря Герману, он меня оживляет, вдохновляет, придаёт мне силы духа и бодрости. И очень люблю я ночные молитвы, после отбоя. Девчата все позасыпают, а я ещё всё общаюсь с Господом. И поплакать могу себе позволить, рассказать к чему пришла, чего не успела, чего  достигла а что хочу ещё достичь. Это мои лучшие молитвы.<br />&nbsp;Дорогой отец Игорь, то что вы писали за какой-то сюрприз чи подарунок для Германа на день Николая, то я ничего не получала.&nbsp;<br />&nbsp;Ну, буду ждать ответа. Пишите. Помолитесь за наше освобождение. На комиссию я пойду не 5-го, а 20-го января с 14.30 до 15.30 нас будут представлять. Кого положительно, кого отрицательно. У меня вроде всё хорошо, но нет единственной справки о прописке. А она решает многое. Помолитесь за нас! А мы вас любим с Герочкой.&nbsp;<br />&nbsp;До свидания в Новом 2011 году! Здоровья вам, милости Господней и благословения Его.<br />&nbsp;С искренним уважением<br />&nbsp;				              Наталья и мой малыш Герман.&nbsp;<br />&nbsp;			27.12.2010 г.&nbsp;<br /></span></span></p>','Блудний Син','uk',2315,3798,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молюся за Вас і прошу Божого милосердя, яке випрошуєте самі своєю вірою, надією, любов&rsquo;ю. Ви самі є співучасниками свого спасіння через покаяння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ми готові листуватись з Вами, та від тепер зворотного конверту підписувати не будемо. Ви самі визначатимете, чи спілкуватиметесь з нами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У нас немає такої можливості допомогти усім, ми можемо лише молитись за спасіння душ. Допомагатимемо ліками, допомагатимемо &laquo;покинутій дитині&raquo; через ваші материнські прохання. Кожна ваша співучасть: вишивання; малювання; дописи статей &ndash; будуть винагородженні. Прошу, будьте сильними у своїй вірі, Божа ласка не омине нас.</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З любов&rsquo;ю Ваш священик Ігор.</span></span></p>','2010-10-11 17:24:51',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Молюся за Вас і прошу Божого милосердя, яке випрошуєте самі своєю вірою, надією, любов&rsquo;ю. Ви самі є співучасниками свого спасіння через покаяння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Ми готові листуватись з Вами, та від тепер зворотного конверту підписувати не будемо. Ви самі визначатимете, чи спілкуватиметесь з нами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">У нас немає такої можливості допомогти усім, ми можемо лише молитись за спасіння душ. Допомагатимемо ліками, допомагатимемо &laquo;покинутій дитині&raquo; через ваші материнські прохання. Кожна ваша співучасть: вишивання; малювання; дописи статей &ndash; будуть винагородженні. Прошу, будьте сильними у своїй вірі, Божа ласка не омине нас.</span></span></p>\r\n<p style=\"text-align: right;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">З любов&rsquo;ю Ваш священик Ігор.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">МОЛИТВА ЩОДЕННОГО ПОЖЕРТВУВАННЯ </span></span></strong><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">ДЛЯ ЧЛЕНІВ АПОСТОЛЬСТВА МОЛИТВИ</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">О, Божественне Серце Ісуса! У злуці з тим наміром, з яким Ти на землі віддавав славу Богові і тепер щоденно віддаєш у Пресвятій Тайні Євхаристії, жертвую Тобі через Непорочне Серце Пречистої Діви Марії усі свої молитви, справи, слова, думки й витривалість у терпінню нинішнього дня у винагороду за всі зневаги, образи і кривди, завдані Тобі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Жертвую їх особливо за святішого Отця Папу Римського, за святу Церкву, за навернення грішників та в усіх намірах Апостольства молитви, призначених на цей місяць і на сьогоднішній день.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Пресвяті Серця Ісуса і Марії, спомагайте святу Церкву та Україну!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Святий Йосифе, Покровителю і Заступнику приятелів Ісусового Серця, моли Бога за нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Святий Архангеле Михаїле, святий Миколаю, святий Володимире, святий Йосафате, Заступники України, моліть Бога за нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">МІСЦЕВИЙ: Щоб люди в Україні під покровом Пресвятої Богородиці глибше усвідомили своє життєве покликання та вибір.</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Великий князь Ярослав Мудрий, бажаючи забезпечити державу та народ, віддав землю й людей під Покров Пресвятої Богородиці. Вона, сказавши один раз ангелу:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">&laquo;Ось я, слугиня Господня, нехай зі мною станеться за твоїм словом&raquo; (Як. 1, 38), ціле своє життя віддала у Господні руки, а разом з тим стала найдосконалішим&nbsp; інструментом Його Провидіння, через який Боже Слово увійшло в світ для спасіння людини. Її заступництво відкриває нам шлях до спасіння, тому Бог вчинив її царицею неба та землі. Кожний, хто вдається з молитвою до Богородиці та вдивляється пильно в її образ, навчаючись пізнавати й любити Божу волю, ніколи не осоромиться, віднайде правдиве життя та щастя. Сьогоднішнє суспільство великою мірою дезорієнтоване, людські цінності часто перевернуті &laquo;з ніг на голову&raquo;, особливо молоді люди бояться здійснювати життєвий вибір, йти назустріч поклику Божому, який кличе нас бути святими в будь-якому стані. Молімося разом, щоб Пречиста Діва Марія через свій Покров і заступництво випросила ласки, яка торкнулася б кожного серця, кожної людини в нашому народі, щоб ми могли більш усвідомлено та відповідально переживати своє покликання та сміливо,&nbsp; уповаючи на Боже Провидіння, здійснювати моральні вчинки в нашому житті.</span></span></p>','Допомога матерям і покинутим дітям ','uk',2172,3655,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\">&nbsp;</span></p>','2010-12-22 22:09:23',NULL,'P','<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','Сучасні твори про князя Аскольда','uk',2259,3742,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Молитва за українську родину</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Боже великий, Боже отців наших! Дай  нашому народові якнайбільше добрих, святих християнських родин. Дай нам таких батьків, які голосно й  відверто признавалися б до божественної Твоєї євангелії і до Твоєї служби. Дай  нам батьків, які для своїх дружин були б прикладом християнського життя,  правдивими опікунами та добрими провідниками в житті. Дай нам таких матерів, що вміли б  добре, по-християнськи виховувати своїх дітей, а для своїх чоловіків були б  поміччю, потіхою та доброю радою. Дай нам таких дітей, які були б  потіхою та славою батьків і красою свого народу. Благослови, всемогутній Боже,  український нарід. Даруй йому ласку вірно Тобі служити і доступити колись вічної  нагороди в небі, бо Тобі, Боже, у Святій Тройці єдиний, Отче, Сину і Духу  Святий, належиться вся слава, честь і поклін на віки вічні. Амінь.</span></span></p>\r\n<p>&nbsp;</p>','2008-12-08 18:26:14',NULL,'P','','','uk',771,1751,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Молитва, ранішнього намірення</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи, дай мені зустріти з душевним  спокоєм усе, що принесе мені цей день. Дай мені цілковито віддатися на волю  твою святу. На всяк час дня цього в усьому настав  і підтримай мене. Які б я не дістав (дістала) вістки  сьогодні, навчи мене прийняти їх із спокійною душею і твердим переконанням, що  на все - твоя свята воля. У всіх ділах моїх і словах керуй  думками моїми і почуттями. У всіх непередбачених випадках не дай  мені забути, що все Ти послав. Навчи мене просто й мудро ставитися до  всіх людей, нікого не ображаючи, ні не засмучуючи. Господи, дай мені силу перенести втому  і всі події цього дня. Керуй моєю волею і навчи мене  молитися, вірити, надіятися, терпіти, прощати й любити. Амінь.</span></span></p>','2009-02-26 08:42:49',NULL,'P','','','uk',708,1688,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6   16 СЕРПНЯ 2009 р.Б.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна  комісія Київської Архиєпархії УГКЦ&nbsp;  запрошує Вас взяти участь у паломництві  до <strong><em>Люрду (Франція),</em></strong> яке триватиме <strong><em> з 6 по 16 серпня. </em></strong> Вік  від 18 років.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Маршрут  подорожі: </span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>КиївПрагаПариж  Люрд (3 дні) МіланВіденьКиїв</strong></span></span></p>','2009-05-25 00:19:47',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6   16 СЕРПНЯ 2009 р.Б.</strong>&nbsp;<br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молодіжна  комісія Київської Архиєпархії УГКЦ&nbsp;  запрошує Вас взяти участь у паломництві  до <strong><em>Люрду (Франція),</em></strong> яке триватиме <strong><em> з 6 по 16 серпня. </em></strong> Вік  від 18 років.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\">Маршрут  подорожі: </span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>КиївПрагаПариж  Люрд (3 дні) МіланВіденьКиїв</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Вартість   </strong><strong><span style=\"text-decoration: underline;\">430  евро, </span></strong><strong>в  яку входить:</strong></span></span></p>\r\n<ul style=\"text-align: justify;\" type=\"disc\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виготовлення    шенгенської візи (35 &euro;+50грн)</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Страхування</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорога</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проживання    у готелях: Прага, Люрд (2 ночі), Відень</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> 4 сніданки,    2 обіди та  2 вечері</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рекомендована    програма</span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зголоситися  якнайшвидше, але не пізніше 1 червня.</span></span></li>\r\n<li><strong></strong></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Контактний  телефон  80661422737</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Володимир  і Оленка Мальчин</strong></span></span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n</ul>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','ПАЛОМНИЦТВО ДО ЛЮРДУ ','uk',1510,2566,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Бог не запитає</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Бог не запитає тебе, яким автомобілем їздив, але скільки людей ти          підвіз...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Бог не запитає тебе, яку площу мав твій дім, а скільки подорожніх          ти в ньому прийняв...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Бог не запитає тебе, як ти одягався, а запитає, скількох людей ти          зодягнув...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Бог не запитає тебе скільки грошей ти заробляв, але яким способом...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5. Бог не запитає тебе яку посаду ти займав, але чи ти працював чесно          і сумлінно...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Бог не запитає тебе, скільки ти мав приятелів, але для кого з них          був приятелем ти...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7. Бог не запитає тебе, в якому оточенні ти жив, але як ти ставився до          своїх сусідів...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8. Бог не запитає тебе, скільки разів ти говорив правду, але запитає          скільки разів ти збрехав.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9. Бог не запитає тебе, чому ти так довго зволікав з покаянням і виправленням.          Він з любов\'ю запровадить тебе до твого небесного житла.</span></span></p>\r\n<p>&nbsp;</p>','2008-12-26 19:09:39',NULL,'N','','','uk',888,1862,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересyя 2010 навчального шкільного року, дітям, які залишtні батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>','2010-09-23 20:15:00',NULL,'N','<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>З</strong><strong>віт наданої допомоги дітям, до початку 1 вересня 2010 навчального шкільного року, дітям, які залишені батьківської опіки (їх матері ув&rsquo;язнені). </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Болісно дивитися на страждання дітей, яких доля змусила терпіти. Економічна криза та багато інших негативних факторів, призвели до появи в нашому суспільстві безпритульних дітей.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ув&rsquo;язнені<strong> </strong>матері турбуються за своїх дітей, ось слова матері Алли, яка терпить покарання, просить допомоги в школу її донечці Діані.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пише мати, Діани: &laquo;<em>моей</em><em> дочери</em><em> Дианочке 12 лет, воспитывает ее мама. Мамае 72 года, очень болеет, нужны деньги, чтобы собрать в сентябре Диану в школу, а денег нет. Дианочке нужны школьные принадлежности, сумка ранец, ручки, тетради, фломастеры, линейки, блокнот, школьная форма, а у матери просто денег нет, и на ноги обуви нет. Позвонила я домой,</em> <em>ребенок расстроенный. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Отец Игорь, помогите собрать ребенка в школу, чтобы 1-го сентября она была счастлива. Господи, как мне жить с моей несостоятельностью? Помогите моей доченьке Дианочке.</em>&raquo;&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист матері Світлани, яка відбуває покарання в Чернігівській виправній колонії:&nbsp; &laquo;<em>У </em><em>меня</em><em> дома остался</em><em> маленький сынок</em><em>, ему</em><em> 7 лет. Он живет</em><em> с моей</em><em> мамой. Маме очень тяжело, а сын мой Женя идет в первый класс. Вы знаете, сколько надо сейчас денег на школу. Извините, но я пишу Вам с надеждой, что Вы поможете. Для себя я ничего не прошу, прошу, помогите моему сыну. Я знаю, что Бог со мной и он услышит мои молитвы.</em>&raquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мати Алла А&hellip; в листі до нас, просить допомоги її дитині Андрію, в нього закінчується навчання в Університеті, має відбутися випускний вечір, а у нього немає одягу і взуття.&nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуючи Богу, парафіяльна служба церкви Святого Миколая на Аскольдовій могилі в особі настоятеля церкви<em> </em>отця Іго, а також завдяки доброчинцям п. Лесі з родини О.Тихого, п. Олександру з Тернополя, п. Євстахію &nbsp;з Києва, яки допомогли коштами, ва які помогли коштами, служба придбала необхідні канцтовари, а також солодощі для потребуючих дітей. </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Багато сил, часу і коштів потрібно для такої праці в допомозі, але протягнута рука часто рятує і підтримує дитячі зболені серця. &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Волонтери від церкви Святого Миколая на Аскольдовій могилі п. Ганна, п. Марта, п. Людмила, п. Галина підготували допомогу дітям до школи.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ось, що входило в гуманітарні пакети: зошити в клітинку зошити в лінійку, ручки, альбоми для малювання, олівці кольорові, олівці звичайні, фарби акварельні, ручки кулькові, щоденники, а також ранці.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одягом&nbsp; і взуттям допомогли прихожани церкви.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також придбали гігієнічні товари: мило, зубні щітки, зубну пасту, шампунь. Солодощі дітям, цукерки та печиво.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подяка матері Алли&hellip; за допомогу її дитині &ndash; синові, який навчався в Інтернаті і закінчував навчатись. &laquo;<em>У мене не було меж радості, мені хотілось кудись бігти &ndash; щось робити, я плакала і сміялась.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Боже я не знаю, як Вам дякувати за Ваше розуміння, за допомогу моєму синові. Ранком на роботі всім жінкам розказувала, що є Бог і Він чує наші молитви і допомагає нашім дітям.</em>&raquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист дитини Андрія &ndash; &laquo;П<em>осилку від Вас я отримав, за яку Вам велике спасибі, Всі речі підійшли, а також дякую за солодощі. Костюм буде на випускний вечір. </em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Завдяки Вам от. Ігор, я до випускного вечора вже готовий. Нехай Бог Вас береже за Вашу доброту.</em>&raquo;.</span></span></p>\r\n<ol>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виготовили і вислали дітям гуманітарні пакети на суму <span style=\"text-decoration: underline;\">703,27 грн.</span>&nbsp; </span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В пакети вложили конверти з почтовими знаками на суму <span style=\"text-decoration: underline;\">20,40 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено за поштові коробки <span style=\"text-decoration: underline;\">102,00 грн.</span></span></span></p>\r\n</li>\r\n<li>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заплачено пошті за пересилку посилок <span style=\"text-decoration: underline;\">235,08 грн.</span></span></span></p>\r\n</li>\r\n</ol>\r\n<p align=\"right\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><span style=\"text-decoration: underline;\">Разом витрачено: 1&nbsp;060, 75 грн.&nbsp; </span></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Настоятель церкви св. Миколая на Аскольдовій могилі отець Ігор Онишкевич</span></span></p>','Звіт наданої допомоги дітям','uk',2151,3634,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі діти!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні усіх Вас закликаю молитися за мир і свідчіть про нього у своїх сім\'ях, щом мир став найбільшим благом на цій неспокійній землі. Я ваша Цариця світу. І ваша Мати. Я хочу вести Вас &nbsp;шляхом миру, який сходить тільки від Бога. <br /></span></span></p>','2009-04-26 21:48:57',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі діти!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні усіх Вас закликаю молитися за мир і свідчіть про нього у своїх сім\'ях, щом мир став найбільшим благом на цій неспокійній землі. Я ваша Цариця світу. І ваша Мати. Я хочу вести Вас &nbsp;шляхом миру, який сходить тільки від Бога. Тому, моліться, моліться, моліться. Щиро дякую, що відгукнулися на мій заклик.</span></span></p>','Послання Богородиці у Меджугорії 25 квітня 2009 року Божого','uk',1423,2479,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі читачі, дорогі парафіяни!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наша церковна служба милосердя Карітас подає до вашої уваги витяги з листів засуджених і їхніх покинутих діток Прошу вашої допомоги, щоб спільно допомогти потребуючим. Проявіть милосердя і відкликніться на їхні потреби. Відпишіть лист і ми перешлемо. Принесіть допомогу до Церкви з позначкою для кого і ми перешлемо. Я знаю, що сьогодні є інші проблеми як особисті так і суспільні. Але вірю, що з Божим Благословенням допоможемо цим жінкам у їхньому покаянні. Господи, поклич слуг твоїх до тюремного служіння. Допоможи Боже нам і тим хто кається чи з примусу, чи всім своїм серцем. Буду чекати ваших листів відповідей для засуджених. Бог благословить нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ваш священик Ігор, недостойний раб Божий.</span></span></p>','2011-06-29 16:29:17',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі читачі, дорогі парафіяни!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наша церковна служба милосердя Карітас подає до вашої уваги витяги з листів засуджених і їхніх покинутих діток Прошу вашої допомоги, щоб спільно допомогти потребуючим. Проявіть милосердя і відкликніться на їхні потреби. Відпишіть лист і ми перешлемо. Принесіть допомогу до Церкви з позначкою для кого і ми перешлемо. Я знаю, що сьогодні є інші проблеми як особисті так і суспільні. Але вірю, що з Божим Благословенням допоможемо цим жінкам у їхньому покаянні. Господи, поклич слуг твоїх до тюремного служіння. Допоможи Боже нам і тим хто кається чи з примусу, чи всім своїм серцем. Буду чекати ваших листів відповідей для засуджених. Бог благословить нас!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ваш священик Ігор, недостойний раб Божий</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Звіт №5 про НАДХОДЖЕННЯ&nbsp; ЛИСТІВ 19.06.2011 р. &mdash; 25 листів</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em><span style=\"text-decoration: underline;\">Лист від дітей ув\'язнених (подяка за подарунок)</span></em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>всё что было в посылке мы получили большое вам спасибо от души</strong>...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Катя, Даша, Юля&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; м.Антрацит Луганської обл.(діти ув\'язненої Чернігівської ВК Тетяни Б.)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em><span style=\"text-decoration: underline;\">Лист від батька багатодітної родини &mdash; 8 дітей &mdash; з м.Червонограда Львівської обл. (подяка за допомогу)</span></em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Сердечно дякуємо за допомогу</strong> і просимо про молитву за нашу родину. Як ласка Божа, <strong>може б нам приїхати до Вас у м.Київ по святих місцях з дітьми</strong>...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Також просимо про молитву за хвору Марію, нашу сусідку&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Валерій, Оксана</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; діти: Тарас, Роман, Микола, Володимир, Іван, Марія, Святослав</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&nbsp;</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених ( з них вперше звернулись 5 осіб) </span></em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; 16 листів </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &mdash; 1 лист </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чорноморська виправна колонія ЧВК-74 (м.Одеса) &mdash; 2 листи</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Сніжнянська виправна колонія СВК-127 (Донецька обл.)&mdash; 1 лист</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Качанівська виправна колонія КВК-54 (м.Харків) &mdash; 1 лист</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дніпродзержинська виправна колонія (Дніпропетровська обл.) &mdash; 1 лист</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Орджонікідзевський виправний центр (Дніпропетровська обл.) &mdash; 1 лист</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Благодарю Вас за помощь</strong>. Спасибо большое за всё, но только очень Вас прошу не высылайте мне вещи...многое из вещей нам не положено, их просто не отдают на руки...Если будет возможность мне помочь, то нуждаюсь я в следующих вещах &mdash; дезодорант от пота, бальзам для волос, кипятильник, продукты питания по возможности.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ...<strong>Мне очень часто снятся дедуля с бабулей покойные, я очень Вас прошу помолитесь пожалуйста за них и если можно поставьте свечки.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>...<strong>Высылаю Вам новый рисунок</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена К. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Высылаю вышиту иконку маленькую.</strong> Я верю что Вам она понравится, она вышита от всей души.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ...если сможете выслать...&rdquo; (перелік продуктів і засобів гігієни)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Раїса О. (Чорноморська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Ко мне прицепилась какая-то нечисть и я не знаю как с ним бороться это продолжается уже много лет...<strong>Помогите мне нужен священник-экзорцист</strong>&rdquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Алла Х<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я отбываю наказание в Чернигове. В этом году <strong>10 марта я стала мамой</strong> <strong>и родила своего первенца</strong>, которого назвала Богданом. В день праздника Святой Троицы буду крестить сына и он будет под Божьей защитой. ...<strong>Мы нуждаемся в памперсах 3-й размер, продукты питания и мыльные</strong>...&rdquo;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Марина Т<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;01.06. получила от бабушки письмо в котором просит Вас поблагодарить за Вашу помощь,<strong> они получили в апреле от Вас посылку для Викунечки. Огромное Вам человеческое спасибо за Вашу помощь и понимание</strong>. Ваша помощь им очень нужна. <strong>Я отсылала Вам стих... Хочу вас попросить если конечно возможно, помочь мне с ингалятором &ldquo;Беротек&rdquo; и по возможности не оставляйте мою доченьку и бабушку</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тетя<em>на С. (Дніпродзержинська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Срок у меня восемь лет. Имею дочь 2004 г.р. Каждый день, перед&nbsp; сном вот уже в течение 3- лет читаю Библию... вникаю тяжеловато. <strong>Прошу Вас пожалуйста выслать литературу для более ясного понимания Библии</strong>... <strong>Очень хочется исповедаться</strong>...почему-то чувствую что общаясь с вами мне будет легче здесь находиться&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Оксана<em> З. (Качанівська ВК, м.Харків)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Одержала від Вас посилочку, за яку щиро дякую. Вишивати вже почала... Вишліть ще 3-4 схеми.</strong>.. В\'язати не має ниток, спиців. Я б могла гарні безрукавочки пов\'язати... <strong>В мене зараз велика нужда я зовсім боса</strong>, кип\'ятильник, шампунь...&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ольга<em> В. (Збаразька ВК, Тернопільська обл.)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...получила от Вас письмо, которому была безумно рада, благодарю за Вас Бога... <strong>Вы очень многое для нас делаете.</strong> <strong>Спасибо Вам за то что помогли пересмотреть свою жизнь и сделать переоценку ценностей жизненных</strong>, я знаю что без Бога этого бы никогда не произошло... Даю снова адрес доченьки&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тетяна Б<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Я очень Вам признательна что Вы сыновьям моим пытались дозвониться</strong>, я тоже пыталась но нет связи... письма друзьям писать узнать о них у меня нет конвертов и негде взять... Я не знаю что могло с ними случиться, но не могли они вот просто забыть что у них есть я, ведь кроме их у меня и меня уних нет никого больше родственников... <strong>В июле мне ехать на Львов ВТЭК (комиссия на группу) у меня нет ничего самого необходимого</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Олена Р<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>У мене є дочечка Настінька... 9 міс.</strong> Я довго не могла мати дітей. Но коли я віддала цю проблему в Божі руки, Господь послав мені це ангелятко. Мені 30 років, но я втратила все... <strong>У моєму житті є Господь</strong> і я вірю, що з Господом я не буду ні в чим нуждатись. <strong>Правда, дуже тяжко іти за Господом.</strong> Но Господь бачить і знає, що нам потрібно. Я Вас дуже прошу <strong>помоліться за мене і мою дочечку</strong>... <strong>Мені потрібно зробити паспорт,</strong> щоб коли я вийду з цих стін, я могла оформити Настіньку. Паспортистка сказала, що <strong>потрібно 53 гривні</strong>. Якщо у Вас є така можливість, то <strong>допоможіть</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Людмила К<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>Мне 42 года, я нахожусь в колонии с маленькой доченькой Лилей 18.04.2011 г.р. Помогите мне сделать паспорт.</strong> Мне до конца срока осталось не так уж много 23 августа 2012 г., а условно досрочное освобождение выпадает на декабрь этого года, а без паспорта не отпустят. Очень прошу ради моей доченьки помогите мне, чтобы мы могли поскорее оставить эти стены&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Б. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>до бесконечности благодарю Вас за ту помощь которую Вы оказываете моим деткам </strong>(6 діток під опікою бабусі)... <strong>молитесь за моих деток и маму</strong>... Если на то будет воля Ваша то <strong>для Инги </strong>(немовля, народжене в колонії)<strong> вышлите: памперсы №2 и детскую косметику</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Олена Т<em>. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Хочу Вас поблагодарить и поблагодарить Господа Нашего за посылочку и размноженные фотографии. А в особенности хочу поблагодарить Вас за молитвослов</strong>...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отец Игорь, <strong>от лица всех мам </strong>(которые находятся у нас в колонии и от детишек &mdash; а всего этой радости 32 человечка) &mdash; <strong>огромная просьба?!</strong> Не могли бы Вы осуществить маленькое чудо?! <strong>1 июня (на день защиты детей) наших детишек пофотографировали &mdash; скинули на диск &mdash; а сделать фотграфии не за что</strong>! Денег нет ни у кого т.к. все находятся в декрете и не работают. В этом году многие освобождаются, очень бы хотелось забрать с собой на память фото. Отец Игорь, <strong>если у Вас есть возможность разрешить нам эту проблему &mdash; то напишите пожалуста мне ответ. И мы тогда сможем переслать Вам диск</strong> &rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Оксана Ш. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>Я умею вышивать. Для этого нужно: иголки, мулинэ, ткань</strong>...У меня есть дочь Дарья, ей 16 лет, но я с ней не имею переписку, т.к. не хочу чтоб она знала где я нахожусь. Когда освобожусь, попробую с ней поговорить, я знаю, что Боженька, Отец Наш Небесный даст мне силы и вразумления как поступать в дальнейшем... <strong>Молитесь за мою дочь &mdash; Дарью и за меня &mdash; р.Божью Светлану</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Б. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>Пожалуста поможить мне сделать паспорт... А моєї дочки Оксанки адрис Чернігівська обл. ...интернат Старо-Басань, год рождениє 1998 г р. 2 сентября.</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Надія Ф. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 34 года, родом я с г.Мелитополя. <strong>У меня сын Никита, ему 13 лет, воспитывают его мои родители-пенсионеры</strong>...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я случайно увидела, что некоторые девочки вышивают крестиком и бисером. У меня также появилось желание за няться этим делом и помочь Вам. Крестиком у меня не очень получается, а вот <strong>бисером я с большим удовольствием вышиваю, в чём Вас и хочу попросить</strong>... Не откажите мне в этой просьбе, пожалуйста&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія М. (Чернігівська ВК)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор Онишкевич!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пише до Вас Ганна.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я дуже Вам дякую за відповідь на мій лист, за те що всі сестри, браття і Ви молитеся за нас. Дуже Вам ще дякую за підтримку висилали сладості, одежу, мильне, <strong>Господу дякую що дав мені добрих людей які допомагали мені, як людям треба прощати, допомагати, любити.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отець Ігор мені привозили мою дочку Юлю, яку я родила в ЧВК-74 і її забрали в д/дом Перлина, їй 5 роки. Така розумна знає &ldquo;Отченаш&rdquo; цілий і &ldquo;Ангеле Божий&rdquo; віршів, пісень вміє рахувати, малювати, знає деякі букви я горжуся нею, дочка яка має зараз приїхати до мене її 20 років я знаю що Бог зі мною він мене не покине в біді, він дає дар мені зустрітися із своїми соколиками, ангеликами. Син мій Коля зараз займається дуже серйозною роботою &ldquo;Столяр&rdquo; робить ремонти в хаті і забере до себе мене йому буде 19 роки ми будемо з Божою помощю всі разом великою сім\'єю і Бог буде в нашому серці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дякую Вам всім за підтримку я дуже Вас люблю</strong>.<strong> Бувайте здорові Бог нас ніколи не забуде </strong>я знаю&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ганна Я. (Чорноморська ВК, м.Одеса)</em></span></span></p>','Звіт №5 про НАДХОДЖЕННЯ  ЛИСТІВ 19.06.2011 р. Божого — 25 листів','uk',2511,3994,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У КВІТНI 2011 Р. Б.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','2011-06-02 19:09:26',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ</strong></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>У КВІТНI 2011 Р. Б.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<table style=\"width: 458px; height: 809px; text-align: justify;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>№ з/п</strong></span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Пожертва на (для):</strong></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ігор Давидов<strong><em>&nbsp;</em></strong></span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Медикаменти для ув&rsquo;язнених жінок</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лесі (з родини О. Тихого)</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Придбання медикаментів для ув&rsquo;язнених хворих жінок</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег і Віра Кошіль</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тетяна Сікора, сини хворий Богдан та Іван</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпис храму</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Ільків</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потреби Церкви</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Марія Шпак</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дітям-сиротам до Великодня</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сергій</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оздобу храму</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяни</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олег Лужний</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Саджанці дерев</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юрій та Ліда</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Степан, Роксолана та Тарас</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дітям &laquo;Карітас&raquo;</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ірина та Марія з родиною</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій та Ліна Стаховські</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир та Марія</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розпис церкви</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Роїк</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потреби Церкви</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Курило</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Родина Турковських</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церкву за мир та любов</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир та Володимира</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потреби Церкви</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"40\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19</span></span></p>\r\n</td>\r\n<td width=\"300\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафіяни</span></span></p>\r\n</td>\r\n<td width=\"263\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На паски для дітей</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Милостиню роби згідно з тим,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>що маєш;</em></strong><strong><em>&nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Коли у тебе достатків повно,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Роби з них,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>&nbsp;</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>а коли мало маєш,</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>то не бійся з того дати.</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Бо так готуєш собі добрий скарб</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>На день потреби.</em></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></strong><strong>Товит 4. 8-9.</strong></span></span></p>','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ','uk',2471,3954,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Постанови Патріаршого Синоду Єпископів </span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Української Греко-Католицької Церкви</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>що відбувся у Львові-Брюховичах, </em><em><br /> <em>від 2 до 9 вересня 2008 року Божого</em></em> </span></span><strong><br /> </strong></p>','2008-09-16 11:46:22',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">На славу Святої, Єдиносущної, Животворящої і Нероздільної Трійці, Отця, і Сина, і Святого Духа і на добро ввіреного нам Божого люду. Амінь.</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">БОЖОЮ МИЛІСТЮ<br /> І В ПОВНОМУ СОПРИЧАСТІ <br /> З РИМСЬКИМ АПОСТОЛЬСЬКИМ ПРЕСТОЛОМ</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">+ ЛЮБОМИР <br /> Верховний Архиєпископ Києво-Галицький<br /> Української Греко-Католицької Церкви</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">Високопреосвященним і Преосвященним Владикам, <br /> Всечесним Отцям духовним, Преподобним Ченцям і Черницям <br /> та Мирянам Помісної Української Греко-Католицької Церкви</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">ПРОГОЛОШУЮ І ОПРИЛЮДНЮЮ</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">Постанови</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\">Патріаршого Синоду Єпископів Української Греко-Католицької Церкви <br /><em>що відбувся у Львові-Брюховичах, </em><em><br /> <em>від 2 до 9 вересня 2008 року Божого</em></em></span></p>\r\n<blockquote style=\"text-align: justify;\">\r\n<p><span style=\"font-size: small;\">1. Для належного доопрацювання та популяризації Катехизму Української Греко-Католицької Церкви:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">А. Затвердити <em>Статут</em> <em>Патріаршого катехитичного фонду &bdquo;Катехизм УГКЦ\"</em> на трьохрічний термін.</span></p>\r\n<p><span style=\"font-size: small;\">Б. Затвердити такий склад управління Патріаршого катехитичного фонду &bdquo;Катехизм УГКЦ\":</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">1. Преосвященний Владика Петро (Стасюк), голова Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">2. Преподобна сестра Луїза Цюпа, СНДМ, заступник голови Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">3. Всечесніший отець Богдан Тимчишин, секретар Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">4. Преосвященний Владика Павло (Хомницький), фінансовий секретар Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">5. Преосвященний Владика Василь (Лостен), член управління Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">6. Преосвященний Владика Річард (Семінак), член управління Фонду;</span></p>\r\n<p><span style=\"font-size: small;\">7. Преосвященний Владика Кен (Новаківський), член управління Фонду.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">В. Призначити Наглядову раду Патріаршого катехитичного фонду &bdquo;Катехизм УГКЦ\" у такому складі:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">1. Преосвященний Владика Володимир (Війтишин), голова Наглядової ради;</span></p>\r\n<p><span style=\"font-size: small;\">2. Преосвященний Владика Гліб (Лончина), член Наглядової ради;</span></p>\r\n<p><span style=\"font-size: small;\">3. Преосвященний Владика Богдан (Дзюрах), член Наглядової ради.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">Г. Фінансовий контроль над діяльністю Фонду доручити Синодальній ревізійній комісії.</span></p>\r\n<p><span style=\"font-size: small;\">Д. Поблагословити збір коштів на Патріарший катехитичний фонд &bdquo;Катехизм УГКЦ\" у всіх єпархіях та екзархатах Української Греко-Католицької Церкви та просити Блаженнішого Любомира видати у цій справі окреме звернення.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">2. Для активізації пасторально-місійної діяльності Української Греко-Католицької Церкви:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">А. Затвердити <em>Стратегію пасторальної діяльності УГКЦ на територіях, де немає її структур</em>.</span></p>\r\n<p><span style=\"font-size: small;\">Б. Створити при Главі УГКЦ Пасторально-місійний відділ на чолі з єпископом для забезпечення і координації у межах його компетенції душпастирської опіки над вірними нашої Церкви, які проживають на територіях, де немає її структур.</span></p>\r\n<p><span style=\"font-size: small;\">В. Головою Пасторально-місійного відділу призначити Преосвященного Владику Діонісія (Ляховича).</span></p>\r\n<p><span style=\"font-size: small;\">Г. Патріаршому економатові забезпечити належне фінансування діяльності Пасторально-місійного відділу УГКЦ.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">3. Створити <em>Комісію священичого виховання в семінаріях</em> у складі ректорів усіх наших семінарій - єпархіальних та монаших.</span></p>\r\n<p><span style=\"font-size: small;\">4. Головою&nbsp; <em>Комісії священичого виховання в семінаріях</em> призначити Преосвященного Владику Кена (Новаківського).</span></p>\r\n<p><span style=\"font-size: small;\">5. Прийняти з внесеними поправками та доповненнями <em>Програму священичої формації </em>Української Греко-Католицької Церкви на п\'ятирічний випробувальний термін.</span></p>\r\n<p><span style=\"font-size: small;\">6.&nbsp; Прийняти фінансовий звіт Патріаршого економату УГКЦ за 2007 рік.</span></p>\r\n<p><span style=\"font-size: small;\">7. Затвердити бюджет на 2009 рік, що його запропонував Патріарший економат УГКЦ, та податок на загальноцерковні потреби патріаршого рівня на 2009 фінансовий рік.</span></p>\r\n<p><span style=\"font-size: small;\">8.&nbsp; Прийняти з внесеними поправками та доповненнями документ &bdquo;<em>Про статус та повноваження єпископів Патріаршої курії</em>\"<em> </em>як частину Статуту синодального управління УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">9. Дати згоду на створення Відділу зовнішніх зв\'язків у складі Патріаршої курії УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">10. Встановити, що термін, на який обрані члени синодальних комітетів, становитиме три роки з правом одноразового переобрання.</span></p>\r\n<p><span style=\"font-size: small;\">11.&nbsp; Затвердити на наступні три роки такий склад синодальних комітетів:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">1.&nbsp;&nbsp;&nbsp;&nbsp; Літургійний комітет</span></p>\r\n<p><span style=\"font-size: small;\">Високопреосвященний Митрополит Лаврентій (Гуцуляк), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Гліб (Лончина), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Йосафат (Говера), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">2.&nbsp;&nbsp;&nbsp;&nbsp; Екуменічний комітет</span></p>\r\n<p><span style=\"font-size: small;\">Блаженніший Любомир, голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Іван (Бура), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">3.&nbsp;&nbsp;&nbsp;&nbsp; Комітет духовенства і монашества</span></p>\r\n<p><span style=\"font-size: small;\">Високопреосвященний Архиєпископ&nbsp; Ігор (Возьняк), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Мирон (Мазур), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">4.&nbsp;&nbsp;&nbsp;&nbsp; Комітет у справах мирян</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Володимир (Ковбич), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Василь (Семенюк), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">5.&nbsp;&nbsp;&nbsp;&nbsp; Комітет катехизації і християнської освіти</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Петро (Стасюк), голова Комітету; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Даниїл (Козлинський), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Софрон (Мудрий), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">6.&nbsp;&nbsp;&nbsp;&nbsp; Богословський комітет</span></p>\r\n<p><span style=\"font-size: small;\">Високопреосвященний Митрополит Іван (Мартиняк), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Володимир (Ковбич), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">7.&nbsp;&nbsp;&nbsp;&nbsp; Комітет беатифікацій і канонізацій</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Михаїл (Гринчишин), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Гліб (Лончина), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Ричард (Семінак), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">8.&nbsp;&nbsp;&nbsp;&nbsp; Канонічно-правничий комітет </span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Володимир (Ющак), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Давид (Мотюк), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">9.&nbsp;&nbsp;&nbsp;&nbsp; Комітет подружжя і сім\'ї</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Юліян (Вороновський), голова Комітету;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Василь (Семенюк), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">10.&nbsp;&nbsp; Пасторально-місійний комітет </span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Степан (Меньок), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Роберт (Москаль), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Мирон (Мазур), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">11.&nbsp;&nbsp; Комітет суспільних комунікацій і культури</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Тарас (Сеньків), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Кен (Новаківський), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Юліян (Ґбур), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">12.&nbsp;&nbsp; Комітет суспільної справедливості, екології та благодійної діяльності</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Микола (Сімкайло), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Петро (Крик), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Михаїл (Микицей), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">13.&nbsp;&nbsp; Комітет у справах молоді </span></p>\r\n<p><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Преосвященний Владика Ярослав (Приріз), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Браєн (Байда), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">14.&nbsp;&nbsp; Фінансовий комітет</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Володимир (Війтишин), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Високопреосвященний Митрополит Стефан (Сорока), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Степан (Хміляр), член Комітету.</span></p>\r\n<p><span style=\"font-size: small;\">15.&nbsp;&nbsp;&nbsp; Комітет емігрантів, біженців і моряків цивільного флоту</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Василь (Івасюк), голова Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Діонісій (Ляхович), член Комітету;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Павло (Хомницький), член Комітету.&nbsp;</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">12. Відновити мандат Ревізійної комісії у такому складі:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Василь (Лостен), голова Комісії;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Северіян (Якимишин), член Комісії;</span></p>\r\n<p><span style=\"font-size: small;\">Преосвященний Владика Василь (Медвіт), член Комісії.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">13. Додати до теперішнього складу Ревізійної комісії двох заступників та першим заступником члена Ревізійної комісії призначити Преосвященного Владику Павла (Хомницького), а другим - Преосвященного Владику Кена (Новаківського).&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">14. Призначити Преосвященного Владику Гліба (Лончину) головою Патріаршої літургійної комісії УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">15. Призначити Преосвященного Владику Петра (Стасюка) головою Патріаршої катехитичної комісії УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">16. Призначити Преосвященного Владику Тараса (Сеньківа) головою Патріаршої комісії УГКЦ у справах євангелізації.&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">17. Призначити Преосвященного Владику Ярослава (Приріза) головою Богословського відділу УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">18. Призначити Преосвященного Владику Давида (Мотюка) Генеральним керівником управління справедливості УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">19. Продовжити тему Євангелізації як головну тему Патріаршого Синоду 2009.&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">20.&nbsp; У рамках підготовки до Патріаршого Синоду 2009 року здійснити такі заходи:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">А. Доручити Патріаршій комісії у справах євангелізації підготувати на основі синодальних напрацювань 2008 року та загального навчання Церкви матеріали щодо євангелізації та надіслати їх до кожної єпархії та кожного екзархату.</span></p>\r\n<p><span style=\"font-size: small;\">Б. Рекомендувати правлячим архиєреям створити у своїх єпархіях та екзархатах Комісію у справах євангелізації.</span></p>\r\n</blockquote>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">В. До початку Великого посту у кожній єпархії та кожному екзархаті, там, де цього ще не зроблено, провести місцевий собор на тему євангелізації.</span></p>\r\n<p><span style=\"font-size: small;\">Г. На місцевих соборах на тему євангелізації прийняти короткострокові плани євангелізації і до кінця червня 2009 року відзвітувати Патріаршій комісії з питань євангелізації про їх реалізацію.</span></p>\r\n<p><span style=\"font-size: small;\">Ґ. На місцевих соборах на тему євангелізації підготувати та переслати до Патріаршої комісії з питань євангелізації до кінця червня 2009 року Довгострокову програму євангелізації єпархії/екзархату (на 5-10 років).</span></p>\r\n<p><span style=\"font-size: small;\">Д. Доручити Патріаршій&nbsp; комісії з питань євангелізації на підставі зібраних матеріалів опрацювати та представити Патріаршому Синодові 2009 року Пропозиції щодо довгострокового плану євангелізації УГКЦ.&nbsp; &nbsp;</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">21. Для належної підготовки священиків-місіонерів здійснити такі заходи:</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">А. У навчально-виховних програмах семінарій УГКЦ звернути особливу увагу на виховання євангелізаційних та місійних якостей семінаристів. &nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">Б. Доручити Комісії священичого виховання в семінаріях опрацювати програму місійного виховання.</span></p>\r\n<p><span style=\"font-size: small;\">В. Доручити Комісії священичого виховання в семінаріях опрацювати програму для семінаристів, які готуються до діяльності на місійних теренах.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">22. Визнати пріоритетами діяльності Патріаршої літургійної комісії опрацювання богослужбових книг у такій послідовності: Псалтир, Требник, Часослов (незмінні частини), Святкова Мінея, Святковий Октоїх, Великопісна та Квітна Тріодь (головні відправи), Служебник з Літургіями Івана Золотоустого, Василія Великого та Напередосвячених Дарів, Архиєратикон.</span></p>\r\n<p><span style=\"font-size: small;\">23. Затвердити текст <em>Декрету посланництва монаших осіб УГКЦ на місійну діяльність</em>.&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">24. Наступну сесію Патріаршого Собору УГКЦ&nbsp; присвятити темі &bdquo;Монашество\".&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">25. Провести сесію Патріаршого Собору УГКЦ, присвячену темі монашества, у Бразилії в 2011 році.</span></p>\r\n<p><span style=\"font-size: small;\">26.&nbsp;  Підготувати Загальний шематизм УГКЦ.</span></p>\r\n<blockquote>\r\n<p><span style=\"font-size: small;\">А. Опрацювання Загального шематизму УГКЦ доручити Департаменту інформації Патріаршої курії УГКЦ.</span></p>\r\n<p><span style=\"font-size: small;\">Б. Контроль за виконанням проекту Загального шематизму УГКЦ доручити Секретаріатові Патріаршого Синоду.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-size: small;\">27. Встановити, щоб у день початку Нового церковного року (1/14 вересня) звернути особливу увагу на охорону дарованого Богом&nbsp; довкілля (екологія).&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">28.&nbsp;  Проголосити бл. священномученика Омеляна Ковча покровителем душпастирів Української Греко-Католицької Церкви.&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">29.&nbsp; Продовжити на 2009 календарний рік тему покликання з особливим наголосом на священиче покликання.&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\">30. На прохання Генеральної управи Отців салезіян благословити початок беатифікаційного процесу отця Степана Чміля.</span></p>\r\n<p><span style=\"font-size: small;\">31. На прохання українських греко-католицьких єпископів Канади провести Патріарший Синод 2012 року в Канаді з нагоди 100-ліття прибуття туди першого єпископа бл. свщм. Микити Будки.</span></p>\r\n<p><span style=\"font-size: small;\">32.&nbsp;&nbsp;&nbsp;&nbsp; Наступний Патріарший Синод провести у Львові-Брюховичах, в днях від 29 листопада до 9 грудня 2009 року Божого.</span></p>\r\n<p><span style=\"font-size: small;\"><br /></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\">Ці постанови вступають у дію 30 вересня 2008 року Божого</span></p>\r\n<p><span style=\"font-size: small;\"><br /></span></p>\r\n<p align=\"right\"><span style=\"font-size: small;\">+ Любомир (Гузар) <br /> Верховний Архиєпископ Української Греко-Католицької Церкви</span></p>\r\n<p align=\"right\"><span style=\"font-size: small;\">+ Богдан (Дзюрах) <br /> Секретар Синоду Єпископів <br /> Української Греко-Католицької Церкви</span></p>\r\n<p><span style=\"font-size: small;\"><em>Дано у Києві </em></span><em><span style=\"font-size: small;\"><br /></span> <em><span style=\"font-size: small;\">15 вересня 2008 року Божого</span> </em></em></p>\r\n</blockquote>\r\n</span></span><strong><span style=\"font-size: 10pt; font-family: &quot;Arial&quot;,&quot;sans-serif&quot;;\"><br /> </span></strong></p>','Постанови Патріаршого Синоду Єпископів Української Греко-Католицької Церкви ','uk',494,1474,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва до </span></strong></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\">Св. Архангела Михаїла</span></strong></span></p>\r\n<p align=\"justify\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Святий Архангеле Михаїле, найславетніший князю й        полководцю ангельських воїнств! Прийди на допомогу людям, яким загрожують        сили темряви. Адже люди створені за образом Бога; відкуплені життям,        смертю й воскресінням Ісуса Христа, Його Сина;&nbsp;       призначені бути храмом Святого Духа. В тобі, свтий Михаїле, Церква        має свого Захисника й Покровителя. Ти ведеш спасенних до небесного щастя.        Проси Бога миру, щоб Він переміг владу сатани - сіяча неспокою і        лицемірства - і не дозволив йому тримати людей в рабстві та шкодити        Церкві. Нехай при твоїй допомозі Жінка, одягнена в сонце, Цариця ангелів,        розчавить голову антихристу й захистить Своїх земних дітей від його        підступів. Перший захиснику слави Всевишнього!        Занеси наші молитви до Бога й проси Його        змилосердитись над нами. Зв\'яжи сатану й кинь його в пекельну безодню, щоб        він більше не обманював народи. Вчини так, щоб ми все своє земне життя й        всю вічність звіщали гасло &laquo;Хто ж як Бог!&raquo;</span></span></p>\r\n<p>&nbsp;</p>','2008-11-17 09:12:11',NULL,'P','','','uk',657,1637,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><span style=\"font-size: small;\"><strong><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва до святого отця Миколая</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Всеблагий отче Миколаю, великий і предивний чудотворче! Ти своїми молитвами лікував усяку недугу і болість, ти своїм заступництвом визволяв від певної смерти, ти своєю опікою відганяв біду. Щиро благаю тебе, скорого потішителя тих, що є в горі і нуждах теперішнього життя: Поспіши й до мене із своєю поміччю, захисти мене й усіх людей від вогню й води, від голоду й пошести, від тяжкої хвороби і несподіваної смерти. Хорони мене через ціле моє життя, а особливо в страшну хвилину переходу з дочасного життя до вічности. Захисти мене тоді щитом твоєї молитви перед справедливим гнівом Христа Бога і допоможи мені, щоб я сподобився (сподобилася) навіки в небі, разом з тобою, прославляти й величати боже милосердя. Амінь.</span></span></p>','2011-12-16 09:32:53',NULL,'P','','','uk',829,1809,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: center;\"><strong><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Молитва за померлих</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Умилосердися, Господи Ісусе Христе, над душами тих, що вже за гробом, але ще потребують очищення, щоб у вогні терпіння та муки була їм повернена одіж святости. Згадай, Господи, що ти для їхнього відкуплення прийняв людське тіло і став одним із нас. Умилосердися над їх плачем та мольбами, які до тебе підносять, і відпусти їм їхні кари. А те, чого їм нестає для осягнення вічного щастя, доповни твоїми заслугами та заслугами твоєї Пресвятої Матері і всіх святих. Подай їм твою спасаючу руку і введи їх у світлі місця прохолоди, відпочинку і спокою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Боже, ти хочеш спасення всіх людей, бо вони твої діти. Тому просимо тебе: Подай блаженство неба нашим померлим отцям і матерям, братам і сестрам, і рідні, добродіям і друзям, тим, що вмерли наглою, несподіваною смертю, тим, що найбільше забуті і найбільше потребують помочі, та всім померлим. Усіх їх пом\'яни, Господи. Амінь.</span></span></p>','2008-11-01 19:32:58',NULL,'P','','','uk',596,1576,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: center;\"><strong><strong><em>ВИСЛУЖЕНОГО СЕКРЕТАРЯ СВЯЩЕННОЇ КОНҐРЕҐАЦІЇ СХІДНИХ ЦЕРКОВ ВИСОКОПРЕОСВЯЩЕНОГО АРХІЄПИСКОПА МИРОСЛАВА МАРУСИНА,<br /> ЯКИЙ УПОКОЇВСЯВ ГОСПОДІ 21 ВЕРЕСНЯ 2009 РОКУ БОЖОГО,<br /> В ПРАЗНИК РІЗДВА ПРЕСВЯТОЇ БОГОРОДИЦІ <br /> В ПАПСЬКІЙ КОЛЕГІЇ СВЯТОГО ЙОСАФАТА В РИМІ</em></strong></strong></p>\r\n<p style=\"text-align: center;\"><strong>МІЙ ДУХОВНИЙ ЗАПОВІТ</strong></p>\r\n<p align=\"justify\"><strong>Я, многогрішний раб Господа мого Ісуса Христа, недостойний архієрей Мирослав, перед останнім моїм віддихом і переходом з цього світу у вічність віддаю ще раз тут на землі славу, честь і поклін Пресвятій, одноістотній, животворній і нероздільній Трій&shy;ці, Отцю і Синові і Святому Духові. Вірую сильно і визнаю все те, чого навчав Ісус Христос, що проповідували св. Апостоли, що учили святі і богоносні Отці, а зосібна Вселенські Собори, чого навчали римські Архієреї та що подає до вірування свята католицька Церква. В тій святій вірі я жив, сам її як єпископ проповідав і в тій вірі хочу вмерти з надією на воскресення і на вічне життя та безконечне блаженство з Ангелами і Святими.</strong></p>','2009-11-02 13:23:34',NULL,'P','<p style=\"text-align: center;\"><strong><strong><em>ВИСЛУЖЕНОГО СЕКРЕТАРЯ СВЯЩЕННОЇ КОНҐРЕҐАЦІЇ СХІДНИХ ЦЕРКОВ ВИСОКОПРЕОСВЯЩЕНОГО АРХІЄПИСКОПА МИРОСЛАВА МАРУСИНА,<br /> ЯКИЙ УПОКОЇВСЯВ ГОСПОДІ 21 ВЕРЕСНЯ 2009 РОКУ БОЖОГО,<br /> В ПРАЗНИК РІЗДВА ПРЕСВЯТОЇ БОГОРОДИЦІ <br /> В ПАПСЬКІЙ КОЛЕГІЇ СВЯТОГО ЙОСАФАТА В РИМІ</em></strong></strong></p>\r\n<p style=\"text-align: center;\"><strong>МІЙ ДУХОВНИЙ ЗАПОВІТ</strong></p>\r\n<p align=\"justify\"><strong>Я, многогрішний раб Господа мого Ісуса Христа, недостойний архієрей Мирослав, перед останнім моїм віддихом і переходом з цього світу у вічність віддаю ще раз тут на землі славу, честь і поклін Пресвятій, одноістотній, животворній і нероздільній Трій&shy;ці, Отцю і Синові і Святому Духові. Вірую сильно і визнаю все те, чого навчав Ісус Христос, що проповідували св. Апостоли, що учили святі і богоносні Отці, а зосібна Вселенські Собори, чого навчали римські Архієреї та що подає до вірування свята католицька Церква. В тій святій вірі я жив, сам її як єпископ проповідав і в тій вірі хочу вмерти з надією на воскресення і на вічне життя та безконечне блаженство з Ангелами і Святими.</strong></p>\r\n<p align=\"justify\"><strong>Прощаючись з цим світом, заявляю передусім вірність та послух Святішому Вселенському Римському Архієреєві, Намісникові Ісуса Христа на землі, та видимому голові святої Церкви. Заявляю рівнож мою єдність з цілим католицьким Єпископатом, як продовженням апостольського собору, що є тісно з\'єднаний з Римським Архієреєм, наступником св. Петра.</strong></p>\r\n<p align=\"justify\"><strong>Дякую Всевишньому Господеві за його превелику ласку, що від юности моєї влив у моє серце велику любов до Вселенського Архієрея, заступника Ісуса Христа на землі.<em> </em> </strong></p>\r\n<p align=\"justify\"><strong>Знімка Папи Пія ХІІ, за якого Архієрейського служіння я прийшов на світ, висіла в моїй сільській хатині разом з портретом Митрополита Андрея. Мій батько не раз висловлювався з великою пошаною про Папу Римського.</strong></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p align=\"justify\"><strong>Як прибув я до Риму, мав я ласку вже в навечір\'я Різдва Христового, 24 грудня 1949 р. клячати у стіп Ангельського Пастиря Пія XII і дістати від нього особливе благословення для мене і для моїх рідних та для цілого українського народу.</strong></p>\r\n<p align=\"justify\"><strong>Папа Іван XXIII іменував мене своїм шамбеляном і кілька разів благословляв мене.. Для нього моя безмірна вдячність.</strong></p>\r\n<p align=\"justify\"><strong>Полюбив я вельми Папу Павла VI, що іменував мене єпископом і дав відповідальне завдання у підготовці Кодексу Східного Канонічного Права.</strong></p>\r\n<p align=\"justify\"><strong>За Папи Івана Павла І ніс я далі цю відповідальність доки Папа Іван Павло II не призначив мене бути ще ближче себе передусім в його журбі за Церкви Сходу, зосібна ж тих, що переслідувані терплять імени Ісусового ради.</strong></p>\r\n<p align=\"justify\"><strong>Слово мого братнього прощання звертаю з особливою любов\'ю до моїх співбратів українських Єпископів всюди, де вони не були б, на Україні і на чужині, на волі і в неволі, в тюрмах та на засланнях, щоб усі вони під проводом свого Первоієрарха та в тісній злуці з Папою Римським у здоров\'ї і мирі правили словом Христової істини та пасли те стадо, що його Святий Дух їм повірив. У вічності буду неустанно молити Бога про церковну єдність в українському народі, бо одна-єдина є Христова Церква, яку ми в символі віри визнаємо за святу, соборну, тобто католицьку, й апостольську. В тій Церкві наше спасення. Вона наша мати.</strong></p>\r\n<p align=\"justify\"><strong>Як пастир повірених мені душ, звертаю моє слово до моїх священиків та всіх вірних. Прошу в них прощення за всі мої слабості і недосконалості, зокрема за брак напоумлення тоді, коли деякі люди не зносили здорової науки і від правди відвернули вуха, за своїми пожаданнями вибирали собі вчителів та у важливих церковних справах поступали незгідно з навчанням Церкви та святих і богоносних Отців. Боже, май в опіці мою паству, щоб не загинула ні одна овечка.</strong></p>\r\n<p align=\"justify\"><strong>В моєму архієрейському служінні старався я наслідувати взір та приклад великого нашого Митрополита ЙоснФа В.Рутського та його співбрата св.Йосафата, Архиєпископа Полоцького, а в наших часах слуги Божого Митрополита Андрея Шептицького та його Помічника, а мого духовного отця Владики Архиєпископа Івана Бучка, Архіпастиря скитальців. Від мого Святителя Архієрея Івана навчився я любити цілим серцем Церкву святу та мій рідний нарід. Отож, для цього народу я жив і працював та моє серце оставив близько Підлиської Маркіянової гори, звідки також мій рід і де плили мої юні роки. Не перестану благати Господа, що любить увесь людський рід, щоб дав українському народові нове і краще Тисячоріччя, щоб цей нарід був дійсно християнським, щоб чистою зберігав ласку хрещення і щоб був народом святим.</strong></p>\r\n<p align=\"justify\"><strong>З волі Вселенських Архієреїв сповняв я в Римі деякі важливі послуги для цілої католицької Церкви. Велика це була ласка для мене грішного. Мої слова вдячности висловлюю Апостольській Столиці та всім, з ким я спільно трудився і сповняв мої завдання. Серед тих мужів церковних зустрів я немало святих, для яких метою життя була слава Божа і поширення та закріплення Христового царства на землі.</strong></p>\r\n<p align=\"justify\"><strong>В годину моєї смерти спогадую з вдячністю усіх моїх учителів та настоятелів, зачинаючи від народної школи в селі Княжім коло Золочева, де я прийшов на світ на празник Пресвятої Євхаристії, 26 червня 1924 р., почерез народну школу та середню школу в Золочеві та Духовну Семінарію у Львові, Академію в Падерборні та Орієнтальний Інститут у Римі. Благословлю Бога за тих учителів: українців, поляків, німців, а в Римі Отців Т-ва Ісусового з різних європейських народів. Серед не-українців знайшов я немало приятелів та доброчинців, що безкорисно обдарували мене великим духовним добром. Нехай Господь відплатить їм усім у цім житті та у вічності найбагатшими скарбами Своєї безконечної Любови.</strong></p>\r\n<p align=\"justify\"><strong>Відходжу до Господа в надії на його милосердя для мене бідного, щоб побачити сяйво Божественного Лиця, щоб прибігнути під велику милость Пресвятої Богородиці і щоб жити вічно зі Святими в небі. Маю непохитну надію побачити з небі моїх Родичів, що їм по Бозі завдячую життя та виховання. Йду у вічність радо, бо там чекає мене Спільнота Святих та горний Єрусалим і ціла Церква, а в ній праотці, отці, патріярхи, пророки, апостоли, ісповідники, мученики, євангелисти, проповідники та всі праведні, що від віка спочили в надії на воскресення, між ними ж Пресвята і Пречиста, Преблагословенна Діва Марія, мати Бога Слова, Неповинного Ягняти, що взяло на себе гріхи світу, щоб кров\'ю Своєю відкупити синів Адама і всіх до Отця привести, що царює повіки Бог істинний: Отець з Сином і Святим Духом, єдине Божество й єдине Существо.</strong></p>\r\n<p align=\"justify\"><strong>Лишаю тут на землі моїх рідних, а між ними мого брата Ореста і всіх їх прощаю зі сльозами, як колись Йосиф у Єгипті прощав братію свою. Був я з ними на Україні всього літ двадцять, а потім доля розлучила нас. Побачимося у Небесних Дворах, де людська сила не зможе розділити нас. Нехай мої рідні поможуть мені подякувати Господеві за ласку покликання до священства. Я виріс у крилосі церкви св.Іллі в Княжім, де дід мій Михайло і батько Андрій сповняли чин співців. Моя мати Марія вела мене за руку до престола. Моє ім\'я вже тепер вписую у діптих святої Церкви, щоб сподобитись тут на землі аж до кінця світу молитви праведних та жертви Пресвятої Євхаристії і щоб ради страждань і смерти Христової спасся я на страшному суді. А моїх домашніх благаю покірно вчинити мені ще останнє діло милосердя та похоронити тіло моє у землі, бо й так написано: Згадай, чоловіче, що ти земля єси і в порох повернеш. Нічого не приніс я з собою на цей світ і нічого не забираю з собою. Воістину, суєта все; а жизнь оця сон і сновидіння. Але упокой, Господи, з праведними душу мою!</strong></p>\r\n<p align=\"justify\"><strong>В годині смерти все, що я мав, вже не моє. Нагим прийшов я на світ. А про мої дочасні добра заряджую осібним листом, моїм тестаментом, написаним власною рукою, згідно з правними вимогами та порядком. Моїм одиноким спадкоємцем та виконавцем цього тестаменту назначую Священну Конгрегацію для Східних Церков або ще точніше св.Апостольську Столицю, наколи б зайшла яка зміна в номенклатурі та завданнях дикастерій римської Курії. На моїм гробі нехай буде напис: + Тут очікує воскресення з мертвих + єпископ Мирослав Марусин +</strong></p>\r\n<p align=\"justify\"><strong>Від спадкоємства в залишених мною дочасних речах є виключені всі, навіть мої рідні, бо з природи речі все церковне належить Церкві. А особистих дібр я зрікся з хвилиною рукоположення на священика так, що і не бувши монахом, вів життя по євангельським радам.</strong></p>\r\n<p align=\"justify\"><strong>Усіх моїх співбратів Єпископів, священиків, монахів, монахинь та всіх моїх братів і сестер у Христі в годину моєї смерти прошу прощення моїх провин вольних та невольних, якщо кого я образив чи скривдив, якщо осудив чи згіршив, якщо вчинив прикрість або не виявив доброти і любови. А я зі свого боку заявляю торжественно, що нікого не маю за довжника мені, ніхто під сонцем не був мені неприязно наставлений, ніхто не винен мені й одної лепти. Я сам тільки остаюсь для всіх довжником з любові.</strong></p>\r\n<p align=\"justify\"><strong>Під кінець благаю всіх Отців моїх і братів, щоб зберігали вірно заповідь Христової любови і щоб дбали про єдність віри в союзі мира.. Бо один є Господь, одна Церква, одне хрещення, один Бог і Отець усіх.</strong></p>\r\n<p align=\"justify\"><strong>Господи, Боже мій!<br /> В марноті ізчезнули дні мої, я позбавлений Твоєї помочі<br /> й остався без усякого одвіту.<br /> Але в надії на Твої щедроти взиваю:<br /> гріхів юности моєї і невідання не пом\'яни.<br /> Від таємних моїх гріхів очисти мене і не<br /> відкинь мене від себе.<br /> Перш ніж повернуся в землю дай мені<br /> повернутись до Тебе, мого Створителя.<br /> Зміряй мої беззаконня не справедливістю,<br /> але милосердям Твоїм.<br /> Напроти безлічі моїх прогріхів постав безодню Твоїх щедрот. Вчини, Господи, щоб ім\'я моє було вписане у книзі життя -<br /> щоб я сподобився лона Авраама, Ісаака і Якова -<br /> щоб жити мені у країні живих -<br /> в раї насолоди, у царстві небеснім -<br /> з Ангелами і Святими.<br /> Піднеси, Владико, і тіло моє в день,<br /> що його Ти призначив за Твоєю святою<br /> і необманною обітницею.<br /> Як благий і чоловіколюбець Бог наш<br /> ослаби, відпусти і прости гріхопадіння мої<br /> вольні й невольні, свідомі й несвідомі,<br /> явні й неявні, ділом, помислом і словом<br /> і відкрий мені бідному<br /> на страшному Твоєму суді<br /> серце милостиве, добре і чоловіколюбне.<br /> Амінь, амінь.</strong></p>\r\n<p align=\"justify\"><strong>Християнської кончини життя мого: безболізної, бездоганної, мирної та доброго одвіту на страшнім суді Господнім благаю та спомянувши на пречисту і преблагоеловенну Владичицю і Вседіву Марію та усіх Святих, усе життя віддаю Христу Богу.</strong></p>\r\n<p align=\"justify\"><strong>Прийди, Господи Ісусе. Амінь.</strong></p>\r\n<p align=\"justify\"><strong>В руки Твої, Ісусе, віддаю мого духа.</strong></p>\r\n<p align=\"justify\"><strong>Цей мій заповіт духовний написав я в монастирі Отців Пассіоністів у Римі, на Монте Челіо, в часі моїх духовних вправ, дня 17 грудня р.Б. 1981.</strong></p>','Духовний Заповіт','uk',1868,3351,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify; line-height: 15.6pt;\"><span style=\"color: black;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3650,1024,_MG_3737.JPG\" alt=\"\" width=\"200\" height=\"134\" />9 жовтня 2010 року на схилах Дніпра пройшла благодійна толока. Цієї днини, у чудесному куточку Києва, паркового комплексу &laquo;Аскольдова Могила&raquo;зібралось чимало волонтерів і охочих допомогти у прибиранні.</span></span></span></p>','2010-10-10 18:18:03',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: left;\" src=\"image,3651,200,_MG_3757.JPG\" alt=\"\" width=\"133\" height=\"200\" />9 жовтня 2010 року на схилах Дніпра пройшла благодійна толока. Цієї днини, у чудесному куточку Києва, паркового комплексу &laquo;Аскольдова Могила&raquo;зібралось чимало волонтерів і охочих допомогти у прибиранні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\"><img style=\"float: right;\" src=\"image,3651,200,_MG_3746.JPG\" alt=\"\" width=\"133\" height=\"200\" />Загальною метою народної толоки було прибирання дніпровських круч і прилеглої території до пам&lsquo;ятки архітектури, церкви св.. Миколая Чудотворця на Аскольдовій Могилі, а також мали велике бажання посадити ще декілька дерева у цьому мальовничому куточку нашої столиці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Парафіяни церкви разом з працівниками міжнародної фірми, яку очолює пан Лев, разом творили цю благородну місію.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: small;\">Пан Лев: - Ми задоволені своєю працею, частуванням і спілкуванням. Тільки навернеменям до Творця, людина врятує себе і оточуючу природу.</span></span></p>','Чисте сердце, чисте довкілля ','uk',2168,3651,0,'Віталіна Онишкевич'),
(1,NULL,'<p style=\"text-align: justify; text-indent: 14.2pt;\">&nbsp;   <span>В рамках проекту    <em>&laquo;Допомога дітям ув&rsquo;язнених матерів&raquo;</em></span> Церковною службою милосердя   <span>&raquo;Карітас-Печерськ&raquo; 23 листопада були надіслані 25 гуманітарних пакетів з допомогою для 38 потребуючих дітей. Подарунки для дітей були придбані за кошти небайдужих людей:</span></p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> прихожанами церкви Святого Миколая п. Галиною і п. Станіславом - пожертва гігієнічними товарами (шампунь, мило, зубна паста, дезидорант)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> президентом фірми &laquo;Крафт&raquo; п. Юрієм Логушем - допомога солодощами (цукерки)</p>','2009-12-01 18:33:31',NULL,'P','<p style=\"text-align: justify; text-indent: 14.2pt;\">&nbsp;   <span>В рамках проекту    <em>&laquo;Допомога дітям ув&rsquo;язнених матерів&raquo;</em></span> Церковною службою милосердя   <span>&raquo;Карітас-Печерськ&raquo; 23 листопада були надіслані 25 гуманітарних пакетів з допомогою для 38 потребуючих дітей. Подарунки для дітей були придбані за кошти небайдужих людей:</span></p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> прихожанами церкви Святого Миколая п. Галиною і п. Станіславом - пожертва гігієнічними товарами (шампунь, мило, зубна паста, дезидорант)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> президентом фірми &laquo;Крафт&raquo; п. Юрієм Логушем - допомога солодощами (цукерки)</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> парафією святого Дженуарія, м. Беневенто, священиком о. Паскуалем перераховано кошти в    <span>на магазин</span> &laquo;Метро&raquo; для придбання товарів: продуктів харчування і гігієнічних засобів</p>\r\n<p style=\"margin-left: 21pt; text-align: justify; text-indent: -18pt;\"><span> <span>-     <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> церквою святого Миколая - допомога найбільш потребуючим, особливо хворим дітям та дітям, позбавленим батьківської опіки, а також жінкам з новонародженими дітьми, які утримуються під наглядом.</p>\r\n<p style=\"margin-left: 18pt; text-align: justify;\"><span>Цих 25 посилок надіслані через Укрпошту. Поштові витрати:пересилка 701гр. (+ящики 8.50*25=212.50) склали 914.50 &nbsp;грн і були пожертвувані паном Романом.</span></p>\r\n<p style=\"margin-left: 18pt; text-align: justify;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<table style=\"margin-left: 2.75pt; border-collapse: collapse;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<thead> \r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">№</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Назва закладу</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Надана допомога</span></p>\r\n</td>\r\n<td style=\"border: 1pt solid black; padding: 2.75pt; width: 65.65pt;\" width=\"88\" valign=\"top\">\r\n<p><span style=\"font-size: 10pt;\">Сума</span></p>\r\n</td>\r\n</tr>\r\n</thead> \r\n<tbody>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\"><span style=\"font-size: 10pt;\"> <span>1.             <span style=\"font-family: &quot;Times New Roman&quot;; font-style: normal; font-variant: normal; font-weight: normal; font-size: 7pt; line-height: normal; font-size-adjust: none; font-stretch: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span> </span> &nbsp;</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Будинок дитини №1,&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">м. Луганськ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Епецахі Ніні 2001р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Портфель, цукерки, вафлі, зош</span> <span style=\"font-size: 10pt;\">ити, записники, мило, шампунь, зубна щітка, зубна паста.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">84,69</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;2.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Будинок дитини &laquo;Вишенька&raquo;, Львівська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козловському Віктору -2006 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Козловському Олексію &ndash; 2003 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Солодощі, цукерки 2 пачки, вафлі 6 пачок, портфель 1, зошити, ручки, щоденник, олівці кольорові.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">103,33</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;3.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Школа-інтернат, вул. Шлях Леніна, 69, Сахновщина, Харківська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Романів Елісею 1997р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфель, щоденник, олівці, мило, ручки, записники, шампунь.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\" valign=\"top\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">112,71</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;4.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"248\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Школа-інтернат, м. Тлумач, Івано-Франківська обл.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Сергію &ndash; 1997 р.н.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Яворському Юрію &ndash; 1999 р.н.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 191.15pt;\" width=\"255\" valign=\"top\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\">Цукерки, вафлі, портфелі 2, зошити, ручки, щоденник 2, олівці кольорові, шампунь, зубна щітка, зубна паста.</span></p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 65.65pt;\" width=\"88\">\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 10pt;\">124,69</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding: 2.75pt; width: 39pt;\" width=\"52\" valign=\"top\">\r\n<p style=\"margin-left: 36pt; text-indent: -18pt;\">&nbsp;5.</p>\r\n</td>\r\n<td style=\"padding: 2.75pt; width: 186.05pt;\" width=\"