INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></p>','2014-12-17 21:45:24',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></p>\r\n<table style=\"width: 539px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>№ з/п</strong></span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">1</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Юрія та Галини Михальчишин</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">2</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Любов</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Десятина за вересень</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">3</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ігор та Наталія</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">4</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олег та Валентина</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Храм</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">5</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Юрій та Наталя</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Допомогу воїнам, що боронять Україну та бійцям</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">6</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Марія Шпак</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">7</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Артиш</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">8</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олександр та Зоя</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">9</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Богдан та Тетяна Сікора</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">10</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Олександр та Зоя</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дзвіницю</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">11</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ігор та Ірина</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Гостину свята Богородиці Києво-Зарваницької</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">12</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Аскольд</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Дітям</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">13</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Роїк</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Потреби церкви</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">14</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Ніна</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">15</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Михайло з родиною</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву в день уродин</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">16</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Андрія та Ірини Шикули</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"38\">\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">17</span></p>\r\n</td>\r\n<td width=\"236\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Родина Гресько</span></p>\r\n</td>\r\n<td width=\"265\">\r\n<p><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\">Церкву</span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small;\"><strong><em>Милостиню роби згідно з тим,</em></strong><strong><em>що маєш;</em></strong><strong><em>Коли у тебе достатків повно,</em></strong><strong><em>Роби з них,</em></strong><strong><em>а коли мало маєш,</em></strong><strong><em>то не бійся з того дати.</em></strong><strong><em>Бо так готуєш собі добрий скарб</em></strong><strong><em>На день потреби.</em></strong><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></strong><strong>Товит 4. 8-9.</strong></span></p>','ПОЖЕРТВИ ПАРАФІЯН У ЛИСТОПАДІ 2014 Р. Б.','uk',4818,6301,0,'Любов Михайлюк');
