INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7 жовтня 2012 р.</strong><strong> о 17.00 почнеться перша лекція річного освітньо-музичного циклу &laquo;Перший християнський князь&raquo;.</strong> Кожен місяць (з жовтня 2012 р. по квітень 2013 р.) провідні навчальні підрозділи НПУ імені М.П. Драгоманова &ndash; Інститут філософської освіти і науки та Інститут мистецтв &ndash; готуватимуть відкриту лекцію, до якої буде спеціально підібраний живий музичний супровід.</span></p>','2012-10-04 23:43:42',NULL,'P','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7 жовтня 2012 р. о 15.00 почнуться перші публічні історичні читання на Аскольдовій Могилі. Подія відбуватиметься біля церкви св. Миколая Чудотворця (Паркова алея, 1)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цього року відзначаємо 1130 річницю пам&lsquo;яті князя Аскольда &ndash; Миколая, першого у Київської Русі князя-християнина. З його іменем історики пов\'язують початок не тільки християнізації Русі, а й формування Київської держави. Втім, історичне значення постаті князя Аскольда-Миколая було незаслужено забуте, а іноді й навмисне понижене. Культурно-просвітницькі заходи, присвячені 1130-річчю пам&rsquo;яті князя Аскольда-Миколая, організовані з благословення протоієрея Ігоря, настоятеля церкви св. Миколая Чудотворця на Аскольдовій Могилі, за участі Національного педагогічного університету імені М.П. Драгоманова, покликані відновити історичну справедливість, підняти завісу міфів та пропагандистських нашарувань навколо імені першого київського князя-християнина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Провідні історики, культурологи, релігіє знавці, духовні особи Української Греко-Католицької Церкви та Української Православної Церкви Київського патріархату оприлюднять маловідомі та нововиявлені матеріали про князювання князя Аскольда, його похід на Константинополь (Царгород), формування принципів зовнішньої політики Аскольдової України-Русі, значення Аскольдового хрещення для формування Київської Русі як самодостатньої та потужної держави середньовічної Європи.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7 жовтня у перших публічних історичних читання на Аскольдовій могилі візьмуть участь:</span></p>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Протоієрей Ігор Онишкевич &ndash;</strong> настоятель церкви св.. Миколая Чудотворця на Аскольдовій могилі;</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Протоієрей Віталій Клос, </strong>проректор з наукової роботи Православної богословської Академії Української Православної Церкви Київського Патріархату;</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Протоієрей Констянтин Лозинський, </strong>кандидат богословських наук, доцент кафедри церковної історії Київської православної Богословської Академії Української Православної Церкви Київського Патріархату;</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сергій Шумило</strong>&ndash; заступник голови &laquo;Товариства пам&rsquo;яті князя Аскольда&raquo;, співголова Центру досліджень історії Церкви</span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Павло Жук</strong>&ndash; голова &laquo;Товариства пам&rsquo;яті князя Аскольда&raquo;, заступник голови Голосіївської районної державної адміністрації у м. Києві</span></li>\r\n</ul>\r\n<ul style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сергій Савченко, </strong>магістрант релігієзнавства Інституту філософської освіти і науки НПУ імені М.П. Драгоманова</span></li>\r\n</ul>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>О 17.00 почнеться перша лекція річного освітньо-музичного циклу &laquo;Перший християнський князь&raquo;.</strong> Кожен місяць (з жовтня 2012 р. по квітень 2013 р.) провідні навчальні підрозділи НПУ імені М.П. Драгоманова &ndash; Інститут філософської освіти і науки та Інститут мистецтв &ndash; готуватимуть відкриту лекцію, до якої буде спеціально підібраний живий музичний супровід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>жовтня тема лекції: &laquo;Князь Аскольд: погляд крізь віки&raquo; <strong>(</strong>Панорама Європи часів ІХ століття<strong>; </strong>Походження князя Аскольда: літописні свідчення та теорії істориків<strong>; </strong>Постать Аскольда у формуванні княжої традиції Київської Русі).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Музичний супровід - вокальний ансамбль старовинної музики &laquo;Алітея&raquo; Інституту мистецтв НПУ імені М.П. Драгоманова (керівник &ndash; Жанна Гончаренко) та струнний квінтет Інституту мистецтв НПУ імені М.П. Драгоманова (керівник &ndash; Галина Савченко).</span></p>\r\n<h2 style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З додатковими питаннями щодо перших історичних читань звертатися до Мирослави Мазур (т.0507893356), Олени Нагорної (т.068&nbsp;355 04 90); &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;щодо освітньо-музичного циклу &laquo;Перший християнський князь&raquo; - до Сергія Русакова (067-440-8591).</span></h2>','Перші публічні історичні читання на Аскольдовій Могилі','uk',2820,4303,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4335,200,IMG_5517-001.jpg\" alt=\"\" width=\"200\" height=\"133\" />4 листопада 2012 року</strong> о 17 годині в Церкві Святого Миколая на Аскольдовій могилі (м. Київ, Петрівська алея, 1) у рамках освітньо-музичного проекту &laquo;Перший християнський князь&raquo; відбудеться лекція під назвою &nbsp;&laquo;Диво у Влахерні&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У заході також візьме участь всесвітньовідомий органіст, народний артист України <strong>Володимир Кошуба</strong> та лауреат міжнародних конкурсів <strong>Мар&rsquo;яна Головко</strong> (вокал, мецо-сопрано).</span></p>','2012-10-31 08:39:09',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4335,200,IMG_5517-001.jpg\" alt=\"\" width=\"200\" height=\"133\" />4 листопада 2012 року</strong> о 17 годині в Церкві Святого Миколая на Аскольдовій могилі (м. Київ, Петрівська алея, 1) у рамках освітньо-музичного проекту &laquo;Перший християнський князь&raquo; відбудеться лекція під назвою &nbsp;&laquo;Диво у Влахерні&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У заході також візьме участь всесвітньовідомий органіст, народний артист України <strong>Володимир Кошуба</strong> та лауреат міжнародних конкурсів <strong>Мар&rsquo;яна Головко</strong> (вокал, мецо-сопрано).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освітньо-музичний цикл &laquo;Перший християнський князь&raquo; організовує Інститут філософської освіти і науки та Інститут мистецтв Національного педагогічного університету імені М.П. Драгоманова.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: right;\" src=\"image,4335,200,1.jpg\" alt=\"\" width=\"141\" height=\"200\" />Лекція ознайомить присутніх з історичними відомостями про відомий похід князя Аскольда на Константинополь, про погляди сучасників на Влахернське диво, а також допоможе визначити роль вищезгаданого походу для становлення вітчизняної культури. Лектор &ndash; магістрант релігієзнавства НПУ імені М.П. Драгоманова Сергій Савченко.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Освітньо-музичний проект &laquo;Перший християнський князь&raquo; представляє собою органічне поєднання лекційної частини про життя та подвиги князя Аскольда та музичного супроводу, організацією якого займається Інститут мистецтв НПУ ім.. М.П. Драгоманова.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">http://ifon.npu.edu.ua</span></p>','«ДИВО У ВЛАХЕРНІ»','uk',2852,4335,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4351,200,caritas_candle2012_1.jpg\" alt=\"\" width=\"200\" height=\"146\" />Ціль акції &laquo;Різдвяна свічка&raquo;</strong>&nbsp;донести дітям звістку, що Бог і оточуючі люди люблять їх, висловлюють особливу увагу і піклування про їх майбутнє. Кожна запалена різдвяна свічка &ndash; особистий внесок в опіку соціально незахищеними дітьми. Адже усі, хто запалить таку свічку, &ndash; допоможе запалити вогник надії і добра у дитячих серцях.</span></p>','2012-12-13 12:47:50',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійний Фонд &laquo;Карітас Київ&raquo; проводить щорічну благодійну акцію&nbsp;&laquo;Свічка допомоги потребуючій дитині&raquo;&nbsp;або&nbsp;&laquo;Різдвяна свічка&raquo;,&nbsp;зібрані кошти від якої підуть на допомогу соціально-незахищеним дітям. Кожен, хто хоче допомогти потребуючій дитині, має таку можливість, приєднуючись до благодійних справ Карітасу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Акція полягає у розповсюдженні свічок, які мають особливість в тому, що у їх виготовлені приймають участь особи з інтелектуальними і функціональними обмеженнями.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженніший Святослав благословив Акцію та звернувся до всіх людей доброї волі про підтримку і сприяння словами: &laquo;Кожна людина, що купляє цю свічку, до виготовлення якої прикладаються потребуючі діти, приносить теплоту, радість та любов у їхні серця, показуючи та виявляючи їм справжню християнську сутність &ndash; любов до ближнього&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійна акція &laquo;Різдвяна свічка&raquo; відбувається в Україні щороку протягом останніх 8 років. Метою акції є збір коштів для допомоги соціально незахищеним дітям.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4351,200,caritas_candle2012_1.jpg\" alt=\"\" width=\"200\" height=\"146\" />Різдвяні свічки виготовляються руками людей з особливими потребами. З початком Різдвяного посту такі свічки розповсюджуються насамперед через церковні громади в Україні і закордоном, також через партнерські організації, спільноти і серед усіх бажаючих жертводавців.&nbsp;Минулого року &ndash; перед Різдвом Христовим 2012 р. у Київській архиєпархії БФ &bdquo;Карітас Київ&rdquo; вдалось розповсюдити 4000 свічок, а на рівні всієї&nbsp;<a href=\"http://www.ugcc.org.ua/\">Української Греко-Католицької Церкви</a>&nbsp;в Україні та поза її межами понад 100 тисяч свічок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зібрані пожертви спрямовуються на допомогу потребуючим дітям: сиротам, напівсиротам, дітям з малозабезпечених і багатодітних сімей, ВІЛ-інфікованим, неповносправним дітям і молоді, дітям трудових мігрантів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щороку завдяки служінню Карітасу сотні дітей мають можливість брати участь в літніх оздоровчих таборах, відвідувати пізнавальні екскурсії, долучатись до роботи навчальних груп, творчих гуртків в дитячих соціальних центрах, організовувати особливі свята, бути отримувачами адресної допомоги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Завдяки зібраним пожертвам багато дітей були зігріті та нагодовані, отримали позитивний досвід молитви та спілкування. Чимало дітей повернулося до життя у суспільстві та Церкві і у недалекому майбутньому зможуть стати активними громадянами України.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ціль акції &laquo;Різдвяна свічка&raquo;&nbsp;донести дітям звістку, що Бог і оточуючі люди люблять їх, висловлюють особливу увагу і піклування про їх майбутнє. Кожна запалена різдвяна свічка &ndash; особистий внесок в опіку соціально незахищеними дітьми. Адже усі, хто запалить таку свічку, &ndash; допоможе запалити вогник надії і добра у дитячих серцях.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дана благодійна ініціатива має на меті не лише зібрати кошти для допомоги потребуючим дітям, але й звернути увагу кожного з нас до проблем дітей, що живуть поряд з нами і перебувають у кризових обставинах життя. Ми не можемо їх не помічати і повинні розуміти, що допомогти їм не вимагає великих зусиль. Це і добре слово, і виділені кошти чи одяг, їжа, і увага, і психологічна підтримка, і молитва.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійний фонд Карітас Київ опікується потребуючими дітьми пільгових категорій. Це ті категорії дітей, які або не мають батьків взагалі, або їхні батьки є алко- чи нарко- залежними. Зрозуміло, що такі діти є обділеними батьківською увагою, теплом, ласкою та любов&rsquo;ю, тому мусимо спільними зусиллями постаратися принести у їхні душі радість свята та відчуття людського милосердя. Запрошуємо усіх до активного розповсюдження інформації про &laquo;Різдвяну свічку&raquo; та придбання свічки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Запрошуємо усіх, кому є небайдужим життя людини, закликаємо всіх, кого хвилює майбутнє України: приєднайтеся і цього року до Благодійної Акції &bdquo;Свічка допомоги потребуючій дитині&rdquo;. Це не просто свічка, яку запалюємо, а сповнення нашого християнського обов&rsquo;язку і покликання допомагати ближньому. Ця свічка може бути гарним подарунком у місцях нашої роботи, навчання, а також світлом, що горітиме перед іконою, коли будемо молитися, а хтось у цей час радітиме і їстиме теплу їжу завдяки милосердю нашого серця.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відеоролик Акції<em> он-лайн можна подивитися за адресою: </em><a href=\"http://www.youtube.com/watch?v=UIk3fYQ_LyI&amp;feature=plcp&amp;noredirect=1\">http://www.youtube.com/watch?v=UIk3fYQ_LyI&amp;feature=plcp&amp;noredirect=1</a>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Водночас, хочемо висловити щиру вдячність всім тим, хто у минулі роки допомагав у проведенні цієї акції та запросити до подальшої співпраці на добро знедолених дітей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За детальнішою інформацію щодо розповсюдження чи придбання свічки можна звертатися у БФ &laquo;Карітас Київ&raquo; за телефоном&nbsp;(044)512-00-85, або безпосередньо до директора фонду о. Романа Сиротича (097-19-45-911,&nbsp;<a href=\"mailto:syrotych@ukr.net\">syrotych@ukr.net</a>&nbsp;).</span></p>','«Свічка допомоги потребуючій дитині» або «Різдвяна свічка»','uk',2868,4351,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4383,200,ukrainski-kolyadki-ta-shhedrivki-na-rizdvo1.jpg\" alt=\"\" width=\"200\" height=\"122\" />&laquo;Щедрик&raquo;</strong>&nbsp;&mdash; одна з найпопулярніших обробок <a title=\"Леонтович Микола Дмитрович\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D0%B5%D0%BE%D0%BD%D1%82%D0%BE%D0%B2%D0%B8%D1%87_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0_%D0%94%D0%BC%D0%B8%D1%82%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Миколи Леонтовича</a>, знана не тільки в Україні, але й у всьому світі під назвою <strong>&laquo;колядка дзвонів&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щедрик &mdash; то не розкладка пісні, то самоцінний твір, осяяний промінням генія, який посяде не останнє місце в світовій музичній літературі.</span></p>\r\n<p>&nbsp;</p>','2013-01-18 03:00:38',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,4383,200,ukrainski-kolyadki-ta-shhedrivki-na-rizdvo1.jpg\" alt=\"\" width=\"200\" height=\"122\" /></strong></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&laquo;Щедрик&raquo;</strong>&nbsp;&mdash; одна з найпопулярніших обробок <a title=\"Леонтович Микола Дмитрович\" href=\"http://uk.wikipedia.org/wiki/%D0%9B%D0%B5%D0%BE%D0%BD%D1%82%D0%BE%D0%B2%D0%B8%D1%87_%D0%9C%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0_%D0%94%D0%BC%D0%B8%D1%82%D1%80%D0%BE%D0%B2%D0%B8%D1%87\">Миколи Леонтовича</a>, знана не тільки в Україні, але й у всьому світі під назвою <strong>&laquo;колядка дзвонів&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щедрик &mdash; то не розкладка пісні, то самоцінний твір, осяяний промінням генія, який посяде не останнє місце в світовій музичній літературі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">М. Леонтович (1877-1921) є класиком української музики кінця ХІХ &ndash; початку ХХ ст. і водночас одним із фундаторів сучасної музичної культури.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Корені хорової творчості М. Леонтович йдуть крізь століття, сягаючи глибин народного мелосу. Загалом його поступ як композитора можна окреслити двома значними віхами і він йшов від народної творчості і до народної творчості.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Саме завдяки художньому переосмисленню М. Леонтович сформувався як неперевершений майстер хорової мініатюри, її тонкий імператор. Він, як ніхто, зумів підняти народну пісню до рівня найкращих зразків світового хорового мистецтва надавши їй філігранної витонченості та вишуканості.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Українська народна пісня стала для композитора невичерпним джерелом, що повсякчас надавало йому творчої снаги та слугувало орієнтиром в пошуках краси гармонії. В пісні сконцентрувалися найкращі здобутки та надбання народу, на яких виховувалося не одне покоління.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший ранній період творчості завершується орієнтовно в 1904 р. На той час композитор підготував і видрукував дві збірки народних пісень для хору з Поділля. У роботі над ними він спирався на творчість М. Лисенка в галузі обробки українських народних пісень, а також на вітчизняну хорову музику ХУІ- поч. ХІХ ст.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Другий зрілий період творчості (1905-1917) був дуже плідний для композитора. На цей час припадає остаточне формування творчих принципів М. Леонтовича, його манери письма.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Передусім композитор робить серйозний відбір пісень, шукаючи такі, які відповідають його художнім задумам,поставленим перед ним завданням. Робота над обробкою кожної пісні триває досить довго, нерідко композитор здійснює 3-4 її варіанти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім основного образу М. Леонтович розробляє й сюжет твору. Залишаючи мелодію незмінною, він застосовує багатий арсенал гармонічних поліфонічних, фактурно-тембрових засобів для творення індивідуальних образів, деталізації сюжету. В цей час з&rsquo;являються кращі твори композитора. &ldquo;Мала мати одну дочку&rdquo;, &ldquo;Пряла&rdquo;, &ldquo;Над річкою беріжком&rdquo;, &ldquo;П&rsquo;ють півні&rdquo;, &ldquo;Дударик&rdquo;, &ldquo;Щедрик&rdquo;, оригінальні твори &ndash; хорові поеми &ldquo;Льодолом&rdquo;, &ldquo;Літні тон&rdquo;, &ldquo;Моя пісня&rdquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В обробках народних пісень М. Леонтович все ставить надзвичайний інтерес: поліфонічна тканина і гармонія, народна і авторська мелодика, метро-ритм народної пісні і його прояв у всій композиції, фактура і хорова оркестровка, прийоми розвитку і форма твору, злиття стилістики і жанру даної народної пісні і його прояв у всій композиції, фактура і хорова оркестровка, прийоми розвитук і форма твору, злиття стилістики і жанру даної пісні та стилю композитора.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Син священика і сам закінчив духовне училище і семінарію. Він ще в дитинстві поряд з народними піснями чув багато культово-обрядових мелодій (різв&rsquo;яних і літургійних творів на церковних крилосах, що безумовно стало причиною створення власної духовної музики: Літургії І. Злаотоустого, Молебну Благодарственного Господу Богу, Херувимських пісень оранжеровок старовинних церковних піснеспівів &ndash; Кантів, Псалмів, а також колядок &ndash; &ldquo;Дивная новина&rdquo; Пречиста Діва&rdquo;, &ldquo;Що то за предиво&rdquo;, &ldquo;Як на річці&rdquo;, &ldquo;небо і земля&rdquo; та ін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У колядках, а саме вони становлять найбільший інтерес в даному аналізі &ldquo;Щедрика&rdquo; як пісні зимового обрядового циклу, досить чітко виявився той же композиторський стиль М. Леонтовича, що й у відомих його обробках пісень, особливо щод опоєднання народних і професійних прийомів багатоголосся.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Велике місце в українській народно-пісенній культурі займають обрядові пісні, зокрема пісні зимового циклу &ndash; колядки,щедрівки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знаменитий &ldquo;Щедрик&rdquo; М. Леонтовича є, по суті, виразною картиною обряду щедрування: щедрівники перебігають від хати до хати, починає один два, поступово приєднуються інші: доспівавши і отримавши подарунки біжать далі (це знаменує повторення хору), і все починається спочатку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Основний зміст тексту новорічні побажання господареві усіх благ. Цей хор написав за замовленням Б. Яворського.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Є припущення, що роботу&nbsp; над &ldquo;Щедриком&rdquo; композитор почав ще в 1910 р. Але по рукописі, надісланому в Київ у 1916 р. Стоїть дата 18.У.1916&nbsp; р. Отже, можна припустити, що задумана ця обробка була давно, але остаточне здійснення припадає на 1916 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Важко переоцінити значення &ldquo;Щедрика&rdquo; і для самого М. Леонтовича: адже завдяки тому, що в 1916 р. Цей твір був виконаний студентським хором Київської консервтаорії під керівництвом О. Кошиця, коли сам автор жив ще в глибокій провінції в Тульчині - ім&rsquo;я Леонтовича стало відомим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аналізуючи народний текст твору, бачимо, що музичний зміст, композиція, особливості метру суперечать рівновазі тексту.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аналіз вказує, що двоскладова метрична строфа вірша з наголосом на першій долі накладається на музичний розмір&nbsp;&nbsp;&nbsp; , що веде до неспівпадання сильних долей словесного і музичного тексту. Але ця особливість закладена у самій народній темі щедрівки і аж ніяк е позначається на її художньому апекті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аналіз &ldquo;Щедрика&rdquo; М. Леонтовича являє виключний інтерес, бо навіть для недосвідченої&nbsp; людини впадає в око та особливість, що пісню побудовано на матеріалі надзвичайно простої, здавалося б на перший погляд бідної і примітивної людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чотиритактна мелодія складається з чотирьох повторень тієї самої фрази &ndash; невеликої наспівки &ndash; темп остинато. Але М. Леонтович розкриває в цій простій фразі багатющі внутрішні художні можливості. Ця фраза служить і матеріалом, на якому виростає вся поліфонічна будова пісні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Композитор застосував варіаційний принцип розвитку при незмінній темі &ndash; варіації на мелодію &ndash; остинато.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Протиставляючи у другому проведенні теми повільну мелодію, яка поступово сходить у квінту тонічного тризвука в альта.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4383,200,kompozitor-leontovich-_rojkov_aleksandr_1303198834.jpg\" alt=\"\" width=\"155\" height=\"200\" />М. Леонтович тим самим досягає особливо підкресленої динаміки, яка створюється внаслідок зіставлення швидкого і повільного руху.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Третя строфа на більш високому динамічному рівні &ndash; йде в акордовому викладі, контрастуючи з ІУ строфою, в якій мелодія є стрижнем розвитку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У цій строфі сопрано, передавши остинато басом, природно переходять до самостійного контрапунктурного голосу, досягаючи регістрової кульмінації.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мелодія сопрано є варіантом, у зменшенні низхідного контрапунктуючого голосу ІІ строфи.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У УІ строфі, що являє собою елодине&nbsp; ???, знову поступово зменшується діапазон пісні. І в ній поспівки контапунктуючих голосів взаємопов&rsquo;язані &ndash; розпів сопрано повторюється у збільшені в альтів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Органний пункт на домінанті у цій фразі підготовляє репризу &ndash;УІІ строфу. Знов повертається первісне звучання, зокрема подібні контрапунктуючі голоси (хід баса- відповідь з ІІ строфи). Заключна строфа обрамлює пісню, проводячи одиноку поспівку її початку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пісня м&rsquo;яко розгорнулася, наче хвиля, та плавно відхлинула залишивши почуття надзвичайної завершенності.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отже &ldquo;Щедрику&rdquo; притаманні риси сонатно-симфонічного мислення з вивідними контрастами у процесі наскрізного розвитку, поступовим розкритям теми.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Розмір&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - простий, незмінний на протязі всього твору.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Темп: allegretta.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Градація динаміки: Із всіма динамічними відтінками між нимми:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Драматургія музичного розвитку пройнята внутрішньою боротьбою мелодико-інтонаційних утворень за зверхність. Ця боротьба має свій мужичний сюжет і напрям розвитку: спочатку неподільне панування основної теми, далі ледве чутне її розвоєння, потім виникненя контапунктичної противаги, яка долає своє першоджерело, і, нарешті, найвища фаза цієї боротьби (перша половина третього періоду), яка не збігається із загальною кульмінацією усього твору.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В цій фазі нова, навіть ритмічно вона зовсім самостійна мелодія, підтримала власним супроводом, остаточно перемагає початкову тему, відводячи її у контексті третьорядну роль фігураційного тла.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Щедрик&rdquo; є вищим досягненням М. Леонтовича щодо форми. Як вже було сказано вище, в основу &ldquo;Щедрика&rdquo; покладено однотактовий мотив, який протягом усього твору проведено на одній ладовій висоті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Послідовне продовження єдиноого мотиву надає всій п&rsquo;єсі єдності матеріалу, але воно ж, здавалось, мусило внести одноманітність. Щоб уникнути цього, композитор влив увесь звуковий матеріал в дивну ладову схему, естетична логіка, яка в даному випадку полягає в тяжінні до Т, яке все збільшується і заспокоюється лише в останніх тактах твору.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тоніка &ldquo;Щедрика&rdquo; &ndash; g-moll. Спочатку, поки мотив висловлює лише один голос&nbsp; (1-4 такти), тоніка мамйже не виявлена. В наступних чотирьох тактах, далі (9-16), - с - moll.&nbsp;&nbsp; <br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З 17 по 24 - g-moll, який, однак немає сталості, бо її порушують звуки,&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; , чергування діє у 23 такті. 25-28 такти&nbsp; , що підготовляє слухача до Т, яка постуає у 32 такті. Але твір не закінчено, бо раптом на кінцевий акорд покладено початок (пісня знов повторюється).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лише за другим разом М. Леонтович дає 4 такти тоніки, які повністю заскокоюють чуття ладового тяжіння.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Т.ч., мудро підібраною ладовою схемою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">М. Леонтович об&rsquo;єднав бідніши одноманітний матеріал у цільну, естетичну, високої художньої активності форму, логіку якої можна показати в лінійній схемі:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли до цього додати прекрасну звучність (послідовність звукового наростання), симетричність динамічного елемента, красус голосоведення на цей твір ми визнаємо, як шедевр в осягненні вищих музичних фомр у межах пісенного матеріалу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одним із засобів досягнення єдності куплетно-варіаційного циклу є незбіг граней строфи і музичного періоду з завершенням ладогармонічного розвитку. Він надає рівноправності початкам і кінцям куплетів пісні, сприяє наскрізному рухові, перетворює каданси на джерело кінетичної енергії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Голосоведення &ldquo;Щедрикка&rdquo; органічно виростає з почуття та потреб гармонічно-функційного процесу. Мелодійна лінія голосів, які супроводять голчний мотив та обрамляють остинатну тему, не є наслідком якихось поліфонічних чи імітаційних міркувань, а виключно гармонічно-функціональньих.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І такі протяжні ноти альта і тенора на початку і&nbsp; такі ж у баса та альта на закінченні творять чисто гармонічну основу, в якій функії розкішно переливаються одна в одну.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від варіації до варіації виявляється ладова природа пісні, характерні гармонії (УІ, ІІІ).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">При витриманих басах і проведенні теми в тенорах, сопрано і альта&nbsp; доручені вихідні контрасні лінії. У цій варіації, що виконує рорль предикту до гепризи використано нові гармонічні звороти&nbsp; (автентичні).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І зворотній (висхідний) рух мелодичних ліній. Все це вносить відповідний контраст до попередніх розділів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сама варіація є репризною. Тут знов тему переданоо сопрано, знову з&rsquo;являються плогамні гармонії та низхідні мелодичні лінії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кода &ndash; останній чотиритакт &ndash; звучить на витриманій тоніці. Однаково проспівується двома солістами остання фраза.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як висновок розділу аналізу, ми повинні сказати, що в обробці &ldquo;Щедрика&rdquo; особливо рельєфно виявилися риси притаманні всій творчості Леонтовича, які можна сформувати в декількох принципах:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; продовжуючи традиції української класики,&nbsp; Леонтович створив свій самобутній стиль в жанрі обробок для хору;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; народна пісня зазнає у Леонтовича вільного творчого розвитку;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; своєрідність стилю М.Леонтовича виникла на основі глибокого пізнання народної творчості та засвоєння професіональної майстерності і являє собою якісно новий щабель;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; кожний прийом має глибоке смислове значення. Таким є значення стягування голосів в унісонні чи октавні&nbsp; &ldquo;вузли&rdquo; в найбільш важливих розділах пісні, введення імітації, що нагадують тужіння, яке створює ефект луни;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; введення контрапунктуючих голосів, наче других смислових ліній пісні, є одним з найяскравіших прийомів композитора. Виражаючи більш гостро та індивідуально зміст народної пісні, вони близькій народній пісенності взагалі;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">-&nbsp;&nbsp; на основі складного комплексу прийомів народного та професійного багатоголосся композитоор створив обробки &ndash; хорові мініатюри, досконалі в своїй простоті, глибокі за змістом. Стислий і лаконічний образ народної пісні Леонтович розкриває з середини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Принцип обробок Леонтовича в цілому і особливо його симфонізація народної пісні відіграли величезну роль у розвитку багатьох жанрів української музики.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/H-GXJpwgYIQ\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всесвітньо відома обробка &laquo;Щедрик&raquo; належить до тих, над якими Микола Леонтович працював майже все життя<sup class=\"reference\"><a href=\"http://uk.wikipedia.org/wiki/%D0%A9%D0%B5%D0%B4%D1%80%D0%B8%D0%BA_%28%D0%9B%D0%B5%D0%BE%D0%BD%D1%82%D0%BE%D0%B2%D0%B8%D1%87%29#cite_note-1\">[1]</a></sup>. Перша її редакція була написана до <a title=\"1901\" href=\"http://uk.wikipedia.org/wiki/1901\">1901</a>&mdash;<a title=\"1902\" href=\"http://uk.wikipedia.org/wiki/1902\">1902</a>&nbsp;рр., друга редакція&nbsp;&mdash; в 1906&mdash;1908&nbsp;рр., третя&nbsp;&mdash; <a title=\"1914\" href=\"http://uk.wikipedia.org/wiki/1914\">1914</a>&nbsp;р., четверта&nbsp;&mdash; <a title=\"1916\" href=\"http://uk.wikipedia.org/wiki/1916\">1916</a>&nbsp;р., і нарешті, п\'ята&nbsp;&mdash; <a title=\"1919\" href=\"http://uk.wikipedia.org/wiki/1919\">1919</a>&nbsp;р.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уперше &laquo;Щедрик&raquo; був виконаний хором <a class=\"mw-redirect\" title=\"Київський університет\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2%D1%81%D1%8C%D0%BA%D0%B8%D0%B9_%D1%83%D0%BD%D1%96%D0%B2%D0%B5%D1%80%D1%81%D0%B8%D1%82%D0%B5%D1%82\">Київського Університету</a> у <a title=\"1916\" href=\"http://uk.wikipedia.org/wiki/1916\">1916</a> році&nbsp;&mdash; у період, коли композитор працював у <a title=\"Київ\" href=\"http://uk.wikipedia.org/wiki/%D0%9A%D0%B8%D1%97%D0%B2\">Києві</a>, де керував хоровими колективами, викладав в <a class=\"new\" title=\"Державний музично-драматичний інститут імені М. В. Лисенка (ще не написана)\" href=\"http://uk.wikipedia.org/w/index.php?title=%D0%94%D0%B5%D1%80%D0%B6%D0%B0%D0%B2%D0%BD%D0%B8%D0%B9_%D0%BC%D1%83%D0%B7%D0%B8%D1%87%D0%BD%D0%BE-%D0%B4%D1%80%D0%B0%D0%BC%D0%B0%D1%82%D0%B8%D1%87%D0%BD%D0%B8%D0%B9_%D1%96%D0%BD%D1%81%D1%82%D0%B8%D1%82%D1%83%D1%82_%D1%96%D0%BC%D0%B5%D0%BD%D1%96_%D0%9C._%D0%92._%D0%9B%D0%B8%D1%81%D0%B5%D0%BD%D0%BA%D0%B0&amp;action=edit&amp;redlink=1\">Музично-драматичному інституті ім. Миколи Лисенка</a>, працював в музичному відділі Київського обласного комітету та у Всеукраїнському комітеті мистецтв та очолив щойно створений державний оркестр.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><iframe src=\"http://www.youtube.com/embed/WZW61DvcA_U\" frameborder=\"0\" width=\"425\" height=\"350\"></iframe><br /></span></p>','«Щедрик»','uk',2900,4383,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: left;\" src=\"image,4612,200,106408_medium.jpg\" alt=\"\" width=\"170\" height=\"106\" />&laquo;Господи, Ти &ndash; дихання моє, Ти &ndash; сила моя, Ти &ndash; любов моя, Ти &ndash; покора моя, Ти &ndash; свобода моя, Ти &ndash; радість моя. Ти &ndash; дихання мого дихання, Ти &ndash; серце мого серця, Ти &ndash; душа моєї душі, Ти &ndash; мудрість моєї мудрості. Ти &ndash; натхнення мого серця, Ти &ndash; небо мого серця, Ти &ndash; багатство мого серця, Ти &ndash; царство Боже мого серця.&raquo;.</strong></span></p>','2013-04-08 12:26:51',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,4612,200,0127.jpg\" alt=\"\" width=\"200\" height=\"150\" />Передвеликодні ре колекції, які провадив отець Онуфрій Репецький, ЧСВВ на парафії св.. Миколая Чудотворця на Аскольдовій з 5 по 7 квітня, посіяли здорове зерно довіри до Христа, скріпили віру у Христа у багатьох серцях. Головною темою реколекційних наук була тема &laquo;ПОКАЯННЯ&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За загальним сприйняттям: <strong>&laquo;Покаяння &ndash; це таїнство, в якому християнин, що відверто визнає свої гріхи перед Богом і виявляє тверду волю покаятися, отримує відпущення гріхів видимо від священика, а невидимо &ndash; від Самого Господа нашого Ісуса Христа&raquo;.</strong> Отець Онуфрій це твердження розширює до простоти людського сприйняття:<strong> &laquo;Покаяння &ndash; це омивання свого серця, очищення відносин з Тими, проти кого Ти щось маєш. Покаяння &ndash; це плач над вчинками. Якими Ти ображаєш Бога всередині себе. Покаяння &ndash; це неупереджений суд, який ми виносимо собі перед Богом. Покаяння &ndash; це внутрішня переоцінка самого себе. Покаяння &ndash; це відречення від старої людини. Покаяння &ndash; це повернення до безгрішного життя&hellip;&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Духовна віднова, якою щиро ділився отець перед світлим празником воскресіння Христового, відкрила нове бачення живої молитви, живого спілкування з Богом:<strong> &laquo;Господи, Ти &ndash; дихання моє, Ти &ndash; сила моя, Ти &ndash; любов моя, Ти &ndash; покора моя, Ти &ndash; свобода моя, Ти &ndash; радість моя. Ти &ndash; дихання мого дихання, Ти &ndash; серце мого серця, Ти &ndash; душа моєї душі, Ти &ndash; мудрість моєї мудрості. Ти &ndash; натхнення мого серця, Ти &ndash; небо мого серця, Ти &ndash; багатство мого серця, Ти &ndash; царство Боже мого серця.&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слідкуйте за дописами, згодом, Ви зможете побачити реколекції отця Онуфрія, що проходили у церкві св.. Миколая Мирлікійського на Аскольдовій Могилі, у відео форматі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підготувала Любов Михайлюк, &laquo;Оранта&raquo;.</span></p>','«Покаяння – це омивання свого серця»','uk',3129,4612,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,4341,200,cz2.JPG\" alt=\"\" width=\"200\" height=\"133\" />26 листопада 2012 відбудеться проща на Святу Землю</strong>. Про це повідомили в Паломницькому центрі Матері Божої неустанної Помочі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Під час цієї поїздки ми відвідаємо основні святині Святої Землі: Єрусалим, Вифлеєм, Назарет, Канну Галілейську, зануремось&nbsp; у водах ріки Йордан&raquo;, - розповів керівник київської філії паломницького центру Володимир Клименко.</span></p>','2012-11-20 07:33:09',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,4341,200,cz2.JPG\" alt=\"\" width=\"200\" height=\"133\" />26 листопада 2012 відбудеться проща на Святу Землю</strong>. Про це повідомили в Паломницькому центрі Матері Божої неустанної Помочі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Під час цієї поїздки ми відвідаємо основні святині Святої Землі: Єрусалим, Вифлеєм, Назарет, Канну Галілейську, зануремось&nbsp; у водах ріки Йордан&raquo;, - розповів керівник київської філії паломницького центру Володимир Клименко</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За його словами, паломницька поїздка &ndash; це можливість вирватись з буденної суєти і застановитися&nbsp; над духовними пошуками людини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Володимир Клименко переконаний, що кожен християнин повинен хоча б раз в житті відвідати Святу Землю. &laquo;На моєму досвіді, не один приклад, коли навіть невіруючі люди, які побували &nbsp;з нами в паломництві повертались зі Святої Землі вже новонаверненими&nbsp; християнами, - розповів Клименко, -&nbsp; ніколи не знаємо, як діє Бог&raquo;, - додав він.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З програмою поїздки можна ознайомитись на сайті: www.palomnyk.info</span></p>','Паломництво на Святу Землю','uk',2858,4341,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5469,200,43543.jpg\" alt=\"\" width=\"200\" height=\"133\" />29 січня 2014 р. Б. о 12.00</strong> на Аскольдовій Могилі відбудеться панахида пам&rsquo;яті загиблих студентів-героїв Крут, які 1918 року, в такі самі морозні дні, безстрашно виступили на захист своєї Батьківщини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвою вшануємо також пам&rsquo;ять новітніх мучеників +Сергія, +Михайла, +Романа, +Юрія, усіх невинно вбитих за волю України у теперішній час.</span></p>','2014-01-28 22:47:49',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христос раждається!</span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогі у Христі брати й сестри!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5469,200,43543.jpg\" alt=\"\" width=\"200\" height=\"133\" />29 січня 2014 р. Б. о 12.00</strong> на Аскольдовій Могилі відбудеться панахида пам&rsquo;яті загиблих студентів-героїв Крут, які 1918 року, в такі самі морозні дні, безстрашно виступили на захист своєї Батьківщини.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвою вшануємо також пам&rsquo;ять новітніх мучеників +Сергія, +Михайла, +Романа, +Юрія, усіх невинно вбитих за волю України у теперішній час.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Заступництвом святих мучеників студентів-крутянців просимо, Господи, Твоєї милості для України, щоб утвердились на ній гідність, воля, мир та спокій.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На молитву запрошує громада УГКЦ св. Миколая Мирликійського на Аскольдовій Могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З вірою, митрофорний протопресвітер о. Ігор Онишкевич</span></p>','Панахида пам’яті загиблих','uk',3986,5469,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><img style=\"float: right;\" src=\"image,5576,200,1303402_0.jpg\" alt=\"\" width=\"200\" height=\"149\" />Благодійна організація &laquo;Фундація духовного відродження&raquo; УГКЦ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Код ЄДРПОУ 38172005</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">МФО 325365</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Р/р: 2600701394169 у ЦФ ПАТ &laquo;КРЕДОБАНК&raquo; м. Львова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призначення платежу:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійна пожертва на програму &laquo;Люди для Майдану&raquo;</span></p>','2014-02-28 01:56:56',NULL,'P','<p style=\"text-align: center;\">&nbsp;<span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>УКРАЇНСЬКА ГРЕКО-КАТОЛИЦЬКА ЦЕРКВА &laquo;ФУНДАЦІЯ ДУХОВНОГО ВІДРОДЖЕННЯ&raquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img style=\"float: left;\" src=\"image,5576,200,zapisati_18.jpg\" alt=\"\" width=\"143\" height=\"200\" />Програма надання допомоги особам, що потерпіли під час акцій протесту на Майдані: сім&rsquo;ї полеглих на Майдані, особи, що постраждали фізично та психологічно і потребують реабілітації, духовної підтримки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Благодійна організація &laquo;Фундація духовного відродження&raquo; УГКЦ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Код ЄДРПОУ 38172005</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">МФО 325365</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Р/р: 2600701394169 у ЦФ ПАТ &laquo;КРЕДОБАНК&raquo; м. Львова</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призначення платежу:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благодійна пожертва на програму &laquo;Люди для Майдану&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>контакти Фундації Духовного Відродження:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Львів, пл. Св. Юра, 5</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">096&nbsp;955 93 31 096&nbsp;955 93 31 о. Тарас Милян, керівник Фундації</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><a href=\"mailto:foundation.la.ugcc@gmail.com\">foundation.la.ugcc@gmail.com</a></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p>&nbsp;</p>','Програма допомоги потерпілими під час акцій протесту на Майдані «Люди для Майдану»','uk',4093,5576,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Великодні Богослужіння</strong> на Першому Національному телеканалі розпочнуться у суботу, <strong>4 травня</strong>, о <strong>22:55</strong>, і триватимуть усю ніч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Показ Хресної ходита ВоскресноїУтренівідбудеться <strong style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">з кафедральних соборів України</strong></span></p>','2013-05-04 00:15:49',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший Національний телеканал вітає українців із прийдешнім Святом Воскресіння Христового!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У цьому світлому часі щиро запрошуємо долучитися до <strong>прямих трансляцій </strong><strong>знаменних Великодніх богослужінь із кафедральних соборів України</strong><strong>.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Великодні Богослужіння</strong> на Першому Національному телеканалі розпочнуться у суботу, <strong>4 травня</strong>, о <strong>22:55</strong>, і триватимуть усю ніч.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Показ Хресної ходита ВоскресноїУтренівідбудеться <strong>з кафедральних соборів України</strong>:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Володимирський кафедральний собор у Херсонесі;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Володимирський кафедральний собор у Києві;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Храм Святого Василія Великого;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Свято-Андріївський собор.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Повтор Великодніх Богослужінь відбудеться у <strong>неділю, </strong><strong>5 </strong><strong>трав</strong><strong>ня</strong><strong>, з 8:00.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коментують:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Головний редактор Першого Національного телеканалу&nbsp;<strong><em>Христина Стебельська</em></strong>, доктор богослов&rsquo;я <strong><em>Дмитро Степовик</em></strong> і духовенство традиційних українських Церков з часу Володимирового хрещення: <strong><em>о. Ігор Яців</em></strong>, <strong><em>о. Георгій Коваленко </em></strong>та <strong><em>о.</em></strong> <strong><em>Віталій Дан</em></strong><strong><em>ь</em></strong><strong><em>чак</em></strong>.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Запрошуємо до єднання у молитві в часі Воскресіння Христового!</span></p>\r\n<p>&nbsp;</p>',' Великодні Богослужіння на Першому Національному телеканалі','uk',3207,4690,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Витрачено на поштові марки для листів-відповідей до ув&rsquo;язнених жінок &ndash; 435*2.00*2=1&nbsp;740 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всього отримано листів: від ув&rsquo;язнених жінок &ndash; 435, листів-подяк за посилки для дітей ув&rsquo;язнених жінок &ndash; 59, листів від жінок, звільнених з ув&rsquo;язнення &ndash; 9.</span></p>','2014-01-11 21:08:17',NULL,'P','<table style=\"width: 469px; height: 438px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col width=\"176\" /> <col width=\"79\" /> <col width=\"101\" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td class=\"xl65\" colspan=\"2\" width=\"255\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></td>\r\n<td class=\"xl67\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всього за 2013 рік</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl68\" width=\"176\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки,&nbsp;</span></td>\r\n<td class=\"xl64\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кількість, шт</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">228</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl68\" width=\"176\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">в т.ч.</span></td>\r\n<td class=\"xl64\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вага, кг</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">764,626</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl70\" width=\"176\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></td>\r\n<td class=\"xl64\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12&nbsp;955,80*)</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" rowspan=\"3\" width=\"176\" height=\"60\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.Допомога ув&rsquo;язненим жінкам</span></td>\r\n<td class=\"xl63\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кількість, шт</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">95</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вага, кг</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">197,266</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl72\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4 679,51</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" width=\"176\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">в т.ч.</span></td>\r\n<td class=\"xl63\" rowspan=\"2\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl72\" rowspan=\"2\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2 751,26</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" width=\"176\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">медика-менти&nbsp;</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" rowspan=\"3\" width=\"176\" height=\"60\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.Допомога дітям</span></td>\r\n<td class=\"xl63\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кількість, шт</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">132</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вага, кг</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">524,88</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7&nbsp;940,59</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" rowspan=\"3\" width=\"176\" height=\"60\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.Допомога в Будинок матері і дитини&nbsp;</span></td>\r\n<td class=\"xl63\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кількість, шт</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вага, кг</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">14,1</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">194,74</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl71\" rowspan=\"3\" width=\"176\" height=\"61\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.Допомога в медсан-частини колоній</span></td>\r\n<td class=\"xl63\" width=\"79\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кількість, шт</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl63\" width=\"79\" height=\"20\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вага, кг</span></td>\r\n<td class=\"xl69\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">28,38</span></td>\r\n</tr>\r\n<tr>\r\n<td class=\"xl74\" width=\"79\" height=\"21\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість, грн.</span></td>\r\n<td class=\"xl75\" width=\"101\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">140,96</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">*1) &laquo;Різдвяні подарунки&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">*2) &laquo;Пасхальний кошик&raquo; (сформований з подарунків, що надійшли від доброчинців, в т.ч.поінформованих через веб-сайт &laquo;detdom.info&raquo;, врахована тільки грошова пожертва в сумі 700.00 грн. та кошти на пересилання і конверти з марками, вкладені в кожну посилку по 2 шт. &ndash; 1 для зворотнього зв&rsquo;язку і 1 для листа до мами)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">*3) &laquo;Шкільний портфелик&raquo; (подарунки закуплені самими жертводавцями і доповнені речами особистого вжитку, отриманими від доброчинців)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">*4) &laquo;Подарунок від святого Миколая&raquo; (врахована тільки оплата поштових витрат та конвертів з марками, вкладеними у кожну посилку, подарунки прислані громадою Української Католицької Церкви зі США та доповнені одягом, що надійшов від доброчинців)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">*) Без врахування вартості упакування для кожної посилки, пожертвуваного парафіянами церкви св.Миколая на Аскольдовій могилі<strong>.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всього отримано листів: від ув&rsquo;язнених жінок &ndash; 435, листів-подяк за посилки для дітей ув&rsquo;язнених жінок &ndash; 59, листів від жінок, звільнених з ув&rsquo;язнення &ndash; 9.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Витрачено на поштові марки для листів-відповідей до ув&rsquo;язнених жінок &ndash; 435*2.00*2=1&nbsp;740 грн.</strong></span></p>','Звіт про матеріальну допомогу ув’язненим жінкам та їх дітям за 2013 р.','uk',3940,5423,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>03.12.2013</strong><strong> р. </strong>&mdash;<strong> 475 грн.06 коп., в т.ч.медикаменти &ndash; 430 грн.40 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>20 грн. </strong>пересилання &mdash;<strong> 12</strong><strong>9</strong><strong> грн.41 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>03.12.2013</strong><strong> р. &mdash; </strong><strong>624</strong><strong> грн. 47 коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>03.12.2013</strong><strong> р. &ndash; </strong><strong>19</strong><strong>,</strong><strong>50</strong><strong> кг.</strong></span></p>','2013-12-24 21:50:56',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дякуючи пожертвам парафіян, а також гуманітарній допомозі від БФ &laquo;Олива&raquo;, надана медикаментозна допомога, жінкам, які відбувають покарання в місцях позбавлення волі, а також допомога одягом дитячим та жіночим, взуттям дитячим для матері з дитиною, яка звільняється з місць позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомога надавалась медикаментами, гігієнічними засобами, придбаними за пожертви, а також речами особистого вжитку, що надійшли від жертводавців.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожен знайшов у пакуночку духовну літературу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів спаковано і відправлено адресатам 10 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомогу надано:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ользі К. (Чернігівська ВК) на суму 54 грн. 13 коп. +пожертви, в т.ч.<span style=\"text-decoration: underline;\">ліки</span> &ndash; 46 грн.18 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>срок 7 (семь) лет.</strong> Я сирота и помочь мне некому. Если у Вас есть возможность <strong>помогите мне лекарствами</strong>.. Сама я <strong>с Киева</strong>, мама умерла в 2002 г. Выпрыгнула с девятого этажа, у неё был рак крови (42 г.), и потом я и начала сидеть, но я знаю, что Бог меня любит и Он видит моё сердце и мои нужды&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 04.09.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 54 грн.13 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Олені Б. (Чернігівська ВК) на суму 54 грн. 04 коп. +пожертви, в т.ч.<span style=\"text-decoration: underline;\">ліки</span> &ndash; 49 грн.76 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... мне 38 лет... в молитвах прошу у Господа Бога прощение за свои грехи и поступки. Обращаюсь к Вам за помощью <strong>Сделали операцию по-женски, нужны лекарства на продолжение лечения после операции</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 21.08.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 54 грн.04 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Галині А. (Чернігівська ВК) на суму 63 грн.65 коп. +пожертви, в т.ч.<span style=\"text-decoration: underline;\">ліки</span> &ndash; 62 грн.20 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Ми з Вами знайомі уже багато років</strong>. Отець Ігорь, я завжди турбувалась за сина. А зараз потрібна мені допомога... <strong>дуже потрібні ліки від жилудка у мене страшна печія.</strong> Були в санчасті давали &laquo;раутідін&raquo; а зараз і того немає. Дуже прошу допоможіть. Земний Вам уклін, всіх земних благ. Пробачте <strong>як зможете вишліть конвертів</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &mdash; 05.02.2010 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Галині А.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">30.03.2010 р. на суму 81 грн.23 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">синові Андрію</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">21.04.2010 р. в школу-інтернат м.Батурин Бахмацького р-ну Чернігівської обл. на суму 87 грн.41 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.02.2011 р. в м.Кролевець Сумської обл.на суму 77 грн.18 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 309 грн.47 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Катерині І. (Чернігівська ВК) на суму 57 грн. 88 коп. +пожертви, в т.ч.<span style=\"text-decoration: underline;\">ліки </span>&ndash; 55 грн.23 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Прошу Вас помогите мне медикаментами: от кашля, противогрипозные и высылаю Вам рецепт. И если можете очки (+5) и звёздочку</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 04.09.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги 57 грн. 88 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Людмилі К. (Чернігівська ВК ДМР-6) - пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>В цьому році ми будемо вдома. Я звільняюсь 6 грудня. </strong>Коли я звільнюсь мені дуже хочеться, щоб я ходила в храм. І водила Настіньку з собою. <strong>Я висилаю Вам фотки. І дуже прошу Вас, розмножте їх. Зробіть по дві фотки</strong>... Зараз я пішла на роботу, щоб заробити трішки грошенят. Щоб було за що добратись додому... <strong>Якщо у Вас буде можливість, то допоможіть одягом на звільнення. Настусі потрібні: теплі штани, сапожки 28 розміру, рубзачок, тепла шапочка, рукавички</strong>. Мені: тепла кофта, штани... Мені дуже хочеться Катюшу побачити. І щоб сестрички скоріш познайомились<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Людмилі К.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">08.02.2012 р. на суму 55 грн.66 коп., в т.ч. окуляри</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.09.2012 р. на суму 39 грн.31 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.11.2012 р. на суму 57 грн.55 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17.11.2013 р. &ndash; речі на звільнення</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дочці Людмили К. Катерині в школу-інтернат у Волинській обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.01.2013 р. на суму 55 грн.89 коп. &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.09.2013 р. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 208 грн.41 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ользі Л. (Чернігівська ВК) на суму 32 грн.83 коп., <span style=\"text-decoration: underline;\">ліки</span></strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я Вам висилаю рецепт це прости таблетки от голови и от желудка </strong>бо вже ниможу болить<strong> пожалуста вишлить мені лики</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 20.12.2009 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Ользі Л.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">27.10.2010 р. на суму 52 грн.82 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">01.11.2011 р. на суму 39 грн.75 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">30.05.2012 р. на суму 54 грн.83 коп (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.11.2012 р. на суму 29 грн.88 коп., в т.ч.ліки &ndash; 26 грн.29 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18.05.2013 р. на суму 32 грн.57 коп., в т.ч. ліки &ndash; 29 грн.82 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">дочці Галині в школу-інтернат смт Ульянівка Сумської обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.12.2010 р. на суму 122 грн.15 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги </strong><strong>&mdash; 340 грн.94 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Аллі Б. (Чернігівська ВК) на суму 74 грн.04 коп., в т.ч. <span style=\"text-decoration: underline;\">ліки</span> &ndash; 62 грн.15 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>мне необходимы лекарства, названия которых я прикрепляю в двух рецептах на письме</strong>. И сразу же вас благодарю низким поклоном и всем сердцем и от себя и от всех, кому неоднократно помогли и поддержали&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 24.06.2010 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Аллі Б.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.04.2011 р. на суму 72 грн.79 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.02.2012 р. на суму 57 грн. 29 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">27.06.2013 р. на суму 76 грн.11 коп., в т.ч.ліки</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">синові Алли Б. в Дніпропетровську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.11.2010 р. на суму 71 грн.30 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.08.2011 р. на суму 108 грн. 63 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.01.2012 р. &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24.04.2012 р. на суму 78 грн.23 коп. &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">31.08.2012 р. на суму 87 грн.61 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.04.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19.09.2013 р. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 626 грн. 00 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Наталії Н. (Чернігівська ВК) на суму</strong><strong> 47 </strong><strong>грн.15 коп., <span style=\"text-decoration: underline;\">ліки </span></strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Вы мне в 2012 году в марте помогли с медикаментами пройти курс лечения желудка. Я Вам писала благодарное письмо... Сейчас <strong>начал уже с неделю тревожить желудок, </strong>мне очень неудобно просить у Вас ещё рас помощи, но надеятся больше не на кого. <strong>Высылаю Вам рецепт, что сможите...</strong> Срок у меня 7,6 лет мне никто не помогает, <strong>мама сказала: заработала сама себя и обеспечивай</strong>, вот и карапкаюсь уже в апреле будет пол срока, <strong>выживаю с Божьими молитвами</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 20.02.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Наталії Н.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.05.2012 р. на суму 40 грн.61 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">25.04.2013 р. на суму 59 грн.12 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Синові Наталії Н. в Херсонську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.06.2012 р. на суму 45 грн.70 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.09.2012 р. на суму 106 грн.82 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.01.2013 р. на суму 03 грн.60 коп.+пожертви &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 255 грн. 85 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Тетяні П. (Чернігівська ВК) на суму 52грн.95 коп., <span style=\"text-decoration: underline;\">ліки</span></strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Мне требуются лекарства</strong>, и кроме вас я не знаю, к кому мне обратиться. Если у вас есть возможность помочь мне, то я буду вам очень благодарна. <strong>Рецепт с печатью сан.части я вкладываю в это письмо. </strong>Спасибо вам, за то что вы не лицеприятны, а помогаете безкорыстно и искренне&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &mdash; 20.11.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали синові Тетяни П. в Чернігівську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo; на суму 33 грн.22 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 86 грн.17 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Наталії А. (Чернігівська ВК) на суму 38 грн.44 коп., в т.ч.<span style=\"text-decoration: underline;\">ліки</span> &ndash; 21 грн.95 коп.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>у меня проблема с ногой (коленн</strong><strong>ы</strong><strong>й сустав)</strong><strong>. Лечить нечем. Пожалуйста если сможете не откажите моей беде</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 10.10.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Наталії А.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.02.2013 р. на суму 35 грн.00 коп. &ndash; окуляри</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; </strong><strong>7</strong><strong>3 грн.</strong><strong>44</strong><strong> коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>03.12.2013</strong><strong> р. </strong>&mdash;<strong> 475 грн.06 коп.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в т.ч.медикаменти &ndash; 430 грн.40 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>20 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong> 12</strong><strong>9</strong><strong> грн.41 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>03.12.2013</strong><strong> р. &mdash; </strong><strong>624</strong><strong> грн. 47 коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>03.12.2013</strong><strong> р. &ndash; </strong><strong>19</strong><strong>,</strong><strong>50</strong><strong> кг.</strong></span></p>','ЗВІТ №27 про надання матеріальної допомоги жінкам, які відбувають покарання в місцях позбавлення волі (03.12.2013 р.)','uk',3903,5386,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>06.07.2013</strong><strong> р. </strong>&mdash;<strong> 264 грн.80 коп., в т.ч.медикаменти &ndash; 212 грн.67 коп., </strong>вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>12 грн., </strong>пересилання &mdash;<strong>94</strong><strong> грн.15 коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>06.07.2013</strong><strong> р. &mdash; </strong><strong>370</strong><strong> грн. 95 коп. Загальна вага поштових відправлень від </strong><strong>06.07.2013</strong><strong> р. &ndash; </strong><strong>19</strong><strong>,</strong><strong>20</strong><strong> кг.</strong></span></p>','2013-10-29 23:11:18',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дякуючи пожертвам парафіян, а також гуманітарній допомозі від БФ &laquo;Тріумф серця&raquo;, надана матеріальна допомога, в .т.ч.медикаментозна, жінкам, які відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Крім медикаментів, допомога надавалась гігієнічними засобами, продуктами і солодощами, пожертвуваними парафіянами та придбаними за пожертви, а також речами особистого вжитку, що надійшли від жертводавців.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожен знайшов у пакуночку духовну літературу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів спаковано і відправлено адресатам 6 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення парафіянином церкви св.Миколая п.Петром.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомогу надано:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Антоніні Ш. (Качанівська ВК, м.Харків) на суму 13 грн. 27 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я позбавлена волі строком на 5 років. Відбула в колонії вже 1 рік</strong>, залишилось ще 4. <strong>Я інвалід ІІІ групи</strong>. Будучи ще на свободі підчас аварії <strong>втратила ліве око</strong>. <strong>Вашу адресу мені дала хороша людина </strong>&ndash; це Іра В., я в неї попросила допомогти, так як <strong>я не маю звязку з рідними</strong>. <strong>Прошу Вас отець Ігор, якщо Ви в змозі мені допомогти &ndash; вишліть будь-ласка мені речей 50-го розміру, взуття 39 розмір. І обовязково пришліть духовну літературу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 29.12.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 13 грн.27 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Аллі Б. (Чернігівська ВК) на суму 76 грн. 11 коп., в т.ч.<span style=\"text-decoration: underline;\">ліки &ndash; 66 грн.60 коп</span>.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>прошу Вас по возможности помочь лекарствами, которые в выписанном рецепте высылаю</strong>. Понимая, что для каждого, это &ndash; благословение &ndash; благословлять нуждающихся, я молю Бога, чтобы у вас была возможность благословлять других и самим жить в достатке и избытке...<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 24.06.2010 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Аллі Б.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.04.2011 р. на суму 72 грн.79 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.02.2012р. на суму 57 грн. 29 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">синові Алли Б. в Дніпропетровську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.11.2010 р. на суму 71 грн.30 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.08.2011 р. на суму 108 грн. 63 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.01.2012 р. &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24.04.2012 р. на суму 78 грн.23 коп. &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">31.08.2012 р. на суму 87 грн.61 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.04.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 551 грн. 96 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Людмилі Н. (Селезнівська ВК, Луганська обл.) на суму 87 грн.48 коп., в т.ч.<span style=\"text-decoration: underline;\">ліки &ndash; 77 грн.97 коп</span>.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Начальник санчасти прочитал мою медицинскую карту, выписку с Львовского МОБа, и вот какая проблема, <strong>у меня гипатит &laquo;С&raquo;</strong> роспад печени. Я <strong>посылаю Вам рецепт, если в Ваших силах, купите лекарства</strong>, <strong>осталось сидеть 1 год</strong>, но окупировали кучи болячек, и самое основное печень. <strong>Я слёзно вас прошу помогите, в 36 лет я поняла что жить хочеться ещё больше</strong>. Ходила в церковь, помолилась, просила у Господа здоровья родным, прощения грехов, после молитвы роскаялась, я будто-бы родилась на свет, настолько легче стало. <strong>Если сможете вышлите ладанку на грудь, с Матерью и Иисусом</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 09.10.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Людмилі Н.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.02.2013 р. на суму 08 грн.49 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 95 грн.97 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій &nbsp;Оксані К. &nbsp;(Чернігівська ВК) &nbsp;на суму &nbsp;74 грн. 67 коп., в т.ч.<span style=\"text-decoration: underline;\">ліки &ndash; 68 грн.10 коп</span>.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>осужденная сроком на 2 года... сама я из г.Донецка. Сидеть ещё 1 год и 6 месяцев</strong>... Я имею <strong>большие проблемы со здоровьем. Делали уже три операции на правой ноге, сделали одиннадцать надрезов, которые через время перешли в трофические язвы. Уже второй год они не заживают.</strong> Так как я <strong>в прошлом наркоманка, у меня тромбофлебит глубоких вен, нарушена трофика сосудов</strong>... <strong>Очень прошу и умоляю Вас помочь мне с медикаментами... </strong>Я уже так устала от постоянной боли, от постоянных недосыпаний ночами. Я в полном отчаянии. <strong>На всякий случай прилаживаю к своему письму рецепты от врача</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 08.06.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги 74 грн. 67 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Начальникові медсанчастини Чернігівської ВК:</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З благодійних пожертв: розчин для дезинфекції рук, шприци 20 мл, системи для переливання, серветки для прибирання</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ірині Д. (Качанівська ВК, м.Харків) на суму 13 грн. 27 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... И что меня поразило в Вашем письме: &ndash; о мыслях написать книгу... Я действительно думала об этом, и не раз. Но как это зделать? Каким &laquo;языком&raquo;? Ведь все мои воспоминания о прошлом до Бога, &ndash; это греховная, неосознанная, тёмная и пустая жизнь!.. Я давно (слава моему Спасителю) простила всех и за всё, Бог мне свидетель. Но как рассказать всё, через тот ад, что я прошла?..</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Без Бога жизнь наша бессмысленна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И каждый пусть знает о том,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Что вечно живёт только истина,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воскресшая вместе с Христом&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 07.11.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Ірині Д.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.02.2013 р. на суму 17 грн.01 коп.+пожертви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 30 грн.28 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>06.07.2013</strong><strong> р. </strong>&mdash;<strong> 264 грн.80 коп.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в т.ч.медикаменти &ndash; 212 грн.67 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>12 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong>94</strong><strong> грн.15 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>06.07.2013</strong><strong> р. &mdash; </strong><strong>370</strong><strong> грн. 95 коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>06.07.2013</strong><strong> р. &ndash; </strong><strong>19</strong><strong>,</strong><strong>20</strong><strong> кг.</strong></span></p>','ЗВІТ №17 про надання матеріальної допомоги жінкам, які відбувають покарання в місцях позбавлення волі (06.07.2013 р.)','uk',3727,5210,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>22.06.2013</strong><strong> р. </strong>&mdash;<strong> 130 грн.97 коп., </strong>вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>14 грн., </strong>пересилання &mdash;<strong>135</strong><strong> грн.4</strong><strong>9</strong><strong> коп. Загальна сума витрат від&nbsp; </strong><strong>22.06.2013</strong><strong> р. &mdash; </strong><strong>280</strong><strong> грн. 4</strong><strong>6</strong><strong> коп.Загальна вага поштових відправлень від </strong><strong>22.06.2013</strong><strong> р. &ndash; </strong><strong>29</strong><strong>,9</strong><strong>2</strong><strong> кг.</strong></span></p>','2013-10-29 10:26:05',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Дякуючи пожертвам парафіян, а також гуманітарній допомозі від БФ &laquo;Тріумф серця&raquo;, надана матеріальна допомога жінкам, які відбувають покарання в місцях позбавлення волі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомога надавалась гігієнічними засобами, пожертвуваними парафіянами та придбаними за пожертви, а також продуктами і солодощами, придбаними за пожертви, та речами особистого вжитку, що надійшли від жертводавців.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожен знайшов у пакуночку духовну літературу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів спаковано і відправлено адресатам 7 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення парафіянином церкви св.Миколая п.Орестом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомогу надано:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій</strong><strong> Ользі К. (Селезнівська ВК, Луганська обл.) на суму 15 грн.23 коп.+пожертви </strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; мне 47 лет, родом я с Умани Черкасской обл. Была у меня семья, муж, шестеро детей, но мужа убили, а дети от меня отказались... помогите пожалуйста чем можете. И вышлите если можно литературу, чтобы побольше узнать о Боге... <strong>Работы пока у нас нет, в прошлом месяце зарплата была аж 16 гривен. А надо и помыться и постираться</strong>... Может Господь смилуется и даст нам работу. Каждый день молюсь и прошу у Него Благословения&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 23.10.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 15 грн.23 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ірині В. (Качанівська ВК, м.Харків) на суму 18 грн. 76 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; дякую Вам, що<strong> мої розповіді ви друкуєте в інтернет-газеті для молоді, щоб люди зупинялися вчасно і не робили дурних вчинків. Напишу Вам цікаву історію із свого дитинства</strong>...<strong> я була малою дівчинкою, мені було років 11... Тепер я задаю собі сто раз таке запитання: &laquo;Як я, будучи одинадцятирічною дитиною, могла мати таку тверду віру. Чому подорослішавши, я загубила цю віру тверду, цю святу любов до Господа&raquo;</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 28.09.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Ірині В.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.01.2012 р. вислали &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">05.04.2012 р. на суму 25 грн.90 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.05.2012 р. на суму 23 грн.68 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">25.10.2012 р.на суму 128 грн.31 коп., в т.ч.ліки &mdash; 77 грн.65 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">дитині Ірини В. в Кіровоградську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">13.12.2011 р. - &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19.04.2012 р. на суму 91 грн.46 коп. <strong>&mdash;</strong> &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">31.08.2012 р. на суму 97 грн.23 коп. &ldquo;Шкільний портфелик&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. - &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.04.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 385 грн.34 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Юзепі К. (Збаразька ВК, Тернопільська обл.) на суму 29 грн.82 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>сильно важко тут...</strong> я читаю книги, Біблію, молитви переписую хороші за нашого Господа... Моя бабуля Юзепа <em>(80р.)</em> впала, стало плохо закрутилась голова, синяки набила аж чорні плохо неможе... <strong>дякуємо Господу Богу, що Він нас некинув, а дав Вас, що Ви за нас піклуєтесь...</strong> може зможете пасти для ручок бо я багато пишу собі із газет, журналів, з книг<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 29 грн.82 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Олені М. (Качанівська ВК, м.Харків) на суму 18 грн.51 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Пожалуйста, не забывайте обо мне, мне очень не хватает общения с Вами</strong>. <strong>Прошу Вас помочь мне с предметами личной гигиены, </strong>не осталось ни мыла, ни порошка... <strong>И если сможете, вышлите мне церковную литературу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 14.07.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Олені М.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">25.09.2012 р. на суму 47 грн.47 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 65 грн.98 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Оксані С. (Селезнівська ВК, Луганська обл.) на суму 20 грн. 31 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я работаю на швейке &ndash; швеёй. Вот один заказ вышьем, и сидим месяца полтора без работы. </strong>Шьём трикотажные изделия, но <strong>вся зарплата уходит на выщеты. Если остаётся 7 &ndash; 9 грн. на счету, и что на них купиш</strong>. Один брусок мыла маленького в отоварке стоит 3 грн... <strong>Вот подходит тепло, а одивать и обувать нечего...</strong> Помощи жду как Господа Бога... Спасибо за Вашу духовную поддержку. У Вас очень доброе сердце, что стараетесь уделить внимание таким как я письмами и помогаете материально... Целую Ваши руки&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 06.12.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги 20 грн. 31 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Ользі М. (Селезнівська ВК, Луганська обл.) на суму 15 грн. 07 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Обращаюсь к вам со своей нуждой, <strong>помогите чем сможете. Вещами и продуктами</strong>... и повозможности литературу. Я знаю только молитву &laquo;Отче наш&raquo;<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 21.12.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 15 грн.07 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ув\'язненій Вірі С. (Селезнівська ВК, Луганська обл.) на суму 13 грн. 27 коп.+пожертви</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Мне вещей ненадо... <strong>помогите по-возможности мыльное</strong>: шампунь, мыло туал., порошок <strong>и продуктов что Бог даст</strong>... <strong>кипятильник (05) потому что нечем даже воды греть... если будет возможность хоть гр 200 сала</strong>... Болела, грипп был, но вроде бы прошло...<strong> ещё 1 г. впереди</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 13.12.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 13 грн.27 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>22.06.2013</strong><strong> р. </strong>&mdash;<strong> 130 грн.97 коп.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того, вартість:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>14 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong>135</strong><strong> грн.4</strong><strong>9</strong><strong> коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; </strong><strong>22.06.2013</strong><strong> р. &mdash; </strong><strong>280</strong><strong> грн. 4</strong><strong>6</strong><strong> коп. </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>22.06.2013</strong><strong> р. &ndash; </strong><strong>29</strong><strong>,9</strong><strong>2</strong><strong> кг.</strong></span></p>','ЗВІТ №15 про надання матеріальної допомоги жінкам, які відбувають покарання в місцях позбавлення волі (22.06.2013 р.)','uk',3725,5208,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Может найдется такой человек, что захочет общатся со мной буду искренне рада</strong>. Только пожалуйста вложите в ваше письмо чистый листок и конверт, чтобы я могла Вам ответить. Спасибо этой женщине за это. Помолитесь за её здоровье &ndash; зовут её Оксана &ndash; и за прощение её грехов.</span></p>','2013-02-04 12:53:24',NULL,'P','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лист Людмили Т., яка відбуває покарання в Селезнівській&nbsp; виправній колонії (Луганська обл.)</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мир Вашему Дому!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Так начинаются письма адрессованные Вам. Так же начала и я. Извените, что вторгаюсь в Вашу жизнь с её проблемами и заботами. Обращается к Вам &ndash; бывший БОМЖ, а теперь осужденая, отбывающая наказание в исправительной колонии. <strong>Посадив дерево, построив дом, вырастив дочь в свои 50 лет осталась одна, без крыши над головой</strong>. Да что говорить &ndash; сама так построила свою жизнь. <strong>Тяжело осознавать, что очень трудно всё исправить. </strong>Но ведь можно?! Не правда ли?! Только немножко поддержки, немного милосердия, маленькая частичка чьего-то участия в твоей судьбе. Не добивать упавшего, а помочь подняться, но пока все остается по прежнему: <strong>&laquo;У меня нет матери&raquo;.</strong> Да, виновата я перед дочерью, очень виновата &ndash; опозорила свою седую голову. Вывалятся в грязи легко, а вот отмыться...<strong> Говорят надо научится прощать, но моя кровинка пока что еще не научилась</strong>. А ведь росла хорошим ребенком, у неё было хорошее детство и юность, да и взрослая &ndash; семейная жизнь &ndash; вроде бы в порядке. Характер у неё такой &ndash; правильный. Никогда не лжет, и считает что надо жить так, чтобы не огорчать близких и перед людьми не было стыдно за свои поступки. Все это конечно верно, но жизнь такая штука, что не знаешь другой раз где упадешь или оступишься.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Когда жили на ИваноФранковщине (бывший муж у меня был оттуда родом) она сама пришла к Отцу Нашему Небесному.</strong> Побывав несколько раз со мной на богослужении в праздничные дни (Пасхи, Рождества Христового) <strong>стала ходить в церковь,</strong> иногда со мной, но чаще с соседской девочкой. Говорила, что ей там интересно, <strong>стала посещать уроки Закона Божьего, иногда пыталась учить меня жить за заповедями Божьими.</strong> Ведь я выросла &laquo;во времена застоя&raquo;, времена повального аттеизма. <strong>Я тоже начала ходить в церковь, но для меня очень многое было непонятно, да и не вникала я очень</strong>. Служба &ndash; служба, утренняя &ndash; вечерняя, какая разница. Пришла &ndash; послушала, помолилась своими словами, попросила прощения за грехи свои, ну вот и ладно. <strong>Она, маленькая девочка рассказывала мне о таких простых истинах &ndash; о жизи по Заповедям Божьим, что надо молиться о спасении души своей</strong>. Но для меня это было &ndash; извените за прямоту &ndash; как сказки о &laquo;Тысячи и одной ночи&raquo;. Ведь даже Библию, эту святую книгу, я прочитав шесть раз читала как научно-фантастическую эпопею.<strong> Вникать в суть написанного в ней я стала совсем случайно. В марте этого года сидя в камере и разгадывая кросворды, случайно уловила краем уха, сокамерница вслух читала об изгнании Адама и Евы из Рая</strong>. До этого момента она одна читала тихонько эту святую книгу. Что её заставило прочитать эти строки вслух я не знаю. Но <strong>с этого момента она каждый день читала нам вслух</strong>. И мы все взрослые люди, разные по возврасту, вероисповеданиям, да и кто задумывался об этом, в звенящей тишине ловили каждое ею прочитанное слово.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Жизнь с мужем не сложилась, и я забрав дочь вернулась к родительскому порогу на Херсонщину. Здесь всё пошло по другому. Поначалу она спорила со своими сверстниками, </strong>оставляя и отстаивая своё право выбора, <strong>ходила в церковь.</strong> Меня вызывали в школу, <strong>но прошёл год, пошёл второй. И мой ребенок стал менятся</strong>, возможно устала бороться или начал сказываться &laquo;переходной возвраст&raquo;. <strong>Стала всё реже и реже ходить в церковь, перестала исповедоваться, как-то замкнулась в себе.</strong> Раньше мы с ней очень были дружны и откровенные разговоры &ndash; это был наш душевный праздник. Но теперь всё изменилось, и <strong>достучатся до неё становилось всё трудней и трудней</strong>. Как-то всё таки <strong>ответила:</strong> &laquo;Мне там делать нечего. <strong>Разве это православные христиане &ndash; неуспев выйти из дверей церкви, отстояв службу &ndash; начинают сквернословить, злословить и судить о ближних своих.</strong> Нет, мама (тогда я ещёе была мамой) это не по мне&raquo;. <strong>Дома она продолжала молится, но происходило это все реже и реже. А маме некогда было следить за духовным развитием своего ребенка. Мама устраивала свою личную жизнь. </strong>Вот и доустраивалась, упустила важный момент общения &ndash; потеряла взаимопонимание. Да у нас было все хорошо. <strong>Дочь окончила школу. Выучилась. Выдала её замуж</strong>. Вроде бы все прекрасно. Помогала чем могла. Она меня тоже не обижала. Но <strong>отношения становились все натянутее и натянутее. И в один &laquo;прекрасный&raquo; момент эта ниточка лопнула и я ушла из нашего дома</strong>. Сначала жила на квартире. <strong>Появились &laquo;сочувствующие, заботящиеся&raquo; - рюмка, вторая и всё в розовом свете.</strong> Очнешься, а оно теперь еще хуже чем было. <strong>Покатилось, поехало и вот я здесь</strong>. Знаю, что моя дочь самая хорошая и когда нибудь она простит меня. Ну, а пока... Говорят время лечит &ndash; буду надеется. Извените, что навалила на Вас столько информации, <strong>оно Вам и не надо, но уж хочется с кем нибудь пообщаться, поговорить и послушать,</strong> <strong>да здесь это не возможно. Здесь выслушают, но только осудят и осмеют, </strong>такой уж здесь собран контингент. <strong>Нет, я не говорю, что все плохие. Мы все здесь одинаковы. Но скрываем своё настоящее лицо за маской эгоизма, боясь что не дай Бог ко-нибудь узнает о наших слабостях, болях и печалях.</strong> Зачем, почему, ради чего? Почему нельзя оставатся таким каким ты был до того как попал сюда, зачем всё это лицедейство6 эта грубость, заносчивость, чувство превосходства?! Почему, ну почему мы забываем о том, что мы &ndash; женщины, чьи-то матери, дочери, жены! Очень многие не задумываются об этом или искусно скрывают это за личиной безразличия. <strong>Может найдется человек который мне напишет о своей точке зрения на вопрос: &laquo;В заключении человек исправляется или еще больше морально и душевно разлагается?&raquo;</strong> Этот вопрос задают часто и ответов на него много, но ведь у каждого человека своя точка зрения и свои доводы обосновующиеся на реальных случаях. Давайте пообщаемся!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Еще хочу обратится к Вам с просьбой о помощи</strong> так как к сердцу дочери пока дорога закрыта &ndash; на её помощь расчитывать не приходится, то приходится просить помощь у людей. <strong>Колония еще только обустраивается. Работы как таковой нет.</strong> Работают единицы &ndash; хозобслуга (повара, прачки) да бригада строителей. Вот и все кто может расчитывать на заработанные деньги. Все остальные перебиваются кто как. Кому-то помогают родственники, кто-то &laquo;шестерит&raquo;. Извените, находится в услужении у этих &laquo;щасливчиков&raquo;. Но здесь конкуренция похлеще чем на бирже. Еще раз извените за такие выражения, но как говорят &laquo;из песни слова не выбросишь&raquo; да и &laquo;с кем поведешься от того и наберешься&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Я не боюсь работы</strong> &ndash; пусть она меня боится. Трудилась, трудится буду, было бы к чему руки приложить, <strong>но заработать пока даже на самое необходимое нет возможности. </strong>Даст Бог может со временем все наладится а до этого времени помогите, если есть такая возможность Вы.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Нужны деньги на востановление документов</strong>. Пока нахожусь здесь это более приемливый вариант, так как на свободе это очень долговременная и материально недешевая операция. Здесь же на все это нужно пятдесят гривен. Так же как гипертоник нуждаюсь в лекарствах или можно деньги на приобретение этих лекарств начальником медсанчасти этого учреждения. <strong>Помогите моющими средствами, предметами личной гигиены. Нужны тетради, конверты, ручка. А еще чай и сахар. Что-нибудь из продуктов питания. Очень хочется сладкого</strong>, наверное все женщины в любом возрасте остаются большими сластенами. Извените меня за назойливость и наглость. За то, что я попрошайничаю, но обстоятельства заставляют. Всю жизнь я привыкла обеспечивать себя всем необходимым сама. Но вот пришло такое время, что сейчас это пока не возможно. Помогите чем сможете.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Любая, самая маленькая помощь, доброе слово или совет принесут в мою серую жизнь лучик света и радости.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Может найдется такой человек, что захочет общатся со мной буду искренне рада</strong>. Только пожалуйста вложите в ваше письмо чистый листок и конверт, чтобы я могла Вам ответить. У меня пока что с этим большая проблема. Даже это письмо я пишу на чужой бумаге, которую мне дали (вернее поделились тем последним, что у нее было) и предложили вложить это письмо в её же конверт. Спасибо этой женщине за это. Помолитесь за её здоровье &ndash; зовут её Оксана &ndash; и за прощение её грехов.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Простите еще раз за мою назойливость и наглость. Уж очень не люблю просить, но другого выхода пока что нет.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Пусть Господь Наш бережет Вас и Ваших близких.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С уважением Людмила Т.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лист Марини Д., яка відбуває покарання в Селезнівській&nbsp; виправній колонії (Луганська обл.)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мир! Вашему дому.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Здравствуйте милые братья и сёстры.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пообщавшися с женщиной я многое услышала о вашей церкви, и что то из нутри мне подсказало написать Вам. Я верю в то что это письмо будет не последним и мы сможем и в дальнейшем общатся. Немного напишу о себе и о своей жизни. Мне 37 лет. Я родилась и выросла в городе Днепродзержинске, Там закончила школу, поступила в училище, где проучилась два года и получила диплом плиточник-мазаячник. Закончив учёбу устроилась на работу по професии, у меня замечательная семья была. Я вышла замуж и уехала жить к мужу, где и получила первый срок. Надеялась что прийду и будет всё нормально, буду жить без кременала, устроюсь на работу. Но мои надежды не осуществились. Я вышла поехала к родителям, мужу интереса до меня небыло. Я начала строить свою жизнь с чистого листа, стала работать реализатором, всё у меня наладилось. И в один прекрасный момент ко мне приехал брат моего мужа и сообщил мне что моего мужа посадили на шесть лет. И что он очень просит моего прощения, приносит свои извинения что бросил не дождался меня, и если у меня есть хоть немного жалости, я смогу простить и приехать к нему. Конечно я его понимала потому что сама всё это прошла, прочуствывала. Я пошла в церкву, я молилась я просила у Господа прощения и просила уразумить моего мужа, стать ему путеводителем, укрепить его. Я стала к нему ездить, и расказывала ему, что меня окружають очень хорошие люди, что они потдержуют меня, молятца за меня и мою семью. Я верила в то, что он выйдет и у нас будет всё хорошо. Я проездила к нему шесть лет, хотя те кто были его друзьями, все отвернулись и мне говорили брось его, найди себе другого. Я не слушала никого я его ждала. Пришло время я встретила его. И сразу все друзья появились. Он меня перестал слушать, стал позно приходить домой, грубить мне. Я стала уходить из дому стала выпивать, прогуливать работу, а потом и совсем её потеряла. Можно сказать почуствовала себя безответственной и независимой. Ма, ма просила остановитца, братья и сёстры много раз проводили со мной беседу но я вела себя как то страно как будто находилась под какимто гепнозом. В том году у меня умер отец. Мама пенционерка, осталась одна, и сейчас я понимаю её как ей нелегко жить одной в своём доме, где нескем не поговорить не посмеятца, не попить чая, непрошло и года как повесился мой муж. И после его смерти я несмогла удержатца, ещё больше меня потянуло на всякие подвиги. И вот совсем стало всё безразлично, стала отходить всё дальше и дальше от дома, оставатца на ночь у знакомых, много пить, ходить соберать металолом потом сздавать его и тратить деньги на спертное, и всё это довело меня до двух лет этой колонии. Теперь только молю Господа чтобы простил мои грехи сделанные необдуманно. И я верю в то что когда я выйду из этих мест, некогда не вернусь к прошлому. Пишу Вам это письмо в надежде что вы потдержите меня, будите мне писать. Я знаю что из нашей колонии многие потдержуют с вами переписку, и людям нравитца с вами общатца. Хочетца верить что есть люди которые тебя поймут и потдержут. И если у Вас будет свободное время, неоставте меня без своего внимания, напишите мне. Я очень буду ждать вашего письма. Обращаюсь к вам со своей нуждой. Как я вам уже писала что мама у меня пенционерка и помочь мне не может, кроме что написать мне письмо, и за это я ей благодарна, ведь мама это святое в моей жизни я люблю её и нехочу её утруждать, просить о помощи. Обращаюсь к Вам помогите пожайлуста чем сможите. Здесь колония новая, быта некакого, очень холодно, у меня нет тёплых вещей и обуви, хожу в тапочках, да и печенюшку с чаем хочетца хоть какую скушать. Если у Вас будет такая возможность не откажите пожайлуста. Я очень буду надеятца на ваш ответ и потдержку. У меня размер обуви 37, а вещи 46 размера. Помогите пожайлуста, и если можно вышлите кипятильник. За рание очень вам благодарна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поздравляю Вас с наступающим новым годом, счастья вам, здоровья, чистого неба над головой, чтобы всё задуманое сбылось, все невзгоды отошли, а в Новом году было только самое лучшее земное счастье.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Очень жду Вашего письма.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">С ув. Марина</span></p>','Листи з Селезнівської виправної колонії','uk',2942,4425,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сума витрат-пожертв для &laquo;Шкільного портфелика&raquo; &mdash; 4 000 грн. </strong>Вартість поштових конвертів, вкладених у посилки &mdash; <strong>122 грн., </strong>пересилання &mdash;<strong> 876 грн.57 коп. Загальна сума витрат на проект &laquo;Шкільний портфелик&raquo; - 4 998 грн.57 коп. Загальна вага поштових відправлень 173,4</strong><strong>1</strong><strong> кг.</strong></span></p>','2013-10-31 14:06:52',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Завершуємо наповнення &laquo;Шкільного портфелика&raquo;. Він навідається до всіх, про кого потурбувалась мати за в&rsquo;язничними мурами, покутуючи провину перед дитиною.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая на Аскольдовій могилі о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А листи приходять ще і ще... З проханнями, з вдячністю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки отримали 11 адресатів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Діана-8р., Олександра-6р.) ув\'язненої Інни К. (Чернігівська ВК) в Київську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо Вам большое за то что Вы поддержуете меня и мою семью </strong>Дианка сказала что пускай этих людей Бог охраняет и спасибо за всё, что вы делаете.<strong>...</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 30.05.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Інни К. в Київську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.062012 р. на суму 45 грн.70 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.09.2012 р. на суму 141 грн.13 коп. &ndash; &laquo;Шільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">30.04.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 186 грн.83 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Настя - 6р.)</strong> <strong>ув\'язненої Юлії Є. (Чернігівська ВК) в Київську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Волею судьбы я оказалась в м.л.с. мой муж также находится неизвестно где. <strong>Мой ребёнок (6 лет) находится на попечении моей мамы. Мама ещё не на пенсии, работает и еле сводит концы с концами</strong>. Настя (это моя дочка) ходит в садик это последний год, а в сентябре уже идёт в школу. <strong>Здесь я узнала что больна &laquo;ВИЧ&raquo;</strong> теперь молю Бога чтоб дал мне немного здоровья, чтоб выйти и воспитывать дочь. <strong>Пожалуйста помогите по возможности</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 25.01.2013 р.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Іванна - 7р.) ув\'язненої Любові Д. (Збаразька ВК, Тернопільська обл.) в Тернопільську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Я Вам очень за детей благодарная, шо Ви сможете помочь моїм детям до школи</strong>&hellip;Росла тоже в дет.доме раднія розбілісь мне було 3 года и осталась я сиротой но стала мамой двоїх детей&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 21.07.2011 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Любові Д. в Тернопільську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">03.09.2012 р. на суму 129 грн.80 коп. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 129 грн.80 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Іванко - 13 р.) ув\'язненої Галини Б (Чернігівська ВК) в м.Вінниця </strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Мои семеро деток которые проживают в Виннице, очень благодарны Вам, за помощь</strong>... я молюсь, хожу по суботам в церковь. Прошу у Господа нашего, чтобы дал мне здоровья, сил, терпенья, выдержать эти муки, и выйти живой к детям, <strong>мне только 48 лет, а так хочется ещё жить, быть вместе с детьми... Молитесь за меня и моих деток. Я нахожусь в местах лишения свободы уже без двух месяцев 5 лет, ещё 1 год 9 мес.&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 10.01.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомогали дітям Галини Б. в м.Вінниця:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.02.2012 р. на суму 123 грн.67 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">26.04.2012 р. на суму 112 грн.49 коп. &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">31.08.2012 р. на суму 92 грн.54 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">04.01.2013 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">01.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 263 грн.24 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Марія і Дарія &ndash; двійнята, 6 р.) звільненої з ув\'язнення Наталії.Н. (Чернігівська ВК) в Чернігівську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... ходила с детьми в церковь помолилась и поставила свечку за Ваше здоровье и всех кто не был безразличен в судьбе моих детей... <strong>Благодаря Вашим и маминым молитвам Господь помиловал меня. Моя душа и жизнь дальнейшая в руках Господа и ради детей есть смысл жизни</strong>. Поэтому я не хочу оступатся, <strong>я выбор сделала быть с Господом </strong>ведь не зря Он мне дал такой шанс, и этот шанс я не хочу упускать&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 18.01.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Наталії Н. в Чернігівську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.04.2012 р. на суму 105 грн.49 коп. &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.09.2012 р. на суму 126 грн.58 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15.12.2012 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 232 грн.07 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Денис &ndash; 15 р.) ув\'язненої Анжели К. (Селезнівська ВК, Луганська обл.) в м.Дніпропетровськ</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасибо что Вы есть! Вы согласились помочь моему сыну с вещами он в сентябре пойдёт в школу. И очень нуждается в куртке, ботинках 38р. спортивный костюм 44р.</strong> По возможности, чем сможете помогите... Еще раз благодарю, за всё что Вы делаете и не оставляете людей со своим горем&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 24.04.2013 р.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Анастасія-13р., Олег-6р.) ув\'язненої Ірини С. (Чернігівська ВК) в м.Запоріжжя</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Слава Господу нашему, меня раньше отпустили домой и теперь я с Георгием (<em>народився 06 червня 2013 р. в Чернігівській ВК</em>) нахожусь дома!<strong> Огромное Вам спасибо за ту помощь и поддержку, какую вы высылали моим деткам.</strong> Сейчас очень тяжело. <strong>Везде натыкаюсь на непонимание и предвзятое отношение. </strong>В оформлении помощи детям есть проблемы... <strong>Очень надеюсь на вашу помощь&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &mdash; 23.05.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Ірині С.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.06.2012 р на суму 42 грн.80 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19.04.2013 р. на суму 110 грн.21 коп., в т.ч.ліки &ndash; 100 грн.85 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям Ірини С. в м.Запоріжжя:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.09.2012 р. на суму 115 грн.15 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23.01.2013 р. на суму 24 грн.47 коп. &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги 292 грн.63 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Володимир-7,5р.)</strong><strong>ув\'язненої Світлани В. (Чернігівська ВК)</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в Чернігівську обл.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... У нас дозволяють звонить 2 рази на тиждень. <strong>З сином спілкуюсь. Здоровья в нього пока нормально (</strong><em>ВІЛ-інфікований</em><strong>). Дуже він цікавиться Біблейними історіями.</strong> <strong>Читає дитячю &laquo;Біблію&raquo;.</strong> У мене теж все нормально <strong>готуюся до ісповіді 4.05. Я хожу до церкви до отця Віталія. Греко -католицької</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 28.12.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали синові Світлани В. в Чернігівську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Євгенія-6р.) ув\'язненої Ірини Б. (Селезнівська ВК, Луганська обл.) в Дніпропетровську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Срок у меня 5 лет... <strong>На свободе меня ждут две мои дочьки</strong>. <strong>Старшей Сюзанне исполнился 21 год и она оформляет опекунство над младшей Женечкой, которой 6 лет</strong>. Раньше они были под присмотром моего старшего брата, но он сейчас находится под следствием в СИЗО... <strong>Ежемесячные пособия не получают... я Вас умоляю уделите моим крошкам внимание. По возможности самым необходимым</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 18.01.2013 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Ірини Б. в Дніпропетровську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Софія - 9р.) ув\'язненої Оксани З. (Качанівська ВК, м.Харків) в м.Харків</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>до кінця сроку ще 2,5 років</strong>... <strong>З донечкою я спілкуюся</strong>.... Я дзвонила додому, дочка була така рада, <strong>питала в мене коли я приїду додому. А що я їй скажу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 05.06.2011 р.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Оксані З.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.05.2012 р. на суму 23 грн.68 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">дочці Оксани З.в м.Харків:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">29.08.2011р. на суму 112 грн. 29 коп. &ldquo;Шкільний портфелик&rdquo; (<em>посилка повернулась</em>)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.01.2012 р. - &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.09.2012 р. на суму 82 грн.88 коп. - &ldquo;Шкільний портфелик&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &mdash; 135 грн. 97 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>11.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина &nbsp;(Євген - 12 р.) &nbsp;ув\'язненої &nbsp;Лілії С. &nbsp;(Чернігівська ВК) </strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в Сумську обл.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>мои дети остались одни. Старшая дочь Анастасия ей 22 года является опекуном еще двоих моих детей, Маша &ndash; 17 лет и Евгений &ndash; 12 лет</strong>. Я конечно понимаю, что я виновата, но я Вас очень прошу <strong>помогите моим детям чем сможете</strong>... Я выросла без родителей в детском доме. Родственников тоже нет&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 29.11.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Лілії С. в Сумську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">03.01.2013 р. на суму 21 грн.17 коп.+пожертви &ndash; &laquo;Різдвяний подарунок&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">02.05.2013 р. &ndash; &laquo;Пасхальний кошик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги - 21 грн.17 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість поштових конвертів, вкладених у посилки &mdash; <strong>36 грн.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong> 282 грн.31 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>24.09</strong><strong>.2013 р. &ndash;53,5</strong><strong>1</strong><strong> кг.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сума витрат-пожертв для &laquo;Шкільного портфелика&raquo; &mdash; 4 000 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вартість поштових конвертів, вкладених у посилки &mdash; <strong>122 грн.,</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong> 876 грн.57 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат на проект &laquo;Шкільний портфелик&raquo; - 4 998 грн.57 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень 173,4</strong><strong>1</strong><strong> кг.</strong></span></p>','ЗВІТ №22 про «Шкільний портфелик» дітям, матері яких відбувають покарання в місцях позбавлення волі (24.09.2013 р.)','uk',3735,5218,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Я хочу попросить помощи для своего сыночка Жени&hellip; одеждой, продуктами и школьными принадлежностями, </strong>костюм спортивный р.1м50 см, сапоги или кросовки 34-35 р. свитерок и водолазку он среднего телосложения, а из школьных предметов тетрадки, ручки, карандаши, линейки, обложки на тетради и книжки он пошёл в 6 класс, пенал, папки, альбомы и набор для черчения&hellip;</span></p>','2013-03-11 03:11:55',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пожертви наших парафіян, зокрема п.Уляни, п.Оксани і п.Володимира, п.Уляни і п.Юрія, а також благодійних фондів &laquo;Олива&raquo; (одяг, взуття, іграшки, засоби гігієни) та &laquo;Тріумф серця&raquo; (солодощі) забезпечили можливість продовжити свято Різдвяних подарунків для дітей, матері яких відбувають покарання в місцях позбавлення волі. Кожен подарунок упакований в поштові коробки, пожертвувані шановними добродіями п.Вірою та п.Олександром, і відправлений кожному із дітей на адресу опікунів, в яких вони виховуються. П.Дмитро та п.Світлана поповнили наші ресурси поштових конвертів та марок, пп.Ліля та Андрій К. принесли в дарунок поштові марки для конвертів зворотнього зв&rsquo;язку з адресатами, які вкладаються в кожну посилку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Крім перелічених видів матеріальної допомоги, діти отримають дитячу літературу, книжечку &laquo;Бог говорить до своїх дітей&raquo;, різдвяні образочки, які нам любязно подарувала п.Дана К.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Силами волонтерів: п.Віри, п.Мирослави, п.Михайла та п.Анни спаковано, оформлено і відправлено адресатам 8 посилок.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая о.Ігорем Онишкевичем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посилки отримали:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Євген-11р., Кирило-6р., Настя-4р., Василиса-2,5р.) ув\'язненої Інни К. (Чернігівська ВК) на суму (40грн.80коп.+пожертви) в м.Євпаторію АР Крим</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; Я сама из г.Евпатории, <strong>мне 31 год, у меня 4 деток им 11 лет Женя, Кирилл 6 лет, Насти 4 года и Василиса ей 2 г.3 мес. Я хочу попросить помощи для своего сыночка Жени&hellip; одеждой, продуктами и школьными принадлежностями, </strong>костюм спортивный р.1м50 см, сапоги или кросовки 34-35 р. свитерок и водолазку он среднего телосложения, а из школьных предметов тетрадки, ручки, карандаши, линейки, обложки на тетради и книжки он пошёл в 6 класс, пенал, папки, альбомы и набор для черчения&hellip;Немного о себе: я работаю <strong>в Доме ребёнка</strong> нянькой на старшей группе, тут их у нас <strong>16 человек на старшей группе</strong>. <strong>Сижу за то, что заняла 900 гривен на лечение младшей дочери и не вернула</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &mdash; 24.09.2012 р.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 40 грн.80 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Адам-3р.) ув\'язненої Інни Г (Чернігівська ВК) </strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>в Харківську обл.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Огромное Вам спасибо, что не забываете обо мне и моих детках!</strong> Пишу Вам адреса моих детей... Младший сыночек Адам живет с бабушкой... Старший сыночек Илья живёт с моей мамой. <strong>У Илюшеньки травма колена</strong>. Врачи говорят, что если не срастётся кость то придётся оперировать. Молюсь и днём и вечером. Для Господа нет ничего невозможного. Хочу Вас попросить Благословенный Отец Игорь <strong>помолитесь за здоровье моего сыночка Илюшеньки</strong>. Пусть хранит Вас Господь!&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист <strong>&mdash;</strong> 24.07.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали синові Іллі в Харківську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">03.09.2012 р. на суму 102 грн.46 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 102 грн.46 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (ЛізаЄвген &ndash; 13 р.) ув\'язненої Олени А. (Чернігівська ВК.) на суму (03 грн. 60 коп.+пожертви) в м.Чернігів</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Я очень благодарна Вам за помощь детям, поэтому не надо мне лекарства,</strong> сейчас всё очень дорогое и я приблизительно понимаю сколько нужно денег чтобы отправить посылку ребенку. <strong>Спасибо Вам за эту помощь&hellip; Она (<em>дочка, 13 р.</em>) любит рисовать, может быть у Вас есть какая-то книга по-рисованию, она была бы рада&hellip; За всё чтобы Вы не выслали ей я буду благодарна</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 03.07.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дитині Олени А. в.м.Чернігів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">05.09.2012 р. на суму 93 грн.18 коп. &ndash; &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 96 грн.78 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Анастасія-12р., Олег-4р.) ув\'язненої Ірини С. (Чернігівська ВК) на суму (24 грн.47 коп.+пожертви) в м.Запоріжжя</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Моей дочери Анастасии 21 декабря исполнится 12 лет &ndash; она учится в школе, занимается бальными танцами.</strong> Ездит на соревнования, занимает призовые места. Маме очень тяжело способствовать её занятиям, но она делает всё, чтоб воплотить Настины мечты в жизнь. <strong>Мой сын Олежка 2008 г.р., 26 сентября ему будет 4 года. Он ходит в садик. Очень любит танцевать и читать</strong>. Они у меня немного слабоваты здоровьем &ndash; хронический бронхит у обох... <strong>Буду Вам безмерно благодарна за помощь и поддержку им</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &mdash; 23.05.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Ірині С.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.06.2012 р на суму 42 грн.80 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям Ірини С. в м.Запоріжжя:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.09.2012 р. на суму 115 грн.15 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги 182 грн.42 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Михайло, 11р.)</strong> <strong>ув\'язненої Наталії Н. (Чернігівська ВК) на суму (03 грн.60 коп.+пожертви) в Херсонську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip;<strong>мне так хотелось чегото хорошего зделать для своего сыночка</strong>&hellip;<strong> воспитывают его бабушка с дедушкой, хотелось бы ему немножко помочь, но у меня нет возможности...</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 20.02.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали Наталії Н.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">07.05.2012 р. на суму 40 грн.61 коп. (ліки)</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Синові Михайлові, 10 р. в Херсонську обл.:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20.06.2012 р. на суму 45 грн.70 коп.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">06.09.2012 р. на суму 106 грн.82 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 196 грн. 73 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (дочка, 14 р.) ув\'язненої Наталії У. (Чернігівська ВК) на суму (19 грн.94 коп.+пожертви) в Луганську обл.</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; 33 года&hellip; Срок большой, осталось ещё 4 года. <strong>У меня дома мать, отец и дочь (14 лет)&hellip; сейчас у дочери обнаружили множество заболеваний</strong>... Сейчас мать была в Луганском институте пока дочь поставили на месяц на курс личения. <strong>Я молю Вас о помощи</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 18.09.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 19 грн.94 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Діти (Азіз-6,5р., Алі-3р.)ув\'язненої Надії П. (Чернігівська ВК) на суму (05грн.50коп.+пожертви) в м.Алушта АР Крим</strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>Спасибо Вам что окликнулись на мою просьбу помочь моим детям. И теперь они получат подарки на день св.Николая</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 30.03.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Допомагали дітям Надії П. в м.Алушта АР Крим:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">26.04.2012 р. на суму 85 грн.34 коп &laquo;Пасхальний кошик&raquo;посилка повернулась</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">03.09.2012 р. на суму 117 грн.21 коп. &laquo;Шкільний портфелик&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; 117 грн. 21 коп.</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитина (Віталій, 8 міс.) Інни П., звільненої з Чернігівської ВК, </strong></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>на суму (05 грн.45 коп.+пожертви) в АР Крим</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;&hellip; <strong>мы с моим сыночком освобождаемся 10 декабря</strong>. Моя мама старушечка заберает нас напоруки... Если вы сможете, если вас это незатруднит Пожалуста <strong>вышлите нам посылкой Ігрушку...</strong> О.Ігор <strong>помолитесь за нас в дорогу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший лист &ndash; 01.08.2012 р.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума допомоги &ndash; </strong><strong>05</strong><strong> грн. </strong><strong>45</strong><strong> коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума матеріальної допомоги </strong><strong>01.02.2013</strong><strong> р. </strong>&mdash;<strong> 103 грн.36 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крім того., вартість:&nbsp;&nbsp;&nbsp; поштових конвертів, вкладених у посилки &mdash; <strong>16 грн.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">пересилання &mdash;<strong> 151 грн.05 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна сума витрат від&nbsp; 01.02.2013 р. &mdash; 270 грн. 41 коп.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Загальна вага поштових відправлень від </strong><strong>01.02</strong><strong>.2013 р. &ndash;33,</strong><strong>2</strong><strong> кг.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>','ЗВІТ №2 про Різдвяні подарунки дітям, матері яких відбувають покарання в місцях позбавлення волі,  (01.02.2013 р.)','uk',3026,4509,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, сміливий і гордий,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великий звитяжець минулих століть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, у серці народу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ять про тебе не спить.</span></p>','2013-12-28 21:17:02',NULL,'P','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Аскольде, Аскольде!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, Муз. Катерини Кучерявої</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Прекрасна наша ненька Україна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Багато мала видатних синів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що рятували й захищали від руїни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що їх вогонь через віки горів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У стольнім нашім граді князювали</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І мудрістю провадили народ,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І славу й честь країні здобували,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серед них &ndash; великий князь Аскольд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, сміливий і гордий,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великий звитяжець минулих століть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, у серці народу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ять про тебе не спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Замріяний Дніпро, завмерли хвилі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи спить, а чи когось згадав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В твоїй цілющій прохолоді синій</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд-князь силу набирав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ця сила тут, вона між нами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відвічним світочем горить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб об&rsquo;єднати Україну</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І розбудити всіх, хто спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Аскольде, ти оспіваний в віках&hellip;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Світлани Маховської</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Господарка незайманих гаїв,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Славетна барвінкова Україна.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одна з найщасливіших матерів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо народила видатного сина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відоме всім його просте ім&rsquo;я &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд, який в бою шукав натхнення,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І для своєї неньки за життя</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У Бога попросив благословення.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Приспів:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, ти оспіваний в віках,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Київській Землі, в Дніпровських Водах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З мечем відважним у міцних руках</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти перемогу здобував в походах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Почуй наш спів, крізь плин тисячоліть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай голос віри крізь роки лунає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Твого прозріння неповторну мить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай кожне вдячне серце пам&rsquo;ятає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>2. </strong>Історії барвисте полотно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про багатьох синів своїх забуде,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та в серці материнськім все одно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Безсмертною дитина завжди буде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Колись &ndash; могутня неповторна Русь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сьогодні &ndash; незалежна Україна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Продовжує, як кожна із матусь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любити свого хрещеного сина!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Приспів:</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Як Аскольд тут проживав</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Богдана Водинюка</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. І до хрещення Русі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Так на кручах цих Дніпрових</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд тут проживав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Провістив слова Христові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цим пророцтво він здійснив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від апостола святого,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що майбутнє сповістив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">шлях до Бога тут живого.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Навернувшись до Христа</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Із походу з Візантії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він відчув дивні діла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Все у вірі і надії.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І охрещений народ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Аскольдовій могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву тут він збудував,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Честь йому дає донині.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Як Андрій, пізніш Аскольд</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будьмо ми завжди і всюди,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Йти, де ще ніхто не був,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З нами щоб спаслися люди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На землі цій грішній й скрізь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Царство Боже проростало,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Стало стержнем як та вісь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всіх із богом поєднало.</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Аскольде!</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Олени Сакалюк, муз. Роксолани Шпіци</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Земля вже вкрита сніговим покровом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лиш перший промінь сяє у красі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ми прийшли до церкви і святково</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Помолимося на святій горі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Приспів:</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, з небес подивись,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти цар древньоруський &ndash; за неї молись.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збирає до церкви святковий дзвін нас,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, серця ти молитвою спас.</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У день оцей святого миколая</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лунає звідусіль аскольдів глас</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любов&rsquo;ю кожне серденько палає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитва щира всіх з&rsquo;єднала нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У церкві цій святого Миколая</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Душа ніколи болем не живе.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серденько до Господа співає:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Аскольдів глас&raquo; ми любимо тебе.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там біля могили в церкві чути спів</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти в блаженстві з Богом спочиваєш.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Святий Миолай, твій ангел прилетів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти в серцях людей вогнем палаєш.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знов лунає передзвін Аскольдів глас</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мовби заклик &laquo;Жиймо всі у Бозі&raquo;,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пломеніє факел віри ще не згас,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо ідем по праведній дорозі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>','Пісні про Аскольда','uk',3898,5381,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженнеє, святеє місце те є,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Був з нього тут початок, а вкінець</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підем у тьмі світити Прометеєм,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо з каяття дістанем вічності вінець.</span></p>','2013-10-08 00:25:18',NULL,'P','<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Анастасія Попеско </strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Аскольдовий глас</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Анастасії Попеско, &nbsp;муз. Сергія Інгленда</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На розлогих Дніпрових схилах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми зберемося дружнім колом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Де Аскольда свята могила,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Де душа не хворіє болем.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Заспіваємо про минуле,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як в краю нашім гордім жилося.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Серце линути завжди буде</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В край без меж золотого колосся.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нас гостинно стрічає Київ,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знов каштани запалять свічі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Золоті купола Софії,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І усмішка сія на обличчі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти поглянь у блакитне небо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На моря, гаї, гори, луки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світ не буде таким без тебе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти повинен щасливим бути.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Любов Кокоєва</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Аскольдів глас</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Люби Кокоєвої, муз. Дмитра Гречина</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Княгиня Ольга, князь Влодимир &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великі руськії князі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Котрі боролись за Вкраїну,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Котрих боялись вороги.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та був у них великий попередник</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольдом-князем звався у миру,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Господня заповідь &ndash; єдиний радник,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тому здолати міг він будь-яку біду.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольдів глас &ndash; велика Божа воля,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що послана з тобою нам з небес,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У неї вірить буду прославлять &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це наша світла доля.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Нас споконвіку били &ndash; убивали,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Рубали нашу віру на корню,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В тіла і душі цвяхи нам вбивали,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та відстояли віру ми свою.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пройшли віки, змінились покоління,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А віра наша тверда та міцна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Залишиться в серцях вона назавжди,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як мудрий князь Аскольд заповідав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Оксана Сливка</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Про Аскольда</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Нестора Нартинця, муз. Ірини Батюк</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Віки прошуміли, мов вітром, грозою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">По древній, могутній Русі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І рід український гордиться тобою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І славу співає тобі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, могутній вояче</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На бій ти за край рідний став.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не раз за тобою земля ще заплаче,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Яку ти колись захищав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Ти вірним був сином своєї держави</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Примножував славу її.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На смерть проти війська чужого стояли,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб мир був на рідній землі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Хоч час той не можна й на мить повернути</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вперед він рікою пливе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Але ми не зможем про тебе забути</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо пам\'ять про тебе живе.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Руслана Лоцман</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>У день святого Миколая</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Руслани Лоцман, муз. Ірини Кириліної</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Коли Господь зішле на землю сніг,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вся природа в казку убереться,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прийде велике свято для усіх,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любов&rsquo;ю ороситься кожне серце.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То вслід за Катериною й Андрієм</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Йде в хату Чудотворець Миколай,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він зерна ласки Божої розсіє</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На наш чудовий український край.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів: </em></strong>А ми у день святого Миколая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Згадаємо великого царя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай з нами древній Київ заспіває,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зійде Аскольда праведна зоря.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Святковий дзвін до церкви зазива,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збирається родина в добрий час,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І Миколай дарує нам дива:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лунає в небесах Аскольдів глас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То молиться наш древньоруський цар за нас</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Людей, і ці місця священні.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми в пісні славим Бога за той дар</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда-Миколая у хрещенні!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів: </em></strong>У день святого Миколая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сіяє в небесах зоря,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То древній Київ величає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда &ndash; славного царя!</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Наталя Сиротич</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">На могилі Аскольда</strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Наталі Сиротич</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"text-indent: 0em; font-family: arial, helvetica, sans-serif; font-size: small; line-height: 1.3em;\">1. Співає Небо: &laquo;Царю Небесний!&raquo;,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До серця входить Христос Воскреслий.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З молитви щирої зростають квіти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І серце вчиться життю радіти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Могилі Аскольда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чути Слово Господнє,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Могилі Аскольда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я у Небо дивлюсь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Могилі Аскольда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Лине пісня Господня,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Могилі Аскольда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я з Аскольдом молюсь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Співає Небо: &laquo;Будьмо уважні!&raquo;,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Душа торкається Святої Чаші.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З молитви щирої зростають квіти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І серце вчиться життю радіти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Мар&rsquo;яна Головко</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Як вабить спокоєм це місце</strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Сл. Олени Кулінської, муз. Мар&rsquo;яни Головко</strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Як вабить спокоєм це місце!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи Дух Святий його обрав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прикрасив сяйвом променистим,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В кленовім золоті сховав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;2. Забудеться моя турбота,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I радощi, спокуса й труд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">I стане свiтлою скорбота...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, гарно в тебе тут!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3. Отут не давнина &ndash; а вiчнiсть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отут не затишок &ndash; а рай.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть вiки, як буднi звичнi,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пливуть думки &ndash; за небокрай.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Ольга Засадна</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кує зозуля</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Нелі Шейко-Медведєвої, муз. О.Засадної</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Засвічу я свічку, засвічу воскову,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уклонюся князю, що приніс нам Слово,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що хотів злучити з християнським світом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Руси-України карооких ді́тей.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів: </em></strong>Кує зозуля на Аскольдовій могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зозуля і кувати завжди буде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Рахує роки і тисячоліття,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Коли любов Христа спізнали наші люди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зоуля, ой кує, наснаги зичить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Вкраїні й церкві нашій, що постала</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;На чесній крові й непохитній вірі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Хрестителів Руси &ndash; князів Аскольда й Діра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Принесу я квіти в церкву українську,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Котики вербові, калинову кров.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не стьмяніє пам&lsquo;ять &ndash; не погаснуть свічі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не змаліє віра &ndash; не помре любов.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів: </em></strong>Кує зозуля на Аскольдовій могилі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зозуля і кувати завжди буде.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Рахує роки і тисячоліття,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Коли любов Христа спізнали наші люди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зозуля, ой кує нам, не вгаває,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зозуля і кувати завжди буде,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Допоки дзвонів голоси лунають</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;І щиро моляться у білих храмах люди.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує зозуля.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Ой&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;Кує, кує&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Марина Коробко</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Аскольде, сину мій</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Зої Дикої, муз. Людмили Бутуханової</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, сину вірний мій,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вкраїна кличе, твоя мати,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Майбутнє краю зрозумій,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зайди у владнії палати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Проліг ти кладкою святих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До ніг стражденного народу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Осяй промінням ще живих</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І освяти дніпровську воду.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, сину мій!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Помилуй наших ворогів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прости братів своїх по крові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай зніме Бог тягар гріхів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благання матері Покрови.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відгороди дорогу злим</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хрестом правиці православних,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоби добро прийшло за тим</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від джерела до справ державних.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, сину мій!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Твоє ім&rsquo;я крізь тінь віків</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Під куполами мого краю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай молодим провідників,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, сину мій, благаєм&hellip;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Складаю шану золоту</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Діянням Божим у щоденні,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За віру праведну святу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що творить милість у натхненні</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, сину мій, Аскольде!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Руслана Дмитровська</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Аскольде, Аскольде!</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, муз. Катерини Кучерявої</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Прекрасна наша ненька Україна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Багато мала видатних синів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що рятували й захищали від руїни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що їх вогонь через віки горів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У стольнім нашім граді князювали</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І мудрістю провадили народ,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І славу й честь країні здобували,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серед них &ndash; великий князь Аскольд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, сміливий і гордий,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великий звитяжець минулих століть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде, у серці народу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пам&rsquo;ять про тебе не спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Замріяний Дніпро, завмерли хвилі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чи спить, а чи когось згадав?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В твоїй цілющій прохолоді синій</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд-князь силу набирав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ця сила тут, вона між нами</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відвічним світочем горить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб об&rsquo;єднати Україну</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І розбудити всіх, хто спить.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Юрій Вольбин</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Аскольд-лицар</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, муз. Катерини Кучерявої</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1. Славний, відважний Аскольд</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В битві сміливо змагався.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він охрестив наш народ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В серцях навіки зостався.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми пам&rsquo;ятаємо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми не забудемо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава! Слава!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів</em></strong>: Іменем твоїм назву я сина,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І може, він прославить Україну,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ти за нього Богу помолись,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб став таким, як і ти колись,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде, Аскольде!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Київ прадавній не спить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князя Аскольда вітає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ворог від страху дрижить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Військо в похід вирушає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Він не зупиниться,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гідно повернеться.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава! Слава!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Андрій Маслаков</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Син Руси-України</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Зої Ружин, муз. Жанни Колодуб</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд &ndash; наш князе, Русі-України.</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Відкрив у хресті князь віру єдину.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усі християни по білому світі</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвами князя зігріті (2).</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хрестом освятив князь Русь-Україну.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Найпершим Ісуса привів у родину,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У вірі рідній цілющая сила,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У нашій пісні від Бога,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Крила від Бога, від Бога.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольде-князю, ти серця утіха</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитви зерна засіюєш тихо.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Великий князю, нам добре з тобою</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У вірі христовій сповиті любов&rsquo;ю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сповиті любов&rsquo;ю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усі християни і сестри і браття.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В родині єдиній творці завзяті.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай сад Аскольда-князя квітує,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітчизні щастя в Христові дарує.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Христові дарує.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Зорина Валіхновська</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Балада про Аскольдів вінець</strong></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Сл. і муз. Зорини Валіхновської</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одної крові Кий і князь Аскольд &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від нього давньоруської історії початки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Походом могутнім прославилась Русь</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про це і нині пам&rsquo;ятаєм ми &ndash; нащадки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Скоривсь Константинополь і цар Михаїл,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Який дивився зверхньо на інші держави.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В Офіри-адмірала була жменька сил</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Й гігантські мури впали для руської слави.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На світанку славетний Аскольд</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Йде до Золотого Рогу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не був напнутий важкий ланцюг &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І наша рать розпочала тижневу столичну облогу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Глава Візантійської церкви</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Історію в творах своїх записав:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">\"Місто ледве не було підняте на спис!\" &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про це Патріарх Фотій повідомляв.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Барвисту легенду \"Повість\" Нестора</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам сповіща: \"У Влахерні цар і Фотій</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До Богородиці молитви склада\".</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І так як в казці щасливий кінець &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мирна угода з Візантією і хрещення &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольда вінець!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Неля Франчук</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Ми знаєм на Русі жили колись князі</strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, муз. Нелі Франчук</strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми знаєм на Русі жили колись князі,</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вони в історії свій залишили слід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А серед них Аскольд, що мріяв, як і всі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вкраїну й Київ берегти від бід.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княже Аскольде, тебе пам&rsquo;ятають</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Київські схили і кручі Дніпра.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нас із тобою віки розділяють,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наше єднання &ndash; віра свята.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що думав він, чим жив,</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З ким був, кого любив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І що хотів залишити нащадкам?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Про це мовчать книжки і не дізнатись нам,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це вічна нерозгадана загадка.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Часом буває так, що чується вночі</span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На берегах Дніпра зітхання й голоси.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чия ж могила тут, хто з древніх тут спочив</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І мудрістю віків усіх благословив?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>&nbsp;</em></strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Виконує Ірина Голоско</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Врятуймо Україну!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Миколи Голоска, муз. І.Голоско</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прокиньмось, браття, встаньмо із руїни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Це кличе нас вельможний князь Аскольд.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвою порятувати Україну</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Напевно, тут велів нам сам Господь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Блаженнеє, святеє місце те є,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Був з нього тут початок, а вкінець</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підем у тьмі світити Прометеєм,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо з каяття дістанем вічності вінець.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай, Боже, єдності нам в вірі і молитві,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зішли на нашу землю благодать.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зі злом щоб ми не згинули у битві</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Опіка Божа буде нам під стать.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Благословив нас тут отець Святіший,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб зберегти від злого наш край.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А з неба допоможе наймиліший</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Для України наш чудесний Миколай.</span></p>','Аскольдів глас - збірник 2010','uk',3660,5143,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В першу чергу я хочу подякувати вас за те, що мій син отримав від вас &laquo;Шкільний портфелик&raquo;. Він дуже вдячний вам за підтримку... Я зараз дуже бажаю написати вам листа на українській рідній мові, я з вами частіше спілкуюся на російській, але розумію, що краще української мови не має і треба розвиватися у цьому напрямку.</span></p>','2013-12-20 21:28:33',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись 5 осіб)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; 10 лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збараз</strong><strong>ька виправна колонія </strong><strong>З</strong><strong>ВК-</strong><strong>63</strong><strong> (</strong><strong>Тернопільська обл.</strong><strong>) &mdash; </strong><strong>4</strong><strong> лист</strong><strong>и </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>4</strong><strong> лист</strong><strong>и </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Надержинщинська </strong><strong>виправна колонія Н</strong><strong>ВК</strong><strong>-65</strong><strong>(</strong><strong>Полтавська обл.</strong><strong>)</strong><strong> &mdash; </strong><strong>1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний ОВЦ-79 (Дніпропетровська обл.) </strong><strong>&mdash;</strong><strong>1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;3 листи</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>спасибо Вам большое</strong><strong>, как хорошо</strong><strong>что Вы мне ответили и позвонили моей маме. Да, ей действительно сейчас тяжело у Алиночки даже назиму тёплых вещей нет, не сапог, не куртки</strong>... Я молюсь Господу и прошу прощения и здоровья родным и близким, я верю в Господа и знаю что Он им поможет... <strong>Мне осталось ещё 1,5 года </strong>скоро я буду дома, и Алиночку и маму некогда не брошу&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ольга Ш. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... нахожусь в местах лишения свободы за кражу, <strong>срок 3 года и 3 мес</strong>. Я знаю что я грешница и за свои грехи я каюсь и молюсь. Прошу Вас о помощи, <strong>помогите пожайлуста лекарствами</strong>. Очень болею, а помощь некому, <strong>дома мать с маленьким ребёнком. Я вам высылаю рецепт</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Л. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Добрий день о.Ігор! <strong>Отримала вашого листа з якого дізналася, що ви відгукнулися на моє прохання, а потім і від свекрухи теж дізналася, що вона отримала від вас посилочку. Я вам щіро вдячна</strong>. В своєму листі <strong>ви питаєте за мого синочка Женю, який знаходився до трьох річного віку в будинку дитини на території колонії. Так йому вже пішов п&rsquo;ятий рік і знаходиться він під опікою в дитячому будинку сімейного типу</strong>... Я обрала для нього сім&rsquo;ю, яка має тринадцять діток і перебуває в мирі з Богом. <strong>Вони сім&rsquo;я віруюча, Жені добре там.</strong> Ми з ними листуємось, я телефоную їм, розмовляю з сином, вони привозять його до мене на побачення. Мені дуже приємно, що ви пам&rsquo;ятаєте про мене й моїх діточок. Ось адреса по якій Женя зараз проживає. <strong>Мені буде дуже приємно, якщо ви поздравите Женю зі святом св.Миколая</strong>. Мені залишилось до кінця мого строку два роки. Але <strong>мені ставлять тяжкий діагноз і відправляють на лікарню в м.Львів. Я ще раз вам щіро вдячна за вашу допомогу</strong>. Якщо приїду назад з лікарні можно я напишу вам листа?&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена П. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Прошу Вас помогите мне медикаментами: от кашля, противогрипозные и высылаю Вам рецепт. И если можете очки (+5) и звёздочку</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Катерина І. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>в моей жизни ничего хорошего не было. Друзья, компании, водка, а итог &ndash; тюрьма</strong>... В свои 40 лет у меня нету совершенно ничего. Даже жилья своего не имею. Весной, летом, осенью &ndash; мне легче жить, потому что работаю по людям &ndash; огороды. Копейка своя есть, а вот зимой &ndash; очень трудно. Работы нету, живу в подвале. Хорошо, что горячие трубы есть, что я могу хоть немного согреться и поспать. Кушать нечего, а постоянно просить у людей &ndash; стыдно. <strong>Родных у меня нету, может где-то и есть, но я за них не знаю. Воспитывалась я в детском доме для сирот. Ну, а дальше жизнь пошла такая, что я никому не желаю</strong>... Хочу Вас попросить о своей нужде. <strong>У меня нету: мыла, порошка, прокладок, шампунь, зубной пасты, туалетной бумаги, крем для рук, если сможите, то тетрадь и пару конвертов</strong>. По силе Вашей возможности, то продукты, которые запариваются: <strong>мивина вермишель и приправа, картошка-пюре, олия, сахар, чай, консервы, лук, чеснок, конфеты, печенье, халва и т.д</strong>. Я всё перечислять не буду. Господь Вам подскажет, то Вы и вышлите. С вещей мне ничего не надо&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Срок у меня 8 лет пятый год уже сижу и мучаюсь с желудком <strong>у меня хронический гастрит, и мне два раза в год нужно проходить курс лечения. Помогите мне пожалуйста медикаментами</strong>... мне только 39 лет. <strong>Я очень нуждаюсь в Вашей помощи. Сейчас у меня воспалился желудок, а лечить нечем</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Наталія Н. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Кожен день ми з сестрами на вечірній молитві дякуємо Господа </strong>за вас, за кожного служителя,<strong> за усіх, хто працює на Божій ниві!!! </strong>В першу чергу<strong> я хочу подякувати вас за те, що мій син отримав від вас &laquo;Шкільний портфелик&raquo;. Він дуже вдячний вам за підтримку</strong>... Я зараз <strong>дуже бажаю написати вам листа на українській рідній мові, я з вами частіше спілкуюся на російській, але розумію, що краще української мови не має і треба розвиватися у цьому напрямку.</strong> Тепер хочу поділитися з вами своєю радістю. Але ж цю радість ви мені принесли... Серед жіночих колоній наша колонія має два переможних місця &ndash; 1-е і 3-е. Я зраділа за наших дівчат. А коли ви мені повідомили, що моя робота відправлена на конкурс до Німеччини, я від такої новини аж розплакалася на радощах!.. <strong>Хочу поділитися з вами Господніми чудесами у нашій колонії. З різних відділень Господь зібрав сестер, з яких народилася християнська група прославлення. Ми її назвали &laquo;Фіміам&raquo;</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Алла Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>из за болезни нахожусь в инвалидном отряде</strong> молюсь у Бога за свои грехичтобы простыв и у Вас прошу помочь мне в трудную менуту люды добрые мне неоткого бо <strong>немогу себя обезпечить</strong>. <strong>Помогите мощимися придлажнестями продуктами быстрого приготовленыя и чтонебуть слаткого</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена В. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Благодарю Бога за ваше письмо, за то что молитесь за меня, незабываете</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Оксана К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Получила Ваше письмо за которое большое спасибо, я даже и неждала ответа спасибо что откликнулись... Я даже незнаю что Вам писать. <strong>Если сможете то помогите, чем сможете</strong>(<em>перебуває в туб.лікарні</em>). Да благословит Вас Господь за Ваши добрые дела&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина І. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>в нас нема українського словника</strong>, я не дуже грамотно пишу и це е моя грішність що не вчила в школі. Це е <strong>писеник де молитви та різдвяні пісні а якщо можете то можна діски я залюбки та дівчата будемо слухати. А ще я хотіла щоб дуже допомогли з вишивкою. Можна попросити у Вас Біблію и мені дуже хотілось почитать литературу поучительну... О.Ігор можна попросити календарь на 14 год</strong>... не повірите але <strong>немаю ні одежі ні взуття</strong> хожу ще в резинових, Боже я так устала просити, але Він не хоче забирати у вічне свое царствіе я мабуть ще не зовсім збагнула що без Ісуса немає життя на землі і щирого каяття. Я буду молитися кожну вільну минуту и просити прощення за все скоєне&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Віра Н. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>сестра Юзепа в листопаді піде до дому</strong> покине муки всі. <strong>Мені ще нада добувати до кінця, скільки осталось до весни. На все Божья воля</strong>, Він за нас все время беспокоєтся, нипокидає в біді. От бабуля Юзепа переболіла грипом, сійчас вона теж задихається нехоче в санчастину лягати помаленько ходи. Сійчас мене прижало но стараюсь непідатися, щоб злягти, а перехожую. <strong>Ми нашого Бога все славимо ми без него нікуди негодимось.</strong> Мені <strong>В. із Луцька</strong> із дома пристарелих 33 роки, <strong>інвалід на коляскі</strong>, пише листи присилає газети за Бога нашего молитви я всігда рада получити і відповісти на їй листи, таких людей жалко но вона <strong>пише, що 7 років лежача була, а Бог поміг вона в даний момент може сама с кроваті коляску сісти і листи писати. Пише Бог мене спас, ізцілив </strong>і все мені поміг. <strong>Ми всігда молимося за Вас за Ваші сімї. А я благодарю Господу за то, що криша над головою, за хліб за жизню. Слава Богу</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Юзепа К. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>почала займатись вишиванням. Вишила невеличку ікону Св.Миколая Чудотворця</strong>. Віддала нач.відділення, мабуть відправлять в Київ... <strong>Скорбимо, захворів отець Стефаній який нас дуже часто провідував роками</strong>. Але тепер знаходиться в лікарні протягом багатьох місяців. Я також <strong>хворію, зараз проходжу амбулаторно-профілактичний курс лікування </strong>&ndash; уколи &ndash; капельниці і думаю що Господь не покине мене. Молюсь, відвідую молитовну кімнату кожного дня&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я дуже Вам вдячна за листи і підтримку якщо можна то вишліт старі в</strong><strong>&rsquo;</strong><strong>язані речі щоб мені пов</strong><strong>&rsquo;</strong><strong>язати носочки і рукавички діткам на подарунки до миколая в дитячий будинок</strong>... Я пам&rsquo;ятаю як моя бабуся мене вчила що Бог дивиться з неба за нами і все бачить і я Його завжди боялась і слухалась свою маму хотья вона не рідна в мене була <strong>моя мама померла при пологах і я росла в будинку малят до трьох років потім тато одружився і мене забрали але</strong> <strong>з дитинства я мало бачила добра мама пила тато</strong> <strong>теж і постійні сварки</strong> одна бабуся яку я дуже любила але вона скоро померла, але я пам&rsquo;ятаю як вона до мене приходила і я знею розмовляла, це правда я її бачила і знаю вона була мій ангел охоронець її присутність я відчувала скрізь і мені було добре своїх дітей я теж навчала боятись Бога і негрішити і я вірю, що насіння яке я в них посіяла зійде</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Я хочу розповісти свою історію, що непотрібно красти</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одного разу прийшла до мене подруга і сказала підем сьогодні вночі в колгосп і накопаєм картоплі молодої і повезем в Київ і продамо вона зараз дорога я їй кажу давай дома накопаєм, а вдома нехай на далі буде каже вона я подумала і згодилась пішли накопали продали накупили речі, але думки мене не покидали це гріх Бог накаже але згодом забула. Прийшов час копати свою картоплю, гичка на картоплі така гарна стали копати і одна дрібна і ями під кожним кущем ніби хтось покопав і майже стільки скільки ми перекопали в колгоспі і я зрозуміла, що це кара ібо сказано не кради с тих пір я ніколи неходила ні пощо в колгоспі красти бо знала що візьму чуть а забереться на більше лучше давати і Бог дасть і дітей навчала... І ще багато повчальних історій в дитинстві було як квіти на могилах брали з подругою і в букет збирали а потім бабуся заставляла ввечері нести на кладовище і ми пішли хоть плакали бо боялись що мертві до нас прийдуть&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Я Вам очень благодарна за поддержку,</strong> у меня 30 октября комиссия первая, даст Бог в начале ноября будет вторая и с Божьей помощью пойду домой. Будет возможность напишите я ещё и в ноябре здесь буду не знаю когда ещё суд будет.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Песня</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">(<em>для собрания верующих в Орджоникидзевском исправительном центре</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>на мотив песни &laquo;Снова замерло всё до рассвета&raquo;)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Снова вместе и снова: &laquo;Спасибо&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Говорим мы Отцу своему</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пусть тревоги проносятся мимо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пропоём же мы славу Ему.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Так случилось в беде повстречались</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; И нелегкую ношу несём</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Богу, что вместе собрались</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И поддержку друг другу даём.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Защити от соблазнов нас, Боже,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И тернением нас надели</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай любви нам и мудрости тоже</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ты нас, Господи, благослови.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Время лечит, спасает от боли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Все мы Господа просим: &laquo;Спаси!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Если будет на то Твоя воля,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Чашу эту, Отец, пронеси,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Помоги одолеть все невзгоды,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И достойно пройти путь земной,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Чтоб на все отведённые годы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Были мы нераздельны с Тобой&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановні добродії Церковної громади УГКЦ Аскольдова Могила</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">та о.Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Я отримала від Вас посилку для мого онука Євгенія Б., за що щіро Вам дякую! </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Все, що Ви надіслали нам доречно</strong>. Я ще раз Вам дякую за турботу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай Вам буде удача у всіх справах, низький Вам уклін.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген, 12р. &ndash; син Алли Б.,Чернігівська ВК)</em></span></p>','Звіт №17 проНАДХОДЖЕННЯ  ЛИСТІВ03.11.2013 р. — 23листи','uk',3899,5382,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В історію записана,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В молитвах заколисана.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тарас колись намалював,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як храм цей Київ прикрашав.</span></p>','2013-10-08 22:42:48',NULL,'P','<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-size: small;\"><strong>Цар Русі &ndash; Аскольд</strong></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Зої Ружин, муз. Олени Личкіної</strong></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Зійшла на князя благодать...</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І князь Аскольд прийняв хрещення.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Настав час вірі возсіять!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Послав Господь благословення...</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І подвиг, й мученицький шлях...</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Безсмертному Аскольду - слава!</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Христовим вченням у віках &ndash;</span></p>\r\n<p class=\"Style4\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Під Богом і Народ! Й Держава!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бога прославляє!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар Русі &ndash; Аскольд!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воскресла пам&rsquo;ять крізь віки,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитви слово розквітає...</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Радіють сонце і зірки!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам Цар - Аскольд благо віщає!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгиня Ольга возвела</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На честь Царя - Аскольда храми.</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгині Ользі честь, хвала!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Під України небесами!</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style5\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Приспів:</em></span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд нам сяє,</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бога прославляє!</span></p>\r\n<p class=\"Style2\" style=\"text-align: justify;\" align=\"left\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Першим охрестився,</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Світочем явився!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Цар Русі &ndash; Аскольд!</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"Style3\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>До князя Аскольда</em></strong></p>\r\n<p class=\"aCxSpFirst\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Тетяни Озкан, муз. Катерини Кучерявої</strong></span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. Аскольде-князю, з висоти</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поглянь на нашу Україну.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Земляче рідний, нам прости,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що ненька наша на колінах.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що ми її не підвели,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І підвести не маєм сили,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що славу й честь її святу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зберегти ми не зуміли.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. Навчи нас, князю, перш за все</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Служити рідному народу</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І як мету в життя нести</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Його добробут і свободу.</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молись до Господа за нас,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб він, Всевишній, дав нам ласку,</span></p>\r\n<p class=\"aCxSpMiddle\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У рідній нашій Батьківщині</span></p>\r\n<p class=\"aCxSpLast\" style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Омріяну створити казку.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Нащадки Аскольда</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Олени Сенейко</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. А на нашій Україні був славетний княжий рід,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь Аскольд їх прадід сивий, той що Русь хранив від бід,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Від Аскольдової слави свої корені несем</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я про нього заспіваю, а на серці тихий щем:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Аскольдовій могилі ми згадаємо князів,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти гордись, моя Вкраїно, ти гордись, що мала їх,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На Аскольдовій могилі ми згадаємо тебе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти &nbsp;дивись, Аскольде, сивий із небес.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2. А сини Аскольда славні у походах і піснях,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Переможно підіймали золотої волі стяг</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я до тебе щиро серцем і душею пригорнусь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мій Аскольде і моя Вкраїно-Русь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приспів:</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>&nbsp;</em></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Аскольдовий дзвін</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. і муз. Ольги Лозинської </strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">1. Осяяна, омріяна,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Легендами оспівана</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд колись Христа приймав,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;На вірність Богу присягав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Опалена, зруйнована &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поміж хрестів захована.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Княгиня Ольга зберегла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І знову людям віддала.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Приспів:</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церква Миколая &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Храм, що я кохаю.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зустрічі, надії,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Віра в наші мрії!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;2. В історію записана,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В молитвах заколисана.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тарас колись намалював,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як храм цей Київ прикрашав.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Збудована, оновлена</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молитвами вшанована.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тебе зустріне там отець, -</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гріхам покладемо кінець.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span><em style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Приспів:</em></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Пісня про Аскольда</em></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Сл. Христини Шонь, муз. Марка Дайніна</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&nbsp;</strong></span><span style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\">Ніби лицар крізь віки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде, проходиш &nbsp;ти.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Перший мученик християн Русі,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Що освятив землі ці.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Аскольде, князю всіх киян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">що добробут їхній пильнував.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти збережений в пам&rsquo;яті своїх синів</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як той, хто спокій зберегти зумів.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти хрещення прийняв святе,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">хоч землі ці поганським богам</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">поклонялись щодень.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Та Аскольде ти &ndash; правду людям відкрив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Підступно вбитий із рук поган</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ти святу кров за віру пролляв</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Наслідувати ми будем тебе</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І про Христа розкажемо людям.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><strong style=\"font-family: arial, helvetica, sans-serif; font-size: small; text-indent: 0em; line-height: 1.3em;\"><em>Аскольд і Дір</em></strong></p>\r\n<p style=\"text-align: justify;\" align=\"right\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Сл. Олександра Олеся, муз. Віктора Соломіна</em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Славна в Рюрика дружина,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всім далась вона взнаки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А Аскольд і Дір &mdash; найперші,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Найславніші вояки.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нудно їм сидіти дома</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Без походів, без боїв,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вони прохають князя,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щоб на волю їх пустив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Князь подумав і дозволив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Попрощалися вони</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І з такими ж вояками</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Посідали на човни.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І по Волхову угору</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В невідомий край летять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мов орлів широкі крила,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Їхні весла лопотять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приплили до краю Волхов,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Далі витягли човни</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І в лісах шляхів шукають</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До чужої сторони.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бачать &mdash; річка. Довго нею</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Геть на полудень плили,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аж угледіли на горах</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Місто, вежі і вали.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Приплили, на берег, вийшли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І питають у полян,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хто живе у цьому місті,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хто тепер у ньому пан?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Тут колись жили та вмерли</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кий, Хорив та Щек, брати.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А тепер ми під хозаром,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Животій та дань плати&raquo;.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;Не журіться!&raquo; &mdash; Дір промовив.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&laquo;За мечі!&raquo; &mdash; Аскольд гукнув.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожний птахом стрепенувся,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кожний гнівом спалахнув.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Налякалися хозари</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І в степи біжатъ гуртом.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І дружина здобуває</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пишний Київ над Дніпром.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Стали вільними поляни,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вихваляють вояків,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А Аскольда разом з Діром</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вибирають на князів.</span></p>','Аскольдів глас - збірник 2012','uk',3662,5145,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона живе у Божих палатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одіта у розкішних білих шатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ще вона Ісусику служить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хмаринка Його дуже-дуже любить</span></p>','2014-03-13 21:05:53',NULL,'P','<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Ісусу Христу!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З найкращими вітаннями та своїм шануванням до Вас і Вашої церковної громади пише сестра Ірина.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дорогий о.Ігор, пишу Вам напередодні Свята Святого Миколая, тому що висилаю 2 вірша, один на російській мові, другий я написала на українській дитячий віршик &laquo;Хмаринка&raquo;. З цього віршика можна поставити сценку, в якій прийматимуть участь дітки від 3-х до 7-ми років. Сценка може бути і на свято Миколая і на Різдво. Це мій подарунок до святого Миколая.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Хмаринка</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Живе собі на небі хмаринка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Маленька жартівниця, пушинка</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То з подружками біга, стрибає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То з сонечком у жмурки гуляє</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То губки надува, вередує</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">То слізоньки пуска, капризує</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Вона живе у Божих палатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Одіта у розкішних білих шатах</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; А ще вона Ісусику служить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Хмаринка Його дуже-дуже любить</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ісусику престол прикрашає</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Собою Йому ніжки зігріває</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У неї чистота і порядок,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Все зроблено вміло і до ладу,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вона така вправна майстриня,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зірочок у неї повна скриня,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Коли сонечко спатоньки лягає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Она зіроньки по небу розкладає.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Такі вже творить гарні малюнки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Склдає з них чудові візерунки,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; І дружить вона з місяцем ясним,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Удвох співають пісеньки прекрасні,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Веселі співаночки чудові,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Для діточок усіх колискові.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Живе собі на небі хмаринка,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Маленьк веселунка, смішинка,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Виводить хороводи, стрибає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усіх вона добряче розважає,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В Ісусика їй добре живеться</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тому вона радіє сміється</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>М</strong><strong>ы с тобою дети Бога</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с тобою дети Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам светит чистая дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Его пути</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы Его закон читаем</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с любовью принимая</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В сердце чтим.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Позади у нас печали, слёзы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Горькие рыданья, всё позади</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Предоставь всё в руки Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пусть уйдёт твоя тревога навсегда</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он за нас всё порешает на места свои расставит.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Он судья!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А ты приди к Нему раскайся</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">У грехах своих признайся расскажи</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ведь Он примет тебя знаю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Тихо скажет: &laquo;Я прощаю, ступай, иди!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будь смиренна и послушна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будь добра, великодушна</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Впредь не греши!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прогони свой страх небойся</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Одному Ему доверься,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вперёд иди</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мы с тобою дети Бога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам светит чистая дорога</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Его пути!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; З повагою сестра Ірина &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.11.2013 р.</span></p>','Лист6 ув\'язненої Ірини В., качанівська виправна колонія КВК-54, м.Харків','uk',4141,5624,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вслухайся в голос неба,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітру дзвінкі акорди</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Й підемо в нашу церкву</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">До Миколая-Аскольда.</span></p>','2013-10-08 00:38:45',NULL,'P','<p><strong>Поезія Т.Чаплик</strong></p>\r\n<p><strong>Із заходу на схід із півночі на південь</strong></p>\r\n<p>Сучасні спадкоємці прадавньої країни.</p>\r\n<p>Порозуміння й злагоди збудуємо мости,</p>\r\n<p>Щоби Аскольдовій Русі-землі цвісти.</p>\r\n<p>&nbsp;</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p>Єднаймося у вірі Аскольдові нащадки,</p>\r\n<p>Щоб заповіти предків берегти.</p>\r\n<p>Щоб Батьківщину нашу у світі прославляти</p>\r\n<p>Єднаймося у силі діти і батьки.</p>\r\n<p>&nbsp;</p>\r\n<p>Свідомими вкраїнцями треба нам зростати</p>\r\n<p>І жити треба так, як князь Аскольд учив,</p>\r\n<p>Сміливо й невідступно свій край обороняти,</p>\r\n<p>Щоб люди прославляли і Бог благословив.</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p>&nbsp;</p>\r\n<p>В єднанні наша сила, а у дружбі &ndash; слава</p>\r\n<p>Візьмемося за руки сестри і брати,</p>\r\n<p>Поставимо на ноги великую державу,</p>\r\n<p>Працюймо неустанно до здійснення мети.</p>\r\n<p><strong><em>Приспів:</em></strong></p>\r\n<p><strong><em>&nbsp;</em></strong></p>\r\n<p><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpFirst\"><strong>До князя Аскольда</strong><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Аскольде-князю, з висоти</p>\r\n<p class=\"aCxSpMiddle\">Поглянь на нашу Україну.</p>\r\n<p class=\"aCxSpMiddle\">Земляче рідний, нам прости,</p>\r\n<p class=\"aCxSpMiddle\">Що ненька наша на колінах.</p>\r\n<p class=\"aCxSpMiddle\">Що ми її не підвели,</p>\r\n<p class=\"aCxSpMiddle\">І підвести не маєм сили,</p>\r\n<p class=\"aCxSpMiddle\">Що славу й честь її святу</p>\r\n<p class=\"aCxSpMiddle\">Зберегти ми не зуміли.</p>\r\n<p class=\"aCxSpMiddle\"><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Навчи нас, князю, перш за все</p>\r\n<p class=\"aCxSpMiddle\">Служити рідному народу</p>\r\n<p class=\"aCxSpMiddle\">І як мету в життя нести</p>\r\n<p class=\"aCxSpMiddle\">Його добробут і свободу.</p>\r\n<p class=\"aCxSpMiddle\">Молись до Господа за нас,</p>\r\n<p class=\"aCxSpMiddle\">Щоб він, Всевишній, дав нам ласку,</p>\r\n<p class=\"aCxSpMiddle\">У рідній нашій Батьківщині</p>\r\n<p class=\"aCxSpLast\">Омріяну створити казку.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: large;\"><strong style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">Ти був патріотом, Аскольде</strong></span><span style=\"text-indent: 0em; font-size: 0.625em; line-height: 1.3em;\">&nbsp;</span></p>\r\n<p class=\"aCxSpMiddle\">Ти був патріотом, Аскольде,</p>\r\n<p class=\"aCxSpMiddle\">Колись на Русі князював,</p>\r\n<p class=\"aCxSpMiddle\">Хрестив наш народ і навіки</p>\r\n<p class=\"aCxSpMiddle\">В історію ім&rsquo;я вписав.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">За землю святу Українську</p>\r\n<p class=\"aCxSpMiddle\">Не раз і не два воював,</p>\r\n<p class=\"aCxSpMiddle\">Щоб кат і нападник ворожий</p>\r\n<p class=\"aCxSpMiddle\">Ногою сюди не ступав.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">Багато спливло з того часу,</p>\r\n<p class=\"aCxSpMiddle\">Затерлись деталі в віках.</p>\r\n<p class=\"aCxSpMiddle\">Та ім&rsquo;я твоє незабутнє</p>\r\n<p class=\"aCxSpMiddle\">Лунає у нас на устах.</p>\r\n<p class=\"aCxSpMiddle\">&nbsp;</p>\r\n<p class=\"aCxSpMiddle\">Окрасо Вкраїни і гордість,</p>\r\n<p class=\"aCxSpMiddle\">Про тебе складаєм пісні,</p>\r\n<p class=\"aCxSpMiddle\">Ми знаєм, що ти їх почуєш</p>\r\n<p class=\"aCxSpLast\">Десь там у святій вишині.</p>\r\n<p class=\"aCxSpLast\">&nbsp;</p>\r\n<p style=\"text-align: left;\" align=\"center\"><strong>Аскольдів храм</strong></p>\r\n<p class=\"ListParagraphCxSpFirst\" style=\"text-align: left;\" align=\"right\"><strong>&nbsp;сл. і муз. Ю.Ремезенко</strong></p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<ol>\r\n<li>&nbsp; Пагорби Дніпра (2 р.)</li>\r\n</ol>\r\n<p class=\"ListParagraphCxSpMiddle\">Видно здалека (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Тишу і любов (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Зберігають ці святі місця.</p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<ol>\r\n<li>&nbsp; В кожного свій шлях (2 р.)</li>\r\n</ol>\r\n<p class=\"ListParagraphCxSpMiddle\">Але в далині (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Згадуємо ми (2 р.)</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Із дитинства істини прості.</p>\r\n<p class=\"ListParagraphCxSpMiddle\">&nbsp;</p>\r\n<p class=\"ListParagraphCxSpMiddle\"><strong>Приспів:</strong></p>\r\n<p class=\"ListParagraphCxSpMiddle\">Стежечкою, стежечкою</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Я до храму йду</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Розквітає біля храму</p>\r\n<p class=\"ListParagraphCxSpMiddle\">Кожная душа</p>\r\n<p class=\"ListParagraphCxSpLast\">Благодаттю Божою сповнена.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','Аскольдів глас - збірник 2011','uk',3661,5144,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
