INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесені Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>','2010-09-23 20:21:12',NULL,'P','<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ПОЖЕРТВИ ЗА ВЕРЕСЕНЬ 2010 Р.Б.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесені Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва Володимира Северина на церкву &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва Марії на розпис церкви &nbsp;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва сім&rsquo;ї Петра, Марії </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Криворуків на церкву</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Греськів на будівлю храму </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Курилона церкву</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Роїк на потреби церкви </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Турковських на церкву </span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пожертва родини Турковських &laquo;просимо за молитву за нашу сім&rsquo;ю і всю родину&raquo; </span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp; <br /></span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Милостиню роби згідно з тим,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">що маєш;</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли у тебе достатків повно,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">роби з них,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">а коли мало маєш,</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">то не бійся й з того дати.</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бо так готуєш собі добрий скарб</span></span></p>\r\n<p align=\"left\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">на день потреби.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Товит 4. 8-9.</span></span></p>','ПОЖЕРТВИ ЗА ВЕРЕСЕНЬ 2010 Р.Б.','uk',2152,3635,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Давно уже свирепые печенеги [5] не дерзали приближаться к границам русским; покоренные ятвяги [6] смирились; мятежные радимичи [7], побитые на голову воеводою княжеским, прозванным Волчий Хвост, платили снова обычную дань, и от берегов Черного моря, называемого в то время Русским, до крайних пределов обширной области Новогородской почти все пространство земли, заключающее в себе нынешнюю Европейскую Россию, признавало своим владыкою великого князя Киевского. С ужасом взирала отдаленная Греция на сего ставро-скифского царя<a name=\"_ftnref1\" href=\"#_ftn1\">[1]</a>, который, подражая во всем отцу своему, Святославу, не хотел последовать примеру матери его, благочестивой княгини Ольги, и принять веру христианскую.</span></span></p>','2009-02-20 19:30:11',NULL,'P','<h4 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Часть первая</span></span></h4>\r\n<h4 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">I</span></span></h4>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раскройтесь предо мной, картины времен давно прошедших: явись во всей красе своей, изгибаясь по крутым берегам привольного Днепра, древняя столица царства Русского - великий Киев, первопрестольный град! Бушуй, крутись, быстрый Днепр, и отражай в голубых волнах своих златые верхи высоких теремов и гридниц двора <em>княжеского</em>; расстилайтесь по обширному Подолу киевскому [1], бархатные луга - любимое <em>разгулье</em> удалой дружины князя русского! Жадный взор мой стремится проникнуть сквозь седой туман веков, до тех отдаленных времен России, когда, не озаренная еще светом православия, она приносила кровавые жертвы своему Перуну [2]; когда в дремучих лесах ее перекликались лешие и раздавался хохот хитрых русалок; когда в бурные осенние ночи, на вершине Кучинской горы, собирались колдуны, оборотни и злые ведьмы или, купаясь в утреннем тумане, резвились меж собой вертлявые кикиморы, и грозный Бука [3], на сивом коне своем, носился по лесам и долам, преклонял высокий бор до сырой земли и притаптывал луга заповедные. Я хочу послушать песни вещих соловьев Владимира - вдохновенных баянов древности, хочу взглянуть на веселые хороводы русских дев, пляшущих под тенью дубов или при свете пылающих огней в праздник Купалы [4] - древнего божества дубрав и полей русских; хочу полюбоваться удалыми потехами и посмотреть на игрушки богатырские славянских витязей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пусть называют мой рассказ баснею: там, где безмолвствует история, где вымысел сливается с истиною, довольно одного предания для того, кто не ищет славы дееписателя, а желает только забавлять русских рассказами о древнем их отечестве.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Давно уже свирепые печенеги [5] не дерзали приближаться к границам русским; покоренные ятвяги [6] смирились; мятежные радимичи [7], побитые на голову воеводою княжеским, прозванным Волчий Хвост, платили снова обычную дань, и от берегов Черного моря, называемого в то время Русским, до крайних пределов обширной области Новогородской почти все пространство земли, заключающее в себе нынешнюю Европейскую Россию, признавало своим владыкою великого князя Киевского. С ужасом взирала отдаленная Греция на сего ставро-скифского царя<a name=\"_ftnref1\" href=\"#_ftn1\">[1]</a>, который, подражая во всем отцу своему, Святославу, не хотел последовать примеру матери его, благочестивой княгини Ольги, и принять веру христианскую.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Немногие христиане, рассеянные по России, хотя не были гонимы за их вероисповедание, но, окруженные повсюду идолопоклонниками, повинуясь языческому князю, властолюбивому, не знающему пределов своему могуществу, они должны были беспрестанно опасаться участи их единоверцев, пострадавших в первые годы христианства. Владимир не походил на Нерона [8], но он мог сделаться Диоклетианом [9]. Сооружение нового капища [10] Перуну, богатое изваяние истукана этого первенствующего божества древних славян, частые жертвы, ему приносимые, - все доказывало привязанность великого князя к вере его предков. Он мог пожелать истребить последние остатки православия в своем государстве; мог ли пожелать и не исполнить своего желания тот, кто некогда, для удовлетворения необузданной страсти, предав смерти владетеля земли Полоцкой, Рогвольда, и двух сыновей его, силою женился на их сестре, Рогнеде [11], и, умертвив потом своего родного брата, Ярополка [12], взял в число наложниц своих вдовствующую его супругу? Кто мог тогда предвидеть, что сей грозный князь, облитый кровью своих ближних, будет некогда просветителем народа русского, образцом кротости, смирения, христианской любви и, причтенный к лику святых, станет рядом с апостолами и учениками Спасителя нашего? Впрочем, сами христиане не могли не видеть, что, сделавшись самодержавным и единственным владыкою царства Русского, Владимир перестал злодействовать. Он был еще бичом небесным для народов иноплеменных; но любил свой собственный народ, любил в судах правду, любил своих русских витязей, и хотя гордые варяги, составлявшие некогда его отборную дружину, притесняли иногда простолюдинов, хотя Владимир ласкал еще этих наемных воинов, известных в других землях под общим названием норманнов, но они не смогли уже поступать с русскими, как с побежденным народом, и нередко варяг, обличенный в буйстве и насилии, наказывался наравне с простым воином киевским. Но могли ли надеяться христиане, что тот, кто проводил время в пирах и забавах и, подобно сластолюбивому Соломону [13], имел до восьмисот наложниц, оставит в покое людей, коих вера, основанная на чистоте нравов, исповедующая кротость и целомудрие, была безмолвным, но красноречивым обличителем его буйных потех и увеселений? Несмотря на это, не только одни язычники, но даже многие из христиан любили Владимира. Его величественный вид, неустрашимость в битвах, царское хлебосольство и роскошь, знаменитые победы - одним словом, все пленяло умы россиян. Слава царя всегда становится собственностью его народа, а могущественный Владимир был славнейшим государем своего времени; и когда киевляне, толпясь на площади вокруг Перунова капища, слышали веселые крики бояр и витязей, пирующих в светлых гридницах двора княжеского, когда кому-нибудь удавалось сквозь узкие окна завидеть часть длинных дубовых столов, покрытых яствами, и рассмотреть окованный серебром <em>турий рог</em>, который с шипучим медом или зеленым вином переходил из рук в руки, то он кричал с радостью: &laquo;Братцы, братцы, посмотрите, как пирует со своею удалою дружиной наш великий князь, Владимир Святославович! Вон, видите ль, стоит подле него широкоплечий боярин? Это славный новгородский воевода Добрыня! А вон сидит понасупившись, словно туча громовая, удача-молодец, Рогдай!&raquo; И народ с шумом начинал тесниться вокруг двора княжеского, и тысячи голосов повторяли: &laquo;Да здравствует государь великий князь! Веселись и пируй, наш батюшка, солнце красное всей святой Руси!&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В один из прекрасных весенних вечеров южной России, когда солнце, опускаясь медленно к земле и не застилаемое ни одним облачком, утопает в золотом и огнистом океане; когда поселянин, возвращаясь домой с работы, весело поглядывает на ясные небеса и говорит своим товарищам: &laquo;Ну, ребята, бог дает нам ведро! Посмотрите, как заря погорела!&raquo; - в один из сих благословенных вечеров, тихих, но исполненных какой-то юности и жизни, человек десять рыбаков сидели кругом яркого огня, разведенного на берегу реки, близ урочища нагорной стороны Днепра, называемого Сборичев взвоз. Седой, но, по-видимому, еще бодрый старик заглядывал беспрестанно в большой котел, в котором варилась жирная уха, и от времени до времени отведывал из него деревянного ложкой. Два рыбака разбирали и считали пойманную рыбу, а остальные, лежа беспечно вокруг огня, разговаривали меж собою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что это, ребята? - сказал один из этих последних. - Вот близко десяти дней, как нашего великого князя видом не видать, слыхом не слыхать? Уж здоров ли наш батюшка? Бывало, не пройдет двух дней без пированья, а теперь, посмотрите-ка: и в новых его палатах, и в каменном тереме ни одного огонька не видно!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не все пировать, дитятко, - сказал старый рыбак, - и княжеские яства приедаются, и сладкий мед припивается!.. Да и нельзя же каждый день быть под хмельком; ведь дело его княжеское: надо рядить, судить, давать всем расправу. Тут варяг ограбил русина; там, глядишь, наш брат киевлянин...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что, чай, обидели варяга? - прервал кто-то насмешливым и грубым голосом. Рыбаки поглядели вокруг себя: на берегу никого не было; но в пяти шагах от них, у самой пристани, стоял в легком челноке, облокотясь на весло, колоссального роста мужчина, лет сорока пяти, с окладистою русою бородою. Он был без кафтана, в одной пестрой рубашке, подпоясанной черным с медными бляхами ремнем, за которым заткнуты были широкий, с серебряною рукояткою, засапожник<a name=\"_ftnref2\" href=\"#_ftn2\">[2]</a> и стальной кистень; у ног его лежало верхнее платье из грубой шерстяной ткани.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах, леший его побери, - сказал один из рыбаков, - как он подкрался!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что тебе надобно, молодец? - спросил старик.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ничего, дедушка! - отвечал незнакомец. - Я здесь пристал к берегу, чтоб поотдохнуть немного. Да что ж вы, ребята, замолчали? - продолжал он. - Не бойтесь: я не варяг, не витязь княжеский: не стану вас подслушивать да придираться к вашим речам.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Пожалуй себе подслушивай! - сказал старик, посматривая недоверчиво на незнакомца. - Мы люди простые, так какие у нас речи? Кой о чем меж собой растабарываем.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- В самом деле? А мне сдается, дедушка, что у вас речь шла о Владимире.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- О каком Владимире? Владимиров много на святой Руси! - прервал старик.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- О каком Владимире? Вестимо о каком! Ведь он один у вас, как порох в глазу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Если ты говоришь о нашем государе, молодец, так его не зовут просто Владимир, а величают великим князем Киевским.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Киевским! - повторил сквозь зубы незнакомец. - Киевским! Отца его величали когда-то и царем Болгарским, а недолго же он царствовал. Слыхал ли ты, старинушка, пословицу: чужое добро впрок нейдет?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И, молодец, где нам знать твои пословицы: мы люди темные. Да и что нам за дело, что было в старину! Живи только да здравствуй наш батюшка, великий князь, наше красное солнышко...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Хорошо солнышко, - прервал незнакомец, - летом печет, а зимой не греет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Рыбаки, молча и почти с ужасом, поглядели на незнакомца, который стоял, по-прежнему облокотясь небрежно на весло, и, казалось, не замечал удивления этих простых людей, не понимающих, как можно говорить с такою дерзостью о великом князе Владимире.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, молодец, молодец! - сказал старик, покачивая головою. - Чести твоей мы не порочим. Бог весть, кто ты такой, а не пристало ни тебе говорить такие речи, ни нам их слушать.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А почему же нет? - сказал спокойно незнакомец. - Не прикажешь ли хвалить Владимира и за то, что он накликал сюда этих иноземцев, от которых нашему брату русину и житья нет? Подумаешь, как бы, кажется, этим бездомным пришлецам не быть тише воды, ниже травы; а попытайся-ка повздорить с каким-нибудь варягом...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так что же, - подхватил один молодой рыбак, - или мне с ними и суда не дадут?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Дожидайся, брат! Нет, ребята, не нам обижать этих поморян: они того и норовят, чтобы с нас последнюю одежонку стащить. Мы, дескать, великокняжеская дружина, так все, что его, то наше.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как бы не так! - подхватил один молодой рыбак. - Не прежнее время: наш батюшка великий князь унял порядок этих заморских буянов! Кто и говорит: бывало, при них и тони не закидываем - как раз всю лучшую рыбу по себе разберут. А теперь, небось: не только простой мечник, а даже гридня или отрок княжеский попытайся-ка у меня взять даром хоть эту плотву!.. Нет, любезный, и не понюхает!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомец не отвечал ни слова и, помолчав несколько минут, сказал:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Посмотрите, ребята: кто это там сходит с горы?.. Постойте-ка!.. Никак, один из них, - вот что повыше других и в панцире... ну, так и есть, - варяг!.. Да и другие-то, кажется, витязи княжеские... Они идут сюда.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Сюда? - вскричал торопливо молодой рыбак. - Ей, ребята, проворней оттащите этого осетра в лодку... да помогите мне припрятать куда-нибудь стерлядей!.. Ну, что же вы, братцы, поворачивайтесь!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все рыбаки засуетились вокруг пойманной рыбы.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Добро, не хлопочите, - сказал с насмешливою улыбкою незнакомец, - они поворотили направо. Да что же вы так переполошились, ребята? Ведь теперь, по милости вашего князя, не только простые витязи варяжские, но и ближние его отроки не смеют вас обижать.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Оно так, молодец! - отвечал старик, глядя вслед за небольшою толпою ратных людей, которые, сошедши до половины горы, повернули по тропинке, ведущей к обширному Подолу киевскому. - Оно так, и мы доподлинно знаем, что ратным людям заказано обижать народ и брать у нас даром то, что им приглянется: да знаешь, молодец, все как будто бы вернее, - приберешь к сторонке, так не на что и глазам разгореться.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах вы глупые головы! - сказал незнакомец. - Что уж это за житье, коли надо прятать свое добро, что б его не отняли!.. Да этак и с печенегами уживешься. Нет, ребятушки, не так живали наши отцы в Киеве, при законных своих князьях: Аскольде и Дире [14]. Попытался бы тогда какой-нибудь чужеземец обидеть киевлянина.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А что, молодец, - спросил один из рыбаков, - и впрямь, чай, в старину-то лучше бывало?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не знаешь, так спроси у стариков. Что, дедушка, покачиваешь головою? - продолжал незнакомец, обращаясь к старому рыбаку. - Вестимо, отцы наши жили не по-нынешнему: довольство-то какое во всем было, житье-то какое привольное! Коли ты сам не видал этих времен, так, верно, слыхал о них от отца и матери?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Слыхать-то и мы слыхали, - прервал один рыбак, почесывая в голове. - Недаром поется в песнях, что в старину и реки текли сытою, и берега были кисельные. Да ведь это давно уже было, а что прошло, того не воротишь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Бывало, - продолжал незнакомец, - наш брат киевлянин знал лишь князей своих и боялся одного всемогущего Перуна, а теперь и богов-то у вас много, и господ не перечтешь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что правда, то правда, - прервал один детина с рыжею окладистою бородой. - Господ-то развелось у нас немало: и вирники и тиуны, а уж пуще всех эти метальники<a name=\"_ftnref3\" href=\"#_ftn3\">[3]</a>, провал бы их взял, - больно обижают нашего брата. Вот в прошлом месяце на меня наложили ставить для княжеского стола полтора сорока стерлядей. Я все честно принес к дворцовому метальнику, да позабыл только ему, проклятому, стерлядкой-другой поклониться... Так что же? Он при мне нарезал шесть зарубок на бирке, расколол, отдал одну половину мне. Кажись, дело бы в шапке, - так нет! Дня через три шлют опять за мною: &laquo;Давай еще пол-сорока стерлядей: за тобой недоимка!&raquo; Как так? &laquo;Да так!&raquo; Я за пазуху, вынул бирку: на ней все метки сполна; метальник приложил к ней свою половину: смотрю - двух зарубок нет как нет! Я туда, сюда - не тут-то было! Рыбу с меня доправили да мне же затылок накостыляли.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так что же ты, глупая голова? - прервал незнакомец. - Ты бы ударил челом на этого метальника вашему красному солнышку, великому князю Владимиру!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Попытался было, молодец, да доступ-то до него не легок. Ведь наш брат не кто другой: сунешься невпопад, так и животу не будешь рад, того и гляди, - продолжал рыбак, наморща брови и похватывая себя за спину, - какой-нибудь гридня или разбойник-варяг так тебя пугнет, что ты и ног не уплетешь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Экий ты, братец, какой! - подхватил незнакомец. - Коли не знаешь, так я тебя научу, как дойти до великого князя. Послушай-ка, молодец, женат ли ты?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как же! Вот уж другая весна идет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И жена твоя молода?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Всего семнадцатый годок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А пригожа ли она собою?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Пригожа ли?! - повторил с гордым видом рыбак. - Пригожа ли! Да таких молодиц, как моя, во всем Киеве немного, господин честной! Порасспроси-ка у товарищей: белолицая, румяная - кровь с молоком! Глаза, как цветы лазоревые, шея лебединая, а выступка-то какая, выступка - что и говорить: идет как плывет - пава павою!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, детина, детина, о чем же ты думаешь? Пошли ее заместо себя к вашему князю: так, может быть, она-то сама домой не вернется, да зато стерлядей тебе назад отдадут. Что же ты, любезный, в голове-то почесываешь; иль боишься, чтобы с тобой не было того же, что с покойным братом вашего государя? Да не бойся, молодец: ведь у тебя всего-навсе одна жена, а у покойника-то, князя Ярополка, и невеста была красавица, и вся земля Русская была его; так, вестимо дело, с ним добром нельзя было разделаться: пожалуй, он стал бы отнекиваться, на драку бы пошел. Вот у нашего брата, простолюдина, иная речь: взял жену иль невесту да вытолкал в шею с княжеского двора, так и концы в воду.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Глубокий вздох, похожий на удушливое стенание умирающего, когда в минуту нестерпимой боли каждое дыхание его превращается в болезненный вопль, прервал слова незнакомца. Рыбаки молча взглянули друг на друга, и сострадательные их взоры остановились на одном молодом человеке, который, не принимая никакого участия в общем разговоре, сидел задумавшись близ огня. На полумертвых и впалых щеках его, в неподвижных глазах, на посиневших устах, в каждой черте лица, изможденного бедствием, изображалась глубокая, неизъяснимая горесть. И грубый варяг, и хитрый грек, и полудикий житель лесов древлянских - каждый прочел бы в них с первого взгляда и повторил бы на собственном языке своем ужасные слова: &laquo;Я утратил невозвратно все земное мое счастье!&raquo; Ах, этот всемирный язык души, эти речи без звуков, начертанные кровавыми буквами на бледном челе несчастливца, понятны для всякого!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, брат Дулеб! - сказал один из рыбаков. - Да полно грустить! Мало ли в Киеве красных девушек - не та, так другая! Твоя Любаша приглянулась великому князю; что ж делать, брат: воля его княжеская - не ты первый, не ты последний!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так ваш Владимир, - прервал незнакомец, - и у этого бедняка отнял жену?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не жену, а невесту, - отвечал вполголоса рыбак, поглядывая с сожалением на Дулеба. - Сама виновата: бывало, лишь только великий князь выйдет на улицу, так все ее подружки, словно дождь, кто куда попало, а Любаша тут как тут. Уж я ей говаривал: &laquo;Ей, Любашенька, не суйся на глаза к великому князю, - девка ты пригожая, личмённая - как раз попадешь на житье в Берестово!<a name=\"_ftnref4\" href=\"#_ftn4\">[4]</a>&raquo; Так нет, куда те, бывало, и слушать не хочет! &laquo;Я, дескать, моего Дулебушку ни на какого князя не променяю&raquo;. И рада бы не менять, да променяешь. Глупая, ведь выше лба уши не растут! Чай, станут тебя спрашивать!.. Ну что ж? Ан и вышло по-моему. За два дня до свадьбы, где Любаша, - и след простыл!.. Мы с Дулебом взыскались ее по всему Киеву, обегали все улицы; по домекам завернули на княжеский двор, да лишь только Дулеб вымолвил за чем, как вдруг конюшие, ясельничие, сокольники, гридни, отроки, варяги, русины - ну вся эта княжеская челядь, словно стая голодных псов, так на него и ощетинилась, да ну-ка его в толчки: не дали парню образумиться. Только один княжеский чашник, видно подобрее других, глядя на его горькие слезы, сжалился и шепнул ему, что Любашу отвезли на Лыбедь, в село Предиславино, затем что в Берестово уже места нет для красавиц. Вот с тех пор бедняжка Дулеб и ну чахнуть, - совсем извелся! Недаром говорят, что с радости кудри вьются, а с кручины секутся. Подумаешь, детина-то был какой ражий, да весельчак какой: и попеть, и поплясать, и в дудочку поиграть - на все удача! Бывало, как распотешится, так щеки жаром горят, а теперь... посмотри-ка: кровинки в лице не осталось. А уж исхудал-то как, исхудал!.. Сердечный, в чем душа держится!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Знаешь ли что, старик? - сказал незнакомец, помолчав несколько времени и обращаясь к седому старику. - От этих рассказов и мне охота пришла повеличать вашего государя. Ну-ка, братцы, хватим разом: да здравствует наш батюшка, великий князь, наше красное солнышко!.. Что же вы молчите, ребята!.. Пристань хоть ты, Дулеб! Что, в самом деле, чего же нам еще? Когда у нашего брата взять нечего, так люди ратные ничего у нас даром не отнимают; метальники берут с нас только вдвое, челобитчиков с княжеского двора провожают с честию, и сам государь великий князь жалует своею княжескою милостию наших жен и невест. Да разве это не житье, ребята? На что гневить богов: и хуже бывало, когда печенеги громили нашу родину. Правда, в старину ни о печенегах, ни о метальниках, ни о варягах и речи не было, суд давали по правде, невест и жен ни у кого не отнимали, - да ведь тогда и народ-то был другой. Вот если б отцы наши и деды встали из могил!.. - продолжал незнакомец, и насмешливая улыбка исчезла с уст его, глаза заблистали, а мощный голос, как из громовой тучи, зарокотал над головами рыбаков. - Да, - повторил он, - если б наши деды и отцы встали из могил, и я сказал бы им: &laquo;Граждане киевские, очнитесь, пробудись, народ русский! Не пора ли тебе за ум взяться? Ну-ка, детушки, гоните из Киева разбойников-варягов; мечите в Днепр ваших грабителей; топите всю эту гурьбу мироедов, которые питались кровью вашею, под сенью враждебного для вас поколения злодеев Рюрика и Олега! [15] Люди русские, не прекратился еще род Аскольдов, не погибло племя прежних князей ваших! Боги сохранили для вашего блага одного из их потомков. Да княжит он над великим Киевом, да держит свое княжение честно, без обиды, по старине, как держали, убитые изменою и предательством, его дедичи - знаменитые князья Аскольд и Дир!..&raquo; Ну, ребята, как вы думаете, что сказали бы на это наши старики?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомец замолчал, потух дивный огонь, который сверкал в грозных его взорах; он облокотился снова на весло и, окинув спокойным взором всех рыбаков, повторил свой вопрос. Никто не отвечал ни слова. Как безоружный путешественник, который, один среди дремучего леса, попадает внезапно на стаю голодных волков; как молодая девушка которая, спеша на голос своего друга и обманутая ауканьем хитрого лешего, вдруг встречает перед собою это страшилище лесов русских; как бесприютное дитя, которое видит в руке злой ведьмы сверкающий нож и, очарованное адским ее взглядом, спешит к ней навстречу, - так точно все рыбаки, онемев от испуга, не смея пошевелиться, едва переводя дух, слушали с жадностью и трепетом возмутительные слова этого ужасного незнакомца.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Казалось, один Дулеб не слышал и не видел ничего: он не подымал головы, глаза его ни разу не встретились с глазами незнакомца; но легкий румянец играл на бледных щеках его, грудь волновалась, а из полуоткрытых уст вырывался какой-то невнятный ропот.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Дедушка, а дедушка! - промолвил наконец один молодой парень, дернув за рукав седого рыбака. - Что это он говорит?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что он говорит? - повторил старик, как будто бы пробудясь от сна. - Ух, батюшки, что это? Как этот кудесник нас обморочил! Не слушайте, ребята, этого зловещего ворона! Ах ты печенег проклятый! Да как у тебя язык повернулся говорить такие речи о нашем батюшке? Иль ты думаешь, что для твоей буйной головы и плахи во всем Киеве не найдется?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как не найтись! - отвечал спокойно незнакомец. - Протяни лишь только шею, а за этим у вашего батюшки, великого князя, дело не станет. Да о чем ты, старинушка, так развопился? Ведь я стал бы это говорить не вам, а вашим отцам и дедам. С людьми и говорят по-людски, а с баранами что за речи: стриги их, да дери с них шкуру - на то родились.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ж ты, в самом деле! - вскричал один из рыбаков. - Уж ты, брат, никак, и нас стал поругивать.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Убирайся-ка, покуда цел, - сказал старик, - а не то мы тебе руки назад, да отведем к городскому вирнику, так у него запоешь другим голосом. Экий разбойник, в самом деле, - видишь с чем подъехал!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Порочить нашего государя! - вскричал один рыбак.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Говорить такие речи о нашем отце, Владимире Святославиче, - подхватил другой.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Глупое стадо! - пробормотал незнакомец, принимаясь за весло.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Постой, молодец! - вскричал Дулеб, вскочив поспешно с своего места. - Возьми меня с собою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ты, что ты, дитятко, - прервал старый рыбак, - в уме ли ты?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Он довезет меня до Подола, - продолжил Дулеб, подходя к пристани.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Изволь, молодец, довезу, куда хочешь; хоть до села Предиславина!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Вспомни, Дулеб, - сказал тихим, но строгим голосом седой рыбак, - тому ли тебя учили? То ли ты обещал, когда был вместе со мною... не в Перуновом капище, не там, где льется кровь богопротивных жертв...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах, старик, - вскричал Дулеб, - что ты мне напомнил!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Он остановился и закрыл руками глаза свои.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну что ж ты? - сказал незнакомец. - Садись, что ль!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нет! - прошептал тихим голосом Дулеб. - Он велел любить и злодеев своих; он дает, он и отнимает, - да будет его святая воля! Ступай! Я не еду с тобою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомец взглянул с удивлением на Дулеба, опустил весло, и легкий челн его запорхал по синим волнам Днепра.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- О ком это он говорит? - спросил один из рыбаков, глядя на Дулеба, который сел на прежнее место.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Вестимо о ком, - отвечал другой рыбак, - о нашем великом князе! Кто ж, кроме его, и дает, и отнимает? Ведь он один в нас волен.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А злодеев-то своих любить он также приказывает?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как же! Разве нам не велено жить в любви и совести с варягами, а что они - други, что ль, наши?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А что, парень, - прервал детина с рыжею бородою, - ведь этот долговязый себе на уме! И впрямь житье-то наше незавидное. Эх, кабы воля, да воля! Что бы нам хоть одного проклятого метальника покупать в Днепре?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А там добрались бы и до всех, - прервал старик, - и злых и добрых - топи всех сряду. Нет, ребятушки, как у нашего брата руки расходятся, так и воля будет хуже неволи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да за что ж, дедушка, в старину-то нас никто не обижал?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Право? Да вы, никак, в самом деле поверили этому краснобаю? Эх, детушки! Я два века изжил, так лучше поверьте мне, старику. Бывало и худо и хорошо, что грех таить: и при бабушке нашего государя, премудрой Ольге, злые господа народ обижали, и при сыне ее, Святославе Игоревиче. Коли без того! Ведь одному за всеми не усмотреть. Кто говорит? И при нашем батюшке, великом князе, подчас бывает со всячинкою. Да что ж делать, ребятушки? Видно, уж свет на том стоит!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да о каком он все толковал Аскольде, дедушка?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Неужели не знаешь? Ну вот что похоронен там... близ места Угорского, над самою рекою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А кто он был таков?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Прах его знает! Так, какой-нибудь ледащий [16] князишка. Чай, в его время ленивый не обижал Киева. То ли дело теперь, и подумать-то никто не смеет. Вот недавно завозились было ятвяги да радимичи: много взяли! Лишь только наш удалой князь брови нахмурил, так они места не нашли. Что тут говорить! - продолжал старик с возрастающим жаром. - Да бывал ли на Руси когда-нибудь такой могучий государь; да летал ли когда по поднебесью такой ясный сокол, как наш батюшка Владимир Святославич?..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Правда, правда! - закричали почти все рыбаки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А как выйдет наш кормилец, - промолвил один из них, - на борзом коне своем, впереди своих удалых витязей - что за молодец такой! Так, глядя на него, сердце и запрыгает от радости.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да как сердцу и не радоваться, - подхватил другой, - ведь он наш родной, ему честь - нам честь!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, ребята, - вскричал третий, - напрасно мы не связали этого разбойника. Леший его знает, кто он таков: уж не ятвяги ли его подослали?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, парень, - прервал молодой рыбак - хватился!.. Поди-ка догоняй его; смотри: чуть видно... Эк он начал сажать - словно птица летит!.. Вон, выехал уже в Пачайну...<a name=\"_ftnref5\" href=\"#_ftn5\">[5]</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Пусть идет куда хочет, - сказал старик, - лишь только бы к нам не заезжал. А вот и уха сварилась, - продолжал он, отведывая из котла деревянною ложкою. - О, да знатная какая!.. Ну что ж, детушки, в кружок! Поужинаем засветло, а там и за работу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все рыбаки, выключая Дулеба, уселись кругом котла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А ты что, Дулебушка? - спросил старик. - Присядь к нам да похлебай ушицы. Эх, дитятко, полно! Горе горем, а еда едою. Садись!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вместо ответа Дулеб покачал печально головою и остался на прежнем месте.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Зачахнет он совсем, - сказал вполголоса старик. - Легко ль, сердечный, не пьет, не ест...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Небось, дедушка, - прервал молодой рыбак, подвигаясь к котлу, - проголодается, так станет есть, ведь голод-то не тетка. Нуте-ка, ребята, принимайтесь за ложки! Авось, смотря на нас, и его разберет охота!</span></span></p>\r\n<h4 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">II</span></span></h4>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Теремный двор</em>, в котором Владимир любил угощать своих витязей, стоял в его время на самом видном месте древнего Киева, близ нынешней Андреевской церкви, сооруженной на развалинах <em>каменного терема</em>, из коего, по сказаниям летописца, великая княгиня Ольга смотрела на торжественный въезд послов древлянских, помышляя о кровавой тризне, уготовляемой ею в память убиенного ее супруга [17]. Тут же, перед самым теремным двором, стояло капище Перуна, на холме, на коем впоследствии сооружена была церковь святого Василия, а ныне возвышается храм во имя Трех Святителей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В то самое время, как рыбаки, утолив голод, принялись снова толковать и шуметь меж собою, вверху, над их головами, глубокая тишина царствовала кругом дворца княжеского. Молчали гусли златострунные, и не раздавался веселый звук братин и кубков, которыми <em>чокались</em> храбрые витязи, выпивая их одним духом за здравие удалого князя Владимира; но в некотором расстоянии от дворца народ шумел еще по улицам великого Киева. Поселяне и жители посадов киевских, собравшись отдельными толпами, пели песни на обоих берегах Днепра; на песчаных косах и отмелях пылали яркие огни; кой-где мелькали по воде, как блуждающие звезды, небольшие огоньки, разведенные на лодках рыбаков, которые собирались <em>багрить</em> сонных осетров и белуг. На городском Подоле, тогда еще не заселенном, близ божницы Велесовой [18] резвились молодые горожанки; они то свертывались в шумные хороводы, то заплетали плетень, и громкие их припевы Диду и Ладе [19] разносились по окрестности.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Несколько молодых киевлян, между коих можно было тотчас заметить, по гордой осанке, варяжских воинов и гридней княжеских, стояли небольшою толпою поодаль от хороводов и любовались на игры красных девушек. Шагах в пятидесяти от сей толпы, под самым навесом притвора Велесова капища, стоял, прислонясь к стене, гигантского роста мужчина, закутанный в широкую, темного цвета, верхнюю одежду, похожую несколько на греческую мантию. Он не смотрел на хороводы веселых девиц, не слушал их песен; казалось, все внимание его было устремлено на один отдаленный предмет: он пристально глядел на поросшую частым кустарником гору, которая опускалась с одной стороны пологим скатом к киевскому Подолу, а с другой - нависла утесом над песчаным берегом Днепра. На самом верху сей горы белелось четвероугольное, с двумя красивыми теремами, здание, обнесенное толстым и высоким тыном. Эта гора называлась впоследствии Кучинскою; в этом доме жил Богомил, верховный жрец Перунова капища.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да что ж мы, братцы, стоим здесь, разиня рот, - сказал один из молодых людей, которые продолжали смотреть издали на игры девушек, - кто нам заказал подойти поближе?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- В самом деле, Стемид говорит правду, - подхватил высокий, но неуклюжий воин в остроконечном шеломе и стальном нагруднике, - подойдемте поближе!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нет, молодцы, не трогайте наших девушек, - прервал степенного вида киевский гражданин, приподняв вежливо свою шапку, - вы их распугаете.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Распугаем? - повторил грубым голосом воин. - Ах ты неразумный сын! Что мы, печенеги, что ль, чего нас бояться киевским красавицам?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кто и говорит, государь милостивый, - продолжал гражданин, - вы господа честные, витязи великокняжеские, да не пригоже нашим сестрам и дочерям водиться с людьми ратными.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А с кем же?.. Чай, с вашей братией, торгашами киевскими?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да не во гневе будь сказано твоей милости, - раздался позади воина веселый голос, - с торгашами-то киевскими водиться прибыльнее, чем с вами, господа храбрые витязи! Недаром сложена песенка:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ой ты, гой еси, богатый гость!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ты богатый гость, сын купеческий:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не красив, не пригож ты, мой батюшка,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А красивы, а пригожи твои денежки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Варяг обернулся: подле него стоял человек лет тридцати, в простом смуром [20] кафтане. Он был роста небольшого, но огромная голова его напоминала древнюю повесть о сильном и могучем Полкане-богатыре [21], у которого, по словам предания, <em>буйная головушка была с пивной котел</em>. Красные и раздутые его щеки, небольшие прищуренные глаза, рот, который почти соединял оба уха, круглый, вздернутый кверху нос, и вдобавок какая-то простосердечная и в то же время лукавая улыбка, от которой нос кривился в одну сторону, а рот в другую, - все это вместе составляло такую смешную и странную физиономию, что варяг, захохотав во все горло, вскричал:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Стемид, Простень, посмотрите-ка: что это за чудо морское?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Э, да я знаю этого красавца, - сказал Стемид, - это Торопка Голован. Зачем сюда пожаловал, молодец? Уж не хочешь ли отбивать у меня красных девушек?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Где нам тягаться с вашею милостью! - отвечал приземистый детина. - Ты стремянный великокняжеский, собой молодец, а мы что?.. Правда, если и у тебя в кармане-то не побрякивает, - продолжал он, скривя свой рот, - так немного же и ты возьмешь, боярин!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах ты огородное пугало! - вскричал один осанистый и толстый купец. - Да что ж ты этак порочишь наших девушек? Разве они товар какой? Ну, что зубы-то оскалил? Да если б твою дурацкую образину вылить всю из чистого золота и осыпать самоцветными каменьями, так они и тогда бы взглянуть на тебя не захотели.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну, пусть на него, - подхватил видный собою юноша, в котором, по богатой одежде, нетрудно было узнать одного из гридней княжеских<a name=\"_ftnref6\" href=\"#_ftn6\">[6]</a>, - да неужли-то и нашему брату не очень нельзя с ними речи повести?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Их дело непривычное, господин честной, - отвечал купец, - как подойдет, так они все до одной разбегутся.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да что ж они такие неповадливые? - прервал воин. - Ну, сторонка, - продолжал он, обращаясь к Стемиду, - Нет, на моей родине не только девушки, да и жены молодые не походят на ваших пугливых киевлянок. У нас по всему поморью только и житья что ратным людям. Клянусь Оденом [22], бывало, ни одна красавица не повстречается с молодцем Фрелафом без того, чтоб не взглянуть на него умильно или не промолвить слова ласкового!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Рассказывай нам сказки-то, - подхватил улыбаясь гридня. - Вам хорошо, варягам, похваляться: издалека пришли. Ну, что ты, Фрелаф, расхвастался, в самом деле! Послушай-ка, брат: случалось ли тебе когда-нибудь в тихую погоду припадать лицом к реке, что б напиться водицы?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как не случаться?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так вспомни-ка хорошенько: чай, всякий раз тебе казалось, что сам дедушка водяной выглядывает на тебя из омута. Ну с твоим ли красным носом да рыжими усами приглянуться молодой девушке!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так что ж: разве надобно витязю походить на девчонку, как товарищу твоему, Всеславу?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Всеславу?.. Да, Фрелаф, он покрасивее тебя и помоложе, а попытайся-ка с ним схватиться! Всеслав и не этаких молодцов, как ты, за пояс затыкал.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как, что б этому мальчишке неудалому досталось...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так, видно, брат Фрелаф, ты не видал, как на последней игрушке богатырской, перед княжеским теремом, он сбил с поля Яна Ушмовца и смучил совсем удалого витязя Рохдая?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Неужели в самом деле?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как же! Да с той-то самой поры он и попал в любимые отроки нашего великого князя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да что Всеслав, в Киеве, что ль? - спросил гридня. - Вот уж дней десять я его не видал.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И я также, - прибавил варяг.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А я хоть и видел, - сказал Стемид, - да не узнаю, Вы помните, какой он был весельчак, а теперь как в воду опущенный: все о чем-то думает. Кручина, что ль, какая на сердце пала, не знаю. Подумаешь, так о чем ему тосковать, великий князь его жалует, отца и матери у него нет, ни роду, ни племени - так, кажется, о ком бы у него и сердцу болеть?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да откудова же взялся этот безродный и как попал в княжеские отроки? - спросил варяг.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Родом-то он, кажется, из Великого Новгорода, - сказал гридня, - а кто был его отец, об этом никто из нас не слыхивал.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так, видно, он какой-нибудь подкидыш, - сказал с презрением варяг. - Может статься, отец-то его был где-нибудь бродягою или разбойником, так не диво, что сынок пошел по батюшке: чай, тоскует теперь о том, что живет не на своей воле: в лес хочется.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Слушай, Фрелаф, - вскричал с досадою Стемид, - не глумись над тем, кто тебя лучше! Всеслав - отрок княжеский, а ты что?.. Простой мечник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да зато не русин, а варяг, - прервал с гордостью Фрелаф, - и знаю моего отца: он княжеского рода.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, да, - подхватил с улыбкою гридня, - вы все варяги - князья, только княжить-то вам негде. Но не о том дело!.. Не знаю, как вы, а я мыслю так: Всеслав недаром стал таким нелюдимым. Знаете что? Уж не принял ли он веры греческой? Я слыхал, будто бы кого эти чародеи-христиане обольстят, так тот хоть живой в могилу ложись. Все наши потехи молодецкие, и песни, и пляски, и красные девушки, и всякое житейское веселье не взмилится. Говорят, покойный батюшка нашего князя был гроза грозою на этих колдунов, а все их много осталось. Эх, не в меру милостив наш государь великий князь! И если в самом деле эти злые люди прельстили любимого его отрока...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Вот то-то и дело, что нет, - сказал Стемид. - Я сначала то же думал, да он поклялся мне Перуном, что ненавидит христиан, и рассказывал мне, что слыхал от верховного жреца, Богомила, с которым он часто беседует, такие речи об этих проклятых кудесниках, что волосы у него становятся дыбом, когда он повстречается с христианином. Богомил сказывал ему, что они сбираются по ночам, близ Аскольдовой могилы, на развалины бывшего их храма, который построил при княгине Ольге какой-то боярин Ольм, а после приказал разорить князь Святослав Игоревич; что у них тут происходят такие богомерзкие дела, что даже киевские ведьмы близко к тому месту не подходят; что они едят малых детей, пьют кровь человеческую, поклоняются каким-то расписным доскам и, вместо того что б чтить всемогущего Перуна, Световида [23], Ладу или хоть варяжского Одена, молятся злому Чернобогу [24] и просят его извести нашего отца, великого князя Владимира. А вы знаете, братцы, как любит его Всеслав: так даст ли он себя прельстить этим злодеям.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Отчего же он так переменился? - спросил гридня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Допытаться не мог, а заметил только одно, что несколько дней сряду он каждое утро выезжает чем свет из Киева и возвращается не прежде полуден. Мне он говорит, что будто объезжает Сокола - своего вороного коня. Но зачем же он ездит всегда один и не берет даже с собою слуги своего? Да добро, уж я же его подстерегу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Тс, тише, тише, братцы! - сказал варяг. - Вон, кажется, девушки собрались в кружок: верно, какая-нибудь красавица хочет спеть песенку. Послушаем...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фрелаф не ошибся: все затихло в шумном хороводе, и одна из девушек запела звонким и приятным голосом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не весенний ветерок</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С полуден подул,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не былиночка, сиротиночка</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В поле зашаталася, -</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Заревели ветры буйные,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закачался темный бор,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И все гости поднебесные,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сизокрылые орлы,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">По глубоким дебрям прятались,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">И все мелки пташечки</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">По кусточкам притаилися.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Одна только пташечка,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сиротина горлинка,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Без приюту оставалася:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Она ждала, поджидала</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Своего сизого голубочка.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У окошечка, у косящего</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Красна девица сидит,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поджидая друга милого</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Из далекой стороны,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В слезах поет, рыдаючи:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;О, ветер, ветер-государь!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тебе мало ли высоких гор</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Под облаками дуть,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Или не стало тебе моря синего</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Разыграться, распотешиться?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не бушуй ты во чистом поле,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не мути широкий Днепр,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не мешай ты другу милому</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На свою родную сторонушку</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Воротиться поскорей&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ай да соловьиное горлышко! - сказал большеголовый детина. - Ну, знатно пропела!.. Да и песенка сложена хитро.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Клянусь Геллою [25], - вскричал варяг, - эта певица стоит Фрелафова поцелуя, и во что бы ни стало, я ее поцелую.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А если она чья-нибудь невеста? - прервал Стемид.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так что ж?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И жених ее здесь?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Тем лучше: я при нем ее поцелую.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А если он детина плечистый и не любит, что б его невесту целовали?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не любит! А мне какое до этого дело?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Полно хвастать, Фрелаф! - подхватил гридня. - Ты только боек на словах, а как дойдет дело до кулаков, так первый за куст спрячешься.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кто? Я? - вскричал Фрелаф. - Я, природный варяг, побоюсь ваших русских кулаков? Так ступайте же за мною: я вам покажу, как у нас за морем целуют красных девушек!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фрелаф расправил свои огромные усы, понадвинул на глаза стальной шлем и выступил вперед.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стемид, гридня и несколько других молодых воинов пошли вместе с ним. Девушка, пропевшая песню, сидела на траве посреди своих подруг. Увидя приближающуюся толпу ратных людей, она поспешно вскочила; хоровод расстроился, и все ее подруги, как дождь, рассыпались по лугу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нехорошо, господа честные, нехорошо! - кричали граждане киевские, идя вслед за воинами. - Не трогайте наших девушек!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Но молодые люди, не слушая их криков, стали их ловить, а Фрелаф пустился догонять певицу, которая побежала прямо к Велесову капищу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ага, попалась певунья! - закричал варяг, схватив ее за руку. - Да небось, голубушка, ведь я не медведь, не съем тебя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Пусти меня, пусти! - кричала девушка, стараясь освободиться из рук варяга.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нет, прежде поцелуй, красавица!.. Да полно рваться!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я сказал, что тебя поцелую, - и вертись себе как хочешь, а я поставлю на своем.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Посмотрим! - загремел грубый голос у самых дверей Велесова храма, и мужчина колоссального роста в два прыжка очутился подле варяга. - Оставь эту девушку, - продолжал незнакомец, - или я, несмотря на твою железную шапку, размозжу тебе голову.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кому? Мне? - сказал Фрелаф, схватясь правою рукой за рукоятку своего меча и продолжая держать в левой руку пойманной им девушки. - Да кто ты сам таков, что б смел указывать и грозить варяжскому витязю Фрелафу?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Я тебе говорю, пусти ее! - повторил незнакомец, подняв руку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ого, ты хочешь драться! - закричал варяг, отступя шаг назад и выхватив из ножен свой меч. - Постой, поганый русин, я с тобой переведаюсь!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Быстрее молнии опустился тяжелый кулак незнакомца, и меч выпал из онемевшей руки варяга. Девушка, освободясь из рук его, побежала к своим подругам, которые снова собрались в кучу и, окруженные киевскими гражданами, шли прямо к городу. Фрелаф нагнулся, чтоб поднять свой меч, но, оглушенный новым ударом, почти без памяти упал на землю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что, молодец, - сказал насмешливо незнакомец, - каково целуются русские красавицы? Да что ж ты лежишь?.. Вставай!.. Ага, храбрый витязь, видно, смекнул: знаешь, что на Руси лежачих не бьют! Да добро, так и быть, я тебя и стоячего не трону - вставай.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фрелаф с трудом приподнялся на ноги.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Теперь ступай к своим товарищам, - продолжал незнакомец, - да скажи им, чтоб они вперед не обижали наших девушек.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Но варяг не очнулся еще от последнего удара и, устремив на незнакомца свои одурелые и неподвижные глаза, стоял, как вкопанный, на одном месте.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну, что ж ты, витязь Фрелаф, - сказал, помолчав несколько времени, незнакомец, - иль у тебя язык и ноги отнялись? То-то же! Видно, еще, брат, никогда не отведывал русского кулака? Ах вы грабители, грабители! Нашли кого обижать, буяны! Да чего и ждать от шайки разбойников, у которой атаманом убийца родного своего брата.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Товарищи, товарищи, сюда! - заревел Фрелаф, бросившись бежать от незнакомца.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ей ты, могучий богатырь, - закричал вслед ему незнакомец, - постой, подыми свой булатный меч: неравно наткнешься на какую-нибудь посадскую бабу, так было бы чем оборониться.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Но варяг бежал, не оглядываясь; товарищи его были уже далеко: они отправились вслед за девушками в город и не могли ни слышать его голоса, ни поспеть к нему на помощь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомец завернулся снова в верхнюю свою одежду, сел на одной из ступеней Велесова капища и устремил по-прежнему внимательный взор на вершину Кучинской горы.</span></span></p>\r\n<h4 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">III</span></span></h4>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прошло более часу. Последний свет от догорающей зари становился все бледнее и бледнее; тени сгущались, прозрачные облака темнели, и безлунная ночь, расстилая по небесам свою звездную мантию, медленно опускалась над засыпающими волнами Днепра. Вот затихло все в окрестности, и от времени до времени глубокая тишина прерывала отдаленный гул и глухой, невнятный говор многолюдного города. Огни потухали один после другого; вот замолкли веселые песни, затих шум по улицам, - все покрылось темнотою... Вдруг на вершине Кучинской горы, в одном из теремов белого здания, замелькал слабый огонек.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А, вот и условленный знак! - сказал незнакомый, вставая. - Кажется... да, так точно, в третьем окне полуденного терема.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В близком расстоянии раздался шелест от шагов поспешно идущего человека; он шел прямо к божнице Велесовой, распевая вполголоса:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Как во стольном городе во Киеве,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Что у ласкова князя Владимира,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А и было пированье, почестный пир,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А и было столованье, почестный стол...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Это ты, Тороп? - спросил незнакомец, сделав несколько шагов навстречу к певцу, который, сняв вежливо шапку, поклонился ему в пояс и сказал:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, боярин, это я.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И ты поешь эту проклятую песню, и ты величаешь Владимира?!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не погневайся, боярин: из песни слова не выкинешь, а в Киеве только и песня, что о князе Владимире. Вот вечор я перенял еще песенку, которую сложил Соловей Будимирович, любимый певец великокняжеский. Ах, боярин, что за песня! Послушай-ка!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Светел, светел месяц во полуночи,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ясно солнышко во весенний день;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А светлее чиста месяца,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А яснее красна солнышка</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наш великий князь...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Молчи! - закричал с нетерпением незнакомец. - Разве ты для того живешь в Киеве, чтоб перенимать глупые песни.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Не гневайся, боярин; песни песнями, а дело делом! Тебя дожидается Богомил.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Он дожидается меня, - повторил незнакомец, - и быть может, для того, чтобы выдать головою своему великому князю... Но погоди, старая лисица, ты не перехитришь меня, и если б только мне удалось... Тороп, добился ли ты наконец толку: узнал ли ты, у кого из гридней или отроков Владимировых нет ни роду, ни племени?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- У кого! Да они почти все безродные: у одного отец и мать за морем, у другого в Великом Новогороде. Ведь при лице княжеском и десяти витязей не начтешь из здешних природных киевлян.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Итак, ты до сих пор ничего не умел проведать?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да давно ли, боярин, я живу в Киеве? Давно ли приказал ты мне идти в услужение к этому Богомилу, который сам ест за десятерых, а домочадцев своих морит голодом? Недаром про него сложена песенка:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А и тучен наш верховный жрец, А и тощи его слуги верные; Уж как примется, наш батюшка, Он глотать по целому быку...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Мне некогда слушать твоих песен! - прервал с досадою незнакомец. - И если б ты поменьше пел, а побольше думал о том, что тебе приказано, так, может статься, давно бы уж не жил у Богомила. Я знаю, что тот, кого мы ищем, служит при Владимире; Богомилу известна эта тайна; да от этого хитрого кудесника и сам владыка его, Чернобог, не добьется правды. Ты говоришь, что почти у всех отроков Владимировых нет ни роду, ни племени? Пусть так, но они знают своих отцов и матерей! А нет ли из них такого, который не знает не только отца и матери, но даже своей родины и которому не более двадцати двух лет от роду?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да вот сегодня, боярин, перед закатом солнечным, я подслушал, как здесь на Подоле шла речь у княжеских витязей о каком-то Всеславе, и один усатый варяг, потешаясь над ним, называл его безродным. Они говорили, что он каждый день чем свет выезжает из Киева на своем вороном коне, а зачем и куда - никто из них не знает. Погоди, боярин, я не проронил этих речей, и может статься... Да что об этом говорить, утро вечера мудренее! А теперь не пора ли тебе к Богомилу? Ведь он давно уже дожидается.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Пойдем! - сказал незнакомец. - Посмотрим, что скажет мне этот премудный бездушник!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Они не прошли десяти шагов, как вдруг Тороп остановился и, поднимая что-то с земли, сказал:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что это? Боярин, боярин, посмотри-ка мою находку! Меч... да еще, кажется, не простой!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Брось его! - сказал незнакомец. - Ты человек не ратный, а он и тебе руки замарает...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И, что ты, боярин: он чистехонек, - за что бросать! - продолжал Тороп, затыкая за пояс свою находку. Может статься, он солида четыре ст<em>о</em>ит<a name=\"_ftnref7\" href=\"#_ftn7\">[7]</a>, а прошу не погневаться, боярин, по милости твоей у меня и полшляга за душой не осталось: все проел дочиста, чтоб не умереть с голоду, с тех пор как живу у этого скряги Богомила.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомец не отвечал ни слова и продолжал идти молча к тому месту, где начиналась пробитая в гору, почти по отвесной линии, крутая тропинка. Она служила кратчайшим путем для тех, кои не хотели идти широкою и покойною дорогою, которая, изгибаясь по крутому скату и опоясывая несколько раз всю полуденную сторону Кучинской горы, вела на самую ее вершину.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Покрытые соломою низенькие лачужки для сторожей, клети, построенные на высоких столбах, и обширные огороды, которые, начинаясь с средины Подола, тянулись до самой подошвы Кучинской горы, были некогда единственным предместьем северо-восточной стороны древнего Киева. Наблюдая глубокое молчание, незнакомый и провожатый его вошли в один из бесчисленных закоулков, кои составляли заборы и плетни, отделявшие один огород от другого. Все было тихо кругом. Ночные сторожа дремали у дверей своих избушек, и только изредка кое-где вскрикивал кузнечик и от времени до времени прохладный весенний ветерок шептал в густых листьях кудрявой рябины или ветвистой черемухи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Боярин! - сказал Тороп, прервав наконец продолжительное молчание. - Хоть и не пристало бы мне первому заводить с тобою речь, но не погневайся, если я спрошу тебя: ради чего ты живешь таким отшельником? Когда мы были еще на чужой стороне, ты больно тосковал тогда о святой Руси; да и не диво: хвалисский город [26] Атель, в котором мы жили, в пригородье Киеву не годится, а об хвалисах, узах, печенегах и говорить нечего: народ поганый, хуже, чем эта чудь белоглазая и мещера долгополая. Помнишь ли, боярин, бывало, как затяну песенку:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Уж как тошно, тошно ясну соколу</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С коршунами жить... -</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">так у тебя слезы на глазах навернутся. Ну вот, по милости богов, мы опять на своей родине, и уж годов пяток, побольше, как ушли с тобою из земли печенежской, а что толку-то? Ты забился в лес, живешь в лачужке, как медведь в берлоге! Да этак все равно, если б ты и вовсе у печенегов остался; леса там не хуже здешних, а Волга-то почище нашего киевского Днепра: как расходится, матушка, так словно море Хвалисское, есть где поразгуляться, - не сядешь на мель, не наткнешься, как у нас, на пороги. Нет, боярин, воля твоя, а житье твое не житье! Э, знаешь ли что? Ведь я еще тебе об этом не сказывал: ты здесь не один живешь в лесу - я недавно набрел на хижину старого дровосека. Предобрый человек! Я заплутался и устал до смерти, а он не спросил даже, кто я, а накормил и напоил как родного. Я заходил к нему еще раза два; и в последний раз близко часу проболтал с его дочерью... Ну уж девушка! Сродясь таких не видывал! Как подумаешь, что такая красавица живет почти одна-одинехонька в дремучем лесу! Эх, сиротинка, сиротинка горемычная! Другие песенки попевают, играют в хороводах, колядуют, венки заплетают да женихов высматривают, а она, сердечная, словно горлинка одинокая, и свету божьего не видит! Только и отрады-то, что пойдет иногда побродить по лесу да послушать, как птички поют. Она говорит, что любит мои сказки; не диво: что ей, голубушке, от скуки-то делать, с кем словечко перемолвить? Отец ее часто уходит в Киев, так сидеть все одной, от раннего утра до поздних сумерок, ведь этак и одурь возьмет, - день-деньской за веретеном... Э, чуть было не забыл: она просила меня купить ей веретено поузорчатее. Купить-то я купил, да когда удастся к ней отнести? Что, боярин, завтра я тебе не надобен? Ась? Что?.. Да ты меня не слушаешь? - промолвил болтливый Тороп, заметив наконец, что господин его говорит вполголоса с самим собою и не обращает никакого внимания на его слова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Боярин, боярин!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну что?.. - спросил незнакомый с рассеянным видом. - О каком ты говоришь веретене?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А вот об этом, - сказал Тороп, вынимая из-за пазухи раскрашенное яркими красками деревянное веретено. - Я купил его для дочери дровосека, о котором сейчас тебе рассказывал, и если ты дозволишь... Постой, постой, боярин!.. Нишкни-ка... - продолжал вполголоса Тороп, указывая пальцем на угол дощатого забора, на котором отразился слабый свет. - Что это?.. Никак, сюда идут с огнем.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- В самом деле! - отвечал незнакомый. - Я слышу шорох... да, точно! Сюда идут.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Яркий свет блеснул из-за угла забора, и шагах в тридцати от них показались двое рослых мужчин, поспешно идущих. Один из них нес в руке зажженный факел, от которого свет, отражаясь на стальном шлеме его товарища, вполне освещал его красный нос и огромные рыжие усы.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Спрячемся, боярин, - шепнул с приметною робостью Тороп. - Это ратные люди, а один-то из них, вот тот, что идет с огнем, кажется, Стемид - ближний стремянный великого князя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А какое мне до этого дело? - прервал незнакомый. - Разве дорога проложена для одних стремянных княжеских?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, боярин, тебе какое дело, да меня-то он знает: так ладно ли будет, если он станет рассказывать, что слуга верховного жреца таскается по ночам неведомо с какими людьми? Спрячемся хоть в этом пустом шалаше; они мигом пройдут.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незнакомый, хотя с приметным неудовольствием, но послушался Торопа и вошел вместе с ним в небольшой плетневый балаган, прислоненный к самому забору.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Через полминуты проходящие поравнялись с шалашом. Тороп не ошибся: один из них был точно Стемид, а в товарище его, неуклюжем воине с рыжими усами и красным носом, вероятно, читатели наши узнали уже варяжского мечника Фрелафа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Стой, - вскричал Стемид, прошедши мимо шалаша, - я нейду далее!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И, полно, - сказал варяг, - пойдем, уж недалеко осталось!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да куда же ты меня тащишь? Послушай, Фрелаф, уж не издеваешься ли ты надо мною?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ты, Стемид!.. Клянусь тебе Оденом...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Добро, не клянись, а шути над тем, кто тебя глупее!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да разве я не говорил тебе, что мы, как верные слуги княжеские, должны сослужить ему службу?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, ты говорил мне это, когда поймал меня у самого теремного двора, заставил взять этот светоч и потащил вместе с собою; но я хочу знать, о чем идет дело, и без этого не тронусь с места.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну так слушай же, Стемид: здесь, на Подоле, у самого храма вашего бога Белеса, скрываются враги великого князя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как так?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да так. Помнишь, как я погнался за девушкой, которая так хорошо пела в хороводе?.. Ну, вот я настиг ее у самой божницы, стал целовать, и, надобно сказать правду, она не больно отбивалась. Вдруг, откуда ни возьмись, пребольшой мужчина, да и ну позорить, и добро б меня, а то нашего великого князя; я припугнул его порядком, а меж тем побежал за вами, что б вы помогли мне связать его, и ты попался мне первый. Ну, теперь куда и зачем ты идешь со мной?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Куда - знаю, а зачем - не ведаю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как не ведаешь? Вестимо зачем, чтоб схватить этого Разбойника.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да неужели ты думаешь, что он станет там дожидаться до тех пор, пока за ним придут и скрутят руки назад?.. Я чаю, уж теперь давным-давно и след его простыл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А почему знать?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нет, Фрелаф, я устал и не хочу всю ночь бродить по-пустому! Прощай!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, братец, постой! Ну, если нам не удастся поймать этого злодея, так авось отыщем мой меч.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Твой меч?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну да! Вот видишь: как этот разбойник стал позорить нашего князя, так у меня вся кровь закипела в жилах! Ты знаешь, Стемид, я детина добрый, а уж если расхожусь...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Знаю, братец, знаю!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Говорить непригожие речи о нашем великом князе! И при ком же?.. При молодце Фрелафе! Веришь ли, Стемид, меня взяло такое зло, что я земли под собой не почуял!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Верю, верю!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Выхватил меч, да как махнул со всего плеча могуча!.. Ну, счастлив, разбойник! Кабы я не обмишулился, так раскроил бы его надвое!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А ты промахнулся?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Промахнулся, братец, и вместо головы этого шального хватил по камню. Батюшки мои, как посыплются искры!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эге! - прервал Стемид. - То-то нас всех и осветило, а уж мы думали, думали, что за диковинка такая?..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Камень разлетелся вдребезги... - продолжал варяг, нимало не смущаясь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Один осколок, - прервал снова Стемид, - попал в голову городскому вирнику, который на ту пору обходил киевские улицы.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Эх, братец, ты настоящий гусляр! Тебе бы все глумиться да скоморошничать. Я говорю дело. Вот, как ни крепко я держал в руке меч, а он вылетел.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Зачем же ты его не поднял?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Зачем! Затем, что я боялся упустить этого злодея и побежал скорей за вами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так вот какую службу я должен сослужить великому князю: помочь тебе отыскать твой меч! Нет, Фрелаф, поберегу мой живот до поры до времени, а эта служба мне не под силу. Прощай!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Постой, братец! Да сделай отеческую милость, дойди вместе со мною.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кой прах, Фрелаф, уж не боишься ли ты идти один?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кто? Я боюсь? Что ты, братец! Да давай на одного меня днем полсотни молодцев - усом не поведу! Но теперь дело ночное: кто знает, что случится? Может статься, их там много: зайдут с тыла, наткнешься на засаду... мало ли ратных хитростей? Тут храбрость не поможет; вдвоем то ли дело: стали спинами друг к другу, да и катай на все стороны! Ну, понимаешь ли теперь?..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Понимаю, храбрый витязь, понимаю! Я только одного в толк не возьму, в кого ты уродился? Все твои земляки взросли на боях, удалые воины, молодцы, а ты...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что я?..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да как бы тебе сказать? Бабой тебя назвать нельзя: усы велики; греком также не назовешь: у тебя рыжие волосы, а варягом назвать стыдно.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ты, братец? Неужли в самом деле думаешь, что я робею? Да не будь я Фрелаф, сын Руслава, внук Руальда и правнук Ингелота; да чтоб на моей тризне пели не скальды вещие, а каркали черные вороны; чтоб в мой доспех наряжались старые бабы и вместо меча из моих рук не выходило веретено с пряжею...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Возьми ж свой меч, могучий богатырь Фрелаф! - сказал кто-то позади насмешливым голосом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Варяг обернулся, отпрыгнул назад и вскричал с ужасом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Это он!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, это я! - продолжал спокойно незнакомый. - А вот твой меч, - прибавил он, бросив что-то к ногам Фрелафа. - Подыми его, добрый молодец, да смотри поберегай: он тебе по плечу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сказав сии слова, незнакомый пустился скорыми шагами вслед за небольшим человеком, который, закрывая лицо полою своего кафтана, бежал, не оглядываясь, по тропинке, ведущей на вершину Кучинской горы. В полминуты они оба исчезли из глаз Стемида.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Откуда взялся этот долговязый? - спросил он наконец, перестав смотреть в ту сторону, где скрылся незнакомый. - Не видал ли ты, Фрелаф?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Варяг не отвечал ни слова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ж ты, - продолжал Стемид, - онемел, что ль? Фрелаф, а Фрелаф?.. Да очнись, братец!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Это он! - промолвил, заикаясь, варяг.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Кто он?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Точно он!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да кто?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну вот тот самый, что давеча у Велесовой божницы...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Так что ж ты, прозевал его?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Прозевал!.. Нет, братец, полно теперь спорить! До сих пор я не давал веры речам вашим; бывало, как вы начнете рассказывать о киевских ведьмах и злых чародеях, так я и слушать не хочу, - теперь всему верю! Ах, батюшки, что это?.. Лишь только этот кудесник дыхнул мне в лицо, так у меня и руки опустились, и ноги онемели, и даже язык отнялся. Ну, не диво же, что я давеча промахнулся: он отвел мне глаза... Да, да, он точно меня обморочил: вместо себя подсунул камень, а сам сквозь землю провалился.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Статься может, Фрелаф, только я слыхал, что от испуга также язык отнимается.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да помилуй, братец! Если этот разбойник не чародей, так откуда же он взялся?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- А вот, может быть, из этого шалаша.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как бы не так! Как я обернулся, так он еще по колени был в земле!.. Да разве не слышишь, Стемид? Фу, какой смрад!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Нет, я ничего не слышу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ты, братец! Так и пахнет преисподнею... Уйдем скорей отсюда.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Погоди, Фрелаф: подыми прежде свой меч.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Мой меч?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ну да! Он бросил что-то вот здесь наземь, сказал, что это твой меч, и советовал тебе поберегать его.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- В самом деле? - вскричал с радостью Фрелаф. - То-то же, - видно, не под силу пришелся этому разбойнику! Да где же он?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Постой! - сказал Стемид, наклоняясь. - Вот здесь, кажется... Что это? - продолжал он, подымая с земли большое расписное веретено. - Ну, брат, подшутил же он над тобою! Посмотри, каков твой меч!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Как! Веретено!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Да, да, веретено! - повторил Стемид, умирая, со смеху. - Ай да молодец! Ну, теперь и я вижу, что он чародей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах он собака! - заревел варяг. - Постой, разбойник, вот я тебя!.. Держи его, держи!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Полно горланить-то, Фрелаф! Не умел держать, когда был у тебя под носом, а теперь орешь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Ах он проклятый кудесник! Да я на дне морском его отыщу! Чтоб я не отомстил за эту обиду, я, Фрелаф, сын Руслава, внук Руальда!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- И правнук Ингелота, - промолвил Стемид. - В самом деле, этот чародей вовсе не уважает твоих предков; однако, я чаю, ты не погневаешься на меня, если я не отдам тебе этого расписного меча? Дозволь мне похвастаться им перед товарищами!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Что ты, Стемид! - вскричал Фрелаф. - Пожалуйста, не рассказывай никому, пока я не отомщу за эту смертную обиду. Ты знаешь наших молодцев: как попадется им на язычок это веретено, так мне житья не будет.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Слушай, Фрелаф, - сказал, помолчав несколько времени, Стемид, - я парень добрый, так и быть, не перескажу никому о том, что видел; зато и ты не моги никогда хвастаться передо мною своим удальством и богатырством. Без меня, пожалуй, себе на здоровье, ломай дубья, бери города, разбивай кулаком стены каменные, хвастай сколько душе угодно; но при мне, если ты заикнешься об этом, а пуще коли вымолвишь хоть одно непригожее слово о княжеском отроке Всеславе, - смотри, берегись: этот меч-кладенец будет всегда со мною, и я при всех тебе его отдам... Ну, теперь пойдем - пора спать. Ах он леший проклятый! - продолжал Стемид, пройдя несколько шагов и принимаясь снова смеяться. - И пришло же ему в голову!.. Да кто ему шепнул, разбойнику, что ты храбрый витязь Фрелаф! Ну, видно, он в самом деле кудесник!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пристыженный варяг, повесив голову, отправился вместе со Стемидом по дороге, ведущей в Киев. Он молчал как убитый и только изредка, когда громкий хохот его товарища прерывал ночную тишину, бормотал про себя, пошевеливая своими огромными усами:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Смейся, смейся, проклятый русин! Засмеялся бы ты у меня кабы я был сам-третей или сам-четверт!</span></span></p>\r\n<hr style=\"text-align: justify;\" size=\"1\" />\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn1\" href=\"#_ftnref1\">[1]</a> Греческие летописцы, а в особенности Лев Диакон, называют русских - ставро-скифами. (Здесь и далее примеч. авт.)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn2\" href=\"#_ftnref2\">[2]</a> Род кинжала.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn3\" href=\"#_ftnref3\">[3]</a> <em>Вирниками</em> назывались тогдашнего времени полицейские чиновники, кои впоследствии известны были под именем <em>губных старост</em>. Тиуны, по мнению издателей Русской Правды, были то же самое, что у древних германцев судьи по уездам. <em>Метальники</em> - то же, что впоследствии дьяки: они исполняли также должность нынешних казначеев и принимали вносимые в княжескую казну подати. Вместо нынешних квитанций, употреблялись тогда бирки, то есть небольшие палочки, на которых прямые и крестообразные зарубки заменяли нынешние цифры. Обыкновенно, по сделанным на ней известного числа зарубок, она раскалывалась во всю длину надвое: одна половина оставалась у приемщика, другая у отдатчика. Первая служила приемщику вместо записной тетради, другая заменяла для отдатчика квитанцию или расписку в получении от него денег или вещей.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn4\" href=\"#_ftnref4\">[4]</a> Село, в котором был один из любимых потешных дворов Владимира.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn5\" href=\"#_ftnref5\">[5]</a> Речка, впадающая в Днепр.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn6\" href=\"#_ftnref6\">[6]</a> Гридни и отроки княжеские - то же, что телохранители и оруженосцы.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a name=\"_ftn7\" href=\"#_ftnref7\">[7]</a> Солид - греческая золотая монета тогдашнего времени величиною с червонец; шляг - мелкая серебряная монета.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','«Аскольдова могила»- Повесть времен Владимира Первого  (II)','uk',1168,2142,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Далі Державний Секретар Ватикану, цитуючи документи ІІ Ватиканського Собору, наголосив, що Церква, силою Євангелія Ісуса Христа, звіщає людські права, визнає і позитивно оцінює динамізм, з яким їм у наш час повсюди сприяють. Після того кардинал зупинився детальніше над темою природного права, людської гідності, універсальності, індивідуальності та охороні тих прав, зокрема задумуючись над правом на життя та релігійну свободу.</span></span></p>','2009-02-06 15:56:27',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5 лютого, у місті Мадриді, кардинал Тарчізіо Бертоне, Державний Секретар Ватикану, виступив перед Єпископською Конференцією Іспанії. Його промова була присвячена Загальній Декларації Прав Людини, підписаній державами-членами ООН 10 грудня 1948 року. З нагоди відзначення 60-річчя цієї знаменної для людства події, кардинал Бертоне зосередив свою промову на вченні Святішого Отця Венедикта ХVІ про людські права. На початку свого звернення Державний Секретар передав щирі вітання та благословення від Вселенського Архиєрея і пригадав, що у серпні 2010 року Папа відвідає Іспанію, оскільки саме тоді у Мадриді проходитиме Всесвітній День Молоді. Далі кардинал зазначив, що підписання Загальної Декларації стало фундаментальним жестом в переміні моральної свідомості людства. За його словами, потрібно ще раз наголосити на важливості, яку Апостольська Столиця надає визнанню і охороні основних прав людської особи і вкладу християн-католиків у поширення та оборону цих прав. <br /><br />Переходячи до головної теми свого звернення, кардинал Тарчізіо Бертоне підкреслив, що Церква дуже серйозно ставиться до проблематики людських прав, додаючи: &laquo;Бажання миру, пошук справедливості, пошана людської гідності, співпраця і гуманітарна підтримка, виявляють справедливі бажання людського духу. У цьому сенсі у нас наново звучить відлуння слів Папи Венедикта ХVІ, які він скерував до Генеральної Асамблеї Організації Об\'єднаних Націй 18 квітня минулого року, коли підтвердив, що Загальна Декларація &laquo;була результатом узгодженості релігійних та культурних традицій, а всі вони мотивовані спільним бажанням поставити людську особу в осередку суспільних інституцій, правил і заходів та вважати її головною для світу культури, релігії та науки&raquo;. <br /><br />Кардинал Бертоне пригадав, що Загальна Декларація Людських Прав зродились на основі західно-європейської культури, зокрема, на базі християнства. Вже на перших сторінках Святого Письма людина представлена як образ і подоба Божа. Церква зробила свій вклад, роздумуючи над Загальною Декларацією у світлі Божого слова і людського розуму, як також, поширюючи та захищаючи ці фундаментальні права кожної людини. Впродовж 60 років Вселенські Архиєреї, використовуючи різноманітні нагоди, завжди виявляли свою позитивну оцінку і підкреслювали значення, яке має для світу Загальна Декларація Прав Людини. 4 жовтня 1965 року, під час візиту до ООН, наголосивши на тому, що ця Організація репрезентує обов\'язковий шлях сучасної цивілізації і всесвітнього миру, Папа Павло VІ звернувся до представників ООН такими словами: &laquo;Ви тут звіщаєте фундаментальні права і обов\'язки людини, її гідність, її свободу і, передусім, релігійну свободу&raquo;. <br /><br />Папа Іван Павло ІІ мав нагоду двічі звернутись до Генеральної Асамблеї: 2 жовтня 1979 року і 5 жовтня 1995 року. А 18 квітня 2008 року, з нагоди 60 річниці підписання Загальної Декларації, до представників Організації Об\'єднаних Націй звернувся Папа Венедикт ХVІ. У своїй промові він наголосив на тому, що Декларація дозволила різним культурам та інституціям зосередитись довкола фундаментального центру вартостей, тобто - прав. Папа також пригадав, що ці права мають за основу природне право, що вписане у серці кожної людини та присутнє у будь-якій культурі та цивілізації. <br /><br /> Далі Державний Секретар Ватикану, цитуючи документи ІІ Ватиканського Собору, наголосив, що Церква, силою Євангелія Ісуса Христа, звіщає людські права, визнає і позитивно оцінює динамізм, з яким їм у наш час повсюди сприяють. Після того кардинал зупинився детальніше над темою природного права, людської гідності, універсальності, індивідуальності та охороні тих прав, зокрема задумуючись над правом на життя та релігійну свободу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>','Права людини у навчанні Папи Венедикта XVI. Доповідь кардинала Тарчізіо Бертоне','uk',1107,2081,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дбаючи про розвиток біблійних читань, Патріарша катехитична комісія УГКЦ за останній&nbsp; рік видала 60 тис. примірників Св. Письма з коментарем. На даний час в Україні вже систематично діють 206 біблійних гуртків. Ще одним кроком до зміцнення Біблійного апостоляту стане міжнародна конференція \"Боже Слово у житті та місії Церкви\", яка відбудеться 19-20 травня у Львові.</span></span></p>','2009-04-01 14:33:02',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">25-27 березня 2009 року Патріарша катехитична комісія УГКЦ та Катехитично-педагогічний інститут УКУ провели триденний біблійний семінар \"Боже слово у житті та місії Церкви\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сорок учасників семінару - представники Катехитичних комісій єпархій та екзархатів УГКЦ - миряни і священики, монахи і монахині, катехити і вчителі християнської етики. Дехто з них вже працює у біблійних групах, інші лише вчаться організовувати і вести біблійні читання на парафії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Провів семінар о.Тарас Барщевський - бібліст, в.о. завідувача кафедри богослов\'я УКУ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На семінарі представлено принципи організації та діяльності парафіальних біблійних груп, методи індивідуальної та групової праці зі Святим Письмом, Lectio Divina. Учасники семінару ділилися досвідом, представили ситуацію Біблійного апостоляту у своїх єпархіях та екзархатах. Розглянуто актуальні питання розвитку Біблійного апостоляту в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дбаючи про розвиток біблійних читань, Патріарша катехитична комісія УГКЦ за останній&nbsp; рік видала 60 тис. примірників Св. Письма з коментарем. На даний час в Україні вже систематично діють 206 біблійних гуртків. Ще одним кроком до зміцнення Біблійного апостоляту стане міжнародна конференція \"Боже Слово у житті та місії Церкви\", яка відбудеться 19-20 травня у Львові.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Катерина Ванкович</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Секретар Патріаршої катехитичної комісії УГКЦ</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>','Біблійний семінар \"Боже слово у житті та місії Церкви\"  ','uk',1310,2284,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Девізом життя видатного хірурга Миколи Пирогова, від  дня народження якого сьогодні виповнюється 200 років, був вислів &laquo;Віра  без справ &mdash; мертва&raquo;</span></span></p>','2010-11-28 13:09:06',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://umoloda.kiev.ua/img/content/i29/29872.gif\" alt=\"Микола Пирогов: лікар і педагог.\" /><span lang=\"RU\">\r\n<p><strong>Рівно 200 років тому, 25 листопада (за новим стилем), у  родині рядового московського службовця народилася 13&ndash;та дитина. Хлопчика  назвали Миколою. У дитинстві він дуже любив гратися в &laquo;доктора&raquo;. А  оскільки сім&rsquo;я була чималенька, то &laquo;лікувати&raquo; завжди було кого. Батьки  тоді і не здогадувалися, що їхній непосидючий допитливий нащадок усе  життя покладе на допомогу людям, служіння Богу і стане всесвітньо  відомим ученим, лікарем, педагогом і громадським діячем. Ідеться про  Миколу Івановича Пирогова &mdash; неповторного експериментатора, який поєднав  науку і віру і став прикладом для багатьох поколінь лікарів. Саме він  уперше у світі використав ефірний наркоз у воєнно&ndash;польових умовах, став  &laquo;батьком&raquo; гіпсової пов&rsquo;язки при переломах і засновником військової  хірургії, власноруч провів десятки тисяч операцій. А ще доктор Пирогов  вирощував лікарські рослини, займався селекцією зернових культур,  боровся з пияцтвом і безграмотністю на селі, писав непогані вірші та  мемуари...</strong></p>\r\n<h4>Професор у 26 років</h4>\r\n<p>Навіть за сьогоднішніми мірками Микола Пирогов ріс вундеркіндом. Уже в  14 років його зарахували на медичний факультет Московського  університету, а в 17 років &mdash; обрали кандидатом для підготовки до  професорського звання! Отримавши диплом, хлопець кілька років учився за  кордоном, та ще й за кошти держави (це було великою рідкістю для  тодіш&shy;ньої Росії). Пирогов блискуче захистив докторську дисертацію, а в  26 років його обрали професором Дерптського університету (зараз  університет міста Тарту в Естонії) &mdash; найкращого на ті часи російського  &laquo;вишу&raquo;.</p>\r\n<p>Про молодого талановитого лікаря ходили легенди. Після навчання у  кращих хірургів того часу Пирогов опанував блискавичний стиль проведення  операцій, коли складні розрізи проводилися за лічені секунди. Причому  точність теж була 100&ndash;відсо&shy;ковою, пацієнти рідко помирали. До  &laquo;чудесного лікаря&raquo; їхали з усіх усюд, знаючи, що він нікому не відмовляє  в допомозі. Після операції він завжди ховав руки в кишені, щоб йому не  надумали платити. Він видавав наукові праці, а виручені гроші  переказував на користь незаможних. Здавалося, все було гаразд. Слава  Пирогова зростала, але для повного щастя чогось не вистачало...</p>\r\n<h4>&laquo;Господи, Боже мій! Удостой мене бути знаряддям миру Твого...&raquo;</h4>\r\n<p>Тривалий час майбутній геній хірургії залишався скептиком у питаннях  віри. Але, як визнавав сам Пирогов, зневіра й атеїзм роз&rsquo;їдали його  душу, позбавляючи спокою та істинної радості. Якось він надихався в  лікарні отруйним смородом від ран, які гнили і розкладалися. Тяжко  занедужавши, Пирогов багато переосмислив у житті. Він вирішив  одружитися, створити сім&rsquo;ю і нарешті звернутися до Бога. Пирогов писав:  &laquo;Для мене існування Верховного Розуму і Верховної Волі зробилися такою ж  необхідністю, як і моє власне існування&raquo;.</p>\r\n<p>Наступним ударом для лікаря стала смерть дружини. Вона померла після  пологів, залишивши на чоловіка двох малолітніх дітей. Учений згадував,  що в той час він був дуже самотнім, не мав друзів і відчував гостру  потребу в підтримці Небесного Отця, щоб заспокоїти схвильовану душу:  &laquo;Мені був потрібен відсторонений, недосяжний, високий ідеал віри. І,  взявшись за Євангеліє..., я знайшов для себе цей ідеал&raquo;.</p>\r\n<p>Відтоді кожен день Пирогова був служінням &mdash; Богу та стражденним  людям. Він намагався зробити все, аби полегшити біль пацієнтів,  урятувати життя і, якщо є можливість, обійтися без ампутації кінцівок.  Утім впроваджувати євангельські ідеали в життя було нелегко. Сьогодні  важко повірти в те, що колись пацієнти лягали &laquo;під ніж&raquo; без наркозу,  знеболюючих препаратів і відчували страшний біль під час операцій.  Лікарі різали в буквальному сенсі &laquo;по живому&raquo;, намагаючись не звертати  уваги на крики та стогони хворих. Серед медиків навіть існувало негласне  правило: людині, яка надто болісно сприймала страждання хворих,  смикалася на волання та зойки, якій не вдавалося спокійно й безпомилково  оперувати в таких умовах, краще не йти в хірургію.</p>\r\n<p>Звісно, лікарі намагалися щось придумати, аби полегшити собі роботу.  Нерідко вони використовували в ролі наркозу спирт чи горілку. Спожитий  алкоголь і справді допомагав пацієнту &laquo;випасти&raquo; зі свідомості,  знепритомніти й пропустити таким чином найболючіші моменти розрізання.  Утім лікарі розуміли, що алкогольна &laquo;анестезія&raquo; доволі ризикована й не  завжди ефективна. Було дуже важко розрахувати належну дозу, яка могла б  &laquo;виключити&raquo; конкретного пацієнта, але не вбити його.</p>\r\n<p>Зацікавившись дією парів ефіру на живий організм, Пирогов проводив  досліди на собаках. Потім багато разів випробовував наркоз на собі. Від  цього лікар схуд, погано почувався, проте зрештою вирішив: для  конкретного хворого, для однієї&ndash;двох операцій, які йому судилося  перенести, ефір є безпечним. І надалі він ніколи не допускав, щоб  оперували без заспокійливого ефіру. Щоправда, Пирогов був не першим і не  єдиним хірургом, хто застосовував такий вид наркозу в лікарнях. Але він  уперше в історії медицини почав оперувати поранених із застосуванням  ефіру в польових умовах. Це сталося під час війни на Кавказі. До слова,  маску для ефірного наркозу, сконструйовану Пироговим, використовують у  медицині досі.</p>\r\n<p>Невдовзі Пирогов почув, що для наркозу можна застосовувати хлороформ.  На зворотному шляху з Кавказу лікар випробував цей новий засіб.  Побачивши ефективність, за п&rsquo;ять років він зробив 2,5 тисячі таких  операцій!</p>\r\n<h4>Гіпсувати перелом &mdash; теж його ідея!</h4>\r\n<p>Пирогов був проти війни, але значна частина його життя пройшла в  польових шпиталях, поруч із центром воєнної &laquo;м&rsquo;ясорубки&raquo;. &laquo;Війна &mdash; це  травматична епідемія,&mdash; писав учений. &mdash; Як при великих епідеміях завжди  недостатньо лікарів, так і під час великих воєн завжди їх бракує&raquo;. 1847  року хірург виїхав на Кавказ, де російська армія воювала проти місцевих  горян. Там хірург мало не падав із ніг, оперуючи поранених, виконував  титанічну роботу, але результат його не втішав. Багатьом доводилося  робити ампутацію кінцівок, чимало пацієнтів помирали від гнійних  ускладнень. Сотні солдатів та офіцерів після переломів ставали каліками,  тому що тоді лікарі не знали, як можна нормально фіксувати уламки  кістки. Від рухів уламки зміщувалися, рана довго не загоювалася, а коли  кістки нарешті зросталися, кінцівки потворно скривлювалися. І взагалі,  за канонами тодішньої військової медицини, нагноєння в рані, ураження  кістки, складний перелом &mdash; це все неминуче означало ампутацію. Але невже  немає іншого виходу? Ця думка не йшла лікареві з голови. Він хотів  боротися до останнього, щоб зберегти при переломі руку чи ногу. Пирогов  пробував стягувати кістки крохмальними пов&rsquo;язками. А щоб перевірити, як  діє метод, лікар після багатогодинних операцій супроводжував обози, які  виводили поранених iз місця битви. Накрохмалені бинти були непоганою  альтернативою, але мали багато недоліків... Проблема здавалася  нерозв&rsquo;язною, аж поки Пирогов не потрапив у майстерню знайомого  скульптора. Там лікар побачив гіпсові форми, які скульптори  використовують на проміжному етапі. Еврика! Саме те, що потрібно! Тож  коли в 1853 році почалася Кримська війна, Микола Іванович вважав своїм  громадянським обов&rsquo;язком вирушити до Севастополя і домігся призначення в  діючу армію. Оперуючи поранених, Пирогов уперше в історії медицини  застосував гіпсову пов&rsquo;язку. Так гіпс потрапив у лікарні й користується  популярністю до нашого часу.</p>\r\n<h4>Солдати називали Пирогова &laquo;наш&raquo;</h4>\r\n<p>У період оборони Севастополя 1854&mdash;1855 років Пирогов проявив себе не  лише як блискучий хірург. Він був переконаний, що всю систему медичної  допомоги пораненим треба правильно організувати. Якщо в польових  шпиталях &laquo;навалювати&raquo; хворих в одну купу, а лікарі будуть метушитися між  ними, не знаючи, за кого першого братися, толку не буде. Саме такий  хаос постав перед очима хірурга, який приїхав на передову.</p>\r\n<p>Госпіталі Кримського півострова були розраховані на 2 тисячі ліжок, а  поранених було 9 тисяч. У самому Севастополі поранені лежали без їжі,  без води, просто на вулицях, на подвір&rsquo;ях під відкритим небом.  &laquo;Щасливці&raquo;, які потрапили під госпітальний дах, валялися на голій  підлозі. Добре, якщо вдавалося знайти місце під чужим ліжком. Нещасні,  які отримали рани в бою, стогнали, благали про допомогу і хапали за ноги  лікарів і фельдшерів, що проходили поруч. На операційний стіл брали не  того, кому це найбільше потрібно, а того, хто ближче лежить до проходу  чи голосніше волає. Хлороформу було мало, тому операційна, як і в минулі  роки, наповнювалась криками страждальців...</p>\r\n<p>&laquo;Над табором мучеників, складених і звалених у солдатські намети,  раптом вибухнула злива і промочила наскрізь не тільки людей, але навіть і  матраци під ними,&mdash; згадує Пирогов у своїх &laquo;Севастопольських листах&raquo;. &mdash;  Нещасні так і валялися в брудних калюжах. Можна собі уявити, як їм було з  відрізаними ногами лежати на землі. Матраци майже плавали в бруді. Коли  хто&ndash;небудь входив у цей намет лазарету, то поранені волали про  допомогу, з усіх боків голосно лунали надривні пронизливі стогони і  зубний скрегіт. Лікарі і сестри могли допомагати не інакше, як стоячи на  колінах у болоті. По 20 і більше ампутованих умирали щодня...&raquo;</p>\r\n<p>Талановитий хірург, побачивши таку картину, відразу зрозумів, що  метушня і хаос &mdash; це сили, витрачені даремно, додаткові лікарі і сестри,  помилки в діагнозі і лікуванні та смерть багатьох пацієнтів. Щоб навести  лад, Пирогов наказав &laquo;сортувати&raquo; поранених на категорії. Перші &mdash;  безнадійні. Їм потрібні засоби для заспокоєння останніх страждань,  турботливі сестри, священик. Другі &mdash; невідкладні. Цих негайно на стіл,  щоб не опинились у першій категорії. Треті &mdash; ті, що можуть зачекати з  операцією або зов&shy;сім без неї обійтись. Їм &mdash; гарний догляд, а коли спаде  напруга перших годин і днів, уважний огляд і ретельне лікування.  Четверті &mdash; легкопоранені. Із цими просто: відправляти до фельдшерів на  перев&rsquo;язку. Завдяки такому простому методу в польових шпиталях вдалося  подолати хаос і набагато ефективніше лікувати пацієнтів. Із цього  сортування згодом виросло все лікувально&ndash;евакуаційне забезпечення  поранених, яким користуються по сьогоднішній день. Неймовірно, але факт &mdash;  до Пирогова до цього ніхто не додумався.</p>\r\n<p>У Севастополі Пирогов остаточно припинив лікарську метушню на полі  бою: під обстрілом, на брудній землі, лікар може зробити не більше, ніж  санітар. А наближати медичну допомогу до пораненого &mdash; це не тягнути  лікаря на передову, а негайно виносити пораненого з&ndash;під вогню. Завдяки  такому підходу лікарі могли врятувати набагато більше життів. Солдати це  чудово розуміли. Вони бачили і відчували щиру турботу Пирогова, тому з  теплотою називали його &laquo;наш&raquo;.</p>\r\n<p>Мало хто знає, що саме Микола Іванович заснував інститут сестер  милосердя в Росії, що став родоначальником вітчизняного Товариства  Червоного Хреста. Згодом медичні сестри брали участь практично у всіх  великих війнах, а тоді це здавалося неможливим. &laquo;Жінки в бойових умовах?  Жінки, відповідальні за винесення поранених з&ndash;під вогню? Господині  лазаретів? Це нісенітниця!&raquo; &mdash; кричали тодішні чиновники та моралісти.  Чимало недоброзичливців казали, що жінки сіють розпусту в армії, а  насправді жінки навели там лад: знайшли на складах &laquo;загублені&raquo; та  &laquo;списані&raquo; ковдри, одяг для солдатів, розібралися із належним харчуванням  поранених, припинили розкрадання грошей. І все це &mdash; ризикуючи потрапити  під кулю. Кожна четверта сестра милосердя, яка приїхала до Севастополя,  знайшла там свою загибель.</p>\r\n<h4>Спільний проект із... Шевченком</h4>\r\n<p>У ті далекі часи гімназистів часто били різками за будь&ndash;яку провину.  Микола Пирогов називав тілесне покарання засобом, який принижує дитину,  завдає непоправної шкоди її психіці та привчає до рабської покори, що  ґрунтується на страху. На думку педагога, рабська покора формує порочну  натуру, яка шукає відплати, жадає помститися за своє приниження. Тому  Пирогов виступав за скасування різок як буденного &laquo;засобу виховання&raquo;. Як  результат &mdash; статут гімназій і прогімназій 1864 року скасував тілесні  покарання дітей.</p>\r\n<p>У 1858&mdash;1861 роках Микола Іванович Пирогов працював попечителем  Київського навчального округу. Тоді на всю Малоросію прогриміла його  ініціатива створення недільних шкіл для дорослих. У 1860 році їх було  відкрито сто одинадцять, безплатні уроки в них вели викладачі гімназій і  студенти університетів. Простий робочий люд учився вечорами і у вихідні  дні.</p>\r\n<p>Цікаво, що Тарас Шевченко підтримав ініціативу Пирогова і вислав iз  Санкт&ndash;Петербурга для учнів українських недільних шкіл 50 примірників  свого &laquo;Кобзаря&raquo;. Крім того, Тарас Григорович спеціально для недільних  шкіл написав і за власний кошт видав &laquo;Буквар південноросійський&raquo; &mdash;  рідкісний для тих часів підручник з української мови. Шевченко і Пирогов  задумали великий спільний проект &mdash; цілу серію підручників для дорослих  учнів. Вони мали задум найближчим часом скласти для учнів арифметику,  етнографію, географію та історію України. Але встигли випустити тільки  &laquo;Буквар&raquo;.</p>\r\n<h4>Забальзамований &laquo;світлий образ&raquo;</h4>\r\n<p>Останні 20 років Пирогов із синами та другою дружиною жив у садибі  Вишня, що на Вінниччині. Він продовжував оперувати хворих, привів до  ладу великий дубовий ліс, посадив два березові гаї, побудував дві  греблі, млин. Займався також селекцією пшениці, винограду, троянд. А ще &mdash;  виїжджав у польові шпиталі рятувати поранених під час різних воєн. Тут  же, у Вишні, 5 грудня 1881 року Микола Пирогов і упокоївся.</p>\r\n<p>На прохання вдови тіло Пирогова було забальзамоване док&shy;тором Давидом  Іллічем Виводцевим, який приїхав у Вишню з Петербурга. Причому пані  Пирогова отримала найвищий дозвіл від Святійшого Синоду в Петербурзі.  Випадок в історії християнства унікальний &mdash; церква, врахувавши заслуги  Миколи Пирогова як зразкового християнина та всесвітньо відомого  вченого, дозволила не віддавати тіло землі, а залишити його нетлінним,  щоб учні та послідовники могли бачити &laquo;його світлий образ&raquo;.</p>\r\n<p>Спочатку забальзамоване тіло Миколи Івановича перенесли до дерев&rsquo;яної  церкви села Шереметки &mdash; за кілометр від садиби. Офіційне поховання  затримувалося, бо склеп не був готовий.</p>\r\n<p>Нарешті 24 січня 1882 року саркофаг із тілом Миколи Пирогова було  покладено у склеп, а через три роки над ним спорудили церкву Миколи  Чудотворця.</p>\r\n<p>Сьогодні у Вишні розташований Національний меморіальний музей&ndash;садиба  М. І. Пирогова. Там зберігається безліч речей, книжок, медичного  інструментарію, якими користувався Пирогов. У фондах &mdash; понад 16 тисяч  експонатів, а для огляду виставлено майже 1700. У бібліотеці зібрано  кілька тисяч книжок та журналів, чимало &mdash; з позначками вченого.  Відвідувачі можуть побачити і самого Пирогова &mdash; у його усипальниці.</p>\r\n</span></span></span> <span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"EN\">\r\n<h5><span style=\"text-decoration: underline;\">ЦІКАВО ЗНАТИ</span></h5>\r\n</span></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"RU\">Зрозумівши, що повсякденний одяг лікаря  може стати джерелом небезпечних мікробів, Микола Пирогов висунув вимогу,  щоб російські хірурги проводили операції в білих халатах, виварених в  окропі. Почувши таку новину, колеги запроторили хірурга до божевільні.  Проте його випустили через три дні, не виявивши порушень у психіці.</span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"EN\">\r\n<h5><span style=\"text-decoration: underline;\">ФАКТ</span></h5>\r\n</span></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"RU\">Микола Пирогов запропонував низку  операцій, що увійшли до золотого фонду хірургії під назвою &laquo;операції  Пирогова&raquo;. Запропоновану ним операцію &laquo;кістково&ndash;пластичного подовження  кісток гомілки у разі ампутації стопи&raquo; широко використовують і в наші  дні. </span></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"RU\"> </span></span></span><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"EN\">\r\n<h5><span style=\"text-decoration: underline;\">ПЕРЕХРЕСТЯ ДОЛІ</span></h5>\r\n<h4><span lang=\"RU\">Якби не Пирогов, не було б у нас... системи Менделєєва!</span></h4>\r\n<p>За своє життя видатний лікар допоміг тисячам пацієнтів, серед яких  були і прості селяни, і всесвітньо відомі люди, і ті, чий талант мав  розквітнути через багато років після вдалого лікування.</p>\r\n<p>Одного разу Миколу Пирогова попросили проконсультувати вчителя  Сімферопольської гімназії. Учителеві минав 22&ndash;й рік, але він не виглядав  молодим та квітучим. Блідий, худий, виснажений тривалою хворобою  чоловік ніяк не міг з&rsquo;ясувати, що саме з ним відбувається. Лікарі  припускали, що це туберкульоз легенів &mdash; діагноз, який для того часу  фактично був вироком. Але Микола Іванович уважно оглянув хворого і  поставив зовсім інший діагноз &mdash; серцева недостатність. Пирогов призначив  курс лікування, і вже через два місяці вчитель був здоровий.Згодом  колишній пацієнт став ученим&ndash;хіміком, а через чотирнадцять років після  консультації в Пирогова подарував людству періодичну систему хімічних  елементів. Це був Дмитро Іванович Менделєєв!</p>\r\n<p>У жовтні 1862 року Пирогов їздив до Італії, щоб допомогти пораненому в  ногу вождю національно&ndash;визвольного руху Джузеппе Гарібальді. Найкращі  лікарі Європи намагалися визначити місцезнаходження кулі, але всі зусиля  були марні. Їм не вдавалося з&rsquo;ясувати, чи залишилася в рані Гарібальді  куля, тому лікарі наполягали на ампутації ноги. Тільки Пирогов,  оглянувши пацієнта, зміг встановити справжній характер поранення. Микола  Іванович порадив власний метод лікування і вилікував Гарібальді, навіть  не вдаючись до операції. Згодом революціонер не раз писав своєму  рятівникові листи подяки.</p>\r\n<p>http://umoloda.kiev.ua/number/162/0/63414/</p>\r\n</span></span></span></p>','	      Скальпелем і молитвою','uk',2232,3715,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Депутат Львівської обласної ради від БЮТ, заступник голови Львівської ОДА Ігор Держко під час сесії Львівської обласної ради 9 квітня звернувся до депутатів ЛОР, аби вони звернулися до Президента України з клопотанням щодо присвоєння звання \"Герой України\" отцю Омеляну Ковчу.</span></span></p>','2009-04-10 09:04:14',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Депутат Львівської обласної ради від БЮТ, заступник голови Львівської ОДА Ігор Держко під час сесії Львівської обласної ради 9 квітня звернувся до депутатів ЛОР, аби вони звернулися до Президента України з клопотанням щодо присвоєння звання \"Герой України\" отцю Омеляну Ковчу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Блаженний Омелян Ковч (1884-1944). Народився 20 серпня 1884 року в Україні у Космачі (Косівський район) в родині Григорія Ковча (1861-1919) та Марії з дому Яскевич-Волфельд (1891-1939). Понад 20 років душпастирював у Перемишлянах (Львівська область). Ув\'язнений гітлерівцями за рятування людської гідності спочатку у тюрмі на Лонцького у Львові, а потім в концентраційному таборі на Майданеку. У листах просив родину, щоби не чинила старань про його звільнення, оскільки його обов\'язком є нести підтримку співв\'язням. Загинув на Майданеку 25 березня 1944 року. Своє перебування в концтаборі на Майданеку сприймав як Божий дар. Єврейська рада України надала о. Ковчу звання \"Праведника України\". У 2001 році у Львові був беатифікований Іваном Павлом ІІ. Восени 2008 року Синод УГКЦ проголосив священномученика Омеляна Ковча Покровителем душпастирів УГКЦ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.zaxid.net/</span></p>','Депутати Львівщини просять Президента визнати отця Ковча Героєм України','uk',1342,2316,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десятиліттями історія битви під Крутами або замовчувалася, або обростала міфами і вигадками, через що історики і досі не мають повної достовірної картини тих подій. Однак вони надихали поетів на творчість, яку, втім, радянська пропаганда приховувала. Так було із віршем Павла Тичини \"Пам\"яті тридцяти\".</span></span></p>','2008-01-29 19:59:46',NULL,'P','<p style=\"text-align: justify;\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"208\" align=\"right\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" bgcolor=\"#ffffff\"><img src=\"http://www.bbc.co.uk/f/t.gif\" border=\"0\" alt=\"\" width=\"5\" height=\"1\" /></td>\r\n<td><img src=\"http://www.bbc.co.uk/worldservice/images/2006/08/20060825105910kruty203.jpg\" alt=\"Церемонія під Крутами\" width=\"203\" height=\"152\" /></td>\r\n</tr>\r\n<tr>\r\n<td><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Дев\'яносто років тому 29 січня відбувся бій поблизу станції  Крути, що  на  Чернігівщині, за 130 кілометрів від Києва. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Проти 4-тисячної армії Михайла  Муравйова виступили малочисельні загони Української Центральної  Ради.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Серед захисників були і зовсім юні хлопці - п\'ятнадцяти та вісімнадцятирічні студенти та  гімназисти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бій під Крутами на сьогодні приховує ще чимало таємниць.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Водночас,  за  дев\'яносто років навколо цієї історичної події виникло і багато  легенд.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На  думку чернігівського історика Сергія  Лаєвського, битва під Крутами  була досить звичайною як на ті часи подією.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І  те, що проти армії Муравйова стояли лише недосвідчені, фактично  неозброєні юнаки - до певної міри - перебільшення.</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"208\" align=\"right\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" bgcolor=\"#ffffff\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.bbc.co.uk/f/t.gif\" border=\"0\" alt=\"\" width=\"5\" height=\"1\" /></span></span></td>\r\n<td><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.bbc.co.uk/worldservice/images/2007/02/20070202171307kruty203.jpg\" alt=\"Пам\'ятник загиблим\" width=\"203\" height=\"152\" /></span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До  загону Центральної Ради входили і колишні офіцери, і юнкера, і  старшини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Захисники  незалежної  України  мали  гвинтівки, кулемети, була  навіть  одна  гармата.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім  того, оборона  Крут  була  побудована  досить  грамотно.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За  словами Сергія Лаєвського, це було нормальне військо, але Муравйов  мав чисельну перевагу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бій був нетривкий,  але своє завдання загін Центральної Ради виконав - він  призупинив рух більшовицької армії у напрямку                   Києва.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головна ж трагедія сталася вже після того, як  основна  частина  захисників  відступила до столиці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розповідає  директор  Чернігівського  історичного  музею  Сергій  Лаєвський:<br />\"Невеличкий загін саме тих молодих гімназистів, який ніс чатування наприкінці цієї усієї оборони - він не встиг відступити разом із усіма. І вже почали наставати сутінки, і ті юнаки вже почали там блукати в тих полях, хуторах і побачили вогні на станції. Чи то був сигнал, про який домовлялися - що коли з\'являться вогні - усі збираються, але вони прийшли на станцію. Саме туди, де знаходилися частини Муравйова, розлючені, по-перше, тим супротивом, розлючені тими втратами, які вони понесли. Катували їх по-звірячому цих юнаків - їх примушували відрікатися від України, яку вони намагалися захищати\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли хлопці заспівали &bdquo;Ще  не  вмерла  Україна\", їх почали розстрілювати. А  потім добивали багнетами та ножами, розповідає                    історик.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закатованих юнаків потім поховали селяни і відразу начебто насипали  курган.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Через рік відбулося перепоховання героїв Крут на Аскольдовій могилі в Києві, у якому взяли участь члени Української Центральної Ради на чолі із Михайлом Грушевським.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як свідчать історики, тоді Грушевський сказав такі слова: \"Стримайте ж ваші сльози, які котяться. Ці юнаки поклали свої голови                   за визволення Вітчизни і Вітчизна збереже про них вдячну пам\"ять на віки вічні...\"</span></span></p>\r\n<p style=\"text-align: justify;\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"208\" align=\"right\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" bgcolor=\"#ffffff\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.bbc.co.uk/f/t.gif\" border=\"0\" alt=\"\" width=\"5\" height=\"1\" /></span></span></td>\r\n<td><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img src=\"http://www.bbc.co.uk/worldservice/images/2006/08/20060825152550kruty_military203.jpg\" alt=\"Пам\'ятник загиблим\" width=\"203\" height=\"152\" /></span></span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 січня у Києві широко відзначали події 1918 року. Перші особи держави  поклали квіти на Аскольдову могилу, вшанувати загиблих                   юнаків прийшли відомі в Україні люди.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Актор Богдан Бенюк каже про важливість цієї події для українців: \"Цей день є і радісний, і сумний одночасно. І цей день обов\"язково має бути в державному плані надзвичайно потужний. Бо відроджує гордість і патріотизм кожного, хто живе в Україні\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десятиліттями історія битви під Крутами або замовчувалася, або обростала міфами і вигадками, через що історики і досі не мають повної достовірної картини тих подій. Однак вони надихали поетів на творчість, яку, втім, радянська пропаганда приховувала. Так було із віршем Павла Тичини \"Пам\"яті тридцяти\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тривалий час про бій під Крутами нагадував лише курган та хрест на ньому, але кілька років тому неподалік побудували меморіальний комплекс.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За цей час пам\'ятник Героям Крут вандали кілька разів вже обливали  фарбою та писали на ньому проросійські гасла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Міліція так і не знайшла зловмисників.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.bbc.co.uk/</span></span></p>','Про легенди і таємниці довкола битви під Крутами','uk',1057,2031,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десятки тисяч прочан взяли участь у загальній аудієнції із Святішим Отцем Венедиктом&nbsp;XVI, яка у середу, 22 жовтня 2008 року, відбулась на площі перед базилікою святого апостола Петра у Ватикані.</span></span></p>','2008-10-22 22:31:32',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1531,200,foto_02.jpg\" alt=\"\" />Десятки тисяч прочан взяли участь у загальній аудієнції із Святішим Отцем Венедиктом&nbsp;XVI, яка у середу, 22 жовтня 2008 року, відбулась на площі перед базилікою святого апостола Петра у Ватикані.<br /><br />У своїй катехизі Папа знову звернувся до повчань святого апостола Павла, цим разом зупинившись на тому, що Апостол народів навчав про центральність Христа воскреслого в таїнстві спасіння. Святий Павло не турбувався тим, щоб розповісти окремі факти з життя Ісуса, але звіщав першим християнам Христа розп\'ятого і прославленого, живого і присутнього у Церкві. Він звіщав воплочення і прославлення Христа, але також і Його предвічне існування з Отцем перед усіма віками. Щоб це зрозуміти, треба брати до уваги Старий Завіт, який говорить про Божу Премудрість, що була з Ним і зійшла до людей. У 90 псалмі читаємо: &laquo;Перш, ніж постали гори і народилася земля, і всесвіт, від віку й до віку ти єси Бог&raquo; (Пс 90,2).<br /><br /><img style=\"float: left;\" src=\"image,1531,200,foto_10.jpg\" alt=\"\" />Святий Павло представляв Христа як &laquo;Божу Премудрість&raquo;, осередок і сповнення відвічного задуму спасіння людей. У Посланні до Филип\'ян він протиставляє відвічне існування Христа в &laquo;образі Божім&raquo; і Його наступне пониження &laquo;аж до смерті, смерти ж - хресної&raquo; (пор. Фил 2, 2-11). <br /><br />Святий Павло звіщає Христа &laquo;єдиним посередником між Богом і людиною&raquo;, &laquo;первородним усякого створіння, бо в ньому все було створене... Він раніш усього і все існує в ньому. Він також голова тіла, тобто Церкви&raquo; (див. Кол 1,15-20). <br /><br />Апостол народів, - наголосив Папа Венедикт&nbsp;XVI, - закликає нас прийняти спасіння, яке приніс розп\'ятий і воскреслий Господь, Предвічний Син, істинна Премудрість і сила Божа.</span></span></p>','На загальній аудієнції з Папою Венедиктом  XVI (22 жовтня 2008 р.)','uk',551,1531,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для вісімнадцяти студентів сьомого курсу, що майже всі вже є дияконами, це була прощальна літургія, адже вони закінчили своє навчання. Більшість поки що залишаться виконувати своє служіння в Україні, а деякі невдовзі від\'їдуть на служіння або навчання закордон. Двоє співбратів, які навчальний шлях починали разом із теперішніми випускниками, отримали ієрейські свячення ще влітку та від\'їхали на навчання до Варшави.</span></span></p>','2009-01-02 12:02:22',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30 грудня 2008&nbsp;р., на закінчення зимової сесії, у каплиці Василіянського інституту філософсько-богословських студій у Брюховичах була відслужена подячна Архиєрейська Свята Літургія, яку очолив владика Софрон Мудрий,&nbsp;ЧСВВ, вислужений єпарх Івано-Франківський. Для вісімнадцяти студентів сьомого курсу, що майже всі вже є дияконами, це була прощальна літургія, адже вони закінчили своє навчання. Більшість поки що залишаться виконувати своє служіння в Україні, а деякі невдовзі від\'їдуть на служіння або навчання закордон. Двоє співбратів, які навчальний шлях починали разом із теперішніми випускниками, отримали ієрейські свячення ще влітку та від\'їхали на навчання до Варшави.<br /><br />Під час богослужіння випускників привітав владика Софрон, побажавши їм бути відважними, справжніми місіонерами Божого Слова на шляху до вічності. Коротким словом до вітань прилучився й о.&nbsp;Роберт Лисейко, ЧСВВ, Ректор інституту, вручивши кожному випускникові Компендіум Катехизму Католицької Церкви, щоби, за його словами, безпомилково навчали вірних Божих правд.<br /><br />Після скромного сніданку випускники-монахи мали зустріч з о.&nbsp;Протоігуменом Йоанікієм Чверенчуком, ЧСВВ. Він дав учорашнім студентам ряд практичних вказівок на подальше життя у Василіянському Чині Святого Йосафата. Пригадав, що їх попереду чекає духовний досвід, та те, що час їхньої людської формації не закінчився, але триватиме й надалі. О.&nbsp;Йоанікій зазначив, що раніше вони під час навчання здавали різноманітні іспити, проте тепер матимуть важливий іспит перед самим Богом.<br /><br />Для Радіо Ватикану, диякон Миколай МИКОСОВСЬКИЙ, ЧСВВ, Львів</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span><strong><em><br /></em></strong></p>','Перший випуск з Василіянського інституту філософсько-богословських студій у Брюховичах','uk',921,1895,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дні зустрічі були збагачені хвилинами молитви та богослужіннями з місцевими громадами: у п\'ятницю, 10 липня, в латинській катедрі Успіння та в неділю, 12 липня, в греко-католицькому соборі св. Юра. Користуючись нагодою бути разом у свято св. Венедикта, покровителя Європи, секретарі молилися за Європу та підписали і вислали лист подяки Папі Венедикту XVI за його енцикліку та привітання з його іменинами. Вкінці, у неділю, 12 липня, в післяобідній час секретарі зустрілися з Міським головою Львова.</span></span></p>','2009-07-15 21:18:52',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З 9 по 13 липня 2009 року Генеральні секретарі Єпископських Конференцій Європи зустрілися в Реколекційно-відпочинковому центрі Львівської архиєпархії УГКЦ у Львові-Брюховичах. Були представлені 27 країн: Албанія, Австрія, Білорусь, Хорватія, Франція, Німеччина, Англія та Уельс, Ірландія, Італія, Литва, Люксембург, Мальта, Нідерланди, Польща, Португалія, Чеська Республіка, Румунія, Росія, Міжнародна Єпископська Конференція Свв. Кирила та Методія (Сербія, Македонія та Чорногорія), Шотландія, Словенія, Іспанія, Швейцарія, Туреччина, Угорщина, Україна (УГКЦ та РКЦ). Цей щорічний з\'їзд відбувся за сприяння Ради Єпископських Конференцій Європи (РЄКЄ) та на спільне запрошення Синоду Єпископів Української Греко-Католицької Церкви та Єпископської Конференції Римо-Католицької Церкви в Україні. Також був присутній на зустрічі Архиєпископ Іван Юркович, Апостольський Нунцій в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Католицька Церква в Україні</em></strong><br /> Зустріч розпочалася презентацією Католицької Церкви в Україні. Єпископська колегіальність є представлена тут двома інституціями, обоє з яких є членами <em>ССЕЕ</em>: Синодом Єпископів Української Греко-Католицької Церкви та Єпископською Конференцією Римо-Католицької Церкви в Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Традиція приписує апостолові Андрієві першу проповідь для племен, які жили на території сучасної України. Знаним є те, що в ІХ столітті свв. Кирило і Методій проповідували Євангелію в Кримському регіоні. Возз\'єднання Візантійської Церкви з Римською Католицькою Церквою датується 1596 роком, що є знаним як Берестейська унія. Через свідчення найвищих представників цих двох інституцій, Блаженнішого Любомира (УГКЦ) та Митрополита Архиєпископа Мєчислава Мокшицького (РКЦ в Україні), Генеральним секретарям було представлено багато розпочатих заходів, які є свідченням відродження християнства та особливо Католицької Церкви в Україні. Це сталося після років переслідування комуністичним режимом, під час яких на заслання вивозили тисячі священиків, монахів, монахинь і мирян. Багато з них мучеництвом засвідчили вірність своїй Церкві. Всупереч всім намаганням заморити голодом, репресувати та закрити структури Греко-Католицької Церкви, віра українського народу вижила. Сьогодні, коли Церква активно відбудовується (зводяться нові храми та інші церковні будівлі, так як конфіскована власність ще повністю не є поверненою), нові виклики видніються на горизонті, особливо ті, які відносяться до міграційного феномену, і тому вони потребують нової відповіді - відповіді, яка є в синергії з іншими Католицькими Церквами на континенті.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Священик: його життя та місія<br /> </em></strong>Генеральні секретарі радісно сприйняли проголошення Папою Венедиктом XVI Священичого Року, як привілейовану можливість для Церкви зосередитися в особливий спосіб над покликанням, життям, духовністю та служінням священиків. Учасники зробили свій внесок до цих важливих аспектів священичого служіння, наголосивши на особливостях та проблемах, з якими стикається священик сьогодення.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після короткої презентації діяльності Європейської служби покликань (<em>European Vocations Service</em> (<em>EVS</em>)), Комісією &laquo;Покликань&raquo; ССЕЕ, за посередництвом о. д-ра Ференца Янки, Заступника генерального секретаря ССЕЕ, було наголошено на потребі роздумати над покликанням священика на рівні Європи. &laquo;Є необхідним, - зауважив о. Янка, - щоб священики передавали Слово, яке відкрито закликає до священства зі сміливістю&raquo;. Для о. Мануеля Моружау, Генерального секретаря Португальської Єпископської Конференції, &laquo;священик є іменником, а не прикметником&raquo; і &laquo;визначення священика не є функцією чи завданням, яке він виконує&raquo;. Це священство не є &laquo;особистим самоуправлінням&raquo; або роботою, професією чи ремеслом, але покликанням та місією, які походять від Христа через Його Церкву. Священик є &laquo;людиною, яка спеціалізується в Бозі&raquo;, щоб служити людям 24 години на добу. Він експерт в мистецтві зустрічі з Богом. Саме тому він є покликаним бути автентичним шукачем Бога та користати з Євхаристії як свого найвищого ступеня самореалізації. На думку о. Пола Конроя, Генерального секретаря Шотландської Єпископської Конференції, ми часто говоримо про кризу покликань щодо священства та віри, проте ми також повинні пам\'ятати, що криза існує у всьому західному суспільстві. І можливо саме ця криза має найбільший вплив на інших. Отець Конрой продовжує, кажучи, що спад покликань до священства бере свій початок від кризи в родинах, які вже не є місцем, де покликання ростуть та дозрівають. Можливо відповідь на цю проблему потрібно шукати в &laquo;життєвому свідченні самих священиків&raquo;. Отець Вєкослав Гузяк, Генеральний секретар Хорватської Єпископської Конференції, зазначив, що священиче сопричастя черпає своє життя та приклад із Пресвятої Трійці. Подібно до відношення в Пресвятій Трійці, священик є перш за все покликаний мати відношення в сопричасті зі своїм єпископом, зі своїми співбратами у священстві і на кінець зі спільнотою. Священик бо є &laquo;співпрацівником єпископа, а не службовцем&raquo;. В часі, коли наше суспільство виглядає розділеним та не виявляє багатьох об\'єднуючих чинників, цінностей та свідчення, є спокуса думати, що також &laquo;намагання до сопричастя, можливо, здається не на своєму місці, так як вони не може бути виміряне кількісними результатами&raquo;. Ось чому, згідно зі словами о. Гузяка, одним з головних завдань священика є бути &laquo;творцем єдності&raquo; серед довірених йому спільнот.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Caritas in veritate<br /> </em></strong>Отцеві єзуїту Гансу Лангендорферу, Генеральному секретареві Єпископської Конференції Німеччини, було доручено представити нову енцикліку Папи Венедикта XVI &laquo;Любов у правді&raquo;. Отець єзуїт висвітлив три пріоритети, що знаходяться в новому документі: центральне місце людини, суспільний вимір економіки і тема глобалізації та взаємозалежність поліцентрів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже важливим, хоч і не новим у суспільному вченні Церкви є те, що Святіший Отець хотів ще раз підтвердити в цьому документі центральне місце людини, оскільки теперішнє суспільно-економічно-культурне середовище в останні роки радикально змінилося. &laquo;У світі економіки та політики охоче говориться про техніку, про програму, про те, як врятувати банки чи розвивати деякі особливі галузі економіки, - сказав о. Лангендорфер. - І хоча всі ці питання є важливими, не завжди ясно, яке є основне питання: чому все це?&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Для Святішого Отця відповідь чітка: центральною орієнтацією повинен бути пошук спільного добра та цілісний розвиток людської особи. Економіка та ринок потребують етики для свого правильного функціонування, етики, близької до (людської) особи. В цьому сенсі християнська релігія може справді зробити свій внесок в розвиток народів, за умови, що Бог матиме місце також і в громадській сфері.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Суспільний вимір економіки, який Папа описує - це вимір, що не терпить винятків і похибок. Кожна людина має право брати участь у світі ринку і праці. І це особливо важливо в цей час, в якому ми стаємо свідками переоцінювання &laquo;державної влади&raquo; (24). Тому необхідно мати державу відкриту для більшої участі громадянського суспільства. Передусім профспілок, підкреслює о. Лангендорфер, особливо для охорони систем соціального та професійного захисту, а також інших суспільних інстанцій, як Церква. З цією економічно-фінансовою кризою, ми зрозуміли, як ринок не може розраховувати лише на себе, але &laquo;мусить зачерпнути моральну енергію від інший суб\'єктів&raquo;, тому що суспільне питання економіки, нерегульованого розвитку техніки, стає також антропологічним питанням. Сам розвиток народів не може бути відокремлений від поваги до життя, релігійної свободи, захисту прав працівника...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Третій пріоритет, висвітлений о. Лангендорфером, - це розвиток глобалізованого та поліцентричного світу. &laquo;Все більше глобалізоване суспільство робить нас близькими, - стверджує Папа, - але не робить нас братами&raquo;. Енцикліка підкреслює, наскільки необхідно переосмислити розвиток у глобальних та взаємозалежних термінах. Найперше, економічна реконструкція не повинна стосуватися лише країн з розвинутою промисловістю; європейська економіка не може не звертати увагу на Африку чи Азію. Тим більше, глобалізація повинна стати інструментом інтеграції бідних країн. Отже, глобалізації потрібна орієнтація, звернена на особу та на спільноту і відкрита до трансценденції.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У дискусії, що відбулася після презентації, секретарі висловили свою високу оцінку цьому документові, охарактеризувавши його &laquo;Енциклікою суспільного богослов\'я&raquo; та за ясність, з якою Святіший Отець зумів визначити невідкладні питання сьогодення і в той же час ще раз підкреслити основну думку суспільного вчення Церкви: центральне місце людини. Він це робить вільно і є відкритим до поглиблення. Папа не висловлює поспішних суджень, але проводить справжнє і властиве осмислення небезпек і ризиків безконтрольного розвитку економіки. Потрібно пам\'ятати, що ця енцикліка не звертається виключно до промислово розвинутих країн, а звернена до всіх людей і всіх народів. І енцикліка не є збіркою технічних вирішень, а поясненням &laquo;місії істини, яку треба сповнити&raquo; (9). Секретарі оцінюють цей документ, як спробу Церкви організувати власну думку і відкрити дискусію на тему суспільного розвитку, яка в кінцевому рахунку є темою суспільної любові. З цією енциклікою, оптимістичною і в той же час реалістичною, Папа мав відвагу закликати до змін у способі думання про людину. Тепер енцикліка поручена всім християнам і особливо єпископським конференціям в Європі та світі, щоб вони розпочали процес осмислення на місцевому рівні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Актуальні теми: евтаназія, церква і засоби масової інформації, Світовий день молоді 2001<br /> </em></strong>У Європі поширюються феномени, які через антропологічну модель, яка є далекою від культури життя і природної моделі сім\'ї ранять гідність людської особи та хвилюють Церкву. Учасники були поінформовані головно про &laquo;справу Елуани Енгларо&raquo;, що призвела до інтенсивної дискусії стосовно евтаназії. Інша справа, подана до уваги секретарів - це справа англійських католицьких сиротинців, змушених вести процес усиновлення також для гомосексуальних пар. Їхня відмова призвела до закриття їхніх агентств.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ця зустріч була також нагодою, щоб обговорити стосунки Церкви і засобів масової інформації. Церква все більше відчуває необхідність дбати про стосунки з засобами масової інформації зі свідомістю, що треба зростати у професійності в, часом, надзвичайно неприхильному середовищі. Вкінці, учасники були поінформовані про приготування до наступного Світового Дня молоді у Мадриді (2011 р.).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong><em>Служба CCEE та COMECE<br /> </em></strong>Генеральні секретарі єпископських конференцій Європи були поінформовані про теперішню діяльність CCEE та їхніх окремих комісій та про їхню діяльність на 2010 рік. Зі свого боку Генеральний секретар COMECE о. Пйотр Мазуркевич прокоментував результати останніх голосувань до європейського парламенту.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Церква в Європі підтримує і слідкує з увагою за процесом об\'єднання Європи, яке несе з собою розвиток і мир, але яке має перед собою важливі питання стосовно певних вартостей, які представники Євросоюзу через європейське законодавство досі старалися поширювати в деяких країнах, особливо стосовно виховання, ролі релігії, життя та сім\'ї, які є ризиком для самого бачення людської особи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дні зустрічі були збагачені хвилинами молитви та богослужіннями з місцевими громадами: у п\'ятницю, 10 липня, в латинській катедрі Успіння та в неділю, 12 липня, в греко-католицькому соборі св. Юра. Користуючись нагодою бути разом у свято св. Венедикта, покровителя Європи, секретарі молилися за Європу та підписали і вислали лист подяки Папі Венедикту XVI за його енцикліку та привітання з його іменинами. Вкінці, у неділю, 12 липня, в післяобідній час секретарі зустрілися з Міським головою Львова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зустріч 2010 року відбудеться в Римі з 10 до 13 червня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прес-служба Ради Єпископських Конференцій Європи. За матеріалами: http://ugcc.org.ua/</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','Офіційний прес-реліз про 37-ий З’їзд Генеральних секретарів Єпископських Конференцій Європи','uk',1679,3162,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До вас простягають свої спраглі долоні діти, позбавлені батьківської опіки, з різних куточків України. Наповніть їх вашим милосердям!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Він обов\'язково прийде. Він обов\'язково встигне відвідати всіх на земній кулі. Він не залишить жодного дитячого серця битись із своєю мрією наодинці.  Бо на те Він - Святий Миколай. Бо на те ми з вами колись були дітьми, щоб сьогодні стати Його помічниками. Ми маємо усі задатки для того, щоби стати добрими ангелами, які допомагають роздавати подарунки у чарівну ніч на Святого Миколая. </span></span><strong><br /></strong></p>','2008-12-18 21:50:56',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Він обов\'язково прийде. Він обов\'язково встигне відвідати всіх на земній кулі. Він не залишить жодного дитячого серця битись із своєю мрією наодинці. Бо на те Він - Святий Миколай. Бо на те ми з вами колись були дітьми, щоб сьогодні стати Його помічниками. Ми маємо усі задатки для того, щоби стати добрими ангелами, які допомагають роздавати подарунки у чарівну ніч на Святого Миколая.<br /><br />Відповідальність за те, чи відвідає Святий кожного вбогого і забутого, хворого і розчарованого, лежить на кожному з нас. В Україні є багато дітей, до яких не просто ніколи не приходив Святий Миколай, а які навіть про Нього не знають. Вони ніколи не бували у церкві, їм ані бабуся, ані батьки не казали бути чемними, щоб заслужити подарунок від Доброго Дідуся і почути дзвінкий голос білосніжних ангелів. Але вони - така ж кирпатоноса малеча, спрагла любові та тепла, надії та віри в казку.<br /><br />Голосом срібних дзвіночків хай задзвенить совість кожного з нас, щоби розбудити в собі оте малятко, яке, не дочекавшись ввечері Святого, встає зранку і усміхається, радіючи святковому пакунку. Подаруйте цю усмішку ще одній дитині, бо вона надіється саме на Вас!<br /><br /><strong>До вас простягають свої спраглі долоні діти, позбавлені батьківської опіки, з різних куточків України. Наповніть їх вашим милосердям!</strong><br /><br /><img style=\"float: right;\" title=\"Святий Миколай мандрує Україною\" src=\"http://sn.org.ua/img/content/main/children.jpg\" alt=\"Святий Миколай мандрує Україною\" width=\"406\" height=\"216\" /><br /><br /><strong>До участі в акції запрошуються всі бажаючі - студенти, представники громадських організацій, бізнесмени, священики різних християнських конфесій, журналісти.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://sn.org.ua/<strong><br /></strong></span></span></p>','Акція „Святий Миколай мандрує  Україною“','uk',817,1797,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доброго Вам здоров&rsquo;я і всій Вашій великій родині. Я отримала від Вас листа, за якого Вам дуже вдячна, а ще більше я дякую і молюся Богу, що не покидає нас у важкі хвилини нашого життя.<br />Також велике дякую Вам, що є такі люди Божі на землі на світі, які допомагають нужденним і дітям-сиротам. Дай Бог Вам усім міцного здоров&rsquo;ячка, мира і благодаті.</span></span></p>','2012-01-21 15:24:21',NULL,'P','<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Лист ув\'язненої Ірини В., Качанівська виправна колонія КВК-54, м.Харків<br />&ldquo;Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доброго Вам здоров&rsquo;я і всій Вашій великій родині. Я отримала від Вас листа, за якого Вам дуже вдячна, а ще більше я дякую і молюся Богу, що не покидає нас у важкі хвилини нашого життя. Я дуже вам вдячна, що Ви допоможете моєму синочку. Я народила його 20 січня. Це на велике свято 19 Водохрещення, хлопчик народився дуже слабким і ніхто із лікарів не давали мені надію на те, що дитина залишиться здоровою і живою. У нього була пневмонія (вода в легенях), дитя плакало і плакало без кінця. Я також дуже плакала. Тому, що я дуже хотіла цю дитиночку. До мене підходили лікарі і кожен з них висловлював свою думку по-різному. Одні питали, чому ви плачете, адже у вас діти є. Другі прямо так і казали, що краще хай іде куди направився, аніж вам по лікарнях з ним їздити. А я, як мати, все одно молилася і плакала і просила у Бога здоров&rsquo;ячка для моєї дитиночки. І тут одного разу до мене підійшла чи то медсестра, чи лікар і каже: &laquo;Ми як лікарі будемо все робити із своєї сторони, а ви я бачу жінка віруюча просіть і моліть Бога за свого хлопчика&raquo;. Я попросила принести Йорданської свяченої водички із церкви і покропила всі Іванкові пеленочки і одежку просила і до Бога говорила: &laquo;Боже, Тебе хрестив в ріці Йордані Іван, прошу Тебе похрести і Ти мого Івана&raquo;. І дала йому ім&rsquo;я Іванко. Моя дитинка росла і жила зі мною ні разу не хворівши і я дякую за це нашому Господу Богу. Але сталося таке жахливе горе, що я опинилася в тюрмі. Були страшні хвилини відчаю, тоді я завжди зверталася до Бога, я бачила перед собою розп&rsquo;ятого Христа і мені так ставало стидно, що Ісус переніс всі найтяжчі муки і так страждав за мене, а я ще чогось ною. Знаю, що Господь Бог мене любить і я не одна.<br />Також велике дякую Вам, що є такі люди Божі на землі на світі, які допомагають нужденним і дітям-сиротам. Дай Бог Вам усім міцного здоров&rsquo;ячка, мира і благодаті.<br />З повагою до Вас, Ірина&rdquo;</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /><br />Лист ув\'язненої Любові С., Збаразька виправна колонія ЗВК-63,Тернопільська обл.<br />Слава Ісусу Христу! </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br />&nbsp;&nbsp; &nbsp;Я одержала Вашого листа і була дуже рада&hellip; Велике Вам дякую!<br />&nbsp;&nbsp; &nbsp;Можливо я так гарно не напишу цей лист до Вас тому що пишу подібне вперше, але буду старатись. Просите написати про себе? Добре.<br />&nbsp;&nbsp; &nbsp;Сама я родом з Волинської області, мені 55 років, засуджена по ст..115, стр.10 р. В мене є дорослий син Віктор 1974 р.народження. Був в шлюбі до 2010 р., тобто 15 років, має троє діточок, синові 14, донці 12 і ще одна донька 1р.6 міс. Але вийшло так що після народження третьої дитини через 10 міс. Померла його дружина Татяна. Тепер мій син проживає з дітьми, велике горе, але нічого не вдієш на все воля Божа.<br />&nbsp;&nbsp; &nbsp;А я звільнюсь через 6 років, якщо дасть Бог здоровя. Вас мабуть зацікавить про що говорить ст.115 КК України? Зараз коротко розповім. Це вбивство. Вийшло ось як. Я познайомилась з чоловіком звали Віктором, сам з Запоріжської обл., працював електрозварником у нас на Волині. Він мені показався розумним, начитаним, роботящим, бо так воно і було з самого початку.Вирішили жити разом і переїхали в Запоріжську обл. Ось тут все і почалося. Випивки, які на нього діяли негативно, ставав наглим, агресивним, не розумів що робить, а пізніше не пам&rsquo;ятав що наробив коли просипався і бачив синці на моєму обличчі і в хаті все перевернуто вверх ногами. Просив пробачення, але дарма, усе було до першої рюмки і повторявся &laquo;ад крошешний&raquo;. Незнаю чому я йому пробачала, мабуть тому що хотіла жити, вірила що поміняється він сам цього хотів, був не рад сам собі коли вранці бачив наслідки нічної &laquo;аварії&raquo;. Йшов час і дійшло до того, що нормальна людина подібного не може зробити, він переступив рамки розумного. Невернувшись з роботи вчасно а прийшов в 3 години ночі і не один а з жінкою п&rsquo;яною як і він, обоє в соломі у неї волосся довге і все в соломі, я побачила і очам не повірила думала що сниться, але ні. Віктор говорить дай десь людині поспать, я промовчала, а їй сказала іди додому поки я добра. Вона тихенько повернулась іти, а він схопив мене бити як це так я її вигоняю серед ночі. Вона все таки пішла як побачила все що він витворяє, а Віктор устроїв таку &laquo;бойню&raquo; що я уже не знала що мені робити, вийти з будинку не могла &ndash; не випускав. Побачила на столі ніж взяла в руку і хотіла так просто махнути в його бік щоб він відійшов від дверей і я змогла вийти надвір. Але вийшло так що махнула неудачно і попала в легеню, поки визвала швидку, поки приїхала міліція і лікарі, то получився крововилив в брючну порожнину &ndash; помер. Осудили мене на 10 р. мотивуючи тим що потрібно було раніше писати на нього заяву за його нелюдську поведінку і він був би наказан, а я получилось влаштувала самосуд, за що тепер відповідаю. А тепер я зробила висновок для себе6 неможна вірити, жаліти, тому що повертається доброта злом і зрадою. Я розумію що не мала права цього робити і я страшенно грішна, і прошу в Бога прощення гріхів моїх. Читаю молитву кожного дня в вільну хвилину, ходжу в церкву і молюся. Дуже багато чого з релігійної літератури не розумію читаючи. З&rsquo;являються якісь сумніви і т.д.<br />&nbsp;&nbsp; &nbsp;Але прошу Господа щоб допоміг мені вникнути в Слово Боже і прошу прийти до мене в душу повністю щоб я не сумнівалася, а твердо вірила всією душею і всім серцем. Ось наприклад нам приніс отець Стіфаній таку іконку на папері лік Ісуса Христа де з другого боку написано що в Оренбургській обл. в однієї жінки 68 років була ікона Лік Господа Ісуса Христа&nbsp; і 5 травня 1999 р. цей образ став благоухати і сильно миро точить, а 5 квітня 2000 р. на образі почали появлятися п&rsquo;ятна жидкості похожої на кров. І в результаті біохімічна експертиза підтвердила що це людська кров, а гематологи встановили IV&nbsp; групу що співпало з даними експертизи знайдено на Турійській плащаниці кровопідтьоками в яку був облачьон (не знаю по-українськи це слово. вибачте) наш Господь Ісус Христос. До чого веду?<br />З повагою, Лариса</span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /><br />Лист ув\'язненої Лариси Я., Збаразька виправна колонія ЗВК-63,Тернопільська обл.<br />Слава Ісусу Христу! </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br />Вітаю Вас, пані З.! Насамперед хочу подякувати Вас, шановна пані З., за Вашу небайдужість до скрути іншої людини. Дякую отця Ігоря за те, що він переймається за таких, як я. Та що можуть люди, коли вони не знають Бога. Правда ж? Якби Бог не хотів, щоб мені хтось допоміг, то й людям до цього не було б ніякого діла. Я мабуть не зовсім чітко формулюю свою думку, але якщо коротко про те, що я хочу сказати &ndash; на все воля Бога. Знаєте. Пані З., тут у тюрмі багато жінок дорікають на те, що тут важко, що потрапили сюди ні за що, що ми такі &laquo;бідні&raquo; і т.д. Воно звичайно так &ndash; і важко, і погано. Але головний &laquo;плюс&raquo; я для себе бачу в тому, що тут я пізнала Бога. Навіть страшно стає, коли починаю задумуватися над тим, як я жила, скільки знайомих вже повмирали від наркотиків, а скільки ще &ndash; повільно і з великими муками вмирають від ВІЛ-інфекції, СПІДУ, туберкульозу. Але ж для чогось то мене Господь уберіг від всіх цих хвороб? Щоб я жила і славила Його. Мені 46 років (у січні буде 47), але тільки останні 2 роки, ну трохи більше, я пізнала Бога, нарешті зрозуміла, що не так жила. Жила лише для себе, турбувалася лише про себе. Та й то, як сказати. Життя з наркотиками &ndash; то смерть. Але коли кололася, якось не дуже замислювалася, як жити далі! Будинок продали з співмешканцем, збиралися жити у його матері. Та трапилося так, що співмешканець помер, помер від наркотиків фактично (гепатит С, сепсис) хоча і був на 5 років молодший за мене. Отак я і залашалася без житла, бо мати померлого сказала: на мене не розраховуй. Стала жити то у одних знайомих, то у інших. Страшна круговерть. Крадеш, щоб &laquo;уколотися&raquo;, колешся, щоб украсти. Мама, тато, брат, вже всі відійшли до Господа у вічність. Донька, їй 26 років, зараз у Італії та в мене немає з нею зв&rsquo;язку. Є лише двоюрідна сестра, працює зав.дитячим садком у м.Павлоград, звідки і я родом. Та вона не спілкується зі мною. Я не нарікаю на долю, я розумію, що пожинаю плід того, що сама посіяла. Посіяла зло і отримала байдужість навіть від своїх рідних та близьких.<br />&nbsp;&nbsp; &nbsp;Вірю, що Бог многомилостивий, милосердний, люблячий, суддя справедливий і уповаю на нього... Я дякую Вам, пані З., за Ваше добре серце. Нехай бог Вас благословить.<br />З повагою, Лариса</span></span></p>','Листи з місць позбавлення волі','uk',2649,4132,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Добігає до завершення Синод Єпископів Католицької Церкви, присвячений Божому Слову, який від 5 жовтня триває у Ватикані. 21 жовтня, під час 20-го пленарного засідання, у присутності Папи Венедикта&nbsp;XVI, переставлено зведений перелік кінцевих пропозицій, остаточне голосування яких відбудеться у суботу, 25 жовтня.</span></span></p>','2008-10-23 16:24:40',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Добігає до завершення Синод Єпископів Католицької Церкви, присвячений Божому Слову, який від 5 жовтня триває у Ватикані. 21 жовтня, під час 20-го пленарного засідання, у присутності Папи Венедикта&nbsp;XVI, переставлено зведений перелік кінцевих пропозицій, остаточне голосування яких відбудеться у суботу, 25 жовтня. Йдеться про 53 пропозиції, що стосуються належної оцінки значення Догматичної Конституції ІІ&nbsp;Ватиканського Собору &laquo;Dei Verbum&raquo; (Слово Боже), кращого усвідомлення ролі Божого Слова, його спасенної сили, зміцнення Церквою її місіонерського покликання. Цими днями триває праця в малих групах для остаточного опрацювання і підготовки документу до суботнього голосування.<br /><br />Пригадаємо, що Синод Єпископів є дорадчим органом при Святішому Отцеві. На основі його пропозицій Вселенський Архиєрей видає документи, які є зобов\'язуючими для Церкви.</span></span></p>','Синод Єпископів у Ватикані закінчує свою роботу','uk',562,1542,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога  надавалась продуктами, солодощами, гігієнічними засобами, речами  особистого вжитку, що надійшли від жертводавців. На прохання ув\'язненої&nbsp;  Раїси О. їй вислали полотно та нитки для вишивання, Олені К., яка має  здібності до малювання і присилає свої роботи, вислали набір кольорових  олівців та папір для малювання.</span></span></p>','2011-07-11 16:43:57',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коштом жертводавців, значною мірою п.Лесі з родини О.Тихого, зібрані та відправлені посилки з матеріальною допомогою жінкам, які відбувають покарання у Чернігівській та Чорноморській виправних колоніях та долучаються до благодійної діяльності вишиванням та малюванням.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. На прохання ув\'язненої&nbsp; Раїси О. їй вислали полотно та нитки для вишивання, Олені К., яка має здібності до малювання і присилає свої роботи, вислали набір кольорових олівців та папір для малювання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтера п.Марти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Посилки доставлені до поштового відділення п.Мартою особисто.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коротка інформація про тих, кому надавалась допомога:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Допомога&nbsp; ув\'язненій&nbsp; Олені К.&nbsp; (Чернігівська ВК)&nbsp; на суму&nbsp; 166 грн. 06 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 08.03.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 36 лет, в местах лишения свободы второй раз и надолго. Здесь я уже 4 года и 2 месяца, а ещё отбывать наказание 4 года, конец срока в 2015 г.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Воспитывали меня дедуля с бабулей. В шестом году умерли, Царство им небесное, а в седьмом году я попала в эти места. Если можно, помолитесь пожалуйста за меня, мне очень часто стала бабуля сниться и я просыпаюсь в холодном потуи потом очень плохо мне, душевное состояние не пойму какое. Да и рисовать начала от того, что на душе камень, тяжело, а когда рисую, мне легко, почти как молюсь.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Здесь, на лагере, столько много ненависти, лжи, зависти, что просто жуть. Но жуть для тех, кто без Бога, я стараюсь каждый день читать Библию,слово Божье мне придаёт сил, терпения, старания, веру в счастливое будущее.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Детей у меня нет. Здесь работаю поваром в Доме ребёнка, готовлю деткам от 1-го года до 3-х и от рождения до года. Помолитесь пожалуйста за всех деток, находящихся здесь.&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Вы пишете, чтоб попробовала осуждённых женщин рисовать, я пробовала, не получается. Высылаю Вам ещё один рисунок, рисую не карандашами, а тенями для век. Карандаши, что Вы выслали, их в отряде держать нельзя, это запрет, можно нарушение заработать.&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прислала два жіночі портрети та один дитячий власної роботи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали18.04.2011 р. на суму 81 грн.51 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 247 грн.57 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>2. Допомога ув\'язненій Раїсі О.&nbsp; (Чорноморська ВК)&nbsp; на&nbsp; суму&nbsp; 172 грн. 01 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 18.04.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Термін &mdash; 11 років, в ув\'язненні від початку&nbsp; 2005 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Від першого одруження було двоє діток. Доня Оленка померла в 2005 р. у 6-річному віці. У 2003 р. Раїса втратила сина Діму від другого одруження у 3-річному віці. Син Олександр (15.10.1997 р. н.), залишився під опікою батьків Раїси в селі Торчин у Винницькій області, де вона жила до ув\'язнення. &ldquo;У сім\'ї нас багато, 6 братів і 6 сестер, тато і мама мають 26 внуків, серед них я була одна неслухняна&rdquo;. Батьки дуже хворі: у батька грижа, необхідна операція, але нема коштів; мама майже лежача. В серпні 2010 р. сина взяла під свою опіку свекруха Раїси. &ldquo;Свекруха пише мені лист, щоб я за сина не переживала, а молилась за батьків... Я Вас дуже прошу, умоляю, моліться за моїх батьків, щоб їх ще застати живими&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 07.05.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїсі О. &mdash;&nbsp; 20.07.2009 р. на суму 40 грн.06 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 22.09.2009 р. на суму 65 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.10.2010 р. на суму 42 грн. 03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 18.02.2011 р. на суму 55 грн. 94 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.03.2011 р. на суму 75 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру &mdash;&nbsp;&nbsp; 30.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 03.12.2009 р. на суму 70 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.12.2010 р. на суму 73 грн.03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 618 грн.07 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В підсумку надано матеріальну допомогу поштовими відправленнями від 22.06. 2011 р. на суму <strong>338 грн. 07 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, вартість:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; 34 грн.38 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вкладених поштових конвертів &mdash; 3 грн.00 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від 22.06. 2011 р. &mdash; 375 грн.45 коп.</strong></span></span></p>','ЗВІТ №12 про надання матеріальної допомоги жінкам, які відбувають покарання в місцях позбавлення волі (22.06.2011 р.)','uk',2529,4012,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. Крім того діти отримали в подарунок дитячі книжечки, іграшки, ігри дитячі, пластилін, кольорові олівці. Кожен знайшов у пакуночку духовну літературу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Силами волонтерів з подарунків спаковано і відправлено адресатам 20 посилок.</span></span></p>','2012-02-21 19:48:06',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp; В рамках проекту &laquo;Різдвяний подарунок&raquo; парафіянами церкви св.Миколая на Аскольдовій Могилі зібрані подарунки дітям ув&rsquo;язнених жінок, залишеним під опікою держави, родичів або призначених державою опікунів, &nbsp;жінкам, які відбувають покарання в місцях позбавлення волі разом з маленькими діточками (до 3-річного віку), а також жінкам, які долучаються до благодійної діяльності своєю творчою роботою.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. Крім того діти отримали в подарунок дитячі книжечки, іграшки, ігри дитячі, пластилін, кольорові олівці. Кожен знайшов у пакуночку духовну літературу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Силами волонтерів з подарунків спаковано і відправлено адресатам 20 посилок.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Посилки доставлені до поштового відділення настоятелем церкви св.Миколая о.Ігорем Онишкевичем.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Кому були адресовані &laquo;Різдвяні подарунки&raquo;:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (син Володимир, 5 р.) ув\'язненої &nbsp;Тетяни Г. (Чернігівська ВК)&nbsp; в м.Молодогвардійськ Луганської обл.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; грудень 2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині&nbsp; (син Євген, 10 р.) ув\'язненої Алли Б. (Чернігівська ВК) в м.Марганець Дніпропетровської обл.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;... Я уже изолирована от общества почти шесть лет. У меня одиннадцать лет лишения свободы. Но я не жалею о том, что я сейчас там, где я есть. В нашей жизни нет случайностей...без всего этого я не узнала бы никогда о том сокровище, которое закрыто тьмой в каждом человеке, в сердце. А также я благодарна Богу за все те испытания на моём жизненном пути, которые Он мне давал. Всё познаётся в сравнении&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Дуже, дуже вже хочу повернутися додому, до свого сина. Ми з ним розлучилися, коли йому було 3 роки, зараз йому вже 9 років (18.08.2010 р.)...і чим дорослішим він стає, тим більше потребує спілкування з мамою. Уповаю на Господа і молюсь, щоб Бог дарував мені дострокове звільнення...&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Аллі Б. 04.04.2011 р. на суму 72 грн.79 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Алли Б. на адресу:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Литвиненко Світлана</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; вул.Київська 165, кв.8</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">м.Марганець Дніпропетровської обл.&nbsp;&nbsp;&nbsp;&nbsp; 53403</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.11.2010 р. на суму 71 грн.30 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29.08.2011 р. на суму 108 грн. 63 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 252 грн. 72 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Віола, 5 р.) ув\'язненої Катерини Б. (Чернігівська ВК) в м.Херсон</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Мой ребенок (донечка Віолетта 30.06.2006 р.н.) воспитывается у крестной</strong><strong> </strong>(№моб.тел 095 696 79 48), которая тянется из последних сил. Пожалуйста отец Игорь я вас очень прошу <strong>помогите им по возможности, чем сможете</strong>. Я буду за вас молиться&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 24.09.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Діма, 9 р.) ув\'язненої Світлани Ф. (Чернігівська ВК) в м.Кривий Ріг Дніпропетровської обл. &nbsp;</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 12.12.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Анастасія, 11 р.) ув\'язненої Оксани Т. (Чернігівська ВК) в школу-інтернат м.Городня Чернігівської обл. </strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash;09.12.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Наталія, 12 р.) ув\'язненої Тетяни Б. (Чернігівська ВК) в м.Харцизьк Донецької обл.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 25.11.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Дитині (Софія, 11 р.) ув\'язненої Оксани З. (Качанівська ВК, м.Харків) в школу-інтернат &laquo;Отрадное&raquo; м.Харків</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 05.06.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;Срок у меня восемь лет&hellip; Имею дочь 2004 г.р. &nbsp;... Буду Вам благодарна за любую поддержку&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дочці Оксани З. в м.Харків.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 112 грн. 29 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Серафима, 3р.) ув\'язненої Олени Г. (Чернігівська ВК) в м.Жовті Води Дніпропетровської обл.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;У меня здесь в колонии была <strong>дочь Серафима</strong>, но в этом месяце я отдала ее своей маме домой, так как ей скоро исполнилось бы <strong>3 года</strong>, а у меня конец срока в 2014 г. У меня просьба к Вам, мне &ndash; ничего не нужно, <strong>помогите пожалуйста </strong>им. Любой помощи будут рады: как и <strong>одежда на 3-летнего ребенка, так и гигиенические средства, продукты питания, обувь зимняя 24 размер (детский), может летняя на вырост, благодарны будем за все... А также молитесь как и за них, так и за меня</strong>&hellip;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&hellip; Спасибо большое, что Вы есть и приносите радость людям, тем самым служа Господу</strong>&hellip; <strong>Молюсь, благодарю Бога за все</strong>, хожу на служения, читаю Библию. <strong>Стараюсь своей жизнью показать людям Христа, чтобы люди во мне видели свет</strong>. Рассказываю окружающим за Христа, говорю им, что Бог их любит и был распят на кресте за всех грешников, некоторые слушают с удовольствием, а некоторые уходят. Да благословит Вас Господь&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 07.03.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Олені Г.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17.03.2010&nbsp;&nbsp;&nbsp; на суму 28 грн.28 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">30.03.2010 р. на суму 78 грн.81 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги 107 грн. 09 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Дитині (Данилко, 6 р.) &nbsp;ув\'язненої Наталії П. (Чернігівська ВК) в м.Рубіжне Луганської обл.</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...На свободе у меня мать пенсионер и шестилетний сын&hellip; у него онкоболезнь, что-то с кровью&hellip; Узнала его <strong>точный диагноз &ldquo;Синдром Фишера Эванса: аутоимунная гематолитическая анемия с неполными тепловыми агглютининами в сочетании с тромбоцитопенической пурпурой&rdquo; </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу попросить у Вас помощи на лечение ребёнка. Помогите деньгами сколько сможете&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 16.05.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині Наталії П. в м.Рубіжне Луганської обл.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29.08.2011 р. на суму 101 грн. 91 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 101 грн. 91 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Дитині (Дарія, 3 р.) ув\'язненої Ганни Л. (Чернігівська ВК) в м.Суми</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 07.12.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>11.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Наталії Н. (Чернігівська ВК) в Будинок матері і дитини при колонії (донечці 9,5 міс.)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;<strong> Божого Вам благословення і з наступаючим Новим роком хай Вам щастить здоров&rsquo;я &nbsp;Вам кріпкого твердої віри і всього найкращого. Спаси і сохрани Вас Боже</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чекаю листа від свого синочка з інтернату йому в січні виповниться 13 років</strong> вже майже дорослий. Дуже сумую за ним але все буде добре бо ми з Господом і Господь нас не залише. <strong>Моїй донечці вже девять з половиною місяців</strong> &hellip; Хочу Вас запитати який піст після Різдва і як годуючим матерям дотримуватись посту&hellip; <strong>Моєму синові 28 січня день народження якщо Ваша ласка пошліть йому якийсь подарунок наприклад альбом для малювання і фломастери тощо</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 04.05.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>12.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Ірині А. (Чернігівська ВК) в Будинок матері і дитини при колонії</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 30 лет, отбываю &nbsp;наказание &nbsp;в колонии &nbsp;с ребенком, мальчик Никита 8.09.2010 г.р. Родом я с Александрии, там <strong>у меня остались мама (ей 54 года) и доченька (4 года), </strong>отец в прошлом году умер, а сожитель сидит, срок у меня 5 лет. Маме очень тяжело, зарплата маленькая и <strong>иногда без хлеба сидят&hellip; помогите пожайлуста моим родным чем сможете</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 10.10.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині Ірини А.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.12.2011 р. вислали &laquo;Подарунок від св.Миколая&raquo;</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>13.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Тетяні С. (Качанівська ВК, м.Харків)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip; Я ще Вас попрошу якщо можна то вишліть старі кофти для того щоб їх можна було розв&rsquo;язати і в\'язати дітям носки в інтернати, шарфики, шапочки, рукавички&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;<strong>Щиро вдячна Вам за посилку</strong>&hellip; <strong>я дівчатам повязала носки і дівчата передають Вам подяку і Боже благословення тим людям, що допомагають</strong>&hellip; Мені 15 грудня 52 роки і жаль що рідні ніхто мені не пише і непоздоровлять, але я молюсь за них, щоб Господь допоміг їм стати на правильний шлях і щоб вони навчили своїх дітей. Бо <strong>без Бога тяжко вижити в цьому світі. Я молюсь за нашу владу за президента за всю Україну. Якби вони більше читали слово Боже то знали б істину і негрішили бо гріх це злочин і хто живе в гріху той неможе бути вільний він хуже замкнений ніж ми в в&rsquo;язниці.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Я вдячна Вам, що Ви молитесь за мене я це відчуваю навіть тут. Тому що молитва має велику силу.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мені <strong>дуже потрібне взуття 41 розмір бо пухнуть ноги</strong> і нівчому нема ходити. Я <strong>буду чекати на нитки</strong> які є такі висилайте.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>За ліки особлива вдячність і уклін</strong> якщо доживу до звільнення то обов&rsquo;язково приїду&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок листування &mdash; вересень 2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Тетяні С.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.11.2010 р. на суму 65 грн.21 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.02.2011 р. на суму 89 грн. 44 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">01.11.2011 р. на суму 70 грн.20 коп. (ліки)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 224 грн.85 коп</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>14.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Оксані З. (Качанівська ВК, м.Харків)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;1978 г.р. Сама я с г.Харькова, отца нет уже много лет, а мама, ну в общем я ей наверное просто не очень нужна. Срок у меня восемь лет&hellip; Имею дочь 2004 г.р.&nbsp; ...Очень хочется исповедаться, последний раз исповедовалась в 2009 году на тюрьме... Чувствую, что общаясь с Вами мне будет легче здесь находиться... Буду Вам благодарна за любую поддержку и моральную и материальную&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 05.06.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дочці Оксани З. в м.Харків.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 112 грн. 29 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>15.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Ірині В. (Качанівська ВК, м.Харків)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я засуджена до 10 років позбавлення волі по статті 115. На волі у мене залишився малолітній хлопчик Іванко, йому <strong>сім з половиною років</strong>. В даний час він проживає із старшим сином Олегом і невісткою&hellip;Я <strong>дуже Вас прошу допомогти моєму малолітньому синові&hellip; Помоліться за мого синочка, бо я знаю, що молитва священника дуже помічна</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 28.09.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині Ірини В.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong>13.12.2011 р. вислали &laquo;Подарунок від св.Миколая&raquo;</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>16.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Раїсі О. (Чорноморська ВК, м.Одеса)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Термін &mdash; 11 років, в ув\'язненні від початку&nbsp; 2005 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Син Олександр (15.10.1997 р. н.), залишився під опікою батьків Раїси О. Батьки дуже хворі: у батька грижа, необхідна операція, але нема коштів; мама майже лежача. В серпні 2010 р. сина взяла під свою опіку свекруха Раїси. &ldquo;Свекруха пише мені лист, щоб я за сина не переживала, а молилась за батьків... Я Вас дуже прошу, умоляю, моліться за моїх батьків, щоб їх ще застати живими&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 07.05.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїса О. постійно присилає свої роботи: вишивані хрестиком та гладдю квіткові композиції, ікони.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїсі О. &mdash;&nbsp; 20.07.2009 р. на суму 40 грн.06 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 22.09.2009 р. на суму 65 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.10.2010 р. на суму 42 грн. 03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 18.02.2011 р. на суму 55 грн. 94 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.03.2011 р. на суму 75 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 21.06.2011 р. на суму 172 грн.01 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.09.2011 р. на суму 54 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру &mdash;&nbsp;&nbsp; 30.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 03.12.2009 р. на суму 70 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.12.2010 р. на суму 73 грн.03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 29.08.2011 р. на суму 138 грн. 57 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 13.12.2011р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 810 грн. 64 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>17.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Роксолані К. (Чернігівська ВК)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Мені 28 років</strong>. Народилась я на Донеччині, там і виросла. Усе було начебто добре, одружилась, мала роботу та своє житло. Але <strong>чоловік помер, а батьки від мене відвернулись</strong>&hellip; я <strong>скоїла злочин </strong>на 5 з половиною років. <strong>Я вірю, що у кожної людини є шанс на поліпшення свого життя. Я вірю в Бога, який надає мені терпіння</strong>. У вільний час займаюся складанням віршів та вишиванками. Зараз працюю над іконою св.Миколая Чудотворця. У наступному листі спробую Вам її відіслати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&hellip;В 2000 году я родила мальчика, в 2001 дочку. Но пока я находилась в колонии они были в Донецком приюте. А после мне сообщили что их усыновила семья с Испании. И теперь я даже не знаю как мне их найти, к кому обратиться. Поэтому прошу Вас о помощи разыскать моих деток. Возможно ли это? И что для этого нужно? Я их не видела с 2004 года. Я надеюсь на Божье чудо, что я смогу с ними когда-нибудь встретиться</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &ndash; 24.12.2011 р.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>18.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Ользі З. (Чернігівська ВК)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>10 лет уже сижу</strong> да и помоч то мне некому...У меня <strong>выпали почти все зубы</strong> даже есть нечем... &hellip;Я сильно болею сейчас&hellip;Мне уже осталось с 7 января 2 года и 5 месяцев 15 дней да Бог побыстрей вернуться дома легче ведь дома и питание то не такое да и лечение лучьше будет люди по обьявление помогут кто деньгами кто лекарством да кровью для переливання&hellip; <strong>Лекарства мои нашли</strong>&hellip; скоро через 4-5 дней кончатся у меня от печени ну если что мне выпишут я вам рецепт вишлю... <strong>Дети 4г. мне не пишут</strong> из-за сестры&hellip; <strong>О.Игорь молитесь за нас и наших детей</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 02.01.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">03.03.2010 р. вислали допомогу медикаментами: гематоген &mdash; 20 шт., карсил.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В серпні 2010 р. &mdash; допомога в сумі 100 грн. для медичного обстеження.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26.07.2011 на суму 101 грн.02 коп (ліки)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 201 грн. 02 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>19.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ув\'язненій Вікторії О. (Чернігівська ВК)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 42 года. Освободившись в 2004 г., устроилась на работу. Жила с гражданским мужем и двумя детьми... Единственным моим грехом было то, что иногда я позволяла себе колоться. И вот в 2009 г. работники милиции, узнав о том, что я ранее судима, сфабриковали на меня дело не за то, что я кололась, а за то, что якобы я продавала наркотики. Видит Бог, что к продаже я не имела никакого отношения. Дали мне 6 лет, &mdash; незаконно, за преступление, которого я не совершала. В тюрьме я стала читать Библию. И вот уже полтора года читаю её регулярно&hellip; Очень тяжело мне морально в плане смирения, но я не отчаиваюсь ведь я понимаю, что за один день я не изменюсь, если 30 лет жила в грехе</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&hellip; Моя мама <strong>&mdash;</strong> пенсионерка и ей очень тяжело ... Владику &mdash; 1 октября будет 7 лет и он в этом году пойдёт в первый класс, а Алинке &mdash; 3 октября будет 12 лет и она идёт в 6 класс. Помогите им &mdash; чем сможете... если можете, то вышлите мне телефонную карточку, чтобы я позвонила детям и маме домой&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист <strong>&mdash;</strong> 01.03.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.04.2011 р. на суму 130 грн.39 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">26.08.2011 р. на суму 181 грн. 56 коп. (&ldquo;Шкільний портфелик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.01.2011 р. &ndash; &laquo;Подарунок від св.Миколая&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 311 грн. 95 коп.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>20.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Матері ув\'язненої Ольги М. (Чернігівська ВК)</strong></span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;&hellip;мне 44 года. <strong>У меня старенькая мама, ей 75 лет с 1936 г.</strong> Я Вас очень слезно прошу, сделайте ей если это можно какой нибудь подарочек к Новому году, у меня нет такой возможности, хотя я и работаю но обеспечить себя не можем, так как <strong>нахожусь в инвалидном отряде, у меня дистрофия вес 43 кг. Но мне ничего ненадо. Я очень Вас прошу сделайте маме подарочек.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Да благословит Вас Господь Ваши семьи и Вашу церковь&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна вага поштових відправлень від 06.01.2012 р. &ndash; 75,68 кг.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість поштових конвертів, вкладених у посилки &mdash; <strong>40 грн.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість пересилання &mdash; <strong>352 грн.94 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума поштових витрат від&nbsp; 06.01.2012 р. &mdash; 392 грн. 94 коп.</strong></span></span></p>','ЗВІТ №1 про надання матеріальної допомоги жінкам, які відбувають покарання  в місцях позбавлення волі (06.01.2012 р.)','uk',2675,4158,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. На прохання ув\'язненої&nbsp; Оксани К. їй вислали полотно та нитки для вишивання. Кожен з адресатів отримав у подарунок молитовник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Робота виконана силами волонтерів: п.Марти, п.Петра, п.Ніни, п.Ірини.</span></span></p>','2011-06-10 11:21:31',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коштом жертводавців: п.Марти, п.Марії&nbsp; зібрані та відправлені посилки з матеріальною допомогою жінкам, які відбувають покарання у Чернігівській виправній колонії,&nbsp; та багатодітній (8 дітей) родині Валерія Ш. з м.Червонограда Львівської обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога надавалась продуктами, солодощами, гігієнічними засобами, речами особистого вжитку, що надійшли від жертводавців. На прохання ув\'язненої&nbsp; Оксани К. їй вислали полотно та нитки для вишивання. Кожен з адресатів отримав у подарунок молитовник.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Марти, п.Петра, п.Ніни, п.Ірини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Коротка інформація про тих, кому надавалась допомога:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Допомога&nbsp; ув\'язненій&nbsp; Оксані К.&nbsp; (Чернігівська ВК)&nbsp; на суму&nbsp; 84 грн. 20 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Відбуває покарання в інвалідному загоні Чернігівської ВК. Після черепно-мозкової травми погано працює права рука. &ldquo;У меня обнаружили токсоплазмоз (отмирают клетки головного мозга). Молюсь Господу Богу нашему, прошу у него помощи и сил. Прошу Вас помолитесь за меня, боль иногда становится невыносимой&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Перед праздником св.Николая всем желающим раздавали листы с 58 вопросами на знание Библии, из них я смогла ответить на 41 вопрос правильно, а недавно нам раздали эти листы, я заняла 4 место. Меня это очень обрадовало, ведь я ещё совсем недавно пришла к Богу...С Божьей помощью, наша администрация нашла средства и меня вывезли на томограмму. Хотя результат оказался неутешительным, поставили диагноз туберкулёз костного мозга, я стала плохо ходить, теперь большую часть дня я сижу и читаю. Мучаюсь от болей в костях и голове. <strong>Отец Игорь, я обращаюсь к Вам с мольбой о помощи. Вышлите мне ткани и ниток для вышивки</strong>. Очень хочется что-то дальше шить. Ведь просто сидеть сложа руки я не могу, а из-за болезни работать я не в силах&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Двоє дітей: дочка Юля (11 р.) виховується матір\'ю Оксани К. в м.Чернігові, син Максим (6,5 р.) виховується свекрухою Оксани К. в м.Орджонікідзе Дніпропетровської обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 17.08.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З власної ініціативи вишила картину &ldquo;Дівчина-бандуристка&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В січні 2011 р. вишила ікону &ldquo;Неопалима купина&rdquo; за малюнком з журналу на своїх матеріалах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>В березні 2011 р. прислала дуже якісно вишитий Великодний рушник.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Отец Игорь, <strong>я обращаюсь к Вам с мольбой о помощи, вышлите мне ткани и ниток для вышивки</strong>. Очень хочется что-то дальше шить, но у меня совсем нет ткани&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Оксані К. &mdash; 23.11.2010 р. на суму 60 грн.30 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 09.01.2011 р. на суму 55 грн.99 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 04.04.2011 р. на суму 86 грн.25 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дітям:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Юлі в м.Чернігів &mdash; 10.02.2011 р. на суму 52 грн.73 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 18.04.2011 р. на суму 81 грн.19 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Максиму в м.Орджонікідзе &mdash; 10.02.2011 р. на суму 55 грн.43 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;18.04.2011 р. на суму 82 грн.99 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 559 грн.08 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>2. Допомога ув\'язненій Оксані Ш.&nbsp; (Чернігівська ВК)&nbsp; на&nbsp; суму&nbsp; 25 грн. 20 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 18.04.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...нахожусь в местах лишения свободы с сыном, которого родила два месяца назад. Очень хочу кормить грудью как можно дольше. Но без поддержки и помощи &mdash; это практически невозможно. Сидеть ещё полтора года, родных &mdash; кроме отца никого нет (и тот от нас отказался &mdash; даже словечка не написал!). Помогите пожалуста...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Есть ли у Вас такая возможность оплатить мне восстановление паспорта? Мне обязательно надо паспорт по освобождению для того, чтобы зарегистрировать в ЗАГСЕ сына&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Після підтвердження адміністрації <strong>вислали грошову допомогу на оформлення паспорта в розмірі 55 грн.</strong> Вартість пересилання &mdash; 5 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Благодаря Вашей помощи &mdash; теперь я снова полноценный гражданин Украины...Вы сможете размножить нам несколько Ваниных фотографий?&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Висилаємо розмножені дитячі фотографії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Оксані Ш.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16.06.2010 р. на суму 107 грн.45 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 132 грн.65 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>3. Допомога багатодітній родині Валерія Ш. з м.Червоноград, Львівської обл., на суму 143 грн. 53 коп. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали родині:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">22.11.2010 р. на суму 155 грн.19 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08.12.2010 р. на суму 187 грн.80 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29.12.2010 р. на суму 28 грн.50 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 515 грн.02 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В підсумку надано матеріальну допомогу поштовими відправленнями від 05.05. 2011 р. на суму <strong>252 грн. 93 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, вартість:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; 93 грн.00 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вкладених поштових конвертів &mdash; 4 грн.50 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від 05.05. 2011 р. &mdash; 350 грн.43 коп.</strong></span></span></p>','ЗВІТ №9 про надання допомоги жінкам, які відбувають покарання в місцях позбавлення волі, та багатодітній родині (05.05.2011 р.)','uk',2486,3969,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, до 15 травня приймаються замовлення на відпочинок дітей у літньому християнському просвітницько-оздоровчому таборі в Івано-Франківській області (село Заріччя, 10 км. від Яремче).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Анкети для заповнення можна роздрукувати з сайту <a href=\"item,4210\">http://askoldova-mohyla.org/uk/item,4210</a>, або взяти в &laquo;Книжковій крамниці&raquo; нашої парафії, заповнені анкети залишати у крамниці, з обов&lsquo;язковою позначкою &laquo;літній табір&raquo;, а також вказати контактний телефон, або відправити на e-mail: <a href=\"mailto:mirpress@ukr.net\">mirpress@ukr.net</a> електронну версію заповненої анкети. Нам важливо якнайшвидше знати остаточну кількість дітей та дорослих, адже необхідно бронювати місця, внести передоплату, а також викупити квитки на потяг.<br /><br /></span></span></p>','2012-05-15 15:22:34',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі парафіяни, до 15 травня приймаються замовлення на відпочинок дітей у літньому християнському просвітницько-оздоровчому таборі в Івано-Франківській області (село Заріччя, 10 км. від Яремче).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Анкети для заповнення можна роздрукувати з сайту <a href=\"item,4210\">http://askoldova-mohyla.org/uk/item,4210</a>, або взяти в &laquo;Книжковій крамниці&raquo; нашої парафії, заповнені анкети залишати у крамниці, з обов&lsquo;язковою позначкою &laquo;літній табір&raquo;, а також вказати контактний телефон, або відправити на e-mail: <a href=\"mailto:mirpress@ukr.net\">mirpress@ukr.net</a> електронну версію заповненої анкети. Нам важливо якнайшвидше знати остаточну кількість дітей та дорослих, адже необхідно бронювати місця, внести передоплату, а також викупити квитки на потяг.<br /> УВАГА!!! Батьки також можуть брати участь у поїздці і відпочинку, щоправда за повну вартість проживання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Повна вартість проживання для батьків - 850 грн. Квитки на потяг - 216 грн., вартість доїзду з Івано-Франківська до Заріччя і назад&nbsp; - 40 грн. Загалом повна вартість складає <strong>1106 грн</strong>. Дитячий відпочинок: проживання - 255 грн., квитки - 162 грн., трансфер - 40 грн. Загалом: - <strong>4</strong><strong>57 грн</strong>. &nbsp;<br /> ВЕЛИКЕ ПРОХАННЯ: НАДАТИ КОПІЇ СВІДОЦТВ ПРО НАРОДЖЕННЯ ДІТЕЙ (для придбання квитків). Анкети, копії можна залишити в &laquo;КНИЖКОВІЙ КРАМНИЦІ&raquo;.<br /> Контактна особа п. Мирослава +38 050 789 33 56</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Садиба &laquo;У Заріччі над Прутом&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">(Івано-Франківська обл., Надвірнянський район, с. Заріччя, вул. Шевченко, 105 а)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Триповерховий будинок на 16 номерів може вмістити 38 осіб.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На першому поверсі &mdash; їдальня (50 місць), кухня (холодильник, газові плитки, мікрохвильова піч, електро чайник), 4 двомісних номери категорії \"Стандарт В\", в кожному &mdash; односпальні ліжка, меблі. Санвузли окремо на поверсі (2 туалети, 3 умивальники та 3 душові кабінки). На поверсі є сауна з кімнатою відпочинку.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На другому поверсі &mdash; номери категорії \"Стандарт А\" &mdash; один 1-місний, п\'ять 2-місних, один 3-місний та один 5-місний номери, в кожному &mdash; односпальні ліжка, меблі. В тримісному номері &mdash; розкладний диван. Номери оздоблені деревом. Санвузли окремо на поверсі (4 санвузли з туалетом та умивальником, 2 умивальники та 3 душові кабінки окремо). В одному з 2-місних номерів є окремий вихід на терасу. На поверсі є спільний вихід на терасу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На третьому поверсі (наполовину мансардний) &mdash; номери категорії \"Стандарт А\" &mdash; два 2-місних, один 3-місний та один 4-місний номери, в кожному &mdash; односпальні ліжка, меблі. Номери оздоблені деревом. Конференц-зал на 50 осіб (телевізор з супутниковим телебаченням, відео, музичний центр, караоке). &nbsp;В цоколі є більярдний стіл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Опалення: автономне парове.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Водопостачання: холодна та гаряча вода постійно, є резервуар для води на 200  л.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На території: мангал.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Харчування</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Домашня кухня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До найближчого кафе &mdash; 3 км, до магазину &mdash; 400 м.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сервіс, включений у вартість</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Паркінг, більярд.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сервіс за додаткову платню</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сауна до 5 осіб.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Спорт, розваги</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Екскурсії, походи в гори, збирання грибів та ягід.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Мобільний зв\'язок, Інтернет (КИЇВСТАР, МТС, Life:)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Як доїхати</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З Івано-Франківська &nbsp;60  км, до центру м. Яремче 10 км.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','Літній відпочинок - ОГОЛОШЕННЯ','uk',2740,4223,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі співвітчизники!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> На керівниках лежить велика відповідальність за долю України, але це не означає, що частини цієї відповідальності не несе кожен з нас. Не потрібно чекати, доки хтось інший вирішить наші проблеми. Якщо кожен на своєму місці в міру можливого працюватиме на славу Божу та благо України - тоді ми дійсно зможемо виправити недоліки нашого державного і суспільного життя.</span></span></p>\r\n<p>&nbsp;</p>','2008-10-07 11:40:40',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі браття і сестри, співвітчизники! </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Усяке царство, що розділилося в собі, спустошиться, </em><em><br /> </em><em>і всяке місто чи дім, що розділився у собі, не встоїть.</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>(Мф. 12,25)</em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В умовах, коли знову в нашій державі загострюється політичне суперництво, що підриває основи нашої державності, коли провідні державні інституції не можуть знайти порозуміння, а розчарування, байдужість та зневіра опановують суспільство - християнські Церкви України вважають своїм обов\'язком звернутися до вас з викладенням свого погляду на ці проблеми та можливі шляхи їхнього подолання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ми переконані, що незалежність нашої держави - дар Божий, яким ми повинні розпоряджатися мудро і зважено, примножуючи, а не марнуючи дане нам Господом. Заповідана Спасителем любов до ближнього, як до самого себе <em>(Мф. 22,39) </em>повинна виявлятися не тільки через любов до окремих осіб, але й через любов до всього українського народу, як до своєї великої родини.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ознакою істинної любові є жертовність, коли заради того, кого любиш, готовий віддати - і віддаєш - найцінніше, включно з самим життям. &laquo;Немає більше від тієї любові, як хто душу свою покладе за друзів своїх&raquo; <em>(Ін. 15, 13) - </em>говорить Господь Ісус Христос. Заради спасіння свого народу від гніву Божого пророк Мойсей був готовий прийняти на себе покарання за його відступництво. &laquo;Я жадав би сам бути відлученим від Христа за братів моїх, рідних мені по плоті&raquo; <em>(Рим. 9, 3) - </em>говорить апостол Павло. Ці приклади жертовної любові до свого народу повинні надихати кожного з нас, а особливо кожного, хто наділений владою, на служіння Україні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Влада дається Богом для того, щоб її носій, тримаючи в руках меч закону, захищав добро і карав зло. Кожен представник влади є невід\'ємною частиною народу, а не стоїть над ним. Той, в чиї руки дана влада, - законодавча, виконавча чи судова - повинен пам\'ятати, що за її використання він дасть відповідь не тільки перед суспільством та судом історії, але й перед Самим Богом - чи служив він народові, виявляючи любов до нього, чи служив він самому собі?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні ми вважаємо особливо актуальними винесені в епіграф євангельські слова. Це - нагадування всім, що боротьба за кращий політичний устрій, за утримання або перерозподіл владних повноважень може привести до остаточного руйнування нашого державного дому.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не раз в історії українського народу непримиренна боротьба між його провідниками приводила до знищення державності. Міжусобні чвари князів, боротьба між собою гетьманів часів Руїни, взаємне поборювання керівників національно-визвольного руху в XX ст. - все це сумні приклади того, як у боротьбі за владу над Україною руйнувалася сама Україна, нищилася її державність, чим вміло користувалися супротивники. Не варто повторювати цих помилок!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі співвітчизники!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На керівниках лежить велика відповідальність за долю України, але це не означає, що частини цієї відповідальності не несе кожен з нас. Не потрібно чекати, доки хтось інший вирішить наші проблеми. Якщо кожен на своєму місці в міру можливого працюватиме на славу Божу та благо України - тоді ми дійсно зможемо виправити недоліки нашого державного і суспільного життя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не зважаючи на політичні конфлікти за час незалежності України наш народ ніколи не опускався до вуличних заворушень чи збройного протистояння, а громадянські протести завжди носили мирний характер. Це свідчить про мудрість і зваженість народу, яка є запорукою можливості знайти порозуміння.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Цим спільним зверненням ми хочемо показати приклад того, що, не зважаючи на об\'єктивні відмінності між нами, ми можемо працювати разом. Віримо, що і політики та державні діячі, залишаючись прихильниками різних поглядів, зможуть знайти форми співпраці на благо України.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зневіра і розчарування не повинні зупиняти нашого суспільного поступу, бо тільки на нас, громадянах України, лежить відповідальність за те, що буде з нашим спільним домом завтра. Молімося Богові за владу і народ, працюймо разом - і нехай Господь допоможе нам!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закликаємо на всіх вас Боже благословення!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><span style=\"text-decoration: underline;\">Звернення підписали:</span></em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Філарет (Денисенко) </strong>- Патріарх Київський і всієї Руси-України, Предстоятель Української Православної Церкви Київського Патріархату; <strong>Любомир (Гузар) </strong>- Верховний Архієпископ, Глава Української Греко-Католицької Церкви;<strong> </strong><strong>Митрофан (Юрчук)</strong> - Архієпископ Білоцерківський і Богуславський, Керуючий справами Української Православної Церкви; <strong>Маркіян Трофим\'як </strong>-<strong> </strong>Єпископ, Заступник Голови конференції єпископів Римсько-католицької Церкви в Україні; <strong>В\'ячеслав Нестерук </strong>- Голова Всеукраїнського Союзу об\'єднань євангельських християн - баптистів; <strong>Михайло Паночко </strong>- Старший єпископ Всеукраїнського Союзу Церков християн віри євангельської-п\'ятидесятників;<strong> Леонід Падун </strong>- Старший єпископ Української Християнської Євангельської Церкви; <strong>В\'ячеслав Горпинчук </strong>- Єпископ Української Лютеранської Церкви.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Ухвалено на зустрічі глав християнських Церков України від 7 жовтня 2008 р. м. Київ</em></span></span></p>\r\n<p>&nbsp;</p>','Звернення керівників християнських Церков України до українського народу','uk',493,1473,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Досягнута домовленість про подальшу допомогу родині від &laquo;Карітас Печерськ&raquo;. щомісячну підтримка продуктами на суму 700 гривень. Шкільнятам з родини Махової портфелики наповнять доброчинці з-поміж парафіян церкви св. Миколая на Аскольдовій могилі.</span></span></p>','2008-08-15 23:44:55',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Декан київський, протоієрей, настоятель церкви св. Миколая на Аскольдовій могилі Ігор Онишкевич з благословення владики Богдана (Дзюраха) відвідав велику родину усиновлених дітей</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Елади Махової в селі Іванків Бориспільського району.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Досягнута домовленість про подальшу допомогу родині від &laquo;Карітас Печерськ&raquo;. щомісячну підтримка продуктами на суму 700 гривень. Шкільнятам з родини Махової портфелики наповнять доброчинці з-поміж парафіян церкви св. Миколая на Аскольдовій могилі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Усіх дітей у родині 14. Три дівчинки зараз відпочивають у санаторії. Наймолодшій дитині 1 рік, найстаршому незабаром виповнюється 18 років.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://noabort.org.ua/</span></span></p>','Київський декан призначив стипендію багатодітній родині','uk',1436,2492,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий рік поспіль в Україні відзначають найдостойніших благодійників на конкурсі &laquo;Благодійник року&raquo;. Цього року перемогу у конкурсі здобув і Міжнародний благодійний фонд \"Карітас України\".</span></span></p>\r\n<p>&nbsp;</p>','2008-12-29 21:48:31',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Другий рік поспіль в Україні відзначають найдостойніших благодійників на конкурсі &laquo;Благодійник року&raquo;. Цього року перемогу у конкурсі здобув і Міжнародний благодійний фонд \"Карітас України\".</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нагороду президенту Карітасу України Андрію Васьковичу вручила дружина президента України Катерина Ющенко, яка є Головою Наглядової ради Міжнародного благодійного фонду &laquo;Україна 3000&raquo;, що виступив одним з організаторів конкурсу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час вручення нагороди пані Катерина відзначила: &laquo;Благодійність - це не обов\'язково вкладання коштів. В багатьох країнах світу люди роблять свою благородну справу, працюючи волонтерами. Займаючись благодійною роботою, людина виконує свій християнський обов\'язок і стає ближчою до тих сердець, яким вона допомагає. А це, в свою чергу, робить її більш терплячою до життєвих негараздів&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За словами організаторів конкурсу, перемогу Карітасу України зумовила, насамперед, багатостороння діяльність фонду. Адже організація втілює у життя проекти по домашній опіці, допомоги ВІЛ-позитивним особам та людям, які хворі на СНІД; допомагає узалежненим від алкоголю та наркотиків; займається з дітьми з неблагополучних та кризових сімей, а також дітьми вулиці. Окрім цього Карітас України завдяки розвиненій мережі консультаційних пунктів активно займається міграційними питаннями, надає допомогу людям, що постраждали внаслідок міграційних процесів, зокрема торгівлі людьми. І, врешті, Карітас України ніколи не залишається осторонь кризових ситуацій, у які зненацька потрапили люди, наприклад, внаслідок стихійних лих. Так, Карітас організовує збір коштів як в Україні, так і за кордоном і передає їх та іншу допомогу постраждалим. Наприклад, після повені у Західній Україні влітку нинішнього року, організація зібрала і передала допомогу потерпілим на суму понад 300 тисяч євро.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Саме тому усім людям, які працюють в Карітас України дуже приємно, що їх роботу помітили і відзначили. На цьому наголосив і президент Карітасу України Андрій Васькович. &laquo;Дуже приємно отримати цю нагороду, адже вона свідчить про те, що роботу сотень наших працівників та волонтерів помічають не лише ті, кому ми безпосередньо допомагаємо, а вона важлива загалом для суспільства&raquo;, - зазначив пан Васькович і додав: &laquo;Також для нас дуже важливо, що це відзнака саме від громадських організацій, які знають та розуміють благодійну діяльність&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сама церемонія нагородження була доволі камерною і пройшла у теплій невимушеній обстановці, що відповідало саме такій імпрезі, яка ставить у центр уваги людину. На цей захід завітали також відомі митці та спортсмени - співаки Олег Скрипка та Марічка Бурмака, актор Богдан Ступка, генеральний директор Міжнародного кінофестивалю &laquo;Молодість&raquo; Андрій Халпахчі, олімпійська чемпіонка Стела Захарова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Організатори та учасники цього конкурсу, зокрема і Андрій Васькович, сподіваються, що незважаючи на світову фінансову та економічну кризу, благодійництво в Україні розвиватиметься. &laquo;Усі наші програми, які ми розглядаємо як моделі соціальної праці фінансуються, переважно, з-за кордону. Ми ж прагнемо до реалізації цих соціальних програм активніше залучати українських донорів, - розповів Андрій Васькович. - Тому ідеї поширення благодійництва в Україні, які пропагує цей конкурс є дуже важливими. Сподіваємося, що до підтримки соціальних програм і соціальних послуг, що їх надають неурядові організації, долучиться і держава. Надіюся на те, що Карітас України в наступному році матиме необхідні кошти для того, щоб і надалі реалізовувати нині існуючі програми, адже дуже не хочеться їх скорочувати, позаяк бачимо їх велику потребу для українського суспільства&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.caritas-ua.org/index.php?pg=424\" target=\"_blank\">http://www.caritas-ua.org/index.php?pg=424</a></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Довідка:</em><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Конкурс &laquo;Благодійник року&raquo; проводиться в Україні вдруге. Минулого року нагороду &laquo;Благодійник року&raquo; отримала і благодійна медична установа Шпиталь імені Митрополита Андрія Шептицького у Львові, відповідальність за розвиток якого з 2001 року несе Карітас України. </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Організаторами конкурсу &laquo;Благодійник року-2008&raquo; виступили Міжнародний благодійний фонд &laquo;Україна 3000&raquo;, Всеукраїнський благодійний фонд &laquo;Дитячий світ&raquo;, Центр філантропії, Український Форум благодійників та ІАЦ &laquo;Громадський простір&raquo;. Мета конкурсу - винагородження та визнання представників української громадськості за проведення благодійної діяльності, спрямованої на вирішення актуальних проблем суспільства, а також популяризацію благодійництва в Україні. В конкурсі можуть брати участь фізичні та юридичні особи, які надали одно- чи багаторазову благодійну допомогу для розв\'язання суспільних проблем. До журі конкурсу входять експерти та громадські діячі, які мають високий авторитет в суспільстві. </em></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>Конкурс &laquo;Благодійник року - 2008&raquo; розпочався 3 липня 2008 року, його результати були оприлюднені 12 грудня, а нагородження переможців відбулося 22 грудня в Києві.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.ugcc.org.ua/</span></span></p>','Карітас України став «Благодійником року — 2008»  ','uk',897,1871,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дуже, дуже вже <strong>хочу повернутися додому, до свого сина. Ми з ним розлучилися, коли йому було 3 роки, зараз йому вже 9 років (18.08.2010 р.)</strong>...і чим дорослішим він стає, тим більше потребує спілкування з мамою. Уповаю на Господа і молюсь, щоб Бог дарував мені дострокове звільнення...</span></span></p>','2011-08-20 10:28:56',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Ніна М. (Чернігівська ВК) </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 06.07.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Сама я из г.Луганска, мне 35 лет, <strong>имею 4 детей</strong>, мужа у меня нет, разбился на машине в 2010 г. Осудили меня 3 года 6 мес. Сижу я почти 2 года, сдесь я родила сына зовут его Богдан, ему 6 августа будет один годик, болею я туберкулёзом лёгких... <strong>Маме 78 лет, а отцу 85</strong>...недавно мама похоронила моего брата Руслана ему было 37 лет... Я Вам пишу с надеждой в том, чтобы Вы помогли моим детям они <strong>находятся возле моей мамы, 3 то есть 2 дочки и один сын</strong> ему 13 числа октября исполница <strong>8 лет... помогите продуктами, одеждой, обувью</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Наталія М. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; червень 2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 34 года, родом я с г.Мелитополя.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">...Мне девочка один набор для вышивания бисером отдала, что Вы ей высылали, т.к. она сломала руку... в одном конверте отправили с Инной Д., моя работа &ldquo;Распьятье&rdquo;... <strong>У меня есть сын несовершеннолетний, 1997 г.р.,</strong> Никита, опекуны мои родители пенсионеры. Если сможете,<strong> помогите</strong> канцелярией, тетради в линейку и клеточку (общие), ручки, пенал, линейку, простой карандаш, сладостями, продуктами питания и средствами для гигиены&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Прислала вишиту бісером ікону &ldquo;Розп\'яття&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Наталії М.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">11.07.2011 р. на суму 93 грн.82 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги 93 грн.82 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>3.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Надія Ф. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 53 года <strong>родных и близких нет кроме 12-летней дочери которая в данный момент находится в доме интернате.</strong> Я сирота я росла без родных... Я сичас сильно болею на работу не хожу здоровъя непозволяет. &rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 18.05.2011 р.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали Надії Ф.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16.06.2011 р. на суму 87 грн.55 коп. (ліки)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 87 грн.55 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>4.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Тетяна Б. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 06.12.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Троє дітей (Юля -13 р., Катя -11 р. і Даша -9 р.)</strong> виховує свекруха (78 р.) в м.Антрацит Луганської обл. Згоріла квартира.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дітям:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.02.2011 р. на суму 62 грн. 24 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.04.2011 р. на суму 135 грн.82 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>всё что было в посылке мы получили большое вам спасибо от души</strong>...</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Катя, Даша, Юля&rdquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 198 грн.06 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>5.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Алла Х. (Чернігівська ВК)</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Листується від 2010 р.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дитину (Діана, 8 р.) виховує свекруха (м.Донецьк). Не бачила дитини 3 роки.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомогу дитині надсилали в м.Донецьк:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07.09.2010 р. &mdash; на суму 65 грн.39 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.11.2010 р. &mdash; на суму 51 грн.87 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">09.02.2011 р. &mdash; на суму 49 грн.66 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.04.2011 р. на суму 86 грн.63 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 253 грн.55 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Марина А. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 03.11.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Из-за меня страдают детки. <strong>У меня трое детей. Старшему сыну 10 лет,</strong><strong>пожалуйста, помогите ему</strong>&rdquo;</span> второму сыну 6 лет, а доци 4 годика. Я прошу Вашей помощи моему старшему сыну Славе, который сейчас находится в Центре реабилитации.&nbsp; Он мне пишет &ldquo;Мама я все твои письма берегу, и я их как читаю и плачу в подушку, чтобы никто не видел&rdquo;. Отец Игорь, </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Вячеславу</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">23.11.2010 р. на суму 49 грн.90 коп. в Центр соц.-психолог. реабілітації дітей, с.Хмільниця Чернігівської обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">19.04.2011 р. на суму 72 грн.08 коп. - в школу-інтернат м.Городня Чернігів. обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру в м.Ніжин Чернігівської обл.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.04.2011 р. на суму 60 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">донечці Арині в м.Ніжин Чернігівської обл.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.04.2011 р. на суму 85 грн.16 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 267 грн.14 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>7.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Віта П. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 05.05.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Я <strong>прошу Вас помочь моей девочке.</strong> Моей дочери 2 июня исполнилось <strong>15 лет.</strong><strong>Ей идти в школу</strong>... Я сижу почти два года и ещё чуть больше трёх осталось. Я употребляла наркотики... Помолитесь, пожалуйста за мою дочь и за свекровь&rdquo;</span> Она живёт с моей свекровью, свёкр умер... У меня с ней нет никакой связи. Вернее я пишу, а в ответ тишина... Помогите, очень Вас прошу. </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Олена Т. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 10.11.2010 р.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я безмерно благодарна Вам за всё, что Вы делаете для меня и моих деток...&nbsp; переживаю очень за моих деток и маму ведь Рому нужно снова везти к врачу, чтобы сказали будут ему ещё делать операцию или нет... Вот пишу Вам <strong>имена и даты рождения моих деток</strong>: дочь Танюша &mdash; 25.12.1991; сын Александр &mdash; 21.12.1993; <strong>сын Назар &mdash; 2.07.1999; дочь Роксаночка &mdash; 4.02.2003;</strong> сын Роман &mdash; 5.10.2007; дочь Анастасия &mdash; 27.09.2008; дочь Инга &mdash; 4.04.2011. 12 июня отец Артемий покрестил мою маленькую дочурку Ингочку...&rdquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олені Т. &mdash; 23.11.2010 р. на суму 36 грн.63 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дітям (на адресу бабусі, в м.Долинська Кіровоградської обл.)</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">10.02.2011 р. на суму 102грн. 37 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.04.2011 р. на суму 134 грн.87 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 273 грн.87 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>9.&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Анна К. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 15.07.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Родом я из Луганской области. Напишу о своих детках.<strong> Старшего зовут Алексей, ему 6 лет, в этом году идёт в школу</strong>, младшему Виталику 16 августа исполнится 4 годика. Малышей воспитывает моя мамочка, дай Бог ей здоровья! Мне осталось сидеть полтора года. Виталика я родила здесь в млс, мама год назад забрала его домой... Если сможете <strong>помогите собрать старшего Алёшеньку в школу &mdash; письменные принадлежности, альбомы </strong>и т.д.&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Олена В. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 13.07.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я из Донецкой области, г.Макеевка. <strong>Дома остались два сыночка</strong>. Старший &mdash; Стасик, ему 21 год, а <strong>младший &mdash; Данюша, ему 8 лет</strong>... Я его поздно родила, вторым браком. Даже в садик его не отдавала, везде-везде с ним рядом. А за старшим не уследила. Уверена была очень в нём и вот беда... Не могла я, отец Игорь, в тюрьму его отдать. Вот и взяла всё на себя.&nbsp; Срок 2 года 9 месяцев...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Данюше в этом году в школу, мама пишет, что денег нет, еле концы с концами сводят. Вся пенсия уходит на бюрократические заслоны, не может она добиться опекунства, т.к. у неё российское гражданство. А <strong>если Данюша не пойдёт в школу, его в детдом заберут</strong>. Я работаю на швейной фабрике, но нам платят максимум 50 грн. в месяц. Я&nbsp; стараюсь каждый месяц хоть 20 грн, но выслать маме, но это же так мало. Отец Игорь, миленький, <strong>помогите, умоляю Вас, Данюше пойти в школу</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>11.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Валерія О. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист <strong>&mdash;</strong> 01.03.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...вы помогали моим детям посылкой к Пасхе. Дети и моя мама, а также и я, очень вам благодарны... Если вы имеете такую возможность, то помогите пожалуйста моим детям собраться в школу, т.к. моя мама <strong>&mdash;</strong> пенсионерка и ей очень тяжело с ними. <strong>Владику &mdash; 1 октября будет 7 лет и он в этом году пойдёт в первый класс, а Алинке &mdash; 3 октября будет 12 лет и она идёт в 6 класс. Помогите им &mdash; чем сможете..</strong>.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я молюсь каждый день, мне очень удивительно видеть в себе перемены в лучшую сторону. Очень тяжело мне морально в плане смирения, но я не отчаиваюсь ведь я понимаю, что за один день я не изменюсь, если 30 лет жила в грехе&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дітям Валерії О. (Аліна - 11 р., Владислав - 6 р.) 18.04.2011 р. на суму 130 грн.39 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 130 грн.39 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>12.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Наталія П. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 15.07.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мне 32 года, нахожусь в местах лишения свободы 2 с половиной года, до конца срока ещё полтора года. <strong>Дома осталась мать-пенсионерка и сын, которому 14 лет, и он прешёл в 9 класс.</strong> Мне сказали девчёнки из нашей колонии, что Вы помогаете детям матерей, которые отбывают срок наказания. Вот я и решила просить Вас помочь и моему сыну Егору предметами школьных принадлежностей... Ещё мой ребёнок страдает экземой кистей рук, врачи говорили, что перерастёт, но увы, этого не случилось. Ему нужны лекарства, но я не знаю, какие именно?! Может Вы сможете ему помочь?!&rdquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>13.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Олена Р. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 03.04.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>У меня есть дочка ей в этом году будет 7 лет, она идёт в 1-й класс.</strong><strong>пожалуста помогите собрать мою дочку Катю в школу.</strong> Я думаю чем ну может ручки, тетради&rdquo;</span> Воспитывает мою дочку Катю моя свекровь, мужа нет уже, он умер. Если у Вас есть такая возможность </span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олені Р. &mdash; 31.07.2009 р. на суму 30 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дитині (м.Феодосія) &mdash; 31.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 17.10.2009 р. на суму 27 грн.02 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 03.12.2009 р. на суму 63 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 20.04.2010 р. на суму 72 грн.97 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 05.11.2010 р. на суму 37 грн.43 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 07.12.2010 р. на суму 79 грн.53 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 13.04.2011 р. на суму 98 грн.16 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 433 грн.11 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>14.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Раїса О. (Чорноморська ВК, м.Одеса)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 07.05.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;<strong>Большое Вам спасибо за всё, что Вы делаете, помогаете</strong>. Я всё получила 2-го липня. <strong>Я Вам висилаю маленьку вишивку, ангелятко</strong>, через 2-2,5 неділі я Вам вишлю іконку... <strong>Пишу Вам адресу, де проживає мій синочок</strong>&rdquo;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїсі О. &mdash;&nbsp; 20.07.2009 р. на суму 40 грн.06 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 22.09.2009 р. на суму 65 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.10.2010 р. на суму 42 грн. 03 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 18.02.2011 р. на суму 55 грн. 94 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.03.2011 р. на суму 75 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 21.06.2011 р. на суму 172 грн.01 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру &mdash;&nbsp;&nbsp; 30.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 03.12.2009 р. на суму 70 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.12.2010 р. на суму 73 грн.03 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 618 грн.07 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>15.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Наталія В. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 01.12.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1978 р.н. &ldquo;...родом из Кривого Рога... В 1998 г. мне поставили страшный диагноз &mdash; ВИЧ, а в 2007 г. мой муж умер от СПИДа. <strong>У меня остался сын Антоша (23.09.2001 р.н.)... с моей мамой</strong>. Она на ногах перенесла инфаркт... Я очень сожалею о содеянном. Мне жалко свою маму...Я нахожусь в колонии и не могу ничем им помочь...Мне бы очень хотелось, чтобы Вы написали моему сыночку Антоше и маме слова поддержки. Мамочке очень тяжело сейчас воспитывать внука, он взрослеет, совсем её не слушает, не хочет учиться, только гульня в голове и телевизор&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Зона это очень тяжёлое испытание для женщины и хороший урок на всю оставшуюся жизнь&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;В кінці 2010 р. переведена на поселення в м.Орджонікідзе (ОВЦ-79).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Антону в м.Кривий Ріг &mdash; 26.08.2010 р. на суму 77 грн.67 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 23.11.2010 р. на суму 93 грн.92 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 08.12.2010 р. на суму 90 грн.30 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 13.04.2011 р. на суму 93 грн.85 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 355 грн.74 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>16.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Оксана Д. (СВК-127, м.Сніжне Донецької обл.)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок листування &mdash; 2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Двоє дітей, Олександр (12 р.) і Дмитро (6 р.)</strong>, виховувались у бабусі, матері Оксани Д., в м.Макіївка Донецької обл. Бабуся померла в січні 2010 р. Стараннями волонтера п.Марти Б. з\'ясували, що діти віддані під державну опіку:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Олександр &mdash; в школу-інтернат у м.Вуглегірську Донецької обл., &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Діма &mdash; в дитячий будинок &ldquo;Пролісок&rdquo; у м.Макіївка Донецької обл.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перша допомога Оксані Д. &mdash; в жовтні 2009 р.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Друга &mdash; 09.12.2009 р. на суму 55 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомога дітям на адресу бабусі &mdash; 20.04.2010 р. на суму 81 грн.73 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В державні установи інтернатного типу:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Олександру &mdash; 26.08.2010 р. на суму 69 грн.71 коп</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 23.11.2010 р. на суму 63 грн.43 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 10.02.2011 р. на суму 61грн.14коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дмитрові &mdash; 23.11.2010 р. на суму 73 грн.23 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;11.02.2011 р. на суму 62 грн.64 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 343 грн.10 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>17.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Маргарита Є. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 12.04.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29.04.1975 р.н. Термін &mdash; 3 р.6міс. На волі (с.Скородна Марківського р-ну Луганської обл.) <strong>залишилась донечка Христина (2002 р.н.)</strong> під опікунством сусідів &mdash; Сергія Федоровича Р. та його дочки. Рідні нема. На початку вересня 2010 р. дитина, катаючись на велосипеді, впала, отримала черепно-мозкову травму. Прооперована, майже місяць провела в лікарні. &ldquo;У их сейчас очень сильная напряженка материально, не могут после больницы Кристиночкиной выбраться с долгов. Уже почти с хозяйства продали всё (порося, козу), осталась птица. Им приходится продукты питания брать в долг в магазине под пенсию. Сергей Федорович переживает, но говорит, что как бы тяжело ни было, Кристиночку никому не отдам&rdquo;.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">17.06.2010 р. на суму 92 грн.96 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07.09.2010 р. на суму 49 грн.10 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08.12.2010 р. на суму 105 грн.51 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15.03.2011 на суму 89 грн.60коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">18.04.2011 р. на суму 81 грн.19 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги </strong><strong>&mdash;</strong><strong> 418 грн.36 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>18.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Ольга С. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 19.04.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Закінчення терміну ув\'язнення в 2014 р. &ldquo;На свободе (м.Лисичанськ Луганської обл.) у меня осталась мать (1938 р.н.), у которой больное сердце и давление. И на её обеспечении <strong>мой сын (1996 р.н.) с диагнозом дальнозоркость, очень плохое зрение.</strong> Я и мама, мы вдовы. Решила обратиться к Вам и прошу Вашего благословения. Я буду очень благодарна за любую помощь моей семье и прошу помолитесь за здравие моей мамы Валентины и сына Дмитрия&rdquo;. Син навчається в 9 кл.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині в м.Лисичанськ:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14.06.2010 р. на суму 73 грн.83 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">04.11.2010 р. на суму 56 грн.50 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">08.12.2010 р. на суму 95 грн.99 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.04.2011 р. на суму 94 грн.63 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 320 грн.95 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>19.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Валентина П. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 09.01.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чоловік теж в місцях позбавлення волі. Залишилось троє дітей. Старшим сином Валентином (1995 р.н.) опікується сестра ув\'язненої. Дочка Людмила виховувалась в школі-інтернаті в м.Новомиргород Кіровоградської обл. В 16-річному віці народила двоє діток-близнят. Молодший <strong>син, Іван (7 р.), на вихованні в школі-інтернаті </strong>І-ІІІ ст. в с.Пантаївка, Олександрівського р-ну Кіровоградської обл.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дітям:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Людмилі &mdash; в березні 2009 р. на суму 43 грн.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Івану &mdash; 30.09.2010 р. на суму 47 грн.08 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 17.02.2011 р. на суму 55 грн.94 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 15.04.2011 р. на суму 92 грн.23 коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 238 грн.25 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>20.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Наталія Л. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 01.02.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Мій син Дмитро закінчив перший клас в українській школі. Зараз почались канікули і бабуся (75 р.) дуже заклопотана, адже двоє хлопців (Євген, 2009 р.н., дуже живий, цікавиться усім, старший &mdash; Дмитро, 2003 р.н., хворий: ожиріння, поганий зір) постійно потребують надзвичайної уваги. Так боляче на душі, що не можу їм бути у пораді. Почнеться новий навчальний рік...хочу звернутись до Вас з проханням &mdash; <strong>допомогти бабусі зібрати до школи Дмитра і по можливості вислати канцтовари (зошити, ручки, олівці)</strong>...Я <strong>від щирого серця вдячна Вам, отець Ігор, за Вашу діяльність &mdash; місія, котра всім нам зігріває серця</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дітям в м.Шостка Сумської обл. 13.04.2011р. на суму 121грн.18коп.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 121 грн.18 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>21.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Наталія С. (Качанівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Звернулась вперше 08.04.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Засудили мене до семи років і з них три з половиною я вже відбула... Народилась я в повноцінній сім\'ї, з батьками жили ми добре, але через 4 роки мама з татом розлучились...Мама вийшла вдруге заміж. Вітчим був дуже хорошою людиною, поки не почав вживати алкоголь. Через деякий час мама також почала пити разом з вітчимом... Сестра почала вживати наркотики, потім і я також. Ми то збивались з правильної дороги, то знову повертались. І так довгий час йшла боротьба за наші душі. А потім в стані алкогольного сп\'яніння я скоїла злочин, про який навіть нічого не пам\'ятаю. Мене засудили. В 2007 р. під час слідства померла мама, а через декілька місяців і вітчим також пішов з життя. На той час у мене залишились три брати і молодша сестричка. В старшої вже була своя сім\'я, чоловік і донечка. Моїх молодших братів і сестричку забрали в дім для діток сиріт.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І потім прийшли зміни в мій розум, я звернулась в молитві до Бога, так як колись вчила мене бабуня. Я молилась за братів і сестричку, а найбільше мене непокоїв мій молодший братик, якого розлучили з старшими. Їх відвезли до школи, а його залишили одненького. Через деякий час Господь відповів на мою молитву &mdash; Андрійка усиновлено. Його взяла сім\'я з дуже хорошими людьми.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зараз старшому <strong>брату 12, сестрі 11 і брату 9 років.</strong> <strong>Вони навчаються в школі-інтернаті.</strong> Якщо Ви в змозі підтримати моїх рідних, то <strong>будь ласка допоможіть.</strong>.. навіть не знаю, на що можу розраховувати. Нехай Господь Вас благословить і керує в благословінні інших людей, хто в чому має нужду&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дітям в школу-інтернат в с. Стара Басань 19.04.2011 р. на суму 61 грн.68 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 61 грн.68 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>22.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Тамара Ш. (ОВЦ-79, м. Орджонікідзе Дніпропетровської обл.)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 18.03.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На час написання першого листа відбула 2 роки ув\'язнення. Самотня, <strong>син Андрій (09.10.2001 р.н.) виховується в школі-інтернаті</strong> &ldquo;Казка&rdquo; м.Щорс Чернігівської обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тамарі Ш. &mdash; 07.03.2011 р.на суму 101 грн.23 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові &mdash;&nbsp;&nbsp;&nbsp; 20.04.2010 р. на суму 78 грн.57 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 30.09.2010 р. на суму 53 грн.98 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 07.12.2010 р. на суму 59 грн.69 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 14.04.2011 р. на суму 74 грн.97 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 368 грн.44 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>23.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Оксана Ш. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 25.05.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я совершила преступление, за которое несу сейчас наказание, я очень раскаиваюсь в том, что совершила, но самое главное,что не могу простить, то что <strong>дома с мамой осталась моя маленькая дочка. Зовут её Кристина, ей 8 лет (24.08.2001 р.н.).</strong> Мама моя пенсионерка, ей 60 лет. У мамы сахарный диабет уже 10 лет. Я никакой работы не боюсь и с радостью помогала бы своей дочке, но нечем... <strong>помогите пожалуйста чем сможете моей дочке</strong>. Да хранит Вас Господь Бог!&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бабуся з внучкою відгукнулись подякою на отримані посилки: &ldquo;Отец Игорь, я Вам очень благодарна за посылки! Пусть Бог даёт Вам здоровья!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кристина 9 лет&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дитині Оксани Ш. в пос.Пантелеймонівку Донецької обл.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">21.04.2010 р. на суму 110 грн.98 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">16.06.2010 р. на суму 78 грн.06 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15.04.2011 р. на суму 134 грн.24 коп. (&ldquo;Пасхальний кошик&rdquo;)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 323 грн.28 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>24.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Надія Н. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 23.04.2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В ув\'язненні захворіла на виразку шлунку. &ldquo;Нахожусь в колонії уже другий год, <strong>маю багато діток, сама менша находиться в школі-інтернаті. Їй сповниться 25 апреля 10 років&rdquo;.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Прошу Вас сделайте мне маленькую посылку конвертами, ручками чтоб я могла с дочкой переписуваться&rdquo;&ldquo;Мне никто не пишет кроме Вас и дочери... Ещё раз хочу поблагодарить Вас, что в тяжёлую минуту протянули мне руку помощи и за дочь и за себя...Я молюсь Богу чтобы побыстрее отсюда выйти, чтобы заботиться о ней... У меня мама умерла в сорок лет, отец оставил нас, когда мне было 10 лет, женился на другой и потом вскоре тоже умер. Меня забрали в детдом, а квартиру отняли, я считай осталась на улице. После детдома в 17 лет я вышла замуж. Я не знала, что он наркоман, я с ним пожила и разошлась... воспитывала дочь одна с Божьей помощью... Срок у меня 6 лет, из них сижу почти три&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Надії Н. -&nbsp; &nbsp;01.06.2011 р. на суму 55 грн. (для оформлення паспорта)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.07.2011 р. на суму 57 грн.80 коп. (ліки)</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дочці Ніколаєнко Любі:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">в школу-інтернат м.Городня Чернігівської обл.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">12.11.2010 р. на суму 76 грн.49 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">13.04.2011р. на суму 95 грн.07 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 284 грн.36 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>25.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Алла Б. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Я уже изолирована от общества почти шесть лет... У меня одиннадцать лет лишения свободы... Я благодарна Богу за все те испытания на моём жизненном пути, которые Он мне давал. Всё познаётся в сравнении.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Когда я выйду из этих стен, я мечтаю вынести в свет свою первую книгу, которую уже начала писать, под названием &ldquo;Жить, а не существовать&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;Дуже, дуже вже <strong>хочу повернутися додому, до свого сина. Ми з ним розлучилися, коли йому було 3 роки, зараз йому вже 9 років (18.08.2010 р.)</strong>...і чим дорослішим він стає, тим більше потребує спілкування з мамою. Уповаю на Господа і молюсь, щоб Бог дарував мені дострокове звільнення...<strong>Вже я можу розпочинати писати у Департамент прошення про те, щоб мені скасували вирок. </strong>На це вже в мене є багато плюсів...І зцим усім в мене є <strong>великий мінус. Моя заробітна плата не дозволяє мені виплатити господарчий іск, який на даний час складає майже 700 грн.</strong> <strong>Тому я зараз звертаюсь до усіх, хто може мені допомогти і вислати гроші, хто скільки зможе</strong>&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пише вірші-псалми, складає музику до них.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;... благодарю Бога за то, что Он дал мне этот талант...&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Алли Б. в м.Марганець Дніпропетровської обл. 04.11.2010 р. на суму 71 грн.30 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Аллі Б. 04.04.2011 р. на суму 72 грн.79 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 144 грн.09 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>26.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Олена К. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист 26.04.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...<strong>На свободе с моей мамой трое моих деток: Таня 1995 г.р., Влад 1997 г.р., Полина 1998 г.р.</strong> Моей маме 67 лет. На каждого ребёнка получает 100 грн. Это очень мало, прокормить и одеть тяжело. Я Вас слёзно прошу<strong> помогите пожалуйста моим детям одеждой, обувью и продуктами</strong>. Помолитесь за моих детей и маму!&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>27.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Алла Н. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...Я принесла столько боли людям и зла... <strong>Спасибо вам огромное, что Вы поддерживаете мою доченьку Дианочку (12 р.) материально.</strong> Я напишу Вам снова данные моей доченьки... Может в Вашем фонде спонсируют отдых для детей &mdash; какие-то путёвки? Выезды куда-нибудь на лето в лагеря детские отдохнуть? Напишите маме моей&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Початок листування &mdash; 2010 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали дочці Діані в м.Прилуки:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07.09.2010 р. на суму 51 грн.99 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подяка бабусі: &ldquo;Спасибі Вам за одяг, все підійшло, все дуже гарне, все потрібне. Та й шкільне приладдя знадобилося для моєї внучки-школярки. Ще раз Вам дуже вдячна за ту добру справу, яку Ви робите. Нехай Вас береже Бог!&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">07.12.2010 р. на суму 76 грн.20 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 128 грн.19 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>28.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Тетяна С. (Дніпродзержинська ВК, Дніпропетровська обл.)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 15.10.2008 р., після відбування половини терміну ув\'язнення (термін &mdash; 8 років). Хворіє на туберкульоз і бронхіальну астму, йшов пісок із нирок, починає турбувати печінка. Пише, що в 2009 р. заочно вступила на 1-й курс Духовної Академії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;... <strong>дочке моей исполнится 29 марта только 10 лет, проживает она со своей прабабушкой (82 р.).</strong> Она очень развитая девочка, занимается танцами и её даже по телевизору показывали у нас по &ldquo;Скифии&rdquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тетяні С. &mdash; 20.07.2009 р. на суму 59 грн. 02 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.02.2011 р. на суму 89 грн. 44 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дитині Вікторії, на адресу бабусі Євдокії П. в м.Таврійськ Херсонської обл.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 20.09.2008 р. &mdash; на суму 107 грн.19 коп.;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;25.08.2010 р. &mdash; на суму 103 грн.40 коп.;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 09.12.2010 р. &mdash; на суму 95 грн.83 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 454 грн.88 коп.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>29.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Оксана Б. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; червень 2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...срок 6 лет...мне 37 лет, родных у меня нету, мать и отец умерли 10 лет назад, мужа 5 лет назад похоронила, <strong>имею дочь Ирочку, ей 9 лет</strong>. Она живёт с тётей своей, моего покойного мужа родная сестра. <strong>Живут очень бедно</strong>... <strong>Помогите моей дочери, чем сможете, мне ничего не надо.</strong> <strong>Я хоть инвалид, у меня нету правой почки,</strong> я хожу на работу, чтобы заработать себе на мыло. Если бы я получала здесь много денег, я бы помогла дочери, а так прошу вашей помочи. Если на то будет Божья воля&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>30.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Оксана З. (Качанівська ВК, м.Харків)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 05.06.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;1978 г.р. Срок у меня восемь лет, здесь на лагере 9-ть месяцев, остальное время судилась 3 года. <strong>Имею дочь 2004 г.р.</strong> Каждый день перед сном вот уже на протяжении 3-х лет я для успокоения души читаю Библию... вникаю тяжеловато. Прошу Вас пожалуйста выслать литературу для более ясного понимания Библии. Сама я с г.Харькова, отца нет уже много лет, а мама, ну в общем я ей наверное просто не очень нужна. ...Очень хочется исповедаться, последний раз исповедовалась в 2009 году на тюрьме... <strong>Буду Вам благодарна за любую поддержку и моральную и материальную</strong>&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>31.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;Наталія К. (Чернігівська ВК)</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 01.07.2011 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&ldquo;...32 года, 14 лет срока, позади уже 9 лет.<strong> Мой сын Вячеслав 11.10.97 г.р. находится в детском доме </strong>в Сумской обл. В этом детском доме немного деток всего душ 10, не больше и всех жалко до боли в сердце...Если у Вас есть возможность, то <strong>помогите моему сыну</strong> и по возможности этим деткам. Мой сын всё равно поделится с каждым из них всем, что у него будет.&rdquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>32.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Раїса О. (Чорноморська ВК, м.Одеса) </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Перший лист &mdash; 07.05.2009 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Термін &mdash; 14 років, в ув\'язненні від початку&nbsp; 2005 р.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Син Олександр (15.10.1997 р. н.), залишився під опікою батьків Раїси в селі Торчин у Винницькій області, де вона жила до ув\'язнення. &ldquo;У сім\'ї нас багато, 6 братів і 6 сестер, тато і мама мають 26 внуків, серед них я була одна неслухняна&rdquo;. Батьки дуже хворі: у батька грижа, необхідна операція, але нема коштів; мама майже лежача. В серпні 2010 р. сина взяла під свою опіку свекруха Раїси.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Допомагали:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Раїсі О. &mdash;&nbsp; 20.07.2009 р. на суму 40 грн.06 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 22.09.2009 р. на суму 65 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 26.10.2010 р. на суму 42 грн. 03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 18.02.2011 р. на суму 55 грн. 94 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.03.2011 р. на суму 75 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 21.06.2011 р. на суму 172 грн.01 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">синові Олександру в с. Бутків Вінницької обл.:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 30.07.2009 р. на суму 25 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 03.12.2009 р. на суму 70 грн.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.12.2010 р. на суму 73 грн.03 коп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума допомоги &mdash; 618 грн.07 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Зупинись!&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прочитай!&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Відгукнись!</strong></span></span></p>','ШКІЛЬНИЙ ПОРТФЕЛИК 2011','uk',2554,4037,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовенство, богопосвячені особи та українська громада Риму спільно вшанує в центрі католицького світу пам\'ять жертв Голодомору 1932-1933 років. Опівдні у неділю 23 листопада 2008 р. духовенство та миряни зберуться на площі святого апостола Петра у Ватикані, щоб помолитись за невинні жертви Голодомору разом із Святішим Отцем Венедиктом&nbsp;XVI та прийняти його Апостольське благословення.</span></span></p>','2008-11-19 14:51:57',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовенство, богопосвячені особи та українська громада Риму спільно вшанує в центрі католицького світу пам\'ять жертв Голодомору 1932-1933 років. Опівдні у неділю 23 листопада 2008 р. духовенство та миряни зберуться на площі святого апостола Петра у Ватикані, щоб помолитись за невинні жертви Голодомору разом із Святішим Отцем Венедиктом&nbsp;XVI та прийняти його Апостольське благословення. <br /><br />Як і у всьому світі, також і в Італії, на спогад про жертви Голодомору 22 листопада запалають свічки, зливаючись з полум\'ям &laquo;Невгасимої свічки&raquo;, яка пройшла через Україну та 33 країни світу, включно з Італією, та підіймуться до Господа молитовні благання, єднаючись у спільній молитві за упокій душ мільйонів жертв Голодомору.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.radiovaticana.org/</span></span></p>','Вшанування 75-ої річниці Голодомору в Римі','uk',677,1657,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовні провідники погодились з позицією Блаженнішого Любомира, Глави УГКЦ, озвучену представником УГКЦ, про те, що телебачення та радіо не повинно допускати лихослів&rsquo;я в ефірі &ndash; нецензурної лексики, яка за останні часи стала широко використовуватись у молодіжних розважальних програмах, гумористичних проектах та іншій телепродукції.</span></span></p>','2009-09-08 00:09:03',NULL,'P','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Голова Національної експертної комісії України з питань захисту суспільної моралі Василь Костицький представив главам та представникам конфесій звіт про діяльність очолюваного ним відомства. Про активізацію діяльності Національної комісії та її досягнення у сфері утвердження моральних засад суспільства за останній період Василь Костицькій розповів під час засідання Секретаріату Всеукраїнської Ради Церков і релігійних організацій 3 вересня 2009 року. Про це повідомляє <a href=\"http://www.irs.in.ua/\" target=\"_blank\">Інститут релігійної свободи, м.Київ</a>.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У своїй доповіді голова Національної комісії повідомив про заходи, вжиті за наслідками виявлення фактів незаконного поширення матеріалів еротичного та порнографічного характеру, публікацій ксенофобського, антисемітського змісту та таких, що ображають національну гідність українців та представників інших націй, а також релігійні почуття віруючих. Особливу увагу він приділив загрозливій тенденції вільного поширення продукції, яка пропагує жорстокість та насильство, має еротичний чи порнографічний характер,&nbsp; у різних медійних засобах, Інтернеті та у мобільному контенті, відкритих для доступу дітей.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час обговорення даної проблематики представники Церков і релігійних організацій висловили свій категоричний протест проти публікації розпусних та образливих матеріалів. У якості прикладів були згадані публікації у щоденній газеті &laquo;Блік&raquo;, які були визнані Національною комісією такими, що порушують законодавство про захист суспільної моралі. На думку релігійних діячів, у виданні не тільки ображаються християнські цінності та почуття віруючих людей, але й нав&rsquo;язується розпусний спосіб життя читачам, серед яких можуть бути підлітки та діти. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Учасники засідання звернули увагу на важливу у цьому сенсі відкриту Заяву Всеукраїнської Ради Церков і релігійних організацій від 2 червня 2006 року щодо необхідності поважати релігійні почуття, символи, традиції та дотримуватись високих моральних стандартів у журналістській діяльності.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Духовні провідники погодились з позицією Блаженнішого Любомира, Глави УГКЦ, озвучену представником УГКЦ, про те, що телебачення та радіо не повинно допускати лихослів&rsquo;я в ефірі &ndash; нецензурної лексики, яка за останні часи стала широко використовуватись у молодіжних розважальних програмах, гумористичних проектах та іншій телепродукції.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У ході зустрічі Василь Костицький також поінформував про реалізацію положень <a href=\"http://www.irs.in.ua/index.php?option=com_content&amp;view=article&amp;id=88&amp;catid=34&amp;Itemid=61\" target=\"_blank\">Меморандуму про співробітництво</a> між Всеукраїнською Радою Церков і релігійних організацій та Національною комісією, підписаного у грудні 2008 року. Він запропонував низку проблемних питання, задля вирішення яких Національна комісія та релігійна спільнота може об&rsquo;єднати свої зусилля та поглибити співпрацю.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На завершення, учасники засідання запевнили керівництво Національної комісії, що&nbsp;Церкви та релігійні організації і надалі будуть послідовно підтримувати всі заходи, спрямовані на формування в Україні суспільства, яке складається з духовно і морально здорових та гармонійно розвинених особистостей.</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><a href=\"http://www.irs.in.ua/\" target=\"_blank\">http://www.irs.in.ua</a> </em></span></span></p>','Співпраця Церкви і держави задля утвердження в Україні високих моральних цінностей  ','uk',1780,3263,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діла милосердя необхідні людині як хліб і вода, бо без їжі не може жити наше тіло, а без віри, молитви, милосердя не може жити наша душа. Тому Господь каже нам, що Він спитає кожного за те, чи робив він добру справу, чи був милосердним до своїх ближніх. У своєму другому пришесті Господь промовить &ndash; &laquo;прийдіть благословенні Отця мого, візьміть у спадщину царство приготовлене вам від сотворення світу, бо голодним Я був, а ви нагодували мене, був спраглим, а ви напоїли мене, був подорожнім і ви прийняли мене, був нагим і ви одягнули мене, був хворим і ви відвідали мене, у в&rsquo;язниці був і ви прийшли до мене&raquo; (Матей 25, гл. 34-36).</span></span></p>','2009-10-07 00:35:18',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діла милосердя необхідні людині як хліб і вода, бо без їжі не може жити наше тіло, а без віри, молитви, милосердя не може жити наша душа. Тому Господь каже нам, що Він спитає кожного за те, чи робив він добру справу, чи був милосердним до своїх ближніх. У своєму другому пришесті Господь промовить &ndash; &laquo;прийдіть благословенні Отця мого, візьміть у спадщину царство приготовлене вам від сотворення світу, бо голодним Я був, а ви нагодували мене, був спраглим, а ви напоїли мене, був подорожнім і ви прийняли мене, був нагим і ви одягнули мене, був хворим і ви відвідали мене, у в&rsquo;язниці був і ви прийшли до мене&raquo; (Матей 25, гл. 34-36).</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За цими словами наші парафіяни, яким ми щиро вдячні, відгукнулися на допомогу людям, які потерпіли від повені. Від Карітас Коломийської єпархії ми отримали списки потерпілих від повені в с. Воскресінці і протягом цілого року надавали їм допомогу. Сьогодні ми подаємо звіт цієї допомоги. Велика подяка усім нашим парафіянам, які працювали, допомагали, збирали речі, пересилали посилками, чи у інший спосіб долучилися до цього Божого діла.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я особисто відвідав потерпілі родини у Воскресенці, побачив, що дійсно люди зазнали лиха, а наша допомога їм стала дуже і дуже важливою. Останню допомогу, яку ми надали за програмою шкільний портфелик - це була допомога дітям. Сьогодні і отець Григорій, парох Воскресіньської церкви, і парафіяни церкви, і ми щиро радіємо що змогли допомогти потерпілим. Віримо що місцева громада не залишить потребуючих людей, без своєї уваги і буде надалі їм допомагати. Якщо дасть Бог і буде у нас можливість, ми й надалі старатимемось у своїх молитвах, у своїх ділах милосердя підтримувати ці родини. Ще раз усім велика і щира подяка за діла милосердні.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вірний у Христі, отець Ігор Онишкевич.</span></span></p>\r\n<table style=\"border-collapse: collapse; width: 381px; height: 5030px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<col style=\"width: 53pt;\" width=\"70\"></col> <col style=\"width: 41pt;\" width=\"54\"></col> <col style=\"width: 36pt;\" width=\"48\"></col> <col style=\"width: 42pt;\" width=\"56\"></col> <col style=\"width: 39pt;\" width=\"52\"></col> <col style=\"width: 32pt;\" width=\"42\"></col> <col style=\"width: 47pt;\" width=\"63\"></col> <col style=\"width: 48pt;\" width=\"64\"></col> \r\n<tbody>\r\n<tr style=\"height: 42.75pt;\" height=\"57\">\r\n<td style=\"height: 42.75pt; width: 211pt;\" colspan=\"8\" width=\"280\" height=\"57\"><span lang=\"UK\">Церковною громадою церкви св. Миколая надана   допомога родинам, які потрапили в біду пов&rsquo;язаною з природними даними (повінь   &ndash; літо 2008 р.)</span></td>\r\n</tr>\r\n<tr style=\"height: 64.5pt;\" height=\"86\">\r\n<td style=\"border-top: medium none; height: 64.5pt; width: 53pt;\" width=\"70\" height=\"86\"><span lang=\"UK\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>П. І.</span></td>\r\n<td style=\"border-top: medium none; width: 41pt;\" width=\"54\"><span lang=\"UK\">Продукти   харчування</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 36pt;\" width=\"48\"><span lang=\"UK\">Гігієна</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 42pt;\" width=\"56\"><span lang=\"UK\">Канц. товари</span></td>\r\n<td style=\"border-top: medium none; width: 39pt;\" width=\"52\"><span lang=\"UK\">Художня   література</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 32pt;\" width=\"42\"><span lang=\"UK\">Медикаменти</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 47pt;\" width=\"63\"><span lang=\"UK\">Речі дом. вжитку</span></td>\r\n<td style=\"border-top: medium none; width: 48pt;\" width=\"64\"><span lang=\"UK\">Разом<span>&nbsp;</span></span></td>\r\n</tr>\r\n<tr style=\"height: 17.25pt;\" height=\"23\">\r\n<td style=\"height: 17.25pt; width: 211pt;\" colspan=\"8\" width=\"280\" height=\"23\">&nbsp;</td>\r\n</tr>\r\n<tr style=\"height: 79.5pt;\" height=\"106\">\r\n<td style=\"height: 79.5pt; width: 53pt;\" width=\"70\" height=\"106\"><span lang=\"UK\"><span>&nbsp; </span>Мироняк Роман<span>&nbsp; </span>3-є дітей</span></td>\r\n<td style=\"width: 41pt;\" width=\"54\"><span lang=\"UK\">77,92</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 36pt;\" width=\"48\"><span lang=\"UK\">&nbsp;</span></td>\r\n<td style=\"border-top: medium none; border-left: medium none; width: 42pt;\" width=\"56\"><span lang=\"UK\">&nbsp;