INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сестри і почесні гості прийняли участь у ювілейних святкуваннях чотирьох сестер зі Сполучених Штатів Америки і однієї з Канади в монастирі Пречистої Діви Марії у Словцбургу, США.</span></span></p>','2008-10-16 07:27:59',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сестри і почесні гості прийняли участь у ювілейних святкуваннях чотирьох сестер зі Сполучених Штатів Америки і однієї з Канади в монастирі Пречистої Діви Марії у Словцбургу, США. Свої ювілеї монашого служіння відзначали с. Йонатан Дученська (50-ліття), с. Анісія Симьонов (60-ліття), с. Ґрейс Ладуцер (60-ліття), с. Тарасія Гладьо (65-ліття). Приєдналася до святкувань також с. Натанаїла Перейма (Канадійська провінція Христа-Царя), яка відзначала своє 70-ліття монашого життя. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"image,1478,200,RTEmagicC_16.10.2008.jpg.jpg\" alt=\"\" />Високопреосвященний Архиєпископ Стефан (Сорока), Митрополит Філадельфійський, під час святкової Літургії у своїй проповіді підкреслив глибоку вірність до Всевишнього у сестер в їхньому служінні. Сумлінно виконуючи обіцянку убогості, чистоти і покори, ідучи за прикладом блаженної Йосафати та маючи Пречисту Діву Марію за покровительку, ці сестри сумлінно зносили години труднощів, переживань та страждань в імя Христа.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сестра Мішель Якимович, Провінційна настоятелька, у своєму слові до сестер підкреслила їхню багаторічну працю в Божому Винаграднику в педагогічній сфері релігійної освіти. Кожна сестра присвятила роки у служінні в школах у всіх чотирьох єпархіях Сполучених Штатів Америки, працювали у семінарійній бібліотеці, реколекційному домі в Словцбурзі.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ніщо не було неможливим для сердець, котрі завжди були віддані Богові, який завжди був і залишається центром їхнього життя. Святкування 70-ліття монашого життя сестри Натанаїли нагадало їм про їхню духовну єдність в Христі Ісусі з сестрами з Канадійської провінції Христа-Царя, з якими вони проходили новіціят в Канаді, де разом вчилися життя з Христом під Покровом Матері Божої. Сестра Йонатана промовляла від імені всіх ювіляток, дякуючи почесним гостям Митрополитові Стефану, Владиці-емериту Стемфорської єпархії Василю (Лостену), Англіканському єпископу Джордже (Ленґберґту), о. Марселу Шабу, капелану-духівникові сестер у Словцбурзі, о. Василю Корніцькому, родинам та друзям, що змогли цей день посвятити у співсвяткуванні разом із сестрами-ювілятами в честь Богові, на славу Марії, а нам та всім людям на мир.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сестри провінції Непорочного Зачаття</span></span></p>\r\n<p>&nbsp;</p>','Ювілейні святкування Сестер служебниць провінції Непорочного Зачаття','uk',498,1478,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу! Дорогі в Христі сестри!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наша церковна служба милосердя &laquo;Карітас&ndash;Печерськ&raquo; знову отримала від Вас велику кількість листів, і нині мої церковні помічники стараються дати відповідь кожній з Вас.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Почався великий піст, і за прикладом Господа нашого Ісуса Христа, який 40 днів перебував в пустелі, постився, церква закликає нас до молитви, посту-покаяння, милосердя-милостині.<br /></span></span></p>','2010-02-22 11:33:48',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу! Дорогі в Христі сестри!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наша церковна служба милосердя &laquo;Карітас&ndash;Печерськ&raquo; знову отримала від Вас велику кількість листів, і нині мої церковні помічники стараються дати відповідь кожній з Вас.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Почався великий піст, і за прикладом Господа нашого Ісуса Христа, який 40 днів перебував в пустелі, постився, церква закликає нас до молитви, посту-покаяння, милосердя-милостині. Де б ми не перебували, де б не були Ви, завжди можемо покликати Господа на пораду й допомогу, натомість виявити покору і терпеливість, а найголовніше &ndash; віру в швидке звільнення, тому для всіх Вас подаю &laquo;Правди віри&raquo;, за якими треба жити та яких дотримуватися. Всі вони взяті з Біблії, тому постарайтесь їх перечитувати і звершувати свої вчинки згідно з Божою любов&rsquo;ю.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щодо прохання про допомогу, на жаль, змушені відмовити. Зараз наша громада збирає Пасхальний кошик потребуючим. Шкода, та ми не в силі всім допомогти. Як Ви вже знаєте, ми допомагаємо дітям, чиї матері перебувають у місцях позбавлення волі. Так ми хочемо відновити зв&rsquo;язок матері&ndash;дитини і вселити надію, щастя і радість у ці родини. Окрім того, прагнемо підтримати тих, хто не має допомоги взагалі...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Кожен рядочок, кожне слово з Ваших листів говорять про те, що Ви одинокі і не маєте з ким спілкуватись, &ndash;&nbsp; пишіть нам! Ми радо будемо вести духовне листування та відписувати Вам. Не забувайте, що Вас запросили до співпраці, тому чекаємо на Ваші статті, вірші, розповіді про вчинки, за які Ви шкодуєте, пишіть про все хороше, що сталося з Вами, про свої надії і сподівання&hellip; Дуже раді вишитим іконам та малюнкам, у які Ви вкладаєте свою душу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Господь любить УСІХ НАС! Бажаю Вам у час посту очищення, оздоровлення і радісно зустріти Христове Воскресіння!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Під час посту читайте молитву святого Єфрема Сиріна. Цей святий народився на початку IV&nbsp;ст. в м. Низибії, що в Месопотамії. Маючи доброчесних батьків-християн, навчався в низибійського єпископа Іоанна, який постарався дати Єфрему духовну освіту. Після смерті Іоанна Єфрем перебрався в сирійське місто Едессу, де знайшов добрих наставників в науках благочестя. Одержавши священний сан, Єфрем успішно проповідував, а також писав церковні повчання, котрі читались при богослужіннях. Щоб повніше пізнавати високі духовні істини, він вдавався до далеких мандрівок, щоб переймати науку великих пустинників. Так Єфрем побував у&nbsp;Єгипетських і Сирійських старців, відвідав св. Василія Великого повернувся в&nbsp;Едессу. Невдовзі, бажаючи усамітнитися, Єфрем поселився недалеко в печері, молився і писав твори. До нього почали сходитися учні, щоб слухати чернечі настанови і переймати молитви. Одна з таких молитов дійшла і до нас і читається у Великий піст:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Молитва святого Єфрема Сирина <br /></strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Господи й Владико мого життя, не дай мені духа лінивства, безнадійности, владолюбства й пустомовства. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доземний уклін </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Духа ж чистоти, смиренности, терпеливости й любові дай мені, рабові Твоєму. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доземний уклін &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Так, Господи Царю, дай мені здатність бачити мої провини й не засуджувати мого брата, бо Благословенний Ти на віки вічні, амінь. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доземний уклін </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Боже, очисти мене, грішного. </strong>(12 разів з поясними поклонами)<strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Господи й Владико мого життя, не дай мені духа лінивства, безнадійности, владолюбства й пустомовства. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Духа ж чистоти, смиренности, терпеливости й любові дай мені, рабові Твоєму. </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Так, Господи Царю, дай мені здатність бачити мої провини й не засуджувати мого брата, бо Благословенний Ти на віки вічні, амінь.</strong><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дякуємо Богу за все!</span></span></p>\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Слава Богу за все!&raquo;</span></span></h2>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десь далеко-далеко і дуже давно</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Жив премудрий і праведний старець,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Він завжди говорив і завжди він твердив:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Слава Богу за смуток і радість&raquo;.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Слава Богу за все, слава Богу за все,</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Слава Богу за смуток і радість. } 2р</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Все Господь нам дає для спасіння душі,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не тривожся, якщо буде трудно</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А змирися тоді і завжди говори:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&laquo;Значить Господу все так угодно&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Научися творить волю Божу святу,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Може й ти колись будеш гонимим.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З сумкою на плечах, зі сльозами в очах,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Навіть рідним не будеш любимий.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли будеш веселий ти, ситий, здоров,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Друзів будеш ти мати багато.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Та уважним ти будь і тоді не забудь</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Милосердному дякувать Богу.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А коли тобі грубо хтось щось і сказав,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чи погано до тебе віднісся.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Знай, мій друже, що й тут воля Божа свята</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Щоби завжди із цим ти мирився.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів.&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли будуть журба і хвороба, і сум,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чи у чомусь терпіти прийдеться,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Винних ти не шукай, а завжди говори:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Все від Бога нам грішним дається&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приспів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наші роки пройдуть, друзі всі відійдуть.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незамітно підкрадеться старість.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Та завжди ти тверди і тоді говори:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Слава Богу за смуток і радість!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З ЛЮБОВ&rsquo;Ю ДО ВАС,&nbsp; ОТЕЦЬ ІГОР</span></span></p>\r\n<table style=\"text-align: justify; width: 449px; height: 2131px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"246\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>КАТИХИЗМОВІ ПРАВДИ</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віра і заповіді.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Шість правд віри:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Є один Бог, що все створив і всім управляє. 2. Бог є справедливий суддя, що за добро нагороджає, а за зло карає. <br />3. Є три Божі Особи: Бог Отець, Бог Син і Бог Святий Дух; це є Пресвята Тройця. <br />4. Друга Особа Божа, Син Божий, <br />став чоловіком і вмер на хресті <br />задля нашого спасення.<br />5. Людська душа є безсмертна. 6. Божа благодать є необхідна до спасення.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дві головні заповіді лю6ови: Люби Господа Бога твого всім серцем твоїм, всією душею Твоєю, всією силою Твоєю і всіма мислями твоїми. Це перша і найбільша заповідь.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А друга подібна до неї: Люби ближнього твого, як самого себе (Мт. 22, 37&ndash;39).</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Десять Божих заповідей:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Я &ndash; Господь Бог твій. 1. Нехай не буде в <br />Тебе інших богів, окрім мене. <br />2. Не взивай намарне імени <br />Господа Бога твого. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />3. Пам&rsquo;ятай день святий святкувати.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />4. Шануй батька твого і матір твою.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;5. Не вбивай. 6. Не чужолож. 7. Не кради.&nbsp;&nbsp;&nbsp; <br />&nbsp;8. Не свідчи ложно на ближнього твого.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;9. Не пожадай жінки ближнього твого.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />10. Не пожадай нічого того, що є власністю ближнього твого (див. Вихід 20, 2&ndash;17).</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Шість церковних заповідей :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Установлені свята святкувати. <br />2. У неділі і свята брати участь <br />у святій Літургії.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<br />3. Установлені пости постити. <br />4. Кожного року, бодай раз, у пасхальному часі, сповідатися і причащатися. <br />5. У заборонені часи весіль <br />і забав не справляти. <br />6. Злих книжок і писем не читати.</span></span></p>\r\n</td>\r\n<td width=\"255\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Християнська праведність: </span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вистерігайся злого &ndash; твори добро.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чесноти &ndash; Дари Святого Духа</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сім святих таїнств:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Хрещення. 2. Миропомазання. <br />3. Покаяння-сповідь. 4. Пресвята Євхаристія &ndash; тіло і кров Господа нашого Ісуса Христа під видами хліба й вина. 5. Подружжя. <br />6. Священство. 7. Єлеопомазання.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Божі чесноти: <br />1. Віра. 2. Надія. 3. Любов.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Моральні чесноти: <br />1. Мудрість.<br />&nbsp;2. Справедливість.<br />3. Мужність.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; <br />4. Здержливість.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Головні добрі діла: <br />1. Молитва.&nbsp; 2. Піст.&nbsp; 3. Милостиня.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сім діл милосердя для душі: <br />1. Грішника навернути. 2. Невіжу навчити. <br />3. У сумніві порадити. 4. Сумного потішити. <br />5. Кривду терпеливо зносити. 6. Образу з серця прощати. 7. За живих і померлих молитися.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сім діл милосердя для тіла: 1. Голодного нагодувати. 2. Спрагненого напоїти.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />3. Нагого зодягнути. 4. Подорожнього в дім прийняти. 5. Недужому послужити. <br />6. В&rsquo;язня відвідати. 7. Померлого похоронити.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сім дарів Святого Духа: <br />1. Мудрість. 2. Розум. 3. Рада. <br />4. Кріпость. 5. Знання.&nbsp; 6. Побожність. <br />7. Страх Божий (див. Ісая 11, 2).</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дев&rsquo;ять плодів Святого Духа: <br />1. Любов.&nbsp; 2. Радість. 3. Мир. 4. Терпеливість.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5 . Добротливість. 6. Милосердя.<br />7. Віра. 8. Лагідність. <br />9. Поміркованість (див. Гл. 5, 22&ndash;23).</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"246\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Три євангельські ради:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Добровільна вбогість. <br />2. Досмертна чистота. <br />3. Досконалий послух.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Євангельські блаженства:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Блаженні вбогі духом, бо їх є царство небесне. 2. Блаженні плачучі, бо вони утішаться. 3. Блаженні лагідні, бо вони наслідять землю. 4. Блаженні голодні і спрагнені правди, бо вони наситяться. 5.&nbsp;Блаженні милостиві, бо вони поми-лувані будуть. 6.&nbsp;Блаженні чисті серцем, бо вони Бога побачать. 7.&nbsp;Блаженні миротворці, бо вони синами Божими назвуться. 8. Блаженні вигнані за правду, бо їх є царство небесне (Мт. 5, 3&ndash;10).</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Гріхи &ndash; </strong>Головні гріхи і противні <br />їм чесноти:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Гордість. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Покора.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Захланність.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2. Щедрість.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Нечистота.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3. Чистота.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Заздрість. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4. Зичливість.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp; 5.&nbsp;Неуміркованість.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5.&nbsp;Уміркованість.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Гнів.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6. Лагідність.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7. Лінивство. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7. Пильність.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чужі гріхи: </span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Наказувати гріх.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp; 2. Спонукувати до гріха. &nbsp;</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Дораджувати до гріха.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Дозволяти на гріх.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5. Допомагати до гріха.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Не карати за гріх.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">7. Гріх боронити.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">8. Гріх хвалити.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9. На гріх мовчати.</span></span></p>\r\n</td>\r\n<td width=\"255\" valign=\"top\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Християнська праведність:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;Вистерігайся злого &ndash; твори добро.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Чесноти &ndash; Дари Святого Духа</strong></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гріхи проти Святого Духа:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Надмірна надія на Боже милосердя.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Безнадійність на Боже милосердя.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3.&nbsp;Спротив пізнаній правді християнської віри.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Завидування ближньому Божої ласки.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">5. Закаменілість на спасенні упімнення.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">6. Нерозкаяність аж до смерти.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Гріхи, що кличуть про помсту до неба:</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1. Умисне людиновбивство.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">2. Гріх содомський.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">3. Скривдження вбогих, удів і сиріт.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">4. Задержання заробітної заплати.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Останні речі: Смерть, суд, небо, пекло.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Дякую Тобі, Господи, за всі блага, якими Ти мене обдарував&raquo;.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Одначе, не як Я бажаю, але як Ти&raquo;.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">О, Боже мій! Хочу любити Тебе не тому, щоб осягнути вічне небо, <br />і не тому, щоб оминути пекло; <br />люблю Тебе саме тому, що Ти &ndash; мій Господь, Ти &ndash; моє все. Дозволь мені воздавати Тобі, і не чекати нагороди; боротися за Тебе, і не дивитися на рани; працювати для Тебе, і не чекати заплати, в тім переконанні, що служу Тобі, мій Боже.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Завжди раді Вам у церкві Святого Миколая на Аскольдовій могилі.</span></span></p>\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Свята Літургія щодня о 08.00 та о 18.00</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','Будьмо милосердні!','uk',1970,3453,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Думаю, що ніколи не пізно подякувати людям, котрі зробили багато доброго.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу висловити щиру вдячність усім, хто організовував катехитичний табір цього літа для дітей у Рудному. Багатьох я не знаю, а тих, про кого знаю, хотіла би розповісти.</span></span></p>','2011-09-23 18:26:02',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Думаю, що ніколи не пізно подякувати людям, котрі зробили багато доброго.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хочу висловити щиру вдячність усім, хто організовував катехитичний табір цього літа для дітей у Рудному. Багатьох я не знаю, а тих, про кого знаю, хотіла би розповісти.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Приємною несподіванкою для мене стало те, що о. Ігор Онишкевич повідомив: дітям нашої парафії вона буде вполовину дешевшою, хоч коштувала й так недорого, бо що таке нині 150 грн.? Тож хоч тут фінансові проблеми не стали перешкодою.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">О. Роман, о. Ярослав та інші, чиїх імен я навіть не знаю, опікувалися нашими дітьми у Рудному, щоб і відпочили вони, і Слово Боже пізнали.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Моя донька не відзначається спокійною вдачею, тож з нею буває дуже нелегко. У перший же вечір вона впала і вдарилася  це те, що я дізналась наступного дня, та напевне там було не все так просто, бо о. Роман серед ночі возив її у травмпункт, їй зробили знімок і сказали, що все добре, просто є сильне забиття, і приписали мазь, яку отець відразу ж купив. Я всього і не знаю, але впевнена, нелегко було їм з моєю дитиною, і дуже вдячна всім.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слава Ісусу Христу!</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Світлана</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;</span></span></p>','Подяка','uk',2576,4059,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слова про любов і діла милосердя, пов&rsquo;язані з людськими стражданнями,  дозволяють нам ще раз відкрити в основі людських страждань спасенне  страждання Христа. Ісус сказав: &laquo;Ви мені це робили&raquo;. Він сам є Тим, хто  зазнає любові, Він є Тим, хто отримує допомогу, коли її надається  людині, яка страждає. Він сам присутній у людині, що терпить, оскільки  Його спасенне страждання відкрите раз і назавжди для кожного людського  страждання.</span></span></p>','2011-06-09 17:42:58',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"text-decoration: underline;\"><a href=\"http://212.77.9.15/audio/ra/00264960.RM\"><img src=\"http://www.radiovaticana.org/ucr/img/listen.gif\" border=\"0\" alt=\"\" /></a> </span> Блаженний Іван Павло ІІ часто говорив і навчав про значення терпіння  та болю в людському житті, зокрема, в Апостольському Листі &laquo;Salvifici  Dolores&raquo; (Спасенного болю), що був оприлюднений 11 лютого 1984 року.  Блаженний Іван Павло ІІ, серед іншого, пише:<br /><br />&laquo;Слова про любов і  діла милосердя, пов&rsquo;язані з людськими стражданнями, дозволяють нам ще  раз відкрити в основі людських страждань спасенне страждання Христа.  Ісус сказав: &laquo;Ви мені це робили&raquo;. Він сам є Тим, хто зазнає любові, Він є  Тим, хто отримує допомогу, коли її надається людині, яка страждає. Він  сам присутній у людині, що терпить, оскільки Його спасенне страждання  відкрите раз і назавжди для кожного людського страждання. І всі ті, що  страждають, раз і назавжди покликані стати учасниками &laquo;Христових мук&raquo;,  як і всі покликані &laquo;доповнити&raquo; своїми стражданнями &laquo;те, чого ще бракує  скорботам Христовим&raquo; (Кол 1,24). Христос навчив людину одночасно творити  добро через страждання і творити добро тим, хто страждає. У цьому  подвійному аспекті Він повністю розкрив значення страждання&raquo;, &ndash; написав  блаженний Іван Павло ІІ.<br />http://www.radiovaticana.org/</span></span></p>','Через поріг тисячоліття з Іваном Павлом ІІ (44)','uk',2483,3966,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слуга Божий Папа Пій ХІІ, 50-річчя смерті якого припадає на 9 жовтня 2008 року, протягом усього свого понтифікату, що тривав майже 20 років і випав у важкі роки ІІ Світової війни та неспокійні повоєнні часи, невтомно турбувався про мир. Він написав 41 Енцикліку і багато послань на релігійно-суспільні теми.</span></span></p>','2008-10-10 13:37:34',NULL,'P','<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Слуга Божий Папа Пій ХІІ, 50-річчя смерті якого припадає на 9 жовтня 2008 року, протягом усього свого понтифікату, що тривав майже 20 років і випав у важкі роки ІІ Світової війни та неспокійні повоєнні часи, невтомно турбувався про мир. Він написав 41 Енцикліку і багато послань на релігійно-суспільні теми. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Його окремою турботою була доля тих Церков, які зазнавали переслідування комуністичного режиму, зокрема доля УГКЦ. Українці відчували його любов, прихильність і захист. Папа Пій ХІІ згадував про переслідувану Церкву в численних проповідях та посланнях, видав 18 важливих документів, представляючи світові великі страждання переслідуваних Церков та засуджуючи жорстокість і несправедливість атеїстичного режиму. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1450,200px-PopePiusXII_choir.jpg\" alt=\"\" />Папа Пій ХІІ видав 2 Енцикліки у справі Східних Церков, в яких дуже прихильно висловився про УГКЦ. 1945 року, під час святкування 350-річчя Берестейської унії 1596 р., він видав Енцикліку з протестом проти переслідувань УГКЦ в Радянському Союзі. 1952 року вийшла Енцикліка до всіх Східних Церков, в якій Папа Пій ХІІ в особливий спосіб звертається до переслідуваних християн в Болгарії, Україні та Румунії. Окремо підкреслено несправедливий засуд українських владик. У цій Енцикліці Пій ХІІ писав: &laquo;В особливий спосіб хочу згадати тих єпископів Східного обряду, що першими зазнали терпінь, горя і несправедливостей, захищаючи релігію: їх вивезено в місто Київ, там їх віддали під суд і засудили на різні покарання; саме у місті Києві, як ми сказали, що колись був центром поширення християнства в тому регіоні. Деякі з них вже пішли на славну смерть і тому сподіваємось, що з місця своєї небесної слави вони із щирою любов&rsquo;ю звертають свій погляд на дітей і товаришів боротьби і випрошують для них великої Божої допомоги&raquo;. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1956 року Папа Пій ХІІ видав Апостольського листа до всіх українських владик з нагоди 1000-річчя хрищення святої княгині Ольги. У листі підкреслюється страждання та руїна колись квітучої Східної Церкви, але, одночасно, він закликає до надії: &laquo;Не втрачайте віри: Бог не дозволить з Себе насміхатись&hellip; майте віру, повторюю&hellip; Іноді Він затримується, випробовуючи віру, стійкість, витривалість та терпеливість Своїх слуг, але Він добрий і справедливий, бачить ваші сльози і чує ваші молитви&hellip;&raquo;. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За Папи Пія XII було створено українську католицьку митрополію в Канаді й у США та екзархати у Великобританії і Австралії. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В архіві українського відділу Радіо Ватикану зберігається текст передачі з жовтня 1958 року під назвою &laquo;Папа Пій ХІІ і переслідувана Церква&raquo;. Ця передача закінчувалась словами: &laquo;Святої пам&rsquo;яті Пій ХІІ співчував з усіма переслідуваними, а, зокрема, з українцями, бо вони терплять найбільше і найдовше. Українці можуть бути певні, що в особі святої пам&rsquo;яті Папи Пія ХІІ вони мали найкращого оборонця перед світом&raquo;. </span></span></p>\r\n<p style=\"margin: 0pt; text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><a href=\"http://www.oecumene.radiovaticana.org/ucr/index.asp\" target=\"_blank\">http://www.oecumene.radiovaticana.org/ucr/index.asp</a></span></span></p>','Папа Пій ХІІ – захисник переслідуваної УГКЦ  ','uk',470,1450,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба  милосердя при церкві св. Миколая на Аскольдовій Могилі, зібравши  пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття,  ігри, іграшки дитячі та інші речі особистого вжитку.</span></span></p>','2012-04-03 16:42:18',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св. Миколая на Аскольдовій Могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, розподілила їх таким чином:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash; уділила потребуючим мирянам;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash;&nbsp; відправила гуманітарний вантаж Благодійному фонду &laquo;Сузір&rsquo;я&raquo; в с. Іванкові Бориспільського р-ну Київської обл. 06.03.2012 р. &ndash; 19 мішків + 7 коробів</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів:&nbsp; п. Олександра, п. Тетяни, п. Анатолія, п. Анни.</span></span></p>','ЗВІТ №4 про розподілення благодійних пожертвувань парафіян церкви св. Миколая на Аскольдовій Могилі  (06.03.2012 р.)','uk',2714,4197,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба  милосердя при церкві св.Миколая на Аскольдовій Могилі отримала  гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo; та відправила  медико-санітарним частинам жіночих виправних колоній, кожній у  кількості:</span></span></p>','2011-11-14 20:39:47',NULL,'N','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій Могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo; та відправила медико-санітарним частинам жіночих виправних колоній, кожній у кількості:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Одноразовий стоматологічний набір для санації ротової порожнини &ndash; 8 лотків;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Гель дезінфікуючий &ndash; 5 флаконів;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Термометр медичний &ndash; 2 шт.;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пакети гігієнічні &ndash; 20 шт.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp; </strong><strong>Чернігівській виправній колонії ЧВК-44 (м.Чернігів).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp; </strong><strong>Чорноморській виправній колоній ЧВК-74 (м.Одеса).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>3.&nbsp;&nbsp;&nbsp; </strong><strong>Качанівській виправній колонії КВК-54 (м.Харків).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>4.&nbsp;&nbsp;&nbsp; </strong><strong>Збаразькій виправній колонії ЗВК-63 (Тернопільська обл.)</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>5.&nbsp;&nbsp;&nbsp; </strong><strong>Уманській виправній колонії УВК-129 (Черкаська обл.)</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6.&nbsp;&nbsp;&nbsp; </strong><strong>Дніпродзержинській виправній колонії ДВК-34&nbsp; </strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>(Дніпропетровська обл.)</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>7.&nbsp;&nbsp;&nbsp; </strong><strong>Орджонікідзевському виправному центру ОВЦ-79 </strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>(Дніпропетровська обл.)</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8.&nbsp;&nbsp;&nbsp; </strong><strong>Сніжнянській виправній колонії СВК-127 (Донецька обл.)</strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Ганни, п.Луки та п.Анни.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Посилки доставлені до поштового відділення о.І.Онишкевичем.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В підсумку відправлено <strong>28.10.2011 р</strong>. гуманітарний вантаж&nbsp; вагою <strong>15,2 кг</strong>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">поштових коробок &ndash; <strong>64 грн.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; <strong>76 грн. 32 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від&nbsp; 28.10.2011 р. &mdash; 140 грн. 32 коп.</strong></span></span></p>','ЗВІТ №23 про надання гуманітарної допомоги медико-санітарним частинам жіночих виправних колоній України (02.11.2011 р.)','uk',2605,4088,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба  милосердя при церкві св.Миколая на Аскольдовій Могилі отримала  гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo; та відправила  медико-санітарним частинам жіночих виправних колоній, кожній у  кількості:</span></span></p>','2011-11-14 20:39:47',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій Могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo; та відправила медико-санітарним частинам жіночих виправних колоній, кожній у кількості:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Одноразовий стоматологічний набір для санації ротової порожнини &ndash; 8 лотків;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Гель дезінфікуючий &ndash; 5 флаконів;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Термометр медичний &ndash; 2 шт.;</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Пакети гігієнічні &ndash; 20 шт.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>1.&nbsp;&nbsp;&nbsp; </strong><strong>Чернігівській виправній колонії ЧВК-44 (м.Чернігів).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>2.&nbsp;&nbsp;&nbsp; </strong><strong>Чорноморській виправній колоній ЧВК-74 (м.Одеса).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>3.&nbsp;&nbsp;&nbsp; </strong><strong>Качанівській виправній колонії КВК-54 (м.Харків).</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>4.&nbsp;&nbsp;&nbsp; </strong><strong>Збаразькій виправній колонії ЗВК-63 (Тернопільська обл.)</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>5.&nbsp;&nbsp;&nbsp; </strong><strong>Уманській виправній колонії УВК-129 (Черкаська обл.)</strong></span></span></li>\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>6.&nbsp;&nbsp;&nbsp; </strong><strong>Дніпродзержинській виправній колонії ДВК-34&nbsp; </strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>(Дніпропетровська обл.)</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>7.&nbsp;&nbsp;&nbsp; </strong><strong>Орджонікідзевському виправному центру ОВЦ-79 </strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>(Дніпропетровська обл.)</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>8.&nbsp;&nbsp;&nbsp; </strong><strong>Сніжнянській виправній колонії СВК-127 (Донецька обл.)</strong></span></span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Ганни, п.Луки та п.Анни.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Посилки доставлені до поштового відділення о.І.Онишкевичем.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В підсумку відправлено <strong>28.10.2011 р</strong>. гуманітарний вантаж&nbsp; вагою <strong>15,2 кг</strong>.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вартість:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">поштових коробок &ndash; <strong>64 грн.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">пересилання &mdash; <strong>76 грн. 32 коп.</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Загальна сума витрат від&nbsp; 28.10.2011 р. &mdash; 140 грн. 32 коп.</strong></span></span></p>','ЗВІТ №23 про надання гуманітарної допомоги медико-санітарним частинам жіночих виправних колоній України (02.11.2011 р.)','uk',2606,4089,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба  милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши  пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття,  іграшки дитячі та інші речі особистого вжитку, а також гуманітарну  допомогу від благодійного фонду &laquo;Олива&raquo;</span></span></p>','2012-05-01 00:43:50',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі, зібравши пожертвувані парафіянами: одяг чоловічий, жіночий та дитячий, взуття, іграшки дитячі та інші речі особистого вжитку, а також гуманітарну допомогу від благодійного фонду &laquo;Олива&raquo;, розподілила їх таким чином:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash; уділила потребуючим мирянам;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&mdash;&nbsp; відправила гуманітарний вантаж Благодійному фонду &ldquo;Сузір\'я&rdquo; в с.Іванкові Бориспільського р-ну Київської обл. 10.04.2012 р. &ndash; 10 мішків + 8 коробів</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів:&nbsp; п. Михайла, п. Анни.</span></span></p>','ЗВІТ №7 про розподілення гуманітарної допомоги від благодійного фонду «Олива», а також благодійних пожертвувань парафіян церкви св. Миколая на Аскольдовій могилі  (10.04.2012 р.)','uk',2733,4216,0,'Любов Михайлюк'),
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба безпеки України на даний момент встановила прізвища 14191 людини - усім їм було винесено вироки в Києві і всі вони поховані в Биківні, чому знайдено документальні архівні підтвердження.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ми стверджуємо, що вибір місця під масові поховання в Биківні - не випадок, а сплановані дії&raquo;, - зазначив співробітник Галузевого державного архіву СБУ доктор історичних наук, професор Василь Даниленко.</span></span></p>\r\n<p>&nbsp;</p>','2009-05-17 23:50:31',NULL,'P','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>СБУ встановила імена 14 тисяч похованих у Биківні</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Про це у четвер напередодні державних заходів до Дня пам\'яті жертв політичних репресій, який відзначатиметься 17 травня, повідомили в рамках громадських слухань &laquo;Трагедія Биківні: як це було&raquo;, передає прес-центр СБУ.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба безпеки України на даний момент встановила прізвища 14191 людини - усім їм було винесено вироки в Києві і всі вони поховані в Биківні, чому знайдено документальні архівні підтвердження.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Ми стверджуємо, що вибір місця під масові поховання в Биківні - не випадок, а сплановані дії&raquo;, - зазначив співробітник Галузевого державного архіву СБУ доктор історичних наук, професор Василь Даниленко.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У 1936 році тут було створено спецоб\'єкт, який суворо охоронявся військами НКВС, а вже 20 березня 1937 року президією Київської міської ради о другій годині ночі було ухвалено таємну постанову про відведення землі для спеціальних потреб. Ця земля стала місцем поховань для розстріляних у Києві за рішенням судів і позасудових органів. Вироки виконувалися в підвалах приміщення Київського обласного управління НКВС (нині там розміщується Український інститут національної пам\'яті, вул. Липська, 16), звідки тіла вночі вивозили в Биківню. А перед війною заарештованих знищували безпосередньо біля виритих у лісі ям.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сьогодні виявлено 18 місць поховань жертв масових політичних репресій 1937-1941 років, зокрема місце, на якому згодом було побудовано Центральний універмаг у Хмельницькому, парк культури і відпочинку у Вінниці, 9-й кілометр Запорізького шосе, центральне міське кладовище в Сумах.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.dt.ua/</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Слово Президента України у Биківні у День пам\'яті жерт політичних репресій</strong><br /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> <img style=\"float: left;\" src=\"http://www.president.gov.ua/done_img/b/13811.jpg\" alt=\"\" />\r\n<p>Дорогий Український народе,</p>\r\n<p>Дорогі співвітчизники,</p>\r\n<p>Дорога жалобна громадо!</p>\r\n<p>Сьогодні Україна вшановує пам\'ять мільйонів жертв комуністичних репресій.</p>\r\n<p>Ми стоїмо на землі, де кожна травинка і кожне дерево росте з нашої крові.</p>\r\n<p>Це - Биківня. Це Биківнянський ліс. Нехай його голос сьогодні почує вся Україна.</p>\r\n<p>Ми прийшли сюди, щоб пом\'янути всіх, всіх замучених у сталінських тюрмах і концтаборах. Ми прийшли, щоб ніхто й ніколи не забув катастрофи, яку пережив наш народ у ХХ віці. У цьому найкривавішому в історії людства столітті українська нація стала заручником боротьби двох тоталітарних режимів - комуністичного й фашистського. Вони - співмірні за своєю людиноненависницькою суттю. Вони - тотожні за небаченими масштабами масових убивств.</p>\r\n<p>Досі ніхто не знає, скільки десятків тисяч наших співвітчизників лежать у Биківнянському лісі. Биківня, як і Бабин Яр, як Освенцім і Соловки, як Бухенвальд і Катинь - це страшні символи мучеництва невинних жертв тоталітарних терорів. Тут, у Биківні, Сталін і його звироднілі опричники убивали цвіт України і української нації. Десятки, десятки тисяч людей.</p>\r\n<p>Прощення немає і не буде.</p>\r\n<p>Тут поховані письменники Яків Савченко, Михайло Семенко, Микола Скуба, Вероніка Черняхівська-Ганжа, брат Остапа Вишні письменник Василь Чечвянський, фундатор Академії мистецтв, професор Київського художнього інституту Михайло Бойчук, скульптор Іван Падалка, академіки Олександр Асаткін, Євген Оппоков, Володимир Юринець, директор Київського медичного інституту Сергій Сапронов, викладачі кафедри української мови і літератури Київського педагогічного інституту професор Олексій Синявський та доцент Олексій Бондаренко, ієрархи Української Автокефальної Православної Церкви Кость Матюшенко та Володимир Самборовський, священик, професор Київської духовної академії, вчений із світовим іменем Олександр Глаголев. І тисячі, тисячі інших.</p>\r\n<p>Звідси, з Биківні йде незримий зв\'язок до всіх неполіченних кладовищ комуністичного терору на нашій землі. У цьому диявольському переплетінні - вся Україна. Обов\'язок нації - згадати всіх.</p>\r\n<p>Сталінська верхівка боялася й ненавиділа кожен народ, підкорений нею. Найбільший страх вона відчувала перед Україною. Знищенню підлягав наш дух. Знищенню підлягав кожен, запідозрений у найтяжчому зі злочинів - любові до України.</p>\r\n<p>Тому відновлення правди про політичні репресії і гідне вшанування їхніх жертв - це не просто символ. Це - головна ознака, що наша душа жива, що наше життя незнищенне, що наш народ переміг і зло, і смерть.</p>\r\n<p>За останні роки зроблено немало. За моїми дорученнями розсекречено архівні документи про український визвольний рух, політичні репресії та голодомори. У наших містах вже відкриваються центри, де кожен має доступ до актів комуністичного терору. Такі зали створено в 14 обласних центрах, зокрема в Києві, Одесі, Харкові, Сімферополі, Севастополі, Ужгороді, Миколаєві, Житомирі, Запоріжжі, Луганську, Вінниці, Сумах, Хмельницькому. Невдовзі вони будуть створені і запрацюють в усіх областях.</p>\r\n<p>Сьогодні я хочу висловити глибоку вдячність Службі безпеки України за роботу, завдяки якій в останній час вдалося встановити прізвища 14191 особи, похованої в Биківні. На сьогодні нам відомі імена близько 30 тисяч людей, які поховані, зариті, закопані під цими соснами. Вдалося знайти ще 18 великих таємних цвинтарів жертв репресій 1937-1941 років.</p>\r\n<p>Це - велика, національна справа.</p>\r\n<p>Ми говоримо про всіх. Розсекречено й опубліковано документи про злочинну депортацію кримських татар у 1944 році. Завтра кримськотатарським лідерам буде передано матеріали стосовно репресій в 20-30 роках.</p>\r\n<p>Ми доводимо правду до всього світу. Багатьом країнам світу ми сповістили про військовополонених, похованих на теренах України. Німеччині передано інформацію про репресованих німців з Донецької області. Вина їх полягала тільки в тому, що під час Голодомору вони зверталися до Німеччини за продовольчою допомогою.</p>\r\n<p>Робота триває. Робота має продовжуватися.</p>\r\n<p>Поруч із Службою безпеки я дякую Інститутові національної пам\'яті і всім причетним організаціям, які повертають нам правду, правду про нашу болючу історію.</p>\r\n<p>Не можу обійти увагою Соловки, які відвідав минулого року. Щороку українська делегація бере участь у вшануванні загиблих в урочищі Сандормох та на Великому Соловецькому острові. Тепер цій громадській ініціативі надається підтримка держави.</p>\r\n<p>Скажу й про інше.</p>\r\n<p>Україна має нарешті і остаточно очиститись від символів режиму, який знищив мільйони безневинних людей. Виправдання бути не може. Це - не частина нашої історії, як дехто цинічно хоче сказати. Це - частина комуністичної системи. Це - символи вбивства, збереження яких є блюзнірством перед полеглими.</p>\r\n<p>Упродовж 2007-2008 років в Україні демонтовано понад 400 пам\'ятників діячам комуністичного режиму - організаторам Голодомору та політичних репресій 1937-41 рр. Перейменовано понад 3 тисячі топографічних назв, що носили їхні імена. Але до завершення цієї роботи, безумовно, ще дуже далеко. Перед нами стоять великі завдання.</p>\r\n<p>Кожен раз, коли ми навесні приходимо в Биковнянський ліс, ми говоримо про розвиток заповідника &laquo;Биковнянські могили&raquo;. З кожним роком ми стаємо свідками того, як перевіряються проекти, мета яких - повернути нам пам\'ять, дати шану тим людям, які були закопані в 30-х роках в цю святу землю.</p>\r\n<p>Національний статус заповідника отримали &laquo;Биківнянські могили&raquo;. Споруджено офісне приміщення, встановлюються перші меморіальні частини того заповідника, над яким працює сьогодні місто, і віце-прем\'єр-міністр із гуманітарних питань Іван Васюник, міністр культури і багато-багато інших, включаючи, безумовно, Інститут національної пам\'яті, і цілу низку громадських організацій.</p>\r\n<p>Але, переконаний, ми і досі у великому боргу перед цією сторінкою нашої історії.</p>\r\n<p>Я не можу бути спокійний, доки не вирішено питання про гідний соціальний захист колишніх політичних в\'язнів та репресованих.</p>\r\n<p>У парламенті досі не розглянуто внесені мною законопроекти про правовий статус борців за незалежність України.</p>\r\n<p>Мене дуже часто обурює байдужість та відчуженість, яку демонструють наші політики й посадовці. Вони бояться цієї теми. Вони хотіли б владарювати в Україні без української культури, без української мови, без українців. Їхня мрія - це губернія, де б вони були безроздільними господарями.</p>\r\n<p>Як наслідок - ми є свідками спроб реваншу.</p>\r\n<p>Ті, хто заперечують Голодомор і репресії, ті, хто виправдовують Сталіна, ті, хто піднімають руку на наші святині і наших людей - приречені. Вони свідомо стають на бік однієї з найчорніших сил, які знала історія. Їхні чорні надії, глибоко переконаний, є безсилі.</p>\r\n<p>Нація відроджується.</p>\r\n<p>Великий український поет Василь Симоненко писав: &laquo;Можна прострелити мозок, що думку народить, думки ж - не вбить!&raquo;</p>\r\n<p>Сьогодні - істинно скорботний день. Водночас це - день торжества правди й справедливості.</p>\r\n<p>На руїнах радянської імперії постала Україна. Це - спільна і велика перемога всього Українського народу. Це - перемога нашого покоління, перемога тих, хто боровся за свободу Батьківщини, перемога тих, хто впав жертвою терору і репресій. Ми перемогли, і наша перемога - вічна.</p>\r\n<p>Вічна пам\'ять загиблим.</p>\r\n<p>Слава Українському народові!</p>\r\n<p>Слава Україні!</p>\r\n<p><img style=\"float: right;\" src=\"http://www.president.gov.ua/done_img/b/13812.jpg\" alt=\"\" /></p>\r\n<p>У День пам\'яті жертв політичних репресій Президент України Віктор Ющенко взяв участь у заходах із вшанування пам\'яті жертв політичних репресій на території Національного історико-меморіального заповідника &laquo;Биківнянські могили&raquo;.</p>\r\n<p>Разом із Президентом пам\'ять невинно убієнних вшанували Секретар РНБОУ Р.Богатирьова, віце-прем\'єр-міністр України І.Васюник, голова Київської облдержадміністрації В.Ульянченко, Київський міський голова Л.Черновецький, народні депутати України, представники громадських організацій репресованих, родичі загиблих, дослідники політичних репресій, представники політичних партій, громадськість, молодь.</p>\r\n<p>Президент поклав квіти до монумента Політв\'язня при в\'їзді до заповідника, а також до Пам\'ятного Хреста.</p>\r\n<p>Глава держави також взяв участь у скорботній ході до Пам\'ятного Хреста та виступив із промовою.</p>\r\n<p>У рамках заходу відбулася панахида за загиблими за участю духовенства українських церков УПЦ, УГКЦ, РКЦ.</p>\r\n<p>На завершення офіційної частини заходу його учасники вшанували пам\'ять загиблих співвітчизників хвилиною мовчання.</p>\r\n<p>Президент також оглянув історико-документальну виставку &laquo;Зламані долі: комуністичний терор в Україні 1920-1950-х років&raquo;, підготовлену Фондом &laquo;Україна 3000&raquo; спільно з Галузевим архівом Служби безпеки України та Київською міською організацією Всеукраїнського товариства &laquo;Меморіал&raquo; ім. Василя Стуса.</p>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">ДОВІДКА</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У Биківні знаходиться найбільше в Україні місце поховання жертв масових політичних репресій. Тут був об\'єкт спеціального призначення НКВД, де у кінці 30-х - в 40-х роках відбувалися масові захоронення розстріляних та закатованих. Встановлено, що в районі села Биківня закопували приречених до розстрілу в позасудовому порядку, вирок стосовно яких виконано у місті Києві. За даними науковців, у Биківнянському лісі покоїться близько 100 тис. репресованих комуністичною владою. На сьогодні за виявленими і опрацьованими співробітниками СБУ документами встановлено прізвища 14191 особи.<br /> Ці могили було розкрито на огляд громадськості під час окупації німецькою владою з метою викриття злочинів більшовицького режиму. Однак після закінчення війни радянською владою всю інформацію було знову засекречено і проведено &laquo;власні розслідування&raquo;: три державні комісії 1945, 1971, 1987 рр. оголосили про те, що у Биківні поховані жертви фашизму. При цьому в ході діяльності останньої з них було виявлено та знищено багато документів. Лише четверта комісія у липні 1989 року оголосила, що у Биківні поховані жертви сталінських репресій.<br /> Биківня є лише одним з багатьох поховань жертв комуністичного терору. Криваву географію виявлених місць поховань описано 28 лютого 1989 р. у таємному листі Голови КДБ УРСР М. Голушка Першому секретареві ЦК Компартії України В. Щербицькому: Бердичів - район єврейського цвинтаря; Вінниця - парк культури і відпочинку, колишній міський цвинтар, сад по вулиці Підлісній; Дніпропетровськ - 9-й кілометр Запорізького шосе; Донецьк - район шахти №11 Кіровського району; Івано-Франківськ - урочище &laquo;Дем\'янів Лаз&raquo;; Кам\'янець-Подільський - русько-фільварочний цвинтар та поблизу села Кам\'янка; Луганськ - окраїна міста, урочище &laquo;Суча Балка&raquo;; Одеса - другий християнський цвинтар; Полтава - район піщаного кар\'єру поблизу села Макухівка, урочище &laquo;Триби&raquo;; Суми - центральне міське кладовище; Умань - братська могила; Харків - лісопаркова зона; Хмельницький - один з об\'єктів колишнього обласного управління НКВД, де наприкінці 1960-х років побудовано Центральний універмаг; Черкаси - старий єврейський цвинтар по вулиці Т. Шевченка; Чернігів - лісовий масив в районі села Холявіно.<br /> Розстріли й масові безіменні поховання відбувалися в 30-40-х роках у Білій Церкві, Маріуполі Донецької області, Ніжині Чернігівської області, Золочеві та Стрию Львівської області, Чорткові, Бережанах, Кременці Тернопільської області, інших місцях.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.president.gov.ua/<br /></span></span></p>','Пам’яті жертв репресій','uk',1495,2551,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo;: медичний одяг, лікарняну білизну, спідню білизну чоловічу, жіночу та дитячу, а також одяг чоловічий, жіночий та дитячий, ігри (пазли) та іграшки дитячі.</span></span></p>','2011-05-05 12:54:26',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo;: медичний одяг, лікарняну білизну, спідню білизну чоловічу, жіночу та дитячу, а також одяг чоловічий, жіночий та дитячий, ігри (пазли) та іграшки дитячі.</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Отримана допомога, а також одяг, взуття та інші речі особистого вжитку, пожертвувані парафіянами, уділені потребуючим мирянам, а також розподілені наступним чином:</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\">Багатодітна родина п.Елли М. в с.Іванкові Бориспільського р-ну Київської обл. та Благодійний фонд &ldquo;Сузір\'я&rdquo; </span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15.03.2011 р. &mdash; 10 мішків (білизна та одяг чоловічі, жіночі та дитячі, &nbsp; взуття, іграшки дитячі, духовна література &ldquo;Бог говорить до своїх дітей&rdquo;  &mdash; 30 прим.).</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.04.2011 р. &mdash; 14 мішків (білизна та одяг чоловічі, жіночі та дитячі, &nbsp; взуття, ігри та іграшки дитячі, візок інвалідний, духовна література &ldquo;Бог  говорить до своїх дітей&rdquo; &mdash; 10 прим.)</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\">Терапевтичне відділення Київської міської клінічної лікарні №11 &mdash; 2 мішки (медичний одяг, білизна).</span></li>\r\n<li><span style=\"font-size: small;\">Нейрохірургічне відділення Київської міської клінічної лікарні швидкої медичної допомоги &mdash; 2 мішки (медичний одяг, білизна).</span></li>\r\n<li><span style=\"font-size: small;\">Київське обласне психіатрично-наркологічне медичне об\'єднання (смт Глеваха)</span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15.03.2011 р. &mdash; 6 мішків (медичний одяг, білизна);</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 08.04.2011 р. &mdash; 6 мішків (білизна та одяг чоловічі, жіночі).</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\">Київська дитяча міська клінічна лікарня №1, вул.Богатирська (через капелана каплиці прп.Агапіта о.Віталія Воєцу) &mdash; білизна для дітей та немовлят, по 100 штук різних найменувань (трусики, футболки, розпашонки), а також іграшки дитячі.</span></li>\r\n<li><span style=\"font-size: small;\">Благодійний фонд &ldquo;Карітас-Київ&rdquo;</span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; медичний одяг &mdash; 5 мішків;</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; білизна &mdash; 2 мішки.</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\">Медико-санітарна частина Чернігівської виправної колонії &mdash; медичні куртки (12 шт.)</span></li>\r\n<li><span style=\"font-size: small;\">Медико-санітарна частина Чорноморської виправної колонії (м.Одеса) &mdash; медичні куртки (12 шт.)</span></li>\r\n<li><span style=\"font-size: small;\">Медико-санітарна частина Качанівської виправної колонії (м.Харків) &mdash; медичні куртки (12 шт.)</span></li>\r\n</ol>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">10. &nbsp;Медико-санітарна частина Надержищинської виправної колонії (Полтавська обл.) &mdash; медичні куртки (12 шт.)</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">11. &nbsp;Медико-санітарна частина Збаразької виправної колонії (Тернопільська обл.) &mdash; медичні куртки (12 шт.)</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">12. &nbsp;Медико-санітарна частина Дніпродзержинської виправної колонії (Дніпропетровська обл.) &mdash; медичні куртки (12 шт.)</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">13. &nbsp;Медико-санітарна частина Уманської виправної колонії &mdash; медичні куртки (12 шт.)</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">14. &nbsp;Дитячий будинок інтернатного типу в м. Прилуки&nbsp; (вул.Богунська, 1-А) Чернігівської обл. &mdash; дитяча білизна (футболки &mdash; 30 шт., трусики &mdash; 30 шт.), духовна література &ldquo;Бог говорить до своїх дітей&rdquo; &mdash; 10 прим.</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: small;\"> </span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Марти, п.Надії, п.Олександра, п.Тетяни, п.Анни, п.Ірини.</span></p>','ЗВІТ №6 про розподілення гуманітарної допомоги, отриманої від Благодійного фонду “Олива” ','uk',2451,3934,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служба милосердя при церкві св.Миколая на Аскольдовій могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo;: медичний одяг, лікарняну білизну, спідню білизну чоловічу, жіночу та дитячу, а також одяг чоловічий, жіночий та дитячий.</span></span></p>','2011-08-05 16:35:07',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Служба милосердя при церкві св.Миколая на Аскольдовій могилі отримала гуманітарну допомогу від Благодійного фонду &ldquo;Олива&rdquo;: медичний одяг, лікарняну білизну, спідню білизну чоловічу, жіночу та дитячу, а також одяг чоловічий, жіночий та дитячий.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Отримана допомога, а також одяг, взуття, ігри, іграшки дитячі та інші речі особистого вжитку, пожертвувані парафіянами, уділені потребуючим мирянам, а також розподілені наступним чином:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>13.07.2011 р.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Багатодітна родина п.Елли М. в с.Іванкові Бориспільського р-ну Київської обл. та Благодійний фонд &ldquo;Сузір\'я&rdquo; </span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - білизна та одяг чоловічі, жіночі та дитячі, взуття, ігри та іграшки дитячі &mdash; 10 мішків та 14 коробів.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Парафія св.Великомученика Юрія, м.Васильків</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; одяг чоловічий, жіночий та дитячий &mdash; 4 короби;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;шампунь &mdash; 1 коробка.</span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Стрийська епархія УГКЦ </span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- білизна чоловіча й жіноча &mdash; 4 мішки;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- одяг чоловічий, жіночий, дитячий &mdash; 2 короби;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- журнали &ldquo;Прочанин миру та надії&rdquo; &mdash; 30 штук;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- дитяча література.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>20.07.2011 р.</strong></span></span></p>\r\n<ol style=\"text-align: justify;\">\r\n<li><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Київське обласне психіатрично-наркологічне медичне об\'єднання (смт Глеваха)</span></span></li>\r\n</ol>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- медичний одяг та білизна &mdash; 3 мішки;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- одяг для хворих чоловічий та жіночий &mdash; 2 короби і 3 мішки.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Робота виконана силами волонтерів: п.Марти, п.Сергія, п.Владислава.</span></span></p>','ЗВІТ №15 про розподілення гуманітарної допомоги, отриманої від Благодійного фонду «Олива»','uk',2542,4025,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служіння дітям Божого Милосердя поєднує в собі духовні настанови катехита Орести Цибрівської. Діти збираються три рази на тиждень в церковній хатині, разом з вихователями Дмитром та Світланою Засульські, Анною Онисько. Петриківський розпис провадить Ганна Шостак, бісероплетіння, вишивка та в&rsquo;язання &ndash; Галина Заверуха. Творчі служіння для приготування тематичних виступів &ndash; п. Леся та п. Руслана. Смачні обіди для дітей та тим, хто звертається в потребі &ndash; Марія Бортнічук, випічка булочок для дітей та піца, а також гостини &ndash; Ніна Ніколаєнко.</span></span></p>','2011-05-27 01:23:04',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>ФІНАНСОВИЙ ЗВІТ № 1</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Діти Божого Милосердя&raquo;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Кухня-Карітас&raquo;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em>в період з 01. 01. 2011 р. Б. &ndash;&nbsp; 01. 05. 2011 р. Б.</em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&laquo;Потрібно оцінити Божі дари і тих людей, </strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>яких Він нам дав, щоб ми могли обдаровувати їх любов&rsquo;</strong><strong>ю,</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>&nbsp;ділитись з ними хлібом і життям&hellip;&raquo;</strong></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Служіння дітям Божого Милосердя поєднує в собі духовні настанови катехита Орести Цибрівської. Діти збираються три рази на тиждень в церковній хатині, разом з вихователями Дмитром та Світланою Засульські, Анною Онисько. Петриківський розпис провадить Ганна Шостак, бісероплетіння, вишивка та в&rsquo;язання &ndash; Галина Заверуха. Творчі служіння для приготування тематичних виступів &ndash; п. Леся та п. Руслана. Смачні обіди для дітей та тим, хто звертається в потребі &ndash; Марія Бортнічук, випічка булочок для дітей та піца, а також гостини &ndash; Ніна Ніколаєнко. До вашої уваги подаємо графік зустрічей:</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">п&rsquo;ятниця з 17. 00 год.,</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">субота з 12. 00 год.,</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">неділя&nbsp; після третьої Служби Божої, з 14. 00 год.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В зимовий період діти з п. Лесею вивчали колядки, а в лютому мали відпочинок у Карпатах с. Мигово, який щороку жертвує пан Сергій, на початку березня, до дня пам&rsquo;яті Романа Шухевича&nbsp; а також до дня&nbsp; народження Тараса Григоровича Шевченка пані Леся готовила з дітьми&nbsp; концерт, потім бажаючі відправились на екскурсію в с. Моринці, де народився наш Кобзар.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">В часі приготування до Великодня кожній дитині було придбано кошики та все необхідне для їх освячення. (матеріал можна прочитати на сайті</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><em><span style=\"text-decoration: underline;\">ttp://oranta.org/index.php?option=com_content&amp;view=article&amp;id=740:pashalny-koshyk-dlja-dite-bozhogo-myloserdja&amp;catid=25:parafija-cat&amp;Itemid=27).</span></em></span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Дорогі брати та сестри, подаємо до ваші уваги фінансовий звіт про використані кошти, які надходять з пожертв скриньки Святого Миколая <strong>і Зарваницької&nbsp; Богородиці </strong>та всіх людей доброї волі.</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> </span></span></p>\r\n<table style=\"width: 672px; text-align: justify;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" width=\"49\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">місяць</span></span></p>\r\n</td>\r\n<td colspan=\"4\" width=\"296\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">обіди</span></span></p>\r\n</td>\r\n<td rowspan=\"2\" width=\"56\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">проїзд</span></span></p>\r\n</td>\r\n<td rowspan=\"2\" width=\"68\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">екскурсії та свята, подарунки, матер.доп. викладачам</span></span></p>\r\n</td>\r\n<td rowspan=\"2\" width=\"89\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">допомога вязням</span></span></p>\r\n</td>\r\n<td rowspan=\"2\" width=\"57\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">офірки</span></span></p>\r\n</td>\r\n<td rowspan=\"2\" width=\"56\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">інші</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"67\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">дні/порції</span></span></p>\r\n</td>\r\n<td width=\"71\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">продукти</span></span></p>\r\n</td>\r\n<td width=\"72\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">матеріальна допомога</span></span></p>\r\n</td>\r\n<td width=\"87\" valign=\"bottom\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">вартість порції</span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"49\">\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">січень</span></span></p>\r\n</td>\r\n<td width=\"67\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">14/210</span></span></p>\r\n</td>\r\n<td width=\"71\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1821,07</span></span></p>\r\n</td>\r\n<td width=\"72\">\r\n<p align=\"center\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1050</span></span></p>\r\n</td>\r\n<td width=\"87\">\r\n<p align=\"center\"><span sty