INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p style=\"text-align: right;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уважаемый Игорь Онишкевич и вся церковная громада! Дай Бог Вам здоровья, пусть верой и любовью Вас Бог обильно одарит! Большое спасибо за милость, понимание, поддержку. Я была очень удивленна. Ведь я не знала от кого, кто к нам так милосерден.</span></p>\r\n<p>&nbsp;</p>','2014-03-13 12:55:58',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи від ув\'язнених </span></em></strong><strong><em><span style=\"text-decoration: underline;\">&nbsp;(з них вперше звернулись </span></em></strong><strong><em><span style=\"text-decoration: underline;\">3</span></em></strong><strong><em><span style=\"text-decoration: underline;\">ос</span></em></strong><strong><em><span style=\"text-decoration: underline;\">о</span></em></strong><strong><em><span style=\"text-decoration: underline;\">би)</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Чернігівська виправна колонія ЧВК-44 (м.Чернігів) &mdash; </strong><strong>6</strong><strong> лист</strong><strong>ів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Кача</strong><strong>нівська виправна колонія </strong><strong>К</strong><strong>ВК-</strong><strong>5</strong><strong>4 (м.</strong><strong>Харк</strong><strong>ів) &mdash; </strong><strong>3</strong><strong> лист</strong><strong>и</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Збаразька виправна колонія ЗВК-63 (Тернопільська обл.) &ndash; 5 листів</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Селезнівська виправна колонія СВК-143 (Луганська обл.) </strong><strong>&mdash;</strong><strong> 2 листи</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Ч</strong><strong>о</strong><strong>рн</strong><strong>омор</strong><strong>ська виправна колонія ЧВК-</strong><strong>7</strong><strong>4 (м.</strong><strong>Одеса</strong><strong>) &mdash; </strong><strong>1</strong><strong> лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Орджонікідзевський виправний центр ОВЦ-79 (Дніпропетр. обл.) </strong><strong>&mdash;</strong><strong> 1 лист</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Подяка за посилки для дітей &ndash;12 листів</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">&nbsp;</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em><span style=\"text-decoration: underline;\">Листи звільнених з ув</span></em></strong><strong><em><span style=\"text-decoration: underline;\">&rsquo;</span></em></strong><strong><em><span style=\"text-decoration: underline;\">язнення &ndash; 1 лист</span></em></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний отець Ігорь, Братья Сестрички! <strong>Вітаю Вас з Новим роком 2014. Бажаю щастя добра здоров&rsquo;я! З Новим роком!</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина А. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Слава Исусу Христу! <strong>Поздравляю Вас и Ваших всех прихожан с Рождеством Христовым! Да благословит Вас Господь Бог!</strong> &rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Етель Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... очень болею, перенесла 2 инсульта, инфаркт, которак обеих глаз и много разных заболеваний... хочу поздравить Вас с праздником. <strong>С Новым годом и с Рождеством Христовым!.. </strong>У меня никого нет, по возможности <strong>помогите пожалуста гр.200 и карточку таксофоновую... покушать:</strong> сала, колбаски, конфет, свежих овощей, сильно пониженый белоглобин, вафли, печенье если можно сахар, какао... <strong>пожалуста крем БОРО-НОРМ и для лица календула алоевый, шампунь, линор для стирки... и если сможите часы ручные</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Раїса Ч. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасиба большое, что беспокоитесь обо мне и моих детках</strong>... очень Вас прошу <strong>вышлите хоть две таксофонные карточки по 24 минуты</strong>, что бы я могла поговорить со своими детками&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена Т. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Обращаюсь к Вам с огромной просьбой, так как на старости лет осталась в одиночестве. И совершила большой грех, наверное Господь меня и наказал,<strong> у меня вторая группа инвалидности, почти ничего невижу</strong>. Прошу людей, чтобы написали письмо. <strong>Высылаю Вам рецепт, будьте так добры, вышлите пожалуста эти лекарства. </strong>Я Вам за ранее очень благодарна&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Зінаїда Б. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... мне 25 лет. Отец Игорь <strong>помогите пожалуйста конвертами, мою дочь В</strong><strong>икторию забрали в детский дом не с колонии а на свободе и где она я не знаю,</strong> я подаю запрос в свой город в райисполком жду ответа, вдруг прийдется ещё куда то подавать, да и вообще ей написать как только узнаю адрес, в нашем магазине нет конвертов и марок тоже нету... Моей дочери в апреле будет 5 лет&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія К. (Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Маленький Ісусик не спить, не дрімає. Своїми руками весь світ обнімає. І Вашу хатину і Вашу родину і всю Україну ХРИСТОС СЯ РОЖДАЄ!</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Дорогий о.Ігор та Ваша церковна громада, вітаю Вас з Різдвом Христовим&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Ірина В. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Дорогий о.Игор и вся церква вітаю Вас з святом Різдвом Христовим. Бажаю Вам світлих днів і сонячних ранків і чистих помислів і вдячність людей за Вашу допомогу, любов до ближнього. Щиро дякую Вам низький уклін</strong></span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слава Христу!!!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Щиро дякую за Вашого листа і за те що незабуваєте мене. <strong>15.12.13р. в мене був день народження я його ніколи не святкувала навіть і дома тому що в цей день померла моя мама коли мене народила, а їй було 29 років і я її ніколи небачила</strong> я росла до трьох років в дитячому будинку тоді мене забрала мачуха але і там був не мед батько пив і мачуха теж <strong>з дев&rsquo;яти років я вчилась заробляти собі гроші, </strong>щоб купити собі зошити, книжки, портфель в нас в селі копали торф і я ходила перевертати торф і заробляла в день 3 рублі це були великі гроші для мене <strong>в мене не було іграшок тоді ходила обривати хміль і за місяць я получила 20 рублів і купила собі ляльку за 14 рублів яка говорила Ма-ма </strong>я така була рада і мені жалко зараз молодь як вони гинуть вони не хочуть трудитись ці комютори і телевізори роблять їх серця темними&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Тетяна С. (Качанівська ВК, м.Харків)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Вы просите, чтоб я написала о себе. <strong>Сама я сирота, воспитывали меня чужие люди </strong>у меня никого нет. Закончила школу, училище, вышла замуж и уехала из деревни в горд Феодосию. <strong>С мужем прожила 5 лет потом разошлась, я после развода начала сильно выпивать, вот так я и попала в эти места</strong>, конечно я сама виновата но если бы была какая то поддержка может ничиго и не произошло. Я надеюсь что это будет последний раз. <strong>Поздравляю Вас всех с наступающими праздниками&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Галина І. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Слава Ісусу Христу! <strong>Вітаю Вас з Різдвом Христовим! Нехай новонароджений Богомладенець благословляє Вас і дарує масу духовної радості і спасіння.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спасибі Вам всім дорогі хто мені пише ці гарні листи. Запитуєте чи є в нас бібліотека? Звичайно, є дуже хороша література і Кобзар маємо. І також література на теми духовної тематики, кому цікаво, то є що почитати, головне бажання... Мою одну ікону відправили в Київ на конкурс творчості. Хоча я точно неможу сказати де буде відбуватись цей конкурс... Я незнаю як получився вірш, але це прийшло мені на думку сьогодні вночі. Довго думала, згадувала своє дитинство. Свою любиму бабусю (покійну) яка щонеділі водила мене за ручку до церкви і як було приємно і добре тоді.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Як далеко лишилось дитинство,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спогад про це навертає сльозу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молюсь до Тебе Господи щогодинно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сама себе простити не можу.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А час летить неумолимо,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І вже ми виросли давно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І радості в маленькому забули</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Нам все не так, нам всеодно.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ми розучились розуміти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Причину радості в усьому</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І ми уже давно не діти</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І хто зарадить горю цьому.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уже не в радість нам погода,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І без радості спливає час,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І сіро все, і все убого</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">І зачерствіла душа у нас.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Але надію маю і молю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бо Ти є наш Господь Всевишній,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Молюсь щоночі, плачу і терплю,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прости мене по Милості Своїй Спаситель!!!<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Любов С. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Вітаю Вас з Різдвом Христовим і Новим Роком!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою до Вас Ліля і всі жінки мого відділення!</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Письмо Отца</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.138:1</em> Ты можешь не знать Меня, но Я знаю всё о тебе...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Быт.1:27</em> Ты был создан по Моему подобию...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.138:14</em> Ты сотворен чудно и прекрасно...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.8:41-44</em> Меня представляют не таким, каков Я есть на самом деле, это потому что не знают Меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.4:16</em> Я не злой, Я всегда рядом, Я &ndash; совершенное выражение любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иакова 1:17</em> Каждый хороший подарок, который ты получаешь, это от Меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Матв.6:31-33</em> Потому что Я тот, кто обеспечивает тебя всем и выполняет все твои нужды...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иер.31:3</em> Потому что Я люблю тебя любовью вечной...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Втор.4:29</em> Если ты ищешь Меня всем сердцем, то обязательно найдешь...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Пс.36:4</em> Радуйся во Мне и Я исполню желания твоего сердца...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>2Кор.1:3-4</em> Я также Отец, который утешает тебя во всех горестях...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.17:26</em> Ибо Моя любовь открыта тебе в Иисусе...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.4:10</em> Его смерть была наивысшим выражением Моей любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>1Иоан.2:23</em> Если ты принимаешь подарок Иисуса, ты принимаешь меня...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Римл.8:38-39</em> И ничего больше не отделит тебя от Моей любви...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Еф.3:14-15</em> Я всегда был и буду Отцом...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Иоан.1:12:13</em> Мой вопрос: будешь Моим дитём?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я жду тебя...</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любящий Отец, Всемогущий Бог&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія П. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>Спасіба дорогі мої. Посилочку получила... Я Вам дуже благодарна за Вашу поміч.</strong> Да благословить Вас всіх і ваші сім&rsquo;ї Господь и хранить Вас, да воздасть Вам по доброті вашій... Привозили ікону &laquo;Молитви до Пресвятої Богородиці&raquo; на честь Її Чудотворної ікони Чернігівської &laquo;Дубинська&raquo;... Мої дорогі <strong>я 24 травня 2014 р.освобождаюся, я із дому буду із вами листуватись,</strong> я дуже бажаю із Вами встрітися в свої домівки і Вам дуже отблагодарити за ваше дуже добре відношення до нас потіряних. Скільки я тут горя зазнала за ціх 3 роки і скільки переплакала. <strong>Уже додому готовлю вещі но ще нима кофти верхньої і тапочки кімнатні 38р</strong>., а так необходиме все в чому доїхати є. Ще Пасху отпраздную тут і через місяць буду у свої рідні домівкі. <strong>Я молюся за вас всіх, за всіх людей, начальство, щоб був мир і спокій</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Віра Ц. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... <strong>дякую Ісусу Христу що Він дав мені спілкування із Вами</strong>... напишу про себе... <strong>Мама мене покинула ще маленькою, коло своєї мами, бо мама вела розпусний образ життя. Бабушка також полюбляла пити і мене сільська Рада відправили в школу-інтернат</strong>. <strong>Батька я свого незнала і досі незнаю хто він у мене є. </strong>В свої 18 років я вийшла замуж, но довго не прожила із першим чоловіком, він у мене помер, а другий брак тоже не склався, бо любив гуляти, друзів, частенько до мене прикладав руки.<strong> Маю я троє діточок но вони мені не пишуть і не допомагають, а я із свого боку не хочу їм себе нав</strong><strong>&rsquo;</strong><strong>язувати. Бо незнаю може в них немає і на кусок хліба</strong>, раніше я робила тут в зоні, то мала можливість купити карточку таксофону то розмовляла завжди із діточками. А тепер вже другий рік як я не працюю, по состоянію здоров&rsquo;я, в мене діагноз нозі лівій &laquo;коксатрос&raquo; третього ступеня. Хожу я із паличкою... Я до закривання під варту проживала в м.Калинівка Вінницької обл Зараз там проживають мої діточки самі без мене. Праювала я із молодих літ в колгоспі дояркою а потім колгосп розпався і приходилось робити на одного хазяїна в магазині. Про рідніх я немаю що написати бо їх вже немає в живих, осталась я одна із своїми дітками. Дуже важко перебувати в цих місцях коли тобі ніхто не пише і не допомагає. <strong>За мої 46 років даже немає нічого вспомнити хорошого</strong>... В мене бувають такі часи що немає даже чим помити голову а щоб тут в когось щось просити то потрібно повернути. <strong>Уже скоро буде 7 років як я сиджу (<em>термін 10 років</em>), але одно що не втрачаю надію і кожний день читаю молитви і прошу у Господа дай мені терпіння і здоров</strong><strong>&rsquo;</strong><strong>ячко і за всіх засуджених прошу благословення</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Олена К. (Збаразька ВК, Тернопільська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Несколько лет назад вы мне помогли как духовно так и материально. Да воздаст Вам Господь за добрые деяния!.. Уважаемый отец Игорь! <strong>Прошу Вас помочь женщине которая очень нуждается в помощи. Если у Вас есть такая возможность помогите ей самыми необходимыми в этих местах предметами гигиены, мыльными принадлежностями</strong>. С самого раннего возраста она осталась сиротой, какое-то время её воспитывала тётя. <strong>Она отчаянно нуждается в помощи, но не верит что кто-то ей может помочь.. А главное ей нужна не только материальная помощь но и духовная пища</strong>. В своё время благодаря верующим людям и их помощи я пришла к Вере и Богу. И выйдя из этих мест я очень хочу помогать людям таким как я , как Лилия заблудшим и потерявшим смысл в жизни. Большая к Вам просьба напишите ей ответ, <strong>очень хотелось бы что-бы она не замыкалась в себе а наоборот начала верить в хорошее и в то что с Господом намного легче и хорошо жить</strong>. Жить полноценно и радоваться каждому новому дню&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Лілія Г. (Мартинова Н.) (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Мне 41 год, есть у меня дочка Елена, ей 19 лет. Её отца убили, за 2 недели до её рождения. Мама &ndash; пенсионерка, папа в том году умер от инсульта, брат сидит. <strong>Я бывшая наркоманка. В 2000 году, отсидев за кражу, приехала домой, устроилась на работу в г.Новая Каховка. Всё в моей жизни наладилось, как говориться, но не на долго</strong>. Дьявол, &laquo;как рыкающий лев&raquo; наступал на пяты и вот я <strong>через 10 лет снова в тюрьме. Я осуждена на 7 лет, за преступление, которого не совершала</strong>. Я сошлась с мужчиной, который сильно стал избивать меня, я начала колоться, и вся жизнь дальнейшая покатилась по наклонной. Он сейчас на свободе, а я сижу в тюрьме, обещал помогать, но обманул... <strong>У меня сейчас большая проблема. Я лишилась передних зубов, так как в зонах их не ремонтируют, а вырывают,</strong> мне пришлось из-за боли в зубах, по чуть чуть вырвать. <strong>На свободе я поставила мост, продержался он 13 лет, а в этом году спал. Врач по месту насчитал за ремонт 2 тысячи 300 гривен. Для меня это очень большие деньги, мама и рада бы помочь, но не может. Мне сидеть ещё 3 года, кушать нечем</strong>, разговаривать, да улыбнуться даже неудобно. Многие пишут верующим, просят о помощи, вот и я решила написать. <strong>Если сможете, помогите чем сможете, буду очень благодарна</strong>&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Марина Ю. (Селезнівська ВК, Луганська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Поздравляю з святым Николаем. Братья сёстры <strong>миня дострочное освобождения отложылы на пивгода </strong>потому что непрышла прыпыска... Так значить Богови угодно... <strong>Прошу помощь потому что мне некому помочь. </strong>У меня всё позаканчивалось, вышлить пожалуста если сможите шампунь, мыло, зубна паста и счётка, очкы 4+, взуця 37 бо я геть боса и штук двое штанов, крем для рук бо сильно трискаюця а помазать нечим, ручку, титрадку, сухарыкив или бублыкив пожалуста если сможите баночку згущеного молока... Я багато чытаю все у нас у библиотеки есть</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Уж слышу я шум как бы множества вод</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И стон ожидания мира.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Готовся нивеста! Жених твой идёт</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И близок час брачного пира!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Гряди, мой Спаситель, отрада очей!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Готова войти я в обитель.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Омыта в пречистой крови я Твоей,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О Боже, мой Царь, мой Спаситель!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Забудь всё, чем мир твоё сердце мутил,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Все слёзы, печаль, ожиданья.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Люби, как один Он любить Тибя мог,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Страдай, на Исуса взирая.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прощай, как Спаситель твой дивный и Бог</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Простил, за тебя умирая.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И верь как поверил любви Он твоей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">О верь, что Он любит безмерно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Что выше и глубже всех гор и морей</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Любовь Его вечна и верна.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Призыв к покаянию</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">К водам живым поспешите</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сношей греха и забот,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В воду её погрузите &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Сердце отраду найдёт.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Поторопись! Можно ли ждать?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Можно ль спасеньем души рисковать?</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Руки Господь простирает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всякого хочет принять.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">К водам живым поспешите,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хоть вы покрыты виной.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там вас омоет Спаситель,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Там облечет белизной.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Слушай, покайся и верь!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Кто Божью слову внимает,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Входит в открытую дверь.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Воды живые чудесно</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всё оживляют кругом</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Если раскается грешник,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Небо ликует о том&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила В. (Орджонікідзевський ВЦ, Дніпропетровська обл.)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;</strong>Добрий день!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Отче Ігорю, спасибі Вам за Вашу доброту до моєї дочки Олени і її дітей. За допомогу, що підтримуєте піддаєте сили і надії на все найкраще.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Новим роком і Різдвом Христовим вітаю Вас і щоб наш Господь Спаситель допомагав Вам, а головне здоров&rsquo;я.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спасибі Вам всим дуже велике, з повагою Л.М.<strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Любов М. (Кіровоградська обл., Назар-14р., Роксана-11р., Роман-6р., Настя-5р., діти Олени Т., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Щиро дякую</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Надія С. (Харківська обл., Поліна-6,5р., дочка Олени П., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Я дуже Вам вдячна. Дякую Вам&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Лариса Ф. (Донецька обл., Аліна-2р., дочка Ольги Ш., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Уважаемый Игорь Онишкевич и вся церковная громада!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дай Бог Вам здоровья, пусть верой и любовью Вас Бог обильно одарит!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Большое спасибо за милость, понимание, поддержку. Я была очень удивленна. Ведь я не знала от кого, кто к нам так милосерден. Бог нам дал радость внука и мы живем для него и он не одинок. Господь Бог нас не оставляет и Он нам уже не один раз помог. Муж мой 10 лет на группе. Я тоже болею, часто лежу в больнице, мне поставили диагноз эпелепсия. От нервных стрессов. Все удевлялись почему я не делаю группу, что меня удерживает. А удерживал меня Господь Бог. И теперь я понимаю почему. Ведь Он заранее знал, что у нас будет внук, а если два человека на группе нам бы его не дали. Дочь моя лишена родительских прав. Она вела неправильный образ жизни, употребляла наркотики. Вернувшись домой беременной мы ей опять поверили, думали что станет матерью и все старое забудется. Она родила Лешу, побыла с ним пока не получила на него деньги, и ушла. Приходила несколько раз и это только чтобы опять нас обворовать. И опять Господь Бог нам помог, ее образ жизни не отразился на здоровье нашего внука. И мы благодарны Ему за здоровье Леши&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Людмила К. (Дн-петровська обл., Олексій-8р., син Наталії К., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановна Громада!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вашу посилку отримали за що дякуєм.&nbsp; Саша&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Олександр О. (Вінницька обл., 14р., син Раїси О., Чорноморська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Ув. Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">От имени всех деток и их родителей благодарим Вас за вашу доброту и заботу. Поздравляем Вас с Новым 2014 годом и желаем Вам здоровья и долголетия, успехов всегда и везде. Будьте счастливы.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; С ув.семья О. &rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Валентина О. (м.Запоріжжя, Владислав-12р., Вероніка-5р., діти Вікторії Г., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>&ldquo;Дуже дякуємо веселих свят!&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Марія Б. (Тернопільсь обл., Іванна-6,5р., Марія-4,5р., діти Любові Д., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний о.Ігор Онишкевич!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаю Вас і всю Вашу громаду з Новим роком та Різдвом Христовим!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Бажаю всього найкращого та всіх земних благ.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хай Бог допомагає Вам у всіх всіх Ваших справах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Світлана Л.&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Світлана Л. (Дніпропетровська обл., Євген-12р., син Алли Б., Чернігівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;<strong>Спасибо</strong><strong>&rdquo;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Тетяна С. (Харківська обл., Софія-3р., дочка Світлани С., Селезнівська ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;Шановний отец Ігор!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаємо Вас та усіх хто Вам допомагає з Новим 2014 роком! Хай здійсняться всі Ваші мрії, здоров&rsquo;я, радості та щастя, мирного неба та миру у Вашіх серцях та родинах. Хай Господь Вас береже та благословляє.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Велике Вам спасибі за те що Ви не забуваєте про нас, та за те що прикладаєте зусиль чинити угодну Богу справу. Щиро Вам дякуємо. Слава Господу за те що в Нього є такі служителя.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Будьте в мирі та благословінні. Ще раз велике Вам спасибі.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З повагою Віка &ndash; 11 років та Тетяна<strong>&rdquo;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><em>Херсонська обл., Тетяна С. (звільнена з Дніпродзержинської ВК)</em></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&ldquo;... Сразу когда приехала пошла в Церковь взяла свечки за здравие и за упокой зажгла помолилась. Теперь ходить буду всегда сейчас возьму пост среда и пятница до конца своей жизни. Перед выходом поклялась перед Богом что в своей жизни никогда не коснусь к спиртному... <strong>Я искренне уверовала уже 7 лет и так до конца своей жизни буду жить с Богом и людей учу и даже маленьких деток... Я Вам очень благодарна что все эти годы Вы были рядом, дай Бог всем здоровья</strong>. А сын Яша очень больной нет ни одного органа здорового. Помолитесь за Якова за исцеление я только уповаю на Господа&rdquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Світлана Ш. (звільнена з Чернігівської ВК)</em></span></p>','Звіт №1 про НАДХОДЖЕННЯ  ЛИСТІВ 14.01.2014 р. — 31 лист','uk',4139,5622,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><span lang=\"UK\"><img style=\"float: right;\" src=\"image,1785,200,DSC_0002.JPG\" alt=\"\" />Щонеділі у церковній хатині проводять катехитичне навчання парафіяльних дітей різного віку.</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"UK\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Діти збираються і юрбляться навколо своїх старших наставників, активно дискутують і висловлюють свої дитячі думки на запитання вчителів, пізнають християнських засади, вчаться вірі надії, любові.</span></span></span></p>','2008-12-15 21:00:37',NULL,'N','<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Фото: Ігора Андрійовича Савицького</span></span></p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"image,1785,200,DSC_0009.JPG\" alt=\"\" /><img style=\"float: right;\" src=\"image,1785,200,DSC_0008.JPG\" alt=\"\" /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img style=\"float: right;\" src=\"image,1785,200,DSC_0010.JPG\" alt=\"\" /><img style=\"float: left;\" src=\"image,1785,200,DSC_0006.JPG\" alt=\"\" width=\"200\" height=\"133\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><br /></span></span></p>','Катехитичні вчення у церковній хатинці','uk',805,1785,0,'Любов Михайлюк'),
(1,NULL,'<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Милостиню роби згідно з тим, що маєш;Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Товит 4. 8-9.</span></p>\r\n<p>&nbsp;</p>','2013-09-08 23:53:52',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Господи Боже, Спасителю наш милостивий. Ти все твориш і будуєш на спасіння роду людського. Прийми ж ці щирі пожертви, принесення Тобі, а цих жертводавців спаси і помилуй, нагороди їх Твоєю небесною благодаттю, і подай їм відпущення гріхів, і вчини їх учасниками Царства Твого.</em></strong></span></p>\r\n<table style=\"width: 480px; height: 1143px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>№ з/п</strong></span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Ж Е Р Т В О Д А В Ц І</strong></span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Пожертва на (для, до):</strong></span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяни</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шкільний портфелик</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяни</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шкільний портфелик</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Роїк</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Гресько</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олег, Віра, Анна та Богданка</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">6</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Сусла</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву та спасові яблука</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">7</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Іван та Галина</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шкільний портфелик</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">8</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Роїк</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потреби церкви</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">9</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">10</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Володимир</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Десятина на церкву</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">11</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяни</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">12</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Парафіяни</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Подяка Богові за поступлення Зореслави на бюджет</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">13</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ореста</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Видавництво книги</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">14</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Андрій та Світлана</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Спасові яблука</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">15</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Родина Олійник</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Шкільний портфелик</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">16</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олександр та Зоя</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">17</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Аскольд</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дзвіницю</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">18</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дмитро та Світлана</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Подяка Богу за народження донечки</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">19</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ольга</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">20</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Марія Шпак</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям-сиротам до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">21</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Андрій (Сіті транссервіс)</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Потребуючим дітям до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олександру, Володимиру та Михайлу</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Церкву (посвячення автобуса)</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">23</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олег та Лариса</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">24</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Олександр та Анна</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Дітям до школи</span></td>\r\n</tr>\r\n<tr>\r\n<td width=\"47\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">25</span></td>\r\n<td width=\"350\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Марія</span></td>\r\n<td width=\"321\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Подяка Богу за дар уродин</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><em>Милостиню роби згідно з тим, що маєш;Коли у тебе достатків повно, Роби з них, а коли мало маєш, то не бійся з того дати. Бо так готуєш собі добрий скарб На день потреби. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em></strong><strong>Товит 4. 8-9.</strong></span></p>','ПОЖЕРТВИ ПАРАФІЯН ЦЕРКВИ СВ. МИКОЛАЯ ЧУДОТВОРЦЯ НА АСКОЛЬДОВІЙ МОГИЛІ У СЕРПНІ 2013 Р. Б.','uk',3578,5061,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усю громаду Церкви Миколая Чудотворця на Оскольдовій могилі кожного, хто приймає участь у служінні Господу, я дякую всім серцем своїм, всією душою! Бажаю Вам завжди памятати, де і у Кого знаходиться джерело усього, що нам необхідно: радості, любові, вірності, сил, відваги, подяки та мудрості.</span></p>\r\n<p>&nbsp;</p>','2014-03-13 23:08:24',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вітаю Вас усіх, браття і сестри у Господі Іісусі Христі найкращими побажаннями у Новому році! <strong>Бажаю Вам завжди памятати, де і у Кого знаходиться джерело усього, що нам необхідно: радості, любові, вірності, сил, відваги, подяки та мудрості.</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Усю громаду Церкви Миколая Чудотворця на Оскольдовій могилі кожного, хто приймає участь у служінні Господу, я дякую всім серцем своїм, всією душою!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">22 січня у нас на колонії відбувся конкурс віршів, де я теж приймала участь. На конкурсі були присутні гості з Чернігівського телебачення! Мені довелося бути переможцем на цьому конкурсіі увечері я вже дивилася себе по телебаченню. Дуже цікаво! Слава Господу за все! На конкурсі я читала вірш, який присвятила свому синочку. Надсилаю його вам, щоб поділитися своєю часткою душі.Дякую господу за кожного із вас! Для мене велика честь і нагорода мати спілкування з вами. Доброго здоров&rsquo;я вам!!! Христос народився! Славімо Його!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">З Христовою любов\'ю, вдячним серцем і побажаннями усіх благ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">до вас сестра Алла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">27.01.14 рік.</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">***</span></p>\r\n<p style=\"text-align: justify;\" align=\"center\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong>Посвящается сыну</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Всякий раз, когда сына фотографию</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">беру,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я в глаза его прекрасные смотрю</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И вижу в них печаль и грусть...,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Которую разлука нам</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; внезапно принесла</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; и боль в глазах.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я прошу, ты прости меня, сынок,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; за эту боль</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И за то, что я сегодня не с тобой,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">За тяжкий грех, который совершила я</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; на своем жизненном пути,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; сынок, прости!..</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Не знал ты горя, не знал ты слёз,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Пока была я рядом и ты в нежности</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; рос,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Но я споткнулась, ушла с пути,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Который Бог нам предназначил</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; жить в надежде, вере и</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; любви.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Знаю я, нет прощенья мне за прошлые</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; года</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И расплата за грехи столь велика,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Прости, сынок, что оступившись</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; я очаг семейный</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; наш не сберегла,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; накрыла тьма.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я вернусь и совсем другою станет</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; наша жизнь,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Только ты, прошу, сынок, меня</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">дождись!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И от меня не отвернись,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; я умоляю, ты держись!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Меня дождись!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Хочу я крикнуть на целый мир,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Мне без прощенья твоего</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; совсем свет не мил.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">На сердце раны горят огнём,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; прости, сынок, за то, что мы</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; сейчас с тобой</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; не можем быть вдвоём.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">... Я с надеждою буду ждать ответ,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Только не спеши сказать мне: &laquo;Нет!&raquo;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Я не хочу смотреть на мир с</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; открытыми глазами,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Когда в нём нет тебя и</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Связано в нём всё не с нами,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Когда толпа безумных дураков,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Как волки, ищет беспредела,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Им наплевать на честь и совесть, -</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; вот в чём дело.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">... Мне трудно говорить,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; но как могу молчать я,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Когда мне хочется кричать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; на целый белый свет &ndash;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Когда же вновь тебя смогу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; обнять я,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Когда закончится весь этот бред??!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Прости!!!... Хотела я сказать</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; совсем другое,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Но сердце не желает и не хочет</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; понимать,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ради чего оно выносит столько боли??!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Зачем его заставили опять страдать??!</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В надежде лишь на время жду</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ответа,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Тяжёл мой крест, но я его несу</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Лишь для того, чтоб стало</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; больше света</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В несправедливом окружающем миру.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Ты только не сдавайся,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Я с тобою рядом,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Вдвоём нам легче боль преодолеть,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">Раз так угодно Богу, значит, надо</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">В смирении нам всё перетерпеть.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Теперь глаза мои</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; откроются при встрече</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">И в них появится, как прежде,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Жизнь и блеск,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">А без тебя они закрыты бесконечно,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ведь всё пустое,</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Когда рядом тебя нет!</span></p>','Лист2 ув\'язненої Алли Б., яка відбуває покарання в Чернігівській виправній колонії ЧВК-44','uk',4142,5625,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://umoloda.kiev.ua/img/content/i31/31324.gif\" alt=\"Скульптор у Кам&rsquo;янці&ndash;Подільському ліпить бюст Івана Малковича. (Фото з архіву Івана Малковича.)\" width=\"188\" height=\"141\" /><span lang=\"RU\">\r\n<p>10 травня Івану Малковичу &mdash; поету і видавцю, якщо говорити  про видимі іпостасі &mdash; виповнюється 50 років. У нього дуже цікава  біографія ( це без екстремальних суми і тюрми), з якої можна &laquo;наробити&raquo; з  десяток сюжетів, представити його життя і так, і сяк: і героїчно, і  лірично, і драматично, і пафосно. У 19 на Всеукраїнському літературному  семінарі в Ірпені таємним голосуванням двох&ndash;трьох сотень літераторів  Малковича обрали &laquo;Найкращим молодим поетом&raquo;, у 23 вийшла його перша  поетична збірка &laquo;Білий камінь&raquo;, в рецензії на яку сама Ліна Костенко  назвала поета &laquo;найніжнішою скрипкою України&raquo;. Кость Москалець написав  про його поезію: &laquo;Якби Маленький Принц із казки Екзюпері писав вірші, то  вони були би точнісінько такими за настроєм і тоном, як ранні вірші  Малковича&raquo;. У 31 він заснував видавництво &laquo;А&ndash;Ба&ndash;Ба&ndash;Га&ndash;Ла&ndash;Ма&ndash;ГА&raquo; і з тих  пір 95% населення сприймає його як &laquo;нашого Оле Лукойє&raquo;.</p>\r\n</span></span></span></p>','2011-05-08 15:37:22',NULL,'P','<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: right;\" src=\"http://umoloda.kiev.ua/img/content/i31/31324.gif\" alt=\"Скульптор у Кам&rsquo;янці&ndash;Подільському ліпить бюст Івана Малковича. (Фото з архіву Івана Малковича.)\" /><span lang=\"RU\">\r\n<p><strong>10 травня Івану Малковичу &mdash; поету і видавцю, якщо говорити  про видимі іпостасі &mdash; виповнюється 50 років. У нього дуже цікава  біографія ( це без екстремальних суми і тюрми), з якої можна &laquo;наробити&raquo; з  десяток сюжетів, представити його життя і так, і сяк: і героїчно, і  лірично, і драматично, і пафосно. У 19 на Всеукраїнському літературному  семінарі в Ірпені таємним голосуванням двох&ndash;трьох сотень літераторів  Малковича обрали &laquo;Найкращим молодим поетом&raquo;, у 23 вийшла його перша  поетична збірка &laquo;Білий камінь&raquo;, в рецензії на яку сама Ліна Костенко  назвала поета &laquo;найніжнішою скрипкою України&raquo;. Кость Москалець написав  про його поезію: &laquo;Якби Маленький Принц із казки Екзюпері писав вірші, то  вони були би точнісінько такими за настроєм і тоном, як ранні вірші  Малковича&raquo;. У 31 він заснував видавництво &laquo;А&ndash;Ба&ndash;Ба&ndash;Га&ndash;Ла&ndash;Ма&ndash;ГА&raquo; і з тих  пір 95% населення сприймає його як &laquo;нашого Оле Лукойє&raquo;. </strong></p>\r\n<p><strong>В його абетці на &laquo;А&raquo; &mdash; ангелик, на &laquo;Я&raquo; &mdash; якість, як із цим  набором жити правдивому українцю. Іван Малкович, звісно, &laquo;архетипічний&raquo;  українець, а може, навіть артефактний... Він обережний, ощадливий,  приязний, ліричний, домовитий, набожний, але на відміну від багатьох  безхребетних &mdash; затятий. Як сказав, що не подобається слово, малюнок,  дизайн, папір &mdash; не випустить книжку у світ, хоч би скільки людей на  нього тиснули. З такими людьми при всій їхній позитивності дуже важко  робити спільні справи. Тому що він робить усе на віки, а ми часто просто  допрацьовуємо робочий день до кінця. Думається, він був би добрим  душпастирем, а став успішним видавцем у міжнародному масштабі. Просто  його бізнес, по суті, теж душпастирський, і кожен це відчуває на собі.</strong></p>\r\n<p>&nbsp;</p>\r\n<h4>У цифровому вимірі</h4>\r\n<p><strong>&mdash; Пане Іване, 10 травня у вас ювілей. Як ставитеся до цифри 50?</strong></p>\r\n<p>&mdash; Відчуваю приближення цієї дати, подих. Я завжди легко проходив  круглі дати: сорок взагалі не помітив, тридцять трохи мене хвилювало &mdash;  остерігався цього переходу. У мене є сумний вірш з цього приводу:</p>\r\n<p>Зближалося на тридцять літ:<br />моря сповільна замерзали,<br />дороги зчорна позіхали<br />і їжаком котився світ.</p>\r\n<p>Отакий був стан. Хвилюватися, що суне п&rsquo;ятдесятка, ніби й нема часу,  але думаю про це з невеликою приємністю. Бо вже навіть у років 16 я  писав щось таке:</p>\r\n<p>Вичерпався я, як маленька криниця,<br />Мені не спиться.</p>\r\n<p>І в тому віці іноді буває такий стан, здається, що ти вичерпався. Все  це, зрозуміло, залежить від внутрішнього духу, від того, як ти себе  позиціонуєш. Найважливіше не старіти внутрішньо і морально. Мене завжди  підхльостує до життя відповідальність за інших людей. Зараз дуже  складний етап. Життя забирає потрошку рідних людей, і я тому не  зосереджуюся аж так на собі, бо тепер у нас є серйозніші родинні  клопоти.</p>\r\n<p><strong>&mdash; У пресі час від часу вигулькує фото, де ви, Оксана Забужко,  Юрій Андрухович, Василь Герасим&rsquo;юк, Віктор Неборак, такі молоді,  зухвалі. Багатьом у 2010&ndash;2011 виповнюється 50. Ви їздите на ювілеї?</strong></p>\r\n<p>&mdash; Ні, якось ми один одного не запрошуємо. Дуже хотів, щоб цього року  Неборак приїхав, якому 50 виповнюється 9 травня, на день швидше. Але в  нього померла мама, він мусить дозирати тата після операції і фізично не  може бути в Києві. Шкода, що його не буде. Я Віктора дуже люблю і ми з  ним спілкуємося доволі часто, він мій кум. Його 25&ndash;річчя ми святкували в  &laquo;Лисі Микиті&raquo;, у Спілці письменників у Львові. А ми з Яриною, тоді ще  неодружені, втікали від Чорнобиля. І ось під час танців я зауважив, що  ми танцюємо посеред кола, а від нас найближчі пари &mdash; за півтора&ndash;два  метра. Люди підсвідомо боялися, бо ми приїхали з Києва. Я шепочу Ярині  на вухо: ти бачиш? І ми так сумно обійнялися, і почувалися справжніми  біженцями.</p>\r\n<p>... Атили Могильного нема &mdash; ми з ним училися разом, Ігор Римарук помер, уже та фотографія поріділа.</p>\r\n<p><strong>&mdash; Ви відчуваєте поколіннєвість у літературі?</strong></p>\r\n<p>&mdash; Останні 15 років якось розсіялося це відчуття, зараз кожен окремо.  Тільки при зустрічах ти відчуваєш, що ми були разом, є що згадати.</p>\r\n<p><strong>&mdash; Минулого року ви видали книжку своєї сестри, Людмили Малкович. Це родинні борги?</strong></p>\r\n<p>&mdash; Це дуже вчасна книжка. Якби я видав тепер, сестра б не мала тієї  радості &mdash; зараз вона вже майже не встає з ліжка і раптово втратила зір.  Ми 9 років боролися з її онкохворобою &mdash; 22 хімії, двi операції, кілька  опромінень. Мене багато людей запитували, чому я не друкую вірші сестри,  літературознавець Євген Баран написав дуже гарну статтю про її поезію.  Люська (так ми її називаємо) ніколи не афішувала себе як поета, навчала  дітей рідної літератури, її люблять, вона багато робила хороших вечорів,  вистав, а коли захворіла, діти самочинно молилися на перервах за її  здоров&rsquo;я. На такому й тримається людина. У вересні вийшла її книжка, ми  зробили дуже гарну презентацію у Франківську, куди зійшлися її друзі.  Вона прекрасно читала свої вірші, хлопці з радіо зробили зворушливу  передачу...</p>\r\n<h4>If/Якби</h4>\r\n<p><strong>&mdash; У вас рання літературна кар&rsquo;єра: у 23 перша збірка, у 24  вступили у Спілку письменників, у 31 започаткували власне видавництво.  Якщо уявити, що ви б не стали видавцем, були просто поетом. Який би у  вас був штиб життя: з ким би ви дружили, де б виступали, куди прагнули?</strong></p>\r\n<p>&mdash; Це цікаве запитання. Але Бог його знає... Я працював у молодості  редактором у видавництвах, часописах, я так чи інак був би й далі  пов&rsquo;язний з літературою. Але все одно щось шукав би &mdash; може, часопис  створив. Я люблю ще й якось оматеріалізовувати український поетичний  світ. Недавно мама віддала мені дуже &laquo;цінну&raquo; книжечку: у 10 років я сам  її зшив, зробив обкладинку, назву придумав. Коли нас лишили з сестрою  після операції в лікарні &mdash; мені мигдали рвали, а їй поліпи й мигдали, я  писав цю книжку: вірші, потім іде казка про двох братів, далі пісня з  нотами (я ж учився в музичній школі) і знову вірші коротенькі. Отже  навіть по тій книжечці видно, що я прагнув би не тільки щось писати, а й  видавати.</p>\r\n<p><strong>&mdash; А якби ви стали музикантом?</strong></p>\r\n<p>&mdash; Ні&ndash;ні, скрипалем не став би. Чому я покинув заняття музикою? Я  зрозумів, що, граючи в мільйонний раз улюбленого Моцарта чи Баха, я не  вносив своїм потрактуванням нічого особливо нового. Найбільший музичний  комплімент зробили мені моя старенька викладачка позаминулого року у  Франківську. Вона сказала: &laquo;Іване, я коли чую &laquo;Вокаліз&raquo; Рахманінова,  завжди згадую вас&raquo;. Я й справді любив виконувати кантиленні речі,  зокрема й тому, що не дуже любив займатися, а технічні речі потребують  щоденного п&rsquo;ятигодинного вправляння, я до цього був не готовий.</p>\r\n<p>Так що я не міг би стати музикантом видатним, а в оркестрі мені важко  сидіти. У мене й дружина скрипалька &mdash; талановита, гарно виглядає на  сцені, і теж ніколи не уявляла, що може сидіти в оркестрі. Ми двоє  солістів, які фактично не мали на це аж надто віртуозних підстав.</p>\r\n<p><strong>&mdash; Я думала, що Ярина &mdash; з музичної родини, з консерваторською освітою &mdash; відмовилася від музичної кар&rsquo;єри заради сім&rsquo;ї.</strong></p>\r\n<p>&mdash; Ні&ndash;ні&ndash;ні, вона просто не бачила себе в оркестрі. Із неї був би  добрий лікар &mdash; вона рішуча, може взяти на себе відповідальність, коли  треба; не боїться крові. Шкода, що це в ній не розгледіли. Ми  зустрілися, коли вона закінчувала музичну одинадцятирічку, і я сам її  штовхав до вступу в консерваторію. І відповідальність за те, що вона  пішла туди, Ярина покладає й на мене, а не тільки на свого тата.</p>\r\n<p><strong>&mdash; В які моменти ви берете в руки скрипку?</strong></p>\r\n<p>&mdash; На презентаціях.</p>\r\n<p><strong>&mdash; А для себе?</strong></p>\r\n<p>&mdash; Для себе я радше сяду за фортепіано, щось собі награю, буду  намацувати якісь мотиви, щось гудіти. У студентські київські роки я міг  до ранку &mdash; до крові на пальцях &mdash; грати на гітарі, співаючи старовинні  гуцульські, закарпатські і лемківські пісні. Колись в Ірпені я вперше  почув, як виконує свої піснi Кость Москалець &mdash; &laquo;А ще мені цього літа  куля не відлита&raquo;, його дивовижний тембр... Ми співали всю ніч &mdash; то він,  то я. Я в молодості був дуже &laquo;забавовий&raquo; &mdash; міг співати, танцювати так,  що ноги мало не на шафу закидав, але Ярина це не надто схвалювала, вона  більше любить затишну розмову.</p>\r\n<p><strong>&mdash; Ви граєте вдвох на домашніх концертах?</strong></p>\r\n<p>&mdash; Ні, тільки мріяли з нашими друзями&ndash;музикантами робити концерти. Я  багато співав і з синами, особливо з Тарасом, улюблене заняття &mdash;  відгадування мелодій. Тарас із 2&ndash;3 нот відгадував пісню, Гордій інтонує  дивовижно, в обох слух фактично абсолютний. Колись іще Богдан Богданович  Антків, Яринин тато, почув, як малий Гордій лежить на дивані, бавиться в  якусь електронну гру і виконує при цьому арію Цариці ночі з &laquo;Чарівної  флейти&raquo;, інтонує чистісінько. Богдан Богданович запитує: хто це?..  Справді, важко повірити, що дитина може так досконало інтонувати. Але ми  з Яриною маємо травмоване заняттями музикою дитинство. Бо скрипка &mdash; це  травматичний вид музики. Фортепіано: натискаєш &mdash; і воно звучить, а  видобути з маленької скрипчини нормальний звук, який був би в гармонії  зі світом, &mdash; це вдається аж десь, може, в третьому класі. Те, що ти  видобуваєш зi скрипки у перші роки, це нестерпно, єство твоє противиться  цьому, бо то не музика, а мука. Тому ми дітей і не силуємо. Хоча до нас  приходить мій друг&ndash;композитор, займається з хлопцями, вони вміють грати  для себе &mdash; і на фортепiано, і на гітарі.</p>\r\n<h4>Про дім, дерево, синів. І поезію</h4>\r\n<p><strong>&mdash; Під ювілеї згадуються банальні штампи &mdash; про дерево, дім, сина. На ваші книжки пішло ого стільки дерев, а посадили ви багато?</strong></p>\r\n<p>&mdash; Набагато менше, ніж пішло. З дідом тільки колись садив, бо дідо мій  був великий садівник. І сад у нас був чудовий. Тато з людьми з нашого  села наймав фургони, і вони везли яблука аж у Росію і там здавали оптом,  це був такий вагомий заробіток. Я, на щастя, не мав важкого дитинства,  ніколи не почувався бідним. Хоч і не мав якогось надлишку, але всіх міг  пригостити, напоїти, поетичні компанії у мене збиралися &mdash; і завжди було  щось випити й закусити. Але я ніколи не віддавав усе до решти, залишав  щось про запас. Бо знав,що завтра може привалити якась інша компанія.</p>\r\n<p>Зараз я був удома і дивився на ясень, який ми з дідом саджали. Дідо  мені казав: колись, як я вмру, цей ясень буде великий і ти будеш кимити  мене. Кимити &mdash; це пам&rsquo;ятати. Я кимлю, вас, діду, кимлю. Адже дідо Йван &mdash;  єдиний мій дідусь. Дідо по татовій лінії загинув у 50&ndash;му році, мамина  мама померла, коли мамі було 8 років (все почалося з побоїв  енкаведистів, які допитували її, де дідо). Отож дідо Йван для мене був  усім. Він був перфект&shy;ним грибарем, умів усе майстерно робити своїми  руками. Мій диплом &laquo;Чоловіча доля в рекрутських піснях народних&raquo; &mdash; то  була така антитеза до праці Івана Франка &laquo;Жіноча недоля в руських піснях  народних&raquo;, і більшість пісень я записав від діда.. До того ж, підозрюю,  що дідо деякі &laquo;колінця&raquo; сам додумував.</p>\r\n<p><strong>&mdash; Я бачила фотографії вашого будинку в журналі &mdash; він дуже красивий. Це будинок&ndash;мрія, дім&ndash;фортеця, чи це ваш портрет?</strong></p>\r\n<p>&mdash; Щоб мрія &mdash; треба більше землі, і більше всього. Це будинок, який ми  будували з любов&rsquo;ю, хоча і з величезними суперечками. Зранку ми з  Яриною їхали по будівельних магазинах, нас уже знали, віталися, як iз  неодмінними ранковими живими атрибутами. Ми з Яриною дуже сперечалися,  сварилися &mdash; смаки різні, але зрештою завжди виходили на щось таке, яке  задовольняло обох. Я думав тоді: ну чому мені так важко дається  будівництво? Іноді одну стіну перекладали по три рази. Не буду всього  переповідати, бо багато забулося &mdash; ми живемо там уже четвертий рік. Але  коли переночували першу ніч, то було враження, ніби живемо там усе  життя.</p>\r\n<p><strong>&mdash; Ваш Тарас &mdash; поет, колега, у нього цікавий блог в ЖЖ, у вашому видавництві вийшла книжка, впорядкована ним.</strong></p>\r\n<p>&mdash; Він пише вірші, але мені подобається, що не квапиться їх видавати. Я  свою першу книжку видав у 23 роки. Йому, правда, 23, але в нього ще  бракує віршів на повноцінну книжку.</p>\r\n<p><strong>&mdash; Ви його спрямовуєте?</strong></p>\r\n<p>&mdash; Ні, якщо він у мене щось запитає, я скажу. Бо як у поезії спрямуєш?  Я б хотів, щоб він більше перекладав, був вимогливіший до стилю. Його  сватають лишатися в університеті, він мав практику &mdash; читав лекції  студентам 4&mdash;5 курсів, до нього ходили на лекції навіть ті, у кого вільне  відвідування. Він завзятий кіноман, займається кіноперекладом. Багато  перекладав для останньої &laquo;Молодості&raquo; і ще для кількох фестивалів.</p>\r\n<p>А Гордій у 9&ndash;му класі. Він відповідальний щодо науки, на олімпіадах з  англійської, з біології другі місця займав. Уже закохався, дівчина  старша за нього на два класи. Це закохання якесь дуже серйозне...</p>\r\n<p><strong>&mdash; Вірші пише?</strong></p>\r\n<p>&mdash; Ні, він потроху музикує, його люблять у товаристві, дівчата впадають, а він, бачу, однолюб, як і його батечко.</p>\r\n<h4>&laquo;Якби не видавництво, я б ніколи не займався бізнесом&raquo;</h4>\r\n<p><strong>&mdash; Кілька років тому для мене було одкровенням те, як ви самі  читаєте вірші &mdash; це якийсь художній сеанс терапії. Тонко, дотепно,  артистично, лірично. Чому ви практично не виступаєте зі своєю поезією і  чи не хочете записати диск своїх віршів?</strong></p>\r\n<p>&mdash; Ви читаєте думки &mdash; я навіть хотів записати такий диск до дня  народження. Робити ювілейний вечір &mdash; банальнішого й не придумаєш. Я дуже  непафосна людина і втікаю від подiбних речей, а ювілей може  перетворитися на щось таке, що я найбільше не люблю. Думав видати диск  із віршами і з хорошою музикою, але було багато клопотів поза тим. Мрію  це зробити. Я люблю читати вірші, і останнім часом мене незрідка  запрошують виступати, зараз повернувся з такого туру, іще кличуть.  Молоді композитори пишуть пісні на мої вірші і хочуть, щоб їх читали  молоді поети. Це ніби до ювілею хочуть таке зробити у Львові, Ужгороді &mdash;  уже після дня народження. Останнім часом іноді відчуваю, що моя поезія  навіть комусь потрібна, книжка &laquo;Все поруч&raquo; непогано розійшлася. Зараз  буду робити друге видання, трохи доповнене, але трохи й пересіяне. Але  суто мої книжки чомусь завжди відсуваються на другий план, хоча потаємно  я про це мрію.</p>\r\n<p><strong>&mdash; Я помітила, що найбільше ви не любите говорити про бізнес.  Боїтесь, що зурочать, що розкажете комерційні таємниці, що податкова  прочитає щось між рядками? </strong></p>\r\n<p>&mdash; Тільки наш бухгалтер, яка працює з першого дня видавництва, знає,  наскільки мало мене цікавить бізнес і наскільки я мало розуміюся на  дебеті&ndash;кредиті. У підсумках року мене цікавлять двi цифри, мені цього  досить. Я ніколи нічого особливо не планую &mdash; маю приблизний план, бо  знаю, що коли мене щось не влаштує, то я не буду цього видавати. Я геть  не бізнесмен. Якби не видавництво, то ніколи б бізнесом не займався. У  мене у видавництві не дуже сильна маркетингова лінія, все на відчуттях,  інтуїції, все не так, як учать книжки. Але все зав&rsquo;язано на якості. Я  знаю, що якість &mdash; це двигун бізнесу, плюс інтуїція. Я знав, наприклад,  що роман Ліни Костенко &laquo;Записки українського самашедшого&raquo; має  продаватися, але не ризикнув надрукувати більше, ніж 10000, і постійно  потроху додруковував, чим дав шанс піратам скористатися люфтом. За моїми  підрахунками, пірати продали не менше 20 тисяч погано зроблених,  неякісних книжок. Чесно зізнаюся, що тут я міг спрацювати краще &mdash; ми  видали 80 тисяч, а могли ж 100. Але втішно, що за світовими мірками  бестселер &mdash; це 100 тисяч за рік, а у нас, якщо брати і ті 20 тисяч  піратських, то 100 тисяч розійшлися за 3,5 місяця.</p>\r\n<p><strong>&mdash; Це правда, що за час існування видавництва ви продали 4 мільйони книжок?</strong></p>\r\n<p>&mdash; Думаю, що так, адже ми існуємо 19 років.</p>\r\n<p><strong>&mdash; І ліцензії на книжки &laquo;А&ndash;Ба&ndash;Ба&ndash;Га&ndash;Ла&ndash;Ма&ndash;Га&raquo; продані в 19 країн?</strong></p>\r\n<p>&mdash; Так.</p>\r\n<p><strong>&mdash; І що першу &laquo;Абетку&raquo; ви видали на 1500 позичених доларів і на 25 тоннах макулатурного картону?</strong></p>\r\n<p>&mdash; 26&ndash;ти. Мій кум мав макулатурний пункт, а з ним за макулатуру  розраховувалися картоном, який був узагалі нікому не потрібен, от він  мені й позичив його на 5 місяців. Я повернув його гроші за 4,5. Він був  щасливий, я був щасливий, читачі були щасливі.</p>\r\n<p><strong>&mdash; Якби &laquo;Абетка&raquo; не була успішною, ви б кинули цей бізнес?</strong></p>\r\n<p>&mdash; Не знаю. А чому вона мала бути не успішною? Такого не могло бути!  Вона була вчасна, гарна, дотепна, я робив її для свого сина і хотів, щоб  вона була найкраща. Хоча 50 тисяч накладу &laquo;Української абетки&raquo; тільки з  буйної голови можна було запустити, я все ще міряв тими радянськими  накладами. Був правильний художник, я підбирав хороші вірші, зрештою,  ангел на першій сторінці &mdash; а це дуже важливо. Бо це вірш із мого першого  дитячого спомину. Мені десь три з половиною роки, і тато каже: &laquo;Іванку,  завтра до нас прийде Миколайко, принесе тобі даруночки, але він буде  питатися, чи ти вмієш молитися&raquo;. І вчить мене молитву &laquo;Ангелику мій,  охоронцю мій&raquo;. І я вчу цю молитву, щоб потім, майже через 30 років, ця  молитва стала першою сторінкою першої книжки мого видавництва. Це ж  прекрасно. Я вважаю, що не бізнес, в якому я справді небагато розумію, а  саме це для мене важливо, і от про це я люблю говорити, тому що  впевнений, що це важливіше за все.</p>\r\n<p><strong>&mdash; Ви людина, яка запалює зірки &mdash; письменника Володимира  Рутківського, художникiв Владислава Єрка i Костя Лавра. У Єрка ви навіть  стали хрещеним батьком. Це все відбувається на рівні людському?</strong></p>\r\n<p>&mdash; А воно все відбувається на рівні людському або не відбувається  ніяк. У мене все так у видавництві, все дуже особистісне, це і є  найдорожче. Книжка &mdash; це мистецтво, це добра емоція, часто шлях до неї  дуже напружений, на грані добрих стосунків. Це велике щастя, що одного  надвечір&rsquo;я у видавництво прийшов Єрко. Він лишив мені одну роботу, і я  подумав: Господи, цей художник ще ніде не задіяний, як так може бути.</p>\r\n<p>З Костем Лавром ми працювали ще на комбінаті &laquo;Молодь&raquo;. Він недавно  згадував, як мене з комсомолу збиралися виключати, бо я був для них не  дуже безпечний, пропускав те, чого не можна було. Коли мене вперше до  цензора викликали, я думав: це за те, що я вірші воїна УПА опублікував у  альманасі &laquo;Вітрила&raquo; &mdash; Мирослава Кушніра. &laquo;Здригнувся вітер, злопотів на  машті прапор України&raquo; &mdash; це було неможливо тоді уявити, тоді ще був  глибокий совок. Але виявилося, що не за Кушніра, а за якусь дурничку, в  якомусь оповіданні йшлося про військове містечко Десна. А на столі в  цензорки лежав рукопис Тараса Мельничука, який я довго пробивав у друк, а  там уже антирадянські вірші: &laquo;Мовчиш, Росіє, &mdash; і мовчи &mdash; точи, точи на  себе вістря!&raquo;. Я ввійшов у довірливу розмову з цією цензоркою, закурив з  нею, анекдот розказав, кинув оком на рукопис Мельничука, кажу: &laquo;О,  черговий висуванець нашого керівництва, сунуть усяких бездарів. Ви вже  читали?&raquo; &mdash; &laquo;Ще ні&raquo;, &mdash; відповідає вона. &laquo;То, може, вдарите печатку, щоб я  забрав і зробив їм приємність&raquo;. Вона бахнула печатку, я відніс у  редакцію рукопис Мельничука і на другий день звільнився з роботи, бо вже  знав, що книжка вийде, вона вже мала всі необхідні дозволи.</p>\r\n<p>Мельничук був одним із моїх учителів, кілька зустрічей iз ним, коли  він вийшов з тюрми, для мене були дуже важливі. Він відкрив мені світ  метафори, відкрив переді мною двері до справжньої поезії, тож я хотів  йому віддячити, і мені це вдалося.</p>\r\n<h4>&laquo;Найсправжніший я все&ndash;таки у віршах, а вони часто драматичні&raquo;</h4>\r\n<p><strong>&mdash; Ви дипломат по життю?</strong></p>\r\n<p>&mdash; Так, намагаюся.</p>\r\n<p><strong>&mdash; Зараз політична ситуація така, що і слів нормальних немає.</strong></p>\r\n<p>&mdash; Залишається тільки зручно вдавати, що все зло &mdash; це наш Тютюнідзе,  що ми не розуміємо справжнього стану справ. Нестерпно, але я не нарікаю  на тем&shy;ряву, а намагаюся запалити свою свічку. Гарна дитяча українська  книжка &mdash; це дуже важливо. У державі, яка ще недавно мала 50 мільйонів,  не знімалося жодного фільму, я вже не кажу дитячого. Як така держава  могла розвиватися? Мультфільмів своїх не було, тільки про козаків, і то  козаки були там безсловесні, були тільки титри російською мовою і  музика. А книжки &mdash; це єдине, що можна було робити, бо це ще не такі  великі кошти. І навіть я можу своїм таким не бізнесовим розумом дати  цьому раду більш&ndash;менш успішно. Тому якщо є можливість робити щось, що є  частиною культури, і я можу піднімати рівень дітей у цьому суцільному  секонд&ndash;хенді культурному, у цій попсі, гламурі, то я з радістю це роблю.  Є ще кілька книжок, які я хотів би видати &mdash; третій том &laquo;100 казок&raquo;,  другий том &laquo;Улюблених віршів&raquo;, тому що мають бути книжки вагомі,  важливі, такі лонгселери, які на довший час можуть закривати собою певну  нішу і підносити естетичний рівень дітей.</p>\r\n<p>Ми бачимо, що все натягнуто, як найтонша струна. У скрипці це струна  мі: коли ти її перетягнеш &mdash; вона рветься і боляче б&rsquo;ється. І скрипаль  знає, примружує очі, щоб не поранитись. І ти бачиш, як тонко натягнутий  цей світ і ця Україна, а на кожному каналі &mdash; розваги, шоу, щоб люди  тільки забулися, щоб відволікалися від того, як їх зневажають, щоб ми  забули, хто ми, щоб вимити будь&ndash;яку особистісну сутність, щоб не давати  ніякої серйозної культурологічної розмови і проблеми. Ми можемо судити  про глибину світосприйняття власників телеканалів з їхніх програм, яка  там зяє порожнеча, байдужість і цинізм. Ще на першому каналі радіо, на  &laquo;Промені&raquo;, можна почути хорошу людську розмову про щось високе.</p>\r\n<p><strong>&mdash; Не можу оминути гучну історію з туром Ліни Костенко. Мені  було образливо за вас. Я в ті дні не раз говорила: Малкович і так  спілкується з Ліною Василівною, але він захотів поділитися цим з усією  Україною, придумав цей складний тур &mdash; і організаційно, і фінансово, і  по&ndash;людськи. І вийшов ледь не крайнім.. </strong></p>\r\n<p>&mdash; Ліна Василівна жартома зізналася: &laquo;Ви ж думали, що я не поїду, от я  й вирішила вас здивувати своєю згодою&raquo;. Для мене, окрім усього, було  важливо, щоб були хороші професійні зйомки цих вечорів, бо це залишиться  назавжди. І щоб саме тепер &mdash; коли Ліна Костенко ще сповнена енергії,  таланту, добре виглядає &mdash; люди почули, як вона зі сцени читає свої  вірші... І це відбулося, і я щасливий.</p>\r\n<p>Я гадаю, що великою мірою зрив туру відбувся і через  безвідповідальність молодих, ласих на висмоктані з пальця сенсації,  журналістів. Без їхнього агресивного втручання ми провели б прекрасні  вечори, але кілька людей роздули скандал, повиривали з контексту фрази.  Ясно, що один з учасників того львівського &laquo;обговорення&raquo; мав би  вибачитися за те, що його занесло на вiражi. Бо що таке шляхетність? Це  коли ти можеш поза очі говорити тільки те, що наважишся сказати в очі.  Кожного може занести не туди, але завжди можна отямитися і вибачитися.</p>\r\n<p><strong>&mdash; І другий том &laquo;Записок українського самашедшого&raquo; буде?</strong></p>\r\n<p>&mdash; Так, але насамперед я мрію видати двотомник поезії Ліни Костенко і  після цього зробити великий вечір у Палаці &laquo;Україна&raquo;, надрукувати книгу  спогадів &laquo;Дивний сад Івашкевича&raquo;, яку Ліна Василівна цього року,  можливо, закінчить.</p>\r\n<p><strong>&mdash; Ви короткочасно ходили в політику...</strong></p>\r\n<p>&mdash; &laquo;Не співайте мені сеї пісні...&raquo;</p>\r\n<p><strong>&mdash; За яких умов ви себе бачите в політиці?</strong></p>\r\n<p>&mdash; Якщо б тільки барикади якісь.</p>\r\n<p><strong>&mdash; Коли ви були 10&ndash;м номером у виборчих списках однієї партії, вам це принесло розчарування чи якесь пізнання?</strong></p>\r\n<p>&mdash; Суцільне розчарування, тому що коли тебе хочуть, сватають, так  солодко співають. А коли ти вже згодився, ніхто не слухає твоєї думки,  ти ні на що не можеш впливати і контролювати, що там робиться. Насправді  я ніколи не був членом будь&ndash;якої партії.</p>\r\n<p><strong>&mdash;&nbsp; Останнім часом політика які емоції викликає &mdash; розчарування, розпач?</strong></p>\r\n<p>&mdash; То якийсь не чистий розпач, треба ще якісь епітети додавати. Але ти  розумієш, що попри всі розпачі, якщо щось можеш робити &mdash; роби. Де я ще  можу бути? Крикнути щось, викричати свій розпач, роздратування &mdash; що це  змінить? Змінити може, коли ти зробиш щось важливе, хай і не таке й  помітне на перший погляд. Я сам для себе шукаю позитиву, а знаходячи,  намагаюсь ним поділитися з якнайбільшою кількістю людей. Я вважаю, що  тільки так можна виживати й боротися і, не піддаючись, робити своє,  робити ще краще. Дивлюся, де можу бути найкориснішим, адже здається, що  ти плекаєш сад, а насправді ти просто рятуєш його. І сам себе. Садівник,  який, рятуючи сад, рятує сам себе.</p>\r\n<p>Валентина КЛИМЕНКО</p>\r\n<p>http://umoloda.kiev.ua/number/164/0/66702/</p>\r\n</span></span></span></p>','Іван Малкович: Садівник, рятуючи сад, рятує сам себе','uk',2448,3931,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Історія українського народу містить в собі багато славних подій. До них належить і остання наша Визвольна боротьба 1917-1921 рр. Державні й ідейні здобутки її досить добре і ясно були зафіксовані у відповідних державних актах - чотирьох Універсалах Української Центральної Ради, відозвах, хронологічних збірках матеріалів з цієї доби, розвідках і мемуарах.</span></span></p>','2008-03-25 11:54:19',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Історія українського народу містить в собі багато славних подій. До них належить і остання наша Визвольна боротьба 1917-1921 рр. Державні й ідейні здобутки її досить добре і ясно були зафіксовані у відповідних державних актах - чотирьох Універсалах Української Центральної Ради, відозвах, хронологічних збірках матеріалів з цієї доби, розвідках і мемуарах. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Не так добре стоїть справа з зафіксованням чинної збройної боротьби, що ту українську ідеологію державну та ідеї армія наша скропила кров\'ю своєю, як найбільш переконуючим аргументом права українського народу на свою Волю і Державу. Ліпше були зафіксовані події 1919-1920 рр. (хоч на сьогодні після останьої світової війни майже все то було знищено), початки ж творення української мілітарної сили рр. 1917-1918, а тим більш перебіг боїв, перемог і поразок наших в більшості лишаються й досі українському загалові (спеціально в Україні) незнаними або малознаними. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Однією з подій цієї першої доби українських збройних сил, події, що варта уваги й яка, щодалі, то все більше стає шанованою, але, на жаль, не точно, мало записана й з боку фактичного устійнена є - КРУТИ чи Крутянський бій. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> За цих 75 років Крути знайшли собі досить значний літературний відгук. Про події під Крутами не мало писалося: були це поетичні присвяти, мемуари й критичні, політичні, історичні, воєнні й публіцистичні оцінки[...] </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Найавторитетнішим джерелом опису бою під Крутами є звіт командуючого військовою частиною, яка вела бій під Крутами - тоді сотника А. Гончаренка. Цей звіт про перебіг бою ми й беремо за основу сьогоднішньої теми. В часі виголошення IV Універсалу Українською Центральною Радою, командував українським військом полковник Капкан. Він ще в грудні 1917 році вислав на оборону надзвичайно важливого вузла залізничного, на станції Бахмач, старший курс Першої ім. гетьмана Б. Хмельницького Юнацької військової школи під командою Ген. Штабу сотника Носенка. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Ця школа (старший курс), у складі 4-х сотень (по 150 юнаків у сотні), 20 старшин та 18 кулеметів виїхала 8 грудня 1917 року з Києва на станцію Бахмач. Бахмач адміністраційно належав до міста Чернігова й тут виконував функції військово-адміністраційні сотник Ф. Тимченко. Цей останній не мав при собі жодного війська і йому було приділено на допомогу зі складу Юнацької школи сотника М. Богаєвського. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Зразу ж по приїзді школи між двома сотниками - Носенком і Тим-ченком - виникли непорозуміння на тлі підлеглості і влади. Кожен хотів бути старшим. Командуючий військами полковник Капкан мусив був навести тут порядок, і він його навів. Сотника Д. Носенка було усунено від керування школою і фронтом, а на його місце вислано було сотника А. Гончаренка, який разом з молодшим курсом, рештою Юнацької школи, 23 грудня 1917 року поїхали на станцію Бахмач. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Становище нового команданта було надзвичайно тяжке. З півночі насувалися азійські орди червоних москвинів, у самому Бахмачі, в депо - біля 2000 робітників комуністичне розагітованих, залізничники саботували, довколишнє населення, в ліпшому разі, було \"невтральне\". </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Довкола самі вороги й жадної надії на допомогу. Це було для командуючого фронта ясним, бо ж і сам полковник Капкан, виряджаючи його сюди, сказав: \"На поміч - трудно чекати\"... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Активність ворога збільшувалася, бо сили його все зростали. Безпосередній наступ на Юнацьку школу вів \"товаріщ\" Берзін, сили якого складалися з 3000 червоноармійців, 400 матросів та 12 гармат. Коли до того додати червоне запілля з \"товаріщем\" Кудинським та пол. Муравйовим, що 27 січня перенісся вже на станцію Бахмач, та тих 2000 робітників з депо, що, зрештою, повстали проти школи, то сили ворога - фронт і запілля - можна обчислити понад 10 тисяч душ. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Під натиском такої сили Юнацька школа примушена була залишити станцію Бахмач і через станцію Пліски відступити на станцію Крути. Безпосередньо в наступі на Крути з боку червоних банд брало участь біля 4000 багнетів. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &amp;nbsp27 січня 1918 року о 4-й рано приїхав на станцію Крути, на допомогу юнакам Студентській Січовий Курень в кількості 115-130 багнетів під командою старшини Омельченка. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Командант фронту сотник А. Гончаренко приділив студентам найменш загрозливу ділянку фронту, а то на самому лівому крилі. 29 січня ранком червоні банди зімкнутими колонами і розпочали наступ. Вони, очевидно, були певні, що не зустрінуть спротиву. Коли ці ворожі колони підійшли на віддаль стрілу, юнаки і студенти зустріли їх сильним вогнем цілого свого фронту та 16 кулеметів заговорили до ворога. Червоні під вогнем почали розсипатися в розстрільні. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Мали вони дуже великі втрати, але за першою лавою йшла друга, третя і так без кінця....Сили ворога росли - вони мали постійне поповнення й зайняли лінію фронту шириною в 5 кілометрів. А наші?.... Горстка в 500 юнаків та студентів при 20 старшинах ледве чи й обсадили 3-х кілометровий фронт та не мали жодного поповнення. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> На допомогу юнакам прийшла на відкритій залізничній платформі щойно одна гармата сотника Лощенка. Ця гармата влучним вогнем здержувала наступ ворожих банд, але - коли червоні почали робити обхват правого крила юнаків, вони, не маючи змоги повернути гармату для стрілу вбік, відійшли з лінії бою. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Шукаючи допомоги фронтові, сотники Тимченко і Богачевський від\'їхали до міста Ніжина, аби відти привести полк ім. Т. Шевченка, що саме там був, але..... той полк оголосив \"невтралітет\" і на фронт не поїхав. Коло години 12-ї червоні розпочали наступ проти студентів, але, попавши під перехресний вогонь, від цього наміру уступили та продовжували маневр обхвату правого нашого крила. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Сили ворога на це йому дозволяли. Введена була на лінію бою наша резервна сотня, а то саме на праве крило, яке було найбільше загрожане. Так трималися наші безстрашні герої аж до вечора. Треба було відходити. Про відхід дістали наказ перші студенти, потім друга сотня і зрештою 3-тя і 4-та сотні юнаків. Перша сотня, яка прийшла з резерви, прикривала відступ. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Вже коли сідали в поїзд, запримітили відсутність багатьох студентів, яких бачили живими. Вислана розвідка донесла, що вони, відступаючи затемна, пішли на вогні - на станцію Крути, що саме в той момент обсаджували червоні. Ці 32 студенти, а в тім числі і рідний брат команданта фронту А. Гончаренка, Володимир Шульгин, Пипський і другі попали в полон й були в бестіяльний спосіб замордовані червоними бандитами. Поїзд з юнаками й студентами відійшов у напрямі станції Ніжин. Тут стояв Шевченківський полк, який замість того, щоб виступити на фронт, перейшов ганебно на бік ворога, та навіть хотів обеззброїти крутян, та це їм не повелося. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> На станції Бровари командант Юнацької школи і бою під Крутами сотник Аверкій Гончаренко склав свій релятивній звіт Симонові Петлюрі, як командантові Слобідського Коша, що тоді зі своїми гайдамаками готувався до наступу на Київ, на Арсенал. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Скінчився бій під Крутами - біля 250 юнаків і студентів та 10 старшин, а в тому числі й командант студентів Омельченко, зложили свої молоді голови, принісши в жертву Батьківщині своє молоде розквітаюче життя, а з тих 32 студентів, що попали до бандитського полону, 28 душ, зазнавши неймовірних мук, розшматовані багнетами дикої московської орди, випили свій страдницький келих до дна, але не зрадили України. 4-х студентів відведено було поранених до Харкова[...] </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Є необхідним освітити ті обставини і умови тогочасного життя в Україні, які довели до висилки Студентського Січового Куреня на фронт. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> На початках революції в Росії, а тим самим і в Україні українська етнографічна людність прагнула до якнайширшого і найшвидчого улаштування свого національного життя. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Українці-вояки, що були розкинені по широких просторах так званої \"необ\'ятної Росії\", а головне на всіх її фронтах, цілком свідомо віддавали себе до розпорядимості Українській Центральній Раді, готові були йти на найбільші жертви. Цього моменту Українська Центральна Рада не врахувала. Час ішов і діяв не на користь Україні, а проти неї, бо білі, а особливо червоні москалі використали той час на підготовку своїх сил, вони кинули скрайні демогогічні соціальні кличі-гасла й тим самим перетворили в Україні революцію національну в революцію соціальну. Українське робітництво й селянство, а тим самим і фронтовики поволі відходили від гасел національних та кинулися за гаслами соціяльними - \"за фабрики, за землю\".... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Молода Українська Держава творилась у бурхливі часи революції; у фокусі її тодішнього державного апарату переплутувались і переплітались різні, відмінні від себе ідеї, відчуття, прагнення й пристрасті краю, які ще до того інтифікувались і загострювались міжусобною боротьбою розбурханого революцією населення. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Україна тоді кипіла і бурлила. І ця постійна змінність і неспокій надавали тоді всьому українському життю скрайню многогранність. Цілком вільно і рядом з собою могли тоді укладатись і виявлятись і великі людські та громадські чесноти, і низька, підла негідність; з одного боку - справжній вияв ентузіязму, зрив мужності, жертовності, самовідданості, гідності розуму, волі, стійкості, віри і любови, а з другого -- депресія, страх, самолюбство, амбіція, фальш, брехня, підкупність, брутальність, цинізм, обман, глупота і малодушність. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Саме в такий момент виданий був IV Універсал. Військо, стомлене великою війною, розколихане революцією, ставало або байдужньо-невтральне до ідеї самостійності, а подекуди навіть і вороже. З поведінки полків у Києві росла тривога непевності. Це все спонукало національне свідоме студентство подати ініціативу створення організаційного комітету, який би перевів організацію надійної озброєної сили для захисту молодої Української Держави. Це випало саме в ті часи, коли славної пам\'яті С. Петлюра вийшов зі становища Генерального Секретаря військових справ та був замінений М. Поршем. Симон Петлюра добре зрозумів і бачив небезпеку, яка загрожувала Україні, й тому розпочав формування свого Слобідського Коша, тої надійної сили, до якої також закликали і студенти. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Організаційний Комітет оголосив у пресі відозву до академічної молоді. У відозві зазначалося, що надії на українські полки завеликі, що треба братися до того студентству. Відозва ця була повна свідомості всього загрозливого стану суворої дійсності. Студенти почали вписуватись до Українського Студенського Січового Куреня. Спочатку цей курінь мав нести лише службу при охороні Центральної Ради, але... коли червоні банди загрозили безпосередньо столиці України - Києву, той курінь відправлено було на допомогу Юнацькій (старшинській) школі, що займала тоді фронт на станції Бахмач. Так необстріляні в боях, необсмалені в пороховому димі світової війни, недосвідчені вояки приїхали того сивого, зимового, морозного ранку на Крутянську землю..... </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Ні! Це приїхала молодь, студенти, гімназисти, що ще так недавно сиділи в школі і вивчали як щось цілком теоретичне, як щось цілком історичне, далеке від життя, що \"славна смерть за батьківщину,\" - ці учні, майже ще діти, мусили тепер скласти тут справжній іспит зрілости, довести, чи можуть вони добре перекласти на мову рідну, на мову українську це латинське твердження й чи можуть вони застосувати його зі сторінок історії до життя сучасного, зауважає В. Филинович. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> А коли прийшлось мати діло зі справжнім ворогом, коли навкруги з\'явилася кров і смерть,- пише він далі,- не розгубилися ці юнаки і студенти. І хоч у кожного з крутян було велике бажання жити, жити в своїй щойно посталій державі, працювати в ній і для неї - вони свідомо йшли на смерть за Батьківщину, бо ця ж сама Батьківщина вимагала від них найтяжчої жертви, вимагала від них скінчити своє, зовсім ще не розпочате життя, далеко від власного дому, родини, близьких, на холодному снігу біля КРУТ. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> І ніхто з тої молоді не затремтів, ніхто не кинув бою, відважно ставши на герць з неймовірно численнішим, неймовірно сильнішим, жорстоким московським ворогом. Багато з них зложили свої буйні молоді голови на крутянській землі, рясно її окропивши своєю дорогоцінною, щирою українською кров\'ю. А у великій книзі історії України з\'явилася нова золота, окроплена кров\'ю сторінка, назва якої є - КРУТИ! </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Що ж до самого бою під Крутами, з точки погляду воєнного, то нічого особливого немає. Бій, яких наша армія за час боротьби і до Крут, і після них, мала немало. Були бої навіть кривавіші, були й знущання над нашими переможеними вояками. Але бій під Крутами входить у нашу історію, як зразок офірності, мучеництва і героїзму. Кажуть звичайно, що Крути - українські Термопіли, подібні до грецьких, під якими поліг смертю героя спартанський цар Леонід зі своїми 300 воїнами, стримуючи в році 481-му до Різдва Христового навалу війська царя перського Ксеркона, що мав 1700000 душ свого війська. Цей бій під Термопілями впродовж 2500 літ до Крут був для всіх народів світу зразком жертвеності для батьківщини, не занесений, як перлина мистецької творчості генія військового, до історії військового мистецтва. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> З цього боку він нижче не лише творчості Епамідонта при Лавтрах року 371-го до Різдва, а навіть і творчості Мільтіяда під Маратоном року 491-го до Різдва. Однак Термопіли є зразком і перлиною для історії людської офірності й самопосвяти для свого народу й незалежності державної. З цього боку дійсно є схожість поміж спартанцями Леоніда і нашою крутянською молоддю. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Однак таке шаблонове порівняння, коли порівняти самопосвяту й силу жертовності духа та відданості наших Героїв Крутян, не є на місці - бо не треба забувати, що: </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &amp;nbsp1. Спарта в час події в Термопілах була вже 300 літ не лише самостійною державо, але і гегемоном над іншими державами тодішньої Греції, а Україна до часу Крут 264 роки була в неволі, в якій стратила навіть ім\'я своє. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &amp;nbsp2. У Спарті цілий уклад життя, побут були мілітаризовані. У Спарті матері і сестри своїм синам, братам і нареченим, випроваджуючи на війну, зичили: \"Зо щитом або на щиті\". А на Україні?... На Україні плач за новобранцем у народних масах, та й література наша від тих народних мас не відстає - у Винниченка солдат тільки той, що розстрілює революціонерів або, як його \"Боротьба\" є просякнута відразою і нехіттю до військ. Те ж саме бачимо у Кобилянської в творі \"Земля\" і т. п. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &amp;nbsp3. У Спарті цілий народ був за Леонідом з його спартанцями, чекав від його героїзму, як чогось звичайного. Цілий геній народний був з ним, а на Україні розагітоване багатотисячне військо оголошувало нейтралітет, залізничники і нарід не лише не були з Крутянцями, а навіть були вороже наставлені до них. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> &amp;nbsp4. Спартанці в Термопілах використали непереможну природу, місцевість їм сприятливу, а Крутяни опинилися в чистім полі. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> На тлі оцих обставин, що були за спартанців і рішуче проти Крутян, КРУТИ є далеко вищі героїзмом й офірністю за спартанські Термопіли.. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Не бракує жалів і від провідників тодішньої української армії на те, що даремно, необачно вислали на загибель юнацтво українське, що його жертва занадто висока і нічим її оплатити, віддячити. Уряд і провід військовий можуть собі дозволити ці жалі. Бо і від них немало залежало і залежатиме, щоб Крути вже не повторилися. На сьогоднішній час Україна має ще сильного північного ворога, світ ще так байдужий до наших змагань і волі, то чи товаришам, полеглим під Крутами, чи то сучасній молоді треба бути завжди готовими до Крут. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Коли треба буде, коли незнайдеться інших виходів і можливостей - Крути мусять бути. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Потрібно вічно про це пам\'ятати і не забувати. Менше буде прикрих несподіванок. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Крути - це символ нашої вічної непідготованості, символ вічної нашої самітності перед лицем фатальної небезпеки, і то не завжди тільки з нашої вини. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Не довго, на той раз, тяглася окупація московськими бандами України, їх вигнали з нашої землі, і 19 березня 1918 року тіла замордованих Крутян було розшукано і привезено до Києва. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Березневе українське сонце пестило вкриті червоною китайкою домовини -.то українська столиця, молода Українська Держава, урочисто ховала перших своїх безстрашних Героїв, тих, які гордо, за лицарським козацьким звичаєм, душу й тіло поклали за самостійність, за незалежність, за волю Батьківщини. Поховали ці українські гордощі недалеко від могили першого українського князя - біля Аскольдової могили. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> І струміли щирі сльози на сиву бороду першого Голови Української Держави проф. М. Грушевського, коли він тремтячим голосом говорив: </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> \"Стримайте ваші сльози! Ці юнаки поклали свої голови за визволення Вітчизни, і Вітчизна збереже про них вдячну пам\'ять на віки вічні. Славна смерть за Україну\". </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> А в сьогоднішній день, 75-ту річницю їх геройської смерті, з гордощами національними, що український народ має, нехай і трагедію, але Крути - схилімо свої голови в глибокій пошані перед світлою пам\'яттю їх! </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">1993р. </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Віктор Роєнко, </span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">майор</span></span></p>','Крути','uk',336,1316,0,NULL);
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p>&nbsp;<img style=\"float: left;\" src=\"image,3674,200,1.jpg\" alt=\"\" width=\"200\" height=\"150\" />На жаль, реалії сьогодення є такими, що в самій нашій Україні (ні, не десь там по світу!) не всі церковні громади можуть похвалитися сприятливим чи бодай толерантним для них середовищем існування -- конфесійним, національним, мовним. Саме в таких непростих умовах розвивається нині парафія Покрова Пресвятої Богородиці в м. Євпаторія (Крим). Цього року там побувала парафіянка нашої церкви Тетяна Артемова, яка привезла звідти теплі спогади про братів греко-католиків та їхнього настоятеля о. Богдана Костецького.</p>','2010-10-25 21:20:07',NULL,'P','<p>&nbsp;<img style=\"float: left;\" src=\"image,3674,200,2.jpg\" alt=\"\" width=\"200\" height=\"150\" />На жаль, реалії сьогодення є такими, що в самій нашій Україні (ні, не десь там по світу!) не всі церковні громади можуть похвалитися сприятливим чи бодай толерантним для них середовищем існування -- конфесійним, національним, мовним. Саме в таких непростих умовах розвивається нині парафія Покрова Пресвятої Богородиці в м. Євпаторія (Крим). Цього року там побувала парафіянка нашої церкви Тетяна Артемова, яка привезла звідти теплі спогади про братів греко-католиків та їхнього настоятеля о. Богдана Костецького.</p>\r\n<p>Парафія в м. Євпаторії існує вже 5 років. Історія заснування громади до гіркоти нагадує створення нашої власної парафії на Аскольдовій могилі, коли о. Ігор Онишкевич спочатку мало що не сам-один, а відтак лише з кількома вірними починав молитися на порозі перетвореної в альтанку найдавнішої церкви України. Розповідаючи про заснування своєї громади, о. Б.Костецький любить говорити так: &laquo;На початку парафія Покрова Пресвятої Богородиці&nbsp; в м. Євпаторія нараховувала 5 осіб: священик, п. Віра Ковальчук та Господь Бог у Трьох Особах&raquo;.</p>\r\n<p>Сьогодні церква Покрова Пресвятої Богородиці нараховує понад 80 осіб. Щоб <img style=\"float: right;\" src=\"image,3674,200,3.jpg\" alt=\"\" width=\"150\" height=\"200\" />полегшити сприйняття богослужень неукраїномовним вірним у храмі служать греко-католицьку Літургію російською мовою; тут утворений також російськомовний парафіяльний хор. Сьогодні богослуження відбуваються в приміщенні бібліотеки на вул. Набережна Горького, однак зусиллями парафіян громада придбала земельну ділянку (місцева влада не надала безкоштовної землі для побудови греко-католицької церкви) під будівництво храму, яке незабаром розпочнуть. Молімося за успіх наших братів у цій нелегкій, але такій потрібній справі.</p>\r\n<p>Адреса церкви Покрова Пресвятої Богородиці: м. Євпаторія, вул. Набережна Горького, тел.: 0507310376.</p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"image,3674,200,4.jpg\" alt=\"\" width=\"200\" height=\"150\" /> <img src=\"image,3674,200,5.jpg\" alt=\"\" width=\"200\" height=\"150\" /></p>\r\n<p><img src=\"image,3674,200,6.jpg\" alt=\"\" width=\"150\" height=\"200\" /></p>\r\n<p>Наталія Пуряєва</p>\r\n<p>Фото Тетяни</p>','НАШОГО ЦВІТУ ПО ВСЬОМУ СВІТУ: УКРАЇНСЬКА ГРЕКО-КАТОЛИЦЬКА ПАРАФІЯ В КРИМУ','uk',2191,3674,0,'Віталіна Онишкевич');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p><img src=\"http://www.golos.com.ua/img/7301.jpg\" border=\"0\" alt=\"\" hspace=\"5\" vspace=\"0\" width=\"200\" height=\"200\" align=\"left\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виповнилося 90 років з того дня, коли вихованці Першої київської військової школи імені гетьмана Богдана Хмельницького, юнаки куреня Січових стрільців (студенти університету святого Володимира і учні старших класів Української Кирило-Мефодіївської гімназії) та рота гайдамаків стали на заваді більшовицькому наступу на Київ. Тоді бій під невеличкою залізничною станцією Крути неподалік від Ніжина забрав життя майже трьохсот українських юнаків. Їхній подвиг вперше засвідчив, що в Україні з\'явилися люди, для яких рідний край - понад усе. Про значення бою під Крутами, приклади та уроки історії говоримо із головою ради Чернігівської обласної організації політичної партії Народний Союз &laquo;Наша Україна&raquo;, заступником голови Чернігівської обласної ради Олегом ОБУШНИМ <strong>(на знімку).</strong></span></span></p>\r\n<p>&nbsp;</p>','2008-02-01 19:40:16',NULL,'P','<p><img src=\"http://www.golos.com.ua/img/7301.jpg\" border=\"0\" alt=\"\" hspace=\"5\" vspace=\"0\" width=\"200\" height=\"200\" align=\"left\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Виповнилося 90 років з того дня, коли вихованці Першої київської військової школи імені гетьмана Богдана Хмельницького, юнаки куреня Січових стрільців (студенти університету святого Володимира і учні старших класів Української Кирило-Мефодіївської гімназії) та рота гайдамаків стали на заваді більшовицькому наступу на Київ. Тоді бій під невеличкою залізничною станцією Крути неподалік від Ніжина забрав життя майже трьохсот українських юнаків. Їхній подвиг вперше засвідчив, що в Україні з\'явилися люди, для яких рідний край - понад усе. Про значення бою під Крутами, приклади та уроки історії говоримо із головою ради Чернігівської обласної організації політичної партії Народний Союз &laquo;Наша Україна&raquo;, заступником голови Чернігівської обласної ради Олегом ОБУШНИМ <strong>(на знімку).</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>- Олеже Олексійовичу, що для вас особисто означає цей бій під Крутами?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Для мене це передусім урок, який дала нам історія. У січні 1918 року кілька сотень молодих патріотів стримували більш ніж удесятеро чисельніший загін противника. Цей бій відіграв певну стратегічну роль, адже відбувся в день, коли в Києві почалося січневе більшовицьке повстання. Молоді люди, а їм у середньому було 19 років, на добу затримали просування червоних військ на Київ. Це дало змогу виїхати і урядовцям Центральної Ради, і частині мирних жителів. Але бій під Крутами не був визначальним у ході тієї війни, що програла молода Українська держава. Її уряд, який роздирали міжпартійні та міжфракційні суперечності й ворожнеча, не зміг втримати владу. Завважте, що тоді в України був шанс вибороти свою незалежність, але відірваність лідерів УНР від народу, власна політична недалекоглядність не дали цей шанс реалізувати. Фінляндія, невеличкі прибалтійські республіки зуміли відстояти незалежність, а Україна із багатомільйонним населенням і потужними ресурсами - ні. Тодішня політична еліта не зрозуміла свого призначення - створити та відстояти вільну незалежну Україну. Їй не вдалося об\'єднати величезний потенціал нації. І українці десятиріччями змушені були пожинати плоди цієї нездатності: колективізацію, голодомори 1932-1933, 1946-1947 рр., репресії, багатомільйонні втрати під час ІІ Світової війни. Тож бій під Крутами - це урок, який потрібно вивчити і запам\'ятати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>- Навколо бою під Крутами за десятиліття з\'явилося багато міфів. Різні політичні сили оцінюють ці події по-іншому. Як ви вважаєте, чи потрібна ця міфологізація?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Політичні спекуляції навколо подій під Крутами почалися майже відразу після того, як у березні 1918 Центральна Рада вирішила поховати останки загиблих студентів на Аскольдовій могилі в Києві. Вже тоді з\'явився міф про те, що під Крутами загинуло 300 студентів. Але це не зовсім правильно. В бою під Крутами брали участь дві сотні юнкерів Першої київської військової школи імені гетьмана Богдана Хмельницького, рота гайдамаків і лише одна сотня куреня Січових стрільців, який формувався із студентів та гімназистів. Погодьтеся, що юнкери - це люди навчені тримати зброю. А от хто віддав наказ вступити в бій необстріляним студентам, які вперше взяли в руки гвинтівку, достеменно невідомо. 29 (16) січня юнкери та студенти, котрі окопалися вздовж залізничної колії між Крутами та Плисками, дали бій більшовицькому загону. Коли набої скінчилися, юнкери і студенти відступили до ешелону. Але, на жаль, приблизно 30 бійців із студентської сотні під час відступу заблукали і прийшли на станцію Крути, яку вже захопили червоні. На них чекала мученицька смерть. Розлючені великими втратами (а в бою тоді загинуло кілька сотень червоногвардійців) більшовики розстріляли полонених юнаків. Всього в бою загинуло приблизно 300 захисників Крут. Потім почали з\'являтися міфи. За часів української революції вони слугували певним політичним силам, які звинувачували Центральну Раду та її керівництво у загибелі молоді. Дуже прикро, що і нині ніхто не хоче чути правди. Але те, що сталося під Крутами, безперечно, - подвиг. Молоді люди відгукнулися на заклик захистити свою Батьківщину. Головне - це символ жертовності, віра у світлі ідеали України. У кожного народу є такі символи, на яких виховують покоління. У греків - це битва під Фермопілами, коли 300 спартанців царя Леоніда загинули, захищаючи прохід у Фермопільський ущелині. Вони програли цю битву царю персів Дарію, але потім греки перемогли персів. Росіяни у 1812 році програли французам битву під Бородіном, і Наполеон увійшов до Москви, хоча й потім програв війну. Цю поразку під Бородіном росіяни перетворили на перемогу, і вже скільки поколінь пишаються звитягою своїх військ. Нам цього треба навчитися. Останніми роками політики, приїжджаючи в Крути, більше говорять про трагедію і оплакують загиблих. Але герої цього бою - переможці. Вони здобули перемогу духу! І на цьому треба виховувати підростаюче покоління.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>- Олеже Олексійовичу, ви вже багато років поспіль 29 січня вшановуєте пам\'ять героїв Крут на місці тих трагічних подій. Чи змінилося ставлення місцевого населення до подвигу молодих патріотів?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- Я почав брати участь у вшануванні дня пам\'яті героїв Крут з 1992 року, коли цю дату ще не відзначали на державному рівні. З того часу я кожного року був серед тих, хто приїздив 29 січня на місце бою до пам\'ятного знаку загиблим. Тоді місцеве населення, під впливом комуністів - голів сільських рад, колгоспів та районних начальників, вороже ставилося до патріотично налаштованої молоді, називали нас націоналістами, бандерівцями. Пам\'ятаю, як ламали хрести, встановлені на місці бою, як на нас махали ціпками бабусі. Але з часом, коли люди з довколишніх сіл почали прислухатися до того, що ми їм розповідали, коли почули про те, що насправді тут відбувається, їхнє ставлення помінялося. Вони почали згадувати перекази старших людей про ті події січня 1918 року. І тепер місцеві мешканці самі беруть участь у вшануванні загиблих. Люди повинні знати історичну правду. Дуже добре, що день пам\'яті героїв Крут почали відзначати на державному рівні, що тут зараз будується достойний пам\'яті героїв меморіал. Адже на цьому місці розумієш справжню історію України. Це наша жива пам\'ять!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>- Але чи справді всі сьогодні розуміють значення подвигу під Крутами?</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">- На жаль, ні. Адже дехто продовжує жити в полоні історичних та ідеологічних міфів. Лише торік невідомі негідники двічі паплюжили монумент в Крутах. Злочинці розливали фарбу, писали на пам\'ятнику нецензурні слова та антиукраїнські гасла. Але наша партійна організація та її молодіжне крило взяли на себе місію відновлення монумента та дотримання порядку навколо нього. На щастя, сучасні молоді люди в Україні вже оцінюють історичні реалії зовсім інакше. Вони вже знають, за що і чому під Крутами 90 років тому полягли в нерівному бою їхні однолітки. Тому й приїздять сюди кожного року 29 січня.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Розмову вели Валерій ЛИТОВЧЕНКО,Сергій ПАВЛЕНКО.</strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чернігів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.golos.com.ua/</span></span></p>','Олег ОБУШНИЙ: «Бій під Крутами — це урок, який потрібно вивчити й запам’ятати»','uk',1055,2029,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p><img src=\"http://www.vasilkov.info/images/vv/krytu.jpg\" border=\"0\" alt=\"\" width=\"220\" height=\"165\" align=\"right\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Довідка:</strong> Битва пiд Крутами - битва, яка відбулася 29 січня 1918 р. поблизу селища Крути, на малій залізничній станцiї на вiдстанi 130 км вiд Києва. Битва тривала близько 5 годин і закінчилася поразкою українських загонів, які захищали підступи до Києва. Як така, битва не мала вирішального значення в перебігу військових дій, однак вона набула особливого значення в свідомості багатьох завдяки героїзму загону української молоді, яка загинула біля Крут. Під час поховання юнаків на Аскольдовій горі в Києві М. Грушевський назвав їх героями, Павло Тичина присвятив цьому героїчному вчинку вірша. У 2006 р. на місці битви встановили пам\'ятник і на 80-ті роковини випустили пам\'ятну гривню.</span></span></p>','2007-02-02 20:04:15',NULL,'P','<p><img src=\"http://www.vasilkov.info/images/vv/krytu.jpg\" border=\"0\" alt=\"\" width=\"220\" height=\"165\" align=\"right\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Довідка:</strong> Битва пiд Крутами - битва, яка відбулася 29 січня 1918 р. поблизу селища Крути, на малій залізничній станцiї на вiдстанi 130 км вiд Києва. Битва тривала близько 5 годин і закінчилася поразкою українських загонів, які захищали підступи до Києва. Як така, битва не мала вирішального значення в перебігу військових дій, однак вона набула особливого значення в свідомості багатьох завдяки героїзму загону української молоді, яка загинула біля Крут. Під час поховання юнаків на Аскольдовій горі в Києві М. Грушевський назвав їх героями, Павло Тичина присвятив цьому героїчному вчинку вірша. У 2006 р. на місці битви встановили пам\'ятник і на 80-ті роковини випустили пам\'ятну гривню.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">29 січня Україна відмічає День пам\'яті героїв, що загинули в битві під Крутами. Однак далеко не всі українці знають, що то за бій, з ким і за що він вівся. 27 січня 1918 р. з Києва назустріч більшовицьким військам, що наступали на Україну, вирушив добровольчий Студентський Курінь. Він складався зі студентів Університету імені Святого Володимира (нині Київський Національний Університет ім. Т. Шевченка), новоствореного Українського Народного Університету, а також із гімназистів Київських гімназій. Вони майже не мали ніякої бойової підготовки. Молодий цвіт нашої армії - юнаків - кидали майже в безнадійну ситуацію, тоді як серед шаліючої анархії десятки тисяч озброєного, випробованого в боях вояцтва безжурно демобілізувалося. Курінь мав на меті допомогти українським частинам утримати станцію Бахмач. По дорозі прийшло повідомлення, що в Бахмачі уже ворог. Тому загін зупинився відразу за Ніжином, на станції Крути - за 120 км від Києва. Перемога під Крутами давала шанси відбити Бахмач, зруйнувати колії і почекати підтягнення до Києва військових частин з регіонів. Поразка відкривала ворогові шлях на столицю. Окопи українських бійців тягнулися обабіч залізничного насипу на 3-4 км. Поряд зайняли оборону більш досвідчені бійці Української Військової Школи, в кількості 250 осіб. Студентський Курінь становив до 300 юнаків. Якщо додати чотири десятки старших, не дуже надійних бійців різних національностей, то українські сили під Крутами не нараховували навіть шести сотень бійців. Проти них рухався передовий загін військ Муравйова кількістю близько 6 тисяч військових, який підтримувала вогнем артилерія з бронепотягів. У захисників Крут була тільки одна гармата.<br /><img src=\"http://www.vasilkov.info/images/vv/Kruty2.jpg\" border=\"0\" alt=\"\" width=\"220\" height=\"165\" align=\"right\" /></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Студентським Куренем керував студент Українського Народного Університету Омельченко. Бій тривав цілий день з ранку 29 січня 1918 р. й до вечора. Вогнем 35 кулеметів та рушниць студенти й школярі змусили захлинутися кілька атак більшовиків. Увечері, після закінчення патронів, командир правого крила - учнів Військової Школи - дав наказ відступати. Ліве крило (Студентський Курінь) чи переплутало, чи взагалі не почуло і фактично пішло у наступ. У результаті більшість загинуло під час цього неузгодженого маневру. 35 потрапило до полону. 7 полонених більшовики не розстріляли і відправили в більшовицький тил. Один із них згодом чудом урятувався. Решту убивали тут же, під Крутами - спочатку розстрілювали, а потім добивали штиками і ножами. Як свідчили селяни, що бачили страту, учень 7 класу гімназії Пипський заспівав гімн \"Ще не вмерла Україна\", який підхопили інші. Потім убивці привели й розстріляли двадцять восьмого, який босоніж утік до села. Хату, що дала йому притулок, окупанти спалили.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Загалом втрати українців під час бою під Крутами становили близько 300 бійців загиблими. Втрати більшовиків під Крутами оцінюють за непрямими даними. Судячи з усього, вони немалі. Завдяки характеру бою під Крутами вони разом із пораненими могли становити до 2 тис. Відомо, що до Києва з 6 тисяч передового загону більшовиків дійшло 4 тис., хоча по дорозі до Києва, крім бою під Крутами, інших великих сутичок не було. Дорога до столиці України Муравйову була відкрита. 8 лютого 1918 р. він вступив до Києва, де почався червоний терор. У перший день захоплення Києва російські більшовики вбили 3 тис. громадян. Розстрілювали за \"буржуйський вигляд\", за \"косий погляд\", \"за кожне українське слово\" або й просто так, як писав очевидець тих подій поет Володимир Сосюра. Під враженням від тієї різанини Сосюра пішов у петлюрівські війська. 1944 р. він написав вірш \"Любіть Україну\", який за \"петлюрівщину\" і \"бандерівщину\" різко розкритикував Сталін та за який Сосюру виключили з партії. Загалом за неповний місяць окупації червоними було вбито за різними оцінками від 5 до 20 тис. громадян. Муравйов у своїх зведеннях писав, що він воював з \"гайдамаками\" та \"щирими українцями\".<br />Через півроку після бандитського погрому в Києві та відступу більшовиків, самого Муравйова було страчено в міжусобній бійці з більшовиками. За іншими даними, він дезертирував з більшовицьких військ і незабаром скінчив життя самогубством.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Усі багатотисячні людські жертви, вчинені російськими більшовиками в Києві й по дорозі до столиці, трапилися через безглузду і подекуди зрадницьку політику діячів Центральної Ради.<br />2 березня 1918 р. російсько-більшовицькі війська було вигнано з Києва, і 19 березня відбулося перепоховання згаданих вище 28 розстріляних полонених. Їх викопали з братської могили під Крутами. Над цією могилою місцеві селяни, які одразу зрозуміли, де \"наші\", за власною ініціативою після бою насипали курган. Серед упізнаних були студенти В. Шульгін, Божко-Божинський, О. Попович, Андріїв, Л. Дмитренко, І. Кулик, О. Шерстюк, Омельченко, Борозенко-Конончук, Головащук, Чижів, Кирик, гімназисти А. Соколовський, М. Ганкевич, Є. Тарнавський, Пипський, Гнаткевич. Інших впізнати було неможливо - так окупанти понівечили під час страти їхні обличчя. До цих жертв, перелічених у \"Киевской Мысли\" в день перепоховання, деякі історики додають ще прізвища таких студентів, як Пурик, Нітенко, Сірик, Дикий, Лизогуб і гімназистів Кольченка, Мисана. Решта загиблих були поховані на самому полі бою місцевими селянами, і їх до Києва уже не привозили.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Бій українських бійців під Крутами часто порівнюють з боєм грецьких вояків під Фермопілами. Подібність справді є - обидва загони пішли майже на вірну смерть проти багатократно сильнішого супротивника. 480 р. до н. е. спартанський цар Леонід добровільно визвався із загоном з 300 спартанців захищати Фермопільський прохід, поки решта 6 000 греків рятувалися від варварських військ. Під час героїчної оборони проти персів загинув і сам спартанський цар, і весь його загін, та ще воїни з сусіднього міста Феспій, які сказали, що їм соромно бути гіршими від спартанців та ховатися за чужі спини. Однак, між обома ситуаціями є принципова відмінність. Від вчинку спартанців подвиг українців відрізняється насамперед тим, що до складу загону спартанців увійшли виключно особисті охоронці царя Леоніда - загартовані в численних сутичках \"кращі з кращих\" бійців найбільш войовничого царства Греції, та й то лише зрілі воїни - ті, що вже мали синів.<br />В трагічному українському випадку непідготовлені студенти віддавали задля своєї країни все, що мали. У той час ті, хто зобов\'язаний був боронити країну, насамперед політики, маючи значно більше можливостей, ніж прості студенти, не виконали свій прямий обов\'язок перед країною. Політики Української Центральної Ради в 1917 р., діючи за соціалістичними гаслами, популярними на той час, перешкоджали спробам тверезих політиків, зокрема Симона Петлюри і Павла Скоропадського, створити українську армію.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Крім того, в Центральній Раді за оборону відповідали люди, які були тісно пов\'язані з Москвою, за деякими даними, агентурно.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Через це всі спроби організації та самоорганізації Українського війська гасилися згори. Було свідомо припинено бурхливе зростання Вільного козацтва, яке об\'єднувало десятки тисяч боєздатних козаків у регіонах. Козакам зверху наказувалося не виявляти ніякої ініціативи. Також було наказано розформуватися цілій низці українізованих частин російської армії, які виявляли готовність воювати з російськими військами - чи то білими, чи то червоними. Усім їм наказали розійтися по домівках. Фактично, це була зрада. Між тим, Голова Центральної Ради Михайло Грушевський був людиною науки, геніальним істориком, проте далеким від реальної політики. Чи не найголовнішою вадою національної безпеки стало те, що в Україні не було організовано внутрішньої безпеки та протидії більшовицькій пропаганді, яка роз\'їдала військові частини, що залишилися. У самому Києві діяло багато агентів і провокаторів, які готували путч. Тому коли російські війська під командуванням Михайла Муравйова (кількістю близько 30 тис.) рушили в Україну, їм реально не було кого протиставити. Центральна Рада поспіхом почала шукати вірні частини, зв\'язок не працював, а тому швидко викликати з регіонів й організувати розпущені війська не було ніякої змоги. Влада мала під рукою кількісно дуже невеликі сили. Варто відзначити, що вже через рік і через два після Крут, під час грандіозних антибільшовицьких повстань в Україні, пересічне українське село виставляло більше бійців за Україну, ніж їх було під Крутами в січні 1918 р. Навіть не було проголошено мобілізації - лише зроблено заклик до патріотів, та й то тільки в Києві, а не в околицях, які могли дати тисячі бійців. За кілька тижнів після бою під Крутами М. Грушевський писав: \"Недаремно пролилася кров тисяч українських інтелігентів та молоді, коли вона принесла духовне визволення від найтяжчого і найшкідливішого московського ярма: добровільно прийнятого духовного закріпачення!\" Однак, такого закріпачення вже не було в тих бійців, які загинули під Крутами. Воно лишалося тільки в безглуздих і безвідповідальних політиків, які виявилися нездатними мобілізувати свій народ і перемогти в тих умовах, коли поразка означала смерть.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.vasilkov.info/</span></span></p>','Бій під Крутами — героїчна сторінка історії України','uk',1058,2032,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p><img src=\"image,3650,200,_MG_3647.JPG\" alt=\"\" width=\"162\" height=\"200\" /></p>','2010-10-09 17:59:21',NULL,'N','<p><img style=\"float: left;\" src=\"image,3650,200,_MG_3647.JPG\" alt=\"\" /></p>','Омеліан Ковч','uk',2167,3650,0,'Віталіна Онишкевич'),
(1,NULL,'<p><img style=\"border: 0pt none; float: left;\" title=\"Михайло Грушевський\" src=\"http://www.kruty.org.ua/images/stories/grushevski.jpg\" alt=\"Михайло Грушевський\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Михайло Грушевський</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Dulce et decorum pro patria mori!</strong> Солодко і гарно вмерти за отчизну - каже латинський поет, поезії котрого були шкільною книжкою тих, котрих тепер ховаємо. Солодко і гарно! Се затямили вони - і не опустили тієї рідкої нагоди, яку давала їм нинішня велична хвиля відбудування нашої держави і охорони вільностей і прав нашого трудящого люду. Вони стали грудьми за свою батьківщину і мали щастя полягти головами в цій святій боротьбі. Велике щастя згинути так, в боротьбі, а не дезертирами, не нейтральними, не замішаними в юрбі страхополохами, що безплатними пасажирами силкуються прослизнути в нове царство української свободи.</span></span></p>\r\n<p>&nbsp;</p>','2008-05-17 12:52:46',NULL,'P','<p><img style=\"border: 0pt none; float: left;\" title=\"Михайло Грушевський\" src=\"http://www.kruty.org.ua/images/stories/grushevski.jpg\" alt=\"Михайло Грушевський\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Михайло Грушевський</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> Dulce et decorum pro patria mori!</strong> Солодко і гарно вмерти за отчизну - каже латинський поет, поезії котрого були шкільною книжкою тих, котрих тепер ховаємо. Солодко і гарно! Се затямили вони - і не опустили тієї рідкої нагоди, яку давала їм нинішня велична хвиля відбудування нашої держави і охорони вільностей і прав нашого трудящого люду. Вони стали грудьми за свою батьківщину і мали щастя полягти головами в цій святій боротьбі. 	Велике щастя згинути так, в боротьбі, а не дезертирами, не нейтральними, не замішаними в юрбі страхополохами, що безплатними пасажирами силкуються прослизнути в нове царство української свободи. Велике щастя окупити своєю кров\'ю забезпечення цієї свободи!	От у цій хвилі, коли провозяться їх домовини перед Центральною Радою, де протягом року кувалась українська державність, з фронтону її будинку здирають російського орла, ганебний знак російської власті над Україною, символ неволі, в котрій вона прожила двісті шістдесят з верхом літ. Видно, можливість його здерти не давалась даремно, видно, вона не могла пройти без жертв, її треба було купити кров\'ю. І кров пролили ці молоді герої, котрих ми нині проводжаємо!	Вони щасливі, що могли купити своєю кров\'ю такі вартості своєму народові! Батьки, брати, сестри тих, котрих ми сьогодні ховаємо! Стримайте сльози, що котяться з ваших очей, як стримую я. Бо ж ті, котрих ви ховаєте, доступили найвищого щастя - вмерти за отчизну! їх слава і вдячна пам\'ять про них житиме з нашою свободою разом, серед народу нашого однині і довіку!  М. Грушевський. Хто такі українці і чого вони хочуть. Київ, 1991, с. 202.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.kruty.org.ua/</span></span></p>',' Михайло Грушевський - Промова біля Центральної Ради під час похорону крутянців','uk',1255,2229,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p><img style=\"border: 1px solid black; float: right;\" src=\"http://www.realtors.com.ua/news/images/2008/07/12/4878d50562dad.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Уже кiлька мiсяцiв триває конфлiкт мiж громадськiстю й приватними забудовниками навколо Аскольдової могили, iсторичної мiсцевостi в центрi столицi. В травнi на будмайданчик напали альпiнiсти. Вони видерлися на пiдйомний кран i провисiли на ньому кiлька годин на знак протесту проти незаконного, на їхню думку, будiвництва...</span></span><strong> </strong></p>','2008-07-12 21:38:30',NULL,'P','<p><img style=\"border: 1px solid black; float: right;\" src=\"http://www.realtors.com.ua/news/images/2008/07/12/4878d50562dad.jpg\" alt=\"\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>Уже кiлька мiсяцiв триває конфлiкт мiж громадськiстю й приватними забудовниками навколо Аскольдової могили, iсторичної мiсцевостi в центрi столицi. В травнi на будмайданчик напали альпiнiсти. Вони видерлися на пiдйомний кран i провисiли на ньому кiлька годин на знак протесту проти незаконного, на їхню думку, будiвництва... </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"> Три тижнi тому, коли забудовник спробував поновити роботи, його атакували кiлькадесят людей, серед яких - i рядженi в давньоруськi шоломи й кольчуги, вони вщент розтрощили будiвельний паркан. То була акцiя &laquo;Аскольд постає iз могили&raquo;, iнiцiйована громадським рухом &laquo;Збережи старий Київ&raquo;. Та, попри громадську думку й публiчнi заяви мiської влади про неприпустимiсть будiвництва в цьому мiсцi, забудовник не поспiшає забиратися геть. Iмовiрно, вiн вичiкує слушну мить, коли можна буде перейти до рiшучiших дiй. Адже орiєнтовний прибуток вiд продажу квартир i офiсiв у запланованому будинку може сягнути $100 млн, вважають експерти.<br />&nbsp;<br />Таємницi мерського двору<br />&nbsp;<br />Неодноразово висловлювалася думка, що за комерцiйною компанiєю, котра намагається звести на вул. Мазепи, 11а багатоповерховий елiтний будинок, стоїть чемпiон свiту з боксу Вiталiй Кличко i його соратники з однойменного полiтичного блоку. Пiдтвердженням таких заяв може бути те, що сам чемпiон i його поплiчники, котрi зазвичай послiдовно критикують забудову центру Києва, уникають рiзких коментарiв стосовно Аскольдової могили. Водночас Тиждень вiдстежив бiзнес-зв\'язки компанiй, що претендують на iсторичну мiсцину, й з\'ясував: брати Клички та їхнi партнери певною мiрою таки причетнi до скандалу. I не тiльки вони.<br />&nbsp;<br />Зв\'язки найкращих українських боксерiв iз будiвельним бiзнесом &laquo;засвiтилися&raquo; ще на столичних виборах у 2006 роцi. Тодi, як i на недавнiх позачергових виборах мiського голови, цю iнформацiю використовували опоненти як контрпропаганду проти Блоку Кличка. Також не таємниця, що брати-боксери зароблене на ринзi вкладають, крiм iншого, в столичну нерухомiсть. Зокрема, їм приписують частку в офiсно-розважальному центрi &laquo;Арена&raquo;, хоча вони це й спростовують. Та й сам факт присутностi в перелiку спонсорiв боксерського блоку i серед членiв його фракцiї в Київрадi такого видатного будiвельника, як Лев Парцхаладзе (компанiя ЗАТ &laquo;IК &laquo;ХХI сторiччя&raquo;), свiдчить, що будiвельний бiзнес Вiталiю Кличку i Ко не зовсiм байдужий.<br />&nbsp;<br />Натомiсть виникає запитання: чому новий-старий мер Леонiд Черновецький не чинить перешкод у збагаченнi на київськiй землi своїм полiтичним супротивникам, до яких смiливо можна вiднести Вiталiя Кличка? Речники мiського голови посилаються на те, що землю на Аскольдовiй могилi видiляли ще в 2004 роцi за часiв мерства Олександра Омельченка, з яким у братiв Кличкiв давнi дружнi стосунки. Та слiд зауважити, що право власностi на 0,89 га на Аскольдовiй могилi ТОВ &laquo;Вант&raquo;, компанiя-забудовник, отримала лише в березнi минулого року, коли влада над Києвом перейшла до Леонiда Михайловича, а в залi Київської мiської ради вже давно тривало протистояння мiж блоками Кличка й Черновецького. Автор цих рядкiв, перебуваючи в стiнах КМДА, особисто був свiдком конфлiктних ситуацiй з елементами рукопашного з боксу утримувався, напевне, щоб нiкого ненароком на скалiчити. I якось важко повiрити, що Леонiд Черновецький, попри його неаби-яку набожнiсть, мiг би подiлитися коштовною столичною землею зi своїм затятим полiтичним ворогом. Тож можна припустити, що або Кличко до Аскольдової могили жодним чином не причетний, або... Але про все по черзi.<br />&nbsp;<br />Кличко, кiно i казино<br />&nbsp;<br />Аби з\'ясувати, хто насправдi стоїть за тим чи iншим будiвництвом, треба знати точну назву замовника й генпiдрядника та мати деякi зв\'язки у Державнiй податковiй адмiнiстрацiї. Втiм, маючи все необхiдне, вiддiл розслiдувань Тижня ледь не зiрвав редакцiйний графiк, дослiджуючи хитросплетiння комерцiйних iнтересiв довкола Аскольдової могили.<br />&nbsp;<br />На iнформацiйному стендi бiля будмайданчика на Мазепи, 11а вказано, що замовником будiвництва є ТОВ &laquo;К. А. Н. Девелопмент&raquo;, засноване у травнi 2006 року не вiдомими широкому загаловi громадянами Сандлером i Ковбасенком. Генпiдрядником вони обрали будiвельну корпорацiю &laquo;Алтiс-Холдинг&raquo;. Ця структура неодноразово брала участь у втiленнi в життя грандiозних проектiв компанiї &laquo;ХХI сторiччя&raquo; Лева Парцхаладзе. Втiм, погодьтеся, це ще нiчого не доводить.<br />&nbsp;<br />Логiчно було б припустити, що засновники &laquo;К. А. Н. Девелопмент&raquo; збираються фiнансувати будiвництво фешенебельного хмарочоса не з власної кишенi. Не виключено також, що ця фiрма є просто ширмою, за якою ховаються справжнi дiйовi особи. Вийти на них нам допоможе генеральний директор &laquo;К. А. Н. Девелопмент&raquo; Андрiй Гуцал. Саме вiн вiд iменi забудовника переконував обурену громадськiсть, що будiвництво на Аскольдовiй могилi цiлком законне. У 2004 роцi, коли цей майданчик вiддавали пiд забудову, Гуцал Андрiй Олександрович заснував уже згадане ТОВ &laquo;Вант&raquo;. Забiгаючи наперед, зазначу, що переважна бiльшiсть причетних до цiєї справи будiвельних компанiй створювалися чомусь саме 2004 року. I, готовий закладатися гонораром, що майже в жодної з них немає анi необхiдної технiки, анi квалiфiкованих робiтникiв, анi власних оборотних коштiв, щоб зводити хмарочоси. Тобто їхнє призначення, швидше за все, - отримати земельнi дiлянки пiд будiвництво, не викриваючи справжнiх власникiв.<br />&nbsp;<br />Другим спiвзасновником ТОВ &laquo;Вант&raquo; є Олег Мойсеєнко. Вiн, у свою чергу, має частку в фiрмi &laquo;К. А. Н. &raquo;, серед спiвзасновникiв якої є такий собi Олександр Лати- шев. Останнiй пов\'язаний з двома будiвельними компанiями: &laquo;Септада&raquo; i ВБК &laquo;Градобуд&raquo;. Обидвi певним чином перебувають пiд впливом київського банкiра Олександра Лойфенфельда, який встиг трошки &laquo;засвiтитись&raquo; у скандалi на Пейзажнiй алеї (див. Тиждень №6,2008). Олег Мойсеєнко також є спiввласником ТОВ &laquo;Арена-центр&raquo;, профiль якого - ресторани. Його партнер у цiй справi - Олександр Булино. У 2006 роцi його згадували в якiйсь спортивнiй газетi в замiтцi про юнацький боксерський чемпiонат у Бердичивi, який Булино вiдвiдав як представник братiв Кличкiв. Крiм цього, пановi Булино належать частки в трьох комерцiйних структурах, зокрема в &laquo;Кристал плюс&raquo;, що контролює столичне казино &laquo;Арена&raquo;, i ЗАТ &laquo;Театральне&raquo;, серед акцiонерiв якого - не вiдомий громадськостi бiзнесмен Валерiан Кiм. Комерцiйнi iнтереси й родиннi зв\'язки пана Кiма ведуть до потужної мережi ресторацiй &laquo;Козирна карта&raquo; й низки операторiв грального бiзнесу. До речi, Тиждень уже писав, що будiвельникам теж не байдужi азартнi iгри (див. № 20,2008). Серед спiввласникiв уже згаданого &laquo;Кристал плюс&raquo; є такий собi Iгор Костянтинович Сiзов. За моїми даними, вiн спiввласник i керiвник щонайменше п\'яти фiрм, три з яких - будiвельнi. Одна з них, ТОВ &laquo;ВIТ-плюс&raquo;, торiк отримала вiд столичної мерiї дозвiл на будiвництво готелю на Подолi, iнша, ТОВ &laquo;Вест Iст Груп&raquo;, 2006-го фiгурувала в скандалi навколо забудови Мiлютинського парку в Алупцi (Крим). Цi фiрми пов\'язанi з компанiєю &laquo;Пiвнiчна будiвельна група&raquo;, яку в 2004 роцi заснувала в Києвi кiпрiотська &laquo;Ферфакс&raquo;. Серед керiвникiв цього офшору й будiвельної групи згадувався Юрiй Iонанов, однокласник i друг Вiталiя Кличка. Iонанов, мiж iншим, також керував ТОВ &laquo;Кватро М&raquo;, яке у 2003 роцi заснували американський громадянин Роберт Кьонiг, Кличко-старший та Павло Димiнський - вiднедавна член &laquo;боксерської&raquo; фракцiї в Київрадi. Пан Кьонiг, до речi є спiввласником ще дев\'яти комерцiйних структур в Українi, серед яких двi спецiалiзуються на рестораторськiй справi, одна - оператор грального бiзнесу й три - сiнематики: ТОВ &laquo;Батерфляй&raquo;, ТОВ &laquo;Кроу&raquo; та ТОВ &laquo;Енкор&raquo;. Останнє в 2005-му стало партнером братiв Кличкiв у придбаннi столичного кiнотеатру &laquo;Загреб&raquo;.<br />&nbsp;<br />Куди ведуть родиннi зв\'язки<br />&nbsp;<br />Згаданий ранiше Андрiй Гуцал, представник замовника будiвництва на Аскольдовi могилi, 2004-го заснував ще й ТОВ &laquo;Будконсалтинг- iнвест&raquo;, якому 24 грудня минулого року мер ЛеонiдЧерновецький дозволив будувати багатоповерхiвку на проспектi Науки, 42а. Вiд заснування &laquo;Будконсалтингом&raquo; керував 28-рiчний Iгор Николин. Вiн пов\'язаний з iще п\'ятьма будiвельними фiрмами. Одна з них, ТОВ &laquo;Мелвiкс&raquo;, наприкiнцi 2006-го отримала дiлянку бiля центрального столичного РАГСу пiд будiвництво 52-поверхового торговельно-офiсного комплексу 250 м заввишки й вартiстю $600 млн. Хоча цей проект поки що залишається на паперi. Партнерами Николина в трьох iнших фiрмах - &laquo;Ярославград&raquo;, &laquo;БП &laquo;Онат&raquo; i &laquo;Седна&raquo; - виступають молодi пiдприємцi Сергiй та Андрiй Шевченки (останньому, до речi, 22 роки). Сергiй Шевченко входить то складу власникiв ще двох будiвельних фiрм - &laquo;Архiтектурно-будiвельний альянс&raquo; та &laquo;Iнтер- будкомплекс&raquo;, iнший спiввласник яких - Андрiй Кощенко. Вiн, у свою чергу, причетний до низки комерцiйних структур, з яких мене зацiкавили двi - Торговельний дiм &laquo;Демiк&raquo; та будiвельна компанiя &laquo;Еверест Плюс&raquo;.<br />&nbsp;<br />За iнформацiєю Тижня, &laquo;Демiк&raquo; контролюють промисловець iз Чернiвцiв Микола Дембiцький та депутат Київської мiської ради вiд Блоку Кличка Дмитро Андрiєвський. У сферi впливу останнього - потужний виробник будматерiалiв &laquo;Iнтеко-холдинг&raquo; та один iз найбiльших будiвельникiв комунiкацiй - трест &laquo;Київпiдземшляхбуд&raquo;.<br />&nbsp;<br />Натомiсть &laquo;Еверест плюс&raquo; входить до холдингу &laquo;УкоIнвестБуд&raquo;, який контролюють київський бiзнесмен Анатолiй Войцеховський та його сини Вiктор i Олександр. Холдинг складається з майже 40 пiдприємств - девелоперських, будiвельних, пiдрядних. Цiкаво, що головний iнженер холдингу Григорiй Лiберман - батько вiдомої поп-спiвачки Тетяни Лiберман, бiльш вiдомої як Тiна Кароль.<br />&nbsp;<br />У 2006 роцi в &laquo;УкоIнвестБуду&raquo; сталася неприємна iсторiя з приватними iнвесторами будiвництва житлового будинку, якi з ним судилися й звинувачували в шахрайствi. А в 2007 роцi ЗАТ &laquo;Укренергобуд&raquo;, що входить до холдингу, фiгурувало в черговому скандалi довкола незаконного видiлення київської землi пiд забудову. Тодi ж &laquo;випливла&raquo; iнформацiя, що головними акцiонерами ЗАТ є зять мера В\'ячеслав Супруненко та Денис Комарницький, депутат Київради вiд блоку Черновецького.<br />&nbsp;<br />&laquo;Так хто ж насправдi стоїть за будiвельним скандалом на Аскольдовiй могилi?&raquo; - запитає, напевне, допитливий читач. Аби дати вiдповiдь, треба знати про циркуляцiю фiнансових коштiв через банкiвськi рахунки фiгурантiв, якi є комерцiйною таємницею, що охороняється законом. Адже сумнiвно, що ТОВ &laquo;К. А. Н. Девелопмент&raquo;, замовник будiвництва, чи заявник на земельну дiлянку ТОВ &laquo;Вант&raquo; (статутний фонд 35 тис. грн i 20,5 тис. грн вiдповiдно станом на 2006 рiк) розраховують самотужки профiнансувати проект, собiвартiсть якого оцiнюють у кiлькадесят мiльйонiв доларiв. Можна припустити, що кошти видiляє дехто з причетних до описаної вище мережi комерцiйних структур, пов\'язаних менеджментом чи номiнальними засновниками. I це можуть бути як брати Клички з полiтичними партнерами, так i Черновецький та Ко. Можливо, причетнi й однi, й iншi одночасно. Хоча не виключено, що хтось (або жоден) з iменитих iнвесторiв цього бiзнесового &laquo;лабiринту&raquo; вiд початку не дуже цiкавився, в якi проекти вкладають його грошi партнери i найманi управлiнцi.<br />&nbsp;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Адже капiтал має, перш за все, примножуватися, iнакше вiн з часом неодмiнно зникне, i вам знову доведеться жити вiд зарплати до зарплати.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.realtors.com.ua/</span></span></p>','Навколо Аскольдової могили триває конфлікт','uk',1003,1977,0,'Любов Михайлюк');
INSERT INTO `pub_items` (`pbi_show_date`, `pbi_rss_id`, `pbi_text_short`, `pbi_date`, `pbi_rss_url`, `pbi_state`, `pbi_text`, `pbi_title`, `pbi_lang_id`, `pbi_id`, `pbi_pub_id`, `pbi_can_comment`, `pbi_author`) VALUES
(1,NULL,'<p><img style=\"float: left;\" src=\"http://www.dt.ua/img/st_img/2006/582/foto-52416-12211.jpg\" alt=\"\" width=\"230\" height=\"200\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У новітній Україні стало вже звичним наприкінці січня кожного року привертати громадську увагу до епізоду, що трапився в розпал революційного зламу, - бою під Крутами. Здавалося б, майже за дев\'ять десятиліть можна достеменно відтворити картину того, що справді сталося, і, врешті, дати неупереджену виважену кваліфікацію як власне самому епізодові, так і набагато ширшій проблемі, яку він (цей епізод) напрочуд рельєфно висвітлює.</span></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>','2009-02-03 15:35:03',NULL,'P','<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><img style=\"float: left;\" src=\"http://www.dt.ua/img/st_img/2006/582/foto-52416-12211.jpg\" alt=\"\" width=\"230\" height=\"200\" /></span></span></p>\r\n<h4 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Автор: Валерій СОЛДАТЕНКО  (доктор історичних наук)</span></span></h4>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">\r\n<p>У новітній Україні стало вже звичним наприкінці січня кожного року привертати громадську увагу до епізоду, що трапився в розпал революційного зламу, - бою під Крутами. Здавалося б, майже за дев\'ять десятиліть можна достеменно відтворити картину того, що справді сталося, і, врешті, дати неупереджену виважену кваліфікацію як власне самому епізодові, так і набагато ширшій проблемі, яку він (цей епізод) напрочуд рельєфно висвітлює.</p>\r\n<p>Однак бій під Крутами, вочевидь, належить до тих феноменів, навколо яких від самого початку сплелися в тугий вузол життєва істина, її карколомна трансформація на догоду політиці й кон\'юнктурне використання складно оформленого в результаті паліативу.</p>\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Із суто наукового, власне, фактологічного погляду великих проблем щодо передісторії, значення бою, його наслідків практично не виникає.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Українська революція на середину січня 1918 р. пройшла досить довгий, складний і, загалом, результативний шлях. Під керівництвом Центральної Ради національний ентузіазм, прагнення нації до всебічного відродження вдалося втілити в реальні досягнення. Ще за часів Тимчасового уряду, всупереч величезній протидії останнього, було запроваджено автономію України. 7 листопада 1917 р. проголошено створення Української Народної Республіки. Однак полі-тичні орієнтації проводу УНР відразу ввійшли в гостру суперечність із курсом ленінського Раднаркому, вилилися в антагоністичний конфлікт двох революцій: соціальної (у загальноросійському масштабі, захоплю-ючи у сферу свого вибухоподібного розвитку й український простір) і національно-визвольної, спрямованої на демократичне розв\'язання українського питання.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Незважаючи на офіційно оголошений ще на початку грудня 1917 р. стан війни (ленінський &laquo;Маніфест до українського народу з ультимативними вимогами до Української ради&raquo; й негативна відповідь на нього Центральної Ради), конфлікт близько місяця протікав мляво, зводився більше до пошуку шляхів виходу з кризи дипломатичними методами. Після І Всеукраїнського з\'їзду Рад у Харкові (11-12 грудня 1917 р.), проголошення України радянською республікою, оформлення двох центрів влади, які не лише не визнавали один одного, а й накопичували взаємну ворожість, антагонізм, стало очевидним: відвернути воєнне зіткнення просто неможливо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Потенції прибічників соціалістичної революції в Україні примножувалися військовою допомогою з Радянської Росії. Ця допомога почала надходити з середини грудня. За підрахунками фахівців (цифри легко підтверджуються архівними документами), в Україну з Росії на січень 1918 р. прибуло 32 тис. червоно-гвардійців та зведених у загони революційних солдатів. 22 тисячі з них через Харків прямували на Донбас, а далі - у напрямку Ростова-на-Дону. Дорогою їх кількість зростала за рахунок поповнення загонами донецьких червоногвардійців. Мета, поставлена В.Леніним перед Верховним головнокомандуючим по боротьбі з контрреволюцією на Півдні Росії В.Антоновим-Овсієнком - розгром кадетсько-каледінського заколоту, - реалізовувалася неухильно.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З решти ж російських військових формувань переважна частина відправлялася Лівобережжям - через Катеринослав (нині Дніпропетровськ), Олександрівськ (Запоріжжя) на Одесу, Миколаїв і Херсон для участі у встановленні й закріпленні в життєво важливих пунктах республіки влади Рад. Цей напрям виявився, по суті, флангом каледінського фронту і тут відзначалися особливою активністю частини, що прямували з Південно-Західного й Румунського фронтів на Дон.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Події на каледінському фронті, сході й півдні України розвивалися дуже стрімко й виявилися загалом сприятливими для сил соціалістичної революції. В загальний успіх вагомий внесок робили повстання, які вибухали на місцях із наближенням радянських військ. Усе це дало змогу більшовикам паралельно перевести у практичну площину плани походу й на Київ - столицю УНР.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Із січня 1918 р. з Харкова у напрямку Полтави під командуванням М.Муравйова вирушив зведений загін із червоногвардійців міста, червоних козаків В.Примакова і одного бронепотяга - всього 700 бійців. Того ж дня у тому ж напрямку вирушив загін катеринославських, донецьких і московських робітників під командуванням П.Єгорова. З 1200 вояків 450 були донецькими червоногвар-дійцями під командуванням Д.Жлоби.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хоча у Полтаві дислокувалося п\'ять тис. солдатів і офіцерів, відданих Центральній Раді, 5 січня вони здали місто без бою. А вже через кілька днів переможці, поповнивши свої лави полтавськими червоногвардійцями під командуванням місцевого більшовика С.Козюри, почали наступ залізницею через Гребінку - Ромодан на захід і північний захід. Паралельно у напрямку Києва почався рух інших загонів, що спішно формувалися. Це були революційно налаштовані елементи, які прагнули політичної дії й охоче відгукувалися на заклики нищення старого ладу, боротьби за новий, справедливіший, гуманніший соціальний устрій. Загалом чисельність таких формувань була незначною, хоча вони гучно іменувалися &laquo;революційними арміями&raquo;. Скажімо, у загоні Р.Берзіна, що наступав з Могилева через Гомель на Бахмач, і в загоні С.Кудинського, що з району Новгорода прямував через Смоленськ - Брянськ - Калиновичі на Новозибків, кількість багнетів ледь перевищувала дві тис. у кожному. Сумарно ж кількість військовиків, які брали участь у поході на Київ, була трохи більшою за шість тис., хоча їх було найменовано Східним фронтом, а його головнокомандуючим призначено полковника М.Муравйова.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Об\'єктивні дослідники давно відійшли від тверджень про те, що процес творення української державності був перерваний агресією численних армій (&laquo;орд&raquo;) Радянської Росії. Надзвичайно авторитетний в діаспорних колах історик зі схильністю до виважених філософських узагальнень І.Лисяк-Рудницький зазначав: &laquo;Легенда, що її треба здати до архіву, це казка про &laquo;надчисленні полчища&raquo; ворогів, що під їх ударами буцімто завалилася українська державність. У дійсності інтервенційні московські армії під час першої та другої навали (зими 1917-18 рр. та 1918-19 рр.) були відносно малі. Кремль до літа 1919 року не диспонував великою регулярною армією. Совєтська експансія була здатна поширюватися на ті країни, що їхнє власне безголів\'я робило з них легку здобич&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Доводиться констатувати: навколо сил Центральної Ради наприкінці 1917 р. - на початку 1918 р. створювався справжній вакуум. Вони з катастрофічною швидкістю втрачали підтримку мас, а деморалізовані військові частини хутко розбігалися звичайно ще до прямих зіткнень з радянськими військами. Ці ж тенденції захопили й &laquo;Вільне козацтво&raquo;, почесним отаманом якого був П.Скоропадський.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За оцінками відомого історика Д.Дорошенка, під орудою національної влади тоді було не більш ніж 15 тис. розкиданих по різних місцях вояків. При цьому слід мати на увазі, що боєздатність українських частин була дуже невисокою. Чи не єдиною бойовою одиницею був Український Гайдамацький Кіш Слобідської України, який складався з двох куренів - &laquo;червоних&raquo; і &laquo;чорних гайдамаків&raquo;. Укомплектований солдатами-фронтовиками, юнкерами, січовими стрільцями та галицько-буковинськими біженцями і полоненими (Є.Коновалець, А.Мельник, Ф.Сушко та ін.), кіш налічував усього одну тис. чоловік. Його очолив С.Петлюра, відправлений у грудні 1917 р. у відставку за допущені недоліки у справі українізації армії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Навпаки ж, військовий потенціал радянської влади в Україні неухильно примножувався. За реаліс-тичними оцінками, у січні 1918 кількість червоногвардійських та інших однотипних формувань, що виступали на боці радянської влади, сягала 120 тис. чоловік. Звісно, їх бойова потужність не могла порівнюватися з якостями регулярного війська. Червоногвардійські загони було важко зібрати в єдине ціле для виконання важливих стратегічних завдань. Та з другого боку, наявні червоногвардійські загони відтягували на себе сили противника, сковували, розпорошували їх. Воєнні дії виплеснулися не на поля битв за участю великих армій, а в робітничі квартали, на підприємства, в казарми, де кінцевий успіх забезпечувався бойовими якостями та моральним станом невеликих загонів і груп.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Розстановка сил в Україні у січні 1918 р. складалася зовсім не на користь національної влади. Голова Генерального Секретаріату В.Винниченко присвятив з\'ясуванню цього аспекту вітчизняного історичного досвіду чимало сторінок у тритомнику &laquo;Відродження нації&raquo;, й дійшов урешті до однозначно невтішних висновків. &laquo;...Це була війна впливом, - писав один з керманичів Української революції. - Ні більшовики, ні ми не мали регулярного, дисциплінованого війська, яким можна було розпоряджатися по волі керуючого центру, незважаючи на те, що й як собі там думало й почувало військо.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Наш вплив був менший. Він був уже остільки малий, що ми з великими труднощами могли складати якісь невеличкі більш-менш дисципліновані частини й висилати їх проти більшовиків. Більшовики, правда, теж не мали великих дисциплінованих частин, але їхня перевага була в тому, що всі наші широкі маси солдатства не ставили їм ніякого опору або навіть переходили на їхній бік, що майже все робітництво кожного міста ставало за ними, що в селах сільська біднота явно була більшовицька, що, словом, величезна більшість самого українського населення була проти нас.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Єдиною активною мілітарною нашою силою була наша інтелігентна молодь і частина національно-свідомого робітництва, яке гаряче стояло за українську державність, розуміючи за нами ту державність так само, як і ми її розуміли...&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">З початком наступу на Київ червоних, в умовах загальної розгубленості, навіть суцільної паніки, здатними на самовідданий порух виявилися передусім студенти й гімназисти. Уже 5 січня 1918 р., тобто у день здачі Полтави, на зборах студентів молодших курсів Київського університету св. Володимира і новозаснованого Українського народного університету, скликаних за ініціативою студентів-галичан, було ухвалено приступити до створення студентського куреня Січових Стрільців. До формування &laquo;під загрозою бойкоту й виключення з української студентської сім\'ї мали вступити всі студенти-українці&raquo;. Окрім студентів, до складу куреня було залучено учнів двох старших класів 2-ї української ім. Кирило-Мефодіївського братства гімназії. Загалом записалося близько 200 осіб (друга сотня потім брала участь у боях у Києві, тобто не залишала міста). Військові власті призначили командиром старшину (сотника) Омельченка, який на той час був зарахований студентом Українського народного університету.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вище державне керівництво, безперечно, було добре поінформоване про патріотичний порух молоді й навіть морально, та й ідейно, підтримало його. Так, 11 січня 1918 р. &laquo;Нова Рада&raquo; - газета українських соці-алістів-федералістів, впливової фракції у Центральній Раді, - опублікувала звернення &laquo;До українського студентства&raquo;, підписане українською фракцією центру Університету св. Володимира: &laquo;Прийшов грізний час для нашої Батьківщини. Як чорна гайворонь, обсіла нашу Україну російсько-&laquo;большевицька&raquo; (котра нічого спільного не має з ідейним большевизмом) грабіжницька орда, котра майже щодня робила у нас нові захвати, і Україна, одрізана звідусіль, може врешті опинитись в дуже скрутному стані. В цей час Українська фракція центру Університету св. Володимира кличе студентів-українців усіх вищих шкіл негайно прийти на підмогу своєму краєві і народові, одностайно ставши під прапор борців за волю України проти напасників, які хотять придушити все, що здобуто нами довгою, тяжкою героїчною працею. Треба за всяку ціну спинити той похід, який може призвести Україну до страшної руїни і довговічного занепаду. Хай кожен студент-українець пам\'ятає, що в цей час злочинно бути байдужим... Сміливо ж, дорогі товариші, довбаймо нашу скелю і йдімо віддати, може, останню послугу тій великій будові, яку ми ж самі будували - Українській державі!</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Записуйтесь до &laquo;Куреня Січових Стрільців&raquo;, який формується з студентів Університету св. Володимира та Українського Народного Університету, звідки, мабуть, ми будемо розподілені серед декотрих українських військових частин, для піднесення культурно-національної свідомості та відваги...&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У цьому ж номері &laquo;Нової Ради&raquo; містився такий заклик: &laquo;Всі товариші, що вписались в курінь, повинні негайно явитись в казарму куреня (Печерськ, Московська вул., Костянтинівська військ. школа)&raquo;. Такі матеріали друкувалися й іншими органами преси.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Настрої студентства підігрівалися також тогочасними найвагомішими державними документами. Так, у IV Універсалі Центральної Ради говорилося: &laquo;До так званих &laquo;більшовиків&raquo; та інших напасників, що нищать та руйнують наш край, приписуємо правительству Української Народної Республіки твердо й рішуче взятися до боротьби з ними, а всіх громадян нашої Республіки закликаємо, не жаліючи життя, боронити добробут і свободу нашого народу. Наша Народна Українська Держава повинна бути вичищена від насланих з Петрограда найманих насильників, які топчуть права Української Республіки&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отже, можна аргументовано говорити про пряму причетність вищого державного керівництва до руху студентської молоді як в ідейному, так і в організаційно-технічному плані.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Впродовж 8-13 січня молоді воїни намагалися одержати амуніцію, озброєння й оволодіти елементарними навичками поводження з ним. У казармах Костянтинівського училища, зайнятих, природно, з дозволу військового начальства, якісного вишколу за вкрай короткий термін здійснити не вдалося. Вранці 13 січня до навчального закладу повернулася частина юнкерів (близько 300 чоловік), які розповіли про те, що вони під командою сотника А.Гончаренка у малій кількості (близько 600 чоловік), погано озброєні, одинокими залишаються на цілому Лівобережному фронті під Бахмачем і потребують негайної підтримки. Хто й як вирішував питання про відправлення на передову студентської сотні, що попервах планувалася для захисту Центральної Ради в Києві, за документами й спогадами достеменно з\'ясувати не можна. Відомо лише, що наказ було одержано &laquo;від командного складу 1-ї військової школи&raquo;. Однак, зрозуміло, що студенти самостійно вирішити багато питань, зокрема й про спеціальний потяг (хоч це й зайняло півтори доби), не могли.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">15 січня зранку 116 студентів (перша сотня) були вже на станції Крути і більшість із них відправилася на риття окопів уздовж залізничної колії між Крутами і Плисками. Обабіч бойові позиції зайняв загін юнкерів з 200 осіб.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Зранку 16 січня (тобто 29 - за новим стилем) загін балтійських матросів під командуванням Ремньова (за деякими даними, до двох тис.) на марші несподівано натрапив на зустрічний щільний вогонь юнкерів і студентів. Виникло сум\'яття. А тут ще наспів панцерник сотника Лощенка з гарматою і почав вести прицільний вогонь по тилах тих, хто наступав. У деяких джерелах згадується ще про дві батареї і два бронепотяги. Так чи інакше, ввірватися до Крут з ходу червоним не вдалося.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Подальший перебіг подій дня відтворити в деталях непросто - настільки суперечливо подають відомості навіть самі їх учасники.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У пресі розвиток трагедії висвітлено так. У кожного юного захисника Крут &laquo;було всього по три обійми патронів. ...А треба вже воювать, бо несподівано підійшов ворог і почав обстрілювати їх кулеметним і гарматним вогнем. Через короткий час виявилась нова несподіванка: з бокової лінії з Чернігова підійшло кілька російських ешелонів і почали обстріл з тилу. Далі було таке: штаб ніяких приказів не шле, патронів і знаряддя бійцям не присилає. Усі патрони витрачені. Одна одним гармата, котра була у юнкерів, мусила замовкнути, випустивши останню шрапнель. Прийшлось посилати когось на станцію шукати командира і штаб (судячи з усього в редакції &laquo;Нової Ради&raquo;, керівництві УПСФ знали справжні імена командира - &laquo;капі-тан Т&raquo; і склад штабу - &laquo;два брати Б.&raquo;, однак із якихось міркувань не стали широко обнародувати дані, що з часом стали нез\'ясовуваною таємницею. - <strong>В.С.</strong>). Їх на станції вже не було. Вони подалися за своїм поїздом, не повідомивши своє військо, не пославши йому ніякого приказу, а хапалися виїздити так, що забулися відчепити від свого поїзда вагони з знаряддям до гармат і з патронами, і завезли їх. Наших вояк росіяне оточили і перебили&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отже, хоч якими героїчними були дії захисників маловідомої доти залізничної станції, вони, врешті, не мали жодного шансу на успіх, тим більше - на перелом загальної ситуації на фронті. На кінець дня Крути опинилися вже в руках більшовиків, а шлях на Київ, де того ж дня почалося ро-бітниче повстання проти Центральної Ради, було відкрито.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Пізніше писали, з одного боку, про кровопролитність бою, неодноразові атаки моряків, що відзначалися нечуваною жорстокістю, і те, що їх мужньо стримували &laquo;напівдіти&raquo; (Д.Дорошенко), що начебто вони ще й кидались у контратаки. А з другого - про відсутність у студентів набоїв та й елементарного вміння стріляти (багато хто з них одержав у руки гвинтівку безпосередньо напередодні бою), про те, що юнакам було вкрай незручно у незграбних битих валянках, в які їх поспіхом взули, хоча склади Першої української військової школи (колишнє Костянтинівське юнкерське училище) ломилися від новеньких чобіт тощо. Все ж студентам якимось дивом удалося відійти з позиції у відкритому полі (на відстані кілометра від станції) і ешелоном, що чекав, від\'їхати у напрямку Києва. Організованому здійсненню останньої операції допомогло те, що юнаки завчасно розібрали залізничні колії і відірвалися від переслідувачів.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Можливо, якоюсь мірою закріпленню в публікаціях очевидних перебільшень сприяло й те, що амбітний М.Муравйов прагнув показати своєму начальству власні особливі заслуги у боротьбі за нову владу й у донесеннях Головнокомандуючому військами по боротьбі з контрреволюцією на Півдні Росії В.Антонову-Овсієнку явно &laquo;прикрашав&raquo; жорстокість єдиного бою, який довелося дати радянським військам на шляху до Києва, умисне завищував силу спротиву ворога, використовував у донесеннях навіть нісенітниці. &laquo;Після дводенного бою, - повідомляв він, - перша революційна армія Єгорова за підтримки другої армії Берзіна біля ст. Крути розбила контрреволюційні війська Ради, керовані самим Петлюрою. Петроградська червона гвардія, Виборзька і Московська гвардія винесли майже самі весь бій на своїх плечах. Петлюра під час бою пустив потяги з беззбройними солдатами з фронту назустріч революційним військам, які наступали, і відкрив по нещасних артилерійський вогонь. Війська Ради складались із баталь-йонів офіцерів, юнкерів і студентів, які, крім звірств, здійснених щодо солдатів, які поверталися з фронту, били сестер милосердя, що потрапили до їх рук. Іду на Київ. Селяни захоплено зустрічають революційні війська&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Неточності документа очевидні. Тут варто звернути увагу не стільки на те, що під Крутами не було С. Петлюри і згаданих у донесенні дій він, природно, не міг вчинити, скільки на переконане твердження, що у військах Центральної Ради були &laquo;батальйони офіцерів&raquo;. Мабуть, такими були кваліфіковані юнкери (хоча про них згадується й окремо), хоча надалі офіцерам така впевненість &laquo;приборкувача Києва&raquo; коштуватиме дуже дорого.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Утім, гадається, прагнути до спростування будь-яких обопільних неточностей, відрафінування деталей не так уже й важливо. Загального уявлення про розстановку сил у районі бою, як і про стратегічне становище УНР, достатньо, щоб зрозуміти абсолютну детермінованість кінцевого підсумку. Просто зайве &laquo;копирсання&raquo; у фактах скидається на бажання поставити під сумнів високий героїзм і патріотичну самопожертву юних борців за ідею, відданих захисників національної справи.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С.Петлюра, який перебував зранку того дня на ст. Бобрик, одержавши повідомлення і докладну доповідь про бій під Крутами, вирішив, що більша небезпека для УНР походить від повсталих арсенальців, і без вагань спрямував свій загін до Києва, наказавши студентам їхати до Дарниці.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І донині існує відчутний різнобій у визначенні не тільки масштабності бою під Крутами, його тривалості, ступеня жорстокості, а й, головне - кількості жертв.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Так, Д.Дорошенко наводить у поіменному переліку лише 11 прізвищ загиблих студентів, хоч пише, що першого дня (тобто 16 січня) була знищена частина куреня, а другого дня було розстріляно 27 полонених, над якими дико знущалися. Вони входили до розвідувальної роти, що відійшла до Крут на момент, коли станцією вже оволоділи червоні. Вісьмох поранених відправили до Харкова, де ними ніхто не зацікавився, і вони щезли зі шпиталів, куди їх влаштували на лікування. До Києва на перепоховання начебто було привезено &laquo;кілька десятків понівечених трупів&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Результати 40-річного документального дослідження питання ввійшли до книги С.Збаразького &laquo;Крути. У 40-річчя великого чину 29 січня 1918 - 29 січня 1956&raquo;, що побачила світ у 1958 р. у Мюнхені й Нью-Йорку у видавництві &laquo;Шлях молоді&raquo;. Книга відкривається таким мартирологом:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Згинули під Крутами:</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Сотник Омельченко - командир Студентського Куреня, студент Українського Народного Університету в Києві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Володимир Яковлевич Шульгин, Лука Григорович Дмитренко, Микола Лизогуб, Олександр Попович, Андріїв, Божко-Божинський - студенти Університету св. Володимира в Києві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ізидор Курик, Олександр Шерстюк, Головощук, Чижів, Кирик - студенти Українського Народного Університету в Києві.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Андрій Соколовський - учень 6-ї кляси 2-ї Української Київської Гімназії.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Микола Корпан з Тяпча, під Болеховом, Західна Україна. М. Ганькевич, Євген Тарнавський, Гнаткевич, Пипський - учень 7-ї кляси, родом з Західної України, розстріляний з 35-ма іншими на станції Крути, перед розстрілом перший почав співати &laquo;Ще не вмерла Україна&raquo;, всі інші підтримали спів&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Отже, називається 18 імен. Відтоді ніяких змін до наведеного переліку ніхто не спромігся внести...</span></span></p>\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">* * *</span></span></h3>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">У вирі тогочасних подій ані бій під Крутами, ані його учасники не привернули уваги громадськості. Однак, коли з поверненням до Києва у березні 1918 р. Центральної Ради ситуація стабілізувалася, близькі й друзі порушили питання про перепоховання загиблих.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">9 березня &laquo;Нова рада&raquo; вмістила такі рядки: &laquo;Гурток родичів звертається до всіх батьків і родичів студентів, середньошкільників і інших, що входили в склад січового стрілецького куреня і загинули в бою та розстріляні після бою біля Крут 16 січня с. р. і пропонує піднести загальне прохання про розкопку могил, щоб розпізнати і перевезти їх тіла з Крут, а також поховати у Києві&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Історія набула резонансного присмаку скандальності. 16 березня під криптонімом &laquo;С.Ш.&raquo; (найімовірніше - Сергій Шемет, один з лідерів Української партії хліборобів-демократів, що тоді дедалі рішучіще критикувала провід Центральної Ради й Генерального Секретаріату) з\'явилася стаття &laquo;Трагедія на Крутах&raquo;. У публікації говорилося: &laquo;Ми хочемо звернути увагу суспільства й української влади на ту страшну трагедію, котра відбулася біля ст. Крути в часи наближення большевиків до Києва. В Крутах загинув цвіт української шкільної молоді. Загинуло кілька сот найкращої інтелігенції - юнаків - ентузіастів української національної ідеї.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Така втрата для культурної нації була б важкою; для нашого народу<br />вона безмірна. Винна в цій трагедії уся система безглуздя, весь наш<br />уряд, котрий після блискучого со-ціального законодавства, після піврічного адміністрування оказався покинутим народом і армією, і в такім безнадійнім становищі рішив захиститись від добре озброєної большевицької армії кількома сотнями шкільної молоді. Узброївши на скору руку ці жертви урядової легковажності, без жодної військової підготовки одправила їх в Крути...&raquo;. Автор вимагав від уряду зробити належні висновки й покарати, &laquo;а хоч принаймні усунути геть від справи винуватців&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хоча в статті не називаються конкретні імена, кожен добре розумів, що йдеться передусім про вище політичне й військове керівництво УНР, зокрема М.Грушевського й особливо С.Петлюру, який повернув собі колишні позиції військового лідера.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Ось тут і стали у великій нагоді глибокі професорські знання й полі-тична інтуїція М.Грушевського. Видатний український історик, безперечний тогочасний національний лідер не раз звертав увагу на певні психологічні особливості української нації, які навіть був схильний відносити до її ментальних рис. Серед них, хоч як дивно - вміння влаштовувати похорони. &laquo;Вони великі майстри в сім і вкладають у похоронні церемонії всю душу, - зазначав Голова Центральної Ради. - Але підтримати за життя, в боротьбі, котру ведуть до останнього найбільш енергійні й віддані інтересам загалу люди - не їх діло, вони тримаються гасла: &laquo;моя хата скраю&raquo;, беруть нейтралітет і вичікують, хто кого переможе: свій чи чужий, і коли свій поляже - справляють йому похорони і записують до національних святців...&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Хоча М.Грушевський говорить про такі національні звичаї з очевидним негативним відтінком, саме до &laquo;традиційного&raquo; варіанта він удався й сам, коли довелося давати відповіді на схвильовані запитання збуреної громадськості і віднаходити вихід з непростої ситуації. На засіданні Малої Ради він запропонував вшанувати пам\'ять загиблих під Крутами і перенести їхні тіла до Києва, на Аскольдову могилу. Зібрання вшанувало пам\'ять героїв вставанням й ухвалило &laquo;прийняти похорон на кошт держави&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Велелюдний похорон відбувся 19 березня 1918 р. На вокзалі, куди привезли останки загиблих, о другій годині дня зібралися їхні рідні, студенти, гімназисти, вояки, духовенство, хор під орудою О.Кошиця, багато киян. Заупокійну відправу відслужив єпископ Никодим. Візники, з двома сіро-блакитними трунами на площадці у кожного, рушили від вокзалу. Біля будинку Центральної Ради до траурної ходи з жалібно-урочистим словом звернувся Михайло Грушевський: &laquo;От у сій хвилі, - мовив він, - коли провозять ся їх домовини перед Центральною Радою, де протягом року кувалась українська державність, з фронтону її будинку здирають російського орла, ганебний знак росийської власти над Україною, символ неволі, в котрій вона прожила двісті шістьдесят з верхом літ. Видко, можливість його здерти не давалась даремно, видко, вона не могла пройти без жертв, її треба було купити кров\'ю. І кров пролили сі молоді герої, котрих ми проважаємо&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">До братської могили на Аскольдовому кладовищі, за даними тогочасної преси, було опущено 17 трун (лише на одиницю відрізняється ця цифра від вищенаведеного поіменного переліку жертв С.Збаразьким). Можливо, тут не було тіла сотника А.Омельченка, смертельно пораненого під час бою 16 січня. Його було відразу відправлено до Києва, та по дорозі він помер. Вочевидь, він був похований не під Крутами, хоча дехто з мемуаристів і фахівців називають і його ім\'я серед тих, кого поховали на Аскольдовій могилі). Газети вміщували траурні промови й статті Л.Старицької-Черняхівської, С.Єфремова, В.Дурдуківського, інших відомих діячів українства.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Павло Тичина присвятив загиблим вірш &laquo;Пам\'яті тридцяти&raquo; (названу кількість тут пояснити непросто, якщо не зважити на поетичну схильність до гіпербол):</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;На Аскольдовій Могилі</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Поховали їх -</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тридцять мучнів українців,</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Славних, молодих...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">На Аскольдовій Могилі</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Український цвіт! -</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">По кривавій по дорозі</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нам іти у світ&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Тодішня преса широко висвітлювала перепоховання героїв: &laquo;Похорон жертв боротьби за волю України&raquo; - &laquo;Народна Воля&raquo;, &laquo;Два похорони&raquo; - &laquo;Боротьба&raquo;, &laquo;Pro patria mori&raquo; - &laquo;Киевская мысль&raquo;. Траплялися й прикрі для влади висловлювання. Так, лікар С.Коломійцев у кореспонденції &laquo;На пам\'ятник жертвам у Крутах&raquo; зазначав: &laquo;Цвіт української інтелігенції, діти, що не вміли стріляти, були послані дезорганізованою українською владою назустріч озброєним &laquo;до зубів&raquo; большевикам-росіянам... Честь і слава молодим героям, і вічна ганьба тим, хто повинен був не себе, а їх спасти, але не зробив цього&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Там же було надруковано і теплий відгук-спомин члена ЦК УПСФ С.Єфремова про Володимира Шульгина, якого вчений-ерудит знав з дитинства і шанував високі обдаровання юного патріота. Партійний і державний діяч також не втримався, щоб не висловити гнівного осуду на адресу влади, яка покликала для своєї оборони молодих романтиків, ідеалістів. &laquo;І от тут починається страшна трагедія таких душ, як Шульгинова, - пише С.Єфремов. - На заклик вони озвалися, пішли й зложили все, що мали... Але я уявляю собі, що довелось їм пережити там, під Крутами, покинутим, беззбройним і беззахисним, перед невблаганним диким ворогом, і, може, з убійчою свідомістю, що чиста жертва їх ні на що не здалася, що вона за даних обставин страшної дезорганізації вийшла зайвою і непотрібною, нікого і нічого не рятувала. Воля, як і доля, &laquo;жертв искупительных просит&raquo; - з цим треба миритись. Але не можна миритись, коли ці жертви розтрачено так марно, як це було з святим поривом української молоді, що головами наложила під Крутами&raquo;.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Однак загальний настрій, у тому числі й родичів загиблих, явно змістився у бік вдячності владі, яка виказала до загиблих такі підкреслені почесті на найвищому державному рівні і взяла зобов\'язання надалі ще більше вшанувати молодих героїв (інших бо й взагалі не було). Навіть опосередкована причетність до загальновизнаного національного подвигу робила незручною подальшу публічну вимогу сатисфакції щодо влади. Всупереч досить поширеним твердженням, ніби М.Грушевський-політик помітно поступається М.Грушевському-науковцю, запропоноване Головою Центральної Ради рішення переконливо свідчить про інше: здатність тримати руку на пульсі громадських настроїв і ефективно, тонко впливати на них.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">С. Петлюра, який у березні 1918 р. жодним словом не обізвався про події під Крутами (причини зрозумілі - остерігався нагадати про свою роль й викликати на себе до часу безіменний вогонь критичних стріл), і тоді ж таки був відсунутий на другорядний політичний план, уже після поразки Української революції також не раз звертався до героїчного подвижництва юнаків. У публіцистичній праці про патріотизм він у притаманній йому патетичній манері писав: &laquo;Коли вимовляю це слово, в моїй уяві постають незабутні моменти історичних днів під Крутами.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Страшні й величні картини!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Нас мало, але дух наш і єдність із криці&raquo;, - чую з уст молодого сина України, що сміливо й одверто йде на нерівний бій з нечесним ворогом.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Мамо, ми вмираємо, але щоб Україна вічно жила!&raquo; - в передсмертних муках кличе його товариш й гине славною смертю з ім\'ям Вітчизни й Матері на устах...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">&laquo;Наша смерть, то лише мізерний, потужний дар вічним, невгасимим змаганням за честь і незалежність України&raquo; - казали інші й з радісною усмішкою на устах приносили себе в жертву Великому укоханої Батьківщини...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Коли ж, нарешті, родиться геній-маляр, що оживить в нашому сумлінні красу смерті за добробути Вітчизни?</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Чи є фарби, що ними зарисує він ці благородні профілі найкращих синів України?..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Нема!..</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">А вони такі необхідні...</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">І справжні, не фальшиві фарби, котрі промовляли б до серця товаришів та братів і батьків тих, що вмирали під Крутами, котрі б нагадали живим заповіт мертвих героїв...&raquo;</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Не вдаючись до цілої низки умовиводів, до яких саме собою настійно спонукає написане С.Петлюрою, гадається, в контексті вищезазначеного, понад інше, варто не пройти повз ремінісценцію особистісного забарвлення. Зовсім небайдужий до &laquo;суду історії&raquo; (самозахисне запевнення, що він його не боїться - безперечна нещирість, оскільки не може бути по-літика, індивідуума взагалі, який би почувався в згаданому сенсі апріорі впевнено), один із провідних діячів Української революції, вочевидь, знайшов форму каяття, що її суто по-людському, по-християнському можна прийняти й зрозуміти.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong> </strong></span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\"><strong>P.S.</strong> Набравши певної інерційної самодостатності, в українській історіографії подія під Крутами набула гіпертрофованих оцінок, обросла міфами, стала порівнюватись із відомим подвигом спартанців під Фермопілами, а загиблими дедалі частіше стали називати всіх 300 юнаків, з них - 250 студентів і гімназистів. За відсутності інших яскравих прикладів вияву національної самосвідомості й жертовності, до цієї події дедалі активніше звертаються, реалізуючи виховні заходи, особливо в середовищі молоді.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Вищевикладене зумовлює висновок про потребу виваженішого підходу до непростої сторінки вітчизняної історії, застосування оцінок і висновків, вільних від кон\'юнктурних збочень і невиправданих політичних маніпулювань, штучного виривання окремих подій із загального контексту й довільного їх препарування. Зрада істини не може бути виправданою жодними мотивами й розрахунками.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">Об\'єктивні, неупереджені уроки з історичного досвіду - якими б незручними й неприємними вони не видавалися - єдина гарантія уникнути повторення прикрих прорахунків і помилок минулого, водночас - надійний ключ до пошуку оптимальних варіантів перспективи суспільного розвитку. Це рівною мірою стосується як науки, так й ідеологічної сфери.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: arial,helvetica,sans-serif;\">За матеріалами: http://www.dt.ua/<br /></span></span></p>','«І справжні, не фальшиві фарби»? Крути: спроба історичної інтерпретації','uk',1141,2115,0,'Любов Михайлюк');
